(window.webpackJsonp=window.webpackJsonp||[]).push([[21],{43:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u5341\u8282 \u5371\u673a\uff0c\u793c\u7269",content:"<article><h2>\u7b2c\u4e8c\u5341\u8282 \u5371\u673a\uff0c\u793c\u7269</h2><p>\u9634\u9b42\u57ce\u7684\u63f4\u519b\u98de\u5feb\u8d76\u5230\uff0c\u6218\u6597\u7ed3\u675f\u3002\u9a6c\u65af\u514b\u7684\u7948\u5e76\u8005\u5927\u90e8\u5206\u6218\u6b7b\uff0c\u8fd8\u6293\u4e86\u51e0\u4e2a\u4fd8\u864f\uff0c\u6253\u7b97\u5e26\u56de\u53bb\u5ba1\u95ee\u3002\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u8dd1\u5230\u9634\u9b42\u57ce\u9644\u8fd1\u63a2\u5934\u63a2\u8111\uff0c\u8fd9\u7740\u5b9e\u662f\u4ef6\u6709\u4e9b\u5947\u602a\u7684\u4e8b\u60c5\uff0c\u4e0d\u80fd\u4e0d\u5f04\u4e2a\u660e\u767d\u3002</p><p>\u6e05\u70b9\u4eba\u6570\uff0c\u51e0\u4e4e\u6ca1\u6709\u53d7\u5230\u4ec0\u4e48\u771f\u6b63\u7684\u635f\u5931\uff0c\u4ec5\u6709\u51e0\u4e2a\u58eb\u5175\u53d7\u4f24\u3002\u6574\u4f53\u6765\u8bf4\uff0c\u7b97\u662f\u6709\u60ca\u65e0\u9669\uff0c\u5c0f\u5c0f\u7684\u610f\u5916\u63d2\u66f2\u7f62\u4e86\u3002\u5168\u961f\u987a\u5229\u8fd4\u56de\u9634\u9b42\u57ce\uff0c\u4e0b\u4e86\u6d6e\u7a7a\u8247\uff0c\u5c06\u6d6e\u7a7a\u9774\u7b49\u7269\u54c1\u4ea4\u8fd8\u519b\u961f\uff0c\u89c1\u4e60\u7267\u5e08\u4eec\u81ea\u56de\u795e\u6bbf\uff0c\u743c\u6069\u548c\u540c\u5b66\u5219\u56de\u5deb\u5e08\u5b66\u6821\u3002</p><p>\u743c\u6069\u5fc3\u91cc\uff0c\u4e5f\u5e76\u6ca1\u6709\u628a\u8fd9\u6b21\u906d\u9047\u592a\u5f53\u56de\u4e8b\uff0c\u53cd\u6b63\u5b89\u7136\u65e0\u6059\u56de\u6765\u3002\u81f3\u4e8e\u66b4\u9732\u4e86\u81ea\u5df1\u5df2\u7ecf\u80fd\u89e6\u6478\u9b54\u7f51\u7b2c\u4e8c\u5c42\uff0c\u5176\u5b9e\u4e5f\u6ca1\u591a\u5927\u8981\u7d27\uff0c\u4e5f\u662f\u8fdf\u65e9\u7684\u4e8b\u60c5\u3002\u5df2\u7ecf\u53d1\u751f\u4e86\u5c31\u4e0d\u8981\u518d\u53bb\u591a\u60f3\uff0c\u8fd9\u662f\u743c\u6069\u7684\u4e00\u8d2f\u51c6\u5219\u3002</p><p>\u56de\u5230\u5b66\u6821\uff0c\u5df2\u7ecf\u662f\u4e0b\u5348\u4e09\u70b9\u949f\uff0c\u4f11\u606f\u4e4b\u540e\uff0c\u743c\u6069\u8d77\u5e8a\u53d6\u51fa\u9b54\u6cd5\u4e66\uff0c\u518d\u6b21\u51c6\u5907\u9b54\u6cd5\u3002\u968f\u540e\uff0c\u4ed6\u8d70\u51fa\u5bbf\u820d\uff0c\u8fdb\u5165\u81ea\u5df1\u7684\u5b9e\u9a8c\u5ba4\u3002</p><p>\u4e09\u5e74\u7ea7\u4ee5\u4e0a\u7684\u5b66\u751f\uff0c\u4fbf\u62e5\u6709\u81ea\u5df1\u7684\u5b9e\u9a8c\u5ba4\u3002\u743c\u6069\u6700\u65e9\u542c\u5230\u5b9e\u9a8c\u5ba4\u8fd9\u4e2a\u540d\u5b57\uff0c\u4ee5\u4e3a\u662f\u505a\u7269\u7406\u5316\u5b66\u8bd5\u9a8c\u7684\u6240\u5728\uff0c\u6446\u6ee1\u4e86\u5404\u79cd\u74f6\u74f6\u7f50\u7f50\u70e7\u676f\u8bd5\u7ba1\uff0c\u540e\u6765\u624d\u77e5\u9053\u6ca1\u8fd9\u4e48\u7b80\u5355\u3002\u7cbe\u7814\u4ea1\u7075\u9b54\u6cd5\u7684\u5deb\u5e08\uff0c\u4ed6\u7684\u5b9e\u9a8c\u5ba4\u91cc\u6709\u53ef\u80fd\u8d70\u6765\u8d70\u53bb\u90fd\u662f\u9ab7\u9ac5\u67b6\u5b50\uff0c\u684c\u4e0a\u5899\u4e0a\u6302\u6ee1\u5185\u810f\u5668\u5b98\uff0c\u4eff\u4f5b\u5c60\u5bb0\u573a\uff1b\u64c5\u957f\u5492\u6cd5\u7684\u5deb\u5e08\uff0c\u5b9e\u9a8c\u5ba4\u91cc\u5c31\u66f4\u53ef\u80fd\u5230\u5904\u662f\u9b54\u6cd5\u9635\u3001\u7b26\u6587\u548c\u5f02\u4f4d\u9762\u602a\u7269\u5728\u7b3c\u5b50\u91cc\u8e66\u6765\u8df3\u53bb\uff1b\u559c\u6b22\u5e7b\u672f\u7684\u5deb\u5e08\uff0c\u5b9e\u9a8c\u5ba4\u91cc\u5c31\u6bd4\u8f83\u8be1\u5f02\u4e86\uff0c\u4ec0\u4e48\u5947\u602a\u7684\u666f\u8c61\u90fd\u6709\u53ef\u80fd\u53d1\u751f\uff0c\u4e00\u8d70\u8fdb\u53bb\u4eff\u4f5b\u5c31\u7f6e\u8eab\u53e6\u5916\u4e00\u4e2a\u4e16\u754c\u2014\u2014\u5176\u5b9e\u5927\u591a\u90fd\u662f\u5e7b\u8c61\u3002</p><p>\u79c9\u627f\u9634\u9b42\u57ce\u4e00\u8d2f\u7684\u201c\u7cbe\u82f1\u5316\u201d\u7406\u5ff5\uff0c\u4ece\u56db\u5e74\u7ea7\u5f00\u59cb\uff0c\u6240\u6709\u5deb\u5e08\u5b66\u6821\u7684\u5b66\u751f\u90fd\u8981\u9009\u62e9\u81ea\u5df1\u7684\u4e13\u4e1a\u65b9\u5411\u3002\u9634\u9b42\u57ce\u57f9\u517b\u5deb\u5e08\uff0c\u91cd\u7cbe\u901a\uff0c\u4e0d\u91cd\u5e7f\u535a\uff0c\u91cd\u4e13\u5bb6\uff0c\u4e0d\u91cd\u901a\u624d\uff0c\u53ea\u6709\u4e24\u79cd\u4eba\u6837\u6837\u90fd\u4f1a\u2014\u2014\u7b2c\u4e00\u79cd\u662f\u7edd\u9876\u5929\u624d\uff0c\u7b2c\u4e8c\u79cd\u5c31\u662f\u4e09\u6d41\u52a3\u7b49\u89d2\u8272\u3002</p><p>\u6309\u7167\u901a\u884c\u7684\u7406\u8bba\uff0c\u9b54\u6cd5\u5206\u516b\u5927\u5b66\u6d3e\uff0c\u5206\u522b\u662f\u9632\u62a4\u3001\u5492\u6cd5\u3001\u5851\u80fd\u3001\u4ea1\u7075\u3001\u5e7b\u672f\u3001\u9644\u9b54\u3001\u53d8\u5316\u548c\u9884\u8a00\u3002\u6bcf\u4e2a\u664b\u5165\u56db\u5e74\u7ea7\u7684\u5b66\u751f\uff0c\u90fd\u8981\u5728\u8fd9\u516b\u5927\u5b66\u6d3e\u4e2d\u9009\u62e9\u5176\u4e00\u4f5c\u4e3a\u81ea\u5df1\u7684\u4e13\u4e1a\u65b9\u5411\u3002</p><p>\u5f53\u7136\uff0c\u9009\u62e9\u4e00\u4e2a\u4e13\u4e1a\u65b9\u5411\uff0c\u4e0d\u7b49\u4e8e\u5b8c\u5168\u653e\u5f03\u6389\u5176\u4ed6\u5b66\u6d3e\u3002\u56e0\u4e3a\u4e0d\u540c\u6d3e\u7cfb\u7684\u9b54\u6cd5\uff0c\u5176\u5b9e\u79c9\u627f\u4e0d\u540c\u7684\u539f\u7406\uff0c\u5f80\u5f80\u5f7c\u6b64\u95f4\u5b58\u5728\u4e25\u91cd\u7684\u51b2\u7a81\u751a\u81f3\u622a\u7136\u5bf9\u7acb\u3002\u6240\u4ee5\u6839\u636e\u6570\u5343\u5e74\u6d41\u4f20\u603b\u7ed3\u4e0b\u6765\u7684\u7ecf\u9a8c\uff0c\u4e3a\u4e86\u9632\u6b62\u610f\u5916\uff0c\u5deb\u5e08\u4e00\u65e6\u9009\u62e9\u4e86\u67d0\u4e2a\u5b66\u6d3e\u4f5c\u4e3a\u81ea\u5df1\u7684\u4e13\u4e1a\u7cbe\u7814\u65b9\u5411\uff0c\u5c31\u81ea\u89c9\u65ad\u7edd\u6389\u53e6\u5916\u67d0\u4e00\u4e24\u4e2a\u5b66\u6d3e\u7684\u9b54\u6cd5\u5b66\u4e60\uff0c\u4ece\u6b64\u78b0\u90fd\u4e0d\u78b0\uff0c\u4ee5\u514d\u51b2\u7a81\u8d77\u6765\u8d70\u706b\u5165\u9b54\uff0c\u4e00\u547d\u545c\u547c\u3002</p><p>\u743c\u6069\u81ea\u7136\u4e5f\u4e0d\u6562\u5192\u9669\uff0c\u4ed6\u8003\u8651\u4e86\u5f88\u4e45\uff0c\u9009\u62e9\u53d8\u5316\u5b66\u6d3e\u4f5c\u4e3a\u81ea\u5df1\u7684\u4e13\u4e1a\u65b9\u5411\uff0c\u7136\u540e\u2026\u2026\u7136\u540e\u5c31\u518d\u4e5f\u4e0d\u53bb\u542c\u4ea1\u7075\u7cfb\u548c\u5851\u80fd\u7cfb\u7684\u6559\u6388\u8bb2\u8bfe\u4e86\uff0c\u53cd\u6b63\u4ed6\u4e5f\u53cd\u611f\u8fd9\u4e24\u4e2a\u5b66\u6d3e\u3002</p><p>\u4e0d\u8fc7\u8fd9\u79cd\u5f3a\u884c\u89c4\u5b9a\u9009\u62e9\u4e13\u4e1a\u65b9\u5411\u7684\u4f5c\u6cd5\uff0c\u4e5f\u53ea\u5728\u9634\u9b42\u57ce\u91cc\u666e\u904d\u63a8\u884c\u3002\u542c\u6559\u6388\u8bf4\u8d77\u8fc7\uff0c\u5728\u7269\u8d28\u754c\uff0c\u5deb\u5e08\u4eec\u5c31\u6ca1\u8fd9\u4e48\u591a\u8bb2\u7a76\uff0c\u5927\u90e8\u5206\u90fd\u662f\u968f\u5fc3\u6240\u6b32\uff0c\u7231\u5b66\u4ec0\u4e48\u5b66\u4ec0\u4e48\uff0c\u666e\u904d\u7684\u98ce\u6c14\u662f\u91cd\u89c6\u901a\u624d\u8d85\u8fc7\u4e13\u5bb6\uff0c\u751a\u81f3\u8ba4\u4e3a\u5e7f\u535a\u6d89\u730e\u624d\u662f\u6b63\u9053\uff0c\u7cbe\u4fee\u67d0\u4e00\u5b66\u6d3e\u662f\u773c\u5149\u72ed\u9698\u4e4b\u4e3e\u3002</p><p>\u53d7\u9634\u9b42\u57ce\u7684\u6559\u80b2\u548c\u98ce\u6c14\u718f\u9676\uff0c\u743c\u6069\u76ee\u524d\u7684\u60f3\u6cd5\uff0c\u81ea\u7136\u4e5f\u8ba4\u4e3a\u7cbe\u7814\u4e13\u4fee\u624d\u662f\u6b63\u9053\uff0c\u6837\u6837\u90fd\u4f1a\u4e9b\uff0c\u90a3\u53eb\u4e07\u91d1\u6cb9\uff0c\u6709\u4ec0\u4e48\u53ef\u53d6\u7684\uff1f\u7269\u8d28\u754c\u7684\u5deb\u5e08\u540c\u884c\uff0c\u771f\u662f\u7b28\u86cb\u5230\u5bb6\u4e86\u3002</p><p>\u743c\u6069\u4e13\u4e1a\u7cbe\u7814\u7684\u53d8\u5316\u7cfb\u9b54\u6cd5\uff0c\u53c8\u6709\u4e2a\u540d\u79f0\u53eb\u70bc\u91d1\u672f\uff0c\u6700\u6700\u62ff\u624b\u7684\u5c31\u662f\u8f6c\u5316\u6539\u53d8\u4e8b\u7269\u7684\u72b6\u6001\uff0c\u8fd9\u5176\u4e2d\u81ea\u7136\u6d89\u53ca\u5927\u91cf\u7684\u7269\u7406\u5316\u5b66\u77e5\u8bc6\uff0c\u6240\u4ee5\u743c\u6069\u7684\u5b9e\u9a8c\u5ba4\u91cc\uff0c\u5012\u786e\u5b9e\u662f\u4e00\u5806\u70e7\u676f\u8bd5\u7ba1\u7684\uff0c\u53ea\u8981\u4ed6\u518d\u5957\u8eab\u767d\u5927\u8902\uff0c\u5c31\u80fd\u626e\u6f14\u79d1\u5b66\u5bb6\u4e86\u3002</p><p>\u5e73\u5e38\u5728\u5b9e\u9a8c\u5ba4\u91cc\uff0c\u743c\u6069\u6700\u559c\u6b22\u5e72\u7684\u4e8b\u60c5\u662f\u914d\u5236\u5404\u79cd\u9b54\u6cd5\u836f\u6c34\uff0c\u6709\u7a7a\u8fd8\u628a\u7530\u4f2f\u5149\u6614\u65e5\u6559\u4ed6\u7684\u90a3\u4e9b\u6625\u836f\u914d\u65b9\u62ff\u51fa\u6765\u7814\u7a76\uff0c\u53c2\u7167\u6240\u5b66\u7684\u9b54\u6cd5\u77e5\u8bc6\u52a0\u4ee5\u6539\u826f\uff0c\u9887\u6709\u6548\u7528\u3002\u5bf9\u4e8e\u5deb\u5e08\u6765\u8bf4\uff0c\u914d\u5236\u9b54\u6cd5\u836f\u6c34\u4e5f\u662f\u57fa\u672c\u529f\uff0c\u51e0\u4e4e\u4eba\u4eba\u90fd\u4f1a\uff0c\u4f46\u7cbe\u7814\u53d8\u5316\u5b66\u6d3e\u7684\u5deb\u5e08\uff0c\u5728\u8fd9\u65b9\u9762\u5f80\u5f80\u66f4\u52a0\u4f18\u79c0\u4e00\u4e9b\u3002</p><p>\u5176\u5b9e\u53d8\u5316\u5b66\u6d3e\u9b54\u6cd5\uff0c\u6700\u8457\u540d\u7684\u4e0d\u662f\u914d\u5236\u9b54\u6cd5\u836f\u6c34\uff0c\u800c\u662f\u5236\u9020\u5404\u79cd\u9b54\u50cf\u5080\u5121\uff0c\u4e0d\u8fc7\u8fd9\u662f\u9876\u5c16\u6280\u5de7\uff0c\u743c\u6069\u8fd8\u6839\u672c\u4e0d\u53ef\u80fd\u505a\u5230\u3002\u5176\u6b21\u662f\u5236\u9020\u5404\u79cd\u9b54\u6cd5\u7269\u54c1\uff0c\u6700\u5bb9\u6613\u7684\u624d\u662f\u914d\u5236\u836f\u6c34\u3002\u5f53\u7136\uff0c\u8fd9\u53ea\u662f\u4e2a\u7c97\u7565\u5927\u6982\u7684\u8bf4\u6cd5\uff0c\u4e0d\u5c11\u590d\u6742\u7684\u9b54\u6cd5\u836f\u6c34\uff0c\u540c\u6837\u662f\u9700\u8981\u975e\u5e38\u9ad8\u6df1\u7684\u6280\u5de7\u7684\u3002</p><p>\u5236\u9020\u9b54\u6cd5\u7269\u54c1\uff0c\u76f8\u5bf9\u800c\u8a00\u6bd4\u914d\u5236\u836f\u6c34\u590d\u6742\u4e00\u4e9b\uff0c\u5728\u80fd\u89e6\u6478\u9b54\u7f51\u7b2c\u4e8c\u5c42\u4e4b\u524d\uff0c\u57fa\u672c\u4e0d\u7528\u8003\u8651\u3002\u743c\u6069\u5728\u524d\u51e0\u5929\u53d1\u73b0\u81ea\u5df1\u7684\u8fdb\u6b65\u7a81\u7834\uff0c\u559c\u4e0d\u81ea\u7981\uff0c\u53c8\u60f3\u5230\u9a6c\u4e0a\u5c31\u662f\u6708\u672b\uff0c\u4fbf\u6253\u7b97\u4eb2\u624b\u7ed9\u59d0\u59d0\u73ca\u5609\u5236\u4f5c\u4e00\u4ef6\u793c\u7269\u3002</p><p>\u5236\u4f5c\u9b54\u6cd5\u7269\u54c1\uff0c\u9700\u8981\u7edd\u5bf9\u7684\u4e13\u5fc3\u81f4\u5fd7\u3002\u743c\u6069\u805a\u7cbe\u4f1a\u795e\uff0c\u65f6\u95f4\u4e0d\u77e5\u4e0d\u89c9\u95f4\u5c31\u98de\u5feb\u901d\u53bb\uff0c\u6700\u7ec8\uff0c\u4ed6\u987a\u5229\u5b8c\u6210\u4e86\u6700\u540e\u4e00\u9053\u6b65\u9aa4\uff0c\u5c06\u6210\u679c\u6536\u8fdb\u6000\u4e2d\uff0c\u63a8\u95e8\u51fa\u6765\uff0c\u5916\u9762\u5df2\u7ecf\u6726\u6726\u5fae\u4eae\u4e86\u3002</p><p>\u203b\u203b\u203b</p><p>\u7a7f\u8fc7\u719f\u6089\u7684\u5c0f\u5df7\uff0c\u8d70\u5230\u81ea\u5df1\u5bb6\u95e8\u53e3\uff0c\u5df2\u7ecf\u542c\u89c1\u53a8\u623f\u91cc\u7684\u8f7b\u5fae\u58f0\u54cd\uff0c\u73ca\u5609\u5728\u51c6\u5907\u65e9\u9910\u3002\u5979\u77e5\u9053\u6bcf\u4e2a\u6708\u7684\u6708\u672b\uff0c\u743c\u6069\u90fd\u8981\u5b9a\u671f\u56de\u6765\uff0c\u6240\u4ee5\u8fd9\u4e00\u5929\u5c31\u95ed\u95e8\u505c\u4e1a\uff0c\u4e0d\u5f00\u5546\u5e97\uff0c\u5728\u5bb6\u91cc\u7b49\u7740\u3002</p><p>\u63a8\u5f00\u95e8\uff0c\u8d70\u8fdb\u5ba2\u5385\uff0c\u5bb6\u91cc\u6536\u62fe\u5f97\u6574\u6574\u9f50\u9f50\u3002\u73ca\u5609\u5176\u5b9e\u8bf4\u4e0d\u4e0a\u6709\u6d01\u7656\uff0c\u4f46\u5f88\u7231\u5e72\u51c0\uff0c\u5982\u679c\u5979\u770b\u5230\u5bb6\u91cc\u4e71\u7cdf\u7cdf\u7684\uff0c\u5c31\u4e00\u5b9a\u8981\u9a6c\u4e0a\u6574\u7406\u597d\uff0c\u5426\u5219\u665a\u4e0a\u7761\u89c9\u90fd\u4e0d\u5b89\u7a33\uff0c\u4ece\u5c0f\u5230\u5927\u90fd\u8fd9\u6837\u3002</p><p>\u542c\u5230\u811a\u6b65\u58f0\uff0c\u73ca\u5609\u4ece\u53a8\u623f\u91cc\u63a2\u51fa\u5934\u6765\uff0c\u201c\u65e9\u9910\u9a6c\u4e0a\u5c31\u597d\uff0c\u201d\u5979\u7b11\u7740\uff0c\u201c\u7b49\u4e94\u5206\u949f\u5c31\u884c\u3002\u201d</p><p>\u743c\u6069\u5e94\u4e86\u4e00\u58f0\uff0c\u5750\u5728\u6c99\u53d1\u4e0a\u3002</p><p>\u73ca\u5609\u7684\u65f6\u95f4\u628a\u63e1\u5f97\u5f88\u51c6\u786e\uff0c\u4e94\u5206\u949f\u540e\uff0c\u59d0\u5f1f\u4fe9\u5df2\u7ecf\u5750\u5728\u684c\u524d\u4eab\u7528\u725b\u5976\u548c\u714e\u9e21\u86cb\u4e86\u3002</p><p>\u201c\u59d0\u59d0\uff0c\u751f\u65e5\u5feb\u4e50\u3002\u201d\u743c\u6069\u770b\u770b\u65f6\u5019\u5dee\u4e0d\u591a\uff0c\u5c06\u6000\u4e2d\u7684\u793c\u7269\u62ff\u4e86\u51fa\u6765\uff0c\u9012\u7ed9\u73ca\u5609\u3002\u524d\u4e9b\u5929\u662f\u59d0\u5f1f\u4e24\u4eba\u7684\u751f\u65e5\uff0c\u53ea\u662f\u5f53\u65f6\u743c\u6069\u5728\u5b66\u6821\uff0c\u6ca1\u6cd5\u56de\u5bb6\uff0c\u4eca\u5929\u8865\u4e0a\u793c\u7269\u3002</p><p>\u90a3\u662f\u4e00\u53ea\u94f6\u8272\u7684\u8033\u9489\uff0c\u6750\u6599\u5012\u4e5f\u5c31\u662f\u666e\u901a\u7684\u767d\u94f6\uff0c\u4f5c\u5de5\u5e73\u5e73\uff0c\u6837\u5f0f\u662f\u4e00\u53ea\u8718\u86db\u5f62\u72b6\u3002\u9634\u9b42\u57ce\u7684\u5973\u6027\uff0c\u5927\u591a\u4e5f\u90fd\u6234\u8033\u9970\uff0c\u6216\u8005\u8033\u73af\uff0c\u6216\u8005\u8033\u9489\uff0c\u73ca\u5609\u4e5f\u4e0d\u4f8b\u5916\uff0c\u53ea\u4e0d\u8fc7\u2026\u2026</p><p>\u201c\u600e\u4e48\u53ea\u6709\u4e00\u53ea\uff0c\u201d\u73ca\u5609\u63a5\u8fc7\u6765\uff0c\u201c\u8fd8\u6709\u4e00\u53ea\u5462\uff1f\u201d</p><p>\u8033\u9489\u548c\u8033\u73af\u4e00\u6837\uff0c\u81ea\u7136\u4e5f\u5e94\u8be5\u662f\u4e00\u5bf9\u7684\uff0c\u4eba\u90fd\u6709\u4e24\u53ea\u8033\u6735\u561b\u3002</p><p>\u201c\u5443\uff0c\u90a3\u4e2a\uff0c\u5c31\u53ea\u6709\u4e00\u53ea\u3002\u201d\u743c\u6069\u6709\u4e9b\u5c34\u5c2c\uff0c\u5012\u4e0d\u662f\u6545\u610f\u5982\u6b64\uff0c\u800c\u662f\u56e0\u4e3a\u5236\u4f5c\u8fd9\u79cd\u9b54\u6cd5\u7269\u54c1\uff0c\u975e\u5e38\u8017\u8d39\u7cbe\u529b\uff0c\u4eca\u5929\u80fd\u505a\u6210\u8fd9\u4e00\u53ea\u5df2\u7ecf\u4e0d\u9519\u4e86\u3002\u8981\u60f3\u518d\u505a\u4e00\u53ea\uff0c\u81f3\u5c11\u4e5f\u5f97\u5148\u4f11\u606f\u4e0a\u5341\u5929\u534a\u6708\uff0c\u90a3\u5c31\u672a\u514d\u592a\u665a\u4e86\u70b9\u3002</p><p>\u73ca\u5609\u5fae\u5fae\u4e00\u7b11\uff0c\u4e5f\u4e0d\u518d\u591a\u95ee\uff0c\u4f4e\u5934\u4ed4\u7ec6\u7aef\u8be6\uff0c\u201c\u5e72\u561b\u505a\u6210\u8718\u86db\u554a\uff0c\u201d\u5979\u6709\u4e9b\u5947\u602a\uff0c\u201c\u4f60\u559c\u6b22\u8718\u86db\uff1f\u201d</p><p>\u743c\u6069\u5e76\u4e0d\u559c\u6b22\u8718\u86db\uff0c\u73ca\u5609\u4e5f\u4e0d\u559c\u6b22\u2014\u2014\u4e0d\u8fc7\uff0c\u5728\u7a7f\u8d8a\u4e4b\u524d\uff0c\u5728\u4e0a\u8f88\u5b50\uff0c\u743c\u6069\u7684\u67d0\u4f4d\u5973\u53cb\uff0c\u5979\u4e5f\u559c\u6b22\u6234\u8033\u9489\uff0c\u6700\u559c\u6b22\u7684\u4e00\u526f\uff0c\u5c31\u662f\u8fd9\u6837\u7684\u3002</p><p>\u51ed\u7740\u56de\u5fc6\uff0c\u743c\u6069\u505a\u51fa\u4e86\u8fd9\u5e45\u8033\u9489\u3002</p><p>\u73ca\u5609\u6ca1\u6709\u518d\u95ee\uff0c\u5979\u603b\u662f\u8fd9\u6837\uff0c\u5b89\u9759\uff0c\u806a\u660e\uff0c\u4e56\u5de7\uff0c\u4e0d\u4f1a\u8bf4\u4efb\u4f55\u591a\u4f59\u7684\u5e9f\u8bdd\uff0c\u95ee\u4efb\u4f55\u6ca1\u5fc5\u8981\u95ee\u7684\u95ee\u9898\u3002</p><p>\u201c\u6211\u5728\u5b83\u4e0a\u9762\u9644\u4e86\u4e00\u4e2a\u5c0f\u9b54\u6cd5\uff0c\u201d\u743c\u6069\u89e3\u91ca\uff0c\u201c\u59d0\u59d0\u53ea\u8981\u7ecf\u5e38\u6234\u7740\u5b83\uff0c\u8eab\u4e0a\u5c31\u4f1a\u6696\u6696\u7684\uff0c\u665a\u4e0a\u7761\u7740\u5c31\u4e0d\u4f1a\u51b7\u5f97\u7761\u4e0d\u7740\u4e86\u3002\u201d</p><p>\u201c\u771f\u7684\u554a\uff0c\u201d\u73ca\u5609\u5f88\u9ad8\u5174\uff0c\u201c\u6211\u542c\u8bf4\u80fd\u5236\u4f5c\u9b54\u6cd5\u7269\u54c1\uff0c\u5c31\u5df2\u7ecf\u53ef\u4ee5\u7b97\u662f\u771f\u6b63\u7684\u5deb\u5e08\u4e86\u5462\u3002\u201d</p><p>\u743c\u6069\u5fae\u7b11\u3002\u786e\u5b9e\uff0c\u73ca\u5609\u8bf4\u5f97\u6ca1\u9519\uff0c\u4e0d\u8fc7\u5176\u5b9e\u8fd9\u8033\u9489\u53ea\u662f\u4e00\u4e2a\u51c6\u9b54\u6cd5\u7269\u54c1\u2014\u2014\u4e5f\u5c31\u662f\u8bf4\uff0c\u53ea\u5728\u9634\u9b42\u57ce\u5185\u6709\u6548\u3002\u4e00\u65e6\u8d70\u51fa\u9634\u9b42\u57ce\uff0c\u8d70\u51fa\u4e00\u5b9a\u7684\u8303\u56f4\uff0c\u8fd9\u8033\u9489\u5c31\u4e27\u5931\u4e86\u6240\u6709\u9b54\u529b\u3002\u4ee5\u743c\u6069\u76ee\u524d\u7684\u80fd\u529b\uff0c\u8fd8\u4e0d\u8db3\u4ee5\u5236\u9020\u51fa\u771f\u6b63\u7684\u9b54\u6cd5\u7269\u54c1\u6765\uff0c\u53ea\u80fd\u505a\u8fd9\u79cd\u66ff\u4ee3\u54c1\u3002</p><p>\u8981\u5bf9\u59d0\u59d0\u8bf4\u8fd9\u4e2a\u4e48\u2026\u2026\u7b97\u4e86\uff0c\u53cd\u6b63\u59d0\u59d0\u57fa\u672c\u4e5f\u4e0d\u4f1a\u51fa\u9634\u9b42\u57ce\u3002</p><p>\u743c\u6069\u7565\u7565\u72b9\u8c6b\u4e86\u4e00\u4e0b\uff0c\u51b3\u5b9a\u4e0d\u591a\u52a0\u89e3\u91ca\u3002\u6216\u8bb8\u662f\u865a\u8363\u5fc3\u4f5c\u602a\u5427\uff0c\u8ba9\u59d0\u59d0\u89c9\u5f97\u81ea\u5df1\u5df2\u7ecf\u6709\u6b63\u5f0f\u5deb\u5e08\u7684\u6c34\u51c6\uff0c\u4e0d\u4e5f\u5f88\u597d\u4e48\u3002</p><p>\u201c\u5e2e\u6211\u6234\u4e0a\u5427\u3002\u201d\u73ca\u5609\u8bf4\uff0c\u5ae3\u7136\u5fae\u7b11\u3002</p><p>\u743c\u6069\u4ece\u73ca\u5609\u7684\u624b\u5fc3\u53d6\u8fc7\u8033\u9489\uff0c\u66ff\u59d0\u59d0\u62c2\u8d77\u5782\u4e0b\u7684\u957f\u53d1\uff0c\u62e2\u5728\u8033\u540e\uff0c\u5c06\u94f6\u8272\u8033\u9489\u5c0f\u5fc3\u5730\u6234\u5728\u5979\u53f3\u8033\u7684\u8033\u5782\u4e0a\u3002</p><p>\u201c\u597d\u770b\u5417\uff1f\u201d\u73ca\u5609\u95ee\uff0c\u8d70\u5230\u955c\u5b50\u524d\u81ea\u6211\u6b23\u8d4f\u3002</p><p>\u743c\u6069\u7528\u529b\u5730\u70b9\u70b9\u5934\u3002</p><p>\u5341\u4e94\u5c81\u7684\u73ca\u5609\uff0c\u5df2\u7ecf\u5b8c\u5168\u4e0d\u662f\u5f53\u5e74\u90a3\u4e2a\u7a1a\u5ae9\u7684\u5c0f\u5973\u5b69\u4e86\u3002\u548c\u53bb\u4e16\u7684\u6bcd\u4eb2\u4e00\u6837\uff0c\u5979\u8eab\u6750\u9ad8\u6311\uff0c\u7ea6\u6709\u4e94\u70b9\u516d\u82f1\u5c3a\uff08\u5f88\u5de7\u5408\uff0c\u8fd9\u4e2a\u4e16\u754c\u7684\u8ba1\u91cf\u5355\u4f4d\u975e\u5e38\u63a5\u8fd1\u5730\u7403\u4e0a\u7684\u82f1\u5236\u5ea6\u91cf\u8861\uff0c\u57fa\u672c\u53ef\u4ee5\u901a\u7528\uff09\uff0c\u5927\u7ea6\u5c31\u662f\u4e00\u7c73\u4e03\u4e8c\uff0c\u53ea\u6bd4\u743c\u6069\u77ee\u534a\u4e2a\u5934\u3002\u5979\u4eca\u5929\u4e0a\u8eab\u7a7f\u4e86\u4ef6\u6de1\u84dd\u8272\u886c\u886b\uff0c\u4e0b\u8eab\u642d\u914d\u6d45\u7070\u8272\u957f\u88e4\uff0c\u8e0f\u4e00\u53cc\u94f6\u767d\u8272\u7684\u957f\u7b52\u5e73\u5e95\u8584\u9774\uff0c\u8d8a\u53d1\u663e\u5f97\u53cc\u817f\u4fee\u957f\u633a\u62d4\uff0c\u81c0\u90e8\u4e30\u7fd8\u3002\u9ed1\u8272\u957f\u53d1\u968f\u610f\u62ab\u6563\uff0c\u7528\u4e00\u6839\u6de1\u9ec4\u8272\u7684\u53d1\u5e26\u677e\u677e\u7cfb\u7740\uff0c\u6d12\u5728\u80a9\u4e0a\uff0c\u4e8e\u82f1\u6c14\u52c3\u52c3\u4e2d\u589e\u6dfb\u4e86\u51e0\u5206\u59a9\u5a9a\u3002</p><p>\u4ece\u743c\u6069\u8fd9\u4e2a\u89d2\u5ea6\uff0c\u53ea\u80fd\u770b\u5230\u73ca\u5609\u7684\u80cc\u9762\uff0c\u770b\u4e0d\u5230\u524d\u9762\u7684\u80f8\u90e8\u2014\u2014\u4e0d\u8fc7\uff0c\u59d0\u59d0\u4e0d\u662f\u6b63\u5728\u7167\u955c\u5b50\u4e48\u3002\u9634\u9b42\u57ce\u7684\u5de5\u827a\u6781\u5176\u53d1\u8fbe\uff0c\u65e9\u65e9\u5c31\u53d1\u660e\u4e86\u5236\u9020\u73bb\u7483\u548c\u9540\u94f6\u7684\u6280\u672f\uff0c\u955c\u5b50\u6548\u679c\u975e\u5e38\u597d\uff0c\u80fd\u6e05\u695a\u5730\u7167\u51fa\u73ca\u5609\u7684\u6b63\u9762\uff0c\u90a3\u9ad8\u8038\u50b2\u4eba\u7684\u80f8\u90e8\uff0c\u8ba9\u743c\u6069\u4e0d\u7531\u81ea\u4e3b\u5730\u54bd\u4e86\u53e3\u53e3\u6c34\u3002</p><p>\u597d\u5927\u2026\u2026\u5927\u6982\u670934D\u5427\u2026\u2026</p><p>\u6b63\u5728\u76ee\u6d4b\u7740\u59d0\u59d0\u7684\u80f8\u56f4\uff0c\u73ca\u5609\u5df2\u7ecf\u7167\u5b8c\u4e86\u955c\u5b50\u3002\u201c\u5c0f\u5f1f\uff0c\u4eca\u5929\u5929\u6c14\u8fd9\u4e48\u597d\uff0c\u6211\u4eec\u51fa\u53bb\u8d70\u8d70\u5427\u3002\u201d\u5979\u63d0\u8bae\u3002</p><p>\u743c\u6069\u81ea\u7136\u6beb\u65e0\u5f02\u8bae\u3002</p><p>\u203b\u203b\u203b</p><p>\u9634\u9b42\u57ce\u5e76\u4e0d\u7b97\u7279\u522b\u5927\uff0c\u800c\u4e14\u6709\u4e00\u5927\u534a\u7684\u533a\u57df\u662f\u743c\u6069\u548c\u73ca\u5609\u8fd9\u79cd\u5e73\u6c11\u767e\u59d3\u6839\u672c\u4e0d\u80fd\u9760\u8fd1\u7684\uff0c\u5305\u62ec\u7687\u5bab\u3001\u8d35\u65cf\u5c45\u4f4f\u533a\u3001\u519b\u961f\u9a7b\u5730\u3001\u5deb\u5e08\u5b66\u6821\u7b49\u7b49\u3002\u81f3\u4e8e\u5e73\u6c11\u3001\u5546\u4eba\u7684\u5c45\u4f4f\u533a\uff0c\u53c8\u5f88\u662f\u62e5\u6324\uff0c\u6240\u4ee5\u8d70\u7740\u8d70\u7740\uff0c\u81ea\u7136\u800c\u7136\u5c31\u5230\u4e86\u96c6\u5e02\u4e0a\u3002</p><p>\u4e00\u822c\u6765\u8bf4\uff0c\u7537\u4eba\u90fd\u4e0d\u559c\u6b22\u966a\u5973\u5b69\u5b50\u901b\u8857\u3002\u4e0d\u8fc7\u966a\u7740\u6f02\u4eae\u59d0\u59d0\uff0c\u90a3\u53c8\u662f\u53e6\u5916\u4e00\u56de\u4e8b\u4e86\u3002</p><p>\u9634\u9b42\u57ce\u6709\u4e13\u95e8\u7684\u5546\u4e1a\u533a\uff0c\u743c\u6069\u5bb6\u7684\u5546\u5e97\u5c31\u5f00\u5728\u90a3\u91cc\u3002\u4efb\u4f55\u4e16\u754c\u7684\u4efb\u4f55\u5730\u65b9\uff0c\u5546\u4e1a\u533a\u5176\u5b9e\u90fd\u5927\u540c\u5c0f\u5f02\uff0c\u4e0d\u8fc7\u662f\u4e00\u4e2a\u53c8\u4e00\u4e2a\u7684\u5e97\u94fa\u7f62\u4e86\uff0c\u91cc\u9762\u6446\u7740\u7433\u7405\u6ee1\u76ee\u7684\u5546\u54c1\uff0c\u5230\u5904\u662f\u62e5\u6324\u7684\u4eba\u7fa4\u3002</p><p>\u73ca\u5609\u5012\u4e5f\u4e0d\u662f\u7279\u522b\u559c\u6b22\u901b\u8857\u4e70\u4e1c\u897f\u7684\u5973\u5b69\uff0c\u66f4\u591a\u662f\u968f\u4fbf\u95f2\u8d70\u7f62\u4e86\uff0c\u4e00\u8def\u95f2\u8c08\uff0c\u8bf4\u8bf4\u5b66\u6821\u91cc\u7684\u60c5\u5f62\u3002\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u743c\u6069\u6f2b\u4e0d\u7ecf\u5fc3\u62ac\u773c\uff0c\u77a5\u89c1\u65c1\u8fb9\u670d\u88c5\u5e97\u91cc\uff0c\u6709\u4e00\u4ef6\u6de1\u9ec4\u8272\u7684\u8fde\u8863\u88d9\uff0c\u6837\u5f0f\u9887\u4e3a\u6f02\u4eae\uff0c\u4e0d\u7531\u5f97\u52a8\u4e86\u5fc3\u3002</p><p>\u201c\u59d0\u59d0\uff0c\u90a3\u4ef6\u8fde\u8863\u88d9\u5f88\u6f02\u4eae\u5462\u3002\u201d</p><p>\u73ca\u5609\u987a\u7740\u743c\u6069\u624b\u6307\u65b9\u5411\u770b\u53bb\uff0c\u5fae\u5fae\u6447\u5934\u3002\u5979\u7d20\u6765\u4e0d\u559c\u6b22\u88d9\u5b50\uff0c\u5927\u7ea6\u4e5f\u662f\u56e0\u4e3a\u8981\u7ecf\u5e38\u5e72\u6d3b\u7684\u7f18\u6545\uff0c\u8bb2\u6c42\u5e72\u51c0\u5229\u843d\uff0c\u8863\u670d\u5927\u591a\u504f\u4e2d\u6027\u5316\uff0c\u8863\u6a71\u91cc\u6700\u591a\u7684\u90fd\u662f\u886c\u886b\u957f\u88e4\uff0c\u5c31\u50cf\u4eca\u5929\u7a7f\u7684\u8fd9\u4e00\u5957\u3002</p><p>\u5979\u8eab\u6750\u4fee\u957f\u633a\u62d4\uff0c\u9762\u90e8\u79c0\u4e3d\uff0c\u8f6e\u5ed3\u9c9c\u660e\uff0c\u518d\u914d\u4e0a\u8fd9\u79cd\u7537\u6027\u5316\u7684\u8863\u670d\uff0c\u663e\u5f97\u975e\u5e38\u82f1\u6b66\u5e05\u6c14\uff0c\u4fbf\u5982\u4fca\u7f8e\u5c11\u5e74\u4e00\u822c\u3002\u743c\u6069\u4e5f\u975e\u5e38\u559c\u6b22\u770b\u5230\u73ca\u5609\u8fd9\u79cd\u6253\u626e\uff0c\u4e0d\u8fc7\uff0c\u5230\u5e95\u662f\u5973\u5b69\u5b50\uff0c\u603b\u8be5\u4e5f\u6709\u67d4\u8f6f\u751c\u5a9a\u7684\u4e00\u9762\u624d\u6bd4\u8f83\u597d\u3002</p><p>\u8eab\u4e3a\u5973\u5b69\u5b50\uff0c\u603b\u8fd8\u662f\u5e94\u8be5\u6709\u51e0\u4ef6\u88d9\u5b50\u7684\uff0c\u5c24\u5176\u662f\u5982\u679c\u6709\u4e00\u53cc\u4fee\u957f\u7f8e\u817f\uff0c\u90a3\u4e0d\u7a7f\u88d9\u5b50\u7b80\u76f4\u5c31\u662f\u6d6a\u8d39\u3002</p><p>\u201c\u59d0\u59d0\uff0c\u6211\u89c9\u5f97\u4f60\u7a7f\u88d9\u5b50\u4e5f\u4f1a\u5f88\u6f02\u4eae\u561b\uff0c\u8bd5\u8bd5\u770b\uff1f\u201d</p><p>\u7981\u4e0d\u4f4f\u743c\u6069\u518d\u4e09\u8981\u6c42\uff0c\u73ca\u5609\u8d70\u8fdb\u5546\u5e97\uff0c\u8ba9\u8001\u677f\u5c06\u90a3\u4ef6\u6de1\u9ec4\u8272\u8fde\u8863\u88d9\u53d6\u4e0b\uff0c\u8d70\u8fdb\u8bd5\u8863\u95f4\u6362\u8863\u670d\u3002\u6700\u7ec8\uff0c\u5728\u63a5\u8fde\u6362\u4e86\u51e0\u4ef6\u4e4b\u540e\uff0c\u73ca\u5609\u9009\u62e9\u4e86\u53e6\u5916\u4e00\u4ef6\u7c89\u7ea2\u8272\u7684\u8fde\u8863\u88d9\uff0c\u4ed8\u4e86\u5e10\uff0c\u548c\u743c\u6069\u8d70\u4e86\u51fa\u6765\u3002</p><p>\u201c\u8fd9\u4e48\u9ad8\u5174\u5e72\u561b\uff1f\u201d\u73ca\u5609\u6709\u4e9b\u5947\u602a\u5730\u95ee\uff0c\u53d1\u73b0\u5f1f\u5f1f\u8138\u4e0a\u4e00\u76f4\u5e26\u7740\u7b11\u610f\u3002</p><p>\u201c\u5514\uff0c\u56e0\u4e3a\u4ee5\u524d\u4ece\u6ca1\u89c1\u8fc7\u59d0\u59d0\u7a7f\u8fd9\u79cd\u8fde\u8863\u88d9\uff0c\u521a\u624d\u770b\u5230\uff0c\u89c9\u5f97\u597d\u6e29\u67d4\u597d\u6f02\u4eae\u7684\u611f\u89c9\uff0c\u6240\u4ee5\u9ad8\u5174\u554a\u3002\u201d</p><p>\u73ca\u5609\u7b11\u4e86\u8d77\u6765\u3002\u201c\u5c0f\u5bb6\u4f19\u3002\u201d</p><p>\u201c\u53ef\u60dc\u6211\u81ea\u5df1\u4e0d\u4f1a\u505a\u8863\u670d\uff0c\u201d\u743c\u6069\u9057\u61be\u5730\u8bf4\uff0c\u201c\u5426\u5219\u6211\u6765\u7ed9\u59d0\u59d0\u505a\u4e00\u5957\u9644\u9b54\u6cd5\u7684\u8fde\u8863\u88d9\uff0c\u8fd9\u6837\u5c31\u66f4\u597d\u4e86\u3002\u201d</p><p>\u73ca\u5609\u867d\u7136\u4e0d\u662f\u5deb\u5e08\uff0c\u4e0d\u66fe\u8fdb\u5deb\u5e08\u5b66\u6821\u5b66\u4e60\uff0c\u4f46\u4e5f\u5f00\u4e86\u8fd9\u4e48\u591a\u5e74\u7684\u6cd5\u672f\u6750\u6599\u5546\u5e97\uff0c\u5e73\u65f6\u4e5f\u542c\u743c\u6069\u8c08\u53ca\uff0c\u5bf9\u9b54\u6cd5\u77e5\u8bc6\u5e76\u4e0d\u5341\u5206\u964c\u751f\uff0c\u4f46\u6d89\u53ca\u66f4\u7ec6\u5fae\u7684\u5730\u65b9\u5c31\u4e0d\u5f88\u4e86\u89e3\u4e86\u3002\u542c\u4ed6\u8fd9\u4e48\u8bf4\uff0c\u4fbf\u4e5f\u968f\u53e3\u95ee\uff1a\u201c\u4e0d\u80fd\u5148\u4e70\u4e00\u5957\u88d9\u5b50\uff0c\u518d\u9644\u9b54\u6cd5\u4e0a\u53bb\u5417\uff1f\u201d</p><p>\u743c\u6069\u6447\u5934\uff0c\u201c\u5236\u4f5c\u9b54\u6cd5\u7269\u54c1\u90fd\u8bb2\u7a76\u4e00\u6c14\u5475\u6210\uff0c\u6700\u597d\u5168\u8fc7\u7a0b\u90fd\u662f\u81ea\u5df1\u4e00\u4eba\u5b8c\u6210\uff0c\u53ef\u4ee5\u4e00\u8fb9\u5236\u4f5c\u4e00\u8fb9\u9644\u9b54\uff0c\u6bd4\u8f83\u5bb9\u6613\u3002\u5982\u679c\u4e70\u4e00\u5957\u73b0\u6210\u7684\u88d9\u5b50\u518d\u9644\u9b54\uff0c\u90a3\u96be\u5ea6\u5c31\u6709\u4e9b\u9ad8\u4e86\uff0c\u6211\u2026\u2026\u8fd8\u505a\u4e0d\u5230\u5462\u3002\u201d</p><p>\u73ca\u5609\u770b\u7740\u743c\u6069\uff0c\u5fae\u7b11\u7740\uff0c\u773c\u795e\u4e2d\u4eff\u4f5b\u5c3d\u662f\u5ba0\u6eba\uff0c\u201c\u6ca1\u5173\u7cfb\uff0c\u201d\u5979\u8f7b\u58f0\u8bf4\uff0c\u201c\u4f60\u80af\u5b9a\u4e0d\u7528\u591a\u4e45\u5c31\u80fd\u505a\u5230\u7684\uff0c\u59d0\u59d0\u7b49\u7740\u4f60\u505a\u7684\u88d9\u5b50\u54e6\u3002\u201d</p><p>\u743c\u6069\u9ed8\u9ed8\u70b9\u5934\u3002</p><p>\u4e24\u4eba\u968f\u610f\u8d70\u7740\uff0c\u8f6c\u51fa\u4e86\u5546\u4e1a\u533a\u3002\u201c\u4f60\u4e0a\u6b21\u597d\u50cf\u8bf4\uff0c\u4e94\u5e74\u7ea7\u4f1a\u6709\u5f88\u591a\u5b9e\u6218\u8bad\u7ec3\u7684\u5427\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u8981\u5c0f\u5fc3\u70b9\uff0c\u9b54\u6cd5\u5f88\u5371\u9669\u7684\u3002\u201d</p><p>\u5b9e\u6218\u8bad\u7ec3\uff0c\u81ea\u7136\u4e0d\u4f1a\u4ec5\u4ec5\u662f\u4e00\u4e2a\u4eba\u5bf9\u7740\u5899\u58c1\u8bad\u7ec3\uff0c\u6709\u5f88\u591a\u65f6\u5019\u662f\u540c\u5b66\u4e4b\u95f4\u76f8\u4e92\u8f83\u91cf\uff0c\u4e5f\u501f\u6b64\u8bd5\u63a2\u5bf9\u65b9\u7684\u80fd\u529b\u9ad8\u4f4e\u3002\u4e0d\u8fc7\u73ca\u5609\u7684\u62c5\u5fc3\u662f\u591a\u8651\u4e86\uff0c\u743c\u6069\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u653e\u5fc3\u5566\uff0c\u59d0\u59d0\uff0c\u771f\u5230\u5b9e\u6218\u8bad\u7ec3\u7684\u65f6\u5019\uff0c\u90fd\u662f\u8981\u8fdb\u4e13\u95e8\u7684\u6cd5\u672f\u51b3\u6597\u7ed3\u754c\u7684\uff0c\u9876\u591a\u4f1a\u53d7\u70b9\u4f24\uff0c\u51b3\u4e0d\u4f1a\u771f\u6709\u4ec0\u4e48\u5927\u5371\u9669\u2026\u2026\u201d</p><p>\u8bdd\u6ca1\u8bf4\u5b8c\uff0c\u743c\u6069\u7a81\u7136\u6014\u4f4f\u3002\u73ca\u5609\u6709\u4e9b\u5947\u602a\uff0c\u62ac\u773c\u4e00\u770b\uff0c\u89c1\u4ed6\u6014\u6014\u7ad9\u5728\u539f\u5730\u4e0d\u52a8\uff0c\u82e5\u6709\u6240\u601d\uff0c\u4e0d\u7981\u6709\u4e9b\u5947\u602a\u3002</p><p>\u201c\u600e\u4e48\u4e86\uff1f\u201d</p><p>\u743c\u6069\u6446\u6446\u624b\uff0c\u793a\u610f\u5979\u522b\u8bf4\u8bdd\u3002\u521a\u624d\u88ab\u73ca\u5609\u65e0\u610f\u95f4\u4e00\u8bed\u63d0\u9192\uff0c\u4eff\u4f5b\u52fe\u8d77\u4e86\u5fc3\u5e95\u67d0\u4e2a\u4e00\u76f4\u6f5c\u85cf\u7684\u4e0d\u5b89\u3002</p><p>\u9b54\u6cd5\u2026\u2026\u4f24\u5bb3\u2026\u2026\u62b5\u6297\u9b54\u6cd5\u2026\u2026</p><p>\u539f\u6765\u5982\u6b64\uff0c\u539f\u6765\u5982\u6b64\u2026\u2026</p><p>\u96be\u602a\u6628\u5929\u519b\u4e8b\u6f14\u4e60\u56de\u6765\u7684\u65f6\u5019\uff0c\u519b\u961f\u90a3\u4e09\u4e2a\u4eba\u7528\u90a3\u79cd\u773c\u795e\u770b\u7740\u81ea\u5df1\u2026\u2026\u6839\u672c\u4e0d\u662f\u56e0\u4e3a\u81ea\u5df1\u80fd\u89e6\u6478\u9b54\u7f51\u7b2c\u4e8c\u5c42\u2014\u2014\u8fd9\u6709\u4ec0\u4e48\u503c\u5f97\u5927\u60ca\u5c0f\u602a\u7684\u3002\u771f\u6b63\u7684\u539f\u56e0\uff0c\u662f\u5728\u90a3\u9053\u88ab\u81ea\u5df1\u53cd\u5f39\u56de\u53bb\u7684\u6697\u94f6\u8272\u5149\u675f\u4e0a\u3002</p><p>\u743c\u6069\u53ea\u89c9\u5f97\u80cc\u4e0a\u5fae\u5fae\u6709\u4e9b\u53d1\u51b7\uff0c\u4e0d\u77e5\u662f\u7d27\u5f20\u8fd8\u662f\u62c5\u5fe7\uff0c\u6216\u8005\u662f\u5174\u594b\u3002\u867d\u7136\u8fd8\u6ca1\u6709\u5b8c\u5168\u660e\u767d\u5176\u4e2d\u7684\u7384\u5999\uff0c\u4f46\u9690\u9690\u7ea6\u7ea6\u95f4\uff0c\u4ed6\u5df2\u7ecf\u77e5\u9053\uff0c\u81ea\u5df1\u4f3c\u4e4e\u9677\u5165\u2014\u2014\u6216\u8005\u5f88\u5feb\u5c31\u8981\u9677\u5165\u6f29\u6da1\u7684\u4e2d\u5fc3\u4e86\u3002</p></article>"}}}]);
