(window.webpackJsonp=window.webpackJsonp||[]).push([[212],{235:function(p){p.exports={chapterTitle:null,title:"\u7b2c212\u7ae0\u6fc0\u8fdb\u4e0e\u5192\u9669",content:"<article><h2>\u7b2c212\u7ae0\u6fc0\u8fdb\u4e0e\u5192\u9669</h2><p>\u3000\u3000\u8981\u8bf4\u51f6\u9669\uff0c\u5f20\u606a\u6240\u7ecf\u624b\u7684\u51e0\u4ef6\u4e8b\uff0c\u54ea\u6b21\u4e0d\u51f6\u9669\uff1f\u53e3\u888b\u91cc\u751a\u81f3\u6ca1\u6709\u51e0\u767e\u5143\u94b1\uff0c\u5c31\u5c06\u7231\u7acb\u4fe1\u5728\u6d77\u5dde\u5730\u533a\u7684\u4ee3\u7406\u6743\u4ece\u76db\u946b\u624b\u91cc\u9a97\u8fc7\u6765\uff0c\u6bcf\u63d0\u8d77\u8fd9\u4ef6\u4e8b\uff0c\u53f6\u5c0f\u6850\u5c31\u6094\u6068\u4ea4\u52a0\uff0c\u5979\u5f53\u65f6\u4ee3\u8868\u76db\u946b\u516c\u53f8\u6765\u6d77\u5dde\u8003\u5bdf\uff0c\u5f53\u65f6\u8fd8\u662f\u6d77\u88d5\u516c\u53f8\u6d77\u5dde\u529e\u4e8b\u5904\uff0c\u5728\u4e00\u6ca1\u8d44\u91d1\u3001\u4e8c\u6ca1\u6e20\u9053\u7684\u60c5\u51b5\u4e0b\u5c31\u5c06\u4ee3\u7406\u6743\u6388\u7ed9\u4ed6\u4eec\uff0c\u62ff\u5979\u81ea\u5df1\u7684\u8bf4\u6cd5\uff1a\u201c\u8c01\u77e5\u9053\u4ed6\u662f\u4e2a\u5c0f\u5c41\u5b69\u554a\uff0c\u76f4\u63a5\u7ed9\u4ed6\u90a3\u5957\u8bc8\u4e4e\u4eba\u7684\u5e02\u573a\u7406\u8bba\u8bf4\u8499\u4e86\u3002\u201d</p><p>\u3000\u3000\u7b79\u5efa\u9526\u6e56\u3001\u8d2a\u5a6a\u7684\u60f3\u8981\u5c06\u6574\u4e2a\u6d77\u5dde\u5e02\u9020\u7eb8\u4ea7\u4e1a\u90fd\u63fd\u5165\u81ea\u5df1\u7684\u6000\u91cc\u65f6\uff0c\u9526\u6e56\u80fd\u8c03\u7528\u7684\u8d44\u91d1\u624d\u4e00\u767e\u591a\u4e07\uff0c\u64e6\u7740\u653f\u7b56\u7684\u8fb9\u7f18\uff0c\u65e0\u6240\u4e0d\u7528\u5176\u6781\uff0c\u5751\u8499\u62d0\u9a97\u7684\u6123\u662f\u5c06\u65b0\u5149\u9020\u7eb8\u5382\u7684\u8d44\u4ea7\u76d8\u6d3b\uff0c\u8fd9\u624d\u8ba9\u9526\u6e56\u6709\u4e86\u4e00\u5b9a\u7684\u57fa\u7840\u3002</p><p>\u3000\u3000\u867d\u7136\u524d\u671f\u5411\u7231\u8fbe\u6295\u5165\u8fd1\u4e94\u767e\u4e07\u7684\u8d44\u91d1\uff0c\u4f46\u5728\u7231\u8fbe\u6295\u4ea7\u524d\uff0c\u4ec5\u6709\u7684\u8d44\u91d1\u8fd8\u662f\u57ce\u5357\u533a\u59d4\u5f3a\u529b\u652f\u6301\u4e0b\u4ece\u94f6\u884c\u8d37\u51fa\u7684\u4e94\u767e\u4e07\u6b3e\u5b50\uff0c\u8fd8\u8ba9\u5f20\u606a\u63b0\u6210\u51e0\u5904\u82b1\uff0c\u77ed\u77ed\u4e24\u4e2a\u6708\u7684\u65f6\u95f4\uff0c\u7231\u8fbe\u7684\u8d44\u4ea7\u89c4\u6a21\u5c31\u7206\u70b8\u4f3c\u7684\u7a81\u7834\u4e94\u5343\u4e07\uff0c\u4f46\u662f\u8d1f\u503a\u7387\u8fbe\u5230\u60ca\u4eba\u768490\uff05\uff0c\u6362\u4f5c\u5176\u4ed6\u4f01\u4e1a\uff0c\u65e9\u5c31\u9677\u5165\u5c8c\u5c8c\u53ef\u5371\u7684\u5883\u5730\uff0c\u504f\u504f\u6b64\u65f6\u7684\u7231\u8fbe\u6b23\u6b23\u5411\u8363\u3002</p><p>\u3000\u3000\u8c22\u665a\u6674\u624b\u6307\u9876\u7740\u4e0b\u9894\uff0c\u60f3\u4e0d\u901a\u5f20\u606a\u7adf\u6709\u8fd9\u79cd\u9b54\u529b\uff0c\u8fd0\u4f5c\u8d44\u672c\u7684\u624b\u8155\u9c9c\u6709\u4eba\u80fd\u4e0e\u4ed6\u76f8\u63d0\u5e76\u8bba\uff0c\u867d\u7136\u7231\u8fbe\u8d44\u91d1\u88e2\u7684\u8106\u5f31\u7a0b\u5ea6\u8ba9\u4eba\u60ca\u5fc3\uff0c\u5374\u8ba9\u4eba\u5bf9\u5f20\u606a\u7684\u8868\u73b0\u6709\u7740\u66f4\u5927\u7684\u671f\u5f85\u3002</p><p>\u3000\u3000\u201c\u5728\u60f3\u4ec0\u4e48\uff1f\u201d\u5f20\u606a\u5750\u5728\u665a\u6674\u7684\u5bf9\u9762\uff0c\u770b\u7740\u5979\u62bf\u7740\u5634\u5728\u7b11\uff0c\u660e\u660e\u521a\u624d\u5728\u8bf4\u5f88\u62c5\u5fc3\u7231\u8fbe\u8d44\u91d1\u88e2\u7684\u73b0\u72b6\uff0c\u82e5\u6709\u6240\u601d\u7684\u5374\u7b11\u4e86\u8d77\u6765\u3002</p><p>\u3000\u3000\u201c\u6ca1\u60f3\u4ec0\u4e48\u3002\u201d\u8c22\u665a\u6674\u6b63\u8fc7\u8eab\u5b50\uff0c\u60f3\u8981\u8212\u5c55\u8eab\u4f53\uff0c\u53c8\u6015\u5728\u5f20\u606a\u9762\u524d\u592a\u653e\u8086\u3002\u80f8\u90e8\u4e30\u633a\uff0c\u4e00\u505a\u6269\u80f8\u8fd0\u52a8\u5c31\u683c\u5916\u7684\u663e\u773c\uff0c\u6362\u4f5c\u4ed6\u65f6\uff0c\u4f1a\u5f88\u968f\u610f\u7684\u4f38\u5c55\u4e00\u4e0b\u8eab\u4f53\uff0c\u6bd5\u7adf\u957f\u65f6\u95f4\u5750\u529e\u516c\u5ba4\u91cc\uff0c\u4e0d\u5229\u4e8e\u8840\u6db2\u5faa\u73af\uff0c\u53ea\u662f\u5fc3\u91cc\u7740\u4e86\u75d5\u8ff9\uff0c\u4e8b\u4e8b\u5c31\u53d7\u7ea6\u675f\uff0c\u5e73\u65f6\u5f88\u968f\u610f\u7684\u52a8\u4f5c\uff0c\u8bf4\u4e0d\u5b9a\u5728\u522b\u4eba\u773c\u5e95\u4f1a\u6709\u5176\u4ed6\u7684\u610f\u5473\u3002\u665a\u6674\u5e0c\u671b\u5f20\u606a\u4e0d\u8981\u628a\u6628\u5929\u7684\u4e8b\u60c5\u7559\u5728\u5fc3\u91cc\uff0c\u4e0d\u7136\u591a\u96be\u582a\u554a\uff01\u4ed4\u7ec6\u6ce8\u89c6\u5f20\u606a\uff0c\u7adf\u4e1d\u6beb\u770b\u4e0d\u51fa\u5341\u4e03\u5c81\u5c11\u5e74\u7684\u7a1a\u6c14\u75d5\u8ff9\uff0c\u773c\u795e\u7565\u6709\u4e9b\u6ca7\u6851\uff0c\u7709\u5f13\u3001\u9ad8\u9686\u5e73\u76f4\u7684\u9f3b\u6881\u65f6\u4e0d\u65f6\u8f7b\u76b1\u7740\uff0c\u7ed9\u4eba\u575a\u6bc5\u679c\u51b3\u7684\u611f\u89c9\uff0c\u8138\u90e8\u7ebf\u6761\u660e\u4fca\u800c\u67d4\u548c\uff0c\u5507\u4e0a\u3001\u4e0b\u9894\u7684\u80e1\u987b\u4e5f\u5bc6\uff0c\u522e\u5f97\u8fd4\u9752\uff0c\u5589\u7ed3\u7c97\u5927\uff0c\u5403\u996d\u65f6\u6216\u8bb8\u559d\u6c34\u4e0a\u4e0b\u6eda\u52a8\uff0c\u8ba9\u665a\u6674\u5fcd\u4e0d\u4f4f\u8981\u6478\u81ea\u5df1\u7684\u54bd\u5589\u90e8\uff0c\u65e0\u8bba\u600e\u4e48\u8bf4\uff0c\u5f20\u606a\u90fd\u5341\u5206\u7684\u82f1\u4fca\uff0c\u8fd8\u5f88\u8ff7\u4eba\uff0c\u4f46\u540e\u9762\u8fd9\u4e2a\u8bcd\u95ea\u5165\u8111\u5b50\uff0c\u5374\u8ba9\u665a\u6674\u6709\u4e9b\u96be\u582a\u4e86\u3002</p><p>\u3000\u3000\u665a\u6674\u6709\u4e9b\u4e0d\u81ea\u7136\u7684\u53cc\u817f\u4ea4\u53e0\uff0c\u8f7b\u8f7b\u8bf4\u9053\uff1a\u201c\u8fd8\u6709\u4ec0\u4e48\u4e8b\u60c5\u8981\u8bf4\uff1f\u201d</p><p>\u3000\u3000\u201c\u6ca1\u6709\u4e86\u3002\u201d\u5f20\u606a\u624b\u653e\u5728\u819d\u76d6\uff0c\u201c\u6211\u8fd8\u4ee5\u4e3a\u665a\u6674\u6709\u4ec0\u4e48\u4e8b\u60c5\u8981\u8bf4\u3002\u201d</p><p>\u3000\u3000\u201c\u554a\uff0c\u6211\u6ca1\u6709\u7279\u522b\u7684\u4e8b\u60c5\u8981\u8bf4\uff0c\u4f60\u6709\u4e8b\u5148\u53bb\u5fd9\u5427\u3002\u201d\u5c06\u5f20\u606a\u8d76\u51fa\u529e\u516c\u5ba4\uff0c\u665a\u6674\u62ff\u8d77\u6c34\u676f\u5927\u53e3\u559d\u7740\u51c9\u8336\u6c34\uff0c\u53ea\u662f\u559d\u6c34\u7684\u58f0\u97f3\u672a\u514d\u592a\u5927\u4e86\u4e00\u4e9b\uff0c\u8fde\u665a\u6674\u81ea\u5df1\u4e5f\u5413\u4e86\u4e00\u8df3\uff0c\u5185\u5fc3\u6df1\u5904\u9690\u9690\u9690\u7ea6\u7ea6\u7684\u6709\u4e9b\u8e81\u70ed\uff0c\u4e0d\u7531\u7684\u6697\u81ea\u601d\u91cf\uff1a\u6211\u8fd9\u662f\u600e\u4e48\u4e86\uff1f</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u6cbf\u7740\u8c61\u5c71\u4e1c\u9e93\u7684\u4e16\u7eaa\u5927\u9053\u5f80\u5357\uff0c\u516c\u8def\u7684\u4e24\u8fb9\u8fd8\u662f\u5927\u7247\u7684\u519c\u7530\uff0c\u7231\u8fbe\u6269\u4ea7\u7684\u9996\u6279\u5de5\u4e1a\u7528\u5730\u5c31\u9009\u5728\u76ca\u9686\u5357\u4e1c\u9996\u7684\u5c0f\u6c5f\u6c5f\u7554\u3002\u4e3a\u4e86\u907f\u514d\u653f\u7b56\u6027\u969c\u788d\uff0c\u7231\u8fbe\u65b0\u5382\u89c4\u6a21\u63a7\u5236\u5728\u516b\u5341\u4ea9\uff0c\u800c\u7531\u57ce\u5357\u533a\u653f\u5e9c\u7b79\u5907\u7535\u5b50\u4ea7\u4e1a\u56ed\u533a\uff0c\u7ed9\u7231\u8fbe\u9884\u7559\u4e00\u5b9a\u91cf\u7684\u5de5\u4e1a\u53d1\u5c55\u7528\u5730\u3002</p><p>\u3000\u3000\u4e00\u8f86\u5e15\u8428\u7279\u8f7f\u8f66\u98de\u5feb\u7684\u9a76\u51fa\u8c61\u5c71\u4e1c\u9e93\u7684\u6797\u5730\uff0c\u62b5\u8fbe\u5230\u5c0f\u6c5f\u6c5f\u7554\uff0c\u6070\u597d\u6709\u4e24\u8f86\u5c0f\u8f66\u8d76\u8fc7\u6765\u6c47\u5408\uff0c\u90fd\u505c\u5728\u6c5f\u5824\u516c\u8def\u4e0a\u3002</p><p>\u3000\u3000\u770b\u89c1\u5b8b\u57f9\u660e\u3001\u82cf\u6d25\u4e1c\u90fd\u5206\u522b\u94bb\u51fa\u8f7f\u8f66\uff0c\u5f20\u606a\u624d\u8fdf\u8fdf\u7684\u4e0b\u4e86\u8f66\uff0c\u6c5f\u5824\u516c\u8def\u4e24\u4fa7\u79cd\u690d\u7740\u4e09\u89d2\u67ab\uff0c\u6811\u836b\u4e0b\uff0c\u6c5f\u98ce\u5439\u6765\uff0c\u7adf\u662f\u5341\u5206\u6e05\u51c9\u3002</p><p>\u3000\u3000\u201c\u7231\u8fbe\u5728\u8fd9\u91cc\u5efa\u6210\u5d2d\u65b0\u7684\u73b0\u4ee3\u5316\u5de5\u5382\u624d\u7b97\u771f\u6b63\u7684\u8d77\u6b65\u2026\u2026\u201d\u8bf4\u8d77\u6765\u7231\u8fbe\u7535\u5b50\u672a\u6765\u7684\u89c4\u5212\uff0c\u5b8b\u57f9\u660e\u4e5f\u662f\u5145\u6ee1\u4e86\u6fc0\u60c5\uff0c\u201c\u53ea\u8981\u534a\u5e74\u7684\u65f6\u95f4\uff0c\u4eba\u4eec\u5c31\u4f1a\u770b\u5230\u8fd9\u91cc\u5927\u4e0d\u540c\u3002\u201d</p><p>\u3000\u3000\u201c\u5b8b\u53d4\u597d\u50cf\u6bd4\u6211\u4eec\u8fd8\u6562\u4e8e\u60f3\u8c61\u672a\u6765\uff1f\u201d\u5f20\u606a\u563f\u7136\u4e00\u7b11\uff0c\u89c9\u5f97\u7ad9\u5728\u90a3\u91cc\u6307\u70b9\u6c5f\u5c71\u6709\u4e9b\u7d2f\u4eba\uff0c\u53c8\u6ca1\u6709\u5916\u4eba\uff0c\u4e0d\u987e\u5f62\u8c61\u7684\u63d0\u7740\u88e4\u817f\u8e72\u4e86\u4e0b\u6765\uff0c\u671b\u7740\u6c5f\u5824\u4e0b\u7cbc\u7cbc\u7684\u6c34\u9762\uff0c\u4e0b\u9762\u7684\u6c5f\u6ee9\u4e0a\u8fd8\u505c\u7740\u51e0\u8f86\u81ea\u884c\u8f66\uff0c\u6709\u4eba\u5728\u6c34\u91cc\u6e38\u6cf3\u3002</p><p>\u3000\u3000\u7701\u5185\u5df2\u7ecf\u627e\u4e0d\u5230\u51e0\u6761\u672a\u53d7\u6c61\u67d3\u7684\u6c34\u7cfb\u4e86\uff0c\u5c0f\u6c5f\u4e0a\u6e38\uff0c\u7279\u522b\u662f\u5b89\u5fbd\u7701\u5185\u7684\u690d\u88ab\u8986\u76d6\u8fd1\u5e74\u6765\u4e0b\u964d\u5f97\u5389\u5bb3\uff0c\u6c5f\u6c34\u6bd4\u5f80\u5e74\u6d51\u6d4a\u591a\u4e86\uff0c\u4f46\u662f\u76f8\u6bd4\u5176\u4ed6\u6cb3\u6d41\uff0c\u5df2\u7ecf\u597d\u5f88\u591a\u4e86\u3002</p><p>\u3000\u3000\u201c\u5b8b\u53d4\uff0c\u8981\u4e0d\u8981\u4e0b\u53bb\u907f\u907f\u6691\uff0c\u518d\u8c08\u6b63\u4e8b\uff1f\u201d\u770b\u5230\u6709\u4eba\u5728\u6c5f\u6c34\u91cc\u6e38\u6cf3\uff0c\u5f20\u606a\u6709\u4e9b\u5fc3\u52a8\uff0c\u5341\u5206\u8ba4\u771f\u7684\u5411\u5b8b\u57f9\u660e\u5efa\u8bae\u3002</p><p>\u3000\u3000\u5b8b\u57f9\u660e\u656c\u8c22\u4e0d\u654f\u7684\u6447\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u529e\u516c\u65f6\u95f4\u6ca1\u6709\u966a\u4eba\u6e38\u6cf3\u4e00\u6761\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6d77\u5dde\u5e02\u5c11\u6709\u4eba\u4e0d\u4f1a\u6c34\u7684\uff0c\u5b8b\u53d4\u4e0d\u4f1a\u6015\u6df9\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u4ece\u5c0f\u5728\u5317\u65b9\u957f\u5927\uff0c\u4e0d\u4f1a\u6c34\u53c8\u6709\u4ec0\u4e48\u7a00\u5947\u7684\uff0c\u201d\u5b8b\u57f9\u660e\u6487\u5634\u7b11\u4e86\u7b11\uff0c\u201c\u533a\u91cc\u8981\u5728\u8fd9\u91cc\u7b79\u5907\u7535\u5b50\u4ea7\u4e1a\u56ed\u533a\uff0c\u5fc5\u987b\u8981\u6709\u5b8c\u6574\u7684\u89c4\u5212\u624d\u884c\uff0c\u7231\u8fbe\u6709\u4eca\u540e\u7684\u5177\u4f53\u89c4\u5212\u6ca1\u6709\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e66\u9762\u7684\u4e1c\u897f\u8fd8\u6ca1\u6709\uff0c\u6ca1\u65f6\u95f4\u3001\u6ca1\u4eba\u641e\u8fd9\u4e2a\u4e1c\u897f\uff0c\u4e0d\u8fc7\u6211\u8fd9\u91cc\u6709\u4e9b\u4e1c\u897f\uff0c\u201d\u5f20\u606a\u6307\u4e86\u6307\u81ea\u5df1\u7684\u8111\u888b\uff0c\u201c\u5b8b\u53d4\u4e0d\u6015\u592a\u8be6\u7ec6\u4e86\uff0c\u5e02\u91cc\u4f1a\u5c06\u9879\u76ee\u62a2\u8fc7\u53bb\uff1f\u6211\u53ef\u8fde\u6211\u5bb6\u8001\u5b50\u90fd\u6ca1\u6709\u7ec6\u8bf4\u554a\u3002\u201d</p><p>\u3000\u3000\u201c\u8c01\u6765\u62a2\uff0c\u6211\u5c31\u8ddf\u8c01\u7ffb\u8138\uff0c\u201d\u5b8b\u57f9\u660e\u563f\u7136\u4e00\u7b11\uff0c\u63fd\u8fc7\u5f20\u606a\u7684\u80a9\u8180\uff0c\u201c\u4f60\u5c0f\u5b50\u8981\u8bb0\u4f4f\u8c01\u5f53\u521d\u62ff\u7740\u4e4c\u7eb1\u5e3d\u5230\u94f6\u884c\u62b5\u62bc\u5e2e\u4f60\u641e\u6765\u8d37\u6b3e\u7684\uff0c\u4e0d\u4f1a\u8fd9\u4e48\u5feb\u8fc7\u6cb3\u62c6\u6865\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u8c01\u77e5\u9053\uff1f\u201d\u5f20\u606a\u644a\u644a\u624b\uff0c\u201c\u4e5f\u4e0d\u662f\u6211\u60f3\u8fc7\u6cb3\u62c6\u6865\uff0c\u5f88\u591a\u65f6\u5019\u5927\u5bb6\u90fd\u8eab\u4e0d\u7531\u5df2\u3002\u8981\u4e0d\u8fd9\u6837\u5427\uff0c\u6211\u4eec\u8fd9\u6b21\u6536\u8d2d\u65e0\u7ebf\u7535\u5143\u4ef6\u5668\u4e8c\u5382\u5168\u90e8\u7684\u8d44\u4ea7\uff0c\u6536\u8d2d\u4e4b\u540e\uff0c\u80fd\u4e0d\u80fd\u5c06\u8fd9\u90e8\u5206\u8d44\u4ea7\u62ff\u5230\u94f6\u884c\u62b5\u62bc\u8d37\u6b3e\uff1f\u201d</p><p>\u3000\u3000\u5b8b\u57f9\u660e\u51f6\u76f8\u6bd5\u9732\uff1a\u201c\u4f60\u8fd9\u662f\u8981\u631f\uff01\u201d</p><p>\u3000\u3000\u201c\u4e5f\u7b97\u4e0d\u4e0a\uff0c\u6211\u5f97\u6709\u7406\u7531\u62d2\u7edd\u522b\u4eba\u7684\u8bf1\u60d1\u624d\u884c\u3002\u201d\u5f20\u606a\u5b09\u76ae\u7b11\u8138\u7684\u8bf4\uff0c\u5411\u94f6\u884c\u501f\u8d37\u6240\u5f62\u6210\u7684\u8d44\u91d1\u88e2\uff0c\u53ea\u8981\u53ca\u65f6\u652f\u4ed8\u5229\u606f\uff0c\u8981\u6bd4\u4ece\u7ecf\u9500\u5546\u90a3\u91cc\u62bd\u53d6\u8d44\u91d1\u8981\u7a33\u5b9a\u5f97\u5f88\uff0c\u6709\u53ef\u80fd\u7684\u8bdd\uff0c\u8fd8\u662f\u8981\u8fdb\u4e00\u6b65\u6539\u5584\u8d44\u91d1\u88e2\u7684\u7ed3\u6784\u3002</p><p>\u3000\u3000\u94f6\u6839\u7d27\u7f29\u5468\u671f\uff0c\u79c1\u8425\u4f01\u4e1a\u7684\u501f\u8d37\u5f88\u96be\u901a\u8fc7\u94f6\u884c\u5ba1\u6279\uff0c\u4f46\u662f\u5404\u7ea7\u653f\u5e9c\u90fd\u6709\u4e00\u5b9a\u91cf\u7684\u6307\u6807\uff0c\u5f20\u606a\u8fd9\u662f\u7ee7\u7eed\u6316\u6398\u5b8b\u57f9\u660e\u7684\u6f5c\u80fd\u3002</p><p>\u3000\u3000\u5b8b\u57f9\u660e\u82e6\u7b11\u7740\u8bf4\uff1a\u201c\u533a\u91cc\u8fd8\u8981\u4e0d\u8981\u6276\u6301\u5176\u4ed6\u4f01\u4e1a\u4e86\uff1f\u4f60\u4eec\u8fd9\u6b21\u76f4\u63a5\u6536\u8d2d\u7684\u4ee3\u4ef7\u592a\u5927\uff0c\u65e0\u7ebf\u4e8c\u5382\u7684\u89c4\u6a21\uff0c\u4f60\u4eec\u5728\u8fd9\u91cc\u62e9\u5730\u65b0\u5efa\uff0c\u4e0d\u9700\u8981\u4e00\u5343\u4e07\uff0c\u4f60\u4eec\u5374\u4e00\u4e0b\u5b50\u638f\u51fa\u4e24\u5343\u591a\u4e07\uff0c\u5c31\u7b97\u62ff\u53bb\u94f6\u884c\u62b5\u62bc\u8d37\u6b3e\uff0c\u96be\u9053\u80fd\u8d37\u8db3\u4e24\u5343\u591a\u4e07\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u76f4\u63a5\u5e76\u8d2d\u662f\u4f01\u4e1a\u6269\u5f20\u6700\u76f4\u622a\u4e86\u5f53\u7684\u65b9\u6cd5\uff0c\u9664\u4e86\u53ef\u8ba1\u7b97\u7684\u6709\u5f62\u8d44\u4ea7\uff0c\u4e1a\u52a1\u8d44\u6e90\u3001\u5e02\u573a\u4efd\u989d\u4e5f\u5c31\u7b97\u4e86\uff0c\u4f46\u662f\u6280\u672f\u8d2e\u5907\u4ee5\u53ca\u5927\u91cf\u7684\u719f\u7ec3\u5de5\u4eba\uff0c\u90fd\u662f\u7231\u8fbe\u76ee\u524d\u6240\u6025\u7f3a\u7684\u3002\u76ee\u524d\u76ca\u9686\u9547\u7684\u5382\u5b50\u65b0\u5382\u623f\u5efa\u6210\u6295\u5165\u4f7f\u7528\uff0c\u603b\u88c5\u7ebf\u6682\u65f6\u6ca1\u6709\u4ec0\u4e48\u95ee\u9898\uff0c\u6574\u4f53\u4ea7\u80fd\u53d7\u5236\u4e8e\u7ebf\u8def\u677f\u7684\u751f\u4ea7\u65e0\u6cd5\u77ed\u671f\u5185\u63d0\u9ad8\uff0c\u5230\u4e0b\u4e2a\u6708\uff0c\u7231\u8fbe\u7535\u5b50\u7684\u4ea7\u80fd\u5c31\u53ef\u80fd\u8ddf\u4e0d\u4e0a\u5e02\u573a\u9700\u6c42\uff1b\u4e00\u65e6\u5c06\u65e0\u7ebf\u7535\u4e8c\u5382\u6536\u8d2d\u5230\u624b\u91cc\uff0c\u63a7\u5236\u7cfb\u7edf\u7684\u7ebf\u8def\u677f\u4e0e\u7535\u8def\u7cfb\u7edf\uff0c\u5c06\u4f1a\u7531\u4e13\u95e8\u7684\u5de5\u5382\u7ec4\u7ec7\u751f\u4ea7\uff0c\u800c\u76ca\u9686\u9547\u7684\u5382\u5b50\u52a0\u4e0a\u540e\u6765\u6269\u5efa\u7684\u5382\u623f\u5219\u5168\u90e8\u6539\u4e3a\u603b\u88c5\u751f\u4ea7\u7ebf\uff0c\u81f3\u5c11\u7b49\u8fd9\u91cc\u7684\u5382\u623f\u5efa\u6210\u4e4b\u524d\uff0c\u4e0d\u7528\u62c5\u5fc3\u4ea7\u80fd\u7684\u95ee\u9898\u3002\u201d</p><p>\u3000\u3000\u201c\u5509\uff0c\u7231\u8fbe\u7684\u901f\u5ea6\u592a\u8ba9\u4eba\u77a0\u76ee\u7ed3\u820c\u4e86\u3002\u201d\u5b8b\u57f9\u660e\u4e0d\u5f97\u7684\u611f\u6168\u8d77\u6765\uff0c\u201c\u6211\u73b0\u5728\u4e5f\u7b97\u5f71\u789f\u673a\u4ea7\u4e1a\u7684\u534a\u4e2a\u4e13\u5bb6\uff0c\u4e00\u5bb6\u4e24\u4e2a\u6708\u524d\u72d7\u5c41\u90fd\u4e0d\u662f\u5382\u5b50\u7adf\u7136\u60f3\u5728\u4e24\u4e2a\u6708\u5185\u8ddf\u4e09\u661f\u4e89\u593a\u534e\u4e1c\u5730\u533a\u7684\u5e02\u573a\uff0c\u5f53\u7136\u8981\u8ba9\u4eba\u7b11\u6389\u5927\u7259\uff01\u201d</p><p>\u3000\u3000\u201c\u5b8b\u53d4\u7684\u5927\u7259\u7b11\u6389\u6ca1\u6709\uff1f\u201d\u5f20\u606a\u620f\u8c11\u7684\u95ee\u3002</p><p>\u3000\u3000\u5b8b\u57f9\u660e\u6447\u5934\u53f9\u606f\uff0c\u516b\u6708\u4e4b\u524d\uff0c\u5f20\u606a\u7684\u5e02\u573a\u63a8\u5e7f\u65b9\u6848\u5728\u8bb8\u591a\u4eba\u773c\u91cc\u662f\u4e00\u9879\u975e\u5e38\u6fc0\u8fdb\u3001\u5192\u9669\u7684\u65b9\u6848\uff0c\u4e24\u4e2a\u6708\u5185\uff0c\u6708\u9500\u91cf\u7a81\u7834\u516d\u5343\u53f0\uff0c\u5728\u8bb8\u591a\u4eba\u773c\u91cc\uff0c\u6839\u672c\u4e0d\u662f\u53ef\u80fd\u5b8c\u6210\u7684\u4efb\u52a1\u3002\u5728\u7231\u8fbe\u5f71\u789f\u673a\u6b63\u5f0f\u63a8\u5411\u5e02\u573a\u7684\u516d\u6708\u4efd\uff0c\u4e09\u661f\u4f5c\u4e3a\u5f71\u789f\u673a\u7684\u7b2c\u4e00\u54c1\u724c\u5728\u534e\u4e1c\u5730\u533a\u7684\u9500\u91cf\u624d\u4e0d\u8fc7\u4e94\u5343\u53f0\uff0c\u800c\u5728\u5f20\u606a\u7684\u65f6\u95f4\u8868\u91cc\uff0c\u5230\u516b\u6708\u5e95\uff0c\u7231\u8fbe\u5bf9\u534e\u4e1c\u5730\u533a\u7684\u5e02\u573a\u63a8\u5e7f\u751a\u81f3\u53ea\u6765\u5f97\u53ca\u5b8c\u6210\u4e00\u534a\uff0c\u5f20\u606a\u62df\u5b9a\u7684\u76ee\u6807\uff0c\u672a\u514d\u6709\u4e9b\u5f3a\u4eba\u6240\u96be\u3002</p><p>\u3000\u3000\u4f46\u662f\u8fd9\u4e2a\u76ee\u6807\u5374\u8ba9\u5f20\u606a\u786c\u751f\u751f\u7684\u505a\u5230\u4e86\uff0c\u5f20\u606a\u4eb2\u81ea\u63a8\u52a8\u7b2c\u4e00\u8f6e\u7684\u5e02\u573a\u63a8\u5e7f\u5de5\u4f5c\uff0c\u4e03\u6708\u4efd\u7684\u5e02\u573a\u9500\u91cf\u5c31\u7a81\u7834\u4e09\u5343\u53f0\uff0c\u5e02\u573a\u6316\u6398\u6bd4\u8f83\u5145\u5206\u7684\u4e1c\u6d77\u7701\uff0c\u4e00\u7701\u7684\u9500\u91cf\u5c31\u7a81\u7834\u4e24\u5343\u53f0\uff0c\u516b\u6708\u4efd\u7684\u5e02\u573a\u63a8\u5e7f\u91cd\u70b9\u662f\u4e0a\u6d77\u5e02\uff0c\u8fd8\u6ca1\u6709\u5230\u6708\u5e95\uff0c\u4e0a\u6d77\u5e02\u7684\u9500\u91cf\u5df2\u7ecf\u7a81\u7834\u4e00\u5343\u4e94\u767e\u53f0\uff0c\u800c\u4e1c\u6d77\u7701\u7684\u9500\u91cf\u4e5f\u7a33\u5b9a\u5728\u4e24\u5343\u53f0\u4ee5\u4e0a\u3002</p></article>"}}}]);
