(window.webpackJsonp=window.webpackJsonp||[]).push([[217],{239:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u4e8c\u8282 \u6b3a\u8bc8",content:"<article><h2>\u7b2c\u5341\u4e8c\u8282 \u6b3a\u8bc8</h2><p>\u5b9d\u77f3\u8df3\u8dc3\uff08Gemjump\uff09\u4e5f\u662f\u4e00\u79cd\u7a7a\u95f4\u4f20\u9001\u9b54\u6cd5\uff0c\u4f46\u6bd4\u901a\u5e38\u6240\u7528\u7684\u4f20\u9001\u672f\uff08Teleport\uff09\u66f4\u52a0\u4f18\u79c0\u9ad8\u6df1\uff0c\u5b83\u5b9a\u4f4d\u7cbe\u51c6\uff0c\u7a33\u59a5\u5b89\u5168\uff0c\u6700\u91cd\u8981\u7684\u662f\u6ca1\u6709\u8ddd\u79bb\u9650\u5236\u2014\u2014\u4f20\u9001\u672f\u80fd\u591f\u7a7a\u95f4\u8de8\u8d8a\u7684\u8ddd\u79bb\u8fdc\u8fd1\uff0c\u662f\u7531\u5deb\u5e08\u7684\u9020\u8be3\u6240\u51b3\u5b9a\u7684\u3002\u4e3e\u4f8b\u6765\u8bf4\uff0c\u4ece\u9634\u9b42\u57ce\u5230\u585e\u5c14\uff0c\u5176\u95f4\u76f8\u9694\u5343\u91cc\uff0c\u743c\u6069\u5c31\u7b97\u7528\u4f20\u9001\u672f\u5377\u8f74\u4e5f\u65e0\u6cd5\u62b5\u8fbe\uff0c\u53ea\u80fd\u8d70\u57ce\u4e2d\u7684\u6c38\u4e45\u4f20\u9001\u95e8\uff0c\u4f46\u82e5\u662f\u501f\u52a9\u5b9d\u77f3\u8df3\u8dc3\uff0c\u5c31\u7b97\u77ac\u95f4\u8de8\u8d8a\u6574\u4e2a\u8d39\u4f26\u5927\u9646\u4e5f\u662f\u8f7b\u800c\u6613\u4e3e\u3002</p><p>\u73ca\u5609\u62b1\u7740\u743c\u6069\uff0c\u95ed\u7740\u773c\u775b\uff0c\u611f\u89c9\u81ea\u5df1\u7684\u8eab\u4f53\u77ac\u95f4\u5ef6\u5c55\uff0c\u81ea\u7acb\u4f53\u5316\u4e3a\u5e73\u9762\uff0c\u77ac\u95f4\u8ff7\u5931\u4e86\u4e0a\u4e0b\u5de6\u53f3\u7684\u65b9\u4f4d\uff0c\u5982\u661f\u4e38\u5728\u6d69\u701a\u865a\u7a7a\u4e2d\u75be\u901f\u7a7f\u68ad\uff0c\u5979\u5e76\u4e0d\u662f\u7b2c\u4e00\u6b21\u7ecf\u5386\u7a7a\u95f4\u4f20\u9001\u4e86\uff0c\u65e9\u4e0a\u4ece\u9634\u9b42\u57ce\u6765\u827e\u5c14\u5854\u67cf\u8d70\u7684\u5c31\u662f\u4f20\u9001\u95e8\uff0c\u4e5f\u5e76\u4e0d\u591a\u4e48\u7d27\u5f20\uff0c\u53ea\u662f\u9759\u9759\u7b49\u5f85\u7740\u3002\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u604d\u60da\u95f4\u53ea\u89c9\u8eab\u4f53\u5fae\u5fae\u4e00\u9707\uff0c\u5df2\u7ecf\u518d\u5ea6\u8e0f\u4e0a\u5b9e\u5730\uff0c\u5979\u77e5\u9053\u662f\u5230\u4e86\u5730\u65b9\uff0c\u7741\u5f00\u773c\u775b\uff0c\u6b63\u770b\u89c1\u4e00\u8eab\u94f6\u7532\u7684\u6885\u83f2\u65af\u7ad9\u5728\u65c1\u8fb9\uff0c\u9887\u6709\u4e9b\u8be7\u5f02\u5730\u770b\u7740\u81ea\u5df1\u3002</p><p>\u201c\u73ca\u5609\u59d0\u59d0\uff1f\u201d\u6885\u83f2\u65af\u663e\u7136\u6709\u4e9b\u8bb6\u5f02\uff0c\u5979\u539f\u672c\u4ee5\u4e3a\u4f1a\u662f\u743c\u6069\u5355\u8eab\u524d\u6765\uff0c\u5374\u4e0d\u6599\u4ed6\u628a\u73ca\u5609\u4e5f\u5e26\u6765\u4e86\uff0c\u8fd9\u5374\u662f\u641e\u4ec0\u4e48\u540d\u5802\uff0c\u96be\u9053\u4ed6\u4ee5\u4e3a\u81ea\u5df1\u53eb\u4ed6\u8fc7\u6765\u662f\u65c5\u6e38\u7684\u4e48\u3002</p><p>\u73ca\u5609\u677e\u5f00\u62b1\u7740\u743c\u6069\u7684\u53cc\u81c2\uff0c\u5bf9\u6885\u83f2\u65af\u5fae\u5fae\u4e00\u7b11\uff0c\u70b9\u5934\u793a\u610f\u3002\u5979\u77e5\u9053\u6885\u83f2\u65af\u7a81\u7136\u4f20\u8baf\u628a\u743c\u6069\u62db\u6765\uff0c\u80af\u5b9a\u662f\u6709\u8981\u4e8b\uff0c\u5979\u4e0d\u61c2\u8fd9\u4e9b\uff0c\u4e5f\u4fbf\u4e0d\u591a\u8bf4\u8bdd\u3002\u7565\u4e00\u5b9a\u795e\uff0c\u518d\u62ac\u773c\u5411\u5468\u56f4\u770b\u53bb\uff0c\u53d1\u73b0\u6b64\u5730\u539f\u6765\u662f\u4e00\u5904\u9698\u53e3\uff0c\u51e0\u6bb5\u6b8b\u7834\u7684\u77f3\u5934\u57ce\u5899\u773c\u770b\u5c31\u8981\u5012\u584c\u4f3c\u7684\uff0c\u4f3c\u4e4e\u90fd\u662f\u8fdc\u53e4\u65f6\u7684\u5efa\u7b51\u4e86\u3002\u6885\u83f2\u65af\u5c31\u6b63\u7ad9\u5728\u57ce\u5899\u540e\uff0c\u8ddd\u79bb\u5979\u4e0d\u8fdc\u5904\u8fd8\u6709\u4e24\u4e2a\u4eba\u3002\u4e00\u4e2a\u8eab\u6750\u77ee\u5c0f\uff0c\u7a7f\u7740\u4e00\u8eab\u7070\u8272\u5e03\u8863\uff0c\u5374\u5df2\u7ecf\u88ab\u6495\u7834\u4e86\u51e0\u4e2a\u5927\u53e3\u5b50\uff0c\u9732\u51fa\u91cc\u9762\u7684\u9ed1\u8272\u76ae\u7532\uff0c\u73ca\u5609\u770b\u4e86\u770b\u4ed6\u7684\u8138\uff0c\u53d1\u73b0\u8fd9\u4eba\u957f\u5f97\u6781\u5176\u666e\u901a\uff0c\u5c31\u662f\u90a3\u79cd\u6254\u8fdb\u4eba\u7fa4\u91cc\u518d\u4e5f\u627e\u4e0d\u51fa\u6765\u7684\u7c7b\u578b\u3002\u53e6\u5916\u4e00\u4eba\u8eab\u6750\u9887\u9ad8\uff0c\u5374\u662f\u4e00\u52a8\u4e0d\u52a8\u8eba\u5728\u5730\u4e0a\u7684\uff0c\u4e0d\u77e5\u9053\u662f\u6b7b\u4e86\u8fd8\u662f\u53d7\u4f24\uff0c\u7a7f\u7740\u7070\u8272\u7684\u957f\u888d\uff0c\u65c1\u8fb9\u653e\u7740\u4e00\u67c4\u957f\u957f\u6cd5\u6756\uff0c\u5e94\u8be5\u662f\u4e2a\u5deb\u5e08\u3002</p><p>\u201c\u51fa\u4ec0\u4e48\u4e8b\u4e86\uff1f\u201d\u743c\u6069\u76f4\u622a\u4e86\u5f53\u5730\u95ee\u3002</p><p>\u6885\u83f2\u65af\u628a\u4e8b\u60c5\u7b80\u5355\u8bf4\u4e86\u4e00\u904d\uff0c\u539f\u6765\u662f\u5979\u53bb\u4e86\u8ff7\u65af\u5353\u8bfa\u62dc\u4f1a\u5927\u4e3b\u6559\uff0c\u6b32\u5f85\u8fd4\u56de\u3002\u56e0\u4e3a\u8ff7\u65af\u5353\u8bfa\u6709\u8ff7\u9501\u7b3c\u7f69\uff0c\u963b\u9694\u4f20\u9001\u9b54\u6cd5\uff0c\u5b9d\u77f3\u8df3\u8dc3\u672f\u4e0d\u80fd\u542f\u52a8\uff0c\u5fc5\u987b\u8d70\u51fa\u8303\u56f4\u624d\u884c\u3002\u6885\u83f2\u65af\u51fa\u4e86\u8ff7\u65af\u5353\u8bfa\uff0c\u6b63\u8981\u5f84\u56de\u9634\u9b42\u57ce\uff0c\u5374\u53d1\u73b0\u4e24\u4e2a\u7ad6\u7434\u624b\u6b63\u5728\u8ddf\u8e2a\u4e24\u4e2a\u4eba\u3002</p><p>\u6885\u83f2\u65af\u4e00\u65f6\u597d\u5947\uff0c\u4fbf\u4e5f\u8ddf\u4e0a\u53bb\u770b\u770b\uff0c\u7ed3\u679c\u53d1\u73b0\u4ed6\u4eec\u8ddf\u8e2a\u7684\u4e24\u4e2a\u4eba\u5c45\u7136\u662f\u9f99\u5deb\u6559\u6210\u5458\u3002\u9f99\u5deb\u6559\u540d\u58f0\u6781\u5176\u6076\u52a3\uff0c\u6bd4\u4e4b\u6563\u5854\u6797\u4f1a\u548c\u7ea2\u888d\u5deb\u5e08\u90fd\u8981\u80dc\u8fc7\u51e0\u5206\u2014\u2014\u56e0\u4e3a\u540e\u4e24\u8005\u81f3\u5c11\u8fd8\u61c2\u5f97\u62ff\u5546\u4e1a\u8d38\u6613\u7b49\u906e\u906e\u8138\u9762\uff0c\u505a\u505a\u5e4c\u5b50\uff0c\u884c\u4e8b\u4e5f\u4e0d\u5bb9\u6613\u88ab\u6293\u4f4f\u628a\u67c4\uff0c\u9f99\u5deb\u6559\u5374\u662f\u8d64\u88f8\u88f8\u7684\u9ed1\u793e\u4f1a\u3002</p><p>\u7136\u800c\u2026\u2026</p><p>\u201c\u9f99\u5deb\u6559\uff1f\u201d\u743c\u6069\u7591\u60d1\uff0c\u4ed6\u66fe\u7ecf\u5728\u70db\u5821\u8bfb\u5230\u8fc7\u6709\u5173\u9f99\u5deb\u6559\u7684\u4e00\u4e9b\u8d44\u6599\uff0c\u201c\u6211\u5370\u8c61\u91cc\uff0c\u8fd9\u4e2a\u7ec4\u7ec7\u81ea\u4ece\u521b\u59cb\u4eba\u8428\u9a6c\u65af\u7279\u88ab\u6668\u66e6\u4e4b\u795e\u51fb\u6740\u4e4b\u540e\uff0c\u4e0d\u5c31\u56db\u5206\u4e94\u88c2\uff0c\u5c31\u6b64\u6d88\u4ea1\u4e86\u4e48\u3002\u201d</p><p>\u201c\u56db\u5206\u4e94\u88c2\u662f\u6ca1\u9519\uff0c\u6d88\u4ea1\u5012\u8fd8\u6ca1\u6709\u5462\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u8fd8\u6709\u4e0d\u5c11\u6b8b\u4f59\u5206\u5b50\u5728\u6697\u4e2d\u6d3b\u52a8\u3002\u201d</p><p>\u7ad6\u7434\u624b\u662f\u8457\u540d\u7684\u6b63\u4e49\u7ec4\u7ec7\uff0c\u8ddf\u8e2a\u9f99\u5deb\u6559\u5f92\u662f\u987a\u7406\u6210\u7ae0\u7684\u4e8b\u60c5\u3002\u6885\u83f2\u65af\u597d\u5947\uff0c\u4e5f\u8ddf\u4e86\u4e0a\u53bb\uff0c\u7ed3\u679c\u53d1\u73b0\u5c45\u7136\u8fd8\u662f\u987a\u8def\uff0c\u4e00\u76f4\u671d\u5927\u6c99\u6f20\u65b9\u5411\u800c\u6765\u3002</p><p>\u6885\u83f2\u65af\u5230\u5e95\u4e0d\u7cbe\u64c5\u8fd9\u79cd\u6f5c\u884c\u8ddf\u8e2a\u7684\u4e8b\u60c5\uff0c\u5f88\u5feb\u4fbf\u88ab\u4e24\u4e2a\u7ad6\u7434\u624b\u53d1\u89c9\uff0c\u9669\u4e9b\u8d77\u4e86\u8bef\u4f1a\u3002\u597d\u5728\u5927\u5bb6\u90fd\u7b97\u5584\u826f\u4eba\u58eb\uff0c\u4e00\u756a\u89e3\u91ca\u4e4b\u540e\uff0c\u4e5f\u5c31\u6682\u65f6\u8054\u624b\u5408\u4f5c\u3002\u539f\u6765\u6700\u8fd1\u9f99\u5deb\u6559\u8822\u8822\u6b32\u52a8\uff0c\u4f3c\u4e4e\u6709\u6b7b\u7070\u590d\u71c3\u7684\u8ff9\u8c61\uff0c\u7ad6\u7434\u624b\u6b63\u81ea\u5fe7\u8651\uff0c\u5982\u4eca\u649e\u4e0a\u8fd9\u4e24\u4e2a\u9f99\u5deb\u6559\u5f92\uff0c\u5c31\u60f3\u64d2\u4f4f\u903c\u95ee\u60c5\u62a5\u3002\u4ed6\u4eec\u672c\u6253\u7b97\u5728\u8fd9\u9698\u53e3\u8bbe\u4e0b\u57cb\u4f0f\uff0c\u7ed3\u679c\u884c\u4e8b\u4e0d\u5bc6\uff0c\u88ab\u5bf9\u65b9\u63d0\u524d\u5bdf\u89c9\uff0c\u53cd\u800c\u5403\u4e86\u70b9\u4e8f\u3002\u4e24\u4e2a\u7ad6\u7434\u624b\u4e2d\u4e00\u4eba\u91cd\u4f24\uff0c\u5c31\u662f\u8eba\u5728\u5730\u4e0a\u7684\u8fd9\u4f4d\uff0c\u5982\u4eca\u521a\u559d\u4e86\u6cbb\u7597\u836f\u6c34\uff0c\u6b63\u5728\u7b49\u5f85\u7f13\u6162\u6062\u590d\u3002</p><p>\u201c\u90a3\u4e24\u4e2a\u9f99\u5deb\u6559\u5f92\u5462\uff1f\u201d</p><p>\u201c\u90a3\u91cc\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u4f38\u624b\u6307\u7740\u524d\u65b9\uff0c\u743c\u6069\u987a\u7740\u770b\u8fc7\u53bb\uff0c\u770b\u4e86\u534a\u5929\u624d\u53d1\u73b0\u8fdc\u5904\u60ac\u5d16\u58c1\u4e0a\u5c45\u7136\u6709\u4e00\u4e2a\u975e\u5e38\u9690\u853d\u7684\u6d1e\u53e3\uff0c\u201c\u90a3\u4e24\u4e2a\u9f99\u5deb\u6559\u5f92\u8eb2\u8fdb\u4e86\u6d1e\u91cc\uff0c\u575a\u5b88\u4e0d\u51fa\uff0c\u4e00\u65f6\u60f3\u4e0d\u51fa\u597d\u529e\u6cd5\uff0c\u201d\u6885\u83f2\u65af\u7709\u5934\u5fae\u76b1\uff0c\u6709\u4e9b\u5fe7\u8651\uff0c\u201c\u65f6\u95f4\u62d6\u5f97\u4e45\u4e86\uff0c\u53ea\u6015\u4ed6\u4eec\u4f1a\u6709\u63f4\u5175\u8fc7\u6765\u3002\u201d</p><p>\u663e\u7136\uff0c\u53eb\u743c\u6069\u8fc7\u6765\uff0c\u5c31\u662f\u4e3a\u4e86\u8fd9\u4e8b\u4e86\u3002</p><p>\u743c\u6069\u5fae\u5fae\u76b1\u7709\uff0c\u4ee5\u4e09\u654c\u4e8c\uff0c\u53cd\u800c\u8fd8\u88ab\u91cd\u4f24\u4e86\u4e00\u4eba\uff0c\u8fd9\u4e24\u4e2a\u9f99\u5deb\u6559\u5f92\u6709\u4e9b\u672c\u9886\u554a\u3002\u5982\u4eca\u53c8\u636e\u6d1e\u56f0\u5b88\uff0c\u786e\u5b9e\u4e0d\u597d\u786c\u653b\uff0c\u53ea\u80fd\u60f3\u5176\u4ed6\u7684\u529e\u6cd5\u3002</p><p>\u201c\u90a3\u4e2a\u5c71\u6d1e\u4f3c\u4e4e\u6709\u70b9\u4e0d\u5bf9\u52b2\uff0c\u201d\u743c\u6069\u8fdc\u8fdc\u89c2\u5bdf\u4e86\u4e00\u4e0b\uff0c\u201c\u50cf\u662f\u4eba\u5de5\u5f00\u51ff\u7684\uff1f\u201d</p><p>\u201c\u5bf9\uff0c\u201d\u4e0d\u8fdc\u5904\u7684\u7ad6\u7434\u624b\u7a81\u7136\u63a5\u53e3\uff0c\u4ed6\u6162\u6162\u8d70\u4e86\u8fc7\u6765\uff0c\u201c\u8fd9\u91cc\u66fe\u7ecf\u662f\u8fdc\u53e4\u8010\u745f\u745e\u5c14\u5e1d\u56fd\u7684\u8fb9\u5883\u3002\u8010\u745f\u4eba\u5974\u96b6\u4f8f\u5112\uff0c\u4fee\u5efa\u57ce\u5899\uff0c\u5f00\u51ff\u6d1e\u7a74\u4f5c\u4e3a\u9690\u853d\u54e8\u6240\u3002\u90a3\u786e\u5b9e\u4e0d\u662f\u5c71\u6d1e\uff0c\u662f\u4e2a\u4eba\u5de5\u5f00\u51ff\u7684\u77f3\u5ba4\u3002\u201d</p><p>\u201c\u77f3\u5ba4\u4e48\uff0c\u201d\u743c\u6069\u6c89\u541f\u7740\uff0c\u201c\u90a3\u7a7a\u95f4\u80af\u5b9a\u6709\u9650\uff0c\u53ef\u4ee5\u7528\u70df\u628a\u4ed6\u4eec\u718f\u51fa\u6765\u3002\u201d</p><p>\u201c\u8bd5\u8fc7\u4e86\uff0c\u6ca1\u7528\uff0c\u201d\u7ad6\u7434\u624b\u8bf4\uff0c\u201c\u90a3\u77f3\u5ba4\u6211\u4ee5\u524d\u66fe\u7ecf\u8fdb\u53bb\u8fc7\uff0c\u91cc\u9762\u6709\u6c34\u6e90\uff0c\u4ed6\u4eec\u53ea\u8981\u628a\u8863\u670d\u5f04\u6e7f\u4e86\u6342\u4f4f\u53e3\u9f3b\uff0c\u4e5f\u5c31\u4e0d\u6015\u70df\u718f\u3002\u800c\u4e14\u2026\u2026\u201d\u4ed6\u72b9\u8c6b\u4e86\u4e00\u4e0b\uff0c\u201c\u5176\u5b9e\u6700\u8981\u7d27\u7684\uff0c\u662f\u6211\u4eec\u5e0c\u671b\u80fd\u6293\u6d3b\u53e3\u3002\u201d</p><p>\u743c\u6069\u9ed8\u9ed8\u601d\u5fd6\uff0c\u8f6c\u7740\u5ff5\u5934\uff0c\u654c\u4eba\u65e2\u7136\u8eb2\u8fdb\u77f3\u5ba4\uff0c\u90a3\u5c31\u4e0d\u6015\u4ed6\u9003\u8131\uff0c\u4f46\u81ea\u5df1\u4e5f\u4e0d\u80fd\u8d38\u7136\u51b2\u8fdb\u53bb\uff0c\u800c\u4e14\u53c8\u8981\u6293\u6d3b\u53e3\uff0c\u6700\u597d\u7684\u529e\u6cd5\u5c31\u662f\u903c\u51fa\u6765\uff0c\u70df\u718f\u672c\u662f\u4e0a\u7b56\uff0c\u5982\u4eca\u770b\u6765\u5374\u884c\u4e0d\u901a\u2026\u2026\u7b49\u7b49\uff0c\u672a\u5fc5\u884c\u4e0d\u901a\u3002</p><p>\u201c\u4ed6\u4eec\u5728\u91cc\u9762\u80fd\u89c2\u5bdf\u5230\u5916\u9762\u7684\u60c5\u5f62\u4e48\uff1f\u201d</p><p>\u201c\u53ef\u4ee5\uff0c\u6709\u77ad\u671b\u53e3\uff0c\u4e0d\u8fc7\u89c6\u91ce\u8303\u56f4\u5f88\u72ed\u7a84\u3002\u201d</p><p>\u201c\u80fd\u770b\u5230\u6211\u4eec\u8fd9\u91cc\u4e48\uff1f\u201d</p><p>\u7ad6\u7434\u624b\u6447\u5934\u3002</p><p>\u743c\u6069\u70b9\u70b9\u5934\uff0c\u201c\u529e\u6cd5\u5012\u4e5f\u4e0d\u662f\u6ca1\u6709\uff0c\u201d\u4ed6\u6162\u6162\u8bf4\uff0c\u7a81\u7136\u8f6c\u8138\u770b\u7740\u7ad6\u7434\u624b\uff0c\u201c\u6211\u542c\u8bf4\u5f88\u591a\u5927\u5deb\u5e08\u90fd\u662f\u7ad6\u7434\u624b\u540c\u76df\u7684\u6210\u5458\uff0c\u8bf7\u6559\u4f4f\u5904\u8ddd\u79bb\u6b64\u5730\u6700\u8fd1\u7684\u662f\u54ea\u4f4d\uff1f\u201d</p><p>\u7ad6\u7434\u624b\u4e00\u6014\uff0c\u538b\u6839\u6ca1\u60f3\u5230\u743c\u6069\u4f1a\u95ee\u8fd9\u79cd\u95ee\u9898\uff0c\u4ed6\u8fd8\u6ca1\u56de\u8fc7\u795e\uff0c\u6885\u83f2\u65af\u5728\u65c1\u8fb9\u5df2\u7ecf\u66ff\u4ed6\u56de\u7b54\u4e86\uff0c\u201c\u82e5\u8bf4\u8ddd\u79bb\u6700\u8fd1\uff0c\u5e94\u8be5\u662f\u9634\u5f71\u8c37\u5927\u8d24\u8005\u4f0a\u5c14\u660e\u65af\u7279\uff0c\u53ea\u662f\u4ece\u8fd9\u91cc\u8fc7\u53bb\u81f3\u5c11\u4e5f\u5f97\u4e24\u5929\u8def\u7a0b\uff0c\u53ea\u6015\u662f\u6765\u4e0d\u53ca\u4e86\u3002\u201d</p><p>\u743c\u6069\u5fae\u7b11\uff0c\u201c\u4e9b\u8bb8\u5c0f\u4e8b\uff0c\u54ea\u91cc\u9700\u8981\u52b3\u52a8\u5927\u8d24\u8005\uff0c\u201d\u4ed6\u7565\u7565\u6c89\u541f\uff0c\u53c8\u95ee\u7ad6\u7434\u624b\uff0c\u201c\u4e0d\u77e5\u9053\u5927\u8d24\u8005\u662f\u4ec0\u4e48\u6a21\u6837\uff1f\u201d</p><p>\u203b\u203b\u203b</p><p>\u8981\u7528\u8bed\u8a00\u63cf\u8ff0\u4e00\u4e2a\u4eba\u7684\u76f8\u8c8c\u4e0d\u592a\u5bb9\u6613\uff0c\u4e0d\u8fc7\u8fd9\u662f\u4e2a\u9b54\u6cd5\u4e16\u754c\uff0c\u53ea\u8981\u5728\u5e7b\u672f\u4e0a\u7565\u6709\u9020\u8be3\uff0c\u5c31\u80fd\u7c97\u7565\u628a\u81ea\u5df1\u6240\u77e5\u7684\u67d0\u4eba\u5f62\u8c61\u7ed9\u5e7b\u5316\u51fa\u6765\u3002\u5f88\u5e78\u8fd0\uff0c\u8fd9\u4e09\u4e2a\u7ad6\u7434\u624b\u4e2d\uff0c\u5c31\u6709\u4eba\u6765\u81ea\u9634\u5f71\u8c37\uff0c\u66fe\u7ecf\u591a\u6b21\u89c1\u8fc7\u5927\u8d24\u8005\u4f0a\u5c14\u660e\u65af\u7279\uff0c\u6e05\u695a\u8bb0\u5f97\u4ed6\u7684\u76f8\u8c8c\uff1b\u66f4\u5e78\u8fd0\u7684\u662f\uff0c\u6b64\u4eba\u8fd8\u662f\u4e2a\u5deb\u5e08\uff0c\u867d\u7136\u9020\u8be3\u4e0d\u9ad8\uff0c\u4f46\u73a9\u51e0\u624b\u5e7b\u672f\u8fd8\u662f\u6ca1\u95ee\u9898\u7684\u3002</p><p>\u7136\u800c\u4e0d\u5e78\u7684\u662f\uff0c\u4ed6\u5c31\u662f\u6b64\u65f6\u6b63\u8eba\u5728\u5730\u4e0a\u7684\u90a3\u4f4d\u7ad6\u7434\u624b\u3002\u636e\u6885\u83f2\u65af\u8bf4\uff0c\u4ed6\u662f\u4e2d\u4e86\u9f99\u5deb\u6559\u5f92\u4e00\u51fb\u9aa8\u77db\uff0c\u9669\u4e9b\u523a\u7a7f\u5fc3\u80ba\uff0c\u867d\u7136\u7d27\u6025\u6cbb\u7597\uff0c\u53c8\u704c\u4e86\u4e24\u74f6\u836f\u6c34\u4e0b\u53bb\uff0c\u4f46\u6ca1\u6709\u4e00\u4e2a\u5c0f\u65f6\u662f\u522b\u60f3\u6062\u590d\u52a8\u5f39\u4e86\u3002</p><p>\u201c\u90a3\u5c31\u53ea\u80fd\u518d\u7b49\u4e00\u5c0f\u65f6\u4e86\u3002\u201d</p><p>\u7ad6\u7434\u624b\u8d70\u56de\u53bb\u966a\u4ed6\u53d7\u4f24\u7684\u540c\u4f34\uff0c\u743c\u6069\u3001\u73ca\u5609\u548c\u6885\u83f2\u65af\u5728\u57ce\u5899\u80cc\u9762\u907f\u5149\u5904\u4f11\u606f\u3002\u743c\u6069\u5728\u57ce\u5899\u4e0a\u6bd4\u5212\u7740\uff0c\u5411\u6885\u83f2\u65af\u89e3\u91ca\u4e0b\u4e00\u6b65\u884c\u52a8\u6b65\u9aa4\uff0c\u73ca\u5609\u542c\u4e0d\u660e\u767d\uff0c\u4e5f\u4e0d\u611f\u5174\u8da3\uff0c\u53ea\u9ed8\u9ed8\u770b\u7740\u6885\u83f2\u65af\u3002</p><p>\u5979\u867d\u7136\u548c\u6885\u83f2\u65af\u76f8\u5904\u51e0\u65e5\uff0c\u4f46\u5728\u9634\u9b42\u57ce\u4e2d\u53c8\u65e0\u4ec0\u4e48\u5371\u9669\uff0c\u5e73\u65f6\u81ea\u7136\u53ea\u7a7f\u5bfb\u5e38\u670d\u9970\uff0c\u77e5\u9053\u5979\u662f\u5723\u6b66\u58eb\uff0c\u5374\u4ece\u6ca1\u5f53\u771f\u89c1\u8fc7\u620e\u88c5\u6a21\u6837\uff0c\u6b64\u6b21\u4e4b\u6240\u4ee5\u975e\u8981\u966a\u743c\u6069\u524d\u6765\uff0c\u4e5f\u6709\u76ee\u7684\u4fbf\u5728\u4e8e\u6b64\u3002\u5982\u4eca\u89c1\u5979\u8eab\u62ab\u94f6\u7532\uff0c\u624b\u62c4\u5927\u5251\uff0c\u4fbf\u53ea\u662f\u9759\u9759\u7ad9\u7740\u4e00\u8a00\u4e0d\u53d1\uff0c\u90a3\u79cd\u8eab\u7ecf\u767e\u6218\u78e8\u7ec3\u51fa\u6765\u7684\u679c\u6562\u6740\u4f10\u4e4b\u6c14\u4e5f\u51db\u51db\u8feb\u6765\uff0c\u8bf4\u4e0d\u51fa\u7684\u82f1\u6b66\u903c\u4eba\uff0c\u4e0d\u7531\u5f97\u5fc3\u5e95\u6084\u6084\u8d77\u4e86\u70b9\u5f02\u6837\u3002</p><p>\u73ca\u5609\u867d\u7136\u6e29\u5a49\u8c26\u548c\uff0c\u7d20\u6765\u4e5f\u81ea\u8d1f\u7f8e\u8c8c\uff0c\u5018\u82e5\u4e0d\u662f\u51fa\u8eab\u5e73\u6c11\uff0c\u53c8\u4e0d\u559c\u6b22\u4ea4\u9645\uff0c\u7a7a\u95f2\u65f6\u53ea\u5728\u5bb6\u4e2d\u4f11\u606f\uff0c\u8eab\u540e\u65e9\u5c31\u8ffd\u6c42\u8005\u65e0\u6570\u4e86\u3002\u8fd9\u6b21\u743c\u6069\u628a\u6885\u83f2\u65af\u5e26\u56de\u5bb6\u6765\uff0c\u5979\u867d\u7136\u6709\u4e9b\u5ac9\u5992\uff0c\u4f46\u77e5\u9053\u4ee5\u5341\u4f59\u5e74\u76f8\u6fe1\u4ee5\u6cab\u7684\u60c5\u5206\uff0c\u81ea\u5df1\u5728\u743c\u6069\u5fc3\u4e2d\u5230\u5e95\u662f\u5206\u91cf\u66f4\u91cd\u4e9b\uff0c\u4e5f\u5e76\u4e0d\u7279\u522b\u5728\u610f\u3002\u5982\u4eca\u89c1\u6885\u83f2\u65af\u5168\u8eab\u620e\u88c5\uff0c\u4e8e\u67d4\u7f8e\u4e2d\u663e\u51fa\u4e00\u80a1\u82f1\u6b66\u521a\u5f3a\uff0c\u4e0d\u514d\u5012\u6709\u4e9b\u6005\u7136\u82e5\u5931\u3002</p><p>\u201c\u743c\u6069\u6700\u559c\u6b22\u5979\u7684\uff0c\u53ea\u6015\u4e5f\u6b63\u5728\u4e8e\u6b64\u5904\u5427\uff0c\u81ea\u5df1\u5374\u662f\u600e\u4e48\u4e5f\u6bd4\u4e0d\u4e0a\u4e86\u3002\u201d</p><p>\u6b64\u65f6\u6b63\u662f\u6b63\u5348\uff0c\u53c8\u662f\u76db\u590f\uff0c\u6c14\u5019\u708e\u70ed\uff0c\u743c\u6069\u548c\u6885\u83f2\u65af\u5012\u7f62\u4e86\uff0c\u73ca\u5609\u4f53\u8d28\u8f83\u5dee\uff0c\u53c8\u5e38\u5e74\u5728\u9634\u9b42\u57ce\u5446\u7740\uff0c\u4e60\u60ef\u4e86\u90a3\u79cd\u9634\u51b7\u51b0\u5bd2\u7684\u73af\u5883\uff0c\u9aa4\u7136\u5230\u5916\u9762\u6765\uff0c\u88ab\u70c8\u65e5\u4e00\u6652\uff0c\u611f\u89c9\u6709\u4e9b\u5934\u6655\u76ee\u7729\uff0c\u6015\u743c\u6069\u62c5\u5fc3\uff0c\u5f3a\u6491\u7740\u4e0d\u8bf4\uff0c\u5374\u6e10\u6e10\u6709\u4e9b\u6655\u6655\u6c89\u6c89\u8fc7\u53bb\u3002</p><p>\u5ffd\u7136\u95f4\u611f\u89c9\u8eab\u4e0a\u900f\u6765\u6c81\u5fc3\u51c9\u723d\uff0c\u5c06\u6691\u6c14\u5b8c\u5168\u9a71\u6563\uff0c\u987f\u65f6\u7cbe\u795e\u4e3a\u4e4b\u4e00\u632f\uff0c\u56de\u8fc7\u795e\u4e00\u770b\uff0c\u5374\u53d1\u73b0\u662f\u6885\u83f2\u65af\u4e0d\u77e5\u4f55\u65f6\u8131\u4e0b\u4e86\u5979\u7684\u94f6\u7532\uff0c\u62ab\u5728\u81ea\u5df1\u8eab\u4e0a\u3002\u6885\u83f2\u65af\u8fd9\u94f6\u7532\u662f\u94f6\u9f99\u76ae\u9769\u6240\u5236\uff0c\u7a7f\u4e0a\u4fbf\u6709\u4e00\u5c42\u5bd2\u6c14\u62a4\u4f53\uff0c\u8fde\u706b\u7130\u707c\u70e7\u90fd\u80fd\u62b5\u5fa1\u5f97\u4f4f\uff0c\u4f55\u51b5\u533a\u533a\u9633\u5149\uff0c\u5979\u6b63\u548c\u743c\u6069\u5546\u8bae\uff0c\u7a81\u7136\u5fc3\u6709\u6240\u611f\uff0c\u8f6c\u8138\u89c1\u73ca\u5609\u8138\u8272\u82cd\u767d\uff0c\u989d\u5934\u4e0a\u6c57\u73e0\u6ef4\u4e0b\uff0c\u663e\u7136\u662f\u6709\u4e9b\u4e2d\u6691\u7684\u8ff9\u8c61\uff0c\u8d76\u5fd9\u628a\u94f6\u7532\u8131\u4e86\u4e0b\u6765\u7ed9\u73ca\u5609\u62ab\u4e0a\u3002</p><p>\u73ca\u5609\u5fae\u7b11\u70b9\u70b9\u5934\uff0c\u8868\u793a\u611f\u8c22\uff0c\u5979\u77e5\u9053\u6885\u83f2\u65af\u7684\u6027\u683c\uff0c\u4e5f\u4e0d\u5fc5\u591a\u8bf4\u4ec0\u4e48\u5ba2\u5957\u8bdd\u3002\u6885\u83f2\u65af\u89c1\u5979\u8138\u8272\u6e10\u6e10\u6062\u590d\uff0c\u653e\u4e0b\u5fc3\u6765\uff0c\u8f6c\u8138\u77aa\u4e86\u743c\u6069\u4e00\u773c\uff0c\u201c\u6211\u53eb\u4f60\u6765\u5fc5\u5b9a\u662f\u6709\u5371\u9669\uff0c\u4f60\u600e\u4e48\u628a\u73ca\u5609\u59d0\u59d0\u4e5f\u5e26\u6765\u4e86\uff1f\u201d</p><p>\u201c\u6ca1\u4e8b\uff0c\u662f\u6211\u81ea\u5df1\u8981\u8ddf\u8fc7\u6765\u770b\u770b\uff0c\u201d\u73ca\u5609\u66ff\u743c\u6069\u89e3\u91ca\uff0c\u201c\u6211\u4eec\u6b63\u597d\u5728\u585e\u5c14\uff0c\u4ed6\u8d76\u7740\u8fc7\u6765\uff0c\u6765\u4e0d\u53ca\u9001\u6211\u56de\u53bb\uff0c\u7559\u6211\u4e00\u4e2a\u4eba\u5728\u964c\u751f\u5730\u65b9\u4e5f\u4e0d\u653e\u5fc3\u3002\u201d</p><p>\u6885\u83f2\u65af\u542c\u73ca\u5609\u8bf4\u5979\u548c\u743c\u6069\u662f\u4ece\u585e\u5c14\u8fc7\u6765\uff0c\u7565\u7565\u6709\u4e9b\u8bb6\u5f02\uff0c\u4f46\u4e5f\u4e0d\u591a\u95ee\u3002\u743c\u6069\u5230\u5e95\u53ca\u4e0d\u5f97\u5973\u5b69\u5b50\u7ec6\u5fc3\uff0c\u5fd9\u4e8e\u5546\u8bae\u4e8b\u60c5\uff0c\u5374\u6ca1\u6ce8\u610f\u5230\u73ca\u5609\u4e0d\u9002\uff0c\u76f4\u5230\u6885\u83f2\u65af\u8131\u4e0b\u94f6\u7532\u4e3a\u5979\u62ab\u4e0a\u624d\u53d1\u89c9\uff0c\u8d76\u5fd9\u8fc7\u6765\u6276\u4f4f\u73ca\u5609\uff0c\u201c\u59d0\u59d0\uff0c\u6ca1\u4e8b\u5427\u3002\u201d</p><p>\u73ca\u5609\u5fae\u7b11\u6447\u5934\uff0c\u201c\u6ca1\u4e8b\u4e86\uff0c\u6211\u5750\u4e00\u4f1a\u5c31\u597d\u3002\u201d</p><p>\u743c\u6069\u89c1\u5979\u8138\u8272\u867d\u7136\u597d\u8f6c\uff0c\u8bf4\u8bdd\u5374\u6709\u4e9b\u6ca1\u529b\u6c14\u7684\u6837\u5b50\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e2d\u61ca\u6094\u3002\u53ea\u662f\u5982\u4eca\u518d\u60f3\u8fd9\u4e9b\u4e5f\u6ca1\u7528\uff0c\u53ea\u5f97\u8d76\u7d27\u628a\u9762\u524d\u7684\u9ebb\u70e6\u89e3\u51b3\u4e86\u518d\u8bf4\u3002\u4ed6\u5df2\u7ecf\u6709\u4e86\u4e3b\u610f\uff0c\u53ea\u7b49\u90a3\u4e2a\u53d7\u4f24\u7684\u7ad6\u7434\u624b\u6062\u590d\u8fc7\u6765\u4fbf\u51c6\u5907\u52a8\u624b\u3002\u4e0d\u8fc7\u5728\u6b64\u4e4b\u524d\uff0c\u6709\u4ef6\u4e8b\u60c5\u4ee4\u4ed6\u6709\u4e9b\u7591\u60d1\u3002</p><p>\u201c\u4f60\u548c\u7ad6\u7434\u624b\u7684\u5173\u7cfb\u4f3c\u4e4e\u4e0d\u592a\u597d\uff1f\u201d\u743c\u6069\u95ee\u6885\u83f2\u65af\u3002</p><p>\u8fd9\u6b21\u8981\u6293\u9f99\u5deb\u6559\u5f92\uff0c\u662f\u7ad6\u7434\u624b\u7684\u76ee\u7684\uff0c\u6885\u83f2\u65af\u53ea\u4e0d\u8fc7\u662f\u6070\u597d\u9047\u4e0a\u5e2e\u5fd9\u3002\u743c\u6069\u88ab\u6885\u83f2\u65af\u53eb\u6765\uff0c\u5f00\u59cb\u4e0d\u6e05\u695a\u60c5\u51b5\uff0c\u4ee5\u4e3a\u4ed6\u4eec\u5173\u7cfb\u826f\u597d\uff0c\u6240\u4ee5\u51fa\u624b\u76f8\u52a9\uff0c\u4f46\u521a\u624d\u7684\u8c08\u8bdd\u4e2d\uff0c\u4ed6\u611f\u89c9\u7ad6\u7434\u624b\u5bf9\u6885\u83f2\u65af\u7684\u6001\u5ea6\u9887\u4e3a\u751f\u758f\u51b7\u6de1\uff0c\u5e76\u4e0d\u5982\u4f55\u4eb2\u8fd1\uff0c\u8fd9\u5374\u662f\u6709\u4e9b\u5947\u602a\u3002</p><p>\u6885\u83f2\u65af\u5fae\u5fae\u7b11\u4e86\u7b11\uff0c\u201c\u5012\u548c\u6211\u6ca1\u5173\u7cfb\uff0c\u7ad6\u7434\u624b\u540c\u76df\u548c\u6211\u4eec\u6559\u4f1a\u7d20\u6765\u4e92\u76f8\u770b\u4e0d\u987a\u773c\u7f62\u4e86\u3002\u201d</p><p>\u743c\u6069\u4e00\u6014\uff0c\u7ad6\u7434\u624b\u540c\u76df\u548c\u63d0\u5c14\u6559\u4f1a\u5173\u7cfb\u4e0d\u4f73\uff1f\u636e\u4ed6\u6240\u77e5\uff0c\u7ad6\u7434\u624b\u540c\u76df\u662f\u8d39\u4f26\u5927\u9646\u8457\u540d\u7684\u6b63\u4e49\u7ec4\u7ec7\uff0c\u548c\u5f88\u591a\u5584\u795e\u7684\u6559\u4f1a\u90fd\u662f\u76df\u53cb\u7684\u3002</p><p>\u201c\u55ef\uff0c\u5b97\u65e8\u4e0d\u5408\uff0c\u6240\u4ee5\u5173\u7cfb\u81ea\u7136\u597d\u4e0d\u5230\u54ea\u91cc\u53bb\u4e86\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u6253\u4e2a\u6bd4\u65b9\uff0c\u5c31\u50cf\u5723\u6b66\u58eb\u548c\u6e38\u8361\u8005\uff0c\u5c31\u7b97\u90fd\u79c9\u6301\u5584\u610f\uff0c\u53ea\u6015\u4e5f\u662f\u6ca1\u6cd5\u76f8\u5904\u7684\u3002\u8c1a\u8bed\u8bf4\uff1a\u2018\u90aa\u6076\u4e0e\u90aa\u6076\u4f1a\u81ea\u76f8\u6b8b\u6740\u2019\uff0c\u540c\u6837\u7684\uff0c\u5584\u826f\u4e0e\u5584\u826f\u4e5f\u4f1a\u4e92\u76f8\u51b2\u7a81\u7684\u2014\u2014\u66f4\u4f55\u51b5\uff0c\u201d\u5979\u5fae\u5fae\u51b7\u7b11\uff0c\u201c\u8fd8\u672a\u5fc5\u5c31\u80fd\u65ad\u5b9a\u81ea\u5df1\u79c9\u6301\u7684\u4fbf\u662f\u5584\u826f\u5462\u3002\u201d</p><p>\u743c\u6069\u5927\u7ea6\u6709\u4e9b\u660e\u767d\u4e86\u3002</p><p>\u4fbf\u5728\u6b64\u65f6\uff0c\u6d88\u606f\u4f20\u6765\uff0c\u90a3\u4f4d\u91cd\u4f24\u7684\u7ad6\u7434\u624b\u5deb\u5e08\uff0c\u5728\u836f\u6c34\u7684\u6cbb\u7597\u4e0b\u5df2\u7ecf\u5927\u4f53\u6062\u590d\u4e86\u3002</p><p>\u203b\u203b\u203b</p><p>\u743c\u6069\u7684\u6253\u7b97\u5176\u5b9e\u5f88\u7b80\u5355\uff0c\u5c31\u662f\u5413\u552c\u4eba\u3002</p><p>\u4ed6\u628a\u81ea\u5df1\u53d8\u6210\u9634\u5f71\u8c37\u5927\u8d24\u8005\uff0c\u7136\u540e\u4ece\u8fd9\u91cc\u5f00\u4e00\u9053\u4efb\u610f\u95e8\uff0c\u4f20\u9001\u5230\u9f99\u5deb\u6559\u5f92\u85cf\u8eab\u7684\u77f3\u5ba4\u524d\uff0c\u88c5\u4f5c\u662f\u63a5\u5230\u6d88\u606f\u8d76\u6765\u3002\u4e8b\u5148\u5728\u9f99\u5deb\u6559\u5f92\u770b\u4e0d\u89c1\u7684\u5730\u65b9\u70b9\u4e0a\u706b\u5806\uff0c\u65bd\u5c55\u70df\u706b\u672f\uff0c\u5236\u9020\u51fa\u6d53\u70df\uff0c\u518d\u8ba9\u7ad6\u7434\u624b\u5deb\u5e08\u7528\u9020\u98ce\u672f\u628a\u70df\u5439\u8fdb\u77f3\u5ba4\u3002</p><p>\u70df\u706b\u672f\u5236\u9020\u51fa\u6765\u7684\u6d53\u70df\uff0c\u81ea\u7136\u4e0d\u8db3\u4ee5\u628a\u9f99\u5deb\u6559\u5f92\u903c\u51fa\u6765\uff0c\u4f46\u82e5\u662f\u5728\u91cc\u9762\u6dfb\u52a0\u70b9\u4e1c\u897f\uff0c\u90a3\u4e48\u53c8\u662f\u53e6\u5916\u4e00\u56de\u4e8b\u4e86\u3002\u9634\u9b42\u57ce\u7684\u70bc\u91d1\u5b66\u9662\u91cc\uff0c\u4ec0\u4e48\u7a00\u5947\u53e4\u602a\u7684\u53d1\u660e\u90fd\u6709\uff0c\u5176\u4e2d\u5c31\u6709\u4e00\u6837\uff0c\u80fd\u591f\u5c06\u70df\u706b\u672f\u7684\u9ed1\u70df\u8f6c\u6362\u6210\u767d\u70bd\u8272\uff0c\u800c\u4e14\u8fd8\u5e26\u4e00\u4e9b\u706b\u7130\u707c\u70e7\u7684\u6548\u679c\u3002</p><p>\u5982\u6b64\u4e00\u6765\uff0c\u4e00\u4e2a\u975e\u5e38\u4f4e\u9636\u7684\u70df\u706b\u672f\uff0c\u5c31\u80fd\u4f2a\u88c5\u6210\u53ea\u6709\u6781\u9ad8\u9020\u8be3\u7684\u5deb\u5e08\u624d\u80fd\u65bd\u5c55\u7684\u711a\u4e91\u672f\u3002</p><p>\u5f53\u7136\uff0c\u8fd9\u79cd\u5047\u5192\u7684\u8d27\u8272\uff0c\u53ea\u8981\u4e00\u4e2a\u7b80\u5355\u7684\u89e3\u9664\u9b54\u6cd5\u5c31\u80fd\u6e05\u695a\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u5c31\u7b97\u4e0d\u89e3\u9664\uff0c\u5176\u5b9e\u4e5f\u4e0d\u4f1a\u771f\u53d7\u5230\u591a\u5c11\u4f24\u5bb3\uff0c\u6700\u591a\u76ae\u80a4\u88ab\u70eb\u7ea2\u70b9\uff0c\u4f46\u5e76\u975e\u6bcf\u4e2a\u4eba\u90fd\u80fd\u5728\u201c\u711a\u4e91\u201d\u8fd9\u79cd\u8981\u547d\u7684\u6cd5\u672f\u9762\u524d\u9547\u5b9a\u81ea\u82e5\u51b7\u9759\u5224\u65ad\u7684\uff0c\u4f55\u51b5\u6b64\u524d\u53c8\u6709\u201c\u9634\u5f71\u8c37\u5927\u8d24\u8005\u201d\u9707\u6151\u5728\u5148\u3002</p><p>\u4e8b\u5b9e\u8bc1\u660e\u4e00\u5207\u987a\u5229\uff0c\u4e24\u4e2a\u9f99\u5deb\u6559\u5f92\u679c\u7136\u4e0a\u5f53\uff0c\u201c\u711a\u4e91\u201d\u521a\u521a\u98d8\u8fdb\u77f3\u5ba4\uff0c\u4ed6\u4eec\u5c31\u8fde\u6eda\u5e26\u722c\u5730\u51fa\u6765\u6295\u964d\u4e86\u3002\u8fd9\u4e5f\u662f\u90aa\u6076\u4e4b\u58eb\u7684\u4e00\u5927\u7f3a\u70b9\uff0c\u76f8\u6bd4\u8f83\u8d77\u6765\uff0c\u4ed6\u4eec\u5f53\u4e2d\u8d2a\u751f\u6015\u6b7b\u8005\u592a\u591a\u4e86\u3002</p><p>\u89e3\u51b3\u4e86\u8fd9\u8fb9\u7684\u95ee\u9898\uff0c\u743c\u6069\u4e0d\u60f3\u518d\u591a\u803d\u6401\uff0c\u585e\u5c14\u90a3\u8fb9\u8fd8\u6709\u4e8b\u60c5\u5728\u7b49\u7740\u3002\u6885\u83f2\u65af\u6682\u4e14\u5148\u7559\u4e0b\uff0c\u5979\u5bf9\u8fd9\u4e24\u4e2a\u9f99\u5deb\u6559\u5f92\u4f3c\u4e4e\u4e5f\u6709\u5174\u8da3\uff0c\u743c\u6069\u4fbf\u5148\u7528\u4f20\u9001\u672f\u9001\u73ca\u5609\u56de\u9634\u9b42\u57ce\uff0c\u7136\u540e\u81ea\u5df1\u901a\u8fc7\u57ce\u4e2d\u7684\u4f20\u9001\u95e8\uff0c\u518d\u5ea6\u6765\u5230\u585e\u5c14\u3002\u5f53\u4ed6\u8d76\u5230\u51b3\u6597\u573a\u7684\u65f6\u5019\uff0c\u53d1\u73b0\u83f2\u4e9a\u6b63\u5750\u5728\u539f\u6765\u7684\u4f4d\u7f6e\u4e0a\u7b49\u5f85\u7740\u3002</p><p>\u201c\u6709\u70b9\u4e8b\u60c5\u803d\u6401\uff0c\u6765\u665a\u4e86\u3002\u201d\u743c\u6069\u5411\u5979\u9053\u6b49\u3002</p><p>\u83f2\u4e9a\u5e76\u6ca1\u591a\u8bf4\uff0c\u89c1\u743c\u6069\u6765\u4e86\uff0c\u4fbf\u627e\u5230\u51b3\u6597\u573a\u7684\u8d1f\u8d23\u4eba\uff0c\u5c06\u8fea\u74e6\u514b\u8d4e\u4e86\u4e0b\u6765\u3002\u81f3\u4e8e\u90a3\u4efd\u9b54\u6cd5\u7b14\u8bb0\uff0c\u786e\u5b9e\u5728\u4ed6\u201c\u8eab\u4e0a\u201d\u3002</p><p>\u4f8f\u5112\u4eec\u7d20\u6765\u90fd\u6709\u5728\u8eab\u4e0a\u523a\u9752\u7684\u7231\u597d\uff0c\u8fea\u74e6\u514b\u4e5f\u4e0d\u4f8b\u5916\uff0c\u4ed6\u7684\u4e0a\u534a\u8eab\u7eb9\u6ee1\u4e86\u5404\u79cd\u5947\u602a\u7684\u56fe\u6848\uff0c\u539f\u672c\u770b\u4e0d\u51fa\u4ec0\u4e48\u540d\u5802\uff0c\u7136\u800c\u5728\u4ed6\u5ff5\u4e86\u4e00\u53e5\u5492\u8bed\u540e\uff0c\u523a\u9752\u6162\u6162\u53d8\u5316\u8d77\u6765\uff0c\u73b0\u51fa\u4e00\u6392\u6392\u9f99\u8bed\uff0c\u8fd9\u5c31\u662f\u4ed6\u7236\u4eb2\u7559\u4e0b\u6765\u7684\u9b54\u6cd5\u7b14\u8bb0\u4e86\u3002</p><p>\u4e00\u5207\u4f3c\u4e4e\u90fd\u5f88\u987a\u5229\uff0c\u76f4\u5230\u6700\u540e\u51fa\u4e86\u70b9\u5c0f\u5c0f\u7684\u610f\u5916\u63d2\u66f2\u3002</p><p>\u6885\u83f2\u65af\u7a81\u7136\u4f20\u9001\u4e86\u8fc7\u6765\u3002</p><p>\u201c\u827e\u5f25\u8587\uff1f\u201d</p><p>\u6885\u83f2\u65af\u4e5f\u662f\u6014\u4e86\u6014\uff0c\u201c\u6211\u4ee5\u4e3a\u4f60\u8fd8\u5728\u9634\u9b42\u57ce\u2026\u2026\u201d</p><p>\u5176\u5b9e\u8fd9\u662f\u4e0d\u503c\u4e00\u63d0\u7684\u5c0f\u4e8b\uff0c\u65e2\u7136\u6765\u4e86\uff0c\u5230\u65f6\u5019\u4e00\u8d77\u56de\u53bb\u5c31\u662f\uff0c\u53cd\u6b63\u6709\u4f20\u9001\u95e8\u3002\u7136\u800c\u6885\u83f2\u65af\u8bdd\u8fd8\u8bf4\u5b8c\u7a81\u7136\u987f\u4f4f\u4e86\uff0c\u5979\u8f6c\u8fc7\u8138\uff0c\u9759\u9759\u5730\u770b\u7740\u65c1\u8fb9\u6b63\u5728\u548c\u8fea\u74e6\u514b\u8bf4\u8bdd\u7684\u83f2\u4e9a\u3002</p><p>\u743c\u6069\u770b\u51fa\u4e86\u5f02\u6837\uff0c\u201c\u600e\u4e48\u4e86\uff0c\u827e\u5f25\u8587\uff1f\u201d\u4ed6\u95ee\uff0c\u5fc3\u60f3\u96be\u9053\u6885\u83f2\u65af\u548c\u83f2\u4e9a\u4ee5\u524d\u8ba4\u8bc6\u4e0d\u6210\uff0c\u7136\u540e\u8111\u4e2d\u7a81\u7136\u7075\u5149\u4e00\u95ea\uff0c\u53cd\u5e94\u8fc7\u6765\u3002</p><p>\u201c\u5979\u662f\u2026\u2026\u201d</p><p>\u201c\u662f\u3002\u201d\u6885\u83f2\u65af\u7b80\u6d01\u5730\u56de\u7b54\u3002</p><p>\u743c\u6069\u76ef\u7740\u83f2\u4e9a\uff0c\u7a81\u7136\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u770b\u8d77\u6765\u6211\u5f97\u518d\u53bb\u62dc\u8bbf\u4e00\u8d9f\u5f17\u96f7\u65af\u5148\u751f\u4e86\u3002\u201d</p></article>"}}}]);
