(window.webpackJsonp=window.webpackJsonp||[]).push([[218],{241:function(p){p.exports={chapterTitle:null,title:"\u7b2c1191\u7ae0 \u81a8\u80c0\u7684\u91ce\u5fc3",content:"<article><h2>\u7b2c1191\u7ae0 \u81a8\u80c0\u7684\u91ce\u5fc3</h2><p>\u3000\u3000\u4e09\u661f\u7535\u5b50\u9ad8\u5c42\u65e9\u5c31\u654f\u611f\u7684\u610f\u8bc6\u5230\u96c6\u6210\u5e76\u4f53\u73b0\u66f4\u4e30\u5bcc\u591a\u5f69\u7684\u5a31\u4e50\u6027\u80fd\u662f\u6570\u5b57\u624b\u673a\u672a\u6765\u7684\u4e3b\u6d41\u8d8b\u52bf\uff0c\u5f53iplayer\u5728\u4e24\u5e74\u524d\u8fd8\u6ca1\u6709\u98ce\u9761\u5168\u7403\u4e4b\u65f6\uff0c\u4e09\u661f\u7535\u5b50\u5728\u97e9\u56fd\u7684\u7814\u7a76\u4eba\u5458\u5c31\u63d0\u51fa\u8981\u5728\u624b\u673a\u4e2d\u79fb\u690diplayer\u7684\u97f3\u9891\u64ad\u653e\u529f\u80fd\uff0c\u5374\u53d1\u73b0\u5176\u4e2d\u7ea0\u7f20\u7740\u590d\u6742\u7684\u4e13\u5229\u95ee\u9898\u3002</p><p>\u3000\u3000Iplayer\u6d89\u53ca\u7684\u4e13\u5229\u4e3b\u8981\u5305\u62ec\u4e24\u7c7b\uff1a\u7b2c\u4e00\u7c7b\u662fmp3\u3001wma\u7b49\u683c\u5f0f\u7684\u97f3\u9891\u7f16\u89e3\u7801\u4e13\u5229\uff0c\u4e13\u5229\u638c\u63e1\u5728\u5fae\u8f6f\u3001\u6c64\u59c6\u900a\u7b49\u516c\u53f8\u624b\u91cc\uff0c\u7f16\u89e3\u7801\u786c\u4ef6\u5382\u5546\u5728\u4f9b\u5e94\u82af\u7247\u65f6\u5c31\u5df2\u7ecf\u89e3\u51b3\u4e86\u7b2c\u4e00\u7c7b\u4e13\u5229\u7684\u6388\u6743\u95ee\u9898\uff0c\u9526\u6e56\u672c\u8eab\u4e5f\u662fiplayer\u7f16\u89e3\u7801\u82af\u7247\u7684\u4e3b\u8981\u4f9b\u5e94\u5546\u4e4b\u4e00\uff0c\u65e9\u5c31\u83b7\u5f97\u76f8\u5173\u6280\u672f\u4e13\u5229\u7684\u6388\u6743\uff0c\u8fd8\u53c2\u4e0e\u7814\u53d1\u5e76\u62e5\u6709\u4e00\u90e8\u5206\u540e\u7eed\u4e13\u5229\u6280\u672f\uff1b\u7b2c\u4e8c\u7c7b\u5219\u662fiplayer\u64ad\u653e\u5668\u672c\u8eab\u7684\u53d1\u660e\u4e13\u5229\u4e0e\u8bf8\u591a\u4e94\u82b1\u516b\u95e8\u7684\u5e94\u7528\u4e13\u5229\uff0c\u76f8\u5173\u4e13\u5229\u62e5\u6709\u8005\u53ef\u80fd\u662f\u6574\u673a\u5382\u5546\uff0c\u4e5f\u53ef\u80fd\u662f\u5143\u5668\u4ef6\u4f9b\u5e94\u5546\uff0c\u4f46\u662f\u9526\u6e56\u662f\u8fd9\u4e9b\u4e13\u5229\u7684\u4e3b\u8981\u62e5\u6709\u8005\uff0c\u5728\u6574\u4e2a\u4e13\u5229\u6c60\u4e2d\u5dee\u4e0d\u591a\u5360\u636e80\uff05\uff0c\u4e5f\u5c31\u610f\u5473\u7740\u9526\u6e56\u751f\u4ea7iplayer\u80fd\u591f\u5f88\u5bb9\u6613\u5c31\u7ed5\u8fc7\u522b\u5bb6\u516c\u53f8\u7684\u4e13\u5229\uff0c\u522b\u5bb6\u516c\u53f8\u5374\u4f11\u60f3\u7ed5\u8fc7\u9526\u6e56\u7684\u4e13\u5229\u3002</p><p>\u3000\u3000\u9526\u6e56\u4e5d\u4e03\u3001\u4e5d\u516b\u5e74\u5f00\u59cb\u5728\u5168\u7403\u63a8\u5e7fiplayer\uff0c\u66fe\u5bf9\u4e13\u5229\u6280\u672f\u8fdb\u884c\u5e7f\u6cdb\u7684\u6388\u6743\uff0c\u4f46\u662f\u8fd9\u79cd\u6388\u6743\u660e\u786e\u4ec5\u9650\u4e8eiplayer\u4ea7\u54c1\u81ea\u8eab\uff0c\u675c\u7edd\u88ab\u6388\u6743\u5382\u5546\u5c06\u76f8\u5173\u4e13\u5229\u6280\u672f\u79fb\u690d\u5230\u5176\u4ed6\u6d88\u8d39\u7c7b\u7535\u5b50\u4ea7\u54c1\u3002</p><p>\u3000\u3000\u4e09\u661f\u66fe\u8bd5\u56fe\u5f00\u53d1\u65b0\u7684\u4e13\u5229\u6280\u672f\u7ed5\u8fc7\u9526\u6e56\u7684\u4e13\u5229\u969c\u788d\uff0c\u4e00\u76f4\u5230\u4e5d\u4e5d\u5e74\u5e74\u4e2d\uff0c\u624d\u53d1\u73b0\u9526\u6e56\u5728iplayer\u4e0a\u7684\u4e13\u5229\u5e03\u5c40\u6bd4\u4ed6\u4eec\u60f3\u8c61\u7684\u8fd8\u8981\u5bc6\u96c6\u3001\u5e7f\u6cdb\uff0c\u53ea\u80fd\u653e\u5f03\u5f00\u53d1\u65b0\u6280\u672f\u7684\u52aa\u529b\uff1b\u7531\u4e8e\u4e0e\u9526\u6e56\u7684\u5173\u7cfb\u4e00\u76f4\u90fd\u5f88\u50f5\uff0c\u610f\u8bc6\u5230\u83b7\u5f97\u9526\u6e56\u6388\u6743\u7684\u53ef\u80fd\u6027\u8f83\u4f4e\uff0c\u8fd9\u4e00\u9879\u76ee\u4fbf\u6682\u65f6\u7ed9\u6401\u7f6e\u8d77\u6765\u3002</p><p>\u3000\u3000\u4ece\u9526\u6e56\u7684\u6280\u672f\u4e13\u5229\u5e03\u5c40\u4e0a\u4e0d\u96be\u63a8\u6d4b\u4ed6\u4eec\u4e5f\u5728\u8fdb\u884c\u76f8\u5173\u65b9\u9762\u7684\u52aa\u529b\uff0c\u4f46\u662f\u5728\u624b\u673a\u4e0a\u79fb\u690diplayer\u97f3\u9891\u64ad\u653e\u529f\u80fd\u9700\u8981\u6781\u9ad8\u7684\u6280\u672f\u6c34\u51c6\uff0c\u539f\u5148\u7684\u57fa\u9891\u82af\u7247\u7ec4\u65e0\u6cd5\u7ed9\u97f3\u9891\u64ad\u653e\u63d0\u4f9b\u591a\u4f59\u7684\u8fd0\u7b97\u5904\u7406\u80fd\u529b\uff0c\u9700\u8981\u589e\u52a0\u65b0\u7684\u5fae\u5904\u7406\u5668\uff0c\u5f62\u6210\u57fa\u9891\u53cc\u5904\u7406\u5668\u786c\u4ef6\u67b6\u6784\uff0c\u4e09\u661f\u5411\u6765\u4e0d\u4ee5\u4e3a\u521a\u521a\u8fdb\u5165\u624b\u673a\u4ea7\u4e1a\u624d\u4e09\u5e74\u7684\u9526\u6e56\u4f1a\u62e5\u6709\u5982\u6b64\u5f3a\u7684\u6280\u672f\u5b9e\u529b\u3002\u6240\u4ee5\u5c31\u7b97\u9526\u6e56\u62e5\u6709iplayer\u7684\u7edd\u5927\u591a\u6570\u786c\u4ef6\u53ca\u5e94\u7528\u4e13\u5229\uff0c\u4e09\u661f\u5185\u90e8\u591a\u6570\u5458\u5de5\u4e5f\u4e0d\u8ba4\u4e3a\u7b2c\u4e00\u53f0iplayer\u624b\u673a\u4f1a\u7531\u9526\u6e56\u6700\u5148\u5f00\u53d1\u51fa\u6765\u3002\u674e\u5728\u6d19\u8ddf\u91d1\u5357\u52c7\u7684\u4fe1\u5fc3\u5374\u4e0d\u662f\u5f88\u5f3a\uff0c\u6bd5\u7adf\u5728\u8fc7\u53bb\u4e00\u5e74\u591a\u65f6\u95f4\u91cc\uff0c\u9526\u6e56\u76f8\u7ee7\u63a8\u51fa\u57fa\u9891\u82af\u7247\u7ec4\u4ee5\u53ca\u9762\u5411PDA\u7684\u5fae\u5904\u7406\u5668\u7b49\u8bf8\u591a\u8d85\u8fc7\u5e38\u4eba\u60f3\u8c61\u7684\u7cbe\u5c16\u4ea7\u54c1\u3002</p><p>\u3000\u3000\u674e\u5728\u6d19\u4e0e\u91d1\u5357\u52c7\u521a\u8fdb\u5165\u4f1a\u573a\u5c31\u7ed9\u8bb0\u8005\u56f4\u4f4f\u95ee\u53ca\u5bf9\u7231\u8fbe\u5373\u5c06\u63a8\u51faM\u7cfb\u5217\u624b\u673a\u6709\u4f55\u770b\u6cd5\u65f6\uff0c\u4ed6\u4eec\u7684\u7b2c\u4e00\u4e2a\u5ff5\u5934\u90fd\u60f3\u5230\u7231\u8fbe\u5f00\u53d1\u51faiplayer\u97f3\u4e50\u624b\u673a\u4e86\u2026\u2026</p><p>\u3000\u3000\u8c1c\u5e95\u59cb\u7ec8\u7ed9\u4e09\u7247\u9ed1\u5e03\u906e\u76d6\u4f4f\uff0c\u674e\u5728\u6d19\u4e0e\u91d1\u5357\u52c7\u5fc3\u4e0d\u5728\u5ae3\u7684\u5e94\u4ed8\u8bb0\u8005\uff0c\u4e00\u76f4\u5728\u671f\u5f85\u7231\u8fbe\u7684\u9ad8\u5c42\u8fdb\u573a\u5c06\u8c1c\u5e95\u63ed\u5f00\uff0c\u5728\u573a\u7684\u8bb0\u8005\u4e0e\u5609\u5bbe\u4e5f\u6709\u7740\u540c\u6837\u7684\u671f\u5f85\u3002</p><p>\u3000\u3000\u4e5d\u4e5d\u5e74\u4ee5\u53ca\u4eca\u5e74\u4e0a\u534a\u5e74\u56fd\u4ea7\u624b\u673a\u7684\u5d1b\u8d77\uff0c\u4f7f\u5f97\u6d77\u5916\u624b\u673a\u54c1\u724c\u5728\u4e2d\u56fd\u5e02\u573a\u6240\u5360\u636e\u7684\u4efd\u989d\u4e09\u5e45\u7f29\u6c34\uff0c\u5728\u4e2d\u56fd\u624b\u673a\u5e02\u573a\u6392\u540d\u7b2c\u4e00\u7684\u6469\u6258\u7f57\u62c9\u4eca\u5e74\u4e0a\u534a\u5e74\u5e02\u573a\u5360\u6709\u7387\u5df2\u7ecf\u4e0b\u964d\u523021\uff05\u5de6\u53f3\uff0c\u8bfa\u57fa\u4e9a\u7565\u4f4e\u4e00\u4e9b\uff0c\u4e3a18\uff05\uff0c\u7231\u8fbe\u4f4d\u4e8e\u7b2c\u4e09\u4f4d\uff0c\u5e02\u573a\u5360\u6709\u7387\u4e3a16\uff05\uff0c\u610f\u5473\u7740\u7231\u8fbe\u7a0d\u7a0d\u52aa\u529b\u5c31\u6709\u8d85\u8d8a\u6469\u6258\u7f57\u62c9\u4e0e\u8bfa\u57fa\u4e9a\u6210\u4e3a\u4e2d\u56fd\u624b\u673a\u5e02\u573a\u7b2c\u4e00\u54c1\u724c\u7684\u53ef\u80fd\u6027\u3002</p><p>\u3000\u3000\u8fd9\u5176\u4e2d\u4e5f\u8bb8\u6709\u672c\u571f\u5e02\u573a\u7684\u5929\u7136\u4f18\u52bf\uff0c\u4f46\u662f\u5728\u9ad8\u65b0\u6280\u672f\u9886\u57df\uff0c\u56fd\u5185\u4f01\u4e1a\u9996\u6b21\u5728\u5c40\u90e8\u5e02\u573a\u62e5\u6709\u5168\u9762\u538b\u5236\u6d77\u5916\u5382\u5546\u7684\u7efc\u5408\u5b9e\u529b\uff0c\u800c\u4e0d\u662f\u4e00\u5473\u7684\u4f9d\u8d56\u4f4e\u5ec9\u7684\u52b3\u52a8\u529b\u6210\u672c\u2014\u2014\u8fd9\u79cd\u53ef\u80fd\u6027\u5149\u60f3\u60f3\u5c31\u8ba9\u4eba\u5174\u594b\u4e0d\u5df2\uff0c\u7231\u8fbe\u7684\u65b0\u54c1\u53d1\u5e03\u4f1a\u81ea\u7136\u4e5f\u6bd4\u4e09\u661f\u7535\u5b50\u66f4\u80fd\u6311\u9017\u56fd\u5185\u5a92\u4f53\u7684\u654f\u611f\u795e\u7ecf\u3002</p><p>\u3000\u3000\u674e\u5728\u6d19\u4e0e\u91d1\u5357\u52c7\u5bf9\u9152\u4f1a\u63d0\u4f9b\u7684\u4f73\u80b4\u4e0d\u611f\u5174\u8da3\uff0c\u4ed6\u4eec\u5076\u5c14\u770b\u5230\u9648\u4fe1\u751f\u4e0e\u6768\u4e91\u5728\u5bb4\u4f1a\u5385\u5916\u7684\u8fc7\u9053\u91cc\u8ddf\u5f20\u606a\u5728\u7a83\u7a83\u79c1\u8bed\uff0c\u7d27\u63a5\u7740\u5c31\u770b\u7740\u5f20\u606a\u8f6c\u8eab\u8f6c\u5916\u8d70\u53bb\uff0c\u9648\u4fe1\u751f\u4e0e\u6768\u4e91\u5927\u6b65\u8d70\u4e86\u8fdb\u6765\uff0c\u51e0\u4e4e\u5438\u5f15\u4e86\u5bb4\u4f1a\u5385\u91cc\u6240\u6709\u4eba\u7684\u76ee\u5149\u3002</p><p>\u3000\u3000\u7b80\u5355\u7684\u4eea\u5f0f\u8fc7\u540e\uff0c\u6768\u4e91\u5148\u63ed\u53bb\u5de8\u578b\u6d77\u62a5\u4e0a\u8499\u7740\u7684\u9ed1\u5e03\uff0c\u8ba9\u6d77\u62a5\u91cc\u7684\u56fe\u7247\u5185\u5bb9\u5c55\u73b0\u5728\u4e0e\u4f1a\u8005\u773c\u524d\u3002</p><p>\u3000\u3000\u770b\u7740\u6d77\u62a5\u91cc\u7684\u65b0\u6b3e\u624b\u673a\u5f69\u8272\u5c4f\u5e55\u98d8\u51fa\u97f3\u7b26\u56fe\u6848\uff0c\u91d1\u5357\u52c7\u4e0b\u610f\u8bc6\u7684\u53bb\u770b\u674e\u5728\u6d19\u7684\u53cd\u5e94\uff0c\u53ea\u89c1\u674e\u5728\u6d19\u6b7b\u6b7b\u7684\u76ef\u7740\u6d77\u62a5\u3002\u9526\u6e56\u6b64\u6b21\u63a8\u51fa\u97f3\u4e50\u624b\u673a\u4e0d\u4f1a\u8ba9\u4ed6\u4eec\u7279\u522b\u7684\u610f\u5916\uff0c\u4f46\u662f\u9526\u6e56\u4f55\u65f6\u5728STN\u6db2\u6676\u8272\u5f69\u6280\u672f\u53d6\u5f97\u5173\u952e\u6027\u7684\u7a81\u7834\uff1f\u9526\u6e56\u6b64\u6b21\u63a8\u51fa\u7684\u65b0\u54c1\u6740\u624b\u950f\u7adf\u7136\u662f\u5f69\u5c4f\u97f3\u4e50\u624b\u673a\uff0c\u5b8c\u5168\u51fa\u4e4e\u674e\u5728\u6d19\u3001\u91d1\u5357\u52c7\u7684\u610f\u6599\u3002</p><p>\u3000\u3000\u591a\u5f69STN\u6db2\u6676\u5c4f\u7684\u624b\u673a\u65e9\u5728\u4e5d\u516b\u5e74\u5e74\u521d\u5c31\u95ee\u4e16\u4e86\uff0c\u53ea\u662f\u4e09\u8272\u5c4f\u8fd8\u79f0\u4e0d\u4e0a\u771f\u6b63\u7684\u5f69\u5c4f\u624b\u673a\uff0c\u771f\u6b63\u7684\u7b2c\u4e00\u6b3e\u5f69\u5c4f\u624b\u673a\u4e8e\u53bb\u5e74\u4e5d\u6708\u5728\u65e5\u672c\u63a8\u51fa\u3002\u968f\u540e\u5f69\u5c4f\u624b\u673a\u5c31\u8fc5\u901f\u5728\u65e5\u672c\u98ce\u9761\u8d77\u6765\uff0c\u4eca\u5e74\u4e0a\u534a\u5e74\u65e5\u672c\u5f69\u5c4f\u624b\u673a\u9500\u552e\u7a81\u7834800\u4e07\u53f0\uff0c\u5360\u636e\u65e5\u672c\u540c\u671f\u624b\u673a\u9500\u552e\u768430\uff05\u3002</p><p>\u3000\u3000\u867d\u7136TFT\u8584\u819c\u6db2\u6676\u7406\u8bba\u4e0a\u4e5f\u53ef\u7528\u6765\u505a\u624b\u673a\u5f69\u8272\u5c4f\u5e55\uff0c\u800c\u4e14\u8272\u6cfd\u66f4\u9c9c\u8273\u3001\u9971\u6ee1\uff0c\u4f46\u662fTFT\u8584\u819c\u6db2\u6676\u7684\u9ad8\u8017\u7535\u4e0e\u9ad8\u5236\u9020\u6210\u672c\u662f\u773c\u524d\u5f88\u96be\u514b\u670d\u7684\u969c\u788d\uff0c\u73b0\u5728\u65e5\u672c\u5e02\u573a\u7edd\u5927\u591a\u6570\u5f69\u5c4f\u624b\u673a\u90fd\u662f\u91c7\u7528\u6539\u8fdb\u578bSTN\u6db2\u6676\u6280\u672f\uff0c\u867d\u7136\u8272\u5f69\u663e\u793a\u6548\u679c\u7565\u5dee\u4e00\u4e9b\uff0c\u4f46\u662f\u5728\u8282\u7535\u4e0e\u5236\u9020\u6210\u672c\u6709\u7740\u660e\u663e\u7684\u4f18\u52bf\u3002</p><p>\u3000\u3000\u4e09\u661f\u4e5f\u53bb\u5e74\u8fc5\u901f\u8ffd\u8e2a\u65b0\u7684\u6280\u672f\u70ed\u70b9\u8fdb\u884c\u5f69\u5c4f\u624b\u673a\u7684\u5f00\u53d1\uff0c\u674e\u5728\u6d19\u4e5f\u8003\u8651\u8fc7\u9526\u6e56\u4e0d\u53ef\u80fd\u770b\u4e0d\u5230\u65e5\u672c\u624b\u673a\u5e02\u573a\u7684\u53d8\u5316\uff0c\u4f46\u662f\u6839\u636e\u641c\u96c6\u5230\u7684\u60c5\u62a5\u663e\u793a\u9526\u6e56\u4e00\u76f4\u90fd\u6ca1\u6709\u8ddf\u5f69\u8272STN\u6db2\u6676\u5c4f\u4f9b\u5e94\u5546\u63a5\u89e6\u7684\u8ff9\u8c61\uff0c\u5b8c\u5168\u6ca1\u6709\u60f3\u5230\u9526\u6e56\u51ed\u501f\u81ea\u5df1\u7684\u6280\u672f\u5b9e\u529b\u7a81\u7834STN\u6db2\u6676\u7684\u8272\u5f69\u663e\u793a\u969c\u788d\u3002\u518d\u8054\u60f3\u5230\u9526\u6e56\u6b64\u65f6\u5927\u8d39\u5468\u7ae0\u7684\u6536\u8d2d\u73b0\u4ee3\u534a\u5bfc\u4f53\u7684\u6db2\u6676\u4e1a\u52a1\uff0c\u674e\u5728\u6d19\u4e0e\u91d1\u5357\u52c7\u90fd\u610f\u8bc6\u5230\u9526\u6e56\u5728\u6db2\u6676\u9886\u57df\u7684\u91ce\u5fc3\u2026\u2026</p><p>\u3000\u3000\u4f5c\u4e3a\u5ba3\u4f20\u7684\u6b65\u9aa4\u4e4b\u4e00\uff0c\u9526\u6e56\u4f1a\u5411\u7279\u5b9a\u7684\u4e13\u4e1a\u5a92\u4f53\u63d0\u4f9b\u65b0\u673a\u6837\u54c1\u4ee5\u4f5c\u5e94\u7528\u6d4b\u8bd5\u6240\u7528\uff0c\u5728\u9152\u4f1a\u73b0\u573a\uff0c\u5de5\u4f5c\u4eba\u5458\u4e5f\u64cd\u4f5c\u5c55\u793a\u9526\u6e56\u65b0\u6b3e\u624b\u673a\u7684\u6700\u65b0\u529f\u80fd\u3002</p><p>\u3000\u3000\u674e\u5728\u6d19\u3001\u91d1\u5357\u52c7\u5dee\u4e0d\u591a\u4e5d\u70b9\u949f\u79bb\u5f00\u9152\u4f1a\u73b0\u573a\uff0c\u5750\u8fdb\u8f66\u91cc\uff0c\u611f\u89c9\u90fd\u76f8\u5f53\u7684\u4e0d\u597d\u53d7\u3002\u6bd4\u8d77\u5e38\u89c4\u7684STN\u6db2\u6676\u6539\u8fdb\u578b\u6280\u672fCSTN\uff0c\u9526\u6e56\u5f69\u5c4f\u97f3\u4e50\u624b\u673a\u7684\u5c4f\u5e55\u8272\u6cfd\u9971\u548c\u5ea6\u66f4\u9ad8\uff0c\u867d\u7136\u65e0\u6cd5\u5f00\u673a\u68c0\u6d4b\uff0c\u674e\u5728\u6d19\u6000\u7591\u9526\u6e56\u5df2\u7ecf\u638c\u63e1\u4e86\u53cc\u5c42STN\u5f25\u8865\u8272\u5dee\u7684\u9ad8\u7aef\u6280\u672f\uff0c\u8fd9\u5df2\u7ecf\u662f\u5f53\u524d\u5f69\u5c4f\u624b\u673a\u5e02\u573a\u6700\u9ad8\u7aef\u6280\u672f\u4e86\uff0c\u80fd\u4f7f\u4e24\u82f1\u5bf8\u7684\u624b\u673a\u5c4f\u5e55\u8272\u5f69\u9971\u548c\u5ea6\u8fbe\u523016\u4f4d\u8272\uff0c\u4e5f\u5c31\u4e09\u661f\u8fd8\u6253\u7b97\u4ece\u590f\u666e\u5f15\u8fdb\u8be5\u9879\u6280\u672f\u3002</p><p>\u3000\u3000\u5373\u4f7f\u6ca1\u6709\u4e13\u5229\u4e0a\u7684\u969c\u788d\uff0c\u4e09\u661f\u73b0\u9636\u6bb5\u8981\u5f00\u53d1\u4e00\u6b3e256M\u5bb9\u91cf\u7684\u95ea\u5b58\u5f0f\u97f3\u4e5016\u4f4d\u8272\u5f69\u5c4f\u624b\u673a\u53c8\u540c\u65f6\u96c6\u6210WAP\u3001MQ\u7b49\u8bf8\u591aGPRS\u5e94\u7528\u529f\u80fd\uff0c\u4e5f\u662f\u4e07\u5206\u56f0\u96be\u7684\uff0c\u9762\u4e34\u6781\u9ad8\u7684\u5931\u8d25\u98ce\u9669\uff1b\u504f\u504f\u9526\u6e56\u62ff\u51fa\u6765\u4e86\uff0c\u4e5f\u96be\u602a\u5f20\u606a\u6628\u591c\u6562\u5982\u6b64\u56a3\u5f20\u7684\u8bf4\u90a3\u756a\u8bdd\u3002</p><p>\u3000\u3000\u867d\u7136\u6280\u672f\u4e0a\u7684\u4f18\u52bf\u672a\u5fc5\u80fd\u8f6c\u5316\u6210\u5e02\u573a\u4e0a\u7684\u4f18\u52bf\uff0c\u6bd5\u7adf\u9526\u6e56\u8fd9\u6b3em1808\u97f3\u4e50\u5f69\u5c4f\u624b\u673a\u5e02\u573a\u552e\u4ef7\u4f1a\u5728\u4e07\u5143\u4ee5\u4e0a\uff0c\u9488\u5bf9\u7684\u662f\u4e00\u7247\u76f8\u5bf9\u72ed\u7a84\u7684\u9ad8\u7aef\u5e02\u573a\uff0c\u5728\u9ad8\u7aef\u5546\u52a1\u624b\u673a\u5e02\u573a\u4e5f\u672a\u5fc5\u53d7\u6b22\u8fce\uff0c\u9526\u6e56\u6b64\u65f6\u540c\u65f6\u63a8\u51fa\u53e6\u4e24\u6b3e\u96c6\u6210FM\u8c03\u9891\u529f\u80fd\u7684i\u7cfb\u5217\u3001d\u7cfb\u5217\u4e2d\u7aef\u65b0\u6b3eGPRS\u624b\u673a\u624d\u662f\u771f\u6b63\u4e89\u593a\u5e02\u573a\u7684\u4e3b\u529b\u519b\u3002</p><p>\u3000\u3000\u6b64\u65f6\u8bf4\u4e09\u661f\u7535\u5b50\u65b0\u63a8\u51faA228\u624b\u673a\u4e00\u5b9a\u4f1a\u7ed9\u9526\u6e56\u538b\u5236\u4f4f\u8fd8\u592a\u65e9\u4e86\u4e9b\uff0c\u4f46\u662f\u674e\u5728\u6d19\u5fc3\u91cc\u4f9d\u65e7\u4e0d\u662f\u6ecb\u5473\u3002</p><p>\u3000\u3000\u91d1\u5357\u52c7\u8fd8\u6ce8\u610f\u5230\u6469\u6258\u7f57\u62c9\u3001\u8bfa\u57fa\u4e9a\u7b49\u6d77\u5916\u624b\u673a\u5382\u5546\u4ee3\u8868\u4eca\u5929\u5728\u9152\u4f1a\u4e0a\u7684\u8868\u60c5\uff0c\u8fd9\u4e9b\u6d77\u5916\u624b\u673a\u5382\u5546\u6b64\u756a\u96c4\u5fc3\u58ee\u5fd7\uff0c\u6253\u7b97\u501f\u4e2d\u56fd\u79fb\u52a8\u5168\u9762\u5347\u7ea7\u79fb\u52a8\u901a\u8baf\u7f51\u7edc\u7684\u673a\u4f1a\u501f\u52a9\u5e02\u573a\u4f18\u52bf\u4e0d\u65ad\u7684\u63a8\u51fa\u65b0\u6b3e\u4ea7\u54c1\u5c06\u4e2d\u56fd\u56fd\u4ea7\u624b\u673a\u5f7b\u5e95\u7684\u6df9\u6ca1\u6389\uff0c\u4ed6\u4eec\u6ca1\u6709\u60f3\u5230\u9526\u6e56\u7684\u624b\u673a\u6280\u672f\u5df2\u7ecf\u53d1\u5c55\u5230\u5982\u6b64\u7684\u6c34\u5e73\u3002\u81f3\u5c11\u5728GPRS\u65f6\u4ee3\uff0c\u9526\u6e56\u5df2\u7ecf\u5b8c\u5168\u4e0d\u754f\u60e7\u6d77\u5916\u624b\u673a\u5382\u5546\u65bd\u52a0\u673a\u6d77\u6218\u672f\uff0c\u9526\u6e56\u6709\u96c4\u539a\u7684\u6280\u672f\u5e95\u5b50\u652f\u6491\uff0c\u53c8\u6709\u5728\u5168\u7403\u5404\u5730\u7684\u4ea7\u54c1\u8bbe\u8ba1\u4e0e\u5f00\u53d1\u4e2d\u5fc3\u80fd\u591f\u6e90\u6e90\u4e0d\u65ad\u7684\u63d0\u4f9b\u65b0\u4ea7\u54c1\u8bbe\u8ba1\uff1b\u800c\u4e14\u9526\u6e56\u8fd9\u6b21\u5c06\u5728\u5168\u7403\u540c\u6b65\u53d1\u884c\u8fd9\u6b3e\u97f3\u4e50\u624b\u673a\u3002</p><p>\u3000\u3000\u771f\u4e0d\u77e5\u9053\u4e2d\u56fd\u7684\u5a92\u4f53\u4f1a\u5bf9\u9526\u6e56\uff08\u7231\u8fbe\uff09\u7684\u79cb\u5b63\u65b0\u54c1\u9884\u544a\u9152\u4f1a\u6709\u600e\u6837\u7684\u62a5\u9053\uff0c\u91d1\u5357\u52c7\u9ed8\u9ed8\u7684\u60f3\u7740\uff0c\u4e0e\u674e\u5728\u6d19\u4e00\u8def\u4e0a\u90fd\u6ca1\u6709\u600e\u4e48\u8bf4\u8bdd\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4e16\u7eaa\u9526\u6e56\u5927\u9152\u5e97\u7684\u9876\u5c42\uff0c\u9664\u4e86\u5bb4\u4f1a\u5927\u5385\u5916\uff0c\u4e1c\u5357\u89d2\u8fd8\u6709\u72ec\u7acb\u7684\u89c2\u666f\u53f0\uff0c\u53ef\u4ee5\u8fdc\u773a\u71d5\u5f52\u5c71\u3001\u71d5\u5f52\u6e56\u7684\u591c\u666f\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u7740\u624b\u91cc\u7684m1808\u97f3\u4e50\u5f69\u5c4f\u624b\u673a\uff0c\u867d\u7136\u8fd9\u6b3e\u624b\u673a\u5728\u7ec6\u8282\u6027\u80fd\u4e0a\u8fd8\u6709\u80fd\u4e0d\u65ad\u63d0\u9ad8\u7684\u5730\u65b9\uff0c\u4f46\u4ed6\u76f8\u4fe1\u5728\u667a\u80fd\u624b\u673a\u95ee\u4e16\u4e4b\u524d\u8fd9\u6b3e\u624b\u673a\u5728\u5e02\u573a\u4e0a\u90fd\u4e0d\u4f1a\u9eef\u7136\u65e0\u5149\u3002</p><p>\u3000\u3000\u9648\u9759\u624b\u62b1\u5728\u80f8\u524d\uff0c\u9876\u90e8\u7684\u6c14\u7a97\u6253\u5f00\uff0c\u98ce\u704c\u8fdb\u6765\uff0c\u4f7f\u5f97\u79cb\u591c\u6709\u4e9b\u6e05\u5bd2\u3002\u9648\u9759\u6ca1\u6709\u7a7f\u7740\u5f20\u606a\u8131\u7ed9\u5979\u7684\u8fd0\u52a8\u886b\uff0c\u968f\u65f6\u90fd\u4f1a\u6709\u4eba\u8d70\u8fdb\u6765\u2014\u2014\u5979\u6709\u4e9b\u597d\u5f3a\uff0c\u4e0d\u60f3\u522b\u4eba\u8bf4\u79d1\u738b\u9ad8\u79d1\u7684\u6210\u529f\u662f\u56e0\u4e3a\u5979\u8ddf\u5f20\u606a\u7279\u6b8a\u7684\u5173\u7cfb\u3002</p><p>\u3000\u3000\u201c\u540e\u5929\u5c31\u53bb\u97e9\u56fd\uff1f\u201d\u9648\u9759\u8f6c\u8fc7\u8eab\u95ee\u5f20\u606a\uff0c\u5979\u7684\u773c\u775b\u5728\u706f\u5149\u4e0b\u660e\u6f88\u6e05\u4eae\uff0c\u5341\u5206\u7684\u8ff7\u4eba\u3002</p><p>\u3000\u3000\u201c\u4e5f\u4e0d\u77e5\u9053\u540e\u5929\u80fd\u4e0d\u80fd\u8d70\u6210\uff0c\u6700\u8fd1\u4e8b\u60c5\u592a\u591a\uff0c\u201d\u5f20\u606a\u5c06\u65b0\u624b\u673a\u4e22\u684c\u4e0a\uff0c\u201c\u8fd8\u662f\u6709\u4e9b\u653e\u5fc3\u4e0d\u4e0b\uff0c\u89c1\u4e0a\u4e00\u9762\u5c31\u56de\u6765\u2014\u2014\u6211\u60f3\u6211\u4e5f\u6709\u8d44\u683c\u53bb\u89c1\u4e00\u89c1\u674e\u5065\u7199\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u91d1\u5c71\u7684\u624b\u673a\u751f\u4ea7\u57fa\u5730\u4e5f\u5c31\u8981\u5efa\u6210\u4e86\uff0c\u90fd\u8bf4\u674e\u5065\u7199\u662f\u97e9\u56fd\u7684\u7ecf\u8425\u4e4b\u795e\uff0c\u8981\u662f\u674e\u5065\u7199\u8fd8\u5bf9\u9526\u6e56\u8f7b\u89c6\uff0c\u90a3\u4e5f\u5c31\u62c5\u4e0d\u8d77\u5982\u6b64\u7684\u8d5e\u8a89\u4e86\uff0c\u201d\u9648\u9759\u6ca1\u6709\u7ec6\u95ee\u674e\u99a8\u4e88\u7684\u4e8b\u60c5\uff0c\u8bf4\u9053\uff0c\u201c\u4ec0\u4e48\u65f6\u5019\u4f60\u4f1a\u7ed9\u56fd\u5185\u5c01\u4e3a\u7ecf\u8425\u4e4b\u795e\u7684\u5934\u8854\u4e5f\u8bf4\u4e0d\u5b9a\uff0c\u8981\u662f\u4f60\u516c\u5f00\u8d70\u5230\u53f0\u524d\u7684\u8bdd\u3002\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u5f20\u606a\u6447\u4e86\u6447\u5934\uff0c\u4ed6\u8fd8\u6ca1\u6709\u8d70\u5230\u53f0\u524d\u7684\u5fc3\u601d\uff0c\u4e0d\u4e60\u60ef\u88ab\u516c\u4f17\u4e0e\u5a92\u4f53\u76ee\u5149\u805a\u7126\u7684\u751f\u6d3b\uff0c\u90a3\u4e5f\u4e0d\u662f\u4ed6\u6240\u5411\u5f80\u7684\u751f\u6d3b\uff0c\u201c\u8fd9\u79cd\u865a\u540d\u8c01\u559c\u6b22\u6234\u8c01\u5934\u4e0a\u53bb\u597d\u4e86\uff0c\u53cd\u6b63\u6211\u4e0d\u7a00\u7f55\u2026\u2026\u201d\u53c8\u770b\u4e86\u770b\u8868\uff0c\u95ee\u9648\u9759\uff0c\u201c\u73b0\u5728\u65f6\u95f4\u8fd8\u65e9\uff0c\u591c\u91cc\u8d76\u5230\u91d1\u5c71\uff0c\u8fd8\u80fd\u8212\u670d\u7684\u7761\u4e00\u89c9\uff0c\u660e\u5929\u8d77\u65e9\u770b\u8fc7\u624b\u673a\u57fa\u5730\u518d\u8d76\u56de\u6765\u5efa\u90ba\u4e5f\u4e0d\u788d\u4e8b\u2026\u2026\u4f60\u966a\u6211\u53bb\u91d1\u5c71\uff1f\u201d</p><p>\u3000\u3000\u91d1\u5c71\u7684\u5927\u578b\u624b\u673a\u5236\u9020\u57fa\u5730\u5df2\u7ecf\u5174\u5efa\u8fd1\u4e00\u5e74\u7684\u65f6\u95f4\uff0c\u4e2d\u6676\u5fae\u82af\u7684\u91d1\u5c71\u5de5\u5382\u5c11\u8bf4\u8fd8\u8981\u534a\u5e74\u7684\u65f6\u95f4\u624d\u80fd\u5efa\u6210\uff0c\u9664\u4e86\u79d1\u738b\u9ad8\u79d1\u4e0e\u65b0\u5143\u7535\u5668\u7684\u7ec4\u88c5\u5de5\u5382\u5916\uff0c\u5176\u4ed6\u5de5\u5382\u7684\u914d\u5957\u80fd\u529b\u8fd8\u76f8\u5bf9\u8f83\u5f31\uff0c\u4f46\u662f\u4e5f\u8ba9\u91d1\u5c71\u5177\u5907\u5e74\u4ea7\u4e24\u5343\u4e07\u53ea\u624b\u673a\u7684\u80fd\u529b\u3002\u4eca\u5e74\u5185\u5411\u8054\u901a\u63d0\u4f9b\u4e86\u4e09\u767e\u4e07\u53f0\u5b9a\u5236\u624b\u673a\u5927\u90e8\u5206\u90fd\u5c06\u7531\u79d1\u738b\u9ad8\u79d1\u4e0e\u65b0\u5143\u7535\u5668\u5728\u91d1\u5c71\u7684\u624b\u673a\u5de5\u5382\u63d0\u4f9b\uff0c\u4e5f\u5c06\u6709\u5927\u91cf\u7684\u624b\u673a\u5c06\u4ece\u8fd9\u91cc\u7531\u65b0\u5143\u7535\u5668\u8ddf\u79d1\u738b\u5411\u4e1c\u5357\u4e9a\u53ca\u5357\u4e9a\u5730\u533a\u8f93\u9001\uff0c\u5173\u952e\u9526\u6e56\u4e5f\u9700\u8981\u4f9d\u9760\u91d1\u5c71\u7684\u624b\u673a\u751f\u4ea7\u80fd\u529b\u3002</p><p>\u3000\u3000\u9526\u6e56\u5728\u6d77\u5dde\u7684\u624b\u673a\u751f\u4ea7\u57fa\u5730\uff0c\u5e74\u4ea7\u80fd\u53ea\u6709\u516b\u767e\u4e07\u4ef6\uff0c\u800c\u9526\u6e56\u4eca\u5e74\u5728\u56fd\u5185\u7684\u9500\u552e\u76ee\u6807\u5c31\u662f\u516b\u767e\u4e07\u4ef6\uff0c\u6d77\u5916\u5e02\u573a\u6240\u5f62\u6210\u7684\u7f3a\u53e3\u90fd\u59d4\u6258\u65b0\u5143\u7535\u5668\u4ee3\u5de5\u3002\u5728\u9526\u6e56\u7684\u624b\u673a\u4e1a\u52a1\u4e2d\uff0c\u4e4b\u524d\u6d77\u5916\u5e02\u573a\u4efd\u989d\u5f88\u5c11\uff0c\u4eca\u5e74\u524d\u4e09\u4e2a\u5b63\u5ea6\u9500\u552e\u7d2f\u79ef\u624d\u4e86\u4e24\u767e\u4e07\u4ef6\u624b\u673a\uff0c\u5927\u6982\u624d\u5360\u5168\u7403\u624b\u673a\u9500\u91cf\u76841\uff05\uff0c\u5bf9\u4e8e\u666e\u901a\u624b\u673a\u5382\u5546\u6765\u8bf4\uff0c\u5728\u6d77\u5916\u5e02\u573a\u5df2\u7ecf\u6709\u4e86\u4e0d\u9519\u7684\u6210\u9519\uff0c\u4f46\u662f\u5f20\u606a\u7684\u76ee\u6807\u5c31\u662f\u8981\u5728\u660e\u5e74\u9500\u91cf\u8981\u8fdb\u5165\u5168\u7403\u524d\u516d\u5927\u624b\u673a\u5382\u5546\u4e4b\u5217\u3002</p><p>\u3000\u3000\u9884\u8ba1\u660e\u5e74\u5168\u7403\u7684\u624b\u673a\u9500\u552e\u80fd\u8fbe\u5230\u4e09\u4ebf\u4e94\u5343\u4e07\u90e8\uff0c\u9526\u6e56\u8981\u6324\u5165\u5168\u7403\u516d\u5927\u624b\u673a\u5382\u5546\u4e4b\u5217\uff0c\u4f9d\u9760\u4eca\u5e74\u7684\u5e02\u573a\u5360\u6709\u7387\u6807\u51c6\uff0c\u9526\u6e56\u660e\u5e74\u81f3\u5c11\u8981\u751f\u4ea7\u9500\u552e\u4e24\u5343\u56db\u767e\u4e07\u4ef6\u624b\u673a\u624d\u591f\u3002\u5230\u660e\u5e74\uff0c\u9526\u6e56\u81ea\u5df1\u7684\u5de5\u5382\u4f53\u7cfb\u624d\u80fd\u591f\u6ee1\u8db3\u4e00\u5343\u4e24\u767e\u4e07\u4ea7\u91cf\u9700\u6c42\uff0c\u5176\u4ed6\u7684\u751f\u4ea7\u76ee\u6807\u5c31\u8981\u91d1\u5c71\u7684\u624b\u673a\u5236\u9020\u57fa\u5730\u6765\u5b8c\u6210\u3002\u800c\u9526\u6e56\u4ee5ESS\u57fa\u9891\u82af\u7247\u6280\u672f\u62a2\u5360\u4e2d\u4f4e\u7aef\u624b\u673a\u82af\u7247\u5e02\u573a\u3001\u660e\u5e74\u6324\u5165\u5168\u7403\u516d\u5927\u624b\u673a\u82af\u7247\u4f9b\u5e94\u5546\u884c\u5217\u7684\u6218\u7565\u6784\u60f3\u4e5f\u9700\u8981\u91d1\u5c71\u5236\u9020\u57fa\u5730\u6765\u5b8c\u6210\u3002</p><p>\u3000\u3000\u867d\u7136\u91d1\u5c71\u7684\u624b\u673a\u5236\u9020\u57fa\u5730\u5efa\u8bbe\u4e3b\u8981\u7531\u65b0\u5143\u4e0e\u79d1\u738b\u9ad8\u79d1\u8054\u5408\u627f\u62c5\uff0c\u4f46\u4e8b\u5173\u9526\u6e56\u672a\u6765\u7684\u4f01\u4e1a\u6218\u7565\u53d1\u5c55\u65b9\u5411\uff0c\u660e\u5e74\u53c8\u5c06\u627f\u62c5\u4e09\u5343\u4e07\u53ea\u624b\u673a\u4ee5\u4e0a\u7684\u751f\u4ea7\u91cd\u4efb\uff0c\u5305\u62ec\u9648\u4fe1\u751f\u3001\u82cf\u6d25\u4e1c\u7b49\u9526\u6e56\u9ad8\u5c42\u90fd\u773c\u775b\u76ef\u7740\u91d1\u5c71\u90a3\u8fb9\uff0c\u4e0d\u5bb9\u51fa\u4e00\u4e01\u70b9\u7684\u5dee\u9519\u3002</p><p>\u3000\u3000\u81f3\u5c11\u5728\u91d1\u5c71\u624b\u673a\u5236\u9020\u57fa\u5730\u9879\u76ee\u5206\u62c6\u5f00\u59cb\u8fdb\u884c\u65f6\uff0c\u8fd8\u6ca1\u6709\u4eba\u80fd\u9884\u6d4b\u5230\u91d1\u5c71\u7684\u624b\u673a\u4ea7\u4e1a\u4f1a\u5728\u4e00\u4e24\u5e74\u95f4\u95f4\u7a81\u5d1b\u8d77\uff0c\u660e\u5e74\u5c31\u8981\u627f\u62c5\u4e09\u5343\u4e07\u5230\u4e94\u5343\u4e07\u53f0\u624b\u673a\u7684\u751f\u4ea7\u91cd\u4efb\u2014\u2014\u5dee\u4e0d\u591a\u662f\u4e94\u767e\u4ebf\u81f3\u4e00\u5343\u4ebf\u7684\u5de5\u4e1a\u4ea7\u503c\u3002</p></article>"}}}]);
