(window.webpackJsonp=window.webpackJsonp||[]).push([[218],{240:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u4e09\u8282 \u7ffb\u500d",content:"<article><h2>\u7b2c\u5341\u4e09\u8282 \u7ffb\u500d</h2><p>\u743c\u6069\u5408\u4e0a\u9b54\u6cd5\u4e66\uff0c\u653e\u5230\u4e00\u8fb9\uff0c\u9760\u7740\u5e8a\u5934\u5750\u7740\uff0c\u9ed8\u9ed8\u56de\u5fc6\u521a\u90a3\u624d\u6240\u7814\u8bfb\u7684\u6cd5\u672f\u3002\u6885\u83f2\u65af\u8f7b\u8f7b\u63a8\u95e8\u8fdb\u6765\uff0c\u7a7f\u7740\u65b0\u4e70\u7684\u7761\u8863\uff0c\u5934\u53d1\u8fd8\u662f\u6e7f\u6f09\u6f09\u7684\uff0c\u663e\u7136\u521a\u521a\u6d17\u6d74\u51fa\u6765\u3002\u5979\u722c\u4e0a\u5e8a\uff0c\u7136\u540e\u88ab\u743c\u6069\u4e00\u628a\u62b1\u8fdb\u6000\u91cc\uff0c\u624b\u5f00\u59cb\u4e0d\u8001\u5b9e\u5730\u5728\u8eab\u4e0a\u629a\u6478\u6e38\u8d70\uff0c\u6885\u83f2\u65af\u7565\u7565\u6323\u624e\u4e86\u4e0b\uff0c\u4fbf\u4e0d\u518d\u6297\u62d2\uff0c\u4e56\u4e56\u5730\u4efb\u4ed6\u8f7b\u8584\u3002</p><p>\u201c\u600e\u4e48\u50cf\u4e2a\u8272\u72fc\u4f3c\u7684\u3002\u201d\u5979\u5a07\u55d4\u7740\u3002</p><p>\u201c\u77ed\u6682\u79bb\u522b\uff0c\u5c24\u80dc\u65b0\u5a5a\u561b\u3002\u201d</p><p>\u201c\u77ed\u6682\u79bb\u522b\uff0c\u5c24\u80dc\u65b0\u5a5a\uff1f\u201d\u6885\u83f2\u65af\u91cd\u590d\u7740\uff0c\u201c\u633a\u6709\u8da3\u7684\uff0c\u8fd9\u662f\u4f60\u4eec\u9634\u9b42\u57ce\u7684\u8c1a\u8bed\u4e48\u3002\u201d</p><p>\u743c\u6069\u7565\u7565\u72b9\u8c6b\u4e86\u4e0b\uff0c\u4ed6\u539f\u672c\u53ea\u662f\u968f\u53e3\u628a\u201c\u5c0f\u522b\u80dc\u65b0\u5a5a\u201d\u7528\u8fd9\u4e2a\u4e16\u754c\u7684\u8bed\u8a00\u7ffb\u8bd1\u8fc7\u6765\uff0c\u4e5f\u6ca1\u591a\u60f3\u5176\u4ed6\u3002\u5982\u4eca\u88ab\u6885\u83f2\u65af\u8fd9\u4e00\u95ee\uff0c\u5018\u82e5\u8bf4\u662f\u9634\u9b42\u57ce\u7684\u8c1a\u8bed\uff0c\u4e07\u4e00\u5979\u548c\u73ca\u5609\u8bf4\u8d77\uff0c\u5c82\u4e0d\u5c31\u8981\u9732\u9985\uff1f\u5728\u8fd9\u79cd\u53ef\u80fd\u6d89\u53ca\u5230\u81ea\u5df1\u7a7f\u8d8a\u8005\u8eab\u4efd\u79d8\u5bc6\u7684\u4e8b\u60c5\u4e0a\uff0c\u743c\u6069\u5411\u6765\u6bd4\u8f83\u654f\u611f\u4e9b\uff0c\u4ed6\u60f3\u4e86\u60f3\uff0c\u6447\u5934\u8bf4\uff1a\u201c\u4e0d\u662f\uff0c\u6211\u4ee5\u524d\u5728\u4e00\u672c\u53e4\u7c4d\u4e0a\u770b\u5230\u7684\uff0c\u5012\u4e0d\u662f\u9634\u9b42\u57ce\u7684\u8c1a\u8bed\u3002\u201d</p><p>\u201c\u54e6\u3002\u201d</p><p>\u6885\u83f2\u65af\u5176\u5b9e\u4e5f\u6ca1\u591a\u60f3\uff0c\u53ea\u662f\u987a\u53e3\u4e00\u95ee\u2014\u2014\u4e8b\u5b9e\u4e0a\u5979\u4e5f\u6ca1\u529f\u592b\u53bb\u591a\u60f3\uff0c\u743c\u6069\u4f4e\u5934\u5728\u5979\u4fcf\u8138\u4e0a\u4eb2\u543b\u7740\uff0c\u624b\u4ece\u7761\u8863\u4e0b\u9762\u63a2\u8fdb\u53bb\uff0c\u8f7b\u8f7b\u63c9\u634f\u7740\u9165\u80f8\uff0c\u201c\u5514\uff0c\u600e\u4e48\u611f\u89c9\u53d8\u5927\u4e86\u70b9\u3002\u201d</p><p>\u201c\u771f\u7684\uff1f\u201d</p><p>\u201c\u55ef\uff0c\u521a\u8ba4\u8bc6\u4f60\u7684\u65f6\u5019\u597d\u50cf\u662fB\uff0c\u73b0\u5728\u5dee\u4e0d\u591a\u6709C\u4e86\u3002\u201d</p><p>\u8fd9\u4e2a\u4e16\u754c\u65e9\u5c31\u53d1\u660e\u4e86\u6587\u80f8\uff0c\u751a\u81f3\u90fd\u6709\u9b54\u6cd5\u6587\u80f8\uff0c\u81ea\u7136\u4e5f\u65e9\u5c31\u76f8\u5e94\u8bde\u751f\u4e86\u201c\u7f69\u676f\u201d\u7684\u6982\u5ff5\uff0c\u867d\u7136\u548c\u5730\u7403\u4e0a\u7684\u6807\u51c6\u4e0d\u5b8c\u5168\u4e00\u81f4\uff0c\u4f46\u4e5f\u76f8\u5dee\u65e0\u51e0\u3002\u743c\u6069\u4e00\u8fb9\u8bc4\u4ef7\u7740\uff0c\u4e00\u8fb9\u7528\u624b\u6307\u8f7b\u8f7b\u63c9\u634f\u90a3\u4e24\u9897\u84d3\u857e\uff0c\u611f\u89c9\u5979\u4eec\u5df2\u7ecf\u6084\u6084\u50b2\u7136\u575a\u633a\u8d77\u6765\u3002\u201c\u6211\u89c9\u5f97\u8fd9\u80af\u5b9a\u662f\u6211\u7ecf\u5e38\u6309\u6469\u7684\u529f\u52b3\u3002\u201d\u4ed6\u4e00\u672c\u6b63\u7ecf\u5730\u8bf4\u3002</p><p>\u6885\u83f2\u65af\u542c\u743c\u6069\u8fd9\u4e48\u8bf4\uff0c\u8138\u4e0a\u4e0d\u7531\u5f97\u6cdb\u7ea2\uff0c\u5979\u7ec8\u7a76\u662f\u521d\u5c1d\u60c5\u7231\u6ecb\u5473\u7684\u5c11\u5973\uff0c\u88ab\u60c5\u4eba\u8fd9\u6837\u4eb5\u73a9\u6311\u9017\uff0c\u54ea\u91cc\u627f\u53d7\u5f97\u4f4f\uff0c\u547c\u5438\u6e10\u6e10\u90fd\u53d8\u5f97\u6025\u4fc3\u8d77\u6765\u3002\u201c\u8ba8\u538c\uff01\u8fd9\u6709\u4f60\u4ec0\u4e48\u4e8b\u60c5\u2026\u2026\u201d</p><p>\u201c\u90a3\u4f60\u8bf4\u8bf4\u770b\uff0c\u5982\u679c\u4e0d\u662f\u56e0\u4e3a\u6211\u7ecf\u5e38\u6309\u6469\uff0c\u5979\u4eec\u600e\u4e48\u4f1a\u957f\u5927\u5f97\u8fd9\u4e48\u5feb\u3002\u201d</p><p>\u201c\u56e0\u4e3a\u2026\u2026\u56e0\u4e3a\u6211\u5728\u53d1\u80b2\u4e2d\u561b\uff0c\u201d\u5c11\u5973\u7406\u76f4\u6c14\u58ee\u5730\u56de\u7b54\uff0c\u201c\u518d\u8fc7\u51e0\u5929\u6211\u5c31\u5341\u4e94\u5c81\u4e86\u5462\u3002\u201d</p><p>\u201c\u5514\uff0c\u5341\u4e94\u5c81\u4e86\u554a\u3002\u201d\u743c\u6069\u70b9\u70b9\u5934\uff0c\u5f00\u59cb\u8003\u8651\u8981\u4e0d\u8981\u8d81\u7740\u751f\u65e5\u628a\u5979\u7ed9\u5f7b\u5e95\u5403\u4e86\u3002\u8fd9\u4e2a\u4e16\u754c\u7684\u4eba\u7c7b\u53d1\u80b2\u666e\u904d\u6bd4\u8f83\u5feb\uff0c\u50cf\u9634\u9b42\u57ce\u4e2d\uff0c\u5341\u4e09\u5c81\u5c31\u7b97\u6210\u5e74\uff0c\u743c\u6069\u7b2c\u4e00\u6b21\u628a\u8299\u857e\u72c4\u5f04\u4e0a\u5e8a\u7684\u65f6\u5019\uff0c\u5979\u4e5f\u624d\u5341\u56db\u5c81\u3002\u6885\u83f2\u65af\u5feb\u5230\u5341\u4e94\u5c81\uff0c\u5df2\u7ecf\u662f\u4e0d\u6298\u4e0d\u6263\u7684\u5c11\u5973\u4e86\u3002</p><p>\u201c\u60f3\u4ec0\u4e48\u5462\uff1f\u4e00\u8138\u574f\u7b11\uff0c\u201d\u6885\u83f2\u65af\u6297\u8bae\uff0c\u201c\u53c8\u5728\u6253\u4ec0\u4e48\u9b3c\u4e3b\u610f\uff1f\u201d</p><p>\u201c\u54ea\u6709\uff0c\u201d\u743c\u6069\u8868\u793a\u65e0\u8f9c\uff0c\u201c\u6211\u4e0d\u8fc7\u662f\u5728\u8003\u8651\uff0c\u5982\u4f55\u624d\u80fd\u66f4\u52a0\u6df1\u5165\u5730\u4e86\u89e3\u5bf9\u65b9\u3002\u201d</p><p>\u4ed6\u628a\u201c\u6df1\u5165\u201d\u8fd9\u4e2a\u8bcd\u7684\u53d1\u97f3\u54ac\u5f97\u5f88\u91cd\uff0c\u6885\u83f2\u65af\u5f53\u7136\u660e\u767d\u4ed6\u7684\u610f\u601d\u3002\u201c\u4f60\u8fd8\u4e0d\u591f\u6df1\u5165\u554a\uff1f\u201d\u5979\u77aa\u4e86\u743c\u6069\u4e00\u773c\uff0c\u201c\u6bcf\u6b21\u5728\u6211\u8eab\u4f53\u6a2a\u51b2\u76f4\u649e\u7684\u90a3\u4e1c\u897f\u662f\u8c01\u7684\uff1f\u201d</p><p>\u201c\u4f46\u8fd8\u5dee\u4e00\u4e2a\u5730\u65b9\u6ca1\u63a2\u7d22\u8fc7\u5462\uff1f\u201d\u743c\u6069\u5728\u5979\u8033\u8fb9\u8f7b\u8f7b\u8bf4\u3002</p><p>\u6885\u83f2\u65af\u8138\u8272\u5fae\u5fae\u4e00\u9eef\uff0c\u4f46\u743c\u6069\u6b63\u4f4e\u5934\u4eb2\u543b\u5979\uff0c\u4e5f\u6ca1\u6709\u5728\u610f\u3002\u201c\u4f60\u56de\u8ff7\u65af\u5353\u8bfa\u89c1\u5230\u5927\u4e3b\u6559\u4e86\uff1f\u201d</p><p>\u201c\u55ef\uff0c\u89c1\u5230\u4e86\u3002\u201d</p><p>\u201c\u4ed6\u600e\u4e48\u8bf4\uff1f\u201d</p><p>\u201c\u4e5f\u6ca1\u8bf4\u4ec0\u4e48\u3002\u201d\u6885\u83f2\u65af\u6de1\u6de1\u5e94\u7740\u3002</p><p>\u743c\u6069\u89c1\u5979\u8bed\u6c14\u9690\u7136\u4e0d\u5feb\uff0c\u5fc3\u60f3\u5979\u5bf9\u88ab\u5f00\u9664\u6559\u7c4d\u7684\u4e8b\u60c5\u7ec8\u7a76\u8fd8\u662f\u803f\u803f\u4e8e\u6000\uff0c\u4e5f\u4fbf\u4e0d\u518d\u591a\u95ee\u3002\u201c\u6ca1\u4e8b\u5566\uff0c\u201d\u4ed6\u5b89\u6170\u7740\uff0c\u201c\u8fc7\u53bb\u7684\u5c31\u90fd\u8fc7\u53bb\u4e86\uff0c\u6709\u6211\u5728\u8eab\u8fb9\u966a\u7740\u4f60\u5462\u3002\u201d</p><p>\u201c\u55ef\uff0c\u201d\u6885\u83f2\u65af\u5c06\u5934\u501a\u9760\u5728\u4ed6\u80a9\u4e0a\uff0c\u6e29\u987a\u5730\u504e\u4f9d\u7740\uff0c\u201c\u4f60\u4f1a\u4e00\u76f4\u966a\u7740\u6211\u4e48\u3002\u201d</p><p>\u201c\u4f1a\u3002\u201d</p><p>\u201c\u4f1a\u4e00\u76f4\u559c\u6b22\u6211\uff1f\u201d</p><p>\u201c\u4f1a\u3002\u201d</p><p>\u201c\u771f\u7684\uff1f\u201d</p><p>\u201c\u771f\u7684\u3002\u201d</p><p>\u201c\u53d1\u8a93\u3002\u201d</p><p>\u201c\u6211\u53d1\u8a93\uff0c\u201d\u743c\u6069\u543b\u7740\u5979\u7684\u989d\u5934\uff0c\u201c\u6c38\u8fdc\u6c38\u8fdc\u7231\u4f60\u3002\u201d</p><p>\u5c11\u5973\u5ae3\u7136\u5fae\u7b11\uff0c\u201c\u771f\u641e\u4e0d\u61c2\u5462\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u5c31\u542c\u4f60\u8fd9\u4e48\u54c4\u51e0\u53e5\uff0c\u5fc3\u91cc\u5c31\u751c\u751c\u7684\u3002\u201d</p><p>\u8fd9\u5c31\u662f\u7231\u60c5\u554a\uff0c\u50bb\u4e2b\u5934\u3002</p><p>\u201c\u5bf9\u4e86\uff0c\u743c\u6069\uff0c\u201d\u6885\u83f2\u65af\u60f3\u8d77\u4ef6\u4e8b\u60c5\u6765\uff0c\u201c\u4f60\u4e70\u4e0b\u90a3\u4e2a\u5973\u5b69\u5b50\u505a\u4ec0\u4e48\uff1f\u201d</p><p>\u4eca\u5929\u767d\u5929\u5728\u585e\u5c14\uff0c\u743c\u6069\u4e70\u4e0b\u4e86\u8fea\u74e6\u514b\u548c\u83f2\u4e9a\u5144\u59b9\u2014\u2014\u800c\u4e14\u662f\u81ea\u5df1\u638f\u94b1\uff0c\u5e76\u975e\u7528\u9634\u9b42\u57ce\u7684\u516c\u6b3e\u3002\u5982\u6b64\u4e00\u6765\uff0c\u8fd9\u4e24\u4eba\u5c31\u6210\u4e3a\u743c\u6069\u7684\u5974\u96b6\uff0c\u6210\u4e3a\u4ed6\u7684\u79c1\u4eba\u8d22\u4ea7\u3002\u4e0d\u4ec5\u5982\u6b64\uff0c\u743c\u6069\u6ca1\u6709\u628a\u4ed6\u4eec\u5e26\u56de\u9634\u9b42\u57ce\uff0c\u800c\u662f\u76f4\u63a5\u5728\u585e\u5c14\u4e70\u4e86\u4e2a\u4f4f\u5904\uff0c\u8ba9\u4ed6\u4eec\u4f4f\u8fdb\u53bb\u3002\u8fd9\u8ba9\u6885\u83f2\u65af\u975e\u5e38\u7591\u60d1\uff0c\u4e0d\u77e5\u9053\u4ed6\u5230\u5e95\u60f3\u505a\u4ec0\u4e48\uff0c\u53ea\u662f\u4e00\u76f4\u4e0d\u597d\u8be2\u95ee\uff0c\u76f4\u5230\u73b0\u5728\u591c\u6df1\u4eba\u9759\uff0c\u4e24\u4eba\u72ec\u5904\uff0c\u5979\u624d\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u95ee\u4e86\u51fa\u6765\u3002</p><p>\u743c\u6069\u5fae\u5fae\u7b11\u7b11\uff0c\u8f7b\u8f7b\u5728\u5979\u8138\u86cb\u4e0a\u634f\u4e86\u634f\uff0c\u611f\u53d7\u90a3\u5439\u5f39\u53ef\u7834\u7684\u67d4\u5ae9\u808c\u80a4\uff0c\u201c\u653e\u5fc3\u597d\u4e86\uff0c\u827e\u5f25\u8587\uff0c\u6709\u4f60\u8fd9\u6837\u7684\u7edd\u8272\u7f8e\u4eba\u513f\u5728\u8eab\u8fb9\uff0c\u96be\u9053\u6211\u8fd8\u4f1a\u770b\u4e0a\u5979\u4e0d\u6210\u3002\u201d</p><p>\u201c\u6211\u77e5\u9053\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u6240\u4ee5\u6211\u624d\u5947\u602a\u4f60\u5230\u5e95\u6253\u4ec0\u4e48\u4e3b\u610f\u554a\u3002\u201d</p><p>\u743c\u6069\u72b9\u8c6b\u7740\uff0c\u8003\u8651\u8981\u4e0d\u8981\u5bf9\u5979\u8bf4\uff0c\u4f46\u6700\u7ec8\u8fd8\u662f\u51b3\u5b9a\u5148\u6682\u4e14\u7792\u7740\uff0c\u201c\u662f\u6709\u70b9\u4e8b\u60c5\uff0c\u827e\u5f25\u8587\uff0c\u201d\u4ed6\u8bf4\uff0c\u629a\u6478\u7740\u5c11\u5973\u7684\u91d1\u53d1\uff0c\u201c\u4f46\u6211\u6682\u65f6\u4e0d\u65b9\u4fbf\u8bf4\uff0c\u4ee5\u540e\u518d\u544a\u8bc9\u4f60\uff0c\u597d\u4e48\u3002\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u6885\u83f2\u65af\u70b9\u70b9\u5934\uff0c\u6ca1\u6709\u518d\u8ffd\u95ee\u3002\u6bcf\u4e2a\u4eba\u90fd\u6709\u5404\u81ea\u7684\u79d8\u5bc6\uff0c\u5c31\u7b97\u662f\u81ea\u5df1\uff0c\u4e0d\u4e5f\u6b63\u6709\u4e8b\u60c5\u9690\u7792\u7740\u4ed6\u4e48\u3002\u81f3\u5c11\uff0c\u4ed6\u8fd8\u80fd\u5766\u7136\u627f\u8ba4\uff0c\u800c\u81ea\u5df1\u5374\u662f\u8fde\u8fd9\u70b9\u90fd\u505a\u4e0d\u5230\u5427\u3002</p><p>\u4e24\u4eba\u7684\u8eab\u4f53\u7d27\u8d34\u5728\u4e00\u8d77\uff0c\u6885\u83f2\u65af\u6e05\u695a\u611f\u89c9\u7537\u4eba\u4e0b\u8eab\u6709\u575a\u786c\u706b\u70ed\u7684\u4e1c\u897f\u6084\u6084\u633a\u7acb\uff0c\u6b63\u62b5\u5728\u81ea\u5df1\u5927\u817f\u5185\u4fa7\uff0c\u867d\u7136\u9694\u7740\u7761\u8863\uff0c\u4f46\u90a3\u70bd\u70ed\u7684\u6c14\u606f\u4f9d\u65e7\u6e05\u695a\u5730\u4f20\u6765\uff0c\u70eb\u5f97\u81ea\u5df1\u4e00\u9635\u9635\u7684\u5fc3\u614c\u610f\u4e71\u3002\u201c\u60f3\u8981\u4e86\uff1f\u201d\u5979\u4f4e\u58f0\u95ee\u3002</p><p>\u201c\u55ef\u3002\u201d</p><p>\u6885\u83f2\u65af\u6ca1\u6709\u8bf4\u8bdd\uff0c\u6e29\u987a\u5730\u5c06\u81ea\u5df1\u7684\u7761\u8863\u892a\u53bb\uff0c\u8dea\u4f0f\u5728\u743c\u6069\u817f\u95f4\uff0c\u4f38\u624b\u5c06\u90a3\u65e9\u5df2\u7ecf\u6309\u637a\u4e0d\u4f4f\u7684\u5c0f\u5bb6\u4f19\u91ca\u653e\u51fa\u6765\uff0c\u4f4e\u5934\u542b\u8fdb\u53e3\u4e2d\u3002\u5979\u4e00\u70b9\u4e00\u70b9\u5730\u541e\u54bd\u7740\uff0c\u820c\u5c16\u7075\u5de7\u5730\u8214\u8210\uff0c\u611f\u89c9\u5b83\u50cf\u82cf\u9192\u4e00\u822c\uff0c\u6025\u901f\u81a8\u80c0\u8d77\u6765\u3002\u743c\u6069\u8212\u670d\u5730\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u95ed\u4e0a\u773c\u775b\u6162\u6162\u4eab\u53d7\u7740\uff0c\u624b\u8f7b\u8f7b\u629a\u6478\u7740\u5979\u7684\u79c0\u53d1\uff0c\u8868\u793a\u9f13\u52b1\u3002</p><p>\u201c\u827e\u5f25\u8587\uff0c\u4f60\u7684\u6280\u5de7\u8d8a\u6765\u8d8a\u597d\u4e86\u5462\u3002\u201d\u4ed6\u8c03\u7b11\u7740\u3002</p><p>\u201c\u5514\u3002\u201d\u5c11\u5973\u542b\u7cca\u5730\u5e94\u7740\u3002</p><p>\u201c\u8fd8\u8bb0\u5f97\u4f60\u7b2c\u4e00\u6b21\u2026\u2026\u90a3\u65f6\u5019\u4f60\u53ef\u662f\u5f88\u4e0d\u60c5\u613f\u7684\u5462\uff0c\u8c01\u60f3\u5230\u73b0\u5728\u8fd9\u4e48\u4e3b\u52a8\u2026\u2026\u54ce\u54df\uff01\u201d</p><p>\u6885\u83f2\u65af\u8f7b\u8f7b\u54ac\u4e86\u4ed6\u4e00\u53e3\uff0c\u62ac\u773c\u77aa\u7740\u4ed6\uff0c\u201c\u518d\u6562\u8bf4\uff0c\u6211\u5c31\u628a\u5b83\u54ac\u65ad\u3002\u201d</p><p>\u201c\u5f00\u73a9\u7b11\u5566\u3002\u201d</p><p>\u6885\u83f2\u65af\u4e0d\u518d\u7406\u4f1a\u743c\u6069\uff0c\u5979\u7ec6\u5fc3\u5730\u541e\u5410\u7740\uff0c\u5f88\u5feb\u5c31\u611f\u89c9\u81ea\u5df1\u7684\u5c0f\u5634\u5df2\u7ecf\u5bb9\u7eb3\u4e0d\u4e0b\u3002\u5979\u6162\u6162\u5410\u51fa\u6765\uff0c\u8f6c\u8fc7\u8eab\uff0c\u8dea\u4f0f\u5728\u5e8a\u4e0a\uff0c\u633a\u8d77\u81c0\u90e8\uff0c\u7b49\u5f85\u7740\u7537\u4eba\u7684\u4fb5\u72af\u3002</p><p>\u4f46\u743c\u6069\u6ca1\u6709\u52a8\uff0c\u4ed6\u53ea\u662f\u8f7b\u8f7b\u62cd\u4e86\u62cd\u5c11\u5973\u7684\u96ea\u81c0\uff0c\u5c06\u5979\u62c9\u5230\u6000\u91cc\u6765\u3002\u201c\u6211\u4eec\u6362\u4e2a\u59ff\u52bf\uff0c\u201d\u4ed6\u8f7b\u8f7b\u8bf4\uff0c\u201c\u4eca\u665a\u4f60\u4e3b\u52a8\u70b9\u3002\u201d</p><p>\u201c\u600e\u4e48\u4e3b\u52a8\uff1f\u201d\u6885\u83f2\u65af\u6709\u4e9b\u4e0d\u89e3\u3002</p><p>\u743c\u6069\u4e0d\u6000\u597d\u610f\u5730\u7b11\u7740\uff0c\u201c\u81ea\u5df1\u4e0a\u6765\u3002\u201d</p><p>\u6885\u83f2\u65af\u4e00\u6014\uff0c\u968f\u5373\u660e\u767d\u4e86\u743c\u6069\u7684\u610f\u601d\uff0c\u5979\u6709\u4e9b\u8e0c\u8e87\uff0c\u56e0\u4e3a\u4ee5\u524d\u4ece\u672a\u5c1d\u8bd5\u8fc7\u8fd9\u79cd\u59ff\u52bf\uff0c\u4e00\u65f6\u95f4\u4e0d\u77e5\u6240\u63aa\uff0c\u4f46\u770b\u89c1\u743c\u6069\u773c\u4e2d\u7684\u7b11\u610f\uff0c\u5374\u6fc0\u8d77\u4e86\u5c11\u5973\u7684\u597d\u5f3a\u5fc3\u7406\u3002\u5979\u5206\u5f00\u817f\uff0c\u8de8\u5750\u5728\u7537\u4eba\u8170\u4e0a\uff0c\u5bf9\u51c6\u4f4d\u7f6e\u4e00\u70b9\u4e00\u70b9\u5730\u5750\u4e0b\u53bb\u3002</p><p>\u201c\u597d\u5927\u2026\u2026\u201d\u5979\u5fcd\u4e0d\u4f4f\u547b\u541f\u7740\u3002</p><p>\u4f34\u968f\u7740\u8f7b\u5fae\u80c0\u75db\u548c\u9178\u9ebb\u7684\u6655\u7729\u611f\uff0c\u50cf\u6f6e\u6c34\u4f3c\u7684\u4e00\u6ce2\u6ce2\u51b2\u51fb\u7740\u5979\u7684\u610f\u8bc6\uff0c\u5c11\u5973\u60f3\u8d77\u8eab\u52a8\u4f5c\uff0c\u5374\u53ea\u89c9\u8170\u817f\u9178\u8f6f\uff0c\u600e\u4e48\u4e5f\u63d0\u4e0d\u8d77\u534a\u70b9\u529b\u6c14\uff0c\u53ea\u80fd\u7d27\u7d27\u62b1\u7740\u8eab\u524d\u7684\u7537\u4eba\uff0c\u5c06\u8138\u8d34\u5728\u4ed6\u7684\u80a9\u5934\u4e0a\u8f7b\u8f7b\u5598\u606f\u3002\u743c\u6069\u4e5f\u4e0d\u6025\u4e8e\u52a8\u4f5c\uff0c\u6162\u6162\u4eab\u53d7\u7740\u5c11\u5973\u4f53\u5185\u7684\u706b\u70ed\u6eda\u70eb\u3002</p><p>\u201c\u505a\u5f97\u4e0d\u9519\uff0c\u827e\u5f25\u8587\uff0c\u201d\u4ed6\u5938\u5956\u7740\uff0c\u201c\u4f46\u8fd8\u4e0d\u591f\u5462\u3002\u201d</p><p>\u4ed6\u53cc\u624b\u6258\u7740\u5c11\u5973\u7684\u96ea\u81c0\uff0c\u6162\u6162\u5c06\u5979\u7684\u8eab\u4f53\u62ac\u8d77\uff0c\u8ba9\u81ea\u5df1\u4ece\u5979\u4f53\u5185\u4e00\u70b9\u70b9\u9000\u51fa\uff0c\u7136\u540e\u7a81\u7136\u677e\u5f00\u624b\uff0c\u8ba9\u5979\u91cd\u91cd\u843d\u4e0b\u3002\u6885\u83f2\u65af\u611f\u89c9\u81ea\u5df1\u4eff\u4f5b\u9aa4\u7136\u95f4\u88ab\u4e00\u4e2a\u70bd\u70ed\u575a\u786c\u7684\u70d9\u94c1\u7a7f\u900f\uff0c\u5404\u79cd\u8bf4\u4e0d\u51fa\u7684\u611f\u89c9\u6df7\u6742\u5728\u4e00\u8d77\u6d8c\u6765\uff0c\u8ba9\u5979\u521a\u521a\u79ef\u6512\u8d77\u7684\u4e00\u70b9\u529b\u6c14\u53c8\u518d\u5ea6\u6d88\u6563\u3002\u201c\u574f\u2026\u2026\u574f\u86cb\uff01\u201d\u5979\u5a07\u5598\u7740\uff0c\u5374\u72b9\u81ea\u4e0d\u80af\u8ba4\u8f93\uff0c\u6162\u6162\u8c03\u6574\u547c\u5438\uff0c\u6b47\u4e86\u4e00\u4f1a\uff0c\u6e10\u6e10\u6062\u590d\u4e86\u4e9b\u4f53\u529b\uff0c\u5f00\u59cb\u4e0a\u4e0b\u8038\u52a8\u3002</p><p>\u8fd9\u79cd\u59ff\u52bf\u975e\u5e38\u8017\u8d39\u4f53\u529b\uff0c\u6885\u83f2\u65af\u867d\u7136\u5e38\u5e74\u4e60\u6b66\uff0c\u4f46\u521d\u7ecf\u6b64\u9053\uff0c\u6e10\u6e10\u4e5f\u6709\u4e9b\u627f\u53d7\u4e0d\u4f4f\uff0c\u8170\u817f\u6e10\u6e10\u9178\u8f6f\uff0c\u5598\u606f\u58f0\u4e5f\u8d8a\u6765\u8d8a\u91cd\uff0c\u53ea\u662f\u5979\u751f\u6027\u597d\u5f3a\uff0c\u89c1\u743c\u6069\u5c1a\u672a\u53d1\u6cc4\uff0c\u4fbf\u54ac\u7259\u786c\u6491\u7740\u4e0d\u80af\u8ba4\u8f93\u3002\u743c\u6069\u6b63\u95ed\u76ee\u4eab\u53d7\uff0c\u5ffd\u7136\u89c9\u5f97\u8eab\u4e0a\u5c11\u5973\u7684\u52a8\u4f5c\u6e10\u6e10\u8fdf\u7f13\uff0c\u7741\u773c\u4e00\u770b\u89c1\u5979\u8138\u8272\u6709\u4e9b\u82cd\u767d\uff0c\u989d\u5934\u4e0a\u6c57\u73e0\u70b9\u70b9\u6ef4\u4e0b\uff0c\u60c5\u77e5\u662f\u4f53\u529b\u4e0d\u7ee7\uff0c\u8fde\u5fd9\u5c06\u5979\u62b1\u4f4f\uff0c\u6402\u5728\u6000\u91cc\u4eb2\u543b\u3002</p><p>\u201c\u50bb\u4e2b\u5934\uff0c\u7d2f\u4e86\u5c31\u5148\u4f11\u606f\u4f1a\u554a\u3002\u201d</p><p>\u201c\u6ca1\u4e8b\u2026\u2026\u201d</p><p>\u5979\u8fd8\u8981\u518d\u52a8\uff0c\u88ab\u743c\u6069\u6309\u4f4f\u4e86\uff0c\u201c\u542c\u8bdd\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u4f11\u606f\u4f1a\u3002\u201d</p><p>\u5c11\u5973\u5fc3\u91cc\u6709\u4e9b\u751c\u4e1d\u4e1d\u7684\uff0c\u4e56\u4e56\u6402\u7740\u743c\u6069\u7684\u8116\u9888\uff0c\u9762\u5bf9\u9762\u5750\u5728\u4ed6\u8eab\u4e0a\uff0c\u8eab\u4f53\u91cc\u8fd8\u5305\u5bb9\u7740\u7537\u4eba\u706b\u70ed\u7684\u6b32\u671b\uff0c\u6eda\u70eb\u5f97\u4eff\u4f5b\u8981\u8ba9\u81ea\u5df1\u90fd\u70e7\u8d77\u6765\u4f3c\u7684\u3002\u201c\u5b83\u600e\u4e48\u53d8\u5f97\u8fd9\u4e48\u5927\uff0c\u201d\u5979\u57cb\u6028\u7740\uff0c\u201c\u611f\u89c9\u6574\u4e2a\u4eba\u90fd\u5feb\u8981\u88ab\u4f60\u7ed9\u6491\u88c2\u5f00\u4e86\u3002\u201d</p><p>\u201c\u56e0\u4e3a\u5b83\u51e0\u5929\u6ca1\u6709\u5c1d\u8fc7\u4f60\u4e86\u561b\u3002\u201d\u743c\u6069\u8bf4\uff0c\u543b\u7740\u5979\u7684\u8138\u86cb\uff0c\u7528\u6795\u5dfe\u66ff\u5979\u5c06\u989d\u5934\u4e0a\u7684\u6c57\u73e0\u62ed\u53bb\u3002\u6885\u83f2\u65af\u6e29\u987a\u5730\u4eab\u53d7\u7740\u60c5\u4eba\u7684\u6e29\u67d4\uff0c\u4e00\u65f6\u95f4\u4e24\u4eba\u90fd\u6ca1\u6709\u8bf4\u8bdd\u3002</p><p>\u201c\u827e\u5f25\u8587\uff0c\u201d\u743c\u6069\u4e00\u76f4\u72b9\u8c6b\u7740\uff0c\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u5f00\u53e3\uff0c\u201c\u548c\u4f60\u5546\u91cf\u4ef6\u4e8b\u60c5\u597d\u4e48\u3002\u201d</p><p>\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d\u6885\u83f2\u65af\u6709\u4e9b\u8be7\u5f02\u3002</p><p>\u201c\u90a3\u4e2a\uff0c\u6211\u60f3\u6709\u7a7a\u53bb\u966a\u966a\u59d0\u59d0\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u73ca\u5609\u8eab\u4f53\u5f31\uff0c\u80c6\u5b50\u53c8\u5c0f\uff0c\u665a\u4e0a\u4e00\u4e2a\u4eba\u603b\u7761\u4e0d\u7740\uff0c\u4e60\u60ef\u4e86\u6211\u966a\u7740\uff0c\u4ee5\u524d\u8fd9\u4e48\u591a\u5e74\u90fd\u662f\u8fd9\u6837\u8fc7\u6765\u7684\uff0c\u73b0\u5728\u2026\u2026\u201d</p><p>\u6885\u83f2\u65af\u9759\u9759\u51dd\u89c6\u7740\u4ed6\uff0c\u7a81\u7136\u5fae\u5fae\u7b11\u4e86\u4e00\u7b11\uff0c\u743c\u6069\u5fc3\u4e2d\u6709\u4e9b\u5fd0\u5fd1\u4e0d\u5b89\uff0c\u6015\u5979\u662f\u4e0d\u662f\u770b\u51fa\u4e86\u4ec0\u4e48\u540d\u5802\u3002\u201c\u6ca1\u5173\u7cfb\u554a\uff0c\u201d\u5c11\u5973\u67d4\u58f0\u8bf4\uff0c\u201c\u4f46\u5982\u679c\u4f60\u90fd\u966a\u7740\u73ca\u5609\u59d0\u59d0\uff0c\u51b7\u843d\u4e86\u6211\uff0c\u6211\u53ef\u4e5f\u662f\u4f1a\u751f\u6c14\u7684\u5462\u3002\u201d</p><p>\u201c\u6211\u662f\u8bf4\u2026\u2026\u201d</p><p>\u201c\u4e00\u4eba\u4e00\u5929\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u4f60\u966a\u73ca\u5609\u59d0\u59d0\u4e00\u5929\uff0c\u5c31\u8981\u966a\u6211\u4e00\u5929\u3002\u6211\u4e0d\u62a2\u5979\u7684\u5f1f\u5f1f\uff0c\u4f46\u5979\u4e5f\u4e0d\u80fd\u62a2\u6211\u7684\u60c5\u4eba\u3002\u201d</p><p>\u743c\u6069\u677e\u4e86\u53e3\u6c14\uff0c\u201c\u597d\u3002\u201d</p><p>\u201c\u522b\u7b54\u5e94\u90a3\u4e48\u5feb\uff0c\u8fd8\u6709\u4e2a\u6761\u4ef6\u5462\u3002\u201d</p><p>\u201c\u4ec0\u4e48\u6761\u4ef6\uff1f\u201d</p><p>\u201c\u4ee5\u524d\u6211\u4eec\u6bcf\u5929\u665a\u4e0a\u4f1a\u505a\u51e0\u6b21\u7684\uff1f\u4e09\u6b21\u8fd8\u662f\u56db\u6b21\uff1f\u5c31\u7b97\u4e09\u6b21\u597d\u4e86\u3002\u73b0\u5728\u65e2\u7136\u5c11\u966a\u6211\u4e00\u534a\u7684\u65f6\u95f4\uff0c\u90a3\u6b21\u6570\u5c31\u8981\u7ffb\u500d\u8865\u56de\u6765\u3002\u201d</p><p>\u201c\u7ffb\u2026\u2026\u7ffb\u500d\uff1f\u827e\u5f25\u8587\uff0c\u522b\u5f00\u73a9\u7b11\u2026\u2026\u201d</p><p>\u201c\u8c01\u8ddf\u4f60\u5f00\u73a9\u7b11\u4e86\uff1f\u201d\u5c11\u5973\u8f7b\u8f7b\u54ac\u7740\u4ed6\u7684\u8033\u5782\uff0c\u53cc\u81c2\u62b1\u7740\u8116\u9888\uff0c\u8170\u817f\u6162\u6162\u7528\u529b\uff0c\u7f13\u7f13\u8ba9\u4ed6\u4ece\u81ea\u5df1\u4f53\u5185\u9000\u51fa\uff0c\u51c6\u5907\u5f00\u59cb\u53d1\u52a8\u4e0b\u4e00\u8f6e\u653b\u51fb\uff0c\u201c\u8bb0\u7740\u54e6\uff0c\u8fd9\u624d\u662f\u7b2c\u4e00\u6b21\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u867d\u7136\u4e89\u53d6\u5230\u4e86\u4e00\u534a\u7684\u65f6\u95f4\u53ef\u4ee5\u7528\u6765\u966a\u73ca\u5609\uff0c\u4f46\u4ee3\u4ef7\u5c31\u662f\u743c\u6069\u7b2c\u4e8c\u5929\u65e9\u4e0a\u5dee\u70b9\u722c\u4e0d\u8d77\u5e8a\uff0c\u53ea\u89c9\u8170\u817f\u9178\u75db\u5f97\u50cf\u662f\u6628\u5929\u521a\u521a\u8dd1\u4e86\u4e00\u573a\u9a6c\u62c9\u677e\u6bd4\u8d5b\u3002\u6885\u83f2\u65af\u662f\u4e2a\u4fe1\u5b88\u627f\u8bfa\u7684\u5973\u5b69\u5b50\uff0c\u679c\u7136\u8bf4\u5230\u505a\u5230\uff0c\u6574\u6574\u8981\u4e86\u516d\u6b21\uff0c\u8ba9\u4ed6\u6700\u540e\u51e0\u4e4e\u90fd\u8981\u786c\u4e0d\u8d77\u6765\u4e86\uff0c\u5b8c\u5168\u662f\u9760\u7740\u5c11\u5973\u7684\u5507\u820c\u670d\u4f8d\u624d\u52c9\u5f3a\u91cd\u6574\u96c4\u98ce\uff0c\u5b8c\u6210\u4efb\u52a1\u3002</p><p>\u5b9e\u9645\u4e0a\uff0c\u6885\u83f2\u65af\u7684\u60c5\u51b5\u4e5f\u6ca1\u597d\u5230\u54ea\u91cc\u53bb\uff0c\u88ab\u72c2\u66b4\u8e42\u8e8f\u7684\u5730\u65b9\u7ea2\u80bf\u80c0\u75db\uff0c\u8fd8\u4f34\u968f\u7740\u8f7b\u5ea6\u808c\u8089\u6495\u88c2\u62c9\u4f24\uff0c\u8fde\u8d77\u8eab\u7a7f\u8863\u90fd\u56f0\u96be\uff0c\u76f4\u5230\u743c\u6069\u51fa\u95e8\u7684\u65f6\u5019\uff0c\u5979\u8fd8\u8eb2\u5728\u623f\u95f4\u91cc\u4e0d\u597d\u610f\u601d\u51fa\u6765\u3002\u7136\u800c\u5979\u6bd4\u743c\u6069\u7684\u4f18\u52bf\u5728\u4e8e\uff1a\u5979\u4e0d\u7528\u4e0a\u73ed\u3002</p><p>\u8fd9\u5c31\u662f\u7537\u4eba\u7684\u60b2\u54c0\u554a\u3002</p><p>\u201c\u4e0d\u884c\u4e86\uff0c\u8fd9\u6837\u4e0b\u53bb\u7684\u8bdd\uff0c\u6839\u672c\u652f\u6491\u4e0d\u4e86\u4e09\u5929\u5c31\u8981\u5b8c\u86cb\u3002\u201d\u743c\u6069\u4e00\u8fb9\u63c9\u7740\u81ea\u5df1\u7684\u8170\u4e00\u8fb9\u53f9\u6c14\uff0c\u4ed6\u4ece\u6ca1\u5982\u6b64\u6df1\u5207\u5730\u4f53\u4f1a\u5230\u81ea\u5df1\u4f53\u8d28\u7684\u865a\u5f31\uff0c\u7136\u800c\u4e8b\u5df2\u81f3\u6b64\uff0c\u953b\u70bc\u662f\u6765\u4e0d\u53ca\u4e86\uff0c\u53ea\u80fd\u53e6\u60f3\u529e\u6cd5\u3002</p><p>\u8eab\u4e3a\u7530\u4f2f\u5149\u7684\u4f20\u4eba\uff0c\u5c45\u7136\u4f1a\u4e3a\u5728\u5e8a\u4e0a\u5bf9\u4ed8\u4e0d\u4e86\u4e00\u4e2a\u5973\u4eba\u800c\u82e6\u607c\uff0c\u8bf4\u51fa\u53bb\u771f\u8981\u4ee4\u4eba\u803b\u7b11\u3002\u7136\u800c\u8fd9\u4e5f\u6ca1\u529e\u6cd5\uff0c\u5bb6\u91cc\u867d\u7136\u6709\u4e24\u4e2a\u7edd\u8272\u7f8e\u5973\uff0c\u4e00\u4e2a\u662f\u81ea\u5df1\u59d0\u59d0\uff0c\u6682\u65f6\u4e0d\u80fd\u63a8\u5012\uff0c\u4e00\u4e2a\u662f\u60c5\u4eba\uff0c\u5374\u53ea\u80fd\u8d70\u540e\u95e8\u3002\u800c\u518d\u5f3a\u52b2\u7684\u91c7\u8865\u672f\uff0c\u4e5f\u6ca1\u529e\u6cd5\u901a\u8fc7\u8d70\u540e\u95e8\u6765\u8fdb\u884c\uff0c\u8fd9\u7740\u5b9e\u8ba9\u743c\u6069\u611f\u89c9\u82f1\u96c4\u65e0\u7528\u6b66\u4e4b\u5730\u3002</p><p>\u201c\u7b97\u4e86\uff0c\u5824\u5185\u635f\u5931\u5824\u5916\u8865\uff0c\u5982\u679c\u81ea\u5df1\u7684\u731c\u6d4b\u8bc1\u5b9e\uff0c\u5728\u83f2\u4e9a\u8eab\u4e0a\u8bd5\u9a8c\u6210\u529f\uff0c\u90a3\u4e48\u5341\u4e2a\u827e\u5f25\u8587\u90fd\u4e0d\u5728\u8bdd\u4e0b\u3002\u201d</p><p>\u62b1\u7740\u8fd9\u79cd\u5ff5\u5934\uff0c\u4ed6\u8d70\u8fdb\u4e86\u70bc\u91d1\u5b66\u9662\uff0c\u5f00\u59cb\u4e00\u5929\u7684\u5de5\u4f5c\u3002</p><p>\u9634\u9b42\u57ce\u529e\u4e8b\u6548\u7387\u5f88\u9ad8\uff0c\u8fd9\u51e0\u5929\u91cc\u5df2\u7ecf\u5236\u9020\u51fa\u4e86\u51e0\u5c0a\u4e2d\u7a7a\u7684\u94c1\u9b54\u50cf\uff0c\u80cc\u540e\u5f00\u4e86\u4e00\u6247\u9690\u853d\u7684\u95e8\uff0c\u65b9\u4fbf\u64cd\u63a7\u8005\u8fdb\u51fa\u3002\u6309\u7167\u8ba1\u5212\uff0c\u4eca\u5929\u5c31\u662f\u8981\u8fdb\u884c\u5b9e\u6218\u6f14\u7ec3\u3002</p><p>\u7ed3\u679c\u8fd8\u7b97\u5dee\u5f3a\u4eba\u610f\uff0c\u56e0\u4e3a\u4e2d\u7a7a\uff0c\u9b54\u50cf\u7684\u7269\u7406\u9632\u5fa1\u80fd\u529b\u4e0b\u964d\u4e86\u5f88\u591a\uff0c\u597d\u5728\u5bf9\u9b54\u6cd5\u7684\u62b5\u6297\u80fd\u529b\u4f9d\u65e7\u4fdd\u7559\u3002\u6218\u6597\u7684\u65f6\u5019\uff0c\u9b54\u50cf\u632f\u8361\u6643\u52a8\uff0c\u91cc\u9762\u7684\u64cd\u63a7\u8005\u96be\u4ee5\u7a33\u4f4f\u8eab\u5f62\uff0c\u4e0d\u5229\u4e8e\u6307\u6325\uff0c\u4e0d\u8fc7\u8fd9\u4e2a\u95ee\u9898\u4e5f\u65e9\u5c31\u5728\u610f\u6599\u4e4b\u4e2d\uff0c\u7528\u529b\u573a\u9b54\u6cd5\u5236\u9020\u51fa\u7f13\u51b2\u5373\u53ef\u89e3\u51b3\u3002\u5728\u9b54\u50cf\u4e0a\u9644\u7740\u98de\u884c\u672f\u7684\u8bbe\u60f3\u6682\u65f6\u9047\u5230\u4e86\u70b9\u969c\u788d\uff0c\u4e0d\u77e5\u9053\u54ea\u4e2a\u6b65\u9aa4\u51fa\u4e86\u95ee\u9898\uff0c\u9700\u8981\u8fdb\u4e00\u6b65\u7814\u7a76\u3002</p><p>\u81f3\u4e8e\u63a7\u5236\u8005\u5728\u9b54\u50cf\u5185\u90e8\u5982\u4f55\u89c2\u5bdf\u5916\u9762\u7684\u95ee\u9898\uff0c\u70bc\u91d1\u5b66\u9662\u4f7f\u7528\u4e86\u73bb\u7483\u94a2\uff08Glassteel\uff09\uff0c\u8fd9\u662f\u7cbe\u7075\u7684\u70bc\u91d1\u672f\u6210\u679c\uff0c\u7cc5\u5408\u4e86\u51b6\u91d1\u5b66\u548c\u73bb\u7483\u5439\u5236\u6280\u672f\uff0c\u9634\u9b42\u57ce\u662f\u4ece\u6c38\u805a\u5c9b\u79d8\u5bc6\u76d7\u7a83\u4e86\u914d\u65b9\u8fc7\u6765\u3002\u73bb\u7483\u94a2\u7684\u5916\u89c2\u5b8c\u5168\u900f\u660e\uff0c\u4e0d\u50cf\u4e00\u822c\u73bb\u7483\u8fd8\u5e26\u7740\u5fae\u7eff\u7684\u5149\u6cfd\uff0c\u5176\u5f3a\u5ea6\u80dc\u8fc7\u94a2\u94c1\uff0c\u4f46\u91cd\u91cf\u66f4\u8f7b\u3002\u5f53\u743c\u6069\u770b\u7740\u9b54\u50cf\u7684\u80f8\u53e3\u9576\u5d4c\u7740\u4e24\u5757\u900f\u660e\u73bb\u7483\u94a2\u7684\u65f6\u5019\uff0c\u4ed6\u8111\u5b50\u91cc\u7a81\u7136\u5192\u51fa\u4e00\u4e2a\u8bcd\uff1a\u64ce\u5929\u67f1\u3002</p><p>\u201c\u53ef\u60dc\u8fd9\u73bb\u7483\u94a2\u5b9e\u5728\u592a\u8d35\u4e86\uff0c\u5426\u5219\u5012\u5e94\u8be5\u7528\u5b83\u6765\u6253\u9020\u4e00\u8eab\u94e0\u7532\u7ed9\u827e\u5f25\u8587\u7a7f\u2026\u2026\u5514\uff0c\u900f\u89c6\u88c5\u94e0\u7532\uff0c\u60f3\u60f3\u5c31\u6d41\u53e3\u6c34\u2026\u2026\u201d</p><p>\u53e6\u5916\u4e00\u4e2a\u4ee4\u4eba\u5b89\u6170\u7684\u6d88\u606f\uff0c\u662f\u9093\u80af\u7684\u706b\u67aa\u6539\u826f\u5de5\u4f5c\u6709\u4e86\u660e\u663e\u7684\u8fdb\u6b65\uff0c\u636e\u4ed6\u8bf4\u5df2\u7ecf\u6210\u529f\u5c06\u706b\u7ef3\u70b9\u706b\u6539\u6210\u4e86\u71e7\u53d1\u70b9\u706b\uff0c\u4e0b\u4e00\u6b65\u7684\u76ee\u6807\u5c31\u662f\u540e\u88c5\u5f39\u3002\u5982\u679c\u987a\u5229\u7684\u8bdd\uff0c\u4e09\u56db\u5929\u5c31\u80fd\u5b8c\u6210\u3002</p><p>\u4e00\u5929\u5f88\u5feb\u8fc7\u53bb\uff0c\u508d\u665a\u65f6\u5206\uff0c\u743c\u6069\u8d70\u51fa\u70bc\u91d1\u5b66\u9662\uff0c\u5374\u5e76\u6ca1\u6709\u76f4\u63a5\u56de\u5bb6\u3002\u539f\u672c\u6309\u60ef\u4f8b\uff0c\u4ed6\u662f\u8981\u53bb\u5965\u6c83\u7684\u4f4f\u5904\u5b66\u4e60\u9b54\u6cd5\u7684\uff0c\u8fd9\u51e0\u5929\u8001\u5deb\u5996\u56e0\u4e3a\u8981\u95ed\u95e8\u505a\u8bd5\u9a8c\uff0c\u5df2\u7ecf\u653e\u4e86\u4ed6\u7684\u5047\uff0c\u7136\u800c\u743c\u6069\u5374\u6ca1\u6709\u628a\u8fd9\u4e8b\u60c5\u5bf9\u73ca\u5609\u548c\u6885\u83f2\u65af\u8bf4\u3002</p><p>\u4ed6\u8981\u53bb\u505a\u4e2a\u8bd5\u9a8c\u3002</p><p>\u9634\u9b42\u57ce\u548c\u585e\u5c14\u4e4b\u95f4\u5efa\u6709\u4f20\u9001\u95e8\uff0c\u65e5\u591c\u6709\u536b\u5175\u628a\u5b88\uff0c\u5e73\u6c11\u767e\u59d3\u662f\u65e0\u6743\u4f7f\u7528\u7684\uff0c\u4f46\u743c\u6069\u5982\u4eca\u5df2\u7ecf\u662f\u8d27\u771f\u4ef7\u5b9e\u7684\u8d35\u65cf\uff0c\u957f\u888d\u4e0a\u7684\u80f8\u9488\u6807\u660e\u4ed6\u662f\u70bc\u91d1\u5b66\u9662\u7684\u5de5\u4f5c\u4eba\u5458\uff0c\u536b\u5175\u81ea\u7136\u4e0d\u4f1a\u963b\u62e6\u3002\u665a\u4e0a\u516d\u70b9\u949f\uff0c\u6b63\u662f\u534e\u706f\u521d\u4e0a\u65f6\uff0c\u4ed6\u5df2\u7ecf\u7ad9\u5728\u585e\u5c14\u9996\u90fd\u827e\u5c14\u5854\u67cf\u57ce\u5357\u4e00\u5ea7\u5b85\u9662\u7684\u95e8\u53e3\u3002</p></article>"}}}]);
