(window.webpackJsonp=window.webpackJsonp||[]).push([[219],{242:function(p){p.exports={chapterTitle:null,title:"\u7b2c219\u7ae0 \u72af\u7f6a\u9884\u9632",content:"<article><h2>\u7b2c219\u7ae0 \u72af\u7f6a\u9884\u9632</h2><p>\u3000\u3000\u76db\u9752\u53ef\u6ca1\u6709\u5f20\u606a\u6240\u8bf4\u7684\u8fd9\u4e48\u6e05\u767d\uff0c\u4ed6\u81ea\u4ee5\u4e3a\u6ca1\u505a\u8fc7\u4ec0\u4e48\u4f24\u5929\u5bb3\u7406\u7684\u4e8b\u60c5\uff0c\u6536\u4fdd\u62a4\u8d39\u3001\u6572\u8bc8\u7f8a\u726f\u3001\u5356\u5047\u9152\u3001\u5047\u70df\u3001\u6536\u7559\u5c0f\u59d0\u5728\u573a\u5b50\u91cc\u5750\u53f0\uff0c\u5728\u4ed6\u770b\u6765\u662f\u9053\u4e0a\u5929\u7ecf\u5730\u4e49\u7684\u4e8b\u60c5\uff0c\u81f3\u4e8e\u6536\u4fdd\u62a4\u8d39\u3001\u6572\u7f8a\u726f\u4e0d\u987a\u5229\uff0c\u6216\u8bb8\u8981\u5c06\u4eba\u6253\u4f24\u6253\u6b8b\uff0c\u6536\u7559\u5c0f\u59d0\u5750\u53f0\uff0c\u6216\u8bb8\u5c0f\u59d0\u672c\u8eab\u662f\u7ed9\u62d0\u9a97\u3001\u88ab\u903c\u8feb\u7684\uff0c\u4ed6\u4e0d\u76f4\u63a5\u53c2\u4e0e\uff0c\u4e5f\u4f1a\u89c6\u82e5\u672a\u7779\u3002</p><p>\u3000\u3000\u76db\u9752\u5e74\u521d\u501f\u8f6c\u79fb\u5230\u56db\u51e4\u6865\u7684\u673a\u4f1a\u6d17\u767d\uff0c\u4e5f\u4e0d\u662f\u4ed6\u826f\u5fc3\u53d1\u73b0\u8981\u8ddf\u4ee5\u524d\u7684\u751f\u6daf\u8131\u79bb\u5173\u7cfb\uff0c\u800c\u662f\u4ed6\u8ba4\u8bc6\u5230\u4ee5\u524d\u7684\u65b9\u5f0f\uff0c\u6ca1\u6709\u4ec0\u4e48\u524d\u9014\uff0c\u8fd8\u5f88\u6709\u53ef\u80fd\u7ed9\u653f\u5e9c\u4e00\u7a9d\u7aef\u4e86\u3002\u5373\u4f7f\u5230\u56db\u51e4\u6865\u6253\u7406\u573a\u5b50\u4e4b\u540e\uff0c\u4ed6\u4e5f\u6ca1\u6709\u5b8c\u5168\u6446\u8131\u8fc7\u53bb\u7684\u610f\u601d\uff0c\u5f88\u591a\u4e8b\u8ddf\u4eba\u4ed6\u90fd\u63a7\u5236\u5728\u624b\u91cc\uff0c\u53ea\u662f\u4e0d\u4f1a\u76f4\u63a5\u53c2\u4e0e\u7f62\u4e86\u3002</p><p>\u3000\u3000\u6309\u4ed6\u7684\u610f\u601d\uff0c\u4e07\u52c7\u4ed6\u513f\u5b50\u8981\u505a\u51fa\u4ec0\u4e48\u4e0d\u53ef\u6536\u62fe\u7684\u4e8b\u60c5\u624d\u597d\uff0c\u4e07\u52c7\u73b0\u5728\u4ed7\u5468\u5bcc\u660e\u7684\u52bf\uff0c\u5728\u6d77\u5dde\u98ce\u751f\u6c34\u8d77\uff0c\u60f9\u4e86\u5f88\u591a\u4eba\u5fcc\u6068\uff0c\u6b63\u6101\u6ca1\u6253\u51fb\u4ed6\u7684\u673a\u4f1a\u3002</p><p>\u3000\u3000\u542c\u4e86\u5f20\u606a\u7684\u8bdd\uff0c\u76db\u9752\u795e\u5fd7\u4e00\u51db\uff0c\u5634\u91cc\u7ec6\u7ec6\u5480\u56bc\u7740\u5f20\u606a\u8bdd\u91cc\u7684\u610f\u601d\u3002</p><p>\u3000\u3000\u5f20\u606a\u89c1\u76db\u9752\u6709\u4e9b\u53d1\u8499\uff0c\u5fc3\u60f3\u81ea\u5df1\u7684\u8bdd\u8fc7\u4e8e\u4e25\u5389\u4e86\u4e00\u4e9b\uff0c\u4ed6\u6bd5\u7adf\u4e5f\u6709\u81ea\u5df1\u7684\u73ed\u5e95\uff0c\u8fd9\u4e48\u8bf4\u4ed6\uff0c\u4f1a\u8ba9\u4ed6\u96be\u582a\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u4f1a\u4e0d\u4f1a\u592a\u4e25\u8083\u4e86\uff0c\u68cb\u9662\u7684\u8bb8\u9e3f\u4f2f\u603b\u8bf4\u4ec0\u4e48\u6709\u6240\u4e3a\u6709\u6240\u4e0d\u4e3a\u7684\u8bdd\uff0c\u6211\u8fd9\u8c03\u8c03\u8ddf\u4ed6\u5b66\u574f\u4e86\u3002\u201d</p><p>\u3000\u3000\u76db\u9752\u7b11\u7740\u8bf4\uff1a\u201c\u6211\u9a6c\u4e0a\u8ba9\u4eba\u53bb\u67e5\uff0c\u603b\u4e4b\u4e0d\u4f1a\u8ba9\u4e24\u6df7\u86cb\u6709\u673a\u4f1a\u505a\u574f\u4e8b\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u62db\u547c\u675c\u98de\u4e00\u8d77\u53bb\u5230\u76db\u9752\u7684\u8f66\u91cc\u8bf4\u4e8b\uff0c\u4e0b\u697c\u68af\u9047\u5230\u52b3\u52a8\u79ef\u6781\u5206\u5b50\u5510\u5a67\u3002\u79d1\u6559\u697c\u4e0e\u529e\u516c\u5ba4\u4e4b\u95f4\u7684\u573a\u5730\u662f\u4ed6\u4eec\u73ed\u7684\u536b\u751f\u5305\u5e72\u533a\uff0c\u5510\u5a67\u521a\u9886\u7740\u4eba\u4ece\u90a3\u91cc\u52b3\u52a8\u56de\u6765\uff0c\u770b\u7740\u5f20\u606a\u9886\u7740\u76db\u9752\u3001\u675c\u98de\u4e0b\u697c\uff0c\u95ee\u4ed6\u4eec\u53bb\u54ea\u91cc\u3002</p><p>\u3000\u3000\u5f20\u606a\u60f3\u8d77\u4e00\u4ef6\u4e8b\uff0c\u95ee\u5979\uff1a\u201c\u90a3\u4e2a\u7f8e\u5c11\u5973\u4fc3\u9500\u5ba3\u4f20\u961f\uff0c\u4f60\u8fd8\u7ec4\u7ec7\u4e0d\uff1f\u201d</p><p>\u3000\u3000\u201c\u9648\u5983\u84c9\u5979\u4eec\u90fd\u4e0a\u9ad8\u4e09\u4e86\uff0c\u5269\u4e0b\u9ad8\u4e8c\u7a00\u7a00\u62c9\u62c9\u7684\u51e0\u4e2a\u4eba\uff0c\u600e\u4e48\u73a9\uff1f\u201d</p><p>\u3000\u3000\u201c\u86ee\u53ef\u60dc\u7684\uff0c\u53f6\u54e5\u4e0a\u56de\u8fd8\u8ddf\u6211\u8bf4\uff0c\u4f60\u4eec\u7684\u5ba3\u4f20\u961f\u5f88\u5389\u5bb3\uff0c\u8fd8\u60f3\u7ee7\u7eed\u8bf7\u4f60\u4eec\u7ed9\u897f\u57ce\u5356\u573a\u505a\u5ba3\u4f20\u5462\uff0c\u201d\u5f20\u606a\u968f\u53e3\u80e1\u626f\uff0c\u7f8e\u5c11\u5973\u5ba3\u4f20\u961f\u7684\u5f62\u5f0f\u662f\u4e0d\u9519\uff0c\u4f46\u5510\u5b66\u8c26\u7684\u5973\u513f\uff0c\u6709\u54ea\u4e48\u5bb9\u6613\u5dee\u4f7f\u5417\uff1f\u53f6\u5efa\u658c\u5b81\u53ef\u53e6\u8bf7\u6a21\u7279\u961f\uff0c\u4e5f\u4e0d\u60f3\u4f3a\u5019\u5510\u5927\u5c0f\u59d0\uff0c\u201c\u9648\u5983\u84c9\u5979\u4eec\u9000\u51fa\u53bb\u4e86\uff0c\u4f60\u4e0d\u4f1a\u4ece\u9ad8\u4e00\u65b0\u751f\u91cc\u6311\u4e9b\u4eba\uff1f\u201d</p><p>\u3000\u3000\u201c\u771f\u7684\uff0c\u201d\u5510\u5a67\u54ea\u91cc\u77e5\u9053\u5f20\u606a\u5fc3\u91cc\u60f3\u4ec0\u4e48\uff0c\u5979\u4e5f\u89c9\u5f97\u7f8e\u5c11\u5973\u5ba3\u4f20\u961f\u53ea\u641e\u4e00\u6b21\u6709\u4e9b\u53ef\u60dc\u4e86\uff0c\u201c\u6211\u53bb\u627e\u9648\u5983\u84c9\u5546\u91cf\u53bb\uff0c\u53f6\u5927\u54e5\u90a3\u8fb9\uff0c\u4f60\u53ef\u8981\u5e2e\u6211\u8bf4\u8bdd\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u8ddf\u4f60\u8c01\u8ddf\u8c01\uff1f\u201d\u5f20\u606a\u628a\u5510\u5a67\u7cca\u5f04\u8d70\uff0c\u5230\u697c\u4e0b\uff0c\u5085\u4fca\u5f00\u8f66\u5c06\u9a6c\u6d77\u9f99\u63a5\u4e86\u8fc7\u6765\u3002</p><p>\u3000\u3000\u76db\u9752\u4e5f\u77e5\u9053\u9a6c\u6d77\u9f99\u7684\u4e8b\u60c5\uff0c\u77e5\u9053\u4ed6\u73b0\u5728\u4e5f\u6ca1\u5904\u7533\u51a4\uff0c\u5468\u5bcc\u660e\u751a\u81f3\u5728\u5e02\u59d4\u4f1a\u8bae\u4e0a\u62cd\u684c\u5b50\u8bf4\u4e0d\u80fd\u7eb5\u5bb9\u62c6\u8fc1\u6237\u3002</p><p>\u3000\u3000\u9a6c\u6d77\u9f99\u7ed9\u5f20\u606a\u6551\u4e86\u540e\uff0c\u5728\u533b\u9662\u91cc\u9192\u6765\u65f6\u89c1\u8fc7\u4e00\u9762\uff0c\u90a3\u65f6\u9ebb\u9189\u836f\u52b2\u8fd8\u6ca1\u6709\u8fc7\u53bb\uff0c\u5934\u8111\u660f\u6c89\u6c89\u7684\uff0c\u4ed6\u80fd\u5c06\u5f53\u65f6\u7684\u4e8b\u53d1\u7ecf\u8fc7\u8bf4\u6e05\u695a\u5df2\u7ecf\u4e0d\u7b80\u5355\uff0c\u81ea\u7136\u6ca1\u6709\u591a\u4f59\u7684\u7cbe\u529b\u597d\u597d\u6253\u91cf\u5f20\u606a\u3002\u5f20\u606a\u7684\u8bb8\u591a\u4e8b\u60c5\u4ee5\u53ca\u5f20\u606a\u6709\u62db\u4ed6\u8fdb\u516c\u53f8\u7684\u610f\u601d\uff0c\u90fd\u662f\u5085\u4fca\u62bd\u7a7a\u544a\u8bc9\u4ed6\u7684\u3002</p><p>\u3000\u3000\u9a6c\u6d77\u9f99\u51fa\u9662\u534a\u4e2a\u6708\u4e86\uff0c\u5728\u5bb6\u91cc\u7ee7\u7eed\u4fee\u517b\uff0c\u751f\u6027\u8c28\u614e\u7684\u4ed6\u6ca1\u6709\u7acb\u5373\u7b54\u5e94\u7ed9\u5f20\u606a\u5de5\u4f5c\uff0c\u4f46\u662f\u5f20\u606a\u8bf7\u4ed6\u8fc7\u6765\u4e34\u65f6\u505a\u4e9b\u4e8b\u60c5\uff0c\u4ed6\u7edd\u6ca1\u6709\u9053\u7406\u62d2\u7edd\uff0c\u4ed6\u5728\u533b\u9662\u7684\u533b\u7597\u8d39\u7528\u8fd8\u662f\u5f20\u606a\u4ee3\u4e3a\u652f\u4ed8\u7684\u3002</p><p>\u3000\u3000\u201c\u4e00\u76f4\u6ca1\u6709\u673a\u4f1a\u5f53\u9762\u5411\u606a\u5c11\u9053\u8c22\u5462\uff0c\u8981\u4e0d\u662f\u606a\u5c11\u62e6\u7740\u8d75\u9526\u8363\u4ed6\u4eec\uff0c\u4ed6\u4eec\u53ef\u662f\u4ec0\u4e48\u4e8b\u60c5\u90fd\u505a\u5f97\u51fa\u6765\u3002\u201d</p><p>\u3000\u3000\u201c\u4e3e\u624b\u4e4b\u52b3\uff0c\u201d\u5f20\u606a\u4e0d\u5728\u610f\u7684\u8bf4\uff0c\u201c\u4f60\u8bf4\u7684\u592a\u591a\uff0c\u4f1a\u8ba9\u6211\u60ed\u6127\u7684\u3002\u201d</p><p>\u3000\u3000\u6d77\u5dde\u4e0d\u7b97\u5927\uff0c\u4e07\u5929\u624d\u3001\u8d75\u53f8\u660e\u53c8\u90fd\u96c6\u4e2d\u5728\u897f\u57ce\u533a\u73a9\u4e50\uff0c\u76db\u9752\u5f88\u5feb\u5c31\u6253\u542c\u5230\u8ddf\u6700\u8fd1\u8ddf\u4e07\u5929\u624d\u3001\u8d75\u53f8\u660e\u73a9\u5f97\u6bd4\u8f83\u719f\u7684\u51e0\u4e2a\u5c0f\u6df7\u6df7\u90fd\u662f\u5bb6\u91cc\u6709\u4e9b\u653f\u5546\u80cc\u666f\u7684\u7ea8\u7ed4\u5b50\u5f1f\uff0c\u4e0d\u8fc7\u8fd9\u51e0\u4e2a\u7ea8\u7ed4\u5b50\u5f1f\u4e0b\u5348\u90fd\u5931\u53bb\u8e2a\u5f71\uff0c\u624b\u673a\u4e5f\u8054\u7edc\u4e0d\u4e0a\u3002</p><p>\u3000\u3000\u8fd9\u8ba9\u5927\u5bb6\u5fc3\u91cc\u90fd\u8499\u4e0a\u4e00\u5c42\u9634\u5f71\uff0c\u5f20\u606a\u62ac\u624b\u770b\u8868\uff0c\u5dee\u4e0d\u591a\u5feb\u5230\u653e\u665a\u5b66\u7684\u65f6\u95f4\uff0c\u624d\u5f00\u5b66\u7b2c\u4e00\u5929\uff0c\u8d70\u8bfb\u751f\u90fd\u4e0d\u9700\u8981\u4e0a\u591c\u81ea\u4fee\uff0c\u5f20\u606a\u4e0d\u6e05\u695a\u5d14\u90c1\u66fc\u4f1a\u4e0d\u4f1a\u7559\u6821\u770b\u73ed\uff0c\u6307\u7ed9\u9a6c\u6d77\u9f99\u770b\u5d14\u90c1\u66fc\uff1a\u201c\u603b\u89c9\u5f97\u6709\u4e9b\u4e0d\u5bf9\u52b2\uff0c\u6211\u8ba9\u76db\u54e5\u7ee7\u7eed\u627e\u5230\u90a3\u51e0\u4eba\uff0c\u8fd8\u8981\u9ebb\u70e6\u9a6c\u5927\u54e5\u4eca\u5929\u5e2e\u6211\u770b\u7740\u5c0f\u5d14\u8001\u5e08\uff0c\u4e0d\u8981\u8ba9\u5979\u51fa\u4ec0\u4e48\u610f\u5916\u2026\u2026\u201d</p><p>\u3000\u3000\u76db\u9752\u5230\u5e95\u5fcd\u4e0d\u4f4f\u95ee\u4e86\u4e00\u53e5\uff1a\u201c\u8fd9\u4e2a\u5c0f\u5d14\u8001\u5e08\u5230\u5e95\u662f\u4ec0\u4e48\u80cc\u666f\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e0d\u77e5\u9053\uff0c\u6ca1\u60f3\u8fc7\u3002\u201d\u5f20\u606a\u6123\u4e86\u6123\uff0c\u4ed6\u4e4b\u6240\u4ee5\u6709\u4e0d\u5999\u7684\u611f\u89c9\uff0c\u4ecd\u662f\u770b\u5230\u51e0\u4e2a\u6708\u540e\u6709\u53ef\u80fd\u4f1a\u88ab\u4e07\u5929\u624d\u3001\u8d75\u53f8\u660e\u7ea0\u96c6\u793e\u4f1a\u4e0a\u4e00\u4f19\u4eba\u51cc\u8fb1\u7684\u5973\u5b69\u5b50\uff0c\u53ef\u4e0d\u662f\u56e0\u4e3a\u5d14\u90c1\u66fc\u6709\u6ca1\u6709\u80cc\u666f\u624d\u8fd9\u4e48\u505a\u3002</p><p>\u3000\u3000\u8fd9\u4e9b\u731c\u6d4b\u4e00\u65f6\u8fd8\u65e0\u6cd5\u8ddf\u5510\u5a67\u8bf4\u4ec0\u4e48\uff0c\u53ea\u5f97\u5148\u8ba9\u5085\u4fca\u5f00\u8f66\u9001\u4ed6\u966a\u5510\u5a67\u56de\u5e08\u9662\uff0c\u521a\u5de7\u5510\u5b66\u8c26\u4eca\u5929\u4e5f\u5f88\u65e9\u56de\u5bb6\uff0c\u5f20\u606a\u5c31\u4e0e\u5085\u4fca\u7559\u5728\u5510\u5a67\u5bb6\u91cc\u5403\u996d\uff0c\u521a\u521a\u5403\u5b8c\uff0c\u5c31\u63a5\u5230\u76db\u9752\u7684\u7535\u8bdd\uff0c\u627e\u5230\u4e07\u5929\u624d\u3001\u8d75\u53f8\u660e\u6700\u8fd1\u7ecf\u5e38\u6df7\u5728\u4e00\u8d77\u7684\u4e00\u4e2a\u72d0\u670b\u72d7\u53cb\uff0c\u662f\u5e02\u5efa\u59d4\u526f\u4e3b\u4efb\u9ec4\u67f1\u5168\u7684\u513f\u5b50\uff0c\u4ed6\u4eca\u5929\u4e0b\u5348\u4e5f\u662f\u4e0d\u89c1\u8e2a\u5f71\uff0c\u5230\u5403\u665a\u996d\u65f6\u624d\u7a81\u7136\u6253\u8f66\u56de\u5bb6\uff1b\u76db\u9752\u624b\u4e0b\u5144\u5f1f\u4e0d\u65b9\u4fbf\u5e26\u4ed6\u51fa\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u72b9\u8c6b\u4e86\u4e00\u4e0b\uff0c\u8fd9\u4e8b\u60c5\u6bd5\u7adf\u8fd8\u53ea\u662f\u731c\u6d4b\uff0c\u4ed6\u4e5f\u53ea\u662f\u9884\u9632\u4e0d\u8981\u53d1\u751f\u4ee4\u4eba\u5fc3\u91cc\u53d1\u5bd2\u7684\u6076\u6027\u4e8b\u4ef6\u3002\u5510\u5b66\u8c26\u5012\u662f\u770b\u51fa\u5f20\u606a\u6709\u8bdd\u8981\u8bf4\u7684\u6837\u5b50\uff0c\u95ee\u4ed6\uff1a\u201c\u6709\u4ec0\u4e48\u4e8b\u60c5\uff0c\u541e\u541e\u5410\u5410\u7684\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u5b81\u53ef\u5510\u5a67\u4e0d\u77e5\u9053\u8fd9\u4e16\u754c\u7adf\u7136\u8fd8\u6709\u8fd9\u4e48\u4e11\u964b\u7684\u4e8b\u60c5\u5b58\u5728\uff0c\u5230\u4e66\u623f\u91cc\u5c06\u81ea\u5df1\u7684\u731c\u6d4b\u90fd\u544a\u8bc9\u5510\u5b66\u8c26\u3002</p><p>\u3000\u3000\u4e07\u5929\u624d\u8981\u771f\u505a\u51fa\u4ec0\u4e48\u4e0d\u53ef\u6536\u62fe\u7684\u4e8b\u60c5\uff0c\u7edd\u5bf9\u662f\u6536\u62fe\u4e07\u52c7\u7684\u7edd\u4f73\u673a\u4f1a\uff0c\u5f20\u606a\u9ed8\u9ed8\u7684\u4f4e\u7740\u5934\uff0c\u4f59\u5149\u5374\u5728\u6253\u91cf\u5510\u5b66\u8c26\uff0c\u770b\u4ed6\u4f1a\u6709\u4ec0\u4e48\u53cd\u5e94\u3002</p><p>\u3000\u3000\u201c\u6211\u770b\u8fd9\u6837\uff0c\u6211\u4eca\u5929\u665a\u4e0a\u6b63\u597d\u6709\u65f6\u95f4\u5173\u5fc3\u4e00\u4e0b\u5510\u5a67\u7684\u5b66\u4e60\uff0c\u6211\u6d3e\u4eba\u53bb\u8bf7\u5c0f\u5d14\u8001\u5e08\u5230\u6211\u5bb6\u6765\u505a\u5bb6\u8bbf\uff0c\u7136\u540e\u6d3e\u4eba\u9001\u5979\u56de\u5bb6\uff0c\u201d\u5510\u5b66\u8c26\u60f3\u4e86\u4e00\u4f1a\u513f\uff0c\u8bf4\u9053\uff0c\u201c\u5173\u952e\u8981\u77e5\u9053\u8fd9\u51e0\u4e2a\u6df7\u5c0f\u5b50\u5230\u5e95\u60f3\u505a\u4ec0\u4e48\uff0c\u4e0d\u80fd\u8ba9\u4eba\u65e0\u7f18\u65e0\u6545\u53d7\u5230\u4f24\u5bb3\uff0c\u4e5f\u4e0d\u80fd\u8fd9\u6837\u5c31\u51a4\u6789\u4e86\u4ed6\u4eec\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u53bb\u9ec4\u67f1\u5168\u5bb6\uff0c\u5c0f\u5b69\u5b50\u8ddf\u5c0f\u5b69\u5b50\uff0c\u603b\u80fd\u8bf4\u5f97\u4e0a\u8bdd\uff0c\u53ea\u8981\u521d\u6b65\u80fd\u8bc1\u5b9e\u6211\u7684\u63a8\u6d4b\uff0c\u6211\u5c31\u76f4\u63a5\u62a5\u8b66\u3002\u201d\u5510\u5b66\u8c26\u5230\u5e95\u6ca1\u8ba9\u4eba\u5931\u671b\uff0c\u5f20\u606a\u8f7b\u677e\u7684\u7b11\u4e86\u7b11\u3002</p><p>\u3000\u3000\u201c\u8fd9\u6837\u4e5f\u597d\u3002\u201d\u5510\u5b66\u8c26\u5524\u5510\u5a67\u8fdb\u6765\uff0c\u8ba9\u5979\u6253\u7535\u8bdd\u7ed9\u73ed\u4e3b\u4efb\uff0c\u51c6\u5907\u8bf7\u5d14\u90c1\u66fc\u5230\u5bb6\u91cc\u6765\u505a\u5ba2\u3002</p><p>\u3000\u3000\u5510\u5a67\u8fd8\u4ee5\u4e3a\u505a\u9519\u4e86\u4ec0\u4e48\u4e8b\uff0c\u8ba9\u5f20\u606a\u5077\u5077\u6478\u6478\u7684\u544a\u4e86\u72b6\uff0c\u773c\u775b\u72e0\u72e0\u7684\u77aa\u7740\u5f20\u606a\uff0c\u53c8\u4e0d\u6562\u4e0d\u53bb\u627e\u7535\u8bdd\uff0c\u8d81\u7740\u5510\u5b66\u8c26\u4e0d\u6ce8\u610f\uff0c\u4f38\u624b\u634f\u7740\u5f20\u606a\u7684\u8170\u8089\uff0c\u5a01\u80c1\u4ed6\u8bf4\uff1a\u201c\u521a\u521a\u8ddf\u6211\u7238\u544a\u6211\u4ec0\u4e48\u72b6\uff1f\u201d</p><p>\u3000\u3000\u7535\u8bdd\u6253\u5230\u5b66\u6821\u529e\u516c\u5ba4\uff0c\u503c\u73ed\u5ba4\u95e8\u536b\u770b\u89c1\u5d14\u90c1\u66fc\u653e\u665a\u5b66\u540e\u79bb\u5f00\u4e86\u5b66\u6821\uff0c\u6ca1\u6709\u4eba\u77e5\u9053\u5d14\u90c1\u66fc\u5bb6\u91cc\u7684\u7535\u8bdd\u3002</p><p>\u3000\u3000\u76db\u9752\u5c06\u4ed6\u5728\u897f\u57ce\u533a\u80fd\u8c03\u52a8\u7684\u5f1f\u5144\u90fd\u8c03\u52a8\u8d77\u6765\uff0c\u5230\u516b\u70b9\u949f\u8fd8\u6ca1\u6709\u53d1\u73b0\u4e07\u5929\u624d\u3001\u8d75\u53f8\u660e\u4ee5\u53ca\u4ed6\u4eec\u90a3\u4e9b\u72d0\u670b\u72d7\u53cb\u7684\u884c\u8e2a\uff0c\u8fd9\u4e9b\u4eba\u4e5f\u90fd\u6ca1\u6709\u5728\u5bb6\u3002\u6709\u4e9b\u4eba\u6709\u624b\u673a\uff0c\u5f88\u5bb9\u6613\u5c31\u67e5\u5230\u4ed6\u4eec\u7684\u624b\u673a\u53f7\u7801\uff0c\u4f46\u662f\u90fd\u5904\u4e8e\u5173\u673a\u72b6\u6001\u3002</p><p>\u3000\u3000\u5d14\u90c1\u66fc\u90a3\u8fb9\u6709\u9a6c\u6d77\u9f99\u8ddf\u7740\uff0c\u4e0d\u9700\u8981\u592a\u62c5\u5fc3\uff0c\u5f20\u606a\u51b3\u5b9a\u5148\u53bb\u5efa\u59d4\u526f\u4e3b\u4efb\u9ec4\u67f1\u5168\u5bb6\u91cc\uff0c\u9ec4\u67f1\u5168\u4ed6\u513f\u5b50\u6700\u8fd1\u8ddf\u4e07\u5929\u624d\u3001\u8d75\u53f8\u660e\u4ed6\u4eec\u6df7\u5f97\u5f88\u719f\uff0c\u8bf4\u4e0d\u5b9a\u4e0b\u5348\u4e5f\u8ddf\u4ed6\u4eec\u5728\u4e00\u8d77\u3002</p><p>\u3000\u3000\u8d76\u5230\u5efa\u59d4\u7684\u5bb6\u5c5e\u5c0f\u533a\uff0c\u76db\u9752\u5df2\u7ecf\u5728\u5c0f\u533a\u95e8\u5916\u7b49\u5019\uff0c\u90b5\u81f3\u521a\u4e5f\u5728\u90a3\u91cc\u3002\u76db\u9752\u8981\u67e5\u4e07\u5929\u624d\u3001\u8d75\u53f8\u660e\u51e0\u4e2a\u4eba\u7684\u884c\u8e2a\uff0c\u90b5\u81f3\u521a\u542c\u5230\u6d88\u606f\uff0c\u5c31\u89c9\u5f97\u4e0d\u5bf9\u52b2\uff0c\u76f4\u63a5\u627e\u76db\u9752\u95ee\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u8fd9\u65f6\u5019\u4e5f\u8d76\u4e86\u8fc7\u6765\u51d1\u70ed\u95f9\u3002</p><p>\u3000\u3000\u4ed6\u770b\u89c1\u5f20\u606a\u7684\u8f66\u8fc7\u6765\uff0c\u8ddf\u76db\u9752\u5750\u8fdb\u8f66\u91cc\uff0c\u5bf9\u5f20\u606a\u8bf4\uff1a\u201c\u8fd9\u4e48\u597d\u7684\u673a\u4f1a\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u5229\u7528\uff1f\u90a3\u8fb9\u518d\u6d3e\u4e9b\u4eba\u76ef\u7740\uff0c\u8fd8\u6015\u4ed6\u4eec\u518d\u80fd\u505a\u51fa\u4ec0\u4e48\u4f24\u5929\u5bb3\u7406\u7684\u4e8b\u60c5\u6765\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u5012\u4e0d\u662f\u6211\u6b63\u4e49\u611f\u81a8\u80c0\uff0c\u90a3\u6837\u7684\u8bdd\uff0c\u9634\u8c0b\u7684\u5473\u9053\u592a\u91cd\u2026\u2026\u201d\u8fd9\u51e0\u6708\u6765\u5c0f\u5fc3\u5e03\u7f6e\uff0c\u60f3\u5fc5\u5468\u5bcc\u660e\u5bf9\u4e07\u52c7\u5df2\u7ecf\u5fc3\u5b58\u82a5\u8482\uff0c\u4e07\u4e00\u8ba9\u5468\u5bcc\u660e\u55c5\u5230\u9634\u8c0b\u7684\u5473\u9053\uff0c\u4f1a\u751f\u51fa\u8b66\u60d5\u4e5f\u8bf4\u4e0d\u5b9a\u3002</p><p>\u3000\u3000\u5f20\u606a\u627e\u5230\u5efa\u59d4\u526f\u4e3b\u4efb\u9ec4\u67f1\u5168\u5bb6\uff0c\u6572\u95e8\u95ee\u9053\uff1a\u201c\u9ec4\u6653\u660e\u5728\u4e0d\u5728\uff1f\u201d</p><p>\u3000\u3000\u201c\u8c01\u554a\uff1f\u201d\u4e00\u4e2a\u4e2d\u5e74\u4eba\u7684\u58f0\u97f3\uff0c\u95e8\u80cc\u540e\u6709\u52a8\u9759\uff0c\u732b\u773c\u6253\u5f00\u540e\u9732\u51fa\u4e00\u4e1d\u4eae\u5149\uff0c\u63a5\u4e0b\u6765\u53c8\u6697\u4e86\u4e0b\u53bb\uff0c\u6709\u4eba\u5728\u95e8\u540e\u9762\u770b\u5916\u9762\u3002</p><p>\u3000\u3000\u201c\u662f\u6211\uff0c\u6211\u7238\u7238\u662f\u5e02\u653f\u5e9c\u7684\u5f20\u77e5\u884c\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b49\u7740\u95e8\u6253\u5f00\uff0c\u4e00\u811a\u8e0f\u8fdb\u95e8\uff0c\u7b49\u5728\u697c\u68af\u62d0\u89d2\u4e0b\u7684\u5085\u4fca\u3001\u90b5\u81f3\u521a\u3001\u76db\u9752\u8ddf\u7740\u7a9c\u4e86\u4e0a\u6765\uff0c\u62e5\u7740\u5f20\u606a\u4e00\u8d77\u6324\u8fdb\u95e8\u3002</p><p>\u3000\u3000\u5f00\u95e8\u7684\u4e2d\u5e74\u80d6\u5b50\u5c31\u662f\u9ec4\u67f1\u5168\uff0c\u8138\u4e0a\u90fd\u662f\u7599\u7629\u75d8\uff0c\u4ed6\u770b\u5230\u5f20\u606a\u8fd9\u67b6\u5f0f\uff0c\u5413\u4e86\u4e00\u8df3\uff1a\u201c\u4f60\u4eec\u60f3\u5e72\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u201c\u9ec4\u4e3b\u4efb\u5fd8\u8bb0\u6211\u4e86\uff1f\u201d\u90b5\u81f3\u521a\u563f\u7136\u4e00\u7b11\uff0c\u4f38\u51fa\u624b\u6307\u6413\u4e86\u6413\uff0c\u201c\u4e16\u7eaa\u5730\u4ea7\u7684\u90b5\u81f3\u521a\uff0c\u524d\u4e9b\u5929\u521a\u53bb\u5efa\u59d4\u62dc\u8bbf\u8fc7\u9ec4\u4e3b\u4efb\uff0c\u9ec4\u4e3b\u4efb\u628a\u8fd9\u4e2a\u4e5f\u5fd8\u8bb0\u4e86\uff1f\u201d\u626d\u5934\u770b\u7740\u5ba2\u5385\u6c99\u53d1\u4e0a\u5750\u7740\u4e2a\u4e8c\u5341\u5c81\u5de6\u53f3\u7684\u9752\u5e74\uff0c\u7b11\u7740\u95ee\uff0c\u201c\u8fd9\u4f4d\u662f\u9ec4\u4e3b\u4efb\u7684\u516c\u5b50\u9ec4\u5c0f\u660e\u5427\uff1f\u201d</p></article>"}}}]);
