(window.webpackJsonp=window.webpackJsonp||[]).push([[219],{241:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u56db\u8282 \u8bd5\u9a8c",content:"<article><h2>\u7b2c\u5341\u56db\u8282 \u8bd5\u9a8c</h2><p>\u743c\u6069\u5fc3\u4e2d\u4e00\u76f4\u6f5c\u4f0f\u7684\u4e00\u4e2a\u7591\u60d1\uff0c\u5c31\u662f\u7530\u4f2f\u5149\u6559\u4ed6\u7684\u90a3\u91c7\u7389\u8bc0\uff0c\u5230\u5e95\u6709\u4ec0\u4e48\u7528\u3002</p><p>\u6309\u7167\u6700\u521d\u7684\u8bf4\u6cd5\uff0c\u5b83\u662f\u7528\u6765\u5438\u5185\u529b\u7684\uff0c\u7c7b\u4f3c\u5317\u6e9f\u795e\u529f\uff0c\u4f46\u8fd9\u4e2a\u4e16\u754c\u91cc\u538b\u6839\u65e0\u4eba\u4fee\u70bc\u5185\u529f\uff0c\u6240\u4ee5\u743c\u6069\u4e00\u76f4\u628a\u5b83\u5f53\u4f5c\u6beb\u65e0\u7528\u5904\u7684\u5c60\u9f99\u672f\uff0c\u629b\u4e4b\u8111\u540e\uff0c\u51e0\u4e4e\u90fd\u7ed9\u5fd8\u4e86\u3002\u7136\u800c\u4e0a\u6b21\u5728\u70db\u5821\uff0c\u56e0\u4e3a\u88ab\u5973\u6740\u624b\u838e\u73de\u514b\u903c\u8feb\uff0c\u8131\u8eab\u4e0d\u5f97\uff0c\u50cf\u6293\u6551\u547d\u7a3b\u8349\u4f3c\u7684\u7528\u4e86\u4e00\u6b21\uff0c\u7ed3\u679c\u8bef\u6253\u8bef\u649e\uff0c\u5c45\u7136\u4ece\u5bf9\u65b9\u4f53\u5185\u5438\u6765\u4e00\u70b9\u9634\u5bd2\u6c14\u606f\u3002\u867d\u7136\u81ea\u5df1\u5dee\u70b9\u56e0\u6b64\u88ab\u51bb\u50f5\uff0c\u4f46\u6162\u6162\u5316\u89e3\u4e4b\u540e\uff0c\u5374\u9887\u6709\u4e9b\u6536\u76ca\uff0c\u611f\u89c9\u81ea\u5df1\u539f\u672c\u5fae\u8584\u7684\u5185\u529b\uff0c\u90fd\u7565\u7565\u589e\u5f3a\u4e86\u4e00\u70b9\u3002</p><p>\u8fd9\u6b21\u7684\u610f\u5916\u6536\u83b7\uff0c\u8ba9\u743c\u6069\u5174\u594b\u4e0d\u5df2\uff0c\u7136\u800c\u81ea\u6b64\u4e4b\u540e\uff0c\u4ed6\u5c61\u6b21\u8bd5\u9a8c\uff0c\u4e00\u6982\u5168\u65e0\u6548\u7528\uff0c\u4ec5\u6709\u7684\u4e00\u6b21\u6210\u529f\u5c31\u662f\u5728\u838e\u73de\u514b\u8eab\u4e0a\u2014\u2014\u7136\u800c\u5728\u5de8\u9b54\u5c71\u8109\u91cc\uff0c\u838e\u73de\u514b\u53c8\u6b7b\u4e86\u3002</p><p>\u6b63\u5f53\u4ed6\u4ee5\u4e3a\u7ebf\u7d22\u5c31\u6b64\u4e2d\u65ad\u7684\u65f6\u5019\uff0c\u5374\u4ece\u6885\u83f2\u65af\u53e3\u4e2d\u5f97\u77e5\uff0c\u539f\u6765\u838e\u73de\u514b\u548c\u5979\u90fd\u662f\u795e\u5b50\u3002\u51e0\u4e4e\u662f\u672c\u80fd\uff0c\u4ed6\u8111\u4e2d\u5192\u51fa\u4e00\u4e2a\u60f3\u6cd5\uff1a\u83ab\u975e\u8fd9\u91c7\u7389\u8bc0\u80fd\u591f\u7528\u6765\u5438\u53d6\u795e\u529b\uff1f</p><p>\u5982\u679c\u8fd9\u4e2a\u5047\u8bbe\u6210\u7acb\uff0c\u90a3\u4e48\u6b64\u524d\u7684\u4e00\u5207\u5012\u90fd\u80fd\u8bf4\u5f97\u901a\uff0c\u7136\u800c\u5047\u8bbe\u7ec8\u7a76\u662f\u5047\u8bbe\uff0c\u9700\u8981\u66f4\u8fdb\u4e00\u6b65\u53bb\u8bd5\u9a8c\u8bc1\u5b9e\u3002\u8eab\u8fb9\u5012\u662f\u5c31\u6709\u4e00\u4e2a\u4e0a\u7b49\u7684\u8bd5\u9a8c\u54c1\uff1a\u6885\u83f2\u65af\u4e5f\u662f\u795e\u5b50\uff0c\u800c\u4e14\u4f53\u5185\u8574\u542b\u7684\u5df4\u5c14\u795e\u529b\u8fdc\u8fdc\u80dc\u8fc7\u838e\u73de\u514b\uff0c\u4f46\u5979\u65e0\u8bba\u5982\u4f55\u4e0d\u80af\u548c\u743c\u6069\u771f\u6b63\u4ea4\u5408\uff0c\u8fd9\u5c31\u5f88\u9ebb\u70e6\u3002\u743c\u6069\u4e0d\u60f3\u4e3a\u8fd9\u79cd\u672a\u8bc1\u5b9e\u7684\u5047\u8bbe\u53bb\u5f3a\u884c\u63a8\u5012\u5979\uff0c\u7834\u574f\u6389\u4e24\u4eba\u597d\u4e0d\u5bb9\u6613\u5efa\u7acb\u8d77\u6765\u7684\u611f\u60c5\uff0c\u4e5f\u5c31\u53ea\u80fd\u6682\u65f6\u5148\u653e\u4e0b\u3002</p><p>\u201c\u53cd\u6b63\u8fdf\u65e9\u4f1a\u5403\u5230\uff0c\u4e5f\u4e0d\u5fc5\u6025\u4e8e\u4e00\u65f6\u3002\u201d\u743c\u6069\u5982\u6b64\u60f3\u3002</p><p>\u795e\u5b50\u4e0d\u662f\u5e02\u573a\u4e0a\u7684\u767d\u83dc\uff0c\u904d\u5730\u90fd\u662f\uff0c\u743c\u6069\u96be\u5f97\u649e\u4e0a\u4e24\u4e2a\uff0c\u4e00\u4e2a\u6b7b\u4e86\uff0c\u4e00\u4e2a\u662f\u81ea\u5df1\u5fc3\u7231\u7684\u4eba\uff0c\u6682\u65f6\u4e0d\u80fd\u63a8\u5012\uff0c\u8bd5\u9a8c\u4e5f\u5c31\u53ea\u597d\u5148\u6401\u7f6e\u3002\u8fd9\u534a\u4e2a\u6708\u4ee5\u6765\uff0c\u4ed6\u548c\u6885\u83f2\u65af\u7684\u5173\u7cfb\u5927\u6709\u8fdb\u5c55\uff0c\u963f\u683c\u62c9\u9686\u4e00\u884c\uff0c\u5728\u51db\u7684\u5e2e\u52a9\u4e0b\uff0c\u5df2\u7ecf\u6210\u529f\u653b\u9677\u4e86\u4e09\u5206\u4e4b\u4e8c\uff0c\u867d\u7136\u8fd8\u672a\u7adf\u5168\u529f\uff0c\u4f46\u81f3\u5c11\u518d\u4e0d\u9700\u8981\u50cf\u4ee5\u524d\u90a3\u6837\u9760\u65e9\u5b89\u54ac\u6765\u6d88\u89e3\u6b32\u706b\u4e86\u3002\u7167\u8fd9\u79cd\u901f\u5ea6\u4e0b\u53bb\uff0c\u5b8c\u5b8c\u5168\u5168\u5360\u6709\u6885\u83f2\u65af\u5e94\u8be5\u4e5f\u662f\u6307\u65e5\u53ef\u5f85\u7684\u4e8b\u60c5\u3002</p><p>\u53ea\u4e0d\u8fc7\uff0c\u8fd9\u4e16\u754c\u4e0a\u7684\u4e8b\u60c5\uff0c\u6c38\u8fdc\u662f\u8ba1\u5212\u8d76\u4e0d\u4e0a\u53d8\u5316\u3002\u6ca1\u60f3\u5230\u5076\u5c14\u6765\u4e00\u6b21\u585e\u5c14\uff0c\u5c45\u7136\u4f1a\u53c8\u649e\u4e0a\u4e00\u4e2a\u5df4\u5c14\u5b50\u55e3\uff0c\u8fd9\u7740\u5b9e\u8ba9\u743c\u6069\u559c\u51fa\u671b\u5916\u3002</p><p>\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u4e48\u539f\u672c\u7684\u6253\u7b97\uff0c\u4e5f\u5c31\u8981\u76f8\u5e94\u5730\u505a\u4e00\u4e0b\u53d8\u52a8\u4e86\u3002</p><p>\u4ed6\u539f\u672c\u662f\u51c6\u5907\u628a\u8fd9\u4e2a\u4f8f\u5112\u8fea\u74e6\u514b\u5e26\u56de\u9634\u9b42\u57ce\uff0c\u4ed6\u8eab\u4e0a\u7684\u90a3\u4efd\u9b54\u6cd5\u7b14\u8bb0\u81ea\u7136\u4e5f\u4e0a\u4ea4\uff0c\u8fd9\u79cd\u7528\u4e8e\u9884\u8a00\u63a2\u6d4b\u5730\u8d28\u77ff\u77f3\u7684\u9b54\u6cd5\u6280\u5de7\uff0c\u4ed6\u81ea\u5df1\u5374\u6ca1\u5174\u8da3\u53bb\u5b66\u3002\u4f46\u5982\u4eca\u5f97\u77e5\u83f2\u4e9a\u662f\u5df4\u5c14\u5b50\u55e3\uff0c\u53c8\u6253\u7b97\u62ff\u5979\u6765\u505a\u91c7\u7389\u8bc0\u7684\u8bd5\u9a8c\uff0c\u8fd9\u79cd\u9690\u79d8\u7684\u4e8b\u60c5\uff0c\u81ea\u7136\u5c31\u4e0d\u80fd\u518d\u8ba9\u9634\u9b42\u57ce\u77e5\u6653\u3002</p><p>\u743c\u6069\u5feb\u901f\u8f6c\u7740\u5ff5\u5934\uff0c\u76d8\u7b97\u7740\u5f97\u5931\uff0c\u968f\u5373\u505a\u4e86\u51b3\u5b9a\u3002\u4ed6\u4f9d\u65e7\u4e70\u4e0b\u4e86\u8fea\u74e6\u514b\uff0c\u5374\u4e0d\u662f\u62ff\u9634\u9b42\u57ce\u7684\u516c\u6b3e\uff0c\u800c\u662f\u81ea\u5df1\u638f\u94b1\u3002\u7d27\u63a5\u7740\uff0c\u4ed6\u627e\u5230\u5f17\u96f7\u65af\u5148\u751f\uff0c\u540c\u6837\u662f\u81ea\u5df1\u638f\u94b1\u628a\u83f2\u4e9a\u4e5f\u4e70\u4e86\u4e0b\u6765\u3002\u5f17\u96f7\u65af\u6709\u4e9b\u5947\u602a\uff0c\u4e0d\u77e5\u9053\u743c\u6069\u600e\u4e48\u7a81\u7136\u770b\u4e0a\u4e86\u81ea\u5df1\u7684\u4f8d\u5973\uff0c\u4f46\u4e5f\u6ca1\u6709\u62d2\u7edd\uff0c\u4ed6\u662f\u5927\u5974\u96b6\u8d29\u5b50\uff0c\u8981\u591a\u5c11\u7f8e\u8c8c\u4e56\u5de7\u4f8d\u5973\u6ca1\u6709\uff0c\u4e5f\u4e0d\u5728\u4e4e\u83f2\u4e9a\u8fd9\u4e00\u4e2a\u3002</p><p>\u5982\u6b64\u4e00\u6765\uff0c\u83f2\u4e9a\u5144\u59b9\u5c31\u6210\u4e86\u743c\u6069\u7684\u79c1\u4eba\u8d22\u4ea7\u3002</p><p>\u9762\u524d\u7684\u8fd9\u5ea7\u5b85\u9662\uff0c\u4e5f\u662f\u743c\u6069\u8d2d\u4e70\u7684\u3002\u585e\u5c14\u7684\u5730\u76ae\u4e0d\u8d35\uff0c\u4f46\u6240\u6709\u7684\u8d39\u7528\u52a0\u8d77\u6765\uff0c\u8fd8\u662f\u82b1\u4e86\u4ed6\u4e00\u5927\u7b14\u94b1\u3002\u4e0d\u8fc7\u4ed6\u7684\u94b1\u539f\u672c\u5c31\u662f\u767d\u5f97\u6765\u7684\uff0c\u5c0f\u90e8\u5206\u662f\u4ece\u4eba\u9762\u72ee\u795e\u6bbf\u91cc\u641c\u522e\u7684\uff0c\u5927\u90e8\u5206\u662f\u8679\u5f69\u9f99\u7ed9\u7684\uff0c\u7b80\u76f4\u5c31\u662f\u5929\u4e0a\u6389\u4e0b\u6765\u7684\u86cb\u7cd5\uff0c\u53c8\u4e0d\u662f\u81ea\u5df1\u8f9b\u82e6\u6240\u6323\uff0c\u6240\u4ee5\u82b1\u8d77\u6765\u4e5f\u5c31\u4e0d\u751a\u5728\u4e4e\u4e86\u3002</p><p>\u4ed6\u8d70\u8fdb\u5b85\u9662\uff0c\u53d1\u73b0\u8fea\u74e6\u514b\u4f3c\u4e4e\u5df2\u7ecf\u5165\u7761\uff0c\u4ed6\u7684\u623f\u95f4\u706f\u662f\u706d\u7684\u3002\u83f2\u4e9a\u7684\u623f\u95f4\u5012\u662f\u706f\u706b\u901a\u660e\uff0c\u743c\u6069\u5f84\u76f4\u63a8\u95e8\u8fdb\u5165\uff0c\u53d1\u73b0\u5c11\u5973\u6b63\u5728\u6574\u7406\u7740\u4e00\u53e0\u7f8a\u76ae\u7eb8\uff0c\u5c06\u5b83\u4eec\u88c5\u8ba2\u6210\u518c\u3002</p><p>\u89c1\u4ed6\u8fdb\u6765\uff0c\u83f2\u4e9a\u8d77\u8eab\u884c\u793c\uff0c\u201c\u665a\u4e0a\u597d\uff0c\u5148\u751f\u3002\u201d</p><p>\u5979\u7684\u8bed\u6c14\u4f9d\u65e7\u4e0d\u5351\u4e0d\u4ea2\uff0c\u4f46\u79f0\u547c\u5374\u4ece\u201c\u5170\u5c3c\u65af\u7279\u5148\u751f\u201d\u6539\u6210\u4e86\u201c\u5148\u751f\u201d\uff0c\u8fd9\u610f\u5473\u7740\u8eab\u4efd\u7684\u8f6c\u53d8\u3002\u743c\u6069\u66fe\u7ecf\u90aa\u6076\u5730\u8003\u8651\u8fc7\u8981\u4e0d\u8981\u8ba9\u5979\u53eb\u201c\u4e3b\u4eba\u201d\uff0c\u4e0d\u8fc7\u6700\u7ec8\u8fd8\u662f\u89c9\u5f97\u4e0d\u592a\u4e60\u60ef\uff0c\u653e\u5f03\u4e86\u8fd9\u4e2a\u5ff5\u5934\u3002</p><p>\u9664\u4e86\u79f0\u547c\u7684\u6539\u53d8\uff0c\u53e6\u5916\u4e00\u4e2a\u6e05\u695a\u663e\u793a\u5979\u73b0\u5728\u7684\u5974\u96b6\u8eab\u4efd\u7684\uff0c\u662f\u8116\u5b50\u4e0a\u7684\u94f6\u767d\u8272\u9879\u5708\u3002\u90a3\u662f\u7ea2\u888d\u5deb\u5e08\u4f1a\u7279\u5236\u4e4b\u7269\uff0c\u6750\u6599\u6df7\u5408\u4e86\u7cbe\u91d1\u548c\u79d8\u94f6\uff0c\u4e0a\u9762\u9644\u7740\u5f3a\u5927\u7684\u9b54\u6cd5\u3002\u53ea\u8981\u6234\u4e0a\uff0c\u9664\u975e\u4e3b\u4eba\u4eb2\u53e3\u8bf4\u51fa\u53e3\u4ee4\uff0c\u5426\u5219\u5c31\u65e0\u6cd5\u53d6\u4e0b\u6765\uff1b\u5728\u77ed\u8ddd\u79bb\u5185\uff0c\u4e3b\u4eba\u53ef\u4ee5\u7528\u610f\u5ff5\u63a7\u5236\u5b83\u7684\u4f38\u7f29\uff1b\u5982\u679c\u7528\u5916\u529b\u5f3a\u884c\u7834\u574f\uff0c\u9879\u5708\u4f1a\u81ea\u52a8\u6536\u7f29\uff0c\u5c06\u8116\u5b50\u52d2\u65ad\u3002\u9664\u6b64\u4e4b\u5916\uff0c\u9879\u5708\u4e0a\u8fd8\u9644\u5e26\u4e86\u5b9a\u4f4d\u8ddf\u8e2a\u7684\u6548\u679c\uff0c\u5982\u679c\u5974\u96b6\u9003\u8d70\u7684\u8bdd\uff0c\u4e3b\u4eba\u53ef\u4ee5\u8bf7\u7ea2\u888d\u5deb\u5e08\u4f1a\u534f\u52a9\uff0c\u5feb\u901f\u627e\u5230\u76ee\u6807\u3002\u4e0d\u8fc7\u6709\u4e00\u70b9\u4e0d\u597d\uff0c\u8fd9\u79cd\u9879\u5708\u662f\u4e00\u6b21\u6027\u7269\u54c1\uff0c\u4e0d\u80fd\u91cd\u590d\u4f7f\u7528\uff0c\u5982\u679c\u7528\u53e3\u4ee4\u53d6\u4e0b\u6765\uff0c\u90a3\u4e48\u6240\u6709\u7684\u9b54\u529b\u5c31\u4f1a\u5168\u90e8\u4e27\u5931\u4e86\u3002</p><p>\u585e\u5c14\u662f\u5974\u96b6\u8d38\u6613\u5927\u56fd\uff0c\u81ea\u7136\u6709\u5404\u79cd\u63aa\u65bd\u4fdd\u8bc1\u4e3b\u4eba\u5bf9\u5974\u96b6\u7684\u6240\u6709\u6743\u4e0d\u53d7\u4fb5\u72af\uff0c\u5305\u62ec\u767b\u8bb0\u5907\u6848\u5236\u5ea6\u548c\u5404\u79cd\u4e13\u95e8\u9488\u5bf9\u5974\u96b6\u7684\u9b54\u6cd5\u9053\u5177\uff0c\u6839\u636e\u6548\u7528\u7684\u5f3a\u5f31\u9ad8\u4f4e\u5212\u5206\u4e3a\u4e0d\u540c\u7b49\u7ea7\uff0c\u8fd9\u9879\u5708\u662f\u5176\u4e2d\u6700\u9ad8\u4e5f\u6700\u6602\u8d35\u7684\u4e00\u7ea7\uff0c\u5176\u4ef7\u683c\u62b5\u5f97\u4e0a\u4e8c\u5341\u4e2a\u83f2\u4e9a\u3002</p><p>\u743c\u6069\u770b\u7740\u9879\u5708\uff0c\u7a81\u7136\u8111\u4e2d\u95ea\u8fc7\u4e00\u4e2a\u5ff5\u5934\uff0c\u5982\u679c\u518d\u7cfb\u4e0a\u4e00\u6839\u7ec6\u7ec6\u7684\u94f6\u94fe\uff0c\u90a3\u5c31\u53ef\u4ee5\u53bb\u5c1d\u8bd5\u4e00\u4e0b\u7f8e\u5973\u72ac\u7684\u8c03\u6559\u6e38\u620f\u4e86\u3002\u53ef\u60dc\u4ed6\u5bf9\u83f2\u4e9a\u8c08\u4e0d\u4e0a\u591a\u5927\u5174\u8da3\uff0c\u5bb6\u91cc\u90a3\u4e24\u4f4d\uff0c\u65e0\u8bba\u662f\u73ca\u5609\u8fd8\u662f\u6885\u83f2\u65af\u90fd\u4e0d\u9002\u5408\u3002\u5982\u679c\u743c\u6069\u6562\u8fd9\u4e48\u73a9\uff0c\u80af\u5b9a\u4f1a\u88ab\u5979\u4eec\u4ece\u697c\u4e0a\u4e22\u4e0b\u53bb\u2026\u2026\u5514\uff0c\u8bf4\u8d77\u6765\uff0c\u9694\u58c1\u90a3\u4f4d\u5012\u662f\u5f88\u5408\u9002\u7684\u4eba\u9009\u2026\u2026\u7b97\u4e86\uff0c\u8fd9\u79cd\u4e0d\u7740\u8fb9\u9645\u7684\u5ff5\u5934\u4e22\u5f00\uff0c\u5148\u529e\u6b63\u4e8b\u3002</p><p>\u201c\u7b14\u8bb0\u6574\u7406\u597d\u4e86\u5417\uff1f\u201d\u743c\u6069\u95ee\uff0c\u5750\u5728\u6905\u5b50\u4e0a\u3002</p><p>\u83f2\u4e9a\u5c06\u521a\u521a\u88c5\u8ba2\u597d\u7684\u4e66\u518c\u9012\u8fc7\u6765\uff0c\u9759\u9759\u4f8d\u7acb\u5728\u4e00\u8fb9\uff0c\u6ca1\u6709\u8bf4\u8bdd\u3002</p><p>\u90a3\u4efd\u9b54\u6cd5\u7b14\u8bb0\u662f\u7eb9\u5728\u8fea\u74e6\u514b\u7684\u8eab\u4e0a\uff0c\u743c\u6069\u81ea\u7136\u6ca1\u5174\u8da3\u53bb\u6b23\u8d4f\u4e00\u4e2a\u4f8f\u5112\u7684\u88f8\u4f53\uff0c\u6240\u4ee5\u8ba9\u83f2\u4e9a\u7167\u6284\u6574\u7406\u4e00\u4efd\u7ed9\u4ed6\u3002\u5982\u4eca\u8fd9\u4e24\u4eba\u5df2\u7ecf\u662f\u4ed6\u7684\u5974\u96b6\uff0c\u8fd9\u4efd\u7b14\u8bb0\u81ea\u7136\u4e5f\u5f52\u4ed6\u79c1\u4eba\u6240\u6709\uff0c\u4e0d\u5fc5\u4e0a\u4ea4\u9634\u9b42\u57ce\u4e86\u3002</p><p>\u743c\u6069\u7ffb\u5f00\u7b14\u8bb0\uff0c\u7c97\u7565\u6d4f\u89c8\u4e86\u4e00\u904d\uff0c\u4ed6\u53d1\u73b0\u8fd9\u4efd\u88ab\u83f2\u4e9a\u7684\u517b\u7236\u53d6\u540d\u4e3a\u201c\u6df1\u571f\u9884\u8a00\u201d\u7684\u9b54\u6cd5\u6280\u5de7\u5176\u5b9e\u5f88\u7b80\u5355\uff0c\u5bf9\u5b66\u4e60\u8005\u7684\u9020\u8be3\u8981\u6c42\u4e5f\u5f88\u4f4e\uff0c\u53ea\u9700\u8981\u662f\u7cbe\u7814\u9884\u8a00\u7684\u5deb\u5e08\uff0c\u80fd\u591f\u89e6\u6478\u5230\u9b54\u7f51\u7b2c\u4e8c\u5c42\u5373\u53ef\uff0c\u4f46\u540c\u65f6\u9700\u8981\u5bf9\u5e7d\u6697\u5730\u57df\u7684\u5730\u7406\u77e5\u8bc6\u6709\u5f88\u6df1\u7684\u4e86\u89e3\uff0c\u8fd9\u5c31\u6bd4\u8f83\u9ebb\u70e6\u4e86\u3002\u4ed6\u60f3\u4e86\u60f3\uff0c\u5c06\u7b14\u8bb0\u6536\u5165\u6000\u4e2d\uff0c\u53cd\u6b63\u81ea\u5df1\u53c8\u4e0d\u6253\u7b97\u53bb\u5b66\uff0c\u8bf4\u4e0d\u5b9a\u53ef\u4ee5\u8003\u8651\u62ff\u53bb\u9001\u4eba\u3002</p><p>\u4ed6\u62ac\u8d77\u5934\uff0c\u770b\u7740\u5c11\u5973\uff0c\u540e\u8005\u6f20\u65e0\u8868\u60c5\uff0c\u201c\u83f2\u4e9a\u5c0f\u59d0\uff0c\u201d\u743c\u6069\u637b\u52a8\u7740\u624b\u6307\uff0c\u201c\u6211\u60f3\u8bf7\u6559\u51e0\u4e2a\u95ee\u9898\uff0c\u53ef\u4ee5\u4e48\u3002\u201d</p><p>\u201c\u60a8\u4e0d\u5fc5\u5982\u6b64\u5ba2\u6c14\uff0c\u201d\u83f2\u4e9a\u8bf4\uff0c\u201c\u56de\u7b54\u4e3b\u4eba\u7684\u95ee\u9898\uff0c\u662f\u5974\u96b6\u7684\u4e49\u52a1\u4e4b\u4e00\u3002\u201d</p><p>\u743c\u6069\u81ea\u5632\u5730\u4e00\u7b11\uff0c\u4ed6\u5230\u5e95\u8fd8\u662f\u6709\u4e9b\u4e0d\u592a\u9002\u5e94\u8fd9\u79cd\u4e3b\u4eba\u548c\u5974\u96b6\u7684\u5173\u7cfb\uff0c\u603b\u89c9\u5f97\u6709\u4e9b\u4e0d\u592a\u81ea\u5728\u3002\u201c\u597d\u5427\uff0c\u90a3\u4e48\u83f2\u4e9a\u5c0f\u59d0\uff0c\u6211\u60f3\u8bf7\u6559\u4f60\u7236\u4eb2\u7684\u540d\u5b57\u2014\u2014\u662f\u7236\u4eb2\uff0c\u4e0d\u662f\u517b\u7236\u3002\u201d</p><p>\u83f2\u4e9a\u6447\u5934\uff0c\u201c\u6211\u4e0d\u77e5\u9053\u4ed6\u7684\u540d\u5b57\u3002\u201d</p><p>\u743c\u6069\u5fae\u5fae\u626c\u7709\uff0c\u201c\u4e0d\u77e5\u9053\uff1f\u201d</p><p>\u201c\u4ece\u6211\u51fa\u4e16\u8d77\u5c31\u6ca1\u89c1\u8fc7\u4ed6\uff0c\u6211\u6bcd\u4eb2\u4e5f\u4ece\u6765\u6ca1\u6709\u5bf9\u6211\u63d0\u8fc7\u3002\u201d</p><p>\u662f\u8fd9\u6837\u4e48\uff1f</p><p>\u743c\u6069\u6c89\u601d\u7740\uff0c\u7136\u540e\u4ece\u6000\u91cc\u53d6\u51fa\u4e00\u5f20\u7279\u610f\u51c6\u5907\u597d\u7684\u5377\u8f74\u6765\u3002\u201c\u8dea\u5728\u6211\u9762\u524d\u3002\u201d\u4ed6\u547d\u4ee4\u7740\u3002</p><p>\u83f2\u4e9a\u4e1d\u6beb\u6ca1\u6709\u72b9\u8c6b\uff0c\u4f9d\u8a00\u8dea\u4e0b\uff0c\u743c\u6069\u5c55\u5f00\u5377\u8f74\uff0c\u5feb\u901f\u8bfb\u5b8c\uff0c\u4ed6\u4f38\u624b\u6309\u5728\u5c11\u5973\u7684\u989d\u5934\u4e0a\uff0c\u4e00\u56e2\u6de1\u7d2b\u8272\u7684\u5149\u8292\u4ece\u638c\u5fc3\u6d8c\u51fa\uff0c\u6e17\u900f\u8fdb\u83f2\u4e9a\u4f53\u5185\u3002</p><p>\u5c11\u5973\u539f\u672c\u51b0\u6f88\u6c89\u9759\u7684\u773c\u795e\u53d8\u5f97\u8ff7\u832b\u800c\u7a7a\u6d1e\u8d77\u6765\uff0c\u5979\u7684\u610f\u8bc6\u5df2\u7ecf\u88ab\u6cd5\u672f\u6240\u4fb5\u88ad\uff0c\u5728\u63a5\u4e0b\u6765\u7684\u4e00\u6bb5\u65f6\u95f4\u91cc\uff0c\u53ea\u8981\u743c\u6069\u4e0d\u89e3\u9664\u6cd5\u672f\uff0c\u5979\u4fbf\u4f1a\u50cf\u5080\u5121\u4e00\u6837\u5b8c\u5168\u670d\u4ece\u547d\u4ee4\uff0c\u56de\u7b54\u95ee\u9898\uff0c\u800c\u8fd9\u6b63\u662f\u743c\u6069\u6240\u60f3\u8981\u7684\u3002</p><p>\u743c\u6069\u518d\u6b21\u8be2\u95ee\uff0c\u800c\u5f97\u5230\u7684\u56de\u7b54\u5e76\u6ca1\u6709\u6539\u53d8\uff0c\u83f2\u4e9a\u786e\u5b9e\u4e0d\u77e5\u9053\u81ea\u5df1\u7684\u7236\u4eb2\u8eab\u4efd\uff0c\u5979\u751a\u81f3\u4ece\u6ca1\u60f3\u8fc7\u81ea\u5df1\u53ef\u80fd\u662f\u90a3\u4f4d\u90aa\u795e\u7684\u5b50\u55e3\u3002\u5979\u6bcd\u4eb2\u65e9\u592d\uff0c\u81ea\u5df1\u5728\u4e00\u6b21\u5076\u7136\u7684\u673a\u4f1a\u4e2d\u9047\u5230\u8fea\u74e6\u514b\u7684\u7236\u4eb2\uff0c\u88ab\u4ed6\u6536\u517b\uff0c\u968f\u4f8f\u5112\u4eec\u5728\u5e7d\u6697\u5730\u57df\u7684\u5e03\u7075\u767b\u77f3\u57ce\u5c45\u4f4f\u4e86\u51e0\u5e74\u3002\u5728\u53bb\u5e74\uff081371DR\uff09\uff0c\u5e7d\u6697\u5730\u57df\u7206\u53d1\u4e86\u4e00\u573a\u6218\u4e89\uff0c\u9b54\u7d22\u5e03\u83b1\u57ce\u7684\u9ed1\u6697\u7cbe\u7075\u4eec\u653b\u7834\u4e86\u5e03\u7075\u767b\u77f3\u57ce\uff0c\u5927\u90e8\u5206\u5730\u5e95\u4f8f\u5112\u88ab\u6740\u6b7b\u6216\u8005\u6293\u83b7\uff08\u5305\u62ec\u83f2\u4e9a\u7684\u517b\u7236\uff0c\u6df1\u571f\u9884\u8a00\u5e08\u8fea\u74e6\u514b\u5148\u751f\uff09\uff0c\u4f46\u8fd8\u6709\u5c11\u90e8\u5206\u9003\u5230\u4e86\u5730\u8868\uff08\u5305\u62ec\u83f2\u4e9a\u548c\u5251\u821e\u8005\u8fea\u74e6\u514b\u8fd9\u5bf9\u5144\u59b9\uff09\u3002\u540e\u6765\u4ed6\u4eec\u88ab\u5974\u96b6\u8d29\u5b50\u6293\u4f4f\uff0c\u8f97\u8f6c\u8fd0\u5230\u4e86\u585e\u5c14\uff0c\u8fea\u74e6\u514b\u88ab\u6254\u8fdb\u51b3\u6597\u573a\u5f53\u89d2\u6597\u58eb\uff0c\u83f2\u4e9a\u5219\u88ab\u5f17\u96f7\u65af\u770b\u4e2d\uff0c\u6210\u4e3a\u4ed6\u7684\u4f8d\u5973\u3002</p><p>\u201c\u539f\u6765\u5982\u6b64\u4e48\uff0c\u4e5f\u597d\uff0c\u8fd9\u6837\u4e00\u6765\u5c31\u6ca1\u5fc5\u8981\u6740\u4eba\u706d\u53e3\u4e86\u3002\u201d</p><p>\u743c\u6069\u6b63\u8981\u51c6\u5907\u65bd\u6cd5\u89e3\u9664\u5bf9\u83f2\u4e9a\u7684\u7cbe\u795e\u63a7\u5236\uff0c\u7a81\u7136\u8111\u4e2d\u6d6e\u73b0\u51fa\u4e00\u4e2a\u90aa\u6076\u7684\u5ff5\u5934\u3002\u8fd9\u4e2a\u60f3\u6cd5\u8ba9\u4ed6\u81ea\u5df1\u90fd\u5fae\u5fae\u4e00\u60ca\uff0c\u4f46\u968f\u5373\u91ca\u7136\uff0c\u53cd\u6b63\u73b0\u5728\u73ca\u5609\u548c\u6885\u83f2\u65af\u90fd\u4e0d\u5728\u8eab\u8fb9\uff0c\u653e\u7eb5\u4e00\u4e0b\u53c8\u4f55\u59a8\u3002</p><p>\u4ed6\u53d1\u51fa\u6307\u4ee4\uff0c\u8ba9\u83f2\u4e9a\u6380\u5f00\u4ed6\u7684\u888d\u5b50\uff0c\u5f20\u53e3\u542b\u4f4f\uff0c\u7f13\u7f13\u541e\u5410\u3002\u4f5c\u4e3a\u4f8d\u5973\uff0c\u83f2\u4e9a\u663e\u7136\u662f\u63a5\u53d7\u8fc7\u670d\u4f8d\u7537\u4eba\u7684\u8bad\u7ec3\u7684\uff0c\u5c3d\u7ba1\u662f\u5728\u65e0\u610f\u8bc6\u4e2d\uff0c\u4f46\u52a8\u4f5c\u7684\u8f7b\u91cd\u7f13\u6025\u4f9d\u65e7\u6070\u5230\u597d\u5904\u3002\u743c\u6069\u6ee1\u610f\u5730\u4eab\u53d7\u7740\uff0c\u7136\u540e\u91ca\u653e\u51fa\u4e86\u89e3\u9664\u9b54\u6cd5\u3002</p><p>\u4ed6\u89c2\u5bdf\u7740\u5c11\u5973\u7684\u53cd\u5e94\u3002</p><p>\u767d\u8272\u7684\u96fe\u6c14\u6563\u53bb\uff0c\u83f2\u4e9a\u7684\u773c\u795e\u6062\u590d\u4e86\u5e73\u65f6\u7684\u51b0\u6f88\u6c89\u9759\uff0c\u5979\u663e\u7136\u8bb0\u4e0d\u8d77\u6765\u81ea\u5df1\u521a\u624d\u90fd\u8bf4\u4e86\u4ec0\u4e48\uff0c\u505a\u4e86\u4ec0\u4e48\uff0c\u4f46\u5979\u4ec5\u4ec5\u53ea\u7528\u4e86\u534a\u79d2\u949f\u7684\u65f6\u95f4\u6765\u91cd\u65b0\u9002\u5e94\u8fd9\u4e2a\u4e16\u754c\uff0c\u786e\u5b9a\u81ea\u5df1\u7684\u5904\u5883\uff0c\u7136\u540e\u4fbf\u82e5\u65e0\u5176\u4e8b\u822c\u7ee7\u7eed\u52a8\u4f5c\u3002</p><p>\u743c\u6069\u8f7b\u8f7b\u62cd\u7740\u5979\u7684\u5934\uff0c\u793a\u610f\u5979\u8eba\u5230\u5e8a\u4e0a\u53bb\u3002\u201c\u4f60\u6709\u70b9\u8ba9\u6211\u51fa\u4e4e\u610f\u6599\uff0c\u5c0f\u59d0\u3002\u201d\u743c\u6069\u8bf4\u3002</p><p>\u201c\u6211\u53ea\u662f\u60f3\u6d3b\u4e0b\u6765\u3002\u201d\u5c11\u5973\u8bf4\uff0c\u6162\u6162\u892a\u53bb\u81ea\u5df1\u7684\u8863\u670d\uff0c\u7b49\u5f85\u7740\u3002</p></article>"}}}]);
