(window.webpackJsonp=window.webpackJsonp||[]).push([[221],{244:function(p){p.exports={chapterTitle:null,title:"\u7b2c221\u7ae0 \u751f\u9699",content:"<article><h2>\u7b2c221\u7ae0 \u751f\u9699</h2><p>\u3000\u3000\u6d77\u5dde\u5e02\u7684\u897f\u5317\u90ca\uff0c\u5317\u5173\u533a\u7684\u65b0\u57ce\u6e2f\uff0c\u6709\u9526\u57ce\u96c6\u56e2\u7684\u4e00\u5904\u79cd\u690d\u56ed\u3002\u8bf4\u662f\u79cd\u690d\u56ed\uff0c\u5176\u5b9e\u662f\u8d75\u9526\u8363\u5efa\u7684\u79c1\u4eba\u56ed\u5b50\uff0c\u524d\u524d\u540e\u540e\u5360\u5730\u4e09\u56db\u767e\u4ea9\uff0c\u79cd\u690d\u4e86\u4e9b\u7af9\u6811\u82b1\u6728\uff0c\u4ece\u7d27\u90bb\u7740\u5927\u9053\u7684\u5ead\u56ed\u5927\u95e8\uff0c\u4e00\u6761\u5341\u7c73\u5bbd\u7684\u6c34\u6ce5\u752c\u9053\u5f80\u79cd\u690d\u56ed\u6df1\u5904\u5ef6\u4f38\uff0c\u4e00\u7247\u5f00\u9614\u7684\u8349\u9642\u85cf\u5728\u79cd\u690d\u56ed\u6df1\u5904\uff0c\u8349\u9642\u4e0a\u6709\u4e03\u680b\u522b\u5885\u7c07\u62e5\u5728\u4e00\u8d77\u3002</p><p>\u3000\u3000\u4e5d\u5341\u5e74\u4ee3\u521d\uff0c\u8d75\u9526\u8363\u5c31\u5efa\u597d\u8fd9\u5ea7\u56ed\u5b50\uff0c\u4f20\u8a00\u82b1\u4e86\u4e09\u5343\u591a\u4e07\uff0c\u4e01\u5411\u5c71\u5728\u4efb\u4e0a\u65f6\uff0c\u4e07\u5411\u524d\u3001\u4e07\u52c7\u7ed9\u538b\u7684\u62ac\u4e0d\u8d77\u5934\uff0c\u4e0e\u4e07\u5bb6\u540c\u6c14\u8fde\u679d\u7684\u8d75\u9526\u8363\u8fd8\u4e0d\u6562\u4e3e\u5bb6\u642c\u8fdb\u6765\uff0c\u4e0d\u8fc7\u8fd9\u91cc\u6210\u4e3a\u4ed6\u4eec\u79c1\u4e0b\u91cc\u76f8\u805a\u7684\u573a\u6240\uff0c\u8bbe\u65bd\u670d\u52a1\u4e0d\u6bd4\u897f\u57ce\u996d\u5e97\u5dee\u534a\u661f\uff0c\u4eab\u53d7\u4e00\u4e9b\u7279\u6b8a\u7684\u670d\u52a1\u8fd8\u80fd\u907f\u4eba\u8033\u76ee\u3002</p><p>\u3000\u3000\u4e01\u5411\u5c71\u5012\u53f0\u4e4b\u540e\uff0c\u8fd9\u4e00\u5904\u573a\u6240\uff0c\u5728\u6d77\u5dde\u5df2\u7ecf\u662f\u534a\u516c\u5f00\u7684\u5b58\u5728\u3002\u6587\u5c71\u996d\u5e97\u6bd5\u7adf\u662f\u516c\u5bb6\u7684\u573a\u6240\uff0c\u4e0d\u6bd4\u8fd9\u91cc\uff0c\u79d8\u5bc6\u6027\u66f4\u5f3a\uff0c\u5468\u5bcc\u660e\u5e74\u540e\u6765\u8fc7\u4e00\u4e24\u6b21\uff0c\u5c31\u559c\u6b22\u8fd9\u4e2a\u5730\u65b9\uff0c\u4ed6\u81ea\u6301\u8eab\u4efd\uff0c\u8fd8\u4e0d\u4f1a\u505a\u4e9b\u592a\u8352\u6deb\u7684\u4e8b\uff0c\u66f4\u4e0d\u60f3\u7ed9\u8d75\u9526\u8363\u63a7\u5236\u4f4f\uff0c\u771f\u8981\u56fe\u4eab\u53d7\uff0c\u4f1a\u8ba9\u66f4\u4eb2\u8fd1\u7684\u4eba\u5b89\u6392\u3002\u4f46\u662f\u4eba\u5904\u95f9\u4e16\u4e2d\uff0c\u5076\u5c14\u5230\u8fd9\u56ed\u5b50\u91cc\u6765\u9759\u4e00\u9759\uff0c\u5374\u5341\u5206\u7684\u8212\u5766\uff0c\u4e0d\u7531\u7684\u611f\u6168\uff1a\u5b98\u505a\u7684\u518d\u5927\uff0c\u4e5f\u4e0d\u6bd4\u624b\u91cc\u6709\u94b1\u597d\uff0c\u54ea\u4e2a\u515a\u653f\u5e72\u90e8\u6709\u80c6\u91cf\u7ed9\u81ea\u5df1\u5efa\u8fd9\u4e48\u4e00\u5904\u56ed\u5b50\uff1f</p><p>\u3000\u3000\u5468\u5bcc\u660e\u63a5\u5230\u91d1\u56fd\u6d77\u7684\u7535\u8bdd\u91cc\uff0c\u4ed6\u6b63\u5728\u8d75\u9526\u8363\u5317\u5173\u7684\u56ed\u5b50\u91cc\u73a9\u724c\uff0c\u9664\u4e86\u8d75\u9526\u8363\uff0c\u8fd8\u6709\u5e02\u59d4\u79d8\u4e66\u957f\u5468\u6653\u5065\uff0c\u4ee5\u53ca\u65b0\u52a0\u5761\u65b0\u57ce\u96c6\u56e2\u7684\u526f\u603b\u88c1\u7f57\u6587\u519b\u3002</p><p>\u3000\u3000\u8d75\u9526\u8363\u7684\u9526\u57ce\u5730\u4ea7\u5c31\u6709\u65b0\u57ce\u96c6\u56e2\u7684\u6295\u8d44\uff0c\u8fd9\u6b21\u62ff\u4e0b\u6c99\u7530\u8857\u5e02\u91cc\u7684\u90a3\u5757\u5730\uff0c\u8fd8\u8981\u7ee7\u7eed\u63a5\u53d7\u65b0\u57ce\u96c6\u56e2\u7684\u5165\u8d44\u3002</p><p>\u3000\u3000\u5bf9\u4e8e\u65b0\u57ce\u96c6\u56e2\u6765\u8bf4\uff0c\u4ed6\u4eec\u66f4\u559c\u6b22\u76f4\u63a5\u5bf9\u5177\u4f53\u7684\u9879\u76ee\u5165\u8d44\uff0c\u8fd9\u6837\u6765\u64a4\u51fa\u53bb\u4e5f\u5feb\u3002</p><p>\u3000\u3000\u8d75\u9526\u8363\u4e0e\u5468\u5bcc\u660e\u642d\u4e0a\u5173\u7cfb\uff0c\u8fd8\u662f\u56e0\u4e3a\u5468\u5bcc\u660e\u7684\u5973\u513f\u5468\u7ea2\u6885\u65c5\u5c45\u65b0\u52a0\u5761\u6210\u7acb\u4e00\u5bb6\u5916\u8d38\u4ee3\u7406\u516c\u53f8\uff0c\u8d75\u9526\u8363\u5148\u901a\u8fc7\u65b0\u57ce\u96c6\u56e2\u4e0e\u5468\u7ea2\u6885\u7684\u516c\u53f8\u53d1\u751f\u4e1a\u52a1\u4e0a\u7684\u5173\u7cfb\uff0c\u9001\u4e86\u51e0\u7b14\u597d\u5904\u53ef\u89c2\u7684\u4e1a\u52a1\uff0c\u81ea\u7136\u5c31\u4e0e\u5468\u5bcc\u660e\u642d\u4e0a\u5173\u7cfb\uff0c\u8fd9\u624d\u6709\u5e74\u540e\u4e07\u5bb6\u4e0e\u5468\u5bcc\u660e\u59a5\u534f\u7684\u4e8b\u60c5\u53d1\u751f\u3002</p><p>\u3000\u3000\u5468\u5bcc\u660e\u4e0e\u4e07\u5bb6\u7236\u5b50\u4e00\u76f4\u4ee5\u6765\u90fd\u6709\u77db\u76fe\uff0c\u6709\u8d75\u9526\u8363\u5c45\u4e2d\u5abe\u548c\uff0c\u624d\u4e3a\u4e86\u79cd\u79cd\u5229\u76ca\u8d70\u5230\u4e00\u8d77\uff0c\u5468\u5bcc\u660e\u63a5\u5230\u91d1\u56fd\u6d77\u7684\u7535\u8bdd\uff0c\u4e0d\u6653\u5f97\u91d1\u56fd\u6d77\u662f\u5426\u6709\u610f\uff0c\u5355\u5355\u53ea\u63d0\u5230\u4e07\u5929\u624d\u7684\u540d\u5b57\uff0c\u6ca1\u6709\u63d0\u8d75\u9526\u8363\u7684\u513f\u5b50\u8d75\u53f8\u660e\uff0c\u5468\u5bcc\u660e\u60f3\u8d77\u524d\u4e9b\u5929\u4e07\u52c7\u4e0e\u5510\u5b66\u8c26\u4eb2\u70ed\u7684\u6a21\u6837\uff0c\u5fc3\u91cc\u538b\u7740\u4e00\u4e1d\u4e0d\u5feb\uff0c\u53ea\u662f\u6de1\u6de1\u7684\u8bf4\uff1a\u201c\u6211\u77e5\u9053\u4e86\uff0c\u4e07\u526f\u5e02\u957f\u90a3\u91cc\u6709\u6ca1\u6709\u901a\u77e5\uff1f\u201d</p><p>\u3000\u3000\u91d1\u56fd\u6d77\u6307\u793a\u5e02\u5c40\u6d3e\u51fa\u5f97\u529b\u5e72\u8b66\u8ffd\u67e5\u6b64\u6848\u7684\u540c\u65f6\uff0c\u9996\u5148\u5c06\u6d88\u606f\u544a\u8bc9\u5468\u5bcc\u660e\uff0c\u4e07\u52c7\u90a3\u91cc\u80fd\u62d6\u4e00\u5206\u949f\uff0c\u4ed6\u5f53\u7136\u8981\u62d6\u8db3\u4e00\u5206\u949f\uff0c\u4ed6\u8fd9\u6837\u5904\u7f6e\uff0c\u53ef\u4ee5\u8bf4\u662f\u7ed9\u8db3\u5468\u5bcc\u660e\u7684\u9762\u5b50\uff0c\u5468\u5bcc\u660e\u4e5f\u6ca1\u6709\u60f3\u5230\u4ed6\u53e6\u6709\u7528\u5fc3\u3002</p><p>\u3000\u3000\u5468\u5bcc\u660e\u5fc3\u60f3\u7ed9\u4e07\u52c7\u4e00\u4e9b\u6559\u8bad\uff0c\u514d\u5f97\u4ed6\u5fd8\u4e4e\u6240\u4ee5\uff0c\u5e73\u6de1\u7684\u8bf4\uff1a\u201c\u4f60\u90a3\u8fb9\u5c31\u4f9d\u6b63\u5e38\u7684\u7a0b\u5e8f\u6765\uff0c\u6700\u597d\u80fd\u907f\u514d\u4e8b\u60c5\u53d1\u751f\uff0c\u627e\u4e2a\u9002\u5f53\u7684\u65f6\u673a\u77e5\u4f1a\u4e07\u526f\u5e02\u957f\u4e00\u58f0\u2026\u2026\u201d\u4ed6\u7684\u8bdd\u8bf4\u5f97\u6a21\u68f1\u4e24\u53ef\uff0c\u4e5f\u6709\u81ea\u6211\u4fdd\u62a4\u7684\u610f\u601d\uff0c\u77a5\u4e86\u5bf9\u9762\u7684\u8d75\u9526\u8363\u4e00\u773c\uff0c\u4e5f\u51b3\u5b9a\u4e0d\u8ddf\u8d75\u9526\u8363\u8bf4\uff0c\u4ed6\u77e5\u9053\u8d75\u9526\u8363\u8ddf\u4e07\u5bb6\u7684\u5173\u7cfb\u6bd4\u4ed6\u8ddf\u8d75\u9526\u8363\u8981\u5bc6\u5207\u5f97\u591a\uff0c\u4ed6\u751a\u81f3\u5e0c\u671b\u4e8b\u60c5\u95f9\u5927\u4e00\u4e9b\uff0c\u8ba9\u4e07\u52c7\u5f00\u53e3\u6765\u6c42\u4ed6\uff0c\u597d\u53eb\u4ed6\u77e5\u9053\u8c01\u624d\u80fd\u771f\u6b63\u7684\u906e\u4f4f\u6d77\u5dde\u7684\u5929\u3002\u8bed\u6c14\u6de1\u6de1\u7684\uff0c\u4fbf\u5c06\u7535\u8bdd\u6302\u4e86\u3002</p><p>\u3000\u3000\u724c\u684c\u4e0a\u7684\u5176\u4ed6\u4e09\u4e2a\u4eba\uff0c\u53ea\u5f53\u662f\u5e02\u59d4\u7684\u4e8b\u60c5\u6709\u4eba\u6253\u7535\u8bdd\u6765\u8ddf\u4ed6\u6c47\u62a5\uff0c\u5fc3\u91cc\u597d\u5947\uff0c\u5374\u4e0d\u65b9\u4fbf\u95ee\u3002</p><p>\u3000\u3000\u5468\u5bcc\u660e\u6ca1\u6709\u5fc3\u601d\u7559\u4e0b\u6765\u7ee7\u7eed\u73a9\u724c\uff0c\u5c06\u724c\u5f80\u684c\u5b50\u4e2d\u95f4\u4e00\u63a8\uff1a\u201c\u4eca\u5929\u5c31\u8981\u8fd9\u91cc\u5427\u3002\u201d\u8d77\u8eab\u5c31\u8981\u8d70\u3002\u8fd9\u65f6\u5019\u624d\u516b\u70b9\u534a\u949f\uff0c\u8d75\u9526\u8363\u6709\u4e9b\u5947\u602a\uff0c\u8fd8\u662f\u8d70\u8fc7\u6765\u62ff\u8d77\u4ed6\u62bd\u5c49\u91cc\u7684\u94b1\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u5468\u4e66\u8bb0\u6bcf\u6b21\u90fd\u8981\u5fd8\u8bb0\u5c06\u8d62\u7684\u94b1\u62ff\u8d70\uff0c\u6211\u53ef\u6ca1\u8d23\u4efb\u66ff\u5468\u4e66\u8bb0\u4fdd\u7ba1\u3002\u201d\u7a0d\u7a0d\u6574\u4e86\u6574\uff0c\u6709\u4e09\u56db\u4e07\u7684\u6837\u5b50\uff0c\u8fd8\u5c0f\u5fc3\u7ffc\u7ffc\u7684\u5c06\u96f6\u5e01\u53e0\u5728\u4e0a\u9762\uff0c\u9012\u7ed9\u5468\u5bcc\u660e\u3002</p><p>\u3000\u3000\u5468\u5bcc\u660e\u5c06\u96f6\u5e01\u62ff\u4e0b\u6765\u4e22\u684c\u4e0a\uff1a\u201c\u627e\u96f6\u94b1\u4e0d\u5bb9\u6613\uff0c\u514d\u5f97\u4e0b\u56de\u73a9\u627e\u4e0d\u5230\u96f6\u94b1\u3002\u201d</p><p>\u3000\u3000\u5468\u6653\u5065\u4ece\u62bd\u5c49\u91cc\u62ff\u51fa\u4e00\u53e0\u94b1\uff0c\u8ddf\u7740\u5468\u5bcc\u660e\u79bb\u5f00\u3002</p><p>\u3000\u3000\u201c\u6bcf\u661f\u671f\u6253\u8fd9\u4e48\u4e00\u4e24\u6b21\u724c\uff0c\u8d75\u5148\u751f\u5012\u662f\u5f88\u8f9b\u82e6\uff0c\u8fd8\u4e0d\u5982\u4e00\u6b21\u90fd\u585e\u7ed9\u4ed6\u3002\u201d\u7f57\u6587\u519b\u8f7b\u7b11\u7740\uff0c\u201c\u56fd\u5185\u7684\u5b98\u5458\uff0c\u4ec0\u4e48\u65f6\u5019\u80c6\u5b50\u53d8\u5c0f\u4e86\uff1f\u201d</p><p>\u3000\u3000\u6bcf\u6b21\u73a9\u724c\u4e00\u4e24\u4e07\u3001\u4e09\u56db\u4e07\u7684\u7ed9\uff0c\u5927\u534a\u5e74\u585e\u5230\u5468\u5bcc\u660e\u53e3\u888b\u91cc\u4e5f\u4e0d\u4e0b\u4e94\u5341\u4e07\uff0c\u603b\u6bd4\u4e0d\u4e86\u4e00\u6b21\u585e\u7ed9\u4ed6\u4e94\u5341\u4e07\u9707\u61be\uff0c\u5468\u590d\u660e\u6700\u8fd1\u7684\u6001\u5ea6\u4f3c\u4e4e\u6709\u4e9b\u61c8\u6020\uff0c\u5bf9\u4ed6\u4eec\u7684\u4e8b\u60c5\u4e0d\u90a3\u4e48\u70ed\u5fc3\u3002</p><p>\u3000\u3000\u8d75\u9526\u8363\u60f3\u7740\u5468\u5bcc\u660e\u5306\u5fd9\u79bb\u5f00\u65f6\u7684\u795e\u60c5\uff0c\u5468\u5bcc\u660e\u770b\u4e0a\u53bb\u795e\u8272\u5e73\u6de1\uff0c\u5584\u4e8e\u5bdf\u8a00\u89c2\u8272\u7684\u8d75\u9526\u8363\u8fd8\u662f\u770b\u51fa\u53d1\u751f\u4ec0\u4e48\u4e8b\u60c5\uff0c\u5e76\u4e14\u4f3c\u4e4e\u5e76\u4e0d\u6025\u7740\u901a\u77e5\u4e07\u52c7\u3002</p><p>\u3000\u3000\u8d75\u9526\u8363\u8d70\u5230\u623f\u95f4\u7684\u89d2\u843d\u91cc\uff0c\u62ff\u8d77\u89d2\u67dc\u4e0a\u7684\u7535\u8bdd\uff0c\u62e8\u7ed9\u4e07\u52c7\uff0c\u624b\u673a\u5360\u7ebf\uff0c\u62e8\u4e07\u52c7\u5bb6\u91cc\u7684\u7535\u8bdd\u4e5f\u662f\u5360\u7ebf\uff0c\u62d4\u4e07\u52c7\u79d8\u4e66\u7684\u7535\u8bdd\uff0c\u8fd8\u662f\u5360\u7ebf\u3002\u4e07\u5bb6\u8001\u7237\u5b50\u4e07\u5411\u524d\u8c03\u5230\u7701\u91cc\uff0c\u501f\u7740\u8003\u5bdf\u7684\u673a\u4f1a\u51fa\u56fd\u65c5\u6e38\u53bb\u4e86\uff0c\u6b27\u6d32\u5341\u56fd\u73a9\u4e00\u5708\uff0c\u8fd8\u6709\u4e94\u516d\u5929\u624d\u80fd\u56de\u56fd\u3002</p><p>\u3000\u3000\u8d75\u9526\u8363\u5fc3\u91cc\u538b\u7740\u4e0d\u7965\u7684\u9884\u611f\uff0c\u60f3\u7740\u5468\u5bcc\u660e\u79bb\u5f00\u65f6\u9634\u90c1\u7684\u795e\u60c5\uff0c\u624b\u6307\u90fd\u6709\u4e9b\u6296\uff0c\u4e0d\u65ad\u7684\u62d4\u6253\u51e0\u4e2a\u7535\u8bdd\uff0c\u597d\u4e0d\u5bb9\u6613\u62e8\u8fdb\u53bb\uff0c\u662f\u4e07\u52c7\u7684\u79d8\u4e66\u63a5\u7684\u7535\u8bdd\uff1a\u201c\u4e07\u5e02\u957f\u5462\uff1f\u201d</p><p>\u3000\u3000\u201c\u521a\u624d\u7ed9\u4f60\u6253\u7535\u8bdd\uff0c\u4e00\u76f4\u6253\u4e0d\u901a\u2026\u2026\u8d75\u603b\uff0c\u51fa\u4e86\u4e9b\u4e8b\u60c5\uff0c\u5e02\u5c40\u521a\u521a\u4f20\u6765\u6d88\u606f\uff0c\u8bf4\u5929\u624d\u4e0e\u53f8\u660e\u4eca\u665a\u627e\u4e86\u4e9b\u4eba\uff0c\u6253\u7b97\u6559\u8bad\u4ed6\u4eec\u5b66\u6821\u7684\u4e00\u540d\u5973\u6559\u5e08\uff0c\u662f\u5efa\u59d4\u526f\u4e3b\u4efb\u9ec4\u67f1\u5168\u62a5\u7684\u6848\uff0c\u539f\u5148\u5929\u624d\u4e0e\u53f8\u660e\u51c6\u5907\u62c9\u4ed6\u513f\u5b50\u4e00\u8d77\u53bb\uff0c\u4ed6\u513f\u5b50\u5b6c\uff0c\u6ca1\u6562\u53bb\uff0c\u5374\u80cc\u5730\u91cc\u62a5\u4e86\u6848\u3002\u53ef\u80fd\u5929\u624d\u4e0e\u53f8\u660e\u4ed6\u4eec\u7684\u60f3\u6cd5\u6709\u4e9b\u6fc0\u70c8\uff0c\u5e02\u5c40\u7814\u7a76\u4e86\u4e00\u4e0b\uff0c\u51b3\u5b9a\u76f4\u63a5\u5411\u653f\u6cd5\u59d4\u4e66\u8bb0\u91d1\u56fd\u6d77\u6c47\u62a5\uff0c\u91d1\u56fd\u6d77\u5230\u5e95\u6253\u7b97\u600e\u4e48\u505a\uff0c\u8fd8\u4e0d\u6e05\u695a\uff0c\u5e02\u5c40\u5df2\u7ecf\u6d3e\u51fa\u51e0\u6279\u8b66\u529b\uff0c\u5e78\u597d\u4e07\u5e02\u957f\u8ddf\u5e02\u5c40\u7684\u4eba\u5173\u7cfb\u5bc6\u5207\uff0c\u624d\u63d0\u524d\u5f97\u5230\u6d88\u606f\u2026\u2026\u201d</p><p>\u3000\u3000\u8d75\u9526\u8363\u6068\u5f97\u5dee\u70b9\u5c06\u7535\u8bdd\u673a\u7838\u6389\uff0c\u91d1\u56fd\u6d77\u662f\u5916\u6765\u6237\uff0c\u8fd9\u4e8b\u6d89\u53ca\u5e02\u91cc\u9886\u5bfc\u7684\u5b50\u5973\uff0c\u4ed6\u4e0d\u76f4\u63a5\u8ddf\u4e07\u52c7\u6253\u62db\u547c\uff0c\u4e5f\u4f1a\u8ddf\u5468\u5bcc\u660e\u6c47\u62a5\u3002\u5468\u5bcc\u660e\u521a\u624d\u5c31\u662f\u63a5\u7684\u91d1\u56fd\u6d77\u7684\u7535\u8bdd\uff0c\u5374\u82e5\u65e0\u5176\u4e8b\u7684\u7ed3\u675f\u724c\u5c40\uff0c\u4ed6\u5230\u5e95\u60f3\u505a\u4ec0\u4e48\uff1f\u8fd9\u5927\u534a\u5e74\u6765\u966a\u4ed6\u73a9\u724c\uff0c\u56db\u4e94\u5341\u4e07\u90fd\u4ed6\u5988\u7684\u5582\u72fc\u4e86\u3002</p><p>\u3000\u3000\u8d75\u9526\u8363\u81ea\u8ba4\u4e3a\u5fc3\u72e0\u624b\u8fa3\uff0c\u60f3\u5230\u5468\u5bcc\u660e\u79bb\u5f00\u65f6\u9634\u90c1\u7684\u773c\u795e\uff0c\u5fc3\u5934\u6709\u4e9b\u53d1\u5bd2\uff0c\u5fc3\u60f3\uff1a\u8fd9\u624d\u662f\u5403\u8089\u4e0d\u5410\u9aa8\u5934\u7684\u4e3b\u3002</p><p>\u3000\u3000\u201c\u4e07\u5e02\u957f\u4eba\u5462\uff1f\u201d\u8d75\u9526\u8363\u9634\u6c89\u7740\u8138\u95ee\u3002</p><p>\u3000\u3000\u201c\u73b0\u5728\u8054\u7cfb\u4e0d\u4e0a\u5929\u624d\u3001\u53f8\u660e\uff0c\u8d75\u603b\u7684\u7535\u8bdd\u4e00\u65f6\u4e5f\u6253\u4e0d\u901a\uff0c\u4e07\u5e02\u957f\u8ba9\u6240\u6709\u4eba\u90fd\u51fa\u53bb\u627e\u4ed6\u4eec\uff0c\u4ed6\u4eb2\u81ea\u5f00\u8f66\u51fa\u53bb\u4e86\uff0c\u8ba9\u6211\u7559\u4e0b\u8054\u7cfb\u8d75\u603b\u3002\u201d</p><p>\u3000\u3000\u8d75\u9526\u8363\u8fd8\u6ca1\u6709\u614c\u795e\uff0c\u95ee\u9053\uff1a\u201c\u4e00\u4e2d\u7684\u5973\u6559\u5e08\u662f\u8c01\uff0c\u4f4f\u54ea\u91cc\uff0c\u7535\u8bdd\u53f7\u7801\u662f\u591a\u5c11\uff0c\u5feb\u6d3e\u4eba\u53bb\u90a3\u91cc\u76ef\u7740\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5177\u4f53\u7684\u4e8b\u60c5\u4e0d\u6e05\u695a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4e07\u5e02\u957f\u4e0d\u662f\u5728\u5e02\u5c40\u6709\u670b\u53cb\u5417\uff0c\u4e09\u4e2a\u95ee\u9898\uff0c\u4e00\u4e2a\u95ee\u9898\u5341\u4e07\u5757\uff0c\u94b1\u6211\u6765\u51fa\u2026\u2026\u201d</p><p>\u3000\u3000\u8d75\u9526\u8363\u8ba4\u5b9a\u5468\u5bcc\u660e\u7684\u5fc3\u601d\u53d8\u4e86\uff0c\u591a\u5c0f\u7684\u4e8b\u60c5\u4e5f\u4f1a\u641e\u6210\u5927\u4e8b\u60c5\uff0c\u4e00\u5b9a\u8981\u4e0d\u60dc\u4e00\u5207\u4ee3\u4ef7\u5236\u6b62\u4e8b\u60c5\u53d1\u751f\uff01</p><p>\u3000\u3000\u201c\u66f9\u5b8f\u5b9d\u3001\u8d75\u4e16\u8d35\uff01\u201d\u8d75\u9526\u8363\u987e\u4e0d\u53ca\u62db\u547c\u7f57\u6587\u519b\uff0c\u5927\u58f0\u558a\u4ed6\u7684\u4fdd\u9556\uff0c\u4e00\u8fb9\u8bd5\u5708\u62e8\u4ed6\u513f\u5b50\u7684\u624b\u673a\u53f7\uff0c\u5173\u673a\uff01</p><p>\u3000\u3000\u201c\u53f8\u660e\u4eca\u5929\u6709\u6ca1\u6709\u627e\u8fc7\u4e0b\u9762\u4eba\uff1f\u201d\u8d75\u9526\u95ee\u95fb\u58f0\u8d76\u6765\u7684\u4fdd\u9556\uff0c\u5927\u5bb6\u90fd\u6447\u5934\u4e0d\u77e5\u9053\u3002</p><p>\u3000\u3000\u56e0\u4e3a\u521a\u5230\u624b\u7684\u4e09\u83f1\u8dd1\u8f66\u53d7\u635f\uff0c\u8d75\u53f8\u660e\u6015\u4ed6\u8001\u5b50\u8d23\u9a82\uff0c\u8fd9\u79cd\u4e8b\u81ea\u7136\u4e0d\u4f1a\u627e\u9526\u57ce\u96c6\u56e2\u517b\u7684\u6253\u624b\u3002</p><p>\u3000\u3000\u8d75\u9526\u8363\u5c06\u624b\u4e0b\u4eba\u90fd\u6d3e\u4e86\u51fa\u53bb\uff0c\u8ba9\u4ed6\u4eec\u5206\u5934\u53bb\u627e\u4ed6\u513f\u5b50\u53ef\u80fd\u8ddf\u4e07\u5929\u624d\u53bb\u7684\u5730\u65b9\u3002</p><p>\u3000\u3000\u6700\u597d\u7684\u7ed3\u679c\u81ea\u7136\u662f\u5c06\u4eba\u622a\u4f4f\uff0c\u4f46\u662f\u5e02\u5c40\u5df2\u7ecf\u6d3e\u51fa\u597d\u51e0\u6279\u8b66\u529b\uff0c\u8c01\u80fd\u4fdd\u8bc1\u6bd4\u8b66\u65b9\u5148\u627e\u5230\u8fd9\u51e0\u4e2a\u6df7\u8d26\uff1f</p><p>\u3000\u3000\u8d75\u9526\u8363\u5fc3\u91cc\u6068\u5f97\u53d1\u75d2\uff0c\u65e2\u7136\u5bf9\u513f\u5b50\u4e0d\u4e89\u6c14\u7684\u6068\uff0c\u4e5f\u6709\u5bf9\u5468\u56f4\u8d76\u7740\u770b\u70ed\u95f9\u4eba\u7684\u6068\uff0c\u4e5f\u6709\u5bf9\u5468\u5bcc\u660e\u80cc\u53db\u7684\u6068\uff0c\u4ed6\u5fc5\u987b\u8981\u60f3\u7740\u4e07\u5168\u4e4b\u7b56\uff0c\u4ed6\u751a\u81f3\u641e\u4e0d\u6e05\u695a\u5468\u5bcc\u660e\u4e3a\u4ec0\u4e48\u5c31\u7a81\u7136\u53d8\u4e86\uff01</p><p>\u3000\u3000\u8d75\u9526\u8363\u6253\u901a\u4e07\u52c7\u7684\u7535\u8bdd\uff0c\u95ee\u4ed6\u5728\u54ea\u91cc\uff0c\u7535\u8bdd\u91cc\u6765\u4e0d\u53ca\u591a\u8bf4\u4ec0\u4e48\uff0c\u8d76\u7d27\u5f00\u8f66\u8fc7\u53bb\u6d77\u5dde\u4e00\u4e2d\u6c47\u5408\u3002\u4e07\u52c7\u6b64\u65f6\u8fd8\u6ca1\u6709\u63a5\u5230\u6b63\u5f0f\u7684\u901a\u77e5\uff0c\u4ed6\u4ece\u8d75\u9526\u8363\u5634\u91cc\u5f97\u77e5\u5468\u5bcc\u660e\u53ef\u80fd\u65e9\u5c31\u5728\u724c\u684c\u4e0a\u77e5\u9053\u6d88\u606f\uff0c\u5fc3\u5934\u5927\u6068\u7684\u5c06\u4ed6\u90a3\u8f86\u5c3c\u6851\u524d\u8f66\u95e8\u8e39\u9677\u4e0b\u53bb\uff1a\u201c\u64cd\u4ed6\u5a18\u7684\uff0c\u517b\u53ea\u72d7\u8fd8\u77e5\u9053\u5431\u5514\u4e24\u58f0\uff0c\u4ed6\u5a18\u7684\u662f\u5934\u6076\u72fc\uff0c\u4ed6\u5468\u5bcc\u660e\u5fd8\u4e86\u662f\u8c01\u8ba9\u4ed6\u5750\u7a33\u8fd9\u4e2a\u4f4d\u5b50\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e8b\u60c5\u4e0d\u4f1a\u8fd9\u4e48\u7b80\u5355\uff0c\u201d\u8d75\u9526\u8363\u5c06\u4e07\u52c7\u62c9\u8fdb\u4ed6\u7684\u7687\u51a0\uff0c\u53ef\u4ee5\u505a\u7684\u4e8b\u60c5\uff0c\u4ed6\u90fd\u6d3e\u4eba\u505a\u7684\uff0c\u4f46\u662f\u6ca1\u6709\u5177\u4f53\u7684\u6d88\u606f\uff0c\u53ef\u4ee5\u505a\u7684\u4e8b\u60c5\u5341\u5206\u6709\u9650\uff0c\u751a\u81f3\u4e0d\u77e5\u9053\u90a3\u51e0\u4e2a\u6df7\u8d26\u5c0f\u5b50\u60f3\u6559\u8bad\u7684\u662f\u54ea\u4e2a\u5973\u8001\u5e08\uff0c\u4e5f\u4e0d\u77e5\u9053\u90a3\u4e2a\u5973\u8001\u5e08\u600e\u4e48\u60f9\u6bdb\u7684\u8fd9\u51e0\u4e2a\u6df7\u8d26\u5c0f\u5b50\uff0c\u5b88\u5728\u5b66\u6821\u95e8\u53e3\u4e5f\u662f\u5984\u8d39\u529b\u6c14\uff0c\u201c\u5173\u952e\u662f\u5468\u5bcc\u660e\u7684\u6001\u5ea6\u8ba9\u4eba\u7422\u78e8\u4e0d\u900f\uff0c\u96be\u9053\u4ed6\u8fd8\u5728\u8bb0\u6068\u4ee5\u524d\u7684\u4e8b\u60c5\uff1f\u201d</p></article>"}}}]);
