(window.webpackJsonp=window.webpackJsonp||[]).push([[221],{243:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u516d\u8282 \u4ee5\u8eab\u6b89\u804c",content:"<article><h2>\u7b2c\u5341\u516d\u8282 \u4ee5\u8eab\u6b89\u804c</h2><p>\u8fd9\u662f\u4e00\u4e2a\u9633\u5149\u707f\u70c2\u7684\u6e05\u6668\uff0c\u743c\u6069\u4e00\u5927\u65e9\u5c31\u795e\u6e05\u6c14\u723d\u5730\u51fa\u95e8\u4e0a\u73ed\u3002\u5728\u6628\u5929\u508d\u665a\uff0c\u4ed6\u524d\u53bb\u585e\u5c14\uff0c\u5c06\u83f2\u4e9a\u4f53\u5185\u6b8b\u4f59\u7684\u795e\u529b\u5168\u90e8\u5438\u6536\uff0c\u73b0\u5728\u53ea\u5dee\u6162\u6162\u6d88\u5316\u3002\u591c\u91cc\u966a\u7740\u73ca\u5609\uff0c\u6402\u6402\u62b1\u62b1\u4e0a\u4e0b\u5176\u624b\uff0c\u5fd9\u5f97\u4e0d\u4ea6\u4e50\u4e4e\u3002</p><p>\u6309\u7167\u4e00\u4eba\u4e00\u5929\u8f6e\u6d41\u7684\u534f\u8bae\uff0c\u4eca\u665a\u5e94\u8be5\u662f\u8981\u966a\u6885\u83f2\u65af\u3002\u743c\u6069\u8003\u8651\u8981\u4e0d\u8981\u628a\u91c7\u7389\u8bc0\u7684\u4e8b\u60c5\u544a\u8bc9\u5979\uff0c\u6885\u83f2\u65af\u4e0d\u60f3\u6210\u4e3a\u90aa\u795e\uff0c\u5df4\u5c14\u795e\u529b\u5bf9\u5979\u6765\u8bf4\u5c31\u5b8c\u5168\u662f\u8d1f\u62c5\uff0c\u5982\u679c\u80fd\u6709\u673a\u4f1a\u6d88\u9664\u6389\uff0c\u76f8\u4fe1\u5979\u4f1a\u5f88\u4e50\u610f\u3002\u5b9e\u9645\u4e0a\uff0c\u5979\u4f53\u5185\u5982\u4eca\u6709\u5df4\u5c14\u795e\u529b\uff0c\u6709\u63d0\u5c14\u795e\u529b\uff0c\u8fd8\u6709\u9ece\u660e\u4e4b\u77f3\u7684\u529b\u91cf\uff0c\u5f7c\u6b64\u51b2\u7a81\u538b\u5236\uff0c\u5927\u5927\u9650\u5236\u4e86\u5979\u7684\u5b9e\u529b\u3002\u5982\u679c\u80fd\u591f\u6d88\u9664\u6389\u5df4\u5c14\u795e\u529b\uff0c\u76f8\u4fe1\u5b9e\u529b\u4f1a\u7a81\u98de\u731b\u8fdb\u5427\u3002</p><p>\u5f53\u7136\uff0c\u6700\u91cd\u8981\u7684\u662f\uff0c\u8fd9\u662f\u4e00\u4e2a\u540d\u6b63\u8a00\u987a\u53ef\u4ee5\u63a8\u5012\u6885\u83f2\u65af\u7684\u501f\u53e3\u3002\u5982\u679c\u5979\u8fd8\u8981\u5982\u5f80\u65e5\u4e00\u822c\u63a8\u8131\uff0c\u90a3\u743c\u6069\u53ea\u6015\u5c31\u771f\u8981\u7528\u70b9\u5f3a\u786c\u624b\u6bb5\u903c\u8feb\u4e86\u3002</p><p>\u5ff5\u53ca\u6b64\u5904\uff0c\u4e0d\u7531\u5f97\u5fc3\u60c5\u6109\u5feb\uff0c\u53ea\u89c9\u5f97\u5916\u9762\u7684\u9633\u5149\u4e5f\u683c\u5916\u660e\u5a9a\u7684\u51e0\u5206\u3002\u4ed6\u51fa\u4e86\u5bb6\u95e8\uff0c\u7ecf\u8fc7\u83ab\u5c3c\u5361\u5bb6\u95e8\u53e3\u65f6\uff0c\u9662\u5b50\u95e8\u63a8\u5f00\uff0c\u91cc\u9762\u8d70\u51fa\u4e00\u4e2a\u5c11\u5973\u6765\uff0c\u4e24\u4eba\u731d\u4e0d\u53ca\u9632\u6253\u4e86\u4e2a\u7167\u9762\uff0c\u90fd\u6014\u4e86\u6014\u3002</p><p>\u201c\u65e9\u4e0a\u597d\uff0c\u8299\u857e\u72c4\u3002\u201d\u743c\u6069\u6253\u62db\u547c\u3002</p><p>\u7136\u800c\u5bf9\u65b9\u5e76\u6ca1\u6709\u56de\u7b54\uff0c\u53ea\u662f\u7528\u5947\u602a\u7684\u773c\u795e\u76ef\u7740\u743c\u6069\u770b\u4e86\u51e0\u79d2\u949f\uff0c\u4e00\u8a00\u4e0d\u53d1\u8f6c\u8eab\u4fbf\u8d70\u3002\u743c\u6069\u4e5f\u53cd\u5e94\u8fc7\u6765\uff0c\u77e5\u9053\u81ea\u5df1\u4ed3\u4fc3\u95f4\u8ba4\u9519\u4e86\u4eba\uff0c\u8fd9\u4e2a\u5c11\u5973\u867d\u7136\u76f8\u8c8c\u548c\u8299\u857e\u72c4\u4e00\u6a21\u4e00\u6837\uff0c\u4f46\u6c14\u8d28\u5374\u8fe5\u7136\u76f8\u5f02\u3002\u8299\u857e\u72c4\u5a07\u67d4\u5355\u7eaf\uff0c\u4eff\u4f5b\u6e29\u5ba4\u91cc\u7684\u7cbe\u81f4\u82b1\u6735\uff0c\u8ba9\u4eba\u603b\u6709\u4e00\u78b0\u5373\u788e\u7684\u9519\u89c9\uff1b\u5979\u5374\u66f4\u52a0\u7684\u82f1\u9510\u903c\u4eba\uff0c\u6574\u4e2a\u4eba\u5c31\u50cf\u4e00\u53ea\u77eb\u5065\u7684\u5c0f\u730e\u8c79\uff0c\u6d3b\u529b\u6d0b\u6ea2\u4e2d\u4f3c\u4e4e\u8fd8\u900f\u7740\u4e9b\u8bb8\u51f6\u731b\u7684\u6c14\u606f\u3002</p><p>\u5979\u7a7f\u7684\u662f\u7d2b\u9ed1\u8272\u7684\u886c\u886b\u957f\u88e4\uff0c\u5f88\u7d27\u8eab\uff0c\u52fe\u52d2\u51fa\u5b8c\u7f8e\u7684\u8eab\u6750\uff0c\u5916\u9762\u867d\u7136\u7f69\u7740\u4e00\u4ef6\u7070\u8272\u6597\u7bf7\uff0c\u4f46\u4f9d\u65e7\u80fd\u6e05\u695a\u5730\u770b\u89c1\u81c0\u90e8\u8bf1\u4eba\u7684\u633a\u7fd8\u66f2\u7ebf\u3002\u5982\u679c\u743c\u6069\u8bb0\u5f97\u6ca1\u9519\uff0c\u521a\u624d\u9762\u5bf9\u9762\u7684\u65f6\u5019\uff0c\u77a5\u89c1\u5979\u7684\u6597\u7bf7\u9886\u7ed3\u5904\uff0c\u662f\u4e00\u4e2a\u7f00\u7740\u7d2b\u8fb9\u7684\u9ed1\u66dc\u77f3\u5706\u76d8\uff0c\u90a3\u662f\u838e\u5c14\u5973\u795e\u7684\u5723\u5fbd\u3002</p><p>\u867d\u7136\u9634\u9b42\u57ce\u4e2d\u4eba\u4eba\u90fd\u662f\u838e\u5c14\u5973\u795e\u7684\u4fe1\u5f92\uff0c\u4f46\u5723\u5fbd\u5374\u662f\u53ea\u6709\u7267\u5e08\u624d\u6709\u8d44\u683c\u4f69\u6234\u5728\u8eab\u4e0a\u7684\u3002\u5f88\u663e\u7136\uff0c\u521a\u624d\u7684\u5c11\u5973\u4e0d\u662f\u8299\u857e\u72c4\uff0c\u662f\u5979\u7684\u59d0\u59d0\u8299\u8389\u5a05\uff0c\u5979\u4eec\u662f\u53cc\u80de\u80ce\u3002\u743c\u6069\u66fe\u7ecf\u89c1\u8fc7\u5979\u4e00\u6b21\uff0c\u4e0a\u6b21\u542c\u8299\u857e\u72c4\u8bf4\u5979\u5728\u795e\u6bbf\u8868\u73b0\u4f18\u5f02\uff0c\u5df2\u7ecf\u5feb\u8981\u6210\u4e3a\u6b63\u5f0f\u7267\u5e08\u4e86\u3002</p><p>\u201c\u5514\uff0c\u660e\u660e\u8bb0\u5f97\u81ea\u5df1\u6ca1\u6709\u5f97\u7f6a\u8fc7\u5979\u5440\uff0c\u600e\u4e48\u6bcf\u6b21\u89c1\u5230\u90fd\u4eff\u4f5b\u8ddf\u6211\u6709\u4ec7\u4f3c\u7684\u3002\u4e0a\u6b21\u8d77\u7801\u8fd8\u6253\u4e2a\u62db\u547c\uff0c\u8fd9\u6b21\u76f4\u63a5\u8f6c\u5934\u5c31\u8d70\uff0c\u8fde\u57fa\u672c\u7684\u793c\u8282\u90fd\u4e0d\u8981\u4e86\u3002\u201d</p><p>\u743c\u6069\u5600\u5495\u7740\uff0c\u89c1\u5979\u7684\u8eab\u5f71\u4ece\u524d\u65b9\u62d0\u89d2\u5904\u6d88\u5931\uff0c\u6697\u81ea\u6447\u4e86\u6447\u5934\u6b63\u8981\u79bb\u5f00\uff0c\u542c\u89c1\u65c1\u8fb9\u6709\u4eba\u8f7b\u58f0\u53eb\u4ed6\u3002\u8f6c\u8138\u4e00\u770b\uff0c\u89c1\u662f\u8299\u857e\u72c4\u8d70\u51fa\u6765\uff0c\u5979\u4eca\u5929\u6362\u4e86\u4e00\u8eab\u6de1\u7d2b\u8272\u7684\u8fde\u8863\u88d9\uff0c\u857e\u4e1d\u82b1\u8fb9\uff0c\u811a\u4e0a\u7a7f\u7740\u767d\u8272\u51c9\u978b\uff0c\u957f\u53d1\u7528\u4e00\u6839\u9ec4\u8272\u4e1d\u5e26\u677e\u677e\u7cfb\u7740\uff0c\u7b80\u7b80\u5355\u5355\u7684\u6253\u626e\uff0c\u770b\u4e0a\u53bb\u5374\u50cf\u4e2a\u5c0f\u516c\u4e3b\u4f3c\u7684\u3002\u743c\u6069\u4ed4\u7ec6\u770b\u5979\u7684\u8138\uff0c\u53d1\u73b0\u51e0\u5929\u4e0d\u89c1\uff0c\u6c14\u8272\u4f3c\u4e4e\u9887\u6709\u597d\u8f6c\u3002</p><p>\u201c\u65e9\u4e0a\u597d\uff0c\u8299\u857e\u72c4\u3002\u201d</p><p>\u8299\u857e\u72c4\u5fae\u7b11\uff0c\u70b9\u5934\u793a\u610f\u3002\u4e00\u95ee\u4e4b\u4e0b\uff0c\u539f\u6765\u5979\u51c6\u5907\u53bb\u7b2c\u4e09\u56fe\u4e66\u9986\uff0c\u5012\u662f\u548c\u70bc\u91d1\u5b66\u9662\u5728\u4e00\u4e2a\u65b9\u5411\uff0c\u6b63\u597d\u987a\u8def\u3002</p><p>\u201c\u5bf9\u4e86\uff0c\u4f60\u6700\u8fd1\u8fd8\u5931\u7720\u4e48\u3002\u201d</p><p>\u201c\u597d\u591a\u4e86\uff0c\u201d\u8299\u857e\u72c4\u8bf4\uff0c\u201c\u8c22\u8c22\u4f60\uff0c\u743c\u6069\u3002\u201d</p><p>\u743c\u6069\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u6211\u53c8\u6ca1\u505a\u4ec0\u4e48\u3002\u201d</p><p>\u8299\u857e\u72c4\u5fae\u5fae\u7b11\u7740\uff0c\u4e5f\u6ca1\u518d\u591a\u8bf4\u3002\u201c\u554a\uff0c\u201d\u5979\u60f3\u4e86\u8d77\u6765\uff0c\u201c\u521a\u624d\u6211\u59d0\u59d0\u2026\u2026\u5979\u6700\u8fd1\u5fc3\u60c5\u4e0d\u597d\uff0c\u4f60\u522b\u4ecb\u610f\u3002\u201d</p><p>\u4e9b\u8bb8\u5c0f\u4e8b\uff0c\u4ecb\u610f\u5012\u4e0d\u7528\uff0c\u4e0d\u8fc7\u743c\u6069\u5bf9\u8299\u857e\u72c4\u8bf4\u7684\u201c\u5979\u6700\u8fd1\u5fc3\u60c5\u4e0d\u597d\u201d\u8d77\u4e86\u5174\u8da3\uff0c\u201c\u600e\u4e48\u4e86\uff1f\u201d</p><p>\u8299\u857e\u72c4\u7565\u7565\u72b9\u8c6b\u4e86\u4e0b\uff0c\u201c\u5a5a\u4e8b\u3002\u201d\u5979\u8f7b\u58f0\u8bf4\u3002</p><p>\u83ab\u5c3c\u5361\u5148\u751f\u5728\u4e16\u7684\u65f6\u5019\uff0c\u4e3a\u8299\u8389\u5a05\u5b9a\u4e0b\u4e86\u5a5a\u4e8b\uff0c\u5bf9\u65b9\u662f\u5e93\u80af\u5bb6\u65cf\u7684\u957f\u5b50\uff0c\u4e5f\u5c31\u662f\u6bd5\u4e1a\u8003\u8bd5\u65f6\u593a\u8d70\u7b2c\u4e00\u540d\uff0c\u5f97\u5230\u8679\u5f69\u6212\u6307\u7684\u90a3\u4e2a\u5bb6\u4f19\uff0c\u5982\u4eca\u6b63\u5728\u5965\u672f\u5e08\u5b66\u6821\u91cc\u5b66\u4e60\u3002\u6309\u7167\u9884\u5b9a\u8ba1\u5212\uff0c\u5728\u8299\u8389\u5a05\u5341\u4e94\u5c81\u751f\u65e5\u65f6\u4e3e\u884c\u5a5a\u793c\uff0c\u5982\u4eca\u8fd8\u6709\u4e0d\u5230\u4e09\u4e2a\u6708\u7684\u65f6\u95f4\u3002</p><p>\u7136\u800c\u8299\u8389\u5a05\u4f3c\u4e4e\u5e76\u4e0d\u559c\u6b22\u5e93\u80af\u2014\u2014\u5012\u4e5f\u8c08\u4e0d\u4e0a\u53cd\u611f\uff0c\u53ea\u662f\u5bf9\u4ed6\u6ca1\u611f\u89c9\u3002</p><p>\u201c\u539f\u6765\u5982\u6b64\u3002\u201d\u743c\u6069\u70b9\u70b9\u5934\uff0c\u8fd9\u5012\u786e\u5b9e\u662f\u4e2a\u9ebb\u70e6\u3002\u867d\u7136\u4ed6\u548c\u8299\u8389\u5a05\u6ca1\u6253\u8fc7\u4ec0\u4e48\u4ea4\u9053\uff0c\u4f46\u5927\u81f4\u4e5f\u80fd\u770b\u5f97\u51fa\u5979\u7684\u6027\u683c\uff0c\u663e\u7136\u662f\u81ea\u6211\u610f\u8bc6\u5f88\u5f3a\u7684\u5973\u5b69\u5b50\uff0c\u8981\u5ac1\u7ed9\u4e00\u4e2a\u81ea\u5df1\u4e0d\u559c\u6b22\u7684\u4eba\uff0c\u8fd9\u6beb\u65e0\u7591\u95ee\u662f\u4e00\u79cd\u6298\u78e8\u3002</p><p>\u4e0d\u8fc7\u743c\u6069\u5bf9\u8fd9\u5bb6\u4e8b\u60c5\u4e5f\u61d2\u5f97\u5173\u5fc3\uff0c\u867d\u8bf4\u4ee5\u524d\u4e5f\u66fe\u7ecf\u8d77\u8fc7\u5ff5\u5934\uff0c\u8981\u628a\u8fd9\u5bf9\u53cc\u80de\u80ce\u59d0\u59b9\u4e00\u8d77\u5f04\u4e0a\u5e8a\u73a9\u53cc\u98de\uff0c\u7136\u800c\u5982\u4eca\u8fde\u8299\u857e\u72c4\u90fd\u5df2\u7ecf\u4ece\u60c5\u4eba\u53d8\u6210\u670b\u53cb\uff0c\u4e5f\u5c31\u4e0d\u7528\u6253\u5979\u59d0\u59d0\u7684\u4e3b\u610f\u4e86\u3002\u4f55\u51b5\u81ea\u5df1\u4e5f\u5e2e\u4e0d\u4e0a\u4ec0\u4e48\u5fd9\uff0c\u66f4\u4e0d\u613f\u610f\u4e3a\u6b64\u5f97\u7f6a\u4e00\u4e2a\u8d35\u65cf\u3002\u5e93\u80af\u5bb6\u65cf\u5728\u9634\u9b42\u57ce\u662f\u9887\u6709\u4e9b\u52bf\u529b\u7684\uff0c\u6bd4\u8870\u5fae\u7684\u83ab\u5c3c\u5361\u5bb6\u65cf\u5f3a\u591a\u4e86\u3002\u522b\u4eba\u7684\u4e8b\u60c5\uff0c\u5c31\u8ba9\u522b\u4eba\u81ea\u5df1\u64cd\u5fc3\u53bb\u5427\u3002</p><p>\u4e00\u8def\u95f2\u8c08\uff0c\u5728\u7b2c\u4e09\u56fe\u4e66\u9986\u524d\u5206\u624b\uff0c\u743c\u6069\u81ea\u53bb\u70bc\u91d1\u5b66\u9662\u3002\u4eca\u5929\u662f\u9093\u80af\u7684\u6539\u826f\u706b\u67aa\u73b0\u573a\u6f14\u793a\u7684\u65e5\u5b50\uff0c\u5728\u7cbe\u64c5\u673a\u68b0\u5de5\u827a\u7684\u5ca9\u4f8f\u5112\u4eec\u7684\u534f\u52a9\u4e0b\uff0c\u8fd9\u4f4d\u519b\u706b\u5546\u4eba\u517c\u53d1\u660e\u5bb6\u7ec8\u4e8e\u5b8c\u6210\u4e86\u71e7\u53d1\u70b9\u706b\u548c\u540e\u88c5\u5f39\u4e24\u9879\u6539\u826f\u5de5\u4f5c\uff0c\u8feb\u4e0d\u53ca\u5f85\u5730\u8981\u5411\u5927\u5bb6\u5c55\u793a\u4ed6\u7684\u6210\u5c31\u3002</p><p>\u70bc\u91d1\u5b66\u9662\u7684\u9ad8\u5c42\u9886\u5bfc\u548c\u9b54\u50cf\u7814\u5236\u5de5\u4f5c\u5ba4\u7684\u6210\u5458\u90fd\u5230\u9f50\u4e86\uff0c\u5305\u62ec\u739b\u63d0\u514b\u548c\u74e6\u63d0\u514b\u4e24\u4f4d\u9662\u957f\u5927\u4eba\u5728\u5185\uff0c\u9664\u6b64\u4e4b\u5916\uff0c\u8fd8\u6765\u4e86\u4e24\u4f4d\u5ba2\u4eba\uff0c\u4e00\u4f4d\u662f\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\uff0c\u53e6\u5916\u4e00\u4f4d\u662f\u8001\u76f8\u8bc6\uff0c\u60c5\u62a5\u8d29\u5b50\u6b27\u51ef\u3002</p><p>\u4e0a\u6b21\u5728\u963f\u683c\u62c9\u9686\uff0c\u5e03\u96f7\u7eb3\u65af\u9080\u8bf7\u4ed6\u6765\u9634\u9b42\u57ce\uff0c\u8fd9\u5bb6\u4f19\u679c\u7136\u771f\u8dd1\u6765\u4e86\u3002\u542c\u8bf4\u70bc\u91d1\u5b66\u9662\u7814\u5236\u51fa\u4e86\u65b0\u578b\u706b\u67aa\uff0c\u5174\u81f4\u52c3\u52c3\u8981\u8fc7\u6765\u89c2\u770b\uff0c\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u4fbf\u966a\u540c\u524d\u6765\u3002\u770b\u5230\u743c\u6069\uff0c\u6b27\u51ef\u5f88\u9ad8\u5174\u5730\u6325\u624b\u6253\u62db\u547c\uff0c\u4eff\u4f5b\u8001\u670b\u53cb\u4f3c\u7684\u9080\u8bf7\u4ed6\u5750\u5728\u65c1\u8fb9\u3002\u743c\u6069\u4e0d\u592a\u60f3\u548c\u8fd9\u4e2a\u72d7\u4ed4\u961f\u6253\u4ea4\u9053\uff0c\u7136\u800c\u89c1\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u4e5f\u793a\u610f\uff0c\u53ea\u5f97\u5750\u4e86\u8fc7\u53bb\u3002</p><p>\u6f14\u793a\u573a\u4e2d\uff0c\u9093\u80af\u6b63\u5728\u6307\u6325\u4f8f\u5112\u4eec\u505a\u51c6\u5907\u5de5\u4f5c\uff0c\u4ed6\u7684\u624b\u4e2d\u63d0\u7740\u4e00\u6746\u706b\u67aa\uff0c\u611f\u89c9\u6bd4\u4ee5\u524d\u7684\u90a3\u201c\u706b\u7ef3\u94a9\u67aa\u201d\u4f53\u79ef\u66f4\u5927\u4e9b\uff0c\u9020\u578b\u4e0a\u5012\u662f\u6709\u4e86\u5f88\u5927\u6539\u53d8\uff0c\u539f\u672c\u5f15\u4eba\u6ce8\u76ee\u7684\u94c1\u94a9\u548c\u957f\u957f\u706b\u7ef3\u6d88\u5931\u4e86\uff0c\u770b\u4e0a\u53bb\u987a\u773c\u591a\u4e86\u3002</p><p>\u4ed6\u5c06\u4e00\u5305\u5305\u9ed1\u8272\u9897\u7c92\u72b6\u7684\u706b\u836f\u5012\u8fdb\u67aa\u819b\u4e2d\uff0c\u5927\u7ea6\u4f30\u7b97\u4e86\u4e00\u4e0b\uff0c\u89c9\u5f97\u4f3c\u4e4e\u8fd8\u4e0d\u591f\uff0c\u4e8e\u662f\u53c8\u5012\u4e86\u4e00\u4e9b\u3002\u4e00\u5207\u51c6\u5907\u5c31\u7eea\uff0c\u4ed6\u7aef\u8d77\u706b\u67aa\uff0c\u8fdc\u8fdc\u7784\u51c6\u65e9\u5df2\u7ecf\u7ad6\u8d77\u6765\u7684\u9776\u5b50\uff0c\u6263\u52a8\u4e86\u677f\u673a\u3002</p><p>\u7136\u540e\u3002</p><p>\u7136\u540e\u4ed6\u5c31\u6b7b\u4e86\u3002</p><p>\u53ef\u80fd\u662f\u706b\u836f\u88c5\u5f97\u592a\u591a\uff0c\u706b\u67aa\u8f70\u7136\u70b8\u88c2\uff0c\u5316\u4f5c\u4e00\u56e2\u8000\u773c\u7729\u76ee\u7684\u706b\u5149\uff0c\u7ffb\u817e\u7684\u51cc\u5389\u6c14\u6d6a\u5982\u5de8\u9524\u822c\u7838\u5728\u9093\u80af\u7684\u8138\u4e0a\uff0c\u9c9c\u8840\u98de\u6e85\u4e2d\uff0c\u519b\u706b\u5546\u4eba\u5f80\u540e\u7ffb\u8eab\u4fbf\u5012\u3002</p><p>\u8fd9\u4e00\u5e55\u53d8\u6545\u53d1\u751f\u5f97\u592a\u7a81\u7136\uff0c\u4ee5\u81f3\u4e8e\u5728\u573a\u7684\u6240\u6709\u89c2\u4f17\u90fd\u88ab\u9707\u4f4f\u4e86\uff0c\u8c01\u4e5f\u6ca1\u6599\u5230\u4f1a\u662f\u8fd9\u79cd\u7ed3\u5c40\u3002\u5c31\u5728\u534a\u79d2\u949f\u524d\uff0c\u6240\u6709\u4eba\u90fd\u8fd8\u6e05\u695a\u5730\u770b\u89c1\u9093\u80af\u8138\u4e0a\u7684\u81ea\u4fe1\u6ee1\u6ee1\u7684\u7b11\u5bb9\uff0c\u7136\u800c\u5982\u4eca\u4ed6\u7684\u534a\u4e2a\u8111\u888b\u90fd\u5df2\u7ecf\u88ab\u70b8\u98de\u4e86\u3002</p><p>\u6700\u5148\u53cd\u5e94\u8fc7\u6765\u7684\uff0c\u4e0d\u662f\u9634\u9b42\u57ce\u7684\u4e3b\u4eba\uff0c\u53cd\u5012\u662f\u4f5c\u4e3a\u5ba2\u4eba\u7684\u6b27\u51ef\u3002\u4ed6\u5feb\u82e5\u95ea\u7535\u5730\u624b\u8155\u4e00\u7ffb\uff0c\u638c\u5fc3\u5df2\u7ecf\u591a\u4e86\u4e00\u679a\u6676\u83b9\u5254\u900f\u7684\u5c0f\u94bb\u77f3\uff0c\u53e3\u4e2d\u6025\u901f\u5ff5\u4e86\u4e00\u53e5\u5492\u8bed\u3002</p><p>\u4ed6\u7684\u58f0\u97f3\u5f88\u8f7b\uff0c\u5927\u6982\u4e5f\u53ea\u6709\u5750\u5728\u65c1\u8fb9\u7684\u743c\u6069\u80fd\u52c9\u5f3a\u542c\u89c1\u3002\u8bdd\u97f3\u672a\u843d\uff0c\u638c\u5fc3\u7684\u5b9d\u77f3\u5df2\u7ecf\u81ea\u5df1\u60ac\u6d6e\u8d77\u6765\uff0c\u6025\u901f\u65cb\u8f6c\uff0c\u7136\u540e\u743c\u6069\u4eff\u4f5b\u770b\u89c1\u4e00\u4e2a\u6a21\u7cca\u7684\u767d\u5f71\u4ece\u9093\u80af\u7684\u5c38\u4f53\u4e0a\u817e\u8d77\uff0c\u77ac\u95f4\u88ab\u5438\u8fdb\u4e86\u94bb\u77f3\u4e2d\u3002</p><p>\u7075\u9b42\u652b\u53d6\u3002</p><p>\u548c\u4e0a\u6b21\u5728\u5de8\u9b54\u5c71\u8109\u4e2d\u4e00\u6837\uff0c\u6b27\u51ef\u518d\u5ea6\u65bd\u51fa\u4e86\u7075\u9b42\u652b\u53d6\uff0c\u4e8e\u5343\u94a7\u4e00\u53d1\u4e4b\u9645\u5c06\u9093\u80af\u7684\u7075\u9b42\u62a2\u4e86\u4e0b\u6765\u3002\u53ea\u8981\u518d\u7a0d\u8fdf\u77ac\u606f\uff0c\u9093\u80af\u7684\u7075\u9b42\u5c31\u4f1a\u81ea\u52a8\u524d\u5f80\u6b7b\u795e\u514b\u5170\u6c83\u7684\u795e\u56fd\uff0c\u7136\u540e\u88ab\u4ed6\u6240\u4fe1\u4ef0\u7684\u5de5\u827a\u4e0e\u953b\u9020\u4e4b\u795e\u8d21\u5fb7\u63a5\u8d70\u3002</p><p>\u6b27\u51ef\u5c06\u94bb\u77f3\u9012\u7ed9\u4e86\u65c1\u8fb9\u7684\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\uff0c\u9093\u80af\u7684\u7075\u9b42\u5982\u4eca\u5c31\u56da\u7981\u5728\u5176\u4e2d\uff0c\u53ef\u4ee5\u9690\u7ea6\u770b\u89c1\u4e00\u4e2a\u5c0f\u5c0f\u7684\u4eba\u5f71\u3002\u4ea1\u8005\u7684\u7075\u9b42\u662f\u6709\u610f\u8bc6\u6709\u8bb0\u5fc6\u7684\uff0c\u9020\u8be3\u8db3\u591f\u9ad8\u7684\u5deb\u5e08\uff0c\u62ff\u7740\u8fd9\u679a\u94bb\u77f3\u5c31\u53ef\u4ee5\u548c\u9093\u80af\u8fdb\u884c\u4ea4\u6d41\uff0c\u53ef\u4ee5\u8ba9\u82f1\u5e74\u65e9\u901d\u7684\u519b\u706b\u5546\u4eba\u7ee7\u7eed\u4e3a\u706b\u67aa\u6539\u826f\u4e8b\u4e1a\u8d21\u732e\u4ed6\u7684\u806a\u660e\u624d\u667a\u3002\u4e0d\u8fc7\u8fd9\u79cd\u673a\u68b0\u53d1\u660e\uff0c\u975e\u5e38\u9700\u8981\u5b9e\u8df5\u7684\u52a8\u624b\u64cd\u4f5c\uff0c\u9093\u80af\u5982\u4eca\u53ea\u5269\u4e0b\u7075\u9b42\uff0c\u6ca1\u6709\u8eaf\u4f53\uff0c\u4ef7\u503c\u5c31\u4e0d\u514d\u5927\u6253\u6298\u6263\u4e86\u3002</p><p>\u5728\u9093\u80af\u65c1\u8fb9\u7684\u51e0\u4e2a\u5ca9\u4f8f\u5112\u4e5f\u88ab\u6b83\u53ca\uff0c\u90fd\u53d7\u4e86\u4e0d\u540c\u7a0b\u5ea6\u7684\u4f24\uff0c\u597d\u5728\u9634\u9b42\u57ce\u7267\u5e08\u4f17\u591a\uff0c\u53ea\u8981\u4e0d\u662f\u5f53\u573a\u54bd\u6c14\u5c31\u80fd\u6551\u56de\u6765\u2014\u2014\u5176\u5b9e\u4ece\u7406\u8bba\u4e0a\u8bf4\uff0c\u8d77\u6b7b\u56de\u751f\u4e5f\u5e76\u4e0d\u662f\u4e0d\u80fd\u529e\u5230\u7684\uff0c\u4f46\u6700\u5173\u952e\u7684\u73af\u8282\u662f\u9700\u8981\u5f97\u5230\u76f8\u5e94\u795e\u7947\u7684\u8bb8\u53ef\u3002\u4eba\u6b7b\u4e4b\u540e\uff0c\u7075\u9b42\u4f1a\u524d\u5f80\u751f\u524d\u6240\u4fe1\u4ef0\u7684\u795e\u7947\u56fd\u5ea6\uff08\u65e0\u4fe1\u8005\u548c\u4f2a\u4fe1\u8005\u5f52\u6b7b\u795e\u514b\u5170\u6c83\u638c\u7ba1\uff09\uff0c\u7267\u5e08\u8981\u60f3\u8d77\u6b7b\u56de\u751f\uff0c\u5c31\u5f97\u5148\u7948\u6c42\u8fd9\u4e9b\u795e\u7947\u7684\u540c\u610f\uff0c\u8ba9\u4ed6\u4eec\u613f\u610f\u628a\u4fe1\u5f92\u7075\u9b42\u653e\u56de\u6765\u3002\u9634\u9b42\u57ce\u7684\u7267\u5e08\u666e\u901a\u4fe1\u4ef0\u838e\u5c14\uff0c\u548c\u4f8f\u5112\u8bf8\u795e\u53ef\u662f\u534a\u70b9\u4ea4\u60c5\u4e5f\u6ca1\u6709\uff0c\u8fd9\u4e2a\u4e3b\u610f\u662f\u4e0d\u7528\u6253\u3002</p><p>\u5174\u81f4\u52c3\u52c3\u7b49\u7740\u89c2\u770b\u706b\u67aa\u8868\u6f14\uff0c\u7ed3\u679c\u5374\u51fa\u4e86\u8fd9\u79cd\u610f\u5916\u53d8\u6545\uff0c\u8fd8\u635f\u5931\u4e86\u9093\u80af\u8fd9\u79cd\u6838\u5fc3\u7814\u53d1\u4eba\u5458\uff0c\u6240\u6709\u4eba\u90fd\u5f88\u6cae\u4e27\u3002\u743c\u6069\u5012\u662f\u53e6\u6709\u4e00\u6869\u7591\u60d1\uff0c\u4ed6\u521a\u624d\u5750\u5f97\u8fd1\uff0c\u9690\u7ea6\u542c\u89c1\u4e86\u6b27\u51ef\u652b\u53d6\u7075\u9b42\u65f6\u5ff5\u7684\u90a3\u53e5\u5492\u8bed\u2014\u2014\u53d1\u97f3\u975e\u5e38\u8be1\u5f02\uff0c\u900f\u7740\u68ee\u68ee\u90aa\u6c14\uff0c\u53ef\u4ee5\u65ad\u5b9a\u4e0d\u662f\u9f99\u8bed\uff0c\u4e5f\u4e0d\u662f\u4ed6\u6240\u901a\u6653\u7684\u4efb\u4f55\u4e00\u79cd\u8bed\u8a00\uff0c\u504f\u504f\u53c8\u89c9\u5f97\u6709\u4e9b\u8033\u719f\u3002</p><p>\u201c\u5947\u602a\uff0c\u600e\u4e48\u603b\u89c9\u5f97\u50cf\u5728\u54ea\u91cc\u542c\u5230\u8fc7\u4f3c\u7684\uff1f\u201d</p><p>\u743c\u6069\u5728\u8bed\u8a00\u4e0a\u5929\u8d4b\u4e0d\u9ad8\uff0c\u76ee\u524d\u4e3a\u6b62\u6240\u638c\u63e1\u7684\uff0c\u4e5f\u5c31\u662f\u9f99\u8bed\u3001\u8010\u745f\u8bed\u548c\u901a\u7528\u8bed\u4e09\u79cd\uff0c\u60f3\u6765\u60f3\u53bb\u6ca1\u6709\u5934\u7eea\uff0c\u51b3\u5b9a\u56de\u53bb\u95ee\u6885\u83f2\u65af\uff0c\u5979\u5374\u662f\u7cbe\u901a\u5404\u79cd\u8bed\u8a00\u7684\u3002</p></article>"}}}]);
