(window.webpackJsonp=window.webpackJsonp||[]).push([[223],{246:function(p){p.exports={chapterTitle:null,title:"\u5c3e\u58f0\uff082\uff09",content:"<article><h2>\u5c3e\u58f0\uff082\uff09</h2><p>\u3000\u3000\u9526\u6e56\u4ee5\u7231\u8fbe\u96c6\u56e2\u7684\u540d\u4e49\u6536\u8d2d\u73b0\u4ee3\u534a\u5bfc\u4f53\u6db2\u6676\u4e1a\u52a1\u53c8\u6709\u6240\u4e0d\u540c\uff0c\u9996\u5148\u662f\u97e9\u56fd\u5a92\u4f53\u5bf9\u6b64\u6b21\u6295\u8d44\u8fdb\u884c\u957f\u8fbe\u534a\u5e74\u7684\u70ed\u70b9\u8ffd\u8e2a\uff0c\u5728\u6d77\u5916\u3001\u7279\u522b\u662f\u4e9a\u6d32\u5730\u533a\u7684\u5f71\u54cd\u5f88\u5927\uff1b\u9526\u6e56\u4eca\u5e74\u4e5f\u4e0d\u518d\u63a9\u9970\u5728\u6d88\u8d39\u7c7b\u7535\u5b50\u4ea7\u4e1a\u94fe\u4e0a\u7684\u52a8\u4f5c\u3002</p><p>\u3000\u3000\u7231\u8fbe\u96c6\u56e2\u4eca\u5e74\u56fd\u5185\u624b\u673a\u4e1a\u52a1\u9500\u552e\u603b\u989d\u5c31\u5c06\u8d85\u8fc7\u4e09\u5341\u4ebf\u7f8e\u5143\uff0c\u4e1a\u7ee9\u518d\u5206\u5272\u3001\u9690\u85cf\u4e5f\u65e0\u6cd5\u5c06\u8d22\u52a1\u89c4\u6a21\u505a\u5230\u6392\u540d\u5728\u534e\u590f\u7535\u5b50\u4e4b\u4e0b\u3002\u65e2\u7136\u4eca\u5e74\u6ce8\u5b9a\u8981\u5728\u7535\u5b50\u5de5\u4e1a\u767e\u5f3a\u699c\u4e0a\u593a\u51a0\uff0c\u6210\u4e86\u51fa\u5934\u9e1f\uff0c\u8fd8\u4e0d\u5982\u7d22\u6027\u5c06\u58f0\u52bf\u641e\u641e\u5927\u3002\u9526\u6e56\u5728\u6280\u672f\u8ddf\u751f\u4ea7\u4ee5\u53ca\u7ba1\u7406\u5df2\u7ecf\u505a\u4e86\u76f8\u5bf9\u5145\u8db3\u7684\u51c6\u5907\uff0c\u4e5f\u786e\u5b9e\u62c5\u5f97\u8d77\u56fd\u5185\u7535\u5b50\u5de5\u4e1a\u767e\u5f3a\u699c\u5934\u540d\u7684\u5730\u4f4d\u3002</p><p>\u3000\u3000\u5373\u4f7f\u97e9\u56fd\u5a92\u4f53\u4e0e\u6c11\u4f17\u89c6\u91ce\u88ab\u674e\u5065\u7199\u5bb6\u65cf\u6210\u5458\u674e\u99a8\u4e88\u60c5\u604b\u4e11\u95fb\u6240\u8f6c\u79fb\uff0c\u7231\u8fbe\u96c6\u56e2\u5c06\u4ee53.2\u4ebf\u7f8e\u5143\u6536\u8d2d\u73b0\u4ee3\u534a\u5bfc\u4f53\u65d7\u4e0b\u7684\u6db2\u6676\u4e1a\u52a1\u662f\u4e2d\u56fd\u4f01\u4e1a\u6709\u53f2\u4ee5\u6765\u6700\u5927\u7684\u4e00\u6869\u6d77\u5916\u6536\u8d2d\u6848\u800c\u88ab\u4e24\u56fd\u653f\u5e9c\u4e0e\u6c11\u4f17\u5173\u6ce8\u3002\u97e9\u56fd\u77e5\u8bc6\u7ecf\u6d4e\u90e8\u957f\u5b98\u8d74\u534e\u8bbf\u95ee\u671f\u95f4\uff0c\u66fe\u5982\u5723\u526f\u603b\u7406\u63a5\u89c1\u65f6\u8fd8\u7279\u610f\u5c06\u6536\u8d2d\u6848\u4f5c\u4e3a\u4f1a\u89c1\u7684\u4e00\u4e2a\u8bae\u9898\u6765\u8ba8\u8bba\uff0c\u51cf\u5f31\u4e86\u8bb8\u591a\u97e9\u56fd\u653f\u5e9c\u7684\u538b\u529b\u3002</p><p>\u3000\u3000\u6536\u8d2d\u6848\u5168\u90e8\u4ee5\u73b0\u91d1\u652f\u4ed8\uff0c\u97e9\u5174\u94f6\u884c\u7b49\u4e94\u5bb6\u97e9\u56fd\u5546\u4e1a\u94f6\u884c\u4e3a\u6b64\u6b21\u6536\u8d2d\u6848\u5411\u7231\u8fbe\u96c6\u56e2\u63d0\u4f9b1.6\u7f8e\u5143\u7684\u62b5\u62bc\u8d37\u6b3e\uff0c\u7231\u8fbe\u96c6\u56e2\u9700\u8981\u53e6\u5916\u518d\u63d0\u4f9b1.6\u4ebf\u7f8e\u5143\u7684\u73b0\u91d1\u3002\u9664\u4e86\u73b0\u4ee3\u534a\u5bfc\u4f53\u539f\u6db2\u6676\u5de5\u5382\u7b49\u56fa\u5b9a\u8d44\u4ea7\u5916\uff0c\u7231\u8fbe\u96c6\u56e2\u8fd8\u5c06\u83b7\u5f97\u5305\u62ec4000\u9879\u6db2\u6676\u4e13\u5229\u6280\u672f\u8ddf\u4f7f\u7528\u8bb8\u53ef\u6388\u6743\u5728\u5185\u7684\u8bf8\u591a\u65e0\u5f62\u8d44\u4ea7\uff1b\u73b0\u4ee3\u534a\u5bfc\u4f53\u5219\u5f7b\u5e95\u4ece\u6db2\u6676\u884c\u4e1a\u9000\u51fa\u3002</p><p>\u3000\u3000\u7231\u8fbe\u96c6\u56e2\u5c06\u5728\u7231\u8fbe\u6676\u5c4f\u7535\u5b50\u6709\u9650\u516c\u53f8\u7684\u57fa\u7840\u4e0a\u6210\u7acb\u6db2\u6676\u4ea7\u4e1a\u4e8b\u52a1\u90e8\uff0c\u5f20\u4e9a\u5e73\u517c\u4efb\u4e8b\u52a1\u90e8\u603b\u88c1\uff0c\u5c06\u8058\u7528\u539f\u73b0\u4ee3\u534a\u5bfc\u4f53\u6db2\u6676\u90e8\u95e8\u603b\u88c1\u6734\u661f\u4e1c\u4e3a\u4e8b\u52a1\u90e8\u526f\u603b\u88c1\uff0c\u6536\u8d2d\u540e\u5c06\u4fdd\u7559\u97e9\u56fd\u5de5\u5382\uff0c\u8058\u8bf7\u539f\u6709\u7684\u7ba1\u7406\u4eba\u5458\u8ddf\u5458\u5de5\u4e13\u95e8\u751f\u4ea7\u4e2d\u5c0f\u5c3a\u5bf8\u6db2\u6676\u5c4f\u3002</p><p>\u3000\u3000\u4e0e\u6b64\u540c\u65f6\uff0c\u7231\u8fbe\u96c6\u56e2\u9996\u5e2d\u6267\u884c\u5b98\u9648\u4fe1\u751f\u5728\u5efa\u90ba\uff0c\u5728\u5efa\u90ba\u5e02\u59d4\u5e02\u653f\u5e9c\u7ec4\u7ec7\u7684\u62db\u5546\u9152\u4f1a\u4e0a\u4ee3\u8868\u7231\u8fbe\u96c6\u56e2\u5ba3\u5e03\u5c06\u5728\u5efa\u90ba\u62df\u5efa\u663e\u793a\u5668\u53ca\u7b14\u8bb0\u672c\u7535\u8111\u7528\u7684\u7b2c\u4e94\u4ee3TFT\u6db2\u6676\u751f\u4ea7\u7ebf\uff0c\u8ba1\u5212\u6295\u8d44\u989d\u9ad8\u8fbe\u523012\u4ebf\u7f8e\u5143\u3002</p><p>\u3000\u3000\u5f20\u606a\u672c\u4e0d\u60f3\u8fd9\u4e48\u7126\u6025\u5c31\u5ba3\u5e03\u5728\u5efa\u90ba\u6295\u8d44\u5efa\u8bbe\u6db2\u6676\u4ea7\u4e1a\u57fa\u5730\u7684\u8ba1\u5212\uff0c\u4f46\u662f\u4ed6\u5230\u97e9\u56fd\u540e\u7684\u7b2c\u4e8c\u5929\uff0c\u5c31\u77e5\u9053\u4e0e\u6c5f\u3001\u66f9\u4e24\u5bb6\u5173\u7cfb\u5bc6\u5207\u7684\u8463\u7b80\u5e74\u53ef\u80fd\u5230\u5efa\u90ba\u6765\u9876\u66ff\u7f57\u541b\u62c5\u4efb\u5efa\u90ba\u5e02\u59d4\u4e66\u8bb0\u2014\u2014\u8d76\u5728\u7f57\u541b\u79bb\u5f00\u4e4b\u524d\u5ba3\u4f20\u6db2\u6676\u4ea7\u4e1a\u57fa\u5730\u7684\u6295\u8d44\u8ba1\u5212\uff0c\u4e00\u65b9\u9762\u7b97\u662f\u7ed9\u7f57\u541b\u7684\u9001\u522b\u793c\uff0c\u8fd9\u4efd\u62db\u5546\u5f15\u8d44\u7684\u529f\u52b3\u4ecd\u8981\u7b97\u5230\u7f57\u541b\u7684\u5934\u4e0a\uff0c\u53e6\u4e00\u65b9\u9762\u662f\u4e3a\u5c06\u6765\u53ef\u80fd\u7684\u6295\u8d44\u8c03\u6574\u7559\u4e9b\u4f59\u5730\uff0c\u5c31\u7b97\u5c06\u6765\u8fd9\u7b14\u6295\u8d44\u8c03\u6574\u5230\u5176\u4ed6\u5730\u65b9\u6216\u8005\u6682\u7f13\u6267\u884c\uff0c\u90fd\u65e0\u9700\u5bf9\u540e\u6765\u7684\u5e02\u59d4\u4e66\u8bb0\u6709\u4ec0\u4e48\u4ea4\u5f85\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u6536\u8d2d\u6848\u7b7e\u7ea6\u5e86\u795d\u9152\u4f1a\u4e4b\u540e\uff0c\u5f20\u606a\u56de\u5230\u9152\u5e97\u623f\u95f4\uff0c\u4fef\u77b0\u7740\u591c\u8272\u8ff7\u4eba\u3001\u706f\u5149\u4f3c\u6d77\u3001\u9713\u8679\u9646\u79bb\u7684\u6c49\u57ce\u3002\u5f20\u606a\u5e76\u6ca1\u6709\u90a3\u79cd\u7ad9\u5728\u4f17\u751f\u4e4b\u4e0a\u7684\u4f18\u8d8a\u611f\uff0c\u60f3\u7740\u8fd9\u5ea7\u57ce\u5e02\u91cc\u6709\u7740\u4e0a\u5343\u4e07\u8ddf\u4ed6\u4e00\u6837\u7684\u666e\u901a\u4eba\uff0c\u4e3a\u7740\u6216\u5927\u6216\u5c0f\u6216\u8ff7\u832b\u7684\u76ee\u6807\u800c\u751f\u6d3b\u7740\uff0c\u603b\u6709\u79cd\u6e3a\u5c0f\u7684\u611f\u89c9\u3002</p><p>\u3000\u3000\u4e5f\u8bb8\u674e\u99a8\u4e88\u4e5f\u4f1a\u6709\u8fd9\u79cd\u611f\u89c9\u5427\uff1f</p><p>\u3000\u3000\u5f20\u606a\u5fc3\u91cc\u9ed8\u9ed8\u60f3\u7740\uff0c\u51dd\u89c6\u7740\u5bf9\u9762\u9ad8\u697c\u697c\u8170\u95f4\u4e00\u95ea\u4e00\u95ea\u6307\u793a\u98de\u673a\u907f\u9669\u7684\u4fe1\u53f7\u7ea2\u706f\uff0c\u5085\u4fca\u8d70\u8fc7\u6765\uff0c\u5c06\u65e0\u7ebf\u7535\u8bdd\u9012\u7ed9\u4ed6\uff1a\u201c\u53f6\u5efa\u658c\u53f6\u603b\u7684\u7535\u8bdd\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u5c06\u60f3\u5ff5\u674e\u99a8\u4e88\u7684\u5fc3\u601d\u6682\u65f6\u6452\u9664\u6389\uff0c\u63a5\u8fc7\u7535\u8bdd\uff0c\u95ee\u9053\uff1a\u201c\u600e\u4e48\u8fd9\u65f6\u5019\u60f3\u8d77\u7ed9\u6211\u6253\u7535\u8bdd\u6765\uff1f\u201d</p><p>\u3000\u3000\u201c\u544a\u8bc9\u4f60\u4e00\u4e2a\u597d\u6d88\u606f\uff0c\u201d\u53f6\u5efa\u658c\u5728\u7535\u8bdd\u8bf4\u9053\uff0c\u201c\u8bc1\u76d1\u4f1a\u5e38\u52a1\u4f1a\u8bae\u4e0a\uff0c\u6709\u4eba\u63d0\u51fa\u5bf9\u6d77\u7c9f\u79d1\u6280\u8fdd\u89c4\u4e8b\u9879\u9001\u4ea4\u6709\u5173\u90e8\u95e8\u7acb\u6848\u4fa6\u5bdf\uff01\u8fd9\u4e2a\u63d0\u8bae\u5728\u4f1a\u8bae\u4e0a\u7ed9\u6401\u7f6e\u4e86\uff0c\u4f46\u662f\u4e5f\u91ca\u653e\u51fa\u4e00\u4e2a\u5f3a\u70c8\u7684\u4fe1\u53f7\uff0c\u90e8\u59d4\u91cc\u7ec8\u4e8e\u6709\u4eba\u7ad9\u51fa\u6765\u8981\u8ddf\u4e25\u5bb6\u5bf9\u7740\u5e72\u4e86\u2026\u2026\u4f60\u89c9\u5f97\u662f\u4e0d\u662f\u8463\u7b80\u5e74\uff1f\u201d</p><p>\u3000\u3000\u201c\u773c\u998b\u5efa\u90ba\u5e02\u59d4\u4e66\u8bb0\u4f4d\u5b50\u3001\u8981\u62ff\u6d77\u7c9f\u79d1\u6280\u7684\u8f6f\u4ef6\u56ed\u9879\u76ee\u6253\u51fb\u8096\u660e\u5efa\u7684\u4eba\uff0c\u4e5f\u4e0d\u6b62\u8463\u7b80\u5e74\u4e00\u4e2a\uff0c\u6211\u4eec\u554a\uff0c\u51b7\u773c\u770b\u620f\u5c31\u662f\u3002\u201d\u5f20\u606a\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u5475\u5475\uff0c\u201d\u53f6\u5efa\u658c\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u4f60\u8bf4\u8fd9\u8bdd\u602a\u6ca1\u6709\u826f\u5fc3\u7684\uff0c\u597d\u50cf\u8fd9\u4e8b\u8ddf\u4f60\u6ca1\u6709\u534a\u70b9\u5173\u7cfb\u4f3c\u7684\uff0c\u8fd8\u4e0d\u662f\u4f60\u6574\u51fa\u6765\u7684\u5996\u86fe\u5b50\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u56de\u5e94\u7684\u7b11\u4e86\u4e24\u58f0\uff0c\u4e16\u4e8b\u5982\u5c40\uff0c\u6709\u65f6\u5019\u53ea\u9700\u8981\u62ff\u68cd\u5b50\u8f7b\u8f7b\u7684\u6405\u4e00\u4e0b\u5c31\u53ef\u4ee5\u9000\u56de\u6765\u51b7\u773c\u65c1\u89c2\u4e86\uff0c\u8ddf\u53f6\u5efa\u658c\u5728\u7535\u8bdd\u91cc\u53c8\u95f2\u626f\u4e86\u51e0\u53e5\uff0c\u6302\u4e86\u7535\u8bdd\uff0c\u674e\u99a8\u4e88\u53c8\u6d6e\u73b0\u5fc3\u5934\uff0c\u5fc3\u77e5\u8fd9\u4e48\u7b49\u4e0d\u4e0b\u53bb\u4e0d\u662f\u4e2a\u4e8b\uff0c\u6293\u8d77\u6c99\u53d1\u4e0a\u7684\u5916\u5957\uff0c\u8ba9\u5085\u4fca\u51c6\u5907\u8f66\uff0c\u4ed6\u8981\u51fa\u53bb\u4e00\u8d9f\u3002</p><p>\u3000\u3000\u201c\u8fd9\u662f\u6734\u8d1e\u513f\u7684\u7535\u8bdd\u2026\u2026\u201d\u5f20\u606a\u5c06\u8981\u51fa\u53bb\u65f6\uff0c\u675c\u98de\u8d70\u4e86\u8fdb\u6765\uff0c\u5c06\u4e00\u5f20\u5199\u6709\u7535\u8bdd\u53f7\u7801\u7684\u4fbf\u6761\u4ea4\u7ed9\u5f20\u606a\uff0c\u5f20\u606a\u5728\u97e9\u56fd\u8fd8\u6709\u5176\u4ed6\u4e8b\u52a1\u8981\u5904\u7406\uff0c\u4ed6\u8ddf\u7740\u5230\u97e9\u56fd\uff0c\u662f\u7eaf\u7cb9\u534f\u52a9\u5f20\u606a\u5904\u7406\u674e\u99a8\u4e88\u7684\u4e8b\u60c5\uff0c\u201c\u6211\u60f3\u7ea6\u5979\u89c1\u9762\uff0c\u7ed9\u5979\u62d2\u7edd\uff0c\u4f60\u53ef\u4ee5\u6253\u8fd9\u7535\u8bdd\u518d\u8bd5\u4e00\u8bd5\u2026\u2026\u201d</p><p>\u3000\u3000\u6734\u8d1e\u513f\u662f\u4e09\u661f\u7535\u5b50\u6d19\u5f0f\u4f1a\u793e\u526f\u793e\u957f\u3001\u4e09\u661f\u6218\u7565\u7b56\u5212\u5ba4\u5ba4\u957f\u6734\u9e64\u6d19\u7684\u5973\u513f\uff0c\u6734\u9e64\u6d19\u5728\u4e09\u661f\u7684\u5730\u4f4d\uff0c\u5dee\u4e0d\u591a\u76f8\u5f53\u4e8e\u9648\u4fe1\u751f\u5728\u9526\u6e56\u7684\u5730\u4f4d\u3002</p><p>\u3000\u3000\u6734\u8d1e\u513f\u540c\u65f6\u4e5f\u662f\u674e\u99a8\u4e88\u7684\u95fa\u4e2d\u5bc6\u53cb\uff0c\u5f20\u606a\u66fe\u5728\u5317\u4eac\u3001\u5efa\u90ba\u8ddf\u5979\u89c1\u8fc7\u4e24\u6b21\u9762\u3002</p><p>\u3000\u3000\u5f20\u606a\u5750\u5230\u6c99\u53d1\u4e0a\uff0c\u7167\u7740\u4fbf\u6761\u4e0a\u7684\u53f7\u7801\u62e8\u6253\u8fc7\u53bb\uff0c\u5bf9\u9762\u4f20\u6765\u4e00\u4e2a\u719f\u6089\u7684\u5973\u58f0\u3002</p><p>\u3000\u3000\u201c\u6211\u662f\u5f20\u606a\uff0c\u6211\u8fd9\u6b21\u5230\u6c49\u57ce\u6765\u5c31\u662f\u4e3a\u4e86\u8ddf\u99a8\u4e88\u89c1\u4e0a\u4e00\u9762\uff0c\u4f60\u4e0d\u8981\u6025\u7740\u6302\u7535\u8bdd\uff0c\u6211\u4e5f\u4e0d\u4f1a\u8ba9\u4f60\u4e3a\u96be\u66ff\u6211\u8054\u7edc\u99a8\u4e88\uff0c\u6211\u53ea\u5e0c\u671b\u5728\u6211\u89c1\u5230\u99a8\u4e88\u4e4b\u524d\uff0c\u4f60\u80fd\u591a\u966a\u966a\u5979\uff0c\u6211\u62c5\u5fc3\u5979\u60c5\u7eea\u4f1a\u4e0d\u592a\u7a33\u5b9a\u2026\u2026\u524d\u5e74\u5728\u4e1c\u4eac\u8ddf\u99a8\u4e88\u89c1\u9762\u65f6\uff0c\u6211\u5c31\u6709\u8fd9\u6837\u7684\u611f\u89c9\uff0c\u8fd9\u79cd\u611f\u89c9\u5f88\u7cdf\u7cd5\uff0c\u6240\u4ee5\u6211\u624d\u5230\u6c49\u57ce\u6765\uff0c\u4e5f\u4e0d\u60f3\u4e8b\u60c5\u4f1a\u95f9\u51fa\u8fd9\u4e48\u5927\u7684\u5f71\u54cd\u3002\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u97e9\u56fd\u6c49\u57ce\u6df1\u79cb\u7684\u96e8\u6dc5\u6dc5\u6ca5\u6ca5\uff0c\u50cf\u662f\u6d77\u5dde\u6885\u5b50\u9ec4\u65f6\u5b63\u8282\u7684\u96e8\u3002</p><p>\u3000\u3000\u542c\u7740\u96e8\u6ef4\u843d\u5728\u8f66\u9876\u84ec\u4e0a\u7684\u8f7b\u54cd\uff0c\u5f20\u606a\u5750\u5728\u8f66\u91cc\uff0c\u770b\u7740\u8f66\u7a97\u5916\u591c\u96e8\u4e2d\u50cf\u662f\u6e05\u5e7d\u56ed\u6797\u4f3c\u7684\u674e\u6c0f\u79c1\u5b85\uff0c\u5ead\u9662\u5185\u5916\u7684\u706f\u5149\uff0c\u5c06\u591c\u96e8\u7167\u5f97\u95ea\u4eae\u5982\u4e1d\uff0c\u9662\u5899\u9ed1\u6a90\u767d\u5899\uff0c\u8d8a\u8fc7\u9662\u5899\u7684\u963b\u9694\uff0c\u8471\u90c1\u7684\u7af9\u6728\u63a2\u51fa\u68a2\u5934\uff0c\u660e\u5904\u6216\u9690\u85cf\u5728\u89d2\u843d\u91cc\u7684\u51e0\u76cf\u6444\u50cf\u5934\u662f\u674e\u5b85\u5b89\u4fdd\u7cfb\u7edf\u7684\u4e00\u90e8\u5206\uff0c\u7a7a\u5bc2\u7684\u95e8\u524d\uff0c\u6ca1\u6709\u4e00\u4e2a\u4eba\uff0c\u5c31\u50cf\u56da\u7b3c\u3002</p><p>\u3000\u3000\u5085\u4fca\u4e0e\u7ffb\u8bd1\u5192\u96e8\u7ad9\u5230\u95e8\u67f1\u524d\u7684\u5bf9\u8bb2\u673a\u524d\uff0c\u8ddf\u5b85\u5b50\u91cc\u7684\u4eba\u4ea4\u6d89\uff0c\u8fc7\u4e86\u7247\u523b\uff0c\u8d70\u4e86\u56de\u6765\uff0c\u9694\u4e86\u8f66\u7a97\u8ddf\u5f20\u606a\u8bf4\u9053\uff1a\u201c\u91cc\u9762\u8bf4\uff0c\u674e\u5065\u7199\u5df2\u7ecf\u7761\u4e0b\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u6709\u8bf4\u8bdd\uff0c\u6495\u4e86\u4e00\u5f20\u4fbf\u6761\uff0c\u53d6\u51fa\u7b14\u6765\u5237\u5237\u5237\u5199\u4e86\u51e0\u884c\uff0c\u5c06\u4fbf\u6761\u4ea4\u7ed9\u5085\u4fca\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8ba9\u91cc\u9762\u4eba\u5c06\u4fbf\u6761\u4ea4\u7ed9\u674e\u4f1a\u957f\u3002\u201d</p><p>\u3000\u3000\u5085\u4fca\u63a5\u8fc7\u4fbf\u6761\uff0c\u5192\u7740\u96e8\u53c8\u8d70\u56de\u53bb\uff0c\u501f\u7740\u95e8\u67f1\u524d\u67d4\u548c\u7684\u706f\u5149\uff0c\u770b\u4e86\u4e00\u773c\u4fbf\u6761\uff0c\u53ea\u89c1\u5f20\u606a\u5728\u4fbf\u6761\u4e0a\u5199\u9053\uff1a\u201c\u9526\u6e56\u81ea\u6709\u5802\u5802\u6b63\u6b63\u7684\u624b\u6bb5\u8d85\u8d8a\u4e09\u661f\uff0c\u6211\u4eca\u65e5\u662f\u4ee5\u665a\u8f88\u7684\u8eab\u4efd\u62dc\u8bbf\u4e1a\u754c\u7684\u524d\u8f88\uff0c\u674e\u4f1a\u957f\u83ab\u4e0d\u4f1a\u5e0c\u671b\u4ed6\u65e5\u51b7\u773c\u50b2\u6162\u76f8\u5bf9\uff1f\u201d</p><p>\u3000\u3000\u5085\u4fca\u4f5c\u4e3a\u5f20\u606a\u7684\u4e3b\u8981\u52a9\u624b\u4e4b\u4e00\uff0c\u5bf9\u9526\u6e56\u5185\u90e8\u7684\u4e8b\u60c5\u77e5\u9053\u5f88\u591a\uff0c\u8fd8\u662f\u9996\u6b21\u770b\u5230\u5f20\u606a\u5982\u6b64\u660e\u786e\u8bf4\u6709\u4fe1\u5fc3\u8d85\u8d8a\u4e09\u661f\uff0c\u4e5f\u8bb8\u6709\u6fc0\u5c06\u6cd5\u7684\u7528\u610f\u5728\u5185\uff0c\u4ed6\u4e0d\u77e5\u9053\u674e\u5065\u7199\u8ba4\u4e0d\u8ba4\u4e2d\u6587\uff0c\u5c06\u4fbf\u6761\u5de7\u5999\u7684\u6298\u53e0\u8d77\u6765\uff0c\u907f\u514d\u8ba9\u5176\u4ed6\u4e0d\u76f8\u5e72\u7684\u4eba\u770b\u5230\u4fbf\u6761\u91cc\u7684\u5185\u5bb9\uff0c\u4e5f\u6ca1\u6709\u8ba9\u8eab\u8fb9\u7684\u7ffb\u8bd1\u770b\uff0c\u4fbf\u6761\u91cc\u7684\u5185\u5bb9\u8bf4\u8d77\u6765\u4e5f\u6ca1\u6709\u5546\u4e1a\u673a\u5bc6\u5728\uff0c\u4f46\u82e5\u662f\u7ed9\u597d\u4e8b\u8005\u6345\u51fa\u6765\uff0c\u5fc5\u7136\u53c8\u8981\u6380\u8d77\u8206\u8bba\u5927\u6ce2\u3002\u5085\u4fca\u5c06\u4fbf\u6761\u4ea4\u7ed9\u95e8\u5185\u7684\u4fdd\u9556\u8ba9\u4ed6\u9001\u7ed9\u674e\u5065\u7199\uff0c\u81f3\u4e8e\u8bf4\u674e\u5065\u7199\u5df2\u7ecf\u7761\u4e0b\u7684\u8bdd\u90a3\u7eaf\u7cb9\u662f\u9a97\u4e09\u5c81\u5c0f\u5b69\u5b50\u7684\u3002</p><p>\u3000\u3000\u5728\u95e8\u5916\u53c8\u7b49\u4e86\u5927\u6982\u6709\u4e00\u523b\u949f\u7684\u65f6\u95f4\uff0c\u8fd9\u6b21\u8dd1\u5230\u5927\u95e8\u53e3\u7684\u9664\u4e86\u5148\u524d\u90a3\u4e2a\u4fdd\u9556\u5916\uff0c\u53c8\u591a\u4e86\u4e00\u4e2a\u7a7f\u7740\u5957\u88d9\u6491\u9ed1\u4f1e\u7684\u5e74\u8f7b\u5973\u5b50\uff0c\u5bb9\u8c8c\u79c0\u4e3d\uff0c\u81ea\u627f\u662f\u674e\u5065\u7199\u7684\u52a9\u624b\uff0c\u53ea\u540c\u610f\u8ba9\u5f20\u606a\u4e00\u4eba\u5165\u5185\u3002</p><p>\u3000\u3000\u5012\u4e0d\u662f\u62c5\u5fc3\u4ec0\u4e48\uff0c\u53ea\u662f\u8fd9\u6837\u7684\u6761\u4ef6\u5bf9\u6b64\u65f6\u7684\u5f20\u606a\u6765\u8bf4\uff0c\u5df2\u7ecf\u662f\u76f8\u5f53\u7684\u7c97\u9c81\u8ddf\u65e0\u793c\u4e86\u3002</p><p>\u3000\u3000\u201c\u4f60\u4eec\u5728\u8f66\u4e0a\u7b49\u7740\u5427\uff0c\u6211\u8d70\u8fdb\u53bb\u3002\u201d\u5f20\u606a\u6ca1\u6709\u65a4\u65a4\u8ba1\u8f83\u8fd9\u4e9b\uff0c\u5429\u5490\u5085\u4fca\u8ddf\u4e00\u8d77\u8d76\u8fc7\u6765\u7684\u675c\u98de\uff0c\u5c31\u4e0b\u4e86\u8f66\uff0c\u674e\u5065\u7199\u7684\u5973\u52a9\u624b\u5c06\u4f1e\u4e3e\u8fc7\u6765\uff0c\u5e2e\u7740\u4ed6\u906e\u7740\u96e8\u3002</p><p>\u3000\u3000\u9662\u5899\u91cc\u7684\u5ead\u56ed\u5f88\u5927\uff0c\u5f2f\u66f2\u4e0d\u76f4\u7684\u67cf\u6cb9\u8def\u5d4c\u5728\u78a7\u6cb9\u6cb9\u7684\u8349\u576a\u5761\u5730\u95f4\uff0c\u6dcb\u6e7f\u4e86\u96e8\uff0c\u5728\u67d4\u548c\u7684\u5730\u576a\u706f\u5149\u8292\u4e2d\u53cd\u5c04\u51fa\u53d1\u767d\u7684\u4eae\u5149\u3002</p><p>\u3000\u3000\u674e\u5065\u7199\u7684\u5973\u52a9\u624b\u5e2e\u5f20\u606a\u6491\u7740\u4f1e\uff0c\u51fa\u4e8e\u793c\u8282\uff0c\u8fd8\u662f\u843d\u540e\u534a\u6b65\u7684\u8ddf\u7740\uff0c\u5979\u5f88\u5947\u602a\uff0c\u6765\u81ea\u4e2d\u56fd\u7684\u8fd9\u4e2a\u5e74\u8f7b\u7537\u4eba\u771f\u7684\u5c31\u662f\u6700\u8fd1\u7ed9\u4e09\u661f\u5236\u9020\u51fa\u90a3\u4e48\u591a\u9ebb\u70e6\u7684\u9526\u6e56\u7684\u521b\u59cb\u4eba\uff1f</p><p>\u3000\u3000\u5373\u4f7f\u4e09\u661f\u662f\u5728\u4f1a\u957f\u7684\u624b\u4e2d\u771f\u6b63\u7684\u5d1b\u8d77\uff0c\u4e0d\u8fc7\u524d\u4f1a\u957f\u901d\u4e16\u524d\u5df2\u7ecf\u6253\u4e0b\u575a\u5b9e\u7684\u6839\u57fa\uff0c\u4e09\u661f\u53d1\u5c55\u5230\u4eca\u5e74\uff0c\u5df2\u7ecf\u6709\u8fd1\u56db\u5341\u5e74\u7684\u5386\u53f2\u4e86\uff0c\u9526\u6e56\u5374\u624d\u6709\u516d\u5e74\uff0c\u96be\u602a\u4f1a\u957f\u4e00\u76f4\u5f3a\u8c03\u4e2d\u56fd\u662f\u4e2a\u904d\u5730\u7686\u9ec4\u91d1\u7684\u56fd\u5bb6\u3002</p><p>\u3000\u3000\u9886\u5bfc\u5f20\u606a\u5230\u4f1a\u5ba2\u5385\uff0c\u5973\u52a9\u624b\u63a9\u4e0a\u95e8\uff0c\u6bd5\u606d\u6bd5\u656c\u7684\u5750\u5728\u5916\u5ba4\uff0c\u9690\u7ea6\u542c\u89c1\u91cc\u9762\u7684\u8c08\u8bdd\u58f0\u3002</p><p>\u3000\u3000\u201c\u5728\u674e\u4f1a\u957f\u7684\u5fc3\u91cc\uff0c\u97e9\u56fd\u4e0e\u53d1\u8fbe\u56fd\u5bb6\u7684\u5dee\u8ddd\u6beb\u65e0\u6539\u89c2\uff0c\u4e2d\u56fd\u7684\u8ffd\u8d76\u6b65\u4f10\u65e5\u76ca\u52a0\u5feb\uff0c10\u5e74\u523015\u4e4b\u540e\uff0c\u97e9\u56fd\u8d56\u4ee5\u751f\u4ea7\u7684\u4ea7\u4e1a\u6709\u53ef\u80fd\u840e\u7f29\u2014\u2014\u8fd9\u79cd\u5185\u5fc3\u7684\u7126\u8e81\u96be\u9053\u771f\u8981\u674e\u4f1a\u957f\u52a8\u7528\u4eb2\u4eba\u4e3a\u68cb\u5b50\u6765\u6d88\u89e3\uff1f\u201d</p><p>\u3000\u3000\u5973\u52a9\u624b\u5fc3\u60f3\u6765\u4eba\u8fd8\u662f\u771f\u4e0d\u7559\u60c5\u9762\uff0c\u4ed6\u771f\u4ee5\u4e3a\u5c31\u770b\u900f\u4f1a\u957f\u7684\u5185\u5fc3\u5417\uff1f\u7adf\u7136\u6562\u8bbd\u523a\u4f1a\u957f\u4e8b\u5b9e\u662f\u4e0d\u81ea\u4fe1\uff01\u4f1a\u957f\u4e5f\u8bb8\u4f1a\u8d76\u4ed6\u51fa\u53bb\u5427\u3002</p><p>\u3000\u3000\u201c\u6211\u662f\u99a8\u4e88\u7684\u670b\u53cb\uff0c\u674e\u4f1a\u957f\u662f\u99a8\u4e88\u7684\u7236\u4eb2\uff0c\u4e5f\u8bb8\u6211\u4eec\u4ee5\u8fd9\u6837\u7684\u8eab\u4efd\uff0c\u53ef\u4ee5\u7ee7\u7eed\u4ea4\u6d41\u4e0b\u53bb\u2026\u2026\u201d</p></article>"}}}]);
