(window.webpackJsonp=window.webpackJsonp||[]).push([[225],{247:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u5341\u8282 \u7b2c\u56db\u7eaa",content:"<article><h2>\u7b2c\u4e8c\u5341\u8282 \u7b2c\u56db\u7eaa</h2><p>\u4ece\u5965\u6c83\u4f4f\u5904\u51fa\u6765\uff0c\u743c\u6069\u7684\u5fc3\u60c5\u6709\u6240\u597d\u8f6c\uff0c\u4ed6\u5c06\u6cd5\u672f\u9006\u8f6c\u6212\u6307\u6234\u4e0a\u53f3\u624b\u65e0\u540d\u6307\uff0c\u4e00\u80a1\u51b0\u51c9\u7684\u611f\u89c9\u4ece\u6212\u6307\u4e0a\u4f20\u6765\u3002\u6709\u4e86\u8fd9\u679a\u6212\u6307\uff0c\u4ed6\u4fbf\u80fd\u9006\u8f6c\u4e00\u5b9a\u6570\u91cf\u548c\u5f3a\u5ea6\u7684\u653b\u51fb\u6cd5\u672f\uff0c\u4e0d\u4f46\u662f\u9632\u5fa1\u81ea\u8eab\u7684\u575a\u76fe\uff0c\u540c\u65f6\u8fd8\u662f\u51fa\u5176\u4e0d\u610f\u7684\u653b\u51fb\u5229\u5668\u3002\u5927\u7ea6\u4e5f\u53ea\u6709\u5965\u6c83\u8fd9\u6837\u7684\u5343\u5e74\u8001\u5deb\u5996\uff0c\u624d\u4f1a\u628a\u8fd9\u79cd\u63a5\u8fd1\u795e\u5668\u7ea7\u522b\u7684\u5b9d\u7269\u968f\u624b\u9001\u4eba\u5427\u3002</p><p>\u4ed6\u6f2b\u65e0\u76ee\u7684\u5730\u5728\u8857\u4e0a\u8d70\u7740\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5230\u4e86\u5e7f\u573a\u9644\u8fd1\uff0c\u62ac\u5934\u770b\u89c1\u536b\u5175\u628a\u5b88\u7684\u901a\u5f80\u585e\u5c14\u7684\u4f20\u9001\u95e8\uff0c\u5012\u662f\u60f3\u8d77\u83f2\u4e9a\u5144\u59b9\u6765\u3002\u8bf8\u4e8b\u5b8c\u6bd5\uff0c\u81ea\u5df1\u4e5f\u5f88\u5feb\u8981\u52a8\u8eab\u53bb\u5e7d\u6697\u5730\u57df\uff0c\u4e00\u65f6\u95f4\u4e0d\u4f1a\u56de\u6765\uff0c\u8fd8\u662f\u628a\u8fd9\u4ef6\u4e8b\u60c5\u5904\u7406\u6389\u5427\u3002</p><p>\u901a\u8fc7\u4f20\u9001\u95e8\uff0c\u743c\u6069\u6765\u5230\u827e\u5c14\u5854\u67cf\u3002\u4ed6\u539f\u672c\u662f\u60f3\u76f4\u63a5\u7ed9\u83f2\u4e9a\u5144\u59b9\u4e00\u7b14\u94b1\uff0c\u89e3\u9664\u5974\u96b6\u5951\u7ea6\uff0c\u4ece\u6b64\u5206\u9053\u626c\u9573\uff0c\u518d\u65e0\u74dc\u845b\u3002\u4f46\u5728\u8def\u4e0a\uff0c\u4ed6\u7a81\u7136\u60f3\u8d77\u4e00\u4ef6\u4e8b\u60c5\u6765\u3002</p><p>\u8fea\u74e6\u514b\u662f\u4e2a\u5730\u5e95\u4f8f\u5112\uff0c\u66fe\u7ecf\u5c45\u4f4f\u5728\u5e03\u7075\u767b\u77f3\u57ce\uff0c\u5bf9\u5e7d\u6697\u5730\u57df\u7684\u60c5\u51b5\u5e94\u8be5\u975e\u5e38\u719f\u6089\uff0c\u8fd8\u66fe\u7ecf\u662f\u63a2\u77ff\u961f\u7684\u6210\u5458\u3002\u81ea\u5df1\u8fd9\u6b21\u7a81\u7136\u88ab\u585e\u8fdb\u519b\u961f\uff0c\u4e3a\u5236\u9020\u9b54\u50cf\u7684\u4e8b\u60c5\u524d\u5f80\u5e7d\u6697\u5730\u57df\uff0c\u5012\u662f\u6b63\u597d\u7528\u5f97\u7740\u4ed6\u3002</p><p>\u53ea\u662f\u4ed6\u4f1a\u613f\u610f\u4e48\uff1f</p><p>\u743c\u6069\u548c\u8fea\u74e6\u514b\u6ca1\u6253\u8fc7\u592a\u591a\u4ea4\u9053\uff0c\u5c31\u4ed6\u7684\u611f\u89c9\uff0c\u8fd9\u4e2a\u5730\u5e95\u4f8f\u5112\u662f\u4e2a\u5f88\u5e73\u5eb8\u7684\u4eba\uff0c\u80fd\u529b\u4e5f\u5e73\u5eb8\uff0c\u6027\u60c5\u4e5f\u5e73\u5eb8\uff0c\u6c89\u9ed8\u5be1\u8a00\uff0c\u6574\u5929\u9634\u6c89\u7740\u8138\uff0c\u4eff\u4f5b\u6240\u6709\u4eba\u90fd\u5728\u654c\u89c6\u9488\u5bf9\u4ed6\u4e00\u822c\u2014\u2014\u8fd9\u662f\u5730\u5e95\u4f8f\u5112\u7684\u6807\u51c6\u7279\u5f81\u4e4b\u4e00\u3002\u603b\u4f53\u6765\u8bf4\uff0c\u6ca1\u4ec0\u4e48\u503c\u5f97\u91cd\u89c6\u7684\u5730\u65b9\u3002\u4e0d\u8fc7\u8fd9\u6837\u4e5f\u597d\uff0c\u743c\u6069\u73b0\u5728\u6709\u70b9\u60ca\u5f13\u4e4b\u9e1f\u7684\u611f\u89c9\uff0c\u5982\u679c\u771f\u5e26\u4e00\u4e2a\u6709\u5a01\u80c1\u7684\u4eba\u4e0a\u8def\uff0c\u4ed6\u8fd8\u771f\u4e0d\u653e\u5fc3\u3002</p><p>\u6b64\u65f6\u5df2\u7ecf\u63a5\u8fd1\u665a\u4e0a\u5341\u4e00\u70b9\u949f\uff0c\u623f\u95f4\u91cc\u4f9d\u65e7\u8fd8\u4eae\u7740\u706f\u706b\uff0c\u743c\u6069\u8d70\u8fdb\u6765\u7684\u65f6\u5019\uff0c\u53d1\u73b0\u83f2\u4e9a\u5144\u59b9\u4e24\u4eba\u6b63\u5728\u8bf4\u4ec0\u4e48\uff0c\u89c1\u4ed6\u5230\u6765\uff0c\u4f4f\u53e3\u4e0d\u8c08\u3002</p><p>\u743c\u6069\u4e5f\u4e0d\u5728\u610f\uff0c\u8bf4\u660e\u6765\u610f\uff0c\u95ee\u8fea\u74e6\u514b\u662f\u5426\u613f\u610f\u968f\u4ed6\u524d\u5f80\u5e7d\u6697\u5730\u57df\uff0c\u6ca1\u60f3\u5230\u4f8f\u5112\u542c\u4e86\u4ee5\u540e\uff0c\u53cd\u95ee\u4e86\u4e00\u4e2a\u95ee\u9898\u3002</p><p>\u201c\u4f60\u4eec\u662f\u8981\u53bb\u653b\u6253\u9ed1\u6697\u7cbe\u7075\uff1f\u201d</p><p>\u743c\u6069\u4e00\u6014\uff0c\u968f\u5373\u53cd\u5e94\u8fc7\u6765\uff0c\u83f2\u4e9a\u66fe\u7ecf\u8bf4\u8fc7\uff0c\u4ed6\u4eec\u539f\u672c\u5c45\u4f4f\u7684\u4f8f\u5112\u57ce\u5e02\u5e03\u7075\u767b\u77f3\u57ce\uff0c\u53bb\u5e74\u88ab\u9ed1\u6697\u7cbe\u7075\u7684\u9b54\u7d22\u5e03\u83b1\u57ce\u653b\u9677\uff0c\u65cf\u4eba\u4f24\u4ea1\u60e8\u91cd\uff0c\u53ea\u6709\u5f88\u5c11\u4e00\u90e8\u5206\u9003\u51fa\u6765\uff0c\u80cc\u4e95\u79bb\u4e61\u6d41\u843d\u5730\u8868\u4e16\u754c\u3002</p><p>\u8fd9\u4e2a\u8fea\u74e6\u514b\u7684\u610f\u601d\uff0c\u662f\u60f3\u62a5\u4ec7\u5427\u3002</p><p>\u201c\u662f\u3002\u201d\u743c\u6069\u8bf4\uff0c\u8fd9\u5012\u4e5f\u4e0d\u7b97\u6492\u8c0e\u3002\u9ed1\u6697\u7cbe\u7075\u662f\u5e7d\u6697\u5730\u57df\u7684\u4e3b\u4eba\uff0c\u9634\u9b42\u57ce\u8fd9\u6b21\u524d\u53bb\uff0c\u80af\u5b9a\u8981\u627e\u4ed6\u4eec\u4e0b\u624b\uff0c\u5426\u5219\u96be\u9053\u53cc\u65b9\u8fd8\u4f1a\u5ba2\u5ba2\u6c14\u6c14\u5750\u4e0b\u6765\u548c\u5e73\u8c08\u5224\u4e48\u3002</p><p>\u201c\u884c\uff0c\u6211\u8ddf\u4f60\u53bb\u3002\u201d\u4f8f\u5112\u4e00\u53e3\u7b54\u5e94\u3002</p><p>\u5982\u6b64\u987a\u5229\u5730\u5c31\u89e3\u51b3\u4e86\u95ee\u9898\uff0c\u743c\u6069\u5012\u6709\u4e9b\u51fa\u4e4e\u610f\u6599\u4e4b\u5916\uff0c\u4e0d\u8fc7\u4e5f\u6ca1\u591a\u8bf4\u4ec0\u4e48\uff0c\u53ea\u662f\u8ba9\u8fea\u74e6\u514b\u5728\u8fd9\u91cc\u7b49\u5f85\u4ed6\u7684\u6d88\u606f\u3002\u81f3\u4e8e\u83f2\u4e9a\uff0c\u5979\u6ca1\u6709\u81ea\u4fdd\u80fd\u529b\uff0c\u539f\u672c\u4ec5\u6709\u7684\u4e00\u70b9\u56e0\u4e3a\u5df4\u5c14\u8840\u8109\u800c\u5e26\u6765\u7684\u5929\u8d4b\uff0c\u5982\u4eca\u4e5f\u5df2\u7ecf\u6d88\u5931\uff0c\u81ea\u7136\u53ea\u80fd\u7559\u5728\u585e\u5c14\u4e86\u3002</p><p>\u9664\u4e86\u6885\u83f2\u65af\u4e4b\u5916\uff0c\u7b2c\u4e8c\u4e2a\u6210\u5458\u4e5f\u6709\u4e86\u3002\u743c\u6069\u6709\u4e9b\u5934\u75bc\uff0c\u5fb7\u82cf\u5f97\u8bf4\u662f\u8bf4\u6709\u5341\u4e2a\u540d\u989d\uff0c\u5176\u5b9e\u4e09\u5929\u4e4b\u5185\uff0c\u743c\u6069\u4ece\u54ea\u91cc\u53bb\u627e\u5341\u4e2a\u4eba\uff1f\u53ea\u6015\u8fde\u4e00\u534a\u90fd\u51d1\u4e0d\u9f50\u3002</p><p>\u4e0d\u8fc7\u4e5f\u7b97\u4e86\uff0c\u5b81\u53ef\u4eba\u5c11\u4e9b\uff0c\u81f3\u5c11\u81ea\u5df1\u66f4\u5bb9\u6613\u638c\u63e1\u3002</p><p>\u518d\u6b21\u56de\u5230\u9634\u9b42\u57ce\uff0c\u5df2\u7ecf\u662f\u6df1\u591c\u96f6\u70b9\u4e86\uff0c\u743c\u6069\u8d70\u51fa\u4f20\u9001\u95e8\uff0c\u6b63\u8981\u56de\u5bb6\uff0c\u7a81\u7136\u957f\u888d\u4e0a\u7684\u80f8\u9488\u5fae\u5fae\u4eae\u8d77\uff0c\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u7684\u58f0\u97f3\u4f20\u51fa\uff0c\u8bf7\u4ed6\u8fc7\u53bb\u3002</p><p>\u743c\u6069\u5fae\u5fae\u72b9\u8c6b\u4e86\u4e00\u4e0b\uff0c\u6700\u7ec8\u8fd8\u662f\u4f9d\u547d\u524d\u5f80\u3002\u5e03\u96f7\u7eb3\u65af\u6b63\u5728\u6446\u5f04\u4e00\u526f\u68cb\u5c40\uff0c\u89c1\u743c\u6069\u8fdb\u6765\uff0c\u62db\u547c\u4ed6\u5728\u68cb\u67b0\u5bf9\u9762\u5750\u4e0b\u3002</p><p>\u201c\u4e0a\u6b21\u5728\u827e\u5c14\u5854\u67cf\uff0c\u6211\u8bf4\u8fc7\u8981\u6559\u4f60\u4e0b\u68cb\uff0c\u540e\u6765\u4e8b\u60c5\u7e41\u5fd9\uff0c\u4e00\u76f4\u7ed9\u5fd8\u4e86\uff0c\u201d\u738b\u5b50\u8bf4\uff0c\u201c\u4eca\u5929\u6b63\u597d\u6709\u7a7a\u3002\u201d</p><p>\u743c\u6069\u6c89\u9ed8\u5730\u542c\u7740\uff0c\u6ca1\u6709\u8bf4\u8bdd\u3002</p><p>\u5e03\u96f7\u7eb3\u65af\u5411\u743c\u6069\u4ecb\u7ecd\u89c4\u5219\uff0c\u5176\u5b9e\u4e5f\u5f88\u7b80\u5355\uff0c\u8fd9\u4e2a\u4e16\u754c\u7684\u6218\u68cb\u662f\u4ece\u91ce\u5916\u5192\u9669\u4e2d\u53d1\u5c55\u51fa\u6765\u7684\u3002\u68cb\u76d8\u4e0a\u6807\u8bb0\u6709\u5404\u79cd\u5730\u5f62\uff0c\u53ef\u4ee5\u4e34\u65f6\u53d8\u6362\uff1b\u53cc\u65b9\u5404\u6709\u4e5d\u679a\u68cb\u5b50\uff0c\u5206\u522b\u662f\u201c\u5deb\u5e08\u201d\u3001\u201c\u6218\u58eb\u201d\u3001\u201c\u91ce\u86ee\u4eba\u201d\u3001\u201c\u7267\u5e08\u201d\u3001\u201c\u5fb7\u9c81\u4f9d\u201d\u3001\u201c\u541f\u6e38\u8bd7\u4eba\u201d\u3001\u201c\u6e38\u8361\u8005\u201d\u548c\u201c\u6e38\u4fa0\u201d\u548c\u201c\u5723\u6b66\u58eb\u201d\uff0c\u5404\u81ea\u62e5\u6709\u4e0d\u540c\u7684\u6218\u6597\u6280\u80fd\uff0c\u68cb\u624b\u53ef\u4ee5\u5bf9\u4ed6\u4eec\u505a\u5404\u79cd\u66f4\u5177\u4f53\u7684\u8bbe\u5b9a\u3002\u5f53\u4e00\u679a\u68cb\u5b50\u6740\u6b7b\u5bf9\u65b9\u7684\u68cb\u5b50\u65f6\uff0c\u5219\u4f1a\u83b7\u5f97\u76f8\u5e94\u7684\u7ecf\u9a8c\u503c\uff0c\u7d2f\u79ef\u5230\u4e00\u5b9a\u7a0b\u5ea6\u4fbf\u53ef\u4ee5\u5347\u7ea7\uff0c\u751a\u81f3\u8fdb\u9636\uff0c\u4f8b\u5982\u201c\u6e38\u8361\u8005\u201d\u8fdb\u9636\u4e3a\u201c\u523a\u5ba2\u201d\uff0c\u62e5\u6709\u66f4\u5f3a\u5927\u7684\u7834\u574f\u529b\u3002</p><p>\u201c\u6765\u8bd5\u8bd5\u770b\uff1f\u201d\u5e03\u96f7\u7eb3\u65af\u89c1\u743c\u6069\u5df2\u7ecf\u57fa\u672c\u719f\u6089\u89c4\u5219\uff0c\u63d0\u8bae\u8bf4\u3002</p><p>\u743c\u6069\u70b9\u70b9\u5934\uff0c\u6446\u4e0a\u4e86\u68cb\u5b50\uff0c\u5f00\u59cb\u548c\u738b\u5b50\u5bf9\u5c40\u3002\u4ed6\u7b2c\u4e00\u6b21\u4e0b\u8fd9\u79cd\u6218\u68cb\uff0c\u5230\u5e95\u4e0d\u592a\u719f\u6089\uff0c\u4e5f\u6ca1\u4ed4\u7ec6\u7422\u78e8\u68cb\u5b50\u7684\u8bbe\u7f6e\uff0c\u53ea\u5c06\u201c\u5723\u6b66\u58eb\u201d\u7684\u4fe1\u4ef0\u795e\u7947\u8bbe\u5b9a\u4e3a\u63d0\u5c14\uff0c\u5176\u4ed6\u968f\u610f\u3002\u738b\u5b50\u5f88\u5c0f\u5fc3\u5730\u9009\u62e9\u7740\uff0c\u4ed6\u8bbe\u7f6e\u81ea\u5df1\u7684\u201c\u5deb\u5e08\u201d\u7cbe\u7814\u53d8\u5316\u5b66\u6d3e\uff0c\u201c\u7267\u5e08\u201d\u4fe1\u4ef0\u77e5\u8bc6\u4e4b\u795e\u6b27\u683c\u739b\uff0c\u201c\u91ce\u86ee\u4eba\u201d\u6765\u81ea\u5317\u5730\u51b0\u98ce\u8c37\uff0c\u6700\u5947\u602a\u7684\u662f\uff0c\u4ed6\u5c45\u7136\u628a\u201c\u6e38\u4fa0\u201d\u8bbe\u7f6e\u4e3a\u4e00\u4f4d\u6765\u81ea\u5e7d\u6697\u5730\u57df\u7684\u9ed1\u6697\u7cbe\u7075\uff0c\u800c\u4e14\u8fd8\u4fe1\u4ef0\u68ee\u6797\u5973\u795e\u6885\u4e3d\u51ef\uff0c\u8fd9\u8ba9\u743c\u6069\u8be7\u5f02\u4e07\u5206\uff0c\u6000\u7591\u738b\u5b50\u662f\u4e0d\u662f\u53d1\u6655\u4e86\uff0c\u9ed1\u6697\u7cbe\u7075\u54ea\u91cc\u6709\u4fe1\u4ef0\u6885\u4e3d\u51ef\u7684\uff1f\u4e0d\u8fc7\u6700\u7ec8\u5fcd\u4f4f\u6ca1\u8bf4\u4ec0\u4e48\u3002\u5f53\u505a\u8fd9\u4e00\u5207\u7684\u65f6\u5019\uff0c\u738b\u5b50\u5168\u795e\u8d2f\u6ce8\uff0c\u90d1\u91cd\u5176\u4e8b\uff0c\u4eff\u4f5b\u9762\u4e34\u771f\u6b63\u7684\u6218\u6597\u4e00\u822c\uff0c\u5373\u4f7f\u662f\u9762\u5bf9\u743c\u6069\u8fd9\u79cd\u83dc\u9e1f\u521d\u5b66\u8005\uff0c\u4ed6\u4e5f\u4f9d\u65e7\u6ca1\u6709\u534a\u70b9\u6389\u4ee5\u8f7b\u5fc3\u7684\u758f\u6f0f\u3002</p><p>\u7ed3\u679c\u662f\u6beb\u65e0\u7591\u95ee\u7684\uff0c\u4f5c\u4e3a\u521d\u5b66\u8005\u7684\u743c\u6069\u5f88\u5feb\u5c31\u88ab\u51b2\u7834\u9632\u7ebf\uff0c\u5e72\u8106\u5229\u843d\u5730\u8f93\u6389\u4e86\u8fd9\u4e00\u5c40\uff0c\u4ed6\u6709\u4e9b\u6cae\u4e27\u5730\u7ad9\u8d77\u8eab\u6765\u3002\u5e03\u96f7\u7eb3\u65af\u5fae\u7b11\uff0c\u201c\u6ca1\u5173\u7cfb\uff0c\u6162\u6162\u5b66\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u53cd\u6b63\u4f60\u6709\u7684\u662f\u65f6\u95f4\u548c\u673a\u4f1a\u3002\u201d</p><p>\u743c\u6069\u6ca1\u6709\u8bf4\u8bdd\uff0c\u770b\u7740\u5e03\u96f7\u7eb3\u65af\u5c06\u68cb\u5c40\u590d\u539f\uff0c\u7136\u540e\u81ea\u5df1\u548c\u81ea\u5df1\u4e0b\u8d77\u68cb\u6765\u3002\u4ed6\u4e00\u4eba\u540c\u65f6\u6307\u6325\u53cc\u65b9\u4ea4\u6218\uff0c\u7b80\u76f4\u4fbf\u5982\u5206\u5fc3\u4e92\u640f\u4e00\u822c\u3002\u743c\u6069\u9ed8\u9ed8\u770b\u4e86\u4e00\u4f1a\uff0c\u201c\u6bbf\u4e0b\uff0c\u201d\u4ed6\u7a81\u7136\u95ee\uff0c\u201c\u8fd9\u4e9b\u68cb\u5b50\uff0c\u5982\u679c\u5b83\u4eec\u4e5f\u6709\u610f\u8bc6\u7684\u8bdd\uff0c\u60f3\u5fc5\u4e5f\u4f1a\u4ee5\u4e3a\u81ea\u5df1\u5f53\u771f\u662f\u5728\u594b\u529b\u4f5c\u6218\uff0c\u628a\u63e1\u81ea\u5df1\u7684\u547d\u8fd0\u5427\u3002\u4f46\u4e8b\u5b9e\u4e0a\u751f\u6b7b\u7978\u798f\u64cd\u4e8e\u4eba\u624b\uff0c\u53c8\u6709\u4ec0\u4e48\u2018\u65f6\u95f4\u548c\u673a\u4f1a\u2019\u53ef\u8a00\u5462\u3002\u201d</p><p>\u738b\u5b50\u6ca1\u6709\u76f4\u63a5\u56de\u7b54\uff0c\u800c\u662f\u5148\u5bf9\u68cb\u76d8\u4e0a\u7684\u201c\u5deb\u5e08\u201d\u4e0b\u4e86\u4e2a\u547d\u4ee4\u3002\u201c\u5deb\u5e08\u201d\u91ca\u653e\u51fa\u53ec\u5524\u602a\u7269\u6cd5\u672f\uff0c\u62db\u6765\u4e09\u53ea\u5251\u8718\u86db\uff0c\u4e0a\u524d\u56f4\u4f4f\u5bf9\u624b\u7684\u201c\u5723\u6b66\u58eb\u201d\u3002\u201c\u4f60\u770b\u8fd9\u4e09\u53ea\u5251\u8718\u86db\uff0c\u201d\u738b\u5b50\u8bf4\uff0c\u201c\u5bf9\u4e8e\u5b83\u4eec\u800c\u8a00\uff0c\u81ea\u5df1\u662f\u68cb\u5b50\uff0c\u8fd9\u4e2a\u2018\u5deb\u5e08\u2019\u662f\u68cb\u624b\uff1b\u7136\u800c\u5bf9\u4e8e\u2018\u5deb\u5e08\u2019\u800c\u8a00\uff0c\u81ea\u5df1\u662f\u68cb\u5b50\uff0c\u4f60\u6211\u624d\u662f\u68cb\u624b\uff0c\u201d\u4ed6\u62ac\u5934\u770b\u4e86\u743c\u6069\u4e00\u773c\uff0c\u201c\u65e2\u7136\u8eab\u5728\u6b64\u4e16\uff0c\u4fbf\u5728\u68cb\u5c40\u4e4b\u4e2d\uff0c\u8c01\u4e5f\u6ca1\u6709\u4f8b\u5916\u3002\u4efb\u4f55\u4eba\u90fd\u662f\u68cb\u5b50\uff0c\u4e5f\u90fd\u53ef\u4ee5\u662f\u68cb\u624b\uff0c\u4e0d\u8fc7\u770b\u4f60\u4ece\u4ec0\u4e48\u89d2\u5ea6\u53bb\u770b\u7f62\u4e86\u3002\u201d</p><p>\u201c\u6bbf\u4e0b\u4e5f\u662f\u68cb\u5b50\u4e48\u3002\u201d\u743c\u6069\u76f4\u622a\u4e86\u5f53\u5730\u95ee\u3002</p><p>\u8fd9\u8bdd\u95ee\u5f97\u5f88\u662f\u5931\u793c\uff0c\u7136\u800c\u5e03\u96f7\u7eb3\u65af\u5168\u4e0d\u4ecb\u610f\uff0c\u201c\u662f\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u8fd9\u4e16\u754c\u4fbf\u662f\u68cb\u5c40\uff0c\u771f\u6b63\u80fd\u4e0d\u4ee5\u68cb\u5b50\u8eab\u4efd\u5b58\u5728\u7684\uff0c\u53ea\u6709\u4e09\u4f4d\u2014\u2014\u4e24\u4e2a\u5bf9\u5c40\u8005\uff0c\u4e00\u4e2a\u7834\u574f\u8005\u3002\u201d</p><p>\u201c\u90a3\u4e48\u6bbf\u4e0b\u4e5f\u662f\u60f3\u6210\u4e3a\u68cb\u624b\uff1f\u201d</p><p>\u201c\u4e0d\u662f\uff0c\u201d\u5e03\u96f7\u7eb3\u65af\u5fae\u7b11\uff0c\u201c\u6211\u53ea\u662f\u60f3\u628a\u8fd9\u68cb\u67b0\u7ed9\u7838\u4e86\u3002\u201d</p><p>\u743c\u6069\u5fae\u5fae\u6014\u4e86\u4e0b\uff0c\u4f46\u6ca1\u6709\u518d\u8ffd\u95ee\u8fd9\u4e2a\u95ee\u9898\u3002\u201c\u65e2\u7136\u751f\u5728\u6b64\u4e16\uff0c\u5c31\u5728\u5c40\u4e2d\uff0c\u90a3\u8bf7\u6559\u6bbf\u4e0b\uff0c\u5982\u4f55\u624d\u80fd\u5168\u8eab\u4fdd\u547d\u5462\uff1f\u201d</p><p>\u5e03\u96f7\u7eb3\u65af\u6307\u4e86\u6307\u68cb\u76d8\u4e0a\u7684\u4e00\u679a\u201c\u5deb\u5e08\u201d\uff0c\u201c\u6211\u53ef\u4ee5\u968f\u65f6\u628a\u5b83\u727a\u7272\u6389\uff0c\u6216\u8005\u76f4\u63a5\u53d6\u51fa\u68cb\u76d8\uff0c\u201d\u738b\u5b50\u8bf4\uff0c\u201c\u4f46\u4f60\u8bf4\uff0c\u6211\u4f1a\u4e0d\u4f1a\u8fd9\u4e48\u505a\uff1f\u201d</p><p>\u743c\u6069\u770b\u4e86\u4e00\u4f1a\uff0c\u6447\u6447\u5934\uff0c\u201c\u4e0d\u4f1a\u3002\u201d</p><p>\u5e03\u96f7\u7eb3\u65af\u7684\u5e03\u5c40\uff0c\u4ee5\u8fd9\u679a\u201c\u5deb\u5e08\u201d\u4e3a\u6838\u5fc3\uff0c\u5982\u679c\u5c11\u4e86\u5b83\uff0c\u8fd9\u76d8\u68cb\u5c31\u5f88\u96be\u53d6\u80dc\u4e86\u3002</p><p>\u201c\u90a3\u4f60\u660e\u767d\u4e86\uff1f\u201d</p><p>\u201c\u660e\u767d\u4e86\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6bbf\u4e0b\uff0c\u8bf7\u5141\u8bb8\u6211\u95ee\u6700\u540e\u4e00\u4e2a\u95ee\u9898\u3002\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u201c\u73b0\u5b9e\u7ec8\u7a76\u4e0d\u540c\u4e8e\u4e0b\u68cb\uff0c\u68cb\u5b50\u4e5f\u6709\u81ea\u6211\u610f\u8bc6\u3002\u90a3\u4e48\u4f5c\u4e3a\u4e0a\u4f4d\u8005\uff0c\u5982\u4f55\u4fdd\u8bc1\u5c5e\u4e0b\u7684\u5fe0\u8bda\u5462\uff1f\u201d</p><p>\u738b\u5b50\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u5fe0\u8bda\uff1f\u4e0a\u4f4d\u8005\u4e0d\u9700\u8981\u5fe0\u8bda\u3002\u201d</p><p>\u201c\u8fd9\u5ea7\u9634\u9b42\u57ce\u4e2d\uff0c\u5f88\u591a\u4eba\u5bf9\u591c\u5973\u58eb\u5e76\u4e0d\u5fe0\u8bda\uff0c\u4f46\u6211\u4eec\u4f9d\u65e7\u80fd\u7ad9\u5728\u6b64\u5904\uff0c\u800c\u6ca1\u6709\u88ab\u964d\u4e0b\u795e\u6012\u3002\u539f\u56e0\u65e0\u4ed6\uff0c\u56e0\u4e3a\u6211\u4eec\u6709\u65e0\u53ef\u66ff\u4ee3\u7684\u4ef7\u503c\uff0c\u201d\u738b\u5b50\u53cc\u624b\u4ea4\u53c9\uff0c\u770b\u7740\u743c\u6069\uff0c\u201c\u5fe0\u8bda\uff0c\u90a3\u662f\u592a\u8fc7\u4e8e\u73cd\u8d35\u7684\u4e1c\u897f\uff0c\u6216\u8bb8\u5728\u670b\u53cb\u548c\u60c5\u4eba\u4e4b\u95f4\u4f1a\u5bb9\u6613\u5b58\u5728\uff0c\u4f46\u4f5c\u4e3a\u4e0a\u4f4d\u8005\u800c\u8a00\uff0c\u6ca1\u6709\u8fd9\u4e2a\u8d44\u683c\u53bb\u5962\u6c42\u3002\u743c\u6069\uff0c\u8bb0\u7740\u6211\u7684\u5fe0\u544a\uff0c\u5c06\u6765\u6709\u4e00\u5929\uff0c\u4f60\u4f1a\u660e\u767d\u7684\u3002\u201d</p><p>\u201c\u5982\u679c\u4e0d\u80fd\u4fdd\u8bc1\u5fe0\u8bda\uff0c\u90a3\u4e48\u5982\u4f55\u624d\u80fd\u9a7e\u9a6d\u5c5e\u4e0b\u5462\uff1f\u201d</p><p>\u201c\u5982\u679c\u8fde\u9a7e\u9a6d\u5c5e\u4e0b\u7684\u4fe1\u5fc3\u548c\u80fd\u529b\u90fd\u6ca1\u6709\uff0c\u5c31\u7b97\u62e5\u6709\u5fe0\u8bda\uff0c\u8d25\u4ea1\u4e5f\u662f\u8fdf\u65e9\u7684\u4e8b\u60c5\u5427\uff0c\u201d\u738b\u5b50\u5e73\u6de1\u5730\u8bf4\uff0c\u201c\u8eab\u4e3a\u4e0a\u4f4d\u8005\uff0c\u82e5\u662f\u6ca1\u6709\u8fd9\u70b9\u80c6\u6c14\u548c\u81ea\u4fe1\uff0c\u4e0d\u5982\u65e9\u65e9\u9000\u4f4d\u8ba9\u8d24\uff0c\u8fd8\u80fd\u4fdd\u4f4f\u6027\u547d\u3002\u201d</p><p>\u743c\u6069\u8eac\u8eab\uff0c\u51c6\u5907\u544a\u8f9e\uff0c\u201c\u7a0d\u7b49\uff0c\u201d\u5e03\u96f7\u7eb3\u65af\u8bf4\uff0c\u201c\u6211\u9001\u4f60\u4e00\u4ef6\u793c\u7269\u3002\u201d</p><p>\u793c\u7269\u662f\u4e00\u526f\u6218\u68cb\uff0c\u201c\u5e7d\u6697\u5730\u57df\u4e0d\u5982\u5730\u8868\u4e16\u754c\uff0c\u6c89\u95f7\u5f97\u5f88\uff0c\u95f2\u6687\u4e0d\u59a8\u4e0b\u4e0b\u68cb\uff0c\u201d\u738b\u5b50\u8bf4\uff0c\u201c\u6211\u559c\u6b22\u4e0b\u68cb\uff0c\u53ef\u60dc\u8fd9\u57ce\u91cc\u4e00\u76f4\u627e\u4e0d\u5230\u51e0\u4e2a\u5408\u9002\u7684\u5bf9\u624b\uff0c\u62c9\u83ab\u6765\u514b\u53c8\u592a\u5fd9\u4e86\u3002\u4f60\u6709\u4e0b\u68cb\u7684\u5929\u8d4b\uff0c\u5e0c\u671b\u4e0b\u6b21\u518d\u89c1\u5230\u7684\u65f6\u5019\uff0c\u80fd\u966a\u6211\u591a\u4e0b\u51e0\u5c40\u3002\u201d</p><p>\u201c\u6211\u60f3\uff0c\u4e0d\u4f1a\u4ee4\u60a8\u5931\u671b\uff0c\u6bbf\u4e0b\u3002\u201d</p><p>\u743c\u6069\u8bf4\uff0c\u8eac\u8eab\u544a\u8f9e\u51fa\u95e8\u3002</p><p>\u203b\u203b\u203b</p><p>\u5e03\u96f7\u7eb3\u65af\u7ee7\u7eed\u4f4e\u5934\u4e0b\u68cb\uff0c\u5c31\u5728\u521a\u624d\u743c\u6069\u6240\u5750\u7684\u4f4d\u7f6e\u540e\u9762\uff0c\u4e00\u56e2\u6a21\u7cca\u7684\u9634\u5f71\u6e10\u6e10\u6d6e\u51fa\uff0c\u6162\u6162\u51dd\u805a\uff0c\u5851\u6210\u5b9e\u4f53\uff0c\u663e\u51fa\u4e00\u4e2a\u4eba\u6765\u3002\u4ed6\u770b\u8d77\u6765\u5927\u7ea6\u4e8c\u5341\u4f59\u5c81\uff0c\u6b63\u503c\u9752\u5e74\uff0c\u8138\u8272\u5374\u82cd\u767d\u5f97\u60ca\u4eba\uff0c\u773c\u7a9d\u6df1\u9677\uff0c\u900f\u7740\u6697\u91d1\u8272\u7684\u5fae\u5149\u3002\u9732\u51fa\u8896\u53e3\u7684\u53cc\u624b\u5012\u662f\u79c0\u6c14\u5f97\u5f88\uff0c\u7ea4\u7ec6\u4fee\u957f\uff0c\u4eff\u4f5b\u5973\u5b50\uff0c\u76ae\u80a4\u4e0a\u6cdb\u7740\u6de1\u6de1\u7684\u4e00\u5c42\u9634\u5f71\u5149\u6cfd\u3002\u4ed6\u7a7f\u7740\u7070\u8272\u957f\u888d\uff0c\u9880\u957f\u6d88\u7626\u7684\u8eab\u8eaf\u88f9\u5728\u6597\u7bf7\u4e2d\uff0c\u80f8\u53e3\u90e8\u4f4d\u954c\u7740\u4e00\u679a\u5fbd\u7ae0\uff1a\u6d45\u7070\u8272\u5e95\u4e0a\uff0c\u4e00\u53ea\u624b\u638c\u4e2d\u63e1\u7740\u6697\u91d1\u8272\u7684\u7403\u4f53\u3002</p><p>\u4ed6\u662f\u9634\u9b42\u738b\u5b50\u739b\u62c9\u65af\xb7\u5766\u8212\u5c14\u3002</p><p>\u6781\u5c11\u6709\u4eba\u8ba4\u8bc6\u8fd9\u4f4d\u738b\u5b50\uff0c\u751a\u81f3\u8fde\u542c\u8bf4\u8fc7\u4ed6\u7684\u540d\u5b57\u7684\u4eba\u90fd\u4e0d\u591a\u2014\u2014\u5728\u9634\u9b42\u57ce\u4e2d\uff0c\u4eba\u4eba\u90fd\u77e5\u9053\u6709\u5341\u4e8c\u4f4d\u9634\u9b42\u738b\u5b50\uff0c\u4f46\u8ba9\u4ed6\u4eec\u6570\u540d\u5b57\uff0c\u5374\u5927\u591a\u53ea\u80fd\u6570\u51fa\u5341\u4e2a\uff0c\u8fd8\u6709\u4e24\u4f4d\u4eff\u4f5b\u9690\u8eab\u5e55\u540e\uff0c\u4ece\u4e0d\u5728\u516c\u5f00\u573a\u5408\u51fa\u73b0\u3002\u8fd9\u4f4d\u739b\u62c9\u65af\u738b\u5b50\u5c31\u662f\u5176\u4e2d\u4e4b\u4e00\uff0c\u4ed6\u662f\u5e03\u96f7\u7eb3\u65af\u7684\u5144\u957f\uff0c\u9634\u9b42\u57ce\u4e2d\u6700\u9ad8\u660e\u7684\u9644\u9b54\u5deb\u5e08\u3002</p><p>\u201c\u600e\u4e48\u56de\u4e8b\uff0c\u739b\u62c9\u65af\uff1f\u201d\u5e03\u96f7\u7eb3\u65af\u76ef\u7740\u68cb\u76d8\uff0c\u5934\u4e5f\u4e0d\u62ac\u5730\u95ee\uff0c\u7136\u540e\u5c06\u6700\u540e\u4e00\u4e2a\u201c\u6218\u58eb\u201d\u5f80\u524d\u79fb\u52a8\u56db\u683c\uff0c\u62b5\u4f4f\u4e86\u5bf9\u65b9\u7684\u201c\u91ce\u86ee\u4eba\u201d\u3002</p><p>\u201c\u6211\u4e5f\u4e0d\u6e05\u695a\uff0c\u201d\u739b\u62c9\u65af\u8bf4\uff0c\u201c\u867d\u7136\u4ed6\u80fd\u8fd9\u4e48\u5feb\u51dd\u6210\u771f\u540d\uff0c\u786e\u5b9e\u8fdc\u8fdc\u51fa\u4e4e\u6211\u7684\u610f\u6599\uff0c\u4f46\u6211\u4e5f\u5e76\u4e0d\u662f\u6ca1\u6709\u9884\u5148\u505a\u51c6\u5907\u3002\u7136\u800c\u2026\u2026\u201d\u4ed6\u505c\u987f\u4e86\u4e00\u4e0b\uff0c\u201c\u6211\u7684\u6cd5\u672f\u7a81\u7136\u5c31\u88ab\u5b8c\u5168\u6d88\u9664\u4e86\u3002\u201d</p><p>\u201c\u4e0d\u80fd\u518d\u4e0b\u4e00\u4e2a\u8bb0\u5fc6\u7f16\u7ec7\u5417\u3002\u201d</p><p>\u201c\u5f88\u5947\u602a\uff0c\u6211\u5b8c\u5168\u65e0\u6cd5\u518d\u5ea6\u4fb5\u5165\u4ed6\u7684\u610f\u8bc6\uff0c\u201d\u739b\u62c9\u65af\u8bf4\uff0c\u201c\u4ed6\u88ab\u67d0\u79cd\u529b\u91cf\u4fdd\u62a4\u4f4f\u4e86\u3002\u201d</p><p>\u201c\u662f\u4ed6\u624b\u4e0a\u7684\u6cd5\u672f\u9006\u8f6c\u6212\u6307\uff1f\u201d</p><p>\u201c\u4e0d\u662f\uff0c\u662f\u53e6\u5916\u4e00\u79cd\u5f62\u5f0f\u7684\u529b\u91cf\u3002\u201d</p><p>\u5e03\u96f7\u7eb3\u65af\u5fae\u5fae\u76b1\u8d77\u7709\u5934\uff0c\u201c\u662f\u5965\u6c83\u5148\u751f\uff1f\u201d</p><p>\u739b\u62c9\u65af\u6447\u5934\uff0c\u201c\u4e0d\u662f\u3002\u5965\u6c83\u5148\u751f\u5e76\u4e0d\u7cbe\u64c5\u9644\u9b54\u672f\uff0c\u66f4\u4f55\u51b5\uff0c\u201d\u4ed6\u7684\u8bed\u6c14\u5e73\u6de1\uff0c\u6ca1\u6709\u4e1d\u6beb\u7684\u6291\u626c\u987f\u632b\uff0c\u5374\u9690\u9690\u5e26\u4e0a\u4e86\u4e9b\u8bb8\u81ea\u50b2\uff0c\u201c\u4ec5\u4ee5\u9644\u9b54\u672f\u800c\u8bba\uff0c\u8fd9\u4e2a\u4e16\u754c\u4e0a\u7684\u51e1\u4eba\uff0c\u6ca1\u6709\u51e0\u4f4d\u5728\u6211\u4e4b\u4e0a\u7684\u3002\u201d</p><p>\u5e03\u96f7\u7eb3\u65af\u6c89\u541f\u7740\uff0c\u4ed6\u542c\u660e\u767d\u4e86\u739b\u62c9\u65af\u7684\u6697\u793a\uff0c\u201c\u4e0d\u662f\u51e1\u4eba\u2026\u2026\u8fd9\u4e48\u8bf4\uff0c\u662f\u591c\u5973\u58eb\uff1f\u201d</p><p>\u201c\u6211\u60f3\uff0c\u662f\u5979\u3002\u201d</p><p>\u5e03\u96f7\u7eb3\u65af\u6c89\u9ed8\u4e0d\u8bed\uff0c\u64cd\u7eb5\u68cb\u76d8\u4e0a\u7684\u201c\u5deb\u5e08\u201d\uff0c\u8ba9\u5b83\u91ca\u653e\u4e86\u4e00\u4e2a\u86db\u7f51\u672f\uff0c\u6682\u65f6\u963b\u62e6\u5bf9\u624b\u7684\u653b\u51fb\u3002\u201c\u591c\u5973\u58eb\u4e48\uff0c\u201d\u4ed6\u6700\u540e\u6162\u6162\u5f00\u53e3\uff0c\u201c\u73b0\u5728\u624d\u63d2\u624b\uff0c\u5df2\u7ecf\u8fdf\u4e86\u3002\u201d</p><p>\u8bdd\u97f3\u672a\u843d\uff0c\u9aa4\u7136\u95f4\u4e00\u9053\u8000\u773c\u7684\u94f6\u8272\u95ea\u7535\u4ece\u5929\u9645\u5212\u8fc7\uff0c\u5288\u7834\u591c\u7a79\uff0c\u7167\u8000\u5f97\u6574\u4e2a\u623f\u95f4\u90fd\u662f\u660e\u6643\u6643\u7684\uff0c\u968f\u5373\u9707\u96f7\u9690\u9690\uff0c\u5929\u5730\u8f70\u9e23\uff0c\u5c31\u8fde\u6574\u5ea7\u6d6e\u7a7a\u57ce\u90fd\u5fae\u5fae\u98a4\u6296\u8d77\u6765\u3002\u5e03\u96f7\u7eb3\u65af\u63a8\u5f00\u68cb\u76d8\uff0c\u8d77\u8eab\u8d70\u5230\u7a97\u53e3\u5f80\u5916\u770b\u53bb\uff0c\u53ea\u89c1\u9ed1\u6c89\u6c89\u7684\u591c\u7a7a\u4e4b\u4e0a\uff0c\u5343\u4e07\u9897\u661f\u8fb0\u79fb\u52a8\u53d8\u5e7b\u7740\uff0c\u6784\u6210\u4e86\u4e00\u4e2a\u65e0\u8fb9\u65e0\u9645\u7684\u7b26\u6587\u4e4b\u6d77\uff0c\u53d1\u51fa\u4ee4\u4eba\u4e0d\u53ef\u6b63\u89c6\u7684\u523a\u773c\u5149\u8292\u3002\u7d27\u63a5\u7740\u8f70\u9e23\u4e00\u58f0\uff0c\u5343\u4e07\u9897\u6d41\u661f\u81ea\u90a3\u7b26\u6587\u6d77\u6d0b\u4e2d\u5760\u843d\u4e0b\u6765\uff0c\u62d6\u66f3\u7740\u957f\u957f\u7684\u706b\u5149\u707f\u70c2\u71c3\u70e7\uff0c\u6700\u7ec8\u88ab\u6c89\u6c89\u9ed1\u6697\u541e\u566c\u3002</p><p>\u96f7\u4e91\u6eda\u8361\uff0c\u661f\u5760\u5982\u96e8\uff0c\u5217\u7f3a\u9739\u96f3\uff0c\u5929\u5730\u9707\u6447\uff0c\u738b\u5b50\u8d1f\u624b\u800c\u7acb\uff0c\u770b\u7740\u7a7a\u4e2d\u90a3\u707f\u70c2\u7684\u661f\u8fb0\u7b26\u6587\uff0c\u5634\u89d2\u5fae\u5fae\u9732\u51fa\u4e00\u4e1d\u7b11\u610f\u6765\u3002</p><p>\u201c\u771f\u6b63\u7684\u68cb\u5c40\u5f00\u59cb\uff0c\u7b2c\u56db\u7eaa\u8981\u5230\u6765\u4e86\u3002\u201d</p></article>"}}}]);
