(window.webpackJsonp=window.webpackJsonp||[]).push([[226],{248:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u5341\u4e00\u8282 \u5e95\u7ebf",content:"<article><h2>\u7b2c\u4e8c\u5341\u4e00\u8282 \u5e95\u7ebf</h2><p>\u743c\u6069\u8d70\u51fa\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u7684\u4f4f\u5904\uff0c\u9634\u9b42\u57ce\u5b9e\u884c\u5bb5\u7981\uff0c\u6b64\u65f6\u5df2\u7ecf\u6df1\u591c\uff0c\u8857\u9053\u4e0a\u7a7a\u65e0\u4e00\u4eba\u3002\u8d35\u65cf\u884c\u52a8\u5012\u4e0d\u5728\u5bb5\u7981\u4e4b\u5217\uff0c\u743c\u6069\u4e5f\u4e0d\u5728\u610f\uff0c\u9ed8\u81ea\u56de\u60f3\u7740\u521a\u624d\u548c\u738b\u5b50\u7684\u8c08\u8bdd\uff0c\u4e00\u8fb9\u5c06\u624b\u4e0a\u7684\u90a3\u76d2\u6218\u68cb\u4e22\u8fdb\u6b21\u5143\u888b\u91cc\u3002</p><p>\u624b\u4e0a\u7684\u5b9d\u77f3\u8df3\u8dc3\u6212\u6307\u7a81\u7136\u98a4\u52a8\u8d77\u6765\uff0c\u4f20\u51fa\u6885\u83f2\u65af\u7684\u58f0\u97f3\uff0c\u201c\u4f60\u5728\u54ea\uff1f\u201d\u5979\u4f3c\u4e4e\u6709\u4e9b\u7740\u6025\uff0c\u201c\u8fd9\u4e48\u665a\u4e86\u3002\u201d</p><p>\u201c\u54e6\uff0c\u5965\u6c83\u8001\u5e08\u627e\u6211\u6709\u70b9\u4e8b\u60c5\u803d\u6401\u4e86\uff0c\u9a6c\u4e0a\u56de\u6765\u3002\u201d</p><p>\u786e\u5b9e\u662f\u5df2\u7ecf\u5f88\u665a\u4e86\uff0c\u743c\u6069\u81ea\u4ece\u56de\u9634\u9b42\u57ce\u4ee5\u6765\uff0c\u8fd8\u4ece\u6ca1\u8fd9\u4e48\u665a\u56de\u5bb6\u8fc7\uff0c\u73ca\u5609\u548c\u6885\u83f2\u65af\u60f3\u5fc5\u90fd\u7b49\u6025\u4e86\u3002\u6b64\u524d\u4ed6\u5927\u90e8\u5206\u65f6\u95f4\u90fd\u5728\u5965\u6c83\u548c\u5e03\u96f7\u7eb3\u65af\u7684\u4f4f\u5904\uff0c\u8fd9\u79cd\u5730\u65b9\u80af\u5b9a\u90fd\u662f\u6709\u9b54\u6cd5\u5c4f\u969c\u963b\u9694\uff0c\u4f20\u8baf\u4f20\u9001\u4e4b\u7c7b\u7684\u6cd5\u672f\u65e0\u6cd5\u7a7f\u900f\u7684\uff0c\u6240\u4ee5\u6885\u83f2\u65af\u4e5f\u8054\u7cfb\u4e0d\u4e0a\u3002</p><p>\u201c\u662f\u8be5\u56de\u5bb6\u4e86\u3002\u201d\u4ed6\u60f3\u3002</p><p>\u6b63\u5728\u6b64\u65f6\uff0c\u4e00\u9053\u8000\u773c\u95ea\u7535\u8f70\u7136\u5288\u4e0b\uff0c\u5239\u90a3\u95f4\u773c\u524d\u90fd\u662f\u4e00\u6643\uff0c\u8fd8\u6ca1\u56de\u8fc7\u795e\u6765\uff0c\u7d27\u63a5\u7740\u5c31\u662f\u9707\u96f7\u8f70\u9e23\uff0c\u811a\u4e0b\u571f\u5730\u90fd\u5728\u9690\u9690\u98a4\u52a8\uff0c\u4eff\u4f5b\u6574\u5ea7\u6d6e\u7a7a\u57ce\u90fd\u8981\u5d29\u584c\u4f3c\u7684\u3002\u7535\u95ea\u96f7\u9e23\uff0c\u743c\u6069\u5012\u4e5f\u89c1\u8fc7\u4e0d\u5c11\u4e86\uff0c\u8fd9\u7b49\u9707\u5929\u64bc\u5730\u7684\u5a01\u52bf\u5374\u8fd8\u662f\u7b2c\u4e00\u6b21\u9886\u6559\uff0c\u9aa4\u7136\u95f4\u51e0\u4e4e\u7ed9\u5413\u4e86\u4e00\u8df3\uff0c\u62ac\u5934\u671d\u5929\u4e0a\u770b\u53bb\uff0c\u53ea\u89c1\u5343\u4e07\u9897\u661f\u8fb0\u7f16\u7ec7\u6210\u4e00\u9053\u9053\u660e\u4eae\u7684\u7b26\u6587\uff0c\u7eb7\u7ec7\u4ea4\u9519\uff0c\u8fde\u7ef5\u65e0\u5c3d\uff0c\u604d\u82e5\u6c6a\u6d0b\u5f25\u6f2b\u5929\u9645\u3002</p><p>\u661f\u5149\u707f\u70c2\u523a\u76ee\uff0c\u743c\u6069\u53ea\u62ac\u5934\u770b\u4e86\u4e24\u79d2\u949f\u5c31\u89c9\u5f97\u53cc\u773c\u751f\u75bc\uff0c\u51e0\u4e4e\u8981\u6d41\u51fa\u6cea\u6765\uff0c\u4ed6\u614c\u5fd9\u4f4e\u5934\u4e0d\u6562\u518d\u770b\uff0c\u5fc3\u4e2d\u5374\u662f\u9887\u4e3a\u7591\u60d1\u3002\u4ed6\u9690\u7ea6\u8bb0\u5f97\uff0c\u5728\u70db\u5821\u7684\u65f6\u5019\u66fe\u7ecf\u8bfb\u8fc7\u4e00\u4efd\u8d44\u6599\uff0c\u8bf4\u5341\u4e94\u5e74\u524d\u7684\u52a8\u8361\u65f6\u4ee3\uff0c\u8bf8\u795e\u4ee5\u5723\u8005\u5f62\u6001\u964d\u4e34\u51e1\u95f4\u4e4b\u524d\uff0c\u4e5f\u66fe\u6709\u5f88\u591a\u4eba\u770b\u5230\u8fd9\u79cd\u5929\u8c61\u5f02\u5146\u3002</p><p>\u201c\u96be\u9053\uff0c\u662f\u53c8\u4e00\u6b21\u52a8\u8361\u5e74\u4ee3\u5c06\u8981\u5230\u6765\uff0c\u7136\u800c\u8ddd\u79bb\u4e0a\u6b21\u624d\u4e0d\u8fc7\u5341\u4e94\u5e74\u800c\u5df2\uff1f\u201d</p><p>\u5ff5\u53ca\u6b64\u5904\uff0c\u7a81\u7136\u60f3\u8d77\u4e00\u4e2a\u95ee\u9898\u6765\uff0c\u52a8\u8361\u5e74\u4ee3\u662f1358DR\uff0c\u8ddd\u4eca\uff081372DR\uff09\u662f\u5341\u4e94\u5e74\uff0c\u81ea\u5df1\u4eca\u5e74\u4e5f\u662f\u5341\u4e94\u5c81\u2014\u2014\u81ea\u5df1\u7684\u51fa\u751f\u548c\u8bf8\u795e\u5723\u8005\u4e34\u51e1\uff0c\u6070\u597d\u662f\u540c\u4e00\u5e74\u3002</p><p>\u8fd9\u539f\u672c\u4e5f\u6ca1\u4ec0\u4e48\uff0c\u636e\u70db\u5821\u4e00\u4efd\u8d44\u6599\u7edf\u8ba1\uff0c\u6574\u4e2a\u8d39\u4f26\u5927\u9646\u7684\u4eba\u53e3\uff08\u5305\u62ec\u7cbe\u7075\u3001\u77ee\u4eba\u3001\u4f8f\u5112\u7b49\u7b49\u5728\u5185\uff09\uff0c\u5927\u7ea6\u662f\u516b\u5343\u4e07\u4eba\uff0c\u5e73\u5747\u6bcf\u5e74\u90fd\u4f1a\u6709\u4e00\u767e\u591a\u4e07\u4eba\u51fa\u751f\uff0c\u4e0d\u5dee\u743c\u6069\u8fd9\u4e00\u4e2a\u3002\u7136\u800c\u7ecf\u5386\u4e86\u8fd9\u4e24\u5929\u7684\u4e8b\u60c5\uff0c\u4ed6\u5982\u4eca\u795e\u7ecf\u683c\u5916\u654f\u611f\uff0c\u8fd9\u4ef6\u5e73\u65f6\u6216\u8bb8\u5b8c\u5168\u4e0d\u4f1a\u5728\u610f\u7684\u4e8b\u60c5\uff0c\u73b0\u5728\u4e5f\u88ab\u91cd\u89c6\u8d77\u6765\u3002</p><p>\u201c\u8fd9\u5176\u4e2d\u4f1a\u6709\u4ec0\u4e48\u8054\u7cfb\u4e48\uff1f\u201d</p><p>\u7136\u800c\u60f3\u6765\u60f3\u53bb\uff0c\u5374\u4e5f\u4f3c\u4e4e\u6ca1\u4ec0\u4e48\u5173\u8054\uff0c\u56e0\u4e3a\u5f53\u65f6\u9634\u9b42\u57ce\u8fd8\u5c01\u5728\u5e7d\u5f71\u754c\u4e2d\uff0c\u800c\u8bf8\u795e\u5723\u8005\u662f\u96c6\u4f53\u964d\u4e34\u7269\u8d28\u754c\uff0c\u5e94\u8be5\u6ca1\u6709\u4ea4\u96c6\u624d\u5bf9\u3002\u6bd4\u5982\u8bf4\u90a3\u4f4d\u5df4\u5c14\uff0c\u5c31\u7b97\u60f3\u6765\u9634\u9b42\u57ce\u64ad\u79cd\u90fd\u529e\u4e0d\u5230\u3002</p><p>\u661f\u5149\u7b26\u6587\u4e4b\u6d77\u53ea\u51fa\u73b0\u4e86\u7247\u523b\uff0c\u4e0d\u5230\u5341\u79d2\u949f\u65f6\u95f4\u5c31\u6e10\u6e10\u9690\u53bb\uff0c\u53c8\u91cd\u65b0\u6062\u590d\u5230\u539f\u672c\u7684\u591c\u7a79\u666f\u8c61\u3002\u5728\u6b64\u671f\u95f4\uff0c\u9634\u9b42\u57ce\u4e2d\u5168\u65e0\u52a8\u9759\uff0c\u6ca1\u6709\u534a\u4e2a\u4eba\u51fa\u95e8\u89c2\u770b\u662f\u51fa\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff0c\u8fd9\u8ba9\u743c\u6069\u6709\u4e9b\u5947\u602a\u3002</p><p>\u56de\u5230\u5bb6\u4e2d\uff0c\u73ca\u5609\u548c\u6885\u83f2\u65af\u90fd\u5728\u7b49\u4ed6\uff0c\u201c\u600e\u4e48\u8fd9\u4e48\u665a\u624d\u56de\u6765\uff0c\u201d\u73ca\u5609\u57cb\u6028\uff0c\u201c\u90fd\u5df2\u7ecf\u5feb\u4e00\u70b9\u949f\u4e86\u3002\u201d</p><p>\u201c\u5965\u6c83\u8001\u5e08\u627e\u6211\u6709\u70b9\u4e8b\u60c5\u3002\u201d\u743c\u6069\u89e3\u91ca\u3002</p><p>\u201c\u90a3\u81f3\u5c11\u4e5f\u8be5\u8bf4\u4e00\u58f0\u554a\u3002\u201d</p><p>\u201c\u55ef\uff0c\u5bf9\u4e0d\u8d77\u59d0\u59d0\uff0c\u201d\u743c\u6069\u9053\u6b49\uff0c\u201c\u4e0b\u6b21\u6211\u8bb0\u5f97\u4e86\u3002\u201d</p><p>\u201c\u6211\u624d\u61d2\u5f97\u7ba1\u4f60\uff0c\u662f\u827e\u5f25\u8587\u7b49\u6025\u4e86\u3002\u201d</p><p>\u6885\u83f2\u65af\u5012\u6ca1\u6709\u591a\u8bf4\u4ec0\u4e48\uff0c\u81ea\u5df1\u5148\u56de\u623f\u95f4\u4f11\u606f\u53bb\u4e86\u3002</p><p>\u56e0\u4e3a\u9a6c\u4e0a\u5c31\u8981\u79bb\u57ce\u53bb\u5e7d\u6697\u5730\u57df\uff0c\u743c\u6069\u65e9\u548c\u6885\u83f2\u65af\u8bf4\u597d\uff0c\u8fd9\u4e24\u5929\u665a\u4e0a\u90fd\u5f97\u966a\u7740\u73ca\u5609\u3002\u82e5\u5728\u4ee5\u524d\uff0c\u8fd9\u662f\u4ed6\u6c42\u4e4b\u4e0d\u5f97\u7684\u4e8b\u60c5\uff0c\u7136\u800c\u56e0\u4e3a\u6628\u665a\u7684\u4e8b\u60c5\uff0c\u8ba9\u4ed6\u5fc3\u4e2d\u6f5c\u751f\u7591\u8651\uff0c\u4eca\u5929\u6298\u817e\u4e00\u65e5\uff0c\u53c8\u6709\u4e9b\u75b2\u5026\u4e86\uff0c\u8349\u8349\u6d17\u6f31\u5b8c\u4e86\uff0c\u8131\u8863\u670d\u4e0a\u5e8a\u5c31\u51c6\u5907\u7761\u53bb\u3002</p><p>\u73ca\u5609\u62cd\u4e86\u62cd\u4ed6\uff0c\u201c\u5c0f\u5f1f\uff0c\u600e\u4e48\u4e86\uff0c\u201d\u5979\u5173\u5207\u5730\u95ee\uff0c\u201c\u600e\u4e48\u597d\u50cf\u4eca\u5929\u7cbe\u795e\u4e0d\u592a\u597d\uff0c\u51fa\u4ec0\u4e48\u4e8b\u60c5\u4e86\uff1f\u201d</p><p>\u201c\u6ca1\u6709\uff0c\u59d0\u59d0\uff0c\u53ea\u662f\u6709\u70b9\u7d2f\u4e86\u5427\u3002\u201d</p><p>\u73ca\u5609\u6ca1\u6709\u518d\u8bf4\u8bdd\uff0c\u5173\u4e86\u706f\uff0c\u623f\u95f4\u91cc\u9677\u5165\u9ed1\u6697\u3002\u743c\u6069\u786e\u5b9e\u6709\u4e9b\u5026\u4e86\uff0c\u6e10\u6e10\u6709\u4e86\u7761\u610f\uff0c\u6b63\u8ff7\u8ff7\u7cca\u7cca\u95f4\uff0c\u611f\u89c9\u4e00\u5177\u67d4\u8f6f\u7684\u8eab\u4f53\u94bb\u5230\u81ea\u5df1\u6000\u91cc\u6765\uff0c\u4f34\u968f\u7740\u719f\u6089\u7684\u4eff\u4f5b\u9634\u5f71\u5170\u5473\u9053\u7684\u4f53\u9999\u3002\u743c\u6069\u987a\u624b\u629a\u6478\u5728\u5979\u7684\u810a\u80cc\u4e0a\uff0c\u808c\u80a4\u5149\u6d01\u7ec6\u81f4\uff0c\u4eff\u4f5b\u6700\u540d\u8d35\u7684\u7ef8\u7f0e\uff0c\u53c8\u4eff\u4f5b\u7cbe\u7f8e\u7684\u74f7\u5668\uff0c\u6ca1\u6709\u4e00\u4e1d\u7455\u75b5\u3002</p><p>\u743c\u6069\u611f\u89c9\u73ca\u5609\u5728\u81ea\u5df1\u7684\u8116\u9888\u4e0a\u4eb2\u543b\u7740\uff0c\u6e29\u70ed\u7684\u5634\u5507\u8ba9\u4ed6\u6062\u590d\u4e86\u4e9b\u6e05\u9192\uff0c\u7741\u5f00\u773c\u775b\u770b\u7740\u6000\u91cc\u7684\u5c11\u5973\uff0c\u53d1\u73b0\u5979\u5df2\u7ecf\u5168\u8eab\u8d64\u88f8\uff0c\u8fd9\u597d\u50cf\u662f\u73ca\u5609\u7b2c\u4e00\u6b21\u4e3b\u52a8\u628a\u81ea\u5df1\u8131\u5149\u5462\u3002</p><p>\u201c\u59d0\u59d0\u3002\u201d</p><p>\u201c\u5514\uff0c\u201d\u73ca\u5609\u542b\u7cca\u5730\u5e94\u7740\uff0c\u8f7b\u8f7b\u5728\u4ed6\u80f8\u53e3\u54ac\u556e\uff0c\u5410\u51fa\u820c\u5c16\u8f7b\u8f7b\u8214\u7740\uff0c\u201c\u5c0f\u5f1f\uff0c\u9a6c\u4e0a\u5c31\u8981\u8d70\u4e86\uff0c\u4e0d\u60f3\u5148\u628a\u59d0\u59d0\u5403\u4e86\u4e48\uff1f\u201d</p><p>\u743c\u6069\u6c89\u9ed8\u7740\uff0c\u8f7b\u8f7b\u629a\u6478\u5979\u7684\u79c0\u53d1\uff0c\u201c\u7b49\u6211\u56de\u6765\u5427\uff0c\u59d0\u59d0\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u6211\u73b0\u5728\u5fc3\u91cc\u6709\u70b9\u4e8b\u60c5\u3002\u201d</p><p>\u201c\u4ec0\u4e48\u4e8b\u60c5\uff1f\u4e0d\u80fd\u5bf9\u59d0\u59d0\u8bf4\u5417\uff1f\u201d</p><p>\u201c\u6709\u70b9\u4e8b\u60c5\u6ca1\u60f3\u6e05\u695a\u7f62\u4e86\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u59d0\u59d0\uff0c\u522b\u95ee\u4e86\u597d\u5417\u3002\u201d</p><p>\u73ca\u5609\u55ef\u4e86\u4e00\u58f0\uff0c\u4e0d\u518d\u8ffd\u95ee\uff0c\u201c\u6211\u7684\u5f1f\u5f1f\u539f\u6765\u662f\u4e2a\u5b8c\u7f8e\u4e3b\u4e49\u8005\u5462\u3002\u201d\u5979\u8f7b\u58f0\u7b11\u7740\u3002</p><p>\u201c\u56e0\u4e3a\u8981\u9762\u5bf9\u7684\u662f\u59d0\u59d0\u4f60\u554a\u3002\u201d</p><p>\u73ca\u5609\u7684\u5934\u5fae\u5fae\u540e\u4ef0\uff0c\u770b\u7740\u743c\u6069\uff0c\u5979\u7684\u53cc\u773c\u5728\u9ed1\u6697\u4e2d\u770b\u8d77\u6765\u4eff\u4f5b\u660e\u4eae\u7684\u5b9d\u77f3\uff0c\u539f\u672c\u5c31\u8f6e\u5ed3\u5206\u660e\u7684\u8138\u770b\u8d77\u6765\u66f4\u52a0\u82f1\u6c14\u903c\u4eba\u3002\u201c\u5c0f\u5f1f\uff0c\u4f60\u7b54\u5e94\u59d0\u59d0\uff0c\u8bf4\u8981\u7ed9\u59d0\u59d0\u5efa\u4e00\u5ea7\u6d6e\u7a7a\u57ce\u5462\u3002\u201d</p><p>\u201c\u55ef\uff0c\u6211\u7b54\u5e94\u7684\u3002\u201d</p><p>\u201c\u4e00\u8a00\u4e3a\u5b9a\uff1f\u201d</p><p>\u201c\u4e00\u8a00\u4e3a\u5b9a\u3002\u201d</p><p>\u201c\u90a3\u59d0\u59d0\u7b49\u7740\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u522b\u8ba9\u6211\u7b49\u592a\u4e45\u3002\u201d</p><p>\u743c\u6069\u5c06\u73ca\u5609\u62b1\u8fdb\u6000\u91cc\uff0c\u201c\u4e0d\u4f1a\uff0c\u201d\u4ed6\u4fdd\u8bc1\uff0c\u4f3c\u4e4e\u662f\u5bf9\u73ca\u5609\u4e5f\u50cf\u662f\u5bf9\u81ea\u5df1\u8bf4\uff0c\u201c\u6709\u65f6\u5019\uff0c\u4e16\u754c\u4e0a\u7684\u4e8b\u60c5\uff0c\u53d8\u5316\u4e4b\u5feb\u8d85\u51fa\u6211\u4eec\u7684\u60f3\u8c61\u7684\u3002\u201d</p><p>\u201c\u53ef\u80fd\u5462\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u5c06\u8138\u57cb\u5728\u4ed6\u80f8\u53e3\uff0c\u201c\u5bf9\u4e86\uff0c\u201d\u5979\u7a81\u7136\u60f3\u4e86\u8d77\u6765\uff0c\u201c\u5dee\u70b9\u5fd8\u4e86\u4ef6\u4e8b\u60c5\uff0c\u8299\u857e\u72c4\u665a\u4e0a\u6765\u627e\u8fc7\u4f60\u3002\u201d</p><p>\u743c\u6069\u7565\u7565\u6709\u4e9b\u8be7\u5f02\uff0c\u201c\u5979\u6765\u505a\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u6211\u4e5f\u6ca1\u4ed4\u7ec6\u95ee\uff0c\u8bf4\u662f\u4e3a\u5979\u59d0\u59d0\u8299\u8389\u5a05\u7684\u4e8b\u60c5\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u597d\u50cf\u662f\u542c\u8bf4\u4f60\u8981\u53bb\u5e7d\u6697\u5730\u57df\uff0c\u60f3\u8ba9\u4f60\u5e26\u4e0a\u8299\u8389\u5a05\u4e00\u8d77\u3002\u201d</p><p>\u201c\u5e26\u4e0a\u8299\u8389\u5a05\uff1f\u201d\u743c\u6069\u5f53\u771f\u6014\u4f4f\u4e86\uff0c\u201c\u5979\u5728\u5f00\u4ec0\u4e48\u73a9\u7b11\u2026\u2026\u54e6\uff0c\u6211\u77e5\u9053\u4e86\u3002\u201d</p><p>\u4e0a\u6b21\u8299\u857e\u72c4\u66fe\u7ecf\u8bf4\u8fc7\uff0c\u8299\u8389\u5a05\u7684\u5a5a\u671f\u5c06\u8fd1\uff0c\u4f46\u5979\u672c\u4eba\u5374\u53c8\u4e0d\u60c5\u613f\u3002\u53ea\u6015\u662f\u8981\u501f\u7740\u8fd9\u6b21\u51fa\u5f81\u5e7d\u6697\u5730\u57df\u7684\u673a\u4f1a\uff0c\u987a\u7406\u6210\u7ae0\u5730\u9003\u5a5a\u5427\u3002</p><p>\u201c\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u73ca\u5609\u95ee\u3002</p><p>\u743c\u6069\u5c06\u81ea\u5df1\u7684\u731c\u6d4b\u5927\u81f4\u8bf4\u4e86\uff0c\u201c\u59d0\u59d0\uff0c\u4f60\u5e2e\u6211\u56de\u7edd\u5979\u5427\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u8fd9\u4e2a\u5fd9\u6211\u6050\u6015\u5e2e\u4e0d\u4e86\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\u554a\uff0c\u201d\u73ca\u5609\u76b1\u7709\uff0c\u201c\u6211\u89c9\u5f97\u8fd9\u4e5f\u4e0d\u7b97\u4ec0\u4e48\u96be\u4e8b\u3002\u201d</p><p>\u743c\u6069\u6447\u5934\uff0c\u201c\u59d0\u59d0\uff0c\u4f60\u8981\u77e5\u9053\uff0c\u6211\u5982\u679c\u5e26\u4e0a\u5979\uff0c\u4f1a\u6709\u5f88\u591a\u9ebb\u70e6\u7684\u3002\u201d</p><p>\u9093\u80af\u5bb6\u65cf\u90a3\u8fb9\u5c31\u4e14\u4e0d\u8bf4\uff0c\u66f4\u5173\u952e\u5728\u4e8e\uff0c\u8299\u8389\u5a05\u662f\u4e2a\u7267\u5e08\u3002\u743c\u6069\u65e2\u7136\u5df2\u7ecf\u6e05\u695a\u9634\u9b42\u57ce\u5185\u90e8\u7684\u6d3e\u7cfb\u6597\u4e89\uff0c\u4ed6\u672c\u4eba\u53c8\u548c\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u8d70\u5f97\u8fd1\uff0c\u4e0d\u7ba1\u81ea\u5df1\u613f\u4e0d\u613f\u610f\uff0c\u90fd\u663e\u7136\u5df2\u7ecf\u88ab\u7b97\u4f5c\u662f\u5deb\u5e08\u6d3e\u7cfb\u7684\u4eba\u5458\u3002\u6240\u8c13\u5e8a\u53ef\u4ee5\u4e71\u4e0a\uff0c\u94b1\u53ef\u4ee5\u4e71\u6536\uff0c\u961f\u4f0d\u4e07\u4e07\u4e0d\u80fd\u7ad9\u9519\uff0c\u8fd9\u79cd\u6d3e\u7cfb\u6597\u4e89\uff0c\u81ea\u5df1\u8fd8\u662f\u5c3d\u53ef\u80fd\u4e0d\u8981\u5377\u8fdb\u53bb\u7684\u4e3a\u597d\uff0c\u660e\u54f2\u4fdd\u8eab\u5c31\u662f\uff0c\u518d\u8bf4\uff0c\u8fd9\u79cd\u4e8b\u60c5\u5bf9\u81ea\u5df1\u53c8\u6ca1\u6709\u534a\u70b9\u597d\u5904\u3002</p><p>\u201c\u4f46\u6211\u542c\u8bf4\u5979\u662f\u4e2a\u5f88\u4e0d\u9519\u7684\u7267\u5e08\u5462\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u867d\u7136\u597d\u50cf\u8fd8\u5728\u89c1\u4e60\u9636\u6bb5\uff0c\u4f46\u6bd4\u8d77\u5f88\u591a\u6b63\u5f0f\u7684\u7267\u5e08\u90fd\u4e0d\u900a\u8272\u3002\u4f60\u8fd9\u6b21\u53bb\u5e7d\u6697\u5730\u57df\uff0c\u961f\u4f0d\u91cc\u603b\u662f\u9700\u8981\u6709\u4e2a\u7267\u5e08\u7684\u3002\u201d</p><p>\u743c\u6069\u5fae\u5fae\u4e00\u6014\uff0c\u73ca\u5609\u8fd9\u53e5\u8bdd\u5012\u662f\u8bf4\u5f97\u6ca1\u9519\uff0c\u8fd9\u6b21\u4e0d\u662f\u53bb\u90ca\u6e38\uff0c\u5371\u9669\u514d\u4e0d\u4e86\uff0c\u961f\u4f0d\u91cc\u603b\u8be5\u662f\u8981\u6709\u4e2a\u7267\u5e08\u7684\uff0c\u5426\u5219\u4f1a\u5f88\u9ebb\u70e6\u3002\u867d\u7136\u81ea\u5df1\u7279\u6b8a\uff0c\u4e0d\u80fd\u76f4\u63a5\u63a5\u53d7\u795e\u672f\u7684\u6cbb\u7597\uff0c\u4f46\u961f\u4f0d\u91cc\u7684\u5176\u4ed6\u4eba\u5374\u662f\u9700\u8981\u7684\u3002\u539f\u672c\u6885\u83f2\u65af\u8fd9\u4e2a\u5723\u6b66\u58eb\u4e5f\u53ef\u4ee5\u52c9\u5f3a\u627f\u62c5\u7267\u5e08\u7684\u5de5\u4f5c\uff0c\u7136\u800c\u5979\u662f\u5df4\u5c14\u5b50\u55e3\uff0c\u65e0\u6cd5\u4f7f\u7528\u4efb\u4f55\u795e\u672f\u3002</p><p>\u8ba4\u8bc6\u7684\u4eba\u4e2d\u95f4\uff0c\u6709\u7267\u5e08\u53ef\u4ee5\u62c9\u6765\u5e2e\u5fd9\u4e48\uff1f\u743c\u6069\u60f3\u6765\u60f3\u53bb\uff0c\u53d1\u73b0\u81ea\u5df1\u867d\u7136\u66fe\u7ecf\u8ba4\u8bc6\u51e0\u4e2a\u7267\u5e08\uff0c\u7136\u800c\u4ed6\u4eec\u5168\u90fd\u65e0\u4e00\u4f8b\u5916\u5730\u6b7b\u4e86\uff0c\u6709\u7684\u662f\u88ab\u4eba\u9762\u72ee\u6740\u4e86\uff0c\u6709\u7684\u88ab\u6b66\u50e7\u9686\u5947\u6740\u4e86\uff0c\u8fd8\u6709\u7684\u662f\u88ab\u6885\u83f2\u65af\u6740\u4e86\u3002</p><p>\u4ed6\u6c89\u541f\u7740\uff0c\u6709\u4e9b\u72b9\u8c6b\u8d77\u6765\uff0c\u7136\u540e\u542c\u89c1\u73ca\u5609\u8bf4\u4e86\u53e5\u8bdd\u3002</p><p>\u201c\u5c0f\u5f1f\uff0c\u6211\u89c9\u5f97\uff0c\u4f60\u597d\u50cf\u592a\u5c0f\u5fc3\u4e86\u4e00\u4e9b\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u4ece\u5c0f\u5c31\u8fd9\u6837\uff0c\u4eff\u4f5b\u603b\u5728\u62c5\u5fe7\u7740\u4ec0\u4e48\uff0c\u603b\u662f\u5c0f\u5fc3\u7ffc\u7ffc\u7684\u3002\u6211\u89c9\u5f97\u8fd9\u4e5f\u672a\u5fc5\u597d\u5462\uff0c\u592a\u8fc7\u8c28\u614e\uff0c\u4f1a\u628a\u4eba\u7684\u80c6\u6c14\u6d88\u78e8\u6389\u7684\u3002\u201d</p><p>\u743c\u6069\u9ed8\u7136\u7247\u523b\uff0c\u201c\u6211\u660e\u5929\u53bb\u89c1\u89c1\u8299\u857e\u72c4\u518d\u8bf4\u5427\u3002\u201d</p><p>\u201c\u4f60\u7b54\u5e94\u4e86\uff1f\u201d</p><p>\u201c\u770b\u60c5\u51b5\u518d\u8bf4\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4e0d\u8fc7\u59d0\u59d0\u521a\u624d\u8bf4\u5f97\u5bf9\u3002\u65e2\u7136\u65e0\u8bba\u6211\u600e\u4e48\u8c28\u5c0f\u614e\u5fae\uff0c\u600e\u4e48\u5c0f\u5fc3\u7ffc\u7ffc\uff0c\u7ed3\u679c\u90fd\u4f9d\u7136\u8fd8\u662f\u5728\u5c40\u4e2d\u4e0d\u80fd\u6446\u8131\u3002\u90a3\u4e48\u2026\u2026\u4e0d\u59a8\u5c31\u8ba9\u6211\u6765\u770b\u770b\uff0c\u5230\u5e95\u8fd9\u5c40\u68cb\u7684\u5e95\u7ebf\u5728\u54ea\u91cc\u5427\u3002\u201d</p></article>"}}}]);
