(window.webpackJsonp=window.webpackJsonp||[]).push([[230],{252:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e00\u8282 \u5353\u5c14\u7684\u9b54\u6cd5\u57ce\u5e02",content:"<article><h2>\u7b2c\u4e00\u8282 \u5353\u5c14\u7684\u9b54\u6cd5\u57ce\u5e02</h2><p>\u9ed1\u6697\u7684\u5730\u4e0b\u752c\u9053\u4e2d\u6b7b\u4e00\u822c\u7684\u5bc2\u9759\uff0c\u53ea\u6709\u8f7b\u5fae\u7684\u811a\u6b65\u58f0\u9690\u7ea6\u56de\u54cd\uff0c\u743c\u6069\u8c28\u614e\u5730\u89c2\u5bdf\u7740\u56db\u5468\uff0c\u786e\u8ba4\u6240\u6709\u7684\u961f\u5458\u90fd\u5728\u5404\u81ea\u7684\u4f4d\u7f6e\u4e0a\uff0c\u7136\u540e\u4ed6\u5fae\u5fae\u677e\u4e86\u53e3\u6c14\uff0c\u987a\u4fbf\u5c06\u8eab\u4e0a\u7684\u7075\u5316\u6597\u7bf7\u88f9\u5f97\u66f4\u7d27\u4e86\u4e00\u4e9b\u3002</p><p>\u6b64\u5904\u8ddd\u79bb\u5730\u8868\u81f3\u5c11\u5df2\u7ecf\u8d85\u8fc7\u4e8c\u5341\u82f1\u91cc\uff0c\u7a7a\u6c14\u9634\u51b7\u800c\u6f6e\u6e7f\uff0c\u5e26\u7740\u523a\u9aa8\u7684\u51b0\u5bd2\uff0c\u5373\u4f7f\u743c\u6069\u66fe\u7ecf\u5728\u5e7d\u5f71\u754c\u90a3\u79cd\u6697\u65e0\u5929\u65e5\u7684\u5730\u65b9\u751f\u6d3b\u4e86\u6574\u6574\u5341\u4e94\u5e74\uff0c\u4ed6\u4f9d\u65e7\u8fd8\u662f\u611f\u5230\u6709\u4e9b\u4e0d\u80fd\u9002\u5e94\u3002\u4e8b\u5b9e\u4e0a\uff0c\u9634\u9b42\u57ce\u7684\u540e\u52e4\u90e8\u95e8\u4f53\u8d34\u5730\u8003\u8651\u5230\u4e86\u8fd9\u4e2a\u95ee\u9898\uff0c\u4e3a\u4e86\u9632\u6b62\u5173\u8282\u9178\u75db\uff0c\u7b2c\u4e8c\u8fdc\u5f81\u5e08\u914d\u53d1\u7684\u6240\u6709\u6597\u7bf7\u2014\u2014\u65e0\u8bba\u662f\u743c\u6069\u8fd9\u79cd\u519b\u5b98\u8eab\u4e0a\u7684\u7075\u5316\u6597\u7bf7\u8fd8\u662f\u666e\u901a\u58eb\u5175\u7684\u9ed1\u6697\u6597\u7bf7\u2014\u2014\u90fd\u704c\u6ce8\u4e86\u62b5\u6297\u6e7f\u6c14\u7684\u9b54\u6cd5\uff0c\u4f46\u5374\u5fd8\u4e86\u518d\u9644\u4e0a\u4fdd\u6696\u6548\u679c\uff0c\u6216\u8bb8\u662f\u89c9\u5f97\u5bd2\u6c14\u6709\u52a9\u4e8e\u4fdd\u6301\u5934\u8111\u6e05\u9192\u3002</p><p>\u743c\u6069\u6709\u4e9b\u540e\u6094\u6ca1\u6709\u9884\u5148\u51c6\u5907\u4e00\u679a\u6e29\u6696\u6212\u6307\uff0c\u90a3\u5e76\u4e0d\u662f\u4ec0\u4e48\u73cd\u7a00\u6602\u8d35\u7684\u9b54\u6cd5\u7269\u54c1\uff0c\u51ed\u501f\u4ed6\u7684\u804c\u6743\uff0c\u53ef\u4ee5\u5f88\u5bb9\u6613\u4ece\u70bc\u91d1\u5b66\u9662\u91cc\u83b7\u5f97\u3002</p><p>\u6885\u83f2\u65af\u548c\u83ab\u5c3c\u5361\u59d0\u59b9\u4fe9\u663e\u7136\u4e5f\u548c\u743c\u6069\u6709\u76f8\u540c\u7684\u611f\u53d7\uff0c\u80fd\u591f\u5b8c\u5168\u4e0d\u53d7\u5f71\u54cd\u7684\u5c31\u53ea\u6709\u8fea\u74e6\u514b\u548c\u6765\u81ea\u4f0a\u5361\u6c99\u57ce\u7684\u77ee\u4eba\u9c81\u6587\xb7\u77f3\u80a9\uff0c\u4ed6\u4eec\u539f\u672c\u5c31\u662f\u8fd9\u5e7d\u6697\u5730\u57df\u7684\u5c45\u6c11\u3002\u81f3\u4e8e\u8fd9\u652f\u961f\u4f0d\u7684\u6700\u540e\u4e00\u540d\u6210\u5458\u6b27\u51ef\uff0c\u4ed6\u6bcf\u6b21\u53ea\u5728\u51fa\u53d1\u65f6\u9732\u9762\uff0c\u5728\u4f11\u606f\u65f6\u51fa\u73b0\uff0c\u5176\u4f59\u65f6\u95f4\u90fd\u4e0d\u89c1\u8e2a\u5f71\uff0c\u4e0d\u77e5\u9053\u80cc\u5730\u91cc\u5e72\u4ec0\u4e48\u9b3c\u795f\u52fe\u5f53\u53bb\u4e86\uff0c\u743c\u6069\u4e5f\u61d2\u4e8e\u7406\u776c\u3002</p><p>\u201c\u6211\u4eec\u8ddd\u79bb\u6c99\u739b\u65af\u57ce\u8fd8\u6709\u591a\u8fdc\uff1f\u201d\u743c\u6069\u8f7b\u58f0\u95ee\u3002</p><p>\u201c\u6309\u7167\u5730\u56fe\u663e\u793a\uff0c\u8fd8\u6709\u4e09\u5230\u56db\u4e2a\u5c0f\u65f6\u7684\u8def\u7a0b\uff0c\u201d\u4ed6\u8eab\u540e\u7684\u8299\u857e\u72c4\u7acb\u523b\u56de\u7b54\uff0c\u4f46\u968f\u5373\u53c8\u8865\u5145\u4e86\u4e00\u53e5\uff0c\u201c\u5982\u679c\u4e0d\u51fa\u73b0\u4efb\u4f55\u610f\u5916\u7684\u8bdd\u3002\u201d</p><p>\u663e\u7136\u8fd9\u662f\u5962\u671b\u3002</p><p>\u4e94\u5929\u4e4b\u524d\uff0c\u4ed6\u4eec\u4ece\u5965\u56fe\u57ce\u51fa\u53d1\uff0c\u76ee\u6807\u662f\u9065\u8fdc\u7684\u4f0a\u5361\u6c99\u57ce\u3002\u6839\u636e\u9c81\u6587\u7684\u8bf4\u6cd5\uff0c\u4ed6\u6765\u65f6\u5728\u8def\u4e0a\u82b1\u8d39\u7684\u65f6\u95f4\u8d85\u8fc7\u4e86\u4e09\u4e2a\u6708\uff0c\u8fd9\u8ba9\u743c\u6069\u9887\u4e3a\u76b1\u7709\u3002\u4e00\u60f3\u5230\u8981\u5728\u8fd9\u4e9b\u9634\u51b7\u5e7d\u6697\u7684\u6d1e\u7a9f\u96a7\u9053\u6d6a\u8d39\u6389\u4e00\u5e74\u7684\u56db\u5206\u4e4b\u4e00\uff0c\u5e76\u4e14\u5ea6\u8fc7\u81ea\u5df1\u7684\u751f\u65e5\uff0c\u4ed6\u5c31\u6709\u4e9b\u90c1\u90c1\u4e0d\u4e50\uff0c\u4e0d\u8fc7\u6709\u4ec0\u4e48\u529e\u6cd5\u5462\u3002</p><p>\u552f\u4e00\u80fd\u4e3a\u8fd9\u7b80\u5355\u4e4f\u5473\u7684\u65c5\u7a0b\u589e\u52a0\u523a\u6fc0\u7684\uff0c\u5c31\u662f\u5e7d\u6697\u5730\u57df\u91cc\u5c42\u51fa\u4e0d\u7a77\u7684\u602a\u7269\u4eec\u3002\u8ba1\u7b97\u8d77\u6765\uff0c\u4ed6\u4eec\u8fd9\u4e00\u8def\u4e0a\u5e73\u5747\u6bcf\u4e24\u5c0f\u65f6\u5c31\u8981\u906d\u9047\u4e00\u573a\u6218\u6597\uff0c\u6709\u65f6\u5019\u9891\u7387\u8fd8\u4f1a\u66f4\u9ad8\u3002\u5c31\u5728\u534a\u5c0f\u65f6\u524d\uff0c\u4ed6\u4eec\u521a\u521a\u89e3\u51b3\u4e86\u4e24\u53ea\u6050\u722a\u602a\uff0c\u8fd9\u662f\u79cd\u72e1\u733e\u800c\u6781\u5177\u9886\u5730\u610f\u8bc6\u7684\u602a\u7269\uff0c\u5bf9\u6240\u6709\u201c\u95ef\u5165\u8005\u201d\u90fd\u4f1a\u53d1\u8d77\u6beb\u4e0d\u7559\u60c5\u7684\u653b\u51fb\uff0c\u5b83\u4eec\u5934\u50cf\u79c3\u9e6b\uff0c\u722a\u5229\u5982\u94a9\uff0c\u7532\u866b\u4f3c\u7684\u5e9e\u5927\u8eab\u8eaf\u4e0a\u8986\u76d6\u7740\u539a\u91cd\u7684\u5916\u58f3\uff0c\u4eff\u4f5b\u5ca9\u77f3\u4e00\u822c\u575a\u786c\u2014\u2014\u4e0d\u8fc7\u8fd8\u4e0d\u8db3\u4ee5\u62b5\u6321\u6885\u83f2\u65af\u7684\u94f6\u5251\uff0c\u66f4\u4e0d\u80fd\u62b5\u5fa1\u9b54\u6cd5\u3002</p><p>\u5bf9\u4e8e\u743c\u6069\u548c\u4ed6\u7684\u961f\u5458\u4eec\u6765\u8bf4\uff0c\u8fd9\u79cd\u5c0f\u89c4\u6a21\u906d\u9047\u6218\u5e76\u6ca1\u6709\u4ec0\u4e48\u5371\u9669\uff0c\u53cd\u800c\u662f\u719f\u6089\u73af\u5883\u3001\u953b\u70bc\u6280\u827a\uff0c\u751a\u81f3\u6253\u53d1\u65f6\u95f4\u7684\u826f\u597d\u6d88\u9063\u65b9\u5f0f\u3002\u743c\u6069\u81ea\u5df1\u662f\u4e00\u4e2a\u4f18\u79c0\u7684\u5deb\u5e08\uff0c\u5c31\u7b97\u7528\u9634\u9b42\u57ce\u7684\u6807\u51c6\u6765\u8861\u91cf\u4e5f\u5df2\u7ecf\u7b97\u662f\u51fa\u8272\uff0c\u6885\u83f2\u65af\u5219\u662f\u7b2c\u4e00\u6d41\u7684\u5251\u624b\u3002\u8fea\u74e6\u514b\u5728\u585e\u5c14\u7684\u7ade\u6280\u573a\u5185\u8868\u73b0\u5e73\u5e73\uff0c\u4f46\u5728\u8fd9\u79cd\u5730\u5e95\u65c5\u884c\u4e2d\u5374\u5982\u9c7c\u5f97\u6c34\u3002\u83ab\u5c3c\u5361\u59d0\u59b9\u4e5f\u80fd\u591f\u5f88\u597d\u5730\u4fdd\u62a4\u81ea\u5df1\uff0c\u867d\u7136\u9700\u8981\u501f\u52a9\u4e00\u4e9b\u5bb6\u4f20\u7684\u9b54\u6cd5\u7269\u54c1\u3002\u5c31\u8fde\u4f5c\u4e3a\u6444\u653f\u8bae\u5458\u7684\u77ee\u4eba\u9c81\u6587\u90fd\u662f\u4e2a\u6280\u827a\u9ad8\u8d85\u7684\u6218\u58eb\u2014\u2014\u4e0d\u8fc7\u8fd9\u5e76\u4e0d\u51fa\u4e4e\u743c\u6069\u7684\u610f\u6599\uff0c\u5426\u5219\u4ed6\u600e\u4e48\u53ef\u80fd\u4e00\u8def\u6d3b\u4e0b\u6765\u3002</p><p>\u771f\u6b63\u7684\u9ebb\u70e6\uff0c\u5176\u5b9e\u662f\u8ff7\u8def\u3002</p><p>\u5728\u5e7d\u6697\u5730\u57df\u4e2d\u65c5\u884c\uff0c\u548c\u5730\u8868\u4e16\u754c\u5b8c\u5168\u4e0d\u540c\uff0c\u6ca1\u6709\u4efb\u4f55\u660e\u663e\u7684\u6807\u5fd7\u548c\u5f00\u9614\u7684\u89c6\u91ce\uff0c\u53ea\u6709\u56db\u901a\u516b\u8fbe\u4eff\u4f5b\u86db\u7f51\u72b6\u7684\u96a7\u9053\uff0c\u4ee5\u53ca\u65e0\u5904\u4e0d\u5728\u7684\u9ed1\u6697\u3002\u5c31\u7b97\u662f\u6700\u6709\u7ecf\u9a8c\u7684\u65c5\u4eba\uff0c\u62ff\u7740\u8be6\u7ec6\u7684\u5730\u56fe\uff0c\u5728\u8fd9\u79cd\u5730\u65b9\u4e5f\u5f88\u96be\u8fa8\u8ba4\u65b9\u4f4d\uff0c\u800c\u56e0\u4e3a\u5730\u8109\u8f90\u5c04\u7684\u5f71\u54cd\u5e72\u6270\uff0c\u6307\u5357\u9488\u6839\u672c\u6ca1\u6cd5\u4f7f\u7528\u3002\u8fea\u74e6\u514b\u548c\u9c81\u6587\u867d\u7136\u90fd\u662f\u539f\u5c45\u6c11\uff0c\u4f46\u4ed6\u4eec\u719f\u6089\u7684\u53ea\u662f\u81ea\u5df1\u5bb6\u56ed\u9644\u8fd1\uff0c\u800c\u65e0\u8bba\u662f\u5e03\u7075\u767b\u77f3\u57ce\u8fd8\u662f\u4f0a\u5361\u6c99\u57ce\uff0c\u8ddd\u79bb\u6b64\u5904\u90fd\u592a\u9065\u8fdc\u4e86\u3002</p><p>\u201c\u53ef\u662f\uff0c\u9c81\u6587\u5148\u751f\uff0c\u201d\u743c\u6069\u4e0d\u89e3\uff0c\u201c\u4f60\u65e2\u7136\u80fd\u4ece\u4f0a\u5361\u6c99\u57ce\u5230\u5965\u56fe\u57ce\uff0c\u90a3\u4e48\u5e26\u6211\u4eec\u539f\u8def\u8fd4\u56de\u4fbf\u662f\u4e86\u3002\u201d</p><p>\u201c\u4f60\u786e\u5b9a\u771f\u8981\u8fd9\u4e48\u529e\uff1f\u201d\u77ee\u4eba\u62e7\u7740\u7c97\u77ed\u7684\u7709\u6bdb\uff0c\u201c\u9996\u5148\u6211\u4e5f\u4e0d\u80fd\u6e05\u695a\u8bb0\u5f97\u8d70\u8fc7\u7684\u8def\uff1b\u5176\u6b21\u6211\u6765\u65f6\u53ef\u662f\u7ed5\u4e86\u5f88\u5927\u4e00\u4e2a\u5f2f\u8def\u2026\u2026\u201d</p><p>\u201c\u2026\u2026\u4e0d\u5fc5\u4e86\u3002\u201d</p><p>\u6700\u7ec8\u89e3\u51b3\u8fd9\u4e2a\u95ee\u9898\u7684\u662f\u8299\u857e\u72c4\uff0c\u5979\u8fd1\u6765\u8fdb\u6b65\u975e\u5e38\u5feb\uff0c\u4eff\u4f5b\u50cf\u662f\u8981\u628a\u8fd9\u5c0f\u534a\u5e74\u8f8d\u5b66\u803d\u8bef\u7684\u8bfe\u4e1a\u5168\u90e8\u5f25\u8865\u56de\u6765\u4f3c\u7684\u3002\u83ab\u5c3c\u5361\u5bb6\u65cf\u7684\u5929\u8d4b\u8840\u8109\uff0c\u539f\u672c\u5c31\u4ee5\u9884\u8a00\u672f\u7740\u79f0\uff0c\u743c\u6069\u53c8\u5c06\u90a3\u4efd\u201c\u6df1\u571f\u9884\u8a00\u201d\u9b54\u6cd5\u7b14\u8bb0\u7ed9\u4e86\u5979\uff0c\u8299\u857e\u72c4\u73b0\u5728\u5df2\u7ecf\u80fd\u591f\u6e05\u695a\u5730\u5728\u5e7d\u6697\u5730\u57df\u4e2d\u611f\u5e94\u65b9\u4f4d\uff0c\u5224\u65ad\u8ddd\u79bb\uff0c\u5c06\u5730\u56fe\u4e0a\u7684\u6807\u8bb0\u548c\u73b0\u5b9e\u73af\u5883\u7cbe\u786e\u5bf9\u5e94\uff0c\u4e3a\u540c\u4f34\u6307\u793a\u524d\u8fdb\u7684\u76ee\u6807\u3002\u6709\u610f\u65e0\u610f\u4e4b\u95f4\uff0c\u5979\u6210\u4e3a\u4e86\u8fd9\u652f\u961f\u4f0d\u7684\u5411\u5bfc\u3002</p><p>\u5c11\u5973\u4f3c\u4e4e\u5f88\u6ee1\u610f\u4e8e\u81ea\u5df1\u65b0\u7684\u89d2\u8272\u5b9a\u4f4d\u3002</p><p>\u201c\u6211\u73b0\u5728\u751a\u81f3\u90fd\u80fd\u591f\u611f\u53d7\u5230\u5927\u5730\u529b\u91cf\u7684\u8109\u7edc\u548c\u5954\u6d8c\u6c47\u805a\uff0c\u201d\u5728\u6628\u665a\u5bbf\u8425\u4f11\u606f\u7684\u65f6\u5019\uff0c\u8299\u857e\u72c4\u5174\u81f4\u52c3\u52c3\u5730\u5bf9\u743c\u6069\u8bf4\uff0c\u201c\u6309\u7167\u8fd9\u4efd\u7b14\u8bb0\u4e0a\u8bf4\uff0c\u518d\u8fdb\u4e00\u6b65\u63d0\u5347\u7684\u8bdd\uff0c\u5e94\u8be5\u5c31\u53ef\u4ee5\u548c\u5ca9\u77f3\u4ea4\u6d41\u4e86\u3002\u201d</p><p>\u201c\u6df1\u571f\u9884\u8a00\u662f\u975e\u5e38\u6709\u4ef7\u503c\u7684\u9b54\u6cd5\u6280\u827a\uff0c\u201d\u743c\u6069\u5c0f\u5fc3\u5730\u9009\u62e9\u7740\u63aa\u8f9e\uff0c\u201c\u4f46\u5b83\u4e0d\u5177\u5907\u4efb\u4f55\u6218\u6597\u529f\u7528\uff0c\u5982\u679c\u8fc7\u5206\u4e13\u6ce8\u4e8e\u6b64\u7684\u8bdd\uff0c\u4f60\u7684\u529b\u91cf\u5c06\u5f88\u96be\u4e0a\u5347\u3002\u201d</p><p>\u201c\u55ef\uff0c\u6211\u77e5\u9053\uff0c\u4f46\u8fd9\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\u5462\uff1f\u201d\u8299\u857e\u72c4\u8bf4\uff0c\u201c\u6211\u53c8\u4e0d\u559c\u6b22\u548c\u4eba\u4e89\u6597\u3002\u800c\u4e14\u9b54\u6cd5\u662f\u7cbe\u5999\u7684\u6280\u827a\uff0c\u539f\u672c\u5c31\u4e0d\u5e94\u8be5\u662f\u7528\u6765\u6740\u4eba\u7684\u5de5\u5177\uff0c\u8fd9\u662f\u4f60\u4ee5\u524d\u5bf9\u6211\u8bf4\u8fc7\u7684\u554a\u3002\u201d</p><p>\u743c\u6069\u6447\u6447\u5934\u3002\u201c\u90a3\u65f6\u5019\u6211\u8fd8\u4e0d\u61c2\u4e8b\u3002\u201d\u4ed6\u672c\u60f3\u8fd9\u4e48\u8bf4\uff0c\u4f46\u968f\u5373\u53d1\u89c9\u4e5f\u4e0d\u8fc7\u5c31\u662f\u534a\u5e74\u524d\u7684\u4e8b\u60c5\uff0c\u4e8e\u662f\u653e\u5f03\u4e86\u3002</p><p>\u201c\u8fd9\u662f\u4e00\u4e2a\u5371\u9669\u7684\u4e16\u754c\u3002\u201d\u4ed6\u6700\u540e\u8bf4\u3002</p><p>\u8299\u857e\u72c4\u9759\u9759\u770b\u7740\u4ed6\uff0c\u201c\u4f46\u6211\u4e0d\u60f3\u8ba9\u4f60\u89c9\u5f97\u6211\u5371\u9669\u3002\u201d</p><p>\u743c\u6069\u4e8e\u662f\u6c89\u9ed8\u3002</p><p>\u5230\u5e95\u662f\u4ec0\u4e48\uff0c\u8ba9\u66fe\u7ecf\u4eb2\u5bc6\u7684\u60c5\u4eba\uff0c\u90fd\u9700\u8981\u7528\u8fd9\u79cd\u5c0f\u5fc3\u7ffc\u7ffc\u7684\u65b9\u5f0f\u6765\u6073\u6c42\u5f7c\u6b64\u7684\u4fe1\u4efb\u5462\u3002</p><p>\u957f\u888d\u4e0a\u7684\u80f8\u9488\u7a81\u7136\u98a4\u52a8\u8d77\u6765\uff0c\u6253\u65ad\u4e86\u743c\u6069\u7684\u601d\u7eea\uff0c\u8fd9\u662f\u793a\u8b66\u7684\u4fe1\u53f7\uff0c\u610f\u5473\u7740\u67d0\u79cd\u5371\u9669\u7684\u751f\u7269\u6b63\u5728\u903c\u8fd1\u3002\u743c\u6069\u770b\u7740\u81ea\u5df1\u7684\u961f\u5458\uff0c\u4ed6\u4eec\u540c\u6837\u4e5f\u5bdf\u89c9\u5230\u4e86\uff0c\u5728\u7b2c\u4e00\u65f6\u95f4\u505a\u51fa\u4e86\u53cd\u5e94\u3002\u6885\u83f2\u65af\u88f9\u7d27\u8eab\u4e0a\u7684\u6597\u7bf7\uff0c\u5c06\u81ea\u5df1\u9690\u6ca1\u5728\u4e00\u56e2\u9b54\u6cd5\u9ed1\u6697\u4e4b\u4e2d\uff0c\u968f\u5373\u542f\u52a8\u4e86\u6d6e\u7a7a\u9774\uff0c\u5f80\u4e0a\u6f02\u6d6e\uff1b\u8fea\u74e6\u514b\u548c\u9c81\u6587\u7d27\u8d34\u7740\u5899\u58c1\uff0c\u8eb2\u5728\u7a81\u8d77\u7684\u5ca9\u77f3\u4e4b\u540e\uff1b\u83ab\u5c3c\u5361\u59d0\u59b9\u5219\u540c\u65f6\u6fc0\u6d3b\u4e86\u523b\u6709\u5bb6\u5fbd\u7684\u94f6\u6212\u6307\uff0c\u5239\u90a3\u95f4\u4ece\u7a7a\u6c14\u4e2d\u5b8c\u5168\u6d88\u5931\u3002</p><p>\u743c\u6069\u540e\u9000\uff0c\u80cc\u8d34\u4e0a\u51b0\u51b7\u7684\u77f3\u58c1\uff0c\u7136\u540e\u201c\u9677\u201d\u8fdb\u53bb\uff0c\u6d88\u5931\u4e0d\u89c1\u3002\u8fd9\u662f\u4ed6\u811a\u4e0a\u7684\u7d22\u5c14\u77f3\u884c\u602a\u4e4b\u9774\u7684\u6548\u7528\uff0c\u80fd\u591f\u8ba9\u7a7f\u6234\u8005\u6682\u65f6\u83b7\u5f97\u7d22\u5c14\u602a\u7684\u77f3\u884c\u80fd\u529b\uff0c\u5f53\u7136\u4e5f\u6709\u65f6\u95f4\u9650\u5236\uff0c\u6bcf\u5929\u4e09\u6b21\uff0c\u6bcf\u6b21\u5341\u5206\u949f\u3002\u548c\u7075\u5316\u6597\u7bf7\u4e00\u6837\uff0c\u8fd9\u90fd\u662f\u519b\u5b98\u624d\u6709\u7684\u5f85\u9047\uff0c\u9634\u9b42\u57ce\u7684\u4e25\u5bc6\u7b49\u7ea7\u5236\u5ea6\u662f\u65e0\u6240\u4e0d\u5728\u7684\u3002</p><p>\u5468\u56f4\u4e00\u7247\u5bc2\u9759\uff0c\u5927\u7ea6\u516d\u5230\u4e03\u79d2\u949f\u4e4b\u540e\uff0c\u524d\u65b9\u4f20\u6765\u6c89\u91cd\u800c\u6e05\u6670\u7684\u811a\u6b65\u58f0\uff0c\u50cf\u662f\u67d0\u79cd\u5e9e\u5927\u7684\u722c\u884c\u602a\u7269\u6b63\u5728\u671d\u8fd9\u91cc\u79fb\u52a8\uff0c\u5b83\u7684\u901f\u5ea6\u4e0d\u5feb\uff0c\u4f46\u4f53\u91cd\u663e\u7136\u60ca\u4eba\uff0c\u611f\u89c9\u9644\u8fd1\u7684\u5ca9\u77f3\u571f\u5c42\u90fd\u88ab\u9707\u5f97\u8f7b\u5fae\u6447\u6643\u3002\u743c\u6069\u5c3d\u53ef\u80fd\u653e\u7f13\u547c\u5438\uff0c\u7b49\u5f85\u7740\uff0c\u7136\u540e\u770b\u89c1\u4e00\u4e2a\u5706\u6eda\u6eda\u7684\u9ed1\u5f71\u5728\u89c6\u91ce\u4e2d\u51fa\u73b0\u3002</p><p>\u5b83\u770b\u8d77\u6765\u50cf\u662f\u4e00\u53ea\u5de8\u5927\u7684\u9ed1\u8272\u86c6\u866b\uff0c\u4ece\u5934\u5230\u5c3e\u5927\u7ea6\u6709\u5341\u82f1\u5c3a\u957f\uff0c\u5374\u6709\u7740\u516d\u53ea\u7c97\u77ed\u7684\u722a\u5b50\uff0c\u4ee5\u53ca\u4e09\u89d2\u5f62\u7684\u5c16\u5c16\u8111\u888b\uff0c\u5634\u5df4\u90e8\u4f4d\u662f\u4e00\u6839\u7a81\u51fa\u7684\u7ec6\u957f\u7ba1\u5b50\uff0c\u6ca1\u6709\u773c\u775b\u3002\u743c\u6069\u7acb\u523b\u8fa8\u8ba4\u51fa\u4e86\u8fd9\u79cd\u602a\u7269\uff1a\u6697\u97f3\u76f2\u602a\uff0c\u7b2c\u4e8c\u8fdc\u5f81\u5e08\u914d\u53d1\u7684\u300a\u5e7d\u6697\u5730\u57df\u602a\u7269\u56fe\u9274\u300b\u91cc\u9762\u66fe\u7ecf\u8be6\u7ec6\u63cf\u8ff0\u8fc7\u3002\u8fd9\u79cd\u602a\u7269\u6ca1\u6709\u89c6\u529b\uff0c\u4f46\u6709\u654f\u9510\u53d1\u8fbe\u7684\u8033\u6735\uff0c\u5b83\u4eec\u7684\u7ba1\u72b6\u7ec6\u5634\u80fd\u591f\u4ea7\u751f\u7cbe\u786e\u63a7\u5236\u7684\u5171\u9e23\uff0c\u53d1\u51fa\u7684\u97f3\u6ce2\u5a01\u529b\u4e4b\u5f3a\u8db3\u4ee5\u9707\u788e\u77f3\u58c1\uff08\u5f53\u7136\u6697\u97f3\u76f2\u602a\u53ef\u4ee5\u81ea\u884c\u8c03\u6574\u7834\u574f\u529b\u5927\u5c0f\uff09\uff0c\u88ab\u5217\u4e3a\u5e7d\u6697\u5730\u57df\u6700\u5371\u9669\u7684\u602a\u7269\u4e4b\u4e00\u3002</p><p>\u4e0d\u8fc7\uff0c\u77e5\u5df1\u77e5\u5f7c\uff0c\u767e\u6218\u4e0d\u6b86\uff0c\u65e2\u7136\u6709\u6e05\u695a\u7684\u8d44\u6599\uff0c\u90a3\u4e48\u5bf9\u4ed8\u8d77\u6765\u4e5f\u5c31\u5bb9\u6613\u5f97\u591a\u4e86\u3002</p><p>\u743c\u6069\u7b49\u5f85\u7740\u5b83\u9760\u8fd1\uff0c\u51c6\u5907\u65bd\u5c55\u4e00\u4e2a\u6cd5\u672f\uff0c\u7136\u800c\u8fd9\u602a\u7269\u7684\u542c\u89c9\u6bd4\u60f3\u50cf\u5f97\u8fd8\u8981\u654f\u9510\uff0c\u5b83\u6216\u8bb8\u5bdf\u89c9\u5230\u4e86\u4ec0\u4e48\uff0c\u7a81\u7136\u505c\u4e86\u4e0b\u6765\uff0c\u7d27\u63a5\u7740\u62ac\u8d77\u5934\uff0c\u7ba1\u72b6\u7ec6\u5634\u5fae\u5fae\u98a4\u52a8\uff0c\u53d1\u51fa\u4e00\u80a1\u8db3\u4ee5\u7a7f\u91d1\u88c2\u77f3\u7684\u97f3\u6ce2\uff0c\u76ee\u6807\u6b63\u662f\u5df2\u7ecf\u9690\u5f62\u7684\u83ab\u5c3c\u5361\u59d0\u59b9\u3002</p><p>\u5b83\u521a\u4e00\u52a8\uff0c\u4f0f\u51fb\u8005\u4e5f\u968f\u5373\u53d1\u52a8\u4e86\uff0c\u65e2\u7136\u53d1\u73b0\u662f\u6697\u97f3\u76f2\u602a\uff0c\u90a3\u4e48\u4e5f\u5c31\u4e0d\u5fc5\u518d\u906e\u853d\u8eab\u5f62\uff0c\u56e0\u4e3a\u5b83\u539f\u672c\u5c31\u6ca1\u6709\u89c6\u89c9\u3002\u9c81\u6587\u4ece\u85cf\u8eab\u5904\u8df3\u4e86\u51fa\u6765\uff0c\u5de6\u624b\u6301\u76fe\u6321\u5728\u83ab\u5c3c\u5361\u59d0\u59b9\u8eab\u524d\uff0c\u6b63\u8fce\u4e0a\u602a\u7269\u53d1\u51fa\u7684\u97f3\u6ce2\u3002\u4e00\u9635\u65e0\u58f0\u7684\u98a4\u52a8\u4e4b\u540e\uff0c\u4ed6\u7684\u76fe\u724c\u788e\u88c2\u6210\u4e86\u51e0\u5341\u5757\uff0c\u80f8\u53e3\u5982\u906d\u91cd\u51fb\uff0c\u8e09\u8dc4\u540e\u9000\u51e0\u6b65\u3002\u5982\u679c\u4ed6\u7a7f\u7684\u8fd8\u662f\u4f0a\u5361\u6c99\u57ce\u5de5\u5320\u5927\u5e08\u953b\u9020\u7684\u94e0\u7532\uff0c\u90a3\u4e48\u5c31\u7b97\u4e0d\u9760\u76fe\u724c\u7f13\u51b2\uff0c\u76f4\u63a5\u786c\u6328\u4e00\u51fb\u4e5f\u4e0d\u4f1a\u6709\u4e8b\uff0c\u7136\u800c\u90a3\u526f\u79d8\u94f6\u94e0\u7532\u65e9\u5c31\u88ab\u5965\u56fe\u57ce\u7684\u773c\u9b54\u4eec\u593a\u8d70\uff0c\u4e0d\u77e5\u4e0b\u843d\u3002\u5982\u4eca\u4e00\u8eab\u88c5\u5907\u90fd\u662f\u9634\u9b42\u57ce\u53cb\u60c5\u8d5e\u52a9\u7684\uff0c\u867d\u7136\u4e5f\u4e0d\u5dee\uff0c\u76f8\u6bd4\u8d77\u6765\u8fd8\u662f\u5dee\u8fdc\u4e86\u3002</p><p>\u201c\u5bc2\uff01\u201d</p><p>\u9c81\u6587\u8fd9\u4e00\u6321\u4e89\u53d6\u5230\u4e86\u65f6\u95f4\uff0c\u9690\u5f62\u72b6\u6001\u7684\u8299\u8389\u5a05\u6210\u529f\u5b8c\u6210\u6cd5\u672f\uff0c\u5979\u7684\u838e\u5c14\u5723\u5fbd\u4e0a\u53d1\u51fa\u4e00\u80a1\u5723\u529b\uff0c\u77ac\u95f4\u62b9\u6d88\u4e86\u5979\u548c\u8299\u857e\u72c4\u5468\u56f4\u533a\u57df\u7684\u6240\u6709\u58f0\u97f3\u3002\u5bf9\u4e8e\u5b8c\u5168\u9760\u542c\u89c9\u6765\u6355\u730e\u7684\u6697\u97f3\u76f2\u602a\u800c\u8a00\uff0c\u5979\u4eec\u5c31\u7b49\u4e8e\u662f\u5f7b\u5e95\u6d88\u5931\u4e86\u3002</p><p>\u6885\u83f2\u65af\u4ece\u7a7a\u4e2d\u8dc3\u4e0b\uff0c\u63d0\u5251\u524d\u51b2\u3002\u602a\u7269\u6602\u8d77\u5934\uff0c\u6b63\u51c6\u5907\u518d\u6b21\u53d1\u51fa\u97f3\u6ce2\uff0c\u743c\u6069\u6070\u5230\u597d\u5904\u5730\u5b8c\u6210\u4e86\u5e7b\u97f3\u672f\uff0c\u8fd9\u662f\u4e2a\u5c0f\u620f\u6cd5\uff0c\u80fd\u591f\u5236\u9020\u4e00\u6bb5\u865a\u5047\u58f0\u97f3\u3002\u5f53\u65e5\u743c\u6069\u521d\u51fa\u9634\u9b42\u57ce\u7684\u65f6\u5019\uff0c\u66fe\u7ecf\u7528\u5b83\u8bf1\u6355\u4e86\u4e00\u53ea\u4eba\u9762\u72ee\u3002\u7eb7\u4e71\u5608\u6742\u7684\u8bf4\u8bdd\u3001\u558a\u53eb\u3001\u5531\u6b4c\u548c\u91d1\u5c5e\u649e\u51fb\u58f0\u4e00\u77ac\u95f4\u6d8c\u5165\u6697\u97f3\u76f2\u602a\u7684\u8033\u4e2d\uff0c\u6df7\u6dc6\u4e86\u5b83\u7684\u542c\u89c9\uff0c\u5b83\u7684\u4e16\u754c\u5239\u90a3\u95f4\u53d8\u5f97\u6a21\u7cca\u52a8\u8361\u8d77\u6765\uff0c\u5b8c\u5168\u65e0\u6cd5\u5b9a\u4f4d\u654c\u4eba\u7684\u6240\u5728\u3002</p><p>\u6885\u83f2\u65af\u6293\u4f4f\u673a\u4f1a\uff0c\u51b2\u5230\u602a\u7269\u8ddf\u524d\uff0c\u4e00\u5251\u5288\u65ad\u4e86\u5b83\u7ec6\u957f\u7684\u5c16\u5634\uff0c\u8fea\u74e6\u514b\u548c\u9c81\u6587\u4e5f\u90fd\u51b2\u4e86\u4e0a\u6765\uff0c\u6325\u821e\u7ec6\u5251\u548c\u6218\u9524\u731b\u70c8\u6253\u51fb\u7740\u3002\u602a\u7269\u4ed3\u7687\u5730\u6323\u624e\u7740\uff0c\u5e9e\u5927\u7684\u8eab\u8eaf\u5de6\u53f3\u626d\u52a8\uff0c\u4f46\u5b83\u592a\u8fc7\u7b28\u62d9\uff0c\u5df2\u7ecf\u65e0\u6cd5\u9003\u8131\uff0c\u53ea\u80fd\u675f\u624b\u5f85\u6bd9\u3002</p><p>\u5f88\u5feb\uff0c\u5b83\u6b7b\u4e86\u3002</p><p>\u5468\u56f4\u518d\u6b21\u9677\u5165\u6c89\u5bc2\uff0c\u80f8\u9488\u4e0a\u7684\u8b66\u6212\u9b54\u6cd5\u6ca1\u6709\u4efb\u4f55\u53cd\u5e94\uff0c\u8bf4\u660e\u9644\u8fd1\u5df2\u7ecf\u6682\u65f6\u5b89\u5168\u3002\u743c\u6069\u4ece\u5ca9\u77f3\u4e2d\u8d70\u51fa\u6765\uff0c\u770b\u5230\u4ed6\u7684\u961f\u5458\u4eec\u4e5f\u9010\u6e10\u73b0\u8eab\uff0c\u653e\u677e\u6212\u5907\u3002\u9c81\u6587\u7684\u80f8\u90e8\u53d7\u4e86\u649e\u51fb\uff0c\u94e0\u7532\u6709\u4e9b\u53d8\u5f62\u51f9\u9677\uff0c\u4f46\u6ca1\u6709\u771f\u6b63\u53d7\u4f24\uff0c\u4ed6\u7684\u76fe\u724c\u6bc1\u635f\u4e86\uff0c\u597d\u5728\u9a6c\u4e0a\u5c31\u8981\u62b5\u8fbe\u6c99\u739b\u65af\u57ce\uff0c\u5230\u65f6\u5019\u53ef\u4ee5\u53e6\u6362\u4e00\u5757\uff0c\u5176\u4ed6\u4eba\u90fd\u6ca1\u6709\u53d7\u4f24\u3002</p><p>\u51e0\u53ea\u5ca9\u9f20\u4e0d\u77e5\u4ece\u4ec0\u4e48\u5730\u65b9\u5192\u51fa\uff0c\u65c1\u82e5\u65e0\u4eba\u5730\u722c\u5230\u6697\u97f3\u76f2\u602a\u7684\u5c38\u4f53\u4e0a\u5927\u5403\u5927\u56bc\u8d77\u6765\uff0c\u53d1\u51fa\u5431\u5431\u7684\u58f0\u97f3\u3002\u743c\u6069\u538c\u6076\u5730\u770b\u4e86\u5b83\u4eec\u4e00\u773c\uff0c\u5f84\u884c\u4ece\u65c1\u8fb9\u8d70\u8fc7\u3002</p><p>\u56db\u4e2a\u5c0f\u65f6\u4e4b\u540e\uff0c\u4ed6\u4eec\u62b5\u8fbe\u6c99\u739b\u65af\u57ce\u3002</p><p>\u203b\u203b\u203b</p><p>\u4ee5\u9ed1\u6697\u7cbe\u7075\u7684\u6807\u51c6\u6765\u770b\uff0c\u6c99\u739b\u65af\u57ce\u662f\u4e00\u5ea7\u79bb\u7ecf\u53db\u9053\u7684\u57ce\u5e02\uff0c\u5b83\u7684\u7edf\u6cbb\u8005\u662f\u5deb\u5e08\u800c\u975e\u795e\u540e\u7267\u5e08\u3002\u5728\u8fd9\u5ea7\u57ce\u5e02\uff0c\u5965\u672f\u7684\u5730\u4f4d\u81f3\u9ad8\u65e0\u4e0a\uff0c\u6b64\u5916\u4e00\u5207\u90fd\u5728\u5176\u4e0b\uff0c\u5b97\u6559\u4fe1\u4ef0\u5728\u6b64\u5904\u4e0d\u53d7\u91cd\u89c6\u751a\u81f3\u88ab\u6392\u65a5\uff0c\u8fd9\u5728\u5176\u4ed6\u5353\u5c14\u57ce\u5e02\u662f\u5b8c\u5168\u4e0d\u53ef\u60f3\u8c61\u7684\u3002</p><p>\u5728\u5965\u56fe\u57ce\u7684\u65f6\u5019\uff0c\u743c\u6069\u77e5\u9053\u7684\u6d88\u606f\u662f\u9634\u9b42\u57ce\u5206\u5175\u8fdb\u653b\u5951\u5fb7\xb7\u7eb3\u6492\u548c\u6c99\u739b\u65af\uff0c\u4f46\u5728\u51fa\u53d1\u4e4b\u524d\uff0c\u4ed6\u63a5\u5230\u6700\u65b0\u6d88\u606f\uff1a\u9634\u9b42\u57ce\u5df2\u7ecf\u548c\u6c99\u739b\u65af\u57ce\u7ed3\u76df\u3002\u743c\u6069\u5bf9\u6b64\u5e76\u4e0d\u611f\u5230\u534a\u70b9\u60ca\u8bb6\uff0c\u8fd9\u4e24\u5ea7\u57ce\u5e02\u6709\u592a\u591a\u7684\u76f8\u4f3c\u4e4b\u5904\u4e86\u3002\u800c\u4e14\u5bf9\u4e8e\u4ed6\u6765\u8bf4\uff0c\u8fd9\u5c31\u610f\u5473\u7740\u8def\u4e0a\u6709\u591a\u4e86\u4e00\u4e2a\u4fee\u6574\u7684\u5730\u65b9\u3002</p><p>\u5728\u8fdb\u5165\u57ce\u5e02\u4e4b\u540e\uff0c\u6b27\u51ef\u4e0d\u77e5\u9053\u53c8\u4ece\u54ea\u91cc\u5192\u4e86\u51fa\u6765\uff0c\u4ed6\u4f3c\u4e4e\u5bf9\u6b64\u5730\u975e\u5e38\u4e86\u89e3\uff0c\u4e00\u8def\u4e0a\u6307\u6307\u70b9\u70b9\uff0c\u70ed\u60c5\u5730\u5411\u743c\u6069\u4ecb\u7ecd\u60c5\u51b5\u3002\u201c\u5927\u7ea6\u5728\u516d\u5343\u591a\u5e74\u524d\uff0c\u6c99\u739b\u65af\u57ce\u5176\u5b9e\u4e5f\u548c\u5176\u4ed6\u5353\u5c14\u57ce\u5e02\u4e00\u6837\uff0c\u662f\u7531\u7f57\u4e1d\u7267\u5e08\u4e3b\u5bfc\u7684\u5bb6\u65cf\u7edf\u6cbb\uff0c\u5f53\u65f6\u7684\u7b2c\u4e00\u5bb6\u65cf\u662f\u6c99\u739b\u65af\uff0c\u4e5f\u662f\u8fd9\u5ea7\u57ce\u5e02\u7684\u521b\u5efa\u8005\uff0c\u201d\u6b27\u51ef\u8bf4\uff0c\u201c\u540e\u6765\u7206\u53d1\u4e86\u4e00\u573a\u5185\u6218\uff0c\u5404\u5927\u5bb6\u65cf\u7684\u4e3b\u6bcd\uff0c\u4ee5\u53ca\u6240\u6709\u591f\u5206\u91cf\u7684\u7267\u5e08\u5168\u90e8\u4e27\u751f\uff0c\u5deb\u5e08\u5f00\u59cb\u638c\u6743\u3002\u201d</p><p>\u5deb\u5e08\u4eec\u6539\u53d8\u4e86\u4ee5\u5f80\u5bb6\u65cf\u7edf\u6cbb\u7684\u6a21\u5f0f\uff0c\u5efa\u7acb\u8d77\u6267\u653f\u8bae\u4f1a\uff0c\u7531\u5404\u4e2a\u5965\u672f\u5b66\u6d3e\u7684\u9886\u8896\u4eba\u7269\u7ec4\u6210\u3002\u540c\u65f6\u4ed6\u4eec\u4fee\u6539\u4e86\u5f8b\u6cd5\uff0c\u91cd\u65b0\u6784\u5efa\u6743\u4f4d\u4f53\u7cfb\uff1a\u5deb\u5e08\u6700\u4e0a\uff0c\u7267\u5e08\u548c\u672f\u58eb\u3001\u541f\u6e38\u8bd7\u4eba\u7b49\u662f\u6b21\u7b49\u516c\u6c11\uff0c\u56e0\u4e3a\u4ed6\u4eec\u7684\u529b\u91cf\u5e76\u975e\u4e2a\u4eba\u52aa\u529b\u6240\u5f97\uff0c\u800c\u662f\u6765\u81ea\u8840\u7edf\u6216\u8005\u795e\u8d50\uff0c\u800c\u8fd9\u662f\u5e94\u8be5\u88ab\u9119\u89c6\u7684\u3002\u4eba\u7c7b\u548c\u7cbe\u7075\u4e0d\u4f1a\u88ab\u654c\u89c6\uff0c\u56e0\u4e3a\u4ed6\u4eec\u62e5\u6709\u6210\u4e3a\u5deb\u5e08\u7684\u53ef\u80fd\uff0c\u81f3\u4e8e\u5730\u7cbe\u3001\u98df\u4eba\u9b54\u3001\u725b\u5934\u4eba\u6216\u8005\u77f3\u76f2\u86ee\u65cf\u4e4b\u7c7b\u201c\u52a3\u667a\u79cd\u65cf\u201d\uff0c\u5219\u88ab\u6beb\u4e0d\u5ba2\u6c14\u5730\u4f5c\u4e3a\u5974\u96b6\u5bf9\u5f85\uff0c\u751a\u81f3\u77ee\u4eba\u3001\u4f8f\u5112\u4e5f\u4e0d\u4f8b\u5916\u3002</p><p>\u201c\u770b\u5230\u5317\u8fb9\u90a3\u4e9b\u5c16\u9876\u9ad8\u5854\u6ca1\u6709\uff0c\u95ea\u7740\u5404\u79cd\u9b54\u6cd5\u7075\u5149\u7684\uff0c\u201d\u6b27\u51ef\u6307\u70b9\u7740\uff0c\u201c\u90a3\u91cc\u662f\u5404\u5927\u5deb\u5e08\u5b66\u9662\u3002\u4ed6\u4eec\u662f\u6267\u653f\u8bae\u4f1a\u4e4b\u4e0b\u7684\u7b2c\u4e8c\u6743\u529b\u673a\u6784\uff0c\u662f\u8fd9\u5ea7\u57ce\u5e02\u7684\u5b9e\u9645\u7edf\u6cbb\u8005\u3002\u8981\u5c0f\u5fc3\uff0c\u201d\u4ed6\u8f6c\u5934\u5bf9\u8299\u8389\u5a05\u548c\u8fea\u74e6\u514b\u7b49\u4eba\u8bf4\uff0c\u201c\u9664\u4e86\u5deb\u5e08\uff0c\u5176\u4ed6\u4eba\u8fdb\u5165\u90a3\u91cc\u4f1a\u88ab\u7acb\u523b\u5904\u6b7b\u3002\u201d</p><p>\u201c\u8fd9\u91cc\u7684\u611f\u89c9\u548c\u9634\u9b42\u57ce\u5f88\u50cf\u3002\u201d\u8299\u857e\u72c4\u8bf4\u3002</p><p>\u201c\u8fd8\u662f\u4e0d\u592a\u4e00\u6837\u7684\uff0c\u201d\u6b27\u51ef\u6447\u7740\u624b\u6307\uff0c\u201c\u9634\u9b42\u57ce\u7684\u5deb\u5e08\u5b66\u9662\u4ec5\u4ec5\u662f\u6559\u80b2\u673a\u6784\uff0c\u800c\u5728\u8fd9\u91cc\u540c\u65f6\u4e5f\u662f\u884c\u653f\u673a\u6784\u3002\u521a\u624d\u6211\u4eec\u5165\u57ce\u7684\u65f6\u5019\uff0c\u770b\u89c1\u90a3\u51e0\u4e2a\u536b\u5175\u6ca1\u6709\uff0c\u4ed6\u4eec\u5c31\u662f\u9632\u62a4\u5b66\u9662\u7684\u4eba\u3002\u201d</p><p>\u5404\u5927\u5deb\u5e08\u5b66\u9662\u90fd\u6709\u7279\u5b9a\u7684\u6743\u529b\u548c\u4e49\u52a1\uff0c\u5171\u540c\u6784\u6210\u4e86\u8fd9\u5ea7\u57ce\u5e02\u7684\u7edf\u6cbb\u7f51\u7edc\u3002\u9632\u62a4\u5b66\u9662\u8d1f\u8d23\u57ce\u5e02\u7684\u76f4\u63a5\u9632\u5fa1\u3001\u7ef4\u6301\u79e9\u5e8f\uff1b\u5851\u80fd\u5b66\u9662\u8d1f\u8d23\u62b5\u5fa1\u5916\u6765\u4fb5\u7565\uff0c\u5b9a\u671f\u626b\u8361\u56db\u5468\uff1b\u9644\u9b54\u5b66\u9662\u8d1f\u8d23\u5974\u96b6\u5e02\u573a\u3001\u62f7\u95ee\u4fd8\u864f\uff1b\u9884\u8a00\u5b66\u9662\u5219\u5728\u6697\u4e2d\u76d1\u63a7\u5168\u57ce\uff0c\u7b49\u7b49\u3002\u6c99\u739b\u65af\u57ce\u6ca1\u6709\u4e00\u652f\u6b63\u89c4\u7684\u519b\u961f\uff0c\u5b83\u7684\u519b\u4e8b\u529b\u91cf\u5c31\u662f\u5404\u5927\u5deb\u5e08\u5b66\u9662\u7684\u6559\u5b98\u548c\u5b66\u751f\uff0c\u4ee5\u53ca\u6570\u5343\u5e74\u6784\u5efa\u8d77\u6765\u7684\u4e25\u5bc6\u9b54\u6cd5\u9632\u5fa1\u4f53\u7cfb\u3002</p><p>\u201c\u743c\u6069\uff0c\u60f3\u50cf\u4e00\u4e0b\uff0c\u5982\u679c\u4f60\u662f\u6307\u6325\u5b98\uff0c\u600e\u6837\u624d\u80fd\u8fdb\u653b\u8fd9\u5ea7\u57ce\u5e02\u3002\u201d\u6b27\u51ef\u5efa\u8bae\u3002</p><p>\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u8003\u8651\u8fd9\u4e2a\uff1f\u201d</p><p>\u201c\u53ea\u662f\u60f3\u60f3\u800c\u5df2\u561b\uff0c\u505a\u4e2a\u6e38\u620f\u3002\u201d</p><p>\u743c\u6069\u6709\u4e9b\u4e0d\u89e3\uff0c\u4f46\u8fd8\u662f\u4f9d\u8a00\u5f00\u59cb\u601d\u7d22\uff0c\u4ed6\u770b\u7740\u57ce\u5899\uff0c\u4f30\u7b97\u7740\u57ce\u5e02\u7684\u9632\u5fa1\u6f0f\u6d1e\uff0c\u4f46\u7acb\u523b\u5c31\u611f\u89c9\u5230\u4e00\u80a1\u65e0\u5f62\u7684\u5c16\u9510\u529b\u91cf\u76f4\u523a\u8fdb\u81ea\u5df1\u7684\u610f\u8bc6\u3002\u4ed6\u9a87\u5f02\u4e4b\u4e0b\uff0c\u8fde\u5fd9\u51dd\u795e\u62b5\u5fa1\uff0c\u7136\u540e\u529b\u91cf\u6d88\u5931\u4e86\uff0c\u4f46\u81ea\u5df1\u7684\u8eab\u4f53\u6709\u4e9b\u8f7b\u5fae\u9ebb\u75f9\u3002\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u743c\u6069\u95ee\u3002</p><p>\u201c\u8ff7\u9501\uff0c\u201d\u6b27\u51ef\u89e3\u91ca\uff0c\u201c\u7531\u5404\u5927\u5deb\u5e08\u5b66\u9662\u8054\u5408\u6784\u5efa\uff0c\u7b3c\u7f69\u7740\u8fd9\u5ea7\u57ce\u5e02\u548c\u5468\u8fb9\u533a\u57df\uff0c\u4efb\u4f55\u6709\u654c\u610f\u7684\u751f\u7269\u90fd\u4f1a\u81ea\u52a8\u88ab\u653b\u51fb\u3002\u201d</p><p>\u743c\u6069\u9ed8\u9ed8\u70b9\u5934\uff0c\u201c\u5f88\u4e86\u4e0d\u8d77\u3002\u201d</p><p>\u8ff7\u9501\u5c31\u662f\u8d85\u5927\u578b\u7684\u9b54\u6cd5\u7ed3\u754c\uff0c\u9634\u9b42\u57ce\u3001\u70db\u5821\u548c\u8ff7\u65af\u5353\u8bfa\u8fd9\u4e9b\u5730\u65b9\u90fd\u6709\uff0c\u76f8\u5f53\u4e8e\u8425\u9020\u4e00\u4e2a\u6781\u5ea6\u5f3a\u5316\u7684\u4e3b\u573a\u4f18\u52bf\uff0c\u5f53\u7136\u6548\u7528\u5404\u6709\u4e0d\u540c\u3002\u6c99\u739b\u65af\u57ce\u7684\u8fd9\u4e2a\u8ff7\u9501\u5a01\u529b\u80af\u5b9a\u4e0d\u6b62\u4e8e\u6b64\uff0c\u8fd9\u53ea\u662f\u5176\u4e2d\u4e00\u65b9\u9762\u7f62\u4e86\u3002</p><p>\u201c\u9c81\u6587\u548c\u8fea\u74e6\u514b\u5148\u751f\u4e24\u4f4d\u8981\u5c0f\u5fc3\uff0c\u201d\u6b27\u51ef\u7ee7\u7eed\u8bf4\uff0c\u201c\u8ddf\u7d27\u961f\u4f0d\uff0c\u5982\u679c\u4f60\u4eec\u843d\u5355\u7684\u8bdd\uff0c\u5f88\u53ef\u80fd\u4f1a\u88ab\u5f53\u4f5c\u5974\u96b6\u6293\u6355\uff0c\u5230\u65f6\u5019\u9ebb\u70e6\u5c31\u5927\u4e86\u3002\u201d</p><p>\u4ed6\u4eec\u4e00\u8def\u524d\u884c\uff0c\u6765\u5230\u65e9\u5df2\u5b89\u6392\u597d\u7684\u4f4f\u5904\uff0c\u5404\u81ea\u4f11\u606f\u3002\u6b27\u51ef\u4e00\u8f6c\u773c\u53c8\u4e0d\u89c1\u4e86\u8e2a\u5f71\uff0c\u8fea\u74e6\u514b\u548c\u9c81\u6587\u8eab\u4efd\u7279\u6b8a\uff0c\u4e3a\u4e86\u907f\u514d\u9ebb\u70e6\uff0c\u5446\u5728\u623f\u95f4\u91cc\u4e0d\u6562\u5916\u51fa\u3002\u83ab\u5c3c\u5361\u59d0\u59b9\u4e5f\u4e0d\u559c\u6b22\u70ed\u95f9\uff0c\u53ea\u6709\u743c\u6069\u548c\u6885\u83f2\u65af\u51b3\u5b9a\u665a\u9910\u4e4b\u540e\u51fa\u6765\u8d70\u8d70\u3002</p><p>\u201c\u65e2\u7136\u6765\u4e86\uff0c\u5c31\u5e94\u8be5\u5c3d\u53ef\u80fd\u591a\u89c1\u8bc6\u4e00\u4e9b\u3002\u201d</p><p>\u743c\u6069\u5982\u6b64\u8bf4\uff0c\u6885\u83f2\u65af\u4e5f\u6ca1\u6709\u5f02\u8bae\uff0c\u4ed6\u4eec\u9996\u5148\u6765\u5230\u57ce\u5e02\u5357\u90e8\u7684\u201c\u9ed1\u706b\u77f3\u67f1\u201d\uff0c\u4e00\u5757\u7531\u77f3\u7b0b\u548c\u6d1e\u7a74\u6784\u6210\u7684\u533a\u57df\u3002\u6c38\u4e0d\u7184\u706d\u7684\u9ed1\u706b\u5728\u77f3\u7b0b\u5185\u90e8\u7ffb\u6eda\uff0c\u8fd9\u4e00\u533a\u57df\u56e0\u6b64\u800c\u5f97\u540d\u3002\u8fd9\u91cc\u662f\u6c99\u65af\u739b\u57ce\u94f8\u9020\u884c\u4f1a\u6240\u5728\uff0c\u94c1\u5320\u4eec\u7684\u805a\u5c45\u5730\uff0c\u7531\u53d8\u5316\u5b66\u9662\u8d1f\u8d23\u7ba1\u8f96\u3002\u743c\u6069\u66ff\u9c81\u6587\u8d2d\u4e70\u4e86\u4e00\u9762\u76fe\u724c\uff0c\u953b\u9020\u624b\u827a\u8bf4\u4e0d\u4e0a\u7cbe\u6e5b\uff0c\u6750\u8d28\u4e5f\u4e00\u822c\uff0c\u4f46\u5176\u4e2d\u9644\u7740\u7684\u9b54\u6cd5\u5374\u4ee4\u5b83\u6bd4\u7cbe\u91d1\u8fd8\u8981\u7262\u56fa\uff0c\u8fd9\u5728\u5730\u8868\u4e16\u754c\u90fd\u662f\u5f88\u7f55\u89c1\u7684\uff0c\u5728\u6c99\u65af\u739b\u57ce\u5374\u50cf\u666e\u901a\u5546\u54c1\u4e00\u6837\u51fa\u552e\u3002</p><p>\u201c\u5730\u8109\u8f90\u5c04\u7684\u7f18\u6545\uff0c\u201d\u743c\u6069\u5411\u6885\u83f2\u65af\u89e3\u91ca\uff0c\u201c\u5b83\u4eec\u5f71\u54cd\u7740\u5e7d\u6697\u5730\u57df\u7684\u4e00\u5207\uff0c\u5305\u62ec\u9b54\u6cd5\u7269\u54c1\uff0c\u8ba9\u5200\u5251\u66f4\u950b\u5229\uff0c\u94e0\u7532\u66f4\u7262\u56fa\uff0c\u8574\u542b\u6cd5\u672f\u7684\u5a01\u529b\u66f4\u5f3a\u5927\u2014\u2014\u4f46\u4e0d\u80fd\u89c1\u9633\u5149\u3002\u201d</p><p>\u4ed6\u4eec\u63a5\u7740\u6765\u5230\u9690\u5bc6\u9b54\u6cd5\u5e02\u573a\uff0c\u5b83\u548c\u5730\u8868\u4e16\u754c\u7684\u96c6\u5e02\u5f88\u50cf\uff1a\u6570\u4ee5\u767e\u8ba1\u7684\u5e10\u7bf7\u5206\u6563\u5176\u95f4\uff0c\u51fa\u552e\u5404\u79cd\u5404\u6837\u7684\u5546\u54c1\uff0c\u5546\u5bb6\u5927\u591a\u4ee5\u4e2d\u7a7a\u7684\u77f3\u7b0b\u4f5c\u4e3a\u8d27\u7269\u4ed3\u5e93\u3002\u743c\u6069\u8865\u5145\u4e86\u4e00\u4e9b\u5fc5\u987b\u7684\u6cd5\u672f\u6750\u6599\uff0c\u5e76\u4e14\u8d2d\u4e70\u4e86\u51e0\u5f20\u5377\u8f74\uff0c\u4ed6\u8fd8\u770b\u4e2d\u4e86\u4e00\u4e2a\u7ea2\u5b9d\u77f3\u8033\u5760\uff0c\u6253\u7b97\u4e70\u7ed9\u6885\u83f2\u65af\uff0c\u4f46\u88ab\u5723\u6b66\u58eb\u62d2\u7edd\u4e86\u3002\u201c\u6211\u4ece\u6ca1\u8bd5\u8fc7\u8fd9\u4e1c\u897f\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u800c\u4e14\u6211\u4e5f\u4e0d\u77e5\u9053\u600e\u4e48\u6234\u5b83\u3002\u201d</p><p>\u201c\u6253\u4e2a\u8033\u6d1e\u5c31\u884c\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5f88\u5bb9\u6613\u7684\u3002\u201d</p><p>\u201c\u4e0d\u884c\uff0c\u201d\u6885\u83f2\u65af\u6447\u5934\uff0c\u201c\u6211\u6015\u75bc\u3002\u201d</p><p>\u4e24\u4eba\u4e00\u9f50\u7b11\u4e86\u8d77\u6765\uff0c\u743c\u6069\u5c06\u8033\u5760\u8fd8\u7ed9\u8001\u677f\uff0c\u7ee7\u7eed\u524d\u884c\u3002\u8fd9\u91cc\u6709\u5f88\u591a\u50cf\u743c\u6069\u4e00\u6837\u7684\u5deb\u5e08\uff0c\u4ed6\u4eec\u5927\u591a\u5e26\u7740\u9b54\u50cf\u3001\u5143\u7d20\u751f\u7269\u6216\u8005\u4ea1\u7075\u4f5c\u4e3a\u62a4\u536b\uff0c\u8fd9\u4f3c\u4e4e\u662f\u6c99\u739b\u65af\u57ce\u666e\u904d\u901a\u884c\u7684\u4e60\u4fd7\u3002\u4ed6\u4eec\u6700\u7ec8\u6765\u5230\u57ce\u5e02\u7684\u4e2d\u592e\u90e8\u4f4d\uff0c\u8fd9\u91cc\u77d7\u7acb\u7740\u4e00\u6839\u76f4\u5f84\u8d85\u8fc7\u4e24\u5343\u82f1\u5c3a\u7684\u5de8\u5927\u77f3\u67f1\uff0c\u87ba\u65cb\u578b\u7684\u6f2b\u957f\u9636\u68af\u73af\u7ed5\u800c\u4e0a\uff0c\u53ea\u901a\u5f80\u6d1e\u7a9f\u7684\u7a79\u9876\u3002\u743c\u6069\u62b1\u7740\u6885\u83f2\u65af\uff0c\u76f4\u63a5\u7528\u6cd5\u672f\u98de\u884c\u800c\u4e0a\uff0c\u4ed6\u4eec\u7ad9\u5728\u8ddd\u79bb\u5730\u9762\u5927\u7ea6\u4e00\u767e\u82f1\u5c3a\u9ad8\u7684\u5730\u65b9\uff0c\u501f\u52a9\u949f\u4e73\u77f3\u95f4\u7ffb\u817e\u4e0d\u606f\u7684\u5996\u706b\u4fef\u89c6\u7740\u8fd9\u5ea7\u7279\u7acb\u72ec\u884c\u7684\u57ce\u5e02\u3002</p><p>\u201c\u5b83\u548c\u6211\u4ee5\u524d\u6240\u542c\u8bf4\u548c\u60f3\u50cf\u8fc7\u7684\u5353\u5c14\u57ce\u5e02\u4e0d\u592a\u4e00\u6837\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6ca1\u6709\u90a3\u4e48\u53ef\u6015\u3002\u6211\u60f3\u8fd9\u5e94\u8be5\u5f52\u529f\u4e8e\u5b83\u7684\u9886\u5bfc\u8005\u662f\u5deb\u5e08\u800c\u975e\u7267\u5e08\u3002\u201d</p><p>\u6885\u83f2\u65af\u626c\u4e86\u626c\u7709\uff0c\u201c\u4f60\u8ba4\u4e3a\u5deb\u5e08\u4f5c\u4e3a\u57ce\u5e02\u7684\u7edf\u6cbb\u8005\u6bd4\u7267\u5e08\u66f4\u52a0\u9002\u5408\uff1f\u201d</p><p>\u201c\u81ea\u7136\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\uff1f\u56e0\u4e3a\u4f60\u4eec\u66f4\u806a\u660e\uff1f\u201d</p><p>\u743c\u6069\u5fae\u7b11\uff0c\u201c\u867d\u7136\u6211\u6240\u53d7\u8fc7\u7684\u6559\u80b2\u544a\u8bc9\u6211\u5e94\u8be5\u8c26\u900a\uff0c\u4e0d\u8fc7\u6211\u66f4\u5e94\u8be5\u8bda\u5b9e\uff0c\u201d\u4ed6\u53cd\u95ee\uff0c\u201c\u96be\u9053\u4e0d\u662f\u5417\uff1f\u201d</p><p>\u201c\u6216\u8bb8\u662f\uff0c\u201d\u6885\u83f2\u65af\u627f\u8ba4\uff0c\u201c\u7136\u800c\u4f5c\u4e3a\u7ba1\u7406\u8005\uff0c\u5e76\u4e0d\u4ec5\u4ec5\u662f\u806a\u660e\u6216\u8005\u6709\u9ad8\u8d85\u7684\u667a\u529b\u5c31\u8db3\u591f\u7684\u3002\u7267\u5e08\u4eec\u65f6\u523b\u5728\u4f20\u64ad\u4fe1\u4ef0\u3001\u5ba3\u8bb2\u6559\u4e49\u3001\u5f15\u5bfc\u4fe1\u5f92\u3001\u7edf\u9886\u6559\u4f1a\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8ba4\u4e3a\u4ed6\u4eec\u4f5c\u4e3a\u7ba1\u7406\u8005\uff0c\u4f1a\u6bd4\u4f60\u4eec\u8fd9\u4e9b\u6210\u5929\u57cb\u9996\u4e66\u5377\uff0c\u8db3\u4e0d\u51fa\u6237\uff0c\u8fdb\u884c\u5371\u9669\u7684\u8bd5\u9a8c\uff0c\u6216\u8005\u6c89\u8ff7\u5947\u602a\u7684\u5e7b\u60f3\uff0c\u6216\u8005\u5fd9\u4e8e\u548c\u4e0b\u5c42\u754c\u7684\u5996\u9b54\u6253\u4ea4\u9053\uff0c\u5374\u4ece\u4e0d\u80af\u548c\u2018\u4eba\u2019\u4eb2\u8fd1\u7684\u5deb\u5e08\u4eec\u900a\u8272\u5462\uff1f\u201d</p><p>\u201c\u7136\u800c\u5deb\u5e08\u8ffd\u6c42\u7684\u662f\u81ea\u6211\uff0c\u201d\u743c\u6069\u53cd\u9a73\uff0c\u201c\u7267\u5e08\u7684\u751f\u547d\u610f\u4e49\u5219\u5728\u4e8e\u4f8d\u5949\u795e\u7947\u3002\u5982\u679c\u7531\u7267\u5e08\u6765\u6267\u638c\u653f\u6743\uff0c\u90a3\u4e48\u4ed6\u4eec\u5230\u5e95\u662f\u9075\u5faa\u795e\u7947\u7684\u65e8\u610f\uff0c\u8fd8\u662f\u503e\u542c\u51e1\u4eba\u7684\u5fc3\u58f0\uff1f\u201d</p><p>\u201c\u8fd9\u4e24\u8005\u5e76\u4e0d\u4e00\u5b9a\u51b2\u7a81\u3002\u201d</p><p>\u201c\u4f46\u6709\u53ef\u80fd\u51b2\u7a81\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u795e\u7947\u4e5f\u6709\u5584\u826f\u548c\u90aa\u6076\uff0c\u5b83\u4eec\u6240\u559c\u6b22\u7684\uff0c\u5e76\u4e0d\u4e00\u5b9a\u662f\u51e1\u4eba\u6240\u5e0c\u671b\u7684\u3002\u5982\u679c\u4e00\u5ea7\u57ce\u5e02\u7531\u90aa\u795e\u7684\u7267\u5e08\u6765\u6267\u638c\uff0c\u96be\u9053\u4f60\u89c9\u5f97\u90a3\u662f\u4ef6\u5f88\u4ee4\u4eba\u6109\u5feb\u7684\u4e8b\u60c5\uff1f\u201d</p><p>\u201c\u8fd9\u6b63\u662f\u6211\u4eec\u7684\u4f7f\u547d\u6240\u5728\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u5b88\u536b\u5584\u826f\uff0c\u6253\u51fb\u90aa\u6076\uff0c\u6467\u6bc1\u90aa\u795e\u7684\u4fe1\u4ef0\uff0c\u4f20\u64ad\u5584\u795e\u7684\u6559\u4e49\uff0c\u8ba9\u8fd9\u4e2a\u4e16\u754c\u4e0a\u7684\u751f\u547d\u62e5\u6709\u66f4\u7f8e\u597d\u7684\u751f\u6d3b\u3002\u201d</p><p>\u201c\u6ca1\u6709\u795e\u7947\uff0c\u6211\u4eec\u4f9d\u7136\u4e5f\u80fd\u8fc7\u5f97\u5f88\u597d\uff0c\u201d\u743c\u6069\u4f38\u624b\u6307\u5411\u4e0b\u65b9\u7684\u57ce\u5e02\uff0c\u201c\u5c31\u50cf\u8fd9\u6837\u3002\u201d</p><p>\u201c\u4f46\u6709\u795e\u7947\u4f1a\u8ba9\u6211\u4eec\u8fc7\u5f97\u66f4\u597d\uff0c\u201d\u6885\u83f2\u65af\u9488\u950b\u76f8\u5bf9\uff0c\u201c\u6c99\u65af\u739b\u57ce\u629b\u5f03\u4e86\u7f57\u4e1d\u7684\u90aa\u6076\u4fe1\u4ef0\uff0c\u8fd9\u6beb\u65e0\u7591\u95ee\u662f\u6b63\u786e\u7684\uff0c\u4f46\u63a5\u4e0b\u6765\u5c31\u8d70\u5165\u4e86\u6b67\u9014\u3002\u5982\u679c\u4ed6\u4eec\u73b0\u5728\u653e\u5f03\u50b2\u6162\u548c\u81ea\u8d1f\uff0c\u8654\u8bda\u5730\u5d07\u62dc\u63d0\u5c14\uff0c\u6216\u8005\u5170\u68ee\u5fb7\u5c14\uff0c\u6216\u8005\u4efb\u4f55\u4e00\u4f4d\u5584\u795e\uff0c\u96be\u9053\u4f60\u8ba4\u4e3a\u795e\u7947\u4e0d\u4f1a\u964d\u4e0b\u795e\u6069\uff1f\u96be\u9053\u4f60\u8ba4\u4e3a\u8fd9\u5ea7\u57ce\u5e02\u7684\u5b50\u6c11\u4e0d\u4f1a\u8fc7\u5f97\u66f4\u597d\uff1f\u201d</p><p>\u743c\u6069\u6c89\u9ed8\u4e86\u4e00\u4f1a\uff0c\u201c\u4ece\u7406\u8bba\u4e0a\u6765\u8bf4\uff0c\u4f60\u6216\u8bb8\u662f\u5bf9\u7684\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u4f46\u90a3\u4ec5\u4ec5\u53ea\u662f\u7406\u8bba\u548c\u5047\u8bbe\uff0c\u800c\u73b0\u5b9e\u5c31\u6446\u5728\u6211\u4eec\u9762\u524d\u3002\u5176\u4ed6\u5353\u5c14\u57ce\u5e02\u7531\u7267\u5e08\u7edf\u6cbb\uff0c\u6c99\u739b\u65af\u57ce\u7531\u5deb\u5e08\u7edf\u6cbb\u2014\u2014\u800c\u540e\u8005\u663e\u7136\u80dc\u8fc7\u524d\u8005\u3002\u201d</p><p>\u6885\u83f2\u65af\u5fae\u5fae\u51b7\u7b11\u8d77\u6765\uff0c\u201c\u9996\u5148\uff0c\u743c\u6069\uff0c\u4f60\u7684\u89c1\u8bc6\u592a\u5c11\u4e86\uff0c\u201d\u5979\u6709\u4e9b\u81ea\u50b2\u5730\u8bf4\uff0c\u201c\u8fd9\u4e2a\u4e16\u754c\u4e0a\u6709\u592a\u591a\u7684\u5730\u65b9\u4f60\u6ca1\u6709\u89c1\u8bc6\u8fc7\uff0c\u600e\u4e48\u6562\u4ee5\u4e00\u5b54\u4e4b\u89c1\u800c\u5984\u81ea\u81c6\u6d4b\u3002\u5728\u56e0\u5e03\u56fe\uff0c\u6211\u4eec\u7684\u6559\u4f1a\u5efa\u7acb\u4e86\u7a33\u5b9a\u800c\u7e41\u8363\u7684\u57ce\u5e02\uff0c\u6bd4\u8fd9\u5ea7\u6c99\u739b\u65af\u80dc\u8fc7\u5341\u500d\u3002\u6df1\u6c34\u57ce\u662f\u5927\u9646\u7b2c\u4e00\u5927\u57ce\u5e02\u548c\u6e2f\u53e3\uff0c\u5b83\u7684\u5f8b\u6cd5\u6709\u8d85\u8fc7\u4e09\u5206\u4e4b\u4e8c\u662f\u53c2\u7167\u6211\u4eec\u7684\u65e0\u5c3d\u6cd5\u5178\uff08Innumerable Edicts\uff09\u800c\u5236\u5b9a\u3002\u6211\u4eec\u7684\u7267\u5e08\u548c\u5723\u6b66\u58eb\u5e2e\u52a9\u6cf0\u745f\u5c14\u7ed3\u675f\u4e86\u6218\u4e71\uff0c\u5e2e\u52a9\u585e\u65af\u514b\u6210\u529f\u62b5\u5fa1\u7ea2\u888d\u5deb\u5e08\u7684\u4fb5\u7565\uff0c\u5e2e\u52a9\u65e0\u6570\u6df7\u4e71\u7684\u533a\u57df\u91cd\u5efa\u79e9\u5e8f\uff0c\u8ba9\u91ce\u86ee\u8fdb\u5316\u4e3a\u6587\u660e\uff0c\u8fd9\u4e00\u5207\u4f60\u53c8\u77e5\u9053\u591a\u5c11\u5462\uff1f\u201d</p><p>\u201c\u5176\u6b21\uff0c\u201d\u5979\u7ee7\u7eed\u8bf4\uff0c\u201c\u6211\u4e0d\u660e\u767d\u4f60\u4e3a\u4ec0\u4e48\u8ba4\u4e3a\u8fd9\u5ea7\u57ce\u5e02\u5c31\u80dc\u8fc7\u5176\u4ed6\u7684\u5353\u5c14\u57ce\u5e02\u5462\uff1f\u201d\u5723\u6b66\u58eb\u5c06\u80cc\u501a\u9760\u5728\u77f3\u58c1\u4e0a\uff0c\u4fef\u89c6\u7740\u4e0b\u65b9\uff0c\u201c\u4f60\u6ca1\u6709\u53bb\u8fc7\u5176\u4ed6\u4efb\u4f55\u5353\u5c14\u57ce\u5e02\uff0c\u90a3\u4e48\u7ed3\u8bba\u662f\u5982\u4f55\u505a\u51fa\uff1f\u597d\u5427\uff0c\u6211\u59d1\u4e14\u627f\u8ba4\u4f60\u542c\u8fc7\u7684\u90a3\u4e9b\u4f20\u8a00\u548c\u63cf\u8ff0\u90fd\u662f\u771f\u5b9e\uff0c\u4f46\u90a3\u53c8\u5982\u4f55\uff0c\u96be\u9053\u8fd9\u5ea7\u6c99\u739b\u65af\u57ce\u5c31\u53c8\u6709\u4ec0\u4e48\u4e0d\u540c\u5417\uff1f\u6211\u4f9d\u7136\u770b\u89c1\u4e86\u68ee\u4e25\u7684\u7b49\u7ea7\u5236\u5ea6\uff0c\u6b8b\u9177\u7684\u5974\u96b6\u4e70\u5356\uff0c\u4e25\u5389\u800c\u4e0d\u8fd1\u4eba\u60c5\u7684\u5f8b\u6cd5\uff0c\u8d64\u88f8\u88f8\u7684\u79cd\u65cf\u6b67\u89c6\uff0c\u4ee5\u53ca\u6beb\u4e0d\u63a9\u9970\u7684\u5f31\u8089\u5f3a\u98df\uff0c\u80dc\u8005\u4e3a\u5c0a\uff0c\u201d\u5979\u7684\u53cc\u624b\u4ea4\u53c9\uff0c\u51dd\u89c6\u7740\u743c\u6069\uff0c\u201c\u6211\u4eec\u662f\u4eba\u7c7b\uff0c\u4eba\u7c7b\u5728\u5176\u4ed6\u5353\u5c14\u57ce\u5e02\u4f1a\u88ab\u89c6\u4e3a\u4f4e\u8d31\uff0c\u800c\u5728\u8fd9\u91cc\u5219\u4f1a\u88ab\u5e73\u7b49\u5bf9\u5f85\uff1b\u4f60\u662f\u4e2a\u5deb\u5e08\uff0c\u662f\u8fd9\u91cc\u7edf\u6cbb\u9636\u5c42\u7684\u4e00\u5458\uff0c\u81f3\u5c11\u662f\u540c\u7c7b\uff0c\u5904\u5904\u53d7\u5230\u5c0a\u91cd\u548c\u656c\u754f\uff0c\u8def\u4e0a\u7684\u5e73\u6c11\u770b\u5230\u4f60\u7684\u670d\u9970\u90fd\u4f1a\u606d\u8c28\u5730\u8ba9\u8def\u3002\u8fd9\u4e00\u5207\u8ba9\u4f60\u611f\u89c9\u826f\u597d\uff0c\u8ba9\u4f60\u5bf9\u8fd9\u5ea7\u57ce\u5e02\u7684\u8bc4\u4ef7\u63d0\u9ad8\uff0c\u7136\u800c\u4f60\u544a\u8bc9\u6211\uff0c\u9664\u4e86\u628a\u5deb\u5e08\u548c\u7267\u5e08\u7684\u4f4d\u9636\u8c03\u6362\uff0c\u8fd9\u5ea7\u57ce\u5e02\u8fd8\u6709\u4ec0\u4e48\u5b9e\u8d28\u4e0a\u7684\u6539\u53d8\uff1f\u201d</p><p>\u743c\u6069\u9759\u9759\u770b\u7740\u5979\uff0c\u201c\u6211\u5f88\u4e45\u6ca1\u6709\u770b\u89c1\u4f60\u8fd9\u6837\u8ba4\u771f\u4e86\u3002\u201d</p><p>\u201c\u6216\u8bb8\uff0c\u4f46\u6211\u662f\u4e2a\u5723\u6b66\u58eb\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u4e0d\u8981\u5fd8\u4e86\u8fd9\u70b9\u3002\u201d</p><p>\u201c\u6211\u51e0\u4e4e\u90fd\u7ed9\u5fd8\u4e86\uff0c\u201d\u743c\u6069\u5fae\u7b11\uff0c\u201c\u597d\u5427\uff0c\u5723\u6b66\u58eb\u5c0f\u59d0\uff0c\u90a3\u4e48\u8ba9\u6211\u6765\u6b63\u9762\u56de\u5e94\u4f60\u7684\u95ee\u9898\u3002\u786e\u5b9e\uff0c\u6bd4\u8d77\u5b83\u7684\u540c\u7c7b\uff0c\u6c99\u739b\u65af\u6216\u8bb8\u6ca1\u6709\u672c\u8d28\u4e0a\u7684\u8fdb\u6b65\uff0c\u4f46\u5b83\u4f9d\u65e7\u662f\u6709\u6539\u53d8\u7684\u3002\u6b63\u5982\u4f60\u6240\u8bf4\uff0c\u5728\u5176\u4ed6\u5353\u5c14\u57ce\u5e02\uff0c\u4eba\u7c7b\u4f1a\u88ab\u89c6\u4e3a\u4f4e\u8d31\uff0c\u552f\u6709\u5353\u5c14\u624d\u662f\u9ad8\u7b49\u79cd\u65cf\uff0c\u800c\u5728\u8fd9\u91cc\uff0c\u81f3\u5c11\u4ed6\u4eec\u80af\u627f\u8ba4\u4eba\u7c7b\u3001\u7cbe\u7075\u548c\u5353\u5c14\u7684\u5e73\u7b49\u5730\u4f4d\u3002\u5f53\u7136\uff0c\u77ee\u4eba\u3001\u4f8f\u5112\u548c\u5176\u4ed6\u751f\u7075\u4f9d\u65e7\u88ab\u5ffd\u7565\u4e86\uff0c\u4f46\u8fd9\u6bd5\u7adf\u662f\u8fdb\u6b65\uff0c\u4e0d\u662f\u5417\uff1f\u4e09\u4e2a\u603b\u6bd4\u4e00\u4e2a\u597d\uff0c\u201d\u4ed6\u70b9\u70b9\u5934\uff0c\u201c\u786e\u5b9e\u8fd8\u4e0d\u591f\uff0c\u4f46\u4e16\u754c\u4e0a\u7684\u4e8b\u60c5\uff0c\u603b\u662f\u5e94\u8be5\u4e00\u6b65\u4e00\u6b65\u6765\uff0c\u5bf9\u4e0d\u5bf9\u3002\u201d</p><p>\u201c\u6216\u8bb8\uff0c\u201d\u6885\u83f2\u65af\u8868\u793a\u540c\u610f\uff0c\u201c\u4f60\u8bf4\u5f97\u5bf9\u3002\u4e0d\u7ba1\u600e\u6837\uff0c\u6709\u8fd9\u6837\u7684\u5353\u5c14\u57ce\u5e02\uff0c\u603b\u6bd4\u6ca1\u6709\u597d\u3002\u201d</p><p>\u201c\u672c\u8be5\u5982\u6b64\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u53c2\u5dee\u591a\u6001\u662f\u5e78\u798f\u7684\u672c\u6e90\u3002\u201d</p><p>\u201c\u8fd9\u662f\u8c01\u8bf4\u7684\uff1f\u201d</p><p>\u201c\u6211\u8bf4\u7684\uff0c\u201d\u743c\u6069\u4e00\u672c\u6b63\u7ecf\uff0c\u968f\u5373\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u662f\u7f57\u7d20\uff0c\u4e00\u4f4d\u54f2\u5b66\u5bb6\u3002\u201d</p><p>\u201c\u6ca1\u542c\u8bf4\u8fc7\u3002\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u4e0d\u8fc7\u663e\u7136\u4e5f\u6ca1\u6709\u592a\u5728\u610f\u3002</p><p>\u201c\u5176\u5b9e\u6211\u66f4\u5947\u602a\u7684\u662f\uff0c\u86db\u540e\u4e3a\u4ec0\u4e48\u6ca1\u6709\u6bc1\u706d\u8fd9\u5ea7\u57ce\u5e02\uff0c\u201d\u743c\u6069\u8f6c\u79fb\u4e86\u8bdd\u9898\uff0c\u201c\u8fd9\u6beb\u65e0\u7591\u95ee\u662f\u5bf9\u5979\u7684\u5927\u4e0d\u656c\u3002\u201d</p><p>\u201c\u6216\u8bb8\u662f\u56e0\u4e3a\u5979\u529e\u4e0d\u5230\uff0c\u795e\u7947\u7ec8\u7a76\u5e76\u975e\u4e07\u80fd\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u6216\u8bb8\u662f\u56e0\u4e3a\u5979\u6070\u6070\u559c\u6b22\u770b\u5230\u5982\u6b64\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u743c\u6069\u8ff7\u60d1\u3002</p><p>\u201c\u636e\u6211\u6240\u77e5\uff0c\u86db\u540e\u662f\u559c\u597d\u6df7\u4e71\u548c\u80cc\u53db\u7684\u795e\u7947\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u6216\u8bb8\u5979\u89c9\u5f97\u6c99\u739b\u65af\u7684\u5b58\u5728\u540c\u6837\u5f70\u663e\u4e86\u5979\u7684\u610f\u5fd7\uff1f\u8c01\u77e5\u9053\u5462\u3002\u201d</p><p>\u743c\u6069\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u786e\u5b9e\uff0c\u53cd\u6b63\u5979\u6ca1\u6709\u505a\u4ec0\u4e48\u2014\u2014\u73b0\u5728\u66f4\u52a0\u505a\u4e0d\u4e86\u4e86\u3002\u201d</p><p>\u6885\u83f2\u65af\u8f7b\u5fae\u6447\u5934\uff0c\u201c\u8981\u5c0f\u5fc3\uff0c\u743c\u6069\uff0c\u201d\u5979\u8b66\u544a\u8bf4\uff0c\u201c\u6211\u77e5\u9053\u4f60\u4eec\u6709\u786e\u5207\u6d88\u606f\uff0c\u8bc1\u5b9e\u7f57\u4e1d\u5df2\u7ecf\u4e0d\u518d\u8d50\u4e88\u5979\u7684\u4fe1\u5f92\u795e\u672f\uff0c\u4f46\u8fd9\u5e76\u4e0d\u610f\u5473\u7740\u5979\u5c31\u9668\u843d\u4e86\u3002\u795e\u7947\u7684\u4e16\u754c\uff0c\u5e76\u975e\u6211\u4eec\u6240\u80fd\u8f7b\u6613\u731c\u5ea6\u3002\u201d</p><p>\u201c\u63d0\u8bae\u8fd9\u6b21\u8fdc\u5f81\u7684\uff0c\u662f\u6211\u4eec\u5c0a\u656c\u7684\u9996\u5e2d\u5927\u7267\u5e08\u745e\u74e6\u5170\u738b\u5b50\uff0c\u201d\u743c\u6069\u6f2b\u4e0d\u7ecf\u5fc3\u5730\u8bf4\uff0c\u201c\u4f60\u4ee5\u4e3a\u4ed6\u96be\u9053\u6ca1\u6709\u4ece\u6267\u638c\u9690\u5bc6\u7684\u591c\u5973\u58eb\u90a3\u91cc\u5f97\u5230\u70b9\u4ec0\u4e48\u4fdd\u8bc1\u5417\u3002\u201d</p><p>\u6885\u83f2\u65af\u6ca1\u6709\u518d\u8bf4\u8bdd\u3002</p></article>"}}}]);
