(window.webpackJsonp=window.webpackJsonp||[]).push([[234],{257:function(p){p.exports={chapterTitle:null,title:"\u7b2c234\u7ae0 \u591a\u60c5\u7684\u8f6c\u8eab\u800c\u53bb",content:"<article><h2>\u7b2c234\u7ae0 \u591a\u60c5\u7684\u8f6c\u8eab\u800c\u53bb</h2><p>\u3000\u3000\u5f20\u606a\u8ddf\u665a\u6674\u8bf4\u8ba9\u5979\u5c06\u4ece\u6b63\u6cf0\u5957\u51fa\u7684\u8d44\u91d1\u7ed9\u4ed6\u7528\uff0c\u665a\u6674\u6beb\u4e0d\u72b9\u8c6b\u7684\u5c31\u70b9\u5934\u7b54\u5e94\uff0c\u5f20\u606a\u7b11\u7740\u8bf4\uff1a\u201c\u4f60\u600e\u4e48\u4e0d\u60f3\u60f3\u5c31\u7b54\u5e94\uff1f\u201d</p><p>\u3000\u3000\u201c\u8981\u60f3\u4ec0\u4e48\uff1f\u201d\u665a\u6674\u5c06\u5782\u5728\u7738\u5b50\u524d\u7684\u4e00\u7f15\u5934\u53d1\u64a9\u81f3\u8033\u540e\uff0c\u7738\u5b50\u91cc\u85cf\u7740\u4e00\u5206\u7591\u60d1\uff0c\u62cd\u4e86\u62cd\u8111\u888b\u3002\u624d\u604d\u7136\u5927\u609f\uff0c\u201c\u54e6\uff0c\u5f88\u957f\u65f6\u95f4\u6ca1\u542c\u4f60\u8bf4\u7231\u8fbe\u4e86\uff0c\u201d\u786e\u5b9e\uff0c\u5f20\u606a\u5c06\u9526\u6e56\u7684\u4e8b\u60c5\u5b8c\u5168\u63a8\u7ed9\u665a\u6674\uff0c\u4e03\u6708\u53c8\u5c06\u5468\u6e38\u3001\u5218\u660e\u8f89\u3001\u8bb8\u601d\u7b49\u4eba\u4ece\u9526\u6e56\u62bd\u8d70\uff0c\u8c22\u665a\u6674\u4e3a\u9526\u6e56\u7684\u4e8b\u60c5\u5fd9\u5f97\u7126\u5934\u70c2\u989d\uff0c\u6ca1\u6709\u4f59\u7455\u975e\u5e38\u900f\u5f7b\u7684\u5173\u6ce8\u7231\u8fbe\u7684\u4e8b\u60c5\uff0c\u201c\u90a3\u4f60\u7ed9\u6211\u597d\u597d\u8bf4\u8bf4\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u6162\u6162\u5c06\u6700\u8fd1\u4e00\u6bb5\u65f6\u95f4\u5f71\u789f\u673a\u7684\u5e02\u573a\u53d8\u5316\u4e0e\u4eca\u540e\u51e0\u5e74\u7684\u524d\u666f\u5206\u6790\u7ed9\u5979\u542c\u3002</p><p>\u3000\u3000\u8c22\u665a\u6674\u4e0d\u77e5\u4e0d\u89c9\u7684\u5165\u4e86\u795e\uff0c\u53d8\u4e86\u4e00\u4e2a\u59ff\u52bf\uff0c\u62b1\u819d\u8737\u5750\u7740\uff0c\u4e0b\u5df4\u78d5\u5728\u819d\u76d6\u4e0a\uff0c\u9ed1\u8272\u7738\u5b50\u6014\u6014\u7684\u770b\u7740\u5f20\u606a\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u7740\u665a\u6674\u7684\u7738\u5b50\uff0c\u6c34\u76c8\u76c8\u7684\u6676\u4eae\uff0c\u76ee\u5149\u843d\u5230\u5979\u7684\u817f\u4e0a\uff0c\u7ea4\u5de7\u51c0\u767d\uff0c\u5f02\u5e38\u7684\u7cbe\u81f4\uff0c\u9274\u8d4f\u7f8e\u5973\u5148\u770b\u817f\u4e0e\u8db3\uff0c\u4e00\u4e2a\u5973\u4eba\u7684\u96c5\u81f4\u4e0e\u8f7b\u76c8\u90fd\u4f53\u73b0\u7ea4\u7ea4\u5c0f\u817f\u4e0e\u51c0\u767d\u65e0\u9aa8\u7684\u7389\u8db3\u4e0a\u3002</p><p>\u3000\u3000\u8c22\u665a\u775b\u62b1\u819d\u800c\u5750\uff0c\u5706\u8038\u8038\u7684\u80f8\u90e8\u5939\u5728\u80f8\u817f\u4e4b\u95f4\uff0c\u7a7f\u7740\u5bbd\u677e\u7684\u9488\u7ec7\u77ed\u8896\u770b\u4e0d\u51fa\u89c4\u6a21\uff0c\u8fd9\u4e00\u5750\uff0c\u4e24\u808b\u6324\u51fa\u5b8c\u7f8e\u7684\u5f27\u5f62\u66f2\u7ebf\u3002\u5f20\u606a\u5fcd\u4e0d\u4f4f\u8d2a\u5a6a\u7684\u591a\u770b\u4e86\u4e24\u773c\u3002</p><p>\u3000\u3000\u8c22\u665a\u6674\u5165\u795e\u7684\u542c\u5f20\u606a\u7684\u8bdd\uff0c\u89c1\u4ed6\u4e0d\u8bf4\u8bdd\uff0c\u4f4e\u5934\u770b\u5230\u4e86\u4e00\u773c\uff0c\u624d\u6653\u5f97\u4ed6\u76ef\u7740\u90a3\u91cc\u770b\uff0c\u4e0d\u7ecf\u610f\u7684\u6362\u4e86\u4e00\u4e2a\u59ff\u52bf\uff0c\u5973\u4eba\u5e73\u65e5\u5acc\u81ea\u5df1\u80f8\u5927\u7d2f\u8d58\uff0c\u4f46\u80fd\u5438\u5f15\u7537\u4eba\u773c\u7403\uff0c\u53c8\u89c9\u5f97\u5341\u5206\u81ea\u8c6a\uff0c\u53ea\u662f\u5f20\u606a\u6536\u56de\u76ee\u5149\u5341\u5206\u81ea\u7136\uff0c\u53ef\u4e0d\u50cf\u5341\u516d\u4e03\u5c81\u7684\u5c11\u5e74\u8fd9\u65f6\u5019\u4f1a\u6709\u614c\u5f20\u3002</p><p>\u3000\u3000\u8c22\u665a\u6674\u5fc3\u91cc\u7b11\u4e86\u7b11\uff1a\u5f20\u606a\u5012\u662f\u4ec0\u4e48\u5730\u65b9\u90fd\u8868\u73b0\u5f97\u8001\u7ec3\uff0c\u5077\u770b\u7684\u773c\u795e\u4e5f\u975e\u5e38\u6709\u6210\u719f\u7537\u4eba\u7684\u5473\u9053\uff0c\u5fc3\u91cc\u6709\u7740\u9ebb\u9ebb\u7684\u611f\u89c9\uff0c\u6545\u4f5c\u9547\u9759\u7684\u5bf9\u5f20\u606a\u8bf4\uff1a\u201c\u600e\u4e48\u4e0d\u8bf4\u4e0b\u53bb\uff1f\u201d</p><p>\u3000\u3000\u201c\u6ca1\u6709\u4e86\uff0c\u5c31\u8fd9\u4e9b\uff0c\u201d\u5f20\u606a\u8fd8\u4e0d\u60f3\u8bf4\u5f97\u592a\u8fc7\uff0c\u7b11\u7740\u8bf4\uff0c\u201c\u8bb8\u601d\u59d0\u90a3\u8fb9\u53ef\u662f\u6700\u91cd\u8981\u7684\u4e00\u6b65\u68cb\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6bcf\u5757\u89e3\u7801\u677f\u771f\u80fd\u622a\u7559\u8fd9\u591a\u4e48\u7684\u5229\u6da6\uff1f\u201d\u8c22\u665a\u6674\u5b9a\u775b\u770b\u7740\u5f20\u606a\uff0c\u201c\u4e0d\u662f\u6bd4\u6574\u673a\u7684\u5229\u6da6\u8fd8\u8981\u9ad8\uff1f\u201d</p><p>\u3000\u3000\u201c\u5dee\u4e0d\u591a\uff0c\u53ea\u8981\u89e3\u7801\u677f\u7684\u6280\u672f\u5784\u65ad\u4e0d\u88ab\u7b2c\u4e09\u5bb6\u516c\u53f8\u6253\u7834\uff0c\u5c31\u80fd\u4e00\u76f4\u7ef4\u6301\u8fd9\u4e48\u9ad8\u7684\u5784\u65ad\u5229\u6da6\u3002\u8c01\u90fd\u60f3\u4e0d\u5230\u4e24\u4e09\u5e74\u6211\u4eec\u4f1a\u8fd9\u4e48\u6709\u94b1\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u4e0d\u80fd\u5360\u4f60\u7684\u4fbf\u5b9c\uff0c\u201d\u542c\u5230\u5f20\u606a\u8bf4\u6211\u4eec\u4e24\u5b57\uff0c\u665a\u6674\u5fc3\u91cc\u6709\u4e9b\u611f\u52a8\uff0c\u4f46\u662f\u5979\u8fd8\u80fd\u5206\u5f97\u6e05\u5f62\u52bf\uff0c\u5979\u624b\u6342\u7740\u80f8\uff0c\u4e0b\u5df4\u53c8\u91cd\u65b0\u78d5\u5728\u819d\u76d6\u4e0a\uff0c\u5374\u6108\u8ba9\u56de\u60f3\u4e0a\u56de\u770b\u5230\u7684\u60c5\u5f62\uff0c\u4e00\u5bf9\u7389\u767d\u8272\u5927\u4e73\u5728\u7761\u8863\u91cc\u6643\u8361\uff0c\u201c\u6211\u53ea\u5bf9\u7231\u8fbe\u7535\u5b50\u5165\u80a1\uff0c\u7231\u8fbe\u73b0\u5728\u7684\u8d44\u91d1\u94fe\u592a\u60ac\u4e86\uff0c\u6709\u4e24\u4e09\u5343\u4e07\u7684\u73b0\u91d1\u6d41\u4f1a\u597d\u770b\u4e00\u4e9b\uff0c\u9999\u6e2f\u7684\u4ee3\u7406\u516c\u53f8\uff0c\u6211\u4e0d\u53c2\u4e0e\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6ca1\u6709\u665a\u6674\u59d0\uff0c\u6211\u54ea\u91cc\u80fd\u505a\u5230\u8fd9\u4e00\u6b65\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4e0d\u8981\u8bf4\uff0c\u201d\u665a\u6674\u773c\u775b\u7741\u5927\u4e00\u4e9b\uff0c\u4e0d\u8ba9\u5f20\u606a\u8bf4\u4e0b\u53bb\uff0c\u201c\u6d77\u88d5\u80fd\u8d70\u5230\u8fd9\u4e00\u8d70\uff0c\u5df2\u7ecf\u8ba9\u6211\u5f88\u6ee1\u8db3\u4e86\uff0c\u4e0d\u80fd\u518d\u5360\u4f60\u7684\u4fbf\u5b9c\u3002\u201d</p><p>\u3000\u3000\u201c\u7ed9\u522b\u4eba\u5360\u4fbf\u5b9c\u90a3\u5c31\u7b97\u4e86\uff0c\u7ed9\u665a\u6674\u5360\u4fbf\u5b9c\uff0c\u6211\u4e50\u610f\u2026\u2026\u201d\u5f20\u606a\u7b11\u7740\u8bf4\u3002</p><p>\u3000\u3000\u201c\u4e50\u610f\u4ec0\u4e48\uff0c\u8c01\u7231\u5360\u4f60\u7684\u4fbf\u5b9c\uff1f\u201d\u665a\u6674\u6a2a\u4e86\u4ed6\u4e00\u773c\uff0c\u9ed1\u8272\u6df1\u9083\u7684\u7738\u5b50\u5012\u662f\u5a07\u67d4\u7684\u6e29\u67d4\u3002</p><p>\u3000\u3000\u5f20\u606a\u4f38\u4e86\u4f38\u61d2\u8170\uff0c\u8d64\u811a\u5750\u4e86\u8d77\u6765\uff0c\u770b\u7740\u7a97\u5916\u6df1\u6c89\u7684\u591c\u8272\uff0c\u8f7b\u58f0\u7684\u8bf4\uff1a\u201c\u7b49\u7740\u660e\u5929\u5427\uff0c\u8c01\u4e5f\u6599\u60f3\u4e0d\u5230\u7684\u660e\u5929\u2026\u2026\u201d</p><p>\u3000\u3000\u770b\u7740\u5f20\u606a\u5353\u5c14\u4e0d\u7fa4\u7684\u80cc\u5f71\uff0c\u4fee\u957f\u633a\u62d4\uff0c\u6574\u4e2a\u591c\u665a\u6084\u7136\u65e0\u58f0\uff0c\u8fd9\u662f\u4e00\u4e2a\u5438\u5f15\u6210\u719f\u5973\u4eba\u76ee\u5149\u7684\u80cc\u5f71\uff0c\u53ef\u4ee5\u8ba9\u4e00\u4e2a\u6210\u719f\u5973\u4eba\u4f9d\u9760\u7684\u80cc\u5f71\uff0c\u8c22\u665a\u6674\u7684\u773c\u795e\u6709\u4e9b\u8ff7\u79bb\uff0c\u90a3\u50cf\u51b0\u5c01\u8d77\u6765\u7684\u5fc3\u6e10\u6e10\u878d\u5316\uff0c\u4e00\u4e2a\u6210\u719f\u5973\u4eba\u7684\u5185\u5fc3\u5e76\u4e0d\u6c38\u8fdc\u90fd\u662f\u7a7a\u6d1e\u7684\u3001\u82cd\u767d\u7684\u3002</p><p>\u3000\u3000\u8c22\u665a\u6674\u611f\u89c9\u81ea\u5df1\u7684\u773c\u89d2\u6709\u4e9b\u6e7f\u6da6\uff0c\u60f3\u54ed\uff0c\u5934\u57cb\u5230\u819d\u76d6\u4e0b\uff0c\u72e0\u72e0\u7684\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u8981\u8ba9\u81ea\u5df1\u575a\u5f3a\u8d77\u6765\uff0c\u51b0\u51b7\u8d77\u6765\uff0c\u4f46\u662f\u770b\u5230\u5f20\u606a\u8f6c\u8eab\u671b\u6765\u7684\u9ed1\u8272\u7738\u5b50\uff0c\u90a3\u77ac\u95f4\u7684\u51b0\u51b7\u53c8\u5728\u878d\u5316\uff0c\u5fc3\u91cc\u6e29\u51c9\u7684\u6c34\u5728\u8f7b\u8f7b\u7684\u6d41\u6dcc\u3002</p><p>\u3000\u3000\u201c\u554a\uff0c\u90fd\u8fd9\u4e48\u665a\u4e86\uff0c\u6211\u8be5\u8d70\u4e86\uff0c\u6211\u4eec\u4e00\u76f4\u804a\u5929\uff0c\u5bb3\u5f97\u4fdd\u59c6\u8ddf\u6d77\u9f99\u90fd\u4e0d\u5f97\u4f11\u606f\uff0c\u4f60\u4e5f\u8be5\u597d\u597d\u4f11\u606f\u4e86\u3002\u201d</p><p>\u3000\u3000\u8c22\u665a\u6674\u7ad9\u8d77\u8eab\u6765\uff0c\u6ca1\u6709\u53bb\u7a7f\u62d6\u978b\uff0c\u4e5f\u5b66\u5f20\u606a\u90a3\u6837\u8d64\u8db3\u7ad9\u5728\u5730\u677f\u4e0a\uff1a\u201c\u6211\u9001\u4f60\u4e0b\u53bb\u3002\u201d</p><p>\u3000\u3000\u201c\u5ba2\u5385\u91cc\u7684\u5927\u7406\u77f3\u6709\u4e9b\u51b0\uff0c\u201d\u5f20\u606a\u4fef\u8eab\u5c06\u62d6\u978b\u5e2e\u665a\u6674\u62ff\u6765\uff0c\u8c22\u665a\u6674\u5374\u4f3c\u9b3c\u4f7f\u795e\u5dee\u4f3c\u62ac\u8d77\u5de6\u811a\uff0c\u5f20\u606a\u8f7b\u8f7b\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u53ef\u5c31\u662f\u5360\u6211\u4fbf\u5b9c\u4e86\u3002\u201d\u624b\u6367\u8d77\u5979\u7684\u5de6\u811a\uff0c\u5e2e\u5979\u5c06\u62d6\u978b\u7a7f\u4e0a\uff0c\u90a3\u4e00\u53ea\u5a07\u5de7\u7ec6\u8db3\uff0c\u65e0\u9aa8\u822c\u7684\u8174\u6da6\u3002\u90a3\u624b\u6307\u6ed1\u8fc7\u7684\u9165\u9ebb\u5feb\u611f\u4eff\u4f5b\u7535\u6d41\u4e00\u8d77\u7a7f\u8fc7\u5fc3\u810f\uff0c\u665a\u6674\u7a81\u7136\u60ca\u9192\uff0c\u5fd9\u5750\u56de\u6c99\u53d1\uff0c\u60ca\u614c\u5931\u63aa\u7684\u8bf4\uff1a\u201c\u4f60\u778e\u5f04\u4ec0\u4e48\uff1f\u201d\u8138\u988a\u5fae\u7ea2\uff0c\u4e0d\u6562\u53bb\u770b\u5f20\u606a\uff0c\u62a2\u8fc7\u90a3\u53ea\u978b\u5b50\uff0c\u53c8\u7a81\u7136\u4e27\u6c14\u7684\u8bf4\uff0c\u201c\u6211\u4e0d\u9001\u4f60\uff0c\u4f60\u81ea\u5df1\u4e0b\u53bb\u5427\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u8f7b\u8f7b\u7b11\u4e86\u7b11\uff0c\u8f6c\u8eab\u4e0b\u4e86\u697c\uff0c\u60f3\u8d77\u90a3\u4e2a\u5348\u540e\u521d\u89c1\u7684\u60c5\u5f62\uff0c\u5979\u8fd8\u662f\u90a3\u6837\u7f8e\u4e3d\u7684\u4e00\u4e2a\u5973\u4eba\u3002</p><p>\u3000\u3000\u5fc3\u91cc\u5df2\u7ecf\u6709\u4e86\u8bb8\u601d\uff0c\u5bf9\u5510\u5a67\u4e5f\u6253\u5b9a\u6ce8\u610f\u4e0d\u653e\u624b\uff0c\u8c01\u77e5\u9053\u5c0f\u59ae\u5b50\u5fc3\u91cc\u4f1a\u600e\u4e48\u60f3\uff0c\u4e07\u4e00\u4ee5\u4e3a\u81ea\u5df1\u662f\u6b3a\u9a97\u5979\u7684\u611f\u60c5\uff0c\u600e\u4e48\u529e\uff1f\u5f20\u606a\u751a\u81f3\u4e0d\u77e5\u9053\u5982\u4f55\u53bb\u9762\u5bf9\u9648\u5b81\u7684\u5b58\u5728\uff0c\u8fd8\u662f\u5f53\u5979\u4e0d\u5b58\u5728\uff1f\u54ea\u91cc\u6562\u518d\u60f9\u5176\u4ed6\u60c5\u4e8b\uff1f</p><p>\u3000\u3000\u91cd\u65b0\u56de\u5230\u4e5d\u56db\u5e74\uff0c\u5c31\u662f\u8fd9\u70b9\u4e0d\u597d\uff0c\u81ea\u5df1\u5185\u5fc3\u7684\u60c5\u611f\u90fd\u4ed4\u7ec6\u7684\u5256\u6790\u8fc7\uff0c\u5728\u7b2c\u4e00\u6b21\u7684\u4eba\u751f\u91cc\uff0c\u6216\u8bb8\u80fd\u575a\u5b9a\u8ba4\u4e3a\u4e00\u4e2a\u4eba\u540c\u65f6\u53ea\u53ef\u80fd\u7231\u4e0a\u4e00\u4e2a\u5973\u4eba\uff0c\u4f46\u662f\u91cd\u6d3b\u4e00\u56de\u5c31\u5b8c\u5168\u4e0d\u662f\u90a3\u4e48\u4e00\u56de\u4e8b\uff0c\u4e0d\u53ef\u80fd\u518d\u65e0\u6240\u8c13\u7684\u653e\u5f03\u8c01\u6216\u4f24\u5bb3\u8c01\uff0c\u7136\u800c\u591a\u60c5\u6216\u8bb8\u662f\u8584\u60c5\u3001\u65e0\u60c5\uff0c\u8c01\u77e5\u9053\u5462\uff0c\u53c8\u4e0d\u662f\u8c01\u90fd\u80fd\u91cd\u65b0\u6d3b\u4e00\u56de\u3002</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u6709\u770b\u5230\u665a\u6674\u5a07\u8f6f\u65e0\u529b\u7684\u5750\u5728\u6c99\u53d1\u3001\u773c\u775b\u6f6e\u6da6\u7684\u8bf1\u4eba\u6837\u5b50\uff0c\u4ed6\u603b\u8981\u5c06\u8fd9\u4e9b\u70e6\u607c\u629b\u5728\u8111\u540e\u624d\u4e0b\u697c\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4e5d\u5341\u5e74\u4ee3\u4e2d\u671f\uff0c\u6bcf\u5e74\u4e0a\u7f34\u56fd\u5bb6\u7a0e\u8d39\u8d85\u4ebf\u5143\u7684\u6c11\u8425\u4f01\u4e1a\u5c48\u6307\u53ef\u6570\uff0c\u6bd4\u8d77\u9526\u57ce\u96c6\u56e2\uff0c\u6b63\u6cf0\u7684\u516c\u4f17\u5f62\u8c61\u8981\u6b63\u9762\u5f97\u591a\uff0c\u8c22\u6c49\u9756\u9891\u9891\u5728\u5a92\u4f53\u4e0a\u9732\u8138\uff0c\u62ff\u4ed6\u7684\u8bdd\u6765\u8bf4\uff0c\u4ed6\u7684\u4f5c\u79c0\uff0c\u82e5\u80fd\u589e\u52a0\u4f01\u4e1a\u77e5\u540d\u5ea6\uff0c\u4f55\u4e50\u4e0d\u4e3a\uff1f</p><p>\u3000\u3000\u8d75\u9526\u8363\u5219\u4e0d\u559c\u6b22\u5728\u5a92\u4f53\u524d\u9732\u8138\uff0c\u5373\u4f7f\u6709\u9526\u57ce\u96c6\u56e2\u7684\u62a5\u9053\uff0c\u4e5f\u6709\u5f88\u591a\u8d1f\u9762\u7684\u6d88\u606f\u3002</p><p>\u3000\u3000\u4e0d\u9700\u8981\u8c22\u6c49\u9756\u544a\u8beb\u4ec0\u4e48\uff0c\u8d75\u9526\u8363\u5fc3\u91cc\u80fd\u660e\u767d\u8981\u5236\u6b62\u4e8b\u60c5\u7ee7\u7eed\u6076\u5316\u4e0b\u53bb\uff0c\u5c31\u662f\u5411\u5468\u5bcc\u660e\u4f4e\u5934\uff0c\u6bd5\u7adf\u5728\u8fd9\u6837\u7684\u5c40\u52bf\u4e0b\uff0c\u53ea\u6709\u5468\u5bcc\u660e\u5bf9\u53f8\u6cd5\u673a\u5173\u6709\u8db3\u591f\u7684\u5f71\u54cd\u529b\uff0c\u6700\u4e3b\u8981\u7684\uff0c\u5728\u6d77\u5dde\u624e\u4e0b\u7684\u6839\u57fa\u4e0d\u80fd\u8f7b\u6613\u7684\u5c31\u6bc1\u6389\u3002</p><p>\u3000\u3000\u5173\u952e\u662f\u5e02\u91cc\u7684\u95ee\u9898\u6bd4\u653f\u5b98\u5173\u7cfb\u8981\u590d\u6742\u5f97\u591a\uff0c\u4e07\u5411\u524d\u4eca\u5929\u5df2\u7ecf\u63d0\u524d\u8fd4\u56de\u7701\u57ce\u4e86\uff0c\u6ca1\u6709\u8ddf\u5e02\u91cc\u5176\u4ed6\u4eba\u6253\u62db\u547c\uff0c\u4ed6\u8d70\u4e4b\u524d\u8ddf\u8d75\u9526\u8363\u8bf4\u8d77\u8fc7\u4e07\u52c7\u7684\u51fa\u8def\uff1a\u201c\u8c03\u8d70\uff0c\u53ea\u6709\u8c03\u8d70\uff0c\u624d\u80fd\u5c06\u8fd9\u76d8\u68cb\u91cd\u65b0\u8d70\u6d3b\uff0c\u4e24\u4e2a\u6df7\u8d26\u5c0f\u5b50\uff0c\u8ba9\u4ed6\u4eec\u53d7\u70b9\u6559\u8bad\u4e5f\u597d\uff0c\u591a\u5c11\u5e74\u7684\u57fa\u4e1a\u5dee\u70b9\u90fd\u6bc1\u5728\u4ed6\u4eec\u624b\u91cc\u2026\u2026\u201d</p><p>\u3000\u3000\u5c31\u7b97\u5411\u5468\u5bcc\u660e\u5c48\u670d\uff0c\u4e07\u52c7\u5df2\u7ecf\u4e0d\u53ef\u80fd\u518d\u6309\u7167\u8bbe\u8ba1\u7684\u8f68\u9053\u6324\u8fdb\u5e38\u59d4\uff0c\u5468\u5bcc\u660e\u4e00\u9000\uff0c\u5510\u5b66\u8c26\u4e0a\u4f4d\uff0c\u4e07\u52c7\u5c31\u6c38\u8fdc\u6ca1\u6709\u4e0a\u5347\u7684\u673a\u4f1a\uff0c\u5bf9\u8fd9\u70b9\uff0c\u4e07\u5411\u524d\u770b\u5f97\u5f88\u900f\uff1a\u201c\u5510\u5b66\u8c26\u90a3\u662f\u53e3\u871c\u8179\u5251\uff0c\u4ed6\u8ddf\u6211\u4eec\u4e0d\u662f\u4e00\u8def\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u8c22\u6c49\u9756\u6ca1\u80fd\u89c1\u5230\u4e07\u5411\u524d\u4e00\u9762\uff0c\u6709\u4e9b\u9057\u61be\uff0c\u4e07\u8d75\u4e24\u5bb6\u4f3c\u4e4e\u6293\u4f4f\u89e3\u51b3\u95ee\u9898\u7684\u5173\u7a8d\uff0c\u4ed6\u5c31\u6ca1\u6709\u518d\u9976\u820c\u3002\u4ed6\u6b64\u884c\u662f\u5e2e\u8c22\u5251\u5357\u4ece\u8d75\u9526\u8363\u8fd9\u91cc\u6253\u542c\u76db\u946b\u7684\u52a8\u5411\uff0c\u76db\u946b\u4e0e\u9526\u57ce\u7b7e\u7f72\u5408\u4f5c\u534f\u8bae\uff0c\u751a\u81f3\u8fd8\u5728\u6d77\u5dde\u7559\u4e0b\u4e00\u4e2a\u7b79\u5907\u6c99\u7530\u5bb6\u7535\u5927\u5356\u573a\u7684\u5c0f\u7ec4\uff0c\u80fd\u77e5\u9053\u76db\u946b\u4e0e\u9526\u57ce\u6846\u67b6\u534f\u8bae\u7684\u5177\u4f53\u5185\u5bb9\uff0c\u5bf9\u4e86\u89e3\u76db\u946b\u7684\u52a8\u5411\u6709\u7740\u5f88\u5927\u7684\u5e2e\u52a9\u3002</p><p>\u3000\u3000\u9526\u57ce\u5730\u4ea7\u5728\u6c99\u7530\u8857\u5e02\u5f00\u53d1\u90a3\u5e62\u5546\u4e1a\u697c\u76d8\u662f\u6d77\u5dde\u5e02\u7b2c\u4e00\u9ad8\u697c\uff0c\u4e09\u5c42\u88d9\u697c\u7684\u603b\u9762\u79ef\u5c31\u8fbe\u5230\u4e00\u4e07\u591a\u5e73\u65b9\u7c73\uff0c\u5b8c\u5168\u5efa\u6210\u6700\u5feb\u4e5f\u8981\u4e00\u5e74\u534a\u7684\u65f6\u95f4\uff0c\u76db\u946b\u6b64\u65f6\u5c31\u786e\u5b9a\u4e0b\u5408\u4f5c\u610f\u5411\uff0c\u53ef\u89c1\u76db\u946b\u7684\u51b3\u5b9a\u8fd8\u771f\u4e0d\u662f\u4e00\u822c\u7684\u5927\u3002</p><p>\u3000\u3000\u8c22\u6c49\u9756\u5bf9\u8c22\u5251\u5357\u8bf4\uff1a\u201c\u770b\u6765\u5bf9\u8fd9\u884c\u4e1a\u6709\u4fe1\u5fc3\u7684\uff0c\u534e\u4e1c\u4e0d\u53ea\u4f60\u4e00\u4e2a\u4eba\u3002\u201d\u53f6\u5efa\u658c\u4e5f\u6ca1\u6709\u60f3\u5230\u4ed6\u914d\u5408\u5f20\u606a\u7ed9\u8d75\u9526\u8363\u8bbe\u4e0b\u7684\u5751\u8fd8\u4f1a\u8ff7\u60d1\u5230\u522b\u4eba\uff0c\u4e0d\u8fc7\u662f\u4fc3\u4f7f\u8c22\u5bb6\u4e00\u5f00\u59cb\u5c31\u5c06\u4ed6\u7684\u76db\u946b\u5f53\u4f5c\u5546\u4e1a\u654c\u4eba\u6765\u770b\u5f85\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u4e0d\u4ecb\u610f\u7684\u7b11\u7740\u8bf4\uff1a\u201c\u5c31\u8981\u770b\u8c01\u7684\u624b\u5feb\u4e86\uff0c\u8bf4\u4e0d\u5b9a\u4e24\u5e74\u540e\uff0c\u8fd8\u662f\u6211\u4eec\u6d77\u6cf0\u8ddf\u9526\u57ce\u5408\u4f5c\u5462\u2026\u2026\u201d</p><p>\u3000\u3000\u8c22\u5251\u5357\u7684\u8bdd\u5012\u662f\u7ed9\u4e86\u8d75\u9526\u8363\u5f88\u5f3a\u7684\u4fe1\u5fc3\uff0c\u4ed6\u7b11\u7740\u8bf4\uff1a\u201c\u4e0d\u9700\u8981\u4e24\u5e74\u3002\u5468\u5bcc\u660e\u4e00\u76f4\u8981\u6c42\u6c99\u7530\u6539\u9020\u5c3d\u5feb\u52a8\u624b\uff0c\u4ed6\u5927\u6982\u4e5f\u60f3\u5728\u4efb\u671f\u5185\u591a\u51fa\u70b9\u653f\u7ee9\uff0c\u591a\u6df7\u4e00\u5c4a\u3002\u5c31\u7b97\u8001\u4e07\u8c03\u8d70\uff0c\u6211\u770b\u5468\u5bcc\u660e\u591a\u534a\u4f1a\u4eb2\u81ea\u6293\u571f\u5730\u8fd9\u5757\u2026\u2026\u201d\u8d75\u9526\u8363\u6b64\u65f6\u5bf9\u5468\u5bcc\u660e\u80fd\u5426\u518d\u5e72\u4e00\u5c4a\u6ca1\u6709\u5f88\u5f3a\u7684\u671f\u5f85\uff0c\u65e2\u7136\u8981\u5411\u4ed6\u4f4e\u5934\uff0c\u603b\u8981\u6709\u4e9b\u5b9e\u8d28\u6027\u7684\u8868\u793a\uff0c\u73b0\u5728\u9001\u94b1\uff0c\u4ed6\u4e00\u5b9a\u4e0d\u4f1a\u6536\uff0c\u4ed6\u4e5f\u4e0d\u662f\u4ece\u5176\u4ed6\u6e20\u9053\u635e\u4e0d\u5230\u94b1\uff0c\u90a3\u5c31\u4e3a\u4ed6\u505a\u4e9b\u5176\u4ed6\u4e8b\u60c5\u5427\u3002</p></article>"}}}]);
