(window.webpackJsonp=window.webpackJsonp||[]).push([[235],{257:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u516d\u8282 \u6218\u4fd8",content:"<article><h2>\u7b2c\u516d\u8282 \u6218\u4fd8</h2><p>\u201c\u8fd9\u8ff7\u9501\u548c\u90a3\u4e2a\u5df4\u62c9\u514b\u56fd\u738b\u6709\u5173\u3002\u201d</p><p>\u201c\u8ff7\u9501\u6548\u529b\u5df2\u7ecf\u4e25\u91cd\u524a\u5f31\u3002\u201d</p><p>\u743c\u6069\u8111\u4e2d\u7acb\u523b\u53cd\u5e94\u51fa\u8fd9\u4e24\u4e2a\u7ed3\u8bba\u6765\uff0c\u90a3\u4e48\u2026\u2026</p><p>\u4e00\u822c\u6765\u8bf4\uff0c\u8ff7\u9501\u90fd\u4f1a\u6709\u4e00\u4e2a\u201c\u6838\u5fc3\u201d\uff0c\u5927\u591a\u662f\u67d0\u79cd\u5f3a\u529b\u7684\u9b54\u6cd5\u7269\u54c1\u751a\u81f3\u795e\u5668\uff0c\u53ea\u8981\u6838\u5fc3\u4e0d\u51fa\u95ee\u9898\uff0c\u8ff7\u9501\u4e5f\u5c31\u4e00\u76f4\u6709\u6548\uff0c\u5373\u4f7f\u53d7\u635f\u4e5f\u80fd\u81ea\u884c\u4fee\u590d\uff1b\u5982\u679c\u6838\u5fc3\u6bc1\u574f\uff0c\u8ff7\u9501\u4e5f\u4f1a\u540c\u6837\u53d7\u5230\u524a\u5f31\uff0c\u751a\u81f3\u6d88\u5931\u3002\u9634\u9b42\u57ce\u8981\u8fdb\u5165\u8ff7\u65af\u5353\u8bfa\uff0c\u5c31\u5148\u60f3\u529e\u6cd5\u628a\u9ece\u660e\u4e4b\u77f3\u2014\u2014\u8ff7\u65af\u5353\u8bfa\u8ff7\u9501\u7684\u6838\u5fc3\u2014\u2014\u7ed9\u6bc1\u4e86\uff0c\u5982\u6b64\u4e00\u6765\u8ff7\u9501\u7684\u5a01\u529b\u4fbf\u4f1a\u4e25\u91cd\u524a\u5f31\uff0c\u9664\u975e\u80fd\u591f\u518d\u627e\u5230\u65b0\u7684\u8db3\u591f\u5206\u91cf\u7684\u6838\u5fc3\u6765\u4ee3\u66ff\u3002\u9634\u9b42\u57ce\u8ff7\u9501\u7684\u6838\u5fc3\u662f\u4ec0\u4e48\uff0c\u743c\u6069\u81ea\u7136\u4e0d\u6e05\u695a\uff0c\u731c\u6d4b\u8d77\u6765\u4e5f\u662f\u67d0\u79cd\u795e\u5668\u3002</p><p>\u8ff7\u9501\u6700\u5927\u7684\u4f18\u52bf\uff0c\u4e0d\u4ec5\u4ec5\u5728\u4e8e\u8d85\u5927\u8303\u56f4\u2014\u2014\u591a\u4e2a\u9b54\u6cd5\u9635\u8fde\u73af\u8054\u7ed3\u4e5f\u80fd\u6709\u8fd9\u79cd\u6548\u679c\u2014\u2014\u66f4\u5728\u4e8e\u5b83\u65e0\u6cd5\u88ab\u53cd\u5236\uff0c\u53ea\u80fd\u786c\u6297\uff0c\u5bf9\u4e8e\u653b\u51fb\u8005\u5c31\u5927\u4e3a\u4e0d\u5229\u3002\u5f53\u7136\u540c\u4e3a\u8ff7\u9501\uff0c\u4e5f\u6709\u5f3a\u5f31\u9ad8\u4f4e\u4e4b\u5206\uff0c\u4f0a\u5361\u6c99\u57ce\u7684\u8ff7\u9501\u53ea\u80fd\u9488\u5bf9\u9ed1\u6697\u7cbe\u7075\uff0c\u529f\u80fd\u8f83\u4e3a\u5355\u4e00\uff0c\u6784\u5efa\u96be\u5ea6\u6bd4\u8d77\u9634\u9b42\u57ce\u548c\u6c99\u739b\u65af\u7684\u8ff7\u9501\u81ea\u7136\u4f4e\u5f97\u591a\u3002</p><p>\u201c\u8fd9\u5ea7\u57ce\u5e02\u7684\u8ff7\u9501\u662f\u5728\u524a\u5f31\uff0c\u96be\u9053\u662f\u6838\u5fc3\u51fa\u4e86\u4ec0\u4e48\u95ee\u9898\u2026\u2026\u8fd9\u548c\u56fd\u738b\u7684\u6c89\u7761\u53c8\u6709\u4ec0\u4e48\u8054\u7cfb\uff1f\u201d</p><p>\u6b63\u6c89\u601d\u95f4\uff0c\u6218\u573a\u4e0a\u7684\u547c\u558a\u58f0\u8ba9\u4ed6\u60ca\u9192\u8fc7\u6765\uff0c\u77ee\u4eba\u5df2\u7ecf\u62b5\u6321\u4e0d\u4f4f\uff0c\u5f00\u59cb\u6253\u7b97\u64a4\u9000\uff0c\u521a\u624d\u7684\u6df7\u6218\u8ba9\u53cc\u65b9\u5404\u81ea\u635f\u5931\u4e0d\u5c0f\uff0c\u9ed1\u6697\u7cbe\u7075\u7684\u725b\u5934\u4eba\u3001\u98df\u4eba\u9b54\u548c\u5730\u7cbe\u90e8\u961f\u4f24\u4ea1\u8d85\u8fc7\u4e09\u5206\u4e4b\u4e00\uff0c\u5e94\u8be5\u5df2\u7ecf\u5f88\u96be\u7ee7\u7eed\u653b\u57ce\u4e86\u3002</p><p>\u5c31\u5728\u6b64\u65f6\uff0c\u53c8\u6709\u4e00\u652f\u8725\u8734\u9a91\u5175\u7a81\u7136\u5821\u5792\u4e2d\u6d8c\u51fa\uff0c\u4ece\u4fa7\u9762\u5feb\u901f\u5305\u6284\u8fc7\u6765\uff0c\u663e\u7136\u662f\u8981\u5207\u65ad\u9000\u8def\u3002\u4f0a\u5361\u6c99\u57ce\u5934\u4e0a\u7684\u58eb\u5175\u53d1\u73b0\u4e86\u8fd9\u70b9\uff0c\u53d1\u5c04\u5927\u91cf\u7684\u5f29\u7bad\u548c\u77f3\u5757\uff0c\u60f3\u8981\u622a\u4f4f\u4ed6\u4eec\uff0c\u4f46\u8ddd\u79bb\u592a\u8fdc\uff0c\u800c\u4e14\u8725\u8734\u9a91\u5175\u5b9e\u5728\u592a\u8fc7\u4e8e\u7075\u5de7\u8fc5\u6377\uff0c\u5f88\u5bb9\u6613\u5c31\u907f\u5f00\u4e86\u6240\u6709\u88ad\u51fb\u3002</p><p>\u743c\u6069\u638f\u51fa\u671b\u8fdc\u955c\uff0c\u7136\u540e\u60ca\u8bb6\u5730\u53d1\u73b0\u8fd9\u652f\u8725\u8734\u9a91\u5175\u7684\u7edf\u5e05\uff0c\u5c45\u7136\u662f\u4e00\u4f4d\u5c11\u5973\uff0c\u800c\u4e14\u5979\u6ca1\u6709\u548c\u5176\u4ed6\u9a91\u5175\u4e00\u6837\u7a7f\u7740\u9501\u7532\uff0c\u53cd\u800c\u662f\u4e00\u8eab\u957f\u888d\uff0c\u5916\u7f69\u6597\u7bf7\uff0c\u624b\u4e2d\u63e1\u7740\u8840\u7ea2\u8272\u7684\u957f\u67aa\uff0c\u51b2\u950b\u5728\u961f\u4f0d\u7684\u6700\u524d\u5217\u3002\u8f6c\u77ac\u4e4b\u95f4\uff0c\u5979\u5c31\u51b2\u5165\u4e86\u654c\u9635\uff0c\u9ed1\u8272\u7684\u706b\u7130\u4ece\u8eab\u4e0a\u7206\u53d1\u51fa\u6765\uff0c\u5c06\u9644\u8fd1\u7684\u51e0\u4e2a\u77ee\u4eba\u6218\u58eb\u5168\u90fd\u70e7\u7740\u4e86\u3002\u5979\u7684\u957f\u67aa\u4e0a\u7535\u5149\u95ea\u70c1\uff0c\u663e\u7136\u9644\u5e26\u4e86\u9b54\u6cd5\uff0c\u51e1\u662f\u88ab\u5b83\u523a\u4e2d\u2014\u2014\u65e0\u8bba\u662f\u523a\u4e2d\u8eab\u4f53\u8fd8\u662f\u523a\u5728\u94e0\u7532\u6216\u76fe\u724c\u4e0a\uff0c\u90a3\u4e9b\u4e0d\u5e78\u7684\u77ee\u4eba\u4eec\u90fd\u4f1a\u7acb\u523b\u5168\u8eab\u9ebb\u75f9\uff0c\u4e27\u5931\u884c\u52a8\u80fd\u529b\u3002</p><p>\u201c\u5979\u662f\u8c01\uff1f\u201d</p><p>\u201c\u7ef4\u5eb7\u5c3c\u4e9a\xb7\u83f2\u5c14\u4f26\uff0c\u201d\u65c1\u8fb9\u4e00\u4e2a\u77ee\u4eba\u6218\u58eb\u56de\u7b54\uff0c\u201c\u8fd9\u6b21\u9ed1\u6697\u7cbe\u7075\u7684\u7edf\u5e05\u3002\u201d</p><p>\u201c\u7edf\u5e05\uff1f\u5979\u592a\u5e74\u8f7b\u4e86\u5427\u3002\u201d</p><p>\u201c\u4e0d\u80fd\u4ee5\u76f8\u8c8c\u6765\u5224\u65ad\u9ed1\u6697\u7cbe\u7075\u7684\u5e74\u7eaa\uff0c\u201d\u9c81\u6587\u89e3\u91ca\uff0c\u201c\u4ed6\u4eec\u5728\u751f\u547d\u4e2d\u7684\u5927\u591a\u6570\u65f6\u95f4\u91cc\u90fd\u4f1a\u4fdd\u6301\u5e74\u8f7b\u65f6\u7684\u6a21\u6837\u3002\u4e00\u767e\u5c81\u548c\u56db\u767e\u5c81\u7684\u9ed1\u6697\u7cbe\u7075\uff0c\u4ece\u5916\u8868\u5f88\u96be\u770b\u51fa\u4ec0\u4e48\u533a\u522b\u3002\u800c\u4e14\uff0c\u83f2\u5c14\u4f26\u662f\u74dc\u7406\u5fb7\u65af\u7684\u7b2c\u4e00\u5bb6\u65cf\u3002\u201d</p><p>\u6b63\u8bf4\u8bdd\u95f4\uff0c\u6218\u573a\u4e2d\u7684\u60c5\u51b5\u53c8\u6709\u4e86\u53d8\u5316\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u52d2\u4f4f\u8725\u8734\uff0c\u4ece\u957f\u888d\u53e3\u888b\u91cc\u638f\u51fa\u4e00\u628a\u7ec6\u6c99\u6d12\u4e0b\uff0c\u7d27\u63a5\u7740\u524d\u65b9\u7684\u77ee\u4eba\u6218\u58eb\u90fd\u4eff\u4f5b\u559d\u9189\u4e86\u9152\u4f3c\u7684\uff0c\u6447\u6447\u6643\u6643\u7eb7\u7eb7\u5012\u5730\u3002\u8725\u8734\u9a91\u5175\u4eec\u4ece\u4e3b\u5e05\u4e24\u4fa7\u8d8a\u8fc7\uff0c\u4e58\u52bf\u63a9\u6740\uff0c\u4e00\u65f6\u95f4\u5c40\u52bf\u5bf9\u77ee\u4eba\u975e\u5e38\u4e0d\u5229\u3002</p><p>\u201c\u7761\u7720\u2026\u2026\u4e0d\uff0c\u662f\u5f3a\u70c8\u7761\u7720\uff0c\u7136\u800c\u8fd9\u5206\u660e\u662f\u5965\u672f\u2026\u2026\u201d</p><p>\u201c\u5979\u662f\u4e2a\u5deb\u5e08\uff1f\u201d\u743c\u6069\u95ee\u9c81\u6587\uff0c\u201c\u7136\u800c\u6211\u4e0d\u662f\u542c\u8bf4\u9ed1\u6697\u7cbe\u7075\u7684\u8d35\u65cf\u5973\u6027\u90fd\u4f1a\u6210\u4e3a\u86db\u540e\u7267\u5e08\uff1f\u201d</p><p>\u201c\u5979\u662f\u4e2a\u7267\u5e08\uff0c\u4f60\u770b\u5979\u8170\u4e0a\u6302\u7684\u86c7\u9996\u97ad\uff0c\u201d\u9c81\u6587\u56de\u7b54\uff0c\u201c\u4f46\u542c\u8bf4\u5979\u4e5f\u5b66\u4e60\u8fc7\u5deb\u5e08\u7684\u6280\u5de7\u3002\u201d</p><p>\u743c\u6069\u5514\u4e86\u4e00\u58f0\uff0c\u4ece\u6597\u7bf7\u5185\u4fa7\u53d6\u51fa\u6b27\u51ef\u9001\u4ed6\u7684\u7d2b\u6c34\u6676\u6cd5\u6756\u6765\uff0c\u201c\u5982\u679c\u80fd\u6293\u5230\u5979\uff0c\u90a3\u4e48\u9ed1\u6697\u7cbe\u7075\u5e94\u8be5\u4f1a\u64a4\u9000\u5427\u3002\u201d</p><p>\u201c\u4f60\u8981\u5e72\u4ec0\u4e48\uff1f\u201d\u9c81\u6587\u5413\u4e86\u4e00\u8df3\u3002</p><p>\u201c\u9ed1\u6697\u7cbe\u7075\u4ee5\u5929\u8d4b\u7684\u6297\u9b54\u80fd\u529b\u7740\u79f0\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u5c31\u7b97\u662f\u4e00\u6d41\u7684\u5deb\u5e08\uff0c\u6cd5\u672f\u4e5f\u53ef\u80fd\u4f1a\u5bf9\u4ed6\u4eec\u5b8c\u5168\u65e0\u6548\u3002\u201d</p><p>\u201c\u6211\u77e5\u9053\u3002\u201d\u743c\u6069\u8bf4\uff0c\u4e00\u8fb9\u7ed9\u81ea\u5df1\u65bd\u52a0\u4e86\u4e00\u4e2a\u77f3\u80a4\u672f\uff0c\u4ed6\u6574\u4e2a\u4eba\u5239\u90a3\u95f4\u53d8\u6210\u4e86\u6697\u7070\u8272\uff0c\u4eff\u4f5b\u6d3b\u52a8\u7684\u77f3\u50cf\u3002</p><p>\u201c\u6211\u548c\u4f60\u4e00\u8d77\u53bb\u3002\u201d</p><p>\u743c\u6069\u6447\u5934\uff0c\u201c\u6211\u4eca\u5929\u6ca1\u6709\u51c6\u5907\u4e24\u4e2a\u4f20\u9001\u672f\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u6211\u9700\u8981\u4f60\u5728\u8fd9\u91cc\uff0c\u5e2e\u6211\u4f20\u9001\u56de\u6765\u3002\u201d</p><p>\u4ed6\u5c06\u624b\u4e2d\u7684\u671b\u8fdc\u955c\u9012\u7ed9\u6885\u83f2\u65af\uff0c\u51dd\u89c6\u7740\u573a\u5185\uff0c\u96c6\u4e2d\u7cbe\u529b\u786e\u5b9a\u65b9\u4f4d\uff0c\u7b49\u5f85\u7740\u5408\u9002\u7684\u65f6\u673a\uff0c\u7136\u540e\u8de8\u51fa\u4e00\u6b65\u3002\u4e0b\u4e00\u77ac\u95f4\uff0c\u743c\u6069\u534a\u8dea\u7740\u51fa\u73b0\u5728\u7ef4\u5eb7\u5c3c\u4e9a\u7684\u8eab\u540e\uff0c\u4f38\u624b\u91cd\u91cd\u5f80\u5730\u9762\u4e0a\u4e00\u62cd\u3002\u4ee5\u4ed6\u81ea\u5df1\u4e3a\u5706\u5fc3\uff0c\u534a\u5f84\u5341\u82f1\u5c3a\u5185\u7684\u77f3\u5934\u5730\u9762\u5267\u70c8\u6447\u6643\u8d77\u6765\uff0c\u7d27\u63a5\u7740\u9aa4\u7136\u5347\u9ad8\uff0c\u786c\u751f\u751f\u62c9\u51fa\u4e00\u4e2a\u77f3\u67f1\u6765\uff0c\u743c\u6069\u548c\u7ef4\u5eb7\u5c3c\u4e9a\u6b63\u5728\u9876\u7aef\u3002</p><p>\u5728\u4ee5\u5f80\u7684\u65e0\u6570\u6b21\u4ea4\u6218\u4e2d\uff0c\u9ed1\u6697\u7cbe\u7075\u65e9\u5df2\u6478\u6e05\u4f0a\u5361\u6c99\u57ce\u7684\u5e95\u7ec6\uff0c\u867d\u7136\u6709\u4e00\u4e2a\u6765\u5386\u4e0d\u660e\u800c\u4e14\u4e13\u95e8\u9488\u5bf9\u9ed1\u6697\u7cbe\u7075\u8ff7\u9501\uff0c\u57ce\u4e2d\u5374\u4ece\u6765\u6ca1\u6709\u8fc7\u5deb\u5e08\u53c2\u6218\u7684\u8bb0\u5f55\uff0c\u6240\u4ee5\u4e5f\u538b\u6839\u4e0d\u66fe\u9632\u5907\u3002\u743c\u6069\u7a81\u5982\u5176\u6765\u8de8\u8d8a\u6218\u573a\u8fd1\u8eab\u5077\u88ad\uff0c\u51fa\u4e4e\u6240\u6709\u4eba\u2014\u2014\u5305\u62ec\u77ee\u4eba\u2014\u2014\u7684\u610f\u6599\u4e4b\u5916\u3002</p><p>\u9aa4\u7136\u62c9\u5347\u7684\u77f3\u67f1\u5e76\u4e0d\u5f88\u9ad8\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u6b63\u9a91\u5728\u516b\u722a\u8725\u8734\u4e0a\uff0c\u8fd9\u79cd\u751f\u7269\u5177\u6709\u826f\u597d\u7684\u5f39\u8df3\u80fd\u529b\uff0c\u5c31\u7b97\u76f4\u63a5\u8dc3\u4e0b\u4e5f\u4e0d\u4f1a\u6454\u4f24\u3002\u7136\u800c\u5979\u505a\u4e86\u4e00\u4e2a\u9519\u8bef\u7684\u5224\u65ad\uff0c\u5e76\u6ca1\u6709\u5f53\u5373\u8df3\u4e0b\uff0c\u800c\u662f\u53cd\u624b\u4e00\u67aa\u671d\u743c\u6069\u523a\u6765\u3002</p><p>\u5979\u53cd\u5e94\u5f88\u5feb\uff0c\u800c\u4e14\u5224\u65ad\u4e5f\u5f88\u51c6\u786e\uff0c\u80fd\u591f\u77ac\u95f4\u79fb\u52a8\u5230\u8eab\u8fb9\uff0c\u5e76\u4e14\u7528\u5851\u77f3\u672f\u62c9\u9ad8\u5730\u9762\u7684\uff0c\u663e\u7136\u662f\u5deb\u5e08\u624d\u80fd\u529e\u5230\u3002\u800c\u548c\u5deb\u5e08\u4ea4\u624b\uff0c\u6700\u8981\u7d27\u7684\u5c31\u662f\u538b\u7f29\u8ddd\u79bb\u2014\u2014\u6240\u6709\u4eba\u90fd\u77e5\u9053\uff0c\u5deb\u5e08\u7684\u683c\u8a00\u662f\u201c\u8ddd\u79bb\u5373\u5b89\u5168\u201d\u3002\u53ea\u8981\u6709\u8db3\u591f\u7684\u8ddd\u79bb\uff0c\u5deb\u5e08\u5c31\u6709\u5145\u8db3\u7684\u65bd\u6cd5\u65f6\u95f4\uff0c\u4ed6\u4eec\u80fd\u591f\u521b\u9020\u51fa\u4ee4\u4eba\u610f\u60f3\u4e0d\u5230\u7684\u5947\u8ff9\uff0c\u4f46\u5018\u82e5\u88ab\u8fd1\u8eab\u903c\u8feb\uff0c\u90a3\u4e48\u5c31\u7b97\u662f\u9ad8\u660e\u7684\u5deb\u5e08\u4e5f\u5f88\u53ef\u80fd\u4f1a\u88ab\u5c0f\u6d41\u6c13\u7684\u5315\u9996\u6345\u6b7b\uff0c\u5386\u53f2\u4e0a\u662f\u66fe\u7ecf\u53d1\u751f\u8fc7\u8fd9\u79cd\u5148\u4f8b\u7684\u3002</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u81ea\u5df1\u4e5f\u7814\u4e60\u5965\u672f\uff0c\u7d20\u6765\u559c\u6b22\u4ee5\u5deb\u5e08\u81ea\u5c45\uff0c\u5bf9\u6b64\u81ea\u7136\u662f\u975e\u5e38\u4e86\u89e3\u3002\u4e00\u5bdf\u89c9\u88ab\u5deb\u5e08\u903c\u8fd1\uff0c\u773c\u89d2\u4f59\u5149\u77a5\u89c1\u8eab\u5f71\uff0c\u4e0d\u5047\u601d\u7d22\u5c31\u662f\u53cd\u624b\u4e00\u67aa\u523a\u51fa\uff0c\u7136\u800c\u5979\u4f4e\u4f30\u4e86\u743c\u6069\u7684\u65bd\u6cd5\u901f\u5ea6\u3002\u867d\u7136\u4ee5\u9020\u8be3\u800c\u8bba\uff0c\u743c\u6069\u4f9d\u65e7\u53ea\u662f\u521a\u521a\u8de8\u5165\u9ad8\u9636\u5deb\u5e08\u7684\u884c\u5217\uff0c\u4f46\u8bba\u53ca\u65bd\u6cd5\u901f\u5ea6\uff0c\u5374\u5df2\u7ecf\u8db3\u4ee5\u548c\u7b2c\u4e00\u6d41\u7684\u5927\u5deb\u5e08\u76f8\u63d0\u5e76\u8bba\u4e86\u3002</p><p>\u56e0\u4e3a\u4ed6\u5b8c\u5168\u65e0\u9700\u8bf5\u5492\uff0c\u65e0\u9700\u6bd4\u5212\u59ff\u52bf\uff0c\u65e0\u9700\u53d6\u65bd\u6cd5\u6750\u6599\u3002</p><p>\u5965\u6c83\u4f20\u6388\u7684\u6280\u5de7\uff0c\u867d\u7136\u8ba9\u4ed6\u6bcf\u5929\u51c6\u5907\u6cd5\u672f\u7684\u65f6\u95f4\u591a\u51fa\u4e24\u500d\uff08\u666e\u901a\u5deb\u5e08\u7528\u4e00\u5c0f\u65f6\u5c31\u80fd\u51c6\u5907\u5b8c\u6210\u6240\u6709\u6cd5\u672f\uff0c\u743c\u6069\u9700\u8981\u4e09\u4e2a\u5c0f\u65f6\uff09\uff0c\u4f46\u53cd\u6b63\u4ed6\u539f\u672c\u5c31\u7761\u7720\u6bd4\u5e38\u4eba\u5c11\uff0c\u6240\u4ee5\u4e5f\u5e76\u4e0d\u7b97\u591a\u5927\u9ebb\u70e6\uff0c\u800c\u5b83\u6240\u5e26\u6765\u7684\u597d\u5904\u5219\u662f\u65e0\u6cd5\u4f30\u91cf\u7684\u3002</p><p>\u957f\u67aa\u5c1a\u672a\u523a\u5230\uff0c\u743c\u6069\u5df2\u7ecf\u5b8c\u6210\u4e86\u7b2c\u4e8c\u4e2a\u6cd5\u672f\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u8eab\u4e0b\u7684\u8725\u8734\u9661\u7136\u50cf\u5439\u6c14\u4e00\u6837\u81a8\u80c0\u8d77\u6765\uff0c\u53d8\u6210\u4e86\u4e00\u4e2a\u5927\u5706\u7403\u3002\u9ed1\u6697\u7cbe\u7075\u786e\u5b9e\u62e5\u6709\u4f18\u79c0\u7684\u6297\u9b54\u80fd\u529b\uff0c\u4f46\u4ed6\u4eec\u7684\u8725\u8734\u5374\u5e76\u4e0d\u540c\u6837\u5177\u5907\u3002\u7ef4\u5eb7\u5c3c\u4e9a\u88ab\u6380\u7ffb\u5728\u5730\uff0c\u5728\u6454\u5012\u7684\u4e00\u77ac\u95f4\u5979\u63b7\u51fa\u4e86\u957f\u67aa\uff0c\u867d\u7136\u5931\u4e86\u51c6\u5934\uff0c\u4f9d\u65e7\u8fd8\u662f\u51fb\u4e2d\u4e86\u743c\u6069\u7684\u80a9\u8180\u3002\u77f3\u5c51\u7eb7\u98de\u4e2d\uff0c\u5deb\u5e08\u9000\u540e\u4e24\u6b65\uff0c\u5e76\u6ca1\u6709\u53d7\u4f24\u3002</p><p>\u4ed6\u63e1\u7d27\u6cd5\u6756\uff0c\u516d\u9897\u7eef\u7ea2\u4e4b\u6cea\u5b9d\u77f3\u540c\u65f6\u660e\u4eae\u8d77\u6765\uff0c\u53d1\u51fa\u523a\u773c\u7684\u7075\u5149\uff0c\u5374\u5e76\u6ca1\u6709\u7acb\u523b\u91ca\u653e\u51fa\u6cd5\u672f\uff0c\u4eff\u4f5b\u662f\u5728\u72b9\u8c6b\u6216\u8005\u7b49\u5f85\u3002\u7ef4\u5eb7\u5c3c\u4e9a\u6ca1\u6709\u653e\u8fc7\u8fd9\u4e2a\u673a\u4f1a\uff0c\u5979\u7ffb\u8eab\u722c\u8d77\uff0c\u624b\u8098\u6491\u5730\uff0c\u9ed1\u66dc\u77f3\u822c\u7684\u4fee\u957f\u624b\u6307\u6025\u901f\u5728\u7a7a\u6c14\u4e2d\u5212\u51fa\u4e00\u4e2a\u7ec6\u5fae\u7b26\u53f7\uff0c\u5bf9\u51c6\u743c\u6069\u4e00\u6307\uff0c\u201c\u5b9a\u8eab\uff01\u201d</p><p>\u9ed1\u6697\u7cbe\u7075\u7684\u5bff\u547d\u8fdc\u8fdc\u80dc\u8fc7\u4eba\u7c7b\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u867d\u7136\u8fd8\u53ea\u80fd\u7b97\u662f\u5c11\u5973\uff0c\u4f46\u4e5f\u5df2\u7ecf\u5c06\u8fd1\u767e\u5c81\u3002\u5728\u5979\u8fc7\u53bb\u7684\u5c81\u6708\u91cc\uff0c\u4e0d\u4f46\u63a5\u53d7\u4e86\u4e25\u683c\u7684\u7267\u5e08\u8bad\u7ec3\uff0c\u540c\u65f6\u8fd8\u5077\u5077\u82b1\u8d39\u4e86\u5927\u91cf\u7684\u65f6\u95f4\u5728\u5965\u672f\u4e0a\uff0c\u5c24\u5176\u559c\u7231\u64c5\u957f\u5fc3\u667a\u64cd\u63a7\u7684\u9644\u9b54\u7cfb\u3002\u5979\u4e5f\u786e\u5b9e\u5f88\u6709\u8fd9\u65b9\u9762\u7684\u5929\u8d4b\uff0c\u65bd\u5c55\u51fa\u6765\u7684\u6cd5\u672f\u8fde\u9ed1\u6697\u7cbe\u7075\u90fd\u5f88\u96be\u62b5\u6321\uff0c\u66f4\u522b\u8bf4\u5bf9\u624b\u662f\u4e2a\u4eba\u7c7b\u3002</p><p>\u6240\u4ee5\u5979\u5f88\u6709\u4fe1\u5fc3\u3002</p><p>\u5728\u7ef4\u5eb7\u5c3c\u4e9a\u65bd\u6cd5\u7684\u540c\u65f6\uff0c\u743c\u6069\u6084\u6084\u542f\u52a8\u4e86\u6cd5\u672f\u9006\u8f6c\u6212\u6307\uff0c\u540c\u65f6\u6cd5\u6756\u4e0a\u516d\u9897\u7eef\u7ea2\u4e4b\u6cea\u53d1\u51fa\u7684\u7075\u5149\u8054\u6210\u4e00\u7ebf\uff0c\u6bb7\u7ea2\u5982\u8840\uff0c\u5c06\u4ed6\u534a\u4e2a\u624b\u81c2\u90fd\u7b3c\u7f69\u5176\u4e2d\u3002</p><p>\u201c\u6cd5\u672f\u589e\u5e45\uff08Spell Enhancer\uff09\uff01\u201d</p><p>\u5f53\u65e5\u4e34\u522b\u4e4b\u524d\uff0c\u5965\u6c83\u66fe\u7ecf\u5728\u4ed6\u7684\u9b54\u6cd5\u4e66\u4e0a\u6284\u5f55\u4e86\u51e0\u4e2a\u7279\u522b\u7684\u6cd5\u672f\uff0c\u975e\u5e38\u7684\u504f\u50fb\u7f55\u89c1\uff0c\u743c\u6069\u8fde\u540d\u5b57\u90fd\u6ca1\u542c\u8fc7\uff0c\u4e0d\u77e5\u9053\u662f\u8001\u5deb\u5996\u81ea\u5df1\u53d1\u660e\u8fd8\u662f\u6614\u65e5\u8010\u745f\u5e1d\u56fd\u5931\u4f20\u7684\u9057\u4ea7\uff0c\u8fd9\u6cd5\u672f\u589e\u5e45\u4fbf\u662f\u5176\u4e2d\u4e4b\u4e00\u3002\u5b83\u4e0d\u80fd\u4ea7\u751f\u4efb\u4f55\u76f4\u63a5\u7684\u653b\u51fb\u548c\u9632\u5fa1\u529b\uff0c\u4f46\u5374\u80fd\u548c\u5176\u4ed6\u6cd5\u672f\u914d\u5408\u4f7f\u7528\uff0c\u6781\u5ea6\u5f3a\u5316\u5176\u5a01\u529b\u3002</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u7684\u5b9a\u8eab\u672f\u649e\u4e0a\u743c\u6069\uff0c\u968f\u5373\u88ab\u65e0\u5f62\u7684\u5c4f\u969c\u53cd\u5f39\u56de\u6765\uff0c\u4e34\u65f6\u589e\u5e45\u7684\u6cd5\u672f\u8f70\u7136\u900f\u5165\u9ed1\u6697\u7cbe\u7075\u7684\u8eab\u4f53\uff0c\u8ba9\u5979\u77ac\u95f4\u50f5\u76f4\u9ebb\u75f9\u3002\u743c\u6069\u62a2\u6b65\u4e0a\u524d\uff0c\u6293\u4f4f\u5979\u7684\u80f3\u818a\uff0c\u51e0\u4e2a\u9ed1\u6697\u7cbe\u7075\u5df2\u7ecf\u8df3\u4e0b\u8725\u8734\uff0c\u722c\u4e0a\u77f3\u67f1\uff0c\u6325\u821e\u957f\u67aa\u523a\u6765\uff0c\u4f46\u4ed6\u4eec\u7ec8\u7a76\u8fd8\u662f\u8fdf\u4e86\u4e00\u6b65\u3002</p><p>\u8f7b\u5fae\u7684\u7a7a\u6c14\u6ce2\u6298\uff0c\u743c\u6069\u5e26\u7740\u4fd8\u864f\u4ece\u773c\u524d\u6d88\u5931\uff0c\u5728\u4e0b\u4e00\u77ac\u95f4\uff0c\u4ed6\u51fa\u73b0\u5728\u6885\u83f2\u65af\u8eab\u65c1\u3002</p></article>"}}}]);
