(window.webpackJsonp=window.webpackJsonp||[]).push([[236],{259:function(p){p.exports={chapterTitle:null,title:"\u7b2c236\u7ae0 \u52bf\u529b",content:"<article><h2>\u7b2c236\u7ae0 \u52bf\u529b</h2><p>\u3000\u3000\u5468\u5bcc\u660e\u90fd\u4e0d\u4f1a\u5728\u9152\u4f1a\u4e0a\u803d\u6401\u592a\u591a\u7684\u65f6\u95f4\uff0c\u5c31\u8fde\u5510\u5a67\u521a\u8fc7\u4e5d\u70b9\u4e5f\u7ed9\u5510\u5b66\u8c26\u3001\u987e\u5efa\u840d\u7ed9\u62c9\u4e86\u56de\u53bb\uff0c\u660e\u5929\u8fd8\u8981\u4e0a\u8bfe\uff0c\u5979\u4e0d\u53ef\u80fd\u6709\u5f20\u606a\u8fd9\u4e48\u8212\u670d\uff0c\u5979\u72e0\u72e0\u7684\u77aa\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u624d\u4e0d\u7518\u5fc3\u7684\u8d70\u4e86\u3002\u8c22\u5251\u5357\u3001\u8c22\u6c49\u660e\u7b49\u6b63\u6cf0\u96c6\u56e2\u7684\u4eba\u66f4\u6ca1\u6709\u5fc3\u601d\u5728\u9152\u4f1a\u4e0a\u9017\u7559\uff0c\u8fc7\u4e86\u4e5d\u70b9\uff0c\u522b\u5885\u91cc\u5c31\u6ca1\u6709\u591a\u5c11\u4eba\uff0c\u5ead\u9662\u91cc\u82b3\u8349\u840b\u840b\uff0c\u5f20\u606a\u559c\u6b22\u5750\u5728\u6c34\u5cb8\u8fb9\u770b\u7740\u7cbc\u7cbc\u6e56\u6c34\uff0c\u770b\u7740\u7238\u7238\u5f88\u60a0\u95f2\u7684\u7ad9\u5728\u5cb8\u8fb9\u62bd\u7740\u70df\uff0c\u5f88\u60f3\u4e5f\u6765\u4e00\u652f\uff0c\u4e0d\u8fc7\u5988\u5988\u6881\u683c\u73cd\u5c31\u7ad9\u5728\u540e\u5ead\u9662\u91cc\uff0c\u5f20\u606a\u8fd8\u6ca1\u6709\u5f53\u7740\u5979\u9762\u62bd\u70df\u7684\u52c7\u6c14\u3002</p><p>\u3000\u3000\u201c\u5510\u4f2f\u4f2f\u8bf4\u7684\u95ee\u9898\u5f88\u73b0\u5b9e\u5462\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u9526\u6e56\u4e0e\u7231\u8fbe\u7684\u5f62\u52bf\u597d\u7684\u8bdd\uff0c\u7238\u7238\u5c31\u8981\u5230\u5176\u4ed6\u5730\u65b9\u9ad8\u5c31\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u51fa\u53bb\u4e5f\u597d\uff0c\u4e0d\u8fc7\u518d\u600e\u4e48\u5feb\uff0c\u4e5f\u8981\u7b49\u4f60\u9ad8\u4e2d\u6bd5\u4e1a\u4e4b\u540e\uff0c\u4f60\u6ca1\u4e8b\u4e5f\u4e0d\u7528\u62c5\u5fc3\u8fd9\u4e2a\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u5728\u60f3\u6d77\u5dde\u63a7\u80a1\u7684\u95ee\u9898\uff0c\u6d77\u5dde\u63a7\u80a1\u80fd\u4e0d\u80fd\u5728\u4e24\u5e74\u7684\u65f6\u95f4\u91cc\u5960\u5b9a\u575a\u5b9e\u7684\u6839\u57fa\uff0c\u5510\u4f2f\u4f2f\u80fd\u4e0d\u80fd\u60f3\u4fe1\u4efb\u4f60\u90a3\u6837\u4fe1\u4efb\u674e\u660e\u5b66\uff1f\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u4f60\u5173\u5fc3\u8fd9\u4e2a\u2026\u2026\u201d\u5f20\u77e5\u884c\u5750\u4e0b\u6765\uff0c\u5fc3\u60f3\u8fd9\u91cc\u771f\u662f\u4e00\u5904\u597d\u4f4f\u5904\uff0c\u96be\u9053\u4eba\u4eba\u4e89\u7740\u505a\u5b98\uff0c\u201c\u68c9\u7eba\u7ec7\u5de5\u4e1a\u56ed\u533a\u4e00\u671f\u57fa\u672c\u5efa\u6210\uff0c\u6d77\u68c9\u4e09\u5382\u3001\u4e8c\u5382\u3001\u516d\u5382\u53ef\u4ee5\u7b2c\u4e00\u6279\u8fc1\u8fc7\u53bb\uff0c\u6211\u4eec\u5c31\u53ef\u4ee5\u5728\u5317\u5173\u533a\u62ff\u51fa\u4e09\u5757\u5730\uff0c\u53d1\u5c55\u4e13\u4e1a\u7684\u6279\u53d1\u5e02\u573a\uff0c\u4e4b\u524d\u6d77\u68c9\u56db\u5382\u6539\u5c0f\u5546\u54c1\u6279\u53d1\u5e02\u573a\u7684\u7ecf\u9a8c\u5f88\u6210\u529f\uff0c\u94f6\u884c\u4e5f\u613f\u610f\u63d0\u4f9b\u66f4\u591a\u7684\u8d44\u91d1\uff0c\u5176\u5b9e\u6211\u4eec\u4ec5\u4ec5\u4ece\u5c0f\u5546\u54c1\u6279\u53d1\u5e02\u573a\u91cc\u5957\u53d6\u7684\u8d44\u91d1\u5c31\u8db3\u4ee5\u652f\u4ed8\u68c9\u7eba\u7ec7\u5de5\u4e1a\u56ed\u533a\u7684\u5efa\u8bbe\u8d39\u7528\uff0c\u7ecf\u8fc7\u8fd9\u4e48\u4e00\u6574\u5408\uff0c\u5176\u5b9e\u662f\u767d\u8d5a\u7684\u4e09\u5757\u5317\u5173\u57ce\u533a\u7684\u5730\u76ae\uff0c\u6709\u4e86\u8fd9\u4e09\u5757\u5730\u76ae\u5468\u8f6c\uff0c\u5c31\u53ef\u4ee5\u5c06\u66f4\u591a\u7684\u5de5\u5382\u8fc1\u51fa\u57ce\u533a\uff0c\u6700\u5173\u952e\u8fd8\u662f\u8981\u5c06\u4f01\u4e1a\u76d8\u6d3b\uff0c\u4e0d\u7136\u5957\u51fa\u518d\u591a\u7684\u8d44\u91d1\u90fd\u6ca1\u6709\u610f\u4e49\u2026\u2026\u201d</p><p>\u3000\u3000\u51fa\u552e\u56fd\u6709\u4e2d\u5c0f\u578b\u4f01\u4e1a\u80a1\u6743\uff0c\u4e5d\u4e09\u5e74\u5728\u5c71\u4e1c\u7701\u5c31\u51fa\u73b0\u8bd5\u70b9\uff0c\u867d\u7136\u660e\u786e\u63d0\u51fa\u6765\u8fd8\u6709\u7b49\u5230\u4e5d\u4e03\u5e74\u4e4b\u540e\uff0c\u4f46\u662f\u4e5d\u4e94\u5e74\u6b64\u7c7b\u4e8b\u4ef6\u5df2\u7ecf\u4e0d\u7f55\u89c1\u3002</p><p>\u3000\u3000\u5f20\u606a\u8bf4\u9053\uff1a\u201c\u5bf9\u5382\u4f01\u7684\u7ecf\u8425\uff0c\u53ef\u4ee5\u8bf4\u53ea\u6709\u7ba1\u7406\u5c42\u5fc3\u91cc\u6700\u6709\u6570\uff0c\u6d77\u5dde\u63a7\u80a1\u53ea\u9700\u8981\u505a\u597d\u8d44\u4ea7\u91cd\u7ec4\u7684\u4e8b\u60c5\uff0c\u800c\u5e94\u5c06\u5177\u4f53\u7ecf\u8425\u7684\u4e8b\u60c5\u5168\u6743\u4ea4\u7ed9\u7ba1\u7406\u5c42\uff0c\u5e76\u8d1f\u8d23\u5c06\u5382\u4f01\u4e0e\u653f\u5e9c\u90e8\u95e8\u4e4b\u95f4\u7684\u5173\u7cfb\u7406\u987a\u3002\u5982\u4f55\u624d\u80fd\u8ba9\u7ba1\u7406\u5c42\u66f4\u52a0\u5c3d\u8d23\uff1f\u6211\u8ba4\u4e3a\u6216\u51fa\u552e\u6216\u5956\u52b1\u5c11\u90e8\u5206\u80a1\u6743\u7ed9\u6574\u4e2a\u7ba1\u7406\u5c42\u6bd4\u8f83\u5408\u9002\u3002\u627f\u5305\u7ecf\u8425\u5230\u73b0\u5728\u5df2\u7ecf\u4e0d\u662f\u6700\u5408\u9002\u7684\u65b9\u5f0f\uff0c\u56e0\u4e3a\u4f01\u4e1a\u7684\u7ecf\u8425\u66f4\u591a\u7684\u4f9d\u8d56\u4e8e\u6574\u4e2a\u7ba1\u7406\u5c42\u56e2\u961f\uff0c\u800c\u975e\u627f\u5305\u8005\u4e2a\u4eba\u3002\u76ee\u524d\u6709\u8ba1\u5212\u7684\u5c06\u5382\u4f01\u8fc1\u51fa\u57ce\u533a\uff0c\u53ea\u8981\u662f\u5c06\u57ce\u533a\u65e7\u5382\u7684\u5730\u76ae\u8d44\u4ea7\u5265\u79bb\u51fa\u6765\uff0c\u65e0\u8bba\u662f\u56fd\u68c9\u3001\u8fd8\u662f\u519c\u673a\u5382\uff0c\u4f9d\u9760\u94f6\u884c\u8d37\u6b3e\u5efa\u8bbe\u8d77\u6765\u7684\u65b0\u5382\uff0c\u51c0\u8d44\u4ea7\u7684\u76d8\u5b50\u90fd\u4e0d\u4f1a\u592a\u5e9e\u5927\uff0c\u8fd9\u6837\u5c31\u80fd\u8ba9\u7ba1\u7406\u5c42\u4e00\u5f00\u59cb\u5c31\u62e5\u6709\u8db3\u591f\u591a\u7684\u80a1\u6743\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u77e5\u884c\u76ee\u5149\u70af\u70af\u6709\u795e\u7684\u76ef\u7740\u6e56\u9762\uff0c\u4ee5\u9526\u6e56\u3001\u7231\u8fbe\u7684\u53d1\u5c55\u8d8b\u52bf\uff0c\u4ed6\u8c03\u5230\u5916\u5730\u4efb\u804c\u662f\u6700\u5408\u9002\u7684\uff0c\u4ed6\u4e5f\u4e0d\u662f\u60f3\u5728\u6b63\u5904\u7ea7\u7684\u4f4d\u5b50\u8e1f\u8e70\u4e0d\u524d\uff0c\u4e5f\u5e0c\u671b\u5728\u4e24\u5e74\u7684\u65f6\u95f4\u91cc\uff0c\u80fd\u6709\u7a81\u7834\uff0c\u5c31\u7b97\u5728\u6d77\u5dde\u53ea\u6709\u4e24\u5e74\u7684\u65f6\u95f4\uff0c\u4e5f\u53ef\u4ee5\u505a\u4e00\u4e9b\u4e8b\u60c5\u51fa\u6765\u3002</p><p>\u3000\u3000\u8ba9\u7ba1\u7406\u5c42\u6301\u80a1\uff0c\u800c\u6d77\u5dde\u63a7\u80a1\u8f6c\u578b\u6210\u8f83\u7eaf\u7cb9\u7684\u4ee3\u8868\u653f\u5e9c\u6301\u6709\u56fd\u6709\u80a1\u6743\u7684\u63a7\u80a1\u516c\u53f8\uff0c\u8fd9\u79cd\u5f62\u5f0f\u5927\u89c4\u6a21\u7684\u51fa\u73b0\u8fd8\u662f\u57282002\u5e74\u4e4b\u540e\uff0c\u4e3b\u8981\u662f\u589e\u5f3a\u5bf9\u56fd\u6709\u5927\u4e2d\u578b\u4f01\u4e1a\u7684\u56fd\u6709\u80a1\u6743\u7684\u7ba1\u7406\uff0c\u6bd4\u5927\u89c4\u6a21\u7684\u201c\u56fd\u9000\u6c11\u8fdb\u201d\u3001\u51fa\u552e\u56fd\u6709\u4e2d\u5c0f\u578b\u4f01\u4e1a\u8981\u665a\u597d\u51e0\u5e74\u3002\u56fd\u6709\u5927\u4e2d\u578b\u4f01\u4e1a\u65e0\u975e\u4e5f\u662f\u4ece\u4e2d\u5c0f\u578b\u4f01\u4e1a\u53d1\u5c55\u800c\u6765\uff0c\u63d0\u524d\u5b9e\u8df5\u8fd9\u4e00\u6a21\u5f0f\uff0c\u81f3\u5c11\u80fd\u5728\u6d77\u5dde\u8303\u56f4\u4e4b\u5185\uff0c\u589e\u5f3a\u5bf9\u56fd\u6709\u4e2d\u5c0f\u578b\u4f01\u4e1a\u7684\u56fd\u6709\u80a1\u6743\u7ba1\u7406\u3002</p><p>\u3000\u3000\u5c31\u7b97\u6700\u7ec8\u62b5\u6321\u4e0d\u4f4f\u201c\u56fd\u9000\u6c11\u8fdb\u201d\u7684\u5927\u6f6e\uff0c\u4e5f\u80fd\u5c3d\u53ef\u80fd\u7684\u4fdd\u8bc1\u56fd\u6709\u8d44\u4ea7\u4e0d\u6d41\u5931\uff0c\u4e5f\u7b97\u662f\u7ed9\u6d77\u5dde\u5e02\u505a\u51fa\u4e00\u4e9b\u8d21\u732e\u3002</p><p>\u3000\u3000\u770b\u7740\u7236\u4eb2\u9ed8\u4e0d\u4f5c\u58f0\uff0c\u4e0d\u6653\u5f97\u4ed6\u662f\u4e0d\u662f\u6709\u4e9b\u62c5\u5fe7\uff0c\u5f20\u606a\u56de\u5934\u770b\u89c1\u5988\u5988\u8ddf\u6881\u519b\u3001\u6881\u541b\u8339\u4ee5\u53ca\u6881\u519b\u5e26\u5230\u6d77\u5dde\u6765\u7684\u5973\u670b\u53cb\u5b59\u4e3d\u7ad9\u5728\u540e\u5ead\u9662\u91cc\u8bf4\u8bdd\u3002</p><p>\u3000\u3000\u5c0f\u578b\u7684\u5e86\u795d\u9152\u4f1a\uff0c\u751a\u81f3\u8fde\u5f20\u77e5\u975e\u90fd\u6ca1\u6709\u9080\u8bf7\uff0c\u4f46\u662f\u6881\u683c\u73cd\u8fd8\u662f\u62c9\u7740\u4f84\u5b50\u3001\u4f84\u5973\u8fc7\u6765\u89c1\u4e16\u9762\uff0c\u9664\u4e86\u5f20\u606a\u5916\uff0c\u5979\u5c31\u5bf9\u4e24\u4e2a\u54e5\u54e5\u5bb6\u7684\u5b69\u5b50\u62b1\u4ee5\u6700\u5927\u7684\u671f\u671b\uff0c\u4e8c\u54e5\u4e00\u5bb6\u56db\u53e3\u5728\u897f\u5b89\uff0c\u5927\u54e5\u6b63\u5728\u529e\u8f6c\u4e1a\u5730\u65b9\u7684\u4e8b\u60c5\uff0c\u5979\u53ef\u4e0d\u60f3\u8ba9\u5927\u54e5\u5927\u5ac2\u5230\u6d77\u5dde\u62b1\u6028\u81ea\u5df1\u6ca1\u7167\u987e\u597d\u5c0f\u8f88\u3002</p><p>\u3000\u3000\u6881\u519b\u4e0e\u5b59\u4e3d\u90fd\u662f\u5728\u6210\u90fd\u8bfb\u7684\u5927\u5b66\uff0c\u5c31\u57ce\u5e02\u800c\u8a00\uff0c\u6210\u90fd\u8981\u6bd4\u6d77\u5dde\u5927\u8bb8\u591a\uff0c\u4e5f\u4f11\u95f2\u8bb8\u591a\uff0c\u4f46\u662f\u4e5d\u5341\u5e74\u4ee3\u4e2d\u671f\u7684\u5927\u5b66\u751f\u6d3b\u8fd8\u662f\u6bd4\u8f83\u5355\u8c03\uff0c\u8fd8\u6ca1\u6709\u600e\u4e48\u63a5\u89e6\u7e41\u534e\u7684\u57ce\u5e02\u751f\u6d3b\u3002\u5b59\u4e3d\u662f\u5e08\u8303\u751f\uff0c\u6bd5\u4e1a\u8981\u56de\u751f\u6e90\u5730\u5c31\u4e1a\uff0c\u6881\u519b\u624d\u653e\u5f03\u53bb\u5357\u65b9\u5de5\u4f5c\u7684\u673a\u4f1a\uff0c\u8ddf\u7740\u56de\u5c0f\u53bf\u57ce\u3002\u6bd5\u7adf\u5728\u5927\u57ce\u5e02\u91cc\u8bfb\u8fc7\u4e66\uff0c\u518d\u56de\u5230\u5c0f\u53bf\u57ce\uff0c\u5fc3\u91cc\u6709\u4e9b\u4e0d\u7518\uff0c\u6bd5\u4e1a\u4e4b\u524d\u6ca1\u6709\u591a\u60f3\uff0c\u6bd5\u4e1a\u4e4b\u540e\u5c31\u6709\u4ea7\u751f\u5fc3\u91cc\u843d\u5dee\uff0c\u6362\u8c01\u90fd\u4e0d\u4f1a\u7518\u5fc3\u5f85\u5728\u8fde\u4e00\u6761\u67cf\u6cb9\u9a6c\u8def\u90fd\u6ca1\u6709\u7684\u53bf\u57ce\u91cc\u3002</p><p>\u3000\u3000\u6709\u8fd9\u4e2a\u673a\u4f1a\uff0c\u8fd8\u662f\u5b59\u4e3d\u5148\u9f13\u52a8\u6881\u519b\u4e00\u8d77\u51fa\u6765\uff0c\u751a\u81f3\u5148\u539f\u5148\u5b66\u6821\u7684\u4eba\u4e8b\u6863\u6848\u90fd\u6ca1\u6709\u6765\u5f97\u53ca\u62ff\u51fa\u6765\u3002</p><p>\u3000\u3000\u56fd\u4eba\u90fd\u6709\u6734\u7d20\u7684\u5b97\u65cf\u89c2\u5ff5\uff0c\u5f20\u606a\u5b66\u6ca1\u6709\u60f3\u5230\u8981\u8ba9\u5c06\u6765\u7684\u7231\u8fbe\u6216\u8005\u9526\u6e56\u6210\u4e3a\u5bb6\u65cf\u4f01\u4e1a\uff0c\u4f46\u662f\u4e5f\u5e0c\u671b\u5bb6\u65cf\u80fd\u5728\u6d77\u5dde\u624e\u4e0b\u6df1\u539a\u7684\u6839\u57fa\uff0c\u8fd9\u6837\u6765\uff0c\u4e5f\u81f3\u4e8e\u56e0\u4e3a\u4e00\u65f6\u7684\u5931\u52bf\u800c\u56db\u5904\u65e0\u63f4\u3002\u6d77\u5dde\u6709\u5510\u5b66\u8c26\u6491\u7740\uff0c\u7701\u91cc\u6709\u5f90\u5b66\u5e73\uff0c\u9526\u6e56\u3001\u7231\u8fbe\u5e73\u65e5\u5faa\u89c4\u8e48\u77e9\uff0c\u5c31\u7b97\u7238\u7238\u8c03\u5f80\u5916\u5730\u4efb\u804c\uff0c\u591a\u5927\u7684\u4e8b\u60c5\u4e5f\u80fd\u625b\u7740\uff0c\u4f46\u662f\u5f90\u5b66\u5e73\u9000\u4e86\u4e4b\u540e\uff0c\u5510\u5b66\u8c26\u4e5f\u79bb\u5f00\u6d77\u5dde\u5462\uff1f</p><p>\u3000\u3000\u52bf\u529b\u7684\u5f3a\u529b\u7edd\u4e0d\u4ec5\u4ec5\u4f9d\u8d56\u4e8e\u67d0\u4e2a\u4eba\uff0c\u800c\u67d0\u4e9b\u4eba\u7f16\u7ec7\u51fa\u6765\u7684\u7f51\u7edc\uff0c\u91d1\u56fd\u6d77\u3001\u5b8b\u57f9\u660e\u3001\u675c\u5c0f\u5c71\u7b49\u7b49\uff0c\u4ed6\u4eec\u867d\u7136\u4e5f\u662f\u8fd9\u4e2a\u7f51\u7edc\u4e2d\u7684\u4e00\u5206\u5b50\uff0c\u4f46\u662f\u8fd9\u4e2a\u7f51\u7edc\u73b0\u5728\u8fd8\u4e0d\u591f\u7d27\u5bc6\uff0c\u751a\u81f3\u5728\u8fd9\u4e2a\u7f51\u7edc\u4e2d\uff0c\u5927\u5bb6\u90fd\u6709\u81ea\u8eab\u7684\u5229\u76ca\u8bc9\u6c42\uff0c\u5411\u529b\u5fc3\u5c31\u4e0d\u8db3\u591f\u5f3a\uff0c\u6240\u4ee5\u8fd8\u8981\u5f80\u8fd9\u4e2a\u7f51\u7edc\u91cc\u6dfb\u52a0\u4e9b\u4ec0\u4e48\u3002</p><p>\u3000\u3000\u8fd9\u4fbf\u662f\u5f20\u606a\u5f88\u5e0c\u671b\u5927\u8205\u4e00\u5bb6\u4eba\u56de\u6d77\u5dde\u7684\u539f\u56e0\u3002</p><p>\u3000\u3000\u5f20\u606a\u7ad9\u8d77\u6765\uff0c\u5c06\u7238\u7238\u4e22\u5728\u90a3\u91cc\uff0c\u8ba9\u4ed6\u81ea\u5df1\u53bb\u60f3\u90a3\u4e9b\u5934\u75bc\u7684\u95ee\u9898\uff0c\u81ea\u5df1\u5219\u671d\u8868\u5144\u6881\u519b\u4ed6\u4eec\u8d70\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u201c\u4f60\u7238\u5728\u90a3\u91cc\u60f3\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d\u6881\u683c\u73cd\u95ee\u5f20\u606a\u3002</p><p>\u3000\u3000\u201c\u5fd8\u8bb0\u95ee\u4e86\uff0c\u8981\u4e0d\u4f60\u81ea\u5df1\u53bb\u95ee\uff1f\u201d\u5f20\u606a\u56de\u4e86\u4e00\u53e5\u3002</p><p>\u3000\u3000\u201c\u53bb\uff0c\u201d\u6881\u683c\u73cd\u5550\u4e86\u4ed6\u4e00\u53e3\uff0c\u201c\u8ddf\u4f60\u5988\u800d\u82b1\u8154\uff01\u6211\u8fdb\u5c4b\u91cc\u53bb\uff0c\u7b49\u4f1a\u513f\u6211\u8ddf\u4f60\u7238\u5148\u56de\u53bb\uff0c\u65f6\u95f4\u8fd8\u65e9\uff0c\u4f60\u665a\u4e0a\u9886\u7740\u4f60\u5927\u519b\u54e5\u4ed6\u4eec\u5728\u5e02\u91cc\u597d\u597d\u73a9\u4e00\u73a9\u3002\u201d</p><p>\u3000\u3000\u770b\u7740\u5c0f\u59d1\u8d70\u8fdb\u5c4b\u5b50\uff0c\u6881\u541b\u8339\u8be7\u5f02\u7684\u8bf4\uff1a\u201c\u5f20\u606a\uff0c\u5c0f\u59d1\u600e\u4e48\u4e0d\u7ba1\u4f60\uff1f\u201d</p><p>\u3000\u3000\u201c\u653e\u517b\u7684\u5c71\u7f8a\u8089\u66f4\u9c9c\u7f8e\uff0c\u6211\u5988\u6bd4\u4f60\u957f\u8111\u5b50\u3002\u201d\u5f20\u606a\u53d6\u7b11\u8868\u59d0\u8bf4\uff0c\u201c\u4f60\u8fd8\u5728\u519b\u8bad\u5427\uff0c\u6211\u5988\u600e\u4e48\u628a\u4f60\u62c9\u51fa\u6765\u4e86\uff1f\u201d\u6881\u541b\u8339\u51fa\u8eab\u519b\u4eba\u5bb6\u5ead\uff0c\u4eba\u8fc7\u4e8e\u6587\u9759\u3002</p><p>\u3000\u3000\u201c\u519b\u8bad\u4e5f\u6709\u4f11\u606f\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u8bb0\u5f97\u81ea\u5df1\u4e0a\u5927\u5b66\u65f6\uff0c\u519b\u8bad\u5c31\u4e2d\u79cb\u90a3\u665a\u6b63\u5f0f\u4f11\u606f\u4e86\u4e00\u5929\uff0c\u5176\u4ed6\u65f6\u95f4\u53ea\u80fd\u9760\u88c5\u75c5\u9003\u519b\u8bad\uff0c\u8f6c\u8fc7\u95ee\u6881\u519b\uff1a\u201c\u611f\u89c9\u6d77\u5dde\u600e\u4e48\u6837\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd8\u884c\uff0c\u5c31\u662f\u6709\u4e9b\u4eba\u6bd4\u8f83\u8ba8\u538c\u3002\u201d\u6881\u519b\u4e2a\u5b50\u6bd4\u5f20\u606a\u8fd8\u9ad8\u4e00\u4e9b\uff0c\u5927\u5757\u5934\uff0c\u5bbd\u5bbd\u7684\u8138\u819b\uff0c\u5251\u7709\u661f\u76ee\uff0c\u5341\u5206\u7684\u82f1\u6c14\uff0c\u53ea\u662f\u8bf4\u8d77\u5bf9\u6d77\u5dde\u5e02\u91cc\u7684\u611f\u89c2\uff0c\u7709\u5b87\u95f4\u538b\u7740\u4e00\u4e1d\u4e0d\u6109\u5feb\u3002</p><p>\u3000\u3000\u201c\u8c01\u554a\uff0c\u8c01\u8ba9\u4f60\u770b\u4e0d\u987a\u773c\uff0c\u6211\u66ff\u4f60\u4fee\u7406\u4ed6\uff1f\u201d\u5f20\u606a\u7b11\u7740\u8bf4\uff0c\u6881\u519b\u8ddf\u5b59\u4e3d\u662f\u5728\u5c0f\u53d4\u7684\u516c\u53f8\u91cc\u5de5\u4f5c\uff0c\u8c01\u4f1a\u8fd9\u4e48\u4e0d\u957f\u773c\u62db\u4ed6\u4eec\u8ba8\u538c\uff1f</p><p>\u3000\u3000\u201c\u4e0d\u8981\u8bf4\u4e86\uff0c\u6211\u4ee5\u540e\u4e0d\u642d\u7406\u4ed6\u5c31\u662f\u4e86\u2026\u2026\u201d\u5b59\u4e3d\u8f7b\u8f7b\u626f\u4e86\u626f\u6881\u519b\u7684\u8863\u89d2\uff0c\u5a07\u7f8e\u7684\u8138\u6709\u4e9b\u7f9e\u6da9\u3002</p><p>\u3000\u3000\u201c\u7b97\u4e86\uff0c\u4e0d\u8bf4\u4e86\u3002\u201d\u6881\u519b\u607c\u6c14\u7684\u6325\u4e86\u6325\uff0c\u6709\u4e9b\u6cc4\u6c14\u7684\u8bf4\u3002</p><p>\u3000\u3000\u201c\u5f20\u5955\uff1f\u201d\u5f20\u606a\u9510\u5229\u7684\u76ee\u5149\u626b\u8fc7\u5b59\u4e3d\uff0c\u6709\u7740\u521a\u4ece\u5c0f\u5730\u65b9\u51fa\u6765\u7684\u751f\u6da9\uff0c\u7709\u6e05\u76ee\u79c0\uff0c\u5341\u5206\u5a07\u7f8e\uff0c\u5c31\u662f\u5728\u5927\u57ce\u5e02\u4e5f\u5f88\u5c11\u89c1\u5230\u8fd9\u4e48\u6f02\u4eae\u7684\u5973\u5b69\u5b50\u3002</p><p>\u3000\u3000\u201c\u54fc\uff0c\u201d\u542c\u5f20\u606a\u731c\u5230\u4ec0\u4e48\u4e8b\uff0c\u6881\u519b\u6124\u6124\u4e0d\u5e73\u7684\u54fc\u4e86\u4e00\u58f0\uff0c\u201c\u5f20\u606a\uff0c\u4f60\u8ddf\u4f60\u7684\u8001\u5802\u8bf4\u4e00\u58f0\uff0c\u4ed6\u518d\u7f20\u7740\u5b59\u4e3d\uff0c\u53ef\u522b\u602a\u6211\u4e0d\u5ba2\u6c14\u3002\u201d</p><p>\u3000\u3000\u524d\u4e9b\u5929\u542c\u5c0f\u53d4\u8bf4\u5f20\u5955\u8ddf\u56fd\u5149\u5382\u7684\u4e00\u540d\u5973\u804c\u5de5\u5728\u800d\u670b\u53cb\uff0c\u8fd9\u5df2\u7ecf\u662f\u4ed6\u6765\u5e02\u91cc\u540e\u7684\u7b2c\u56db\u4e2a\u5973\u670b\u53cb\uff0c\u60f3\u4e0d\u5230\u4ed6\u8fd8\u771f\u6709\u51fa\u606f\uff0c\u5bb6\u91cc\u4eba\u641e\u51fa\u8fd9\u79cd\u4e8b\u60c5\uff0c\u4ed6\u662f\u60f3\u53cd\u4e0a\u5929\uff0c\u8fd8\u662f\u6b3a\u6881\u519b\u662f\u5916\u6765\u6237\uff1f\u201c\u2026\u2026\u201d\u5f20\u606a\u70b9\u70b9\u5934\uff0c\u6ca1\u6709\u8bf4\u4ec0\u4e48\uff0c\u65e2\u7136\u5988\u5988\u8bf4\u9886\u7740\u6881\u519b\u4ed6\u4eec\u53bb\u597d\u597d\u73a9\u4e00\u73a9\uff0c\u90fd\u4e5d\u5341\u70b9\u949f\u4e86\uff0c\u53ea\u80fd\u53bb\u76db\u4e16\u5e74\u534e\u7684\u5a31\u4e50\u573a\uff0c\u8d76\u7740\u675c\u98de\u5077\u5077\u7684\u5e26\u7740\u73ed\u4e0a\u540c\u5b66\u4e5f\u5728\u90a3\u91cc\u73a9\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd8\u7b97\u4f4e\u8c03\uff0c\u5085\u4fca\u6bd5\u7adf\u662f\u79bb\u5f00\u76db\u4e16\u4e4b\u540e\u624d\u8ddf\u5f20\u606a\uff0c\u8fea\u5385\u91cc\u7684\u5f88\u591a\u4eba\u90fd\u7eb7\u7eb7\u8ddf\u4ed6\u4eec\u70b9\u5934\u6253\u62db\u547c\u3002\u76db\u4e16\u5e74\u534e\u5728\u56fd\u5185\u8981\u7b97\u975e\u5e38\u5e72\u51c0\u7684\u573a\u5b50\uff0c\u4f46\u662f\u5bf9\u4e8e\u4ece\u5c0f\u5bb6\u6559\u5f88\u4e25\u7684\u6881\u519b\u3001\u6881\u541b\u8339\u3001\u5b59\u4e3d\u6765\u8bf4\uff0c\u51b2\u51fb\u529b\u8fd8\u662f\u5f88\u5f3a\u3002\u6881\u541b\u8339\u8be7\u5f02\u7684\u95ee\u5f20\u606a\uff1a\u201c\u5c0f\u59d1\u5c31\u8fd9\u6837\u7684\u653e\u517b\u4f60\uff0c\u4e00\u4e2a\u6807\u51c6\u7684\u7ea8\u7ed4\u5b50\u5f1f\uff0c\u6211\u8ddf\u5927\u519b\u8981\u6562\u8fd9\u4e48\u73a9\uff0c\u817f\u90fd\u4f1a\u7ed9\u6253\u65ad\u3002\u201d</p><p>\u3000\u3000\u8fd9\u8bdd\u5012\u662f\u4e0d\u5047\uff0c\u9a7b\u519b\u5bf9\u5730\u65b9\u4e5f\u6709\u8db3\u591f\u7684\u5f71\u54cd\u529b\uff0c\u4e00\u540d\u4e2d\u5c42\u519b\u5b98\u7684\u968f\u519b\u5b50\u5973\uff0c\u8981\u662f\u5bb6\u5ead\u653e\u7eb5\u7684\u8bdd\uff0c\u5728\u9a7b\u5730\u4e5f\u53ef\u4ee5\u6df7\u5f97\u98ce\u751f\u6c34\u8d77\u3002</p></article>"}}}]);
