(window.webpackJsonp=window.webpackJsonp||[]).push([[244],{266:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u4e94\u8282 \u5fd8\u5374",content:"<article><h2>\u7b2c\u5341\u4e94\u8282 \u5fd8\u5374</h2><p>\u743c\u6069\u6b63\u81ea\u60c5\u6d53\u4e4b\u9645\uff0c\u9661\u7136\u88ab\u8299\u857e\u72c4\u7528\u529b\u4e00\u63a8\uff0c\u91cd\u91cd\u8dcc\u4e0b\u5e8a\u53bb\uff0c\u8fd9\u91cc\u5230\u5904\u90fd\u662f\u575a\u786c\u7684\u77f3\u5934\u5730\u9762\uff0c\u4ed6\u8d64\u8eab\u88f8\u4f53\uff0c\u53c8\u6ca1\u6709\u4ec0\u4e48\u8fd0\u6c14\u62a4\u4f53\u7684\u529f\u592b\u2014\u2014\u5c31\u662f\u6240\u5b66\u7684\u90a3\u70b9\u5185\u529f\uff0c\u5982\u4eca\u6765\u770b\u53ea\u6015\u5341\u6709\u4e03\u516b\u90fd\u662f\u5047\u7684\uff0c\u987f\u65f6\u7830\u7684\u4e00\u58f0\uff0c\u6454\u5f97\u5168\u8eab\u75bc\u75db\uff0c\u4e0d\u7531\u5f97\u5fc3\u5934\u706b\u8d77\uff0c\u8d77\u8eab\u53d6\u5e8a\u8fb9\u7684\u7761\u8863\u5c31\u8981\u6765\u7ed1\u5c0f\u5973\u5b69\u3002</p><p>\u4f5c\u4e3a\u4e0d\u751a\u6807\u51c6\u7684\u5b85\uff0c\u65e5\u672cA\u7247\u81ea\u7136\u662f\u770b\u8fc7\u4e0d\u5c11\uff0c\u5176\u4e2d\u4e0d\u4e4f\u6d89\u53ca\u6346\u7ed1\u675f\u7f1a\u7684\u5185\u5bb9\uff0c\u7136\u800c\u5f88\u60ed\u6127\uff0c\u90a3\u4e9b\u9ad8\u6df1\u590d\u6742\u7684\u6280\u672f\uff0c\u743c\u6069\u662f\u534a\u70b9\u90fd\u6ca1\u5b66\u4f1a\u3002\u8111\u4e2d\u8f6c\u4e86\u51e0\u4e2a\u82b1\u5f0f\uff0c\u6700\u7ec8\u8fd8\u662f\u91c7\u7528\u4e86\u6700\u7b80\u5355\u7684\u624b\u6cd5\u3002\u8299\u857e\u72c4\u4e0d\u77e5\u9053\u4ed6\u8981\u505a\u4ec0\u4e48\uff0c\u6b32\u5f85\u6323\u624e\uff0c\u89c1\u4ed6\u52a8\u6012\uff0c\u767b\u65f6\u5c31\u80c6\u602f\u4e86\u51e0\u5206\uff0c\u602f\u751f\u751f\u5730\u4e0d\u6562\u52a8\u5f39\uff0c\u7b49\u56de\u8fc7\u795e\u6765\uff0c\u4e00\u53cc\u7693\u8155\u5df2\u7ecf\u88ab\u81ea\u5df1\u7684\u7761\u8863\u7f1a\u4f4f\u3002\u6b64\u65f6\u5fc3\u4e2d\u8fd8\u6709\u4e9b\u5bb3\u6015\uff0c\u7b49\u542c\u743c\u6069\u5728\u8033\u8fb9\u8f7b\u8f7b\u8bf4\u4e86\u53e5\u8bdd\uff0c\u987f\u65f6\u4fbf\u5168\u8eab\u8f6f\u7ef5\u7ef5\u7684\uff0c\u4efb\u7531\u6446\u5e03\u4e86\u3002</p><p>\u743c\u6069\u7ed1\u4f4f\u5979\u7684\u53cc\u81c2\uff0c\u4f38\u624b\u6263\u4f4f\u7cbe\u81f4\u7684\u811a\u8e1d\uff0c\u5c06\u5979\u4e00\u53cc\u7389\u817f\u9ad8\u9ad8\u4e3e\u8d77\uff0c\u653e\u5728\u81ea\u5df1\u80a9\u4e0a\uff0c\u8038\u8eab\u6162\u6162\u633a\u5165\u3002\u4ed6\u81ea\u4ece\u51fa\u5965\u56fe\u57ce\u4ee5\u6765\uff0c\u4e00\u76f4\u75b2\u4e8e\u8d76\u8def\uff0c\u597d\u5bb9\u6613\u5230\u4e86\u4f0a\u5361\u6c99\u57ce\u53c8\u8d76\u4e0a\u9ed1\u6697\u7cbe\u7075\u7684\u56f4\u653b\uff0c\u4e0d\u66fe\u6709\u4e00\u65e5\u95f2\u6687\uff0c\u867d\u7136\u6885\u83f2\u65af\u5728\u8eab\u8fb9\uff0c\u5374\u4e5f\u6ca1\u6709\u4eb2\u8fd1\u7684\u65f6\u95f4\uff0c\u5df2\u7ecf\u5927\u534a\u4e2a\u6708\u6ca1\u6709\u78b0\u8fc7\u5973\u4eba\u4e86\u3002\u5982\u4eca\u91cd\u6e29\u65e7\u60c5\uff0c\u672c\u5c31\u6709\u4e9b\u6b32\u706b\u96be\u8010\uff0c\u53c8\u77e5\u9053\u5979\u4eec\u59d0\u59b9\u4e4b\u95f4\u6709\u5fc3\u7075\u611f\u5e94\uff0c\u81ea\u5df1\u5728\u548c\u8299\u857e\u72c4\u6b22\u597d\uff0c\u540c\u65f6\u8299\u8389\u5a05\u4e5f\u611f\u540c\u8eab\u53d7\uff0c\u5c31\u597d\u50cf\u540c\u65f6\u5728\u5e72\u4e24\u4e2a\u5973\u4eba\u4f3c\u7684\u3002\u8fd9\u79cd\u9645\u9047\uff0c\u9664\u4e86\u65e5\u672c\u52a8\u753b\u91cc\u7684\u89e6\u624b\u602a\u7269\uff0c\u53ea\u6015\u518d\u6ca1\u6709\u5176\u4ed6\u7537\u4eba\u80fd\u4eab\u53d7\u5230\uff0c\u4e0d\u4f46\u662f\u4ece\u672a\u7ecf\u5386\uff0c\u95fb\u6240\u672a\u95fb\uff0c\u5c31\u662f\u8fde\u505a\u68a6\u90fd\u4e0d\u66fe\u60f3\u5230\u8fc7\u3002\u5ff5\u53ca\u6b64\u5904\uff0c\u4e0d\u7531\u5f97\u60c5\u6b32\u52c3\u53d1\uff0c\u521a\u5f00\u59cb\u8fd8\u5c3d\u91cf\u8f7b\u67d4\uff0c\u6015\u5f04\u4f24\u4e86\u5979\uff0c\u6e10\u6e10\u5730\u4fbf\u6b32\u706b\u817e\u8d77\uff0c\u52a8\u4f5c\u4e5f\u4e0d\u77e5\u4e0d\u89c9\u95f4\u53d8\u5f97\u7c97\u91cd\u8d77\u6765\uff0c\u8d8a\u6765\u8d8a\u5feb\u3002\u8299\u857e\u72c4\u53ea\u89c9\u81ea\u5df1\u4eff\u4f5b\u88ab\u706b\u70ed\u7684\u70d9\u94c1\u7a7f\u900f\uff0c\u5979\u5411\u6765\u662f\u6700\u6015\u75bc\u7684\uff0c\u767b\u65f6\u51e0\u4e4e\u5c31\u8981\u54ed\u51fa\u58f0\u6765\uff0c\u4f46\u53c8\u6015\u626b\u4e86\u743c\u6069\u5174\u81f4\uff0c\u53ea\u662f\u5f3a\u81ea\u5fcd\u8010\uff0c\u51e0\u4e4e\u5c06\u4e0b\u5507\u90fd\u54ac\u7834\u4e86\u3002\u603b\u7b97\u5df2\u7ecf\u4e0d\u662f\u5904\u5b50\u4e4b\u8eab\uff0c\u6e10\u6e10\u9002\u5e94\u8fc7\u6765\uff0c\u75bc\u75db\u7f13\u53bb\uff0c\u4e00\u9635\u4e00\u9635\u7684\u6655\u7729\u611f\u5982\u6d6a\u6f6e\u822c\u51b2\u51fb\u7740\u8111\u6d77\uff0c\u4eff\u4f5b\u8981\u5c06\u5979\u6df9\u6ca1\u4f3c\u7684\uff0c\u4f34\u968f\u7740\u53c8\u4f3c\u9178\u9ebb\u53c8\u4f3c\u5feb\u7f8e\u7684\u611f\u89c9\uff0c\u604d\u604d\u60da\u60da\u95f4\u611f\u89c9\u81ea\u5df1\u50cf\u662f\u6c6a\u6d0b\u5927\u6d77\u4e2d\u7684\u4e00\u53f6\u5c0f\u821f\uff0c\u6b63\u88ab\u72c2\u98ce\u5de8\u6d6a\u6c79\u6d8c\u62cd\u6253\u649e\u51fb\uff0c\u53ea\u662f\u6c89\u6ca1\u4e0d\u4e0b\u53bb\u3002</p><p>\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u731b\u7136\u95f4\u5168\u8eab\u4e0d\u7531\u81ea\u4e3b\u5730\u7d27\u7ef7\u8d77\u6765\uff0c\u4f53\u5185\u4e00\u9635\u9635\u5730\u6536\u7f29\u75c9\u631b\uff0c\u8db3\u8db3\u6301\u7eed\u4e86\u5927\u7ea6\u6709\u5341\u79d2\u949f\uff0c\u65b9\u624d\u6162\u6162\u6e05\u9192\u3002\u743c\u6069\u4e5f\u505c\u4e86\u52a8\u4f5c\uff0c\u4fef\u8eab\u5728\u5979\u8138\u4e0a\u8f7b\u8f7b\u4eb2\u543b\u3002\u8299\u857e\u72c4\u5b9a\u4e86\u5b9a\u795e\uff0c\u6b63\u60f3\u8981\u4ed6\u7ee7\u7eed\uff0c\u4e0b\u8eab\u5374\u4e00\u9635\u706b\u8fa3\u8fa3\u7684\u75bc\u75db\u3002\u5979\u6b64\u65f6\u9ad8\u6f6e\u892a\u53bb\uff0c\u5bf9\u75db\u695a\u5206\u5916\u654f\u611f\uff0c\u4e0d\u7531\u5f97\u4f4e\u4f4e\u547b\u541f\uff0c\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u54ed\u4e86\u51fa\u6765\u3002</p><p>\u201c\u4f60\u628a\u6211\u5f04\u75bc\u4e86\u3002\u201d\u5979\u54ac\u7740\u5634\u5507\uff0c\u6cea\u6c34\u6d9f\u6d9f\u3002</p><p>\u743c\u6069\u6b64\u65f6\u4e5f\u7a0d\u7a0d\u6e05\u9192\uff0c\u7f13\u7f13\u9000\u51fa\u4e00\u4e9b\uff0c\u4f4e\u5934\u770b\u65f6\uff0c\u89c1\u900f\u660e\u7231\u6db2\u91cc\u6709\u4e9b\u9c9c\u7ea2\u7684\u8840\u4e1d\u75d5\u8ff9\uff0c\u60c5\u77e5\u662f\u628a\u5979\u5f04\u4f24\u4e86\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e2d\u65e2\u662f\u601c\u60dc\u53c8\u662f\u61ca\u6094\uff0c\u614c\u5fd9\u5c06\u5979\u62b1\u7740\uff0c\u4eb2\u543b\u7231\u629a\uff0c\u5728\u5979\u8033\u8fb9\u8bf4\u7740\u60c5\u8bdd\uff0c\u603b\u7b97\u54c4\u5f97\u5c0f\u5973\u5b69\u7834\u6d95\u4e3a\u7b11\uff0c\u201c\u4f60\u574f\u6b7b\u4e86\uff0c\u201d\u5979\u57cb\u6028\uff0c\u201c\u90a3\u4e48\u51f6\u5e72\u561b\uff0c\u53c8\u4e0d\u662f\u4e0d\u8ba9\u4f60\u2026\u2026\u201d</p><p>\u201c\u56e0\u4e3a\u592a\u559c\u6b22\u4f60\u4e86\u561b\uff0c\u201d\u743c\u6069\u66ff\u5979\u62ed\u53bb\u6cea\u6c34\uff0c\u5c06\u7ed1\u4f4f\u5979\u53cc\u81c2\u7684\u7761\u8863\u53d6\u4e0b\u6765\uff0c\u4e22\u5728\u4e00\u8fb9\uff0c\u201c\u4e00\u65f6\u5fcd\u4e0d\u4f4f\u2026\u2026\u51e0\u4e2a\u6708\u6ca1\u78b0\u4f60\uff0c\u597d\u50cf\u53d8\u5f97\u66f4\u7d27\u4e86\u5462\u3002\u201d</p><p>\u8299\u857e\u72c4\u8138\u4e0a\u53d1\u70e7\uff0c\u201c\u54ea\u6709\uff0c\u201d\u5979\u5a07\u55d4\u7740\uff0c\u201c\u662f\u4f60\u53d8\u5f97\u66f4\u5927\u4e86\uff0c\u6211\u90fd\u53d7\u4e0d\u4e86\u4e86\u3002\u201d</p><p>\u6240\u6709\u7537\u4eba\u90fd\u4f1a\u559c\u6b22\u542c\u5230\u8fd9\u79cd\u5938\u5956\uff0c\u743c\u6069\u4e5f\u4e0d\u4f8b\u5916\uff0c\u201c\u5f04\u75bc\u4e86\uff1f\u201d\u4ed6\u6084\u58f0\u95ee\u3002</p><p>\u201c\u5514\u2026\u2026\u4e0d\u8fc7\u597d\u8212\u670d\uff0c\u6700\u540e\u611f\u89c9\u50cf\u8981\u5d29\u6e83\u4e86\u4f3c\u7684\uff0c\u201d\u5c0f\u5973\u5b69\u8138\u4e0a\u65e0\u9650\u5a07\u7f9e\uff0c\u201c\u53ef\u80fd\u597d\u4e45\u90fd\u6ca1\u6709\u2026\u2026\u611f\u89c9\u6bd4\u5370\u8c61\u91cc\u683c\u5916\u5f3a\u70c8\u3002\u201d</p><p>\u201c\u4e0d\u5bf9\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u54ac\u7740\u5979\u7684\u8033\u5782\uff0c\u201c\u662f\u56e0\u4e3a\u4f60\u73b0\u5728\u7684\u611f\u89c9\u662f\u53cc\u500d\u7684\u2014\u2014\u522b\u5fd8\u4e86\u4f60\u59d0\u59d0\u90a3\u4e00\u4efd\u5462\u3002\u201d</p><p>\u201c\u8ba8\u538c\uff01\u201d\u8299\u857e\u72c4\u6492\u5a07\uff0c\u201c\u660e\u660e\u662f\u4f60\u592a\u7528\u529b\u4e86\uff0c\u800c\u4e14\u8fd8\u90a3\u4e48\u51f6\u2026\u2026\u201d</p><p>\u201c\u6709\u4e48\u3002\u201d</p><p>\u201c\u660e\u660e\u5c31\u662f\u554a\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u800c\u4e14\u4f60\u751f\u6c14\u7684\u6837\u5b50\u597d\u53ef\u6015\uff0c\u6211\u4ece\u6765\u6ca1\u89c1\u4f60\u751f\u6c14\u8fc7\u7684\u2026\u2026\u201d</p><p>\u743c\u6069\u6014\u4e86\u4e00\u6014\uff0c\u4eff\u4f5b\u8111\u4e2d\u6709\u6839\u5f26\u88ab\u62e8\u52a8\u4e86\u4f3c\u7684\uff0c\u50cf\u662f\u60f3\u8d77\u4e86\u4ec0\u4e48\uff0c\u4e00\u65f6\u5374\u53c8\u628a\u63e1\u4e0d\u4f4f\u3002\u201c\u4f60\u521a\u624d\u8bf4\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u6211\u8bf4\u4f60\u7a81\u7136\u53d8\u5f97\u5f88\u51f6\uff0c\u800c\u4e14\u751f\u6c14\u7684\u65f6\u5019\u5f88\u5413\u4eba\u7684\u3002\u201d</p><p>\u743c\u6069\u60f3\u4e86\u8d77\u6765\uff0c\u4e0a\u6b21\u4ed6\u5728\u9634\u9b42\u57ce\u91cc\u548c\u6885\u83f2\u65af\u6b22\u597d\u65f6\uff0c\u5979\u4e5f\u8bf4\u8fc7\u7c7b\u4f3c\u7684\u8bdd\uff0c\u8bf4\u4ed6\u597d\u50cf\u7a81\u7136\u53d8\u5f97\u7c97\u66b4\u4e86\u5f88\u591a\u3002\u800c\u4e14\uff0c\u81ea\u5df1\u521a\u624d\u5c45\u7136\u52a8\u6012\u4e86\u4e48\uff1f</p><p>\u5766\u767d\u5730\u8bf4\uff0c\u743c\u6069\u7b97\u5f97\u4e0a\u80f8\u65e0\u5927\u5fd7\u7684\u7c7b\u578b\uff0c\u5c31\u662f\u4e2a\u6807\u51c6\u7684\u666e\u901a\u4eba\u3002\u522b\u4eba\u7a7f\u8d8a\u91cd\u751f\uff0c\u6216\u8005\u60f3\u7740\u5c01\u795e\u6210\u5723\uff0c\u957f\u751f\u4e0d\u6b7b\uff0c\u6216\u8005\u60f3\u7740\u79f0\u738b\u79f0\u5e1d\uff0c\u4e00\u7edf\u5929\u4e0b\uff0c\u800c\u8fd9\u4e9b\u5b8f\u4f1f\u5fd7\u5411\u548c\u4ed6\u5168\u4e0d\u6cbe\u8fb9\u3002\u5728\u4e00\u5f00\u59cb\uff0c\u4ed6\u7684\u68a6\u60f3\u4ec5\u4ec5\u662f\u80fd\u8131\u79bb\u5e73\u6c11\u8eab\u4efd\uff0c\u5f53\u4e0a\u5deb\u5e08\uff0c\u540e\u6765\u6539\u6210\u5f53\u4e00\u4e2a\u5f3a\u5927\u7684\u5deb\u5e08\uff0c\u76f4\u5230\u5982\u4eca\uff0c\u4ed6\u6240\u60f3\u7684\u4e5f\u4e0d\u8fc7\u5c31\u662f\u80fd\u8df3\u51fa\u68cb\u5c40\uff0c\u8131\u79bb\u638c\u63a7\uff0c\u4e0d\u518d\u53d7\u5236\u4e8e\u4eba\u3002\u4ed6\u867d\u7136\u8bf4\u4e0d\u4e0a\u591a\u4e48\u5fc3\u673a\u6df1\u6c89\uff0c\u80f8\u6709\u57ce\u5e9c\uff0c\u7ec8\u7a76\u4e5f\u662f\u6bd4\u540c\u9f84\u4eba\u591a\u4e86\u4e8c\u5341\u4f59\u5e74\u7684\u8bb0\u5fc6\uff0c\u5e73\u7d20\u4e0e\u4eba\u76f8\u5904\uff0c\u6781\u5c11\u6709\u559c\u6012\u4f5c\u8272\u7684\u65f6\u5019\uff0c\u5c31\u7b97\u662f\u5f53\u65e5\u88ab\u8299\u857e\u72c4\u9a97\u4e86\uff0c\u773c\u770b\u5230\u624b\u7684\u8fdb\u5165\u5965\u672f\u5e08\u5b66\u6821\u7684\u673a\u4f1a\u843d\u7a7a\uff0c\u4e5f\u4f9d\u65e7\u5e73\u9759\u9762\u5bf9\uff0c\u4e0d\u66fe\u5931\u4e86\u6c14\u5ea6\u3002\u7136\u800c\uff0c\u521a\u624d\u81ea\u5df1\u4f3c\u4e4e\u786e\u5b9e\u662f\u607c\u6012\u4e86\u5462\u3002</p><p>\u4e0d\u5bf9\uff0c\u8fd9\u5176\u4e2d\u6709\u4e9b\u4e0d\u592a\u5bf9\u52b2\u2026\u2026\u597d\u50cf\u6700\u8fd1\u5fc3\u4e2d\u623e\u6c14\u6ecb\u957f\uff0c\u81ea\u63a7\u80fd\u529b\u51cf\u5f31\u4f3c\u7684\uff0c\u5e73\u65f6\u6216\u8bb8\u8fd8\u770b\u4e0d\u51fa\u6765\uff0c\u6709\u4e9b\u60c5\u7eea\u6fc0\u8361\u7684\u65f6\u5019\u5c31\u6bd4\u8f83\u660e\u663e\u4e86\uff0c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f</p><p>\u96be\u9053\u548c\u81ea\u5df1\u5438\u6536\u7684\u5df4\u5c14\u795e\u529b\u6709\u5173\uff1f\u7b97\u7b97\u65f6\u95f4\uff0c\u4f3c\u4e4e\u4e5f\u6b63\u662f\u5728\u90a3\u4e4b\u540e\u2026\u2026</p><p>\u743c\u6069\u6c89\u541f\u7740\uff0c\u4e00\u65f6\u6ca1\u6709\u8bf4\u8bdd\uff0c\u8299\u857e\u72c4\u89c1\u4ed6\u7a81\u7136\u6c89\u9ed8\uff0c\u8fd8\u4ee5\u4e3a\u81ea\u5df1\u8bf4\u9519\u4e86\u4ec0\u4e48\uff0c\u201c\u600e\u4e48\u4e86\uff0c\u743c\u6069\uff0c\u201d\u5979\u602f\u602f\u5730\u95ee\uff0c\u201c\u6211\u8bf4\u9519\u8bdd\u4e86\uff1f\u201d</p><p>\u201c\u6ca1\uff0c\u201d\u743c\u6069\u8f7b\u8f7b\u629a\u6478\u7740\u5979\u7684\u8138\u86cb\uff0c\u5c06\u8111\u4e2d\u7eb7\u4e71\u7684\u5ff5\u5934\u6452\u53bb\uff0c\u201c\u6211\u53ea\u662f\u7a81\u7136\u60f3\u5230\u4ef6\u6bd4\u8f83\u9ebb\u70e6\u7684\u4e8b\u60c5\u7f62\u4e86\u3002\u201d</p><p>\u201c\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d</p><p>\u201c\u5514\uff0c\u6211\u5728\u60f3\u2026\u2026\u4f60\u59d0\u59d0\uff0c\u5e94\u8be5\u548c\u4f60\u6709\u540c\u6837\u7684\u611f\u89c9\u5427\u3002\u201d</p><p>\u8299\u857e\u72c4\u8138\u4e0a\u6cdb\u7ea2\uff0c\u201c\u8ba8\u538c\uff0c\u201d\u5979\u8f7b\u8f7b\u6376\u7740\u743c\u6069\u7684\u80f8\u53e3\uff0c\u201c\u522b\u8bf4\u4e86\u3002\u201d</p><p>\u201c\u5fc5\u987b\u5f97\u8bf4\uff0c\u201d\u743c\u6069\u8ba4\u771f\u5730\u770b\u7740\u5979\uff0c\u201c\u8299\u857e\u72c4\uff0c\u4f60\u559c\u6b22\u6211\u5bf9\u4e0d\u5bf9\uff1f\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u201c\u53ea\u613f\u610f\u88ab\u6211\u5403\u5bf9\u4e0d\u5bf9\uff1f\u201d</p><p>\u201c\u5514\u3002\u201d</p><p>\u201c\u90a3\u4f60\u770b\uff0c\u6211\u548c\u4f60\u5728\u4e00\u8d77\u7684\u65f6\u5019\uff0c\u4f60\u59d0\u59d0\u4f1a\u6709\u611f\u89c9\u3002\u7b49\u5c06\u6765\u5979\u6709\u4e86\u60c5\u4eba\u2026\u2026\u201d\u743c\u6069\u629a\u6478\u7740\u5979\u7684\u79c0\u53d1\uff0c\u201c\u4f60\u600e\u4e48\u529e\uff1f\u201d</p><p>\u8299\u857e\u72c4\u970d\u7136\u4e00\u60ca\uff0c\u8138\u8272\u5239\u90a3\u95f4\u53d8\u5f97\u82cd\u767d\uff0c\u5979\u53ea\u662f\u5355\u7eaf\uff0c\u5e76\u975e\u611a\u7b28\uff0c\u8fd9\u4e2a\u9053\u7406\u5979\u4ee5\u524d\u5176\u5b9e\u4e5f\u5e76\u975e\u6ca1\u6709\u5bdf\u89c9\u5230\uff0c\u53ea\u662f\u6f5c\u610f\u8bc6\u91cc\u9003\u907f\uff0c\u62d2\u7edd\u53bb\u60f3\u3002\u5982\u4eca\u88ab\u743c\u6069\u7a81\u7136\u70b9\u7834\uff0c\u987f\u65f6\u6709\u4e9b\u60f6\u7136\u4e0d\u77e5\u6240\u63aa\uff0c\u4e00\u60f3\u5230\u5c06\u6765\u4f1a\u88ab\u67d0\u4e2a\u964c\u751f\u7684\u7537\u5b50\u4fb5\u5360\uff0c\u4e0d\u7531\u5f97\u5168\u8eab\u90fd\u8f7b\u5fae\u98a4\u6296\u8d77\u6765\uff0c\u5374\u53c8\u4e0d\u77e5\u9053\u5982\u4f55\u662f\u597d\u3002\u53cc\u80de\u80ce\u95f4\u7684\u5fc3\u7075\u611f\u5e94\u662f\u4e0e\u751f\u4ff1\u6765\u7684\u5929\u8d4b\uff0c\u53c8\u6ca1\u6709\u529e\u6cd5\u963b\u9694\u5207\u65ad\uff0c\u96be\u4e0d\u6210\u8fd8\u4e00\u8f88\u5b50\u4e0d\u51c6\u8299\u8389\u5a05\u6709\u60c5\u4eba\u4e48\uff0c\u4e16\u754c\u4e0a\u54ea\u6709\u8fd9\u79cd\u9053\u7406\u3002</p><p>\u201c\u90a3\u600e\u4e48\u529e\uff1f\u201d\u5979\u832b\u7136\u53cd\u95ee\u3002</p><p>\u201c\u5176\u5b9e\u4e5f\u5f88\u5bb9\u6613\u554a\uff0c\u201d\u743c\u6069\u534a\u771f\u534a\u5047\u5730\u8bf4\uff0c\u201c\u53ea\u8981\u4f60\u4eec\u59d0\u59b9\u4fe9\u90fd\u559c\u6b22\u540c\u4e00\u4e2a\u7537\u4eba\uff0c\u4e0d\u5c31\u4ec0\u4e48\u95ee\u9898\u90fd\u89e3\u51b3\u4e86\u3002\u201d</p><p>\u201c\u90fd\u559c\u6b22\u540c\u4e00\u4e2a\u2026\u2026\u201d\u8299\u857e\u72c4\u53cd\u5e94\u8fc7\u6765\uff0c\u77a5\u4e86\u4ed6\u4e00\u773c\uff0c\u201c\u4f60\u53c8\u770b\u4e0a\u5979\u4e86\u554a\u3002\u201d</p><p>\u201c\u5ac9\u5992\u4e86\uff1f\u201d</p><p>\u201c\u624d\u6ca1\u6709\u5462\uff0c\u201d\u5979\u54ac\u7740\u5634\u5507\uff0c\u6a21\u6837\u8bf4\u4e0d\u51fa\u7684\u53ef\u7231\uff0c\u201c\u5ac9\u5992\u5979\u5e72\u561b\uff0c\u518d\u8bf4\uff0c\u5979\u53c8\u54ea\u91cc\u6bd4\u6211\u597d\u4e86\u3002\u201d</p><p>\u743c\u6069\u7b11\u4e86\u8d77\u6765\uff0c\u634f\u634f\u5979\u7684\u8138\u86cb\uff0c\u201c\u8fd8\u8bf4\u6ca1\u6709\uff1f\u201d</p><p>\u201c\u672c\u6765\u5c31\u662f\u6ca1\u6709\u3002\u201d\u5c0f\u5973\u5b69\u5634\u786c\u3002</p><p>\u201c\u90a3\u6211\u53bb\u628a\u5979\u62b1\u8fc7\u6765\uff1f\u201d\u743c\u6069\u6545\u610f\u9017\u5979\uff0c\u201c\u4f30\u8ba1\u5979\u73b0\u5728\u4e5f\u6ca1\u4ec0\u4e48\u529b\u6c14\u4e86\u5427\u3002\u201d</p><p>\u201c\u5979\u624d\u4e0d\u559c\u6b22\u4f60\u5462\u3002\u201d</p><p>\u201c\u672a\u5fc5\u5462\uff0c\u4f60\u4eec\u662f\u59d0\u59b9\uff0c\u559c\u6b22\u7684\u7537\u4eba\u5e94\u8be5\u4e5f\u5dee\u4e0d\u591a\u5427\u3002\u201d</p><p>\u8299\u857e\u72c4\u89c1\u4ed6\u8bf4\u5f97\u4e00\u672c\u6b63\u7ecf\uff0c\u4fe1\u4ee5\u4e3a\u771f\uff0c\u4e0b\u610f\u8bc6\u5730\u62b1\u7d27\u4e86\u743c\u6069\uff0c\u4e0d\u653e\u4ed6\u79bb\u5f00\uff0c\u201c\u4e0d\u51c6\u8d70\uff0c\u201d\u5979\u6073\u6c42\u7740\uff0c\u201c\u4f60\u8bf4\u8fc7\u4eca\u665a\u966a\u6211\u7684\u3002\u201d</p><p>\u743c\u6069\u4f4e\u58f0\u7b11\u7740\uff0c\u6ca1\u6709\u518d\u8bf4\u8bdd\uff0c\u4fef\u8eab\u4eb2\u543b\u5979\u7684\u5634\u5507\uff0c\u54c1\u5c1d\u7740\u9999\u751c\u7684\u6d25\u6db2\uff0c\u539f\u672c\u6709\u4e9b\u8f6f\u5316\u8ff9\u8c61\u7684\u5bb6\u4f19\u53c8\u8822\u8822\u6b32\u52a8\u8d77\u6765\u3002\u8299\u857e\u72c4\u611f\u89c9\u4f53\u5185\u4e00\u9635\u9635\u706b\u70ed\u81a8\u80c0\uff0c\u70eb\u5f97\u5979\u51e0\u4e4e\u8981\u9165\u8f6f\u4e0b\u53bb\uff0c\u53ea\u80fd\u7d27\u7d27\u62b1\u7740\u5bf9\u65b9\u7684\u8116\u9888\u3002\u201c\u8fd8\u60f3\u4e0d\u60f3\u518d\u6765\u4e00\u6b21\uff1f\u201d\u743c\u6069\u8f7b\u58f0\u95ee\u3002</p><p>\u201c\u5514\u3002\u201d</p><p>\u8299\u857e\u72c4\u4f4e\u4f4e\u5730\u5e94\u7740\uff0c\u5fc3\u4e2d\u5176\u5b9e\u6709\u4e9b\u5bb3\u6015\uff0c\u5979\u4e0b\u8eab\u8fd8\u9690\u9690\u75bc\u75db\uff0c\u53ea\u6015\u627f\u53d7\u4e0d\u4f4f\u518d\u4e00\u8f6e\u72c2\u98ce\u66b4\u96e8\uff0c\u53c8\u4e0d\u613f\u62c2\u4e86\u743c\u6069\u7684\u610f\u3002\u6b63\u5fd0\u5fd1\u95f4\uff0c\u611f\u89c9\u743c\u6069\u7f13\u7f13\u4ece\u5979\u8eab\u4f53\u91cc\u9000\u4e86\u51fa\u6765\uff0c\u5374\u5e76\u4e0d\u518d\u6b21\u8fdb\u5165\uff0c\u201c\u7761\u5427\uff0c\u201d\u4ed6\u6e29\u67d4\u5730\u8bf4\uff0c\u201c\u4e0d\u65e9\u4e86\u3002\u201d</p><p>\u201c\u53ef\u662f\u4f60\u8fd8\u6ca1\u2026\u2026\u201d</p><p>\u201c\u660e\u5929\u5427\uff0c\u201d\u743c\u6069\u543b\u7740\u5979\uff0c\u201c\u6211\u53ef\u4e0d\u60f3\u628a\u4f60\u5f04\u4f24\u4e86\uff0c\u597d\u597d\u4f11\u606f\uff0c\u660e\u5929\u8fd8\u6709\u4e8b\u60c5\u5462\u3002\u201d</p><p>\u8299\u857e\u72c4\u5c06\u8138\u57cb\u5728\u4ed6\u80f8\u53e3\uff0c\u201c\u743c\u6069\uff0c\u201d\u5979\u4f4e\u4f4e\u5730\u53eb\u7740\u4ed6\u7684\u540d\u5b57\uff0c\u201c\u8981\u4e48\uff0c\u4f60\u7528\u6211\u2026\u2026\u540e\u9762\u2026\u2026\u597d\u4e86\u3002\u201d</p><p>\u743c\u6069\u5012\u662f\u6014\u4e86\u6014\uff0c\u9887\u4e3a\u51fa\u4e4e\u610f\u6599\uff0c\u6ca1\u60f3\u5230\u5979\u5c45\u7136\u4f1a\u8fd9\u4e48\u8bf4\uff0c\u201c\u50bb\u4e2b\u5934\uff0c\u201d\u4ed6\u6709\u4e9b\u597d\u5947\uff0c\u201c\u4f60\u4ece\u54ea\u91cc\u77e5\u9053\u7684\uff1f\u6211\u53ef\u4e0d\u8bb0\u5f97\u4ee5\u524d\u8ddf\u4f60\u8bf4\u8fc7\u554a\u3002\u201d</p><p>\u201c\u4e66\u4e0a\u770b\u5230\u7684\uff0c\u201d\u8299\u857e\u72c4\u58f0\u97f3\u8d8a\u6765\u8d8a\u4f4e\uff0c\u201c\u800c\u4e14\u6211\u4e5f\u2026\u2026\u505a\u597d\u6e05\u6d01\u4e86\uff0c\u6d17\u6fa1\u7684\u65f6\u5019\u2026\u2026\u201d</p><p>\u201c\u4f1a\u5f88\u75bc\u7684\u3002\u201d</p><p>\u201c\u6211\u4e0d\u6015\u3002\u201d</p><p>\u743c\u6069\u6258\u7740\u5979\u7684\u4e0b\u5df4\uff0c\u5c06\u5979\u8138\u62ac\u8d77\u6765\uff0c\u5728\u9ed1\u6697\u4e2d\u76f4\u89c6\u7740\uff0c\u201c\u4e3a\u4ec0\u4e48\u5462\uff1f\u201d\u4ed6\u95ee\uff0c\u201c\u600e\u4e48\u7a81\u7136\u6709\u8fd9\u79cd\u60f3\u6cd5\uff1f\u201d</p><p>\u201c\u56e0\u4e3a\uff0c\u6211\u4e00\u5e74\u5c31\u4e00\u6b21\u751f\u65e5\u554a\uff0c\u201d\u5c0f\u5973\u5b69\u56de\u7b54\uff0c\u201c\u5c31\u8fd9\u4e00\u6b21\u673a\u4f1a\uff0c\u6211\u53ef\u4ee5\u6c42\u4f60\u966a\u7740\u6211\u2026\u2026\u6211\u60f3\u628a\u4ec0\u4e48\u90fd\u7ed9\u4f60\u3002\u201d</p><p>\u743c\u6069\u5b9a\u5b9a\u5730\u770b\u7740\u5979\uff0c\u201c\u5e72\u561b\u90a3\u4e48\u559c\u6b22\u6211\u554a\u3002\u201d</p><p>\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\uff0c\u201d\u5979\u4e60\u60ef\u6027\u5730\u54ac\u7740\u5634\u5507\uff0c\u201c\u53cd\u6b63\uff0c\u5c31\u662f\u559c\u6b22\u554a\u3002\u548c\u4f60\u5206\u5f00\u90a3\u51e0\u4e2a\u6708\uff0c\u6211\u767d\u5929\u6e05\u9192\u7740\uff0c\u6ee1\u8111\u5b50\u90fd\u662f\u4f60\uff1b\u665a\u4e0a\u7761\u4e0d\u597d\uff0c\u6574\u591c\u6574\u591c\u5730\u68a6\u89c1\u4f60\u3002\u6211\u60f3\u5f85\u5728\u4f60\u8eab\u8fb9\uff0c\u4e0d\u7136\u505a\u4ec0\u4e48\u90fd\u4e0d\u5b89\u5fc3\uff1b\u6211\u559c\u6b22\u7f20\u7740\u4f60\uff0c\u8d56\u7740\u4f60\uff0c\u62b1\u7740\u4f60\u2026\u2026\u6211\u751a\u81f3\u60f3\uff0c\u8981\u662f\u6211\u53ef\u4ee5\u505a\u4f60\u7684\u9b54\u5ba0\u5c31\u597d\u4e86\uff0c\u5c31\u53ef\u4ee5\u5929\u5929\u966a\u7740\u4f60\uff0c\u5929\u5929\u5728\u4f60\u8eab\u8fb9\uff0c\u4f60\u8d70\u5230\u54ea\u91cc\u90fd\u4f1a\u5e26\u7740\u6211\uff0c\u6c38\u8fdc\u90fd\u4e0d\u5206\u5f00\u4e86\u3002\u201d</p><p>\u743c\u6069\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u4f60\u662f\u4eba\u7c7b\uff0c\u53ef\u6ca1\u529e\u6cd5\u6210\u4e3a\u9b54\u5ba0\u5462\u3002\u201d</p><p>\u901a\u8fc7\u4e00\u5b9a\u7684\u5951\u7ea6\u4eea\u5f0f\uff0c\u5deb\u5e08\u53ef\u4ee5\u548c\u67d0\u4e9b\u7279\u6b8a\u7684\u5f02\u4f4d\u9762\u5b58\u5728\u5efa\u7acb\u65e0\u5f62\u7684\u7cbe\u795e\u8054\u7ed3\uff0c\u53ec\u5524\u4ed6\u4eec\u4f5c\u4e3a\u81ea\u5df1\u7684\u9b54\u5ba0\u3002\u8fd9\u4e9b\u5f02\u4f4d\u9762\u5b58\u5728\u6765\u5230\u7269\u8d28\u754c\uff0c\u5927\u591a\u90fd\u4f1a\u9009\u62e9\u67d0\u79cd\u5c0f\u52a8\u7269\u7684\u5916\u8c8c\uff0c\u4f8b\u5982\u8759\u8760\u3001\u87fe\u870d\u3001\u6e21\u9e26\u6216\u8005\u732b\u5934\u9e70\u4e4b\u7c7b\uff0c\u4f46\u4e5f\u6709\u4fdd\u6301\u539f\u672c\u5f62\u6001\u7684\u3002\u8299\u857e\u72c4\u662f\u6807\u51c6\u7684\u7269\u8d28\u754c\u4eba\u7c7b\uff0c\u81ea\u7136\u4e0d\u53ef\u80fd\u6210\u4e3a\u9b54\u5ba0\uff0c\u8fd9\u4e5f\u53ea\u662f\u8bf4\u8bf4\u7f62\u4e86\u3002</p><p>\u201c\u5982\u679c\u4f60\u771f\u6210\u4e3a\u9b54\u5ba0\uff0c\u90a3\u4e07\u4e00\u6211\u6b7b\u4e86\uff0c\u4f60\u53ef\u4e5f\u6d3b\u4e0d\u4e86\u4e86\u5462\u3002\u201d\u743c\u6069\u5f00\u73a9\u7b11\u3002</p><p>\u201c\u4f60\u6b7b\u4e86\uff0c\u6211\u4e5f\u4e0d\u6d3b\u4e86\u3002\u201d</p><p>\u743c\u6069\u542c\u5979\u8bed\u6c14\u575a\u5b9a\uff0c\u65a9\u9489\u622a\u94c1\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e2d\u4e00\u9635\u611f\u52a8\u3002\u4ed6\u8eab\u8fb9\u7684\u5973\u5b50\u4e5f\u4e0d\u5c11\uff0c\u82e5\u8bba\u60c5\u5206\uff0c\u73ca\u5609\u548c\u6885\u83f2\u65af\uff0c\u4e5f\u4e0d\u6bd4\u8299\u857e\u72c4\u5dee\u4e86\uff0c\u4f46\u8981\u8bf4\u8fd9\u79cd\u5b8c\u5b8c\u5168\u5168\u7684\u4fe1\u8d56\u4f9d\u604b\uff0c\u751f\u6b7b\u76f8\u8bb8\uff0c\u53ea\u6015\u8fd8\u662f\u505a\u4e0d\u5230\u7684\u3002\u8fd9\u79cd\u611f\u89c9\u2026\u2026\u5fc3\u91cc\u6696\u6696\u7684\uff0c\u5f88\u597d\u5462\u3002</p><p>\u201c\u7761\u5427\u3002\u201d\u4ed6\u8f7b\u8f7b\u8bf4\u3002</p><p>\u201c\u6211\u4e0d\u6015\u7684\u3002\u201d\u8299\u857e\u72c4\u9f13\u8db3\u52c7\u6c14\u8bf4\u3002</p><p>\u201c\u53ef\u662f\u6211\u6015\u554a\uff0c\u201d\u743c\u6069\u7b11\u7740\uff0c\u201c\u8fd9\u79cd\u4e8b\u60c5\u9700\u8981\u6162\u6162\u6765\uff0c\u6211\u6015\u628a\u6211\u559c\u6b22\u7684\u4eba\u5f04\u4f24\u4e86\u5462\u3002\u53cd\u6b63\uff0c\u53c8\u4e0d\u7528\u6025\u4e8e\u8fd9\u4e00\u65f6\uff0c\u201d\u4ed6\u4f4e\u5934\u543b\u7740\u8299\u857e\u72c4\u7684\u7709\u6bdb\uff0c\u201c\u4ee5\u540e\u673a\u4f1a\u591a\u5f97\u662f\u2026\u2026\u660e\u5929\u665a\u4e0a\u597d\u4e0d\u597d\uff1f\u201d</p><p>\u201c\u660e\u5929\u665a\u4e0a\u2026\u2026\u201d\u8299\u857e\u72c4\u91cd\u590d\u7740\uff0c\u8138\u4e0a\u9732\u51fa\u6b23\u559c\u7684\u795e\u60c5\u6765\uff0c\u5979\u5df2\u7ecf\u542c\u660e\u767d\u4e86\u743c\u6069\u7684\u610f\u601d\u3002\u201c\u4f60\u539f\u8c05\u6211\u4e86\uff1f\u201d</p><p>\u201c\u539f\u8c05\u4f60\u4e86\uff0c\u201d\u743c\u6069\u8f7b\u8f7b\u8bf4\uff0c\u201c\u8fc7\u53bb\u90a3\u70b9\u4e8b\u60c5\uff0c\u6211\u5168\u90fd\u4e0d\u8bb0\u5f97\u4e86\u3002\u8299\u857e\u72c4\uff0c\u6211\u4eec\u91cd\u65b0\u5f00\u59cb\u597d\u4e0d\u597d\u3002\u201d</p><p>\u8299\u857e\u72c4\u7d27\u7d27\u5c06\u4ed6\u62b1\u4f4f\uff0c\u773c\u6cea\u5fcd\u4e0d\u4f4f\u6d8c\u4e86\u51fa\u6765\u3002</p></article>"}}}]);
