(window.webpackJsonp=window.webpackJsonp||[]).push([[245],{267:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u516d\u8282 \u5bfb\u89c5",content:"<article><h2>\u7b2c\u5341\u516d\u8282 \u5bfb\u89c5</h2><p>\u63a5\u8fde\u88ab\u9001\u4e0a\u4e24\u6b21\u9ad8\u6f6e\uff0c\u8299\u857e\u72c4\u5df2\u7ecf\u7cbe\u75b2\u529b\u7aed\uff0c\u5728\u743c\u6069\u6000\u4e2d\u6c89\u6c89\u7761\u53bb\uff0c\u773c\u89d2\u776b\u6bdb\u4e0a\u72b9\u81ea\u8fd8\u6302\u7740\u6676\u83b9\u6cea\u73e0\uff0c\u8138\u4e0a\u5374\u5c3d\u662f\u5e78\u798f\u7684\u7b11\u610f\u3002\u743c\u6069\u5728\u9ed1\u6697\u4e2d\u9759\u9759\u770b\u7740\u5979\uff0c\u60f3\u7740\u4ee5\u540e\u7684\u4e8b\u60c5\u8be5\u5982\u4f55\u5904\u7406\uff0c\u9762\u4e34\u7684\u96be\u9898\u53c8\u5982\u4f55\u89e3\u51b3\uff0c\u5982\u4eca\u548c\u8299\u857e\u72c4\u91cd\u5f52\u4e8e\u597d\uff0c\u5230\u65f6\u5019\u89c1\u4e86\u6885\u83f2\u65af\u53c8\u600e\u4e48\u89e3\u91ca\u3002\u601d\u6765\u60f3\u53bb\uff0c\u5343\u5934\u4e07\u7eea\u7eb7\u4e71\u6d8c\u4e0a\u5fc3\u5934\uff0c\u4e00\u65f6\u7406\u4e0d\u6e05\u695a\uff0c\u6e10\u6e10\u5730\u5026\u610f\u4e5f\u6d8c\u4e0a\u6765\uff0c\u4e0d\u77e5\u4e0d\u89c9\u95f4\u7761\u4e86\u8fc7\u53bb\u3002</p><p>\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u4ed6\u518d\u6b21\u9192\u6765\uff0c\u9690\u9690\u7ea6\u7ea6\u6709\u4e00\u80a1\u6696\u6d0b\u6d0b\u7684\u611f\u89c9\u81ea\u4e0b\u8eab\u6d8c\u4e0a\u6765\uff0c\u6574\u4e2a\u4eba\u4eff\u4f5b\u90fd\u6ce1\u5728\u6e29\u6c34\u4e2d\u4e00\u822c\uff0c\u8bf4\u4e0d\u51fa\u7684\u8212\u7545\u3002\u8ff7\u8ff7\u7cca\u7cca\u95f4\u5f80\u4e0b\u770b\u53bb\uff0c\u53ea\u89c1\u8299\u857e\u72c4\u4e00\u4e1d\u4e0d\u6302\u5730\u8dea\u4f0f\u5728\u4ed6\u817f\u95f4\uff0c\u6b63\u4e56\u5de7\u5730\u7528\u5507\u820c\u4e3a\u4ed6\u670d\u52a1\u3002\u89c1\u4ed6\u9192\u6765\uff0c\u62ac\u8d77\u4fcf\u8138\u751c\u751c\u4e00\u7b11\uff0c\u201c\u9192\u4e86\uff1f\u201d</p><p>\u201c\u5514\u3002\u201d</p><p>\u8299\u857e\u72c4\u4f4e\u4e0b\u5934\u53bb\uff0c\u7ee7\u7eed\u4e13\u5fc3\u5de5\u4f5c\uff0c\u743c\u6069\u8111\u4e2d\u8fd8\u6709\u4e9b\u6655\u6655\u6c89\u6c89\u7684\uff0c\u4e00\u65f6\u6ca1\u6709\u6e05\u9192\u8fc7\u6765\u3002\u604d\u60da\u4e4b\u95f4\uff0c\u4eff\u4f5b\u53c8\u56de\u5230\u4e86\u4ece\u524d\uff0c\u6bcf\u5929\u65e9\u4e0a\u90fd\u8981\u4eab\u53d7\u8fc7\u8299\u857e\u72c4\u7684\u65e9\u5b89\u54ac\uff0c\u7136\u540e\u795e\u6e05\u6c14\u723d\u5730\u5f00\u59cb\u4e00\u5929\u7684\u5b66\u4e60\u3002\u4ed6\u5b9a\u4e86\u5b9a\u795e\uff0c\u5c0f\u5fc3\u5730\u5750\u8d77\u8eab\u6765\uff0c\u6b23\u8d4f\u7740\u8299\u857e\u72c4\u7684\u88f8\u4f53\uff0c\u5979\u8eab\u6750\u82d7\u6761\uff0c\u80a9\u80cc\u5355\u8584\uff0c\u8170\u80a2\u7ea4\u7ec6\uff0c\u56e0\u4e3a\u662f\u8dea\u4f0f\u7740\uff0c\u81c0\u90e8\u9ad8\u9ad8\u7fd8\u8d77\uff0c\u770b\u8d77\u6765\u4fbf\u5982\u6c34\u871c\u6843\u822c\u8bf1\u4eba\u3002\u4e00\u5934\u4e4c\u9ed1\u7684\u957f\u53d1\u6563\u5782\u4e0b\u6765\uff0c\u6d12\u5728\u5e8a\u4e0a\uff0c\u4eff\u4f5b\u7ef8\u7f0e\u4e00\u822c\u7cbe\u81f4\u5149\u6ed1\uff0c\u886c\u6258\u7740\u808c\u80a4\u683c\u5916\u96ea\u767d\u3002\u65e0\u8bba\u662f\u8eab\u6750\u8fd8\u662f\u76f8\u8c8c\uff0c\u8299\u857e\u72c4\u90fd\u6beb\u65e0\u7591\u95ee\u79f0\u5f97\u4e0a\u662f\u7b2c\u4e00\u6d41\u7684\u7f8e\u4e3d\u5c11\u5973\uff0c\u4f55\u51b5\u5979\u5982\u4eca\u624d\u521a\u521a\u5341\u4e94\u5c81\uff0c\u5e74\u9f7f\u5c1a\u5e7c\uff0c\u7a1a\u6c14\u672a\u8131\uff0c\u4eff\u4f5b\u9752\u6da9\u679c\u5b9e\uff0c\u5c06\u6765\u6210\u719f\u4e86\u8fd8\u4e0d\u77e5\u9053\u80fd\u8ff7\u5012\u591a\u5c11\u7537\u5b50\u3002\u743c\u6069\u60f3\u5230\u8fd9\u6837\u7684\u7f8e\u5973\u5374\u5bf9\u81ea\u5df1\u5343\u4f9d\u767e\u987a\uff0c\u7528\u60c5\u81f3\u6df1\uff0c\u5ff5\u53ca\u6b64\u5904\uff0c\u4e0d\u7531\u5f97\u4e5f\u6709\u4e9b\u98d8\u98d8\u7136\u3002</p><p>\u8299\u857e\u72c4\u5374\u4e0d\u77e5\u9053\u743c\u6069\u5728\u60f3\u4ec0\u4e48\uff0c\u53ea\u662f\u4e13\u5fc3\u670d\u4f8d\uff0c\u4e0d\u65f6\u5077\u5077\u62ac\u773c\u770b\u4ed6\u7684\u53cd\u5e94\uff0c\u552f\u6050\u81ea\u5df1\u505a\u5f97\u4e0d\u597d\u3002\u5f80\u65e5\u5728\u4e00\u8d77\u7684\u65f6\u5019\uff0c\u5979\u6bcf\u5929\u88ab\u743c\u6069\u201c\u8bad\u7ec3\u201d\uff0c\u8fd9\u65b9\u9762\u7684\u6280\u5de7\u5012\u662f\u4e0d\u9519\uff0c\u53ea\u662f\u4e2d\u95f4\u5206\u79bb\u4e86\u51e0\u4e2a\u6708\uff0c\u4e0d\u514d\u6709\u4e9b\u751f\u758f\u3002</p><p>\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u7ec8\u4e8e\u8ba9\u4ed6\u5728\u81ea\u5df1\u5c0f\u5634\u91cc\u53d1\u6cc4\u51fa\u6765\uff0c\u5df2\u7ecf\u662f\u7d2f\u5f97\u9999\u6c57\u6dcb\u6f13\uff0c\u6c14\u5598\u5401\u5401\u3002\u743c\u6069\u5c06\u5979\u62b1\u8d77\uff0c\u8d70\u8fdb\u6d74\u5ba4\uff0c\u4e24\u4eba\u76f8\u62e5\u7740\u6ce1\u5728\u6d74\u7f38\u6e29\u6c34\u91cc\uff0c\u743c\u6069\u514d\u4e0d\u4e86\u53c8\u4e0a\u4e0b\u5176\u624b\uff0c\u4e58\u673a\u5927\u5448\u624b\u8db3\u4e4b\u6b32\uff0c\u8299\u857e\u72c4\u6e29\u987a\u5730\u5c06\u5934\u9760\u5728\u4ed6\u80f8\u53e3\uff0c\u4efb\u4ed6\u8086\u610f\u73a9\u5f04\uff0c\u53ea\u89c9\u5fc3\u4e2d\u6ee1\u662f\u5e78\u798f\u6d0b\u6ea2\uff0c\u4e00\u65f6\u95f4\u4ec0\u4e48\u90fd\u4e0d\u60f3\u4e86\u3002</p><p>\u201c\u771f\u4e56\uff0c\u201d\u743c\u6069\u5728\u5979\u80f8\u524d\u63c9\u634f\u7740\uff0c\u201c\u5c31\u50cf\u5c0f\u732b\u4e00\u6837\u3002\u201d</p><p>\u201c\u6211\u60f3\u505a\u4f60\u7684\u5ba0\u7269\u561b\uff0c\u80fd\u5929\u5929\u966a\u5728\u4f60\u8eab\u8fb9\u5c31\u597d\u3002\u201d</p><p>\u201c\u90a3\u5ba0\u7269\u5bf9\u4e3b\u4eba\u90fd\u6709\u4ec0\u4e48\u8981\u6c42\u5462\uff1f\u201d\u743c\u6069\u4e5f\u968f\u53e3\u73a9\u7b11\u3002</p><p>\u201c\u7231\u6211\uff0c\u5582\u6211\uff0c\u201d\u8299\u857e\u72c4\u5b9a\u5b9a\u5730\u770b\u7740\u4ed6\uff0c\u201c\u4e0d\u8981\u79bb\u5f00\u6211\u3002\u201d\uff08Love me, feed me, never leave me.\uff09</p><p>\u743c\u6069\u4e00\u6014\uff0c\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u4f60\u4ee5\u4e3a\u81ea\u5df1\u662f\u52a0\u83f2\u732b\u554a\u3002\u201d</p><p>\u201c\u52a0\u83f2\u732b\uff1f\u201d</p><p>\u201c\u54e6\uff0c\u4e00\u53ea\u5f88\u80a5\u5f88\u6dd8\u6c14\u5f88\u8d2a\u5403\u53c8\u4e0d\u7231\u8fd0\u52a8\u7684\u732b\uff0c\u4e0d\u8fc7\u5f88\u53ef\u7231\u3002\u201d</p><p>\u201c\u6211\u53c8\u4e0d\u80d6\u3002\u201d\u5979\u6297\u8bae\u3002</p><p>\u201c\u5f53\u7136\uff0c\u4f60\u5f53\u7136\u4e0d\u80d6\uff0c\u6211\u7684\u610f\u601d\u662f\u8bf4\u4f60\u548c\u5b83\u4e00\u6837\u53ef\u7231\uff0c\u201d\u743c\u6069\u8f7b\u8f7b\u543b\u7740\u5979\u7684\u8116\u9888\uff0c\u201c\u5176\u5b9e\u4f60\u73b0\u5728\u6709\u70b9\u7626\u4e86\uff0c\u7a0d\u5fae\u53d8\u80d6\u70b9\u624d\u597d\u3002\u201d</p><p>\u201c\u771f\u7684\uff1f\u201d</p><p>\u201c\u55ef\uff0c\u5973\u5b69\u5b50\u8fd8\u662f\u8981\u6709\u70b9\u8089\u611f\u7684\u3002\u201d</p><p>\u201c\u90a3\u6211\u591a\u5403\u70b9\u3002\u201d</p><p>\u201c\u5bf9\u554a\uff0c\u8fd9\u5730\u4e0b\u6709\u4e9b\u7279\u522b\u7684\u83dc\u80b4\uff0c\u53ef\u662f\u5730\u8868\u4eab\u7528\u4e0d\u5230\u7684\u5462\uff0c\u6bd4\u5982\u6d1b\u65af\u517d\u8089\u2026\u2026\u201d</p><p>\u201c\u90a3\u79cd\u50cf\u725b\u4e00\u6837\u7684\u52a8\u7269\uff1f\u201d</p><p>\u201c\u55ef\uff0c\u6d1b\u65af\u517d\u662f\u9ed1\u6697\u7cbe\u7075\u4e3b\u8981\u7684\u8089\u98df\u6765\u6e90\u561b\uff0c\u8fd8\u6709\u90a3\u4e9b\u8611\u83c7\uff0c\u56e0\u4e3a\u5730\u8109\u8f90\u5c04\uff0c\u6bd4\u5730\u8868\u80a5\u5927\u9c9c\u7f8e\u5f97\u591a\uff0c\u4f60\u5e94\u8be5\u5c1d\u5c1d\u7684\u3002\u201d</p><p>\u6d17\u5b8c\u6fa1\uff0c\u743c\u6069\u4ed4\u7ec6\u66ff\u5979\u64e6\u62ed\u8eab\u4f53\uff0c\u5374\u53d1\u73b0\u70b9\u5c0f\u9ebb\u70e6\u3002\u5979\u6628\u665a\u8fc7\u6765\u65f6\u53ea\u7a7f\u4e86\u4e00\u4ef6\u7761\u8863\uff0c\u5e73\u65f6\u7684\u8863\u670d\u90fd\u7559\u5728\u9694\u58c1\u623f\u95f4\u91cc\u3002\u53ea\u80fd\u5148\u5c06\u7761\u8863\u7a7f\u4e86\uff0c\u56de\u623f\u95f4\u53bb\u53d6\u3002</p><p>\u201c\u7a7f\u597d\u8863\u670d\u548c\u6211\u51fa\u53bb\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4eca\u5929\u5f97\u5f04\u6e05\u70b9\u4e8b\u60c5\u3002\u201d</p><p>\u7136\u800c\u8299\u857e\u72c4\u56de\u81ea\u5df1\u623f\u95f4\u540e\uff0c\u8fdf\u8fdf\u4e0d\u518d\u51fa\u6765\uff0c\u53cd\u800c\u9690\u7ea6\u4ece\u9694\u58c1\u4f20\u6765\u4e89\u6267\u58f0\uff0c\u5efa\u7b51\u9694\u97f3\u6548\u679c\u5f88\u597d\uff0c\u743c\u6069\u5b9e\u5728\u542c\u4e0d\u6e05\u695a\u5185\u5bb9\uff0c\u53ea\u80fd\u9690\u7ea6\u5206\u8fa8\u51fa\u4f3c\u4e4e\u662f\u59d0\u59b9\u4fe9\u5728\u5435\u67b6\u3002\u8fc7\u4e86\u5927\u7ea6\u4e8c\u5341\u5206\u949f\uff0c\u8299\u857e\u72c4\u56de\u5230\u743c\u6069\u7684\u623f\u95f4\uff0c\u5df2\u7ecf\u8863\u7740\u6574\u9f50\uff0c\u7a7f\u597d\u4e86\u5deb\u5e08\u888d\uff0c\u5957\u7740\u6597\u7bf7\uff0c\u90a3\u67c4\u5bb6\u4f20\u7684\u8c61\u7259\u6cd5\u6756\u4e5f\u62ff\u5728\u624b\u4e2d\uff0c\u53ea\u662f\u773c\u5708\u6709\u4e9b\u7ea2\u7ea2\u7684\uff0c\u660e\u663e\u662f\u521a\u624d\u53d7\u4e86\u59d4\u5c48\u3002</p><p>\u201c\u600e\u4e48\u4e86\uff1f\u201d\u743c\u6069\u95ee\u3002</p><p>\u201c\u6ca1\u4e8b\u3002\u201d</p><p>\u201c\u548c\u8299\u8389\u5a05\u5435\u67b6\u4e86\uff1f\u201d</p><p>\u201c\u5514\uff0c\u6ca1\u4ec0\u4e48\u3002\u201d</p><p>\u8299\u857e\u72c4\u6447\u5934\uff0c\u4e0d\u613f\u610f\u8bf4\uff0c\u743c\u6069\u5927\u4f53\u4e5f\u731c\u5230\u4e86\u51e0\u5206\uff0c\u4ed6\u6709\u4e9b\u4e0d\u5feb\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\u4e5f\u5c31\u7f62\u4e86\u3002\u5979\u4eec\u53cc\u80de\u80ce\u59d0\u59b9\u5fc3\u7075\u611f\u5e94\uff0c\u81ea\u5df1\u6628\u665a\u5728\u8fd9\u8fb9\u548c\u8299\u857e\u72c4\u6b22\u597d\uff0c\u8299\u8389\u5a05\u5728\u90a3\u8fb9\u5374\u7b49\u4e8e\u662f\u88ab\u9690\u5f62\u4eba\u5f3a\u66b4\u4e00\u822c\uff0c\u80fd\u5fcd\u4f4f\u6ca1\u6709\u5f53\u573a\u51b2\u8fc7\u6765\u6bb4\u6253\uff0c\u5df2\u7ecf\u662f\u591f\u6c89\u5f97\u4f4f\u6c14\u4e86\u3002\u8fd9\u6bd5\u7adf\u662f\u4eba\u5bb6\u59d0\u59b9\u95f4\u7684\u79c1\u4e8b\uff0c\u81ea\u5df1\u5230\u5e95\u662f\u5916\u4eba\uff0c\u4e0d\u597d\u591a\u8bf4\u4ec0\u4e48\uff0c\u4f55\u51b5\u73b0\u5728\u4e5f\u6ca1\u65f6\u95f4\u8ffd\u7a76\u3002\u5b89\u6170\u4e86\u51e0\u53e5\uff0c\u8f6c\u8eab\u4ece\u684c\u4e0a\u53d6\u8fc7\u6628\u665a\u7ef4\u5eb7\u5c3c\u4e9a\u9001\u6765\u7684\u90a3\u4e2a\u5c0f\u53e3\u888b\uff0c\u653e\u5165\u6000\u4e2d\uff0c\u63fd\u7740\u8299\u857e\u72c4\u7684\u8170\uff0c\u8d70\u51fa\u95e8\u53bb\u3002</p><p>\u8299\u857e\u72c4\u4e0d\u77e5\u9053\u743c\u6069\u662f\u6253\u7b97\u53bb\u54ea\u91cc\uff0c\u4f46\u4e5f\u6ca1\u6709\u591a\u95ee\uff0c\u53ea\u662f\u4e56\u4e56\u8ddf\u968f\u7740\u3002\u83f2\u5c14\u4f26\u4e3b\u6bcd\u7ed9\u4e86\u743c\u6069\u4e00\u679a\u5370\u6709\u5bb6\u5fbd\u7684\u767d\u91d1\u80f8\u9488\uff0c\u51ed\u501f\u7740\u5b83\uff0c\u4e24\u4eba\u4e00\u8def\u7545\u901a\u65e0\u963b\uff0c\u4ed6\u4eec\u8d70\u51fa\u5185\u73af\u7684\u6b63\u95e8\uff0c\u6765\u5230\u5b85\u9662\u7684\u4e2d\u95f4\u4e00\u5c42\u3002\u8f6c\u4e86\u51e0\u4e2a\u5f2f\uff0c\u8299\u857e\u72c4\u770b\u89c1\u4e86\u4e00\u7247\u77f3\u7b0b\u6797\uff0c\u7531\u51e0\u5341\u5ea7\u9ad8\u8038\u7c97\u5927\u7684\u77f3\u7b0b\u67f1\u6784\u6210\uff0c\u5b83\u4eec\u662f\u83f2\u5c14\u4f26\u5bb6\u65cf\u7684\u6218\u58eb\u5c45\u6240\u3002\u743c\u6069\u5f84\u76f4\u8d70\u5230\u5176\u4e2d\u4e00\u6839\u524d\u9762\uff0c\u6478\u51fa\u767d\u91d1\u80f8\u9488\uff0c\u5ff5\u51fa\u53e3\u4ee4\uff0c\u77f3\u7b0b\u5e95\u90e8\u6084\u65e0\u58f0\u606f\u5730\u6ed1\u5f00\u4e86\u4e00\u6247\u95e8\uff0c\u743c\u6069\u63fd\u7740\u8299\u857e\u72c4\u8d70\u4e86\u8fdb\u6765\u3002</p><p>\u77f3\u7b0b\u975e\u5e38\u5927\uff0c\u5185\u90e8\u4e2d\u7a7a\uff0c\u7b49\u4e8e\u5c31\u662f\u4e00\u5ea7\u9ad8\u697c\uff0c\u91cc\u9762\u5374\u9759\u6084\u6084\u7684\uff0c\u4f3c\u4e4e\u65e0\u4eba\u5c45\u4f4f\u3002\u743c\u6069\u987a\u7740\u697c\u68af\u4e00\u76f4\u8d70\u4e0a\u7b2c\u4e09\u5c42\uff0c\u4f9d\u65e7\u6ca1\u6709\u770b\u5230\u4e00\u4e2a\u9ed1\u6697\u7cbe\u7075\uff0c\u4f46\u770b\u623f\u95f4\u91cc\u7684\u9648\u8bbe\uff0c\u53c8\u660e\u663e\u662f\u4e0d\u4e45\u524d\u8fd8\u6709\u4eba\u4f4f\u8fc7\u7684\u8ff9\u8c61\u3002</p><p>\u201c\u8fd9\u91cc\u662f\u2026\u2026\u201d\u8299\u857e\u72c4\u7591\u60d1\u3002</p><p>\u201c\u6700\u8fd1\u4e00\u6279\u9003\u8dd1\u7684\u9ed1\u6697\u7cbe\u7075\u7684\u4f4f\u5904\u3002\u201d\u743c\u6069\u89e3\u91ca\u3002</p><p>\u5728\u4ed6\u4eec\u6765\u5230\u74dc\u7406\u5fb7\u65af\u57ce\u7684\u4e24\u5929\u524d\uff0c\u83f2\u5c14\u4f26\u5bb6\u65cf\u7206\u53d1\u4e86\u4e00\u6b21\u53db\u9003\u3002\u4f4f\u5728\u8fd9\u5ea7\u77f3\u7b0b\u4e2d\u7684\u5341\u51e0\u4e2a\u5bb6\u65cf\u6218\u58eb\u79d8\u5bc6\u7b56\u5212\uff0c\u6740\u6b7b\u4e86\u5176\u4ed6\u4e0d\u80af\u5408\u4f5c\u8005\uff0c\u9003\u79bb\u5b85\u9662\u3002\u4e3b\u6bcd\u6d3e\u9063\u4e3d\u5965\u7387\u9886\u8725\u8734\u9a91\u5175\u524d\u53bb\u8ffd\u6355\uff0c\u7ed3\u679c\u4e00\u65e0\u6240\u83b7\u3002\u81ea\u4ece\u86db\u540e\u6c89\u5bc2\u4ee5\u6765\uff0c\u5bb6\u65cf\u4e2d\u53db\u9003\u4e8b\u4ef6\u867d\u7136\u65f6\u6709\u53d1\u751f\uff0c\u4f46\u90fd\u662f\u96f6\u96f6\u6563\u6563\uff0c\u4ece\u6ca1\u6709\u8fd9\u6837\u5927\u89c4\u6a21\u7684\u3002\u4e00\u65b9\u9762\uff0c\u8fd9\u6837\u5927\u7684\u4e8b\u60c5\uff0c\u5f88\u96be\u901a\u8fc7\u5176\u4ed6\u624b\u6bb5\u63a9\u76d6\u9690\u7792\u4e0b\u53bb\uff0c\u4e0d\u514d\u5c31\u4f1a\u6709\u6d41\u8a00\u6084\u7136\u800c\u8d77\uff1b\u53e6\u4e00\u65b9\u9762\uff0c\u4e3b\u6bcd\u4e5f\u6000\u7591\u8fd9\u4e9b\u53db\u9003\u8005\u662f\u5f97\u5230\u4e86\u5bb6\u65cf\u5185\u90e8\u67d0\u4e9b\u52bf\u529b\u7684\u6697\u4e2d\u652f\u6301\uff0c\u5426\u5219\u4e0d\u53ef\u80fd\u4e8b\u5148\u6084\u65e0\u58f0\u606f\uff0c\u4e8b\u540e\u53c8\u5982\u6b64\u987a\u5229\u5730\u9003\u8131\u2014\u2014\u636e\u4e3d\u5965\u8bf4\uff0c\u4ed6\u4eec\u5728\u9003\u8dd1\u7684\u8fc7\u7a0b\u4e2d\uff0c\u8fd8\u4f7f\u7528\u4e86\u4e00\u4e9b\u9b54\u6cd5\u7269\u54c1\uff0c\u800c\u90a3\u4e9b\u4e1c\u897f\u7406\u5e94\u662f\u4e25\u683c\u63a7\u5236\uff0c\u53ea\u6709\u7267\u5e08\u548c\u5deb\u5e08\u624d\u6709\u8d44\u683c\u52a8\u7528\u7684\u3002</p><p>\u5982\u4eca\u8fd9\u79cd\u751f\u6b7b\u5173\u5934\uff0c\u5973\u6027\u7267\u5e08\u4eec\u5c31\u7b97\u5e73\u65f6\u6709\u518d\u591a\u7684\u52fe\u5fc3\u6597\u89d2\uff0c\u6b64\u65f6\u4e5f\u4e0d\u5f97\u4e0d\u56e2\u7ed3\u5408\u4f5c\u3002\u5426\u5219\u4e8b\u60c5\u8d25\u9732\uff0c\u5927\u96be\u4e34\u5934\uff0c\u5c31\u662f\u7389\u77f3\u4ff1\u711a\u7684\u4e0b\u573a\u3002\u6218\u58eb\u867d\u7136\u4eba\u6570\u4f17\u591a\uff0c\u4f46\u5728\u5bb6\u65cf\u4e2d\u5730\u4f4d\u8f83\u4f4e\uff0c\u6000\u7591\u7684\u76ee\u5149\uff0c\u96be\u514d\u5c31\u8981\u843d\u5230\u4ee5\u4e9a\u5f53\u65af\u4e3a\u9996\u7684\u5deb\u5e08\u4eec\u8eab\u4e0a\u3002\u6bd5\u7adf\uff0c\u4ed6\u4eec\u4f9d\u65e7\u4fdd\u7559\u4e86\u5f3a\u5927\u7684\u9b54\u6cd5\u80fd\u529b\uff0c\u4f46\u6000\u7591\u5f52\u6000\u7591\uff0c\u5728\u6ca1\u6709\u786e\u5207\u8bc1\u636e\u4e4b\u524d\uff0c\u4e5f\u4e0d\u53ef\u80fd\u6253\u8349\u60ca\u86c7\uff0c\u53cd\u800c\u574f\u4e8b\u3002</p><p>\u201c\u6240\u4ee5\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u5c31\u5f97\u627e\u6211\u4eec\u8fd9\u79cd\u5916\u4eba\u6765\u5904\u7406\u4e86\u3002\u201d\u743c\u6069\u6de1\u6de1\u89e3\u91ca\uff0c\u4ece\u6000\u4e2d\u53d6\u51fa\u5c0f\u53e3\u888b\uff0c\u9012\u7ed9\u8299\u857e\u72c4\u3002\u5c0f\u5973\u5b69\u6709\u4e9b\u4e0d\u89e3\u5730\u6253\u5f00\u624e\u7740\u53e3\u7684\u7ec6\u7ef3\uff0c\u53d1\u73b0\u91cc\u9762\u662f\u6ee1\u6ee1\u4e00\u888b\u91d1\u521a\u77f3\u7c89\u672b\uff0c\u987f\u65f6\u660e\u767d\u8fc7\u6765\u3002</p><p>\u91d1\u521a\u77f3\u88ab\u8ba4\u4e3a\u662f\u5927\u5730\u7684\u7ed3\u6676\uff0c\u78e8\u6210\u7c89\u672b\u53ef\u4ee5\u4f5c\u4e3a\u73cd\u8d35\u7684\u65bd\u6cd5\u6750\u6599\uff0c\u901a\u5e38\u7528\u5728\u5404\u79cd\u548c\u201c\u5ca9\u77f3\u201d\u3001\u201c\u571f\u5730\u201d\u76f8\u5173\u7684\u6cd5\u672f\u4e0a\u3002\u8fd9\u4e5f\u662f\u4e3a\u4ec0\u4e48\u7ef4\u5eb7\u5c3c\u4e9a\u7279\u610f\u8b66\u544a\uff0c\u8ba9\u4ed6\u5c0f\u5fc3\u4e0d\u8981\u89e6\u53d1\u77f3\u4e4b\u8bc5\u5492\u7684\u7f18\u6545\u3002\u201c\u77f3\u4e4b\u8bc5\u5492\u201d\u662f\u5730\u8109\u8f90\u5c04\u7684\u53d8\u5f02\u4ea7\u54c1\u4e4b\u4e00\uff0c\u904d\u5e03\u5e7d\u6697\u5730\u57df\uff0c\u6240\u6709\u5927\u5e45\u5ea6\u5f71\u54cd\u5730\u7406\u7ed3\u6784\u7684\u6cd5\u672f\uff0c\u4f8b\u5982\u5730\u9707\u672f\u4e4b\u7c7b\uff0c\u4e0d\u4f46\u4e0d\u4f1a\u6210\u529f\uff0c\u65bd\u6cd5\u8005\u53cd\u800c\u4f1a\u906d\u5230\u731b\u70c8\u53cd\u566c\u3002\u8fd9\u65e2\u662f\u9650\u5236\u540c\u65f6\u4e5f\u662f\u4fdd\u62a4\uff0c\u5426\u5219\u8fd9\u4e9b\u5730\u5e95\u57ce\u5e02\u65e9\u5c31\u88ab\u8f7b\u6613\u6467\u6bc1\u4e86\u3002</p><p>\u743c\u6069\u81ea\u7136\u4e0d\u4f1a\u5192\u8fd9\u4e2a\u98ce\u9669\uff0c\u4ed6\u8981\u91d1\u521a\u77f3\u7c89\u672b\uff0c\u662f\u4e3a\u8299\u857e\u72c4\u51c6\u5907\u7684\u3002</p><p>\u8299\u857e\u72c4\u8fd9\u4e9b\u5929\u6765\u523b\u82e6\u7814\u4e60\u90a3\u672c\u9b54\u6cd5\u7b14\u8bb0\uff0c\u6210\u7ee9\u6590\u7136\uff0c\u5982\u4eca\u5df2\u7ecf\u53ef\u4ee5\u79f0\u5f97\u4e0a\u4e00\u4f4d\u201c\u6df1\u571f\u9884\u8a00\u5e08\u201d\u4e86\u3002\u4e0d\u4f46\u80fd\u591f\u6e05\u6670\u51c6\u786e\u5730\u611f\u5e94\u5230\u5927\u5730\u529b\u91cf\u7684\u5954\u6d8c\u8109\u7edc\uff0c\u751a\u81f3\u62e5\u6709\u4e86\u548c\u5ca9\u77f3\u77ed\u6682\u201c\u4ea4\u6d41\u201d\u7684\u80fd\u529b\u3002\u5f53\u7136\uff0c\u5ca9\u77f3\u7ec8\u7a76\u662f\u6b7b\u7269\uff0c\u4e0d\u53ef\u80fd\u5f00\u53e3\u8bf4\u8bdd\uff0c\u4ec5\u4ec5\u662f\u8299\u857e\u72c4\u501f\u52a9\u6cd5\u672f\uff0c\u77ed\u6682\u5730\u548c\u5ca9\u77f3\u5171\u4eab\u610f\u8bc6\uff0c\u83b7\u5f97\u4e00\u4e9b\u8baf\u606f\u7f62\u4e86\u3002\u81f3\u4e8e\u80fd\u591f\u83b7\u5f97\u591a\u8fd8\u662f\u5c11\uff0c\u6a21\u7cca\u8fd8\u662f\u6e05\u6670\uff0c\u8fde\u8d2f\u8fd8\u662f\u6563\u4e71\u96f6\u788e\uff0c\u90a3\u5c31\u770b\u65bd\u6cd5\u8005\u7684\u9020\u8be3\u6df1\u6d45\u4e86\u3002</p><p>\u5979\u5148\u6309\u4f4f\u80f8\u9488\uff0c\u6fc0\u53d1\u4e86\u5176\u4e2d\u7684\u201c\u901a\u6653\u8bed\u8a00\u201d\u6cd5\u672f\uff0c\u8fd9\u8ba9\u5979\u6682\u65f6\u80fd\u591f\u542c\u61c2\u5353\u5c14\u7684\u8bed\u8a00\u3002\u63a5\u7740\u8299\u857e\u72c4\u4ece\u6597\u7bf7\u5185\u4fa7\u53d6\u51fa\u8c61\u7259\u6cd5\u6756\uff0c\u53f3\u624b\u63e1\u9876\uff0c\u9ed8\u8bf5\u5492\u8bed\uff0c\u5de6\u624b\u6293\u8d77\u4e00\u628a\u91d1\u521a\u77f3\u7c89\u672b\u5f53\u7a7a\u6d12\u4e0b\u3002\u6cd5\u6756\u9876\u7aef\u7684\u4e09\u9897\u541b\u738b\u4e4b\u6cea\u95ea\u95ea\u4eae\u4e86\u8d77\u6765\uff0c\u5149\u8292\u6c47\u805a\u5230\u4e00\u8d77\uff0c\u6700\u7ec8\u8ff8\u5c04\u51fa\u4e00\u9053\u6de1\u7d2b\u8272\u7684\u5fae\u5149\uff0c\u6ca1\u5165\u5899\u58c1\u4e4b\u4e2d\u3002\u8299\u857e\u72c4\u8d70\u8fd1\uff0c\u5c06\u624b\u638c\u6309\u5728\u51b0\u51b7\u575a\u786c\u7684\u5ca9\u77f3\u4e0a\uff0c\u95ed\u4e0a\u773c\u775b\uff0c\u51dd\u795e\u611f\u53d7\u7740\u4f20\u9012\u8fc7\u6765\u7684\u8baf\u606f\u3002</p><p>\u8fc7\u4e86\u5927\u7ea6\u5341\u5206\u949f\uff0c\u5979\u64a4\u56de\u624b\u638c\uff0c\u9ed8\u9ed8\u4e0d\u8bed\uff0c\u56de\u5fc6\u7740\u521a\u624d\u8111\u4e2d\u95ea\u73b0\u7684\u65e0\u6570\u96f6\u788e\u7247\u6bb5\uff0c\u5c3d\u529b\u5c06\u5b83\u4eec\u7ec4\u7ec7\u878d\u5408\u8d77\u6765\uff0c\u629b\u53bb\u65e0\u5173\u7684\u7ec6\u8282\u3002\u6700\u7ec8\uff0c\u5979\u5f97\u51fa\u4e86\u7ed3\u8bba\u3002</p><p>\u201c\u8fd9\u623f\u95f4\u91cc\u66fe\u7ecf\u6709\u51e0\u4e2a\u9ed1\u6697\u7cbe\u7075\uff0c\u4ed6\u4eec\u597d\u50cf\u5728\u4e89\u6267\uff0c\u201d\u5979\u76b1\u7740\u7709\u5934\uff0c\u201c\u53ef\u662f\u7528\u7684\u5168\u90fd\u662f\u624b\u8bed\uff0c\u6211\u770b\u4e0d\u61c2\u3002\u201d</p><p>\u5730\u4e0b\u4e16\u754c\uff0c\u5145\u6ee1\u4e86\u9690\u5bc6\u548c\u5371\u673a\uff0c\u9ed1\u6697\u7cbe\u7075\u81ea\u6709\u4e00\u5957\u624b\u8bed\uff0c\u7528\u4e8e\u6084\u65e0\u58f0\u606f\u5730\u4ea4\u6d41\u3002\u8fd9\u4e9b\u53db\u9003\u8005\u65e2\u7136\u7b79\u5212\u8fd9\u6837\u79d8\u5bc6\u7684\u4e8b\u60c5\uff0c\u4e3a\u4e86\u907f\u514d\u9694\u5899\u6709\u8033\uff0c\u624b\u8bed\u81ea\u7136\u662f\u6700\u597d\u7684\u9009\u62e9\u3002\u743c\u6069\u76b1\u8d77\u7709\u5934\uff0c\u4ed6\u5012\u662f\u758f\u5ffd\u4e86\uff0c\u5fd8\u4e86\u8fd8\u6709\u8fd9\u56de\u4e8b\u3002\u8299\u857e\u72c4\u4e0d\u61c2\u624b\u8bed\uff0c\u4e5f\u4e0d\u53ef\u80fd\u8bb0\u4e0b\u90a3\u4e9b\u590d\u6742\u7684\u52a8\u4f5c\uff0c\u7b49\u4e8e\u4f9d\u65e7\u662f\u4e00\u65e0\u6240\u83b7\u3002</p><p>\u8299\u857e\u72c4\u89c1\u4ed6\u76b1\u7709\uff0c\u4e5f\u6709\u4e9b\u7740\u6025\uff0c\u5979\u52aa\u529b\u601d\u7d22\u7740\uff0c\u6700\u540e\u7a81\u7136\u60f3\u4e86\u8d77\u6765\uff0c\u201c\u5bf9\u4e86\uff0c\u60f3\u8d77\u6765\u4e86\uff0c\u201d\u5979\u60ca\u559c\u5730\u8bf4\uff0c\u201c\u6709\u4e00\u4e2a\u4eba\u8eab\u6750\u7279\u522b\u9ad8\uff0c\u597d\u50cf\u662f\u9996\u9886\u2026\u2026\u4ed6\u6700\u540e\u8bf4\u4e86\u4e00\u4e2a\u8bcd\uff1a\u6c34\u6676\u7403\u3002\u201d</p><p>\u201c\u6c34\u6676\u7403\uff1f\u201d\u743c\u6069\u6115\u7136\uff0c\u8fd9\u4e1c\u897f\u901a\u5e38\u6765\u8bf4\u662f\u7528\u4e8e\u9884\u8a00\u5360\u535c\uff0c\u662f\u5deb\u5e08\u559c\u6b22\u4f7f\u7528\u7684\u9053\u5177\uff0c\u8fd9\u4e9b\u53db\u9003\u7684\u9ed1\u6697\u7cbe\u7075\u90fd\u662f\u6218\u58eb\uff0c\u600e\u4e48\u4f1a\u63d0\u5230\u5b83\u3002</p><p>\u201c\u55ef\uff0c\u6211\u611f\u89c9\u8fd9\u91cc\u6240\u6307\u7684\uff0c\u597d\u50cf\u662f\u4e2a\u5730\u540d\uff0c\u4ed6\u4eec\u662f\u8981\u5230\u8fd9\u4e2a\u5730\u65b9\u53bb\u3002\u201d</p><p>\u743c\u6069\u53d6\u51fa\u4e00\u4efd\u74dc\u7406\u5fb7\u65af\u57ce\u7684\u8be6\u7ec6\u5730\u56fe\uff0c\u90a3\u4e0a\u9762\u5bc6\u5bc6\u9ebb\u9ebb\u6807\u8bb0\u4e86\u57ce\u4e2d\u6240\u6709\u573a\u6240\uff0c\u7136\u800c\u6ca1\u6709\u201c\u6c34\u6676\u7403\u201d\u8fd9\u4e2a\u540d\u5b57\u3002\u201c\u96be\u9053\u662f\u8299\u857e\u72c4\u542c\u9519\u4e86\uff1f\u6216\u8005\u662f\u8bfb\u97f3\u76f8\u8fd1\u7684\u8bcd\uff1f\u201d\u743c\u6069\u601d\u5fd6\uff0c\u8fd9\u4e5f\u662f\u5f88\u6709\u53ef\u80fd\u7684\uff0c\u5979\u6bd5\u7adf\u5728\u201c\u6df1\u571f\u9884\u8a00\u201d\u4e0a\u9020\u8be3\u8fd8\u4e0d\u6df1\u3002\u800c\u4e14\u5979\u5176\u5b9e\u4e0d\u61c2\u5353\u5c14\u8bed\uff0c\u51ed\u501f\u7684\u662f\u6cd5\u672f\uff0c\u8fd9\u7ec8\u7a76\u5c31\u4e0d\u751a\u53ef\u9760\u3002\u743c\u6069\u8eab\u4e0a\u6709\u5965\u6c83\u66ff\u4ed6\u6052\u5b9a\u7684\u5de7\u8a00\u672f\uff0c\u80fd\u591f\u6beb\u65e0\u969c\u788d\u5730\u548c\u5353\u5c14\u4ea4\u6d41\uff0c\u4f46\u8fd9\u5e76\u4e0d\u610f\u5473\u7740\u4ed6\u771f\u61c2\u5353\u5c14\u8bed\uff0c\u53ea\u4e0d\u8fc7\u662f\u8ba9\u4ed6\u6240\u770b\u5230\u6587\u5b57\u548c\u542c\u5230\u7684\u8bed\u8a00\uff0c\u5168\u90fd\u81ea\u52a8\u8f6c\u5316\u4e3a\u6700\u719f\u6089\u7684\u8010\u745f\u8bed\uff1b\u800c\u4ed6\u6240\u8bf4\u7684\u8bdd\uff0c\u542c\u5728\u5353\u5c14\u8033\u4e2d\uff0c\u81ea\u7136\u4f1a\u53d8\u6210\u5353\u5c14\u8bed\uff0c\u542c\u5728\u77ee\u4eba\u8033\u4e2d\uff0c\u5219\u4f1a\u53d8\u6210\u77ee\u4eba\u8bed\u3002\u4e5f\u5c31\u662f\u8bf4\uff0c\u901a\u6653\u8bed\u8a00\u6216\u8005\u5de7\u8a00\u672f\u8fd9\u4e9b\u9b54\u6cd5\uff0c\u5176\u5b9e\u771f\u6b63\u7684\u4f5c\u7528\uff0c\u5c31\u76f8\u5f53\u4e8e\u81ea\u52a8\u7ffb\u8bd1\u7684\u673a\u5668\u7f62\u4e86\u3002</p><p>\u201c\u770b\u6765\u5f97\u627e\u4e2a\u9ed1\u6697\u7cbe\u7075\u6765\u95ee\u95ee\u624d\u884c\u3002\u201d</p><p>\u743c\u6069\u8111\u4e2d\u8f6c\u7740\u5ff5\u5934\uff0c\u89c1\u8299\u857e\u72c4\u6709\u4e9b\u6cae\u4e27\uff0c\u4e0d\u7531\u5f97\u7b11\u7740\u62cd\u4e86\u62cd\u5979\u7684\u5934\u3002\u4ed6\u8eab\u6750\u751a\u9ad8\uff0c\u8299\u857e\u72c4\u5374\u662f\u5a07\u5c0f\u7c7b\u578b\uff0c\u4e24\u4eba\u7ad9\u5728\u4e00\u8d77\u9887\u6709\u6f2b\u753b\u6548\u679c\uff0c\u4eff\u4f5b\u54e5\u54e5\u5728\u5b89\u6170\u81ea\u5df1\u7684\u59b9\u59b9\u4f3c\u7684\u3002\u201c\u6ca1\u4e8b\u4e86\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u672c\u6765\u5c31\u6ca1\u6253\u7b97\u4e00\u6b21\u6210\u529f\uff0c\u80fd\u77e5\u9053\u8fd9\u4e9b\u5df2\u7ecf\u4e0d\u9519\u4e86\uff0c\u6211\u4eec\u5148\u56de\u53bb\u3002\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u4e24\u4eba\u4e0b\u4e86\u697c\u68af\uff0c\u521a\u521a\u8d70\u51fa\u77f3\u7b0b\u67f1\uff0c\u8299\u857e\u72c4\u54a6\u4e86\u4e00\u58f0\uff0c\u5979\u770b\u89c1\u4e86\u7ef4\u5eb7\u5c3c\u4e9a\uff0c\u9ed1\u6697\u7cbe\u7075\u5c11\u5973\u7a7f\u7740\u7d27\u81f4\u8d34\u8eab\u7684\u957f\u888d\uff0c\u7f69\u7740\u6597\u7bf7\uff0c\u7a7f\u7740\u94f6\u8272\u7684\u957f\u9774\uff0c\u663e\u5f97\u5341\u5206\u5e72\u8106\u5229\u843d\uff0c\u6b63\u5feb\u6b65\u671d\u8fd9\u8fb9\u8d70\u8fc7\u6765\u3002</p><p>\u201c\u65e9\u3002\u201d\u743c\u6069\u6de1\u6de1\u6253\u4e86\u4e2a\u62db\u547c\u3002</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u89c1\u743c\u6069\u63fd\u7740\u8299\u857e\u72c4\u7684\u8170\u80a2\uff0c\u795e\u6001\u4eb2\u6635\uff0c\u4e0d\u7531\u5f97\u5634\u89d2\u5fae\u5fae\u7fd8\u8d77\uff0c\u4e0d\u6613\u5bdf\u89c9\u5730\u8f7b\u58f0\u7b11\u4e86\u4e00\u7b11\u3002\u5979\u5728\u4f0a\u5361\u6c99\u57ce\u7684\u65f6\u5019\u66fe\u7ecf\u89c1\u8fc7\u6885\u83f2\u65af\uff0c\u77e5\u9053\u5979\u548c\u743c\u6069\u662f\u60c5\u4eba\uff0c\u6ca1\u60f3\u5230\u73b0\u5728\u53c8\u591a\u51fa\u4e00\u4e2a\u3002\u201c\u4e0d\u662f\u8bf4\u4eba\u7c7b\u5bf9\u611f\u60c5\u6bd4\u8f83\u5fe0\u8bda\u4e48\uff1f\u201d\u5979\u6709\u4e9b\u8ba5\u8bbd\u5730\u56de\u5fc6\u7740\u4ee5\u524d\u4ece\u4e66\u4e0a\u770b\u6765\u7684\u8d44\u6599\uff0c\u5374\u5ffd\u7565\u4e86\u8fd9\u4e2a\u8bc4\u4ef7\u7684\u524d\u63d0\uff0c\u662f\u76f8\u5bf9\u4e8e\u5353\u5c14\u800c\u8a00\u3002</p><p>\u4e0d\u8fc7\uff0c\u8fd9\u4e5f\u5e76\u975e\u4ec0\u4e48\u574f\u4e8b\u3002</p><p>\u4e8b\u5b9e\u4e0a\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\uff0c\u6216\u8005\u8bf4\u83f2\u5c14\u4f26\u4e3b\u6bcd\uff0c\u4e4b\u6240\u4ee5\u613f\u610f\u5bb9\u5fcd\u743c\u6069\u7684\u5192\u72af\uff0c\u548c\u4ed6\u8c08\u5224\u5408\u4f5c\uff0c\u4e00\u65b9\u9762\u81ea\u7136\u662f\u770b\u91cd\u4ed6\u8868\u73b0\u51fa\u6765\u7684\u5f3a\u5927\u5b9e\u529b\uff1b\u53e6\u4e00\u65b9\u9762\u5219\u662f\u56e0\u4e3a\u4ed6\u662f\u7eaf\u7cb9\u7684\u5916\u6765\u4eba\uff0c\u548c\u74dc\u7406\u5fb7\u65af\u57ce\u5404\u5927\u52bf\u529b\u90fd\u5b8c\u5168\u6ca1\u6709\u4efb\u4f55\u5229\u5bb3\u5173\u7cfb\uff0c\u5982\u679c\u5728\u5e73\u65f6\uff0c\u8fd9\u662f\u969c\u788d\uff0c\u4f46\u5728\u76ee\u524d\u8fd9\u79cd\u7eb7\u4e71\u590d\u6742\u5343\u5934\u4e07\u7eea\u7684\u5173\u5934\uff0c\u8fd9\u53cd\u800c\u662f\u975e\u5e38\u6709\u5229\u7684\u56e0\u7d20\u3002\u4f46\u9664\u6b64\u4e4b\u5916\uff0c\u66f4\u91cd\u8981\u7684\uff0c\u662f\u5979\u4eec\u51ed\u501f\u6570\u767e\u5e74\u7684\u9605\u5386\u548c\u773c\u5149\uff0c\u51c6\u786e\u5730\u5224\u65ad\u51fa\u743c\u6069\u5e76\u975e\u90a3\u79cd\u91ce\u5fc3\u52c3\u52c3\u4e4b\u8f88\u3002</p><p>\u91ce\u5fc3\u4e0d\u5f3a\uff0c\u5c31\u76f8\u5bf9\u5bb9\u6613\u638c\u63e1\uff0c\u4eba\u603b\u662f\u6709\u6b32\u671b\u7684\uff0c\u4e0d\u5916\u4e4e\u6743\u4f4d\u3001\u540d\u671b\u3001\u91d1\u94b1\u3001\u7f8e\u8272\u4e4b\u7c7b\u3002\u5bf9\u4e8e\u5353\u5c14\u6765\u8bf4\uff0c\u53ea\u8981\u4e0d\u5a01\u80c1\u5230\u81ea\u5df1\u7684\u6743\u4f4d\uff0c\u5176\u4f59\u4e00\u5207\u90fd\u597d\u8bf4\u3002\u5728\u8fd9\u4e2a\u4e16\u754c\u4e0a\uff0c\u83f2\u5c14\u4f26\u5bb6\u65cf\u7ed9\u4e0d\u51fa\u7684\u62a5\u916c\uff0c\u786e\u5b9e\u8fd8\u4e0d\u591a\u3002\u81f3\u5c11\uff0c\u4ec5\u4ee5\u7f8e\u8272\u800c\u8bba\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u81ea\u5ea6\u5c31\u51b3\u4e0d\u4f1a\u6bd4\u743c\u6069\u8eab\u8fb9\u8fd9\u4e2a\u4e00\u770b\u5c31\u7a1a\u6c14\u672a\u8131\u7684\u5c0f\u5973\u5b69\u5dee\u4e86\u3002</p><p>\u201c\u8bf4\u8d77\u6765\uff0c\u81ea\u5df1\u8fd8\u4ece\u6ca1\u548c\u4eba\u7c7b\u505a\u8fc7\u5462\uff0c\u201d\u9ed1\u6697\u7cbe\u7075\u5c11\u5973\u5fc3\u91cc\u60f3\u7740\uff0c\u201c\u6709\u7a7a\u5012\u53ef\u4ee5\u8003\u8651\u5c1d\u8bd5\u4e00\u4e0b\u3002\u201d</p><p>\u201c\u65e9\uff0c\u201d\u5979\u4e5f\u5fae\u7b11\u7740\u56de\u5e94\uff0c\u201c\u6211\u521a\u53bb\u627e\u4f60\uff0c\u542c\u8bf4\u5230\u8fd9\u91cc\u6765\u4e86\uff0c\u6709\u4ec0\u4e48\u9700\u8981\u5e2e\u5fd9\u7684\u5417\uff1f\u201d</p><p>\u743c\u6069\u5fc3\u4e2d\u5fae\u5fae\u51b7\u7b11\uff0c\u9762\u4e0a\u4f9d\u65e7\u82e5\u65e0\u5176\u4e8b\uff0c\u201c\u6709\u70b9\u5934\u7eea\u4e86\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u627e\u6211\u6709\u4e8b\u4e48\uff1f\u201d</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u53f9\u606f\uff0c\u201c\u521a\u521a\u53c8\u6709\u4e24\u4e2a\u4eba\u8bd5\u56fe\u9003\u8dd1\uff0c\u88ab\u5f53\u573a\u683c\u6740\u4e86\u3002\u201d</p><p>\u201c\u90fd\u662f\u6218\u58eb\uff1f\u201d</p><p>\u201c\u662f\u3002\u201d</p><p>\u743c\u6069\u54e6\u4e86\u4e00\u58f0\uff0c\u4ed6\u5df2\u7ecf\u53d1\u73b0\uff0c\u524d\u540e\u8fd9\u4e9b\u53db\u9003\u8005\uff0c\u52a0\u8d77\u6765\u81f3\u5c11\u4e5f\u6709\u51e0\u5341\u4e2a\u4e86\uff0c\u5c45\u7136\u5168\u90fd\u662f\u6218\u58eb\uff0c\u6ca1\u6709\u4e00\u4e2a\u5deb\u5e08\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u5c31\u6bd4\u8f83\u5947\u602a\u3002\u5f53\u7136\uff0c\u76f8\u5bf9\u800c\u8a00\uff0c\u5deb\u5e08\u6570\u91cf\u5f88\u5c11\uff0c\u800c\u4e14\u5730\u4f4d\u8f83\u9ad8\uff0c\u4ec5\u6b21\u4e8e\u7267\u5e08\u2014\u2014\u6700\u9876\u70b9\u7684\u5deb\u5e08\uff0c\u4f8b\u5982\u9996\u5e2d\u5deb\u5e08\u4e9a\u5f53\u65af\uff0c\u5b9e\u9645\u7684\u5730\u4f4d\u548c\u5206\u91cf\u751a\u81f3\u8d85\u8fc7\u5927\u90e8\u4efd\u7267\u5e08\uff0c\u4ed6\u4eec\u53ef\u80fd\u6bd4\u8f83\u7f3a\u4e4f\u53cd\u53db\u7684\u52a8\u529b\u3002\u4f46\u4ec5\u4ec5\u5c31\u8fd9\u4e2a\u7406\u7531\uff0c\u4f3c\u4e4e\u8fd8\u662f\u4e0d\u591f\u6709\u8bf4\u670d\u529b\u7684\u3002</p><p>\u4ed6\u80fd\u611f\u89c9\u5230\uff0c\u5982\u4eca\u6574\u4e2a\u83f2\u5c14\u4f26\u5bb6\u65cf\uff0c\u4e43\u81f3\u6574\u4e2a\u74dc\u7406\u5fb7\u65af\uff0c\u6b63\u5904\u4e8e\u4e00\u79cd\u5fae\u5999\u800c\u8106\u5f31\u7684\u5e73\u8861\u5f53\u4e2d\u3002\u7267\u5e08\u4eec\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u9690\u7792\u7740\u4e8b\u5b9e\u771f\u76f8\uff0c\u60f3\u5c3d\u529e\u6cd5\u91cd\u65b0\u83b7\u5f97\u86db\u540e\u7684\u6069\u5ba0\uff0c\u5979\u4eec\u731c\u5fcc\u7740\u5deb\u5e08\uff0c\u5374\u5728\u8fd9\u79cd\u65f6\u5019\u53c8\u4e0d\u6562\u8d38\u7136\u53d1\u96be\uff0c\u5fc5\u987b\u8c28\u614e\u884c\u4e8b\uff1b\u9690\u5bc6\u7684\u4f20\u8a00\u5728\u6697\u4e2d\u4f20\u64ad\u7740\uff0c\u717d\u52a8\u7740\u539f\u672c\u5904\u4e8e\u793e\u4f1a\u5e95\u5c42\u7684\u5353\u5c14\u7537\u6027\u548c\u5974\u96b6\u53d1\u52a8\u53db\u4e71\uff0c\u63a8\u7ffb\u538b\u8feb\u5728\u4ed6\u4eec\u5934\u4e0a\u7684\u5973\u7267\u5e08\u4eec\uff1b\u800c\u5deb\u5e08\u4eec\uff0c\u5219\u4f3c\u4e4e\u5728\u6697\u4e2d\u7aa5\u4f3a\u7740\uff0c\u7b49\u5f85\u7740\u4ec0\u4e48\u3002</p><p>\u8fd9\u79cd\u5e73\u8861\u4e0d\u4f1a\u4e00\u76f4\u6301\u7eed\u4e0b\u53bb\uff0c\u5fc5\u5b9a\u4f1a\u88ab\u6253\u7834\uff0c\u800c\u743c\u6069\u6240\u8981\u505a\u7684\uff0c\u5c31\u662f\u52a0\u901f\u8fd9\u4e2a\u8fc7\u7a0b\uff0c\u5e76\u4e14\u8ba9\u8fd9\u4e00\u5207\u53d1\u751f\u5f97\u66f4\u52a0\u8fc5\u731b\u6fc0\u70c8\u3002</p><p>\u201c\u83f2\u5c14\u4f26\u5c0f\u59d0\uff0c\u6709\u4e2a\u95ee\u9898\u8bf7\u6559\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u4f60\u542c\u8fc7\u57ce\u91cc\u9762\uff0c\u6709\u4e00\u4e2a\u53eb\u505a\u6c34\u6676\u7403\uff0c\u6216\u8005\u7c7b\u4f3c\u8fd9\u79cd\u53d1\u97f3\u7684\u5730\u65b9\u4e48\uff1f\u201d</p><p>\u201c\u6c34\u6676\u7403\uff1f\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u626c\u4e86\u626c\u7709\uff0c\u201c\u4f60\u53d1\u73b0\u4ec0\u4e48\u4e86\uff1f\u201d</p><p>\u201c\u6709\u70b9\u5934\u7eea\uff0c\u4f46\u6682\u65f6\u8fd8\u4e0d\u592a\u6e05\u695a\u3002\u201d</p><p>\u201c\u6211\u60f3\u4f60\u6307\u7684\u5e94\u8be5\u662f\u2018\u6c89\u9ed8\u4e4b\u6b4c\u2019\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u8bf4\uff0c\u8fd9\u4e24\u4e2a\u8bcd\u5728\u5353\u5c14\u8bed\u4e2d\u53d1\u97f3\u975e\u5e38\u8fd1\u4f3c\uff0c\u201c\u90a3\u662f\u4e00\u5ea7\uff0c\u5514\uff0c\u5a31\u4e50\u573a\u6240\u3002\u201d</p><p>\u743c\u6069\u660e\u767d\u8fd9\u4e2a\u201c\u5a31\u4e50\u573a\u6240\u201d\u7684\u610f\u601d\uff0c\u770b\u6765\u65e0\u8bba\u662f\u4eba\u7c7b\u8fd8\u662f\u9ed1\u6697\u7cbe\u7075\uff0c\u662f\u5730\u8868\u4e16\u754c\u8fd8\u662f\u5e7d\u6697\u5730\u57df\uff0c\u6709\u4e9b\u4e1c\u897f\u90fd\u662f\u5171\u901a\u7684\u3002</p><p>\u201c\u5728\u5170\u83ab\u96f7\u65af\u533a\u3002\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u8865\u5145\u3002</p><p>\u201c\u5170\u83ab\u96f7\u65af\u533a\u4e48\uff0c\u201d\u743c\u6069\u91cd\u590d\u7740\uff0c\u201c\u770b\u6765\u4f60\u7684\u76f4\u89c9\u5f88\u51c6\u3002\u201d</p><p>\u201c\u4f60\u53d1\u73b0\u4ed6\u4eec\u53bb\u4e86\u6c89\u9ed8\u4e4b\u6b4c\uff1f\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u8ffd\u95ee\u3002</p><p>\u201c\u73b0\u5728\u8fd8\u4e0d\u80fd\u786e\u5b9a\uff0c\u201d\u743c\u6069\u56de\u7b54\uff0c\u201c\u4e0d\u8fc7\u6211\u8003\u8651\u53bb\u770b\u770b\u3002\u201d</p><p>\u201c\u9700\u8981\u6211\u505a\u4ec0\u4e48\u5417\uff1f\u201d</p><p>\u201c\u591a\u8c22\uff0c\u5982\u679c\u6709\u9700\u8981\uff0c\u6211\u4f1a\u8bf4\u8bdd\u7684\uff0c\u201d\u743c\u6069\u5fae\u7b11\uff0c\u201c\u6709\u4e9b\u65f6\u5019\uff0c\u6211\u6bd4\u8f83\u559c\u6b22\u72ec\u7acb\u884c\u4e8b\u3002\u201d</p><p>\u9ed1\u6697\u7cbe\u7075\u5c11\u5973\u8038\u8038\u80a9\uff0c\u201c\u968f\u4f60\u7684\u4fbf\uff0c\u53cd\u6b63\u4f60\u77e5\u9053\u5728\u54ea\u91cc\u80fd\u627e\u5230\u6211\u3002\u201d</p><p>\u201c\u81ea\u7136\u3002\u201d</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u8f6c\u8eab\u79bb\u53bb\uff0c\u8eab\u5f71\u4ece\u9053\u8def\u7684\u5c3d\u5934\u6d88\u5931\u3002\u743c\u6069\u6c89\u601d\u7740\uff0c\u5728\u5fc3\u4e2d\u9ed8\u9ed8\u76d8\u7b97\uff0c\u76f4\u5230\u8299\u857e\u72c4\u8f7b\u8f7b\u626f\u4e86\u626f\u4ed6\u7684\u8863\u8896\u3002</p><p>\u201c\u6211\u4eec\u8981\u53bb\u90a3\u4e2a\u6c89\u9ed8\u4e4b\u6b4c\u5417\uff1f\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u201c\u53bb\u8ffd\u6355\u90a3\u4e9b\u53db\u9003\u8005\uff1f\u201d\u5c0f\u5973\u5b69\u602f\u602f\u5730\u95ee\uff0c\u5979\u89c9\u5f97\u8fd9\u6837\u505a\u5f88\u5371\u9669\u3002</p><p>\u8299\u857e\u72c4\u5e76\u4e0d\u6e05\u695a\u743c\u6069\u7684\u60f3\u6cd5\uff0c\u4e5f\u5b8c\u5168\u4e0d\u77e5\u9053\u743c\u6069\u7684\u6253\u7b97\uff0c\u5979\u751a\u81f3\u90fd\u4e0d\u6e05\u695a\u4e3a\u4ec0\u4e48\u8981\u6765\u5230\u74dc\u7406\u5fb7\u65af\u57ce\u3002\u8fd9\u4e9b\u4e8b\u60c5\u743c\u6069\u90fd\u6ca1\u6709\u8ddf\u5979\u89e3\u91ca\u8fc7\uff0c\u5012\u5e76\u975e\u662f\u4e0d\u4fe1\u4efb\u6216\u8005\u4fdd\u5bc6\uff0c\u7eaf\u7cb9\u662f\u89c9\u5f97\u6ca1\u5fc5\u8981\u8ba9\u5979\u77e5\u9053\u3002\u5979\u76ee\u524d\u6240\u77e5\u9053\u7684\uff0c\u662f\u743c\u6069\u548c\u83f2\u5c14\u4f26\u4e3b\u6bcd\u8fbe\u6210\u534f\u8bae\uff0c\u5e2e\u52a9\u5979\u627e\u5230\u90a3\u4e9b\u53db\u9003\u8005\uff0c\u6d88\u9664\u5185\u60a3\u3002\u4f46\u743c\u6069\u5230\u5e95\u4e3a\u4ec0\u4e48\u8981\u8fd9\u4e48\u505a\uff0c\u5979\u5e76\u4e0d\u6e05\u695a\uff0c\u53ea\u662f\u6309\u7167\u5429\u5490\u884c\u4e8b\u3002</p><p>\u540c\u6837\u7684\uff0c\u743c\u6069\u7684\u56de\u7b54\u8ba9\u5979\u518d\u6b21\u51fa\u4e4e\u610f\u6599\u3002</p><p>\u201c\u8ffd\u6355\uff1f\u5514\uff0c\u4e0d\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u4e0d\u662f\u8ffd\u6355\uff0c\u662f\u52a0\u5165\u3002\u201d</p><p>\u201c\u52a0\u5165\uff1f\u201d</p><p>\u201c\u5bf9\uff0c\u201d\u743c\u6069\u4f4e\u5934\u8f7b\u543b\u7740\u5979\uff0c\u201c\u6211\u4eec\u53bb\u52a0\u5165\u4ed6\u4eec\u3002\u201d</p></article>"}}}]);
