(window.webpackJsonp=window.webpackJsonp||[]).push([[246],{268:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u4e03\u8282 \u6c89\u9ed8\u4e4b\u6b4c",content:"<article><h2>\u7b2c\u5341\u4e03\u8282 \u6c89\u9ed8\u4e4b\u6b4c</h2><p>\u4e00\u9053\u7565\u663e\u5f2f\u66f2\u7684\u9ad8\u8038\u77f3\u5899\uff0c\u5c06\u74dc\u7406\u5fb7\u65af\u5206\u4e3a\u4e0a\u4e0b\u4e24\u4e2a\u57ce\u533a\u3002\u4e0a\u57ce\u533a\u5c45\u4f4f\u7740\u4ee5\u83f2\u5c14\u4f26\u5bb6\u65cf\u4e3a\u9996\u7684\u201c\u4e0a\u6d41\u793e\u4f1a\u201d\uff0c\u5305\u62ec\u540d\u5217\u6267\u653f\u8bae\u4f1a\u7684\u4e8c\u5341\u4e00\u4e2a\u5353\u5c14\u5bb6\u65cf\uff0c\u4ee5\u53ca\u201c\u86db\u540e\u5927\u9053\u201d\u2014\u2014\u8fd9\u662f\u57ce\u5e02\u6700\u91cd\u8981\u7684\u573a\u6240\uff0c\u5728\u5927\u9053\u4e24\u4fa7\uff0c\u5ea7\u843d\u7740\u4e09\u5ea7\u201c\u5b66\u9662\u201d\uff0c\u5206\u522b\u662f\u57f9\u517b\u7267\u5e08\u7684\u796d\u53f8\u5b66\u9662\uff0c\u57f9\u517b\u5deb\u5e08\u7684\u9b54\u6cd5\u5b66\u9662\u548c\u57f9\u517b\u6218\u58eb\u7684\u683c\u6597\u5b66\u9662\uff0c\u5c3d\u5934\u5219\u662f\u5b8f\u4f1f\u7684\u7f57\u4e1d\u795e\u6bbf\uff0c\u5f62\u6001\u5b9b\u5982\u4e00\u53ea\u5e9e\u5927\u7684\u516b\u722a\u8718\u86db\uff0c\u5b83\u662f\u74dc\u7406\u5fb7\u65af\u57ce\u7684\u4e2d\u5fc3\u3002\u795e\u6bbf\u4e0a\u65b9\u7528\u9b54\u6cd5\u5236\u9020\u51fa\u7cbe\u7f8e\u903c\u771f\u7684\u5341\u4e8c\u79cd\u5e7b\u8c61\uff0c\u6309\u65f6\u8f6e\u6362\uff0c\u5168\u57ce\u7684\u4eba\u90fd\u80fd\u6e05\u695a\u770b\u89c1\uff0c\u4ee5\u6b64\u4f5c\u4e3a\u8bb0\u65f6\u5de5\u5177\u3002</p><p>\u743c\u6069\u8d70\u51fa\u83f2\u5c14\u4f26\u5bb6\u65cf\u7684\u5b85\u9662\u6b63\u95e8\uff0c\u77a5\u4e86\u773c\u4e24\u65c1\u9759\u9ed8\u8e72\u8e1e\u7684\u8718\u86db\u96d5\u50cf\uff0c\u654f\u9510\u5730\u610f\u8bc6\u5230\u5b83\u4eec\u5176\u5b9e\u662f\u67d0\u79cd\u534a\u6076\u9b54\u534a\u6784\u88c5\u4f53\u7684\u5b58\u5728\uff0c\u8fd9\u79cd\u4e1c\u897f\u5728\u5353\u5c14\u5bb6\u65cf\u4e2d\u5f88\u591a\uff0c\u6bd5\u7adf\u5979\u4eec\u7684\u795e\u540e\u7f57\u4e1d\uff0c\u5c31\u5c45\u4f4f\u5728\u65e0\u5c3d\u6df1\u6e0a\u4e4b\u4e2d\uff0c\u624b\u4e0b\u591a\u6709\u6076\u9b54\uff0c\u5353\u5c14\u4eec\u4e5f\u5f80\u5f80\u4f1a\u53ec\u5524\u8fd9\u4e9b\u90aa\u6076\u5b58\u5728\u6765\u6267\u884c\u4efb\u52a1\u6216\u8005\u53c2\u52a0\u6218\u6597\u3002\u4ed6\u62ac\u8d77\u5934\uff0c\u9065\u9065\u770b\u89c1\u9ad8\u7a7a\u4e2d\u7684\u5e7b\u8c61\uff1a\u4e00\u4f4d\u6210\u719f\u7f8e\u8273\uff0c\u6709\u7740\u8840\u7ea2\u8272\u773c\u775b\u7684\u5353\u5c14\u5973\u5b50\uff0c\u5c06\u624b\u4e2d\u7684\u5315\u9996\u523a\u8fdb\u4e86\u53e6\u4e00\u4f4d\u5168\u8eab\u8d64\u88f8\u7684\u94f6\u53d1\u5353\u5c14\u5c11\u5973\u7684\u80f8\u53e3\u3002\u524d\u8005\u81ea\u7136\u662f\u86db\u540e\u7f57\u4e1d\uff0c\u83f2\u5c14\u4f26\u5bb6\u4e2d\u5230\u5904\u662f\u5979\u7684\u5851\u50cf\uff0c\u743c\u6069\u8fd9\u4e9b\u5929\u90fd\u770b\u5f97\u719f\u6089\u4e86\uff0c\u540e\u8005\u4ed6\u4e0d\u8ba4\u8bc6\uff0c\u4f46\u4e5f\u80fd\u731c\u51fa\u8eab\u4efd\uff0c\u662f\u5e7d\u6697\u5c11\u5973\u4f0a\u5229\u65af\u7fe0\uff0c\u5353\u5c14\u795e\u7947\u4e2d\u552f\u4e00\u5584\u826f\u7684\u5b58\u5728\uff0c\u5979\u662f\u7f57\u4e1d\u7684\u5973\u513f\uff0c\u5f53\u7136\u73b0\u5728\u662f\u4ec7\u654c\u3002</p><p>\u201c\u7f57\u4e1d\u6740\u6b7b\u4f0a\u5229\u65af\u7fe0\uff0c\u8fd9\u610f\u5473\u7740\u73b0\u5728\u662f\u51e0\u70b9\u2026\u2026\u5514\uff0c\u6309\u7167\u5730\u8868\u4e16\u754c\uff0c\u662f\u4e0a\u5348\u5341\u4e00\u70b9\u5230\u5341\u4e8c\u70b9\u4e4b\u95f4\u3002\u201d</p><p>\u743c\u6069\u5728\u5fc3\u4e2d\u9ed8\u9ed8\u6362\u7b97\u7740\uff0c\u4ed6\u7ec8\u7a76\u8fd8\u6ca1\u5b8c\u5168\u9002\u5e94\u8fd9\u79cd\u5947\u602a\u7684\u8bb0\u65f6\u65b9\u6cd5\uff0c\u867d\u7136\u74dc\u7406\u5fb7\u65af\u57ce\u7684\u5353\u5c14\u4eec\u662f\u65e9\u5c31\u4e60\u4ee5\u4e3a\u5e38\u4e86\u3002\u7f57\u4e1d\u6740\u6b7b\u5979\u7684\u5341\u4e8c\u4e2a\u6700\u618e\u6068\u7684\u654c\u4eba\uff08\u5341\u4e00\u4f4d\u7cbe\u7075\u795e\u7947\u52a0\u4e0a\u4f0a\u5229\u65af\u7fe0\uff09\uff0c\u6807\u8bb0\u7740\u4e00\u5929\u7684\u5341\u4e8c\u4e2a\u65f6\u8fb0\uff0c\u8fd9\u786e\u5b9e\u5f88\u6709\u521b\u610f\uff0c\u6807\u51c6\u7684\u5353\u5c14\u98ce\u683c\u3002</p><p>\u4ed6\u88f9\u7d27\u6597\u7bf7\uff0c\u653e\u4e0b\u515c\u5e3d\uff0c\u5c06\u8138\u9690\u85cf\u5728\u9634\u5f71\u4e4b\u4e2d\u3002\u548c\u7ea4\u7ec6\u7626\u5f31\u7684\u7cbe\u7075\u4e0d\u540c\uff0c\u5353\u5c14\u7684\u4f53\u578b\u548c\u4eba\u7c7b\u8fd1\u4f3c\uff0c\u518d\u8f85\u4ee5\u4e00\u4e9b\u5de7\u5999\u7684\u5e7b\u672f\uff0c\u65c1\u4eba\u5f88\u96be\u53d1\u73b0\u4ed6\u7684\u771f\u5b9e\u8eab\u4efd\uff0c\u53ea\u4f1a\u4ee5\u4e3a\u662f\u4e2a\u666e\u901a\u7684\u9ed1\u6697\u7cbe\u7075\u3002</p><p>\u201c\u8d70\u5427\uff0c\u83ab\u5c3c\u5361\u5c0f\u59d0\u3002\u201d\u743c\u6069\u5bf9\u8eab\u65c1\u7684\u5c11\u5973\u8bf4\u3002</p><p>\u4f1a\u88ab\u4ed6\u4ee5\u201c\u83ab\u5c3c\u5361\u5c0f\u59d0\u201d\u79f0\u547c\u7684\uff0c\u81ea\u7136\u4e0d\u662f\u8299\u857e\u72c4\uff0c\u800c\u662f\u5979\u59d0\u59d0\u8299\u8389\u5a05\u3002\u743c\u6069\u539f\u672c\u662f\u6253\u7b97\u5e26\u7740\u8299\u857e\u72c4\u4e00\u8d77\u53bb\u7684\uff0c\u7136\u800c\u5728\u51fa\u53d1\u524d\u51fa\u73b0\u4e86\u70b9\u610f\u5916\u53d8\u6545\u3002\u83f2\u5c14\u4f26\u4e3b\u6bcd\u53ec\u4ed6\u524d\u53bb\uff0c\u8981\u6c42\u7559\u4e0b\u8299\u857e\u72c4\u505a\u4eba\u8d28\uff0c\u5f53\u7136\u8fd9\u662f\u9690\u6666\u5730\u6697\u793a\uff0c\u6ca1\u6709\u660e\u8bf4\uff0c\u743c\u6069\u6709\u4e9b\u4e0d\u5feb\uff0c\u4f46\u4e5f\u6ca1\u6709\u53d1\u4f5c\uff0c\u6bd5\u7adf\u5bf9\u65b9\u7684\u8fd9\u4e2a\u8981\u6c42\u4e5f\u4e0d\u7b97\u65e0\u7406\u3002</p><p>\u8299\u8389\u5a05\u9ed8\u4e0d\u4f5c\u58f0\u5730\u540c\u6837\u62c9\u8d77\u515c\u5e3d\uff0c\u88f9\u7d27\u6597\u7bf7\uff0c\u5c06\u4e00\u5934\u9ed1\u8272\u957f\u53d1\u9690\u85cf\u8d77\u6765\u3002\u743c\u6069\u88c5\u4f5c\u6f2b\u4e0d\u7ecf\u5fc3\u5730\u77a5\u4e86\u5979\u51e0\u773c\uff0c\u53d1\u73b0\u8fd9\u53cc\u80de\u80ce\u59d0\u59b9\u957f\u5f97\u8fd8\u771f\u50cf\uff0c\u8eab\u6750\u8138\u86cb\u5168\u65e0\u4e8c\u81f4\uff0c\u4ec5\u4ec5\u770b\u76f8\u8c8c\u7684\u8bdd\uff0c\u5c31\u8fde\u4ed6\u8fd9\u79cd\u548c\u8299\u857e\u72c4\u5173\u7cfb\u5bc6\u5207\u7684\u4eba\u90fd\u5206\u8fa8\u4e0d\u51fa\u533a\u522b\u3002\u5f53\u7136\uff0c\u743c\u6069\u5e76\u4e0d\u62c5\u5fc3\u4f1a\u5f04\u9519\u4eba\uff0c\u4e24\u4eba\u6c14\u8d28\u4e0a\u7684\u5dee\u5f02\u8fd8\u662f\u5f88\u660e\u663e\u7684\uff0c\u4f55\u51b5\u5bf9\u5f85\u4ed6\u7684\u6001\u5ea6\u4e5f\u5929\u58e4\u4e4b\u522b\u3002\u8299\u857e\u72c4\u966a\u5728\u4ed6\u8eab\u8fb9\uff0c\u5b8c\u5168\u662f\u5fc3\u6ee1\u610f\u8db3\u7684\u5e78\u798f\u5c0f\u5973\u751f\uff0c\u6362\u6210\u8299\u8389\u5a05\u5c31\u6c89\u7740\u8138\uff0c\u51b7\u51b0\u51b0\uff0c\u4e00\u8a00\u4e0d\u53d1\u4e86\u3002</p><p>\u771f\u626b\u5174\uff0c\u743c\u6069\u5728\u5fc3\u4e2d\u5600\u5495\u7740\u3002</p><p>\u4ed6\u4eec\u5e76\u80a9\u800c\u884c\uff0c\u987a\u7740\u873f\u8712\u7684\u77f3\u9636\u8d70\u4e0b\u83f2\u5c14\u4f26\u5b85\u9662\u6240\u5728\u7684\u9ad8\u5d16\uff0c\u7136\u540e\u6cbf\u7740\u8857\u9053\u4e00\u76f4\u671d\u5357\u65b9\u8d70\u53bb\u3002\u534a\u5c0f\u65f6\u4e4b\u540e\uff0c\u4ed6\u4eec\u901a\u8fc7\u4e86\u77f3\u5899\uff0c\u8fdb\u5165\u4e0b\u57ce\u533a\u3002</p><p>\u867d\u7136\u662f\u4e00\u5899\u4e4b\u9694\uff0c\u4f46\u4eff\u4f5b\u5c31\u662f\u4e24\u4e2a\u4e16\u754c\uff0c\u4e0a\u57ce\u533a\u5b89\u9759\u800c\u9634\u6c89\uff0c\u79e9\u5e8f\u4e95\u7136\uff0c\u6574\u6d01\u5bbd\u655e\u7684\u8857\u9053\u4e0a\u5f88\u5c11\u770b\u5230\u884c\u4eba\uff0c\u4e0b\u57ce\u533a\u5374\u7199\u7199\u6518\u6518\uff0c\u8f66\u6c34\u9a6c\u9f99\uff0c\u5404\u79cd\u55a7\u95f9\u58f0\u6df7\u6742\u5728\u4e00\u8d77\uff0c\u8db3\u4ee5\u4ee4\u4eba\u5934\u6655\u8111\u6da8\u3002\u8fd9\u91cc\u5c45\u4f4f\u7740\u74dc\u7406\u5fb7\u65af\u57ce\u6240\u6709\u4f4e\u9636\uff08\u4e0d\u80fd\u540d\u5217\u6267\u653f\u8bae\u4f1a\uff09\u5bb6\u65cf\u3001\u6ca1\u6709\u5bb6\u65cf\u8eab\u4efd\u7684\u5e73\u6c11\u3001\u5974\u96b6\uff0c\u4ee5\u53ca\u6570\u91cf\u4f17\u591a\u7684\u5546\u4eba\u3002\u743c\u6069\u8981\u53bb\u7684\u62c9\u83ab\u96f7\u65af\u533a\uff0c\u5728\u5353\u5c14\u8bed\u4e2d\u5c31\u662f\u5546\u4e1a\u8857\u7684\u610f\u601d\uff0c\u5b83\u4f4d\u4e8e\u57ce\u5e02\u4e1c\u90e8\uff0c\u4ece\u9ad8\u5904\u671b\u53bb\u5448\u4e00\u4e2a\u72ed\u957f\u5f27\u5f62\u533a\u57df\uff0c\u904d\u5e03\u7740\u5404\u79cd\u9152\u9986\u3001\u5546\u5e97\u548c\u5176\u4ed6\u5a31\u4e50\u573a\u6240\u3002</p><p>\u6309\u7167\u5730\u56fe\u7684\u6307\u793a\uff0c\u743c\u6069\u5e26\u7740\u8299\u8389\u5a05\u7a7f\u8fc7\u62e5\u6324\u7684\u4eba\u7fa4\uff0c\u540c\u65f6\u9632\u5907\u7740\u6240\u6709\u8bd5\u56fe\u9760\u8fd1\u4ed6\u7684\u4eba\uff0c\u90a3\u4e9b\u90fd\u6781\u6709\u53ef\u80fd\u662f\u7a83\u8d3c\u751a\u81f3\u6740\u624b\u3002\u4e00\u53ea\u98df\u4eba\u9b54\u4ece\u9762\u524d\u5306\u5fd9\u5730\u8dd1\u8fc7\uff0c\u5b83\u8eab\u4e0a\u6563\u53d1\u51fa\u7684\u81ed\u5473\u4ee4\u743c\u6069\u76b1\u8d77\u4e86\u7709\u5934\uff0c\u4ed6\u5b9e\u5728\u60f3\u50cf\u4e0d\u51fa\u4e00\u8d2f\u4ee5\u7cbe\u81f4\u4f18\u96c5\u81ea\u547d\u7684\u5353\u5c14\u4eec\uff0c\u5c45\u7136\u80fd\u591f\u5bb9\u5fcd\u98df\u4eba\u9b54\u548c\u5730\u7cbe\u8fd9\u4e9b\u5168\u8eab\u6076\u81ed\u7684\u751f\u7269\u5728\u57ce\u5e02\u91cc\u751f\u6d3b\uff0c\u5018\u82e5\u662f\u4eba\u7c7b\u7684\u8bdd\uff0c\u65e9\u5c31\u5c06\u4ed6\u4eec\u6740\u5f97\u4e00\u5e72\u4e8c\u51c0\u4e86\u3002\u5c31\u7b97\u662f\u505a\u5974\u96b6\uff0c\u81f3\u5c11\u4e5f\u5173\u5728\u7b3c\u5b50\u91cc\u5427\u3002</p><p>\u5341\u5206\u949f\u540e\uff0c\u4ed6\u4eec\u8fdb\u5165\u5170\u83ab\u96f7\u65af\u533a\uff0c\u8fdc\u8fdc\u770b\u89c1\u4e86\u201c\u6c89\u9ed8\u4e4b\u6b4c\u201d\u7684\u5de8\u5927\u62db\u724c\u3002</p><p>\u203b\u203b\u203b</p><p>\u5170\u83ab\u96f7\u65af\u533a\u539f\u672c\u662f\u74dc\u7406\u5fb7\u65af\u57ce\u6700\u7e41\u534e\u70ed\u95f9\u7684\u5730\u65b9\uff0c\u6240\u6709\u516c\u5f00\u7684\u8d38\u6613\u90fd\u5728\u6b64\u5904\u5b8c\u6210\uff0c\u53ef\u60dc\u56e0\u4e3a\u6700\u8fd1\u4e3b\u6bcd\u4eec\u5c01\u95ed\u4e86\u57ce\u5e02\uff0c\u7981\u6b62\u5546\u961f\u5916\u51fa\uff0c\u800c\u5176\u4ed6\u5353\u5c14\u57ce\u5e02\u7684\u5546\u961f\u4e5f\u6ca1\u6709\u5982\u671f\u5230\u6765\uff0c\u5bfc\u81f4\u5e02\u573a\u6709\u4e9b\u8427\u6761\u3002\u4e0d\u8fc7\u8fd9\u4e00\u5207\u5e76\u6ca1\u6709\u5f71\u54cd\u6c89\u9ed8\u4e4b\u6b4c\uff0c\u56e0\u4e3a\u5b83\u6240\u771f\u6b63\u7ecf\u8425\u7684\u201c\u5546\u54c1\u201d\uff0c\u5e76\u4e0d\u9700\u8981\u5916\u6765\u8865\u5145\uff0c\u4e5f\u5f88\u5c11\u6d88\u8017\u3002</p><p>\u4ece\u5916\u8868\u770b\u8d77\u6765\uff0c\u6c89\u9ed8\u4e4b\u6b4c\u662f\u4e00\u5bb6\u5f88\u666e\u901a\u5bfb\u5e38\u7684\u9152\u5e97\uff0c\u51fa\u552e\u53e3\u5473\u4f4e\u52a3\u4f46\u4ef7\u683c\u4fbf\u5b9c\u7684\u996e\u6599\uff0c\u5e97\u4e2d\u5750\u7740\u7684\u5ba2\u4eba\u4e5f\u90fd\u662f\u4e9b\u8eab\u4efd\u4f4e\u8d31\u7684\u5e73\u6c11\uff0c\u51e0\u4e2a\u5730\u7cbe\u8dd1\u6765\u8dd1\u53bb\u5f53\u4f8d\u8005\u3002\u4f46\u8fd9\u4e9b\u90fd\u4e0d\u8fc7\u662f\u4f2a\u88c5\uff0c\u5b83\u771f\u6b63\u7684\u751f\u610f\u662f\u5728\u5730\u4e0b\u3002</p><p>\u5f53\u743c\u6069\u548c\u8299\u8389\u5a05\u8d70\u8fdb\u5e97\u95e8\u65f6\uff0c\u5468\u56f4\u4e00\u5239\u90a3\u95f4\u7a81\u7136\u5bc2\u9759\u4e0b\u6765\uff0c\u56e0\u4e3a\u4ed6\u4eec\u5df2\u7ecf\u6380\u5f00\u4e86\u515c\u5e3d\uff0c\u9732\u51fa\u771f\u5b9e\u9762\u5bb9\u3002\u6c89\u9ed8\u4e4b\u6b4c\u81ea\u4ece\u5f00\u4e1a\u4ee5\u6765\uff0c\u8fd8\u4ece\u6765\u6ca1\u6709\u4eba\u7c7b\u5149\u987e\u8fc7\u5462\u3002</p><p>\u743c\u6069\u5e76\u4e0d\u6e05\u695a\u4ed6\u5df2\u7ecf\u65e0\u610f\u95f4\u521b\u9020\u4e86\u4e00\u9879\u8bb0\u5f55\uff0c\u4f46\u8fd9\u79cd\u6548\u679c\u4ed6\u5f88\u6ee1\u610f\u3002\u4e4b\u6240\u4ee5\u4e3b\u52a8\u66b4\u9732\u4eba\u7c7b\u8eab\u4efd\uff0c\u539f\u672c\u5c31\u662f\u8981\u5438\u5f15\u6ce8\u610f\u529b\uff0c\u5426\u5219\u7684\u8bdd\uff0c\u4ed6\u8981\u628a\u81ea\u5df1\u548c\u8299\u8389\u5a05\u4f2a\u88c5\u6210\u9ed1\u6697\u7cbe\u7075\uff0c\u90a3\u662f\u8f7b\u800c\u6613\u4e3e\u7684\u4e8b\u60c5\u3002\u4ed6\u73af\u89c6\u56db\u5468\uff0c\u770b\u4e2d\u4e00\u5904\u9760\u7a97\u6237\u7684\u7a7a\u684c\uff0c\u4fbf\u548c\u8299\u8389\u5a05\u8d70\u4e86\u8fc7\u53bb\uff0c\u6325\u624b\u62db\u547c\u4e00\u53ea\u6709\u7740\u7f57\u5708\u817f\u7684\u5730\u7cbe\u4f8d\u8005\u3002</p><p>\u201c\u4e00\u676f\u8461\u8404\u9152\u2026\u2026\u4f60\u8981\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u8461\u8404\u9152\u3002\u201d\u8299\u8389\u5a05\u56de\u7b54\u3002</p><p>\u201c\u4e24\u676f\u8461\u8404\u9152\u3002\u201d\u743c\u6069\u5bf9\u5730\u7cbe\u8bf4\uff0c\u4e22\u7ed9\u5b83\u4e00\u679a\u94f6\u5e01\u3002</p><p>\u5730\u7cbe\u8eac\u8eab\u544a\u9000\uff0c\u7247\u523b\u4e4b\u540e\u7aef\u7740\u4e24\u676f\u8461\u8404\u9152\u56de\u6765\u3002\u743c\u6069\u6162\u6162\u54c1\u5c1d\u7740\uff0c\u7528\u773c\u89d2\u4f59\u5149\u6253\u91cf\u5468\u56f4\u7684\u4eba\u3002\u6765\u8fd9\u91cc\u7684\u90fd\u662f\u7537\u6027\uff0c\u5927\u591a\u6570\u5ba2\u4eba\u90fd\u5728\u996e\u9152\uff0c\u56db\u4e2a\u9ed1\u6697\u7cbe\u7075\u6b63\u5728\u4e0b\u8428\u74e6\u68cb\uff08\u4e00\u79cd\u5353\u5c14\u7279\u6709\u7684\u6218\u68cb\uff09\uff0c\u4ed6\u4eec\u7684\u8863\u670d\u7834\u65e7\uff0c\u6ca1\u6709\u5bb6\u5fbd\uff0c\u660e\u663e\u662f\u6beb\u65e0\u5bb6\u65cf\u80cc\u666f\u7684\u5e73\u6c11\u3002\u89d2\u843d\u91cc\uff0c\u4e00\u4e2a\u5df2\u7ecf\u559d\u5f97\u9189\u91ba\u91ba\u7684\u9ed1\u6697\u7cbe\u7075\u8db4\u5728\u684c\u4e0a\uff0c\u5634\u91cc\u8fd8\u5728\u542b\u7cca\u4e0d\u6e05\u5730\u8bf4\u4e9b\u4ec0\u4e48\uff0c\u743c\u6069\u4fa7\u8033\u7ec6\u542c\uff0c\u53d1\u73b0\u90fd\u662f\u4e9b\u6beb\u65e0\u4ef7\u503c\u7684\u7262\u9a9a\uff0c\u4e5f\u5c31\u61d2\u5f97\u5728\u610f\u4e86\u3002\u5427\u53f0\u9644\u8fd1\uff0c\u4e09\u4e2a\u9ed1\u6697\u7cbe\u7075\u5728\u5bf9\u7740\u5899\u58c1\u73a9\u98de\u9556\uff0c\u743c\u6069\u4ed4\u7ec6\u89c2\u5bdf\u4e86\u4e00\u4e0b\uff0c\u53d1\u73b0\u4ed6\u4eec\u624b\u8155\u6296\u52a8\u65f6\u5f3a\u52b2\u6709\u529b\uff0c\u660e\u663e\u662f\u4e45\u7ecf\u8bad\u7ec3\u7684\u6b66\u8005\u3002\u5176\u4e2d\u4e00\u4e2a\u9886\u5934\u7684\u6b63\u5bf9\u7740\u5176\u4f59\u4e24\u4e2a\u4eba\u4f4e\u58f0\u8bf4\u7740\u4ec0\u4e48\uff0c\u773c\u5149\u4e0d\u662f\u77a5\u5411\u8fd9\u8fb9\u3002\u5427\u53f0\u540e\uff0c\u4e00\u4e2a\u79c3\u9876\u7684\u7070\u77ee\u4eba\u6b63\u767e\u65e0\u804a\u8d56\u5730\u5ba1\u89c6\u7740\u81ea\u5df1\u7684\u6307\u7532\u3002</p><p>\u201c\u53d1\u73b0\u4f60\u8981\u627e\u7684\u4eba\u4e86\uff1f\u201d\u8299\u8389\u5a05\u95ee\u3002</p><p>\u201c\u81ea\u7136\u6ca1\u6709\uff0c\u201d\u743c\u6069\u56de\u7b54\uff0c\u201c\u96be\u9053\u4f60\u8fd8\u6307\u671b\u4ed6\u4eec\u4f1a\u7559\u5728\u8fd9\u91cc\uff0c\u7b49\u4e3b\u6bcd\u628a\u4ed6\u4eec\u6293\u56de\u53bb\uff1f\u201d</p><p>\u201c\u90a3\u4f60\u51c6\u5907\u600e\u4e48\u529e\uff1f\u7b49\u5f85\u4ed6\u4eec\u6765\u548c\u4f60\u63a5\u5934\uff1f\u201d</p><p>\u201c\u5514\uff0c\u6211\u5012\u662f\u5e0c\u671b\u5982\u6b64\uff0c\u53ef\u60dc\u4ed6\u4eec\u5e94\u8be5\u4e0d\u4f1a\u8fd9\u4e48\u4e3b\u52a8\uff0c\u201d\u743c\u6069\u4e0d\u52a8\u58f0\u8272\uff0c\u201c\u6240\u4ee5\u53ea\u80fd\u6211\u4eec\u4e3b\u52a8\u4e9b\u4e86\u3002\u201d</p><p>\u4ed6\u518d\u6b21\u62ac\u624b\u62db\u6765\u4f8d\u8005\uff0c\u4e22\u7ed9\u4ed6\u4e94\u679a\u91d1\u5e01\uff0c\u5374\u6ca1\u6709\u8bf4\u8981\u4ec0\u4e48\u3002\u5730\u7cbe\u5fc3\u9886\u795e\u4f1a\u5730\u79bb\u5f00\uff0c\u56de\u5230\u5427\u53f0\u540e\u548c\u90a3\u4e2a\u79c3\u9876\u7684\u7070\u77ee\u4eba\u8bf4\u4e86\u51e0\u53e5\uff0c\u5f53\u5b83\u518d\u6b21\u5c0f\u8dd1\u56de\u6765\u7684\u65f6\u5019\uff0c\u9012\u7ed9\u4e86\u743c\u6069\u4e00\u679a\u94f6\u8272\u7684\u957f\u65b9\u5f62\u94ed\u724c\u3002</p><p>\u201c\u6253\u7b97\u53bb\u5bfb\u6b22\u4f5c\u4e50\u4e86\uff1f\u201d\u8299\u8389\u5a05\u5fae\u5fae\u51b7\u7b11\u3002</p><p>\u201c\u4e0d\uff0c\u201d\u743c\u6069\u4e49\u6b63\u8bcd\u4e25\u5730\u53cd\u9a73\uff0c\u201c\u662f\u53bb\u6253\u63a2\u6d88\u606f\u3002\u201d</p><p>\u201c\u540d\u6b63\u8a00\u987a\uff0c\u201d\u8299\u8389\u5a05\u8ba5\u8bbd\uff0c\u201c\u90a3\u4e48\u4f60\u8fd8\u5750\u5728\u8fd9\u91cc\u5e72\u561b\u3002\u201d</p><p>\u201c\u5728\u7b49\u5f85\u3002\u201d</p><p>\u201c\u7b49\u5f85\u4ec0\u4e48\uff1f\u201d</p><p>\u743c\u6069\u6311\u4e86\u6311\u7709\u6bdb\uff0c\u6ca1\u6709\u56de\u7b54\u8fd9\u4e2a\u95ee\u9898\uff0c\u56e0\u4e3a\u5df2\u7ecf\u4e0d\u9700\u8981\u4e86\u3002</p><p>\u4e09\u4e2a\u9ed1\u6697\u7cbe\u7075\u4e22\u4e0b\u4e86\u624b\u4e2d\u7684\u98de\u9556\uff0c\u8d70\u4e86\u8fc7\u6765\u3002\u4ed6\u4eec\u7a7f\u7740\u7cbe\u81f4\u8f7b\u8f6f\u7684\u9501\u7532\uff0c\u7070\u8272\u7684\u6597\u7bf7\u4e0b\u9732\u51fa\u660e\u663e\u7684\u957f\u5251\u75d5\u8ff9\uff0c\u53f3\u624b\u90fd\u4f69\u6234\u7740\u94f6\u8272\u62a4\u8155\uff0c\u4e0a\u9762\u6709\u7c7b\u4f3c\u706b\u7130\u8df3\u8dc3\u7684\u6807\u8bb0\u3002\u4e3a\u9996\u7684\u9ed1\u6697\u7cbe\u7075\u8138\u4e0a\u6709\u4e00\u9053\u957f\u957f\u7684\u5200\u75a4\uff0c\u4ece\u5de6\u8fb9\u773c\u89d2\u4e00\u76f4\u62d6\u5230\u53f3\u8fb9\u5634\u89d2\uff0c\u5c06\u9f3b\u6881\u780d\u65ad\uff0c\u7834\u574f\u4e86\u539f\u672c\u8fd8\u79f0\u5f97\u4e0a\u82f1\u4fca\u7684\u8138\u5e9e\uff0c\u5b83\u662f\u5982\u6b64\u4e4b\u6df1\uff0c\u4ee4\u4eba\u5947\u602a\u5f53\u65f6\u90a3\u4e00\u5200\u6216\u8005\u4e00\u5251\u4e3a\u4ec0\u4e48\u6ca1\u6709\u628a\u8fd9\u4e2a\u9ed1\u6697\u7cbe\u7075\u7684\u8111\u888b\u7ed9\u5288\u6210\u4e24\u534a\u3002</p><p>\u201c\u4eba\u7c7b\uff1f\u201d\u4ed6\u8d70\u5230\u684c\u8fb9\uff0c\u5c45\u9ad8\u4e34\u4e0b\u5730\u95ee\uff0c\u8bed\u6c14\u91cc\u6709\u4e9b\u4e0d\u592a\u80af\u5b9a\uff0c\u6bd5\u7adf\u8fd9\u79cd\u4e8b\u60c5\u6709\u4e9b\u592a\u8fc7\u532a\u5937\u6240\u601d\u4e86\u3002\u5728\u5353\u5c14\u7684\u74dc\u7406\u5fb7\u65af\u57ce\u4e2d\uff0c\u5c45\u7136\u6709\u4e24\u4e2a\u4eba\u7c7b\u5927\u6447\u5927\u6446\u5730\u7a7f\u8fc7\u95f9\u5e02\uff0c\u8d70\u8fdb\u9152\u5e97\uff0c\u5b89\u5b89\u7a33\u7a33\u5730\u5750\u7740\u54c1\u5c1d\u8461\u8404\u9152\u3002\u5982\u679c\u4e0d\u662f\u4eb2\u773c\u76ee\u7779\uff0c\u4ed6\u4e00\u5b9a\u8ba4\u4e3a\u8fd9\u662f\u4e00\u4e2a\u5f88\u4e0d\u9ad8\u660e\u7684\u7b11\u8bdd\u3002</p><p>\u201c\u4eba\u7c7b\uff0c\u201d\u743c\u6069\u51b2\u4ed6\u70b9\u70b9\u5934\uff0c\u201c\u5170\u5c3c\u65af\u7279\uff0c\u6765\u81ea\u5730\u8868\u3002\u201d</p><p>\u9ed1\u6697\u7cbe\u7075\u5028\u50b2\u5730\u5c06\u624b\u653e\u5728\u684c\u9762\u4e0a\uff0c\u4ed6\u7684\u624b\u6307\u4fee\u957f\u6709\u529b\uff0c\u98df\u6307\u4e0a\u6234\u7740\u4e00\u679a\u6d45\u84dd\u8272\u7684\u6212\u6307\u3002\u201c\u79bb\u5f00\u8fd9\u91cc\uff0c\u4eba\u7c7b\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u7559\u4e0b\u4f60\u8eab\u4e0a\u7684\u6240\u6709\u91d1\u5e01\uff0c\u5305\u62ec\u94ed\u724c\uff0c\u8fd8\u6709\u4f60\u7684\u540c\u4f34\u3002\u201d</p><p>\u201c\u5514\uff1f\u201d\u743c\u6069\u5fae\u7b11\u8d77\u6765\uff0c\u201c\u83ab\u975e\u6258\u5c14\u683c\u5bb6\u65cf\u7684\u6b66\u6280\u957f\uff0c\u8fde\u533a\u533a\u5341\u4e94\u679a\u91d1\u5e01\u90fd\u4ed8\u4e0d\u8d77\uff1f\u201d</p><p>\u9ed1\u6697\u7cbe\u7075\u7684\u773c\u795e\u5239\u90a3\u95f4\u53d8\u5f97\u9510\u5229\u8d77\u6765\uff0c\u201c\u4f60\u8ba4\u8bc6\u6211\u3002\u201d</p><p>\u201c\u4e0d\uff0c\u6211\u53ea\u662f\u6070\u597d\u8ba4\u8bc6\u8fd9\u4e2a\u5bb6\u5fbd\uff0c\u201d\u743c\u6069\u6307\u4e86\u6307\u4ed6\u7684\u62a4\u8155\uff0c\u201c\u987a\u4fbf\u4e5f\u542c\u8bf4\u8fc7\u6258\u5c14\u683c\u5bb6\u65cf\u7684\u6b66\u6280\u957f\u76f8\u8c8c\u2026\u2026\u5514\uff0c\u6bd4\u8f83\u72ec\u7279\u3002\u201d</p><p>\u9ed1\u6697\u7cbe\u7075\u51dd\u89c6\u7740\u4ed6\uff0c\u7136\u540e\u5fae\u5fae\u4fef\u8eab\u3002</p><p>\u201c\u6c83\u51e1\u745e\u62c9\xb7\u6258\u5c14\u683c\uff0c\u8bb0\u4f4f\u6211\u7684\u540d\u5b57\u3002\u76f8\u4fe1\u5728\u9b54\u7f51\u91cc\uff0c\u4f60\u4f1a\u540e\u6094\u81ea\u5df1\u8bf4\u51fa\u8fd9\u53e5\u8bdd\u3002\u201d\u4ed6\u4f4e\u58f0\u5a01\u80c1\u7740\uff0c\u987a\u52bf\u5c06\u80cc\u540e\u7684\u957f\u5251\u62bd\u4e86\u51fa\u6765\uff0c\u731b\u529b\u671d\u743c\u6069\u5934\u4e0a\u5288\u4e0b\u3002</p><p>\u4ed6\u5e76\u4e0d\u77e5\u9053\u743c\u6069\u7684\u6765\u5386\uff0c\u4f46\u5353\u5c14\u6bd4\u4eba\u7c7b\u9ad8\u8d35\uff0c\u8fd9\u662f\u9ed1\u6697\u7cbe\u7075\u7684\u6cd5\u5219\uff0c\u5f3a\u8005\u6709\u6743\u529b\u6740\u6b7b\u5f31\u8005\uff0c\u8fd9\u4e5f\u662f\u9ed1\u6697\u7cbe\u7075\u7684\u6cd5\u5219\u3002\u6240\u4ee5\u4ed6\u5b8c\u5168\u4e0d\u62c5\u5fc3\u81ea\u5df1\u7684\u884c\u4e3a\u4f1a\u5f15\u53d1\u4ec0\u4e48\u4e0d\u5229\u540e\u679c\u3002\u5f53\u7136\uff0c\u4ed6\u770b\u51fa\u743c\u6069\u4f3c\u4e4e\u662f\u4e2a\u5deb\u5e08\uff0c\u6240\u4ee5\u9700\u8981\u7528\u7a81\u7136\u88ad\u51fb\u7684\u65b9\u5f0f\uff0c\u8ba9\u5bf9\u65b9\u6765\u4e0d\u53ca\u5ff5\u51fa\u5492\u8bed\u3002</p><p>\u7136\u800c\u743c\u6069\u538b\u6839\u4e0d\u9700\u8981\u5ff5\u8bf5\u5492\u8bed\u3002</p><p>\u65e0\u5f62\u7684\u9b54\u6cd5\u529b\u91cf\u5728\u4ed6\u8eab\u524d\u5f62\u6210\u4e00\u9053\u5c4f\u969c\uff0c\u5c06\u9ed1\u6697\u7cbe\u7075\u7684\u957f\u5251\u53cd\u5f39\u4e86\u56de\u53bb\u3002\u6b66\u6280\u957f\u7684\u4e24\u4e2a\u624b\u4e0b\u4e5f\u62d4\u51fa\u4e86\u957f\u5251\uff0c\u4f46\u4ed6\u4eec\u7684\u52a8\u4f5c\u6162\u4e86\u4e00\u6b65\u3002\u743c\u6069\u5728\u5fc3\u4e2d\u9ed8\u8bf5\u4e86\u4e00\u4e2a\u5f3a\u6709\u529b\u7684\u5355\u8bcd\uff0c\u62ac\u624b\u671d\u6c83\u51e1\u745e\u62c9\u7684\u957f\u5251\u4e00\u6307\uff0c\u6b63\u51c6\u5907\u518d\u5ea6\u8fdb\u653b\u7684\u6b66\u6280\u957f\u7a81\u7136\u611f\u89c9\u81ea\u5df1\u7684\u638c\u5fc3\u4f20\u6765\u9ad8\u6e29\u707c\u70ed\uff0c\u4ed6\u7684\u7cbe\u91d1\u957f\u5251\u4e0d\u77e5\u4f55\u65f6\u53d8\u5f97\u901a\u7ea2\u8d77\u6765\uff0c\u4eff\u4f5b\u70e7\u7ea2\u7684\u70d9\u94c1\u3002\u5373\u4f7f\u662f\u9ed1\u6697\u7cbe\u7075\u4e5f\u5fcd\u53d7\u4e0d\u4f4f\u8fd9\u79cd\u5267\u75db\uff0c\u4ed6\u7684\u638c\u5fc3\u76ae\u8089\u5df2\u7ecf\u88ab\u70eb\u5f97\u7126\u7cca\u4e00\u7247\uff0c\u6b66\u6280\u957f\u5927\u53eb\u4e00\u58f0\uff0c\u6296\u624b\u60f3\u8981\u5c06\u957f\u5251\u6254\u4e0b\uff0c\u4f46\u4ed6\u7684\u6b66\u5668\u7a81\u7136\u873f\u8712\u6e38\u52a8\u8d77\u6765\uff0c\u5728\u7a7a\u6c14\u4e2d\u77ac\u95f4\u53d8\u6210\u4e00\u53ea\u91d1\u8272\u7684\u6bd2\u86c7\uff0c\u5f20\u5f00\u68ee\u68ee\u5229\u9f7f\uff0c\u817e\u7a7a\u7ffb\u8eab\u4e00\u53e3\u54ac\u4e2d\u4e86\u4e3b\u4eba\u7684\u624b\u81c2\u3002</p><p>\u6b66\u6280\u957f\u8e09\u8dc4\u540e\u9000\uff0c\u4ed6\u7684\u53f3\u81c2\u4eff\u4f5b\u5439\u6c14\u4e00\u822c\u81a8\u80c0\u8d77\u6765\uff0c\u900f\u7740\u5371\u9669\u7684\u6697\u7d2b\u8272\uff0c\u90a3\u663e\u7136\u662f\u5267\u6bd2\u3002\u4e00\u4e2a\u624b\u4e0b\u4e22\u4e0b\u5251\u6276\u4f4f\u4e86\u4ed6\uff0c\u5306\u5fd9\u4ece\u8170\u95f4\u76ae\u56ca\u91cc\u53d6\u6cbb\u7597\u836f\u6c34\uff0c\u53e6\u5916\u4e00\u4e2a\u624b\u4e0b\u7684\u957f\u5251\u539f\u672c\u5df2\u7ecf\u5feb\u8981\u5288\u5230\u743c\u6069\u8eab\u4e0a\uff0c\u4f46\u4ed6\u660e\u667a\u5730\u7acb\u523b\u540e\u64a4\uff0c\u8ba9\u8fd9\u4e00\u51fb\u843d\u7a7a\u3002\u9ed1\u6697\u7cbe\u7075\u90fd\u662f\u806a\u660e\u7684\u751f\u7269\uff0c\u4ed6\u4eec\u51b3\u4e0d\u4f1a\u53bb\u6311\u6218\u660e\u663e\u65e0\u6cd5\u6218\u80dc\u7684\u654c\u4eba\uff0c\u65e0\u8bba\u4ed6\u662f\u5353\u5c14\u8fd8\u662f\u4eba\u7c7b\u3002</p><p>\u6bd2\u86c7\u6389\u843d\u5728\u5730\u4e0a\uff0c\u53d1\u51fa\u94ff\u9535\u7684\u91d1\u5c5e\u58f0\uff0c\u518d\u5ea6\u53d8\u56de\u7cbe\u91d1\u957f\u5251\u3002</p><p>\u8fd9\u4e00\u5207\u53d1\u751f\u5f97\u592a\u5feb\uff0c\u77ed\u77ed\u51e0\u79d2\u949f\u5185\u5c31\u5c18\u57c3\u843d\u5b9a\u3002\u743c\u6069\u6ce8\u610f\u5230\u4e00\u76f4\u5728\u5427\u53f0\u540e\u65e0\u804a\u53d1\u5446\u7684\u7070\u77ee\u4eba\u521a\u521a\u5f00\u4e86\u8d4c\u5c40\uff0c\u6b63\u51c6\u5907\u5f00\u59cb\u6536\u9152\u5ba2\u4eec\u7684\u4e0b\u6ce8\uff0c\u5f53\u7136\u73b0\u5728\u4ed6\u7684\u8ba1\u5212\u843d\u7a7a\u4e86\u3002\u743c\u6069\u597d\u6574\u4ee5\u6687\u5730\u7aef\u8d77\u9152\u676f\uff0c\u9065\u9065\u671d\u4e09\u4e2a\u9ed1\u6697\u7cbe\u7075\u793a\u610f\u3002\u6b66\u6280\u957f\u8fd8\u4fdd\u6301\u7740\u6e05\u9192\uff0c\u4ed6\u4f3c\u4e4e\u5f88\u51b7\uff0c\u7259\u9f7f\u63a7\u5236\u4e0d\u4f4f\u5730\u4e0a\u4e0b\u649e\u51fb\u7740\uff0c\u53d1\u51fa\u683c\u683c\u7684\u58f0\u97f3\uff0c\u4eff\u4f5b\u7f6e\u8eab\u51b0\u7a96\uff0c\u4e24\u4e2a\u624b\u4e0b\u53c8\u60ca\u6012\u53c8\u754f\u60e7\u5730\u770b\u7740\u743c\u6069\uff0c\u4e0d\u6562\u4e0a\u524d\u3002\u8299\u8389\u5a05\u5012\u662f\u975e\u5e38\u6c89\u5f97\u4f4f\u6c14\uff0c\u4ece\u5934\u5230\u5c3e\u90fd\u82e5\u65e0\u5176\u4e8b\u5730\u54c1\u5c1d\u7740\u5979\u90a3\u676f\u8461\u8404\u9152\u3002</p><p>\u201c\u6211\u95ee\u4e00\u4e2a\u95ee\u9898\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5982\u679c\u4f60\u4eec\u80fd\u591f\u7ed9\u6211\u6ee1\u610f\u7684\u7b54\u6848\uff0c\u90a3\u4e48\u53ef\u4ee5\u4ece\u8fd9\u91cc\u8d70\u51fa\u53bb\uff0c\u8d76\u7d27\u627e\u4e2a\u7267\u5e08\u6cbb\u7597\u7684\u8bdd\uff0c\u8fd9\u6761\u80f3\u818a\u8fd8\u80fd\u4fdd\u4f4f\u3002\u5426\u5219\u7684\u8bdd\uff0c\u201d\u4ed6\u987f\u4e86\u987f\uff0c\u201c\u613f\u795e\u540e\u5e87\u4f51\u4f60\u4eec\u7684\u7075\u9b42\u3002\u201d</p><p>\u201c\u4ec0\u4e48\u95ee\u9898\uff1f\u201d</p><p>\u201c\u9003\u4ea1\u8005\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u53d1\u73b0\u5f53\u4ed6\u8bf4\u51fa\u8fd9\u4e2a\u8bcd\u7684\u65f6\u5019\uff0c\u5468\u56f4\u7684\u7a7a\u6c14\u9aa4\u7136\u5bc2\u9759\u4e0b\u6765\uff0c\u201c\u6211\u542c\u8bf4\u6709\u4e00\u4e9b\u9003\u4ea1\u8005\u6765\u5230\u8fc7\u8fd9\u91cc\uff0c\u4f60\u4eec\u6216\u8bb8\u89c1\u8fc7\u3002\u201d</p><p>\u201c\u6211\u4eec\u6ca1\u89c1\u8fc7\uff0c\u201d\u4e00\u4e2a\u624b\u4e0b\u8bf4\uff0c\u201c\u6211\u4eec\u5f88\u5c11\u6765\u8fd9\u91cc\uff0c\u4eca\u5929\u53ea\u662f\u51d1\u5de7\u3002\u201d</p><p>\u743c\u6069\u770b\u7740\u4ed6\uff0c\u731c\u6d4b\u4ed6\u8bf4\u5f97\u662f\u5426\u662f\u771f\u8bdd\uff0c\u6700\u540e\u4ed6\u62ac\u4e86\u62ac\u624b\u6307\u3002</p><p>\u201c\u8d70\u5427\u3002\u201d\u4ed6\u8bf4\u3002</p><p>\u4e24\u4e2a\u624b\u4e0b\u6276\u7740\u4ed6\u4eec\u7684\u6b66\u6280\u957f\u5306\u5fd9\u79bb\u53bb\uff0c\u743c\u6069\u9976\u6709\u5174\u81f4\u5730\u770b\u7740\u4ed6\u4eec\u7684\u80cc\u5f71\uff0c\u6700\u540e\u8f6c\u8fc7\u5934\u6765\uff0c\u770b\u7740\u8299\u8389\u5a05\u3002\u201c\u731c\u731c\u770b\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u90a3\u4e2a\u6b66\u6280\u957f\u80fd\u6d3b\u4e0b\u6765\u5417\uff1f\u201d</p><p>\u201c\u6211\u60f3\u4ed6\u6b7b\u5b9a\u4e86\u3002\u201d</p><p>\u201c\u9519\u4e86\uff0c\u201d\u743c\u6069\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u90a3\u53ea\u6bd2\u86c7\u662f\u5047\u7684\uff0c\u6bd2\u6db2\u4e5f\u662f\u5047\u7684\uff0c\u53ea\u8981\u518d\u8fc7\u5341\u5206\u949f\uff0c\u4ed6\u7684\u624b\u81c2\u5c31\u4f1a\u6062\u590d\u5982\u5e38\u3002\u201d</p><p>\u201c\u662f\u4f60\u9519\u4e86\uff0c\u201d\u8299\u8389\u5a05\u5fae\u5fae\u51b7\u7b11\uff0c\u201c\u4f60\u6ca1\u6ce8\u610f\u5230\u4ed6\u90a3\u4e2a\u7070\u8272\u773c\u775b\u624b\u4e0b\u7684\u795e\u60c5\uff1f\u5341\u5206\u949f\uff0c\u8db3\u4ee5\u5b8c\u6210\u4e00\u6b21\u8c0b\u6740\u4e86\u3002\u201d</p><p>\u743c\u6069\u9ed8\u7136\uff0c\u4ed6\u786e\u5b9e\u5012\u6ca1\u60f3\u5230\u8fd9\u4ef6\u4e8b\u3002\u5728\u9ed1\u6697\u7cbe\u7075\u7684\u4e16\u754c\u91cc\uff0c\u8fd9\u662f\u53f8\u7a7a\u89c1\u60ef\u7684\u4e8b\u60c5\u5427\uff0c\u6084\u6084\u6740\u6b7b\u6b66\u6280\u957f\u548c\u540c\u4f34\u706d\u53e3\uff0c\u8fd8\u53ef\u4ee5\u5c06\u8d23\u4efb\u63a8\u7ed9\u743c\u6069\uff0c\u6b63\u662f\u664b\u8eab\u7684\u826f\u673a\u3002</p><p>\u4ed6\u770b\u7740\u8299\u8389\u5a05\uff0c\u91cd\u65b0\u6253\u91cf\u7740\u5979\uff0c\u548c\u8299\u857e\u72c4\u4e00\u6a21\u4e00\u6837\u7684\u4fcf\u4e3d\u9762\u5e9e\uff0c\u7cbe\u81f4\u79c0\u7f8e\uff0c\u7eaf\u51c0\u65e0\u90aa\uff0c\u5374\u80fd\u7406\u6240\u5f53\u7136\u5730\u505a\u51fa\u8fd9\u6837\u7684\u731c\u6d4b\uff0c\u4eff\u4f5b\u5979\u5176\u5b9e\u5c31\u662f\u4e00\u4e2a\u9ed1\u6697\u7cbe\u7075\u4f3c\u7684\u3002\u6216\u8bb8\uff0c\u8fd9\u5c31\u662f\u591c\u5973\u58eb\u838e\u5c14\u7684\u6559\u5bfc\uff1f</p><p>\u201c\u73b0\u5728\u5e94\u8be5\u4e0d\u4f1a\u6709\u4eba\u518d\u6562\u6765\u6253\u6270\u4f60\u4e86\uff0c\u201d\u743c\u6069\u6700\u540e\u8bf4\uff0c\u5c06\u676f\u4e2d\u6b8b\u4f59\u7684\u8461\u8404\u9152\u4e00\u996e\u800c\u5c3d\uff0c\u201c\u5728\u8fd9\u91cc\u7b49\u6211\u56de\u6765\uff0c\u4e0d\u4f1a\u592a\u4e45\u3002\u201d</p><p>\u201c\u54e6\uff0c\u201d\u8299\u8389\u5a05\u5fae\u5fae\u626c\u4e86\u626c\u7709\u6bdb\uff0c\u201c\u4e0d\u4f1a\u592a\u4e45\uff1f\u8fd9\u4e48\u8bf4\uff0c\u4f60\u7684\u6301\u4e45\u529b\u5f88\u5dee\u561b\u3002\u201d</p><p>\u743c\u6069\u9669\u4e9b\u88ab\u564e\u4f4f\u4e86\uff0c\u6709\u90a3\u4e48\u4e00\u77ac\u95f4\uff0c\u4ed6\u51e0\u4e4e\u60f3\u628a\u773c\u524d\u8fd9\u4e2a\u5c0f\u4e2b\u5934\u7ed9\u62ce\u4e0b\u53bb\uff0c\u5429\u5490\u4f8d\u8005\u5f00\u4e00\u95f4\u7a7a\u623f\uff0c\u8ba9\u5979\u597d\u597d\u9886\u6559\u4e00\u4e0b\u81ea\u5df1\u7684\u201c\u6301\u4e45\u529b\u201d\uff0c\u4f46\u6700\u540e\u4ed6\u653e\u5f03\u4e86\u8fd9\u4e2a\u5ff5\u5934\u3002\u73b0\u5728\u8fd8\u6709\u6b63\u4e8b\u8981\u505a\uff0c\u6682\u65f6\u6ca1\u7a7a\u8ddf\u5c0f\u5973\u5b69\u7ea0\u7f20\u3002\u53cd\u6b63\u2026\u2026\u76f8\u4fe1\u5979\u6700\u7ec8\u4e5f\u9003\u4e0d\u51fa\u81ea\u5df1\u7684\u9b54\u722a\u2014\u2014\u8fd9\u4e0d\u662f\u671f\u671b\uff0c\u8fd9\u662f\u5fc5\u987b\u5b8c\u6210\u7684\u4efb\u52a1\uff0c\u8c01\u8ba9\u5979\u4eec\u59d0\u59b9\u4fe9\u504f\u504f\u6709\u5fc3\u7075\u611f\u5e94\u5462\u3002</p><p>\u5deb\u5e08\u8d77\u8eab\uff0c\u7a7f\u8fc7\u5427\u53f0\u65c1\u8fb9\u4e00\u6247\u4e0d\u8d77\u773c\u7684\u5c0f\u95e8\uff0c\u6cbf\u7740\u697c\u68af\u5f80\u4e0b\u8d70\uff0c\u5c3d\u5934\u662f\u4e00\u9053\u957f\u957f\u7684\u8d70\u5eca\uff0c\u4e24\u65c1\u6709\u65e0\u6570\u623f\u95f4\uff0c\u95e8\u655e\u5f00\u7740\u6216\u8005\u9501\u7740\uff0c\u6216\u8005\u534a\u63a9\u7740\uff0c\u4ece\u91cc\u9762\u4f20\u51fa\u4f4e\u4f4e\u7684\u547b\u541f\u548c\u70ed\u60c5\u7684\u5598\u606f\u58f0\uff0c\u751a\u81f3\u8fd8\u6709\u75db\u82e6\u7684\u60e8\u53eb\uff0c\u4f34\u968f\u7740\u4ee4\u4eba\u96be\u4ee5\u5bdf\u89c9\u7684\u6109\u60a6\u3002\u6574\u4e2a\u7a7a\u6c14\u4e2d\u5f25\u6f2b\u7740\u4e00\u79cd\u63cf\u8ff0\u4e0d\u51fa\u7684\u6c14\u606f\uff0c\u8fd9\u91cc\u624d\u662f\u6c89\u9ed8\u4e4b\u6b4c\u771f\u6b63\u7684\u751f\u610f\u6240\u5728\u3002</p><p>\u6c89\u9ed8\u4e4b\u6b4c\u662f\u4e00\u5ea7\u5993\u9662\u2014\u2014\u800c\u4e14\u5b83\u53ea\u63d0\u4f9b\u9ed1\u6697\u7cbe\u7075\u5993\u5973\uff0c\u8fd9\u5c31\u662f\u5b83\u5728\u6574\u4e2a\u74dc\u7406\u5fb7\u65af\u57ce\u90fd\u522b\u5177\u4e00\u683c\uff0c\u540d\u95fb\u9050\u8fe9\u7684\u7f18\u6545\u3002</p></article>"}}}]);
