(window.webpackJsonp=window.webpackJsonp||[]).push([[248],{271:function(p){p.exports={chapterTitle:null,title:"\u7b2c134\u7ae0 \u5bcc\u5bb6\u5b50\u5f1f",content:"<article><h2>\u7b2c134\u7ae0 \u5bcc\u5bb6\u5b50\u5f1f</h2><p>\u3000\u3000\u79bb\u5f00\u6c5f\u9edb\u513f\u5bb6\u56de\u5230\u5bbe\u9986\uff0c\u5f20\u606a\u5728\u8f66\u4e0a\u90fd\u7981\u4e0d\u4f4f\u8981\u80e1\u601d\u4e71\u60f3\uff0c\u5012\u662f\u8bb8\u601d\u6709\u4e9b\u626d\u634f\u4e0d\u5b89\uff0c\u8d8a\u8fd1\u623f\u95e8\uff0c\u8fd9\u79cd\u611f\u89c9\u8d8a\u662f\u5f3a\u70c8\uff0c\u8138\u988a\u6e17\u8840\u4e00\u822c\u7684\u7eef\u7ea2\uff0c\u5c31\u50cf\u51ac\u5b63\u6f84\u51c0\u5929\u7a7a\u7684\u665a\u971e\u70e7\u904d\u5979\u7684\u5168\u8eab\u3002</p><p>\u3000\u3000\u201c\u8981\u4e0d\u8981\u559d\u70b9\u4ec0\u4e48\uff1f\u201d\u5f20\u606a\u53cd\u624b\u5c06\u623f\u95e8\u53cd\u9501\u4e0a\uff0c\u8fdb\u95e8\u5c31\u6709\u5c0f\u7684\u9152\u67dc\uff0c\u4f46\u662f\u53ea\u6446\u7740\u4e00\u4e9b\u996e\u6599\u3002\u8bb8\u601d\u6447\u6447\u5934\uff0c\u4eba\u8981\u5f80\u623f\u95f4\u91cc\u8eb2\uff0c\u5374\u7ed9\u5f20\u606a\u6293\u4f4f\u624b\u3002\u4e0b\u5348\u90a3\u74f6\u7ea2\u9152\u8fd8\u6709\u5269\u4e0b\uff0c\u5f20\u606a\u5c06\u9152\u6db2\u5012\u8fdb\u6676\u83b9\u5254\u900f\u7684\u73bb\u7483\u9ad8\u811a\u676f\u91cc\uff0c\u8f7b\u8f7b\u6cef\u4e86\u4e00\u53e3\uff0c\u9012\u7ed9\u8bb8\u601d\uff1a\u201c\u5473\u9053\u6ca1\u6709\u53d8\uff0c\u4f60\u559d\u559d\u770b\u3002\u201d</p><p>\u3000\u3000\u8bb8\u601d\u63a5\u8fc7\u9152\u6000\uff0c\u5c06\u9152\u676f\u8f6c\u4e86\u4e00\u4e2a\u89d2\u5ea6\uff0c\u523b\u610f\u8f6c\u8fc7\u5f20\u606a\u5634\u5507\u78b0\u8fc7\u7684\u90a3\u8fb9\uff0c\u559d\u4e86\u4e00\u53e3\u9152\uff0c\u5f20\u606a\u62a2\u8fc7\u9152\u676f\u91cc\uff0c\u4f38\u51fa\u820c\u5934\u5728\u676f\u6cbf\u4e0a\u8214\u4e86\u4e00\u5708\uff0c\u518d\u9012\u7ed9\u8bb8\u601d\u3002</p><p>\u3000\u3000\u201c\u6076\u5fc3\uff01\u201d\u8bb8\u601d\u54af\u54af\u7684\u7b11\u4e86\u8d77\u6765\uff0c\u5c06\u9152\u676f\u653e\u8336\u51e0\u4e0a\uff0c\u4f38\u51fa\u624b\u8981\u53bb\u626f\u5f20\u606a\u7684\u8033\u6735\uff0c\u5374\u8ba9\u5f20\u606a\u4e00\u628a\u6293\u4f4f\uff0c\u5f20\u606a\u62ff\u8d77\u9152\u676f\u518d\u770b\u7740\u8bb8\u601d\u90a3\u53cc\u52a8\u4eba\u5fc3\u9b44\u7684\u773c\u775b\u65f6\uff0c\u90a3\u5904\u5df2\u7ecf\u8d77\u4e86\u6c34\u96fe\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u8bb8\u601d\u62e5\u5750\u5728\u6c99\u53d1\u4e0a\uff0c\u4e00\u4eba\u4e00\u53e3\u5c06\u9152\u676f\u91cc\u7684\u7ea2\u9152\u559d\u4e86\u4e0b\u53bb\uff0c\u5f20\u606a\u5012\u662f\u60f3\u8ddf\u8bb8\u601d\u73a9\u6e21\u9152\u7684\u6e38\u620f\uff0c\u5948\u4f55\u8bb8\u601d\u62c9\u4e0d\u4e0b\u8138\u6765\uff0c\u6b7b\u6d3b\u4e0d\u80af\u3002</p><p>\u3000\u3000\u5f20\u606a\u62e5\u7740\u8bb8\u601d\u5230\u623f\u95f4\u91cc\uff0c\u7d27\u7d27\u538b\u7740\u90a3\u52a8\u4eba\u7684\u5a07\u8eaf\uff0c\u52a8\u624b\u60f3\u5265\u8bb8\u601d\u7684\u8863\u670d\uff0c\u8bb8\u601d\u62ff\u624b\u7f20\u7740\u4ed6\uff0c\u4e0d\u8ba9\u4ed6\u778e\u52a8\u3002\u5f20\u606a\u653e\u5f00\u624b\uff0c\u6367\u7740\u5979\u7684\u8138\u95ee\u5979\uff1a\u201c\u8981\u4e0d\u8981\u5148\u6d17\u6fa1\uff1f\u201d\u8bb8\u601d\u53cc\u624b\u98a4\u98a4\u5dcd\u5dcd\u7684\u6402\u8fc7\u5f20\u606a\u7684\u8116\u5b50\uff0c\u5014\u5f3a\u7684\u8bf4\uff1a\u201c\u4e0d\u8981\uff0c\u6211\u5c31\u8fd9\u6837\u7761\uff0c\u8863\u670d\u4e5f\u4e0d\u8131\u3002\u201d\u89c1\u5f20\u606a\u64a9\u8d77\u5979\u7684\u5916\u5957\uff0c\u5fd9\u8bf4\uff0c\u201c\u5916\u5957\u4e5f\u4e0d\u8131\u3002\u201d\u7ec8\u662f\u6ca1\u6709\u575a\u6301\uff0c\u4f46\u662f\u8ba9\u5f20\u606a\u5265\u51fa\u5916\u5957\uff0c\u5c31\u8fc5\u901f\u7684\u94bb\u8fdb\u88ab\u7a9d\u91cc\uff0c\u4e0d\u8ba9\u5f20\u606a\u7ee7\u7eed\u52a8\u4f5c\u3002</p><p>\u3000\u3000\u6ca1\u60f3\u5230\u6bd4\u7b2c\u4e00\u6b21\u8fd8\u9ebb\u70e6\uff0c\u5f20\u606a\u8ddf\u7740\u94bb\u8fdb\u88ab\u7a9d\u3002\u5012\u662f\u5728\u88ab\u7a9d\u91cc\uff0c\u8ba9\u8bb8\u601d\u611f\u89c9\u5230\u88ab\u5b50\u7684\u906e\u63a9\uff0c\u6e29\u987a\u7684\u7ed9\u5f20\u606a\u6402\u5728\u6000\u91cc\uff0c\u4efb\u4ed6\u8fd8\u6709\u51c9\u7684\u624b\u94bb\u91cc\u8863\u670d\u91cc\u5728\u81ea\u5df1\u7684\u8eab\u4f53\u4e0a\u6e38\u8d70\u3002</p><p>\u3000\u3000\u5f20\u606a\u5012\u662f\u6ca1\u6709\u4eab\u53d7\u8fc7\u5265\u8bb8\u601d\u7684\u8863\u670d\u4f1a\u6709\u8fd9\u4e48\u5927\u7684\u6210\u5c31\u611f\uff0c\u5df2\u7ecf\u5fd8\u8bb0\u8fd9\u79cd\u611f\u89c9\u4e86\uff0c\u603b\u611f\u89c9\u5bf9\u65b9\u8131\u8863\u670d\u6bd4\u81ea\u5df1\u8fd8\u8fc5\u901f\u3002\u543b\u7740\u8bb8\u601d\u67d4\u5ae9\u7684\u5507\uff0c\u5c06\u5979\u7684\u8d34\u8eab\u68c9\u8863\u6162\u6162\u5f80\u9888\u90e8\u64a9\u8d77\uff0c\u4ece\u5979\u5934\u4e0a\u8131\u4e0b\u6765\uff0c\u8302\u5bc6\u7684\u79c0\u53d1\u7ed9\u7a84\u5c0f\u7684\u9886\u53e3\u6324\u5728\u90a3\u91cc\uff0c\u5c31\u50cf\u4e00\u6367\u4e30\u8302\u7684\u6c34\u8349\uff0c\u6563\u53d1\u51fa\u6d17\u53d1\u9999\u6ce2\u7684\u9999\u6c14\u3002\u4ece\u8bb8\u601d\u9732\u51fa\u7684\u4e0b\u9894\u5f80\u4e0b\u770b\uff0c\u96ea\u767d\u808c\u80a4\u67d3\u4e0a\u4e00\u5c42\u7ea2\u6655\uff0c\u4ece\u7ea2\u6da6\u7684\u7410\u9aa8\u5f80\u4e0b\u5ef6\u4f38\uff0c\u4e00\u76f4\u5230\u4e30\u633a\u7684\u4e73\u623f\u90fd\u662f\u7c89\u7ea2\u8272\u7684\uff0c\u5982\u8102\u5982\u7389\uff0c\u4e5f\u8ba9\u5f20\u606a\u5982\u75f4\u5982\u9189\uff0c\u987e\u4e0d\u4e0a\u5e2e\u8bb8\u601d\u8131\u8863\u670d\uff0c\u5634\u5df4\u51d1\u4e0a\u53bb\uff0c\u4ece\u5a07\u6da6\u7684\u4e0b\u9894\u6162\u6162\u95ee\u88ad\u5411\u5ae3\u7ea2\u7684\u84d3\u857e\u3002</p><p>\u3000\u3000\u8bb8\u601d\u6323\u624e\u8981\u5c06\u7b8d\u5728\u5934\u4e0a\u7684\u68c9\u8863\u8131\u4e0b\u6765\uff0c\u8ba9\u5f20\u606a\u542b\u8214\u7740\u654f\u611f\u7684\u5730\u65b9\uff0c\u8010\u4e0d\u4f4f\u8fd9\u6837\u7684\u523a\u6fc0\uff0c\u8eab\u5b50\u7981\u4e0d\u4f4f\u50cf\u86c7\u4e00\u6837\u626d\u52a8\u7740\uff0c\u6108\u53d1\u8131\u4e0d\u4e0b\u68c9\u8863\uff0c\u5634\u91cc\u5598\u7740\u7c97\u6c14\uff1a\u201c\u5e2e\u6211\u3002\u201d\u5f20\u606a\u53cc\u624b\u5c06\u6ed1\u4e0b\u53bb\uff0c\u6293\u4f4f\u5979\u5185\u88e4\u7684\u8fb9\u6cbf\uff0c\u8bb8\u601d\u5a07\u55d4\u9053\uff1a\u201c\u4e0d\u662f\u90a3\u91cc\u3002\u201d\u5f20\u606a\u5374\u4e0d\u7406\u5979\uff0c\u624b\u4f38\u5165\u5979\u96ea\u767d\u7684\u5927\u817f\u5185\u4fa7\uff0c\u8fd9\u4e00\u5904\u7684\u808c\u80a4\u6700\u662f\u5a07\u5ae9\uff0c\u5f20\u606a\u7ec6\u7ec6\u7684\u6469\u6332\u7740\uff0c\u65f6\u4e0d\u65f6\u9694\u7740\u5c42\u68c9\u5e03\u538b\u5411\u8bb8\u601d\u7684\u5bc6\u5904\u3002\u8bb8\u601d\u5c06\u68c9\u8863\u8131\u4e0b\u6765\uff0c\u9762\u988a\u7eef\u7ea2\u4e0d\u9000\uff0c\u9762\u82e5\u6843\u82b1\uff0c\u8ba9\u5f20\u606a\u7684\u624b\u89e6\u6478\u7740\uff0c\u867d\u7136\u6781\u529b\u538b\u5236\u7740\uff0c\u5634\u91cc\u8fd8\u65f6\u4e0d\u65f6\u7684\u6cc4\u51fa\u4e00\u4e24\u58f0\u5a74\u513f\u822c\u7684\u5a07\u557c\u3002</p><p>\u3000\u3000\u5f20\u606a\u624b\u6307\u6cbe\u7740\u6cb9\u4e00\u6837\u817b\u6da6\u7684\u4f53\u6db2\uff0c\u538b\u6291\u7684\u60c5\u611f\u4e00\u65e6\u7206\u53d1\u51fa\u6765\uff0c\u4e5f\u4f1a\u8ba9\u60c5\u6b32\u6765\u5f97\u683c\u5916\u6c79\u6d8c\uff0c\u8bb8\u601d\u7684\u7b2c\u4e00\u6b21\u51e0\u4e4e\u611f\u89c9\u4e0d\u5230\u75db\u75bc\uff0c\u8fd9\u65f6\u5019\u4e5f\u6ca1\u6709\u4e00\u70b9\u7684\u4e0d\u9002\u5e94\u3002\u5f20\u606a\u624b\u5fd9\u811a\u4e71\u7684\u5c06\u8bb8\u601d\u8eab\u4e0a\u6302\u7740\u7684\u4e73\u7f69\u3001\u5185\u88e4\u7edf\u7edf\u9664\u4e0b\uff0c\u624b\u91cd\u65b0\u6349\u7740\u5979\u5ae9\u817b\u5982\u7389\u7684\u96ea\u767d\u4e30\u4e73\uff0c\u5634\u5507\u88ad\u5411\u8bb8\u601d\u9732\u51fa\u6765\u7684\u7ea2\u5507\uff0c\u7d22\u53d6\u5979\u7684\u9999\u820c\uff0c\u624b\u91cc\u4e0d\u5fd8\u63c9\u634f\u6324\u538b\u90a3\u5bf9\u5b8c\u7f8e\u7684\u9020\u7269\u3002</p><p>\u3000\u3000\u201c\u4f60\u2026\u2026\u201d\u8bb8\u601d\u53ea\u597d\u610f\u601d\u8bf4\u4e00\u4e2a\u5b57\uff0c\u5f20\u606a\u5fc3\u9886\u795e\u4f1a\u7684\u8dea\u8d77\u8eab\u5b50\uff0c\u5c06\u81ea\u5df1\u8eab\u4e0a\u7684\u8863\u7269\u8fc5\u901f\u9664\u53bb\uff0c\u8dd1\u5230\u8bb8\u601d\u8eab\u8fb9\u7684\u6b63\u9762\uff0c\u8bb8\u601d\u77a5\u773c\u770b\u4e86\u4e00\u773c\u5f20\u606a\u7684\u4e0b\u4f53\uff0c\u5c31\u4e27\u5931\u52c7\u6c14\u7684\u5fae\u4ef0\u7740\u5934\uff0c\u6c14\u606f\u5374\u66f4\u6025\u4fc3\u4e86\uff0c\u8eab\u4f53\u50cf\u8f6f\u8089\u762b\u5728\u5e8a\u4e0a\u4e00\u8d77\uff0c\u4e00\u52a8\u4e0d\u52a8\uff0c\u8eab\u4e0a\u5230\u5904\u90fd\u662f\u7ed9\u5f20\u606a\u6324\u538b\u540e\u7559\u4e0b\u7684\u6de1\u7ea2\u8272\u75d5\u8ff9\u3002\u6ca1\u6709\u7b2c\u4e00\u6b21\u7684\u6025\u4e0d\u53ef\u8010\uff0c\u5f20\u606a\u4e5f\u7b97\u7cbe\u4e8e\u6b64\u9053\uff0c\u9762\u5bf9\u8fd9\u5177\u68a6\u5e7b\u4e00\u6837\u7684\u5a07\u8eaf\uff0c\u8fd8\u662f\u611f\u89c9\u5230\u5fc3\u810f\u627f\u53d7\u5f88\u5927\u7684\u538b\u529b\u3002\u8fdb\u5165\u5979\u5a07\u5ae9\u7684\u8eab\u4f53\uff0c\u5fcd\u4e0d\u4f4f\u50cf\u521d\u54e5\u90a3\u6837\u63d0\u6c14\u5b81\u795e\uff0c\u7f13\u7f13\u52a8\u7740\uff0c\u597d\u8ba9\u81ea\u5df1\u9002\u5e94\u6e7f\u6cde\u7684\u7d27\u8feb\uff0c\u84c4\u52bf\u800c\u55b7\u8584\uff0c\u8f6c\u773c\u5c31\u5c06\u8bb8\u601d\u5e26\u5165\u9b42\u9b44\u9500\u7194\u7684\u53e6\u4e00\u4e2a\u6d1e\u5929\u3002</p><p>\u3000\u3000\u5f20\u606a\u611f\u89c9\u81ea\u5df1\u5c31\u50cf\u4e00\u53ea\u8d2a\u5a6a\u7684\u5c0f\u517d\u5728\u65e0\u4f11\u6b62\u7684\u8214\u8210\u7740\u751c\u679c\uff0c\u770b\u7740\u8bb8\u601d\u8138\u988a\u4e0a\u8273\u4e3d\u7684\u6843\u7ea2\uff0c\u5a07\u5a9a\u800c\u8273\u7f8e\uff0c\u4eff\u4f5b\u65e0\u7aef\u7684\u6625\u8272\uff0c\u65e0\u6cd5\u5f62\u5bb9\u3002</p><p>\u3000\u3000\u53cd\u53cd\u590d\u590d\u7684\u9ad8\u6f6e\u8ba9\u4e24\u4eba\u7684\u610f\u8bc6\u3001\u7406\u667a\u5728\u4e91\u96e8\u4e2d\u5d29\u6e83\uff0c\u5f20\u606a\u611f\u89c9\u6709\u4e9b\u53e3\u6e34\uff0c\u62ff\u6765\u6c34\u74f6\u7ed9\u4e24\u4eba\u5012\u6c34\u91cc\uff0c\u5df2\u7ecf\u662f\u51cc\u6668\u4e09\u70b9\u4e86\uff0c\u5f20\u606a\u6df7\u8ff9\u60c5\u573a\u8fd9\u4e48\u591a\u5e74\uff0c\u4ece\u6ca1\u5bf9\u81ea\u5df1\u8fd9\u4e48\u6ee1\u610f\u8fc7\uff0c\u6027\u7684\u80fd\u529b\uff0c\u5e76\u4e0d\u5b8c\u5168\u662f\u7537\u4eba\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u8bb8\u601d\u4f9d\u7740\u5e8a\u5934\uff0c\u8fd8\u4e0d\u5fd8\u62ff\u88ab\u5b50\u63a9\u76d6\u9165\u4e73\uff0c\u4eab\u53d7\u7740\u72c2\u98ce\u66b4\u96e8\u4e00\u822c\u6301\u7eed\u9ad8\u6f6e\u540e\u7684\u6e29\u99a8\u4e0e\u5b81\u9759\uff0c\u8fd9\u624d\u65f6\u5019\u624d\u611f\u89c9\u6709\u8eab\u4f53\u7ecf\u5386\u521a\u624d\u7684\u72c2\u4e71\u540e\u8fd8\u6709\u4e00\u4e9b\u4e0d\u9002\uff0c\u53cc\u817f\u4ea4\u53e0\u7740\u632a\u4e86\u632a\u3002</p><p>\u3000\u3000\u5f20\u606a\u89c1\u8bb8\u601d\u7684\u8eab\u5b50\u5728\u88ab\u5b50\u8815\u52a8\uff0c\u5c06\u6c34\u676f\u9012\u7ed9\u5979\uff1a\u201c\u600e\u4e48\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd8\u4e0d\u662f\u4f60\uff0c\u8fd9\u4e48\u75af\uff01\u201d\u8bb8\u601d\u5a07\u55d4\u9053\uff0c\u58f0\u97f3\u67d4\u817b\u5982\u6625\u5b63\u7ef5\u7ef5\u7684\u4e1d\u96e8\uff0c\u201c\u5feb\u8fdb\u6765\uff0c\u4e0d\u8981\u7740\u51c9\u4e86\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u5c06\u6c34\u559d\u6389\uff0c\u6ca1\u6709\u6025\u7740\u4e0a\u5e8a\uff0c\u53cd\u800c\u8d70\u5230\u7a97\u524d\uff0c\u5c06\u7a97\u5e18\u62c9\u5f00\uff0c\u6253\u5f00\u4e00\u6247\u7a97\u6237\uff0c\u8ba9\u51b0\u5bd2\u7684\u51b7\u7a7a\u6c14\u60a3\u51fa\u6765\uff0c\u51b2\u51fb\u4ed6\u8d64\u88f8\u7684\u8eab\u4f53\u3002</p><p>\u3000\u3000\u201c\u4f60\u771f\u75af\u4e86\uff01\u201d\u8bb8\u601d\u8d76\u5fd9\u62b1\u7740\u88ab\u5b50\u8fc7\u6765\uff0c\u5c06\u81ea\u5df1\u4e0e\u5f20\u606a\u4e00\u8d77\u88f9\u4f4f\uff0c\u201c\u51bb\u7740\u600e\u4e48\u529e\uff1f\u201d</p><p>\u3000\u3000\u201c\u80fd\u62e5\u6709\u4f60\u8fd9\u6837\u5b8c\u7f8e\u7684\u5973\u4eba\uff0c\u6211\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u6000\u7591\u81ea\u5df1\u5728\u505a\u68a6\uff0c\u5439\u5439\u51b7\u98ce\uff0c\u8bf4\u4e0d\u5b9a\u4f1a\u9192\u8fc7\u6765\u3002\u201d</p><p>\u3000\u3000\u8bb8\u601d\u75f4\u8ff7\u7684\u770b\u7740\u5f20\u606a\u6e05\u6f88\u900f\u4eae\u7684\u773c\u775b\uff0c\u4f38\u624b\u6469\u5a11\u7740\u5f20\u606a\u4e0b\u5df4\uff0c\u6709\u80e1\u5b50\u6e23\u5b58\u5728\u7684\u523a\u75d2\uff0c\u706b\u70ed\u7684\u5a07\u8eaf\u8d34\u7740\u5f20\u606a\u808c\u7406\u5f39\u5b9e\u7684\u8eaf\u5e72\uff1a\u201c\u600e\u4e48\u4f1a\u662f\u505a\u68a6\u5462\uff1f\u6211\u8981\u505a\u4f60\u7684\u5973\u4eba\u554a\uff0c\u600e\u4e48\u4f1a\u662f\u505a\u68a6\u5462\uff1f\u201d\u611f\u89c9\u5ba4\u5916\u7684\u51b7\u7a7a\u6c14\u51db\u51bd\u7684\u5439\u62c2\u7740\u8138\u988a\uff0c\u60c5\u6b32\u9000\u5374\uff0c\u4f46\u662f\u5fc3\u91cc\u7684\u67d4\u60c5\u5374\u66f4\u52a0\u6d53\u90c1\uff0c\u60f3\u7740\u4e0a\u5929\u9020\u4eba\u65f6\uff0c\u5c06\u7537\u4eba\u5973\u4eba\u5206\u5f00\u771f\u662f\u6b8b\u5fcd\uff0c\u4e3a\u4ec0\u4e48\u6068\u4e0d\u5f97\u94bb\u5230\u4ed6\u7684\u8eab\u4f53\u91cc\u53bb\u2026\u2026</p><p>\u3000\u3000\u8981\u4e0d\u662f\u60f3\u7740\u660e\u65e9\u8fd8\u8981\u8d76\u8def\uff0c\u5f20\u606a\u80fd\u4e91\u96e8\u5230\u5929\u660e\uff0c\u4e24\u4eba\u8eab\u8eaf\u7ea0\u7f20\u6c89\u6c89\u7761\u53bb\uff0c\u76f4\u5230\u7ed9\u6572\u95e8\u58f0\u60ca\u9192\uff0c\u542c\u89c1\u662f\u6c5f\u9edb\u513f\u7684\u58f0\u97f3\u3002</p><p>\u3000\u3000\u8fd8\u4ee5\u4e3a\u6c5f\u9edb\u513f\u4e0d\u53ef\u80fd\u53bb\u6d77\u5dde\u5462\uff0c\u5f20\u606a\u9ebb\u5229\u7684\u722c\u8d77\u6765\uff0c\u89c1\u8bb8\u601d\u4e00\u8138\u5fc3\u614c\uff0c\u5fc3\u60f3\u5979\u5a07\u8f6f\u65e0\u529b\u7684\u6837\u5b50\u8ba9\u6c5f\u9edb\u513f\u770b\u89c1\uff0c\u6c5f\u9edb\u513f\u518d\u662f\u5355\u7eaf\uff0c\u4e5f\u4f1a\u6709\u6240\u8054\u60f3\uff0c\u4f4e\u5934\u8f7b\u58f0\u5bf9\u5979\u8bf4\uff1a\u201c\u4f60\u5148\u4e0d\u8981\u51fa\u623f\u95f4\u3002\u201d\u62b1\u7740\u81ea\u5df1\u7684\u8863\u670d\u5230\u53e6\u5916\u4e00\u4e2a\u623f\u95f4\uff0c\u5c06\u5e8a\u5f04\u4e71\uff0c\u624d\u62ab\u7740\u6d74\u888d\u53bb\u7ed9\u6c5f\u9edb\u513f\u5f00\u95e8\uff1a\u201c\u51e0\u70b9\u949f\u4f60\u8d76\u8fc7\u6765\uff1f\u7834\u574f\u6211\u7761\u61d2\u89c9\u554a\uff01\u201d\u8d70\u5230\u8bb8\u601d\u623f\u95f4\u95e8\u53e3\u88c5\u6a21\u4f5c\u6837\u7684\u6572\u4e86\u6572\u95e8\uff0c\u201c\u8bb8\u601d\u59d0\uff0c\u9edb\u513f\u59d0\u59d0\u8fc7\u6765\u4e86\u3002\u201d\u8bb8\u601d\u5728\u91cc\u9762\u5e94\u4e86\u4e00\u58f0\uff0c\u8bf4\u8981\u7a7f\u8863\u670d\u3002</p><p>\u3000\u3000\u6c5f\u9edb\u513f\u4e0d\u597d\u610f\u601d\u7684\u7b11\u4e86\u7b11\uff1a\u201c\u6015\u665a\u4e86\u4f60\u4eec\u5c31\u8d70\u4e86\u3002\u201d</p><p>\u3000\u3000\u6362\u662f\u8bb8\u601d\u7a7f\u6d74\u888d\u51fa\u6765\u5f00\u95e8\uff0c\u5979\u904d\u5e03\u7ec6\u6ed1\u8116\u9888\u4e0b\u7684\u543b\u75d5\u4e00\u5b9a\u906e\u63a9\u4e0d\u4f4f\uff0c\u6c5f\u9edb\u513f\u5f53\u7136\u65e0\u6cd5\u4ece\u5f20\u606a\u7684\u8868\u73b0\u91cc\u770b\u51fa\u4ec0\u4e48\u4e0d\u5bf9\u52b2\u6765\u3002</p><p>\u3000\u3000\u201c\u600e\u4e48\uff0c\u4f60\u8fd8\u8ddf\u6211\u4eec\u53bb\u6d77\u5dde\u554a\uff1f\u201d</p><p>\u3000\u3000\u201c\u600e\u4e48\u4e0d\u53bb\uff0c\u201d\u6c5f\u9edb\u513f\u5ae3\u7136\u4e00\u7b11\uff0c\u773c\u775b\u85cf\u7740\u4e9b\u72e1\u9ee0\uff0c\u201c\u6211\u575a\u6301\u8981\u53bb\uff0c\u4ed6\u4eec\u80fd\u62ff\u6211\u600e\u4e48\u7740\uff1f\u6211\u7238\u5077\u5077\u7684\u540c\u610f\u6211\u51fa\u53bb\u73a9\u4e00\u4e0b\uff0c\u8d5e\u52a9\u6211\u8d39\u7528\u5462\u3002\u201d</p><p>\u3000\u3000\u8bb8\u601d\u5728\u623f\u95f4\u78e8\u8e6d\u4e86\u597d\u4e45\uff0c\u624d\u6536\u62fe\u505c\u5f53\uff0c\u5f20\u606a\u6015\u5979\u8ddf\u81ea\u5df1\u76f4\u63a5\u76f8\u5bf9\u4f1a\u9732\u7834\u7efd\uff0c\u5fd9\u56de\u623f\u95f4\u53bb\u7a7f\u8863\u670d\uff0c\u7b49\u7a7f\u597d\u8863\u670d\u51fa\u6765\uff0c\u8bb8\u601d\u7684\u773c\u795e\u4e00\u76f4\u4e0d\u6562\u770b\u81ea\u5df1\uff0c\u5979\u505a\u8d3c\u5fc3\u865a\u5462\u3002\u6628\u5929\u767d\u5929\u8865\u8fc7\u89c9\uff0c\u591c\u91cc\u867d\u7136\u53ea\u7761\u56db\u4e2a\u5c0f\u65f6\uff0c\u4e5f\u8db3\u591f\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u8ba9\u8bb8\u601d\u5148\u966a\u6c5f\u9edb\u513f\u53bb\u5bbe\u9986\u7684\u9910\u5385\u5403\u65e9\u996d\uff0c\u4ed6\u5c06\u623f\u95f4\u53c8\u5927\u6982\u6536\u62fe\u4e86\u4e00\u4e0b\uff0c\u5c06\u968f\u8eab\u4e1c\u897f\u4e00\u8d77\u62ff\u51fa\u5957\u95f4\uff0c\u5403\u8fc7\u65e9\u9910\uff0c\u76f4\u63a5\u5230\u670d\u52a1\u53f0\u9000\u623f\u3002\u4ece\u6628\u5929\u6c14\u6e29\u56de\u5347\uff0c\u5929\u6c14\u8f6c\u6674\uff0c\u4ece\u6628\u5929\u5165\u591c\u540e\uff0c\u9ad8\u901f\u516c\u8def\u5df2\u7ecf\u53ef\u4ee5\u901a\u884c\u3002\u7ecf\u8fc7\u524d\u5929\u90a3\u6bb5\u8def\u9762\uff0c\u9664\u4e86\u626d\u66f2\u5389\u5bb3\u7684\u6ce2\u5f62\u62a4\u680f\u8fd8\u6ca1\u6709\u5168\u90e8\u53ca\u65f6\u7684\u66f4\u6362\u4e4b\u5916\uff0c\u5df2\u7ecf\u770b\u4e0d\u5230\u8f66\u7978\u75d5\u8ff9\u4e86\uff0c\u5176\u4ed6\u4eba\u7ecf\u8fc7\u8fd9\u65f6\uff0c\u4e5f\u60f3\u8c61\u4e0d\u51fa\u8fd9\u91cc\u53d1\u751f\u51fa\u516b\u6b7b\u4e03\u91cd\u4f24\u7684\u7279\u5927\u4ea4\u901a\u4e8b\u6545\u3002</p><p>\u3000\u3000\u5728\u8def\u4e0a\u6c5f\u9edb\u513f\u63d0\u5230\u90a3\u4e2a\u4ee4\u4eba\u538c\u6076\u7684\u5bcc\u5bb6\u5b50\u5f1f\u53f6\u9e3f\uff0c\u6c5f\u5929\u96c6\u56e2\u5728\u60e0\u5c71\u662f\u76f8\u5f53\u6709\u5b9e\u529b\u7684\u6c11\u8425\u4f01\u4e1a\uff0c\u4ed7\u7740\u6c5f\u9edb\u513f\u7684\u7238\u7238\u8ddf\u6c5f\u5929\u96c6\u56e2\u6709\u4e9b\u4e1a\u52a1\u5f80\u6765\uff0c\u5c31\u6b7b\u7f20\u7740\u6c5f\u9edb\u513f\u3002</p><p>\u3000\u3000\u539f\u6765\u6c5f\u9edb\u513f\u5bb6\u91cc\u4e5f\u5f00\u4e86\u4e00\u5bb6\u5c0f\u89c4\u6a21\u7684\u94f8\u4ef6\u5382\uff0c\u4f46\u770b\u5979\u7238\u7238\u4e5f\u4e0d\u662f\u90a3\u79cd\u4e3a\u4e86\u751f\u610f\u4f1a\u628a\u5973\u513f\u5012\u8d34\u51fa\u6765\u7684\u4eba\uff0c\u5f20\u606a\u6487\u5634\u7b11\u7740\uff0c\u8bf4\u9053\uff1a\u201c\u8d76\u660e\u9001\u9edb\u513f\u59d0\u59d0\u4e00\u53ea\u82cd\u8747\u62cd\uff0c\u8fd9\u4e2a\u59d3\u53f6\u4e0d\u77e5\u8da3\uff0c\u4e00\u62cd\u62cd\u8fc7\u53bb\uff0c\u62cd\u6b7b\u4ed6\uff0c\u4e5f\u4e0d\u770b\u770b\u4ed6\u81ea\u5df1\u957f\u5565\u5fb7\u6027\u2026\u2026\u201d</p></article>"}}}]);
