(window.webpackJsonp=window.webpackJsonp||[]).push([[249],{271:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u5341\u8282 \u66b4\u4e71",content:"<article><h2>\u7b2c\u4e8c\u5341\u8282 \u66b4\u4e71</h2><p>\u86db\u540e\u5927\u9053\u7684\u5c3d\u5934\uff0c\u5b8f\u4f1f\u7684\u795e\u6bbf\u9759\u9759\u77d7\u7acb\u7740\uff0c\u5b83\u7684\u4e0a\u65b9\u5996\u706b\u5347\u817e\uff0c\u5f71\u50cf\u7f13\u7f13\u6d41\u8f6c\uff0c\u6700\u7ec8\u5b9a\u578b\u4e0b\u6765\u3002\u838e\u7f55\u59ae\xb7\u6708\u5f13\uff0c\u4e00\u4f4d\u94f6\u53d1\u5782\u8170\uff0c\u8eab\u62ab\u6d45\u84dd\u8272\u900f\u660e\u4e1d\u888d\u7684\u7cbe\u7075\u5973\u795e\u5728\u865a\u7a7a\u4e2d\u51fa\u73b0\uff0c\u7d27\u63a5\u7740\uff0c\u5979\u8eab\u540e\u7684\u9ed1\u6697\u626d\u66f2\u53d8\u5f62\uff0c\u5e7b\u5316\u6210\u4e00\u53ea\u5e9e\u5927\u7684\u7528\u516b\u4e2a\u8111\u888b\u7684\u9ed1\u5be1\u5987\u8718\u86db\uff0c\u5c06\u6bd2\u7259\u6df1\u6df1\u5d4c\u5165\u7cbe\u7075\u5973\u795e\u7684\u540e\u9888\u4e2d\uff0c\u516b\u4e2a\u722a\u5b50\u50cf\u950b\u5229\u7684\u5200\u5203\uff0c\u5c06\u5979\u6495\u6210\u788e\u7247\u2014\u2014\u7136\u540e\u5e7b\u8c61\u91cd\u7ec4\uff0c\u518d\u6b21\u5f62\u6210\u7cbe\u7075\u5973\u795e\uff0c\u518d\u6b21\u88ab\u6495\u88c2\uff0c\u8fd9\u4e00\u5e55\u5c06\u4f1a\u65e0\u4f11\u65e0\u6b62\u5730\u6301\u7eed\u4e0b\u53bb\uff0c\u76f4\u5230\u4e0b\u4e00\u4e2a\u65f6\u8fb0\u7684\u5230\u6765\u3002</p><p>\u5353\u5c14\u5973\u5b50\u548c\u516b\u9996\u8718\u86db\uff0c\u8fd9\u662f\u7f57\u4e1d\u6700\u7ecf\u5e38\u5411\u4fe1\u5f92\u5c55\u793a\u7684\u4e24\u4e2a\u795e\u76f8\uff0c\u9664\u6b64\u4e4b\u5916\u5979\u6709\u65f6\u5019\u8fd8\u4f1a\u7528\u6076\u9b54\u7684\u5f62\u8c61\u51fa\u73b0\uff0c\u6216\u8005\u5c31\u662f\u4e00\u56e2\u7eaf\u7cb9\u7684\u9ed1\u6697\u3002</p><p>\u5728\u9065\u8fdc\u7684\u8fc7\u53bb\uff0c\u5728\u5353\u5c14\u4eec\u8fd8\u751f\u6d3b\u5728\u5730\u8868\u4e16\u754c\u7684\u6811\u6797\u4e2d\uff0c\u4e0d\u66fe\u88ab\u653e\u9010\u5230\u5e7d\u6697\u5730\u57df\u7684\u65f6\u5019\uff0c\u7f57\u4e1d\u66fe\u7ecf\u662f\u7cbe\u7075\u4e3b\u795e\u67ef\u745e\u9686\u7684\u59bb\u5b50\u3002\u5979\u52fe\u7ed3\u4e86\u67d0\u4e9b\u90aa\u795e\uff0c\u7b79\u5212\u4e86\u4e00\u573a\u9634\u8c0b\uff0c\u8bd5\u56fe\u53d1\u52a8\u53db\u4e71\uff0c\u7be1\u593a\u5979\u4e08\u592b\u7684\u4f4d\u7f6e\uff0c\u4f46\u5374\u88ab\u51e0\u6b21\u838e\u7f55\u59ae\xb7\u6708\u5f13\u6240\u963b\u6320\uff0c\u5e76\u4e14\u6700\u540e\u63ed\u53d1\u3002\u7f57\u4e1d\u7684\u53db\u4e71\u5931\u8d25\uff0c\u88ab\u67ef\u745e\u9686\u9010\u51fa\u963f\u68b5\u591a\uff08\u7cbe\u7075\u8bf8\u795e\u6240\u5c45\u4f4f\u7684\u4f4d\u9762\uff09\uff0c\u5760\u843d\u5230\u65e0\u5c3d\u6df1\u6e0a\uff0c\u5979\u7684\u9ed1\u76ae\u80a4\u5b50\u6c11\u4eec\u4e5f\u88ab\u8feb\u9003\u5165\u5730\u4e0b\uff0c\u518d\u4e5f\u4e0d\u80fd\u6c90\u6d74\u5728\u9633\u5149\u4e4b\u4e2d\u3002\u6240\u4ee5\u5728\u8bf8\u4f4d\u7cbe\u7075\u795e\u7947\u5f53\u4e2d\uff0c\u838e\u7f55\u59ae\xb7\u6708\u5f13\u6beb\u65e0\u7591\u95ee\u662f\u7f57\u4e1d\u6700\u618e\u6068\u7684\u5bf9\u8c61\u4e4b\u4e00\u3002</p><p>\u8fd9\u4e00\u5e55\u7684\u51fa\u73b0\uff0c\u610f\u5473\u7740\u74dc\u7406\u5fb7\u65af\u57ce\u7684\u508d\u665a\u6765\u4e34\uff0c\u5982\u679c\u6362\u7b97\u6210\u5730\u8868\u65f6\u95f4\uff0c\u5927\u81f4\u5c31\u662f\u4e0b\u5348\u516d\u70b9\u5de6\u53f3\u3002\u5170\u83ab\u96f7\u65af\u533a\u5e02\u573a\u4e0a\uff0c\u987e\u5ba2\u4eec\u6e10\u6e10\u7a00\u5c11\u8d77\u6765\uff0c\u4e00\u4e9b\u5546\u4eba\u5df2\u7ecf\u5f00\u59cb\u6536\u62fe\u644a\u4f4d\uff0c\u51c6\u5907\u7ed3\u675f\u8fd9\u4e00\u5929\u7684\u5de5\u4f5c\uff0c\u56de\u5bb6\u4f11\u606f\u6216\u8005\u53bb\u9152\u9986\u559d\u4e0a\u51e0\u676f\uff0c\u751a\u81f3\u53bb\u67d0\u4e9b\u98ce\u6708\u573a\u6240\u627e\u627e\u4e50\u5b50\u3002\u4f9d\u636e\u4e0a\u4ea4\u7ed9\u6267\u653f\u8bae\u4f1a\u7684\u91d1\u5e01\u591a\u5c11\uff0c\u4ed6\u4eec\u6bcf\u4e2a\u4eba\u90fd\u62e5\u6709\u6570\u91cf\u4e0d\u7b49\u7684\u77f3\u7b0b\uff0c\u7a7a\u5fc3\u7684\uff0c\u4f5c\u4e3a\u4ed3\u5e93\uff0c\u6ca1\u6709\u9500\u552e\u51fa\u53bb\u7684\u8d27\u7269\u5c31\u53ef\u4ee5\u50a8\u5b58\u5728\u5176\u4e2d\u3002\u6240\u6709\u77f3\u7b0b\u90fd\u8bbe\u7f6e\u4e86\u9b54\u6cd5\u5bc6\u95e8\uff0c\u53ea\u6709\u77e5\u9053\u76f8\u5e94\u7684\u53e3\u4ee4\u624d\u80fd\u6253\u5f00\u2014\u2014\u53e3\u4ee4\u6bcf\u9694\u516d\u5341\u516d\u5929\u4e00\u6362\uff0c\u5230\u65f6\u5019\u5c31\u9700\u8981\u518d\u6b21\u4e0a\u7f34\u91d1\u5e01\u6765\u83b7\u5f97\u65b0\u53e3\u4ee4\u3002</p><p>\u51ef\u5c14\u7279\xb7\u745e\u8d39\u5fb7\u662f\u4e00\u4f4d\u5e74\u8f7b\u7684\u5546\u4eba\uff0c\u4ed6\u539f\u672c\u662f\u745e\u8d39\u5fb7\u5bb6\u65cf\u4e00\u4e2a\u65c1\u7cfb\u6210\u5458\uff0c\u66fe\u7ecf\u5728\u5deb\u5e08\u5b66\u6821\u8fdb\u4fee\u6bd5\u4e1a\uff0c\u6210\u7ee9\u4e0d\u5dee\u3002\u4f46\u745e\u975e\u5fb7\u5bb6\u65cf\u7d20\u6765\u5c31\u4ee5\u51fa\u4f18\u79c0\u7684\u5deb\u5e08\u8457\u540d\uff0c\u4ed6\u65e0\u6cd5\u8131\u9896\u800c\u51fa\uff0c\u8ddd\u79bb\u5bb6\u65cf\u9996\u5e2d\u5deb\u5e08\u7684\u68a6\u60f3\u9065\u9065\u65e0\u671f\uff0c\u540e\u6765\u7d22\u6027\u8131\u79bb\u4e86\u5bb6\u65cf\uff0c\u52a0\u5165\u5546\u961f\uff08\u5728\u5353\u5c14\u793e\u4f1a\uff0c\u8fd9\u662f\u7537\u6027\u8131\u79bb\u5bb6\u65cf\u5bfb\u6c42\u72ec\u7acb\u7684\u4e3a\u6570\u4e0d\u591a\u7684\u51e0\u79cd\u65b9\u5f0f\u4e4b\u4e00\uff09\uff0c\u5f00\u4e86\u4e00\u5bb6\u5deb\u5e08\u5546\u5e97\uff0c\u51fa\u552e\u5404\u79cd\u6cd5\u672f\u6750\u6599\uff0c\u4ee5\u53ca\u7b80\u5355\u7684\u9b54\u6cd5\u836f\u5242\u3002\u51ed\u501f\u5f80\u65e5\u7ed3\u4ea4\u8425\u9020\u7684\u5173\u7cfb\u7f51\u7edc\uff0c\u4ed6\u7684\u751f\u610f\u84b8\u84b8\u65e5\u4e0a\uff0c\u5f88\u5feb\u53d1\u4e86\u5927\u8d22\u3002</p><p>\u770b\u770b\u65f6\u95f4\uff0c\u4ed6\u6307\u6325\u7740\u624b\u4e0b\u4e24\u4e2a\u725b\u5934\u602a\u5c06\u8d27\u7269\u653e\u8fdb\u65c1\u8fb9\u7684\u4e2d\u7a7a\u77f3\u7b0b\u91cc\uff0c\u9ed8\u8bf5\u5492\u8bed\u5c01\u4e0a\u5bc6\u95e8\u3002\u6382\u4e86\u6382\u53e3\u888b\u91cc\u7684\u91d1\u5e01\uff0c\u4ed6\u8003\u8651\u4eca\u665a\u8981\u4e0d\u8981\u53bb\u4e00\u8d9f\u6c89\u9ed8\u4e4b\u6b4c\uff0c\u90a3\u91cc\u6709\u4ed6\u89ca\u89ce\u5df2\u4e45\u7684\u730e\u7269\uff1a\u6606\u7c73\xb7\u83ab\u5170\u83f2\uff0c\u662f\u5df2\u7ecf\u6d88\u5931\u7684\u83ab\u5170\u83f2\u5bb6\u65cf\u7684\u6b66\u6280\u957f\u3002\u5728\u8fc7\u53bb\u7684\u5c81\u6708\u91cc\uff0c\u8fd9\u4e24\u4e2a\u5bb6\u65cf\u662f\u6b7b\u654c\u3002\u540e\u6765\u83ab\u5170\u83f2\u5bb6\u65cf\u5012\u53f0\uff0c\u5176\u4e2d\u5c31\u6709\u745e\u8d39\u5fb7\u5bb6\u65cf\u7684\u4e00\u4efd\u529f\u52b3\u3002</p><p>\u80fd\u7ed9\u5c06\u6614\u65e5\u7684\u4ec7\u654c\u538b\u5728\u8eab\u4e0b\u8086\u610f\u8e42\u8e8f\uff0c\u8fd9\u60f3\u60f3\u5c31\u4ee4\u4eba\u6fc0\u52a8\u3002</p><p>\u6b63\u5982\u6b64\u6253\u7b97\u7740\uff0c\u4ece\u4e1c\u5357\u8fb9\u7a81\u7136\u4f20\u6765\u4e00\u9635\u9a9a\u52a8\u3002\u745e\u8d39\u5fb7\u6115\u7136\u671b\u53bb\uff0c\u5374\u89c1\u9ed1\u538b\u538b\u7684\u4e00\u5927\u7247\u5974\u96b6\u4eec\u6d8c\u8fc7\u6765\uff0c\u6709\u98df\u4eba\u9b54\uff0c\u6709\u5730\u7cbe\uff0c\u6709\u718a\u5730\u7cbe\uff0c\u6709\u72d7\u5934\u4eba\uff0c\u8fd8\u6709\u725b\u5934\u602a\u548c\u8c7a\u72fc\u4eba\uff0c\u5b83\u4eec\u7a7f\u7740\u7b80\u964b\u7684\u4e0d\u5408\u8eab\u7684\u76ae\u7532\uff0c\u5927\u90e8\u4efd\u90fd\u6ca1\u6709\u9632\u62a4\uff0c\u63e1\u7740\u660e\u663e\u662f\u4e34\u65f6\u51d1\u8d77\u6765\u7684\u6b66\u5668\uff0c\u543c\u53eb\u7740\u671d\u5e02\u573a\u4e0a\u51b2\u8fc7\u6765\u3002</p><p>\u6240\u6709\u5546\u4eba\u548c\u8fd8\u7559\u5728\u5e02\u573a\u4e0a\u987e\u5ba2\u90fd\u60ca\u5446\u4e86\uff0c\u4ed6\u4eec\u505a\u68a6\u90fd\u4e0d\u4f1a\u68a6\u89c1\u5982\u6b64\u8352\u8bde\u7684\u60c5\u5f62\u3002\u4e00\u7fa4\u6700\u4f4e\u8d31\u602f\u61e6\u7684\u5974\u96b6\uff0c\u5c45\u7136\u50cf\u53d1\u4e86\u75af\u4e00\u822c\uff0c\u52c7\u6c14\u767e\u500d\u5730\u671d\u4ed6\u4eec\u7684\u4e3b\u4eba\u51b2\u950b\u3002\u867d\u7136\u542c\u4e0d\u61c2\u5b83\u4eec\u7684\u4f4e\u7ea7\u8bed\u8a00\uff0c\u4f46\u770b\u6a21\u6837\u663e\u7136\u4e0d\u662f\u70ed\u60c5\u53cb\u5584\u7684\u8868\u793a\u3002</p><p>\u201c\u88ad\u51fb\uff01\u201d</p><p>\u4e0d\u77e5\u9053\u662f\u8c01\u6700\u5148\u53cd\u5e94\u8fc7\u6765\uff0c\u5927\u543c\u4e00\u58f0\uff0c\u5176\u4ed6\u4eba\u5982\u68a6\u521d\u9192\uff0c\u7eb7\u7eb7\u558a\u53eb\u8d77\u6765\uff0c\u6307\u6325\u7740\u81ea\u5df1\u7684\u5974\u96b6\u548c\u536b\u961f\u4e0a\u524d\u62b5\u5fa1\uff0c\u81ea\u5df1\u51c6\u5907\u9003\u8dd1\u3002\u5e02\u573a\u533a\u5e76\u6ca1\u6709\u519b\u961f\u5e38\u9a7b\uff0c\u5404\u5927\u5bb6\u65cf\u7684\u6b66\u88c5\u529b\u91cf\u90fd\u7559\u5728\u5b85\u9662\u91cc\uff0c\u5c24\u5176\u662f\u6700\u8fd1\u8fd9\u79cd\u8d8b\u52bf\u8d8a\u6765\u8d8a\u660e\u663e\u3002\u4ec5\u51ed\u8fd9\u4e9b\u5546\u4eba\uff0c\u662f\u65e0\u6cd5\u62b5\u6321\u5982\u6b64\u591a\u7684\u75af\u72c2\u7684\u5974\u96b6\u7684\u3002</p><p>\u745e\u8d39\u5fb7\u4ece\u6597\u7bf7\u5185\u4fa7\u62bd\u51fa\u6cd5\u6756\uff0c\u5de6\u624b\u5df2\u7ecf\u4ece\u53e3\u888b\u91cc\u6293\u8d77\u4e86\u4e00\u679a\u5706\u73af\uff0c\u81ea\u4ece\u79bb\u5f00\u5bb6\u65cf\uff0c\u4ed6\u5c31\u5f88\u5c11\u6709\u953b\u70bc\u81ea\u5df1\u7684\u5deb\u672f\u6280\u5de7\u7684\u673a\u4f1a\uff0c\u4f46\u8fd9\u5e76\u4e0d\u610f\u5473\u7740\u4ed6\u751f\u758f\u4e86\u3002\u4ed6\u7684\u4e24\u4e2a\u725b\u5934\u602a\u4f8d\u536b\u5486\u54ee\u7740\uff0c\u63e1\u7d27\u5de8\u65a7\uff0c\u5374\u4e0d\u6562\u4e0a\u524d\u3002\u725b\u5934\u602a\u867d\u7136\u51f6\u608d\u86ee\u52c7\uff0c\u9762\u5bf9\u5982\u6b64\u591a\u7684\u654c\u4eba\u4e5f\u662f\u4f9d\u7136\u4f1a\u672c\u80fd\u6050\u60e7\u7684\u3002</p><p>\u5974\u96b6\u4eec\u51b2\u4e86\u4e0a\u6765\uff0c\u7136\u540e\u745e\u8d39\u5fb7\u60ca\u6050\u5730\u53d1\u73b0\u5b83\u4eec\u7684\u961f\u4f0d\u5728\u51e0\u79d2\u949f\u91cc\u53c8\u58ee\u5927\u4e86\u3002\u5f88\u591a\u5546\u4eba\u7684\u4f8d\u536b\u51e0\u4e4e\u5728\u4e00\u77ac\u95f4\u53db\u53d8\uff0c\u8f6c\u6295\u5230\u654c\u4eba\u7684\u9635\u8425\u4e2d\uff0c\u53cd\u8fc7\u6765\u5411\u5b83\u4eec\u539f\u672c\u7684\u4e3b\u4eba\u5200\u65a7\u76f8\u5411\u3002\u5deb\u5e08\u779f\u4e86\u773c\u8eab\u8fb9\u7684\u725b\u5934\u602a\u4f8d\u536b\uff0c\u6084\u6084\u540e\u9000\uff0c\u51c6\u5907\u5728\u654c\u4eba\u8fd8\u6ca1\u51b2\u8fc7\u6765\u4e4b\u524d\u9003\u8dd1\u3002\u4f46\u5f53\u4ed6\u521a\u521a\u5ff5\u51fa\u5492\u8bed\u7684\u7b2c\u4e8c\u4e2a\u97f3\u8282\u65f6\uff0c\u4e00\u652f\u5f29\u7bad\u5c04\u4e86\u8fc7\u6765\uff0c\u6b63\u4e2d\u4ed6\u7684\u80a9\u8180\u3002</p><p>\u5deb\u5e08\u7684\u5492\u8bed\u88ab\u6253\u65ad\u4e86\uff0c\u4ed6\u5598\u606f\u7740\uff0c\u770b\u4e86\u4e00\u773c\u8fdc\u5904\u7684\u5f29\u624b\uff0c\u90a3\u662f\u4e00\u53ea\u8c7a\u72fc\u4eba\u3002\u745e\u8d39\u5fb7\u8003\u8651\u628a\u5b83\u7684\u76f8\u8c8c\u8bb0\u4f4f\uff0c\u4ee5\u4fbf\u65e5\u540e\u590d\u4ec7\uff0c\u7136\u800c\u4ed6\u6000\u7591\u81ea\u5df1\u80fd\u505a\u5230\u8fd9\u70b9\uff0c\u5728\u5353\u5c14\u4eec\u773c\u4e2d\u770b\u6765\uff0c\u4e16\u754c\u4e0a\u7684\u8c7a\u72fc\u4eba\u90fd\u957f\u4e00\u4e2a\u6837\u5b50\u3002\u4ed6\u54ac\u7d27\u7259\u5173\uff0c\u62d4\u51fa\u5f29\u7bad\uff0c\u5e86\u5e78\u90a3\u4e0a\u9762\u6ca1\u6709\u62b9\u6bd2\uff0c\u7136\u540e\u8f6c\u8eab\u5f00\u59cb\u5954\u8dd1\u3002\u80a9\u8180\u4e0a\u7684\u75bc\u75db\u4e00\u9635\u4e00\u9635\u5730\u7275\u626f\u7740\uff0c\u5f7b\u900f\u5fc3\u80ba\uff0c\u745e\u8d39\u5fb7\u611f\u89c9\u547c\u5438\u8270\u96be\uff0c\u4ed6\u5fcd\u8010\u7740\uff0c\u8dd1\u8fdb\u8fd8\u6ca1\u6765\u5f97\u53ca\u62c6\u5378\u4e0b\u6765\u7684\u4e34\u65f6\u8d27\u67b6\u540e\u9762\uff0c\u501f\u52a9\u5b83\u4eec\u7684\u63a9\u62a4\u5f00\u59cb\u5ff5\u8bf5\u7b2c\u4e8c\u4e2a\u5492\u8bed\u3002</p><p>\u8fd9\u6b21\u5deb\u5e08\u6210\u529f\u4e86\uff0c\u4ed6\u7684\u8eab\u4f53\u5f00\u59cb\u53d8\u5f97\u8f7b\u5de7\u8d77\u6765\uff0c\u8089\u773c\u770b\u4e0d\u89c1\u7684\u98ce\u5728\u811a\u4e0b\u65cb\u8f6c\u805a\u96c6\uff0c\u5c06\u4ed6\u6258\u8d77\u3002\u5728\u9762\u5bf9\u6d77\u91cf\u7684\u654c\u4eba\u65f6\uff0c\u98de\u8d77\u6765\u662f\u4e2a\u660e\u667a\u7684\u9009\u62e9\uff0c\u53ea\u8981\u8db3\u591f\u9ad8\u7684\u8bdd\uff0c\u5f29\u7bad\u90fd\u5f88\u96be\u8d77\u4f5c\u7528\uff0c\u5974\u96b6\u4eec\u5f53\u4e2d\u6ca1\u6709\u65bd\u6cd5\u8005\uff0c\u745e\u8d39\u5fb7\u4e0d\u5fc5\u62c5\u5fc3\u6709\u8c01\u4f1a\u89e3\u6d88\u81ea\u5df1\u7684\u98de\u884c\u672f\u3002</p><p>\u5c31\u5728\u6b64\u65f6\uff0c\u4e00\u9635\u94fa\u5929\u76d6\u5730\u5730\u706b\u7130\u4ece\u5929\u800c\u964d\uff0c\u4eff\u4f5b\u66b4\u96e8\u4e00\u822c\u503e\u6cfb\u4e0b\u6765\uff0c\u5c06\u4ed6\u5305\u88f9\u8fdb\u53bb\u3002</p><p>\u203b\u203b\u203b</p><p>\u743c\u6069\u5750\u5728\u57ce\u5821\u91cc\uff0c\u4ed6\u65c1\u8fb9\u662f\u8299\u8389\u5a05\uff0c\u7075\u5438\u602a\u5deb\u5996\u60a0\u95f2\u5730\u5750\u5728\u5bf9\u9762\u3002\u4e09\u4e2a\u4eba\u5f62\u6210\u4e00\u4e2a\u73af\u72b6\uff0c\u4fef\u89c6\u7740\u5730\u9762\uff0c\u90a3\u539f\u672c\u662f\u575a\u786c\u5ca9\u77f3\u7684\u5730\u65b9\uff0c\u6b64\u65f6\u88ab\u9b54\u6cd5\u53d8\u6210\u4e86\u900f\u660e\u7684\u955c\u5b50\uff0c\u663e\u793a\u51fa\u74dc\u7406\u5fb7\u65af\u57ce\u7684\u5168\u8c8c\uff0c\u4ed6\u4eec\u5c31\u50cf\u662f\u6b63\u7ad9\u5728\u9ad8\u7a7a\u4fef\u77b0\u4e00\u822c\u3002\u743c\u6069\u53d1\u73b0\u6574\u4e2a\u4e0b\u57ce\u533a\u90fd\u5df2\u7ecf\u9a9a\u52a8\u6df7\u4e71\u8d77\u6765\uff0c\u4eff\u4f5b\u6240\u6709\u7684\u5974\u96b6\u90fd\u5728\u66b4\u52a8\uff0c\u62a2\u8fc7\u6b66\u5668\uff0c\u6740\u6b7b\u5b83\u4eec\u539f\u672c\u7684\u4e3b\u4eba\uff0c\u63a0\u593a\u8d22\u4ea7\uff0c\u751a\u81f3\u5f7c\u6b64\u6597\u6bb4\u3002\u5230\u5904\u90fd\u5728\u8d77\u706b\uff0c\u4e0d\u4ec5\u4ec5\u662f\u90a3\u4e9b\u6728\u5236\u7ed3\u6784\u7684\u7a9d\u68da\uff0c\u5305\u62ec\u77f3\u5934\u623f\u5c4b\u4e5f\u5728\u718a\u718a\u71c3\u70e7\uff0c\u743c\u6069\u53d1\u73b0\u5176\u4e2d\u6709\u4e00\u4e9b\u602a\u7269\u660e\u663e\u5730\u6bd4\u5176\u4ed6\u5974\u96b6\u8981\u8bad\u7ec3\u6709\u7d20\uff0c\u6216\u8005\u8bf4\u884c\u4e8b\u66f4\u6709\u76ee\u7684\u6027\uff0c\u5b83\u4eec\u4e00\u624b\u63d0\u7740\u94c1\u6876\uff0c\u4e00\u624b\u4e3e\u7740\u706b\u628a\uff0c\u5c06\u6876\u91cc\u9762\u7684\u6db2\u4f53\u503e\u5012\u6cfc\u6d12\u5728\u5efa\u7b51\u4e0a\uff0c\u7136\u540e\u7528\u706b\u628a\u70b9\u71c3\uff1b\u53e6\u5916\u4e00\u4e9b\u5974\u96b6\u5219\u5728\u6295\u63b7\u71c3\u70e7\u74f6\u2014\u2014\u5982\u679c\u743c\u6069\u6ca1\u8bb0\u9519\uff0c\u8fd9\u4e1c\u897f\u5728\u74dc\u7406\u5fb7\u65af\u57ce\u5c5e\u4e8e\u8fdd\u7981\u7269\u54c1\uff0c\u53ea\u6709\u9ad8\u5c42\u4eba\u7269\u624d\u80fd\u63a5\u89e6\u5230\u3002</p><p>\u5728\u98df\u4eba\u9b54\u548c\u725b\u5934\u602a\u7684\u7387\u9886\u4e0b\uff0c\u5f88\u591a\u5974\u96b6\u5f00\u59cb\u52c7\u6562\u5730\u653b\u51fb\u90a3\u4e9b\u5bb6\u65cf\u5b85\u9662\u3002\u5e76\u975e\u6bcf\u4e2a\u5bb6\u65cf\u90fd\u6709\u83f2\u5c14\u4f26\u5bb6\u65cf\u90a3\u6837\u7262\u56fa\u7684\u9b54\u6cd5\u5c4f\u969c\u2014\u2014\u5f88\u591a\u4f4e\u9636\u5bb6\u65cf\u751a\u81f3\u538b\u6839\u5c31\u6ca1\u6709\u56f4\u5899\uff0c\u5974\u96b6\u4eec\u6c79\u6d8c\u51b2\u5165\uff0c\u5353\u5c14\u4eec\u5f00\u59cb\u53cd\u51fb\uff0c\u4e00\u961f\u961f\u7684\u6218\u58eb\u5168\u526f\u6b66\u88c5\u5730\u4ece\u77f3\u7b0b\u4e2d\u8dd1\u51fa\u6765\uff0c\u8ffd\u6355\u65a9\u6740\u90a3\u4e9b\u56db\u5904\u7834\u574f\u7684\u5974\u96b6\u4eec\u3002\u8bba\u53ca\u88c5\u5907\u4e4b\u7cbe\u826f\uff0c\u8bad\u7ec3\u4e4b\u6709\u7d20\uff0c\u53db\u4e71\u7684\u5974\u96b6\u4eec\u81ea\u7136\u4e0d\u5982\u5353\u5c14\u6218\u58eb\uff0c\u4f46\u5b83\u4eec\u80dc\u5728\u4eba\u6570\u4f17\u591a\u3002\u6267\u653f\u8bae\u4f1a\u7684\u4e8c\u5341\u4e00\u4f4d\u5bb6\u65cf\u5c45\u4f4f\u5728\u4e0a\u57ce\u533a\uff0c\u4e0b\u57ce\u533a\u5c45\u4f4f\u7684\u90fd\u662f\u6392\u540d\u9760\u540e\uff0c\u5b9e\u529b\u8f83\u5f31\u7684\u5bb6\u65cf\uff0c\u5b83\u4eec\u5171\u901a\u7684\u7279\u70b9\u662f\u5353\u5c14\u6210\u5458\u6570\u91cf\u8f83\u5c11\uff0c\u4e0d\u5f97\u4e0d\u66f4\u591a\u5730\u9a71\u4f7f\u5974\u96b6\uff0c\u8fd9\u65e2\u964d\u4f4e\u4e86\u6574\u4f53\u7684\u519b\u4e8b\u7d20\u8d28\u548c\u6218\u6597\u529b\uff0c\u540c\u65f6\u4e5f\u5bfc\u81f4\u4e86\u66f4\u591a\u7684\u98ce\u9669\u3002\u5927\u91cf\u7684\u5bb6\u65cf\u5974\u96b6\u4e34\u9635\u5012\u6208\uff0c\u53cd\u800c\u52a0\u5165\u5230\u53db\u4e71\u8005\u7684\u884c\u5217\u4e2d\u3002\u5974\u96b6\u4eec\u7684\u6570\u91cf\u6025\u901f\u58ee\u5927\uff0c\u50cf\u6d2a\u6c34\u4e00\u6837\u6467\u6bc1\u7740\u8fd9\u5ea7\u57ce\u5e02\uff0c\u5c31\u8fde\u7d20\u6765\u4ee5\u602f\u61e6\u80c6\u5c0f\u8457\u79f0\u7684\u72d7\u5934\u4eba\u548c\u5730\u7cbe\u4e5f\u53d8\u5f97\u82f1\u52c7\u8d77\u6765\u2014\u2014\u56e0\u4e3a\u5b83\u4eec\u6b64\u65f6\u4eba\u591a\u52bf\u4f17\uff0c\u800c\u8fd9\u4e9b\u602a\u7269\u7684\u52c7\u6c14\u5411\u6765\u662f\u548c\u6570\u91cf\u6210\u6b63\u6bd4\u7684\uff0c\u5c24\u5176\u662f\u53d1\u73b0\u7267\u5e08\u5e76\u4e0d\u80fd\u50cf\u4ee5\u524d\u4e00\u6837\u7528\u5371\u9669\u7684\u9b54\u6cd5\u653b\u51fb\u5b83\u4eec\u4e4b\u540e\u3002\u5353\u5c14\u6218\u58eb\u4eec\u6e10\u6e10\u62b5\u654c\u4e0d\u4f4f\uff0c\u6218\u5c40\u5f00\u59cb\u65f6\u9677\u5165\u80f6\u7740\uff0c\u63a5\u7740\u4fbf\u4ee5\u660e\u663e\u7684\u901f\u5ea6\u504f\u79fb\uff0c\u6709\u51e0\u5ea7\u5c0f\u5bb6\u65cf\u7684\u5b85\u9662\u5df2\u7ecf\u88ab\u653b\u7834\u3002</p><p>\u4e0a\u57ce\u533a\u6682\u65f6\u8fd8\u6ca1\u6709\u88ab\u66b4\u52a8\u6ce2\u53ca\uff0c\u5404\u5927\u5bb6\u65cf\u7684\u5b85\u9662\u91cc\u5996\u706b\u95ea\u70c1\uff0c\u5f00\u59cb\u6709\u4e86\u52a8\u9759\u3002\u8725\u8734\u9a91\u5175\u4eec\u7684\u8eab\u5f71\u51fa\u73b0\u5728\u8857\u9053\u4e0a\uff0c\u4ed6\u4eec\u96c6\u7ed3\u7740\uff0c\u6c47\u6210\u51e0\u4e2a\u5c0f\u961f\uff0c\u7136\u540e\u5f00\u59cb\u5411\u4e0b\u57ce\u533a\u6251\u6765\uff0c\u663e\u7136\u662f\u8981\u5e2e\u52a9\u90a3\u4e9b\u5f31\u5c0f\u7684\u4f4e\u9636\u5bb6\u65cf\u4eec\u5e73\u5b9a\u53db\u4e71\u3002\u4ed6\u4eec\u4eba\u6570\u4e0d\u5c11\uff0c\u7c97\u7565\u4f30\u8ba1\u8d85\u8fc7\u4e0a\u767e\uff0c\u8725\u8734\u9a91\u5175\u662f\u9ed1\u6697\u7cbe\u7075\u6700\u5177\u653b\u51fb\u529b\u7684\u58eb\u5175\uff0c\u7c7b\u4f3c\u4eba\u7c7b\u7684\u91cd\u9a91\u5175\uff0c\u800c\u4e14\u66f4\u52a0\u7075\u6d3b\u3002\u5982\u679c\u8ba9\u8fd9\u652f\u529b\u91cf\u52a0\u5165\uff0c\u5974\u96b6\u4eec\u5fc5\u5b9a\u4e0d\u662f\u5bf9\u624b\uff0c\u5df2\u7ecf\u71c3\u8d77\u7684\u53db\u4e71\u4e4b\u706b\u4f1a\u88ab\u6beb\u65e0\u7591\u95ee\u5730\u6251\u706d\u3002\u743c\u6069\u62ac\u5934\u770b\u7740\u7075\u5438\u602a\u5deb\u5996\uff0c\u4e0d\u77e5\u9053\u5b83\u6709\u4ec0\u4e48\u6253\u7b97\uff0c\u8981\u77e5\u9053\uff0c\u53db\u4e71\u8005\u8fd9\u8fb9\u540c\u6837\u4e5f\u662f\u6709\u8fd1\u767e\u540d\u5353\u5c14\u6218\u58eb\u7684\uff0c\u800c\u5230\u73b0\u5728\u4e3a\u6b62\u4ed6\u4eec\u8fd8\u6ca1\u6709\u51fa\u73b0\u3002</p><p>\u8fd9\u662f\u4e00\u652f\u5947\u5175\uff0c\u5982\u679c\u8fd0\u7528\u5f97\u5f53\uff0c\u52a0\u4e0a\u4e00\u4e9b\u597d\u8fd0\u6c14\u7684\u8bdd\uff0c\u672a\u5fc5\u4e0d\u80fd\u5960\u5b9a\u80dc\u5c40\u3002</p><p>\u7075\u5438\u602a\u7ad9\u8d77\u8eab\u6765\u3002</p><p>\u201c\u8be5\u6211\u4eec\u4e0a\u573a\u4e86\u3002\u201d\u4ed6\u5bf9\u743c\u6069\u8bf4\u3002</p><p>\u201c\u600e\u4e48\u505a\uff1f\u201d</p><p>\u7075\u5438\u602a\u79fb\u52a8\u7740\u624b\u6307\uff0c\u5728\u955c\u9762\u4e0a\u9065\u9065\u70b9\u4e86\u4e00\u70b9\uff0c\u4e00\u62b9\u7ea2\u5149\u95ea\u70c1\u7740\uff0c\u6807\u8bb0\u51fa\u4e00\u4e2a\u65b9\u4f4d\u3002\u743c\u6069\u4f4e\u5934\u770b\u53bb\uff0c\u53d1\u73b0\u6b63\u662f\u86db\u540e\u5927\u9053\u5de6\u4fa7\u7684\u796d\u7940\u5b66\u9662\uff0c\u74dc\u7406\u5fb7\u65af\u57ce\u57f9\u517b\u795e\u540e\u7267\u5e08\u7684\u5730\u65b9\u3002</p><p>\u201c\u6211\u4eec\u771f\u6b63\u7684\u76ee\u6807\u662f\u8fd9\u91cc\uff0c\u201d\u7075\u5438\u602a\u8bf4\uff0c\u201c\u4e0b\u57ce\u533a\u5df2\u7ecf\u9677\u5165\u6df7\u4e71\uff0c\u4e0a\u57ce\u533a\u7684\u5bb6\u65cf\u4eec\u6d3e\u51fa\u4e86\u63f4\u519b\uff0c\u81f3\u5c11\u4e24\u4e2a\u65f6\u8fb0\u4e4b\u5185\uff0c\u8fd9\u4e00\u5207\u4e0d\u4f1a\u5e73\u606f\u3002\u201d</p><p>\u201c\u4f46\u4e0a\u57ce\u533a\u7684\u5bb6\u65cf\u4eec\u4f9d\u65e7\u8fd8\u4fdd\u6709\u8db3\u591f\u7684\u5b9e\u529b\u3002\u201d\u743c\u6069\u63d0\u9192\uff0c\u4e00\u767e\u591a\u540d\u8725\u8734\u9a91\u5175\uff0c\u867d\u7136\u786e\u5b9e\u662f\u5f88\u5927\u7684\u6570\u76ee\uff0c\u4f46\u5374\u8fd8\u8fdc\u8fdc\u7b97\u4e0d\u4e0a\u503e\u5de2\u800c\u51fa\u3002</p><p>\u201c\u786e\u5b9e\u662f\u8db3\u591f\u7684\u5b9e\u529b\u2014\u2014\u8db3\u591f\u4ed6\u4eec\u56fa\u5b88\u81ea\u5df1\u7684\u5b85\u9662\uff0c\u201d\u7075\u5438\u602a\u7ea0\u6b63\uff0c\u201c\u4e3b\u6bcd\u4eec\u5e76\u4e0d\u77e5\u9053\u6211\u4eec\u771f\u6b63\u7684\u529b\u91cf\uff0c\u4f46\u5979\u4eec\u5e94\u8be5\u660e\u767d\u73b0\u5728\u6446\u5728\u53f0\u9762\u4e0a\u7684\u8fd9\u4e9b\u5e76\u975e\u5168\u90e8\u3002\u6ca1\u6709\u54ea\u4e2a\u7edf\u5e05\u4f1a\u5b64\u6ce8\u4e00\u63b7\uff0c\u8fd9\u5b8c\u5168\u4e0d\u7b26\u5408\u9ed1\u6697\u7cbe\u7075\u7684\u98ce\u683c\u3002\u5979\u4eec\u9700\u8981\u63d0\u9632\u6211\u4eec\u7684\u7a81\u7136\u88ad\u51fb\uff0c\u9700\u8981\u9707\u6151\u5bb6\u65cf\u5185\u90e8\u7684\u5f02\u5fd7\u8005\uff0c\u5979\u4eec\u73b0\u5728\u7f3a\u4e4f\u529b\u91cf\uff0c\u53c8\u7f3a\u5c11\u53ef\u4fe1\u4efb\u7684\u52a9\u624b\uff0c\u4e0d\u4f1a\u8d38\u7136\u51fa\u51fb\u7684\u3002\u4e5f\u5c31\u662f\u8bf4\uff0c\u53ea\u8981\u6211\u4eec\u7684\u901f\u5ea6\u8db3\u591f\u5feb\uff0c\u5728\u653b\u9677\u796d\u7940\u5b66\u9662\u4ee5\u540e\uff0c\u5979\u4eec\u7684\u63f4\u519b\u624d\u4f1a\u59d7\u59d7\u6765\u8fdf\u3002\u201d</p><p>\u201c\u7136\u800c\u796d\u7940\u5b66\u9662\u5e76\u975e\u4e0d\u582a\u4e00\u51fb\u3002\u201d\u743c\u6069\u8bf4\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u5b83\u662f\u57ce\u5e02\u91cc\u6700\u5f3a\u5927\u7684\u5730\u65b9\uff0c\u90a3\u91cc\u805a\u96c6\u4e86\u5927\u91cf\u7684\u7267\u5e08\uff08\u5305\u62ec\u6559\u6388\u548c\u5b9e\u4e60\u8005\uff09\uff0c\u62e5\u6709\u6700\u591a\u7684\u9b54\u6cd5\u7269\u54c1\uff08\u5305\u62ec\u5377\u8f74\u3001\u9b54\u6756\u548c\u5176\u4ed6\uff09\uff0c\u800c\u4e14\u8fd8\u6709\u6700\u4e3a\u7262\u56fa\u7684\u9b54\u6cd5\u9632\u5fa1\u4f53\u7cfb\uff0c\u90a3\u662f\u57ce\u4e2d\u4e00\u4ee3\u53c8\u4e00\u4ee3\u7684\u5deb\u5e08\u4e3a\u5b83\u6784\u5efa\u8d77\u6765\u7684\u3002</p><p>\u201c\u800c\u4e14\u6211\u4eec\u600e\u4e48\u80fd\u62b5\u8fbe\u796d\u7940\u5b66\u9662\u800c\u4e0d\u60ca\u52a8\u6cbf\u9014\u7684\u5bb6\u65cf\uff1f\u201d\u743c\u6069\u95ee\uff0c\u796d\u7940\u5b66\u9662\u4f4d\u4e8e\u86db\u540e\u5927\u9053\u7684\u5c3d\u5934\uff0c\u5982\u679c\u987a\u7740\u8857\u9053\u51b2\u8fc7\u53bb\uff0c\u65e9\u5728\u8def\u4e0a\u5c31\u4f1a\u88ab\u5404\u5927\u5bb6\u65cf\u62e6\u622a\u4e0b\u6765\u3002</p><p>\u201c\u4f20\u9001\u3002\u201d\u7075\u5438\u602a\u5deb\u5996\u8bf4\u3002</p><p>\u201c\u4f20\u9001\uff1f\u201d\u743c\u6069\u5fae\u5fae\u76b1\u7709\uff0c\u540c\u5728\u74dc\u7406\u5fb7\u65af\u57ce\u4e2d\uff0c\u8ddd\u79bb\u4e0d\u7b97\u592a\u8fdc\uff0c\u4f20\u9001\u4e5f\u672a\u5c1d\u4e0d\u53ef\uff0c\u7136\u800c\u666e\u901a\u7684\u4f20\u9001\u672f\u7edd\u65e0\u53ef\u80fd\u628a\u8fd1\u767e\u4eba\u9001\u8fc7\u53bb\uff0c\u9664\u975e\u662f\u4f20\u9001\u6cd5\u9635\u3002\u8fd9\u79cd\u9ad8\u6df1\u7684\u5965\u672f\uff0c\u743c\u6069\u662f\u4e0d\u4f1a\u7684\uff0c\u773c\u524d\u7684\u65af\u5170\u666e\u662f\u4e2a\u5deb\u5996\uff0c\u5012\u672a\u5fc5\u4e0d\u80fd\uff0c\u4f46\u95ee\u9898\u5728\u4e8e\uff0c\u796d\u7940\u5b66\u9662\u6709\u9b54\u6cd5\u5c4f\u969c\u4fdd\u62a4\uff0c\u6ca1\u529e\u6cd5\u4f5c\u4e3a\u4f20\u9001\u6cd5\u9635\u7684\u76ee\u7684\u5730\u3002\u5373\u4f7f\u9644\u8fd1\u7684\u533a\u57df\u4e5f\u4e0d\u80fd\uff0c\u86db\u540e\u5927\u9053\u662f\u57ce\u5e02\u91cc\u6700\u795e\u5723\u7684\u6240\u5728\uff0c\u5c3d\u5934\u7684\u7f57\u4e1d\u795e\u6bbf\uff0c\u4ee5\u53ca\u5b83\u4e24\u4fa7\u7684\u796d\u7940\u5b66\u9662\u3001\u5deb\u5e08\u5b66\u9662\u548c\u683c\u6597\u5b66\u9662\uff0c\u90fd\u7b3c\u7f69\u5728\u5c42\u5c42\u9b54\u6cd5\u4fdd\u62a4\u4e4b\u4e2d\uff0c\u9694\u7edd\u4e00\u5207\u5916\u6765\u4f20\u9001\u3002</p><p>\u201c\u4e0d\u5fc5\u62c5\u5fc3\uff0c\u201d\u7075\u5438\u602a\u5deb\u5996\u89e3\u91ca\uff0c\u201c\u5728\u4f20\u9001\u6cd5\u9635\u53d1\u52a8\u7684\u524d\u4e00\u77ac\u95f4\uff0c\u6211\u4f1a\u89e3\u5f00\u796d\u7940\u5b66\u9662\u7684\u9b54\u6cd5\u5c01\u9501\uff0c\u867d\u7136\u53ea\u80fd\u7ef4\u6301\u5f88\u77ed\u6682\u7684\u65f6\u95f4\uff0c\u4f46\u8db3\u591f\u4e86\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\u8981\u653b\u51fb\u796d\u7940\u5b66\u9662\uff1f\u201d\u8299\u8389\u5a05\u5728\u65c1\u8fb9\u7a81\u7136\u63d2\u5634\uff0c\u201c\u8fd9\u4f3c\u4e4e\u5e76\u4e0d\u80fd\u5de6\u53f3\u6218\u5c40\u3002\u201d</p><p>\u201c\u8fd9\u662f\u4e00\u4e2a\u9884\u793a\uff0c\u4e00\u4e2a\u5ba3\u544a\uff0c\u4e00\u4e2a\u699c\u6837\u548c\u4e00\u4e2a\u6307\u5f15\uff0c\u201d\u65af\u5170\u666e\u4f4e\u6c89\u5730\u7b11\u7740\uff0c\u201c\u6211\u4ece\u6765\u6ca1\u6709\u671f\u671b\u8fc7\u4e00\u4e3e\u6210\u529f\uff0c\u90a3\u4e0d\u73b0\u5b9e\u3002\u4f46\u662f\u83ab\u5c3c\u5361\u5c0f\u59d0\uff0c\u60f3\u60f3\u770b\uff0c\u73b0\u5728\u6211\u4eec\u53d1\u52a8\u4e86\u4e00\u573a\u66b4\u4e71\uff0c\u5f88\u591a\u5bb6\u65cf\u90fd\u4f1a\u5143\u6c14\u5927\u4f24\uff0c\u751a\u81f3\u706d\u4ea1\uff0c\u800c\u6700\u91cd\u8981\u7684\u662f\uff1a\u4e3b\u6bcd\u4eec\u6240\u523b\u610f\u4fdd\u4f4f\u7684\u79d8\u5bc6\u5c31\u8981\u5b88\u4e0d\u4f4f\u4e86\u3002\u53ea\u8981\u7ecf\u5386\u4e86\u8fd9\u573a\u66b4\u4e71\uff0c\u4efb\u4f55\u5934\u8111\u6e05\u9192\u7684\u4eba\u90fd\u4f1a\u53d1\u89c9\uff1a\u7267\u5e08\u4eec\u5df2\u7ecf\u4e27\u5931\u4e86\u795e\u672f\uff0c\u81ea\u7136\u800c\u7136\u7684\u63a8\u8bba\u5c31\u662f\u5979\u4eec\u4e27\u5931\u4e86\u7f57\u4e1d\u7684\u6069\u5ba0\u3002\u5982\u679c\u5728\u8fd9\u4e2a\u65f6\u5019\uff0c\u6211\u4eec\u505a\u5f97\u66f4\u8fdb\u4e00\u6b65\uff0c\u6467\u6bc1\u6389\u796d\u7940\u5b66\u9662\uff0c\u6740\u6b7b\u90a3\u4e9b\u7267\u5e08\u548c\u5c06\u6765\u7684\u7267\u5e08\u4eec\uff0c\u90a3\u4e48\u6240\u6709\u4eba\u90fd\u4f1a\u5bf9\u8fd9\u4e2a\u731c\u6d4b\u786e\u5b9a\u4e0d\u79fb\u4e86\u3002\u201d</p><p>\u201c\u7136\u540e\uff1f\u201d\u8299\u8389\u5a05\u7591\u60d1\uff0c\u201c\u8fd9\u4f1a\u635f\u5931\u6389\u4f60\u73b0\u5728\u6240\u805a\u96c6\u8d77\u6765\u7684\u5168\u90e8\u529b\u91cf\uff0c\u5974\u96b6\u4eec\u4f1a\u88ab\u65a9\u6740\uff0c\u800c\u4e14\u6211\u4eec\u90fd\u77e5\u9053\u90a3\u4e9b\u4f4e\u7b49\u751f\u7269\u4e0d\u53ef\u4fe1\u8d56\u3002\u7cbe\u9510\u7684\u5353\u5c14\u6218\u58eb\u4eec\u653b\u9677\u4e86\u796d\u7940\u5b66\u9662\uff0c\u7136\u540e\u4f1a\u88ab\u5404\u5927\u5bb6\u65cf\u7684\u63f4\u519b\u6d88\u706d\u3002\u786e\u5b9e\uff0c\u6240\u6709\u4eba\u90fd\u4f1a\u770b\u5230\u7267\u5e08\u6ca1\u6709\u9b54\u6cd5\u4e86\uff0c\u6240\u6709\u4eba\u90fd\u4f1a\u770b\u5230\u796d\u7940\u5b66\u9662\u88ab\u6467\u6bc1\u4e86\uff0c\u4f46\u4ed6\u4eec\u540c\u65f6\u4e5f\u4f1a\u6e05\u695a\u5730\u770b\u5230\uff0c\u5373\u4f7f\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u7267\u5e08\u4eec\u4f9d\u65e7\u5f3a\u5927\uff0c\u5979\u4eec\u4f9d\u65e7\u6709\u80fd\u529b\u6210\u529f\u5730\u9547\u538b\u66b4\u4e71\uff0c\u7ef4\u62a4\u79e9\u5e8f\u3002\u4e0b\u6b21\u4f60\u518d\u8981\u717d\u52a8\u66b4\u4e71\uff0c\u53ef\u5c31\u6ca1\u6709\u8fd9\u4e48\u5bb9\u6613\u4e86\u2014\u2014\u5982\u679c\u4f60\u80fd\u5728\u8fd9\u6b21\u5168\u8eab\u800c\u9000\u7684\u8bdd\u3002\u201d</p><p>\u201c\u6211\u5f53\u7136\u4f1a\u5168\u8eab\u800c\u9000\uff0c\u201d\u7075\u5438\u602a\u5deb\u5996\u70b9\u70b9\u5934\uff0c\u201c\u6211\u662f\u8bf4\u6211\u4eec\uff0c\u6211\u4eec\u90fd\u4f1a\u5b89\u5168\u5730\u8fd4\u56de\uff0c\u5305\u62ec\u4f60\u3001\u6211\uff0c\u8fd8\u6709\u90a3\u4e9b\u5353\u5c14\u6218\u58eb\u4eec\u3002\u5728\u6467\u6bc1\u796d\u7940\u5b66\u9662\u4ee5\u540e\uff0c\u6211\u4f1a\u518d\u6b21\u6784\u5efa\u4f20\u9001\u6cd5\u9635\uff0c\u5c06\u6240\u6709\u4eba\u9001\u56de\u6765\uff0c\u4e3b\u6bcd\u4eec\u53d1\u73b0\u4e0d\u4e86\u8fd9\u91cc\u3002\u201d</p><p>\u518d\u6b21\u6784\u5efa\u4f20\u9001\u6cd5\u9635\uff1f\u743c\u6069\u6697\u6697\u51b7\u7b11\uff0c\u4f20\u9001\u6cd5\u9635\u662f\u90a3\u4e48\u5bb9\u6613\u6784\u5efa\u8d77\u6765\u7684\u4e48\u3002\u5f53\u65f6\u5728\u5965\u7075\u591a\u5c14\u57ce\uff0c\u90a3\u4e9b\u7075\u5438\u602a\u4eec\u8981\u6784\u5efa\u4f20\u9001\u6cd5\u9635\uff0c\u9001\u743c\u6069\u7b49\u4eba\u53bb\u4f0a\u5361\u6c99\u57ce\uff0c\u5c31\u82b1\u4e86\u56db\u4e2a\u5c0f\u65f6\u3002\u73b0\u5728\u5c31\u51ed\u65af\u5170\u666e\u4e00\u4eba\uff0c\u4e0d\u53ef\u80fd\u505a\u5f97\u66f4\u5feb\u3002\u96be\u9053\u4ed6\u4ee5\u4e3a\u624b\u4e0b\u8fd9\u4e9b\u5353\u5c14\u6218\u58eb\u4eec\u5728\u653b\u4e0b\u796d\u7940\u5b66\u9662\u540e\u8fd8\u80fd\u7ee7\u7eed\u56fa\u5b88\u56db\u4e2a\u5c0f\u65f6\uff1f</p><p>\u8fd9\u79cd\u8c0e\u8bdd\uff0c\u4e5f\u5c31\u80fd\u6b3a\u9a97\u90a3\u4e9b\u4e0d\u7cbe\u901a\u9b54\u6cd5\u7684\u5353\u5c14\u6218\u58eb\u4eec\uff0c\u7792\u4e0d\u4e86\u743c\u6069\u3002</p><p>\u4f46\u4e5f\u786e\u5b9e\u5982\u8299\u8389\u5a05\u6240\u8bf4\uff0c\u65e2\u7136\u4e0d\u6c42\u6bd5\u5176\u529f\u4e8e\u4e00\u5f79\u7684\u8bdd\uff0c\u5982\u679c\u6b64\u6b21\u5c31\u5c06\u624b\u4e0a\u7684\u5175\u529b\u5168\u90e8\u635f\u5931\uff0c\u56fa\u7136\u4f1a\u7ed9\u4e3b\u6bcd\u4eec\u9020\u6210\u91cd\u521b\uff0c\u540c\u65f6\u4e5f\u4f1a\u8ba9\u4ee5\u540e\u7684\u53db\u4e71\u66f4\u52a0\u8270\u96be\u3002\u5931\u8d25\u7684\u8bb0\u5f55\u4f1a\u8ba9\u65b0\u7684\u6210\u5458\u671b\u800c\u5374\u6b65\uff0c\u7267\u5e08\u4eec\u7684\u7edf\u6cbb\u4f1a\u7ee7\u7eed\u82df\u5ef6\u6b8b\u5598\u4e0b\u53bb\u3002</p><p>\u65af\u5170\u666e\u4e0d\u53ef\u80fd\u4e0d\u8003\u8651\u8fd9\u4e9b\uff0c\u4ed6\u5230\u5e95\u5728\u6253\u7b97\u4ec0\u4e48\u2026\u2026\u6216\u8005\u8bf4\uff0c\u4ed6\u522b\u6709\u501a\u4ed7\uff1f</p><p>\u5404\u79cd\u4ea4\u9519\u7684\u8bb0\u5fc6\u5728\u4ed6\u8111\u4e2d\u6d6e\u73b0\uff0c\u4e00\u4e9b\u8109\u7edc\u6e10\u6e10\u6e05\u695a\u8d77\u6765\uff0c\u743c\u6069\u4e0d\u518d\u8bf4\u8bdd\u3002\u7075\u5438\u602a\u5deb\u5996\u4ece\u6000\u91cc\u638f\u51fa\u4e00\u4e2a\u73bb\u7483\u72b6\u7684\u7403\u4f53\uff0c\u671d\u5730\u4e0a\u7838\u53bb\u3002\u5728\u5b83\u7c89\u788e\u7684\u540c\u65f6\uff0c\u4e00\u4e2a\u6de1\u6de1\u7684\u9b54\u6cd5\u9635\u5728\u7a7a\u6c14\u4e2d\u663e\u73b0\u51fa\u6765\u3002\u5468\u56f4\u7684\u623f\u95f4\u91cc\uff0c\u4e00\u961f\u53c8\u4e00\u961f\u7684\u9ed1\u6697\u7cbe\u7075\u6d8c\u51fa\uff0c\u6574\u88c5\u5f85\u53d1\u3002</p></article>"}}}]);
