(window.webpackJsonp=window.webpackJsonp||[]).push([[249],{272:function(p){p.exports={chapterTitle:null,title:"\u7b2c135\u7ae0 \u8f66\u7978\u751f\u51fa\u6765\u7684\u53d8\u6570",content:"<article><h2>\u7b2c135\u7ae0 \u8f66\u7978\u751f\u51fa\u6765\u7684\u53d8\u6570</h2><p>\u3000\u3000\u60e0\u5c71\u79bb\u7701\u57ce\u53ea\u6709\u9694\u4e00\u767e\u516c\u91cc\u7684\u8def\u7a0b\uff0c\u5f20\u606a\u538b\u7740\u901f\u5ea6\uff0c\u5341\u70b9\u949f\u624d\u8d76\u5230\u7701\u91cc\u9886\u5bfc\u5c45\u4f4f\u7684\u65b0\u6885\u82d1\u3002\u6c5f\u9edb\u513f\u966a\u8bb8\u7ef4\u6765\u8fc7\u4e24\u56de\uff0c\u5bf9\u5f90\u5b66\u5e73\u5bb6\u4e5f\u4e0d\u964c\u751f\u3002</p><p>\u3000\u3000\u8c22\u665a\u6674\u770b\u5230\u8f66\u9876\u4e0a\u3001\u8f66\u73bb\u7483\u4e0a\u90fd\u6709\u6ce5\u70b9\u5b50\uff0c\u624d\u77e5\u9053\u5f20\u606a\u4e4b\u524d\u8bf4\u7ed9\u5835\u5728\u60e0\u5c71\u5176\u5b9e\u4ece\u8f66\u7978\u73b0\u573a\u53c8\u8fd4\u56de\u60e0\u5c71\u3002\u5979\u662f\u7ecf\u5386\u8fc7\u8f66\u7978\u7684\u4eba\uff0c\u867d\u7136\u770b\u7740\u5f20\u606a\u3001\u8bb8\u601d\u5b8c\u597d\u65e0\u635f\u7684\u7ad9\u5728\u8ddf\u524d\uff0c\u8fd8\u662f\u611f\u5230\u4e00\u9635\u9635\u7684\u540e\u6015\u3002</p><p>\u3000\u3000\u7701\u91cc\u5927\u4f6c\u591a\u534a\u4f4f\u65b0\u6885\u82d1\uff0c\u73b0\u5982\u4eca\u4e0b\u7ea7\u7ed9\u4e0a\u7ea7\u62dc\u5e74\u5df2\u7ecf\u60ef\u4f8b\uff0c\u5df2\u7ecf\u4e0d\u9700\u8981\u906e\u906e\u63a9\u63a9\uff0c\u5f20\u606a\u8fd8\u4ee5\u4e3a\u65b0\u6885\u82d1\u7684\u5730\u65b9\u516c\u52a1\u8f66\u4f1a\u7edc\u7ece\u4e0d\u7edd\uff0c\u6ca1\u60f3\u5230\u8fdb\u4e86\u65b0\u6885\u82d1\uff0c\u5374\u53d1\u73b0\u683c\u5916\u7684\u51b7\u6e05\u3002</p><p>\u3000\u3000\u5f90\u5b66\u5e73\u6ca1\u6709\u5916\u51fa\uff0c\u4ed6\u7684\u79d8\u4e66\u674e\u4e49\u6c5f\u4e5f\u5728\u8fd9\u91cc\uff0c\u5f20\u606a\u8fdb\u5c4b\u7ed9\u5927\u5bb6\u62f1\u624b\u62dc\u5e74\uff0c\u5c06\u82b7\u5f64\u62b1\u5728\u6000\u91cc\u3002\u5f90\u5b66\u5e73\u662f\u7701\u59d4\u526f\u4e66\u8bb0\u3001\u7701\u653f\u6cd5\u59d4\u4e66\u8bb0\uff0c\u9664\u4e86\u5730\u65b9\u515a\u653f\u9886\u5bfc\u4e4b\u5916\uff0c\u653f\u6cd5\u7cfb\u7edf\u4e5f\u90fd\u5c5e\u4e8e\u5f90\u5b66\u5e73\u7684\u4e00\u4ea9\u4e09\u5206\u5730\uff0c\u5c4b\u5b50\u6ca1\u6709\u522b\u4eba\uff0c\u5f20\u606a\u5fc3\u91cc\u5f88\u8bb6\u8be7\u3002\u5f90\u5b66\u5e73\u518d\u6e05\u5ec9\u5982\u6c34\uff0c\u4e5f\u4e0d\u4f1a\u4e0d\u8fd1\u4eba\u60c5\u5c06\u6765\u8bbf\u8005\u6321\u5728\u95e8\u5916\u3002\u5173\u952e\u6574\u4e2a\u65b0\u6885\u82d1\u90fd\u770b\u4e0d\u5230\u62dc\u5e74\u7684\u4eba\uff0c\u96be\u9053\u5929\u4e0b\u4e4c\u9e26\u90fd\u8f6c\u6027\u5b50\u4e86\uff1f</p><p>\u3000\u3000\u5728\u5ba2\u5385\u91cc\u804a\u4e86\u4e00\u4f1a\u513f\u8bdd\uff0c\u5f20\u606a\u5c31\u8ddf\u5f90\u5b66\u5e73\u5230\u4e66\u623f\u4e0b\u68cb\u53bb\u4e86\uff0c\u5176\u4ed6\u4eba\u90fd\u5e2e\u5fd9\u51c6\u5907\u4e2d\u996d\uff0c\u674e\u4e49\u6c5f\u5e2e\u4ed6\u4eec\u6536\u62fe\u8fc7\u68cb\u76d2\uff0c\u4e5f\u5c31\u51fa\u53bb\u4e86\u3002\u5f20\u606a\u5c06\u5fc3\u91cc\u7684\u8bb6\u5f02\u95ee\u51fa\u6765\u3002\u5f90\u5b66\u5e73\u8f7b\u8f7b\u53f9\u4e86\u4e00\u53e3\u6c14\uff1a\u201c\u60e0\u5c71\u5e02\u653f\u6cd5\u59d4\u526f\u4e66\u8bb0\u6b7b\u4e8e2\u67084\u65e5\u7684\u8f66\u7978\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u989d\u5934\u6e17\u51fa\u4e00\u8eab\u6c57\uff0c\u60f3\u8d77\u8b66\u8f66\u91cc\u51e0\u4e4e\u7ed9\u538b\u70c2\u7684\u90a3\u4e2a\u4e2d\u5e74\u8b66\u5b98\uff0c\u4ed6\u5e94\u8be5\u5c31\u662f\u60e0\u5c71\u5e02\u653f\u6cd5\u59d4\u526f\u4e66\u8bb0\u5427\uff0c\u5f20\u606a\u5bf9\u60e0\u5c71\u7684\u60c5\u51b5\u4e0d\u662f\u5f88\u6e05\u695a\uff0c\u4f46\u662f\u653f\u6cd5\u59d4\u4e66\u8bb0\u6216\u8005\u526f\u4e66\u8bb0\u517c\u4efb\u516c\u5b89\u5c40\u957f\u662f\u5f88\u6b63\u5e38\u7684\u4e8b\u60c5\uff0c\u90a3\u4e2a\u653f\u6cd5\u59d4\u526f\u4e66\u8bb0\u5927\u6982\u517c\u4efb\u60e0\u5c71\u5e02\u516c\u5b89\u5c40\u957f\uff0c\u624d\u7a7f\u7740\u4e00\u8eab\u8b66\u670d\uff0c\u5012\u4e0d\u77e5\u9053\u90a3\u4e2a\u5973\u8b66\u5b98\u662f\u8c01\u3002</p><p>\u3000\u3000\u5f20\u606a\u77e5\u9053\u65b0\u6885\u82d1\u4e3a\u4ec0\u4e48\u8fd9\u4e48\u51b7\u6e05\u4e86\uff0c\u60e0\u5c71\u5e02\u653f\u6cd5\u59d4\u526f\u4e66\u8bb0\u591c\u91cc\u51fa\u8f66\u7978\u662f\u56e0\u4e3a\u8d76\u5230\u7701\u91cc\u6765\u62dc\u5e74\uff0c\u5176\u4ed6\u4eba\u5927\u6982\u4e0d\u4f1a\u8fd9\u65f6\u5019\u6765\u89e6\u9709\u5934\u3002\u5f20\u606a\u5fc3\u60f3\uff1a\u4eca\u5e74\u7684\u6625\u8282\u771f\u662f\u51b7\u6e05\u554a\u3002\u6d77\u5dde\u90a3\u8fb9\u51fa\u53d1\u5f97\u65e9\uff0c\u521d\u4e09\u5c31\u5230\u7701\u91cc\uff0c\u5e94\u8be5\u5c71\u5934\u90fd\u62dc\u5b8c\u4e86\u624d\u56de\u53bb\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0d\u7531\u6076\u6bd2\u7684\u60f3\uff0c\u4fbf\u662f\u6b7b\u4e86\u4e5f\u662f\u7f6a\u6709\u5e94\u5f97\u554a\uff0c\u5fc3\u60f3\u90a3\u8f86\u8b66\u8f66\u5c31\u5728\u81ea\u5df1\u524d\u9762\u4e00\u4e9b\u65f6\u95f4\u51fa\u8f66\u7978\uff0c\u8981\u662f\u60e0\u5c71110\u4e0d\u53d1\u751f\u91cd\u5927\u6e0e\u804c\uff0c\u53ca\u65f6\u901a\u77e5\u8def\u653f\u5173\u95ed\u9ad8\u901f\u516c\u8def\uff0c\u4ed6\u4eec\u4e5f\u5e94\u8be5\u7ed9\u5835\u5728\u9ad8\u901f\u8def\u53e3\u3002</p><p>\u3000\u3000\u4e0d\u6653\u5f97\u5bf9\u5f90\u5b66\u5e73\u4f1a\u4e0d\u4f1a\u6709\u5f71\u54cd\uff0c\u62dc\u5e74\u7684\u4e8b\uff0c\u6ca1\u6709\u4eba\u4f1a\u53bb\u63d0\uff0c\u4f46\u662f\u60e0\u5c71110\u91cd\u5927\u6e0e\u804c\u624d\u5bfc\u81f4\u8f66\u7978\u4e25\u91cd\u5316\uff0c\u8fd9\u662f\u60e0\u5c71\u5e02\u653f\u6cd5\u7cfb\u7edf\u5185\u90e8\u7684\u5927\u95ee\u9898\uff0c\u4e3b\u8981\u7684\u8981\u8d1f\u8d77\u9886\u5bfc\u8d23\u4efb\u7684\u5f53\u4e8b\u4eba\u5374\u6b7b\u4e8e\u8fd9\u8d77\u8f66\u7978\uff0c\u6709\u4e9b\u8bbd\u523a\uff0c\u5f20\u606a\u5374\u66f4\u62c5\u5fc3\u8fd9\u4e8b\u5bf9\u5f90\u5b66\u5e73\u4f1a\u4e0d\u4f1a\u6709\u4e0d\u826f\u7684\u5f71\u54cd\u3002\u8fd9\u79cd\u5f71\u54cd\u6362\u5728\u5e73\u65f6\u90fd\u7b97\u4e0d\u4e0a\u4ec0\u4e48\uff0c\u4f46\u5728\u8fd9\u6837\u7684\u654f\u611f\u65f6\u523b\uff0c\u5374\u5341\u5206\u7684\u5173\u952e\u3002</p><p>\u3000\u3000\u5f20\u606a\u5c06\u4ed6\u8ddf\u8bb8\u601d\u7ecf\u5386\u60e0\u5c71\u8f66\u7978\u524d\u524d\u540e\u540e\u7684\u4e8b\u60c5\u8bf4\u7ed9\u5f90\u5b66\u5e73\u542c\uff0c\u53c8\u628a\u60e0\u5c71\u5e02\u91cc\u6700\u8fd1\u7684\u4e00\u4e9b\u53d8\u6545\u8bf4\u7ed9\u5f90\u5b66\u5e73\u542c\u3002</p><p>\u3000\u3000\u5f90\u5b66\u5e73\u4e5f\u542c\u4e86\u5f20\u606a\u5bf9\u8fd9\u4ef6\u4e8b\u7684\u5206\u6790\uff0c\u75b2\u60eb\u7565\u6709\u8001\u6001\u7684\u8138\u4e0a\uff0c\u7709\u5934\u8f7b\u8f7b\u7684\u76b1\u4e86\u8d77\u6765\uff0c\u4ed6\u4e60\u60ef\u6027\u7684\u62ff\u624b\u6258\u8d77\u76b1\u7eb9\u7eb5\u6a2a\u7684\u989d\u5934\uff0c\u5934\u53d1\u4e4c\u9ed1\u90a3\u662f\u65b0\u67d3\u8fc7\u7684\uff0c\u4ed6\u8f7b\u58f0\u8bf4\u9053\uff1a\u201c\u4e07\u5411\u524d\u5f53\u65f6\u4ece\u6d77\u5dde\u5e02\u59d4\u4e66\u8bb0\u7684\u4f4d\u5b50\u4e0a\u9000\u5c45\u4e8c\u7ebf\uff0c\u5f53\u65f6\u7701\u91cc\u5176\u4ed6\u4eba\u5e0c\u671b\u4ed6\u80fd\u5230\u7701\u4eba\u5927\u5f53\u4e2a\u526f\u4e3b\u4efb\u5b89\u517b\u665a\u5e74\uff0c\u6587\u7701\u957f\u63d0\u51fa\u4e0d\u540c\u610f\u89c1\uff0c\u52a0\u4e0a\u4ed6\u672c\u4eba\u4e5f\u5e0c\u671b\u7559\u5728\u6d77\u5dde\uff0c\u7701\u91cc\u5c31\u5c0a\u91cd\u4ed6\u672c\u4eba\u7684\u610f\u89c1\uff0c\u8ba9\u4ed6\u62c5\u4efb\u6d77\u5dde\u5e02\u4eba\u5927\u4e3b\u4efb\u3002\u6d77\u5dde\u5e02\u73b0\u5728\u7684\u60c5\u51b5\uff0c\u7701\u91cc\u4e5f\u90fd\u6e05\u695a\uff0c\u4e0d\u80fd\u4e3a\u4e86\u641e\u5e73\u8861\u803d\u8bef\u4e86\u7ecf\u6d4e\u5efa\u8bbe\uff0c\u6587\u7701\u957f\u8fd9\u6b21\u9000\u4e0b\u53bb\u4e4b\u540e\uff0c\u8ba9\u4e07\u5411\u524d\u5230\u7701\u91cc\uff0c\u4e5f\u662f\u987a\u7406\u6210\u7ae0\u7684\u4e8b\u60c5\u3002\u81f3\u4e8e\u4e07\u52c7\u7684\u4e8b\u60c5\uff0c\u5012\u662f\u4e00\u70b9\u90fd\u6ca1\u6709\u542c\u4eba\u63d0\u8d77\u8fc7\u2026\u2026\u201d</p><p>\u3000\u3000\u4e01\u5411\u5c71\u5f53\u5e74\u5c06\u4e07\u5411\u524d\u8d76\u5230\u5e02\u4eba\u5927\uff0c\u4e07\u52c7\u4e5f\u662f\u90a3\u4e00\u6b21\u63d0\u5347\u5230\u5e02\u7ecf\u8d38\u59d4\u4e3b\u4efb\u7684\u4f4d\u5b50\uff0c\u6309\u8bf4\u8bba\u8d44\u5386\u4e5f\u591f\u5f80\u4e0a\u63d0\u4e00\u7ea7\u3002\u5bf9\u4e8e\u4e07\u52c7\u6765\u8bf4\uff0c\u6700\u53ef\u80fd\u7684\u4ed5\u9014\u8f68\u8ff9\u5c31\u662f\u5230\u4e0b\u9762\u7684\u533a\u53bf\u653f\u5e9c\u505a\u4e00\u628a\u624b\uff0c\u5f53\u533a\u53bf\u515a\u59d4\u4e66\u8bb0\u90fd\u6709\u4e9b\u7834\u683c\uff0c\u8fd9\u4e5f\u662f\u5468\u5bcc\u660e\u4e0e\u4e07\u5411\u524d\u8054\u624b\u4e4b\u540e\u6700\u5927\u7684\u80fd\u8010\u4e86\u3002</p><p>\u3000\u3000\u4f46\u662f\u4e07\u52c7\u5bf9\u526f\u5e02\u957f\u65b9\u5b8f\u6e05\u8f7b\u89c6\u7684\u6001\u5ea6\u8bf4\u660e\u5176\u4e2d\u7684\u6c34\u6bd4\u8f83\u6df1\uff0c\u5f20\u606a\u66f4\u613f\u610f\u76f8\u4fe1\u4e07\u52c7\u8fd9\u6b21\u53ef\u80fd\u4f1a\u4e00\u6b65\u767b\u5929\uff0c\u4ece\u5916\u7ecf\u8d38\u59d4\u4e3b\u4efb\u7684\u4f4d\u7f6e\u76f4\u63a5\u63d0\u526f\u5e02\u957f\uff0c\u5510\u5b66\u8c26\u4ee3\u5e02\u957f\u4ee5\u6765\uff0c\u5e38\u52a1\u526f\u5e02\u957f\u7684\u4f4d\u7f6e\u8fd8\u7a7a\u7740\uff0c\u5468\u5bcc\u660e\u8981\u5236\u7ea6\u5510\u5b66\u8c26\uff0c\u6700\u597d\u7684\u65b9\u6848\u5c31\u662f\u5c06\u4e07\u52c7\u6367\u5230\u5e38\u52a1\u526f\u5e02\u957f\u7684\u4f4d\u7f6e\u4e0a\u3002</p><p>\u3000\u3000\u771f\u662f\u6309\u5f20\u606a\u7684\u8bbe\u60f3\u53d1\u5c55\uff0c\u4e07\u52c7\u53ef\u4ee5\u79f0\u5f97\u4e0a\u4e00\u6b65\u767b\u5929\uff0c\u7edd\u4e0d\u5bf9\u5468\u5bcc\u660e\u3001\u4e07\u5411\u524d\u4e24\u4eba\u7684\u80fd\u529b\u80fd\u505a\u5230\u7684\u3002</p><p>\u3000\u3000\u6b63\u56e0\u4e3a\u5982\u6b64\uff0c\u5f20\u606a\u624d\u66f4\u52a0\u5934\u75bc\u554a\uff0c\u8bf4\u660e\u7701\u91cc\u6709\u4eba\u4e0d\u5e0c\u671b\u6d77\u5dde\u5e02\u4e00\u4e8c\u628a\u624b\u90fd\u6295\u5230\u5f90\u5b66\u5e73\u7684\u95e8\u4e0b\u3002</p><p>\u3000\u3000\u5f90\u5b66\u5e73\u76b1\u7740\u7709\u5934\uff0c\u90a3\u4e2a\u4eba\u7684\u540d\u5b57\u5c31\u5728\u8111\u6d77\u91cc\u76d8\u65cb\u3002</p><p>\u3000\u3000\u5f20\u606a\u5c0f\u5fc3\u7ffc\u7ffc\u7684\u8bf4\uff1a\u201c\u662f\u4e0d\u662f\u7701\u91cc\u4e5f\u5e0c\u671b\u6d77\u5dde\u90a3\u8fb9\u80fd\u5e73\u8861\u4e00\u4e0b\u2026\u2026\u201d\u4e8b\u5230\u5982\u6b64\uff0c\u8fd9\u5f53\u7136\u662f\u5f20\u606a\u6700\u5e0c\u671b\u51fa\u73b0\u7684\u7ed3\u679c\uff0c\u4ed6\u53ef\u4e0d\u5e0c\u671b\u5468\u5bcc\u660e\u3001\u4e07\u5bb6\u7236\u4eb2\u80fd\u5728\u7701\u91cc\u771f\u6b63\u7684\u627e\u5230\u4e00\u4e2a\u5927\u9760\u5c71\u3002</p><p>\u3000\u3000\u5f90\u5b66\u5e73\u6c89\u541f\u7740\uff0c\u4ed6\u503e\u5411\u8d5e\u540c\u5f20\u606a\u8fd9\u79cd\u610f\u89c1\uff0c\u6709\u4e01\u5411\u5c71\u524d\u8f66\u4e4b\u9274\uff0c\u7701\u91cc\u8ddf\u4e0b\u9762\u7684\u5173\u7cfb\u6ce8\u5b9a\u8981\u758f\u8fdc\u4e00\u4e9b\uff0c\u5927\u6982\u662f\u771f\u4e0d\u5e0c\u671b\u6d77\u5dde\u4e00\u4e8c\u628a\u624b\u90fd\u8ddf\u81ea\u5df1\u8d70\u5f97\u592a\u8fd1\u3002</p><p>\u3000\u3000\u9762\u5bf9\u8fd9\u79cd\u5e73\u8861\u7684\u5c40\u9762\uff0c\u5f90\u5b66\u5e73\u4e5f\u662f\u675f\u624b\u65e0\u7b56\uff0c\u8bf4\u9053\uff1a\u201c\u9676\u664b\u4e66\u8bb0\u6700\u8fd1\u5e38\u8bf4\u8981\u7834\u683c\u63d0\u62d4\u515a\u653f\u5e72\u90e8\uff0c\u5927\u6982\u610f\u6709\u6240\u6307\u2026\u2026\u201d</p><p>\u3000\u3000\u89c1\u5f90\u5b66\u5e73\u90fd\u8981\u8ba4\u547d\uff0c\u5f20\u606a\u65e0\u5948\u7684\u7b11\u4e86\u7b11\uff0c\u603b\u4e0d\u80fd\u5c06\u6d77\u5dde\u5e02\u91cc\u7684\u6069\u6028\u8ddf\u5012\u82e6\u6c34\u4f3c\u7684\u503e\u8bc9\u51fa\u6765\uff0c\u5e02\u91cc\u6709\u5e02\u91cc\u7684\u52fe\u5fc3\u6597\u89d2\uff0c\u7701\u91cc\u4e5f\u4e0d\u4e00\u56e2\u548c\u6c14\uff0c\u53ea\u8981\u5f90\u5b66\u5e73\u80fd\u4e00\u5929\u5728\u4f4d\u4e0a\uff0c\u8fd8\u6015\u5468\u5bcc\u660e\u3001\u4e07\u52c7\u4ed6\u4eec\u6562\u626f\u7834\u8138\u3002\u53ea\u662f\u91cd\u65b0\u56de\u5230\u4e5d\u56db\u5e74\uff0c\u5012\u6ca1\u6709\u5b66\u4f1a\u592a\u59d4\u5c48\u81ea\u5df1\uff0c\u770b\u6765\u8fd8\u8981\u4ece\u5176\u4ed6\u5730\u65b9\u60f3\u60f3\u529e\u6cd5\uff0c\u4e0d\u7136\u5c31\u8981\u7b49\u5f90\u5b66\u5e73\u5728\u65b0\u7684\u4f4d\u5b50\u7ad9\u7a33\u811a\u8ddf\u4e4b\u540e\u624d\u80fd\u4ece\u957f\u8ba1\u8bae\u3002</p><p>\u3000\u3000\u5f90\u5b66\u5e73\u89c1\u5f20\u606a\u6101\u7709\u82e6\u8138\u7684\u6837\u5b50\uff0c\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u624d\u5341\u4e03\u5c81\uff0c\u4e0d\u8981\u5b66\u6211\u4eec\u8001\u5934\u5b50\u76b1\u7709\u5934\uff0c\u4eba\u5bb9\u6613\u8001\uff0c\u91d1\u56fd\u6d77\u5e38\u63d0\u8d77\u4f60\uff0c\u8fd9\u6b21\u6765\u4e0d\u53ca\u4e86\uff0c\u4e0b\u56de\u4f60\u5230\u4ed6\u90a3\u91cc\u53bb\u73a9\u73a9\u2026\u2026\u201d</p><p>\u3000\u3000\u91d1\u56fd\u6d77\u662f\u7701\u68c0\u5bdf\u9662\u526f\u68c0\u5bdf\u957f\uff0c\u662f\u4e01\u5411\u5c71\u6848\u3001\u65b0\u4e30\u96c6\u56e2\u56fd\u6709\u8d44\u4ea7\u6d41\u5931\u6848\u7684\u4e3b\u8981\u8d1f\u8d23\u4eba\uff0c\u4e5f\u5c31\u4e0a\u56de\u966a\u8bb8\u601d\u5230\u7701\u57ce\u6765\u63a5\u53d7\u897f\u971e\u533a\u68c0\u5bdf\u9662\u7684\u590d\u6838\uff0c\u4e0e\u91d1\u56fd\u6d77\u89c1\u8fc7\u4e00\u9762\uff0c\u4e4b\u540e\u4e00\u76f4\u6ca1\u6709\u9047\u5230\u8fc7\u3002\u5f20\u606a\u89c1\u5f90\u5b66\u5e73\u7a81\u7136\u63d0\u5230\u91d1\u56fd\u6d77\uff0c\u5f53\u7136\u4e0d\u4f1a\u5355\u7eaf\u8981\u4ed6\u53bb\u62dc\u8bbf\u91d1\u56fd\u6d77\uff0c\u7591\u60d1\u7684\u770b\u7740\u5f90\u5b66\u5e73\uff1a\u201c\u4eca\u5929\u90fd\u521d\u4e94\u4e86\uff0c\u4e0b\u56de\u6765\u7701\u57ce\u4e0d\u6653\u5f97\u8981\u7b49\u5230\u4ec0\u4e48\u65f6\u5019\uff0c\u603b\u4e0d\u62ff\u62dc\u5e74\u5f53\u501f\u53e3\u5427\uff1f\u201d</p><p>\u3000\u3000\u5f90\u5b66\u5e73\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u65b0\u4e00\u8f6e\u7684\u515a\u653f\u9886\u5bfc\u5e72\u90e8\u8c03\u6574\uff0c\u7ec4\u7ec7\u90e8\u95e8\u5b89\u6392\u66f4\u591a\u7684\u5e72\u90e8\u5230\u65b0\u7684\u5c97\u4f4d\u6302\u804c\u953b\u70bc\uff0c\u662f\u5386\u6765\u90fd\u6765\u7684\u4f20\u7edf\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u62cd\u62cd\u8111\u888b\uff0c\u604d\u7136\u5927\u609f\uff0c\u8fd9\u624d\u5f7b\u5e95\u660e\u767d\u662f\u7701\u91cc\u5728\u641e\u5e73\u8861\uff0c\u8ba9\u4e07\u52c7\u94bb\u4e86\u5927\u7a7a\u5b50\uff0c\u95ee\u5f90\u5b66\u5e73\uff1a\u201c\u662f\u4e0d\u662f\u91d1\u526f\u68c0\u5bdf\u957f\u81ea\u5df1\u63d0\u51fa\u5230\u6d77\u5dde\u53bb\u6302\u804c\u953b\u70bc\uff1f\u201d</p><p>\u3000\u3000\u5f90\u5b66\u5e73\u6447\u4e86\u6447\uff0c\u8bf4\u9053\uff1a\u201c\u91d1\u56fd\u6d77\u4f1a\u5728\u8fd9\u6279\u6302\u804c\u5e72\u90e8\u540d\u5355\u4e4b\u4e2d\uff0c\u4f46\u662f\u4e3b\u8981\u7684\u4e8b\u60c5\uff0c\u8fd8\u6ca1\u6709\u6b63\u5f0f\u62ff\u51fa\u6765\u8ba8\u8bba\u2026\u2026\u201d</p><p>\u3000\u3000\u91d1\u56fd\u6d77\u73b0\u5728\u5df2\u7ecf\u662f\u526f\u5385\u7ea7\uff0c\u5230\u5730\u65b9\u4e0a\u6302\u804c\u81f3\u5c11\u4e5f\u662f\u5e02\u59d4\u5e38\u59d4\uff0c\u8bf4\u4e0d\u5b9a\u80fd\u6302\u5e02\u59d4\u526f\u4e66\u8bb0\u804c\uff0c\u867d\u7136\u8bf4\u6302\u804c\u5e72\u90e8\u90fd\u6ca1\u6709\u5927\u7528\u573a\uff0c\u81f3\u5c11\u80fd\u5728\u5e02\u5e38\u59d4\u4f1a\u8bae\u4e0a\u80fd\u8bf4\u4e0a\u8bdd\u3002</p><p>\u3000\u3000\u5f20\u606a\u5f53\u7136\u671f\u5f85\u91d1\u56fd\u6d77\u80fd\u5230\u6d77\u5dde\u6302\u804c\uff0c\u4f46\u662f\u6709\u4e9b\u62c5\u5fe7\uff1a\u201c\u5e72\u90e8\u6302\u804c\u662f\u4e0d\u662f\u90fd\u8981\u7531\u7ec4\u7ec7\u90e8\u95e8\u7edf\u4e00\u90e8\u7f72\u7684\uff1f\u201d</p><p>\u3000\u3000\u201c\u7531\u7701\u7ec4\u7ec7\u90e8\u7edf\u4e00\u90e8\u7f72\u4e0d\u5047\uff0c\u4f46\u4e5f\u8981\u8003\u8651\u4e2a\u4eba\u7684\u610f\u613f\uff0c\u91d1\u56fd\u6d77\u53bb\u5e74\u5c0f\u534a\u5e74\u8017\u5728\u6d77\u5dde\uff0c\u5bf9\u6d77\u5dde\u7684\u60c5\u51b5\u5f88\u719f\u6089\uff0c\u5230\u6d77\u5dde\u6302\u804c\u5c31\u4e0d\u9700\u8981\u4ece\u5934\u5b66\u8d77\uff0c\u8fd9\u4ef6\u4e8b\u8fd8\u6ca1\u6709\u6b63\u5f0f\u62ff\u51fa\u6765\u8ba8\u8bba\u2026\u2026\u201d\u5f90\u6c34\u5e73\u8f7b\u8f7b\u7684\u6572\u4e86\u6572\u684c\u5b50\uff0c\u6709\u4e9b\u8bdd\u4e0d\u4fbf\u8bf4\u51fa\u53e3\u3002\u9676\u664b\u4e66\u8bb0\u80fd\u63d0\u51fa\u4e0d\u5177\u4e00\u683c\u63d0\u62d4\u4eba\u624d\u7684\u89c2\u70b9\uff0c\u90a3\u4e48\u66f4\u6709\u6548\u7684\u53d1\u6325\u515a\u653f\u5e72\u90e8\u7684\u4e2a\u4eba\u4e13\u957f\u4e5f\u662f\u503c\u5f97\u7ec4\u7ec7\u90e8\u95e8\u8003\u8651\u7684\u95ee\u9898\u3002\u8981\u4e0d\u662f\u60e0\u5c71\u53d1\u751f\u5927\u8f66\u7978\uff0c\u5f90\u5b66\u5e73\u8fd8\u6ca1\u6709\u8fd9\u4e48\u5927\u7684\u628a\u63e1\uff0c\u6b64\u65f6\u5012\u662f\u6709\u4fe1\u5fc3\u5c06\u91d1\u56fd\u6d77\u5b9a\u70b9\u6295\u5230\u6d77\u5dde\u53bb\u3002</p><p>\u3000\u3000\u60e0\u5c71\u5927\u8f66\u7978\u9020\u6210\u8fd9\u4e48\u60e8\u91cd\u7684\u540e\u679c\uff0c110\u7684\u6e0e\u804c\u884c\u4e3a\u7f6a\u4e0d\u5bb9\u6055\uff0c\u4e3b\u7ba1\u9886\u5bfc\u653f\u6cd5\u59d4\u526f\u4e66\u8bb0\u4e27\u8eab\u8f66\u7978\u5f53\u4e2d\uff0c\u4e5f\u4e0d\u662f\u8bf4\u60e0\u5c71\u653f\u6cd5\u59d4\u4e66\u8bb0\u5c31\u4e0d\u9700\u8981\u8d1f\u9886\u5bfc\u8d23\u4efb\u3002\u60e0\u5c71\u5e02\u8fd9\u6b21\u6709\u7a7a\u4f4d\u632a\u51fa\u6765\uff0c\u5b89\u6392\u597d\uff0c\u53ef\u4ee5\u8ba9\u91d1\u56fd\u6d77\u76f4\u63a5\u5230\u6d77\u5dde\u6302\u653f\u6cd5\u59d4\u4e66\u8bb0\u804c\uff0c\u8fd9\u624d\u662f\u6bd4\u8f83\u7406\u60f3\u7684\u5c40\u9762\u3002</p><p>\u3000\u3000\u8fd9\u4e48\u5b89\u6392\u5bf9\u5f90\u5b66\u5e73\u4e2a\u4eba\u6ca1\u6709\u592a\u5927\u7684\u597d\u5904\uff0c\u8fd8\u4e0d\u5982\u76f4\u63a5\u5c06\u91d1\u56fd\u6d77\u5b89\u6392\u5230\u60e0\u5c71\u53bb\uff0c\u5728\u4e1c\u6d77\u7701\uff0c\u60e0\u5c71\u7684\u5730\u4f4d\u53ef\u8981\u6bd4\u6d77\u5dde\u91cd\u8981\u591a\u4e86\uff0c\u4f46\u662f\u4ed6\u4e5f\u4e0d\u5e0c\u671b\u770b\u5230\u5510\u5b66\u8c26\u521a\u521a\u5728\u6d77\u5dde\u5f00\u521b\u7684\u65b0\u5c40\u9762\u4e0d\u81f3\u4e8e\u56e0\u4e3a\u6d77\u5dde\u5e02\u91cc\u67d0\u4e9b\u4eba\u7684\u5185\u8017\u800c\u592d\u6298\u3002</p><p>\u3000\u3000\u5bf9\u4e8e\u91d1\u56fd\u6d77\u80fd\u5230\u6d77\u5dde\uff0c\u5f20\u606a\u76f8\u5f53\u60ca\u559c\uff0c\u5728\u4e01\u5411\u5c71\u4e00\u6848\u7684\u5ba1\u7406\u4e2d\uff0c\u5927\u5bb6\u7684\u5173\u7cfb\u5c31\u975e\u5e38\u878d\u6d3d\u3002\u8bf4\u8d77\u6765\u91d1\u56fd\u6d77\u80fd\u5f97\u5230\u6302\u804c\u953b\u70bc\u7684\u673a\u4f1a\uff0c\u5728\u4e01\u5411\u5c71\u6848\u4e2d\u7acb\u529f\u4e5f\u662f\u4e3b\u8981\u7684\u56e0\u7d20\u4e4b\u4e00\uff0c\u517c\u4e4b\u4ed6\u662f\u5f90\u5b66\u5e73\u4e00\u624b\u63d0\u62d4\u4e0a\u6765\u7684\u4eba\uff0c\u8fd9\u4e48\u4e00\u6765\u81f3\u5c11\u4e0d\u6015\u4e07\u52c7\u8fdb\u5165\u5e38\u59d4\u5c0f\u4eba\u5f97\u5fd7\u592a\u56a3\u5f20\u3002</p></article>"}}}]);
