(window.webpackJsonp=window.webpackJsonp||[]).push([[252],{274:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u5341\u4e09\u8282 \u613f\u671b",content:"<article><h2>\u7b2c\u4e8c\u5341\u4e09\u8282 \u613f\u671b</h2><p>\u795e\u5802\u88ab\u7075\u5438\u602a\u5deb\u5996\u4fb5\u5165\uff0c\u83b7\u5f97\u611f\u5e94\u7684\u5e76\u4e0d\u4ec5\u4ec5\u662f\u743c\u6069\u7b49\u4eba\uff0c\u6240\u6709\u5b66\u9662\u91cc\u7684\u7267\u5e08\u90fd\u5bdf\u89c9\u5230\u4e86\u3002\u53ea\u662f\u5979\u4eec\u5f53\u65f6\u90fd\u5728\u5fd9\u4e8e\u548c\u53db\u4e71\u8005\u751f\u6b7b\u640f\u6740\uff0c\u538b\u6839\u65e0\u6687\u62bd\u8eab\u524d\u6765\u3002\u6700\u7ec8\u5f53\u5979\u4eec\u8d76\u5230\u7684\u65f6\u5019\uff0c\u6240\u770b\u89c1\u7684\u53ea\u662f\u6ee1\u5730\u5c38\u4f53\u3001\u7834\u788e\u7684\u51b0\u6676\uff0c\u4ee5\u53ca\u4e24\u4e2a\u6d3b\u7740\u7684\u4eba\uff1a\u743c\u6069\u548c\u7ef4\u5eb7\u5c3c\u4e9a\u3002</p><p>\u201c\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u8fe6\u6cd5\u5389\u58f0\u95ee\u3002</p><p>\u5979\u7684\u5fc3\u60c5\u5f88\u7cdf\u7cd5\uff0c\u8fd9\u662f\u7406\u6240\u5f53\u7136\u7684\u3002\u53db\u4e71\u8005\u7684\u7a81\u88ad\uff0c\u9020\u6210\u4e86\u796d\u53f8\u5b66\u9662\u8d85\u8fc7\u4e00\u534a\u7684\u5b66\u751f\u548c\u8fd1\u4e09\u5206\u4e4b\u4e00\u7684\u6559\u5b98\u6b7b\u4ea1\uff0c\u8fd9\u662f\u524d\u6240\u672a\u6709\u7684\u635f\u5931\u3002\u4e4b\u6240\u4ee5\u4f24\u4ea1\u5982\u6b64\u4e4b\u91cd\uff0c\u5f88\u5927\u7a0b\u5ea6\u4e0a\u4e5f\u662f\u56e0\u4e3a\u7267\u5e08\u4e27\u5931\u795e\u672f\uff0c\u65e0\u6cd5\u53ca\u65f6\u6cbb\u7597\u7684\u7f18\u6545\u3002\u4f5c\u4e3a\u6b21\u5e2d\u6559\u5b98\uff0c\u5b66\u9662\u7684\u5b9e\u9645\u9886\u8896\uff0c\u8fe6\u6cd5\u6beb\u65e0\u7591\u95ee\u5730\u8981\u4e3a\u6b64\u8d1f\u8d23\u3002\u800c\u4e14\u5728\u521a\u624d\u7684\u6218\u6597\u4e2d\uff0c\u5979\u7684\u5de6\u817f\u88ab\u780d\u4e86\u4e00\u5251\uff0c\u867d\u7136\u5df2\u7ecf\u670d\u7528\u4e86\u6cbb\u7597\u836f\u6c34\uff0c\u4f46\u4f9d\u65e7\u75bc\u75db\u96be\u5fcd\u3002\u67e5\u4e3d\u4e1d\u745e\u7684\u6b7b\u4ea1\u66f4\u52a0\u5267\u4e86\u5979\u7684\u4e0d\u5feb\uff0c\u8fd9\u610f\u5473\u7740\u81ea\u5df1\u4e27\u5931\u4e86\u4e00\u4f4d\u91cd\u8981\u7684\u652f\u6301\u8005\u2014\u2014\u65e0\u8bba\u5728\u5bb6\u65cf\u5185\u90e8\u8fd8\u662f\u5728\u5b66\u9662\u91cc\u3002</p><p>\u5982\u679c\u8bf4\u4ee5\u4e0a\u8fd9\u4e9b\u90fd\u8fd8\u4e0d\u662f\u5173\u952e\u7684\u8bdd\u2014\u2014\u90a3\u4e48\u6700\u4e25\u91cd\u7684\u95ee\u9898\u662f\uff1a\u6df1\u6e0a\u53ec\u5524\u4e4b\u86db\u88ab\u6467\u6bc1\u4e86\u3002</p><p>\u743c\u6069\u5e76\u4e0d\u6e05\u695a\u201c\u6df1\u6e0a\u53ec\u5524\u4e4b\u86db\u201d\u5230\u5e95\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u867d\u7136\u4ece\u65af\u5170\u666e\u53e3\u4e2d\u5f97\u77e5\u662f\u4e2a\u795e\u5668\uff0c\u4f46\u4e5f\u6ca1\u6709\u592a\u5728\u610f\u3002\u5f53\u7136\uff0c\u5f88\u91cd\u8981\u662f\u6beb\u65e0\u7591\u95ee\u7684\uff0c\u5426\u5219\u4e5f\u4e0d\u4f1a\u90d1\u91cd\u5176\u4e8b\u5730\u85cf\u5728\u796d\u53f8\u5b66\u9662\u7684\u795e\u5802\u91cc\u3002\u4f46\u773c\u770b\u7740\u7075\u5438\u602a\u5deb\u5996\u8f7b\u800c\u6613\u4e3e\u5730\u5c31\u5c06\u5b83\u4e00\u9524\u7838\u88c2\uff0c\u968f\u540e\u81ea\u7206\u6467\u6bc1\uff0c\u5b9e\u5728\u5f88\u96be\u8ba9\u4eba\u5bf9\u5b83\u4ea7\u751f\u591a\u5c11\u656c\u754f\u3002</p><p>\u4ed6\u4e0d\u77e5\u9053\uff0c\u8fd9\u662f\u74dc\u7406\u5fb7\u65af\u57ce\uff0c\u751a\u81f3\u6574\u4e2a\u7f57\u4e1d\u6559\u4f1a\u7684\u5723\u7269\u3002</p><p>\u5728\u7f57\u4e1d\u7684\u9634\u8c0b\u8d25\u9732\u540e\uff0c\u5979\u88ab\u7cbe\u7075\u4e3b\u795e\u67ef\u745e\u9686\u9010\u51fa\u4e86\u963f\u68b5\u591a\uff0c\u6253\u843d\u5230\u65e0\u5c3d\u6df1\u6e0a\u3002\u8718\u86db\u5973\u795e\u5360\u636e\u4e86\u6df1\u6e0a\u7684\u7b2c\u516d\u5341\u516d\u5c42\uff0c\u5c06\u90a3\u91cc\u6539\u9020\u6210\u4e86\u81ea\u5df1\u7684\u795e\u57df\uff0c\u5b9a\u540d\u4e3a\u201c\u6df1\u5751\u9b54\u7f51\u201d\u3002\u65e0\u5c3d\u6df1\u6e0a\u662f\u6076\u9b54\u7684\u5c45\u6240\uff0c\u7f57\u4e1d\u4f5c\u4e3a\u5916\u6765\u7684\u795e\u7947\uff0c\u81ea\u7136\u906d\u5230\u4e86\u654c\u89c6\u3002\u7956\u683c\u83ab\u4f0a\uff08\u771f\u83cc\u4e4b\u6bcd\uff0c\u6df1\u6e0a\u7b2c\u4e8c\u767e\u4e8c\u5341\u4e8c\u5c42\u9886\u4e3b\uff09\u5411\u7f57\u4e1d\u53d1\u8d77\u4e86\u8fdb\u653b\uff0c\u5979\u7684\u5927\u519b\u5982\u6f6e\u6c34\u822c\u901a\u8fc7\u4f20\u9001\u95e8\u6d8c\u5165\u6df1\u5751\u9b54\u7f51\u3002\u6839\u57fa\u672a\u56fa\u7684\u7f57\u4e1d\u96be\u4ee5\u62b5\u6321\uff0c\u4e8e\u662f\u5979\u521b\u9020\u4e86\u8fd9\u4ef6\u795e\u5668\u201c\u6df1\u6e0a\u53ec\u5524\u4e4b\u86db\u201d\u3002\u5b83\u770b\u8d77\u6765\u50cf\u662f\u4e00\u4e2a\u7cbe\u7f8e\u7684\u827a\u672f\u54c1\uff0c\u7531\u7eaf\u51c0\u7684\u7ea2\u5b9d\u77f3\u96d5\u6210\u8718\u86db\u7684\u5f62\u72b6\uff0c\u5b8c\u7f8e\u65e0\u6687\uff0c\u6829\u6829\u5982\u751f\u3002\u8718\u86db\u6709\u516d\u5341\u516d\u4e2a\u773c\u775b\uff0c\u7531\u94bb\u77f3\u5236\u6210\uff0c\u73af\u7ed5\u5728\u8eab\u4f53\u56db\u5468\uff0c\u6bcf\u4e2a\u773c\u775b\u91cc\u8574\u542b\u7740\u4e00\u53ea\u72e9\u9b54\u86db\u7684\u771f\u540d\u2014\u2014\u90a3\u662f\u6df1\u6e0a\u4e2d\u4e00\u79cd\u4f53\u578b\u5e9e\u5927\u3001\u6591\u9a73\u591a\u5f69\uff0c\u5f62\u6001\u7c7b\u4f3c\u8718\u86db\u7684\u6076\u9b54\uff0c\u51f6\u608d\u800c\u5f3a\u5927\uff0c\u4ee5\u5176\u4ed6\u6076\u9b54\u4e3a\u98df\u3002\u901a\u8fc7\u8fd9\u4ef6\u795e\u5668\uff0c\u7f57\u4e1d\u53ef\u4ee5\u968f\u65f6\u53ec\u5524\u6765\u516d\u5341\u516d\u53ea\u7cbe\u9510\u7684\u72e9\u9b54\u86db\u52a9\u6218\uff0c\u6700\u7ec8\u6253\u9000\u4e86\u7956\u683c\u83ab\u4f0a\u7684\u8fdb\u653b\uff0c\u7a33\u56fa\u4e86\u795e\u56fd\u3002</p><p>\u7f57\u4e1d\u7684\u5760\u843d\uff0c\u4f34\u968f\u7740\u5353\u5c14\u79bb\u5f00\u5730\u8868\uff0c\u9000\u907f\u5230\u5e7d\u6697\u5730\u57df\uff0c\u8fd9\u5728\u5386\u53f2\u4e0a\u79f0\u4e4b\u4e3a\u201c\u5927\u6c89\u964d\u201d\u3002\u591a\u5e74\u4ee5\u540e\uff0c\u74dc\u7406\u5fb7\u65af\u57ce\u5efa\u7acb\uff0c\u4f5c\u4e3a\u7b2c\u4e00\u4e2a\u5353\u5c14\u5efa\u7acb\u7684\u5730\u5e95\u57ce\u5e02\uff0c\u5b83\u53d7\u5230\u4e86\u795e\u540e\u7684\u5173\u6ce8\u548c\u795d\u798f\u3002\u7f57\u4e1d\u5c06\u6df1\u6e0a\u53ec\u5524\u4e4b\u86db\u8d50\u4e0b\u4f5c\u4e3a\u5956\u8d4f\uff0c\u81ea\u6b64\u4e16\u4ee3\u4fdd\u5b58\u5728\u796d\u53f8\u5b66\u9662\u7684\u795e\u5802\u4e4b\u4e2d\u3002</p><p>\u4e25\u683c\u6765\u8bf4\uff0c\u5b83\u7684\u529b\u91cf\u5b8c\u5168\u5728\u4e8e\u201c\u53ec\u5524\u201d\uff0c\u81ea\u8eab\u53ea\u662f\u4e00\u4e2a\u5a92\u4ecb\uff0c\u4f5c\u4e3a\u795e\u5668\u6709\u4e9b\u540d\u4e0d\u526f\u5b9e\u3002\u5bf9\u4e8e\u5353\u5c14\u4eec\u6765\u8bf4\uff0c\u5b83\u66f4\u91cd\u8981\u7684\u610f\u4e49\u662f\u201c\u5723\u7269\u201d\u3002\u6240\u6709\u540d\u5217\u6267\u653f\u8bae\u4f1a\u7684\u4e8c\u5341\u4e00\u4f4d\u4e3b\u6bcd\uff0c\u90fd\u4f1a\u81ea\u52a8\u548c\u6df1\u6e0a\u53ec\u5524\u4e4b\u86db\u5efa\u7acb\u7cbe\u795e\u8054\u7ed3\uff0c\u5e8f\u4f4d\u6700\u9ad8\u8005\u6709\u6743\u6fc0\u6d3b\u5b83\u7684\u529b\u91cf\uff0c\u62db\u6765\u72e9\u9b54\u86db\uff0c\u5176\u4f59\u4e3b\u6bcd\u4f9d\u6b21\u66ff\u8865\u3002\u552f\u4e00\u7684\u95ee\u9898\u662f\uff0c\u72e9\u9b54\u86db\u662f\u6076\u9b54\uff0c\u5b83\u4e0d\u4f1a\u542c\u4ece\u51e1\u4eba\u7684\u53f7\u4ee4\uff0c\u53ea\u4f1a\u8086\u610f\u6740\u622e\uff0c\u9664\u4e86\u6700\u8654\u8bda\u7684\u7f57\u4e1d\u4fe1\u5f92\uff08\u72e9\u9b54\u86db\u4f1a\u907f\u514d\u4f24\u5bb3\u5979\u4eec\uff09\uff0c\u5176\u4ed6\u4eba\u90fd\u4f1a\u88ab\u6740\u6b7b\uff0c\u5230\u65f6\u5019\u57ce\u5e02\u5fc5\u5b9a\u4f1a\u6bc1\u4e8e\u4e00\u65e6\u3002\u6240\u4ee5\u4e0d\u5230\u4e07\u4e0d\u5f97\u5df2\u7684\u6700\u540e\u5173\u5934\uff0c\u6df1\u6e0a\u53ec\u5524\u4e4b\u86db\u662f\u4e0d\u4f1a\u88ab\u52a8\u7528\u7684\u3002\u53cd\u8fc7\u6765\u8bf4\uff0c\u8fd9\u662f\u4e3b\u6bcd\u4eec\u624b\u4e2d\u6700\u540e\u7684\u5e95\u724c\u3002</p><p>\u73b0\u5728\u5b83\u88ab\u6467\u6bc1\u4e86\u3002</p><p>\u8fe6\u6cd5\u6012\u6c14\u51b2\u51b2\u5730\u770b\u7740\u743c\u6069\u548c\u7ef4\u5eb7\u5c3c\u4e9a\uff0c\u5979\u5bf9\u8fd9\u4e24\u4e2a\u4eba\u90fd\u4e0d\u4fe1\u4efb\u3002\u5b9e\u9645\u4e0a\uff0c\u5979\u975e\u5e38\u6000\u7591\u662f\u4ed6\u4eec\u8c0b\u6740\u4e86\u67e5\u4e3d\u4e1d\u745e\uff0c\u7136\u800c\u6ca1\u6709\u4efb\u4f55\u8bc1\u636e\u3002\u5982\u679c\u662f\u5728\u4eba\u7c7b\u4e16\u754c\uff0c\u6216\u8bb8\u8fd8\u53ef\u4ee5\u53ec\u5524\u4ea1\u9b42\u6765\u4f50\u8bc1\uff0c\u4f46\u5353\u5c14\u7684\u5b97\u6559\u89c2\u51b3\u5b9a\u4e86\u5979\u4eec\u4e0d\u6562\u6253\u6270\u901d\u53bb\u7267\u5e08\u7684\u5b89\u5b81\uff0c\u56e0\u4e3a\u90a3\u5df2\u7ecf\u5c5e\u4e8e\u795e\u540e\u3002\u201c\u5230\u5e95\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u5979\u518d\u6b21\u95ee\u3002</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u4e0a\u524d\u5c06\u4e8b\u60c5\u7ecf\u8fc7\u8bb2\u8ff0\u4e86\u4e00\u904d\uff0c\u5f53\u7136\u6700\u540e\u7684\u90e8\u5206\u505a\u4e86\u4e00\u70b9\u5c0f\u5c0f\u7684\u4fee\u6b63\uff1a\u67e5\u4e3d\u4e1d\u745e\u662f\u76f4\u63a5\u88ab\u65ad\u88c2\u7684\u51b0\u9b54\u624b\u81c2\u523a\u7a7f\u8179\u90e8\u800c\u6b7b\u3002\u56e0\u4e3a\u6240\u8bf4\u57fa\u672c\u90fd\u662f\u4e8b\u5b9e\uff0c\u53ea\u6709\u6700\u540e\u7ec6\u8282\u4e0a\u7565\u6709\u9690\u7792\uff0c\u8fe6\u6cd5\u4e5f\u5b8c\u5168\u542c\u4e0d\u51fa\u4ec0\u4e48\u7834\u7efd\u3002\u5b88\u62a4\u795e\u5802\uff0c\u539f\u672c\u5c31\u662f\u796d\u53f8\u5b66\u9662\u7684\u5206\u5185\u8d23\u4efb\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u5e76\u975e\u5b66\u9662\u6559\u5b98\uff0c\u743c\u6069\u66f4\u662f\u5916\u4eba\uff0c\u867d\u7136\u6700\u540e\u6ca1\u6709\u6210\u529f\u633d\u6551\u5723\u7269\uff0c\u4e5f\u602a\u7f6a\u4e0d\u5230\u4ed6\u4eec\u8eab\u4e0a\u3002</p><p>\u201c\u5965\u672f\u5b66\u9662\u548c\u683c\u6597\u5b66\u9662\u4e3a\u4ec0\u4e48\u6ca1\u6709\u6765\u63f4\u52a9\uff1f\u201d\u8fe6\u6cd5\u5389\u58f0\u53d1\u95ee\uff0c\u201c\u4ed6\u4eec\u8fd1\u5728\u54ab\u5c3a\uff01\u201d</p><p>\u201c\u4ed6\u4eec\u4e5f\u88ab\u653b\u51fb\u4e86\uff0c\u201d\u8ddf\u968f\u5728\u8fe6\u6cd5\u8eab\u540e\u7684\u4e00\u4e2a\u5b66\u751f\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u8bf4\uff0c\u201c\u9996\u5e2d\u5deb\u5e08\u5927\u4eba\u521a\u624d\u6d3e\u4f7f\u8005\u8fc7\u6765\u62a5\u544a\uff0c\u4ed6\u4eec\u906d\u5230\u4e86\u5927\u91cf\u7684\u6076\u9b54\u5165\u4fb5\uff0c\u65e0\u6687\u524d\u6765\u3002\u201d</p><p>\u201c\u6076\u9b54\uff1f\u201d\u8fe6\u6cd5\u6012\u6c14\u7a0d\u51cf\uff0c\u201c\u73b0\u5728\u60c5\u51b5\u600e\u4e48\u6837\uff1f\u201d</p><p>\u201c\u521a\u521a\u5e73\u5b9a\uff0c\u9996\u5e2d\u5deb\u5e08\u548c\u4ed6\u7684\u540c\u50da\u4eec\u628a\u6076\u9b54\u8d76\u56de\u4e86\u6df1\u6e0a\u3002\u201d</p><p>\u201c\u6076\u9b54\u600e\u4e48\u4f1a\u7a81\u7136\u5165\u4fb5\u3002\u201d\u4e00\u4e2a\u5e74\u957f\u7684\u6559\u5b98\u63d0\u51fa\u6000\u7591\u3002</p><p>\u8fe6\u6cd5\u77a5\u4e86\u5979\u4e00\u773c\uff0c\u201c\u663e\u7136\u662f\u90a3\u4e2a\u5deb\u5996\u5728\u6363\u9b3c\u3002\u201d</p><p>\u201c\u4f46\u90a3\u4e2a\u65af\u5170\u666e\u4e5f\u5df2\u7ecf\u6b7b\u4e86\uff0c\u201d\u5b66\u751f\u8c04\u5a9a\u5730\u8bf4\uff0c\u201c\u6211\u4eec\u83b7\u80dc\u4e86\u3002\u201d</p><p>\u8fe6\u6cd5\u53cd\u624b\u4e00\u62f3\u6253\u5728\u5979\u8138\u4e0a\uff0c\u5b66\u751f\u53e3\u9f3b\u9752\u80bf\uff0c\u8e09\u8dc4\u540e\u9000\u3002\u201c\u5b83\u662f\u4e2a\u5deb\u5996\uff01\u201d\u8fe6\u6cd5\u5389\u58f0\u8bf4\uff0c\u201c\u547d\u5323\u4e0d\u88ab\u6467\u6bc1\uff0c\u5b83\u5c31\u5f88\u5feb\u80fd\u590d\u6d3b\uff01\u201d</p><p>\u201c\u6b63\u662f\u5982\u6b64\u3002\u201d\u743c\u6069\u8bf4\u3002</p><p>\u8fe6\u6cd5\u6012\u89c6\u7740\u743c\u6069\uff0c\u8003\u8651\u5288\u5934\u62bd\u8fd9\u4e2a\u4eba\u7c7b\u4e00\u97ad\uff0c\u4f46\u6700\u7ec8\u8fd8\u662f\u5fcd\u4f4f\u4e86\u3002\u201c\u90a3\u4e2a\u5deb\u5996\u8eb2\u5728\u54ea\u91cc\uff1f\u201d</p><p>\u201c\u7eb3\u739b\u65af\u533a\u897f\u5357\u90e8\u7684\u4e00\u5ea7\u77f3\u5934\u623f\u5b50\uff0c\u8fdb\u53bb\u540e\u4f1a\u53d8\u6210\u57ce\u5821\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4f46\u6211\u5f88\u6000\u7591\u5b83\u4f1a\u628a\u547d\u5323\u653e\u5728\u90a3\u91cc\u3002\u201d</p><p>\u201c\u4f60\u7684\u6000\u7591\u5bf9\u6211\u6ca1\u6709\u610f\u4e49\uff0c\u201d\u8fe6\u6cd5\u51b7\u51b7\u8bf4\uff0c\u201c\u8bb0\u4f4f\u8eab\u4efd\uff0c\u7537\u6027\uff0c\u867d\u7136\u4f60\u662f\u4e2a\u5deb\u5e08\u3002\u201d</p><p>\u743c\u6069\u8eac\u8eab\u9053\u6b49\u3002</p><p>\u653b\u8fdb\u5b66\u9662\u7684\u53db\u4e71\u8005\u5df2\u7ecf\u5168\u90e8\u65a9\u6740\uff0c\u4e8b\u540e\u6536\u573a\u81ea\u7136\u6709\u6559\u5b98\u4eec\u53bb\u5b89\u6392\uff0c\u743c\u6069\u548c\u7ef4\u5eb7\u5c3c\u4e9a\u65e2\u6ca1\u8d44\u683c\u4e5f\u6ca1\u5174\u8da3\u53c2\u4e0e\u5176\u95f4\uff0c\u5148\u884c\u544a\u9000\u3002\u7ef4\u5eb7\u5c3c\u4e9a\u4e5f\u66fe\u7ecf\u5728\u8fd9\u91cc\u5b66\u4e60\u591a\u5e74\uff0c\u719f\u6089\u8def\u5f84\uff0c\u5e26\u9886\u743c\u6069\u5f84\u76f4\u51fa\u4e86\u5b66\u9662\u3002\u5f97\u5230\u4e0a\u57ce\u533a\u7684\u63f4\u52a9\uff0c\u6b64\u65f6\u4e0b\u57ce\u533a\u7684\u53db\u4e71\u4e5f\u5df2\u7ecf\u5927\u4f53\u5e73\u5b9a\uff0c\u8fdc\u5904\u7684\u6253\u6597\u55a7\u95f9\u58f0\u6e10\u6e10\u4f4e\u4e86\u4e0b\u53bb\uff0c\u4f30\u8ba1\u518d\u8fc7\u4e00\u4e2a\u65f6\u8fb0\uff0c\u74dc\u7406\u5fb7\u65af\u57ce\u4fbf\u4f1a\u518d\u5ea6\u6062\u590d\u5e73\u9759\u4e86\u3002</p><p>\u770b\u7740\u8fdc\u65b9\u7684\u706b\u5149\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\u3002\u201c\u81ea\u4ece\u5efa\u57ce\u4ee5\u6765\uff0c\u8fd8\u4ece\u6ca1\u53d1\u751f\u8fc7\u8fd9\u6837\u5927\u89c4\u6a21\u7684\u53db\u4e71\u5462\u3002\u201d</p><p>\u201c\u73b0\u5728\u4e0d\u662f\u5df2\u7ecf\u5e73\u606f\u4e86\u5417\uff0c\u201d\u743c\u6069\u6de1\u6de1\u8bf4\uff0c\u201c\u4f60\u4eec\u83b7\u80dc\u4e86\u3002\u201d</p><p>\u201c\u80dc\u5229\uff1f\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u51b7\u7b11\uff0c\u201c\u90a3\u4e2a\u5deb\u5996\u53ef\u8fd8\u6ca1\u6b7b\u3002\u201d</p><p>\u743c\u6069\u6084\u6084\u77a5\u4e86\u5979\u4e00\u773c\uff0c\u201c\u5c31\u7b97\u6ca1\u6b7b\uff0c\u4ed6\u4e00\u4eba\u53c8\u80fd\u6210\u4ec0\u4e48\u6c14\u5019\u3002\u8fd9\u6b21\u53db\u4e71\u8005\u5168\u519b\u8986\u6ca1\uff0c\u5c31\u7b97\u4ee5\u540e\u8fd8\u6709\u4eba\u5fc3\u6000\u5f02\u5fd7\uff0c\u4e5f\u8981\u591a\u8e0c\u8e87\u51e0\u5206\u4e86\u3002\u201d</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u6447\u5934\uff0c\u6ca1\u6709\u8bf4\u8bdd\u3002</p><p>\u743c\u6069\u539f\u672c\u51c6\u5907\u56de\u53bb\u4f11\u606f\uff0c\u4ed6\u4eca\u5929\u5fd9\u788c\u4e00\u65e5\uff0c\u8fed\u906d\u53d8\u6545\uff0c\u5df2\u7ecf\u9887\u4e3a\u75b2\u5026\u4e86\u3002\u6b64\u6b21\u53db\u4e71\uff0c\u867d\u7136\u58f0\u52bf\u714a\u8d6b\uff0c\u7834\u574f\u4e0d\u5c0f\uff0c\u4f46\u8ddd\u79bb\u4ed6\u7684\u76ee\u6807\u7ec8\u7a76\u8fd8\u662f\u5dee\u5f97\u8fdc\uff0c\u4e0b\u4e00\u6b65\u8be5\u5982\u4f55\u4e3e\u63aa\uff0c\u4e5f\u8981\u65e9\u505a\u6253\u7b97\u624d\u662f\u3002\u4f46\u7ef4\u5eb7\u5c3c\u4e9a\u4f3c\u4e4e\u6709\u4e9b\u5fc3\u4e0d\u5728\u7109\uff0c\u987a\u7740\u8857\u9053\u968f\u610f\u884c\u8d70\uff0c\u8ddd\u79bb\u83f2\u5c14\u4f26\u5b85\u9662\u8d8a\u6765\u8d8a\u8fdc\u3002\u743c\u6069\u89c1\u5979\u6709\u5fc3\u4e8b\uff0c\u4e5f\u4e0d\u4fbf\u591a\u95ee\uff0c\u53ea\u662f\u9759\u9759\u966a\u7740\u3002</p><p>\u201c\u6211\u6740\u4e86\u67e5\u4e3d\u4e1d\u745e\uff0c\u4f60\u5f88\u51fa\u4e4e\u610f\u6599\uff1f\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u7a81\u7136\u95ee\u3002</p><p>\u201c\u5514\uff0c\u5f53\u65f6\u662f\u5f88\u60ca\u8bb6\uff0c\u201d\u743c\u6069\u8001\u5b9e\u627f\u8ba4\uff0c\u201c\u4e0d\u8fc7\u53cd\u6b63\u548c\u6211\u65e0\u5173\u3002\u201d</p><p>\u5353\u5c14\u7684\u5634\u89d2\u5fae\u5fae\u7fd8\u8d77\uff0c\u9732\u51fa\u4e00\u4e1d\u8ba5\u8bbd\u7684\u7b11\u610f\u3002</p><p>\u201c\u77e5\u9053\u6211\u4eca\u5929\u7a7f\u7684\u8fd9\u4ef6\u6597\u7bf7\u7684\u6765\u5386\u5417\uff1f\u201d\u5979\u95ee\u3002</p><p>\u201c\u4f60\u8bf4\u53eb\u505a\u4f8d\u7236\u6597\u7bf7\u3002\u201d</p><p>\u201c\u4f8d\u7236\u7684\u610f\u601d\u4f60\u660e\u767d\u5427\u3002\u201d</p><p>\u201c\u77e5\u9053\u3002\u201d</p><p>\u6240\u8c13\u4f8d\u7236\uff0c\u4fbf\u662f\u4e3b\u6bcd\u7684\u914d\u5076\u3002\u9ed1\u6697\u7cbe\u7075\u662f\u5973\u6743\u793e\u4f1a\uff0c\u4f8d\u7236\u7684\u5730\u4f4d\u5e76\u4e0d\u5f88\u9ad8\uff0c\u8f83\u4e4b\u9996\u5e2d\u5deb\u5e08\u548c\u6b66\u6280\u957f\u90fd\u8981\u4f4e\u4e0a\u4e00\u7b49\uff0c\u66f4\u522b\u8bf4\u5973\u6027\u7267\u5e08\u3002\u6240\u8c13\u4ee5\u8272\u4f8d\u4eba\uff0c\u7ec8\u4e0d\u957f\u4e45\uff0c\u6b66\u6280\u957f\u548c\u9996\u5e2d\u5deb\u5e08\u53ea\u8981\u6709\u80fd\u529b\uff0c\u5c31\u80fd\u4fdd\u4f4f\u4f4d\u7f6e\u548c\u76f8\u5e94\u7684\u5f85\u9047\uff0c\u4f8d\u7236\u7684\u8eab\u4efd\uff0c\u5374\u5b8c\u5168\u53d6\u51b3\u4e8e\u4e3b\u6bcd\u7684\u597d\u6076\u3002\u4e00\u65e6\u4e3b\u6bcd\u53e6\u6709\u65b0\u6b22\uff0c\u524d\u4efb\u4f8d\u7236\u7684\u5730\u4f4d\u4e5f\u5c31\u4e00\u843d\u5343\u4e08\u3002</p><p>\u201c\u83f2\u5c14\u4f26\u5bb6\u65cf\u73b0\u5728\u6ca1\u6709\u4f8d\u7236\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u8bf4\uff0c\u201c\u524d\u4e00\u4efb\u4f8d\u7236\uff0c\u5c31\u662f\u6211\u7684\u7236\u4eb2\u3002\u8fd9\u4ef6\u6597\u7bf7\uff0c\u662f\u4e3b\u6bcd\u8d50\u4e88\u4ed6\u7684\u3002\u201d</p><p>\u201c\u5b83\u770b\u8d77\u6765\u4e0d\u9519\u3002\u201d</p><p>\u201c\u662f\u975e\u5e38\u4e0d\u9519\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u7ea0\u6b63\uff0c\u201c\u5b83\u662f\u7528\u7eaf\u7cb9\u7684\u86db\u4e1d\u7f16\u7ec7\u800c\u6210\uff0c\u5f97\u5230\u795e\u540e\u7684\u4eb2\u81ea\u795d\u798f\uff0c\u6bd4\u9501\u7532\u66f4\u575a\u97e7\uff0c\u80fd\u591f\u654c\u4f4f\u6700\u950b\u5229\u7684\u5200\u5251\u780d\u5288\u548c\u7bad\u77e2\u5c04\u51fb\u3002\u800c\u4e14\u5bf9\u9b54\u6cd5\u4e5f\u6709\u6781\u5f3a\u7684\u62b5\u5fa1\u6548\u679c\u3002\u201d\u5979\u77a5\u4e86\u743c\u6069\u4e00\u773c\uff0c\u201c\u5f53\u65e5\u5728\u4f0a\u5361\u6c99\u57ce\uff0c\u5982\u679c\u6211\u7a7f\u8fd9\u4ef6\u6597\u7bf7\uff0c\u4f60\u7684\u6cd5\u672f\u6839\u672c\u5c31\u4e0d\u4f1a\u5bf9\u6211\u6709\u7528\u3002\u201d</p><p>\u743c\u6069\u5fae\u5fae\u4e00\u7b11\uff0c\u5e76\u4e0d\u53cd\u9a73\u3002</p><p>\u201c\u9664\u6b64\u4e4b\u5916\uff0c\u5353\u5c14\u7a7f\u6234\u8fd9\u4ef6\u6597\u7bf7\uff0c\u4fbf\u4e0d\u4f1a\u754f\u60e7\u5f3a\u5149\uff0c\u5c31\u7b97\u5728\u5730\u8868\u4e16\u754c\u884c\u8d70\u4e5f\u6ca1\u6709\u95ee\u9898\u3002\u201d</p><p>\u743c\u6069\u5fae\u5fae\u6311\u4e86\u6311\u7709\u6bdb\uff0c\u5730\u5e95\u751f\u7269\u4e00\u4e2a\u666e\u904d\u7684\u5f31\u70b9\uff0c\u5c31\u662f\u5728\u9ed1\u6697\u4e2d\u751f\u6d3b\u5f97\u592a\u4e45\uff0c\u754f\u60e7\u5f3a\u5149\u3002\u773c\u9b54\u5982\u6b64\uff0c\u9ed1\u6697\u7cbe\u7075\u540c\u6837\u4e5f\u5982\u6b64\u3002\u9634\u9b42\u57ce\u6b63\u662f\u628a\u63e1\u8fd9\u4e00\u70b9\uff0c\u5236\u9020\u4e86\u5927\u91cf\u7684\u95ea\u5149\u7403\uff0c\u624d\u80fd\u5728\u6b64\u6b21\u8fdc\u5f81\u4e2d\u4e00\u8def\u987a\u5229\u63a8\u8fdb\u3002\u7ef4\u5eb7\u5c3c\u4e9a\u8bf4\u7a7f\u4e0a\u8fd9\u4ef6\u6597\u7bf7\u5c31\u4e0d\u6015\u5f3a\u5149\uff0c\u90a3\u5012\u786e\u5b9e\u662f\u5f88\u4e86\u4e0d\u8d77\u4e86\uff0c\u5f25\u8865\u4e86\u4e00\u5927\u5f31\u70b9\u3002</p><p>\u201c\u90a3\u4f60\u5e73\u65f6\u4e3a\u4ec0\u4e48\u4e0d\u7a7f\u5b83\u3002\u201d</p><p>\u201c\u56e0\u4e3a\u5b83\u6709\u4e2a\u81f4\u547d\u7684\u7f3a\u9677\u3002\u201d</p><p>\u201c\u4ec0\u4e48\u7f3a\u9677\uff1f\u201d</p><p>\u201c\u795e\u540e\u5728\u8d50\u4e88\u795d\u798f\u7684\u65f6\u5019\uff0c\u4e5f\u964d\u4e0b\u4e86\u4e00\u9053\u8bc5\u5492\u3002\u7a7f\u7740\u8fd9\u4ef6\u6597\u7bf7\uff0c\u4f1a\u66f4\u5bb9\u6613\u88ab\u5973\u6027\u7267\u5e08\u7684\u795e\u672f\u6240\u4f24\u5bb3\u5230\u3002\u201d</p><p>\u743c\u6069\u6014\u4e86\u6014\uff0c\u968f\u5373\u660e\u767d\u8fc7\u6765\u3002\u201c\u539f\u6765\u5982\u6b64\u3002\u201d\u4ed6\u6709\u4e9b\u8ba5\u8bbd\u5730\u8bf4\u3002</p><p>\u5353\u5c14\u7684\u89c4\u5219\uff0c\u51b3\u5b9a\u4e86\u7537\u6027\u548c\u5973\u6027\u4e4b\u95f4\u6709\u4e0d\u53ef\u903e\u8d8a\u7684\u9e3f\u6c9f\u3002\u4e00\u4e2a\u7537\u6027\u5c31\u7b97\u8c0b\u6740\u4e86\u4ed6\u7684\u5973\u6027\u4e0a\u53f8\uff0c\u4e5f\u4e0d\u53ef\u80fd\u53d6\u800c\u4ee3\u4e4b\uff0c\u6240\u4ee5\u5bf9\u4e8e\u5973\u7267\u5e08\u6765\u8bf4\uff0c\u5979\u4eec\u7684\u654c\u4eba\u4e0d\u662f\u7537\u6027\uff0c\u800c\u662f\u5176\u4ed6\u5973\u7267\u5e08\u3002\u4f8d\u7236\u6597\u7bf7\u6709\u8fd9\u6837\u7684\u7f3a\u9677\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u5e73\u65f6\u81ea\u7136\u4e0d\u4f1a\u4f7f\u7528\u4e86\u3002</p><p>\u201c\u7236\u4eb2\u662f\u5bb6\u65cf\u91cc\u7684\u6218\u58eb\uff0c\u4f46\u4ed6\u5e76\u4e0d\u5f88\u64c5\u957f\u683c\u6597\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u7ee7\u7eed\u8bf4\uff0c\u201c\u53cd\u800c\u662f\u5bf9\u5deb\u5e08\u7684\u5965\u672f\u975e\u5e38\u6709\u5174\u8da3\u3002\u4f46\u4ed6\u5728\u8fd9\u65b9\u9762\u2026\u2026\u55ef\uff0c\u5929\u8d4b\u4e5f\u4e0d\u592a\u9ad8\u3002\u6240\u4ee5\u5728\u5bb6\u65cf\u5185\u90e8\uff0c\u5c31\u6bd4\u8f83\u53d7\u5230\u8f7b\u89c6\u3002\u201d</p><p>\u5979\u7684\u8bdd\u8bf4\u5f97\u5f88\u9690\u6666\uff0c\u4f46\u743c\u6069\u660e\u767d\u5176\u4e2d\u7684\u610f\u601d\u3002\u5728\u5353\u5c14\u8fd9\u79cd\u5b9e\u529b\u81f3\u4e0a\u7684\u5f3a\u6743\u793e\u4f1a\u91cc\uff0c\u4e00\u4e2a\u65e2\u4e0d\u64c5\u957f\u683c\u6597\uff0c\u53c8\u4e0d\u7cbe\u901a\u5965\u672f\u7684\u7537\u6027\uff0c\u90a3\u5c31\u53ef\u4ee5\u5f52\u7c7b\u4e3a\u5e9f\u7269\u3002\u4e00\u4e2a\u5e9f\u7269\u56e0\u4e3a\u4e3b\u6bcd\u7684\u5ba0\u7231\u800c\u6210\u4e3a\u4f8d\u7236\uff0c\u722c\u4e0a\u7537\u6027\u5e8f\u5217\u7684\u7b2c\u4e09\u4f4d\u9636\uff0c\u4ec5\u6b21\u4e8e\u9996\u5e2d\u5deb\u5e08\u548c\u6b66\u6280\u957f\uff0c\u8fd9\u660e\u6446\u7740\u662f\u7b49\u7740\u88ab\u653b\u51fb\u3002\u5c31\u7b97\u6ca1\u6709\u4eba\u6562\u660e\u7740\u4e0b\u624b\uff0c\u4f46\u6697\u4e2d\u505a\u624b\u811a\uff0c\u4e5f\u662f\u53ef\u60f3\u800c\u77e5\u7684\u4e8b\u60c5\u3002</p><p>\u201c\u6240\u4ee5\u4e3b\u6bcd\u5c06\u8fd9\u4ef6\u6597\u7bf7\u8d50\u7ed9\u4ed6\uff1f\u4e3a\u4e86\u4fdd\u62a4\u4ed6\uff1f\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u201c\u542c\u8d77\u6765\u4ed6\u4eec\u611f\u60c5\u5f88\u597d\u3002\u201d\u743c\u6069\u6f2b\u4e0d\u7ecf\u5fc3\u5730\u8bf4\u3002</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u6115\u7136\u770b\u7740\u743c\u6069\uff0c\u8fc7\u4e86\u534a\u664c\u624d\u53cd\u5e94\u8fc7\u6765\uff0c\u201c\u611f\u60c5\uff1f\u201d\u5979\u4f4e\u58f0\u91cd\u590d\u7740\u8fd9\u4e2a\u6709\u4e9b\u964c\u751f\u7684\u8bcd\uff0c\u201c\u6216\u8bb8\u5427\u3002\u201d</p><p>\u201c\u540e\u6765\u5462\uff1f\u201d\u743c\u6069\u95ee\uff0c\u201c\u8fd9\u548c\u4f60\u6740\u67e5\u4e3d\u4e1d\u745e\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d</p><p>\u201c\u67e5\u4e3d\u4e1d\u745e\u662f\u6211\u7684\u59d0\u59d0\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u8bf4\uff0c\u201c\u540c\u6bcd\u540c\u7236\u7684\u59d0\u59d0\u3002\u201d</p><p>\u201c\u54e6\u3002\u201d</p><p>\u201c\u4f46\u540e\u6765\u662f\u5979\u6740\u4e86\u7236\u4eb2\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u56e0\u4e3a\u7236\u4eb2\u628a\u5979\u5f53\u4f5c\u5973\u513f\u5bf9\u5f85\u3002\u201d</p><p>\u201c\u5514\uff1f\u201d</p><p>\u8fd9\u4e2a\u7406\u7531\u592a\u8fc7\u532a\u5937\u6240\u601d\uff0c\u743c\u6069\u8111\u7b4b\u4e00\u65f6\u6ca1\u8f6c\u8fc7\u5f2f\u6765\uff0c\u51e0\u4e4e\u4ee5\u4e3a\u81ea\u5df1\u542c\u9519\u4e86\u3002\u201c\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u56e0\u4e3a\u7236\u4eb2\u628a\u5979\u5f53\u5973\u513f\u5bf9\u5f85\u3002\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u53c8\u91cd\u590d\u4e86\u4e00\u904d\u3002</p><p>\u201c\u8fd9\u7b97\u4ec0\u4e48\u539f\u56e0\uff1f\u201d</p><p>\u201c\u8fd9\u5c31\u662f\u539f\u56e0\u3002\u201d</p><p>\u743c\u6069\u6c89\u9ed8\u3002</p><p>\u201c\u4f60\u4eec\u4eba\u7c7b\u4e5f\u662f\u8fd9\u6837\u7684\u5417\uff1f\u7236\u4eb2\u5bf9\u5f85\u5b50\u5973\uff1f\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u95ee\u3002</p><p>\u201c\u5514\uff1f\u4e3a\u4ec0\u4e48\u8fd9\u4e48\u8bf4\uff1f\u201d</p><p>\u201c\u4e9a\u5f53\u65af\u53d4\u53d4\u548c\u7236\u4eb2\u5173\u7cfb\u5f88\u597d\uff0c\u4ed6\u66fe\u7ecf\u5bf9\u6211\u8bf4\uff0c\u7236\u4eb2\u867d\u7136\u770b\u4e0a\u53bb\u662f\u4e2a\u5353\u5c14\uff0c\u7075\u9b42\u5374\u66f4\u8fd1\u4f3c\u4eba\u7c7b\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u770b\u7740\u743c\u6069\uff0c\u201c\u6211\u5bf9\u4eba\u7c7b\u4e0d\u4e86\u89e3\uff0c\u4e5f\u4e0d\u77e5\u9053\u4ed6\u8bf4\u5f97\u5bf9\u4e0d\u5bf9\u3002\u662f\u8fd9\u6837\u5417\uff1f\u201d</p><p>\u201c\u8fd9\u4e2a\u2026\u2026\u542c\u4f60\u8fd9\u4e48\u8bf4\uff0c\u662f\u6709\u70b9\u50cf\u5427\u3002\u201d</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u70b9\u70b9\u5934\uff0c\u518d\u6ca1\u8bf4\u8bdd\u3002\u4e24\u4eba\u987a\u7740\u8857\u9053\u6162\u6162\u884c\u8d70\uff0c\u6e10\u6e10\u5230\u4e86\u57ce\u5e02\u7684\u897f\u5317\u89d2\uff0c\u8fd9\u91cc\u6ca1\u6709\u5bb6\u65cf\u5c45\u4f4f\uff0c\u662f\u7247\u8352\u5730\u3002\u743c\u6069\u6b63\u8003\u8651\u8981\u4e0d\u8981\u63d0\u8bae\u8fd4\u56de\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u7a81\u7136\u7ad9\u5b9a\uff0c\u62ac\u5934\u770b\u7740\u4ed6\u3002</p><p>\u201c\u5170\u5c3c\u65af\u7279\u5148\u751f\u3002\u201d</p><p>\u201c\u6211\u5728\u542c\uff0c\u5c0f\u59d0\u3002\u201d</p><p>\u201c\u6709\u4e00\u4ef6\u4e8b\u60c5\uff0c\u6211\u60f3\u8bf7\u6559\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u6162\u6162\u8bf4\uff0c\u201c\u4f60\u89c9\u5f97\uff0c\u6211\u80fd\u5f53\u4e0a\u4e3b\u6bcd\u5417\uff1f\u201d</p></article>"}}}]);
