(window.webpackJsonp=window.webpackJsonp||[]).push([[254],{276:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u5341\u4e94\u8282 \u5e55\u540e",content:"<article><h2>\u7b2c\u4e8c\u5341\u4e94\u8282 \u5e55\u540e</h2><p>\u6b63\u5982\u743c\u6069\u6240\u8bf4\uff0c\u4e00\u5207\u624d\u521a\u521a\u5f00\u59cb\u3002</p><p>\u53db\u4e71\u867d\u7136\u88ab\u9547\u538b\u4e0b\u53bb\uff0c\u4f46\u4f9d\u65e7\u5bf9\u74dc\u7406\u5fb7\u65af\u57ce\u9020\u6210\u4e86\u5de8\u5927\u7684\u4f24\u5bb3\uff0c\u4e0b\u57ce\u533a\u6709\u5341\u4e03\u4e2a\u5bb6\u65cf\u88ab\u66b4\u52a8\u7684\u5974\u96b6\u5b8c\u5168\u6467\u6bc1\uff0c\u6240\u6709\u7684\u5973\u6027\u8d35\u65cf\u90fd\u6b7b\u4e8e\u6218\u706b\uff0c\u8fd9\u610f\u5473\u7740\u8fd9\u4e9b\u5bb6\u65cf\u4ece\u6b64\u9664\u540d\uff0c\u518d\u4e5f\u4e0d\u5b58\u5728\u4e86\u3002\u5176\u4ed6\u5bb6\u65cf\u4e5f\u906d\u5230\u4e86\u91cd\u521b\uff0c\u4ed6\u4eec\u4e27\u5931\u4e86\u51e0\u4e4e\u5168\u90e8\u7684\u5974\u96b6\uff0c\u4ee5\u53ca\u76f8\u5f53\u591a\u7684\u7cbe\u9510\u6218\u58eb\u548c\u7267\u5e08\u3002\u5f88\u591a\u57ce\u533a\u88ab\u70c8\u706b\u70e7\u6210\u4e86\u4e00\u7247\u767d\u5730\uff0c\u4f8b\u5982\u5170\u83ab\u96f7\u65af\u548c\u7eb3\u739b\u65af\u2014\u2014\u4e3b\u6bcd\u4eec\u53ef\u4ee5\u4e0d\u5728\u4e4e\u540e\u8005\uff0c\u53cd\u6b63\u662f\u8d31\u6c11\u4eec\u7684\u6816\u8eab\u4e4b\u5730\uff0c\u4f46\u524d\u8005\u5374\u662f\u57ce\u4e2d\u6700\u5927\u7684\u5546\u4e1a\u533a\u3002</p><p>\u8d85\u8fc7\u4e09\u5206\u4e4b\u4e8c\u7684\u5546\u4eba\u88ab\u6740\u6b7b\uff0c\u4ed6\u4eec\u7528\u6765\u5f53\u4f5c\u4ed3\u5e93\u7684\u77f3\u7b0b\u88ab\u7c97\u66b4\u5730\u7838\u5f00\uff0c\u8d27\u7269\u88ab\u62a2\u52ab\u4e00\u7a7a\u2014\u2014\u90a3\u4e9b\u4e0d\u65b9\u4fbf\u5e26\u8d70\u7684\u57fa\u672c\u90fd\u88ab\u6bc1\u4e86\u3002\u5e73\u6c11\u4e5f\u4f24\u4ea1\u60e8\u91cd\uff0c\u5728\u8fd9\u79cd\u5927\u6df7\u4e71\u4e2d\u6ca1\u6709\u8c01\u80fd\u591f\u7f6e\u8eab\u4e8b\u5916\u3002\u552f\u4e00\u503c\u5f97\u5e86\u5e78\u7684\uff0c\u662f\u4e0a\u57ce\u533a\u6ca1\u6709\u53d7\u5230\u592a\u5927\u7684\u6ce2\u53ca\uff0c\u9664\u4e86\u796d\u53f8\u5b66\u9662\u906d\u5230\u4e86\u7a81\u7136\u88ad\u51fb\u3002\u53e6\u5916\u4e24\u5ea7\u5b66\u9662\u5728\u540c\u4e00\u65f6\u523b\u88ab\u4e0b\u5c42\u754c\u7684\u6076\u9b54\u5165\u4fb5\uff0c\u9020\u6210\u4e86\u5f88\u5927\u7684\u6df7\u4e71\uff0c\u4f46\u4f24\u4ea1\u5fae\u4e0d\u8db3\u9053\u3002</p><p>\u201c\u53cd\u6b63\u5b66\u9662\u6bcf\u5e74\u90fd\u6709\u4e00\u5b9a\u7684\u6b7b\u4ea1\u540d\u989d\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u8bc4\u4ef7\uff0c\u201c\u5c31\u5f53\u662f\u6559\u5b98\u4eec\u5728\u6fc0\u53d1\u5b66\u751f\u7075\u611f\u7684\u65f6\u5019\uff0c\u53c8\u4e0d\u5c0f\u5fc3\u4e0b\u624b\u91cd\u4e86\u70b9\u3002\u201d</p><p>\u4e8b\u5b9e\u4e0a\uff0c\u5982\u679c\u4e0d\u662f\u743c\u6069\u63d0\u524d\u62a5\u8baf\u7684\u8bdd\uff0c\u8fd9\u573a\u53db\u4e71\u539f\u672c\u5e94\u8be5\u9020\u6210\u66f4\u5927\u7684\u7834\u574f\uff0c\u4ed6\u4e5f\u56e0\u6b64\u5411\u83f2\u5c14\u4f26\u4e3b\u6bcd\u8bc1\u660e\u4e86\u81ea\u5df1\u7684\u80fd\u529b\u548c\u5fe0\u8bda\u3002\u7b2c\u4e8c\u5929\u4e0a\u5348\uff0c\u4e3b\u6bcd\u53ec\u5f00\u4e86\u5bb6\u65cf\u4f1a\u8bae\uff0c\u5927\u5927\u79f0\u8d5e\u4e86\u743c\u6069\u5728\u66b4\u4e71\u4e2d\u7684\u8868\u73b0\uff0c\u5ba3\u5e03\u4ed6\u4ece\u6b64\u6210\u4e3a\u83f2\u5c14\u4f26\u5bb6\u65cf\u7684\u4e00\u5458\uff0c\u5f53\u7136\u8fd8\u6709\u7269\u8d28\u5956\u8d4f\uff1a\u4e00\u5ea7\u5355\u72ec\u7684\u4e2d\u7a7a\u77f3\u67f1\uff0c\u4f5c\u4e3a\u4f4f\u5904\u548c\u7814\u7a76\u6cd5\u672f\u7684\u573a\u6240\u3002</p><p>\u5bf9\u4e8e\u4e00\u4e2a\u65b0\u6210\u5458\u6765\u8bf4\uff0c\u8fd9\u662f\u975e\u5e38\u96be\u5f97\u7684\u5f85\u9047\u3002</p><p>\u5e7d\u6697\u5730\u57df\u91cc\u904d\u5e03\u7740\u4ece\u9876\u4e0a\u5012\u60ac\u4e0b\u6765\u7684\u949f\u4e73\u77f3\uff0c\u4ee5\u53ca\u4ece\u5730\u9762\u4e0a\u751f\u957f\u51fa\u6765\u7684\u77f3\u7b0b\uff0c\u5b83\u4eec\u88ab\u5f00\u51ff\u6210\u4e2d\u7a7a\uff0c\u4f5c\u4e3a\u5353\u5c14\u4eec\u7684\u4f4f\u623f\u2014\u2014\u524d\u8005\u5c5e\u4e8e\u7267\u5e08\uff0c\u540e\u8005\u5219\u5c45\u4f4f\u7740\u6218\u58eb\u548c\u5730\u4f4d\u66f4\u4f4e\u7684\u5974\u96b6\u3002\u5728\u6781\u5de7\u5408\u7684\u60c5\u51b5\u4e0b\uff0c\u949f\u4e73\u77f3\u548c\u77f3\u7b0b\u4f1a\u751f\u957f\u8054\u7ed3\u5728\u4e00\u8d77\uff0c\u5f62\u6210\u77f3\u67f1\uff0c\u8fd9\u79cd\u7279\u4f8b\u4fbf\u7528\u4e8e\u5b89\u6392\u5deb\u5e08\u3002\u83f2\u5c14\u4f26\u5bb6\u65cf\u6709\u4e94\u540d\u5deb\u5e08\uff0c\u5728\u743c\u6069\u4e4b\u524d\uff0c\u53ea\u6709\u9996\u5e2d\u5deb\u5e08\u4e9a\u5f53\u65af\u624d\u6709\u72ec\u4eab\u4e00\u5ea7\u77f3\u67f1\u7684\u5f85\u9047\uff0c\u5176\u4ed6\u4eba\u90fd\u53ea\u80fd\u62e5\u6709\u4e00\u4e2a\u623f\u95f4\u3002</p><p>\u9664\u6b64\u4e4b\u5916\uff0c\u743c\u6069\u83b7\u5f97\u4e86\u4e00\u67c4\u9576\u6ee1\u7ea2\u5b9d\u77f3\u7684\u5315\u9996\uff0c\u63e1\u67c4\u662f\u4e00\u53ea\u4f38\u51fa\u516b\u6761\u817f\u7684\u8718\u86db\uff0c\u4e0a\u9762\u5e03\u6ee1\u4e86\u5012\u52fe\uff0c\u516b\u6761\u817f\u90fd\u4ee5\u540c\u6837\u7684\u89d2\u5ea6\u5f80\u4e0b\u5f2f\u66f2\uff0c\u7ec4\u6210\u950b\u5229\u7684\u5200\u5203\u3002\u5b83\u7684\u9020\u578b\u72ec\u7279\uff0c\u88c5\u9970\u534e\u4e3d\uff0c\u7528\u6765\u6740\u4eba\u4f30\u8ba1\u4e5f\u5f88\u8d81\u624b\uff0c\u4f46\u4e0e\u5176\u8bf4\u662f\u4e00\u4ef6\u6b66\u5668\uff0c\u4e0d\u5982\u8bf4\u662f\u8eab\u4efd\u548c\u5730\u4f4d\u7684\u8c61\u5f81\u66f4\u52a0\u786e\u5207\u3002</p><p>\u5b83\u88ab\u79f0\u4e3a\u8718\u86db\u4e4b\u5315\uff0c\u56e0\u4e3a\u662f\u5728\u796d\u7940\u86db\u540e\u7684\u5178\u793c\u4e0a\u4f7f\u7528\uff0c\u53c8\u88ab\u79f0\u4e3a\u796d\u5200\u3002\u552f\u6709\u5bf9\u5bb6\u65cf\u505a\u51fa\u7279\u522b\u8d21\u732e\u7684\u4eba\uff0c\u624d\u4f1a\u88ab\u4e3b\u6bcd\u5956\u8d4f\u8718\u86db\u4e4b\u5315\uff0c\u5982\u679c\u662f\u5e73\u6c11\u62e5\u6709\u5b83\uff0c\u90a3\u4e48\u5c31\u610f\u5473\u7740\u83b7\u5f97\u4e86\u76f8\u5f53\u4e8e\u8d35\u65cf\u7684\u8eab\u4efd\u3002</p><p>\u5bf9\u4e8e\u5353\u5c14\u6765\u8bf4\uff0c\u8fd9\u662f\u96be\u5f97\u7684\u8363\u8000\uff0c\u4e0d\u8fc7\u743c\u6069\u5c31\u61d2\u5f97\u5728\u4e4e\u4e86\uff0c\u4ed6\u53c8\u538b\u6839\u6ca1\u6253\u7b97\u771f\u5728\u8fd9\u5ea7\u57ce\u5e02\u91cc\u5b9a\u5c45\u3002\u5deb\u5e08\u7684\u6ce8\u610f\u529b\uff0c\u6b64\u523b\u6b63\u505c\u7559\u5728\u53c2\u52a0\u8fd9\u6b21\u4f1a\u8bae\u7684\u5176\u4ed6\u4eba\u8eab\u4e0a\u3002</p><p>\u56e0\u4e3a\u662f\u6b63\u5f0f\u7684\u5bb6\u65cf\u4f1a\u8bae\uff0c\u6240\u4ee5\u53ea\u6709\u4e3b\u6bcd\u62e5\u6709\u5ea7\u4f4d\uff0c\u5176\u4ed6\u4eba\u5168\u90fd\u5f97\u7ad9\u7740\uff0c\u8fd9\u662f\u89c4\u77e9\u3002\u83f2\u5c14\u4f26\u4e3b\u6bcd\u5750\u5728\u6709\u516b\u7ea7\u53f0\u9636\u7684\u738b\u5ea7\u4e0a\uff0c\u4fef\u89c6\u7740\u5979\u7684\u81e3\u6c11\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u548c\u5979\u7684\u4e24\u4e2a\u59d0\u59d0\u7ad9\u5728\u8f83\u4f4e\u7684\u53f0\u9636\u4e0a\u3002\u7537\u6027\u5219\u4e0d\u5141\u8bb8\u8e0f\u4e0a\u53f0\u9636\uff0c\u4ed6\u4eec\u7ad9\u5728\u4e0b\u9762\u3002\u743c\u6069\u7684\u6b63\u5bf9\u9762\u662f\u82ae\u68ee\u7279\uff0c\u83f2\u5c14\u4f26\u5bb6\u65cf\u7684\u6b66\u6280\u957f\uff0c\u4e00\u4e2a\u9ad8\u5927\u5065\u58ee\u7684\u6218\u58eb\uff0c\u7a7f\u7740\u77ee\u4eba\u6253\u9020\u7684\u5168\u8eab\u94e0\u7532\uff0c\u8fd9\u548c\u5176\u4ed6\u5353\u5c14\u4e0d\u540c\u3002\u4ed6\u662f\u83f2\u5c14\u4f26\u5bb6\u65cf\u7684\u65c1\u652f\uff0c\u5982\u679c\u8ba1\u7b97\u8d77\u6765\uff0c\u5e94\u8be5\u662f\u7ef4\u5eb7\u5c3c\u4e9a\u7684\u5802\u5144\u3002</p><p>\u743c\u6069\u5411\u82ae\u68ee\u7279\u5fae\u5fae\u70b9\u5934\uff0c\u6253\u4e86\u4e2a\u62db\u547c\uff0c\u7136\u540e\u5c06\u76ee\u5149\u8f6c\u5411\u6b66\u6280\u957f\u8eab\u4fa7\u3002\u90a3\u91cc\u7ad9\u7740\u4e00\u4e2a\u5deb\u5e08\uff0c\u4ed6\u770b\u8d77\u6765\u5f88\u865a\u5f31\uff0c\u8eab\u6750\u7626\u524a\uff0c\u7a7f\u7740\u4e00\u4ef6\u9ed1\u8272\u7684\u3001\u7ed8\u6709\u7ea2\u8272\u86db\u7f51\u56fe\u6848\u7684\u5deb\u5e08\u957f\u888d\uff0c\u9732\u51fa\u8896\u53e3\u7684\u624b\u6307\u7ea4\u7ec6\u4fee\u957f\uff0c\u76ae\u80a4\u5149\u6ed1\uff0c\u4f46\u5374\u603b\u662f\u5728\u65e0\u610f\u8bc6\u5730\u8f7b\u5fae\u98a4\u6296\uff0c\u4ee4\u4eba\u6000\u7591\u4ed6\u662f\u4e0d\u662f\u60a3\u6709\u795e\u7ecf\u65b9\u9762\u7684\u75be\u75c5\u3002\u5353\u5c14\u7684\u5e74\u9f84\u5f88\u96be\u4ece\u76f8\u8c8c\u4e0a\u770b\u51fa\u6765\uff0c\u4f46\u743c\u6069\u77e5\u9053\u4ed6\u81f3\u5c11\u5df2\u7ecf\u6d3b\u4e86\u8d85\u8fc7\u4e94\u767e\u5c81\uff0c\u662f\u4e2a\u4e0d\u6298\u4e0d\u6263\u7684\u8001\u4eba\u4e86\u3002</p><p>\u4ed6\u662f\u4e9a\u5f53\u65af\uff0c\u5bb6\u65cf\u9996\u5e2d\u5deb\u5e08\uff0c\u4e3b\u6bcd\u7684\u5f1f\u5f1f\uff0c\u5f53\u5e74\u5c31\u662f\u5728\u4ed6\u7684\u652f\u6301\u4e0b\uff0c\u8eab\u4e3a\u6b21\u5973\u7684\u7f57\u5170\xb7\u83f2\u5c14\u4f26\u4ee5\u96f7\u9706\u624b\u6bb5\u94f2\u9664\u4e86\u81ea\u5df1\u6bcd\u4eb2\u548c\u59d0\u59d0\uff0c\u5750\u4e0a\u4e86\u73b0\u5728\u7684\u4f4d\u7f6e\u3002\u4e9a\u5f53\u65af\u4e0d\u4f46\u662f\u83f2\u5c14\u4f26\u5bb6\u65cf\u7684\u9996\u5e2d\u5deb\u5e08\uff0c\u540c\u65f6\u8fd8\u662f\u5deb\u5e08\u5b66\u9662\u7684\u9886\u8896\uff0c\u4ed6\u7684\u5de6\u624b\u4e0a\u6234\u7740\u4e00\u4e2a\u94f6\u7070\u8272\u7684\u91d1\u5c5e\u62a4\u8155\uff0c\u90a3\u662f\u5b66\u9662\u5bfc\u5e08\u7684\u8eab\u4efd\u6807\u5fd7\u3002</p><p>\u743c\u6069\u6ce8\u610f\u5230\u4e00\u4e2a\u6709\u8da3\u7684\u7ec6\u8282\uff1a\u4e9a\u5f53\u65af\u7684\u957f\u888d\u4e0a\uff0c\u6302\u7740\u4e00\u679a\u5706\u5f62\u7684\u9ed1\u8272\u5fbd\u7ae0\uff0c\u5f53\u4e2d\u662f\u4e00\u53ea\u4ece\u86db\u7f51\u4e0a\u5012\u5782\u4e0b\u6765\u7684\u8718\u86db\uff0c\u6709\u7740\u5973\u6027\u5353\u5c14\u7684\u9762\u5bb9\u3002\u5b83\u5e94\u8be5\u662f\u7528\u67d0\u79cd\u4e0d\u900f\u660e\u7684\u5b9d\u77f3\u5236\u6210\uff0c\u975e\u5e38\u7cbe\u81f4\uff0c\u800c\u4e14\u663e\u7136\u662f\u9b54\u6cd5\u7269\u54c1\uff0c\u5728\u743c\u6069\u7684\u5965\u672f\u89c6\u89c9\u91cc\uff0c\u5b83\u6b63\u70c1\u70c1\u5730\u653e\u7740\u523a\u773c\u7684\u6d45\u84dd\u5149\u8292\u3002</p><p>\u4f5c\u4e3a\u9996\u5e2d\u5deb\u5e08\uff0c\u4e9a\u5f53\u65af\u5728\u8eab\u4e0a\u4f69\u6234\u4e00\u4e9b\u9b54\u6cd5\u9970\u54c1\u4e1d\u6beb\u4e0d\u8db3\u4e3a\u5947\uff0c\u7136\u800c\u95ee\u9898\u5728\u4e8e\uff1a\u8fd9\u662f\u7f57\u4e1d\u7684\u5723\u5fbd\u3002</p><p>\u4e9a\u5f53\u65af\u80af\u5b9a\u662f\u7f57\u4e1d\u7684\u4fe1\u5f92\uff0c\u8fd9\u6beb\u65e0\u7591\u95ee\uff0c\u56e0\u4e3a\u5168\u74dc\u7406\u5fb7\u65af\u57ce\u7684\u5353\u5c14\u90fd\u662f\uff0c\u9664\u4e86\u90a3\u4e9b\u88ab\u53d8\u6210\u86db\u5316\u7cbe\u7075\u7684\u6d41\u653e\u8005\u548c\u5df2\u7ecf\u6b7b\u6389\u7684\u53db\u4e71\u8005\u3002\u4f46\u9664\u4e86\u7267\u5e08\uff0c\u666e\u901a\u7684\u4fe1\u5f92\u662f\u5f88\u5c11\u4f1a\u5728\u8eab\u4e0a\u4f69\u6234\u5723\u5fbd\u7684\uff0c\u5deb\u5e08\u8fd9\u4e48\u505a\u7684\u5c24\u5176\u7f55\u89c1\u3002\u5168\u9634\u9b42\u57ce\u7684\u5deb\u5e08\u90fd\u662f\u838e\u5c14\u7684\u4fe1\u5f92\uff0c\u4f46\u743c\u6069\u4e5f\u4e0d\u66fe\u89c1\u54ea\u4e2a\u5deb\u5e08\u628a\u838e\u5c14\u5723\u5fbd\u6302\u5728\u8eab\u4e0a\u3002</p><p>\u8fd9\u610f\u5473\u7740\u4e9a\u5f53\u65af\u662f\u7f57\u4e1d\u7684\u8654\u8bda\u4fe1\u5f92\uff1f</p><p>\u743c\u6069\u6697\u4e2d\u6447\u5934\uff0c\u4e0d\u76f8\u4fe1\u8fd9\u4e2a\u5224\u65ad\uff0c\u76f4\u89c9\u544a\u8bc9\u4ed6\u4e9a\u5f53\u65af\u7684\u8fd9\u679a\u5723\u5fbd\u4e00\u5b9a\u6709\u4ec0\u4e48\u79d8\u5bc6\uff0c\u4f46\u4e00\u65f6\u4e5f\u731c\u4e0d\u51fa\u3002\u601d\u5fd6\u7247\u523b\uff0c\u4ed6\u628a\u6ce8\u610f\u529b\u91cd\u65b0\u8f6c\u56de\u573a\u5185\u3002\u83f2\u5c14\u4f26\u4e3b\u6bcd\u6b63\u5728\u53d1\u8868\u70ed\u60c5\u6d0b\u6ea2\u7684\u6f14\u8bf4\uff0c\u5938\u5956\u5404\u4f4d\u5728\u8fd9\u6b21\u5e73\u5b9a\u53db\u4e71\u4e2d\u7684\u51fa\u8272\u8868\u73b0\uff0c\u6240\u6709\u4eba\u90fd\u5728\u606d\u656c\u5730\u8046\u542c\u7740\u3002\u743c\u6069\u73af\u89c6\u56db\u5468\uff0c\u7136\u540e\u53d1\u73b0\u5c11\u4e86\u4e00\u4e2a\u7406\u5e94\u5728\u573a\u7684\u4eba\u3002</p><p>\u201c\u8fe6\u6cd5\u53bb\u54ea\u4e86\uff1f\u201d\u4ed6\u7591\u60d1\u7740\u3002</p><p>\u7136\u540e\u4ed6\u770b\u89c1\u5bb6\u65cf\u957f\u5973\u5feb\u6b65\u4ece\u95e8\u53e3\u8d70\u4e86\u8fdb\u6765\u3002</p><p>\u203b\u203b\u203b</p><p>\u8fe6\u6cd5\u7684\u8138\u8272\u5e73\u9759\uff0c\u4f46\u6709\u4e00\u4e1d\u63a9\u76d6\u4e0d\u4f4f\u7684\u5f97\u610f\u3002\u5979\u5feb\u6b65\u8d70\u8fdb\u4f1a\u8bae\u5ba4\uff0c\u6765\u5230\u738b\u5ea7\u4e0b\u4e00\u7ea7\u7684\u53f0\u9636\u4e0a\uff0c\u606d\u8c28\u5730\u5f2f\u4e0b\u8170\u3002</p><p>\u201c\u5c0a\u656c\u7684\u4e3b\u6bcd\u5927\u4eba\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u6211\u627e\u5230\u4e86\u8fd9\u6b21\u53db\u4e71\u7684\u5e55\u540e\u4e3b\u4f7f\u8005\u3002\u201d</p><p>\u201c\u54e6\uff1f\u201d</p><p>\u83f2\u5c14\u4f26\u4e3b\u6bcd\u6709\u4e9b\u5938\u5f20\u5730\u626c\u8d77\u7709\u6bdb\uff0c\u62ac\u773c\u770b\u7740\u81ea\u5df1\u7684\u957f\u5973\uff0c\u201c\u662f\u8c01\uff1f\u201d</p><p>\u201c\u745e\u8d39\u5fb7\u5bb6\u65cf\uff0c\u201d\u8fe6\u6cd5\u7acb\u523b\u56de\u7b54\uff0c\u201c\u6211\u6709\u8bc1\u636e\u3002\u201d</p><p>\u6628\u5929\u5728\u5e73\u606f\u796d\u53f8\u5b66\u9662\u7684\u66b4\u4e71\u4e4b\u540e\uff0c\u8fe6\u6cd5\u4eb2\u81ea\u5e26\u4eba\u53bb\u7eb3\u739b\u65af\u533a\u641c\u6355\u53db\u4e71\u8005\u7684\u4f59\u515a\u2014\u2014\u5f53\u7136\u66f4\u91cd\u8981\u7684\u662f\u627e\u5230\u5deb\u5996\u7684\u547d\u5323\u3002\u5979\u5931\u8d25\u4e86\uff0c\u8fd9\u5728\u610f\u6599\u4e4b\u4e2d\uff0c\u6bcf\u4e00\u4e2a\u5deb\u5996\u90fd\u4f1a\u628a\u81ea\u5df1\u7684\u547d\u5323\u85cf\u5f97\u4e25\u4e25\u5b9e\u5b9e\uff08\u56e0\u4e3a\u5982\u679c\u5b83\u6bc1\u4e86\uff0c\u5deb\u5996\u4e5f\u5c31\u5b8c\u86cb\u4e86\uff09\u3002\u82e5\u5728\u4ee5\u524d\uff0c\u5979\u6216\u8bb8\u8fd8\u53ef\u4ee5\u501f\u52a9\u795e\u672f\u6765\u641c\u7d22\uff0c\u4f46\u73b0\u5728\u8fd9\u4e00\u5207\u90fd\u6210\u4e86\u7a7a\u8c08\u3002</p><p>\u867d\u7136\u5982\u6b64\uff0c\u8fe6\u6cd5\u5374\u6709\u610f\u5916\u7684\u6536\u83b7\u3002\u8fdb\u653b\u796d\u53f8\u5b66\u9662\u7684\u53db\u4e71\u8005\u7edd\u5927\u90e8\u4efd\u90fd\u88ab\u5f53\u573a\u65a9\u6740\u4e86\uff0c\u53ea\u5269\u4e0b\u51e0\u4e2a\u6d3b\u53e3\u3002\u4ed6\u4eec\u90fd\u4e2d\u4e86\u65af\u5170\u666e\u7684\u9b54\u6cd5\uff0c\u5fc3\u667a\u8ff7\u5931\uff0c\u8fe6\u6cd5\u5ba1\u8baf\u4e86\u4e00\u591c\uff0c\u8d39\u4e86\u5f88\u5927\u7684\u529b\u6c14\uff0c\u6d6a\u8d39\u4e86\u597d\u51e0\u5f20\u5b9d\u8d35\u7684\u5377\u8f74\uff0c\u7ec8\u4e8e\u6253\u7834\u4e86\u5deb\u5996\u7684\u9b54\u6cd5\u684e\u688f\uff0c\u4ece\u4fd8\u864f\u4eec\u53e3\u4e2d\u83b7\u5f97\u4e86\u4e00\u4e9b\u6709\u4ef7\u503c\u7684\u6d88\u606f\u3002</p><p>\u201c\u745e\u8d39\u5fb7\u5bb6\u65cf\u662f\u8fd9\u6b21\u53db\u4e71\u7684\u5e55\u540e\u652f\u6301\u8005\u3002\u201d</p><p>\u636e\u4fd8\u864f\u8bf4\uff0c\u4ed6\u66fe\u7ecf\u591a\u6b21\u770b\u89c1\u745e\u8d39\u5fb7\u5bb6\u65cf\u7684\u9996\u5e2d\u5deb\u5e08\u548c\u7075\u5438\u602a\u5deb\u5996\u79d8\u5bc6\u5546\u8c08\uff0c\u800c\u4e14\u8fd9\u6b21\u53db\u4e71\u4e2d\uff0c\u90a3\u4e9b\u5974\u96b6\u4eec\u4f7f\u7528\u7684\u201c\u77f3\u706b\u201d\u2014\u2014\u5c31\u662f\u90a3\u79cd\u80fd\u591f\u8ba9\u77f3\u5934\u90fd\u71c3\u70e7\u8d77\u6765\u7684\u706b\u6cb9\u2014\u2014\u4e5f\u662f\u745e\u8d39\u5fb7\u5bb6\u65cf\u63d0\u4f9b\u7684\u3002\u8fe6\u6cd5\u4e0d\u6562\u6020\u6162\uff0c\u7acb\u523b\u56de\u62a5\u4e3b\u6bcd\u3002</p><p>\u743c\u6069\u80fd\u591f\u7406\u89e3\u8fe6\u6cd5\u4e3a\u4ec0\u4e48\u8fd9\u6837\u9ad8\u5174\uff0c\u4ece\u5979\u7684\u89d2\u5ea6\u6765\u8bf4\uff0c\u8fd9\u662f\u4e2a\u51fa\u4e4e\u610f\u6599\u7684\u597d\u6d88\u606f\u3002\u6b64\u6b21\u53db\u4e71\uff0c\u796d\u53f8\u5b66\u9662\u4f24\u4ea1\u60e8\u91cd\uff0c\u8fde\u795e\u540e\u8d50\u4e88\u7684\u5723\u7269\u90fd\u88ab\u6bc1\u4e86\uff0c\u8fe6\u6cd5\u8eab\u4e3a\u6b21\u5e2d\u6559\u5b98\uff0c\u5b66\u9662\u7684\u9996\u5e2d\u8d1f\u8d23\u4eba\uff0c\u81ea\u7136\u662f\u96be\u8f9e\u5176\u548e\u3002\u5982\u4eca\u627e\u5230\u4e86\u53db\u4e71\u7684\u5e55\u540e\u4e3b\u4f7f\uff0c\u7f6a\u8d23\u81ea\u7136\u5c31\u5168\u7531\u5bf9\u65b9\u627f\u62c5\u4e86\u2014\u2014\u81f3\u5c11\uff0c\u5979\u4e0d\u5fc5\u62c5\u5fc3\u4f1a\u88ab\u9001\u4e0a\u796d\u53f0\uff0c\u53bb\u5e73\u606f\u795e\u540e\u7684\u6012\u706b\u4e86\u3002</p><p>\u7136\u800c\u2026\u2026\u4e8b\u60c5\u6ca1\u8fd9\u4e48\u7b80\u5355\u3002</p><p>\u201c\u60a8\u5e94\u8be5\u7acb\u523b\u53ec\u96c6\u6267\u653f\u8bae\u4f1a\uff0c\u6307\u8bc1\u745e\u8d39\u5fb7\u5bb6\u65cf\u7684\u7f6a\u884c\uff0c\u201d\u8fe6\u6cd5\u6025\u5207\u5730\u5efa\u8bae\u7740\uff0c\u201c\u4ed6\u4eec\u80c6\u6562\u80cc\u53db\u795e\u540e\uff0c\u5e94\u8be5\u53d7\u5230\u6700\u4e25\u5389\u7684\u60e9\u7f5a\u3002\u201d</p><p>\u201c\u745e\u8d39\u5fb7\u5bb6\u65cf\u548c\u6211\u4eec\u83f2\u5c14\u4f26\u4e00\u6837\uff0c\u662f\u8fd9\u5ea7\u57ce\u5e02\u7684\u521b\u5efa\u8005\uff0c\u6700\u53e4\u8001\u7684\u5bb6\u65cf\u4e4b\u4e00\uff0c\u201d\u83f2\u5c14\u4f26\u4e3b\u6bcd\u6162\u6162\u8bf4\uff0c\u201c\u5b83\u540d\u5217\u6267\u653f\u8bae\u4f1a\uff0c\u6392\u540d\u7b2c\u56db\u3002\u6307\u63a7\u4ed6\u4eec\u8c0b\u53cd\u53db\u4e71\uff0c\u90a3\u662f\u9700\u8981\u975e\u5e38\u786e\u51ff\u7684\u8bc1\u636e\u7684\u3002\u201d</p><p>\u201c\u6211\u4eec\u6709\u8bc1\u636e\u3002\u201d</p><p>\u201c\u4ec5\u4ec5\u51ed\u51e0\u4e2a\u53db\u4e71\u8005\u7684\u53e3\u4f9b\u800c\u5df2\uff0c\u4e0d\u53ef\u80fd\u8bf4\u670d\u6267\u653f\u8bae\u4f1a\u7684\u3002\u201d</p><p>\u201c\u6211\u5df2\u7ecf\u79d8\u5bc6\u53ec\u56de\u4e86\u6211\u4eec\u5728\u745e\u8d39\u5fb7\u5bb6\u65cf\u91cc\u7684\u773c\u7ebf\uff0c\u201d\u8fe6\u6cd5\u8bf4\uff0c\u201c\u4ed6\u8bc1\u5b9e\u4e86\uff0c\u90a3\u4e9b\u77f3\u706b\u786e\u5b9e\u662f\u745e\u8d39\u5fb7\u5bb6\u7684\u5deb\u5e08\u4eec\u6700\u8fd1\u5f04\u51fa\u6765\u7684\u3002\u201d</p><p>\u201c\u90a3\u53c8\u5982\u4f55\uff1f\u201d\u4e3b\u6bcd\u51b7\u7b11\u7740\uff0c\u201c\u83ab\u975e\u4f60\u8981\u544a\u8bc9\u6267\u653f\u8bae\u4f1a\u7684\u53e6\u5916\u4e8c\u5341\u4f4d\u4e3b\u6bcd\uff1a\u5979\u4eec\u7684\u5bb6\u65cf\u91cc\u90fd\u6709\u83f2\u5c14\u4f26\u7684\u5185\u7ebf\uff1f\u201d</p><p>\u867d\u7136\u5353\u5c14\u4f34\u968f\u7740\u6df7\u4e71\u548c\u9634\u8c0b\u800c\u751f\uff0c\u4f46\u8fd9\u5e76\u4e0d\u610f\u5473\u7740\u5353\u5c14\u7684\u57ce\u5e02\u6ca1\u6709\u89c4\u5219\u2014\u2014\u5b9e\u9645\u4e0a\uff0c\u4e0d\u4f46\u6709\u89c4\u5219\uff0c\u800c\u4e14\u8fd8\u975e\u5e38\u4e25\u5389\u3002\u5728\u5176\u4ed6\u5bb6\u65cf\u91cc\u5b89\u63d2\u5185\u5978\uff0c\u8fd9\u5c31\u662f\u6700\u4e25\u91cd\u7684\u7f6a\u884c\u4e4b\u4e00\u3002</p><p>\u4efb\u4f55\u6df7\u4e71\u5f53\u4e2d\u90fd\u5305\u542b\u7740\u4e00\u70b9\u79e9\u5e8f\u7684\u79cd\u5b50\uff0c\u8fd9\u662f\u591a\u5143\u5b87\u5b99\u7684\u6cd5\u5219\u3002</p><p>\u201c\u8fd8\u6709\u90a3\u8be5\u8bc5\u5492\u7684\u6c89\u9ed8\u4e4b\u6b4c\uff0c\u201d\u8fe6\u6cd5\u8bf4\uff0c\u5979\u6beb\u4e0d\u9000\u7f29\uff0c\u56e0\u4e3a\u8fd9\u5173\u7cfb\u5230\u5979\u7684\u5207\u8eab\u5229\u76ca\uff0c\u201c\u90a3\u4e9b\u53db\u4e71\u8005\u5728\u9003\u79bb\u5bb6\u65cf\u540e\uff0c\u4e0d\u7ea6\u800c\u540c\u5730\u524d\u5f80\u6c89\u9ed8\u4e4b\u6b4c\uff0c\u8fd9\u96be\u9053\u4e0d\u662f\u8bc1\u636e\u5417\uff1f\u201d</p><p>\u743c\u6069\u542c\u51fa\u4e86\u8fe6\u6cd5\u8bdd\u8bed\u4e2d\u7684\u6697\u793a\uff0c\u5f88\u663e\u7136\uff0c\u6c89\u9ed8\u4e4b\u6b4c\u548c\u745e\u8d39\u5fb7\u5bb6\u65cf\u6709\u975e\u5e38\u5bc6\u5207\u7684\u8054\u7cfb\uff0c\u8fd9\u662f\u4e2a\u6709\u8da3\u7684\u53d1\u73b0\u3002</p><p>\u4e3b\u6bcd\u6c89\u541f\u7740\uff0c\u72b9\u8c6b\u4e0d\u51b3\uff0c\u6b64\u65f6\u4e00\u76f4\u9ed8\u4e0d\u4f5c\u58f0\u7684\u4e9a\u5f53\u65af\u7a81\u7136\u5f00\u53e3\u8bf4\u8bdd\u4e86\u3002</p><p>\u201c\u745e\u8d39\u5fb7\u5bb6\u65cf\u786e\u5b9e\u5f88\u503c\u5f97\u6000\u7591\u3002\u201d</p><p>\u4e3b\u6bcd\u7684\u8eab\u4f53\u5fae\u5fae\u524d\u503e\uff0c\u8868\u793a\u5bf9\u9996\u5e2d\u5deb\u5e08\u7684\u5c0a\u91cd\u3002\u5979\u4e00\u5411\u5f88\u4fe1\u8d56\u8fd9\u4e2a\u5f1f\u5f1f\uff0c\u5c24\u5176\u662f\u5728\u5f53\u4e0a\u4e3b\u6bcd\u4e4b\u540e\uff0c\u4e9a\u5f53\u65af\u4e0d\u8f7b\u6613\u53d1\u8868\u770b\u6cd5\uff0c\u4f46\u603b\u80fd\u5728\u6070\u5f53\u7684\u65f6\u5019\u5e2e\u52a9\u5979\u505a\u51fa\u51b3\u5b9a\uff0c\u5e76\u4e14\u4e8b\u540e\u90fd\u8bc1\u5b9e\u662f\u6b63\u786e\u7684\u3002</p><p>\u201c\u6211\u4eec\u7684\u5b66\u9662\u81ea\u4ece\u5efa\u7acb\u65f6\u8d77\uff0c\u5c31\u88ab\u4e25\u5bc6\u7684\u4fdd\u62a4\u9b54\u6cd5\u7b3c\u7f69\u7740\uff0c\u201d\u9996\u5e2d\u5deb\u5e08\u8bf4\uff0c\u4ed6\u7684\u58f0\u97f3\u6e05\u6717\u4e2d\u5e26\u7740\u78c1\u6027\uff0c\u6709\u79cd\u5929\u7136\u7684\u4ee4\u4eba\u5fc3\u60a6\u8bda\u670d\u7684\u8bf1\u60d1\u529b\uff0c\u201c\u6253\u7834\u9b54\u6cd5\u5c4f\u969c\uff0c\u5c06\u53db\u4e71\u8005\u4f20\u9001\u5230\u796d\u53f8\u5b66\u9662\uff0c\u5e76\u4e14\u5728\u53e6\u5916\u4e24\u5ea7\u5b66\u9662\u4e2d\u53ec\u5524\u6076\u9b54\u2014\u2014\u8fd9\u4e00\u5207\uff0c\u5982\u679c\u4e0d\u662f\u975e\u5e38\u719f\u6089\u7684\u5185\u90e8\u77e5\u60c5\u8005\uff0c\u662f\u80af\u5b9a\u529e\u4e0d\u5230\u7684\u3002\u6211\u6562\u80af\u5b9a\uff0c\u90a3\u4e2a\u7075\u5438\u602a\u5deb\u5996\u4e00\u5b9a\u6709\u5deb\u5e08\u5b66\u9662\u91cc\u7684\u5bfc\u5e08\u505a\u5185\u5e94\u3002\u201d</p><p>\u8fe6\u6cd5\u8fde\u8fde\u70b9\u5934\u3002</p><p>\u201c\u6211\u542c\u8bf4\u745e\u8d39\u5fb7\u5bb6\u65cf\u7684\u5deb\u5e08\u4eec\u5f88\u64c5\u957f\u53ec\u5524\u6076\u9b54\uff1f\u201d\u4e3b\u6bcd\u95ee\u3002</p><p>\u201c\u6b63\u662f\u5982\u6b64\uff0c\u201d\u4e9a\u5f53\u65af\u8bf4\uff0c\u201c\u5728\u70bc\u91d1\u672f\uff0c\u4ee5\u53ca\u548c\u4e0b\u5c42\u754c\u7684\u5b58\u5728\u6253\u4ea4\u9053\u4e0a\uff0c\u4ed6\u4eec\u9996\u5c48\u4e00\u6307\uff0c\u5373\u4f7f\u662f\u6211\u4e5f\u81ea\u6127\u4e0d\u5982\u3002\u201d</p><p>\u201c\u4f60\u592a\u8c26\u865a\u4e86\uff0c\u201d\u4e3b\u6bcd\u5fae\u7b11\uff0c\u201c\u8c01\u90fd\u77e5\u9053\u4f60\u662f\u8fd9\u5ea7\u57ce\u5e02\u91cc\u6700\u4f18\u79c0\u7684\u5deb\u5e08\u3002\u201d</p><p>\u4e9a\u5f53\u65af\u5fae\u5fae\u8eac\u8eab\uff0c\u8868\u793a\u611f\u8c22\u3002</p><p>\u4e3b\u6bcd\u601d\u8003\u7740\uff0c\u5979\u4fee\u957f\u7684\u624b\u6307\u8f7b\u8f7b\u6572\u6253\u7740\u738b\u5ea7\u7684\u6276\u624b\uff0c\u53d1\u51fa\u6e05\u8106\u7684\u58f0\u97f3\u3002\u201c\u8bc1\u636e\u4e0d\u591f\u5f3a\u70c8\uff0c\u201d\u5979\u6700\u540e\u53f9\u606f\uff0c\u201c\u6211\u6000\u7591\u6267\u653f\u8bae\u4f1a\u4e0d\u4f1a\u88ab\u8bf4\u670d\u3002\u201d</p><p>\u201c\u90a3\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d\u4e9a\u5f53\u65af\u8bf4\u3002</p><p>\u201c\u55ef\uff1f\u201d\u4e3b\u6bcd\u4e0d\u89e3\u3002</p><p>\u201c\u73b0\u5728\u548c\u5f80\u6614\u5df2\u7ecf\u4e0d\u4e00\u6837\u4e86\uff0c\u201d\u9996\u5e2d\u5deb\u5e08\u8bf4\uff0c\u201c\u6709\u4e9b\u53d8\u5316\u6b63\u5728\u6084\u6084\u5730\u53d1\u751f\u3002\u5728\u4ee5\u524d\uff0c\u6211\u4eec\u96be\u9053\u80fd\u591f\u60f3\u50cf\u8fd9\u6837\u5927\u7684\u53db\u4e71\u5417\uff1f\u6211\u4eec\u80fd\u591f\u60f3\u50cf\u90a3\u4e9b\u5974\u96b6\u5c45\u7136\u80c6\u6562\u5411\u4e3b\u4eba\u6325\u821e\u5200\u5251\u5417\uff1f\u8718\u86db\u5728\u4e0a\uff0c\u8fd9\u4e00\u5207\u90fd\u662f\u90a3\u6837\u7684\u96be\u4ee5\u60f3\u8c61\uff0c\u4f46\u5b83\u4eec\u4f9d\u65e7\u53d1\u751f\u4e86\uff0c\u800c\u4e14\u8fd8\u4f1a\u7ee7\u7eed\u53d1\u751f\u3002\u795e\u540e\u5df2\u7ecf\u79bb\u6211\u4eec\u8fdc\u53bb\uff0c\u53e4\u8001\u7684\u79e9\u5e8f\u6b63\u5728\u52a8\u6447\uff0c\u8fd9\u662f\u4e00\u4e2a\u975e\u5e38\u65f6\u671f\uff0c\u4f5c\u4e3a\u7b2c\u4e00\u5bb6\u65cf\uff0c\u4f5c\u4e3a\u8fd9\u5ea7\u57ce\u5e02\u7684\u521b\u5efa\u8005\u3001\u5b88\u62a4\u8005\u548c\u7edf\u6cbb\u8005\uff0c\u6211\u4eec\u9700\u8981\u627f\u62c5\u8d77\u76f8\u5e94\u7684\u8d23\u4efb\u3002\u201d</p><p>\u201c\u4ee5\u53ca\u5c55\u793a\u6211\u4eec\u7684\u5b9e\u529b\u3002\u201d\u4ed6\u6700\u540e\u8865\u5145\u8bf4\u3002</p><p>\u4e3b\u6bcd\u51dd\u89c6\u7740\u4ed6\uff0c\u7136\u540e\u773c\u5149\u4ece\u51e0\u4e2a\u5973\u513f\u7684\u8138\u4e0a\u8f6e\u6d41\u626b\u8fc7\uff0c\u63a0\u8fc7\u6b66\u6280\u957f\uff0c\u6700\u540e\u671d\u743c\u6069\u770b\u4e86\u4e00\u773c\u3002\u201c\u6211\u60f3\u4f60\u8bf4\u5f97\u5bf9\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u6211\u4eec\u8d1f\u6709\u795e\u5723\u7684\u8d23\u4efb\u3002\u201d</p></article>"}}}]);
