(window.webpackJsonp=window.webpackJsonp||[]).push([[255],{277:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u5341\u516d\u8282 \u5bb6\u65cf\u6218\u4e89",content:"<article><h2>\u7b2c\u4e8c\u5341\u516d\u8282 \u5bb6\u65cf\u6218\u4e89</h2><p>\u5728\u4e0a\u57ce\u533a\u7684\u4e1c\u5317\u89d2\u843d\uff0c\u5ea7\u843d\u7740\u4e00\u5ea7\u5177\u6709\u5178\u578b\u5353\u5c14\u98ce\u683c\u7684\u57ce\u5821\uff0c\u4e00\u6392\u4e00\u6392\u7684\u77f3\u7b0b\u6797\u6c89\u9ed8\u77d7\u7acb\uff0c\u63d0\u9192\u6240\u6709\u4eba\u8fd9\u91cc\u662f\u745e\u8d39\u5fb7\u5bb6\u65cf\u7684\u5b85\u9662\u3002</p><p>\u5927\u7ea6\u5728\u4e00\u4e07\u5e74\u524d\uff0c\u7cbe\u7075\u795e\u7cfb\u53d1\u751f\u4e86\u4e00\u573a\u5267\u70c8\u52a8\u8361\uff0c\u67ef\u745e\u9686\u7684\u59bb\u5b50\u201c\u7f16\u7ec7\u8005\u201d\u963f\u841d\u8bd7\u53d1\u52a8\u53db\u4e71\uff0c\u5931\u8d25\u540e\u88ab\u653e\u9010\u5230\u6df1\u6e0a\uff0c\u4ece\u6b64\u6539\u540d\u7f57\u4e1d\u3002\u968f\u7740\u795e\u7947\u7684\u5760\u843d\uff0c\u539f\u672c\u5c45\u4f4f\u5728\u5730\u8868\u4e16\u754c\u7684\u9ed1\u6697\u7cbe\u7075\u88ab\u7cbe\u7075\u8bf8\u795e\u964d\u4e0b\u8bc5\u5492\uff0c\u518d\u4e5f\u65e0\u6cd5\u5728\u9633\u5149\u4e0b\u751f\u6d3b\uff0c\u4e0d\u5f97\u4e0d\u9003\u5165\u5730\u4e0b\uff0c\u8fd9\u5c31\u662f\u5386\u53f2\u4e0a\u8457\u540d\u7684\u201c\u5927\u6c89\u964d\u201d\u3002\u5353\u5c14\u4eec\u5728\u5e7d\u6697\u5730\u57df\u751f\u5b58\u4e0b\u6765\uff0c\u548c\u7075\u5438\u602a\u3001\u7070\u77ee\u4eba\u3001\u517d\u4eba\u4ee5\u53ca\u5404\u79cd\u51f6\u9669\u7684\u602a\u7269\u640f\u6597\uff0c\u6700\u7ec8\u5efa\u7acb\u8d77\u4e86\u5927\u5927\u5c0f\u5c0f\u7684\u57ce\u5e02\uff0c\u74dc\u7406\u5fb7\u65af\u662f\u5176\u4e2d\u6700\u53e4\u8001\u4e5f\u6700\u5927\u7684\u4e00\u4e2a\u3002</p><p>\u745e\u8d39\u5fb7\u548c\u83f2\u5c14\u4f26\u4e00\u6837\uff0c\u90fd\u662f\u8fd9\u5ea7\u57ce\u5e02\u7684\u521b\u59cb\u8005\u4e4b\u4e00\u3002\u6700\u521d\u5efa\u57ce\u7684\u4e09\u5341\u4e8c\u4e2a\u5bb6\u65cf\uff0c\u7ecf\u5386\u4e86\u5404\u79cd\u5185\u4e71\u3001\u8c0b\u6740\u3001\u5206\u88c2\u548c\u6df7\u6218\uff0c\u5982\u4eca\u5df2\u7ecf\u53ea\u5269\u4e0b\u516b\u4e2a\u4e86\u3002\u83f2\u5c14\u4f26\u6210\u529f\u83b7\u5f97\u4e86\u7b2c\u4e00\u5bb6\u65cf\u7684\u5b9d\u5ea7\uff0c\u5e76\u4e14\u5728\u5176\u540e\u7684\u51e0\u5343\u5e74\u91cc\u632b\u8d25\u4e86\u6240\u6709\u7684\u6311\u6218\u8005\uff0c\u7a33\u7a33\u5730\u5de9\u56fa\u4e86\u81ea\u5df1\u7684\u5730\u4f4d\u3002\u745e\u8d39\u5fb7\u4e5f\u4ece\u6700\u521d\u7684\u7b2c\u4e8c\u5341\u4e5d\u5bb6\u65cf\u5347\u5230\u4e86\u5982\u4eca\u7684\u7b2c\u56db\u540d\u3002</p><p>\u60a0\u4e45\u7684\u5386\u53f2\uff0c\u8ba9\u4e0d\u540c\u7684\u5bb6\u65cf\u5f62\u6210\u5404\u81ea\u7684\u4f20\u7edf\u548c\u7279\u8272\uff0c\u83f2\u5c14\u4f26\u5bb6\u65cf\u6700\u5f97\u795e\u540e\u7684\u5ba0\u7231\uff0c\u62e5\u6709\u538b\u5012\u6027\u6570\u91cf\u7684\u7267\u5e08\uff1b\u7b2c\u4e09\u5bb6\u65cf\u7c73\u5179\u745e\u56fe\u5c14\u559c\u6b22\u4f7f\u7528\u77ee\u4eba\u5974\u96b6\uff08\u6240\u4ee5\u5b83\u6700\u70ed\u8877\u4e8e\u5411\u4f0a\u5361\u6c99\u57ce\u5f00\u6218\uff09\uff1b\u745e\u8d39\u5fb7\u5bb6\u65cf\uff0c\u5219\u7d20\u6765\u4ee5\u76db\u4ea7\u4f18\u79c0\u7684\u5deb\u5e08\u800c\u8457\u79f0\u3002</p><p>\u74dc\u7406\u5fb7\u65af\u57ce\u4e2d\u904d\u5730\u662f\u6218\u58eb\uff0c\u7267\u5e08\u4e5f\u4e0d\u5c11\uff0c\u4f46\u5deb\u5e08\u5374\u6781\u7f55\u89c1\u3002\u5965\u672f\u8fd9\u95e8\u6280\u827a\u592a\u6ce8\u91cd\u5929\u8d4b\u4e86\uff0c\u800c\u5353\u5c14\u7684\u793e\u4f1a\u5b9e\u884c\u5bb6\u65cf\u7edf\u6cbb\uff0c\u5f88\u662f\u4efb\u4eba\u552f\u4eb2\uff0c\u6ca1\u6709\u4ece\u5e95\u5c42\u9009\u62d4\u4eba\u624d\u7684\u5236\u5ea6\u3002\u5982\u679c\u5e73\u6c11\u51fa\u8eab\uff0c\u538b\u6839\u6ca1\u8d44\u683c\u8fdb\u5165\u5deb\u5e08\u5b66\u9662\uff0c\u9664\u975e\u78b0\u4e0a\u7279\u6b8a\u673a\u7f18\uff0c\u5426\u5219\u5c31\u7b97\u518d\u9ad8\u7684\u9b54\u6cd5\u5929\u8d4b\u4e5f\u767d\u767d\u57cb\u6ca1\u6389\u4e86\uff1b\u7537\u6027\u8d35\u65cf\u6709\u5b66\u4e60\u5965\u672f\u7684\u673a\u4f1a\uff0c\u4f46\u9b54\u6cd5\u5929\u8d4b\u8fd9\u79cd\u4e1c\u897f\uff0c\u5e76\u4e0d\u4f1a\u56e0\u4e3a\u4f60\u5730\u4f4d\u9ad8\u8d35\u5c31\u683c\u5916\u504f\u7231\u7684\u3002\u6bcf\u5e74\u90fd\u6709\u5f88\u591a\u5b66\u5f92\u8fdb\u5165\u5deb\u5e08\u5b66\u9662\uff0c\u4f46\u80fd\u591f\u6210\u529f\u6bd5\u4e1a\u7684\u5be5\u5be5\u65e0\u51e0\u2014\u2014\u5b66\u9662\u7684\u5bfc\u5e08\u4eec\u603b\u662f\u80fd\u628a\u6b7b\u4ea1\u540d\u989d\u5168\u90fd\u7528\u5149\uff0c\u534a\u70b9\u90fd\u4e0d\u6d6a\u8d39\u3002</p><p>\u83f2\u5c14\u4f26\u80fd\u591f\u4e00\u76f4\u7a33\u5ea7\u7b2c\u4e00\u5bb6\u65cf\u7684\u5e2d\u4f4d\uff0c\u5b9e\u529b\u81ea\u7136\u6700\u4e3a\u96c4\u539a\uff0c\u65e0\u8bba\u662f\u5bb6\u65cf\u6210\u5458\u7684\u7d20\u8d28\u3001\u88c5\u5907\u7684\u7cbe\u826f\u548c\u7269\u8d28\u7684\u50a8\u5907\uff0c\u4ee5\u53ca\u57ce\u5821\u7684\u9632\u5fa1\u4e0a\u90fd\u662f\u9996\u5c48\u4e00\u6307\u7684\u3002\u6709\u4f20\u8a00\u8bf4\uff0c\u5b83\u80fd\u591f\u540c\u65f6\u5bf9\u6297\u57ce\u4e2d\u4efb\u4f55\u4e09\u4e2a\u5bb6\u65cf\u7684\u8054\u5408\u653b\u51fb\u800c\u4e0d\u843d\u4e0b\u98ce\u3002\u4f46\u5373\u4f7f\u662f\u83f2\u5c14\u4f26\u5bb6\u65cf\uff0c\u4e5f\u53ea\u6709\u4e94\u540d\u5deb\u5e08\uff0c\u6267\u653f\u8bae\u4f1a\u7684\u5176\u4ed6\u5bb6\u65cf\u5927\u591a\u62e5\u6709\u4e24\u540d\u5230\u56db\u540d\uff0c\u66f4\u4f4e\u9636\u7684\u5bb6\u65cf\u5f80\u5f80\u53ea\u6709\u4e00\u540d\uff0c\u751a\u81f3\u5e72\u8106\u5c31\u6ca1\u6709\u3002\u745e\u8d39\u5fb7\u5374\u6709\u63a5\u8fd1\u4e8c\u5341\u540d\u5deb\u5e08\uff0c\u51e0\u4e4e\u548c\u5bb6\u65cf\u91cc\u7684\u7267\u5e08\u6570\u91cf\u76f8\u5f53\u4e86\uff0c\u5176\u4e2d\u6709\u56db\u540d\u5728\u5deb\u5e08\u5b66\u9662\u91cc\u62c5\u4efb\u5bfc\u5e08\u3002\u8981\u77e5\u9053\uff0c\u6574\u4e2a\u5b66\u9662\u4e5f\u4e0d\u8fc7\u6709\u5341\u4e00\u540d\u5bfc\u5e08\u3002</p><p>\u73b0\u4efb\u7684\u5bb6\u65cf\u9996\u5e2d\u5deb\u5e08\u662f\u514b\u52b3\u62d3\xb7\u745e\u8d39\u5fb7\uff0c\u4ed6\u64c5\u957f\u70bc\u91d1\u672f\uff0c\u7cbe\u660e\u3001\u72e1\u8bc8\uff0c\u8be1\u8ba1\u591a\u7aef\uff0c\u800c\u4e14\u5f02\u4e4e\u5bfb\u5e38\u5730\u957f\u5bff\u3002\u4ece\u7406\u8bba\u4e0a\u8bf4\uff0c\u5353\u5c14\u7684\u5bff\u547d\u53ef\u4ee5\u8fbe\u5230\u4e03\u767e\u5c81\uff0c\u4f46\u7edd\u5927\u591a\u6570\u6210\u5458\u90fd\u6ca1\u6709\u5bff\u7ec8\u6b63\u5bdd\u7684\u597d\u8fd0\u6c14\uff0c\u4ed6\u4eec\u4f1a\u5728\u4e09\u56db\u767e\u5c81\u2014\u2014\u751a\u81f3\u66f4\u65e9\u2014\u2014\u7684\u65f6\u5019\u5e94\u8be5\u5404\u79cd\u201c\u610f\u5916\u201d\u800c\u4e27\u547d\u3002\u514b\u52b3\u62d3\u662f\u4e2a\u4f8b\u5916\uff0c\u4ed6\u5df2\u7ecf\u6d3b\u4e86\u5c06\u8fd1\u4e5d\u767e\u5c81\uff0c\u800c\u4e14\u8fd8\u4f9d\u7136\u4e0d\u89c1\u8870\u8001\u4e4b\u6001\u3002\u4ed6\u628a\u8fd9\u5f52\u529f\u4e8e\u81ea\u5df1\u5c0f\u5fc3\u7ffc\u7ffc\u7684\u8c28\u614e\u548c\u5404\u79cd\u73cd\u8d35\u7684\u9b54\u6cd5\u836f\u5242\uff0c\u4f46\u6709\u8c23\u8a00\u8bf4\u4ed6\u628a\u7075\u9b42\u51fa\u5356\u7ed9\u4e86\u6076\u9b54\uff0c\u6362\u6765\u4e86\u8fd9\u5e45\u8eaf\u58f3\u7684\u957f\u5b58\uff0c\u5f88\u591a\u4eba\u76f8\u4fe1\u8fd9\u79cd\u8bf4\u6cd5\uff0c\u867d\u7136\u6ca1\u6709\u8bc1\u636e\u3002</p><p>\u5728\u56db\u767e\u5c81\u751f\u65e5\u7684\u65f6\u5019\uff0c\u514b\u52b3\u62d3\u5f53\u4e0a\u4e86\u5bb6\u65cf\u9996\u5e2d\u5deb\u5e08\uff0c\u4ed6\u8f9e\u53bb\u4e86\u5b66\u9662\u91cc\u7684\u5bfc\u5e08\u4e4b\u804c\uff0c\u5c3d\u5fc3\u5c3d\u529b\u5730\u8f85\u4f50\u7740\u6bcf\u4e00\u4efb\u4e3b\u6bcd\uff0c\u540c\u65f6\u4e5f\u5728\u4e0d\u52a8\u58f0\u8272\u5730\u6269\u5f20\u7740\u81ea\u5df1\u7684\u6743\u529b\u548c\u5f71\u54cd\uff0c\u5411\u7740\u76ee\u6807\u524d\u8fdb\u3002\u8fd1\u4e09\u767e\u5e74\u91cc\uff0c\u745e\u8d39\u5fb7\u5bb6\u65cf\u4ece\u7b2c\u4e5d\u540d\u8dc3\u5347\u5230\u5982\u4eca\u7684\u7b2c\u56db\u540d\uff0c\u9996\u5e2d\u5deb\u5e08\u514b\u52b3\u62d3\u5c45\u529f\u81f3\u4f1f\u3002</p><p>\u5728\u4ed6\u6210\u4e3a\u9996\u5e2d\u5deb\u5e08\u4e4b\u540e\uff0c\u745e\u8d39\u5fb7\u4e3b\u6bcd\u7684\u5b9d\u5ea7\u4e0a\u5df2\u7ecf\u6362\u4e86\u4e09\u4e2a\u4eba\u2014\u2014\u6bcf\u6b21\u53d8\u52a8\u90fd\u548c\u4ed6\u6709\u5bc6\u5207\u5173\u7cfb\uff0c\u5c24\u5176\u662f\u6700\u8fd1\u4e00\u6b21\uff0c\u53d1\u751f\u5728\u4e09\u5341\u56db\u5e74\u524d\u3002\u73b0\u4efb\u4e3b\u6bcd\u662f\u4ed6\u7cbe\u5fc3\u6311\u9009\u7684\uff0c\u975e\u5e38\u5e74\u8f7b\uff0c\u4f18\u67d4\u5be1\u65ad\uff0c\u5728\u9886\u5bfc\u8fd9\u6837\u5927\u7684\u5bb6\u65cf\u4e0a\u660e\u663e\u80fd\u529b\u4e0d\u8db3\uff0c\u4e0d\u5f97\u4e0d\u4e25\u91cd\u4f9d\u8d56\u5e74\u957f\u777f\u667a\u7684\u9996\u5e2d\u5deb\u5e08\uff0c\u5bf9\u4ed6\u8a00\u542c\u8ba1\u4ece\u3002\u4ece\u67d0\u79cd\u7a0b\u5ea6\u4e0a\u6765\u8bf4\uff0c\u514b\u52b3\u62d3\u624d\u662f\u8fd9\u4e2a\u5bb6\u65cf\u7684\u4e3b\u5bb0\uff0c\u5c3d\u7ba1\u662f\u7ad9\u5728\u5e55\u540e\u7684\uff0c\u5f88\u591a\u4eba\u90fd\u77e5\u9053\u8fd9\u4e00\u70b9\u3002</p><p>\u4f5c\u4e3a\u4e00\u4e2a\u7537\u6027\u5353\u5c14\uff0c\u8fd9\u51e0\u4e4e\u5df2\u7ecf\u662f\u68a6\u60f3\u624d\u80fd\u4f01\u53ca\u7684\u9ad8\u5ea6\uff0c\u7136\u800c\u514b\u52b3\u62d3\u5e76\u4e0d\u6ee1\u8db3\u3002\u4ed6\u548c\u6240\u6709\u7684\u5353\u5c14\u4e00\u6837\uff0c\u91ce\u5fc3\u52c3\u52c3\uff0c\u79ef\u6781\u8fdb\u53d6\uff0c\u5bf9\u6743\u529b\u548c\u5730\u4f4d\u7684\u6e34\u671b\u6c38\u8fdc\u6ca1\u6709\u6b62\u5883\u3002\u638c\u63e1\u4e00\u4e2a\u540d\u5217\u6267\u653f\u8bae\u4f1a\u7684\u5bb6\u65cf\uff0c\u8fd9\u5e76\u4e0d\u662f\u4ed6\u7684\u7ec8\u6781\u76ee\u6807\uff0c\u53ea\u80fd\u7b97\u662f\u9636\u6bb5\u6027\u7684\u6210\u679c\u3002</p><p>\u9996\u5e2d\u5deb\u5e08\u6240\u5e0c\u671b\u7684\uff0c\u662f\u771f\u6b63\u5730\u63a7\u5236\u8fd9\u5ea7\u57ce\u5e02\u3002</p><p>\u8fd9\u662f\u4ed6\u5f88\u4e45\u4ee5\u524d\u5c31\u7acb\u4e0b\u7684\u5fd7\u613f\uff0c\u968f\u7740\u5e74\u9f84\u7684\u589e\u957f\uff0c\u8fd9\u79cd\u60f3\u6cd5\u4e0d\u4f46\u6ca1\u6709\u8870\u51cf\uff0c\u53cd\u800c\u66f4\u52a0\u6e05\u6670\u5f3a\u70c8\u8d77\u6765\u3002\u4ed6\u5df2\u7ecf\u63a5\u8fd1\u4e5d\u767e\u5c81\uff0c\u8fd9\u5927\u5927\u8d85\u51fa\u4e86\u5353\u5c14\u7684\u6b63\u5e38\u5bff\u547d\uff0c\u867d\u7136\u8eab\u4f53\u8fd8\u662f\u548c\u4e00\u767e\u5c81\u65f6\u540c\u6837\u5065\u5eb7\uff0c\u4f46\u514b\u52b3\u62d3\u4e5f\u6e05\u695a\u81ea\u5df1\u7684\u4f59\u65e5\u4e0d\u591a\u4e86\u3002\u4ed6\u6d3b\u5f97\u592a\u4e45\uff0c\u5728\u9996\u5e2d\u5deb\u5e08\u7684\u4f4d\u7f6e\u4e0a\u4e5f\u5446\u5f97\u592a\u4e45\uff0c\u6321\u4f4f\u4e86\u540e\u9762\u4eba\u7684\u8def\uff0c\u8bf4\u4e0d\u5b9a\u54ea\u5929\u5c31\u4f1a\u6709\u4eba\u5728\u4ed6\u5fc3\u810f\u4e0a\u63d2\u4e00\u628a\u5315\u9996\u3002\u514b\u52b3\u62d3\u5e76\u4e0d\u5962\u671b\u81ea\u5df1\u80fd\u591f\u5bff\u7ec8\u6b63\u5bdd\uff0c\u5bf9\u4e8e\u5353\u5c14\u6765\u8bf4\u8fd9\u592a\u4e0d\u5207\u5b9e\u9645\u4e86\uff0c\u4ed6\u53ea\u671f\u671b\u80fd\u591f\u5728\u95ed\u4e0a\u773c\u775b\u4e4b\u524d\u8fbe\u6210\u5fc3\u613f\u3002</p><p>\u90a3\u4e48\uff0c\u9996\u5148\u7b2c\u4e00\u6b65\uff0c\u662f\u8981\u8ba9\u745e\u8d39\u5fb7\u6210\u4e3a\u7b2c\u4e00\u5bb6\u65cf\u2014\u2014\u8fd9\u5f88\u96be\uff0c\u73b0\u5728\u6321\u5728\u524d\u9762\u7684\u4e09\u4e2a\u5bb6\u65cf\u90fd\u66fe\u7ecf\u662f\u57ce\u5e02\u7684\u521b\u5efa\u8005\uff0c\u5386\u53f2\u60a0\u4e45\uff0c\u5b9e\u529b\u96c4\u539a\u3002\u745e\u8d39\u5fb7\u867d\u7136\u8dc3\u5347\u98de\u901f\uff0c\u7ec8\u7a76\u8fd8\u662f\u6839\u57fa\u4e0d\u8db3\uff0c\u76f8\u6bd4\u8f83\u8d77\u6765\u900a\u8272\u5f88\u591a\u3002</p><p>\u9700\u8981\u673a\u4f1a\u3002</p><p>\u800c\u673a\u4f1a\u6765\u4e86\u3002</p><p>\u5728\u4e24\u4e2a\u6708\u524d\u7684\u4e00\u5929\uff0c\u745e\u8d39\u5fb7\u5bb6\u65cf\u7684\u7267\u5e08\u53d1\u73b0\u7948\u7977\u65e0\u6cd5\u83b7\u5f97\u56de\u5e94\uff0c\u5979\u4eec\u5413\u574f\u4e86\uff0c\u4ee5\u4e3a\u89e6\u6012\u4e86\u795e\u540e\u3002\u5728\u74dc\u7406\u5fb7\u65af\uff0c\u4e00\u4e2a\u5bb6\u65cf\u4e27\u5931\u4e86\u7f57\u4e1d\u7684\u6069\u5ba0\uff0c\u90a3\u5c31\u610f\u5473\u7740\u5b83\u8ddd\u79bb\u706d\u4ea1\u4e0d\u8fdc\u4e86\u3002\u4f46\u5f88\u5feb\uff0c\u745e\u8d39\u5fb7\u7267\u5e08\u53d1\u73b0\u5168\u57ce\u6240\u6709\u7684\u540c\u884c\u90fd\u9677\u5165\u4e86\u540c\u6837\u7684\u5371\u673a\u5f53\u4e2d\u3002\u514b\u52b3\u62d3\u7b2c\u4e00\u65f6\u95f4\u5f97\u77e5\u4e86\u8fd9\u4e2a\u6d88\u606f\uff0c\u4ed6\u7acb\u523b\u654f\u9510\u5730\u731c\u51fa\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u60c5\u3002</p><p>\u7f57\u4e1d\u662f\u4e2a\u559c\u6012\u65e0\u5e38\u7684\u795e\u7947\uff0c\u7ecf\u5e38\u4f1a\u56e0\u4e3a\u4e00\u4e9b\u83ab\u540d\u5176\u5999\u7684\u7406\u7531\u629b\u5f03\u81ea\u5df1\u7684\u4fe1\u5f92\uff0c\u4f46\u5979\u4e0d\u53ef\u80fd\u629b\u5f03\u6574\u4e2a\u74dc\u7406\u5fb7\u65af\u57ce\u7684\u7267\u5e08\u3002\u6ca1\u6709\u4efb\u4f55\u795e\u7947\u80fd\u591f\u627f\u53d7\u8fd9\u6837\u7684\u635f\u5931\uff0c\u5c31\u7b97\u662f\u60e9\u7f5a\uff0c\u6301\u7eed\u7684\u65f6\u95f4\u4e5f\u592a\u957f\u4e86\u3002</p><p>\u795e\u7947\u7684\u4e16\u754c\uff0c\u51e1\u4eba\u65e0\u4ece\u7aa5\u89c6\uff0c\u4e0d\u8fc7\u514b\u52b3\u62d3\u4f9d\u7136\u6709\u6253\u63a2\u6d88\u606f\u7684\u6e20\u9053\u3002\u7f57\u4e1d\u5c45\u4f4f\u5728\u6df1\u6e0a\u91cc\uff0c\u800c\u745e\u8d39\u5fb7\u5bb6\u65cf\u7684\u5deb\u5e08\uff0c\u7d20\u6765\u5c31\u4ee5\u64c5\u957f\u548c\u4e0b\u5c42\u754c\u7684\u5996\u9b54\u6253\u4ea4\u9053\u800c\u8457\u79f0\u3002\u514b\u52b3\u62d3\u6700\u7ec8\u786e\u5b9a\u7f57\u4e1d\u5df2\u7ecf\u6d88\u901d\uff0c\u8fd9\u4ee4\u4ed6\u5927\u559c\u8fc7\u671b\u3002</p><p>\u5353\u5c14\u7684\u4e16\u754c\u662f\u5f31\u8089\u5f3a\u98df\u7684\u4e16\u754c\uff0c\u4e00\u5207\u51ed\u5b9e\u529b\u8bf4\u8bdd\u3002\u5bb6\u65cf\u6392\u540d\uff0c\u539f\u672c\u5927\u81f4\u4e0a\u5c31\u53cd\u5e94\u4e86\u5b9e\u529b\u7684\u9ad8\u4f4e\uff0c\u4f46\u5982\u4eca\u7267\u5e08\u4eec\u7a81\u7136\u4e27\u5931\u4e86\u795e\u672f\uff0c\u529b\u91cf\u5927\u5927\u524a\u5f31\uff0c\u800c\u5deb\u5e08\u7684\u91cd\u8981\u6027\u5219\u52a0\u500d\u51f8\u663e\u51fa\u6765\u3002\u6b64\u6d88\u5f7c\u957f\u4e4b\u4e0b\uff0c\u745e\u8d39\u5fb7\u5bb6\u65cf\u7684\u5b9e\u529b\u7b49\u4e8e\u5728\u65e0\u5f62\u4e4b\u95f4\u731b\u7136\u4e0a\u5347\u4e86\u4e00\u4e2a\u53f0\u9636\u3002</p><p>\u514b\u52b3\u62d3\u7684\u7b2c\u4e00\u53cd\u5e94\uff0c\u662f\u8981\u4e0d\u8981\u8d81\u673a\u53d1\u52a8\u5bb6\u65cf\u6218\u4e89\uff0c\u628a\u6392\u540d\u5728\u524d\u9762\u7684\u4e09\u4e2a\u5bb6\u65cf\u8d76\u4e0b\u53f0\u3002\u4f46\u7ecf\u8fc7\u6df1\u601d\u719f\u8651\uff0c\u4ed6\u653e\u5f03\u4e86\u8fd9\u4e2a\u60f3\u6cd5\u3002</p><p>\u5982\u679c\u7f57\u4e1d\u5df2\u7ecf\u6d88\u5931\uff0c\u90a3\u4e48\u5979\u4e3a\u5353\u5c14\u5236\u5b9a\u7684\u89c4\u5219\u81ea\u7136\u4e5f\u5c31\u6ca1\u6709\u7ea6\u675f\u529b\u3002\u5bb6\u65cf\u6392\u540d\u4e5f\u597d\uff0c\u6267\u653f\u8bae\u4f1a\u4e5f\u597d\uff0c\u90fd\u5df2\u7ecf\u53d8\u5f97\u6beb\u65e0\u610f\u4e49\u3002\u53ea\u8981\u94f2\u9664\u6389\u90a3\u4e9b\u5df2\u7ecf\u4e27\u5931\u795e\u672f\u7684\u7267\u5e08\u2014\u2014\u5f53\u7136\u4e0d\u5fc5\u6740\u5149\uff0c\u53ea\u8981\u524a\u5f31\u5230\u8db3\u591f\u7684\u7a0b\u5ea6\uff0c\u8fd9\u5ea7\u57ce\u5e02\u7684\u9886\u5bfc\u6743\u5c31\u4f1a\u843d\u5230\u5deb\u5e08\u624b\u4e2d\uff0c\u800c\u745e\u8d39\u5fb7\u5bb6\u65cf\u62e5\u6709\u6700\u591a\u6700\u4f18\u79c0\u7684\u5deb\u5e08\u3002</p><p>\u5c31\u50cf\u5317\u65b9\u7684\u6c99\u739b\u65af\u57ce\u4e00\u6837\uff0c\u5deb\u5e08\u6267\u638c\u5927\u6743\uff0c\u539f\u672c\u8dbe\u9ad8\u6c14\u626c\u7684\u7267\u5e08\u88ab\u8e29\u5728\u811a\u4e0b\uff0c\u6210\u4e3a\u4e8c\u7b49\u516c\u6c11\u2014\u2014\u8fd9\u662f\u591a\u4e48\u4ee4\u4eba\u6109\u5feb\u7684\u524d\u666f\u554a\uff0c\u6bcf\u5f53\u60f3\u8d77\u6765\uff0c\u514b\u52b3\u62d3\u5c31\u5174\u594b\u5f97\u6d51\u8eab\u53d1\u6296\uff0c\u4ed6\u4f1a\u6210\u4e3a\u82f1\u96c4\u800c\u6c38\u8f7d\u53f2\u518c\u7684\u3002</p><p>\u5728\u5f88\u4e45\u4ee5\u524d\uff0c\u4ed6\u901a\u8fc7\u524d\u4efb\u745e\u8d39\u5fb7\u4e3b\u6bcd\u8bf4\u670d\u4e86\u6267\u653f\u8bae\u4f1a\uff0c\u5141\u8bb8\u4ed6\u5efa\u7acb\u4e86\u6c89\u9ed8\u4e4b\u6b4c\u3002\u5b83\u7684\u5b58\u5728\u4e0d\u4ec5\u4ec5\u662f\u4e3a\u4e86\u7ed9\u90a3\u4e9b\u7537\u6027\u5353\u5c14\u63d0\u4f9b\u4e00\u4e2a\u53d1\u6cc4\u6b32\u671b\u7684\u573a\u6240\uff0c\u66f4\u662f\u7edd\u4f73\u7684\u60c5\u62a5\u4ea4\u6362\u5904\u3002\u901a\u8fc7\u6c89\u9ed8\u4e4b\u6b4c\uff0c\u514b\u52b3\u62d3\u638c\u63e1\u4e86\u8fd9\u5ea7\u57ce\u5e02\u91cc\u7684\u5f88\u591a\u4e0d\u4e3a\u4eba\u77e5\u7684\u79d8\u5bc6\uff0c\u5927\u90e8\u4efd\u90fd\u592a\u8fc7\u7ec6\u5c51\u7410\u788e\uff0c\u6ca1\u6709\u4ef7\u503c\uff0c\u4f46\u5269\u4e0b\u6765\u7684\u5c0f\u90e8\u5206\u5219\u4ee4\u4ed6\u83b7\u76ca\u826f\u591a\u3002</p><p>\u4fe1\u606f\u5c31\u662f\u529b\u91cf\uff0c\u4ed6\u8ba4\u8bc6\u7684\u4e00\u4f4d\u6076\u9b54\u7ecf\u5e38\u8fd9\u4e48\u8bf4\u3002</p><p>\u4fe1\u606f\u7684\u529b\u91cf\u4e0d\u4ec5\u4ec5\u5728\u4e8e\u6536\u96c6\uff0c\u4e5f\u5728\u4e8e\u6563\u5e03\uff0c\u901a\u8fc7\u6c89\u9ed8\u4e4b\u6b4c\uff0c\u745e\u8d39\u5fb7\u51c6\u786e\u5730\u638c\u63e1\u4e86\u4e00\u6279\u5bf9\u73b0\u6709\u7edf\u6cbb\u4e0d\u751a\u6ee1\u610f\u7684\u5353\u5c14\u6218\u58eb\u7684\u540d\u5355\uff0c\u5e76\u4e14\u901a\u8fc7\u79cd\u79cd\u9014\u5f84\u5c06\u86db\u540e\u6c89\u5bc2\u7684\u6d88\u606f\u544a\u8bc9\u4ed6\u4eec\u3002\u5f53\u7136\uff0c\u4e3a\u4e86\u786e\u4fdd\u4e0d\u8d70\u6f0f\u98ce\u58f0\uff0c\u514b\u52b3\u62d3\u8bf7\u51fa\u4e86\u65af\u5170\u666e\uff0c\u4e00\u4e2a\u9690\u5c45\u5728\u5353\u5c14\u57ce\u5e02\u91cc\u7684\u7075\u5438\u602a\u5deb\u5996\u3002</p><p>\u65af\u5170\u666e\u662f\u5728\u4e00\u767e\u591a\u5e74\u524d\u6765\u5230\u74dc\u7406\u5fb7\u65af\u57ce\u7684\uff0c\u4ed6\u53d8\u6210\u5353\u5c14\uff0c\u6df7\u8ff9\u4e8e\u4e0b\u57ce\u533a\u3002\u4e00\u6b21\u5076\u7136\u7684\u673a\u4f1a\uff0c\u514b\u52b3\u62d3\u53d1\u73b0\u4e86\u4ed6\uff0c\u5c06\u4ed6\u8bf7\u5230\u745e\u8d39\u5fb7\u5bb6\u65cf\uff0c\u5f85\u4e3a\u4e0a\u5bbe\u3002\u4ed6\u4eec\u76f8\u5904\u878d\u6d3d\uff0c\u65af\u5170\u666e\u6697\u4e2d\u5e2e\u4e86\u5deb\u5e08\u5f88\u591a\u5fd9\uff0c\u751a\u81f3\u6551\u8fc7\u4ed6\u7684\u547d\uff0c\u56e0\u6b64\u83b7\u5f97\u4e86\u4fe1\u4efb\u2014\u2014\u4e8b\u5b9e\u4e0a\uff0c\u514b\u52b3\u62d3\u66fe\u7ecf\u4e00\u5ea6\u8ba4\u4e3a\u4ed6\u4eec\u4e4b\u95f4\u5b58\u5728\u7740\u53cb\u8c0a\uff0c\u5f53\u7136\u8fd9\u80af\u5b9a\u662f\u9519\u89c9\uff0c\u5353\u5c14\u4e0d\u53ef\u80fd\u6709\u8fd9\u79cd\u5c5e\u4e8e\u4f4e\u7b49\u79cd\u65cf\u7684\u8f6f\u5f31\u60c5\u611f\u3002</p><p>\u7136\u800c\u8fd9\u6b21\u5deb\u5996\u8f9c\u8d1f\u4e86\u4ed6\u7684\u4fe1\u4efb\u3002</p><p>\u8ba1\u5212\u5728\u79d8\u5bc6\u5730\u8fdb\u884c\u7740\uff0c\u4f4e\u7b49\u751f\u7269\u88ab\u717d\u52a8\u7ec4\u7ec7\u8d77\u6765\uff0c\u5353\u5c14\u6218\u58eb\u4ece\u5bb6\u65cf\u4e2d\u9003\u4ea1\uff0c\u805a\u96c6\u5728\u4e00\u8d77\u3002\u6700\u7ec8\u53db\u4e71\u7206\u53d1\u4e86\uff0c\u867d\u7136\u6bd4\u539f\u8ba1\u5212\u63d0\u524d\u4e86\u51e0\u5929\uff0c\u4f46\u4e5f\u4e0d\u662f\u4ec0\u4e48\u5927\u95ee\u9898\u3002\u514b\u52b3\u62d3\u548c\u4ed6\u7684\u624b\u4e0b\u6682\u65f6\u89e3\u5f00\u4e86\u796d\u7940\u5b66\u9662\u7684\u9b54\u6cd5\u5c4f\u969c\uff0c\u8ba9\u7075\u5438\u602a\u5deb\u5996\u987a\u5229\u5b8c\u6210\u4f20\u9001\u6cd5\u9635\uff0c\u796d\u53f8\u5b66\u9662\u906d\u5230\u4e86\u7a81\u88ad\u3002\u4e0e\u6b64\u540c\u65f6\uff0c\u53e6\u5916\u4e24\u5ea7\u5b66\u9662\u4e2d\u51fa\u73b0\u4e86\u88ab\u53ec\u5524\u800c\u6765\u7684\u6076\u9b54\uff0c\u5f00\u59cb\u5927\u8086\u7834\u574f\uff0c\u6210\u529f\u5730\u5438\u5f15\u4e86\u6ce8\u610f\u529b\uff0c\u8ba9\u4ed6\u4eec\u6ca1\u80fd\u7acb\u523b\u8d76\u53bb\u63f4\u52a9\u7267\u5e08\u4eec\u3002</p><p>\u4e00\u5207\u4f3c\u4e4e\u90fd\u5f88\u987a\u5229\uff0c\u76f4\u5230\u6700\u540e\u7a81\u7136\u9006\u8f6c\u3002</p><p>\u796d\u53f8\u5b66\u9662\u7684\u7267\u5e08\u4eec\u4f3c\u4e4e\u63d0\u524d\u5f97\u5230\u4e86\u98ce\u58f0\uff0c\u6b63\u5168\u526f\u6b66\u88c5\u5730\u7b49\u5f85\u7740\uff0c\u7a81\u88ad\u6ca1\u6709\u8fbe\u5230\u9884\u671f\u7684\u6548\u679c\uff0c\u53cd\u800c\u9677\u5165\u82e6\u6218\uff0c\u800c\u5176\u4ed6\u5bb6\u65cf\u7684\u63f4\u519b\u4e5f\u6765\u5f97\u592a\u5feb\u3002\u66f4\u4ee4\u4eba\u6124\u6012\u7684\u662f\uff0c\u65af\u5170\u666e\u8fdd\u53cd\u4e86\u7ea6\u5b9a\uff0c\u4ed6\u6ca1\u6709\u5c06\u6b8b\u4f59\u7684\u53db\u4e71\u8005\u5e26\u56de\u6765\uff0c\u653e\u4efb\u4ed6\u4eec\u88ab\u6740\u6b7b\uff0c\u81ea\u5df1\u5374\u95ef\u8fdb\u795e\u5ba4\u53bb\u6467\u6bc1\u5723\u7269\u3002</p><p>\u5728\u514b\u52b3\u62d3\u7684\u8ba1\u5212\u91cc\uff0c\u6ca1\u6709\u6700\u540e\u8fd9\u4e00\u6b65\u3002</p><p>\u610f\u6599\u4e4b\u5916\u7684\u53d8\u6545\u8ba9\u514b\u52b3\u62d3\u63aa\u624b\u4e0d\u53ca\uff0c\u4ed6\u654f\u9510\u5730\u5bdf\u89c9\u5230\u4e86\u5371\u9669\u7684\u6c14\u606f\u3002\u745e\u8d39\u5fb7\u5bb6\u65cf\u6709\u56db\u540d\u6210\u5458\u5728\u5deb\u5e08\u5b66\u9662\u62c5\u4efb\u5bfc\u5e08\uff0c\u6709\u4e00\u540d\u6210\u5458\u5728\u683c\u6597\u5b66\u9662\u62c5\u4efb\u6559\u5b98\uff0c\u514b\u52b3\u62d3\u5c06\u4ed6\u4eec\u5168\u90fd\u53ec\u4e86\u56de\u6765\u3002\u6218\u4e89\u7684\u9634\u4e91\u5df2\u7ecf\u538b\u5728\u745e\u8d39\u5fb7\u57ce\u5821\u4e0a\uff0c\u6574\u4e2a\u5bb6\u65cf\u8fdb\u5165\u4e86\u6218\u5907\u72b6\u6001\u3002</p><p>\u7136\u540e\u8be5\u6765\u7684\u7ec8\u4e8e\u6765\u4e86\u3002</p><p>\u53db\u4e71\u5e73\u5b9a\u7684\u7b2c\u4e8c\u5929\uff0c\u83f2\u5c14\u4f26\u4e3b\u6bcd\u53ec\u96c6\u6267\u653f\u8bae\u4f1a\uff0c\u6307\u63a7\u745e\u8d39\u5fb7\u5bb6\u65cf\u6697\u4e2d\u652f\u6301\u53db\u4e71\uff0c\u8981\u6c42\u6267\u884c\u201c\u5ba1\u5224\u201d\u3002\u56e0\u4e3a\u8bc1\u636e\u5e76\u4e0d\u5145\u5206\uff0c\u6267\u653f\u8bae\u4f1a\u7684\u4e8c\u5341\u4e00\u4f4d\u4e3b\u6bcd\u4e89\u5435\u5f97\u5f88\u5389\u5bb3\uff0c\u6700\u7ec8\u6ca1\u6709\u80fd\u901a\u8fc7\u8fd9\u4efd\u63d0\u6848\u3002\u8fd9\u8ba9\u745e\u8d39\u5fb7\u5bb6\u65cf\u4e0a\u4e0a\u4e0b\u4e0b\u90fd\u677e\u4e86\u53e3\u6c14\uff0c\u4f46\u514b\u52b3\u62d3\u77e5\u9053\u4e8b\u60c5\u6ca1\u8fd9\u4e48\u7b80\u5355\u3002</p><p>\u83f2\u5c14\u4f26\u4e3b\u6bcd\u4e0d\u4f1a\u63d0\u51fa\u4e00\u4e2a\u660e\u663e\u4e0d\u4f1a\u88ab\u901a\u8fc7\u7684\u63d0\u6848\uff0c\u90a3\u4f1a\u635f\u5bb3\u5979\u7684\u5a01\u671b\u3002\u4e4b\u6240\u4ee5\u8fd9\u4e48\u505a\uff0c\u4e00\u5b9a\u6709\u5176\u4ed6\u7406\u7531\u2026\u2026\u6216\u8bb8\u662f\u4e00\u6b21\u63a9\u62a4\uff1f</p><p>\u6b63\u5982\u4ed6\u6240\u6599\uff0c\u5c31\u5728\u8868\u51b3\u521a\u521a\u7ed3\u675f\u540e\u534a\u4e2a\u65f6\u8fb0\uff0c\u83f2\u5c14\u4f26\u5bb6\u65cf\u7684\u519b\u961f\u62b5\u8fbe\u745e\u8d39\u5fb7\u5bb6\u7684\u57ce\u5899\u4e0b\uff0c\u663e\u7136\u4ed6\u4eec\u84c4\u8c0b\u5df2\u4e45\u3002\u5982\u679c\u4e0d\u662f\u514b\u52b3\u62d3\u65e9\u6709\u51c6\u5907\uff0c\u80af\u5b9a\u4f1a\u88ab\u6253\u4e00\u4e2a\u63aa\u624b\u4e0d\u53ca\u3002\u5373\u4fbf\u5982\u6b64\uff0c\u60c5\u51b5\u4f9d\u65e7\u4e0d\u5bb9\u4e50\u89c2\u3002</p><p>\u4e3b\u6bcd\u7d27\u6025\u53ec\u5f00\u5bb6\u65cf\u4f1a\u8bae\uff0c\u5546\u8ba8\u5982\u4f55\u5e94\u4ed8\uff0c\u514b\u52b3\u62d3\u548c\u5f80\u5e38\u4e00\u6837\u7ad9\u5728\u65c1\u8fb9\u3002\u4e00\u4e2a\u7070\u888d\u5deb\u5e08\u6025\u5306\u5306\u5730\u4ece\u5916\u9762\u8dd1\u8fdb\u6765\uff0c\u201c\u6211\u4eec\u88ab\u5305\u56f4\u4e86\uff0c\u201d\u4ed6\u62a5\u544a\u8bf4\uff0c\u201c\u654c\u4eba\u7684\u6570\u91cf\u8d85\u51fa\u6211\u4eec\u4e09\u500d\u3002\u201d</p><p>\u514b\u52b3\u62d3\u7684\u773c\u775b\u772f\u7f1d\u8d77\u6765\uff0c\u201c\u83f2\u5c14\u4f26\u5bb6\u65cf\u6ca1\u6709\u8fd9\u4e48\u591a\u4eba\u3002\u201d</p><p>\u201c\u4e0d\u4ec5\u4ec5\u662f\u83f2\u5c14\u4f26\uff0c\u8fd8\u6709\u7c73\u5179\u745e\u56fe\u5c14\u548c\u5361\u6d1b\u62c9\u5bb6\u65cf\u3002\u201d\u5deb\u5e08\u56de\u7b54\uff0c\u7c73\u5179\u745e\u56fe\u5c14\u662f\u7b2c\u4e09\u5bb6\u65cf\uff0c\u5361\u6d1b\u62c9\u662f\u7b2c\u516d\u5bb6\u65cf\uff0c\u5b83\u4eec\u4e00\u76f4\u90fd\u662f\u83f2\u5c14\u4f26\u5bb6\u65cf\u7684\u76df\u53cb\u3002</p><p>\u514b\u52b3\u62d3\u76b1\u8d77\u7709\u5934\u3002</p><p>\u201c\u6267\u653f\u8bae\u4f1a\u5df2\u7ecf\u5426\u51b3\u4e86\u83f2\u5c14\u4f26\u4e3b\u6bcd\u7684\u63d0\u6848\uff0c\u201d\u5bb6\u65cf\u6b66\u6280\u957f\u6012\u6c14\u51b2\u51b2\u5730\u8bf4\uff0c\u4ed6\u7684\u58f0\u97f3\u5f88\u5927\uff0c\u4f46\u8fd9\u66f4\u50cf\u662f\u5728\u63a9\u76d6\u5185\u5fc3\u7684\u80c6\u602f\uff0c\u201c\u5979\u4eec\u8fdd\u53cd\u4e86\u89c4\u5219\uff01\u201d</p><p>\u201c\u6ca1\u6709\u8fdd\u53cd\uff0c\u201d\u514b\u52b3\u62d3\u6c89\u7740\u5730\u8bf4\uff0c\u201c\u6267\u653f\u8bae\u4f1a\u5426\u51b3\u4e86\uff0c\u6240\u4ee5\u6211\u4eec\u4e0d\u4f1a\u906d\u5230\u5ba1\u5224\u2014\u2014\u800c\u5979\u4eec\u4e5f\u5e76\u975e\u4ee5\u6267\u653f\u8bae\u4f1a\u7684\u540d\u4e49\u524d\u6765\u6267\u884c\u5ba1\u5224\uff0c\u8fd9\u4ec5\u4ec5\u53ea\u662f\u4e00\u573a\u5bb6\u65cf\u5185\u6218\u3002\u5f88\u5e38\u89c1\u7684\u4e8b\u60c5\uff0c\u5c31\u50cf\u6211\u4eec\u4ee5\u524d\u505a\u8fc7\u7684\u90a3\u6837\u3002\u201d</p><p>\u5bb6\u65cf\u5185\u6218\u786e\u5b9e\u5f88\u5e38\u89c1\uff0c\u4f46\u8fd9\u6b21\u8fd8\u662f\u6709\u4e9b\u7279\u6b8a\u4e4b\u5904\u7684\u3002\u901a\u5e38\u6765\u8bf4\uff0c\u6218\u4e89\u4f1a\u7531\u4f4e\u4f4d\u5bb6\u65cf\u6311\u8d77\uff0c\u76ee\u7684\u662f\u4e3a\u4e86\u83b7\u5f97\u66f4\u9ad8\u7684\u4f4d\u9636\u3002\u50cf\u73b0\u5728\u8fd9\u6837\uff0c\u7b2c\u4e00\u5bb6\u65cf\u8054\u5408\u7b2c\u4e09\u3001\u7b2c\u516d\u5bb6\u65cf\u6765\u653b\u51fb\u7b2c\u56db\u5bb6\u65cf\uff0c\u5728\u57ce\u5e02\u7684\u5386\u53f2\u4e0a\u8fd8\u662f\u7b2c\u4e00\u6b21\u3002</p><p>\u201c\u6d3e\u5f80\u535a\u514b\u672c\u5bb6\u65cf\u7684\u4fe1\u4f7f\u51fa\u53d1\u4e86\u5417\uff1f\u201d\u4e3b\u6bcd\u95ee\uff0c\u535a\u514b\u672c\u662f\u7b2c\u4e8c\u5bb6\u65cf\uff0c\u548c\u745e\u8d39\u5fb7\u5bb6\u65cf\u662f\u76df\u53cb\u3002</p><p>\u201c\u6050\u6015\u6211\u4eec\u73b0\u5728\u4e0d\u80fd\u6307\u671b\u76df\u53cb\u4e86\uff0c\u201d\u9996\u5e2d\u5deb\u5e08\u9634\u90c1\u5730\u8bf4\uff0c\u201c\u5728\u8fd9\u79cd\u5173\u5934\uff0c\u535a\u514b\u672c\u4e0d\u4f1a\u5192\u76f4\u63a5\u548c\u7b2c\u4e00\u5bb6\u65cf\u5f00\u6218\u7684\u98ce\u9669\u3002\u201d</p><p>\u4ed6\u7684\u8bdd\u8ba9\u5728\u573a\u6240\u6709\u4eba\u7684\u8138\u8272\u90fd\u53d8\u5f97\u96be\u770b\u8d77\u6765\u3002\u201c\u6216\u8bb8\uff0c\u6211\u4eec\u53ef\u4ee5\u548c\u83f2\u5c14\u4f26\u5bb6\u65cf\u8c08\u5224\u3002\u201d\u4e00\u4e2a\u4f4e\u9636\u7267\u5e08\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u5efa\u8bae\u3002</p><p>\u201c\u8c08\u5224\uff1f\u201d\u514b\u52b3\u62d3\u5c06\u76ee\u5149\u8f6c\u5411\u7267\u5e08\uff0c\u4ed6\u7684\u8138\u4e0a\u9732\u51fa\u8d5e\u8d4f\u7684\u8868\u60c5\uff0c\u201c\u8bf4\u4e0b\u53bb\uff0c\u83ab\u82e5\u5c0f\u59d0\u3002\u201d</p><p>\u201c\u6211\u4eec\u53ef\u4ee5\u6682\u65f6\u9000\u51fa\u6267\u653f\u8bae\u4f1a\uff0c\u201d\u7267\u5e08\u5f97\u5230\u9f13\u52b1\uff0c\u5979\u7684\u58f0\u97f3\u4e5f\u53d8\u5f97\u9547\u5b9a\u8d77\u6765\uff0c\u201c\u653e\u5f03\u73b0\u6709\u7684\u5bb6\u65cf\u6392\u540d\uff0c\u9000\u540e\u5230\u672b\u5c3e\u3002\u53cd\u6b63\u2026\u2026\u795e\u540e\u5df2\u7ecf\u4e0d\u5728\uff0c\u8fd9\u4e00\u5207\u5728\u5c06\u6765\u4f1a\u53d8\u5f97\u8d8a\u6765\u8d8a\u4e0d\u91cd\u8981\u4e86\u3002\u201d</p><p>\u514b\u52b3\u62d3\u73af\u89c6\u573a\u5185\uff0c\u53d1\u73b0\u5f88\u591a\u4eba\u5728\u5fae\u5fae\u70b9\u5934\uff0c\u6216\u8005\u9732\u51fa\u8d5e\u540c\u7684\u8868\u60c5\uff0c\u751a\u81f3\u8fde\u4e3b\u6bcd\u90fd\u72b9\u8c6b\u5730\u770b\u7740\u4ed6\uff0c\u663e\u7136\u6709\u4e9b\u5fc3\u52a8\u3002\u786e\u5b9e\uff0c\u6ca1\u6709\u4e86\u7f57\u4e1d\uff0c\u6267\u653f\u8bae\u4f1a\u7684\u5e2d\u4f4d\u4e5f\u597d\uff0c\u5bb6\u65cf\u6392\u540d\u4e5f\u597d\uff0c\u90fd\u5df2\u7ecf\u53d8\u5f97\u4e0d\u90a3\u4e48\u91cd\u8981\u4e86\u3002\u65e7\u7684\u79e9\u5e8f\u4e0d\u53ef\u80fd\u7ee7\u7eed\u7ef4\u6301\uff0c\u5b83\u5fc5\u5c06\u88ab\u6253\u7834\uff0c\u800c\u65b0\u7684\u79e9\u5e8f\u5c1a\u672a\u5efa\u7acb\u3002\u53ea\u8981\u80fd\u4fdd\u5b58\u5b9e\u529b\uff0c\u90a3\u4e48\u4e00\u5207\u90fd\u8fd8\u6709\u673a\u4f1a\u3002</p><p>\u4f46\u9996\u5e2d\u5deb\u5e08\u53f9\u4e86\u53e3\u6c14\uff0c\u6307\u5c16\u9661\u7136\u5f39\u51fa\u4e00\u9053\u95ea\u7535\uff0c\u6253\u5728\u7267\u5e08\u8eab\u4e0a\u3002\u5979\u5927\u53eb\u4e00\u58f0\uff0c\u75db\u82e6\u5730\u6454\u5012\u5728\u5730\u3002</p><p>\u201c\u4e0d\u8981\u5fc3\u5b58\u4fa5\u5e78\uff01\u201d\u514b\u52b3\u62d3\u5389\u58f0\u8bf4\uff0c\u773c\u5149\u4ece\u5728\u573a\u6240\u6709\u4eba\u7684\u8138\u4e0a\u9010\u4e00\u626b\u8fc7\uff0c\u201c\u8fd9\u6b21\u548c\u4ee5\u5f80\u4e0d\u540c\u3002\u6240\u8c13\u53db\u4e71\u7684\u6307\u63a7\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e00\u4e2a\u501f\u53e3\uff0c\u6211\u4eec\u7684\u5b58\u5728\u5df2\u7ecf\u5bf9\u83f2\u5c14\u4f26\u5bb6\u65cf\u6784\u6210\u4e86\u5a01\u80c1\uff0c\u8fd9\u624d\u662f\u8fd9\u573a\u6218\u4e89\u7684\u771f\u6b63\u539f\u56e0\u3002\u5979\u4eec\u754f\u60e7\u6211\u4eec\uff0c\u5bb3\u6015\u6211\u4eec\uff0c\u6240\u4ee5\u8d81\u8fd9\u4e2a\u673a\u4f1a\u53d1\u8d77\u653b\u51fb\u3002\u5982\u679c\u4e0d\u80fd\u8ba4\u6e05\u8fd9\u4e00\u70b9\uff0c\u90a3\u5c31\u7b49\u7740\u53bb\u6df1\u6e0a\u5411\u6211\u4eec\u7684\u795e\u540e\u81f4\u610f\u5427\u3002\u201d</p><p>\u201c\u6211\u4eec\u80fd\u591f\u8d62\u5417\uff1f\u201d\u4e3b\u6bcd\u5f00\u53e3\u95ee\uff0c\u5979\u770b\u8d77\u6765\u5f88\u7d27\u5f20\u3002</p><p>\u201c\u5f53\u7136\uff0c\u201d\u514b\u52b3\u62d3\u4e0d\u5047\u601d\u7d22\u5730\u56de\u7b54\uff0c\u201c\u6211\u4eec\u5f53\u7136\u80fd\u591f\u83b7\u80dc\u3002\u81ea\u4ece\u8fd9\u5ea7\u57ce\u5e02\u5efa\u7acb\u4ee5\u6765\uff0c\u6709\u5f88\u591a\u5bb6\u65cf\u6253\u6211\u4eec\u7684\u4e3b\u610f\uff0c\u4f46\u4ed6\u4eec\u65e0\u4e00\u4f8b\u5916\u90fd\u5931\u8d25\u4e86\uff0c\u4e0d\u662f\u5417\uff1f\u6211\u4eec\u662f\u6700\u7ec8\u7684\u80dc\u5229\u8005\u3002\u6211\u4eec\u62e5\u6709\u6700\u575a\u56fa\u7684\u57ce\u5821\uff0c\u6700\u5b8c\u7f8e\u7684\u9b54\u6cd5\u9632\u5fa1\u4f53\u7cfb\uff0c\u6700\u4f18\u79c0\u7684\u5deb\u5e08\u2026\u2026\u548c\u7267\u5e08\uff0c\u8fd8\u6709\u6700\u82f1\u660e\u777f\u667a\u7684\u9886\u8896\u3002\u201d</p><p>\u4ed6\u6ca1\u6709\u8bf4\u660e\u6700\u540e\u4e00\u53e5\u8bdd\u7684\u771f\u5b9e\u6240\u6307\uff0c\u6216\u8bb8\u662f\u5728\u5938\u5956\u4e3b\u6bcd\uff0c\u6216\u8005\u662f\u5728\u79f0\u8d5e\u81ea\u5df1\u3002\u8c01\u77e5\u9053\u5462\uff0c\u4e0d\u8fc7\u8fd9\u5df2\u7ecf\u4e0d\u91cd\u8981\u4e86\u3002</p><p>\u5ead\u9662\u91cc\u7684\u5996\u706b\u9661\u7136\u9eef\u6de1\u4e0b\u6765\uff0c\u4e00\u56e2\u53c8\u4e00\u56e2\u7684\u9ed1\u6697\u7ed3\u754c\u50cf\u4e4c\u4e91\u4e00\u6837\u4ece\u7a7a\u4e2d\u6f02\u6d6e\u8fc7\u6765\uff0c\u906e\u853d\u4e86\u6574\u4e2a\u745e\u8d39\u5fb7\u57ce\u5821\u3002\u8fd9\u662f\u5353\u5c14\u7684\u793c\u8282\uff0c\u610f\u5473\u7740\u5bb6\u65cf\u6218\u4e89\u5373\u5c06\u6b63\u5f0f\u5f00\u59cb\u4e86\u3002</p></article>"}}}]);
