(window.webpackJsonp=window.webpackJsonp||[]).push([[257],{279:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u5341\u516b\u8282 \u6f5c\u5165",content:"<article><h2>\u7b2c\u4e8c\u5341\u516b\u8282 \u6f5c\u5165</h2><p>\u5f53\u98df\u4eba\u9b54\u62ac\u7740\u653b\u57ce\u69cc\u51b2\u4e0a\u6765\u7684\u65f6\u5019\uff0c\u57ce\u5899\u4e0a\u7bad\u77e2\u5982\u66b4\u96e8\u822c\u503e\u6cfb\u4e0b\u6765\uff0c\u4f46\u5b83\u4eec\u65e0\u4e00\u547d\u4e2d\u3002\u8fd9\u5e76\u975e\u662f\u745e\u8d39\u5fb7\u5bb6\u7684\u6218\u58eb\u4eec\u6280\u827a\u751f\u758f\uff0c\u5f53\u7136\u4e5f\u4e0d\u53ef\u80fd\u662f\u8fd9\u4e9b\u98df\u4eba\u9b54\u6b65\u4f10\u7075\u6d3b\uff0c\u95ea\u907f\u53ca\u65f6\uff0c\u771f\u6b63\u7684\u539f\u56e0\u662f\u5b83\u4eec\u8eab\u524d\u548c\u5934\u4e0a\u76d8\u65cb\u98de\u821e\u7684\u900f\u660e\u76fe\u5f62\u529b\u573a\uff0c\u963b\u9694\u4e86\u6240\u6709\u7bad\u77e2\u7684\u88ad\u51fb\uff0c\u90a3\u662f\u51fa\u53d1\u4e4b\u524d\u7267\u5e08\u4eec\u65bd\u52a0\u7684\u795e\u672f\u4fdd\u62a4\u3002</p><p>\u7f57\u4e1d\u5df2\u7ecf\u4e0d\u518d\u56de\u5e94\u7948\u7977\uff0c\u505c\u6b62\u8d50\u4e88\u795e\u672f\uff0c\u57ce\u4e2d\u7684\u7267\u5e08\u4e4b\u6240\u4ee5\u8fd8\u80fd\u65bd\u6cd5\uff0c\u9760\u7684\u5b8c\u5168\u662f\u4ee5\u524d\u50a8\u5b58\u4e0b\u6765\u7684\u5377\u8f74\u3001\u9b54\u6756\u548c\u5176\u4ed6\u9b54\u6cd5\u7269\u54c1\u3002\u8fd9\u4e9b\u4e1c\u897f\u5982\u4eca\u65e0\u6cd5\u5f97\u5230\u8865\u5145\uff0c\u7528\u4e00\u70b9\u5c31\u5c11\u4e00\u70b9\uff0c\u5728\u8fc7\u53bb\u7684\u4e24\u4e2a\u6708\uff0c\u5c24\u5176\u662f\u524d\u5929\u7684\u53db\u4e71\u4e2d\uff0c\u5df2\u7ecf\u6d88\u8017\u4e86\u5f88\u591a\uff0c\u6240\u4ee5\u5fc5\u987b\u8282\u7ea6\u3002\u4f46\u8fd9\u6b21\u653b\u57ce\u5173\u7cfb\u91cd\u5927\uff0c\u5982\u679c\u6210\u529f\uff0c\u4e00\u5207\u8d23\u4efb\u2014\u2014\u89e6\u6012\u795e\u540e\u3001\u53d1\u52a8\u53db\u4e71\u3001\u6bc1\u574f\u5723\u7269\u7b49\u7b49\u2014\u2014\u90fd\u5c06\u7531\u745e\u8d39\u5fb7\u5bb6\u65cf\u6765\u627f\u62c5\uff0c\u53cd\u6b63\u6b7b\u8005\u6ca1\u6709\u7533\u8fa9\u7684\u6743\u529b\uff1b\u5982\u679c\u6218\u8d25\u7684\u8bdd\uff0c\u90a3\u4e00\u5207\u5c31\u53cd\u8fc7\u6765\u4e86\u3002</p><p>\u6240\u4ee5\u7267\u5e08\u4eec\u5fc5\u987b\u5168\u529b\u4ee5\u8d74\u3002</p><p>\u7cbe\u9510\u7684\u725b\u5934\u602a\u548c\u7c73\u5179\u745e\u56fe\u5c14\u5bb6\u7684\u77ee\u4eba\u5728\u540e\u9762\u5217\u9635\uff0c\u53ea\u7b49\u98df\u4eba\u9b54\u649e\u5f00\u57ce\u95e8\uff0c\u5b83\u4eec\u5c31\u8ddf\u7740\u8702\u62e5\u800c\u5165\u3002\u5353\u5c14\u6218\u58eb\u548c\u8c7a\u72fc\u4eba\u5f13\u7bad\u624b\u4e00\u523b\u4e0d\u505c\u5730\u5411\u57ce\u5934\u5c04\u51fb\uff0c\u51e0\u67b6\u6295\u77f3\u8f66\u88ab\u7ec4\u88c5\u8d77\u6765\uff0c\u8fd9\u53ef\u662f\u7f55\u89c1\u7684\u4e1c\u897f\uff0c\u5bb6\u65cf\u6218\u4e89\u4e2d\u6781\u5c11\u4f1a\u52a8\u7528\u5230\u8fd9\u79cd\u5927\u5bb6\u4f19\uff0c\u663e\u7136\u8fdb\u653b\u8005\u5bc4\u5e0c\u671b\u4e8e\u5b83\u4eec\u80fd\u8f70\u5f00\u745e\u8d39\u5fb7\u5bb6\u7684\u7cbe\u91d1\u57ce\u5899\u3002</p><p>\u4e00\u5207\u5f53\u7136\u4e0d\u4f1a\u8fd9\u6837\u987a\u5229\u53d1\u751f\uff0c\u53cd\u51fb\u5f00\u59cb\u4e86\u3002</p><p>\u4e03\u4e2a\u7a7f\u7740\u7070\u888d\u7684\u745e\u8d39\u5fb7\u5deb\u5e08\u98d8\u4e0a\u534a\u7a7a\uff0c\u4ed6\u4eec\u5f53\u4e2d\u4e24\u4e2a\u62bd\u51fa\u9b54\u6756\uff0c\u5bf9\u51c6\u6b63\u5728\u649e\u51fb\u57ce\u95e8\u7684\u98df\u4eba\u9b54\u5c04\u51fa\u5371\u9669\u7684\u95ea\u7535\uff0c\u5176\u4f59\u4eba\u5219\u5c48\u8d77\u6234\u7740\u5bb6\u5fbd\u7684\u624b\u6307\uff0c\u5ff5\u8bf5\u51fa\u53ec\u5524\u5492\u8bed\uff0c\u540c\u65f6\u5728\u5fc3\u4e2d\u5927\u58f0\u91cd\u590d\u7740\u6076\u9b54\u7684\u771f\u540d\u3002</p><p>\u98df\u4eba\u9b54\u6beb\u65e0\u60ac\u5ff5\u5730\u88ab\u95ea\u7535\u51fb\u4e2d\u4e86\uff0c\u7267\u5e08\u7684\u529b\u573a\u62a4\u76fe\u53ea\u80fd\u591f\u7528\u4e8e\u963b\u9694\u7bad\u77e2\uff0c\u6ca1\u529e\u6cd5\u62b5\u6321\u9b54\u6cd5\u3002\u8fd9\u79cd\u81ed\u6c14\u54c4\u54c4\u7684\u602a\u7269\u8eab\u5f3a\u4f53\u58ee\uff0c\u5bf9\u4f24\u5bb3\u7684\u5fcd\u8010\u80fd\u529b\u662f\u5353\u5c14\u7684\u51e0\u500d\uff0c\u6240\u4ee5\u4e00\u4e24\u9053\u95ea\u7535\u5e76\u4e0d\u80fd\u4ee4\u5b83\u4eec\u5012\u4e0b\uff0c\u4f46\u745e\u8d39\u5fb7\u5deb\u5e08\u51c6\u5907\u5145\u5206\uff0c\u9b54\u6756\u91cc\u4e0d\u65ad\u8ff8\u51fa\u8000\u773c\u94f6\u86c7\uff0c\u56db\u4e94\u9053\u3001\u4e03\u516b\u9053\uff0c\u602a\u7269\u4eec\u5c31\u627f\u53d7\u4e0d\u4f4f\u4e86\u3002</p><p>\u4e24\u4e2a\u98df\u4eba\u9b54\u88ab\u6740\uff0c\u5176\u4e2d\u4e00\u4e2a\u662f\u91cd\u4f24\u4e4b\u4e0b\u6389\u8fdb\u4e86\u62a4\u57ce\u6cb3\uff0c\u5b83\u60e8\u53eb\u4e00\u58f0\u5c31\u88ab\u6d53\u5bc6\u7684\u70df\u96fe\u541e\u566c\u8fdb\u53bb\uff0c\u6d88\u5931\u5f97\u65e0\u5f71\u65e0\u8e2a\uff0c\u53e6\u5916\u4e00\u4e2a\u5219\u88ab\u95ea\u7535\u70b8\u6210\u4e86\u788e\u7247\u3002\u5269\u4f59\u56db\u4e2a\u98df\u4eba\u9b54\u8c03\u6574\u4f4d\u7f6e\uff0c\u4f9d\u65e7\u62ac\u7740\u653b\u57ce\u69cc\u649e\u51fb\u57ce\u95e8\uff0c\u5b83\u4eec\u4e0d\u6562\u540e\u9000\uff0c\u56e0\u4e3a\u90a3\u5fc5\u6b7b\u65e0\u7591\u3002\u653b\u57ce\u69cc\u5f88\u91cd\uff0c\u4f46\u56db\u4e2a\u98df\u4eba\u9b54\u5012\u4e5f\u52c9\u5f3a\u80fd\u591f\u8d1f\u8377\uff0c\u7cbe\u91d1\u94f8\u6210\u7684\u57ce\u95e8\u5728\u4e0d\u65ad\u7684\u649e\u51fb\u4e4b\u4e0b\u9690\u7ea6\u6709\u4e9b\u53d8\u5f62\u4e86\u3002</p><p>\u6b63\u5728\u6b64\u65f6\uff0c\u5934\u9876\u98ce\u58f0\u5927\u4f5c\uff0c\u4e00\u4e2a\u53cd\u5e94\u6700\u5feb\u7684\u98df\u4eba\u9b54\u5306\u5fd9\u95f4\u62ac\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u51e0\u53ea\u534a\u4eba\u534a\u79c3\u9e70\u7684\u602a\u7269\u51cc\u7a7a\u6251\u4e0b\uff0c\u6cdb\u7740\u91d1\u5c5e\u5149\u6cfd\u7684\u5229\u722a\u5982\u94a9\uff0c\u5b83\u8fd8\u6765\u4e0d\u53ca\u8eb2\u907f\uff0c\u5c31\u611f\u89c9\u5934\u9876\u4e00\u9635\u5267\u75db\uff0c\u7d27\u63a5\u7740\u9ed1\u6697\u964d\u4e34\u3002</p><p>\u5728\u5492\u8bed\u7684\u53ec\u5524\u4e0b\uff0c\u57ce\u5899\u4e0a\u7684\u5f17\u6d1b\u9b54\u96d5\u50cf\u7eb7\u7eb7\u6d3b\u52a8\u8d77\u6765\uff0c\u5c55\u5f00\u5e9e\u5927\u7684\u7fc5\u8180\u98de\u817e\u5728\u7a7a\u4e2d\uff0c\u7136\u540e\u4fef\u51b2\u800c\u4e0b\uff0c\u53ea\u4e00\u77ac\u95f4\uff0c\u56db\u53ea\u98df\u4eba\u9b54\u5c31\u5168\u90fd\u5934\u9885\u7834\u88c2\uff0c\u8111\u6d46\u8ff8\u6d41\uff0c\u4e00\u547d\u545c\u547c\uff0c\u653b\u57ce\u69cc\u6389\u8fdb\u4e86\u62a4\u57ce\u6cb3\u91cc\uff0c\u6d88\u5931\u4e0d\u89c1\u3002\u5f17\u6d1b\u9b54\u4eec\u518d\u6b21\u98de\u8d77\uff0c\u671d\u5c1a\u672a\u7ec4\u88c5\u5b8c\u6210\u7684\u6295\u77f3\u8f66\u51b2\u53bb\u3002</p><p>\u5854\u62c9\u590f\xb7\u7c73\u5179\u745e\u56fe\u5c14\u7ad9\u5728\u6295\u77f3\u8f66\u8fb9\u6307\u6325\u7740\u77ee\u4eba\u4eec\uff0c\u5979\u662f\u7b2c\u4e09\u5bb6\u65cf\u7684\u957f\u5973\uff0c\u796d\u53f8\u5b66\u9662\u7684\u6559\u5b98\uff0c\u8170\u95f4\u7684\u4e03\u9996\u86c7\u97ad\u8bf4\u660e\u5979\u540c\u6837\u662f\u4e00\u540d\u9ad8\u9636\u7267\u5e08\u3002\u6b63\u5fd9\u788c\u95f4\uff0c\u731b\u7136\u542c\u89c1\u8eab\u8fb9\u7684\u4f8d\u536b\u60ca\u547c\uff0c\u6559\u5b98\u62ac\u5934\u770b\u53bb\uff0c\u53d1\u73b0\u4e94\u4e2a\u5f17\u6d1b\u9b54\u6b63\u671d\u8fd9\u8fb9\u6251\u8fc7\u6765\u3002</p><p>\u7c73\u5179\u745e\u56fe\u5c14\u6beb\u4e0d\u60ca\u614c\uff0c\u5979\u63e1\u7d27\u5723\u5fbd\uff0c\u5728\u610f\u5ff5\u4e2d\u5411\u5f17\u6d1b\u9b54\u53d1\u51fa\u65e0\u58f0\u7684\u547d\u4ee4\u5b57\u7b26\u3002\u201c\u505c\u4e0b\uff01\u201d\u5979\u5389\u58f0\u8bf4\uff0c\u201c\u4f60\u4eec\u73b0\u5728\u9762\u5bf9\u7684\u662f\u4e00\u4f4d\u795e\u540e\u7684\u9ad8\u9636\u796d\u53f8\u3002\u201d</p><p>\u745e\u8d39\u5fb7\u5bb6\u65cf\u7684\u5deb\u5e08\u64c5\u957f\u548c\u4f4e\u5c42\u754c\u5996\u9b54\uff08\u5305\u62ec\u5730\u72f1\u7684\u9b54\u9b3c\u548c\u6df1\u6e0a\u7684\u6076\u9b54\uff0c\u4ee5\u53ca\u5176\u4ed6\uff09\u6253\u4ea4\u9053\uff0c\u4f46\u7f57\u4e1d\u7684\u7267\u5e08\u5728\u8fd9\u65b9\u9762\u540c\u6837\u4e5f\u662f\u4e13\u4e1a\u4eba\u58eb\u3002\u8718\u86db\u5973\u795e\u5c45\u4f4f\u5728\u6df1\u6e0a\u4e2d\uff0c\u53c8\u88ab\u5c0a\u79f0\uff08\u6216\u8005\u662f\u8d2c\u79f0\uff09\u4e3a\u6076\u9b54\u5973\u795e\uff0c\u5979\u7684\u624b\u4e0b\u6709\u5927\u91cf\u7684\u6076\u9b54\uff0c\u670d\u52a1\u4e8e\u5979\uff0c\u4e3a\u5979\u62c5\u4efb\u4f8d\u536b\u3001\u58eb\u5175\u6216\u8005\u4f7f\u8005\uff0c\u6700\u8457\u540d\u7684\u662f\u8721\u878d\u5996\u3002\u8fd9\u79cd\u559c\u6b22\u4e0d\u65ad\u53d8\u5e7b\u5f62\u4f53\uff0c\u5728\u5927\u591a\u6570\u65f6\u5019\u770b\u8d77\u6765\u50cf\u662f\u4e00\u53ea\u71c3\u70e7\u6ef4\u6cea\u7684\u8721\u70db\u7684\u6076\u9b54\u88ab\u5c0a\u79f0\u4e3a\u201c\u7f57\u4e1d\u7684\u4f8d\u5973\u201d\uff0c\u662f\u6240\u6709\u5353\u5c14\u90fd\u8033\u719f\u80fd\u8be6\u7684\u5b58\u5728\uff0c\u5728\u5404\u79cd\u548c\u795e\u540e\u76f8\u5173\u7684\u4eea\u5f0f\u5e86\u5178\u4e0a\u90fd\u53ef\u80fd\u51fa\u73b0\u3002\u5f53\u7f57\u4e1d\u4e0d\u60f3\u4eb2\u81ea\u51fa\u9762\u7684\u65f6\u5019\uff0c\u8721\u878d\u5996\u5c31\u662f\u5979\u7684\u5b98\u65b9\u4ee3\u8a00\u4eba\u3002\u56e0\u4e3a\u8fd9\u79cd\u7f18\u6545\uff0c\u7f57\u4e1d\u7684\u7267\u5e08\u5bf9\u6076\u9b54\u6beb\u4e0d\u964c\u751f\uff0c\u5979\u4eec\u5728\u796d\u53f8\u5b66\u9662\u91cc\u63a5\u53d7\u8fc7\u5927\u91cf\u8bad\u7ec3\uff0c\u80fd\u591f\u548c\u6076\u9b54\u4ea4\u6d41\uff0c\u80fd\u591f\u7528\u5404\u79cd\u5f3a\u5927\u7684\u5492\u8bed\u53ec\u5524\u6355\u6349\u6076\u9b54\uff0c\u6216\u8005\u5c06\u5b83\u4eec\u9a71\u9010\u56de\u6df1\u6e0a\uff0c\u751a\u81f3\u62d8\u675f\u5b83\u4eec\u4f5c\u4e3a\u5bb6\u65cf\u62a4\u536b\u3002\u5982\u679c\u773c\u524d\u7684\u8fd9\u51e0\u4e2a\u5f17\u6d1b\u9b54\u4e5f\u662f\u5982\u6b64\u7684\u8bdd\uff0c\u90a3\u4e48\u5b83\u4eec\u5e94\u8be5\u4f1a\u5bf9\u7f57\u4e1d\u7267\u5e08\u6709\u6240\u656c\u754f\u3002</p><p>\u542c\u5230\u7c73\u5179\u745e\u56fe\u5c14\u7684\u8b66\u544a\uff0c\u5f17\u6d1b\u9b54\u56de\u5e94\u4ee5\u523a\u8033\u7684\u72c2\u7b11\uff0c\u4ee5\u53ca\u4e00\u7ec4\u6beb\u65e0\u903b\u8f91\u53ef\u8a00\u7684\u6df7\u4e71\u566a\u58f0\uff0c\u4fef\u51b2\u4e1d\u6beb\u6ca1\u6709\u505c\u987f\u3002\u201c\u5b83\u4eec\u4e0d\u662f\u795e\u540e\u7684\u4ec6\u4eba\uff01\u201d\u65c1\u8fb9\u4e00\u4f4d\u7267\u5e08\u5927\u58f0\u8bf4\uff0c\u5979\u4e5f\u521a\u521a\u5b8c\u6210\u4e86\u4e00\u6b21\u5931\u8d25\u7684\u4ea4\u6d41\uff0c\u201c\u5b83\u4eec\u4e0d\u542c\u4ece\u547d\u4ee4\u3002\u201d</p><p>\u201c\u603b\u662f\u5bf9\u663e\u800c\u6613\u89c1\u7684\u4e8b\u5b9e\u53d1\u8868\u8bc4\u8bba\uff0c\u4f1a\u964d\u4f4e\u4f60\u7684\u667a\u5546\uff0c\u201d\u7c73\u5179\u745e\u56fe\u5c14\u8bf4\uff0c\u5de6\u624b\u4ece\u8170\u95f4\u62d4\u51fa\u86c7\u9996\u97ad\uff0c\u53f3\u624b\u4e2d\u6084\u6084\u5c55\u5f00\u4e00\u5f20\u5377\u8f74\uff0c\u201c\u6740\u4e86\u5b83\u4eec\u3002\u201d</p><p>\u5979\u5feb\u901f\u5ff5\u5b8c\u5377\u8f74\uff0c\u638c\u5fc3\u6563\u53d1\u51fa\u523a\u773c\u7684\u7ea2\u5149\uff0c\u5c31\u50cf\u662f\u63e1\u4f4f\u4e86\u4e00\u9897\u53d1\u4eae\u7684\u7ea2\u5b9d\u77f3\uff0c\u65e0\u6570\u9053\u7ec6\u5fae\u7684\u4e1d\u7ebf\u4ece\u4e2d\u6e38\u79bb\u800c\u51fa\uff0c\u5c06\u6b63\u5bf9\u9762\u7684\u4e00\u53ea\u5f17\u6d1b\u9b54\u5305\u88f9\u8d77\u6765\uff0c\u5c31\u50cf\u4e00\u53ea\u706b\u7ea2\u7684\u8695\u8327\u3002\u6076\u9b54\u5927\u58f0\u543c\u53eb\u7740\uff0c\u5de6\u51b2\u53f3\u7a81\uff0c\u7136\u800c\u4e0d\u80fd\u6446\u8131\u3002\u7267\u5e08\u4ece\u4ee3\u8868\u796d\u53f8\u5b66\u9662\u6559\u5b98\u8eab\u4efd\u7684\u9879\u94fe\u4e0a\u53d6\u4e0b\u4e00\u679a\u9ed1\u73cd\u73e0\uff0c\u6258\u5728\u638c\u5fc3\uff0c\u201c\u5361\u6ce2\xb7\u82cf\xb7\u5f25\u6492\u83f2\uff01\u201d\u5979\u5ff5\u8bf5\u7740\u5492\u8bed\uff0c\u9ed1\u73cd\u73e0\u4e0a\u53d1\u51fa\u4e00\u9053\u4e4c\u5149\uff0c\u51c6\u786e\u5730\u51fb\u4e2d\u4e86\u5f17\u6d1b\u9b54\u3002</p><p>\u6076\u9b54\u7684\u4e00\u53ea\u7fc5\u8180\u71c3\u70e7\u8d77\u6765\uff0c\u77ac\u95f4\u5316\u4f5c\u7c89\u672b\uff0c\u8fd9\u4ee4\u5b83\u5931\u53bb\u4e86\u5e73\u8861\uff0c\u4ece\u7a7a\u4e2d\u6454\u843d\uff0c\u8eab\u4e0a\u4f9d\u65e7\u88f9\u7740\u706b\u7ea2\u7684\u9b54\u6cd5\u8327\u3002\u624b\u6301\u957f\u67aa\u7684\u5353\u5c14\u6218\u58eb\u4e0a\u524d\u6512\u523a\uff0c\u5f88\u5feb\u5f17\u6d1b\u9b54\u5e9e\u5927\u7684\u8eab\u8eaf\u5c31\u5316\u4f5c\u4e00\u56e2\u6c14\u4f53\uff0c\u6f02\u6d6e\u7740\uff0c\u56de\u5230\u57ce\u5899\u4e0a\uff0c\u4f9d\u65e7\u53d8\u6210\u6c89\u9ed8\u7684\u96d5\u50cf\u3002\u6076\u9b54\u5e76\u6ca1\u6709\u771f\u6b63\u6b7b\u4ea1\uff0c\u5b83\u7684\u672c\u4f53\u5728\u6df1\u6e0a\uff0c\u521a\u624d\u88ab\u6740\u6b7b\u7684\u53ea\u662f\u7269\u8d28\u754c\u7684\u6295\u5f71\u3002\u53ea\u8981\u4f11\u606f\u8db3\u591f\u957f\u7684\u65f6\u95f4\u2014\u2014\u901a\u5e38\u662f\u4e8c\u5341\u56db\u4e2a\u65f6\u8fb0\u2014\u2014\u5b83\u5c31\u80fd\u591f\u88ab\u745e\u8d39\u5fb7\u5bb6\u7684\u5deb\u5e08\u518d\u5ea6\u53ec\u5524\uff0c\u524d\u6765\u52a9\u6218\u3002</p><p>\u5f53\u7136\uff0c\u90a3\u65f6\u5019\u8fd9\u573a\u6218\u4e89\u80af\u5b9a\u65e9\u5c31\u7ed3\u675f\u4e86\u3002</p><p>\u5176\u4ed6\u7267\u5e08\u4e0d\u80fd\u5982\u7c73\u5179\u745e\u56fe\u5c14\u8fd9\u6837\u8f7b\u677e\uff0c\u82b1\u8d39\u4e86\u5f88\u5927\u529b\u6c14\uff0c\u4e94\u53ea\u5f17\u6d1b\u9b54\u7ec8\u4e8e\u88ab\u5168\u90e8\u6253\u56de\u4e86\u96d5\u50cf\u539f\u5f62\uff0c\u5176\u95f4\u635f\u5931\u4e86\u4e09\u67b6\u6295\u77f3\u8f66\u548c\u51e0\u540d\u5353\u5c14\u6218\u58eb\u3002\u607c\u6012\u7684\u7267\u5e08\u4eec\u518d\u5ea6\u53d1\u8d77\u653b\u51fb\uff0c\u53c8\u4e00\u961f\u98df\u4eba\u9b54\u62ac\u7740\u653b\u57ce\u69cc\u8e0f\u8fc7\u6865\uff0c\u51b2\u5411\u57ce\u95e8\uff0c\u7ec4\u88c5\u5b8c\u6210\u7684\u6295\u77f3\u673a\u4e5f\u5f00\u59cb\u5de5\u4f5c\u3002\u745e\u8d39\u5fb7\u5bb6\u65cf\u7684\u5deb\u5e08\u4eec\u5219\u7528\u6cd5\u672f\u53cd\u51fb\uff0c\u4e00\u573a\u62c9\u952f\u6218\u5c31\u6b64\u5c55\u5f00\u3002\u4e00\u65f6\u4e4b\u95f4\uff0c\u8c01\u4e5f\u770b\u4e0d\u5230\u80dc\u5229\u7684\u5e0c\u671b\u3002</p><p>\u203b\u203b\u203b</p><p>\u743c\u6069\u63e1\u7d27\u7ef4\u5eb7\u5c3c\u4e9a\u7684\u624b\u8155\uff0c\u5e26\u7740\u5979\u5c0f\u5fc3\u5730\u6cbf\u7740\u62a4\u57ce\u6cb3\u98de\u884c\uff0c\u6765\u5230\u745e\u8d39\u5fb7\u5b85\u9662\u7684\u5de6\u540e\u65b9\uff0c\u8fd9\u91cc\u8d34\u7740\u9661\u5ced\u60ac\u5d16\uff0c\u5f88\u96be\u88ab\u654c\u4eba\u4ece\u6b64\u653b\u51fb\u3002\u901a\u5e38\u6765\u8bf4\uff0c\u8fd9\u4e2a\u4f4d\u7f6e\u88ab\u7528\u4e8e\u5b89\u7f6e\u5750\u9a91\uff08\u8725\u8734\u6216\u8005\u8718\u86db\uff09\uff0c\u4f46\u745e\u8d39\u5fb7\u5bb6\u65cf\u6ca1\u6709\u9a91\u58eb\uff0c\u53ea\u6709\u6b65\u5175\uff0c\u6240\u4ee5\u8fd9\u91cc\u73b0\u5728\u662f\u755c\u680f\uff0c\u517b\u7740\u5f88\u591a\u6d1b\u65af\u517d\u3002\u8fd9\u79cd\u52a8\u7269\u6027\u60c5\u6e29\u987a\uff0c\u770b\u8d77\u6765\u5f88\u50cf\u5730\u8868\u4e16\u754c\u7684\u725b\uff0c\u8089\u7684\u5473\u9053\u4e5f\u5f88\u8fd1\u4f3c\uff0c\u4f46\u66f4\u7ec6\u5ae9\u53ef\u53e3\uff0c\u662f\u5353\u5c14\u4eec\u6700\u4e3b\u8981\u7684\u8089\u98df\u6765\u6e90\u3002</p><p>\u5f53\u7136\uff0c\u518d\u597d\u7684\u5bb6\u755c\u7ec8\u7a76\u4e5f\u662f\u52a8\u7269\uff0c\u8fd9\u4e48\u591a\u6d1b\u65af\u517d\u6324\u5728\u4e00\u8d77\uff0c\u5473\u9053\u81ea\u7136\u5f88\u96be\u95fb\u3002\u743c\u6069\u770b\u7740\u7ef4\u5eb7\u5c3c\u4e9a\u7684\u8138\u8272\uff0c\u7531\u6d45\u6de1\u7684\u9634\u5f71\u53d8\u6210\u4e86\u6c89\u91cd\u7684\u7070\u6697\uff0c\u8fd9\u8bf4\u660e\u5979\u7684\u611f\u89c9\u5f88\u7cdf\u7cd5\u3002\u4ed6\u5e86\u5e78\u81ea\u5df1\u73b0\u5728\u662f\u865a\u4f53\uff0c\u611f\u53d7\u4e0d\u5230\u7269\u8d28\u754c\u7684\u6c14\u5473\u3002</p><p>\u201c\u6211\u4eec\u79bb\u5f00\u3002\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u8bf4\u3002</p><p>\u201c\u8fd9\u91cc\u5f88\u53ef\u80fd\u662f\u9632\u5fa1\u6700\u8584\u5f31\u7684\u5730\u65b9\uff0c\u201d\u743c\u6069\u89e3\u91ca\uff0c\u201c\u6211\u4eec\u5f97\u4ece\u6b64\u8fdb\u5165\u3002\u201d</p><p>\u743c\u6069\u6b64\u65f6\u662f\u865a\u4f53\u72b6\u6001\uff0c\u4ed6\u8bf4\u8bdd\u7ef4\u5eb7\u5c3c\u4e9a\u65e0\u6cd5\u542c\u89c1\uff0c\u4f46\u5e76\u4e0d\u662f\u4ea4\u6d41\u7684\u969c\u788d\uff0c\u56e0\u4e3a\u4ed6\u4eec\u90fd\u4f69\u6234\u4e86\u5370\u6709\u83f2\u5c14\u4f26\u5bb6\u5fbd\u7684\u767d\u91d1\u80f8\u9488\u3002\u5f53\u743c\u6069\u8bf4\u8bdd\u65f6\uff0c\u4ed6\u4f69\u5728\u6597\u7bf7\u4e0a\u7684\u80f8\u9488\u4fbf\u4f1a\u8f7b\u5fae\u5730\u95ea\u70c1\u7740\uff0c\u5c06\u8baf\u606f\u4f20\u9012\u5230\u7ef4\u5eb7\u5c3c\u4e9a\u7684\u80f8\u9488\u4e0a\uff0c\u4ee4\u5979\u542c\u89c1\uff0c\u53ea\u8981\u4e24\u4eba\u4e4b\u95f4\u7684\u8ddd\u79bb\u8db3\u591f\u8fd1\uff0c\u8baf\u606f\u4f20\u9012\u5c31\u53ef\u4ee5\u987a\u5229\u5b8c\u6210\u3002\u53cd\u8fc7\u6765\u5c31\u4e0d\u9700\u8981\u8fd9\u4e48\u9ebb\u70e6\u4e86\uff0c\u743c\u6069\u80fd\u591f\u76f4\u63a5\u542c\u5230\u7ef4\u5eb7\u5c3c\u4e9a\u8bf4\u8bdd\u3002</p><p>\u53ef\u60dc\u4ed6\u8fd8\u6ca1\u6765\u5f97\u53ca\u5b66\u4f1a\u5353\u5c14\u90a3\u5957\u7cbe\u81f4\u590d\u6742\u7684\u624b\u8bed\uff0c\u5426\u5219\u4e8b\u60c5\u5c31\u7b80\u5355\u591a\u4e86\u3002</p><p>\u5ffd\u7565\u6389\u5b9e\u4f53\u7684\u5efa\u7b51\uff0c\u7a7a\u6c14\u91cc\u53ea\u5269\u4e0b\u4ea4\u9519\u7eb7\u7ec7\u7684\u9b54\u6cd5\u7075\u7ebf\uff0c\u6784\u6210\u4e86\u4e00\u4e2a\u4e25\u5bc6\u7684\u5de8\u5927\u7403\u5f62\uff0c\u5c06\u6574\u4e2a\u745e\u8d39\u5fb7\u57ce\u5821\u4fdd\u62a4\u5176\u4e2d\u3002\u743c\u6069\u6f02\u6d6e\u7740\uff0c\u8d34\u7740\u9b54\u6cd5\u9635\u7684\u8fb9\u6cbf\uff0c\u8bd5\u7740\u5bfb\u627e\u7834\u7efd\uff0c\u4ed6\u731c\u60f3\u8fd9\u91cc\u65e2\u7136\u9760\u8fd1\u60ac\u5d16\uff0c\u5e73\u65f6\u4e0d\u4f1a\u906d\u5230\u653b\u51fb\uff0c\u6216\u8bb8\u5e03\u7f6e\u9b54\u6cd5\u9635\u7684\u65f6\u5019\u4e5f\u4f1a\u6709\u6240\u677e\u61c8\u624d\u5bf9\u3002\u7136\u800c\u7ed3\u679c\u4ee4\u4ed6\u5931\u671b\uff0c\u745e\u8d39\u5fb7\u5bb6\u65cf\u7684\u5deb\u5e08\u663e\u7136\u975e\u5e38\u5c3d\u804c\u5c3d\u8d23\uff0c\u6ca1\u6709\u7559\u4e0b\u534a\u70b9\u6f0f\u6d1e\u3002</p><p>\u90a3\u5c31\u53ea\u80fd\u5f3a\u884c\u7a81\u7834\u4e86\uff0c\u597d\u5728\u8fd9\u6bd5\u7adf\u662f\u6700\u5916\u56f4\uff0c\u96be\u5ea6\u5e76\u4e0d\u9ad8\u3002\u800c\u4e14\u56e0\u4e3a\u4e0d\u4f1a\u88ab\u76f4\u63a5\u653b\u51fb\uff0c\u6240\u4ee5\u6ca1\u6709\u5deb\u5e08\u9a7b\u5b88\uff0c\u53ea\u6709\u8fdc\u5904\u51e0\u4e2a\u5353\u5c14\u6218\u58eb\u5728\u7267\u5e08\u7684\u7387\u9886\u4e0b\u5de1\u903b\uff0c\u8fd9\u70b9\u6bd4\u8f83\u4ee4\u4eba\u9ad8\u5174\u3002</p><p>\u743c\u6069\u4ed4\u7ec6\u89c2\u5bdf\u7740\uff0c\u5728\u4e94\u989c\u516d\u8272\u7684\u7075\u7ebf\u548c\u95ea\u70c1\u7684\u7b26\u6587\u4e2d\u8010\u5fc3\u641c\u7d22\u3002\u773c\u524d\u7684\u8fd9\u4e2a\u9b54\u6cd5\u9635\uff0c\u57fa\u672c\u6784\u9020\u662f\u201c\u5faa\u73af\u201d\uff0c\u65e0\u6570\u4e2a\u5c0f\u7684\u5faa\u73af\u8054\u5408\u6210\u8f83\u5927\u7684\u5faa\u73af\uff0c\u5c42\u5c42\u9012\u8fdb\uff0c\u76f4\u81f3\u6700\u540e\u878d\u5408\u4e3a\u4e00\u3002\u5982\u6b64\u4e00\u6765\uff0c\u4efb\u4f55\u4e00\u70b9\u88ab\u653b\u51fb\uff0c\u5176\u4ed6\u5730\u65b9\u7684\u9b54\u6cd5\u80fd\u91cf\u90fd\u4f1a\u7acb\u523b\u503e\u659c\u8fc7\u6765\uff0c\u53d1\u52a8\u53cd\u6251\uff0c\u6240\u4ee5\u8fd9\u6b21\u4e09\u5927\u5bb6\u65cf\u7684\u5deb\u5e08\u5fc5\u987b\u540c\u65f6\u884c\u52a8\u3002\u65e2\u7136\u662f\u5faa\u73af\uff0c\u90a3\u4e48\u5c31\u6709\u7ebf\u8def\uff0c\u627e\u5230\u5173\u952e\u7684\u5730\u65b9\uff0c\u622a\u65ad\u7ebf\u8def\uff0c\u5c31\u80fd\u7834\u89e3\u8fd9\u4e2a\u5faa\u73af\u3002</p><p>\u4ed6\u6700\u7ec8\u53d1\u73b0\u4e86\u76ee\u6807\u3002</p><p>\u90a3\u662f\u4e09\u4e2a\u5448\u6b63\u4e09\u89d2\u5f62\u6392\u5217\u7684\u7b26\u6587\uff0c\u5206\u522b\u662f\u4e4c\u9ed1\u3001\u6697\u91d1\u548c\u94f6\u7070\u8272\uff0c\u56fe\u6848\u7e41\u590d\u800c\u8be1\u5f02\uff0c\u4f3c\u4e4e\u662f\u67d0\u79cd\u53e4\u8001\u7684\u8c61\u5f62\u6587\u5b57\uff0c\u743c\u6069\u8fa8\u8ba4\u4e0d\u51fa\u3002\u201c\u4f60\u80fd\u8bfb\u61c2\u5417\uff1f\u201d\u4ed6\u5728\u7a7a\u6c14\u4e2d\u52fe\u52d2\u51fa\u8f6e\u5ed3\uff0c\u8be2\u95ee\u7ef4\u5eb7\u5c3c\u4e9a\u3002</p><p>\u201c\u2026\u2026\u6b7b\u4ea1\u2026\u2026\u6df1\u6e0a\u2026\u2026\u5bd2\u51b7\u2026\u2026\u201d\u5353\u5c14\u5c11\u5973\u52aa\u529b\u8fa8\u8ba4\u7740\u9690\u85cf\u5728\u82b1\u7eb9\u4e2d\u7684\u8baf\u606f\uff0c\u201c\u6700\u540e\u4e00\u4e2a\u8bcd\u597d\u50cf\u662f\u6c89\u8ff7\u2026\u2026\u201d</p><p>\u743c\u6069\u70b9\u70b9\u5934\uff0c\u4ed6\u53c8\u89c2\u5bdf\u4e86\u4e00\u4f1a\uff0c\u786e\u5b9a\u8fd9\u4e09\u4e2a\u7b26\u6587\u662f\u6784\u6210\u5468\u56f4\u4e00\u4e2a\u5c0f\u8303\u56f4\u5faa\u73af\u7684\u5173\u952e\u70b9\u6240\u5728\u3002\u5982\u679c\u80fd\u591f\u76f4\u63a5\u5c06\u5b83\u4eec\u6d88\u9664\uff0c\u90a3\u4e48\u8fd9\u4e2a\u5faa\u73af\u5c31\u4f1a\u6682\u65f6\u51fa\u73b0\u4e00\u4e2a\u7f3a\u53e3\uff0c\u76f4\u5230\u6709\u5deb\u5e08\u524d\u6765\u91cd\u65b0\u6784\u5efa\u3002\u8fd9\u4e48\u505a\u81ea\u7136\u662f\u6700\u4f73\u65b9\u6848\uff0c\u4f46\u743c\u6069\u77e5\u9053\u81ea\u5df1\u80af\u5b9a\u529e\u4e0d\u5230\uff0c\u4ed6\u5e76\u4e0d\u7cbe\u64c5\u5492\u6cd5\uff0c\u800c\u4e14\u8fde\u8fd9\u51e0\u4e2a\u7b26\u6587\u7684\u51c6\u786e\u542b\u4e49\u90fd\u5f04\u4e0d\u6e05\u695a\u3002</p><p>\u53ea\u80fd\u56de\u907f\u3002</p><p>\u5deb\u5e08\u4f38\u51fa\u4e00\u6839\u624b\u6307\uff0c\u9ed8\u8bf5\u5492\u8bed\uff0c\u6de1\u84dd\u8272\u7684\u9b54\u6cd5\u80fd\u91cf\u5728\u4ed6\u7684\u6307\u5c16\u805a\u96c6\uff0c\u5f62\u6210\u5f39\u73e0\u5927\u5c0f\u7684\u7403\u4f53\u3002\u743c\u6069\u7528\u610f\u5ff5\u64cd\u7eb5\u7740\u80fd\u91cf\u7403\uff0c\u7f13\u6162\u5411\u524d\u79fb\u52a8\uff0c\u6700\u540e\u7a81\u7136\u52a0\u901f\uff0c\u649e\u5728\u4e4c\u9ed1\u8272\u7b26\u6587\u548c\u94f6\u7070\u8272\u7b26\u6587\u4e4b\u95f4\u7684\u9b54\u6cd5\u7075\u7ebf\u4e2d\u6bb5\u3002</p><p>\u7075\u7ebf\u9aa4\u7136\u5f2f\u66f2\u5230\u6781\u9650\uff0c\u968f\u5373\u65ad\u88c2\u5f00\u6765\uff0c\u539f\u672c\u6d41\u7545\u7684\u5faa\u73af\u51fa\u73b0\u4e86\u6682\u65f6\u7684\u4e2d\u65ad\uff0c\u94f6\u7070\u8272\u7b26\u6587\u7684\u5149\u8292\u77ac\u95f4\u9eef\u6de1\u4e86\u4e0b\u53bb\uff0c\u4e25\u5bc6\u7684\u9632\u5fa1\u9732\u51fa\u4e86\u4e00\u4e2a\u5c0f\u5c0f\u7684\u7f3a\u53e3\u3002\u201c\u5c31\u662f\u73b0\u5728\uff01\u201d\u743c\u6069\u5bf9\u7ef4\u5eb7\u5c3c\u4e9a\u8bf4\uff0c\u6293\u7740\u5979\u7684\u624b\u8155\uff0c\u7a7f\u8fc7\u65b0\u5f00\u8f9f\u7684\u901a\u9053\u3002</p><p>\u4ed6\u4eec\u8fdb\u6765\u4e86\u3002</p><p>\u743c\u6069\u56de\u8fc7\u5934\uff0c\u4ed6\u770b\u89c1\u521a\u624d\u88ab\u5207\u65ad\u7684\u9b54\u6cd5\u7075\u7ebf\u81ea\u52a8\u53c8\u8054\u63a5\u8d77\u6765\uff0c\u9eef\u6de1\u7684\u94f6\u7070\u8272\u7b26\u6587\u91cd\u65b0\u83b7\u5f97\u80fd\u91cf\uff0c\u518d\u5ea6\u5149\u8292\u523a\u76ee\u3002\u4e00\u5207\u53c8\u6062\u590d\u5982\u521d\uff0c\u548c\u5f00\u59cb\u65f6\u4e00\u6837\uff0c\u4e0d\u540c\u4e4b\u5904\u5728\u4e8e\u4ed6\u4eec\u4ece\u5916\u9762\u8fdb\u5165\u5230\u4e86\u57ce\u5821\u91cc\u3002</p><p>\u201c\u771f\u68d2\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u5938\u5956\uff0c\u201c\u6f02\u4eae\u6781\u4e86\u3002\u201d</p><p>\u743c\u6069\u4ece\u5979\u7684\u8bed\u6c14\u91cc\u542c\u51fa\u4e86\u53d1\u81ea\u5185\u5fc3\u7684\u771f\u8bda\u79f0\u8d5e\uff0c\u90a3\u662f\u540c\u6837\u4f5c\u4e3a\u5deb\u5e08\uff0c\u5bf9\u6280\u827a\u9ad8\u8d85\u7684\u540c\u884c\u7684\u94a6\u4f69\u3002\u201c\u8fd9\u624d\u662f\u6700\u5916\u56f4\u7b2c\u4e00\u5c42\uff0c\u201d\u4ed6\u56de\u7b54\u8bf4\uff0c\u5fae\u7b11\u7740\uff0c\u201c\u540e\u9762\u80af\u5b9a\u4f1a\u96be\u5f97\u591a\u3002\u201d</p><p>\u201c\u81ea\u7136\uff0c\u201d\u5353\u5c14\u5c11\u5973\u8bf4\uff0c\u5979\u770b\u8d77\u6765\u4fe1\u5fc3\u5341\u8db3\uff0c\u201c\u4f46\u62e6\u4e0d\u4f4f\u6211\u4eec\u3002\u201d</p><p>\u201c\u6211\u4eec\u7684\u76ee\u7684\u4e0d\u662f\u6f5c\u5165\uff0c\u800c\u662f\u7834\u89e3\uff0c\u201d\u743c\u6069\u63d0\u9192\uff0c\u201c\u5f97\u627e\u5230\u4e00\u4e2a\u5408\u9002\u7684\u7a81\u7834\u53e3\u3002\u201d</p><p>\u4ed6\u4eec\u6162\u6162\u98d8\u6d6e\u8fdb\u6765\uff0c\u8d8a\u8fc7\u6d1b\u65af\u517d\u7684\u755c\u680f\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u7684\u6d6e\u7a7a\u672f\u5df2\u7ecf\u5feb\u8981\u65e0\u6cd5\u7ef4\u6301\uff0c\u4e8e\u662f\u964d\u843d\u5230\u5730\u9762\u3002\u6b63\u5728\u6b64\u65f6\uff0c\u8fdc\u5904\u6b63\u5728\u5de1\u903b\u7684\u7267\u5e08\u548c\u51e0\u4e2a\u6218\u58eb\u4f3c\u4e4e\u611f\u89c9\u5230\u4e86\u4ec0\u4e48\uff0c\u7a81\u7136\u505c\u4f4f\u811a\u6b65\uff0c\u63a5\u7740\u8f6c\u5411\u671d\u8fd9\u8fb9\u8d70\u8fc7\u6765\u3002</p><p>\u201c\u88ab\u53d1\u89c9\u4e86\uff1f\u201d\u743c\u6069\u76b1\u7709\u3002</p><p>\u5de1\u903b\u8005\u8d8a\u8d70\u8d8a\u8fd1\uff0c\u4ed6\u4eec\u795e\u60c5\u8b66\u60d5\uff0c\u63e1\u5728\u624b\u4e2d\u7684\u6b66\u5668\u5df2\u7ecf\u5904\u4e8e\u968f\u65f6\u5c31\u80fd\u653b\u51fb\u7684\u72b6\u6001\uff0c\u76ee\u5149\u7a7f\u900f\u8fc7\u9690\u5f62\u7684\u7ef4\u5eb7\u5c3c\u4e9a\u548c\u743c\u6069\u7684\u8eab\u4f53\uff0c\u4f3c\u4e4e\u662f\u5728\u641c\u5bfb\u7740\u4ec0\u4e48\u3002\u743c\u6069\u76d8\u7b97\u7740\uff0c\u5bf9\u65b9\u663e\u7136\u5df2\u7ecf\u53d1\u73b0\u6709\u4eba\u5165\u4fb5\uff0c\u4f46\u53c8\u5206\u660e\u8fd8\u6ca1\u5bdf\u89c9\u5230\u81ea\u5df1\u4e24\u4eba\u7684\u5b58\u5728\uff0c\u6216\u8bb8\u662f\u745e\u8d39\u5fb7\u5bb6\u7684\u67d0\u4e2a\u5deb\u5e08\u611f\u5e94\u5230\u4e86\u521a\u624d\u9b54\u6cd5\u7075\u7ebf\u88ab\u77ed\u6682\u5207\u65ad\uff0c\u4e8e\u662f\u901a\u77e5\u4e86\u4ed6\u4eec\uff0c\u8fd9\u5f88\u6709\u53ef\u80fd\u3002</p><p>\u5deb\u5e08\u8ba1\u7b97\u7740\u5bf9\u65b9\u7684\u4eba\u6570\uff0c\u4e00\u4e2a\u7267\u5e08\uff0c\u7a7f\u7740\u9576\u9ed1\u8fb9\u7684\u6df1\u7d2b\u8272\u957f\u888d\uff0c\u8fd9\u8bf4\u660e\u5979\u4f4d\u9636\u4e0d\u9ad8\uff1b\u56db\u4e2a\u6218\u58eb\uff0c\u4ece\u6b65\u4f10\u4e0a\u770b\u52a8\u4f5c\u8001\u7ec3\uff0c\u4e0d\u662f\u5eb8\u624b\u3002\u8fd9\u6709\u70b9\u9ebb\u70e6\u3002</p><p>\u6b64\u65f6\u4ed6\u4eec\u8fd8\u6ca1\u6709\u5bdf\u89c9\u5230\u743c\u6069\u548c\u7ef4\u5eb7\u5c3c\u4e9a\uff0c\u4f46\u518d\u9760\u8fd1\u4e00\u4e9b\u5e94\u8be5\u5c31\u80fd\u53d1\u73b0\u4e86\uff0c\u6b64\u65f6\u8eb2\u907f\u4e5f\u5df2\u7ecf\u6765\u4e0d\u53ca\u3002\u743c\u6069\u5012\u7f62\u4e86\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u53ea\u662f\u9690\u5f62\uff0c\u5979\u4f9d\u65e7\u662f\u5b9e\u4f53\u5b58\u5728\uff0c\u4efb\u4f55\u8f7b\u5fae\u7684\u884c\u52a8\u90fd\u4f1a\u4f5c\u7528\u4e8e\u5916\u754c\uff0c\u4ece\u800c\u66b4\u9732\u5f62\u8ff9\u3002\u5982\u679c\u7acb\u523b\u52a8\u624b\u7684\u8bdd\uff0c\u5012\u662f\u80fd\u591f\u6253\u5bf9\u65b9\u4e00\u4e2a\u63aa\u624b\u4e0d\u53ca\uff0c\u4f46\u82e5\u662f\u4e0d\u80fd\u901f\u6218\u901f\u51b3\uff0c\u6216\u8005\u5f04\u51fa\u52a8\u9759\u592a\u5927\uff0c\u5230\u65f6\u5019\u628a\u66f4\u591a\u7684\u4eba\u62db\u6765\uff0c\u90a3\u5c31\u9ebb\u70e6\u5927\u4e86\u3002</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u53ea\u8981\u4e00\u653b\u51fb\uff0c\u9690\u5f62\u672f\u5c31\u4f1a\u81ea\u52a8\u6d88\u5931\uff0c\u5979\u5c06\u76f4\u63a5\u66b4\u9732\u5728\u654c\u4eba\u9762\u524d\uff1b\u743c\u6069\u65e0\u6cd5\u78b0\u89e6\u5b9e\u4f53\uff0c\u4ed6\u53ef\u4ee5\u7528\u9b54\u6cd5\u653b\u51fb\uff0c\u800c\u4e14\u865a\u4f53\u72b6\u6001\u4e5f\u4e0d\u4f1a\u56e0\u4e3a\u653b\u51fb\u800c\u89e3\u9664\uff0c\u4f46\u4ed6\u53ef\u6ca1\u628a\u63e1\u540c\u65f6\u6740\u6b7b\u6216\u8005\u5236\u4f4f\u4e94\u4e2a\u5bf9\u624b\u3002</p><p>\u5353\u5c14\u7684\u5929\u8d4b\u9b54\u6cd5\u6297\u529b\uff0c\u662f\u4e2a\u4ee4\u4eba\u5934\u75bc\u7684\u95ee\u9898\u3002\u518d\u9ad8\u660e\u7684\u5deb\u5e08\u4e5f\u4e0d\u6562\u4fdd\u8bc1\u81ea\u5df1\u7684\u6cd5\u672f\u4e07\u65e0\u4e00\u5931\uff0c\u5c24\u5176\u662f\u9762\u5bf9\u591a\u4e2a\u76ee\u6807\u7684\u65f6\u5019\u3002</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u4e5f\u53d1\u73b0\u4e86\u8fd9\u79cd\u56f0\u5883\uff0c\u5979\u72b9\u8c6b\u5730\u7528\u6cd5\u6756\u5bf9\u51c6\u8d70\u5728\u6700\u524d\u9762\u7684\u4e00\u4e2a\u6218\u58eb\uff0c\u4f46\u53c8\u4e0d\u6562\u8d38\u7136\u52a8\u624b\u3002\u743c\u6069\u6025\u901f\u8f6c\u7740\u5ff5\u5934\uff0c\u770b\u7740\u5bf9\u65b9\u8d8a\u8d70\u8d8a\u8fd1\uff0c\u7136\u540e\u4ed6\u6709\u4e86\u4e2a\u4e3b\u610f\u3002</p><p>\u4ed6\u98d8\u6d6e\u8d77\u6765\uff0c\u79fb\u5230\u65c1\u8fb9\u7684\u755c\u680f\u4e2d\uff0c\u627e\u5230\u4e2a\u6bd4\u8f83\u9690\u853d\u7684\u89d2\u5ea6\uff0c\u7136\u540e\u62bd\u51fa\u6cd5\u6756\uff0c\u5bf9\u51c6\u90a3\u7fa4\u6d1b\u65af\u517d\u5f53\u4e2d\u4e00\u53ea\u6307\u4e86\u6307\u3002</p><p>\u201c\u53d8\u5f62\uff01\u201d\u743c\u6069\u65e0\u58f0\u5730\u8bf4\u3002</p><p>\u6cd5\u672f\u9ed8\u53d1\u7684\u6280\u5de7\u5e2e\u4e86\u5927\u5fd9\uff0c\u4ed6\u65bd\u6cd5\u65e0\u9700\u53d1\u51fa\u4efb\u4f55\u58f0\u97f3\uff0c\u90a3\u53ea\u6d1b\u65af\u517d\u88ab\u65e0\u5f62\u7684\u5492\u8bed\u51fb\u4e2d\uff0c\u968f\u5373\u731b\u7136\u5f39\u8df3\u8d77\u6765\uff0c\u5c31\u50cf\u4e00\u4e2a\u76ae\u7403\u4f3c\u7684\u8dc3\u51fa\u4e86\u56f4\u680f\u3002\u5b83\u7684\u5f62\u4f53\u5728\u4e00\u77ac\u95f4\u53d1\u751f\u4e86\u53d8\u5316\uff0c\u73b0\u5728\u770b\u8d77\u6765\u5c31\u662f\u4e2a\u4e0d\u6298\u4e0d\u6263\u7684\u957f\u80e1\u5b50\u77ee\u4eba\uff0c\u7a7f\u7740\u95ea\u4eae\u8000\u773c\u7684\u91d1\u5c5e\u94e0\u7532\uff0c\u6234\u7740\u5c16\u89d2\u5934\u76d4\uff0c\u5de6\u624b\u63e1\u7740\u6218\u65a7\uff0c\u53f3\u624b\u63d0\u7740\u4e00\u67c4\u5927\u9524\uff0c\u53e3\u4e2d\u53d1\u51fa\u8377\u8377\u602a\u543c\uff0c\u8fc8\u5f00\u4e24\u6761\u5c0f\u77ed\u817f\u671d\u5de1\u903b\u961f\u51b2\u53bb\u3002\u5982\u679c\u4ed4\u7ec6\u770b\u7684\u8bdd\uff0c\u4f1a\u53d1\u73b0\u4ed6\u7684\u6b65\u4f10\u6709\u4e9b\u8dcc\u8dcc\u649e\u649e\uff0c\u56e0\u4e3a\u8fd9\u6bd5\u7adf\u662f\u4e2a\u5192\u724c\u8d27\u3002</p><p>\u5353\u5c14\u542c\u5230\u52a8\u9759\uff0c\u9f50\u9f50\u770b\u5411\u8fd9\u8fb9\uff0c\u4ed6\u4eec\u6014\u4e86\u6014\uff0c\u968f\u5373\u53cd\u5e94\u8fc7\u6765\u3002</p><p>\u201c\u7c73\u5179\u745e\u56fe\u5c14\u5bb6\u7684\u5974\u96b6\u3002\u201d\u4e00\u4e2a\u6218\u58eb\u4e0d\u5c51\u5730\u8bf4\uff0c\u7b2c\u4e09\u5bb6\u65cf\u70ed\u7231\u4f7f\u7528\u77ee\u4eba\u5f53\u5974\u96b6\uff0c\u5168\u57ce\u7686\u77e5\u3002</p><p>\u867d\u7136\u4e0d\u660e\u767d\u4e00\u4e2a\u77ee\u4eba\u600e\u4e48\u4f1a\u51fa\u73b0\u5728\u8fd9\u91cc\uff0c\u4f46\u8fd9\u4e5f\u5e76\u975e\u591a\u4e48\u532a\u5937\u6240\u601d\uff0c\u53ef\u80fd\u662f\u5916\u9762\u67d0\u4e2a\u5deb\u5e08\u628a\u4ed6\u9001\u8fdb\u6765\u5f53\u63a2\u8def\u77f3\u5427\uff0c\u6216\u8005\u662f\u614c\u4e0d\u62e9\u8def\u7684\u9003\u5175\uff1f\u745e\u8d39\u5fb7\u5bb6\u7684\u5de1\u903b\u961f\u5e76\u6ca1\u6709\u592a\u5728\u610f\uff0c\u4e5f\u6ca1\u6709\u53d1\u51fa\u8b66\u62a5\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u4ed6\u4eec\u53cd\u800c\u7a0d\u7a0d\u677e\u61c8\u4e0b\u6765\u3002</p><p>\u4e00\u4e2a\u5974\u96b6\u7f62\u4e86\uff0c\u6240\u6709\u4eba\u90fd\u8fd9\u4e48\u60f3\u3002</p></article>"}}}]);
