(window.webpackJsonp=window.webpackJsonp||[]).push([[258],{280:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u5341\u4e5d\u8282 \u6b3a\u9a97",content:"<article><h2>\u7b2c\u4e8c\u5341\u4e5d\u8282 \u6b3a\u9a97</h2><p>\u4e00\u79d2\u949f\u4e4b\u524d\uff0c\u5b83\u8fd8\u662f\u4e00\u53ea\u5bb6\u755c\uff0c\u5b89\u5b89\u7a33\u7a33\u5730\u5728\u56f4\u680f\u91cc\u5367\u7740\u7761\u5927\u89c9\uff0c\u7b49\u5f85\u7740\u88ab\u5bb0\u6740\u7684\u547d\u8fd0\uff1b\u4e00\u79d2\u949f\u4e4b\u540e\uff0c\u5b83\u5df2\u7ecf\u53d8\u6210\u4e86\u4ee5\u82f1\u52c7\u800c\u8457\u79f0\u7684\u77ee\u4eba\u6218\u58eb\uff0c\u6b63\u5de6\u624b\u5de8\u65a7\uff0c\u53f3\u624b\u6218\u9524\uff0c\u671d\u4e94\u4e2a\u5353\u5c14\u2014\u2014\u5b83\u539f\u672c\u7684\u4e3b\u4eba\u2014\u2014\u51b2\u53bb\u3002</p><p>\u5bf9\u4e8e\u4e00\u53ea\u6d1b\u65af\u517d\u6765\u8bf4\uff0c\u8fd9\u53ef\u662f\u96be\u5f97\u7684\u9645\u9047\u3002</p><p>\u743c\u6069\u501f\u52a9\u6cd5\u6756\u65bd\u6cd5\uff0c\u5728\u53d8\u5f62\u672f\u91cc\u52a0\u5165\u4e86\u4e00\u70b9\u7279\u522b\u7684\u53d8\u5316\uff0c\u9a71\u4f7f\u7740\u8fd9\u4e2a\u65b0\u9c9c\u51fa\u7089\u7684\u77ee\u4eba\u5411\u5353\u5c14\u53d1\u52a8\u653b\u51fb\u3002\u7eef\u7ea2\u4e4b\u6cea\u5b9d\u77f3\u53c8\u88ab\u79f0\u4e3a\u6218\u795e\u5766\u5e15\u65af\u4e4b\u6ce3\uff0c\u539f\u672c\u5c31\u6709\u71c3\u70e7\u6218\u610f\u9f13\u821e\u58eb\u6c14\u7684\u6548\u7528\u3002\u5426\u5219\u7684\u8bdd\uff0c\u8fd9\u53ea\u672c\u8d28\u4e0a\u662f\u6d1b\u65af\u517d\u7684\u77ee\u4eba\u53ea\u4f1a\u4f9d\u65e7\u6e29\u987a\u5730\u8db4\u5728\u5730\u4e0a\uff0c\u6839\u672c\u4e0d\u6562\u5bf9\u5353\u5c14\u5200\u5251\u76f8\u5411\u3002</p><p>\u5b83\u51b2\u5230\u8ddf\u524d\uff0c\u9ad8\u9ad8\u4e3e\u8d77\u5de8\u65a7\u548c\u6218\u9524\uff0c\u76f4\u76f4\u5730\u671d\u6700\u524d\u9762\u7684\u5353\u5c14\u6218\u58eb\u5288\u7838\u4e0b\u53bb\u3002\u53d8\u5f62\u672f\u867d\u7136\u80fd\u591f\u8d4b\u4e88\u5b83\u65b0\u7684\u5f62\u6001\uff0c\u8ba9\u5b83\u548c\u77ee\u4eba\u4e00\u6837\u8eab\u4f53\u5f3a\u58ee\u3001\u76ae\u80a4\u575a\u97e7\uff0c\u4f46\u5374\u6ca1\u529e\u6cd5\u628a\u683c\u6597\u6280\u5de7\u4e5f\u4e00\u5e76\u9644\u9001\u4e86\u3002\u5353\u5c14\u6218\u58eb\u8f7b\u800c\u6613\u4e3e\u5730\u6321\u5f00\u4e86\u8fd9\u6b21\u653b\u51fb\uff0c\u4ed6\u770b\u8d77\u6765\u6709\u4e9b\u7591\u60d1\uff0c\u867d\u7136\u77ee\u4eba\u662f\u5974\u96b6\uff0c\u662f\u4f4e\u7b49\u79cd\u65cf\uff0c\u53ef\u4e5f\u662f\u4ee5\u76db\u4ea7\u6218\u58eb\u800c\u8457\u79f0\u7684\uff0c\u773c\u524d\u7684\u8fd9\u4e2a\u5bb6\u4f19\u52a8\u4f5c\u600e\u4e48\u5982\u6b64\u7b28\u62d9\u53ef\u7b11\uff0c\u5c31\u50cf\u662f\u4e2a\u4ece\u672a\u73a9\u8fc7\u6b66\u5668\u7684\u83dc\u9e1f\uff0c\u5c31\u8fde\u5730\u7cbe\u5974\u96b6\u90fd\u6bd4\u5b83\u505a\u5f97\u66f4\u597d\u3002</p><p>\u6216\u8bb8\u662f\u865a\u62db\uff1f\u6545\u610f\u9732\u51fa\u7834\u7efd\uff1f</p><p>\u5353\u5c14\u6027\u683c\u4e2d\u591a\u7591\u7684\u4e00\u9762\u5f00\u59cb\u8d77\u4f5c\u7528\uff0c\u6218\u58eb\u8c28\u614e\u5730\u8fd0\u7528\u7740\u624b\u4e2d\u7684\u5f2f\u5200\u548c\u76fe\u724c\uff0c\u914d\u5408\u7075\u5de7\u7684\u6b65\u4f10\uff0c\u683c\u4f4f\u6216\u8005\u8eb2\u5f00\u5bf9\u65b9\u7684\u4e00\u6b21\u6b21\u8fdb\u653b\uff0c\u4f46\u6682\u65f6\u6ca1\u6709\u53cd\u51fb\u3002\u77ee\u4eba\u538b\u6839\u4e0d\u77e5\u9053\u81ea\u5df1\u80e1\u4e71\u653b\u51fb\u7ed9\u5bf9\u65b9\u9020\u6210\u4e86\u5982\u6b64\u5927\u7684\u56f0\u60d1\uff0c\u4ed6\u53ea\u662f\u5728\u4e00\u79cd\u72c2\u66b4\u60c5\u7eea\u7684\u9a71\u4f7f\u4e0b\u672c\u80fd\u5730\u6325\u821e\u7740\u6b66\u5668\uff0c\u52c7\u5f80\u76f4\u524d\u3002</p><p>\u201c\u7c73\u5179\u745e\u56fe\u5c14\u6559\u5b98\u6d3e\u4f60\u6765\u7684\uff1f\u201d\u4ed6\u4f4e\u58f0\u559d\u95ee\uff0c\u8bd5\u56fe\u4ece\u5bf9\u65b9\u53e3\u4e2d\u5957\u51fa\u4e00\u4e9b\u60c5\u62a5\uff0c\u7136\u800c\u77ee\u4eba\u4e0d\u7406\u4e0d\u776c\u2014\u2014\u56e0\u4e3a\u53d8\u5f62\u672f\u6ca1\u6709\u8ba9\u5b83\u540c\u65f6\u638c\u63e1\u8bed\u8a00\u80fd\u529b\u3002</p><p>\u540e\u9762\u7684\u5353\u5c14\u6218\u58eb\u7ad9\u5b9a\u811a\u6b65\uff0c\u6258\u8d77\u5355\u624b\u5f29\u7784\u51c6\u7740\u77ee\u4eba\u5c04\u51fa\u4e00\u7bad\u3002\u4ed6\u51c6\u786e\u5730\u51fb\u4e2d\u4e86\uff0c\u4f46\u76ee\u6807\u6ca1\u6709\u53d7\u4f24\uff0c\u7bad\u77e2\u649e\u5728\u5149\u6ed1\u575a\u56fa\u7684\u94e0\u7532\u4e0a\uff0c\u53cd\u5f39\u4e0b\u6765\uff0c\u6389\u843d\u5728\u5730\u3002</p><p>\u53c8\u6709\u4e24\u4e2a\u6218\u58eb\u51b2\u4e0a\u524d\uff0c\u548c\u7b2c\u4e00\u4e2a\u6218\u58eb\u5f62\u6210\u54c1\u5b57\u5f62\uff0c\u5c06\u77ee\u4eba\u5305\u56f4\u5728\u5176\u4e2d\u3002\u7ecf\u8fc7\u521a\u624d\u7684\u89c2\u5bdf\u548c\u4ea4\u624b\uff0c\u5353\u5c14\u5df2\u7ecf\u57fa\u672c\u53ef\u4ee5\u65ad\u5b9a\u5bf9\u65b9\u5e76\u975e\u4f2a\u88c5\uff0c\u800c\u5c31\u662f\u4e2a\u4e0d\u6298\u4e0d\u6263\u7684\u83dc\u9e1f\uff0c\u4ed6\u4e3a\u81ea\u5df1\u4e00\u5f00\u59cb\u7684\u8fc7\u5206\u8c28\u614e\u800c\u607c\u6012\uff0c\u4e8e\u662f\u5f00\u59cb\u8fc5\u731b\u7684\u53cd\u51fb\u3002</p><p>\u77ee\u4eba\u7684\u6218\u65a7\u53c8\u6beb\u65e0\u7ae0\u6cd5\u5730\u5288\u4e0b\u6765\uff0c\u5353\u5c14\u4e3e\u8d77\u76fe\u724c\u683c\u5f00\uff0c\u53f3\u624b\u4e0a\u7684\u5f2f\u5200\u5982\u65cb\u98ce\u822c\u4e00\u7ede\uff0c\u5c06\u5bf9\u624b\u7684\u53f3\u624b\u6218\u9524\u6253\u843d\u5728\u5730\u3002\u201c\u53bb\u6b7b\u5427\uff01\u201d\u4ed6\u6012\u559d\u7740\uff0c\u5f2f\u5200\u7ffb\u8f6c\u671d\u77ee\u4eba\u7684\u8116\u5b50\u524a\u53bb\u3002</p><p>\u5c3d\u7ba1\u53d8\u6210\u4e86\u77ee\u4eba\uff0c\u6d1b\u65af\u517d\u4e0e\u751f\u4ff1\u6765\u7684\u80c6\u602f\u548c\u61e6\u5f31\u4f9d\u65e7\u6ca1\u6709\u6539\u53d8\uff0c\u521a\u624d\u662f\u5728\u6cd5\u672f\u7684\u523a\u6fc0\u4e0b\u80e1\u4e71\u51b2\u950b\uff0c\u5982\u4eca\u773c\u89c1\u5200\u950b\u4e34\u5934\uff0c\u987f\u65f6\u5413\u5f97\u9b42\u4e0d\u9644\u4f53\uff0c\u53cc\u817f\u4e00\u8f6f\u5c31\u5750\u5012\u5728\u5730\uff0c\u7ed3\u679c\u6b63\u597d\u907f\u5f00\u4e86\u5353\u5c14\u81f4\u547d\u7684\u4e00\u51fb\u3002</p><p>\u5f2f\u5200\u51fb\u4e2d\u4e86\u8111\u888b\uff0c\u5c06\u5934\u76d4\u8fdc\u8fdc\u51fb\u98de\u51fa\u53bb\uff0c\u5de8\u5927\u7684\u78b0\u649e\u529b\u8ba9\u77ee\u4eba\u4e00\u9635\u6655\u7729\uff0c\u773c\u524d\u4e00\u9ed1\u5c31\u6655\u8ff7\u8fc7\u53bb\u3002\u5353\u5c14\u6b63\u8981\u518d\u8865\u4e0a\u4e00\u5200\uff0c\u7267\u5e08\u963b\u6b62\u4e86\u4ed6\u3002</p><p>\u201c\u522b\u6740\u4ed6\u3002\u201d\u7267\u5e08\u8bf4\u3002</p><p>\u6218\u58eb\u7684\u5f2f\u5200\u5728\u534a\u7a7a\u505c\u4e0b\uff0c\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u4ed6\u5927\u80c6\u5730\u53cd\u95ee\uff0c\u201c\u4ed6\u662f\u654c\u4eba\u3002\u201d</p><p>\u201c\u8fd9\u4e2a\u77ee\u4eba\u5f88\u8e4a\u8df7\uff0c\u201d\u7267\u5e08\u8bf4\uff0c\u201c\u597d\u597d\u5ba1\u8baf\uff0c\u6216\u8bb8\u6211\u4eec\u80fd\u6709\u4ec0\u4e48\u6536\u83b7\u3002\u201d</p><p>\u5728\u7267\u5e08\u7684\u547d\u4ee4\u4e0b\uff0c\u4e24\u4e2a\u6218\u58eb\u53d6\u4e0b\u8170\u540e\u7684\u7cbe\u91d1\u624b\u94d0\uff0c\u5c06\u6655\u8ff7\u4e2d\u7684\u77ee\u4eba\u6263\u4f4f\uff0c\u7136\u540e\u62ac\u8d77\u6765\u671d\u5185\u9662\u8d70\u53bb\uff0c\u8eab\u5f71\u5f88\u5feb\u6d88\u5931\u5728\u77f3\u7b0b\u6797\u4e2d\uff0c\u5e94\u8be5\u662f\u9001\u4fd8\u864f\u53bb\u5211\u8baf\u5ba4\uff08\u8fd9\u79cd\u5efa\u7b51\u4e00\u822c\u5b89\u6392\u5728\u5730\u4e0b\u5ba4\uff09\u3002\u5176\u4ed6\u4eba\u4f9d\u65e7\u5b88\u5728\u539f\u5730\uff0c\u7b49\u5f85\u7740\u4ed6\u4eec\u8fd4\u56de\u3002</p><p>\u743c\u6069\u5728\u6697\u4e2d\u89c2\u5bdf\u7740\u8fd9\u4e00\u5207\uff0c\u73b0\u5728\u8981\u5bf9\u4ed8\u7684\u654c\u4eba\u6682\u65f6\u53ea\u6709\u4e09\u4e2a\u4e86\u3002\u800c\u8d81\u7740\u521a\u624d\u7684\u6df7\u4e71\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u6084\u6084\u5730\u79fb\u52a8\u5230\u4e86\u5353\u5c14\u4eec\u7684\u4fa7\u540e\u65b9\uff0c\u8ddd\u79bb\u5df2\u7ecf\u5f88\u8fd1\uff0c\u968f\u65f6\u53ef\u4ee5\u53d1\u8d77\u88ad\u51fb\u3002</p><p>\u5deb\u5e08\u4f7f\u7528\u4e86\u4e00\u4e2a\u9884\u8a00\u6cd5\u672f\uff0c\u5206\u6790\u8fd9\u4e09\u4e2a\u5bf9\u624b\u8eab\u4e0a\u7684\u9b54\u6cd5\u9632\u62a4\u3002\u4ed6\u4eec\u7684\u7070\u8272\u6597\u7bf7\u4e0a\u7ed8\u7740\u7cbe\u81f4\u7684\u56fe\u6848\uff0c\u90a3\u5e94\u8be5\u662f\u4e00\u4e2a\u9632\u62a4\u5143\u7d20\u7ed3\u754c\uff0c\u53ef\u4ee5\u62b5\u6d88\u706b\u7130\u3001\u5bd2\u51b0\u3001\u95ea\u7535\u3001\u5f3a\u9178\u6216\u8005\u97f3\u6ce2\u7c7b\u578b\u7684\u4f24\u5bb3\uff1b\u957f\u9774\u4e2d\u8574\u542b\u4e86\u4e00\u4e2a\u77f3\u80a4\u672f\uff0c\u5f53\u4ed6\u4eec\u53d7\u5230\u7269\u7406\u4f24\u5bb3\u7684\u65f6\u5019\u2014\u2014\u4f8b\u5982\u88ab\u5200\u5251\u780d\u4e2d\uff0c\u88ab\u62f3\u811a\u51fb\u6253\uff0c\u6216\u8005\u4ece\u9ad8\u5904\u6454\u4e0b\u4e4b\u7c7b\u2014\u2014\u6cd5\u672f\u5c31\u4f1a\u81ea\u52a8\u6fc0\u53d1\uff0c\u4e3a\u4ed6\u4eec\u751f\u6210\u4e00\u5c42\u5927\u7406\u77f3\u76ae\u80a4\u3002\u9664\u6b64\u4e4b\u5916\uff0c\u9886\u5934\u7267\u5e08\u7684\u8116\u5b50\u4e0a\u6302\u7740\u7cbe\u91d1\u8718\u86db\u5723\u5fbd\uff0c\u90a3\u4e0d\u4ec5\u4ec5\u662f\u65bd\u5c55\u795e\u672f\u7684\u6cd5\u5668\uff0c\u540c\u65f6\u8fd8\u80fd\u5e2e\u52a9\u5979\u589e\u5f3a\u5bf9\u5fc3\u667a\u9b54\u6cd5\u7684\u62b5\u6297\u80fd\u529b\u3002</p><p>\u201c\u7528\u5f3a\u529b\u7761\u7720\u672f\u3002\u201d\u743c\u6069\u8f7b\u58f0\u8bf4\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u53ef\u4ee5\u501f\u52a9\u80f8\u9488\u542c\u5230\u4ed6\u7684\u58f0\u97f3\uff0c\u5176\u4ed6\u4eba\u4e00\u65e0\u6240\u77e5\u3002\u5353\u5c14\u5c11\u5973\u70b9\u70b9\u5934\uff0c\u7528\u624e\u514b\u6728\u6cd5\u6756\u6307\u5411\u4eba\u7fa4\uff0c\u4ece\u6000\u4e2d\u53d6\u51fa\u4e00\u64ae\u7ec6\u6c99\uff0c\u626c\u624b\u8f7b\u8f7b\u6d12\u4e86\u4e0b\u6765\uff0c\u540c\u65f6\u5ff5\u51fa\u5492\u8bed\u3002</p><p>\u5979\u521a\u521a\u53d1\u51fa\u7b2c\u4e00\u4e2a\u97f3\u8282\uff0c\u5353\u5c14\u4eec\u5c31\u7acb\u523b\u5bdf\u89c9\u5230\u4e86\uff0c\u4e09\u4e2a\u4eba\u4e00\u9f50\u8f6c\u8fc7\u8eab\u6765\uff0c\u6b63\u770b\u89c1\u7ef4\u5eb7\u5c3c\u4e9a\u5728\u65bd\u5c55\u6cd5\u672f\u3002\u6ca1\u6709\u534a\u79d2\u949f\u7684\u8fdf\u7591\uff0c\u7267\u5e08\u4e3e\u8d77\u5723\u5fbd\u5f00\u59cb\u8bf5\u5492\uff0c\u4e24\u4e2a\u6218\u58eb\u540c\u65f6\u51b2\u950b\u3002\u6216\u8bb8\u662f\u51fa\u4e8e\u81ea\u4fe1\uff0c\u6216\u8bb8\u662f\u770b\u5230\u5bf9\u65b9\u53ea\u6709\u4e00\u4e2a\u4eba\uff0c\u4ed6\u4eec\u5e76\u6ca1\u6709\u53d1\u51fa\u6c42\u63f4\u7684\u8baf\u53f7\u3002</p><p>\u4e3a\u4e86\u907f\u514d\u5492\u8bed\u88ab\u6253\u65ad\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u4e0d\u6562\u95ea\u907f\uff0c\u5f53\u5979\u5ff5\u5b8c\u7b2c\u4e09\u4e2a\u97f3\u8282\u7684\u65f6\u5019\uff0c\u4e24\u67c4\u957f\u5251\u540c\u65f6\u523a\u8fc7\u6765\u3002\u4f8d\u7236\u6597\u7bf7\u81ea\u52a8\u5408\u62e2\uff0c\u7262\u7262\u4fdd\u62a4\u7740\u4e3b\u4eba\uff0c\u67d4\u8f6f\u7684\u86db\u4e1d\u77ac\u95f4\u53d8\u5f97\u4eff\u4f5b\u7cbe\u94a2\u4e00\u6837\u575a\u786c\uff0c\u5c06\u957f\u5251\u5f39\u5f00\u3002</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u4e5f\u88ab\u51b2\u51fb\u529b\u9707\u5f97\u540e\u9000\u534a\u6b65\uff0c\u4f46\u5979\u4f9d\u65e7\u6210\u529f\u5ff5\u5b8c\u4e86\u5492\u8bed\u3002\u5f3a\u529b\u7761\u7720\u672f\u7b3c\u7f69\u4f4f\u4e86\u4e09\u4e2a\u4eba\uff0c\u4f46\u53ea\u6709\u4e00\u4e2a\u6218\u58eb\u505c\u4f4f\u811a\u6b65\uff0c\u8f6f\u7ef5\u7ef5\u5730\u5012\u4e0b\u4e86\uff0c\u5176\u4ed6\u4e24\u4eba\u5e76\u672a\u53d7\u5230\u5f71\u54cd\u3002\u745e\u8d39\u5fb7\u5bb6\u65cf\u7684\u7267\u5e08\u6b64\u65f6\u4e5f\u5b8c\u6210\u6cd5\u672f\uff0c\u4e00\u56e2\u9eef\u708e\u5728\u638c\u5fc3\u817e\u8d77\uff0c\u63a5\u7740\u88ab\u6295\u63b7\u51fa\u53bb\uff0c\u7838\u5411\u7ef4\u5eb7\u5c3c\u4e9a\u3002\u8fd9\u662f\u6765\u81ea\u6df1\u6e0a\u7684\u90aa\u6076\u4e4b\u706b\uff0c\u5982\u679c\u88ab\u51fb\u4e2d\u53c8\u6ca1\u6709\u6210\u529f\u62b5\u5fa1\u7684\u8bdd\u4fbf\u4f1a\u7acb\u523b\u9677\u5165\u660f\u8ff7\uff0c\u5c31\u7b97\u7528\u5200\u780d\u5251\u523a\u90fd\u4e0d\u4f1a\u9192\u6765\u3002</p><p>\u7136\u540e\u7267\u5e08\u4e00\u58f0\u4e0d\u54cd\u5730\u5012\u4e0b\u4e86\u3002</p><p>\u5728\u9eef\u708e\u521a\u4ece\u7267\u5e08\u638c\u5fc3\u817e\u8d77\u7684\u65f6\u5019\uff0c\u743c\u6069\u5df2\u7ecf\u6084\u6084\u6321\u5728\u5979\u9762\u524d\uff0c\u5e76\u4e14\u542f\u52a8\u4e86\u6cd5\u672f\u9006\u8f6c\u6212\u6307\u3002\u4ed6\u662f\u865a\u4f53\uff0c\u7267\u5e08\u6ca1\u6709\u5bdf\u89c9\u5230\u3002\u7d27\u63a5\u7740\u9eef\u708e\u5c04\u51fa\uff0c\u7136\u540e\u5728\u51e0\u4e4e\u540c\u4e00\u77ac\u95f4\u53c8\u53cd\u5f39\u4e86\u56de\u6765\uff0c\u51fb\u4e2d\u4e86\u65bd\u6cd5\u8005\u3002</p><p>\u6b63\u5728\u653b\u51fb\u7ef4\u5eb7\u5c3c\u4e9a\u7684\u5353\u5c14\u6218\u58eb\u53d1\u73b0\u4e86\u8eab\u540e\u7684\u53d8\u6545\uff0c\u4ed6\u7acb\u523b\u610f\u8bc6\u5230\u8fd8\u6709\u53e6\u5916\u7684\u654c\u4eba\u6697\u4e2d\u6f5c\u4f0f\uff0c\u5728\u4eba\u6570\u4e0a\u81ea\u5df1\u5df2\u7ecf\u5904\u4e8e\u52a3\u52bf\uff0c\u5f53\u52a1\u4e4b\u6025\u662f\u7acb\u523b\u901a\u77e5\u5bb6\u65cf\u91cc\u7684\u5176\u4ed6\u4eba\u3002\u6218\u58eb\u865a\u6643\u4e00\u5251\uff0c\u903c\u9000\u4e86\u7ef4\u5eb7\u5c3c\u4e9a\uff0c\u6df1\u5438\u4e00\u53e3\u6c14\u6b63\u8981\u5927\u58f0\u558a\u53eb\uff0c\u80cc\u540e\u7a81\u7136\u4e00\u9635\u51b0\u51c9\u3002</p><p>\u743c\u6069\u7684\u8eab\u5f62\u51fa\u73b0\u5728\u7a7a\u6c14\u4e2d\uff0c\u4ed6\u7684\u53f3\u624b\u63e1\u7740\u8718\u86db\u5315\u9996\uff0c\u4ece\u80cc\u540e\u523a\u8fdb\u4e86\u6218\u58eb\u7684\u8eab\u4f53\u3002\u8718\u86db\u7684\u516b\u53ea\u5229\u722a\u4eff\u4f5b\u6709\u751f\u547d\u4e00\u822c\u4ece\u9501\u7532\u7684\u7f1d\u9699\u4e2d\u523a\u8fdb\uff0c\u907f\u5f00\u9aa8\u9abc\uff0c\u7a7f\u900f\u8840\u8089\uff0c\u7262\u7262\u6263\u4f4f\u4e86\u5fc3\u810f\u3002\u6bd2\u7d20\u987a\u7740\u8840\u6db2\u6d41\u7ecf\u5168\u8eab\uff0c\u5728\u4e24\u4e2a\u547c\u5438\u4e4b\u95f4\uff0c\u6218\u58eb\u7684\u751f\u547d\u529b\u6d88\u6563\u6b86\u5c3d\uff0c\u4ed6\u6b7b\u4e86\u3002</p><p>\u743c\u6069\u7528\u610f\u8bc6\u547d\u4ee4\u8718\u86db\u5229\u722a\u677e\u5f00\u5fc3\u810f\uff0c\u7136\u540e\u5c06\u5315\u9996\u62d4\u51fa\uff0c\u867d\u7136\u521a\u521a\u6740\u4e86\u4e00\u4e2a\u4eba\uff0c\u950b\u5203\u4f9d\u65e7\u5149\u6d01\u5982\u65b0\uff0c\u6ca1\u6709\u6cbe\u4e0a\u534a\u70b9\u8840\u8ff9\uff0c\u8fd9\u771f\u662f\u4ef6\u4e0d\u9519\u7684\u6b66\u5668\u3002</p><p>\u201c\u4f60\u6ca1\u5fc5\u8981\u6062\u590d\u5b9e\u4f53\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u8bf4\uff0c\u201c\u6211\u80fd\u5bf9\u4ed8\u4ed6\u7684\u3002\u201d</p><p>\u201c\u65f6\u95f4\u5dee\u4e0d\u591a\u4e5f\u5230\u4e86\u3002\u201d\u743c\u6069\u89e3\u91ca\uff0c\u7075\u5316\u6597\u7bf7\u80fd\u591f\u8ba9\u4ed6\u53d8\u6210\u7075\u4f53\uff0c\u4f46\u65f6\u95f4\u5e76\u4e0d\u957f\uff0c\u6bcf\u5929\u53ef\u4ee5\u4f7f\u7528\u4e00\u6b21\u3002</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u70b9\u70b9\u5934\uff0c\u770b\u7740\u5730\u4e0a\u660f\u7761\u4e0d\u9192\u7684\u7267\u5e08\u548c\u6218\u58eb\uff0c\u201c\u6211\u6765\u5904\u7406\u3002\u201d\u5979\u8bf4\uff0c\u53d6\u51fa\u8170\u95f4\u7684\u86c7\u9996\u97ad\uff0c\u8ba9\u6bd2\u86c7\u5728\u4ed6\u4eec\u7684\u8116\u9888\u548c\u624b\u80cc\u4e0a\u5404\u54ac\u4e00\u53e3\u3002</p><p>\u201c\u5979\u4eec\u4f1a\u5728\u4e0d\u77e5\u4e0d\u89c9\u4e2d\u6b7b\u53bb\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u8bf4\uff0c\u201c\u5f88\u6162\uff0c\u4e0d\u53ef\u9006\u8f6c\uff0c\u6ca1\u6709\u75db\u82e6\uff0c\u5c31\u50cf\u505a\u4e86\u4e00\u573a\u68a6\u3002\u201d</p><p>\u201c\u8fd9\u7b97\u662f\u4ec1\u6148\u5417\uff1f\u201d\u743c\u6069\u95ee\u3002</p><p>\u201c\u8fd9\u662f\u8eab\u4e3a\u9ad8\u7b49\u79cd\u65cf\u7684\u81ea\u91cd\u8eab\u4efd\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u56de\u7b54\uff0c\u201c\u6740\u4eba\u6709\u5f88\u591a\u79cd\u65b9\u5f0f\uff0c\u5353\u5c14\u5e94\u8be5\u5c3d\u53ef\u80fd\u5730\u505a\u5230\u7cbe\u81f4\u548c\u4f18\u96c5\u3002\u201d</p><p>\u743c\u6069\u8038\u8038\u80a9\u3002</p><p>\u201c\u6211\u4eec\u5f97\u8d76\u5feb\u79bb\u5f00\u8fd9\u91cc\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u90a3\u4e24\u4e2a\u6218\u58eb\u56de\u6765\u5c31\u4f1a\u53d1\u73b0\u6709\u4eba\u5165\u4fb5\uff0c\u7136\u540e\u5c31\u4f1a\u6709\u641c\u6355\u8fdb\u884c\u2014\u2014\u5e78\u597d\u6211\u4eec\u5e76\u4e0d\u5b64\u5355\u3002\u201d</p><p>\u4e09\u5927\u5bb6\u65cf\u7684\u5176\u4ed6\u5deb\u5e08\uff0c\u6b64\u65f6\u5e94\u8be5\u4e5f\u5df2\u7ecf\u4ece\u5404\u4e2a\u65b9\u5411\u5bf9\u745e\u8d39\u5fb7\u5bb6\u65cf\u7684\u9b54\u6cd5\u9635\u5c55\u5f00\u8fdb\u653b\uff0c\u6216\u8005\u6084\u6084\u6f5c\u5165\u3002\u4ed6\u4eec\u5f53\u4e2d\u6709\u4e9b\u4eba\u53ef\u80fd\u65e0\u6cd5\u5f97\u624b\uff0c\u4f46\u603b\u6709\u4e9b\u80fd\u591f\u50cf\u743c\u6069\u4e00\u6837\u6210\u529f\uff0c\u81f3\u5c11\u4e9a\u5f53\u65af\u80af\u5b9a\u53ef\u4ee5\uff0c\u4ed6\u4e0d\u4f46\u662f\u83f2\u5c14\u4f26\u5bb6\u65cf\u7684\u9996\u5e2d\u5deb\u5e08\uff0c\u540c\u65f6\u8fd8\u662f\u5965\u672f\u5b66\u9662\u7684\u9886\u8896\uff0c\u662f\u8fd9\u5ea7\u57ce\u5e02\u91cc\u6700\u9ad8\u660e\u7684\u5deb\u5e08\u3002\u4ed6\u4eec\u4f1a\u5206\u62c5\u743c\u6069\u548c\u7ef4\u5eb7\u5c3c\u4e9a\u7684\u538b\u529b\u3002</p><p>\u6ca1\u6709\u65f6\u95f4\u5904\u7406\u5c38\u4f53\u4e86\uff0c\u800c\u4e14\u4e5f\u6ca1\u6709\u8fd9\u4e2a\u5fc5\u8981\uff0c\u743c\u6069\u5c06\u5315\u9996\u653e\u56de\u957f\u9774\u91cc\uff0c\u5feb\u901f\u6253\u91cf\u4e86\u4e00\u756a\u5730\u5f62\u3002\u521a\u624d\u5728\u865a\u4f53\u72b6\u6001\u7684\u65f6\u5019\uff0c\u4ed6\u628a\u7edd\u5927\u90e8\u5206\u7cbe\u529b\u90fd\u653e\u5728\u89c2\u5bdf\u9b54\u6cd5\u7075\u7ebf\u4e0a\u4e86\uff0c\u53cd\u5012\u5ffd\u7565\u4e86\u7269\u8d28\u4e16\u754c\u3002\u745e\u8d39\u5fb7\u5bb6\u7684\u949f\u4e73\u77f3\u548c\u77f3\u7b0b\u72ac\u7259\u4ea4\u9519\u7740\uff0c\u5c31\u50cf\u4e00\u53ea\u5de8\u5927\u602a\u7269\u7684\u53c2\u5dee\u4e0d\u9f50\u7684\u7259\u9f7f\uff0c\u5b83\u4eec\u5f53\u4e2d\u90fd\u7528\u98de\u6865\u8054\u63a5\uff0c\u65b9\u4fbf\u5f80\u6765\u3002\u743c\u6069\u8f7b\u8f7b\u629a\u6469\u7740\u6597\u7bf7\u5185\u4fa7\uff0c\u90a3\u91cc\u522b\u7740\u4e00\u679a\u80f8\u9488\uff0c\u662f\u9634\u9b42\u57ce\u7b2c\u4e8c\u8fdc\u5f81\u5e08\u914d\u53d1\u7684\uff0c\u90a3\u91cc\u50a8\u5b58\u4e86\u51e0\u4e2a\u7b80\u5355\u4f46\u5b9e\u7528\u7684\u5c0f\u6cd5\u672f\u3002\u4ed6\u4f4e\u58f0\u5462\u5583\u51fa\u547d\u4ee4\u7684\u5b57\u7b26\uff0c\u80f8\u9488\u6084\u6084\u95ea\u70c1\u4e86\u4e00\u4e0b\uff0c\u5deb\u5e08\u7684\u53cc\u773c\u4e5f\u968f\u7740\u9aa4\u7136\u4e00\u4eae\uff0c\u968f\u5373\u6062\u590d\u539f\u72b6\uff0c\u4ed6\u7684\u89c6\u89c9\u73b0\u5728\u53d8\u5f97\u50cf\u9e70\u4e00\u6837\u9510\u5229\uff0c\u800c\u4e14\u80fd\u591f\u6839\u636e\u4ed6\u7684\u5fc3\u610f\u900f\u8fc7\u5b9e\u4f53\u969c\u788d\u76f4\u63a5\u770b\u5230\u8fdc\u65b9\u3002\u201c\u8d70\u8fd9\u8fb9\uff0c\u201d\u4ed6\u89c2\u5bdf\u4e86\u4e00\u4f1a\uff0c\u6307\u7740\u4e00\u6761\u5c0f\u8def\uff0c\u201c\u6211\u611f\u89c9\u4f1a\u6709\u597d\u8fd0\u6c14\u5728\u7b49\u7740\u6211\u4eec\u3002\u201d</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u8f7b\u7b11\u8d77\u6765\uff0c\u201c\u6211\u4ece\u6765\u4e0d\u66fe\u542c\u8bf4\u5deb\u5e08\u7684\u76f4\u89c9\u51c6\u3002\u201d</p><p>\u201c\u6211\u662f\u4e2a\u4f8b\u5916\u3002\u201d</p><p>\u201c\u90a3\u4e48\u8bd5\u8bd5\u770b\u5427\u3002\u201d\u5353\u5c14\u5c11\u5973\u8bf4\uff0c\u5979\u5728\u6b7b\u53bb\u7684\u7267\u5e08\u8eab\u4e0a\u7ffb\u627e\u4e86\u4e00\u4f1a\uff0c\u53d1\u73b0\u4e86\u51e0\u5f20\u5377\u8f74\uff0c\u4e8e\u662f\u6536\u5165\u6000\u4e2d\u3002\u63a5\u7740\u5979\u7528\u80f8\u9488\u53ec\u5524\u6765\u4e00\u56e2\u9634\u5f71\uff0c\u5c06\u81ea\u5df1\u548c\u743c\u6069\u7684\u8eab\u4f53\u906e\u63a9\u8d77\u6765\uff0c\u5c0f\u5fc3\u5730\u524d\u8fdb\u3002</p><p>\u745e\u8d39\u5fb7\u5bb6\u65cf\u7684\u5927\u90e8\u4efd\u6210\u5458\u5e94\u8be5\u90fd\u5728\u57ce\u5899\u4e0a\u62b5\u5fa1\u653b\u51fb\u53bb\u4e86\uff0c\u57ce\u5821\u91cc\u5f88\u5b89\u9759\uff0c\u521a\u624d\u9001\u77ee\u4eba\u53bb\u5211\u8baf\u5ba4\u7684\u4e24\u4e2a\u5353\u5c14\u5de1\u903b\u8005\u4e5f\u8fd8\u6ca1\u6709\u8fd4\u56de\uff0c\u4e00\u5207\u4f3c\u4e4e\u90fd\u5f88\u987a\u5229\u3002\u4f46\u5c31\u5728\u6b64\u65f6\uff0c\u743c\u6069\u7a81\u7136\u505c\u4f4f\u811a\u6b65\u3002</p><p>\u201c\u7b49\u7b49\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u7ad9\u7740\u522b\u52a8\u3002\u201d</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u4e0d\u89e3\uff0c\u201c\u600e\u4e48\u4e86\uff1f\u201d</p><p>\u201c\u6211\u4eec\u9762\u524d\u6709\u4e00\u4e2a\u6cd5\u672f\u9677\u9631\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5982\u679c\u8d38\u7136\u649e\u4e0a\u53bb\uff0c\u5c31\u4f1a\u89e6\u53d1\u4e00\u4e2a\u529b\u573a\u7262\u7b3c\uff0c\u5c06\u6211\u4eec\u56da\u7981\u8d77\u6765\uff0c\u7d27\u63a5\u7740\u4f1a\u6709\u5404\u79cd\u5143\u7d20\u6784\u6210\u7684\u7bad\u77e2\u6765\u6740\u6b7b\u6211\u4eec\u3002\u201d</p><p>\u201c\u7ed5\u5f00\u5462\uff1f\u201d</p><p>\u201c\u7ed5\u4e0d\u5f00\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u8fd9\u5c31\u662f\u6574\u4e2a\u9b54\u6cd5\u9635\u7684\u7b2c\u4e8c\u5c42\u4e86\u3002\u201d</p><p>\u201c\u4f60\u4e0d\u80fd\u89e3\u9664\u5b83\uff1f\u6216\u8005\u50cf\u521a\u624d\u90a3\u6837\u6682\u65f6\u5207\u65ad\uff1f\u201d</p><p>\u201c\u4e0d\u80fd\uff0c\u201d\u743c\u6069\u6447\u5934\uff0c\u201c\u8fd9\u4e2a\u6bd4\u521a\u624d\u590d\u6742\u5f97\u591a\uff0c\u4e5f\u5f3a\u5f97\u591a\uff0c\u6211\u529e\u4e0d\u5230\u3002\u201d</p><p>\u53e6\u5916\u4e00\u4e2a\u6ca1\u6709\u8bf4\u51fa\u53e3\u7684\u7406\u7531\u662f\uff0c\u529b\u573a\u3001\u5143\u7d20\u8fd9\u79cd\u7c7b\u578b\u7684\u9b54\u6cd5\u662f\u5851\u80fd\u5b66\u6d3e\u7684\u4e13\u4e1a\u8303\u56f4\uff0c\u4ed6\u5b8c\u5168\u4e0d\u719f\u6089\uff0c\u4e0d\u6562\u5192\u9669\u3002</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u629a\u6478\u7740\u624b\u6307\u4e0a\u7684\u7eff\u5b9d\u77f3\u6212\u6307\uff0c\u201c\u5b83\u5e94\u8be5\u4f1a\u5e2e\u52a9\u6211\u8eb2\u8fc7\u653b\u51fb\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u4e9a\u5f53\u65af\u53d4\u53d4\u662f\u8fd9\u4e48\u8bf4\u7684\u3002\u201d</p><p>\u201c\u4f46\u5b83\u53ea\u6709\u4e00\u679a\u3002\u201d</p><p>\u201c\u521a\u624d\u90a3\u51e0\u4e2a\u4eba\u2026\u2026\u201d</p><p>\u201c\u6211\u68c0\u67e5\u8fc7\u4e86\uff0c\u201d\u743c\u6069\u6253\u65ad\u8bf4\uff0c\u201c\u90a3\u4e2a\u7267\u5e08\u4f69\u6234\u7740\u5bb6\u5fbd\u6212\u6307\uff0c\u4f46\u4e0d\u662f\u9b54\u6cd5\u5236\u54c1\u3002\u201d</p><p>\u201c\u53ef\u662f\u521a\u624d\u4ed6\u4eec\u4e0d\u5c31\u662f\u4ece\u8fd9\u8fb9\u8d70\u8fc7\u6765\u7684\u4e48\u3002\u201d</p><p>\u743c\u6069\u6c89\u541f\u7740\uff0c\u201c\u662f\u6ca1\u9519\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u8fd9\u8bf4\u660e\u4ed6\u4eec\u8eab\u4e0a\u6709\u53e6\u5916\u7684\u901a\u8fc7\u8fd9\u4e2a\u6cd5\u672f\u9677\u9631\u7684\u2018\u94a5\u5319\u2019\uff0c\u6216\u8005\u4ed6\u4eec\u672c\u4eba\u5c31\u662f\u94a5\u5319\u3002\u201d</p><p>\u9ad8\u660e\u7684\u9b54\u6cd5\u9635\u53ef\u4ee5\u8bbe\u7f6e\u4e00\u5b9a\u7684\u89e6\u53d1\u6761\u4ef6\uff0c\u4ee5\u514d\u8bef\u4f24\uff0c\u4f8b\u5982\u8bf4\u51e1\u662f\u4f69\u6234\u9b54\u6cd5\u5bb6\u5fbd\u8005\u53ef\u4ee5\u901a\u884c\u65e0\u963b\uff0c\u6216\u8005\u51e1\u662f\u62e5\u6709\u745e\u8d39\u5fb7\u5bb6\u8840\u8109\u8005\u53ef\u4ee5\u81ea\u7531\u7ecf\u8fc7\uff0c\u6216\u8005\u5176\u4ed6\u66f4\u590d\u6742\u66f4\u7ec6\u81f4\u7684\u5224\u65ad\u6807\u51c6\uff0c\u8fd9\u5f97\u770b\u5e03\u7f6e\u9b54\u6cd5\u9635\u7684\u5deb\u5e08\u9020\u8be3\u9ad8\u4f4e\u3002\u5982\u679c\u6709\u8db3\u591f\u7684\u65f6\u95f4\uff0c\u743c\u6069\u4e5f\u53ef\u4ee5\u6162\u6162\u53bb\u5206\u6790\u63a8\u6d4b\uff0c\u4f46\u4ed6\u73b0\u5728\u6ca1\u6709\u8fd9\u4e2a\u95f2\u6687\u3002</p><p>\u4ed6\u4eec\u56de\u5230\u521a\u624d\u6218\u6597\u7684\u5730\u65b9\uff0c\u8bd5\u56fe\u4ece\u4e09\u4e2a\u5353\u5c14\u8eab\u4e0a\u5bfb\u627e\u7ebf\u7d22\uff0c\u4f46\u6ca1\u4ec0\u4e48\u6536\u83b7\u3002\u4e24\u4e2a\u6218\u58eb\u90fd\u5df2\u7ecf\u6b7b\u53bb\uff0c\u7267\u5e08\u5012\u8fd8\u6ca1\u65ad\u6c14\uff0c\u5979\u88ab\u7ef4\u5eb7\u5c3c\u4e9a\u7684\u86c7\u97ad\u4e0a\u7684\u6bd2\u86c7\u54ac\u4e2d\u4e86\uff0c\u4f46\u7f57\u4e1d\u7684\u796d\u53f8\u539f\u672c\u5c31\u5bf9\u6bd2\u7d20\u6709\u8f83\u5f3a\u7684\u62b5\u6297\u80fd\u529b\u3002</p><p>\u201c\u6709\u529e\u6cd5\u4e86\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u7a81\u7136\u7075\u673a\u4e00\u52a8\uff0c\u201c\u4f60\u7528\u9b54\u58f6\u9b42\uff0c\u6682\u65f6\u501f\u7528\u5979\u7684\u8eab\u4f53\uff0c\u8fd9\u6837\u5c31\u6ca1\u95ee\u9898\u4e86\u2014\u2014\u4f60\u5e94\u8be5\u80fd\u4f7f\u7528\u8fd9\u4e2a\u6cd5\u672f\u5427\u3002\u201d</p><p>\u9b54\u58f6\u9b42\u662f\u4e00\u79cd\u90aa\u6076\u7684\u6cd5\u672f\uff0c\u5deb\u5e08\u5c06\u81ea\u5df1\u7684\u7075\u9b42\u8f6c\u79fb\u5230\u67d0\u5757\u5b9d\u77f3\uff08\u6216\u8005\u6c34\u6676\uff09\u4e2d\uff0c\u4ed6\u539f\u672c\u7684\u8eaf\u4f53\u5904\u4e8e\u5047\u6b7b\u72b6\u6001\u3002\u7075\u9b42\u5728\u5b9d\u77f3\u4e2d\u53ef\u4ee5\u611f\u5e94\u5230\u9644\u8fd1\u7684\u751f\u7269\uff0c\u4fb5\u5360\u4ed6\u4eec\u7684\u8eaf\u4f53\uff0c\u5c06\u5bf9\u65b9\u539f\u672c\u7684\u7075\u9b42\u66ff\u6362\u5230\u5b9d\u77f3\u4e2d\u6c38\u8fdc\u56da\u7981\uff0c\u76f4\u81f3\u5deb\u5e08\u613f\u610f\u8c03\u6362\u56de\u6765\u3002\u7528\u743c\u6069\u8bb0\u5fc6\u4e2d\u7684\u67d0\u4e2a\u8bcd\u6765\u5f62\u5bb9\uff0c\u8fd9\u5c31\u53ef\u4ee5\u79f0\u4e3a\u201c\u593a\u820d\u201d\u3002\u8fd9\u79cd\u4f5c\u6cd5\u5f88\u5371\u9669\uff0c\u5982\u679c\u81ea\u5df1\u7684\u7075\u9b42\u4e0d\u591f\u5f3a\u5927\uff0c\u65e0\u6cd5\u538b\u5012\u5bf9\u65b9\u7684\u7075\u9b42\uff0c\u90a3\u4e48\u7ed3\u679c\u5c31\u4f1a\u5f88\u7cdf\u7cd5\u3002\u5f53\u7136\uff0c\u73b0\u5728\u7267\u5e08\u5df2\u7ecf\u5904\u4e8e\u6fd2\u6b7b\u72b6\u6001\uff0c\u7075\u9b42\u6781\u5ea6\u865a\u5f31\uff0c\u593a\u820d\u80af\u5b9a\u4f1a\u6210\u529f\uff0c\u6240\u4ee5\u7ef4\u5eb7\u5c3c\u4e9a\u624d\u6709\u8fd9\u4e2a\u5efa\u8bae\u3002\u8fd9\u4e2a\u6cd5\u672f\u96be\u5ea6\u5f88\u9ad8\uff0c\u5979\u81ea\u5df1\u662f\u4e0d\u4f1a\u7684\u3002</p><p>\u5982\u679c\u6cd5\u672f\u6210\u529f\uff0c\u743c\u6069\u5c06\u501f\u7528\u7267\u5e08\u7684\u8eab\u4f53\uff0c\u4ee5\u53ca\u5979\u643a\u5e26\u7684\u6240\u6709\u7269\u54c1\uff0c\u5982\u6b64\u4e00\u6765\uff0c\u4e0d\u7ba1\u201c\u94a5\u5319\u201d\u5230\u5e95\u662f\u4ec0\u4e48\uff0c\u743c\u6069\u5e94\u8be5\u4e5f\u80fd\u987a\u5229\u901a\u8fc7\u4e86\u3002</p><p>\u743c\u6069\u6447\u5934\uff0c\u201c\u6211\u6ca1\u51c6\u5907\u90a3\u4e2a\u6cd5\u672f\u3002\u201d</p><p>\u5b9e\u9645\u4e0a\uff0c\u4ed6\u662f\u538b\u6839\u4e0d\u4f1a\u3002\u8fd9\u79cd\u64cd\u7eb5\u7075\u9b42\u7684\u4ea1\u7075\u672f\uff0c\u4ece\u6765\u5c31\u4e0d\u662f\u743c\u6069\u7684\u64c5\u957f\uff0c\u4ed6\u8001\u5e08\u5965\u6c83\u5012\u662f\u6b64\u9053\u9ad8\u624b\u3002</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u76b1\u7709\uff0c\u201c\u7528\u53d8\u5f62\u672f\u8bd5\u8bd5\uff1f\u201d\u5979\u63d0\u8bae\uff0c\u201c\u53d8\u6210\u5979\u3002\u201d</p><p>\u201c\u6ca1\u7528\u7684\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u90a3\u4e2a\u6cd5\u672f\u9677\u9631\u91cc\u8574\u542b\u6709\u89e3\u9664\u53d8\u5f62\u7684\u80fd\u529b\uff0c\u6211\u4e00\u9760\u8fd1\u5c31\u4f1a\u88ab\u6062\u590d\u539f\u72b6\u3002\u201d</p><p>\u201c\u6df1\u6e0a\u7684\u7b2c\u516d\u767e\u516d\u5341\u516d\u5c42\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u8bc5\u5492\u7740\uff0c\u5979\u770b\u8d77\u6765\u4e5f\u6ca1\u4e86\u4e3b\u610f\uff0c\u201c\u90a3\u600e\u4e48\u529e\uff1f\u201d</p><p>\u743c\u6069\u4e5f\u5728\u601d\u7d22\uff0c\u5982\u679c\u7ed9\u81ea\u5df1\u52a0\u4e0a\u8db3\u591f\u7684\u9632\u62a4\u786c\u95ef\u7684\u8bdd\uff0c\u5012\u4e5f\u662f\u53ef\u4ee5\u901a\u8fc7\u7684\uff0c\u4f46\u90a3\u5c31\u6beb\u65e0\u610f\u4e49\u4e86\u3002\u4ed6\u4eec\u7684\u4efb\u52a1\u662f\u627e\u5230\u7a81\u7834\u53e3\uff0c\u524a\u5f31\u751a\u81f3\u6467\u6bc1\u8fd9\u4e2a\u9b54\u6cd5\u9635\u3002</p><p>\u8f7b\u5fae\u7684\u811a\u6b65\u58f0\u5728\u8fdc\u5904\u54cd\u8d77\uff0c\u6709\u4eba\u6765\u4e86\u3002</p><p>\u743c\u6069\u770b\u8fc7\u53bb\uff0c\u900f\u8fc7\u4e00\u6392\u77f3\u7b0b\u4ed6\u770b\u89c1\u4e86\u4e24\u4e2a\u5353\u5c14\u6218\u58eb\uff0c\u6b63\u662f\u521a\u624d\u88ab\u6d3e\u53bb\u62bc\u9001\u77ee\u4eba\u4fd8\u864f\u7684\u90a3\u4e24\u4f4d\uff0c\u73b0\u5728\u5b8c\u6210\u4efb\u52a1\u5f52\u6765\u3002\u770b\u4ed6\u4eec\u7684\u795e\u60c5\uff0c\u5e94\u8be5\u8fd8\u6ca1\u6709\u53d1\u73b0\u90a3\u77ee\u4eba\u5176\u5b9e\u662f\u4e2a\u5192\u724c\u8d27\u3002\u743c\u6069\u6b63\u8981\u8eb2\u907f\uff0c\u7a81\u7136\u6709\u4e86\u4e2a\u4e3b\u610f\uff0c\u4ed6\u6309\u4f4f\u7ef4\u5eb7\u5c3c\u4e9a\u7684\u80a9\uff0c\u793a\u610f\u5979\u522b\u51fa\u58f0\u3002</p><p>\u201c\u653e\u8f7b\u677e\uff0c\u201d\u4ed6\u6084\u58f0\u8bf4\uff0c\u4f38\u51fa\u53f3\u624b\u98df\u6307\uff0c\u6307\u5c16\u805a\u96c6\u4e86\u4e00\u70b9\u9634\u51b7\u7684\u9634\u5f71\u80fd\u91cf\uff0c\u5728\u5353\u5c14\u5c11\u5973\u7684\u8138\u4e0a\u8f7b\u8f7b\u52fe\u52d2\uff0c\u201c\u4e0d\u8981\u62b5\u6297\u3002\u201d</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u611f\u89c9\u5230\u76ae\u80a4\u51b0\u51c9\uff0c\u5979\u7acb\u523b\u610f\u8bc6\u5230\u743c\u6069\u4f7f\u7528\u4e86\u4e00\u4e2a\u5e7b\u672f\uff0c\u6539\u53d8\u4e86\u81ea\u5df1\u7684\u76f8\u8c8c\uff0c\u4f46\u5979\u6ca1\u529e\u6cd5\u770b\u5230\u81ea\u5df1\u7684\u8138\uff0c\u624b\u8fb9\u53c8\u6ca1\u6709\u955c\u5b50\u3002\u4f46\u5f53\u5979\u4f4e\u5934\uff0c\u770b\u89c1\u5730\u9762\u4e0a\u7684\u7267\u5e08\u5c38\u4f53\u2014\u2014\u5df2\u7ecf\u65ad\u6c14\u4e86\u2014\u2014\u4e0d\u77e5\u4f55\u65f6\u53d8\u6210\u4e86\u81ea\u5df1\u7684\u6a21\u6837\uff0c\u63a5\u7740\u53d1\u73b0\u81ea\u5df1\u8eab\u4e0a\u7684\u670d\u9970\u4e5f\u53d1\u751f\u4e86\u53d8\u5316\uff0c\u6b63\u662f\u8fd9\u4e2a\u7267\u5e08\u539f\u672c\u7684\u7a7f\u6234\uff0c\u4e8e\u662f\u5979\u660e\u767d\u4e86\u743c\u6069\u7684\u610f\u601d\u3002</p><p>\u201c\u53eb\u4e00\u4e2a\u5deb\u5e08\u8fc7\u6765\uff0c\u201d\u743c\u6069\u5728\u5979\u8033\u8fb9\u8f7b\u58f0\u4f4e\u8bed\uff0c\u201c\u5deb\u5e08\u8eab\u4e0a\u5e94\u8be5\u4f1a\u6709\u9b54\u6cd5\u5bb6\u5fbd\u3002\u201d</p><p>\u201c\u597d\u7684\u3002\u201d</p><p>\u743c\u6069\u6084\u6084\u9000\u5f00\uff0c\u5728\u65c1\u8fb9\u4e00\u5ea7\u77f3\u7b0b\u7684\u9634\u5f71\u4e2d\u9690\u85cf\u8d77\u6765\uff0c\u7b49\u5f85\u7740\u3002\u4e24\u4e2a\u6218\u58eb\u8d70\u8fc7\u6765\uff0c\u7136\u540e\u770b\u89c1\u5730\u4e0a\u7684\u5c38\u4f53\uff0c\u4ee5\u53ca\u552f\u4e00\u7ad9\u7740\u7684\u7267\u5e08\u3002\u4ed6\u4eec\u5927\u60ca\u5931\u8272\uff0c\u7acb\u523b\u62d4\u51fa\u4e86\u6b66\u5668\uff0c\u6212\u5907\u7740\uff0c\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u83b1\u5bc6\u65af\u51ef\u5c0f\u59d0\uff1f\u201d</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u51b7\u51b7\u5730\u76ef\u7740\u4ed6\u4eec\uff0c\u795e\u60c5\u5028\u50b2\uff0c\u201c\u4f60\u4eec\u5fd8\u4e86\u52a0\u4e0a\u656c\u79f0\uff0c\u7537\u6027\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u5e94\u8be5\u662f\u5c0a\u656c\u7684\u83b1\u5bc6\u65af\u51ef\u5c0f\u59d0\u3002\u201d</p><p>\u5973\u7267\u5e08\u662f\u795e\u540e\u5728\u51e1\u95f4\u7684\u4ee3\u8a00\u4eba\uff0c\u662f\u6574\u4e2a\u5353\u5c14\u793e\u4f1a\u7684\u9886\u5bfc\uff0c\u7537\u6027\u5728\u79f0\u547c\u5979\u4eec\u7684\u65f6\u5019\uff0c\u6309\u7167\u793c\u8282\u5e94\u8be5\u52a0\u4e0a\u656c\u79f0\u2014\u2014\u867d\u7136\u67d0\u4e9b\u5f3a\u5927\u7684\u7537\u6027\u6709\u610f\u65e0\u610f\u5730\u4e0d\u9075\u5b88\u8fd9\u4e2a\u89c4\u77e9\uff0c\u4f8b\u5982\u9996\u5e2d\u5deb\u5e08\uff0c\u4ed6\u4eec\u4e5f\u80fd\u88ab\u5bb9\u5fcd\u3002\u7ef4\u5eb7\u5c3c\u4e9a\u8ba4\u4e3a\u8fd9\u4e24\u4e2a\u745e\u8d39\u5fb7\u5bb6\u7684\u7537\u6027\u6218\u58eb\u5e94\u8be5\u6ca1\u6709\u8fd9\u79cd\u80c6\u91cf\uff0c\u4f46\u5f53\u5979\u8fd9\u4e48\u8bf4\u7684\u65f6\u5019\uff0c\u5bf9\u65b9\u4f3c\u4e4e\u6709\u4e9b\u8be7\u5f02\uff0c\u4f46\u4ed6\u4eec\u8fd8\u662f\u4f9d\u8a00\u8eac\u8eab\u884c\u793c\u3002\u201c\u5c0a\u656c\u7684\u83b1\u5bc6\u65af\u51ef\u5c0f\u59d0\uff0c\u201d\u5176\u4e2d\u4e00\u4e2a\u6218\u58eb\u95ee\uff0c\u201c\u5230\u5e95\u53d1\u751f\u4ec0\u4e48\u4e8b\u60c5\u4e86\uff1f\u201d</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u5ffd\u7565\u6389\u4ed6\u8bed\u6c14\u4e2d\u7684\u4e0d\u656c\uff0c\u6216\u8bb8\u662f\u745e\u8d39\u5fb7\u5bb6\u7684\u5deb\u5e08\u4eec\u628a\u7537\u6027\u7eb5\u5bb9\u5f97\u592a\u5389\u5bb3\u4e86\uff0c\u201c\u521a\u624d\u90a3\u4e2a\u77ee\u4eba\u53ea\u662f\u4e2a\u63a9\u62a4\uff0c\u201d\u5979\u8bf4\uff0c\u671d\u5730\u4e0a\u7684\u7267\u5e08\u5c38\u4f53\u8e22\u4e86\u4e00\u811a\uff0c\u201c\u771f\u6b63\u7684\u5165\u4fb5\u8005\u662f\u5979\u3002\u201d</p><p>\u6218\u58eb\u671d\u5730\u4e0a\u770b\u53bb\uff0c\u4ed6\u4eec\u8fa8\u8ba4\u51fa\u4e86\u5c38\u4f53\u957f\u888d\u4e0a\u7684\u767d\u91d1\u80f8\u9488\uff0c\u201c\u83f2\u5c14\u4f26\uff1f\u201d</p><p>\u201c\u7ef4\u5eb7\u5c3c\u4e9a\xb7\u83f2\u5c14\u4f26\uff0c\u201d\u5353\u5c14\u5c11\u5973\u4e00\u672c\u6b63\u7ecf\u5730\u8bf4\u51fa\u81ea\u5df1\u7684\u540d\u5b57\uff0c\u5979\u611f\u89c9\u9887\u6709\u4e9b\u5947\u5999\uff0c\u201c\u4ed6\u4eec\u5bb6\u65cf\u6700\u5e74\u8f7b\u7684\u7267\u5e08\uff0c\u5f88\u6709\u624d\u534e\u3002\u201d</p><p>\u201c\u4f46\u8fd8\u662f\u88ab\u60a8\u6253\u8d25\u4e86\u3002\u201d\u6218\u58eb\u606d\u7ef4\u3002</p><p>\u201c\u90a3\u662f\u81ea\u7136\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u8bf4\uff0c\u4e0d\u60f3\u518d\u7ee7\u7eed\u8fd9\u4e2a\u8bdd\u9898\uff0c\u201c\u73b0\u5728\uff0c\u201d\u5979\u4e0b\u547d\u4ee4\uff0c\u201c\u4f60\u4eec\u8d76\u5feb\u53bb\u627e\u4e2a\u5deb\u5e08\u6765\uff0c\u4e0d\u7ba1\u8c01\u90fd\u884c\u3002\u6211\u611f\u89c9\u8fd9\u4e2a\u4f4d\u7f6e\u5df2\u7ecf\u53d8\u5f97\u4e0d\u518d\u5b89\u5168\uff0c\u5f88\u6709\u53ef\u80fd\u662f\u67d0\u4e2a\u7b26\u6587\u592a\u8fc7\u53e4\u8001\uff0c\u4e27\u5931\u4f5c\u7528\uff0c\u6216\u8005\u67d0\u4e2a\u7ed3\u754c\u88ab\u6211\u4eec\u7684\u654c\u4eba\u6697\u4e2d\u7834\u574f\u4e86\uff0c\u6240\u4ee5\u624d\u80fd\u88ab\u5979\u6f5c\u5165\u3002\u5982\u679c\u6b63\u5982\u6211\u6240\u6599\uff0c\u90a3\u4e48\u6211\u4eec\u5c31\u9700\u8981\u4e00\u4e2a\u5deb\u5e08\u6765\u8865\u4e0a\u6f0f\u6d1e\uff0c\u5426\u5219\u8bf4\u4e0d\u5b9a\u4e0b\u6b21\u5c31\u662f\u5927\u4e3e\u8fdb\u653b\u4e86\u3002\u201d</p><p>\u201c\u90a3\u8fd9\u91cc\u2026\u2026\u201d</p><p>\u201c\u6211\u5b88\u5728\u8fd9\u91cc\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u8bf4\uff0c\u5979\u793a\u5a01\u6027\u5730\u5c06\u624b\u6309\u5728\u8170\u95f4\u86c7\u9996\u97ad\u7684\u7cbe\u91d1\u624b\u67c4\u4e0a\uff0c\u201c\u6216\u8005\u4f60\u4eec\u6000\u7591\u6211\u7684\u80fd\u529b\uff1f\u201d</p><p>\u201c\u4e0d\u4e0d\uff0c\u7edd\u65e0\u6b64\u610f\uff0c\u5c0a\u656c\u7684\u83b1\u5bc6\u65af\u51ef\u5c0f\u59d0\uff0c\u201d\u6218\u58eb\u540e\u9000\uff0c\u201c\u6211\u4eec\u73b0\u5728\u5c31\u53bb\u3002\u201d</p><p>\u201c\u6469\u767b\u8fe6\u6216\u8005\u5965\u68ee\u5c31\u884c\u3002\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u8865\u5145\u8bf4\uff0c\u5979\u559c\u597d\u5965\u672f\uff0c\u5e73\u65f6\u548c\u5deb\u5e08\u6253\u4ea4\u9053\u8f83\u591a\uff0c\u5012\u8fd8\u8ba4\u8bc6\u51e0\u4e2a\u745e\u8d39\u5fb7\u5bb6\u65cf\u7684\u5deb\u5e08\uff0c\u8bf4\u7684\u8fd9\u4e24\u4e2a\u90fd\u662f\u6bd4\u8f83\u5e74\u8f7b\u7684\uff0c\u80fd\u529b\u4e0d\u5f3a\u3002\u6bd5\u7adf\u662f\u6253\u7740\u6740\u4eba\u8d8a\u8d27\u7684\u4e3b\u610f\uff0c\u5018\u82e5\u53eb\u6765\u4e00\u4e2a\u9ad8\u624b\uff0c\u5230\u65f6\u5019\u5bf9\u4ed8\u4e0d\u4e86\uff0c\u90a3\u5c31\u662f\u642c\u77f3\u5934\u7838\u81ea\u5df1\u811a\u4e86\u3002</p><p>\u201c\u597d\u7684\u3002\u201d\u6218\u58eb\u8bf4\u3002</p><p>\u4ed6\u4eec\u5306\u5306\u79bb\u53bb\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u7b49\u5f85\u4e86\u4e00\u4f1a\uff0c\u7136\u540e\u8d70\u5230\u743c\u6069\u85cf\u8eab\u7684\u5730\u65b9\uff0c\u201c\u600e\u4e48\u6837\uff1f\u201d\u5979\u4e0d\u65e0\u70ab\u8000\u5730\u8bf4\u3002</p><p>\u201c\u68d2\u6781\u4e86\uff0c\u201d\u743c\u6069\u534a\u771f\u534a\u5047\u5730\u5938\u5956\uff0c\u201c\u5b8c\u7f8e\u65e0\u6687\uff0c\u53ef\u4ee5\u53bb\u7ade\u4e89\u5965\u65af\u5361\u4e86\u3002\u201d</p><p>\u201c\u5965\u65af\u5361\uff1f\u201d</p><p>\u201c\u6211\u5bb6\u4e61\u7684\u4e00\u4e2a\u8868\u6f14\u827a\u672f\u5956\u3002\u201d\u743c\u6069\u542b\u7cca\u89e3\u91ca\u3002</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u9762\u9732\u5fae\u7b11\uff0c\u5979\u6b64\u523b\u770b\u8d77\u6765\u5c31\u50cf\u662f\u4e00\u4e2a\u5929\u771f\u65e0\u90aa\u7684\u5c11\u5973\uff0c\u521a\u505a\u4e86\u4e00\u4ef6\u5f97\u610f\u7684\u4e8b\u60c5\uff0c\u6b63\u5411\u60c5\u4eba\u70ab\u8000\u9080\u529f\u3002\u743c\u6069\u770b\u7740\u5979\uff0c\u5fc3\u4e2d\u5fae\u5fae\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u6ca1\u6709\u8bf4\u8bdd\u3002</p><p>\u5927\u7ea6\u5341\u5206\u949f\u4e4b\u540e\uff0c\u4e24\u4e2a\u6218\u58eb\u8fd4\u56de\uff0c\u4ed6\u4eec\u8eab\u540e\u8ddf\u968f\u7740\u4e00\u4e2a\u7070\u888d\u5deb\u5e08\uff0c\u8eab\u6750\u77ee\u5c0f\uff0c\u8138\u4e0a\u6234\u7740\u4e00\u4e2a\u9ed1\u8272\u4e1d\u7ec7\u9762\u5177\uff0c\u624b\u4e2d\u63e1\u7740\u767d\u9aa8\u6cd5\u6756\uff0c\u6756\u5934\u96d5\u523b\u7740\u6076\u9b54\u7684\u5934\u9885\u3002\u743c\u6069\u6253\u91cf\u7740\uff0c\u5c06\u76ee\u5149\u79fb\u5230\u4ed6\u7684\u5de6\u624b\uff0c\u60f3\u770b\u6709\u6ca1\u6709\u4f69\u6234\u9b54\u6cd5\u6212\u6307\uff0c\u7ed3\u679c\u5374\u53d1\u73b0\u624b\u8155\u4e0a\u6709\u4e00\u4e2a\u94f6\u7070\u8272\u7684\u91d1\u5c5e\u62a4\u8155\u3002</p><p>\u5deb\u5e08\u901a\u5e38\u662f\u4e0d\u6234\u91d1\u5c5e\u62a4\u8155\u7684\uff0c\u90a3\u662f\u6218\u58eb\u4eec\u7684\u504f\u597d\u3002\u5728\u74dc\u7406\u5fb7\u65af\u57ce\uff0c\u8fd9\u6709\u7279\u522b\u7684\u610f\u4e49\u3002</p><p>\u201c\u8be5\u6b7b\uff0c\u201d\u743c\u6069\u4f4e\u58f0\u8bf4\uff0c\u201c\u4e00\u4e2a\u5bfc\u5e08\u3002\u201d</p></article>"}}}]);
