(window.webpackJsonp=window.webpackJsonp||[]).push([[258],{281:function(p){p.exports={chapterTitle:null,title:"\u7b2c143\u7ae0 \u8c6a\u60c5\u58ee\u5fd7",content:"<article><h2>\u7b2c143\u7ae0 \u8c6a\u60c5\u58ee\u5fd7</h2><p>\u3000\u3000\u9664\u4e86\u5f20\u606a\uff0c\u5176\u4ed6\u4e09\u4eba\u90fd\u65e0\u6cd5\u51c6\u5907\u5224\u65ad\u5f71\u789f\u673a\u7684\u5e02\u573a\u5230\u5e95\u6709\u591a\u5927\uff0c\u6574\u4e2a\u4e5d\u56db\u5e74\uff0c\u56fd\u5185\u53ea\u552e\u51fa\u4e24\u4e07\u53f0\u5f71\u789f\u673a\uff0c\u4e5d\u4e94\u5e74\u7684\u5c40\u52bf\u80af\u5b9a\u8981\u8fdc\u8fdc\u597d\u8fc7\u4e5d\u56db\u5e74\uff0c\u4f46\u662f\u5230\u5e95\u6709\u591a\u597d\uff0c\u73b0\u5728\u505a\u51fa\u5224\u65ad\u8fd8\u4e3a\u65f6\u8fc7\u65e9\u3002</p><p>\u3000\u3000\u5f20\u606a\u5374\u77e5\u9053\uff0c\u5230\u4e5d\u4e03\u5e74\u90a3\u662f\u4e00\u5343\u4e07\u53f0\uff01</p><p>\u3000\u3000\u5f20\u606a\u5e73\u9759\u7684\u8bf4\u9053\uff1a\u201c\u5728\u5e02\u573a\u5bb9\u91cf\u6ca1\u6709\u786e\u5b9a\u4e4b\u524d\uff0c\u4ec0\u4e48\u4ef7\u683c\u90fd\u662f\u4e0d\u5408\u9002\u7684\uff0c\u6211\u4eec\u53ef\u4ee5\u514d\u8d39\u63d0\u4f9b\u8fd9\u4efd\u65b9\u6848\u7ed9TI\uff0c\u4f46\u662f\u8981\u6c42TI\u516c\u53f8\u6210\u529f\u5f00\u53d1\u51fa\u65b0\u4e00\u4ee3\u82af\u7247\u4e4b\u540e\uff0c\u81f3\u5c11\u5728\u4e5d\u516b\u5e74\u4e4b\u524d\uff0c\u4ee5\u56fa\u5b9a\u7684\u534f\u8bae\u4ef7\u683c\u4f9b\u5e94\u6211\u4eec\u751f\u4ea7\u6240\u9700\u7684\u89e3\u7801\u82af\u7247\uff0c\u6211\u4eec\u53ef\u4ee5\u4e0d\u5206\u4eab\u89e3\u7801\u82af\u7247\u7684\u4e13\u5229\u6743\uff0c\u4f46\u662f\u4e3a\u4e86\u7f29\u51cf\u6574\u673a\u4ea7\u54c1\u7684\u5f00\u53d1\u65f6\u95f4\uff0c\u6211\u4eec\u5e0c\u671b\u5728\u4e00\u5b9a\u7a0b\u5ea6\u4e0a\u53c2\u4e0eTI\u516c\u53f8\u89e3\u7801\u82af\u7247\u7684\u7814\u53d1\u2026\u2026\u201d</p><p>\u3000\u3000\u4e00\u4e2a\u4eba\u7684\u773c\u754c\u6709\u591a\u9ad8\uff0c\u89c6\u91ce\u6709\u591a\u5e7f\uff0c\u53ef\u4ee5\u4ece\u4ed6\u5bf9\u5229\u76ca\u7684\u8bc9\u6c42\u4e0a\u5224\u65ad\u3002\u542c\u5f20\u606a\u8bf4\u4ed6\u7684\u8981\u6c42\uff0c\u9648\u4fe1\u751f\u5c31\u77e5\u9053\u8fd9\u6837\u7684\u8c08\u5224\u5e76\u4e0d\u662f\u8c08\u5224\u6280\u529f\u53ef\u4ee5\u6298\u670d\u7684\uff0c\u9648\u4fe1\u751f\u7b11\u7740\u8bf4\uff1a\u201c\u5230\u73b0\u5728\u6211\u624d\u77e5\u9053\u7231\u8fbe\u7684\u6c14\u91cf\uff0c\u8bf4\u53e5\u5b9e\u8bdd\uff0c\u6211\u521a\u4e0b\u98de\u673a\uff0c\u4e5f\u89c9\u5f97\u5f20\u5148\u751f\u90a3\u8f86\u6851\u5854\u90a3\u7a0d\u5fae\u5dee\u4e86\u4e00\u4e9b\uff0c\u8fd9\u65f6\u5019\u6211\u5374\u8981\u60f3\uff1a\u5f20\u5148\u751f\u662f\u4e0d\u662f\u6709\u610f\u5e0c\u671b\u6211\u9519\u8bef\u5224\u65ad\u7231\u8fbe\u7535\u5b50\u7684\u53d1\u5c55\u6f5c\u529b\uff1f\u201d</p><p>\u3000\u3000TI\u516c\u53f8\u8fdb\u5165\u89e3\u7801\u82af\u7247\u5e02\u573a\uff0cC-Cube\u7684\u89e3\u7801\u82af\u7247\u7684\u4ef7\u683c\u80af\u5b9a\u65e0\u6cd5\u7ef4\u6301\u57281000\u6e2f\u5143\u6bcf\u7247\u7684\u9ad8\u4f4d\uff0c\u4f46\u6bd5\u7adf\u53ea\u6709\u4e24\u5bb6\u516c\u53f8\u8fdb\u5165\u89e3\u7801\u82af\u7247\u5e02\u573a\uff0c\u8fd9\u4e24\u5bb6\u516c\u53f8\u4e5f\u4e0d\u4f1a\u81ea\u76f8\u6b8b\u6740\u6253\u7834\u89e3\u7801\u82af\u7247\u7684\u5784\u65ad\u5c40\u9762\uff0c\u5728\u7b2c\u4e09\u5bb6\u4f01\u4e1a\u52a0\u5165\u4e4b\u524d\uff0c\u7ef4\u6301\u5784\u65ad\u4ef7\u683c\u90a3\u662f\u4e00\u5b9a\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u63d0\u51fa\u56fa\u5b9a\u534f\u8bae\u4ef7\u683c\u4f9b\u8d27\u7684\u6761\u4ef6\uff0c\u5c31\u662f\u8981\u4eceTI\u516c\u53f8\u5206\u4eab\u89e3\u7801\u82af\u7247\u5e02\u573a\u7684\u5784\u65ad\u66b4\u5229\uff0c\u8fd9\u672c\u5e94\u8be5\u662f\u4e07\u71d5\u4eceC-Cube\u5206\u4eab\u7684\u6743\u5229\uff0c\u53ef\u60dc\u4e07\u71d5\u6ca1\u6709\u628a\u63e1\u4f4f\u673a\u4f1a\u3002</p><p>\u3000\u3000\u5f20\u606a\u7a76\u7adf\u80fd\u4eceTI\u516c\u53f8\u90a3\u91cc\u5206\u591a\u5c11\u6c64\u6c34\uff0c\u5c31\u770b\u5230\u7231\u8fbe\u7535\u5b50\u5728\u8fd9\u4e24\u4e09\u5e74\u671f\u95f4\u91cc\u7684\u53d1\u5c55\u6f5c\u529b\uff0cTI\u516c\u53f8\u7edd\u4e0d\u53ef\u80fd\u4e0d\u52a0\u9650\u5236\u7684\u4f9b\u8d27\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u80fd\u5750\u5230TI\u4e2d\u56fd\u516c\u53f8\u603b\u88c1\u7684\u4f4d\u5b50\u4e0a\uff0c\u53ef\u4e0d\u662f\u6df7\u8fc7\u6765\u4e86\uff0c\u5bf9\u7231\u8fbe\u7535\u5b50\u6700\u521d\u7684\u5224\u65ad\u4e0d\u8fc7\u51fa\u4e8e\u804c\u4e1a\u6027\u7684\u5224\u65ad\uff0cTI\u516c\u53f8\u4f5c\u4e3a\u5168\u7403\u9886\u5148\u7684\u534a\u5bfc\u4f53\u89e3\u51b3\u65b9\u6848\u63d0\u4f9b\u5546\uff0c\u9648\u4fe1\u751f\u4e00\u4e2a\u6708\u5185\u4e0d\u6653\u5f97\u8981\u63a5\u89e6\u591a\u5c11\u7535\u5b50\u5382\uff0c\u4e00\u65f6\u7684\u5224\u65ad\u5931\u8bef\u662f\u53ef\u4ee5\u7406\u89e3\u7684\uff0c\u5e76\u4e0d\u610f\u5473\u7740\u4ed6\u4f1a\u4e00\u76f4\u7ed9\u5f20\u606a\u7275\u7740\u9f3b\u5b50\u3002</p><p>\u3000\u3000\u5f20\u606a\u89c1\u9648\u4fe1\u751f\u770b\u7a7f\u81ea\u5df1\u7684\u5c0f\u628a\u620f\uff0c\u5c34\u5c2c\u7684\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e5f\u6015\u8ba9\u9648\u5148\u751f\u8fc7\u9ad8\u7684\u4f30\u8ba1\u7231\u8fbe\u7535\u5b50\u7684\u53d1\u5c55\u6f5c\u529b\u554a\u3002\u201d</p><p>\u3000\u3000\u9648\u4fe1\u751f\u5766\u7387\u7684\u8bf4\uff1a\u201c\u8fd9\u6b21\u89c1\u9762\u6211\u662f\u53d7TI\u603b\u90e8\u7684\u59d4\u6258\uff0c\u4f60\u6240\u63d0\u7684\u8981\u6c42\u8ddfTI\u603b\u90e8\u7ed9\u6211\u7684\u6388\u6743\u4e0d\u5c3d\u76f8\u540c\uff0c\u6240\u4ee5\u6211\u8981\u5148\u4e0eTI\u603b\u90e8\u90a3\u8fb9\u6c9f\u901a\uff0c\u4e3b\u610f\u6700\u7ec8\u8fd8\u5f97TI\u603b\u90e8\u90a3\u8fb9\u62ff\u2026\u2026\u201d\u9648\u4fe1\u751f\u53c8\u8bf4\uff0c\u201c\u4e0d\u5f97\u4e0d\u627f\u8ba4\uff0c\u5f20\u5148\u751f\u770b\u5230\u6700\u7b26\u5408\u7231\u8fbe\u5229\u76ca\u7684\u90a3\u6761\u8def\uff0c\u80fd\u4e0e\u7231\u8fbe\u8fd9\u6837\u7684\u516c\u53f8\uff0c\u7279\u522b\u4e0e\u5f20\u5148\u751f\u8fd9\u6837\u6709\u773c\u5149\u7684\u4eba\u5408\u4f5c\uff0c\u662fTI\u6240\u671f\u5f85\u7684\u2026\u2026\u201d\u867d\u7136\u4e0d\u77e5\u9053\u6700\u7ec8\u7684\u7ed3\u679c\u5982\u4f55\uff0c\u9648\u4fe1\u751f\u8fd8\u662f\u8868\u8fbe\u4e86\u5bf9\u5f20\u606a\u3001\u4ee5\u53ca\u7231\u8fbe\u7535\u5b50\u7684\u5c0a\u656c\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u4e86\u770b\u8155\u8868\uff0c\u5df2\u7ecf\u662f\u51cc\u6668\u4e24\u70b9\u4e86\uff0c\u6b64\u65f6\u6b63\u662fTI\u603b\u90e8\u7684\u5de5\u4f5c\u65f6\u95f4\uff0c\u9648\u4fe1\u751f\u8868\u793a\u8981\u56de\u5bbe\u9986\uff0c\u5f20\u606a\u4e0e\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u9001\u4ed6\u56de\u897f\u57ce\u996d\u5e97\u3002</p><p>\u3000\u3000\u4ece\u897f\u57ce\u996d\u5e97\u51fa\u6765\uff0c\u5f20\u606a\u4e0e\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u5e76\u6ca1\u6709\u6025\u4e8e\u5750\u5230\u8f66\u91cc\uff0c\u800c\u662f\u7ad9\u5728\u897f\u57ce\u996d\u5e97\u5916\u7684\u82b1\u575b\u524d\u62bd\u70df\uff0c\u71ac\u591c\u7684\u4eba\u90fd\u6709\u70df\u763e\uff0c\u5f20\u606a\u6700\u8fd1\u4e5f\u4e0d\u5f97\u4e0d\u7528\u70df\u63d0\u795e\u3002</p><p>\u3000\u3000\u201cTI\u8fd9\u6b21\u53ef\u662f\u60f3\u62ff\u771f\u91d1\u767d\u94f6\u6765\u4e70\u65b9\u6848\u5462\uff0c\u6211\u4eec\u8981\u662f\u4e0d\u63d0\u534f\u8bae\u4f9b\u8d27\u7684\u6761\u4ef6\uff0c\u4ed6\u4eec\u6700\u9ad8\u80fd\u5f00\u591a\u5c11\uff1f\u201d\u4e01\u69d0\u7b11\u7740\u95ee\u3002</p><p>\u3000\u3000\u5f20\u606a\u8e72\u5728\u82b1\u575b\u8fb9\uff0c\u770b\u7740\u4e01\u69d0\uff0c\u53c8\u770b\u4e86\u770b\u82cf\u6d25\u4e1c\uff0c\u4ed6\u4eec\u8fd8\u4e0d\u6e05\u695a\u4e07\u71d5\u5931\u53bb\u7684\u673a\u4f1a\u5728\u4e24\u4e09\u5e74\u540e\u4f1a\u8ba9\u4eba\u6709\u591a\u4e48\u7684\u75db\u60dc\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u8001\u4e01\u8fd9\u4e00\u4e2a\u6708\u90fd\u6ca1\u6709\u597d\u597d\u7761\u4e00\u89c9\u5427\uff0c\u4f60\u731c\u731c\u770b\uff0c\u8981\u662f\u76f4\u63a5\u62ff\u94b1\u7684\u8bdd\uff0cTI\u80fd\u7ed9\u591a\u5c11\u94b1\uff1f\u201d</p><p>\u3000\u3000\u4e01\u69d0\u7b11\u4e86\u7b11\uff0c\u6447\u4e86\u6447\u5934\uff0c\u6ca1\u6709\u8bf4\u8bdd\u3002</p><p>\u3000\u3000\u82cf\u6d25\u4e1c\u4e5f\u772f\u8d77\u773c\u775b\u7b11\u4e86\u7b11\uff0c\u5f53\u521d\u5230\u6d77\u5dde\u6765\uff0c\u5f20\u606a\u53ef\u662f\u7b54\u5e94\u9664\u4e86\u56fa\u5b9a\u5e74\u85aa\u4e4b\u5916\u8fd8\u6839\u636e\u76f8\u5e94\u7684\u8d21\u732e\u7ed9\u4e88\u5956\u52b1\uff0c\u7ed3\u679c\u600e\u4e48\u6837\u8fd8\u4e0d\u77e5\u9053\uff0c\u4ec5\u4ec5\u662f\u83b7\u5f97\u80fd\u4e0eTI\u8fd9\u6837\u7684\u5927\u516c\u53f8\u5408\u4f5c\u7684\u673a\u4f1a\uff0c\u5c31\u662f\u7edd\u5927\u7684\u6210\u7ee9\u3002</p><p>\u3000\u3000\u4e0d\u8fc7\u82cf\u6d25\u4e1c\u8fd8\u771f\u4f69\u670d\u5f20\u606a\u7684\u773c\u5149\u4e0e\u9b44\u529b\uff0c\u4ec5\u4ec5\u662f\u5916\u6587\u6742\u5fd7\u4e0a\u7684\u4e00\u6761\u6d88\u606f\uff0c\u5c31\u60f3\u7740\u52aa\u529b\u8ba9TI\u516c\u53f8\u6539\u53d8\u81ea\u5df1\u505a\u51fa\u7684\u51b3\u5b9a\uff0c\u8fd8\u4e0d\u60dc\u653e\u7f13\u7231\u8fbe\u7535\u5b50\u6837\u673a\u7684\u5f00\u53d1\u8fdb\u5ea6\u3002</p><p>\u3000\u3000\u6709\u4e86\u6210\u7ee9\uff0c\u5206\u4eab\u81ea\u5df1\u5e94\u5f97\u7684\u5229\u76ca\uff0c\u4e01\u69d0\u6709\u8fd9\u79cd\u60f3\u5012\u4e5f\u4e0d\u4e3a\u602a\u3002\u4e0e\u522b\u4eba\u5206\u5229\u662f\u79cd\u597d\u4e60\u60ef\uff0c\u8981\u662f\u597d\u5904\u5360\u5c3d\uff0c\u90a3\u5c31\u662f\u6210\u4e86\u5b64\u5bb6\u5be1\u4eba\uff0c\u5f20\u606a\u62bd\u7740\u70df\uff0c\u62cd\u4e86\u62cd\u82b1\u575b\u8fb9\uff0c\u8ba9\u4e01\u69d0\u3001\u82cf\u6d25\u4e1c\u4e5f\u8e72\u8fc7\u6765\uff0c\u81ea\u4fe1\u6ee1\u6ee1\u7684\u8bf4\uff1a\u201c\u4e0eTI\u5408\u4f5c\u7684\u4ef7\u503c\u4e0d\u662f\u62ff\u771f\u91d1\u767d\u94f6\u5c31\u80fd\u8861\u91cf\u7684\uff0c\u8001\u4e01\uff0c\u6211\u7ed9\u4f60\u4e00\u4e2a\u9009\u62e9\uff0c\u5e74\u85aa\u589e\u52a0\u523030\u4e07\u6216\u8005\u653e\u5f0315\u4e07\u7684\u5e74\u85aa\uff0c\u6211\u7b97\u4f603\uff05\u7684\u6280\u672f\u5165\u80a1\uff0c\u4f60\u9009\u62e9\u54ea\u4e00\u4e2a\uff1f\u201d</p><p>\u3000\u3000\u4e01\u6127\u4e0e\u82cf\u6d25\u4e1c\u5bf9\u770b\u4e86\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c30\u4e07\u5e74\u85aa\u554a\uff0c\u6765\u6d77\u5dde\u524d\u60f3\u4e0d\u6562\u60f3\uff0c15\u4e07\u90fd\u89c9\u5f97\u5962\u4f88\uff0c\u4f46\u662f\u80fd\u8ddf\u7231\u8fbe\u540c\u7518\u5171\u82e6\uff0c\u5374\u662f\u6211\u60f3\u505a\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u64a9\u773c\u770b\u7740\u4e01\u69d0\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u4f60\u786e\u5b9a\u5382\u5b50\u660e\u5e74\u80fd\u8d5a\u4e00\u5343\u4e07\uff1f\u201d</p><p>\u3000\u3000\u4e01\u69d0\u7b11\u7740\u8bf4\uff1a\u201c\u4e0d\u4f1a\u8ba9\u6211\u4eca\u5e74\u7684\u5341\u4e94\u4e07\u4e5f\u5410\u51fa\u6765\u5427\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u6447\u5934\u7b11\u4e86\u7b11\uff0c\u8f6c\u8fc7\u5934\u95ee\u82cf\u6d25\u4e1c\uff1a\u201c\u4e0b\u9762\u8be5\u8001\u82cf\u505a\u9009\u62e9\u9898\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u82cf\u6d25\u4e1c\u6487\u6487\u5634\u7b11\u4e86\u7b11\uff1a\u201c\u8fd9\u70b9\u773c\u5149\u90fd\u6ca1\u6709\uff0c\u600e\u4e48\u8ddf\u7740\u606a\u5c11\u6df7\uff1f\u201d</p><p>\u3000\u3000\u201cTI\u53ef\u80fd\u4f1a\u9650\u5236\u4f9b\u8d27\u91cf\uff0c\u4f46\u4e0d\u4f1a\u62d2\u7edd\u6211\u4eec\u7684\u65b9\u6848\uff0c\u89e3\u7801\u82af\u7247\u5bf9\u76ee\u524d\u7684TI\u6765\u8bf4\uff0c\u8fd8\u53ea\u662f\u4e00\u9879\u5f88\u7a84\u7684\u4e1a\u52a1\uff0c\u201d\u5f20\u606a\u6491\u7740\u4ed6\u4eec\u4fe9\u4eba\u7684\u80a9\u8180\u7ad9\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u8fd9\u4efd\u6280\u672f\u89e3\u51b3\u65b9\u6848\uff0c\u81f3\u5c11\u80fd\u5e2eTI\u7f29\u77ed\u5927\u534a\u5e74\u7684\u7814\u53d1\u65f6\u95f4\uff0c\u5c31\u62ff\u773c\u4e0b\u80fd\u770b\u5230\u7684\u5e02\u573a\u524d\u666f\u6765\u5224\u65ad\uff0c\u8fd9\u4efd\u65b9\u6848\u81f3\u5c11\u8981\u503c\u4e94\u767e\u4e07\uff0c\u4f46\u662f\u6211\u76f8\u4fe1\uff0c\u5176\u4ef7\u503c\u8fdc\u8fdc\u4e0d\u81f3500\u4e07\uff0c\u4f60\u4eec\u4e0d\u4f1a\u540e\u6094\u4eca\u5929\u7684\u51b3\u5b9a\u7684\uff0c\u73b0\u5728\u7b97\u4f60\u4eec\u4fe9\u4eba\u54043\uff05\u7684\u6280\u672f\u3001\u7ba1\u7406\u5165\u80a1\uff0c\u7b49\u63a8\u51fa\u7b2c\u4e00\u6279\u4ea7\u54c1\u7684\u65f6\u5019\uff0c\u6211\u628a\u4f60\u4eec\u80a1\u4efd\u63d0\u9ad8\u52305\uff05\u2026\u2026\u201d</p><p>\u3000\u3000\u82cf\u6d25\u4e1c\u5012\u8fd8\u6de1\u5b9a\uff0c\u8bf4\u9053\uff1a\u201c\u606a\u5c11\u5e2eTI\u516c\u53f8\u633d\u56de\u53ef\u80fd\u662f\u4ed6\u4eec\u8fd1\u5e74\u6765\u6700\u4e25\u91cd\u7684\u4e00\u9879\u9519\u8bef\u51b3\u7b56\uff0c\u5c31\u51ed\u7740\u8fd9\u4efd\u8d21\u732e\uff0cTI\u516c\u53f8\u4e5f\u4e0d\u4f1a\u63d0\u51fa\u8fc7\u4efd\u82db\u523b\u7684\u6761\u4ef6\u5427\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u770b\u7740\u897f\u57ce\u996d\u5e97\u4e3b\u697c\u5c04\u51fa\u7684\u706f\u5149\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u5962\u671b\u5982\u6b64\uff0c\u505a\u751f\u610f\uff0c\u90fd\u662f\u8981\u4e89\u53d6\u6700\u5927\u5229\u6da6\u7684\u2026\u2026\u201d\u5fc3\u60f3\u8fd9\u591c\u5bf9\u9648\u4fe1\u751f\u6765\u8bf4\uff0c\u5927\u6982\u4e5f\u5f88\u96be\u5165\u7720\u3002\u53c8\u8bf4\u9053\uff0c\u201c\u8001\u82cf\u4e00\u5230\u6d77\u5dde\u6765\u5c31\u8ddf\u6211\u63d0\u51fa\u4e24\u5957\u53ef\u4f9b\u9009\u62e9\u7684\u7ecf\u8425\u6a21\u5f0f\uff0c\u5f53\u65f6\u7684\u6761\u4ef6\u6709\u9650\uff0c\u6211\u9009\u62e9\u7b2c\u4e00\u79cd\uff0c\u7231\u8fbe\u53ea\u5f00\u53d1\u89e3\u7801\u677f\u7684\u7cfb\u7edf\u63a7\u5236\u9762\u677f\uff0c\u73b0\u5728\u7684\u60c5\u51b5\u53d8\u4e86\uff0c\u6211\u770b\u8981\u7167\u7b2c\u4e8c\u5957\u6a21\u5f0f\u51c6\u5907\u4e86\uff0c\u7231\u8fbe\u8981\u5177\u5907\u5f00\u53d1\u6240\u6709\u76f8\u5173\u7cfb\u7edf\u63a7\u5236\u8f6f\u4ef6\u7684\u80fd\u529b\uff0c\u53ea\u6709\u5177\u5907\u8fd9\u6837\u7684\u80fd\u529b\uff0c\u6211\u4eec\u624d\u53ef\u4ee5\u6709\u66f4\u5927\u7684\u9009\u62e9\u6027\uff0c\u8d44\u91d1\u7684\u4e8b\u60c5\uff0c\u6211\u6765\u60f3\u529e\u6cd5\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6df1\u5733\u90a3\u8fb9\u624b\u5feb\u7684\u5382\u5546\u5df2\u7ecf\u63a8\u51fa\u4ea7\u54c1\u4e86\u2026\u2026\u201d\u4e01\u69d0\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u8fd9\u65f6\u95f4\u6025\u7740\u63a8\u51fa\u8d28\u91cf\u4e0d\u7a33\u5b9a\u7684\u4ea7\u54c1\uff0c\u867d\u7136\u80fd\u635e\u4e00\u628a\uff0c\u4f46\u4e0d\u53ef\u80fd\u635e\u591a\u5c11\uff0c\u5de5\u6b32\u5584\u5fc5\u5229\u5176\u5668\uff0c\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff0c\u201c\u8fd8\u6709\u4e00\u70b9\uff0c\u4f60\u4eec\u6709\u6ca1\u6709\u60f3\u5230\uff1fC-Cube\u5c06\u89e3\u7801\u82af\u7247\u5f53\u6210\u6447\u94b1\u6811\uff0c\u800cTI\u5bb6\u5927\u4e1a\u5927\uff0c\u867d\u7136\u91cd\u65b0\u5f00\u53d1\u89e3\u7801\u82af\u7247\uff0c\u6280\u672f\u6295\u5165\u7684\u529b\u5ea6\u52bf\u5fc5\u6bd4\u4e0d\u4e0aC-Cube\u3002\u4f60\u6240\u8bf4\u7684\u90a3\u4e9b\u5357\u65b9\u5c0f\u5382\uff0c\u62c9\u4e2a\u5e8a\u677f\u5c31\u80fd\u7ec4\u88c5\u5f71\u789f\u673a\uff0c\u8fd9\u4e9b\u5382\u5b50\u53ef\u6ca1\u6709\u81ea\u884c\u5f00\u53d1\u7cfb\u7edf\u63a7\u5236\u8f6f\u4ef6\u7684\u80fd\u529b\uff0cC-Cube\u4e00\u5b9a\u4f1a\u6ce8\u610f\u8fd9\u4e9b\u5382\u5b50\u7684\u5b58\u5728\uff0c\u5728\u51fa\u552e\u89e3\u7801\u82af\u7247\u540c\u65f6\uff0c\u4e5f\u53ef\u4ee5\u540c\u65f6\u51fa\u552e\u6574\u4f53\u89e3\u51b3\u65b9\u6848\uff0c\u8ba9\u8fd9\u4e9b\u5c0f\u5382\u5f7b\u5e95\u6ca6\u4e3a\u7ec4\u88c5\u5de5\u5382\u3002TI\u672a\u5fc5\u5c31\u4f1a\u8003\u8651\u5230\u8fd9\u4e00\u70b9\uff0c\u5c31\u7b97\u4ed6\u4eec\u5f00\u53d1\u51fa\u65b0\u4e00\u4ee3\u7684\u89e3\u7801\u82af\u7247\uff0c\u4f46\u662f\u7f3a\u4e4f\u6574\u4f53\u89e3\u51b3\u65b9\u6848\uff0c\u4e5f\u65e0\u6cd5\u5c06\u4ea7\u54c1\u5356\u7ed9\u8fd9\u4e9b\u7ec4\u88c5\u5382\uff0c\u6211\u4eec\u591a\u51c6\u5907\u4e00\u4e9b\uff0c\u8bf4\u4e0d\u5b9a\u5230\u65f6\u5019\u80fd\u518d\u5e2eTI\u4e00\u628a\u2026\u2026\u201d</p><p>\u3000\u3000\u5634\u91cc\u8bf4\u662f\u8981\u5e2eTI\u4e00\u628a\uff0c\u773c\u775b\u91cc\u5374\u662f\u72e1\u9ee0\u7684\u76ee\u5149\uff0c\u5c31\u62ff\u8fd9\u4e00\u6b21\u6765\u8bf4\uff0c\u867d\u7136\u8bf4\u5e2e\u4e86TI\u4e00\u628a\uff0c\u53ef\u662f\u4e5f\u5fc5\u987b\u770b\u5230\u7231\u8fbe\u7535\u5b50\u7684\u5229\u76ca\u3002</p><p>\u3000\u3000\u5f20\u606a\u7ad9\u8d77\u6765\u4f38\u4e86\u4f38\u61d2\u8170\uff0c\u8fd9\u4e9b\u5929\u4e5f\u591f\u7d2f\u4e86\uff0c\u63a5\u4e0b\u6765\u7684\u65e5\u5b50\u66f4\u4e0d\u5f97\u8f7b\u677e\uff0c\u4f46\u662f\u58ee\u9614\u7684\u524d\u666f\u8ba9\u4ed6\u5fc3\u91cc\u751f\u51fa\u8bb8\u591a\u8c6a\u60c5\u3002\u6d77\u5dde\u5e02\u515a\u4ee3\u4f1a\u8fdb\u5165\u7b2c\u4e8c\u5929\u4e86\uff0c\u5f20\u606a\u6839\u672c\u65e0\u7455\u5173\u6ce8\uff0c\u867d\u7136\u8bf4\u5f88\u591a\u4eba\u7684\u547d\u8fd0\u4f1a\u5728\u8fd9\u6b21\u4f1a\u8bae\u4e0a\u53d1\u751f\u5fae\u5999\u7684\u53d8\u5316\uff0c\u4f46\u662f\u76f8\u5bf9\u4e8e\u6ce2\u6f9c\u58ee\u9614\u7684\u4e8b\u4e1a\uff0c\u53c8\u663e\u5f97\u6709\u4e9b\u5c0f\u4e86\u3002\u53ea\u8981\u8fc7\u4e86\u4eca\u5e74\uff0c\u5c31\u7b97\u4e07\u52c7\u5f53\u4e0a\u5e38\u52a1\u526f\u5e02\u957f\uff0c\u5f20\u606a\u4e5f\u5b8c\u5168\u4e0d\u9700\u8981\u754f\u60e7\u4ed6\uff0c\u4eca\u5e74\u53ef\u771f\u4e0d\u8ba9\u4eba\u8f7b\u677e\u554a\uff01</p><p>\u3000\u3000\u5f20\u606a\u53c8\u62bd\u4e86\u8ddf\u70df\uff0c\u4e01\u69d0\u3001\u82cf\u6d25\u4e1c\u5b8c\u5168\u6ca1\u6709\u60f3\u5230\u4ed6\u4f1a\u662f\u5b66\u751f\uff0c\u6240\u4ee5\u4e5f\u9ad8\u9ad8\u5174\u5174\u7684\u51d1\u8fc7\u53bb\u62bd\u70df\u3002\u5468\u6587\u658c\u5148\u9001\u5f20\u606a\u56de\u5bcc\u8d35\u56ed\uff0c\u540e\u9001\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u56de\u76ca\u9686\u9547\u3002\u4e0b\u4e86\u8f66\uff0c\u82cf\u6d25\u4e1c\u4e0e\u4e01\u69d0\u5168\u65e0\u7761\u610f\uff0c\u7ee7\u7eed\u8e72\u5728\u5382\u95e8\u524d\u62bd\u70df\uff0c\u4e0e\u5176\u8fd9\u65f6\u5019\u56de\u5bb6\u60ca\u9192\u5bb6\u4eba\uff0c\u8fd8\u4e0d\u56de\u5728\u5382\u5b50\u968f\u4fbf\u6253\u4e2a\u5730\u94fa\u71ac\u51e0\u4e2a\u5c0f\u65f6\u5230\u5929\u4eae\u3002</p><p>\u3000\u3000\u82cf\u6d25\u4e1c\u5bf9\u4e01\u69d0\u8bf4\uff1a\u201cTI\u8981\u662f\u63a5\u53d7\u6211\u4eec\u7684\u6761\u4ef6\uff0c\u7231\u8fbe\u7684\u524d\u666f\u5c31\u5f88\u503c\u5f97\u671f\u5f85\u554a\uff0c\u6211\u770b\u606a\u5c11\u624b\u91cc\u8d44\u91d1\u4e5f\u6709\u9650\uff0c\u6211\u624b\u5934\u7559\u7740\u94b1\u6682\u65f6\u4e5f\u6ca1\u6709\u7528\u5904\u2026\u2026\u201d</p><p>\u3000\u3000\u4e01\u69d0\u7b11\u7740\u8bf4\uff1a\u201c\u628a\u5341\u4e94\u4e07\u5410\u7ed9\u4ed6\u5f97\u4e86\uff0c\u591a\u4f59\u7684\u94b1\u6211\u4e5f\u6ca1\u6709\uff01\u201d</p></article>"}}}]);
