(window.webpackJsonp=window.webpackJsonp||[]).push([[261],{284:function(p){p.exports={chapterTitle:null,title:"\u7b2c146\u7ae0 \u534e\u4e3d\u7684\u6210\u529f",content:"<article><h2>\u7b2c146\u7ae0 \u534e\u4e3d\u7684\u6210\u529f</h2><p>\u3000\u3000\u76db\u946b\u516c\u53f82\u670814\u65e5\u5728\u4ed6\u4eec\u516c\u53f8\u9644\u8fd1\u3001\u7701\u57ce\u6700\u5927\u7684\u4e1c\u9675\u5e7f\u573a\u4e3a\u7231\u7acb\u4fe1GH398\u5728\u534e\u4e1c\u5730\u533a\u4e0a\u5e02\u4e3e\u8d77\u76db\u5927\u7684\u63a8\u4ecb\u4f1a\uff0c\u5f20\u606a\u8d77\u8349\u63a8\u4ecb\u4f1a\u7684\u6846\u67b6\uff0c\u800c\u90a3\u51e0\u5929\u6b63\u662f\u4ed6\u8fce\u63a5\u4e01\u69d0\u3001\u82cf\u6d25\u4e1c\u5230\u6d77\u5dde\uff0c\u51b3\u5b9a\u6536\u8d2d\u58f0\u8fbe\u7535\u5b50\u5382\u5fd9\u5f97\u5c41\u80a1\u4e0d\u7740\u5730\u7684\u65f6\u5019\uff0c\u4ed6\u53ea\u80fd\u4ece\u7535\u89c6\u53f0\u5f55\u64ad\u7684\u8282\u76ee\u91cc\u770b\u5230\u8fd9\u6b21\u63a8\u4ecb\u4f1a\u7684\u76db\u51b5\uff1a\u8457\u540d\u4e3b\u6301\u4eba\u3001\u6447\u6eda\u4e50\u961f\u3001\u6b4c\u624b\u3001\u6ed1\u7a3d\u620f\u5c0f\u4e11\u3001\u4eac\u5267\u3001\u8bc4\u5f39\u3001\u514d\u8d39\u63d0\u4f9b\u7684\u7f8e\u98df\u3001\u70ed\u6c14\u7403\uff0c\u8ba9\u63a8\u4ecb\u4f1a\u770b\u4e0a\u53bb\u50cf\u4e00\u4e2a\u5927\u6742\u6c47\u5f0f\u7684\u5609\u5e74\u534e\u73b0\u573a\uff0c\u4ec5\u4e8b\u540e\u6536\u62fe\u73b0\u573a\u3001\u4fee\u6574\u635f\u574f\u7684\u573a\u5730\u7816\u5c31\u8017\u5341\u4e07\u5de8\u8d44\uff0c\u4f46\u662f\u8fd9\u4e9b\u4ed8\u51fa\u662f\u5fc5\u8981\u7684\uff0c\u5728\u63a8\u4ecb\u4f1a\u6380\u8d77\u7684\u72c2\u6f6e\u4e0b\uff0c\u76db\u946b\u516c\u53f8\u4e5f\u4e0d\u77e5\u9053\u90a3\u4e9b\u4e0d\u6e05\u695a\u6570\u5b57\u624b\u673a\u4e0e\u5927\u54e5\u5927\u6709\u6ca1\u6709\u533a\u522b\u7684\u8d2d\u4e70\u8005\u5230\u5e95\u5360\u4e86\u591a\u5c11\u6bd4\u4f8b\u3002</p><p>\u3000\u3000\u76db\u946b\u534e\u4e3d\u7684\u8868\u73b0\u751a\u81f3\u60ca\u52a8\u7231\u7acb\u4fe1\u516c\u53f8\u603b\u90e8\uff0c\u5f53\u53f6\u5efa\u658c\u62ff\u5230\u7231\u7acb\u4fe1\u603b\u88c1\u4eb2\u81ea\u53d1\u6765\u7684\u8d3a\u7535\uff0c\u5f20\u606a\u6b63\u8ddf\u9648\u4fe1\u751f\u53c2\u89c2\u7231\u8fbe\u7535\u5b50\u7684\u5de5\u5382\uff0c\u53f6\u5efa\u658c\u51e0\u5ea6\u8054\u7cfb\u5f20\u606a\uff0c\u90fd\u627e\u4e0d\u5230\u4ed6\u4eba\u3002</p><p>\u3000\u3000\u8c22\u665a\u6674\u3001\u5468\u6e38\u4e5f\u4e3a\u9526\u6e56\u6574\u5408\u6d77\u5dde\u9020\u7eb8\u4ea7\u4e1a\u7684\u5e9e\u5927\u8ba1\u5212\u5fd9\u5f97\u7126\u5934\u70c2\u989d\uff0c\u6839\u65e0\u65e0\u6687\u5173\u6ce8\u7231\u8fbe\u7535\u5b50\u7684\u60c5\u51b5\uff0c\u4ed6\u4eec\u53ea\u8ddf\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u5403\u8fc7\u4e24\u6b21\u996d\uff0c\u7b97\u662f\u8ba4\u8bc6\uff0c\u7231\u8fbe\u7535\u5b50\u7684\u5177\u4f53\u60c5\u51b5\uff0c\u5979\u4eec\u4e5f\u4e0d\u6e05\u695a\uff0c\u53ea\u77e5\u9053\u5f20\u606a\u8981\u751f\u4ea7\u53bb\u5e74\u4ece\u7701\u57ce\u4e70\u56de\u6765\u7684\u5f71\u789f\u673a\uff0c\u81f3\u4e8e\u5f20\u606a\u5982\u4f55\u7ec4\u7ec7\u7814\u53d1\u3001\u7ec4\u7ec7\u751f\u4ea7\u90fd\u4e0d\u5f88\u6e05\u695a\u3002</p><p>\u3000\u3000\u5979\u4eec\u4ece\u8bb8\u601d\u90a3\u91cc\u77e5\u9053\u58f0\u8fbe\u7535\u5b50\u5382\u8f6c\u6ce8\u518c\u7231\u8fbe\u7535\u5b50\u6709\u9650\u516c\u53f8\uff0c\u5f20\u606a\u59d4\u6258\u82cf\u6d25\u4e1c\u62c5\u4efb\u516c\u53f8\u6cd5\u4eba\u4ee3\u8868\u3001\u603b\u7ecf\u7406\uff0c\u800c\u5218\u660e\u8f89\u5374\u7ed9\u5f20\u606a\u8e22\u5230\u5317\u4eac\u4e00\u4e2a\u6708\u90fd\u4e0d\u89c1\u4eba\u5f71\uff0c\u4ed6\u4eec\u89c9\u5f97\u6709\u4e9b\u5947\u602a\uff0c\u5374\u65e0\u6687\u987e\u53ca\u3002\u6d77\u5dde\u5e02\u56fd\u8425\u6216\u96c6\u4f53\u9020\u7eb8\u5382\u5171\u516d\u5341\u4e03\u5bb6\uff0c\u4ec5\u8d44\u6599\u6536\u96c6\u3001\u6574\u7406\u5c31\u591f\u4ed6\u4eec\u5fd9\u4e00\u58f6\uff0c\u4f55\u51b5\u8fd8\u8981\u8d1f\u8d23\u65b0\u5149\u5382\u7684\u6b63\u5e38\u8fd0\u8425\u3002</p><p>\u3000\u3000\u5f20\u606a\u9001\u8d70\u9648\u4fe1\u751f\u56de\u5230\u5bcc\u8d35\u56ed\u5c0f\u533a\u5c06\u7535\u8bdd\u62d4\u6389\u95f7\u5934\u5927\u7761\uff0c\u8fde\u8bb8\u601d\u90fd\u4e0d\u61c2\u4ed6\u7684\u884c\u8e2a\uff0c\u5176\u4ed6\u4eba\u66f4\u627e\u4e0d\u5230\u4ed6\u7684\u4eba\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u7b49\u4e0d\u53ca\uff0c\u7b49\u76db\u946b\u90a3\u8fb9\u7684\u4e8b\u60c5\u7a0d\u5b89\u6b47\uff0c\u5c31\u4e0e\u5802\u59b9\u517c\u4ed6\u7684\u603b\u88c1\u52a9\u7406\u53f6\u5c0f\u6850\u8feb\u4e0d\u53ca\u5f85\u7684\u8d76\u5230\u6d77\u5dde\u6765\u5835\u5f20\u606a\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u50cf\u8d3c\u4e00\u6837\u4e32\u51fa\u6765\uff0c\u770b\u5f20\u606a\u7684\u773c\u775b\u707c\u707c\u95ea\u4eae\uff0c\u8ba9\u4eba\u770b\u4e86\u5fc3\u865a\u3002\u636e\u8bf4\u53f6\u5efa\u658c\u5bb6\u91cc\u90a3\u4f4d\u6bd4\u8f83\u5f6a\u608d\uff0c\u79d8\u4e66\u4e0d\u8bb8\u7528\u5973\u7684\uff0c\u53f6\u5efa\u658c\u53ea\u5f97\u8ba9\u4ed6\u5802\u59b9\u53f6\u5c0f\u6850\u6765\u5f53\u4ed6\u7684\u52a9\u7406\u3002</p><p>\u3000\u3000\u5f20\u606a\u6487\u7740\u5634\u95ee\u4ed6\u706b\u6025\u706b\u71ce\u7684\u8d76\u5230\u6d77\u5dde\u6765\u6709\u4ec0\u4e48\u4e8b\u60c5\uff0c\u53f6\u5efa\u658c\u6123\u5728\u90a3\u91cc\uff0c\u53ef\u4e0d\u662f\u554a\uff0c\u4ed6\u53ea\u662f\u60f3\u627e\u4e00\u4e2a\u4eba\u6765\u5206\u4eab\u4ed6\u8fd9\u6bb5\u65f6\u95f4\u4e00\u76f4\u98d8\u5728\u534a\u7a7a\u7684\u5fc3\u60c5\uff0c\u771f\u6ca1\u6709\u7279\u522b\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u5f20\u606a\u659c\u7740\u5934\u76ef\u7740\u53f6\u5efa\u658c\uff0c\u53c8\u4fa7\u5934\u770b\u4e86\u770b\u7ad9\u5728\u665a\u6674\u529e\u516c\u5ba4\u95e8\u53e3\u7684\u53f6\u5c0f\u6850\uff0c\u95ee\u4ed6\u4eec\uff1a\u201c\u7a76\u7adf\u6709\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d</p><p>\u3000\u3000\u8c22\u665a\u6674\u4ece\u529e\u516c\u5ba4\u91cc\u8d70\u51fa\u6765\uff1a\u201c\u5218\u660e\u8f89\u8bf4\u4f60\u90a3\u8fb9\u7684\u4e8b\u60c5\u6682\u65f6\u7f13\u4e86\u7f13\uff0c\u4f46\u662f\u6211\u4eec\u8054\u7cfb\u4e0d\u4e0a\u4f60\uff0c\u53f6\u603b\u6628\u5929\u5c31\u5230\u6d77\u5dde\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u53f6\u5c0f\u6850\u7b11\u7740\u8bf4\uff1a\u201c\u6211\u7ed9\u53f6\u603b\u62c9\u5230\u6d77\u5dde\u6765\uff0c\u4e5f\u83ab\u660e\u5176\u5999\u3002\u201d</p><p>\u3000\u3000\u201c\u5f97\u4e86\uff0c\u662f\u6211\u5403\u9971\u4e86\u6ca1\u4e8b\u6491\u7740\uff0c\u201d\u53f6\u5efa\u658c\u62cd\u62cd\u8111\u95e8\u5b50\uff0c\u201c\u7231\u7acb\u4fe1\u7684\u9500\u552e\u60c5\u51b5\uff0c\u6d77\u6cf0\u8d38\u6613\u5c31\u6ca1\u6709\u8ddf\u4f60\u901a\u6c14\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u644a\u644a\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u63a8\u4ecb\u4f1a\u7684\u5f55\u50cf\u5e26\u6211\u770b\u8fc7\u4e86\uff0c\u6bd4\u6211\u9884\u60f3\u7684\u8fd8\u597d\uff0c\u76db\u946b\u5e02\u573a\u90e8\u7684\u6267\u884c\u80fd\u529b\uff0c\u7684\u786e\u4e0d\u662f\u6211\u4eec\u5c0f\u516c\u53f8\u80fd\u6bd4\u7684\uff0c\u5176\u4ed6\u4ec0\u4e48\u4e8b\uff0c\u6211\u5c31\u4e0d\u6e05\u695a\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u53bb\u5e74\u5165\u51ac\u5c31\u4ece\u6d77\u6cf0\u9000\u51fa\uff0c\u8fd9\u4e2a\u60c5\u51b5\uff0c\u53f6\u5efa\u658c\u662f\u6e05\u695a\u7684\uff0c\u5f53\u65f6\u4ed6\u8fd8\u4f1a\u62c5\u5fc3\u65e0\u6cd5\u501f\u52a9\u5f20\u606a\u90a3\u4ee4\u4eba\u60ca\u8273\u7684\u5e02\u573a\u7b56\u5212\u80fd\u529b\uff0c\u8fd9\u6b21\u8fc7\u6765\u624d\u77e5\u9053\u8c22\u665a\u6674\u5c06\u6d77\u88d5\u516c\u53f8\u7684\u4e3b\u8981\u8d44\u4ea7\u90fd\u7f6e\u5165\u9526\u6e56\uff0c\u660e\u773c\u4eba\u90fd\u6e05\u695a\u5f20\u606a\u7edd\u4e0d\u4f1a\u8ddf\u9526\u6e56\u518d\u8131\u79bb\u5173\u7cfb\uff0c\u8fd9\u4e2a\u5708\u5b50\u4e00\u8f6c\uff0c\u5f20\u606a\u53c8\u4e0e\u6d77\u6cf0\u53d1\u751f\u95f4\u63a5\u5173\u7cfb\u3002</p><p>\u3000\u3000\u6d77\u6cf0\u662f\u7231\u7acb\u4fe1\u5728\u4e1c\u6d77\u7701\u7684\u7701\u4ee3\uff0c\u5f20\u606a\u7adf\u7136\u4e0d\u5173\u5fc3\u7231\u7acb\u4fe1398\u7684\u8fd9\u6b21\u63a8\u4ecb\u60c5\u51b5\uff0c\u53f6\u5efa\u658c\u90a3\u98d8\u5728\u7a7a\u4e2d\u6025\u6b32\u4e0e\u4eba\u5206\u4eab\u7684\u5206\u60c5\u5c31\u7ed9\u5835\u5728\u90a3\u91cc\uff0c\u5f97\u4e0d\u5230\u53d1\u6cc4\uff0c\u5dee\u70b9\u8ba9\u4ed6\u6293\u72c2\uff0c\u66f4\u4f55\u51b5\u6574\u4e2a\u63a8\u4ecb\u65b9\u6848\u7684\u6846\u67b6\u8fd8\u662f\u5f20\u606a\u4e00\u624b\u5236\u8ba2\u3002</p><p>\u3000\u3000\u201c\u4f60\u771f\u6ca1\u6709\u5173\u5fc3\u4e00\u70b9\u70b9\u2026\u2026\u201d\u53f6\u5efa\u658c\u4ecd\u7136\u4e0d\u653e\u5f03\uff0c\u4ed6\u4e0d\u76f8\u4fe1\u5f20\u606a\u4f1a\u8fd9\u4e48\u9547\u5b9a\u81ea\u82e5\u3002</p><p>\u3000\u3000\u201c\u8fd8\u771f\u6ca1\u6709\uff0c\u201d\u5f20\u606a\u4e0d\u597d\u610f\u601d\u7684\u7b11\u4e86\u7b11\uff0c\u201c\u6211\u6700\u8fd1\u5fd9\u522b\u7684\u4e8b\u60c5\uff0c\u9526\u6e56\u8fd9\u8fb9\u90fd\u987e\u4e0d\u4e0a\u63d2\u624b\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4ec0\u4e48\u4e8b\u60c5\u8ba9\u4f60\u8fd9\u4e48\u770b\u7d27\uff1f\u201d\u53f6\u5efa\u658c\u89c1\u5f20\u606a\u4e0d\u4f3c\u6545\u5f04\u7384\u865a\uff0c\u7ed9\u540a\u8d77\u5174\u8da3\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u6487\u4e86\u6487\u5634\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5148\u95ee\u4f60\u7684\u3002\u201d</p><p>\u3000\u3000\u53f6\u5efa\u658c\u4f38\u6320\u7740\u8111\u888b\uff0c\u6307\u7740\u5f20\u606a\u8bf4\uff1a\u201c\u4f60\u5f88\u4e0d\u7ed9\u9762\u5b50\u554a\u3002\u201d</p><p>\u3000\u3000\u8c22\u665a\u6674\u7b11\u7740\u9080\u5927\u5bb6\u5230\u529e\u516c\u5ba4\u91cc\u5750\uff0c\u76db\u946b\u5e02\u573a\u90e8\u603b\u76d1\u90b5\u5fc3\u6587\u8ddf\u5468\u6e38\u3001\u8bb8\u601d\u90fd\u5728\u529e\u516c\u5ba4\u91cc\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u7ed9\u5f20\u606a\u5835\u5f97\u96be\u53d7\uff0c\u5750\u5230\u6c99\u53d1\u4e0a\uff0c\u5bf9\u53f6\u5c0f\u6850\u8bf4\uff1a\u201c\u5f20\u606a\u8fd9\u4eba\u7279\u522b\u6ca1\u52b2\u554a\uff0c\u591a\u5174\u594b\u7684\u4e8b\uff0c\u9047\u5230\u4ed6\uff0c\u5c31\u50cf\u6c14\u7403\u7ed9\u9488\u624e\u4e86\uff0c\u4f60\u6765\u8bf4\u5427\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u7740\u8bf4\uff1a\u201c\u53f6\u603b\u53d1\u4e86\u8d22\uff0c\u6211\u518d\u8ba9\u4f60\u987a\u5fc3\u70ab\u8000\uff0c\u6211\u4eec\u8fd9\u4e9b\u82e6\u5df4\u5df4\u6293\u7d27\u53e3\u888b\u8fc7\u65e5\u5b50\u7684\u4eba\uff0c\u5c82\u4e0d\u662f\u66f4\u90c1\u95f7\uff1f\u201d</p><p>\u3000\u3000\u53f6\u5efa\u658c\u54c8\u54c8\u5927\u7b11\u8d77\u6765\uff1a\u201c\u4f60\u539f\u6765\u8fd8\u662f\u77e5\u9053\u7684\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u9760\u7740\u6c99\u53d1\u4f38\u5c55\u8eab\u4f53\uff0c\u7761\u5f97\u592a\u957f\u65f6\u95f4\uff0c\u8eab\u4f53\u50cf\u751f\u8bf1\u4e00\u6837\u9700\u8981\u8fd0\u52a8\uff0c\u8bb8\u601d\u5b89\u5b89\u9759\u9759\u7684\u5750\u5728\u5979\u7684\u529e\u516c\u684c\u524d\uff0c\u5f88\u4e0d\u70ed\u5207\u7684\u53ea\u8ddf\u5f20\u606a\u70b9\u5934\u793a\u610f\uff0c\u63a5\u4e0b\u6765\u5c31\u4e0d\u6562\u62ff\u773c\u775b\u770b\u4ed6\uff0c\u6709\u5ba2\u4eba\u5728\u8fd9\u91cc\uff0c\u4eca\u5929\u662f\u627e\u4e0d\u5230\u8ddf\u8bb8\u601d\u72ec\u5904\u7684\u673a\u4f1a\uff0c\u8ba9\u5f20\u606a\u6068\u5f97\u7259\u75d2\u75d2\u7684\uff0c\u5f53\u7136\u4e0d\u4f1a\u8ba9\u53f6\u5efa\u658c\u592a\u5f97\u610f\uff1a\u201c\u6d77\u6cf0\u53bb\u5e74\u5c31\u770b\u597d\u7231\u7acb\u4fe1\uff0c\u6ca1\u7406\u7531\u8fd9\u65f6\u5019\u4e0d\u81ea\u4fe1\uff0c\u5c31\u662f\u4e0d\u77e5\u9053\u5177\u4f53\u7684\u6570\u636e\u3002\u201d</p><p>\u3000\u3000\u53f6\u5c0f\u6850\u7b11\u7740\u8bf4\uff1a\u201c2\u670814\u65e5\u4e4b\u540e\u4e09\u5929\uff0c\u6574\u4e2a\u534e\u4e1c\u5730\u533a\u7684\u9500\u552e\u603b\u91cf\u8fbe\u5230\u4e24\u4e07\u90e8\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u70b9\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e0d\u9519\u2026\u2026\u201d\u773c\u775b\u91cc\u5374\u6ca1\u6709\u53f6\u5efa\u658c\u3001\u53f6\u5c0f\u6850\u6240\u5e0c\u671b\u770b\u5230\u60ca\u559c\uff0c\u4ed6\u89c1\u53f6\u5efa\u658c\u3001\u53f6\u5c0f\u6850\u5f88\u4e0d\u6ee1\u610f\u7684\u76ef\u7740\u4ed6\uff0c\u8be7\u7136\u8bf4\uff1a\u201c\u600e\u4e48\u4e86\uff0c\u4f60\u4eec\u8d5a\u4e86\u94b1\uff0c\u8fd8\u8981\u6211\u5e2e\u7740\u4f60\u4eec\u9ad8\u5174\u4e0d\u6210\uff0c\u4e0d\u8981\u8fd9\u4e48\u4e3a\u96be\u6211\u5427\uff1f\u201d</p><p>\u3000\u3000\u53f6\u5c0f\u6850\u5f7b\u5e95\u653e\u5f03\u540a\u5f20\u606a\u80c3\u53e3\u7684\u6253\u7b97\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u63a8\u4ecb\u4f1a\u975e\u5e38\u6210\u529f\uff0c\u5177\u4f53\u6709\u591a\u6210\u529f\uff0c\u8ba9\u4f60\u8fd9\u4e00\u6253\u51fb\uff0c\u90fd\u627e\u4e0d\u5230\u8bcd\u6765\u5f62\u5bb9\u4e86\u30022\u6708\uff0c\u53ea\u6709\u534a\u4e2a\u6708\u7684\u65f6\u95f4\uff0c\u9500\u552e\u603b\u91cf\u5c31\u8d85\u8fc7\u53bb\u5e74\u5168\u5e74\uff0c\u6211\u4eec\u534e\u4e1c\u5730\u533a\u5360\u7231\u7acb\u4fe1\u5168\u56fd\u603b\u9500\u91cf\u768480\uff05\uff0c\u6574\u4e2a\u4e09\u6708\uff0c\u63a8\u4ecb\u4f1a\u6240\u6380\u8d77\u7684\u72c2\u6f6e\u8fd8\u6ca1\u6709\u6d88\u9000\uff0c\u534e\u4e1c\u5730\u533a\u4f9d\u65e7\u5360\u636e\u5168\u56fd\u603b\u9500\u91cf\u768460\uff05\u3002\u76db\u946b\u5728\u4e00\u4e2a\u534a\u6708\u91cc\u5b8c\u6210\u9884\u8ba1\u4e00\u5e74\u624d\u80fd\u5b8c\u6210\u7684\u9500\u552e\u91cf\uff0c\u5e76\u5728\u534e\u4e1c\u5730\u533a\u6240\u5360\u7684\u5e02\u573a\u4efd\u989d\u9996\u5ea6\u8d85\u8d8a\u6469\u6258\u7f57\u62c9\u2026\u2026\u201d</p><p>\u3000\u3000\u53ea\u8981\u8fd9\u4e9b\u6570\u636e\u5728\u53f6\u5efa\u658c\u7684\u8111\u5b50\u91cc\u95ea\u8fc7\uff0c\u4ed6\u90fd\u4f1a\u5fcd\u4e0d\u4f4f\u8981\u5439\u53e3\u54e8\uff0c\u4ed6\u53bb\u5e74\u8bf4\u670d\u5bb6\u91cc\u5176\u4ed6\u4eba\u5b64\u6ce8\u4e00\u63b7\u7684\u62ff\u5230\u7231\u7acb\u4fe1\u7684\u534e\u4e1c\u5730\u533a\u4ee3\u7406\u6743\uff0c\u6700\u521d\u5f20\u606a\u6253\u5f00\u6d77\u5dde\u5730\u533a\u7684\u5e02\u573a\u8ba9\u4ed6\u770b\u5230\u6210\u529f\u7684\u66d9\u5149\uff0c\u8fd9\u6b21\u8ba9\u4ed6\u770b\u5230\u5e02\u573a\u8fd0\u4f5c\u7684\u5de8\u5927\u9b54\u529b\uff0c\u81ea\u7136\u662f\u5f20\u606a\u5360\u4e86\u5f88\u5927\u529f\u52b3\uff0c\u53f6\u5efa\u658c\u53ef\u4ee5\u5df4\u671b\u7740\u627e\u5f20\u606a\u6765\u5e86\u529f\u7684\uff0c\u5948\u4f55\u8ba9\u5f20\u606a\u5c61\u6b21\u6253\u51fb\uff0c\u5e86\u529f\u7684\u5fc3\u60c5\u5c31\u5f31\u4e86\u8bb8\u591a\u3002</p><p>\u3000\u3000\u5f20\u606a\u7b11\u7740\u8bf4\uff1a\u201c\u8fd9\u4e48\u8bf4\u76db\u946b\u73b0\u5728\u635e\u5230\u4e0d\u5c11\u94b1\u55bd\uff1f\u201d</p><p>\u3000\u3000\u53f6\u5efa\u658c\u6ca1\u542c\u51fa\u5f20\u606a\u8bdd\u91cc\u7684\u610f\u601d\uff0c\u89c1\u5f20\u606a\u7ec8\u4e8e\u987a\u7740\u53f6\u5c0f\u6850\u7684\u8bdd\u8868\u793a\u4e86\u4e00\u4e0b\u60ca\u8bb6\uff0c\u5f97\u610f\u6d0b\u6d0b\u7684\u8bf4\uff1a\u201c\u90a3\u8fd8\u662f\u5176\u6b21\uff0c\u4e09\u6708\u7684\u9500\u552e\u91cf\u8fd8\u6ca1\u6709\u7edf\u8ba1\u51fa\u6765\uff0c\u76db\u946b\u7684\u540d\u5934\u5df2\u7ecf\u4f20\u904d\u745e\u5178\u554a\uff0c\u7231\u7acb\u4fe1\u603b\u88c1\u3001\u8463\u4e8b\u957f\u7684\u8d3a\u7535\u90fd\u5df2\u7ecf\u6446\u5728\u6211\u7684\u4e66\u684c\u4e0a\uff0c\u4f60\u8bf4\u6211\u90c1\u95f7\u5feb\u4e00\u5e74\u4e86\uff0c\u597d\u4e0d\u5bb9\u6613\u60f3\u627e\u4eba\u6765\u5206\u4eab\u4e00\u4e0b\uff0c\u4f60\u5012\u662f\u4e0d\u914d\u5408\u554a\u3002\u201d</p><p>\u3000\u3000\u5927\u5bb6\u54c8\u54c8\u5927\u7b11\u3002</p><p>\u3000\u3000\u5f20\u606a\u7b11\u7740\u8bf4\uff1a\u201c\u53ea\u6015\u6469\u6258\u7f57\u6258\u90a3\u8fb9\u5bf9\u4f60\u4eec\u662f\u53c8\u5fcc\u53c8\u6068\uff0c\u8fd9\u662f\u903c\u7740\u6469\u6258\u7f57\u62c9\u63d0\u524d\u63a8\u51fa\u4e2d\u6587\u624b\u673a\u554a\u3002\u201d</p><p>\u3000\u3000\u53f6\u5efa\u658c\u7b11\u7740\u8bf4\uff1a\u201c\u4e0d\u8981\u8bf4\u6469\u6258\u7f57\u62c9\u90a3\u8fb9\u5750\u7acb\u4e0d\u5b89\uff0c\u534e\u5357\u3001\u534e\u5317\u3001\u534e\u4e2d\u51e0\u4e2a\u5927\u533a\u5750\u4e86\u6211\u4eec\u7684\u987a\u98ce\u8f66\uff0c\u5374\u53c8\u62c5\u5fc3\u6211\u4eec\u62a2\u4ed6\u4eec\u7684\u996d\u7897\uff0c\u903c\u7740\u5317\u4eac\u90a3\u8fb9\u7ed9\u6211\u4eec\u5956\u52b1\u3002\u5317\u4eac\u516c\u53f8\u4f1a\u6309\u6bd4\u4f8b\u5206\u644a\u8fd9\u6b21\u534e\u4e1c\u5730\u533a\u63a8\u4ecb\u6240\u82b1\u8d39\u7684\u5e7f\u544a\u8d39\u7528\uff0c\u5dee\u4e0d\u591a\u516d\u767e\u4e07\u5de6\u53f3\uff0c\u60df\u4e00\u7684\u8981\u6c42\u5c31\u662f\u76db\u946b\u4ea4\u51fa\u5b8c\u6574\u7684\u63a8\u4ecb\u65b9\u6848\u2026\u2026\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u53f6\u5efa\u658c\u505c\u4e86\u4e0b\u6765\uff0c\u620f\u8c11\u7684\u770b\u7740\u5f20\u606a\u3002</p><p>\u3000\u3000\u5206\u644a\u516d\u767e\u4e07\u5e7f\u544a\u8d39\uff0c\u5e94\u8be5\u7b97\u662f\u7231\u7acb\u4fe1\u7ed9\u76db\u946b\u7684\u989d\u5916\u5956\u52b1\uff0c\u53f6\u5efa\u658c\u4e0d\u662f\u90a3\u79cd\u505a\u5149\u68cd\u7684\u4eba\uff0c\u8fd9\u65f6\u63d0\u51fa\u6765\uff0c\u5c82\u4e0d\u662f\u540c\u610f\u81ea\u5df1\u4e5f\u80fd\u5206\u4e00\u676f\u7fb9\uff1f</p><p>\u3000\u3000\u5f20\u606a\u5012\u662f\u60c5\u6025\u5fc3\u70ed\u8d77\u6765\uff0c\u62f1\u624b\u544a\u9976\uff1a\u201c\u53f6\u603b\u8fd9\u6b21\u6293\u5230\u6211\u5fc3\u75d2\u7684\u5730\u65b9\u4e86\uff0c\u6211\u5728\u641e\u4e00\u5bb6\u7535\u5b50\u5382\uff0c\u6b63\u7f3a\u7814\u53d1\u7ecf\u8d39\uff0c\u6628\u5929\u521a\u5411\u4e0b\u9762\u4eba\u5751\u8499\u62d0\u9a97\u4e86\u4e09\u5341\u4e07\u3002\u6211\u80fd\u5206\u591a\u5c11\uff0c\u4f60\u723d\u5feb\u8bf4\u4e2a\u6570\u3002\u201d</p><p>\u3000\u3000\u53f6\u5efa\u658c\u5f97\u610f\u6d0b\u6d0b\uff0c\u4ece\u897f\u88c5\u53e3\u91cc\u62ff\u51fa\u4e00\u5f20\u8f6c\u8d26\u652f\u7968\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u53c8\u4e0d\u662f\u76db\u946b\u7684\u5458\u5de5\uff0c\u989d\u5916\u7684\u5956\u52b1\u662f\u6ca1\u6709\u7684\u3002\u4f46\u662f\u63a8\u4ecb\u65b9\u6848\u662f\u4f60\u6267\u7b14\uff0c\u5728\u534e\u4e1c\u5730\u533a\u4f7f\u7528\uff0c\u90a3\u662f\u6211\u4eec\u7684\u4ea4\u60c5\uff0c\u4ea4\u7ed9\u5317\u4eac\u90a3\u8fb9\u7ed9\u5176\u4ed6\u5927\u533a\u501f\u9274\uff0c\u8d39\u7528\u8fd8\u662f\u8981\u8ba1\u7b97\u7ed9\u4f60\u7684\uff0c\u76db\u946b\u4e5f\u4e0d\u4f1a\u6627\u4e86\u4f60\u7684\u94b1\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8fd9\u53ef\u662f\u6551\u6025\u7684\u94b1\uff0c\u591a\u8c22\u4e86\u2026\u2026\u201d\u5f20\u606a\u624b\u5feb\u5c06\u652f\u7968\u62a2\u4e86\u8fc7\u6765\uff0c\u4e00\u770b\u50bb\u4e86\u773c\uff0c\u652f\u7968\u62ac\u5934\u3001\u6570\u989d\u90fd\u6ca1\u6709\u5199\uff0c\u770b\u7740\u53f6\u5efa\u658c\u5f97\u610f\u5728\u90a3\u8fb9\u7b11\uff0c\u8fd9\u65f6\u5019\u53cd\u800c\u6362\u4ed6\u90c1\u95f7\u4e86\u3002</p></article>"}}}]);
