(window.webpackJsonp=window.webpackJsonp||[]).push([[262],{284:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u5341\u4e09\u8282 \u8c1c\u8bed",content:"<article><h2>\u7b2c\u4e09\u5341\u4e09\u8282 \u8c1c\u8bed</h2><p>\u795e\u5802\u53ea\u6709\u6b63\u95e8\u8fd9\u4e00\u4e2a\u5165\u53e3\uff0c\u6b64\u5916\u6ca1\u6709\u95e8\u7a97\uff0c\u4e24\u4e2a\u5168\u526f\u6b66\u88c5\u7684\u536b\u5175\u6b63\u5b88\u5728\u6b63\u95e8\u4e24\u4fa7\u3002\u60f3\u8981\u8fdb\u53bb\u7684\u8bdd\uff0c\u5012\u4e5f\u4e0d\u90a3\u4e48\u5bb9\u6613\u3002\u6700\u4fbf\u6377\u7684\u65b9\u6cd5\u662f\u76f4\u63a5\u4f20\u9001\uff0c\u4f46\u795e\u5802\u4f5c\u4e3a\u4e00\u4e2a\u201c\u7ed3\u70b9\u201d\u7684\u6240\u5728\uff0c\u81ea\u7136\u662f\u6709\u5468\u5bc6\u9632\u62a4\u7684\uff0c\u4e00\u7cfb\u5217\u7684\u6b21\u5143\u951a\u4e92\u76f8\u8054\u7ed3\uff0c\u5c01\u9501\u4e86\u795e\u5802\u5468\u56f4\uff0c\u963b\u9694\u81ea\u5916\u5411\u5185\u7684\u4f20\u9001\uff0c\u800c\u743c\u6069\u6ca1\u6709\u80fd\u529b\u89e3\u9664\u8fd9\u6837\u5bc6\u96c6\u5d4c\u5408\u7684\u6cd5\u672f\u3002\u9664\u6b64\u4e4b\u5916\uff0c\u5899\u58c1\u5185\u5c42\u662f\u7528\u7cbe\u91d1\u878d\u6c41\u6d47\u704c\uff0c\u8fd9\u4ee4\u7a7f\u5899\u672f\uff08Passwall\uff09\u548c\u7d22\u5c14\u77f3\u884c\u672f\u4e5f\u65e0\u6cd5\u8d77\u4f5c\u7528\uff0c\u8fd9\u4e24\u79cd\u6cd5\u672f\u90fd\u6ca1\u529e\u6cd5\u7a7f\u8d8a\u91d1\u5c5e\u3002\u552f\u4e00\u7684\u65b9\u6cd5\uff0c\u5c31\u662f\u4ece\u6b63\u95e8\u8d70\u8fdb\u53bb\uff0c\u9690\u5f62\u9b54\u6cd5\uff0c\u6216\u8005\u7cbe\u5999\u7684\u53d8\u5f62\uff0c\u5e94\u8be5\u53ef\u4ee5\u9a97\u8fc7\u4e24\u4e2a\u536b\u5175\u7684\u773c\u775b\u3002</p><p>\u201c\u95e8\u4e0a\u5e94\u8be5\u4f1a\u6709\u6cd5\u672f\u9677\u9631\u5427\u3002\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u95ee\u3002</p><p>\u743c\u6069\u51dd\u795e\u8fa8\u8bc6\u4e86\u7247\u523b\uff0c\u795e\u6bbf\u6b63\u95e8\u4e0a\u5404\u79cd\u9b54\u6cd5\u7075\u7ebf\u7d27\u5bc6\u7684\u7ea0\u7ed3\u5728\u4e00\u8d77\uff0c\u5f7c\u6b64\u4e92\u76f8\u652f\u63f4\uff0c\u201c\u6709\u4e24\u9053\u9632\u5fa1\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u5728\u6b63\u95e8\u524d\u65b9\u6709\u4e00\u4e2a\u8bc6\u7834\u9690\u5f62\u548c\u4e00\u4e2a\u89e3\u9664\u53d8\u5f62\uff0c\u6211\u4eec\u649e\u4e0a\u7684\u8bdd\u4f2a\u88c5\u5c31\u4f1a\u66b4\u9732\uff0c\u4f46\u6ca1\u5173\u7cfb\uff0c\u536b\u5175\u5df2\u7ecf\u6765\u4e0d\u53ca\u963b\u62e6\u6211\u4eec\u4e86\u3002\u201d</p><p>\u201c\u540e\u9762\u90a3\u9053\u9632\u5fa1\u5462\uff1f\u201d</p><p>\u201c\u6211\u8fa8\u8ba4\u4e0d\u51fa\u6548\u679c\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u968f\u5373\u53c8\u8865\u5145\u4e86\u4e00\u53e5\uff0c\u201c\u4f46\u6211\u57fa\u672c\u53ef\u4ee5\u65ad\u5b9a\u5b83\u4eec\u90fd\u6ca1\u6709\u653b\u51fb\u6027\uff0c\u770b\u4e0d\u5230\u5371\u9669\u7684\u8baf\u53f7\u3002\u201d</p><p>\u201c\u6ca1\u5173\u7cfb\uff0c\u5c31\u7b97\u6709\u653b\u51fb\u6027\uff0c\u6211\u4eec\u4e5f\u6709\u8fd9\u4e2a\u3002\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u8bf4\uff0c\u626c\u4e86\u626c\u624b\uff0c\u5c55\u793a\u90a3\u679a\u7eff\u5b9d\u77f3\u6212\u6307\u3002</p><p>\u743c\u6069\u5fae\u5fae\u70b9\u5934\uff0c\u8fd9\u4e00\u8def\u884c\u6765\uff0c\u6212\u6307\u7684\u4f5c\u7528\u5df2\u7ecf\u5f97\u5230\u5145\u5206\u8bc1\u660e\u3002\u745e\u8d39\u5fb7\u5bb6\u65cf\u7684\u9b54\u6cd5\u9635\uff0c\u8d8a\u5f80\u5185\u90e8\u5c31\u8d8a\u7cbe\u5fae\u590d\u6742\uff0c\u4ee4\u4eba\u9632\u4e0d\u80dc\u9632\uff0c\u6709\u597d\u51e0\u6b21\u743c\u6069\u548c\u7ef4\u5eb7\u5c3c\u4e9a\u90fd\u649e\u5165\u4e86\u6cd5\u672f\u9677\u9631\uff0c\u4f46\u9760\u7740\u6212\u6307\u90fd\u80fd\u5b89\u7136\u65e0\u6059\uff0c\u53ef\u4ee5\u8bf4\u662f\u5e2e\u4e86\u5927\u5fd9\u3002</p><p>\u4ed6\u5411\u7ef4\u5eb7\u5c3c\u4e9a\u70b9\u70b9\u5934\uff0c\u793a\u610f\u5979\u505a\u597d\u51c6\u5907\uff0c\u7136\u540e\u9ed8\u8bf5\u5492\u8bed\uff0c\u4e3a\u4e24\u4eba\u90fd\u65bd\u52a0\u4e0a\u52a0\u901f\u672f\u3002\u4e00\u5207\u5c31\u7eea\uff0c\u4ed6\u4eec\u4ece\u9634\u5f71\u4e2d\u6084\u6084\u79fb\u51fa\uff0c\u501f\u52a9\u9690\u5f62\u7684\u63a9\u62a4\u5f80\u795e\u6bbf\u6b63\u95e8\u6162\u6162\u9760\u8fd1\u3002\u4e24\u4e2a\u536b\u5175\u5168\u526f\u6b66\u88c5\u5730\u7ad9\u5c97\uff0c\u4e1d\u6beb\u6ca1\u5bdf\u89c9\u6709\u4ec0\u4e48\u5f02\u6837\u3002\u201c\u597d\u6781\u4e86\uff0c\u201d\u743c\u6069\u60f3\uff0c\u201c\u4e00\u5207\u987a\u5229\u3002\u201d</p><p>\u8e0f\u5165\u795e\u5802\uff0c\u9690\u5f62\u5c31\u4f1a\u88ab\u89e3\u9664\uff0c\u4ed6\u4eec\u7684\u5f62\u8ff9\u4fbf\u4f1a\u66b4\u9732\uff0c\u5fc5\u987b\u7acb\u523b\u628a\u95e8\u5c01\u8d77\u6765\uff0c\u963b\u6b62\u5916\u9762\u7684\u536b\u5175\u8fdb\u5165\u3002\u8981\u5c01\u4f4f\u6b63\u95e8\u5f88\u5bb9\u6613\uff0c\u5c01\u95e8\u672f\u6216\u8005\u77f3\u5899\u672f\u90fd\u53ef\u4ee5\u529e\u5230\uff0c\u524d\u8005\u4ed6\u6ca1\u600e\u4e48\u5b66\u8fc7\uff0c\u4f46\u6709\u5377\u8f74\u53ef\u7528\uff0c\u540e\u8005\u4ed6\u4eca\u5929\u6b63\u597d\u51c6\u5907\u4e86\u4e00\u4e2a\u3002</p><p>\u4e94\u6b65\u3001\u56db\u6b65\u3001\u4e09\u6b65\u2026\u2026</p><p>\u743c\u6069\u731b\u7136\u52a0\u901f\uff0c\u548c\u7ef4\u5eb7\u5c3c\u4e9a\u4e00\u8d77\u671d\u795e\u5802\u5185\u51b2\u5165\uff0c\u5728\u8d34\u8fd1\u6b63\u95e8\u7684\u65f6\u5019\uff0c\u4ed6\u6e05\u695a\u5730\u611f\u89c9\u5230\u4e86\u5468\u56f4\u7684\u9b54\u6cd5\u7ed3\u754c\u4eae\u4e86\u4eae\uff0c\u6709\u4e00\u80a1\u529b\u91cf\u4eff\u4f5b\u5fae\u98ce\u4e00\u6837\u62c2\u8fc7\u8eab\u4f53\u3002\u743c\u6069\u4f4e\u5934\uff0c\u770b\u89c1\u81ea\u5df1\u7684\u8eab\u4f53\u4ece\u7a7a\u6c14\u4e2d\u663e\u73b0\u51fa\u6765\uff0c\u9690\u5f62\u88ab\u89e3\u9664\u4e86\uff0c\u4e0d\u8fc7\u8fd9\u5728\u610f\u6599\u4e4b\u4e2d\u3002</p><p>\u536b\u5175\u4eec\u60ca\u89c9\u8fc7\u6765\uff0c\u547c\u558a\u7740\u51b2\u4e0a\uff0c\u4f46\u5df2\u7ecf\u6765\u4e0d\u53ca\u4e86\u3002\u743c\u6069\u4e00\u6b65\u8de8\u8fdb\u4e86\u6b63\u95e8\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u7d27\u968f\u5176\u540e\uff0c\u4ed6\u4eec\u770b\u89c1\u4e86\u745e\u8d39\u5fb7\u5bb6\u65cf\u7684\u4e3b\u6bcd\u59ae\u745f\u548c\u5979\u7684\u9b54\u88d4\u5353\u5c14\u513f\u5b50\uff0c\u6b63\u542c\u5230\u52a8\u9759\uff0c\u56de\u8fc7\u8eab\u6765\u3002</p><p>\u7136\u540e\u743c\u6069\u9661\u7136\u611f\u89c9\u773c\u524d\u6025\u5267\u5730\u6643\u4e86\u4e00\u6643\uff0c\u5934\u8111\u4e00\u9635\u731b\u70c8\u7684\u6655\u7729\u3002</p><p>\u4e0b\u4e00\u77ac\u95f4\uff0c\u4ed6\u56de\u8fc7\u795e\u6765\uff0c\u60ca\u8bb6\u5730\u53d1\u73b0\u81ea\u5df1\u5df2\u7ecf\u6362\u4e86\u5730\u65b9\u3002\u4ed6\u6b63\u7ad9\u5728\u4e00\u4e2a\u72ed\u957f\u7684\u8d70\u5eca\u91cc\uff0c\u4e24\u8fb9\u90fd\u662f\u539a\u91cd\u7684\u5899\u58c1\uff0c\u4e00\u76f4\u8054\u7ed3\u5230\u5929\u82b1\u677f\uff0c\u7ec4\u6210\u5c01\u95ed\u7684\u7a7a\u95f4\uff0c\u9664\u4e86\u4e24\u7aef\u51fa\u53e3\u3002\u5730\u9762\u3001\u5899\u58c1\u548c\u5929\u82b1\u677f\uff0c\u5168\u90fd\u662f\u7528\u957f\u65b9\u5f62\u7684\u9ed1\u8272\u77f3\u5757\u780c\u6210\uff0c\u7f1d\u9699\u7b14\u76f4\u6574\u9f50\u5f97\u5c31\u50cf\u662f\u753b\u51fa\u6765\u7684\u3002\u6ca1\u6709\u795e\u5802\uff0c\u6ca1\u6709\u536b\u5175\uff0c\u6ca1\u6709\u59ae\u745f\u4e3b\u6bcd\u548c\u9b54\u88d4\u548c\u9b54\u88d4\u5353\u5c14\uff0c\u4e00\u5207\u4eff\u4f5b\u90fd\u6d88\u5931\u4e86\u3002</p><p>\u8eab\u65c1\u7684\u7a7a\u6c14\u8f7b\u5fae\u6ce2\u6298\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u7684\u8eab\u5f71\u663e\u9732\u51fa\u6765\uff0c\u5979\u5b9a\u4e86\u5b9a\u795e\uff0c\u62ac\u773c\u770b\u56db\u5468\uff0c\u4e5f\u662f\u660e\u663e\u6014\u4f4f\uff0c\u201c\u8fd9\u662f\u54ea\u91cc\uff1f\u201d\u5979\u95ee\u743c\u6069\uff0c\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d</p><p>\u743c\u6069\u6447\u5934\u4e0d\u8bed\uff0c\u4ed6\u89c2\u5bdf\u7740\u56db\u5468\uff0c\u56de\u5fc6\u521a\u624d\u6240\u53d1\u751f\u7684\u4e8b\u60c5\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u5deb\u5e08\u957f\u957f\u5410\u4e86\u53e3\u6c14\u3002</p><p>\u201c\u6211\u660e\u767d\u4e86\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u8fd9\u91cc\u662f\u4e00\u4e2a\u8ff7\u5bab\u3002\u201d</p><p>\u201c\u8ff7\u5bab\uff1f\u201d</p><p>\u201c\u4ed6\u4eec\u5728\u6b63\u95e8\u4e0a\u5b89\u7f6e\u4e86\u4e00\u4e2a\u8ff7\u5bab\u672f\u3002\u201d\u743c\u6069\u89e3\u91ca\u3002</p><p>\u201c\u6211\u4eec\u7684\u6212\u6307\u4e3a\u4ec0\u4e48\u6ca1\u6709\u53d1\u6325\u4f5c\u7528\uff1f\u201d</p><p>\u201c\u56e0\u4e3a\u8ff7\u5bab\u672f\u6839\u672c\u5c31\u4e0d\u662f\u653b\u51fb\u6cd5\u672f\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5b83\u53ea\u662f\u5c06\u6211\u4eec\u8f6c\u79fb\u5230\u4e86\u4e00\u4e2a\u534a\u4f4d\u9762\u91cc\u3002\u201d</p><p>\u8ff7\u5bab\u672f\u662f\u4e00\u79cd\u975e\u5e38\u9ad8\u6df1\u7684\u6cd5\u672f\uff0c\u65bd\u6cd5\u8005\u521b\u9020\u4e00\u4e2a\u5c0f\u578b\u7684\u534a\u4f4d\u9762\uff0c\u968f\u7740\u81ea\u5df1\u7684\u5fc3\u610f\u6784\u5efa\u8d77\u8ff7\u5bab\uff0c\u4efb\u4f55\u4e2d\u4e86\u6cd5\u672f\u7684\u751f\u7269\u90fd\u4f1a\u88ab\u4f20\u9001\u8fdb\u6765\u3002\u6b63\u5982\u743c\u6069\u6240\u8bf4\uff0c\u5b83\u53ea\u662f\u4e00\u79cd\u7a7a\u95f4\u8f6c\u79fb\u6cd5\u672f\uff0c\u4e0d\u5177\u6709\u653b\u51fb\u6027\uff0c\u6240\u4ee5\u745e\u8d39\u5fb7\u5bb6\u5fbd\u4e5f\u6ca1\u6cd5\u62b5\u5fa1\u3002</p><p>\u745e\u8d39\u5fb7\u5bb6\u65cf\u5b85\u9662\u91cc\u88ab\u5e9e\u5927\u9b54\u6cd5\u9635\u7b3c\u7f69\uff0c\u904d\u5730\u90fd\u662f\u5371\u9669\u7684\u6cd5\u672f\u9677\u9631\uff0c\u4e3a\u4e86\u907f\u514d\u8bef\u4f24\u548c\u4e0d\u5fc5\u8981\u7684\u9ebb\u70e6\uff0c\u5bb6\u65cf\u91cc\u7684\u91cd\u8981\u6210\u5458\u4f69\u6234\u9b54\u6cd5\u5bb6\u5fbd\uff0c\u80fd\u591f\u8eb2\u907f\u4f24\u5bb3\u3002\u4f46\u5173\u952e\u7684\u5730\u65b9\uff0c\u5e94\u8be5\u8fd8\u662f\u6709\u66f4\u8fdb\u4e00\u6b65\u7684\u4fdd\u62a4\u63aa\u65bd\u7684\uff0c\u4e0d\u662f\u4f69\u6234\u4e86\u5bb6\u5fbd\u5c31\u80fd\u968f\u610f\u51fa\u5165\u3002\u743c\u6069\u4e00\u65f6\u5931\u7b97\uff0c\u6ca1\u80fd\u8fa8\u8bc6\u51fa\u8ff7\u5bab\u672f\uff0c\u7ed3\u679c\u5c31\u7740\u4e86\u9053\u3002</p><p>\u4ece\u7406\u8bba\u4e0a\u8bf4\uff0c\u8ff7\u5bab\u672f\u6ca1\u6709\u4ec0\u4e48\u592a\u5927\u5371\u9669\uff0c\u867d\u7136\u662f\u4e2a\u96be\u5ea6\u5f88\u9ad8\u7684\u6cd5\u672f\uff0c\u4f46\u4f5c\u7528\u5e76\u4e0d\u5728\u4e8e\u6740\u4f24\u5bf9\u624b\uff0c\u800c\u662f\u6682\u65f6\u56f0\u4f4f\u3002\u5b83\u6700\u5178\u578b\u7684\u7528\u6cd5\uff0c\u5c31\u662f\u5deb\u5e08\u5728\u906d\u9047\u5389\u5bb3\u5bf9\u624b\u53c8\u5be1\u4e0d\u654c\u4f17\u7684\u65f6\u5019\uff0c\u7528\u8fd9\u6cd5\u672f\u6765\u5206\u5272\u654c\u4eba\uff0c\u5404\u4e2a\u51fb\u7834\u3002\u8ff7\u5bab\u662f\u65bd\u6cd5\u8005\u81ea\u5df1\u7528\u610f\u5ff5\u6784\u5efa\u7684\uff0c\u4f46\u5fc5\u5b9a\u6709\u4e00\u4e2a\u51fa\u53e3\uff0c\u800c\u4e14\u4e00\u65e6\u6784\u5efa\u5b8c\u6210\u5c31\u4e0d\u80fd\u518d\u53d8\u52a8\uff0c\u88ab\u56f0\u5728\u5176\u4e2d\u7684\u4eba\uff0c\u53ea\u8981\u80fd\u8d70\u5230\u51fa\u53e3\uff0c\u81ea\u7136\u5c31\u80fd\u8131\u79bb\u534a\u4f4d\u9762\uff0c\u56de\u5230\u539f\u672c\u7684\u4e16\u754c\u3002\u8d70\u8ff7\u5bab\u8fd9\u79cd\u4e8b\u60c5\uff0c\u5bf9\u4e8e\u666e\u901a\u4eba\u6765\u8bf4\u6216\u8bb8\u6709\u70b9\u96be\u5ea6\uff0c\u4f46\u9700\u8981\u52a8\u7528\u8ff7\u5bab\u672f\u6765\u5bf9\u4ed8\u7684\u89d2\u8272\uff0c\u81ea\u7136\u4e5f\u4e0d\u53ef\u80fd\u662f\u5bfb\u5e38\u4e4b\u8f88\uff0c\u533a\u533a\u4e00\u4e2a\u8ff7\u5bab\u53c8\u600e\u4e48\u53ef\u80fd\u963b\u62e6\u5f97\u4f4f\uff0c\u5f80\u5f80\u5341\u5206\u949f\u534a\u5c0f\u65f6\u5c31\u80fd\u8131\u8eab\u3002\u5c31\u7b97\u5b9e\u5728\u8d70\u4e0d\u51fa\u53bb\uff0c\u53ea\u8981\u7b49\u4e0a\u4e8c\u5341\u56db\u4e2a\u5c0f\u65f6\uff0c\u8ff7\u5bab\u81ea\u7136\u6d88\u9664\uff0c\u5230\u65f6\u5019\u81ea\u52a8\u56de\u5230\u539f\u5730\u3002</p><p>\u867d\u7136\u5982\u6b64\uff0c\u743c\u6069\u53ef\u4e0d\u60f3\u5728\u8fd9\u91cc\u5446\u4e0a\u4e8c\u5341\u56db\u5c0f\u65f6\u3002</p><p>\u8fd9\u4e2a\u8ff7\u5bab\u5e76\u975e\u5b9e\u4f53\uff0c\u5176\u5b9e\u5c31\u662f\u4e00\u4e2a\u534a\u4f4d\u9762\uff0c\u6216\u8005\u66f4\u901a\u4fd7\u5730\u8bf4\uff0c\u5b83\u7c7b\u4f3c\u4e8e\u4e00\u4e2a\u201c\u9886\u57df\u201d\uff0c\u662f\u4e00\u4e2a\u534a\u7269\u8d28\u534a\u865a\u62df\u7684\u7a7a\u95f4\u3002\u65e2\u7136\u8fdb\u5165\u8ff7\u5bab\uff0c\u5c31\u5f97\u5b88\u6b64\u5730\u7684\u201c\u89c4\u5219\u201d\uff0c\u8ff7\u5bab\u4e0d\u4f1a\u505a\u4efb\u4f55\u53d8\u5e7b\u79fb\u52a8\uff0c\u5fc5\u5b9a\u6709\u51fa\u53e3\uff0c\u4f46\u540c\u65f6\u4e5f\u4e0d\u53ef\u80fd\u7528\u7a7f\u5899\u3001\u5e7b\u5316\u7075\u4f53\u7684\u65b9\u6cd5\u76f4\u63a5\u7a7f\u8d8a\u5899\u58c1\uff0c\u5fc5\u987b\u8001\u8001\u5b9e\u5b9e\u6309\u7167\u6b63\u5e38\u8d70\u8ff7\u5bab\u7684\u65b9\u5f0f\u6765\u3002</p><p>\u201c\u8fd9\u5012\u4e5f\u4e0d\u96be\uff0c\u201d\u743c\u6069\u6c89\u541f\u7740\uff0c\u201c\u4e0d\u8fc7\u636e\u6211\u6240\u77e5\uff0c\u5982\u679c\u5deb\u5e08\u7684\u6280\u5de7\u8db3\u591f\u9ad8\u660e\u7684\u8bdd\uff0c\u662f\u53ef\u4ee5\u5728\u8ff7\u5bab\u91cc\u8bbe\u7f6e\u66f4\u591a\u7684\u969c\u788d\u7684\u3002\u201d</p><p>\u201c\u8fd9\u8fdd\u53cd\u89c4\u5219\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u63d0\u9192\uff0c\u201c\u8ff7\u5bab\u672f\u4e0d\u5177\u6709\u653b\u51fb\u6027\u3002\u201d</p><p>\u201c\u6211\u6ca1\u8bf4\u6709\u653b\u51fb\u6027\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u53ea\u662f\u8bf4\u8bbe\u7f6e\u969c\u788d\uff0c\u589e\u52a0\u96be\u5ea6\u3002\u201d</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u4f9d\u7136\u6ca1\u6709\u660e\u767d\uff0c\u4f46\u743c\u6069\u4e5f\u4e0d\u518d\u591a\u505a\u89e3\u91ca\uff0c\u201c\u6211\u4eec\u5f97\u8d76\u5feb\u79bb\u5f00\u8fd9\u91cc\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u6211\u4e0d\u60f3\u7b49\u8fc7\u51e0\u4e2a\u5c0f\u65f6\u51fa\u53bb\uff0c\u7ed3\u679c\u53d1\u73b0\u51fa\u53e3\u4e00\u5806\u654c\u4eba\u5728\u7b49\u7740\u6211\u4eec\u3002\u201d</p><p>\u201c\u600e\u4e48\u8d70\uff1f\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u95ee\u3002</p><p>\u743c\u6069\u770b\u770b\u8d70\u5eca\u4e24\u7aef\uff0c\u601d\u7d22\u7740\uff0c\u201c\u6211\u4eec\u6ca1\u65f6\u95f4\u6162\u6162\u63a2\u8def\u4e86\uff0c\u201d\u4ed6\u8bf4\uff0c\u4ece\u6000\u91cc\u53d6\u51fa\u4e00\u5f20\u5377\u8f74\uff0c\u9012\u7ed9\u7ef4\u5eb7\u5c3c\u4e9a\uff0c\u201c\u53ec\u5524\u4e00\u53ea\u725b\u5934\u602a\u6765\u3002\u201d</p><p>\u743c\u6069\u5bf9\u725b\u5934\u602a\u4e0d\u719f\u6089\uff0c\u83f2\u5c14\u4f26\u5bb6\u65cf\u5219\u6709\u5f88\u591a\u725b\u5934\u602a\u5974\u96b6\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u662f\u80af\u5b9a\u719f\u6089\u7684\uff0c\u7531\u5979\u6765\u4f7f\u7528\u53ec\u5524\u5377\u8f74\u6210\u529f\u7387\u66f4\u9ad8\u3002</p><p>\u201c\u53ec\u5524\u725b\u5934\u602a\u505a\u4ec0\u4e48\uff1f\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u6115\u7136\u3002</p><p>\u201c\u56e0\u4e3a\u725b\u5934\u602a\u662f\u4e16\u754c\u4e0a\u552f\u4e00\u4e00\u79cd\u51b3\u4e0d\u4f1a\u88ab\u8ff7\u5bab\u56f0\u4f4f\u7684\u751f\u7269\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u8038\u8038\u80a9\uff0c\u201c\u522b\u95ee\u6211\u539f\u56e0\uff0c\u8fd9\u5c31\u53eb\u505a\u5929\u8d4b\u3002\u201d</p><p>\u786e\u5b9e\uff0c\u8ff7\u5bab\u672f\u8fd9\u79cd\u9b54\u6cd5\u867d\u7136\u6ca1\u6709\u6740\u4f24\u529b\uff0c\u4f46\u57fa\u672c\u662f\u767e\u53d1\u767e\u4e2d\uff0c\u65e0\u89c6\u4efb\u4f55\u9b54\u6cd5\u6297\u529b\u2014\u2014\u552f\u72ec\u56f0\u4e0d\u4f4f\u725b\u5934\u602a\u3002\u8fd9\u539f\u56e0\u5230\u5e95\u4f55\u5728\uff0c\u6ca1\u6709\u4eba\u8bf4\u5f97\u6e05\u695a\uff0c\u53cd\u6b63\u5343\u767e\u6b21\u7684\u8bd5\u9a8c\u7ed3\u679c\u5c31\u662f\u8fd9\u6837\u3002\u6709\u4f20\u95fb\u8bf4\uff0c\u8fd9\u548c\u6df1\u6e0a\u4e2d\u7684\u67d0\u4e2a\u6076\u9b54\u9886\u4e3b\u6709\u5173\uff0c\u5b83\u6781\u64c5\u957f\u8ff7\u5bab\u7c7b\u578b\u7684\u6cd5\u672f\uff0c\u800c\u4e14\u6709\u5927\u91cf\u7684\u725b\u5934\u602a\u4fe1\u5f92\uff0c\u751a\u81f3\u88ab\u8ba4\u4e3a\u662f\u725b\u5934\u602a\u8fd9\u4e2a\u7269\u79cd\u7684\u521b\u9020\u8005\u3002</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u63a5\u8fc7\u5377\u8f74\uff0c\u5148\u9ed8\u8bfb\u4e00\u904d\uff0c\u7136\u540e\u5f00\u59cb\u5ff5\u8bf5\u3002\u51e0\u79d2\u949f\u540e\uff0c\u9ed1\u8272\u7684\u7a7a\u95f4\u95e8\u6253\u5f00\uff0c\u4e00\u53ea\u725b\u5934\u602a\u4ece\u4e2d\u8d70\u51fa\u6765\u3002\u5b83\u521a\u521a\u9732\u9762\uff0c\u743c\u6069\u5c31\u5b8c\u6210\u4e86\u4e00\u4e2a\u5492\u8bed\uff0c\u63a7\u5236\u4e86\u5b83\u7684\u5fc3\u667a\u3002</p><p>\u201c\u8d70\u51fa\u8ff7\u5bab\u3002\u201d\u743c\u6069\u7b80\u77ed\u5730\u4e0b\u7740\u547d\u4ee4\u3002</p><p>\u725b\u5934\u602a\u5446\u5446\u5730\u6014\u4e86\u4e24\u79d2\uff0c\u968f\u5373\u4f4e\u543c\u4e00\u58f0\uff0c\u6492\u5f00\u8e44\u5b50\u5c31\u8dd1\u8d77\u6765\uff0c\u743c\u6069\u548c\u7ef4\u5eb7\u5c3c\u4e9a\u7d27\u968f\u5176\u540e\u3002\u5f88\u795e\u5947\u7684\uff0c\u725b\u5934\u602a\u679c\u7136\u5929\u751f\u5c31\u6709\u8d70\u8ff7\u5bab\u7684\u5929\u8d4b\uff0c\u5b83\u6beb\u65e0\u534a\u70b9\u6ede\u788d\u5730\u5954\u8dd1\u3001\u524d\u8fdb\u3001\u8f6c\u5f2f\uff0c\u5728\u6bcf\u4e2a\u5c94\u53e3\u76f4\u63a5\u505a\u51fa\u9009\u62e9\uff0c\u6ca1\u6709\u4e1d\u6beb\u72b9\u8c6b\u3002\u5927\u7ea6\u4e09\u5206\u949f\u540e\uff0c\u743c\u6069\u548c\u7ef4\u5eb7\u5c3c\u4e9a\u5df2\u7ecf\u770b\u5230\u4e86\u524d\u65b9\u7684\u51fa\u53e3\u3002</p><p>\u725b\u5934\u602a\u4e00\u9a6c\u5f53\u5148\u51b2\u4e86\u8fc7\u53bb\uff0c\u8eab\u5f71\u7acb\u523b\u6d88\u5931\u5728\u4e00\u7247\u767d\u5149\u4e2d\u3002\u743c\u6069\u548c\u7ef4\u5eb7\u5c3c\u4e9a\u6b63\u8981\u8ddf\u7740\u8d70\u51fa\u53bb\uff0c\u7a81\u7136\u9762\u524d\u7684\u5730\u9762\u4e0a\u4e00\u56e2\u9ed1\u96fe\u817e\u8d77\uff0c\u98de\u821e\u65cb\u8f6c\u7740\uff0c\u6700\u540e\u51dd\u805a\u6210\u5e9e\u5927\u7684\u8eab\u5f71\u3002</p><p>\u5b83\u6709\u6697\u7ea2\u8272\u7684\u76ae\u80a4\u548c\u8759\u8760\u4f3c\u7684\u7fc5\u8180\uff0c\u5934\u4e0a\u957f\u7740\u4e24\u53ea\u5f2f\u89d2\uff0c\u5468\u8eab\u706b\u7130\u7f20\u7ed5\u3002\u201c\u5df4\u6d1b\u708e\u9b54\uff01\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u8bf4\uff0c\u4f3c\u4e4e\u60f3\u8981\u540e\u9000\uff0c\u4f46\u743c\u6069\u62c9\u4f4f\u4e86\u5979\u3002\u201c\u653e\u5fc3\uff0c\u201d\u5deb\u5e08\u8bf4\uff0c\u201c\u8fd9\u91cc\u662f\u8ff7\u5bab\uff0c\u5b83\u6ca1\u529e\u6cd5\u653b\u51fb\u6211\u4eec\u3002\u201d</p><p>\u201c\u554a\u54c8\u54c8\u54c8\uff0c\u201d\u6076\u9b54\u4f3c\u4e4e\u975e\u5e38\u9ad8\u5174\u5730\u5927\u7b11\u4e86\u8d77\u6765\uff0c\u4f4e\u5934\u4fef\u89c6\u7740\u9762\u524d\u7684\u4e24\u4e2a\u4eba\uff0c\u201c\u4f60\u4eec\u5df2\u7ecf\u627e\u5230\u8def\u4e86\uff0c\u901f\u5ea6\u771f\u5feb\uff0c\u201d\u4ed6\u5938\u5956\u7740\uff0c\u5de6\u53f3\u6447\u6446\u7740\u8eab\u4f53\uff0c\u770b\u8d77\u6765\u52a8\u4f5c\u5341\u5206\u6ed1\u7a3d\uff0c\u201c\u4f46\u4e8b\u60c5\u6ca1\u6709\u8fd9\u4e48\u7b80\u5355\uff0c\u4eba\u7c7b\uff0c\u201d\u6076\u9b54\u8bf4\uff0c\u201c\u60f3\u8981\u8d70\u51fa\u8ff7\u5bab\uff0c\u8fd8\u5f97\u7ecf\u8fc7\u6211\u8fd9\u4e00\u5173\u3002\u201d</p><p>\u201c\u54e6\u3002\u201d\u743c\u6069\u8bf4\uff0c\u867d\u7136\u5df4\u6d1b\u708e\u9b54\u662f\u6700\u5f3a\u5927\u7684\u6076\u9b54\uff0c\u4f46\u4ed6\u5e76\u4e0d\u5bb3\u6015\uff0c\u8ff7\u5bab\u672f\u6ca1\u6709\u653b\u51fb\u6027\uff0c\u8fd9\u662f\u9b54\u6cd5\u7684\u89c4\u5219\uff0c\u5c31\u7b97\u5728\u8ff7\u5bab\u91cc\u649e\u4e0a\u4e00\u6761\u7ea2\u9f99\u4e5f\u4e0d\u5fc5\u60ca\u8bb6\uff0c\u5b83\u7edd\u5bf9\u4f24\u5bb3\u4e0d\u4e86\u4f60\u2014\u2014\u4f46\u53cd\u8fc7\u6765\u8bf4\uff0c\u4f60\u4e5f\u4f24\u5bb3\u4e0d\u4e86\u5b83\uff0c\u56e0\u4e3a\u4ece\u672c\u8d28\u4e0a\u8bf4\u5b83\u4eec\u5176\u5b9e\u6839\u672c\u4e0d\u5b58\u5728\uff0c\u53ea\u662f\u865a\u62df\u7684\u5f62\u8c61\u3002\u201c\u600e\u6837\u624d\u80fd\u901a\u8fc7\uff1f\u201d\u4ed6\u9547\u5b9a\u5730\u95ee\u3002</p><p>\u201c\u8c1c\u8bed\uff0c\u201d\u6076\u9b54\u8bf4\uff0c\u6258\u7740\u4e0b\u5df4\uff0c\u201c\u6211\u4eec\u6765\u731c\u8c1c\u8bed\u5427\u3002\u201d</p><p>\u201c\u731c\u8c1c\u8bed\uff1f\u201d</p><p>\u201c\u6211\u51fa\u4e09\u4e2a\u8c1c\u8bed\uff0c\u4f60\u4eec\u6765\u731c\u3002\u6bcf\u4e2a\u8c1c\u8bed\u6709\u4e09\u6b21\u731c\u7684\u673a\u4f1a\uff0c\u5982\u679c\u731c\u4e0d\u4e2d\u7684\u8bdd\uff0c\u5c31\u5f97\u7ee7\u7eed\u7559\u5728\u8fd9\u91cc\u3002\u201d</p><p>\u201c\u5982\u679c\u6211\u4eec\u731c\u4e2d\u5462\uff0c\u201d\u743c\u6069\u95ee\uff0c\u201c\u6211\u4eec\u5c31\u53ef\u4ee5\u79bb\u5f00\uff1f\u201d</p><p>\u201c\u8fd8\u4e0d\u884c\uff0c\u4f60\u4eec\u53ef\u4ee5\u4e5f\u51fa\u4e00\u4e2a\u8c1c\u8bed\uff0c\u7531\u6211\u6765\u731c\uff0c\u201d\u6076\u9b54\u72e1\u733e\u5730\u7b11\u7740\uff0c\u201c\u540c\u6837\u4e5f\u662f\u4e09\u6b21\u673a\u4f1a\uff0c\u5982\u679c\u6211\u731c\u4e0d\u51fa\uff0c\u90a3\u4e48\u4f60\u4eec\u53ef\u4ee5\u79bb\u5f00\uff1b\u4f46\u5982\u679c\u6211\u731c\u51fa\u4e86\uff0c\u90a3\u4e48\u4f60\u4eec\u4f9d\u65e7\u8981\u7559\u4e0b\u6765\uff0c\u76f4\u5230\u8ff7\u5bab\u81ea\u5df1\u6d88\u9664\u3002\u201d</p><p>\u743c\u6069\u8fd8\u6ca1\u8bf4\u8bdd\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u6709\u4e9b\u4e0d\u8010\u70e6\u8d77\u6765\uff0c\u5979\u58ee\u7740\u80c6\u5b50\uff0c\u4e0d\u7406\u776c\u6076\u9b54\uff0c\u5f84\u76f4\u4ece\u5b83\u8eab\u8fb9\u5f80\u51fa\u53e3\u8d70\u51fa\u3002\u6076\u9b54\u6ca1\u6709\u963b\u62e6\uff0c\u4f46\u7a7a\u95f4\u4eff\u4f5b\u5728\u6084\u65e0\u58f0\u606f\u95f4\u62c9\u957f\u4e86\uff0c\u539f\u672c\u51e0\u6b65\u8fdc\u7684\u8ddd\u79bb\u7a81\u7136\u53d8\u5f97\u9065\u4e0d\u53ef\u53ca\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u600e\u4e48\u4e5f\u8d70\u4e0d\u5230\u51fa\u53e3\u3002\u201c\u6ca1\u7528\u7684\uff0c\u201d\u6076\u9b54\u60a0\u95f2\u5730\u8bf4\uff0c\u201c\u901a\u8fc7\u6211\u7684\u8003\u9a8c\uff0c\u624d\u80fd\u8d70\u51fa\u8ff7\u5bab\uff0c\u8fd9\u5c31\u662f\u89c4\u5219\u2014\u2014\u6df1\u6e0a\u554a\uff0c\u6211\u8bc5\u5492\u8fd9\u8be5\u6b7b\u7684\u8bcd\uff0c\u4f46\u6ca1\u529e\u6cd5\uff0c\u4e8b\u5b9e\u5c31\u662f\u5982\u6b64\u3002\u201d</p><p>\u201c\u90a3\u597d\u5427\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u8bf4\u4f60\u7684\u8c1c\u8bed\u5427\u3002\u201d</p><p>\u201c\u542c\u597d\u4e86\uff0c\u201d\u6076\u9b54\u7cbe\u795e\u4e00\u632f\uff0c\u5b83\u54b3\u55fd\u4e24\u58f0\uff0c\u6e05\u4e86\u6e05\u55d3\u5b50\uff0c\u201c\u4ece\u524d\u5230\u540e\uff0c\u4ece\u4e0a\u5230\u4e0b\uff0c\u4ece\u5185\u5230\u5916\uff0c\u6211\u5168\u8eab\u5230\u5904\u90fd\u662f\u6d1e\uff0c\u4f46\u6211\u4f9d\u7136\u80fd\u591f\u7528\u6765\u88c5\u6c34\uff0c\u8bf7\u95ee\u6211\u662f\u4ec0\u4e48\uff1f\u201d</p><p>\u743c\u6069\u601d\u7d22\u7740\uff0c\u60f3\u8fd9\u5230\u5e95\u662f\u4e2a\u4ec0\u4e48\u4e1c\u897f\uff0c\u5168\u8eab\u5230\u5904\u662f\u6d1e\uff0c\u5374\u80fd\u591f\u7528\u6765\u88c5\u6c34\u3002\u201c\u6d77\u7ef5\u3002\u201d\u4ed6\u6700\u540e\u60f3\u51fa\u4e86\u7b54\u6848\u3002</p><p>\u201c\u554a\u54e6\uff0c\u201d\u6076\u9b54\u770b\u8d77\u6765\u6709\u4e9b\u5931\u671b\u7684\u6837\u5b50\uff0c\u201c\u597d\u5427\u597d\u5427\uff0c\u4f60\u78b0\u5bf9\u4e86\uff0c\u770b\u6765\u7b2c\u4e00\u6b21\u7684\u8fd0\u6c14\u603b\u662f\u4f1a\u597d\u4e9b\uff0c\u6216\u8005\u6211\u7684\u8c1c\u8bed\u8fd8\u662f\u592a\u5bb9\u6613\u4e86\u70b9\u2026\u2026\u201d</p><p>\u201c\u7b2c\u4e8c\u4e2a\u8c1c\u8bed\uff01\u201d\u743c\u6069\u6709\u70b9\u4e0d\u8010\u70e6\u5730\u6253\u65ad\u3002</p><p>\u201c\u597d\u5427\uff0c\u4eba\u7c7b\uff0c\u542c\u6e05\u695a\u4e86\uff0c\u201d\u6076\u9b54\u8bf4\uff0c\u201c\u5728\u4f60\u6240\u62e5\u6709\u7684\u4e00\u5207\u5f53\u4e2d\uff0c\u6211\u662f\u6700\u96be\u4ee5\u4fdd\u5b88\u7684\u3002\u4e00\u65e6\u6709\u4e86\u6211\uff0c\u4f60\u5c31\u4f1a\u60f3\u628a\u6211\u5206\u7ed9\u522b\u4eba\uff1b\u53ef\u662f\u4e00\u65e6\u628a\u6211\u5206\u7ed9\u522b\u4eba\uff0c\u4f60\u5c31\u5931\u53bb\u4e86\u6211\uff0c\u201d\u5b83\u9732\u9f7f\u5fae\u7b11\uff0c\u201c\u6211\u662f\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u79d8\u5bc6\u3002\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u7acb\u523b\u8bf4\u3002</p><p>\u6076\u9b54\u6709\u4e9b\u60ca\u8bb6\u5730\u770b\u7740\u7ef4\u5eb7\u5c3c\u4e9a\uff0c\u201c\u5514\uff0c\u4e00\u4e2a\u5353\u5c14\uff0c\u201d\u5b83\u8bf4\uff0c\u4eff\u4f5b\u521a\u521a\u624d\u8ba4\u51fa\u7ef4\u5eb7\u5c3c\u4e9a\u7684\u8eab\u4efd\uff0c\u201c\u90a3\u5c31\u4e0d\u5947\u602a\u4e86\u3002\u597d\u5427\uff0c\u4eba\u7c7b\uff0c\u73b0\u5728\u662f\u7b2c\u4e09\u4e2a\u8c1c\u8bed\u3002\u201d</p><p>\u201c\u6211\u6c38\u8fdc\u90fd\u5728\u8bf4\u5b9e\u8bdd\uff0c\u4f46\u6211\u6c38\u8fdc\u90fd\u4e0d\u4f1a\u8bf4\u8bdd\uff0c\u201d\u6076\u9b54\u8bf4\uff0c\u201c\u731c\u731c\u770b\uff0c\u6211\u662f\u4ec0\u4e48\uff1f\u201d</p><p>\u8fd9\u6b21\u7684\u8c1c\u8bed\u6bd4\u524d\u4e24\u4e2a\u66f4\u96be\uff0c\u743c\u6069\u548c\u7ef4\u5eb7\u5c3c\u4e9a\u90fd\u8003\u8651\u4e86\u5f88\u4e45\uff0c\u4f9d\u65e7\u8fd8\u662f\u4e0d\u80fd\u786e\u5b9a\u7b54\u6848\u3002\u201c\u5fc3\u7075\uff1f\u201d\u4ed6\u6709\u4e9b\u8fdf\u7591\u5730\u95ee\u3002</p><p>\u201c\u4e0d\u5bf9\uff01\u201d\u6076\u9b54\u5927\u58f0\u8bf4\u3002</p><p>\u201c\u4e00\u4e2a\u8bda\u5b9e\u7684\u54d1\u5df4\uff1f\u201d</p><p>\u6076\u9b54\u8ba5\u7b11\u8d77\u6765\uff0c\u201c\u4e16\u754c\u4e0a\u6ca1\u6709\u8bda\u5b9e\u7684\u4eba\u3002\u201d</p><p>\u743c\u6069\u76b1\u7d27\u7709\u5934\uff0c\u770b\u7740\u7ef4\u5eb7\u5c3c\u4e9a\uff0c\u671f\u5f85\u5979\u80fd\u4e0d\u80fd\u6709\u4ec0\u4e48\u7075\u5149\u4e00\u95ea\u3002\u6bcf\u4e2a\u8c1c\u8bed\u53ea\u6709\u731c\u4e09\u6b21\u7684\u673a\u4f1a\uff0c\u73b0\u5728\u5df2\u7ecf\u731c\u9519\u4e24\u6b21\u4e86\u3002\u5353\u5c14\u5c11\u5973\u770b\u8d77\u6765\u4e5f\u88ab\u96be\u4f4f\u4e86\uff0c\u6b63\u5728\u82e6\u601d\u51a5\u60f3\uff0c\u743c\u6069\u5fae\u5fae\u53f9\u6c14\uff0c\u6b63\u51c6\u5907\u5c06\u773c\u5149\u79fb\u5f00\uff0c\u7a81\u7136\u5fc3\u4e2d\u4e00\u52a8\u3002</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u7684\u4f8d\u7236\u6597\u7bf7\uff0c\u5e76\u975e\u5bfb\u5e38\u5e03\u6599\u6216\u8005\u76ae\u9769\u6750\u6599\uff0c\u800c\u662f\u7528\u86db\u4e1d\u5236\u6210\uff0c\u6d78\u900f\u4e86\u5f3a\u5927\u7684\u9b54\u6cd5\u529b\u91cf\uff0c\u4eff\u4f5b\u534a\u900f\u660e\u4e2d\u6c89\u6dc0\u7740\u9634\u5f71\u3002\u6b64\u65f6\u88f9\u5728\u5979\u8eab\u4e0a\uff0c\u9690\u9690\u7ea6\u7ea6\u5730\u663e\u51fa\u743c\u6069\u548c\u6076\u9b54\u7684\u5f71\u50cf\u6765\uff0c\u5c31\u50cf\u662f\u4e00\u9762\u955c\u5b50\u2026\u2026</p><p>\u955c\u5b50\uff1f</p><p>\u201c\u6211\u77e5\u9053\u7b54\u6848\u4e86\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u9762\u9732\u5fae\u7b11\uff0c\u201c\u6c38\u8fdc\u90fd\u5728\u8bf4\u5b9e\u8bdd\uff0c\u4f46\u6c38\u8fdc\u90fd\u4e0d\u4f1a\u8bf4\u8bdd\uff0c\u8fd9\u662f\u955c\u5b50\uff01\u201d</p><p>\u6076\u9b54\u76ef\u7740\u743c\u6069\uff0c\u4f3c\u4e4e\u6709\u4e9b\u4e0d\u6562\u7f6e\u4fe1\uff0c\u4f46\u6700\u540e\u8fd8\u662f\u6447\u4e86\u6447\u5de8\u5927\u7684\u8111\u888b\uff0c\u201c\u597d\u5427\uff0c\u4f60\u731c\u5bf9\u4e86\uff0c\u201d\u5b83\u561f\u56d4\u7740\uff0c\u201c\u90a3\u4e48\u8bf4\u4f60\u7684\u8c1c\u8bed\u5427\uff0c\u5982\u679c\u80fd\u591f\u96be\u4f4f\u6211\uff0c\u4f60\u4eec\u5c31\u53ef\u4ee5\u8d70\u4e86\u3002\u201d</p><p>\u743c\u6069\u8e0c\u8e87\u8d77\u6765\uff0c\u4ed6\u5e73\u65f6\u5f88\u5c11\u73a9\u731c\u8c1c\u8fd9\u79cd\u6e38\u620f\uff0c\u6839\u672c\u5c31\u6ca1\u8bb0\u5f97\u51e0\u4e2a\u8c1c\u8bed\uff0c\u8fd8\u90fd\u662f\u7279\u522b\u7b80\u5355\u7684\u90a3\u79cd\u3002\u5982\u4eca\u6076\u9b54\u8981\u4ed6\u51fa\u4e2a\u8c1c\u8bed\uff0c\u53cd\u800c\u6709\u4e9b\u65e0\u4ece\u63aa\u624b\u4e86\u3002</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u89c1\u4ed6\u795e\u8272\uff0c\u4e0a\u524d\u534a\u6b65\uff0c\u201c\u6211\u6765\u51fa\u8fd9\u4e2a\u8c1c\u8bed\uff0c\u201d\u5979\u5bf9\u743c\u6069\u8bf4\uff0c\u201c\u6211\u76f8\u4fe1\u5b83\u4e00\u5b9a\u731c\u4e0d\u5230\u3002\u201d</p><p>\u6076\u9b54\u4ece\u9f3b\u5b54\u91cc\u53d1\u51fa\u55e4\u55e4\u7684\u51b7\u7b11\uff0c\u201c\u522b\u592a\u81ea\u4fe1\uff0c\u5973\u58eb\uff0c\u201d\u5b83\u8bf4\uff0c\u201c\u8fd9\u4e16\u754c\u4e0a\u6211\u731c\u4e0d\u51fa\u7684\u8c1c\u8bed\u53ef\u4e0d\u591a\u3002\u201d</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u6ca1\u6709\u7406\u776c\uff0c\u201c\u6709\u4e00\u5bf9\u592b\u59bb\uff0c\u5728\u65c5\u884c\u4e2d\u906d\u9047\u4e86\u91ce\u517d\u7684\u88ad\u51fb\u3002\u4e08\u592b\u8ba9\u59bb\u5b50\u8d76\u5feb\u9003\u8131\uff0c\u81ea\u5df1\u62d6\u4f4f\u91ce\u517d\u3002\u59bb\u5b50\u6700\u540e\u9003\u8131\u4e86\uff0c\u4f46\u4e08\u592b\u88ab\u91ce\u517d\u6740\u6b7b\u4e86\uff0c\u201d\u5979\u5e73\u9759\u5730\u63cf\u8ff0\u7740\uff0c\u201c\u4e08\u592b\u4e3a\u4ec0\u4e48\u8981\u8fd9\u4e48\u505a\uff1f\u201d</p><p>\u6076\u9b54\u7684\u8138\u4e0a\u9732\u51fa\u82e6\u82e6\u601d\u7d22\u7684\u8868\u60c5\uff0c\u770b\u8d77\u6765\u5b83\u5bf9\u8fd9\u4e2a\u6545\u4e8b\uff0c\u6216\u8005\u8bf4\u5bf9\u6545\u4e8b\u4e2d\u4e08\u592b\u7684\u4f5c\u6cd5\u611f\u5230\u975e\u5e38\u8d39\u89e3\uff0c\u201c\u56e0\u4e3a\u8fd9\u4e2a\u4e08\u592b\u662f\u4e00\u4e2a\u9c81\u83bd\u597d\u6597\u7684\u4eba\uff1f\u201d</p><p>\u201c\u4e0d\u5bf9\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u8bf4\uff0c\u201c\u4ed6\u975e\u5e38\u8c28\u614e\uff0c\u751a\u81f3\u53ef\u4ee5\u8bf4\u80c6\u5c0f\u3002\u201d</p><p>\u201c\u56e0\u4e3a\u6709\u4eba\u7ed9\u4ed6\u5f88\u591a\u94b1\uff0c\u8ba9\u4ed6\u8fd9\u4e48\u505a\uff1f\u201d\u6076\u9b54\u7ee7\u7eed\u731c\u6d4b\u3002</p><p>\u201c\u5982\u679c\u6027\u547d\u90fd\u6ca1\u6709\u4e86\uff0c\u8981\u94b1\u53c8\u6709\u4ec0\u4e48\u7528\uff1f\u201d</p><p>\u6076\u9b54\u6293\u7740\u81ea\u5df1\u5149\u79c3\u79c3\u7684\u8111\u888b\uff0c\u5728\u539f\u5730\u8f6c\u7740\u5708\u5b50\uff0c\u201c\u6211\u660e\u767d\u4e86\uff0c\u201d\u5b83\u7a81\u7136\u5f97\u610f\u8d77\u6765\uff0c\u201c\u6545\u4e8b\u91cc\u7684\u59bb\u5b50\u662f\u4e2a\u5deb\u5e08\uff0c\u5979\u7528\u9b54\u6cd5\u63a7\u5236\u4e86\u4e08\u592b\u7684\u5fc3\u667a\uff0c\u6240\u4ee5\u4ed6\u624d\u4f1a\u8fd9\u4e48\u505a\u3002\u201d</p><p>\u201c\u59bb\u5b50\u662f\u4e2a\u4e1d\u6beb\u4e0d\u901a\u9b54\u6cd5\u7684\u666e\u901a\u4eba\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u8bf4\uff0c\u201c\u4f60\u63a5\u8fde\u731c\u9519\u4e09\u6b21\u4e86\uff0c\u4f60\u8f93\u4e86\u3002\u201d</p><p>\u201c\u4f46\u8fd9\u662f\u6839\u672c\u5c31\u4e0d\u53ef\u80fd\u53d1\u751f\u7684\u4e8b\u60c5\uff0c\u201d\u6076\u9b54\u6297\u8bae\uff0c\u201c\u7b54\u6848\u662f\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u56e0\u4e3a\u4ed6\u7231\u4ed6\u7684\u59bb\u5b50\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u8bf4\uff0c\u201c\u7b54\u6848\u5c31\u662f\u7231\u3002\u201d</p><p>\u201c\u7231\uff1f\u201d\u6076\u9b54\u91cd\u590d\u7740\u8fd9\u4e2a\u8bcd\uff0c\u5b83\u4f9d\u65e7\u662f\u6ee1\u8138\u7684\u56f0\u60d1\uff0c\u4f46\u6ca1\u6709\u518d\u8bf4\u4ec0\u4e48\uff0c\u8eab\u5f62\u6162\u6162\u5316\u4f5c\u9ed1\u96fe\uff0c\u6e10\u6e10\u6d88\u6563\u3002</p><p>\u201c\u5f88\u6709\u8da3\u7684\u8c1c\u8bed\uff0c\u201d\u743c\u6069\u5938\u5956\uff0c\u201c\u4f46\u4f60\u600e\u4e48\u77e5\u9053\u5b83\u731c\u4e0d\u51fa\uff1f\u5b9e\u9645\u4e0a\uff0c\u6211\u89c9\u5f97\u8fd9\u8c1c\u8bed\u592a\u7b80\u5355\u4e86\u3002\u201d</p><p>\u201c\u56e0\u4e3a\u5f53\u5e74\u4e9a\u5f53\u65af\u53d4\u53d4\u8ba9\u6211\u731c\u8fd9\u4e2a\u8c1c\u8bed\u7684\u65f6\u5019\uff0c\u6211\u731c\u4e86\u5341\u6b21\u4e5f\u6ca1\u731c\u51fa\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u8bf4\uff0c\u201c\u6211\u662f\u4e2a\u5353\u5c14\uff0c\u800c\u8bbe\u7f6e\u8fd9\u4e2a\u8ff7\u5bab\u672f\u7684\u5deb\u5e08\u2014\u2014\u4e0d\u7ba1\u4ed6\u662f\u8c01\u2014\u2014\u4e5f\u662f\u4e2a\u5353\u5c14\u3002\u4e9a\u5f53\u65af\u53d4\u53d4\u5f53\u65f6\u8bf4\uff0c\u53ea\u8981\u662f\u5353\u5c14\uff0c\u5c31\u731c\u4e0d\u51fa\u8fd9\u4e2a\u8c1c\u8bed\u3002\u201d</p></article>"}}}]);
