(window.webpackJsonp=window.webpackJsonp||[]).push([[263],{286:function(p){p.exports={chapterTitle:null,title:"\u7b2c148\u7ae0 \u51c6\u5907\u6316\u5927\u5751",content:"<article><h2>\u7b2c148\u7ae0 \u51c6\u5907\u6316\u5927\u5751</h2><p>\u3000\u3000\u65e2\u7136\u53f6\u5efa\u658c\u63d0\u5230\u8c22\u5251\u5357\u4ee5\u53ca\u8c22\u5251\u5357\u521b\u529e\u7684\u4e39\u6d25\u516c\u53f8\uff0c\u5728\u63a5\u4e0b\u6765\u7684\u8bdd\u9898\u91cc\u4e0d\u53ef\u907f\u514d\u7684\u8981\u8c08\u5230\u6b63\u5904\u4e8e\u706b\u7206\u72c2\u70ed\u4e2d\u7684\u4fdd\u5065\u54c1\u884c\u4e1a\u3002</p><p>\u3000\u3000\u6574\u4e2a\u4e5d\u5341\u5e74\u4ee3\u4fdd\u5065\u54c1\u5e02\u573a\u7684\u5d1b\u8d77\uff0c\u4ece\u662f\u5e7f\u4e1c\u592a\u9633\u795e\u3001\u8fbd\u5b81\u98de\u9f99\u5f00\u59cb\u7684\uff0c\u800c\u98de\u9f99\u5ef6\u751f\u62a4\u5b9d\u6db2\u7684\u5e02\u573a\u6a21\u5f0f\u5728\u4e5d\u5341\u5e74\u4ee3\u83b7\u5f97\u6700\u5927\u7684\u6210\u529f\uff0c\u98de\u9f99\u96c6\u56e2\u6ca1\u6709\u76d6\u5382\u623f\uff0c\u4e5f\u6ca1\u6709\u7f6e\u8d44\u4ea7\uff0c\u800c\u662f\u575a\u6301\u201c\u5e7f\u544a\u3001\u5e02\u573a\u3001\u6548\u76ca\u201d\u7684\u8425\u9500\u5faa\u73af\u6218\u7565\uff0c\u5c06\u98de\u9f99\u96c6\u56e2\u63a8\u5230\u4fdd\u5065\u54c1\u884c\u4e1a\u7684\u9f99\u5934\u5730\u4f4d\uff0c\u5176\u540e\uff0c\u5de8\u4eba\u3001\u4e09\u682a\u3001\u7ea2\u6843K\u90fd\u4e0d\u7a0b\u5ea6\u7684\u4eff\u6548\u98de\u9f99\u7684\u505a\u6cd5\uff0c\u5229\u7528\u865a\u5047\u7684\u3001\u5938\u5927\u5176\u8f9e\u7684\u5e7f\u544a\uff0c\u63a0\u593a\u5de8\u989d\u8d22\u5bcc\u3002\u8c22\u5251\u5357\u5229\u7528\u8c22\u6c0f\u5bb6\u65cf\u57fa\u91d1\u521b\u529e\u7684\u4e39\u6d25\u751f\u7269\u4fdd\u5065\u54c1\u516c\u53f8\u53ea\u662f\u8fd9\u80a1\u5927\u6d41\u4e2d\u7684\u4e00\u5458\u3002</p><p>\u3000\u3000\u4e5d\u4e94\u5e74\u3001\u4e5d\u516d\u5e74\u662f\u8fd9\u80a1\u72c2\u6f6e\u7684\u9ad8\u5cf0\u671f\u3002</p><p>\u3000\u3000\u4f46\u8fd9\u79cd\u6a21\u5f0f\u6240\u63a0\u593a\u7684\u5de8\u989d\u5229\u6da6\uff0c\u7edd\u5927\u90e8\u5206\u88ab\u5e9e\u5927\u7684\u9500\u8425\u7f51\u7edc\u541e\u566c\u3002\u4e09\u682a\u96c6\u56e2\u6700\u81c3\u80bf\u5e9e\u5927\u65f6\uff0c\u96c6\u56e2\u76f4\u5c5e\u7684\u9500\u552e\u4eba\u5458\u591a\u8fbe\u5341\u4e94\u4e07\uff0c\u4e09\u682a\u96c6\u56e2\u4e5d\u516d\u5e74\u9500\u552e\u989d\u9ad8\u8fbe\u516b\u5341\u4ebf\uff0c\u8981\u6b63\u5e38\u7684\u7ef4\u6301\u8fd9\u4e2a\u9500\u552e\u7f51\u7edc\u81f3\u5c11\u8981\u6d88\u8017\u4e00\u534a\u7684\u9500\u552e\u989d\uff08\u56db\u5341\u4ebf\uff09\uff0c\u4f46\u662f\u5728\u4e5d\u5341\u5e74\u4ee3\u7684\u4e2d\u56fd\uff0c\u8fd8\u6ca1\u6709\u4e00\u5bb6\u6c11\u8425\u516c\u53f8\u7684\u7ba1\u7406\u6c34\u5e73\u80fd\u6709\u6548\u7684\u7ef4\u6301\u8fd9\u4e2a\u9500\u552e\u7f51\u7edc\u3002\u4e5d\u516d\u5e74\uff0c\u4e09\u682a\u56e0\u4e3a\u4e00\u6b21\u5076\u7136\u7684\u65b0\u95fb\u4e8b\u4ef6\u800c\u5760\u843d\uff1b\u98de\u9f99\u96c6\u56e2\u4e5d\u516d\u5e74\u56e0\u4e3a\u62d6\u6b202\u4ebf\u5e7f\u544a\u6b3e\u3001\u62d6\u6b208000\u4e07\u539f\u6750\u6599\u6b3e\u800c\u5ba3\u544a\u6e83\u8d25\uff1b\u5de8\u4eba\u96c6\u56e2\u6700\u7ec8\u56e0\u4e3a\u56db\u5343\u4e07\u7684\u697c\u82b1\u503a\u52a1\u800c\u5bfc\u81f4\u8d22\u52a1\u5371\u673a\uff0c\u5f15\u8d77\u6574\u4e2a\u96c6\u56e2\u7684\u5d29\u57ae\uff0c\u53f2\u7389\u67f1\u80cc\u8d1f\u5de8\u989d\u503a\u52a1\u800c\u82e6\u82e6\u6323\u624e\u3002</p><p>\u3000\u3000\u5f20\u606a\u5165\u804c\u9686\u88d5\u8d38\u6613\u96c6\u56e2\u6709\u673a\u4f1a\u5c31\u8bfbMBA\uff0c\u524d\u534a\u6bb5\u65f6\u95f4\u5c31\u662f\u5bf9\u90a3\u4e9b\u5931\u8d25\u7684\u2026\u2026\u906d\u53d7\u91cd\u521b\u7684\u3001\u9677\u5165\u56f0\u5883\u7684\u516c\u53f8\u8fdb\u884c\u5206\u6790\uff0c\u4e5d\u5341\u5e74\u4ee3\u7684\u4fdd\u5065\u54c1\u884c\u4e1a\u662f\u5fc5\u9700\u7684\u529f\u8bfe\uff0c\u8c22\u5251\u5357\u7684\u4e39\u6d25\u53e3\u670d\u6db2\u4e5f\u662f\u7ecf\u5178\u6848\u4f8b\u4e4b\u4e00\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u4ece\u5c0f\u5728\u5546\u4e1a\u65b9\u9762\u6240\u53d7\u7684\u85b0\u9676\u6765\u81ea\u4e8e\u8c22\u5bb6\u4f20\u7edf\u7684\u5f71\u54cd\uff0c\u7ba1\u7406\u4e0a\u7684\u7d20\u517b\u8981\u6bd4\u5176\u4ed6\u51fa\u8eab\u8349\u83bd\u7684\u521b\u4e1a\u8005\u8981\u6d53\u539a\u4e00\u4e9b\uff0c\u4f46\u4e0d\u610f\u5473\u7740\u4ed6\u80fd\u66f4\u6210\u529f\u3002\u4e0e\u5176\u4ed6\u4fdd\u5065\u4e1a\u7684\u521b\u4e1a\u8005\u76f8\u7c7b\uff0c\u65e9\u671f\u7684\u8c22\u5251\u5357\u5bf9\u5e02\u573a\u975e\u5e38\u654f\u9510\uff0c\u5374\u758f\u4e8e\u7ba1\u7406\uff0c\u4e39\u6d25\u53e3\u670d\u6db2\u867d\u7136\u83b7\u5f97\u5e02\u573a\u7684\u6210\u529f\uff0c\u4f46\u662f\u76c8\u5229\u65b9\u9762\u7edd\u4e0d\u4e50\u89c2\uff0c\u8fd9\u5927\u6982\u4e5f\u662f\u8c22\u5251\u5357\u5c06\u4e39\u6d25\u516c\u53f8\u4e22\u7ed9\u6b63\u6cf0\u96c6\u56e2\u53ea\u8eab\u5230\u6d77\u5916\u6df1\u9020\u7684\u521d\u8877\u4e4b\u4e00\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u8131\u80ce\u6362\u9aa8\u518d\u56de\u56fd\u5185\uff0c\u624d\u771f\u6b63\u6210\u4e3a\u4e1c\u6d77\u5546\u754c\u91cd\u8981\u7684\u4eba\u7269\u4e4b\u4e00\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u672c\u6765\u5bf9\u4fdd\u5065\u54c1\u5012\u662f\u5f88\u611f\u5174\u8da3\uff0c\u4ee3\u7406\u7231\u7acb\u4fe1\u83b7\u5f97\u5de8\u5927\u7684\u6210\u529f\uff0c\u76db\u946b\u81ea\u7136\u4e0d\u4f1a\u6ee1\u8db3\u4e8e\u73b0\u72b6\u3001\u88f9\u8db3\u4e0d\u524d\uff0c\u9664\u4e86\u7a33\u5b9a\u534e\u4e1c\u5730\u533a\u7684\u901a\u4fe1\u5e02\u573a\u4e4b\u5916\uff0c\u4ed6\u5f00\u59cb\u8003\u8651\u6d89\u53ca\u5176\u4ed6\u9886\u57df\u3002\u4f46\u7ecf\u5f20\u606a\u7684\u5206\u6790\uff0c\u4ed6\u77e5\u9053\u6b64\u65f6\u8fdb\u5165\u4fdd\u5065\u54c1\u884c\u4e1a\u5df2\u7ecf\u4e0d\u662f\u6700\u4f73\u7684\u65f6\u673a\u3002</p><p>\u3000\u3000\u80fd\u5728\u4fdd\u5065\u54c1\u5e02\u573a\u521a\u521a\u542f\u52a8\u7684\u4e5d\u4e8c\u5e74\uff0c\u5b8c\u5168\u53ef\u4ee5\u6324\u8fdb\u53bb\u635e\u4e00\u628a\uff0c\u5f20\u606a\u5bf9\u7ef4\u6301\u90a3\u4e48\u590d\u6742\u7684\u8425\u9500\u7f51\u7edc\u4e5f\u6ca1\u6709\u4fe1\u5fc3\uff0c\u4f46\u662f\u5728\u5229\u6da6\u63a7\u5236\u4e0a\u7a0d\u5fae\u4e25\u683c\u4e00\u4e9b\uff0c\u635e\u8db3\u94b1\u4e4b\u540e\uff0c\u5c31\u8ba9\u8425\u9500\u7f51\u7edc\u5d29\u6e83\u4e5f\u6ca1\u6709\u5173\u7cfb\u3002</p><p>\u3000\u3000\u4f46\u662f\u73b0\u5728\u5df2\u7ecf\u5230\u4e86\u4fdd\u5065\u54c1\u5e02\u573a\u72c2\u70ed\u7684\u4e5d\u4e94\u5e74\uff0c\u660e\u540e\u5e74\uff0c\u56fd\u5bb6\u4f1a\u5bf9\u4fdd\u5065\u54c1\u5e02\u573a\u8fdb\u884c\u6574\u987f\uff0c\u4fdd\u5065\u54c1\u5e02\u573a\u5c31\u4f1a\u5927\u5e45\u6536\u7f29\uff0c\u8fd9\u65f6\u5019\u518d\u6324\u8fdb\u53bb\uff0c\u5c31\u9700\u8981\u6709\u96c4\u539a\u7684\u6280\u672f\u3001\u8d44\u672c\u3001\u7ba1\u7406\u65b9\u9762\u7684\u8d44\u6e90\u624d\u53ef\u80fd\u83b7\u5f97\u6210\u529f\uff0c\u800c\u76db\u946b\u4e5f\u6070\u6070\u6709\u8bb8\u591a\u7684\u4e0d\u8db3\u3002</p><p>\u3000\u3000\u4e0d\u8fc7\u53f6\u5efa\u658c\u7b49\u4eba\u90fd\u9677\u5165\u5e02\u573a\u7b56\u7565\u9b54\u529b\u5e26\u6765\u7684\u72c2\u70ed\u4e4b\u4e2d\uff0c\u4ed6\u4eec\u53ea\u662f\u8ba4\u4e3a\u4fdd\u5065\u54c1\u884c\u4e1a\u65f6\u673a\u4e0d\u518d\uff0c\u8fd8\u6ca1\u6709\u770b\u5230\u4fdd\u5065\u54c1\u5e02\u573a\u5728\u660e\u540e\u4e24\u5e74\u5c06\u4f1a\u51fa\u73b0\u7684\u8840\u6dcb\u6dcb\u7684\u573a\u9762\uff0c\u5bf9\u5e02\u573a\u7684\u53cd\u601d\u81ea\u7136\u4e0d\u8db3\u3002</p><p>\u3000\u3000\u5f20\u606a\u5fae\u5fae\u4e00\u7b11\uff0c\u8fc7\u4e24\u5e74\uff0c\u4e0d\u8981\u8bf4\u4fdd\u5065\u54c1\u4e1a\u4e86\uff0c\u5f71\u789f\u673a\u4ea7\u4e1a\u7684\u72c2\u66b4\u4e0e\u8840\u8165\u4e5f\u80fd\u8ba9\u4ed6\u4eec\u8ba4\u6e05\u5e02\u573a\u4e4b\u624b\u9b44\u529b\u4e0e\u51f6\u6b8b\u3002\u8fd9\u65f6\u5019\u8ddf\u4ed6\u4eec\u8bf4\u201c\u6ca1\u6709\u597d\u7684\u4ea7\u54c1\u5c31\u6ca1\u6709\u597d\u7684\u8425\u9500\u7b56\u7565\u201d\u3001\u201c\u6ca1\u6709\u597d\u7684\u7ba1\u7406\uff0c\u6c38\u8fdc\u90fd\u65e0\u6cd5\u83b7\u5f97\u5229\u6da6\u201d\u4e4b\u7c7b\u7684\u6559\u6761\u5927\u6982\u4e0d\u4f1a\u5f97\u5230\u4ed6\u4eec\u7684\u5171\u9e23\u3002\u65e2\u7136\u8868\u660e\u9ad8\u4e2d\u751f\u7684\u8eab\u4efd\uff0c\u53ea\u6015\u4e5f\u4e0d\u9002\u5408\u62ff\u7740\u6559\u6761\u4e3b\u4e49\u6765\u6559\u5bfc\u522b\u4eba\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u3001\u53f6\u5c0f\u6850\u3001\u90b5\u5fc3\u6587\u7b49\u4eba\u90fd\u89c1\u8bc6\u5230\u5f20\u606a\u4ee4\u4eba\u60ca\u8273\u7684\u5e02\u573a\u7b56\u5212\u80fd\u529b\uff0c\u6b64\u65f6\u9762\u5bf9\u4ed6\u7684\u9ad8\u4e2d\u751f\u8eab\u4efd\uff0c\u53ea\u80fd\u62ff\u5546\u4e1a\u5929\u624d\u6765\u81ea\u6211\u89e3\u91ca\uff0c\u867d\u7136\u6709\u6781\u5927\u7684\u9707\u60ca\uff0c\u5012\u4e5f\u4e0d\u662f\u65e0\u6cd5\u63a5\u53d7\u73b0\u5b9e\u3002\u62dc\u4e0d\u62dc\u5e08\u7684\u95ee\u9898\uff0c\u5374\u6ca1\u6709\u7ea0\u7f20\u4e0b\u53bb\u3002</p><p>\u3000\u3000\u5f53\u7136\u53f6\u5efa\u658c\u6c38\u8fdc\u90fd\u4e0d\u4f1a\u653e\u5f03\u8ffd\u7a76\u65b0\u7684\u673a\u9047\uff0c\u4ed6\u542c\u8bf4\u5f20\u606a\u8fd9\u6bb5\u65f6\u95f4\u6ca1\u6709\u65f6\u95f4\u5173\u6ce8\u7231\u7acb\u4fe1398\u5728\u534e\u4e1c\u5730\u65b9\u7684\u9500\u4fe1\u60c5\u51b5\uff0c\u5c31\u7b97\u9526\u6e56\u73b0\u5728\u6b63\u5904\u4e8e\u91cd\u5927\u8c03\u6574\u4e2d\u4ed6\u4e5f\u6687\u5173\u6ce8\uff0c\u5fc3\u60f3\u5f20\u606a\u5927\u6982\u4e5f\u4e0d\u4f1a\u4e13\u6ce8\u4ed6\u7684\u9ad8\u4e2d\u5b66\u4e1a\u5427\uff0c\u5bf9\u5f20\u606a\u4e4b\u524d\u63d0\u53ca\u7684\u201c\u5176\u4ed6\u4e8b\u201d\u5341\u5206\u611f\u5174\u8da3\u3002</p><p>\u3000\u3000\u5728\u8c22\u665a\u6674\u7684\u529e\u516c\u5ba4\u91cc\u867d\u7136\u803d\u6401\u5f88\u957f\u65f6\u95f4\uff0c\u4f46\u662f\u79bb\u5403\u665a\u996d\u7684\u65f6\u95f4\u8fd8\u957f\uff0c\u5f20\u606a\u7d22\u6027\u51b3\u5b9a\u62c9\u53f6\u5efa\u658c\u4e00\u884c\u5230\u7231\u8fbe\u7535\u5b50\u5382\u53c2\u89c2\u53bb\u3002</p><p>\u3000\u3000\u5468\u6e38\u966a\u7740\u53f6\u5efa\u658c\u3001\u53f6\u5c0f\u6850\u3001\u90b5\u5fc3\u6587\u4ed6\u4eec\u5148\u4e0b\u697c\uff0c\u5f20\u606a\u53bb\u627e\u8bb8\u601d\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u6bb5\u65f6\u95f4\u8fde\u8d70\u5165\u65b0\u6d77\u901a\u5927\u53a6\u7684\u6b21\u6570\u90fd\u5c48\u6307\u53ef\u6570\uff0c\u66f4\u6ca1\u6709\u4e0e\u8bb8\u601d\u72ec\u5904\u7684\u673a\u4f1a\uff0c\u5f20\u606a\u5728\u848b\u8587\u7684\u529e\u516c\u5ba4\u91cc\u622a\u4f4f\u8bb8\u601d\uff1a\u201c\u53bb\u7231\u8fbe\u7535\u5b50\uff0c\u4f60\u4e5f\u53bb\u770b\u770b\uff1f\u201d</p><p>\u3000\u3000\u8bb8\u601d\u62cd\u62cd\u8111\u95e8\uff0c\u5c31\u6015\u4e00\u65c1\u7684\u848b\u8587\u770b\u51fa\u4ec0\u4e48\u5f02\u5e38\u6765\uff0c\u5fc3\u865a\u7684\u8bf4\uff1a\u201c\u624b\u91cc\u5934\u4e00\u5806\u4e8b\u60c5\uff0c\u8981\u4e0d\u662f\u966a\u76db\u946b\u7684\u4eba\uff0c\u665a\u6674\u59d0\u3001\u5468\u603b\u90fd\u505c\u4e0d\u4e0b\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u848b\u8587\u62b1\u6028\u7684\u8bf4\uff1a\u201c\u4e09\u5bb6\u516c\u53f8\u7684\u8d22\u52a1\u90fd\u5806\u5230\u6211\u5934\u4e0a\uff0c\u8d44\u672c\u5bb6\u90fd\u6ca1\u89c1\u8fc7\u4f60\u4eec\u8fd9\u4e48\u5265\u524a\u4eba\u7684\u554a\uff01\u201d</p><p>\u3000\u3000\u8d8a\u79c0\u7684\u8d22\u52a1\u95ee\u9898\u8fd8\u7b80\u5355\u4e00\u4e9b\uff0c\u53cd\u6b63\u662f\u7a7a\u58f3\u516c\u53f8\uff0c\u800c\u4e14\u4ee5\u540e\u7684\u8d44\u91d1\u76f4\u63a5\u5212\u5165\u7231\u8fbe\u7535\u5b50\u7684\u8d26\u6237\u8fdb\u884c\u7ba1\u7406\uff0c\u4f46\u662f\u5c31\u9526\u6e56\u4e0e\u7231\u8fbe\u7535\u5b50\u7684\u8d22\u52a1\u95ee\u9898\u5c31\u5df2\u7ecf\u8ba9\u848b\u8587\u5934\u75bc\u4e86\u3002</p><p>\u3000\u3000\u89c1\u8bb8\u601d\u4e0d\u60f3\u8ddf\u7740\u7231\u8fbe\u7535\u5b50\uff0c\u5f20\u606a\u62b1\u6b49\u7684\u7b11\u4e86\u7b11\uff0c\u53c8\u662f\u62f1\u624b\u53c8\u662f\u97a0\u8eac\u7684\u9000\u51fa\u848b\u8587\u7684\u529e\u516c\u5ba4\u3002</p><p>\u3000\u3000\u770b\u7740\u5f20\u606a\u79bb\u5f00\uff0c\u848b\u8587\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u5bf9\u8bb8\u601d\u8bf4\uff1a\u201c\u4ed6\u5012\u662f\u86ee\u4f53\u8d34\u4f60\u7684\u3002\u201d</p><p>\u3000\u3000\u848b\u8587\u8fd9\u4e48\u8bf4\u4e5f\u662f\u65e0\u610f\uff0c\u8bb8\u601d\u542c\u4e86\u5fc3\u91cc\u4e00\u865a\uff0c\u5a07\u7f8e\u7684\u8138\u86cb\u67d3\u4e0a\u4e00\u5c42\u7ea2\u6655\u3002</p><p>\u3000\u3000\u5f20\u606a\u4ed6\u4eec\u521a\u8d70\uff0c\u5b8b\u57f9\u660e\u5c31\u8ffd\u8fc7\u6765\uff0c\u996e\u98df\u5e7f\u573a\u5c31\u5b9a\u8fd9\u51e0\u5929\u6b63\u5f0f\u5bf9\u5916\u8425\u4e1a\uff0c\u5e7f\u544a\u724c\u90fd\u7ad6\u524d\u95e8\u5927\u8857\u4e86\uff0c\u5176\u4ed6\u4eba\u5012\u4e5f\u662f\u7f62\u4e86\uff0c\u9526\u6e56\u8fd9\u8fb9\uff0c\u53ea\u8981\u80fd\u62bd\u51fa\u65f6\u95f4\uff0c\u5b8b\u57f9\u660e\u90fd\u8981\u4eb2\u81ea\u8d70\u4e00\u8d9f\u7684\u3002\u542c\u8bf4\u8c22\u665a\u6674\u3001\u5f20\u606a\u7b49\u4eba\u521a\u521a\u79bb\u5f00\u516c\u53f8\uff0c\u4ed6\u4e5f\u6ca1\u6709\u5728\u9526\u6e56\u505c\u7559\uff0c\u8d76\u7d27\u4e0b\u697c\u5c31\u8ffd\u4e86\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u5230\u6d77\u5dde\u8fd9\u4e00\u8d9f\u4e5f\u591f\u59d4\u5c48\u7684\uff0c\u60f3\u60f3TI\u4e2d\u56fd\u516c\u53f8\u603b\u88c1\uff0c\u638c\u63e1\u56fd\u5185\u7535\u5b50\u4ea7\u4e1a\u6570\u5341\u4ebf\u7684\u5e02\u573a\u4efd\u989d\uff0c\u65e0\u58f0\u65e0\u606f\u7684\u5230\u6d77\u5dde\u8d70\u4e86\u4e00\u8d9f\uff0c\u53c8\u65e0\u58f0\u65e0\u606f\u7684\u79bb\u5f00\u6d77\u5dde\uff0c\u4e0d\u8981\u8bf4\u5730\u65b9\u5927\u5458\u4eb2\u81ea\u63a5\u5f85\uff0c\u8fde\u6d77\u5dde\u65e5\u62a5\u4e2d\u7f1d\u6d88\u606f\u90fd\u6ca1\u6709\u520a\u767b\u4e00\u6761\u3002</p><p>\u3000\u3000\u5f20\u606a\u81ea\u7136\u6e05\u695a\u5f71\u789f\u673a\u5e02\u573a\u5728\u7a81\u7136\u7206\u53d1\u4e4b\u540e\u4f1a\u53d8\u5f97\u591a\u4e48\u6b8b\u9177\uff0c\u53ea\u8981\u6ca1\u6709\u522b\u7684\u5e72\u6270\uff0c\u65e5\u540e\u7684\u65b0\u79d1\u3001\u91d1\u6b63\u3001\u7231\u591a\u3001\u4e07\u5229\u8fbe\u4e5f\u5c06\u7a81\u7136\u5d1b\u8d77\u4e8e\u5f71\u789f\u673a\u4ea7\u4e1a\uff0c\u5e95\u724c\u80fd\u6342\u5230\u6700\u540e\uff0c\u8fd8\u662f\u6342\u5230\u6700\u540e\u7684\u597d\uff0c\u5c31\u7b97\u5218\u660e\u8f89\u4e5f\u53ea\u77e5\u9053\u7231\u8fbe\u7535\u5b50\u4e0eTI\u6709\u5408\u4f5c\uff0c\u4f46\u662f\u4e0eTI\u516c\u53f8\u534f\u8bae\u7684\u5177\u4f53\u5185\u5bb9\uff0c\u4e5f\u53ea\u6709\u5f20\u606a\u3001\u4e01\u69d0\u3001\u82cf\u6d25\u4e1c\u4e09\u4e2a\u4eba\u77e5\u9053\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd8\u6253\u7b97\u5728\u9999\u6e2f\u6ce8\u518c\u4e00\u5bb6\u4ee3\u7406\u516c\u53f8\uff0c\u89e3\u7801\u82af\u7247\u524d\u51e0\u5e74\u6ce8\u5b9a\u8981\u4ece\u9999\u6e2f\u8f6c\u8fdb\u53e3\uff0c\u5f20\u606a\u5e0c\u671b\u89e3\u7801\u82af\u7247\u534f\u8bae\u4ef7\u683c\u4e0e\u5e02\u573a\u4ef7\u683c\u4e4b\u95f4\u7684\u5dee\u4ef7\u5229\u6da6\u5c31\u622a\u7559\u5728\u9999\u6e2f\u4ee3\u7406\u516c\u53f8\u90a3\u91cc\uff0c\u90a3\u65f6\u522b\u4eba\u4e5f\u4f11\u60f3\u4ece\u7231\u8fbe\u7535\u5b50\u7684\u7a0e\u52a1\u3001\u8d22\u52a1\u4e0a\u53d1\u73b0\u4e01\u70b9\u7834\u7efd\u3002</p><p>\u3000\u3000\u8d76\u5230\u76ca\u9686\u9547\u897f\u9996\u7684\u7231\u8fbe\u7535\u5b50\u76ee\u524d\u60df\u4e00\u7684\u5de5\u5382\uff0c\u53f6\u5efa\u658c\u7684\u5370\u8c61\u662f\u592a\u5c0f\uff0c\u5728\u5de5\u5382\u8f6c\u4e86\u4e00\u5708\uff0c\u8fd8\u4e0d\u5230\u4e00\u523b\u949f\u3002</p><p>\u3000\u3000\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u5012\u662f\u7cbe\u529b\u65fa\u76db\uff0c\u4ed6\u4eec\u6ca1\u6709\u5f20\u606a\u90a3\u4e48\u591a\u6742\u52a1\uff0c\u4e01\u69d0\u4e0e\u53f6\u5efa\u658c\u4ed6\u4eec\u6253\u4e86\u4e2a\u7167\u9762\uff0c\u5c31\u544a\u8f9e\u79bb\u5f00\uff0c\u5bf9\u4ed6\u6765\u8bf4\uff0c\u5206\u79d2\u5fc5\u4e89\uff0c\u518d\u8bf4\u4ed6\u73b0\u5728\u5bf9\u751f\u4ea7\u3001\u5e02\u573a\u65b9\u9762\u7684\u5185\u5bb9\u4e5f\u63d0\u4e0d\u8d77\u5174\u8da3\u3002</p><p>\u3000\u3000\u201c\u4f60\u4eec\u8981\u4e0a\u5f71\u789f\u673a\uff1f\u201d\u53f6\u5efa\u658c\u95ee\u5f20\u606a\u3002</p><p>\u3000\u3000\u4e1c\u6d77\u7701\u7684\u5e02\u573a\uff0c\u4e0d\u6c42\u6d77\u6cf0\uff0c\u90a3\u81ea\u7136\u5c31\u8981\u62dc\u6258\u76db\u946b\uff0c\u5f20\u606a\u5012\u4e0d\u7792\u4ed6\uff1a\u201c\u5c0f\u5382\u5b50\uff0c\u4ee5\u540e\u8fd8\u8981\u8bf7\u53f6\u603b\u7167\u987e\u3002\u201d</p><p>\u3000\u3000\u76db\u946b\u7684\u6e20\u9053\u8986\u76d6\u534e\u4e1c\u5730\u533a\uff0c\u53f6\u5efa\u658c\u5bf9\u5f71\u789f\u673a\u5e02\u573a\u5df2\u7ecf\u840c\u53d1\u51fa\u6765\u7684\u82d7\u5934\u4e5f\u6e05\u695a\uff1a\u8bf4\u9053\uff1a\u201c\u5e7f\u5dde\u3001\u6df1\u5733\u90a3\u8fb9\u5f88\u70ed\u95f9\uff0c\u636e\u8bf4\u53ef\u4ee5\u53d6\u4ee3\u5f55\u50cf\u673a\uff0c\u5f55\u50cf\u673a\u53bb\u5e74\u7684\u9500\u91cf\u5dee\u4e0d\u591a\u6709\u4e00\u767e\u4e07\u53f0\uff0c\u6211\u60f3\u5f71\u789f\u673a\u80fd\u4e0e\u5f55\u50cf\u673a\u5206\u4eab\u8fd9\u4e48\u5927\u7684\u5e02\u573a\uff0c\u5f88\u6709\u524d\u9014\u3002\u76db\u946b\u6709\u505a\u5f55\u50cf\u673a\u7684\u4ee3\u7406\uff0c\u4f60\u4eec\u4ec0\u4e48\u65f6\u5019\u51fa\u4ea7\u54c1\uff0c\u76db\u946b\u5e2e\u4f60\u4eec\u505a\u5e02\u573a\u2026\u2026\u201d</p><p>\u3000\u3000\u53f6\u5efa\u658c\u8ba9\u5e02\u573a\u90e8\u505a\u8fc7\u8c03\u67e5\uff0c\u5e02\u573a\u90e8\u505a\u51fa\u4e24\u5230\u4e09\u5e74\u5e02\u573a\u5bb9\u91cf\u5c06\u8fbe\u5230\u4e94\u5341\u4e07\u53f0\u7684\u9884\u6d4b\uff0c\u76f8\u5bf9\u5f53\u524d\u4e07\u71d5\u5728\u552e\u7684\u5f71\u789f\u673a\u62a5\u4ef7\uff0c\u5e02\u573a\u89c4\u6a21\u8fbe\u5230\u4e8c\u5341\u4ebf\u5de6\u53f3\uff0c\u5df2\u7ecf\u662f\u76f8\u5f53\u4e0d\u9519\u7684\u5e02\u573a\u4e86\u3002</p><p>\u3000\u3000\u5c06\u6838\u5fc3\u5143\u4ef6\u7684\u5784\u65ad\u66b4\u5229\u6263\u9664\uff0c\u5f71\u789f\u673a\u7684\u6210\u672c\u53ea\u6709\u5f55\u50cf\u673a\u7684\u56db\u5206\u4e4b\u4e00\uff0c\u5f53\u7136\uff0c\u524d\u671f\u5e02\u573a\u6700\u5173\u952e\u7684\u51b3\u5b9a\u56e0\u7d20\u8fd8\u662f\u7247\u6e90\uff0c\u5f71\u789f\u673a\u5e02\u573a\u771f\u6b63\u5d1b\u8d77\u4e4b\u65e5\uff0c\u5c31\u662f\u5f55\u50cf\u673a\u5e02\u573a\u5206\u5d29\u79bb\u6790\u4e4b\u65f6\u3002</p><p>\u3000\u3000\u201c\u6211\u770b\u76db\u946b\u8fd8\u662f\u628a\u5f55\u50cf\u673a\u7684\u4e1a\u52a1\u653e\u5f03\u6389\u4e3a\u597d\u2026\u2026\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u4e5f\u4e0d\u591a\u52a0\u89e3\u91ca\uff0c\u4e5f\u6ca1\u6709\u5fc5\u8981\u89e3\u91ca\uff0c\u518d\u8fc7\u4e09\u56db\u4e2a\u6708\uff0c\u53f6\u5efa\u658c\u4e5f\u80fd\u770b\u5230\u5176\u4e2d\u7684\u53d8\u5316\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u53c8\u4e0d\u60f3\u62c9\u76db\u946b\u6295\u8d44\u7231\u8fbe\u7535\u5b50\uff0c\u63cf\u8ff0\u5e02\u573a\u524d\u666f\u7684\u70ed\u60c5\u81ea\u7136\u5c31\u6bd4\u4e0d\u4e0a\u5bf9\u5f85\u9648\u4fe1\u751f\u65f6\u90a3\u4e48\u9ad8\uff0c\u5230\u4e86\u996d\u65f6\uff0c\u5c31\u62c9\u53f6\u5efa\u658c\u5230\u76ca\u9686\u9547\u8457\u540d\u7684\u5c0f\u6c5f\u9c7c\u9986\u5403\u5c0f\u6c5f\u7279\u8272\u6c34\u4ea7\u3002</p></article>"}}}]);
