(window.webpackJsonp=window.webpackJsonp||[]).push([[268],{291:function(p){p.exports={chapterTitle:null,title:"\u7b2c152\u7ae0 \u62c6\u8fc1\u4e4b\u75db",content:"<article><h2>\u7b2c152\u7ae0 \u62c6\u8fc1\u4e4b\u75db</h2><p>\u3000\u3000\u6574\u4e2a\u4e5d\u5341\u5e74\u4ee3\uff0c\u5bf9\u5e02\u6c11\u5229\u76ca\u4fb5\u72af\u548c\u5265\u593a\u6bd4\u8f83\u4e25\u91cd\u7684\u8fd8\u6709\u57ce\u5e02\u91ce\u86ee\u62c6\u8fc1\u95ee\u9898\u3002</p><p>\u3000\u3000\u57ce\u5e02\u6539\u9020\u62c6\u8fc1\u5371\u623f\u672c\u662f\u597d\u4e8b\uff0c\u6574\u4e2a\u516b\u5341\u5e74\u4ee3\u65e7\u57ce\u6539\u9020\u7684\u6307\u5bfc\u601d\u60f3\u662f\u201c\u539f\u62c6\u539f\u5efa\u3001\u623f\u5c4b\u8fd8\u8fc1\u201d\uff0c\u88ab\u62c6\u8005\u8fc1\u540e\u5fc5\u7ed9\u5176\u5c4b\uff0c\u800c\u4e14\u4f4f\u623f\u6761\u4ef6\u5f97\u5230\u6539\u5584\u3002</p><p>\u3000\u3000\u4e5d\u5341\u5e74\u4ee3\u201c\u4e0a\u6d77\u6a21\u5f0f\u201d\u88ab\u5168\u56fd\u8303\u56f4\u5185\u501f\u9274\uff0c\u4e0a\u6d77\u6539\u9020\u6d66\u897f\u65e7\u57ce\u533a\uff0c\u91c7\u7528\u5168\u62c6\u9664\u5f02\u5730\u65b0\u5efa\u7684\u65b9\u5f0f\uff0c\u5c06\u57ce\u4e2d\u5fc3\u7684\u65e7\u623f\u62c6\u9664\uff0c\u5efa\u9020\u9ad8\u6863\u5199\u5b57\u697c\u3001\u5546\u4e1a\u5730\u4ea7\uff0c\u5728\u57ce\u4e2d\u5fc3\u4ee5\u5916\u5efa\u9020\u65b0\u7684\u5c45\u4f4f\u793e\u533a\u5b89\u7f6e\u88ab\u62c6\u8fc1\u5e02\u6c11\u3002</p><p>\u3000\u3000\u4e0a\u6d77\u6a21\u5f0f\u5c5e\u4e8e\u201c\u5efa\u65b0\u57ce\u201d\u6a21\u5f0f\uff0c\u5f53\u65f6\u56fd\u5bb6\u63d0\u5021\u8fd9\u79cd\u6a21\u5f0f\uff0c\u5404\u5730\u4e5f\u5c31\u7eb7\u7eb7\u501f\u7528\u8fd9\u79cd\u6a21\u5f0f\uff0c\u975e\u5e38\u65b9\u4fbf\u901a\u8fc7\u56fd\u5bb6\u7684\u5ba1\u6279\u3002\u4f46\u5728\u6574\u4e2a\u4e5d\u5341\u5e74\u4ee3\uff0c\u6ca1\u6709\u54ea\u5ea7\u57ce\u5e02\u6709\u4e0a\u6d77\u7684\u8d22\u653f\u5b9e\u529b\uff0c\u5728\u62c6\u9664\u65e7\u57ce\u4e4b\u540e\uff0c\u8fd8\u6709\u80fd\u529b\u5efa\u8bbe\u4e00\u5ea7\u65b0\u57ce\u6765\u5b89\u7f6e\u88ab\u62c6\u8fc1\u5e02\u6c11\uff0c\u5f80\u5f80\u662f\u201c\u62c6\u800c\u4e0d\u5efa\u201d\uff0c\u6539\u201c\u623f\u5c4b\u8fd8\u8fc1\u201d\u7684\u826f\u6027\u6a21\u5f0f\u4e3a\u201c\u8d27\u5e01\u62c6\u8fc1\u201d\u3002</p><p>\u3000\u3000\u81ea\u4ece\u4e5d\u5341\u5e74\u4ee3\u201c\u8d27\u5e01\u62c6\u8fc1\u201d\u4f5c\u4e3a\u201c\u597d\u7ecf\u9a8c\u201d\u63a8\u5e7f\u53d6\u4ee3\u201c\u623f\u5c4b\u8fd8\u8fc1\u201d\u540e\uff0c\u5e02\u6c11\u5c31\u5bb3\u6015\u8d77\u62c6\u8fc1\u6765\u4e86\u3002\u56e0\u4e3a\u62c6\u8fc1\u7ed9\u7684\u90a3\u70b9\u94b1\u4e0e\u623f\u4ef7\u4e4b\u6bd4\u76f8\u53bb\u751a\u8fdc\u3002\u7279\u522b\u662f\u6709\u4e9b\u57ce\u5e02\u7684\u9886\u5bfc\u60f3\u201c\u62db\u5546\u5f15\u8d44\u201d\u90fd\u5feb\u60f3\u75af\u4e86\uff0c\u90a3\u4e9b\u5546\u4eba\u548c\u8d44\u672c\u5bb6\u5728\u4ed6\u4eec\u773c\u91cc\u6210\u4e86\u80fd\u4f7f\u81ea\u5df1\u83b7\u53d6\u653f\u7ee9\u548c\u79c1\u5229\u7684\u6551\u661f\u548c\u8d22\u795e\uff0c\u6709\u7684\u9886\u5bfc\u751a\u81f3\u4ee5\u5a01\u80c1\u7684\u53e3\u6c14\u5bf9\u4e0b\u9762\u8bf4\u201c\u8c01\u5413\u8dd1\u4e00\u4e2a\u5916\u5546\u6211\u62ff\u8c01\u662f\u95ee\u201d\u3001\u201c\u8c01\u8ddf\u653f\u5e9c\u8fc7\u4e0d\u53bb\u653f\u5e9c\u5c31\u8ddf\u8c01\u8fc7\u4e0d\u53bb\u201d\u7b49\u7b49\u3002\u56e0\u6b64\u53ea\u8981\u524d\u8005\u770b\u4e2d\u4e00\u5757\u5730\u65b9\uff0c\u540e\u8005\u5c31\u5145\u5f53\u9a6c\u524d\u5352\u2014\u2014\u8fd0\u7528\u624b\u4e2d\u7684\u6743\u529b\u5f3a\u8feb\u8be5\u5730\u65b9\u7684\u5c45\u4f4f\u8005\u62c6\u8fc1\u3002\u5341\u51e0\u5e74\u540e\u62c6\u8fc1\u516c\u53f8\u591a\u4e3a\u79c1\u6709\uff0c\u62c6\u8fc1\u60c5\u51b5\u5f97\u5230\u6539\u5584\uff0c\u4e5d\u5341\u5e74\u4ee3\u7684\u62c6\u8fc1\u516c\u53f8\u7686\u4e3a\u56fd\u6709\uff0c\u7686\u4e3a\u56fd\u6709\u7684\u610f\u601d\uff0c\u5c31\u662f\u53ef\u4ee5\u65e0\u9650\u5236\u7684\u91c7\u53d6\u5927\u91cf\u7684\u884c\u653f\u624b\u6bb5\u5f3a\u5236\u62c6\u9664\uff0c\u8fd9\u66f4\u4f7f\u5e02\u6c11\u96ea\u4e0a\u52a0\u971c\uff0c\u4ed6\u4eec\u4e2d\u7684\u4e00\u4e9b\u4eba\u539f\u6765\u5c1a\u6709\u964b\u5ba4\u53ef\u5c45\uff0c\u62c6\u8fc1\u540e\u53cd\u800c\u4e0a\u65e0\u7247\u74e6\u3002\u91ce\u86ee\u62c6\u8fc1\u4f7f\u623f\u5730\u4ea7\u5546\u7b11\u9010\u989c\u5f00\uff0c\u88ab\u62c6\u8fc1\u8005\u53eb\u82e6\u4e0d\u8fed\u3002\u5373\u4f7f\u591a\u6570\u4eba\u62c6\u8fc1\u540e\u4e70\u4e86\u623f\u4e5f\u662f\u88ab\u903c\u65e0\u5948\uff0c\u503a\u53f0\u9ad8\u7b51\uff0c\u8fc7\u7740\u4f4f\u9ad8\u697c\u559d\u7a00\u7ca5\u7684\u65e5\u5b50\uff0c\u800c\u4e00\u4e9b\u9886\u5bfc\u5374\u4ee5\u6b64\u4f5c\u4e3a\u653f\u7ee9\u81ea\u6211\u5439\u5618\u7c89\u9970\u592a\u5e73\u3002</p><p>\u3000\u3000\u6c99\u7530\u65e7\u57ce\u5927\u6a21\u8303\u62c6\u8fc1\u5df2\u7ecf\u4e0d\u53ef\u907f\u514d\uff0c\u5e02\u91cc\u4e5f\u4e0d\u53ef\u80fd\u5728\u6c99\u7530\u8fd9\u5757\u5546\u4e1a\u5b9d\u5730\u5efa\u9020\u4f4f\u5b85\u793e\u533a\u6765\u5b89\u7f6e\u88ab\u62c6\u8fc1\u5e02\u6c11\uff0c\u4e5f\u6ca1\u6709\u5728\u5176\u4ed6\u5730\u65b9\u5efa\u8bbe\u65b0\u793e\u533a\u5b89\u7f6e\u88ab\u62c6\u8fc1\u5e02\u6c11\u7684\u8ba1\u5212\uff0c\u6700\u7ec8\u53ea\u53ef\u80fd\u91c7\u53d6\u8d27\u5e01\u62c6\u8fc1\u7684\u6a21\u5f0f\uff0c\u800c\u6d77\u5dde\u5728\u4e8c\u5343\u5e74\u4e4b\u524d\u7684\u623f\u5730\u4ea7\u5e02\u573a\u6839\u672c\u5c31\u5bb9\u7eb3\u4e0d\u4e86\u51e0\u4e07\u6237\u4e4b\u5de8\u7684\u62c6\u8fc1\u89c4\u6a21\uff08\u4e3b\u8981\u662f\u62c6\u8fc1\u8d39\u7528\u5f88\u4f4e\uff0c\u800c\u4e5d\u5341\u5e74\u4ee3\u88ab\u62c6\u8fc1\u5e02\u6c11\u7684\u7ecf\u6d4e\u6761\u4ef6\u666e\u904d\u8f83\u5dee\uff0c\u4e0d\u8db3\u4ee5\u5f62\u6210\u5927\u7684\u623f\u5730\u4ea7\u5e02\u573a\uff0c\u90a3\u65f6\u8fd8\u6ca1\u6709\u623f\u8d37\u4e00\u8bf4\uff09\uff0c\u4e3a\u4e86\u7f13\u89e3\u77db\u76fe\uff0c\u62c6\u8fc1\u516c\u53f8\u7eb7\u7eb7\u5728\u8fdc\u79bb\u5e02\u533a\u7684\u57ce\u4e61\u7ed3\u5408\u533a\u5efa\u9020\u5927\u91cf\u8d28\u91cf\u4f4e\u52a3\u7684\u65e0\u4ea7\u6743\u7684\u5b89\u7f6e\u623f\uff0c\u5c06\u65e0\u529b\u5728\u5e02\u533a\u8d2d\u623f\u7684\u5e02\u6c11\u5927\u90e8\u5206\u8d76\u5230\u90a3\u91cc\u53bb\u3002</p><p>\u3000\u3000\u5510\u5b66\u8c26\u4e5f\u4f1a\u63a8\u6321\u4e0d\u4f4f\u5efa\u8bbe\u65b0\u57ce\u7684\u8bf1\u60d1\uff0c\u4e5d\u5341\u5e74\u4ee3\uff0c\u5b98\u5458\u7684\u653f\u7ee9\u5f88\u5c11\u4e0e\u5e02\u6c11\u5229\u76ca\u5212\u4e0a\u7b49\u53f7\uff0c\u5373\u4f7f\u662f\u5510\u5b66\u8c26\u4e5f\u4e0d\u4f8b\u5916\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e00\u5411\u8ba4\u4e3a\u57ce\u5e02\u5efa\u8bbe\u5c31\u6ca1\u6709\u201c\u727a\u7272\u5c11\u90e8\u5206\u4eba\u5229\u76ca\u662f\u53ef\u4ee5\u7684\u201d\u8bf4\u6cd5\uff0c\u4f46\u662f\u5229\u76ca\u727a\u7272\u4e5f\u8f6e\u4e0d\u5230\u56fd\u5bb6\u516c\u52a1\u4eba\u5458\u7684\u5934\u4e0a\uff0c\u6240\u4ee5\u201c\u4e3a\u4e86\u5927\u5c40\u727a\u7272\u5c11\u90e8\u5206\u5229\u76ca\u662f\u53ef\u4ee5\u7684\u201d\u60f3\u6cd5\u5728\u5e02\u91cc\u5f88\u5360\u4e0a\u98ce\uff0c\u4ed6\u4eec\u6c38\u8fdc\u90fd\u611f\u89c9\u4e0d\u5230\u5207\u80a4\u4e4b\u75db\u3002</p><p>\u3000\u3000\u5f20\u606a\u5750\u5728\u5510\u5b66\u8c26\u7684\u5bf9\u9762\uff0c\u5fc3\u60f3\u5510\u5b66\u8c26\u518d\u4e5f\u4e0d\u53ef\u80fd\u611f\u53d7\u5230\u90a3\u79cd\u88ab\u9a71\u9010\u7684\u75db\u82e6\u5427\uff01\u5f20\u606a\u5012\u662f\u6709\u5207\u80a4\u4e4b\u75db\uff0c\u7236\u4eb2\u8f9e\u53bb\u516c\u804c\u4e4b\u540e\u56de\u5230\u5e08\u9662\uff0c\u5e08\u9662\u6ca1\u6709\u7ed9\u518d\u5b89\u6392\u4f4f\u6240\uff0c\u90a3\u65f6\u8fd8\u6709\u4e00\u4e9b\u79ef\u84c4\uff0c\u5c31\u5728\u6c99\u7530\u4e70\u4e0b\u4e00\u6237\u79c1\u623f\uff0c\u4e5d\u4e03\u5e74\u8d76\u4e0a\u65e7\u57ce\u6539\u9020\uff0c\u5e78\u597d\u5c0f\u53d4\u63a5\u6d4e\uff0c\u82e5\u4e0d\u662f\u5982\u6b64\uff0c\u518d\u8d76\u4e0a\u7236\u4eb2\u7ed9\u9a71\u9010\u51fa\u5e08\u9662\uff0c\u8fd8\u4e0d\u6653\u5f97\u5bb6\u91cc\u4f1a\u600e\u4e48\u7684\u60e8\u72b6\u3002</p><p>\u3000\u3000\u6bd5\u7adf\u5728\u6c99\u7530\u751f\u6d3b\u4e86\u4e24\u5e74\uff0c\u867d\u7136\u77ed\u6682\uff0c\u4f46\u662f\u5bf9\u5468\u56f4\u88ab\u62c6\u8fc1\u8005\u7684\u72b6\u51b5\u90fd\u6709\u4e86\u89e3\uff0c\u7279\u522b\u52a0\u4e0a\u5f53\u65f6\u6b63\u641e\u201c\u56fd\u9000\u6c11\u8fdb\u201d\uff0c\u5927\u91cf\u56fd\u6709\u4f01\u4e1a\u804c\u5de5\u4e0b\u5c97\uff0c\u4e00\u90e8\u5206\u5bb6\u5ead\u4e24\u96be\u76f8\u9022\u4f55\u5176\u51c4\u60e8\uff0c\u4ed6\u4eec\u60f3\u5229\u7528\u7269\u6743\u6cd5\u4fdd\u62a4\u81ea\u5df1\u7684\u6743\u76ca\u8fd8\u8981\u7b49\u4e0a\u5341\u51e0\u5e74\u624d\u884c\u3002</p><p>\u3000\u3000\u8fd9\u5df2\u4e0d\u4ec5\u4ec5\u662f\u65e7\u57ce\u4fdd\u62a4\u7684\u95ee\u9898\uff0c\u65e2\u7136\u6d77\u5dde\u57ce\u5efa\u8bbe\u5f97\u518d\u6f02\u4eae\uff0c\u4e5f\u4e0d\u80fd\u4ee5\u4e0a\u4e07\u6237\u5e02\u6c11\u6240\u627f\u53d7\u7684\u62c6\u8fc1\u4e4b\u75db\u4e3a\u4ee3\u4ef7\uff0c\u5510\u5b66\u8c26\u65e2\u7136\u8981\u8bd5\uff0c\u5f20\u606a\u5012\u662f\u60f3\u7740\u62d6\u4e00\u62d6\u4ed6\u7684\u540e\u817f\u3002</p><p>\u3000\u3000\u5510\u5b66\u8c26\u4e0d\u6e05\u695a\u5f20\u606a\u5fc3\u91cc\u5230\u5e95\u5728\u6253\u4ec0\u4e48\u4e3b\u610f\uff0c\u5f20\u606a\u7684\u8bdd\u8ba9\u4ed6\u9887\u4e3a\u5fc3\u52a8\u3002</p><p>\u3000\u3000\u56db\u51e4\u6865\u996e\u98df\u5e7f\u573a\u7684\u6210\u529f\uff0c\u4ee4\u524d\u95e8\u7684\u7e41\u8363\u5ef6\u4f38\u5230\u56db\u51e4\u6865\u4ee5\u524d\uff0c\u7279\u522b\u662f\u56db\u51e4\u6865\u996e\u98df\u5e7f\u573a\u9ad8\u989d\u7684\u5730\u79df\u5f15\u8d77\u5e02\u91cc\u6781\u5927\u7684\u5173\u6ce8\uff0c\u6709\u4ef7\u503c\u7684\u571f\u5730\u5e76\u4e0d\u4ec5\u4ec5\u5c40\u9650\u5728\u524d\u95e8\u4e4b\u5185\uff0c\u4e5f\u8ba9\u5510\u5b66\u8c26\u770b\u5230\u5feb\u901f\u63a8\u52a8\u6c99\u7530\u65e7\u57ce\u5feb\u901f\u6539\u9020\u7684\u5e0c\u671b\u3002</p><p>\u3000\u3000\u5373\u4f7f\u5982\u5510\u5b66\u8c26\u8fd9\u6837\u7684\u777f\u667a\uff0c\u4e5f\u65e0\u6cd5\u60f3\u8c61\u60c5\u51b5\u7684\u590d\u6742\u3002</p><p>\u3000\u3000\u4e5d\u5341\u5e74\u4ee3\u6d77\u5dde\u5e02\u7684\u62c6\u8fc1\u516c\u53f8\u90fd\u4e3a\u56fd\u6709\uff0c\u5f20\u606a\u8fd8\u8bb0\u5f97\u6709\u4e00\u5bb6\u62c6\u8fc1\u516c\u53f8\u7684\u4e3b\u7ba1\u5355\u4f4d\u7adf\u7136\u662f\u5e02\u6587\u7269\u5c40\uff0c\u4e5f\u5c31\u662f\u8fd9\u5bb6\u62c6\u8fc1\u516c\u53f8\u6beb\u4e0d\u624b\u8f6f\u7684\u62c6\u9664\u5927\u91cf\u539f\u4e3a\u5e02\u7ea7\u6587\u7269\u4fdd\u62a4\u5355\u4f4d\u7684\u5efa\u7b51\u3002\u5e02\u91cc\u62e8\u7ed9\u88ab\u62c6\u8fc1\u8005\u7684\u62c6\u8fc1\u8d39\u7528\u672c\u6765\u5c31\u4e0d\u8db3\uff0c\u4f46\u662f\u5374\u5927\u91cf\u7684\u7ed9\u8fd9\u4e9b\u56fd\u6709\u62c6\u8fc1\u516c\u53f8\u6240\u622a\u7559\u3002\u5f53\u65f6\u56fd\u6709\u62c6\u8fc1\u516c\u53f8\u7684\u5229\u6da6\u662f\u73b0\u5728\u79c1\u8425\u62c6\u8fc1\u516c\u53f8\u6240\u671b\u5c18\u83ab\u53ca\u7684\u3002</p><p>\u3000\u3000\u5de8\u989d\u7684\u5229\u6da6\u622a\u7559\u5728\u56fd\u6709\u62c6\u8fc1\u516c\u53f8\u91cc\uff0c\u968f\u540e\u53c8\u901a\u8fc7\u540d\u76ee\u7e41\u591a\u7684\u6295\u8d44\u624b\u6bb5\uff0c\u88ab\u8f6c\u79fb\u5230\u5176\u4ed6\u5730\u65b9\u3002</p><p>\u3000\u3000\u5510\u5b66\u8c26\u80fd\u4e00\u4e0b\u5b50\u5c31\u6253\u7834\u6574\u4e2a\u6d77\u5dde\u5e02\u5b98\u50da\u4f53\u7cfb\u7684\u4f20\u7edf\u6539\u53d8\u8fd9\u79cd\u4e8b\u5b9e\u5417\uff1f</p><p>\u3000\u3000\u5f20\u606a\u5fc3\u60f3\uff1a\u591f\u6b20\uff01</p><p>\u3000\u3000\u5173\u4e8e\u8fd9\u4e00\u70b9\uff0c\u8bb8\u9e3f\u4f2f\u6df1\u5904\u6c11\u95f4\u5c31\u6709\u76f8\u5f53\u6df1\u523b\u7684\u8ba4\u8bc6\uff0c\u5f53\u7136\uff0c\u5c31\u51ed\u7740\u8fd9\u70b9\uff0c\u60f3\u529d\u963b\u5510\u5b66\u8c26\u6682\u65f6\u653e\u7f13\u5bf9\u6c99\u7530\u65e7\u57ce\u7684\u6539\u9020\uff0c\u4e5f\u4e0d\u53ef\u80fd\uff0c\u6bd5\u7adf\u4e0d\u6b62\u5510\u5b66\u8c26\uff0c\u5e02\u91cc\u5176\u4ed6\u4eba\u4e5f\u53ea\u770b\u5230\u65e7\u57ce\u6539\u9020\u7684\u597d\u5904\uff0c\u800c\u770b\u4e0d\u5230\u5f0a\u7aef\uff0c\u66f4\u4e0d\u4f1a\u627f\u8ba4\u5e02\u91cc\u6682\u65f6\u8fd8\u6ca1\u6709\u80fd\u529b\u963b\u6b62\u8fd9\u4e9b\u5f0a\u7aef\u53d1\u751f\u3002</p><p>\u3000\u3000\u5f20\u606a\u5c06\u5510\u5b66\u8c26\u7684\u601d\u8def\u5f80\u5546\u4e1a\u8d44\u672c\u4e0a\u5f15\uff0c\u53ef\u8c13\u7528\u5fc3\u9669\u6076\uff0c\u53ea\u8981\u8bb8\u9e3f\u4f2f\u4e0d\u6345\u7834\uff0c\u5510\u5b66\u8c26\u65e5\u540e\u4e0d\u53ef\u80fd\u770b\u51fa\u4ec0\u4e48\u6765\uff0c\u6bd5\u7adf\u8c01\u4e5f\u4e0d\u4f1a\u60f3\u5230\u5f20\u606a\u80fd\u6e05\u695a\u7684\u9884\u6d4b\u5230\u4eca\u540e\u51e0\u5e74\u7684\u53d1\u5c55\u8109\u7edc\u3002</p><p>\u3000\u3000\u518d\u8bf4\u5f20\u606a\u8fd9\u4e48\u505a\uff0c\u662f\u7ed9\u5510\u5b66\u8c26\u4ed6\u65e5\u540e\u6709\u8865\u6551\u7684\u673a\u4f1a\uff0c\u4e0d\u81f3\u4e8e\u62b1\u61be\u7ec8\u8eab\u3002</p><p>\u3000\u3000\u4e0d\u7ba1\u51fa\u4e8e\u600e\u4e48\u7684\u8003\u8651\uff0c\u56db\u51e4\u6865\u7684\u6210\u529f\uff0c\u5f88\u5bb9\u6613\u5c31\u5c06\u5e02\u91cc\u7684\u601d\u8def\u5f15\u5165\u5f20\u606a\u7ed9\u4ed6\u4eec\u8bbe\u7f6e\u7684\u9677\u9631\u91cc\u3002\u5e02\u8d22\u653f\u7f3a\u94b1\uff0c\u4f46\u662f\u4e2d\u5fc3\u533a\u57df\u7684\u571f\u5730\u4ef7\u503c\u8ba9\u4eba\u773c\u5192\u91d1\u5149\uff0c\u6c99\u7530\u65e7\u57ce\u6539\u9020\u7684\u4f20\u8a00\u98ce\u6d8c\uff0c\u6b63\u8fce\u5408\u4ed6\u4eec\u8fd9\u4e9b\u4eba\u7684\u5fc3\u601d\uff0c\u5c06\u6574\u4e2a\u6c99\u7530\u90fd\u62ff\u51fa\u6765\u6539\u9020\uff0c\u5c06\u80fd\u5957\u53d6\u591a\u5c11\u8d44\u91d1\uff1f</p><p>\u3000\u3000\u65e9\u5728\u4e09\u6708\u95f4\uff0c\u5e02\u5e38\u59d4\u4f1a\u8bae\u5c31\u901a\u8fc7\u4e00\u4efd\u51b3\u8bae\uff0c\u505c\u6b62\u65e0\u507f\u5212\u62e8\u571f\u5730\u7684\u884c\u653f\u884c\u4e3a\uff0c\u5728\u56db\u57ce\u533a\u5927\u5b97\u571f\u5730\u7684\u5212\u62e8\u9700\u8981\u901a\u8fc7\u5e38\u59d4\u4f1a\u8bae\u3002\u8fd9\u91cc\u9762\u6709\u5468\u5bcc\u660e\u52a0\u5f3a\u63a7\u5236\u6743\u7684\u610f\u56fe\uff0c\u5374\u4e5f\u662f\u571f\u5730\u4ef7\u503c\u5f97\u5230\u91cd\u89c6\u7684\u4f53\u73b0\u3002</p><p>\u3000\u3000\u5c31\u7b97\u5510\u5b66\u8c26\u60f3\u653e\u7f13\u811a\u6b65\u90fd\u4e0d\u53ef\u80fd\uff0c\u5e02\u91cc\u751a\u81f3\u51fa\u73b0\u4e00\u6b21\u6027\u5c06\u6c99\u7530\u62c6\u9664\u7684\u58f0\u97f3\uff0c\u800c\u4e14\u8fd9\u79cd\u58f0\u97f3\u7684\u652f\u6301\u529b\u5ea6\u4e0d\u5c0f\uff0c\u5c31\u4e0e\u5e74\u524d\u542f\u52a8\u758f\u6e2f\u6cb3\u758f\u6d5a\u5de5\u7a0b\u7684\u547c\u58f0\u4e00\u6837\uff0c\u5176\u4e2d\u6d89\u53ca\u5230\u65b9\u65b9\u9762\u9762\u7684\u5229\u76ca\u5173\u7cfb\u3002</p><p>\u3000\u3000\u5728\u8fd9\u79cd\u80cc\u666f\u4e0b\uff0c\u5f20\u606a\u7684\u63d0\u8bae\u5728\u5510\u5b66\u8c26\u770b\u6765\u5374\u6210\u4e86\u7a33\u59a5\u7684\u65b9\u6cd5\uff0c\u867d\u7136\u4ed6\u66f4\u5e0c\u671b\u4ece\u4e34\u8fd1\u6c99\u7530\u7684\u533a\u57df\u9010\u6e10\u7684\u5411\u4e1c\u63a8\u8fdb\uff0c\u4f46\u662f\u6709\u65f6\u5019\u5e02\u957f\u7684\u610f\u89c1\u4e5f\u662f\u5f31\u52bf\u7684\u3002</p><p>\u3000\u3000\u505a\u51fa\u6c99\u7530\u6574\u4f53\u62c6\u9664\u7684\u89c4\u5212\uff0c\u5c06\u6c99\u7530\u897f\u533a\u5217\u5165\u7b2c\u4e00\u6279\u6574\u4f53\u62c6\u9664\u7684\u8ba1\u5212\uff0c\u5728\u6c99\u7530\u8857\u5e02\u5148\u62ff\u4e00\u5757\u5730\u51fa\u6765\u8bd5\u63a2\u5e02\u573a\u7684\u70ed\u5ea6\u3002</p><p>\u3000\u3000\u5c31\u662f\u8fd9\u5757\u5730\u63a8\u51fa\u7684\u65b9\u5f0f\uff0c\u5e02\u91cc\u5c31\u5f04\u51fa\u5f88\u591a\u610f\u89c1\u3002</p><p>\u3000\u3000\u5510\u5b66\u8c26\u5e0c\u671b\u4eff\u7167\u4e00\u4e9b\u57ce\u5e02\u641e\u62db\u6807\u767d\u5356\u6302\u724c\u51fa\u8ba9\u7684\u65b0\u6a21\u5f0f\uff0c\u6700\u7ec8\u5e02\u5e38\u59d4\u5f62\u6210\u7684\u610f\u89c1\u662f\u91c7\u7eb3\u4f20\u7edf\u7684\u5212\u62e8\u65b9\u5f0f\uff0c\u53ef\u4ee5\u5185\u90e8\u6bd4\u4ef7\uff0c\u6700\u7ec8\u7531\u5e02\u5e38\u59d4\u4f1a\u8bae\u51b3\u5b9a\u3002\u8fd9\u4e0d\u8fc7\u662f\u5229\u76ca\u5173\u8054\u8005\u89d2\u529b\u7684\u7ed3\u679c\uff0c\u4e5f\u4e0d\u51fa\u4e4e\u5f20\u606a\u7684\u610f\u6599\u3002</p><p>\u3000\u3000\u56db\u51e4\u6865\u7684\u72c2\u70ed\uff0c\u8ba9\u5f88\u591a\u4eba\u610f\u8bc6\u4e0d\u5230\u6c99\u7530\u9999\u9975\u7684\u98ce\u9669\uff0c\u4e5f\u6ca1\u6709\u4eba\u4f1a\u8ba4\u771f\u8003\u8651\u6d77\u5dde\u5e02\u7684\u5546\u4e1a\u8d44\u672c\u89c4\u6a21\u6709\u6ca1\u6709\u80fd\u529b\u4e00\u6b21\u6027\u5b8c\u6574\u6d88\u5316\u6c99\u7530\u897f\u7247\u4e00\u767e\u516c\u9877\u7684\u5546\u4e1a\u5730\u6bb5\u3002</p><p>\u3000\u3000\u5305\u62ec\u5f20\u77e5\u975e\u7684\u5b8f\u8fdc\u516c\u53f8\u3001\u90b5\u81f3\u521a\u7b49\u90fd\u8822\u8822\u6b32\u52a8\uff0c\u4ed6\u4eec\u90fd\u6765\u627e\u5f20\u606a\u8ba8\u8bba\u8fd9\u4e8b\uff0c\u5f20\u606a\u4e0d\u52a8\u58f0\u8272\uff0c\u51ed\u501f\u4ed6\u4eec\u7684\u5b9e\u529b\uff0c\u4e5f\u4e0d\u80fd\u8ba9\u5e02\u91cc\u653e\u5fc3\u5c06\u90a3\u5757\u5730\u4ea4\u7ed9\u4ed6\u4eec\u505a\u8bd5\u9a8c\u7530\uff0c\u5305\u62ec\u540e\u671f\u7684\u5f00\u53d1\uff0c\u5e02\u91cc\u66f4\u5e0c\u671b\u5f15\u8fdb\u5916\u9762\u7684\u8d44\u672c\u3002</p><p>\u3000\u3000\u603b\u4e4b\uff0c\u4ed6\u4eec\u8981\u52aa\u529b\u4e5f\u5c31\u968f\u4ed6\u4eec\u53bb\u4e86\uff0c\u5fc3\u52a8\u7684\u53c8\u4e0d\u662f\u4ed6\u4eec\u4e24\u5bb6\uff0c\u4e0d\u8fc7\u884c\u52a8\u6700\u8fc5\u901f\u7684\u8fd8\u662f\u9526\u57ce\u96c6\u56e2\uff0c\u9526\u57ce\u96c6\u56e2\u91c7\u53d6\u5728\u5f53\u65f6\u770b\u6765\u662f\u6700\u5408\u9002\u7684\u7b56\u7565\uff0c\u4ed6\u4eec\u627e\u6765\u8ddf\u9526\u57ce\u96c6\u56e2\u4e00\u76f4\u6709\u5408\u4f5c\u5173\u7cfb\u7684\u65b0\u52a0\u5761\u65b0\u57ce\u5730\u4ea7\u96c6\u56e2\uff0c\u4e24\u5bb6\u516c\u53f8\u8054\u5408\u8d77\u6765\u62ff\u5730\uff0c\u8fd9\u975e\u5e38\u7b26\u5408\u5e02\u91cc\u62db\u5546\u5f15\u8d44\u7684\u601d\u8def\uff0c\u4f46\u662f\u8981\u6c42\u5e02\u91cc\u5728\u571f\u5730\u8f6c\u8ba9\u8d39\u7528\u4e0a\u7ed9\u4e88\u4f18\u60e0\u3002</p><p>\u3000\u3000\u9526\u57ce\u96c6\u56e2\u52a8\u4f5c\u4e4b\u8fc5\u6377\uff0c\u8ba9\u5f20\u606a\u6000\u7591\u4ed6\u4eec\u5f88\u65e9\u5c31\u76ef\u4e0a\u6c99\u7530\u8fd9\u5757\u5730\u65b9\uff0c\u5fc3\u91cc\u4e0d\u7531\u7684\u5728\u60f3\uff1a\u4e07\u5bb6\u4e0e\u5468\u5bcc\u660e\u59a5\u534f\uff0c\u96be\u9053\u662f\u8d75\u9526\u8363\u5728\u4e2d\u95f4\u6413\u548c\u7684\uff1f</p><p>\u3000\u3000\u5f53\u5f20\u606a\u770b\u5230\u9526\u57ce\u4e0e\u65b0\u57ce\u5730\u4ea7\u8054\u5408\u8bbe\u7acb\u7684\u9526\u57ce\u5730\u4ea7\u516c\u53f8\u7684\u5916\u8d44\u6bd4\u4f8b\uff0c\u51e0\u4e4e\u80af\u5b9a\u8fd9\u662f\u4e00\u5b9a\u7684\uff0c\u9526\u57ce\u5730\u4ea7\u53ea\u662f\u6302\u7740\u5408\u8d44\u7684\u540d\u5934\uff0c\u5b9e\u9645\u4e0a\u662f\u8d75\u9526\u8363\u81ea\u5bb6\u7684\u4e00\u4ea9\u4e09\u5206\u5730\u3002</p></article>"}}}]);
