(window.webpackJsonp=window.webpackJsonp||[]).push([[269],{292:function(p){p.exports={chapterTitle:null,title:"\u7b2c269\u7ae0 \u7537\u5973\u4e09\u91cd\u5883\u754c",content:"<article><h2>\u7b2c269\u7ae0 \u7537\u5973\u4e09\u91cd\u5883\u754c</h2><p>\u3000\u3000\u201c\u665a\u6674\u59d0\u3002\u201d\u5f20\u606a\u653e\u4e0b\u665a\u6674\u7684\u7ea4\u7ea4\u767d\u8db3\uff0c\u5f80\u524d\u5750\u4e86\u5750\uff0c\u60f3\u6273\u8fc7\u5979\u7684\u80a9\u8180\u3002\u665a\u6674\u6ca1\u6709\u52c7\u6c14\u9762\u5bf9\u5f20\u606a\uff0c\u624b\u6342\u7740\u8138\uff0c\u6cea\u6c34\u4ece\u773c\u89d2\u6ea2\u51fa\u6765\uff0c\u6ca1\u6709\u60b2\u4f24\uff0c\u4e5f\u6ca1\u6709\u7ed9\u5f20\u606a\u620f\u5f04\u7684\u7f9e\u6127\uff0c\u53ea\u662f\u4e00\u79cd\u6de1\u6de1\u7684\u60c5\u7eea\u8ba9\u5979\u60c5\u4e0d\u81ea\u7981\u7684\u843d\u6cea\u3002</p><p>\u3000\u3000\u201c\u66fe\u770b\u8fc7\u4e00\u7bc7\u6587\u7ae0\uff0c\u8bf4\u7537\u4eba\u4e0e\u5973\u4eba\u4e4b\u95f4\u7684\u5173\u7cfb\u6709\u4e09\u91cd\u5883\u754c\u2026\u2026\u201d\u5f20\u606a\u8ba9\u665a\u6674\u9760\u5728\u81ea\u5df1\u7684\u6000\u91cc\uff0c\u611f\u89c9\u5979\u8eab\u4f53\u7ec6\u5fae\u7684\u98a4\u6296\u542c\u5230\u8fd9\u53e5\u8bdd\u5c31\u505c\u6b62\u4e86\uff0c\u201c\u2018\u84b9\u846d\u82cd\u82cd\uff0c\u767d\u9732\u4e3a\u971c\uff0c\u6240\u8c13\u4f0a\u4eba\uff0c\u5728\u6c34\u4e00\u65b9\u2019\uff0c\u7b2c\u4e00\u91cd\u5883\u754c\uff0c\u2018\u548c\u7f9e\u8d70\uff0c\u501a\u95e8\u56de\u9996\uff0c\u5374\u628a\u9752\u6885\u55c5\u2019\uff0c\u8fd9\u53e5\u5b8b\u8bcd\u8bf4\u7684\u662f\u7b2c\u4e8c\u91cd\u5883\u754c\uff1b\u2018\u62db\u4e4b\u5373\u6765\uff0c\u6325\u4e4b\u5373\u53bb\u2019\uff0c\u8bf4\u7684\u662f\u7b2c\u4e09\u91cd\u5883\u754c\u3002\u5728\u6211\u770b\u6765\uff0c\u90a3\u53ea\u662f\u4e00\u4e2a\u82b1\u82b1\u516c\u5b50\u6ce1\u599e\u5883\u754c\u7684\u8bf4\u6cd5\uff0c\u65e0\u8bba\u662f\u7537\u4eba\u5bf9\u5973\u4eba\u62db\u4e4b\u5373\u6765\uff0c\u8fd8\u662f\u5973\u4eba\u5bf9\u7537\u4eba\u6325\u4e4b\u5373\u53bb\uff0c\u75f4\u75f4\u7f20\u7f20\u6069\u6069\u6028\u6028\uff0c\u90fd\u4e0d\u4f1a\u6709\u5c0a\u91cd\u3001\u76f8\u77e5\u4e0e\u4fe1\u4efb\uff1b\u501a\u95e8\u56de\u9996\u7684\u8bf1\u60d1\uff0c\u4e5f\u4e0d\u53ca\u5076\u5c14\u7684\u56de\u7738\u66f4\u52a8\u4eba\uff1b\u6240\u8c13\u4f0a\u4eba\u662f\u7537\u4eba\u7684\u53e6\u4e00\u4e2a\u7075\u9b42\uff0c\u5979\u65f6\u800c\u8fd1\u5728\u54ab\u5c3a\uff0c\u65f6\u800c\u5728\u6c34\u4e00\u65b9\uff0c\u4f46\u662f\u5979\u7684\u786e\u5728\u751f\u547d\u91cc\u5b58\u5728\u7740\u2026\u2026\u201d</p><p>\u3000\u3000\u665a\u6674\u6014\u6014\u7684\u770b\u7740\u5f20\u606a\uff0c\u4ed6\u6df1\u9083\u7684\u9ed1\u8272\u773c\u7738\u71a0\u71a0\u751f\u8f89\uff0c\u6563\u53d1\u51fa\u8ff7\u4eba\u7684\u5149\u5f69\uff0c\u8fd9\u4e00\u523b\u624d\u6446\u8131\u4ee4\u4eba\u5c34\u5c2c\u7684\u4e16\u4fd7\u8eab\u4efd\uff0c\u5b89\u9759\u7684\u9760\u7740\u5f20\u606a\u7684\u80f8\u53e3\uff0c\u6709\u4e9b\u65e0\u6cd5\u7406\u89e3\u5f20\u606a\u7684\u8bdd\u3002</p><p>\u3000\u3000\u201c\u59bb\u5b50\u3001\u60c5\u4eba\u4e0e\u77e5\u5df1\u2026\u2026\u201d\u5f20\u606a\u5728\u665a\u6674\u8033\u6735\u8f7b\u58f0\u7684\u8bf4\u3002</p><p>\u3000\u3000\u201c\u53ec\u4e4b\u5373\u6765\u3001\u6325\u4e4b\u5373\u53bb\u201d\u663e\u7136\u662f\u6307\u592b\u59bb\u5173\u7cfb\uff1b\u201c\u548c\u7f9e\u8d70\uff0c\u501a\u95e8\u56de\u9053\uff0c\u5374\u628a\u9752\u6885\u55c5\u201d\u7684\u8bf1\u60d1\uff0c\u81ea\u7136\u53ea\u5b58\u5728\u4e8e\u60c5\u4eba\u4e4b\u95f4\uff1b\u53ea\u6709\u771f\u6b63\u7684\u5f02\u6027\u77e5\u5df1\u4e4b\u95f4\u624d\u5b58\u5728\u201c\u6240\u8c13\u4f0a\u4eba\uff0c\u5728\u6c34\u4e00\u65b9\u201d\u7684\u6de1\u7136\u3002</p><p>\u3000\u3000\u201c\u554a\u2026\u2026\u201d\u665a\u6674\u8f7b\u547c\u4e86\u4e00\u58f0\uff0c\u5f20\u606a\u914d\u5408\u81ea\u5df1\u6f14\u8fd9\u51fa\u620f\uff0c\u4e0d\u662f\u620f\u5f04\uff0c\u4e5f\u4e0d\u662f\u523b\u610f\u7684\u8bf1\u60d1\uff0c\u800c\u662f\u8981\u5c06\u4e24\u4eba\u4e4b\u95f4\u65e9\u5c31\u5b58\u5728\u7684\u6b32\u671b\u8bf1\u60d1\u70b9\u7834\uff0c\u65e2\u7136\u70b9\u7834\u4e86\uff0c\u4e24\u4eba\u7684\u5173\u7cfb\u8be5\u600e\u4e48\u8d70\uff0c\u96be\u9053\u53ea\u6709\u505a\u60c5\u4eba\u4e00\u6761\u8def\u53ef\u4ee5\u8d70\u5417\uff1f\u665a\u6674\u5fc3\u91cc\u7684\u60c5\u6b32\u6084\u7136\u6d88\u9000\uff0c\u660e\u4eae\u7684\u773c\u7738\u51dd\u89c6\u7740\u5f20\u606a\uff0c\u8fd9\u4e2a\u5c0f\u7537\u4eba\u7684\u773c\u775b\u6c38\u8fdc\u90fd\u4e0d\u4f1a\u8ba9\u4eba\u770b\u900f\u554a\uff0c\u6323\u624e\u7740\u5750\u8d77\u6765\uff0c\u867d\u7136\u6709\u4e9b\u7f9e\u6da9\uff0c\u4f46\u662f\u6210\u719f\u7684\u5fc3\u667a\u8ba9\u5979\u8bf4\u51fa\u81ea\u5df1\u771f\u5b9e\u7684\u60f3\u6cd5\uff1a\u201c\u6211\u60f3\u4e00\u76f4\u503e\u542c\u7740\u4f60\uff0c\u6ce8\u89c6\u7740\u4f60\uff0c\u4f46\u662f\u505a\u77e5\u5df1\u4f1a\u4e0d\u4f1a\u592a\u8001\u4e86\u4e00\u4e9b\uff1f\u201d</p><p>\u3000\u3000\u201c\u600e\u4e48\u4f1a\uff1f\u201d\u5f20\u606a\u8ba9\u665a\u6674\u9760\u7740\u81ea\u5df1\u7684\u80f8\u53e3\u5750\u7740\uff0c\u63e1\u7740\u5979\u7ea4\u7ec6\u7ef5\u8f6f\u7684\u624b\uff0c\u201c\u65e2\u662f\u7ea2\u989c\u53c8\u662f\u77e5\u5df1\uff0c\u665a\u6674\u59d0\u8fd9\u4e48\u4e00\u4e2a\u65f7\u4e16\u4f73\u4eba\uff0c\u4e0d\u6653\u5f97\u6709\u591a\u8bf1\u4eba\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u600e\u4e48\u80fd\u770b\u5f97\u8fd9\u4e48\u900f\u5f7b\uff1f\u201d\u665a\u6674\u7591\u60d1\u7684\u6602\u5934\u770b\u7740\u5f20\u606a\u4f4e\u5782\u7684\u773c\u5e18\u3002</p><p>\u3000\u3000\u201c\u6211\u60f3\u6211\u7684\u524d\u751f\u4e00\u5b9a\u662f\u4e2a\u5230\u5904\u62db\u82b1\u60f9\u8349\u7684\u82b1\u82b1\u516c\u5b50\uff0c\u8fd9\u8f88\u5b50\u6240\u4ee5\u5728\u7537\u5973\u5173\u7cfb\u65b9\u9762\u591a\u4e86\u4e9b\u609f\u6027\u3002\u201d\u5f20\u606a\u80e1\u8a00\u4e71\u8a00\u7684\u5e94\u4ed8\u665a\u6674\uff0c\u6402\u7740\u5979\u4e30\u8174\u7684\u8eab\u4f53\u7d27\u4e86\u7d27\uff0c\u770b\u7740\u5979\u5a07\u6da6\u7684\u7ea2\u5507\uff0c\u4f4e\u5934\u60f3\u543b\u4e0b\u53bb\u3002</p><p>\u3000\u3000\u201c\u53ef\u4ee5\u5417\uff1f\u201d\u665a\u6674\u4f38\u624b\u8f7b\u8f7b\u6321\u4f4f\u5f20\u606a\u8981\u538b\u4e0b\u6765\u7684\u5634\u5507\uff0c\u201c\u4e0d\u662f\u8bf4\u53ea\u505a\u77e5\u5df1\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u600e\u4e48\u4e0d\u53ef\u4ee5\uff1f\u201d\u5f20\u606a\u6307\u7740\u5e8a\u5934\u738b\u5c0f\u6ce2\u6240\u8457\u7684\u300a\u9ec4\u91d1\u65f6\u4ee3\u300b\uff0c\u201c\u6211\u5c31\u76f8\u4fe1\u738b\u4e8c\u4e0e\u9648\u6e05\u626c\u4e4b\u95f4\u662f\u7eaf\u6d01\u7684\u9769\u547d\u53cb\u8c0a\u5173\u7cfb\u2026\u2026\u201d</p><p>\u3000\u3000\u738b\u5c0f\u6ce2\u7684\u300a\u9ec4\u91d1\u65f6\u4ee3\u300b\u5199\u6210\u4e00\u6bb5\u6709\u5c0f\u8bf4\u4ee5\u6765\u6700\u6de1\u7136\u7684\u3001\u6700\u8ba9\u4eba\u5fc3\u751f\u6000\u5ff5\u7684\u7537\u5973\u6027\u4e8b\uff0c\u8fd9\u6837\u7684\u4e00\u6bb5\u60c5\u611f\uff0c\u65e2\u4e0d\u662f\u6b7b\u751f\u5951\u9614\u7684\u60c5\u604b\uff0c\u4e5f\u4e0d\u662f\u6267\u624b\u767d\u5934\u7684\u8109\u8109\u6e29\u60c5\uff0c\u66f4\u50cf\u56f0\u5883\u4e2d\u7684\u76f8\u4e92\u4f53\u5df1\uff0c\u6000\u5ff5\u8d77\u6765\u4f1a\u6709\u6de1\u6de1\u7684\u54c0\u4f24\uff0c\u4f46\u4e5f\u4e0d\u4f1a\u5230\u75db\u5fc3\u6b32\u7edd\u7684\u5730\u6b65\uff0c\u4f46\u786e\u5b9e\u662f\u7537\u5973\u4e4b\u95f4\u6700\u5999\u7684\u6027\u4e8b\u3002</p><p>\u3000\u3000\u6216\u8005\u662f\u8ba9\u5f20\u606a\u7684\u9b3c\u8bdd\u8bf4\u52a8\uff0c\u665a\u6674\u677e\u5f00\u624b\uff0c\u6602\u7740\u5934\uff0c\u5a07\u8273\u7684\u7ea2\u5507\u60c5\u4e0d\u81ea\u7981\u7684\u8fce\u4e0a\u53bb\uff0c\u6ca1\u6709\u7279\u522b\u7684\u6fc0\u60c5\u6f8e\u6e43\uff0c\u611f\u89c9\u4e0a\u5374\u66f4\u8212\u670d\uff0c\u4ece\u6765\u90fd\u6ca1\u6709\u4f53\u9a8c\u8fc7\u8fd9\u822c\u7384\u5999\u7684\u611f\u89c9\u3002</p><p>\u3000\u3000\u201c\u8981\u4e0d\u8981\u2026\u2026\u201d</p><p>\u3000\u3000\u665a\u6674\u624b\u6309\u7740\u5f20\u606a\u4ece\u5979\u8170\u95f4\u5f80\u4e0a\u6ed1\u7684\u624b\uff0c\u6447\u4e86\u6447\u5934\uff1a\u201c\u6211\u7684\u609f\u6027\u53ef\u6ca1\u6709\u4f60\u90a3\u4e48\u597d\uff0c\u73b0\u5728\u5c31\u505a\u4e86\u8fd9\u4e8b\uff0c\u5c31\u6015\u4f1a\u641e\u4e0d\u6e05\u6211\u8ddf\u4f60\u7684\u5173\u7cfb\u2026\u2026\u4f60\u4e5f\u4e0d\u8bb8\u6311\u9017\u6211\uff01\u201d\u665a\u6674\u5f88\u5766\u7136\u7684\u8bf4\u51fa\u81ea\u5df1\u5fc3\u91cc\u7684\u611f\u53d7\uff0c\u201c\u5f88\u591a\u7537\u5973\u641e\u4e0d\u6e05\u60c5\u4eba\u4e0e\u77e5\u5df1\u7684\u754c\u9650\uff0c\u4ee5\u4e3a\u82e6\u82e6\u71ac\u7740\u4e0d\u8d70\u6700\u540e\u4e00\u6b65\uff0c\u5c31\u6ca1\u6709\u8d8a\u754c\u2026\u2026\u201d</p><p>\u3000\u3000\u665a\u6674\u8fd9\u4e00\u523b\u89c9\u5f97\u5f20\u606a\u4e0d\u4ec5\u4ec5\u662f\u53ef\u4ee5\u4f9d\u8d56\u7684\u7537\u4eba\uff0c\u53ef\u4ee5\u5d07\u62dc\u7684\u7537\u4eba\uff0c\u8fd8\u662f\u53ef\u4ee5\u7ed9\u5979\u6307\u5bfc\u7684\u7537\u4eba\u3002\u5979\u4e4b\u524d\u82e6\u82e6\u538b\u6291\u7740\u5bf9\u5f20\u606a\u7684\u60c5\u611f\uff0c\u65e2\u65e0\u6cd5\u9762\u5bf9\u51b0\u51b7\u65e0\u60c5\u7684\u793e\u4f1a\u73b0\u5b9e\uff0c\u4e5f\u65e0\u6cd5\u9762\u5bf9\u957f\u4e45\u4ee5\u6765\u5728\u5979\u5185\u5fc3\u6df1\u5904\u5f62\u6210\u7684\u9053\u5fb7\u67b7\u9501\u3002\u8fd9\u4e00\u523b\uff0c\u5374\u89c9\u5f97\u5f02\u5e38\u7684\u8f7b\u677e\uff0c\u4e5f\u6ca1\u6709\u56e0\u4e3a\u8003\u8651\u8981\u4e0d\u8981\u8ddf\u5f20\u606a\u505a\u90a3\u4e8b\u800c\u96be\u582a\uff0c\u4f46\u662f\u5bf9\u8981\u4e0d\u8981\u8ddf\u5f20\u606a\u505a\u90a3\u4e8b\uff0c\u5fc3\u91cc\u5374\u6ca1\u6709\u5341\u5206\u7684\u628a\u63e1\uff0c\u96be\u9053\u4e5f\u8981\u4e3a\u4e86\u6240\u8c13\u7684\u201c\u9769\u547d\u53cb\u8c0a\u201d\u53bb\u505a\u90a3\u4e8b\uff1f</p><p>\u3000\u3000\u665a\u6674\u5fcd\u4e0d\u4f4f\u6251\u54e7\u7b11\u51fa\u58f0\u6765\uff0c\u5a07\u7b11\u7740\u51dd\u89c6\u7740\u5f20\u606a\uff1a\u201c\u6709\u65f6\u5019\u4f1a\u60f3\u8981\uff0c\u4f46\u662f\u73b0\u5728\u4e0d\u60f3\uff0c\u6216\u8005\u4f60\u7ed9\u6211\u5f53\u5c0f\u60c5\u4eba\uff0c\u4e5f\u662f\u4e0d\u9519\u7684\u9009\u62e9\u2026\u2026\u4f46\u662f\u6211\u53c8\u4e0d\u60f3\u8ddf\u8bb8\u601d\u3001\u5510\u5a67\u4e24\u4e2b\u5934\u53bb\u4e89\u4f60\uff0c\u611f\u89c9\u4e0a\u4f1a\u5f88\u606c\u4e0d\u77e5\u803b\uff0c\u6216\u8bb8\u4f60\u4e0a\u8f88\u5b50\u771f\u662f\u4e2a\u82b1\u82b1\u516c\u5b50\u4e5f\u8bf4\u4e0d\u5b9a\u2026\u2026\u201d</p><p>\u3000\u3000\u4eba\u5fc3\u4fbf\u662f\u5982\u6b64\u7684\u5947\u5999\uff0c\u6216\u8bb8\u53ea\u8981\u4e00\u901a\u201c\u80e1\u8a00\u4e71\u8bed\u201d\u5c31\u80fd\u6253\u5f00\u5fc3\u7ed3\u3002</p><p>\u3000\u3000\u89c1\u665a\u6674\u653e\u4e0b\u5fc3\u601d\u80fd\u591f\u5f00\u5fc3\u7684\u8bf4\u7b11\uff0c\u5f20\u606a\u65e0\u5948\u7684\u8bf4\uff1a\u201c\u6211\u8fd9\u662f\u4f5c\u8327\u81ea\u7f1a\u2026\u2026\u201d\u677e\u5f00\u624b\uff0c\u5c06\u665a\u6674\u6210\u719f\u52a8\u4eba\u7684\u8eab\u4f53\u6402\u8fdb\u6000\u91cc\uff0c\u4e0b\u9894\u62b5\u7740\u5979\u79c0\u4e3d\u7684\u957f\u53d1\uff0c\u95fb\u7740\u5979\u8ff7\u4eba\u7684\u53d1\u9999\u3002</p><p>\u3000\u3000\u201c\u8fd9\u600e\u4e48\u529e\uff1f\u201d\u665a\u6674\u811a\u5c16\u591f\u7740\u8e22\u4e86\u8e22\u5e8a\u5934\u7684\u51b0\u888b\uff0c\u804a\u4e86\u8fd9\u4e48\u4e45\uff0c\u51b0\u888b\u91cc\u7684\u51b0\u5757\u6709\u4e9b\u878d\u5316\u3002</p><p>\u3000\u3000\u201c\u554a\uff0c\u4f60\u7684\u811a\u597d\u4e86\uff1f\u201d\u5f20\u606a\u505a\u51fa\u5938\u5f20\u7684\u8868\u60c5\uff0c\u201c\u8d70\u4e86\u8fd9\u4e48\u591a\u8def\uff0c\u4f60\u7684\u5c0f\u817f\u4e00\u5b9a\u5f88\u9178\uff0c\u8981\u4e0d\u8981\u5e2e\u4f60\u63c9\u63c9\uff1f\u201d</p><p>\u3000\u3000\u201c\u53bb\uff01\u201d\u665a\u6674\u6a2a\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u4f38\u817f\u8981\u53bb\u8e22\u4ed6\uff0c\u90a3\u4e00\u77ac\u95f4\u7684\u98ce\u60c5\u5f02\u5e38\u7684\u8ff7\u4eba\uff0c\u5f20\u606a\u5fc3\u60f3\uff0c\u80fd\u5f97\u5230\u8fd9\u6837\u7684\u77e5\u5df1\uff0c\u4f5c\u4e3a\u7537\u4eba\u662f\u4f55\u7b49\u7684\u5e78\u8fd0\uff0c\u6709\u4e9b\u4e0d\u80fd\u8ddf\u5510\u5a67\u3001\u8bb8\u601d\u8ff0\u8bf4\u7684\u611f\u6000\uff0c\u5927\u6982\u53ef\u4ee5\u665a\u6674\u6beb\u65e0\u969c\u788d\u7684\u4ea4\u6d41\u5427\u3002\u62c9\u7740\u665a\u6674\u4e0b\u4e86\u5e8a\uff0c\u7ad9\u5728\u7a97\u524d\u7684\u5730\u6bef\u4e0a\u3002</p><p>\u3000\u3000\u4e00\u4e2a\u692d\u5706\u5f62\u4e0e\u4e3b\u5367\u5ba4\u76f4\u63a5\u76f8\u901a\u7684\u5916\u51f8\u9732\u53f0\uff0c\u7531\u4e8e\u89d2\u5ea6\u7684\u8bbe\u8ba1\uff0c\u5f62\u6210\u4e00\u4e2a\u5438\u5149\u7684\u6697\u5ba4\uff0c\u5373\u4f7f\u662f\u666e\u901a\u7684\u73bb\u7483\uff0c\u4e5f\u53ef\u4ee5\u7ad9\u5728\u91cc\u9762\u770b\u7a97\u5916\u7684\u5f71\u8272\uff0c\u800c\u4e0d\u7528\u62c5\u5fc3\u5916\u9762\u7684\u4eba\u80fd\u770b\u5230\u91cc\u9762\uff0c\u9664\u975e\u5ba4\u5185\u6709\u660e\u4eae\u7684\u5149\u6e90\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u665a\u6674\u7ad9\u5728\u9732\u53f0\u4e0a\uff0c\u8f7b\u8f7b\u7684\u62e5\u7740\uff0c\u770b\u7740\u51ac\u5929\u7684\u5915\u9633\u897f\u4e0b\uff0c\u5915\u9633\u6298\u5c04\u51fa\u91d1\u7ea2\u8272\u7684\u5149\u8292\uff0c\u4f7f\u5f97\u897f\u8fb9\u7684\u4e91\u6d77\u67d3\u4e0a\u91d1\u7ea2\u8272\u7684\u8272\u5f69\uff0c\u5076\u5c14\u4e5f\u4f1a\u4eb2\u543b\uff0c\u6ca1\u6709\u60c5\u4eba\u75f4\u604b\u7684\u70bd\u70ed\uff0c\u5374\u53c8\u683c\u5916\u7684\u6e29\u60c5\u4e0e\u8212\u670d\u3002</p><p>\u3000\u3000\u5085\u4fca\u3001\u674e\u660e\u745c\u5e26\u7740\u4e09\u4e2a\u5c0f\u4e2b\u5934\u6ca1\u6709\u9694\u591a\u5c11\u65f6\u95f4\u5c31\u56de\u6765\u4e86\uff0c\u539f\u6765\u4ed6\u4eec\u90fd\u60e6\u8bb0\u8c22\u665a\u6674\u5d34\u811a\u7684\u4e8b\u60c5\uff0c\u5728\u5e97\u91cc\u70b9\u4e86\u5916\u5356\uff0c\u5e26\u56de\u5230\u522b\u5885\u6765\u4e00\u8d77\u4eab\u7528\u3002</p><p>\u3000\u3000\u201c\u5988\u5988\uff0c\u4f60\u7684\u811a\u8fd8\u75bc\u4e0d\u75bc\uff1f\u201d\u82b7\u5f64\u5976\u58f0\u5976\u6c14\u7684\u5b66\u4f1a\u5173\u5fc3\u4eba\u4e86\u3002</p><p>\u3000\u3000\u201c\u597d\u4e86\u2026\u2026\u201d\u8c22\u665a\u6674\u811a\u8e29\u4e86\u8e29\u5730\uff0c\u6ca1\u6709\u4ec0\u4e48\u96be\u582a\u7684\u5fc3\u7eea\uff0c\u4e00\u5207\u90fd\u5f88\u81ea\u7136\uff0c\u53ea\u662f\u5f20\u606a\u5728\u4eb2\u543b\u65f6\u5634\u5507\u6cbe\u4e0a\u5979\u7684\u5507\u818f\uff0c\u867d\u7136\u64e6\u4e86\u4e00\u4e0b\uff0c\u5374\u6bd4\u5e73\u65f6\u7ea2\u6da6\u4e00\u4e9b\u3002</p><p>\u3000\u3000\u8c22\u665a\u6674\u770b\u4e86\u770b\u89d2\u684c\u4e0a\u7684\u65f6\u949f\uff0c\u4e5f\u5230\u4e86\u4e00\u822c\u4eba\u5bb6\u6b63\u5e38\u5403\u665a\u996d\u7684\u65f6\u95f4\uff0c\u95ee\u9053\uff1a\u201c\u7ecf\u9500\u5546\u5927\u4f1a\u5e94\u8be5\u5168\u90e8\u7ed3\u675f\u4e86\u5427\uff0c\u90fd\u4e0d\u6653\u5f97\u5468\u6e38\u3001\u82cf\u6d25\u4e1c\u4ed6\u4eec\u4f1a\u8eb2\u54ea\u91cc\u53bb\u5e86\u795d\u3002\u201d</p><p>\u3000\u3000\u5468\u6e38\u3001\u82cf\u6d25\u4e1c\u4ed6\u4eec\u5f53\u7136\u8981\u597d\u597d\u5e86\u795d\u4e00\u4e0b\uff0c\u7ecf\u9500\u5546\u5927\u4f1a\u7ed3\u675f\uff0c\u4e0d\u4ec5\u4ec5\u91cd\u9020\u7ecf\u9500\u5546\u7f51\u7edc\uff0c\u8ba9\u66f4\u52a0\u6709\u5b9e\u529b\uff0c\u5e02\u573a\u8d44\u6e90\u66f4\u4e30\u5bcc\u7684\u7ecf\u9500\u5546\u52a0\u76df\u8fdb\u6765\uff0c\u5173\u952e\u5229\u7528\u4fdd\u8bc1\u91d1\u95e8\u69db\uff0c\u4ece\u7ecf\u9500\u5546\u90a3\u91cc\u593a\u5f97\u76f8\u5bf9\u7a33\u5b9a\u7684\u4e00\u4ebf\u516b\u5343\u4e07\u7684\u5de8\u989d\u8d44\u91d1\u3002</p><p>\u3000\u3000\u56e0\u4e3a\u7ecf\u9500\u5546\u5927\u4f1a\u9009\u62e9\u6587\u5c71\u996d\u5e97\u4e3a\u4e3b\u4f1a\u573a\uff0c\u7231\u8fbe\u7535\u5b50\u7684\u5e86\u529f\u5bb4\uff0c\u81ea\u7136\u987a\u4fbf\u5728\u6587\u5c71\u996d\u5e97\u91cc\u4e3e\u884c\uff0c\u5510\u5b66\u8c26\u4e2d\u5348\u6ca1\u6709\u51fa\u73b0\uff0c\u665a\u4e0a\u4e0e\u5b8b\u57f9\u660e\u4e00\u8d77\u53c2\u52a0\u7231\u8fbe\u7535\u5b50\u7684\u5185\u90e8\u5e86\u529f\u5bb4\uff0c\u672c\u6765\u5f20\u606a\u7f3a\u5e2d\uff0c\u5510\u5b66\u8c26\u4e5f\u65e0\u9700\u8fc7\u6765\uff0c\u4f46\u662f\u5e86\u529f\u5bb4\u4e4b\u524d\u4f1a\u53ec\u5f00\u4e00\u4e2a\u7b80\u77ed\u7684\u65b0\u95fb\u53d1\u5e03\u4f1a\uff0c\u5411\u5168\u56fd\u5a92\u4f53\u516c\u5f00\u7ecf\u9500\u5546\u5927\u4f1a\u7684\u4e00\u90e8\u5206\u65e0\u5173\u7d27\u8981\u7684\u6210\u679c\uff0c\u5510\u5b66\u8c26\u8981\u4ee3\u8868\u5e02\u91cc\u9732\u4e00\u4e0b\u9762\uff0c\u6240\u4ee5\u7559\u4e0b\u6765\u5403\u996d\u3002</p><p>\u3000\u3000\u5f20\u606a\u7684\u7f3a\u5e2d\uff0c\u5927\u5bb6\u90fd\u80fd\u7406\u89e3\uff0c\u53ea\u662f\u5f20\u606a\u4e0d\u5728\u573a\uff0c\u4f3c\u4e4e\u6ca1\u6cd5\u5c06\u5fc3\u91cc\u6fc0\u8361\u7684\u60c5\u7eea\u6070\u5982\u5176\u5206\u7684\u8868\u73b0\u51fa\u6765\uff0c\u5927\u5bb6\u90fd\u618b\u7740\u6709\u4e9b\u96be\u53d7\uff0c\u4f3c\u4e4e\u6709\u5fc5\u8981\u5c06\u5f20\u606a\u62ce\u51fa\u6765\u5355\u72ec\u5e86\u795d\u4e00\u4e0b\uff0c\u53d1\u6cc4\u4e00\u4e0b\u3002</p><p>\u3000\u3000\u90b5\u81f3\u521a\u3001\u5f20\u77e5\u975e\u3001\u76db\u9752\u4ed6\u4eec\u4e5f\u5728\u665a\u996d\u4e4b\u524d\u8d76\u56de\u5e02\u91cc\uff0c\u4ed6\u4eec\u63a5\u4e0b\u6765\u7684\u4e8b\u60c5\u4e5f\u5f88\u591a\uff0c\u65e2\u7136\u5e2e\u90a3\u4e9b\u6709\u610f\u67d3\u6307\u76d7\u7248\u5236\u789f\u7684\u4eba\u5efa\u7acb\u5de5\u5382\uff0c\u53c8\u8981\u5728\u6bcd\u76d8\u5236\u4f5c\u4e0a\u53d6\u5f97\u7a81\u7834\uff0c\u4e0d\u4ec5\u4ec5\u662f\u9009\u62e9\u5728\u9999\u6e2f\u6216\u8005\u6df1\u5733\u8bbe\u7acb\u516c\u53f8\u7684\u95ee\u9898\u8fd9\u5f53\u4e2d\u6709\u5f88\u591a\u6cd5\u5f8b\u4e0a\u7684\u7ec6\u8282\u8981\u597d\u597d\u7684\u7814\u7a76\uff0c\u504f\u504f\u8fd9\u53c8\u4e0d\u662f\u4ed6\u4eec\u64c5\u957f\u7684\u4e1c\u897f\u3002\u5f20\u606a\u65e0\u7591\u5bf9\u8fd9\u4e9b\u73a9\u827a\u90fd\u5f88\u7cbe\u901a\uff0c\u867d\u7136\u4e0d\u77e5\u9053\u4ed6\u4e3a\u4ec0\u4e48\u7cbe\u901a\u8fd9\u4e9b\u4e1c\u897f\uff0c\u4f46\u662f\u6709\u5fc5\u8981\u518d\u5c06\u4ed6\u62ce\u51fa\u6765\u76d8\u95ee\u4e00\u756a\u3002</p></article>"}}}]);
