(window.webpackJsonp=window.webpackJsonp||[]).push([[270],{292:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u56db\u5341\u4e00\u8282 \u964c\u751f\u7684\u670b\u53cb",content:"<article><h2>\u7b2c\u56db\u5341\u4e00\u8282 \u964c\u751f\u7684\u670b\u53cb</h2><p>\u5f53\u743c\u6069\u8fd4\u56de\u4f4f\u5904\u7684\u65f6\u5019\uff0c\u5df2\u7ecf\u662f\u4e00\u4e2a\u591a\u65f6\u8fb0\u4e4b\u540e\u4e86\u3002</p><p>\u4ed6\u7528\u53e3\u4ee4\u6253\u5f00\u77f3\u67f1\u7684\u6697\u95e8\uff0c\u987a\u7740\u77f3\u9636\u8d70\u4e0a\u7b2c\u4e8c\u5c42\u3002\u8fd9\u5ea7\u4e2d\u7a7a\u77f3\u67f1\u5185\u5171\u6709\u56db\u5c42\uff0c\u7b2c\u4e00\u5c42\u7528\u4e8e\u50a8\u85cf\uff0c\u743c\u6069\u4f4f\u5728\u7b2c\u4e8c\u5c42\uff0c\u83ab\u5c3c\u5361\u59d0\u59b9\u4f4f\u5728\u7b2c\u4e09\u5c42\uff0c\u7b2c\u56db\u5c42\u5219\u662f\u5deb\u5e08\u7684\u8bd5\u9a8c\u5ba4\u548c\u56fe\u4e66\u5ba4\u3002\u8d70\u8fdb\u5ba2\u5385\uff0c\u53d1\u73b0\u8299\u857e\u72c4\u5750\u5728\u6c99\u53d1\u4e0a\uff0c\u6b63\u5728\u76b1\u7709\u6c89\u601d\uff0c\u817f\u4e0a\u653e\u7740\u90a3\u4efd\u6df1\u571f\u9884\u8a00\u7684\u9b54\u6cd5\u7b14\u8bb0\uff0c\u7ffb\u5f00\u7740\uff0c\u4f46\u663e\u7136\u5fc3\u4e0d\u5728\u7109\uff0c\u773c\u5149\u5e76\u4e0d\u843d\u5728\u4e0a\u9762\u3002</p><p>\u201c\u9047\u5230\u96be\u9898\u4e86\uff1f\u201d\u743c\u6069\u968f\u53e3\u95ee\u3002</p><p>\u201c\u55ef\uff0c\u4e5f\u4e0d\u662f\uff0c\u201d\u8299\u857e\u72c4\u8bf4\uff0c\u201c\u8fd9\u4e00\u90e8\u5206\u7684\u6280\u5de7\u548c\u6cd5\u672f\uff0c\u9700\u8981\u8017\u8d39\u5927\u91cf\u7684\u5b9d\u77f3\u505a\u6750\u6599\uff0c\u5426\u5219\u6ca1\u6cd5\u7ec3\u4e60\u2026\u2026\u201d</p><p>\u201c\u8fd9\u6837\u554a\uff0c\u90a3\u5c31\u5148\u653e\u4e0b\u5427\uff0c\u201d\u743c\u6069\u5b89\u6170\uff0c\u201c\u518d\u8fc7\u51e0\u5929\uff0c\u7b49\u6211\u4eec\u56de\u4f0a\u5361\u6c99\u57ce\uff0c\u5230\u65f6\u5019\u518d\u627e\u90a3\u5e2e\u77ee\u4eba\u8981\u5c31\u597d\u3002\u201d</p><p>\u5c0f\u5973\u5b69\u4f3c\u4e4e\u5fae\u5fae\u6014\u4e86\u6014\uff0c\u201c\u6211\u4eec\u5feb\u8981\u56de\u53bb\u4e86\u5417\uff1f\u201d</p><p>\u201c\u5dee\u4e0d\u591a\u5427\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u8fd9\u8fb9\u7684\u4e8b\u60c5\u4e5f\u5feb\u89e3\u51b3\u4e86\u3002\u201d</p><p>\u8299\u857e\u72c4\u82e5\u6709\u6240\u601d\uff0c\u6ca1\u6709\u8bf4\u8bdd\u3002</p><p>\u743c\u6069\u4e5f\u6ca1\u5728\u610f\uff0c\u8d70\u5230\u8eab\u8fb9\u62b1\u7740\u5979\uff0c\u201c\u5f88\u665a\u4e86\uff0c\u201d\u4ed6\u8bf4\uff0c\u4eb2\u543b\u5c0f\u5973\u5b69\u7684\u79c0\u53d1\uff0c\u201c\u522b\u56de\u53bb\u4e86\uff0c\u966a\u6211\u597d\u4e0d\u597d\u3002\u201d</p><p>\u8299\u857e\u72c4\u7f9e\u7ea2\u4e86\u8138\uff0c\u4f4e\u58f0\u7b54\u5e94\u3002</p><p>\u743c\u6069\u5148\u53bb\u6d17\u4e86\u6fa1\uff0c\u8eba\u5728\u5e8a\u4e0a\u6162\u6162\u56de\u5fc6\u521a\u624d\u548c\u4e9a\u5f53\u65af\u4ea4\u8c08\u7684\u7ec6\u8282\u3002\u7422\u78e8\u534a\u5929\uff0c\u4f9d\u65e7\u4e0d\u5f97\u5176\u89e3\uff0c\u4f46\u53ef\u4ee5\u80af\u5b9a\u95ee\u9898\u7684\u5173\u952e\u662f\u5728\u90a3\u4e2a\u6076\u9b54\u8eab\u4e0a\u3002\u201c\u683c\u62c9\u5179\u7279\u624b\u4e0b\u7684\u5c06\u519b\uff0c\u8d1f\u8d23\u60c5\u62a5\u5de5\u4f5c\uff0c\u6700\u8fd1\u53db\u9003\u5230\u5965\u5580\u65af\u9ebe\u4e0b\u201d\u2014\u2014\u8fd9\u5c31\u662f\u76ee\u524d\u6709\u5173\u90a3\u4e2a\u6076\u9b54\u7684\u5168\u90e8\u6d88\u606f\u4e86\uff0c\u743c\u6069\u53cd\u590d\u601d\u7d22\uff0c\u4f9d\u65e7\u60f3\u4e0d\u51fa\u6765\u8fd9\u662f\u4f55\u65b9\u795e\u5723\u3002\u4e4c\u9eef\u541b\u4e3b\u683c\u62c9\u5179\u7279\u3001\u4ea1\u7075\u541b\u738b\u5965\u5580\u65af\uff0c\u8fd9\u4e24\u4f4d\u540d\u5217\u6df1\u6e0a\u4e09\u5927\u6076\u9b54\u9886\u4e3b\uff0c\u662f\u53ef\u4ee5\u548c\u795e\u7947\u76f8\u63d0\u5e76\u8bba\u7684\u5b58\u5728\uff0c\u4ed6\u4eec\u624b\u4e0b\u7684\u5c06\u519b\u4e5f\u5fc5\u5b9a\u662f\u6df1\u6e0a\u4e2d\u7684\u5927\u6076\u9b54\uff0c\u6309\u9053\u7406\u8bf4\uff0c\u50cf\u4ed6\u4eec\u8fd9\u6837\u7684\u4eba\u7269\uff0c\u7406\u5f53\u5728\u6df1\u6e0a\u91cc\u5fd9\u4e8e\u8840\u6218\uff0c\u6ca1\u9053\u7406\u5173\u6ce8\u81ea\u5df1\u8fd9\u4e2a\u7269\u8d28\u754c\u7684\u65e0\u540d\u5c0f\u5352\u5427\u3002</p><p>\u201c\u96be\u9053\u771f\u662f\u90a3\u6b21\u8bef\u6253\u8bef\u649e\uff0c\u963b\u6b62\u683c\u62c9\u5179\u7279\u53d6\u5f97\u9ece\u660e\u4e4b\u77f3\uff0c\u7ed3\u679c\u88ab\u4ed6\u76ef\u4e0a\u4e86\uff1f\u201d</p><p>\u60f3\u6765\u60f3\u53bb\uff0c\u8fd9\u79cd\u53ef\u80fd\u6027\u6700\u5927\uff0c\u4f46\u542c\u4e9a\u5f53\u65af\u8bdd\u4e2d\u7684\u610f\u601d\uff0c\u5206\u660e\u662f\u8ba4\u5b9a\u743c\u6069\u6765\u74dc\u7406\u5fb7\u65af\u57ce\u662f\u4e3a\u4e86\u5bfb\u627e\u67d0\u4ef6\u7269\u54c1\uff0c\u8fd9\u53c8\u662f\u600e\u4e48\u4e00\u56de\u4e8b\uff1f\u6839\u636e\u743c\u6069\u6b64\u540e\u7684\u5bdf\u8a00\u89c2\u8272\u548c\u65c1\u6572\u4fa7\u51fb\uff0c\u4e9a\u5f53\u65af\u4e0d\u50cf\u662f\u5728\u6545\u610f\u88c5\u50bb\uff0c\u4ed6\u5e94\u8be5\u771f\u662f\u8fd9\u4e48\u76f8\u4fe1\u7684\uff0c\u4f46\u8fd9\u5c31\u66f4\u5947\u602a\u4e86\u3002</p><p>\u4e9a\u5f53\u65af\u4f1a\u8fd9\u4e48\u8ba4\u4e3a\uff0c\u80af\u5b9a\u662f\u90a3\u4e2a\u4e0d\u77e5\u540d\u7684\u6076\u9b54\u63d0\u4f9b\u865a\u5047\u60c5\u62a5\u3002\u6076\u9b54\u9a97\u4eba\uff0c\u5012\u4e5f\u4e0d\u8db3\u4e3a\u5947\uff0c\u751a\u81f3\u53ef\u4ee5\u8bf4\u662f\u7406\u6240\u5f53\u7136\u3002\u4f46\u53ea\u8981\u662f\u4e2a\u4eba\u90fd\u77e5\u9053\uff0c\u6076\u9b54\u662f\u4e16\u754c\u4e0a\u6700\u6ca1\u6709\u8bda\u4fe1\u53ef\u8a00\u7684\u5b58\u5728\uff0c\u4e9a\u5f53\u65af\u65e2\u7136\u77e5\u9053\u5bf9\u65b9\u8eab\u4efd\uff0c\u600e\u4e48\u4f1a\u8fd9\u4e48\u76f8\u4fe1\u5b83\u7684\u8bdd\uff1f</p><p>\u5bfb\u601d\u534a\u5929\uff0c\u4e0d\u5f97\u5176\u89e3\uff0c\u53ea\u5f97\u6682\u65f6\u5148\u653e\u5230\u4e00\u8fb9\uff0c\u9759\u89c2\u5176\u53d8\u518d\u8bf4\u3002\u6b64\u65f6\u8299\u857e\u72c4\u4ece\u6d74\u5ba4\u4e2d\u8d70\u51fa\u6765\uff0c\u6ca1\u88f9\u6d74\u5dfe\uff0c\u7a7f\u7740\u743c\u6069\u7684\u886c\u886b\u5f53\u7761\u8863\u3002\u5979\u8eab\u6750\u5a07\u5c0f\uff0c\u886c\u886b\u4e0b\u6446\u76f4\u906e\u5230\u819d\u76d6\u5904\uff0c\u770b\u8d77\u6765\u4fbf\u5982\u77ed\u88d9\u4f3c\u7684\u3002\u743c\u6069\u770b\u89c1\uff0c\u5fae\u5fae\u7b11\u4e86\u8d77\u6765\uff0c\u4ee5\u524d\u4ed6\u4eec\u5728\u5b66\u6821\u7684\u65f6\u5019\uff0c\u8299\u857e\u72c4\u5c31\u7ecf\u5e38\u62ff\u4ed6\u7684\u886c\u886b\u5f53\u7761\u8863\u7684\uff0c\u5982\u4eca\u518d\u6b21\u89c1\u5230\uff0c\u987f\u65f6\u56de\u5fc6\u8d77\u5f80\u6614\u7684\u65d6\u65ce\u6e29\u5b58\uff0c\u4e0d\u7531\u5f97\u5fc3\u5934\u6d8c\u8d77\u4e00\u9635\u6e29\u6696\u3002</p><p>\u6bd5\u7adf\uff0c\u90a3\u662f\u81ea\u5df1\u5728\u8fd9\u4e2a\u4e16\u754c\u7684\u521d\u604b\uff0c\u7b80\u5355\u800c\u7eaf\u51c0\uff0c\u5c31\u7b97\u4ee5\u540e\u53d1\u751f\u518d\u591a\u7684\u4e8b\u60c5\uff0c\u7ec8\u7a76\u4e5f\u8fd8\u662f\u96be\u4ee5\u5fd8\u6000\u7684\u5427\u3002</p><p>\u8299\u857e\u72c4\u722c\u4e0a\u5e8a\uff0c\u50cf\u5c0f\u732b\u4e00\u6837\u4e56\u5de7\u5730\u8737\u7740\uff0c\u5c06\u5934\u6795\u5728\u743c\u6069\u7684\u5927\u817f\u4e0a\u3002\u8fd9\u4e5f\u662f\u4ee5\u524d\u7684\u4e60\u60ef\u4e86\uff0c\u56e0\u4e3a\u521a\u6d17\u5b8c\u6fa1\uff0c\u5934\u53d1\u662f\u6e7f\u7684\uff0c\u8981\u8fc7\u5f88\u4e45\u624d\u80fd\u667e\u5e72\uff0c\u4e0d\u80fd\u9a6c\u4e0a\u7761\u89c9\uff0c\u8fd9\u79cd\u65f6\u5019\uff0c\u4e24\u4eba\u5f80\u5f80\u4f1a\u95f2\u8c08\u804a\u5929\u3002\u5979\u8eab\u4e0a\u6709\u79cd\u6de1\u6de1\u7684\u6e05\u9999\uff0c\u4eff\u4f5b\u6800\u5b50\u82b1\uff0c\u5e7d\u96c5\u4e2d\u5e26\u7740\u4e9b\u751c\u5473\u3002</p><p>\u743c\u6069\u8f7b\u8f7b\u629a\u6478\u7740\u8299\u857e\u72c4\u7684\u8138\u86cb\uff0c\u5c0f\u5973\u5b69\u7684\u5934\u53d1\u65e2\u957f\u53c8\u6d53\u5bc6\uff0c\u6e7f\u6f09\u6f09\u7684\u6563\u94fa\u5728\u4ed6\u817f\u4e0a\uff0c\u611f\u89c9\u6709\u4e9b\u51b0\u51c9\u3002\u201c\u53c8\u7a7f\u6211\u7684\u886c\u886b\uff0c\u201d\u4ed6\u7b11\u7740\uff0c\u201c\u4ee5\u524d\u7684\u4e60\u60ef\u8fd8\u6ca1\u6539\u554a\u3002\u201d</p><p>\u201c\u56e0\u4e3a\u6709\u4f60\u8eab\u4e0a\u7684\u6c14\u606f\u561b\uff0c\u201d\u8299\u857e\u72c4\u56de\u7b54\uff0c\u201c\u7a7f\u7740\u5b83\uff0c\u5c31\u611f\u89c9\u597d\u50cf\u88ab\u4f60\u62b1\u5728\u6000\u91cc\u4e00\u6837\u3002\u201d</p><p>\u743c\u6069\u634f\u4e86\u634f\u5979\u7684\u8033\u5782\uff0c\u201c\u559c\u6b22\u88ab\u6211\u62b1\u7740\u554a\u3002\u201d</p><p>\u201c\u559c\u6b22\uff0c\u201d\u5979\u4f4e\u58f0\u8bf4\uff0c\u201c\u5728\u4f60\u6000\u91cc\uff0c\u5c31\u6709\u4e00\u79cd\u7279\u522b\u5b89\u5fc3\u7684\u611f\u89c9\u3002\u201d</p><p>\u5979\u95ed\u4e0a\u773c\u775b\uff0c\u957f\u957f\u7684\u776b\u6bdb\u8f7b\u5fae\u98a4\u52a8\uff0c\u628a\u743c\u6069\u7684\u4e00\u53ea\u80f3\u818a\u62b1\u5728\u6000\u91cc\uff0c\u4f3c\u4e4e\u60f3\u8981\u7761\u7740\u3002\u743c\u6069\u6478\u7d22\u7740\uff0c\u89e3\u5f00\u7761\u8863\u886c\u886b\u7684\u51e0\u7c92\u7ebd\u6263\uff0c\u624b\u63a2\u4e86\u8fdb\u53bb\uff0c\u5728\u5979\u80f8\u53e3\u8f7b\u8f7b\u63c9\u634f\u7740\uff0c\u201c\u55ef\uff0c\u786e\u5b9e\u4e0d\u5982\u4ee5\u524d\u5927\u4e86\uff0c\u201d\u4ed6\u82e5\u6709\u6240\u601d\uff0c\u201c\u4e3a\u4ec0\u4e48\u5973\u5b69\u5b50\u4e00\u53d8\u7626\uff0c\u9996\u5148\u5c31\u662f\u80f8\u90e8\u7f29\u6c34\u5462\u3002\u201d</p><p>\u201c\u8ba8\u538c\uff0c\u201d\u5c0f\u5973\u5b69\u6485\u8d77\u5634\uff0c\u201c\u5c31\u77e5\u9053\u4f60\u5acc\u4eba\u5bb6\u5c0f\u2026\u2026\u201d</p><p>\u743c\u6069\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u6240\u4ee5\u4ee5\u540e\u8981\u8ba9\u6211\u591a\u6309\u6469\u561b\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u8fd9\u6837\u5c31\u4f1a\u53d8\u5927\u7684\u3002\u201d</p><p>\u201c\u771f\u7684\uff1f\u201d</p><p>\u201c\u5f53\u7136\uff0c\u4f60\u56de\u5fc6\u770b\u770b\uff0c\u548c\u6211\u5728\u4e00\u8d77\u4e4b\u540e\uff0c\u662f\u4e0d\u662f\u6bd4\u4ee5\u524d\u53d8\u5927\u4e86\u3002\u201d</p><p>\u201c\u597d\u50cf\u662f\u5462\u3002\u201d</p><p>\u201c\u6240\u4ee5\u554a\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4ee5\u540e\u8981\u4e56\u4e56\u7684\u77e5\u4e0d\u77e5\u9053\u3002\u201d</p><p>\u201c\u6211\u4e00\u5411\u5c31\u5f88\u4e56\u554a\u3002\u201d</p><p>\u201c\u771f\u7684\uff1f\u201d\u743c\u6069\u53cd\u95ee\uff0c\u611f\u89c9\u90a3\u5bf9\u5a07\u5ae9\u7684\u84d3\u857e\u575a\u633a\u8d77\u6765\uff0c\u786c\u786c\u5730\u9876\u5728\u638c\u5fc3\uff0c\u201c\u4eca\u5929\u4e0d\u662f\u8ba9\u4f60\u7559\u5728\u8fd9\u91cc\u7b49\u6211\u56de\u6765\u4e48\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u600e\u4e48\u53c8\u8dd1\u53bb\u4e86\uff1f\u201d</p><p>\u8299\u857e\u72c4\u602f\u602f\u5730\u4f4e\u4e0b\u8138\uff0c\u201c\u6211\u62c5\u5fc3\u4f60\u561b\u3002\u201d</p><p>\u743c\u6069\u539f\u672c\u60f3\u8d23\u602a\u51e0\u53e5\uff0c\u89c1\u5979\u8fd9\u5e45\u6a21\u6837\uff0c\u5fc3\u4e5f\u5c31\u8f6f\u4e86\uff0c\u201c\u4e0b\u6b21\u4e0d\u8bb8\u4e86\uff0c\u201d\u4ed6\u8b66\u544a\uff0c\u201c\u5426\u5219\u6211\u4f1a\u751f\u6c14\u7684\u3002\u201d</p><p>\u201c\u77e5\u9053\u4e86\u3002\u201d\u8299\u857e\u72c4\u4e56\u4e56\u5730\u8bf4\u3002</p><p>\u5979\u6795\u5728\u743c\u6069\u817f\u4e0a\uff0c\u886c\u886b\u7ebd\u6263\u88ab\u89e3\u5f00\uff0c\u6ed1\u843d\u4e0b\u6765\uff0c\u88f8\u9732\u51fa\u5149\u6d01\u7684\u80a9\u5934\uff0c\u957f\u957f\u7684\u9ed1\u53d1\u6563\u5728\u4e0a\u9762\uff0c\u886c\u6258\u5f97\u76ae\u80a4\u683c\u5916\u767d\u7699\u8bf1\u4eba\u3002\u743c\u6069\u5fcd\u4e0d\u4f4f\u5fc3\u52a8\uff0c\u60f3\u628a\u5979\u62b1\u8d77\u6765\u4eb2\u543b\uff0c\u53cc\u81c2\u7528\u529b\uff0c\u7a81\u7136\u5de6\u8fb9\u80a9\u8180\u4e00\u9635\u75bc\u75db\uff0c\u4e0d\u7531\u5f97\u201c\u554a\u201d\u4e86\u4e00\u58f0\u3002</p><p>\u201c\u600e\u4e48\u4e86\uff1f\u201d\u8299\u857e\u72c4\u5413\u4e86\u4e00\u8df3\u3002</p><p>\u201c\u6ca1\u4e8b\u3002\u201d\u743c\u6069\u793a\u610f\u5979\u522b\u62c5\u5fc3\uff0c\u8fd9\u662f\u4ed6\u4eca\u5929\u5728\u745e\u8d39\u5fb7\u57ce\u5821\u91cc\uff0c\u649e\u4e0a\u90a3\u4e2a\u53eb\u5361\u6885\u9686\u7684\u5deb\u5e08\uff0c\u5148\u540e\u4e2d\u4e86\u4e24\u53d1\u7075\u7bad\uff0c\u53d7\u4f24\u4e0d\u8f7b\uff0c\u52a0\u4e0a\u53c8\u9644\u5e26\u8bc5\u5492\uff0c\u6291\u5236\u6cbb\u7597\u836f\u6c34\u7684\u4f5c\u7528\u3002\u867d\u7136\u540e\u6765\u7ef4\u5eb7\u5c3c\u4e9a\u7528\u590d\u539f\u672f\u6d88\u9664\u8bc5\u5492\uff0c\u4f46\u7ec8\u7a76\u662f\u803d\u8bef\u4e86\u65f6\u95f4\uff0c\u4f24\u52bf\u6ca1\u6709\u5b8c\u5168\u75ca\u6108\u3002\u7cbe\u795e\u96c6\u4e2d\u7684\u65f6\u5019\u8fd8\u4e0d\u89c9\u5f97\uff0c\u5982\u4eca\u653e\u677e\u4e0b\u6765\uff0c\u987f\u65f6\u4fbf\u611f\u53d7\u5230\u4e86\u3002</p><p>\u201c\u6211\u53bb\u627e\u59d0\u59d0\u6765\u3002\u201d</p><p>\u8299\u857e\u72c4\u60f3\u8981\u8d77\u8eab\uff0c\u5374\u88ab\u743c\u6069\u6309\u4f4f\u4e86\u3002\u201c\u5f88\u665a\u4e86\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u8299\u8389\u5a05\u65e9\u7761\u4e86\u3002\u6211\u7684\u6b21\u5143\u888b\u91cc\u6709\u836f\u6c34\uff0c\u5e2e\u6211\u62ff\u8fc7\u6765\u5c31\u597d\u3002\u201d</p><p>\u8299\u857e\u72c4\u4e0b\u5e8a\uff0c\u4ece\u957f\u888d\u5185\u4fa7\u627e\u5230\u6b21\u5143\u888b\uff0c\u53d6\u51fa\u4e24\u74f6\u6d45\u84dd\u8272\u836f\u6c34\u9012\u7ed9\u743c\u6069\u3002\u201c\u6cbb\u7597\u836f\u6c34\u53ea\u6709\u8fd9\u4e24\u74f6\u4e86\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u660e\u5929\u6211\u8bf7\u59d0\u59d0\u518d\u914d\u51e0\u74f6\u3002\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u743c\u6069\u628a\u836f\u6c34\u704c\u4e0b\uff0c\u9ed8\u81ea\u6c89\u601d\uff0c\u521a\u624d\u8299\u857e\u72c4\u8bf4\u627e\u8299\u8389\u5a05\u6765\u4e3a\u4ed6\u6cbb\u7597\uff0c\u5012\u662f\u53c8\u63d0\u9192\u4e86\u4ed6\uff0c\u60f3\u8d77\u81ea\u5df1\u53d8\u5f97\u4e0d\u80fd\u514d\u75ab\u795e\u672f\u7684\u4e8b\u60c5\u6765\uff0c\u8fd9\u5230\u5e95\u662f\u54ea\u91cc\u51fa\u7684\u95ee\u9898\u5462\uff1f</p><p>\u5176\u5b9e\u8981\u8bf4\u8d77\u6765\uff0c\u514d\u75ab\u795e\u672f\u5e76\u4e0d\u7b97\u662f\u4ec0\u4e48\u4f18\u52bf\uff0c\u751a\u81f3\u53ef\u4ee5\u8bf4\u662f\u52a3\u52bf\u3002\u56e0\u4e3a\u795e\u672f\u504f\u91cd\u4e8e\u6cbb\u7597\u3001\u795d\u798f\u3001\u9632\u5fa1\u3001\u5f3a\u5316\uff0c\u800c\u743c\u6069\u538b\u6839\u4eab\u53d7\u4e0d\u5230\u8fd9\u4e9b\u597d\u5904\u3002\u53cd\u8fc7\u6765\u8bf4\uff0c\u795e\u672f\u5e76\u4e0d\u64c5\u957f\u653b\u51fb\u2014\u2014\u6216\u8005\u8bf4\uff0c\u4e0d\u64c5\u957f\u4e8e\u76f4\u63a5\u653b\u51fb\u3002\u5982\u679c\u4e00\u4e2a\u5deb\u5e08\u8981\u653b\u51fb\u654c\u4eba\uff0c\u4ed6\u4f1a\u76f4\u63a5\u6254\u706b\u7403\u95ea\u7535\u6216\u8005\u5404\u79cd\u6740\u4f24\u9b54\u6cd5\uff0c\u4f46\u7267\u5e08\u8981\u653b\u51fb\u5bf9\u624b\uff0c\u4ed6\u4f1a\u7ed9\u81ea\u5df1\u53e0\u52a0\u5404\u79cd\u8f85\u52a9\u795e\u672f\uff0c\u7136\u540e\u51b2\u4e0a\u6765\u75db\u6253\u3002\u6709\u53e5\u7b11\u8bdd\u8bf4\uff1a\u4e00\u4e2a\u52a0\u6ee1\u8f85\u52a9\u795e\u672f\u7684\u7267\u5e08\u6bd4\u91ce\u86ee\u4eba\u66f4\u51f6\u731b\u96be\u5f53\u3002\u867d\u7136\u662f\u620f\u8a00\uff0c\u5374\u4e5f\u672a\u5c1d\u4e0d\u662f\u4e8b\u5b9e\u3002\u800c\u743c\u6069\u7684\u514d\u75ab\u795e\u672f\u80fd\u529b\uff0c\u9762\u5bf9\u8fd9\u79cd\u60c5\u51b5\u662f\u6ca1\u4ec0\u4e48\u62b5\u6297\u4f5c\u7528\u7684\u3002</p><p>\u95ee\u9898\u5728\u4e8e\uff0c\u8fd9\u4e2a\u80fd\u529b\u672c\u8eab\u6216\u8bb8\u662f\u4e2a\u9e21\u808b\uff0c\u4f46\u5b83\u80cc\u540e\u7684\u542b\u4e49\u5374\u6ca1\u90a3\u4e48\u7b80\u5355\u3002\u514d\u75ab\u795e\u672f\uff0c\u8fd9\u662f\u5927\u5965\u672f\u5e08\u7684\u7279\u5f81\u4e4b\u4e00\uff0c\u5965\u6c83\u4e5f\u66fe\u7ecf\u636e\u6b64\u8bf4\u743c\u6069\u662f\u5929\u751f\u7684\u5927\u5965\u672f\u5e08\u6750\u6599\uff0c\u5e76\u4e14\u6536\u4ed6\u4e3a\u5b66\u751f\u3002\u5982\u4eca\u8fd9\u79cd\u80fd\u529b\u6084\u7136\u4e27\u5931\uff0c\u90a3\u662f\u4e0d\u662f\u610f\u5473\u7740\u8bf4\uff0c\u743c\u6069\u5df2\u7ecf\u4e0d\u53ef\u80fd\u6210\u4e3a\u5927\u5965\u672f\u5e08\u4e86\uff1f</p><p>\u8fd9\u53ef\u662f\u975e\u5e38\u7cdf\u7cd5\u7684\u4e8b\u60c5\u3002</p><p>\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u81ea\u5df1\u7684\u67d0\u79cd\u5929\u8d4b\u80fd\u529b\u7a81\u7136\u4e27\u5931\uff0c\u8fd9\u5176\u4e2d\u7684\u7f18\u6545\u5982\u679c\u4e0d\u641e\u6e05\u695a\uff0c\u603b\u662f\u4e0d\u80fd\u5b89\u5fc3\u3002\u7136\u800c\u601d\u6765\u60f3\u53bb\uff0c\u743c\u6069\u8fd8\u662f\u4e0d\u80fd\u80af\u5b9a\u95ee\u9898\u51fa\u5728\u4ec0\u4e48\u5730\u65b9\uff0c\u6700\u53ef\u7591\u7684\u6709\u4e24\u5904\uff0c\u7b2c\u4e00\u81ea\u7136\u662f\u5438\u53d6\u5df4\u5c14\u795e\u529b\uff0c\u7b2c\u4e8c\u662f\u81ea\u5df1\u7684\u8bb0\u5fc6\u7f16\u7ec7\u88ab\u6d88\u9664\uff0c\u9664\u6b64\u4e4b\u5916\uff0c\u522b\u7684\u4e8b\u60c5\u4f3c\u4e4e\u90fd\u8054\u7cfb\u4e0d\u4e0a\u3002</p><p>\u5438\u6536\u795e\u529b\uff0c\u8eab\u4f53\u88ab\u6539\u9020\uff0c\u8fde\u5e26\u4e5f\u4e27\u5931\u4e86\u5bf9\u795e\u672f\u7684\u514d\u75ab\u80fd\u529b\u2014\u2014\u8981\u8bf4\u8d77\u6765\uff0c\u8fd9\u662f\u6700\u6709\u53ef\u80fd\u4e5f\u662f\u6700\u5408\u60c5\u7406\u7684\u89e3\u91ca\u3002\u4f46\u6709\u4e00\u4e2a\u5730\u65b9\u5374\u8bf4\u4e0d\u901a\uff0c\u4ee5\u524d\u743c\u6069\u5728\u70db\u5821\u7684\u65f6\u5019\uff0c\u9047\u4e0a\u5973\u6740\u624b\u838e\u73de\u514b\uff0c\u5f53\u65f6\u8bef\u6253\u8bef\u649e\u4e5f\u5438\u6536\u8fc7\u4e00\u70b9\u795e\u529b\uff0c\u514d\u75ab\u795e\u672f\u7684\u7279\u6027\u5374\u5e76\u6ca1\u6709\u56e0\u6b64\u800c\u6709\u6240\u6539\u53d8\u3002</p><p>\u5982\u679c\u662f\u7b2c\u4e8c\u79cd\u53ef\u80fd\uff0c\u90a3\u60c5\u51b5\u5c31\u66f4\u590d\u6742\u4e86\u3002\u4e8b\u5230\u5982\u4eca\uff0c\u743c\u6069\u5bf9\u90a3\u4ef6\u4e8b\u60c5\u90fd\u8fd8\u6ca1\u5b8c\u5168\u7406\u6e05\u5934\u7eea\uff0c\u8bb0\u5fc6\u7f16\u7ec7\u7684\u6cd5\u672f\u867d\u7136\u6d88\u9664\uff0c\u4f46\u539f\u672c\u771f\u5b9e\u7684\u8bb0\u5fc6\u5e76\u6ca1\u6709\u56e0\u6b64\u800c\u76f4\u63a5\u6062\u590d\uff0c\u743c\u6069\u4f9d\u7136\u8fd8\u662f\u4e0d\u77e5\u9053\u81ea\u5df1\u5728\u4e03\u5c81\u5230\u5341\u5c81\u7684\u90a3\u6bb5\u65f6\u5149\u91cc\uff0c\u5230\u5e95\u90fd\u505a\u4e86\u4e9b\u4ec0\u4e48\u3002\u751a\u81f3\u2026\u2026\u65e2\u7136\u6709\u5173\u7530\u4f2f\u5149\u7684\u8fd9\u6bb5\u8bb0\u5fc6\u662f\u865a\u5047\u7684\uff0c\u90a3\u81ea\u5df1\u7684\u6765\u5386\u3001\u7a7f\u8d8a\u3001\u51fa\u751f\uff0c\u4ece\u5c0f\u957f\u5927\u7684\u6240\u6709\u8bb0\u5fc6\uff0c\u662f\u4e0d\u662f\u4e5f\u6709\u53ef\u80fd\u90fd\u662f\u5047\u7684\uff1f</p><p>\u4e0d\u4ec5\u5982\u6b64\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u7275\u6d89\u751a\u5e7f\uff0c\u9519\u7efc\u590d\u6742\uff0c\u548c\u9634\u9b42\u57ce\u7684\u9ad8\u5c42\uff0c\u548c\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\uff0c\u751a\u81f3\u548c\u73ca\u5609\uff0c\u53ea\u6015\u90fd\u9690\u9690\u8131\u4e0d\u5f00\u5173\u7cfb\u3002</p><p>\u8bb0\u5fc6\u7f16\u7ec7\u662f\u6781\u5176\u9ad8\u660e\u7684\u6cd5\u672f\uff0c\u662f\u9644\u9b54\u5b66\u6d3e\u7684\u6700\u9ad8\u6210\u5c31\uff0c\u5730\u4f4d\u76f8\u5f53\u4e8e\u5927\u88c2\u89e3\u3001\u65f6\u95f4\u505c\u6b62\uff0c\u80fd\u591f\u8fd0\u7528\u5b83\u7684\u5deb\u5e08\uff0c\u4e16\u754c\u4e0a\u5e94\u8be5\u4e0d\u4f1a\u592a\u591a\u3002\u743c\u6069\u51fa\u751f\u4e8e\u9634\u9b42\u57ce\uff0c\u5f53\u65f6\u5927\u5bb6\u90fd\u8fd8\u5728\u5e7d\u5f71\u754c\u91cc\u5f53\u5b64\u5c9b\u9057\u6c11\uff0c\u4eca\u5e74\u5e74\u521d\u624d\u8fd4\u56de\u7269\u8d28\u754c\uff0c\u90a3\u4e48\u5acc\u7591\u6700\u5927\u7684\u4eba\u662f\u8c01\uff0c\u4e5f\u5c31\u4e0d\u95ee\u800c\u77e5\u4e86\u3002\u7ed3\u5408\u81ea\u5df1\u906d\u9047\u7684\u90a3\u4e48\u591a\u201c\u5de7\u5408\u201d\uff0c\u743c\u6069\u5df2\u7ecf\u57fa\u672c\u65ad\u5b9a\u8fd9\u4ef6\u4e8b\u548c\u9634\u9b42\u57ce\u7684\u90a3\u4e9b\u5927\u5965\u672f\u5e08\u4eec\u6709\u5173\uff0c\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u5c31\u7b97\u4e0d\u662f\u4e3b\u8c0b\uff0c\u81f3\u5c11\u4e5f\u662f\u4e2a\u77e5\u60c5\u8005\u3002</p><p>\u4f46\u73ca\u5609\u5728\u5176\u4e2d\uff0c\u53c8\u662f\u4ec0\u4e48\u89d2\u8272\u5462\uff1f</p><p>\u6b63\u662f\u56e0\u4e3a\u5979\u63d0\u8bae\u8981\u53bb\u796d\u62dc\u7236\u6bcd\uff0c\u53c8\u575a\u6301\u8981\u56de\u6545\u5c45\u89c2\u770b\uff0c\u743c\u6069\u4e2d\u7684\u8bb0\u5fc6\u7f16\u7ec7\u624d\u5f97\u4ee5\u7834\u9664\u3002\u8fd9\u5230\u5e95\u662f\u5076\u7136\u7684\u5de7\u5408\uff0c\u8fd8\u662f\u6545\u610f\u4e3a\u4e4b\uff0c\u5982\u679c\u662f\u540e\u8005\uff0c\u5979\u53c8\u600e\u4e48\u77e5\u9053\u7684\u2026\u2026</p><p>\u743c\u6069\u76b1\u7740\u7709\u5934\uff0c\u672c\u80fd\u5730\u62d2\u7edd\u518d\u7ee7\u7eed\u60f3\u4e0b\u53bb\u3002\u4ece\u5c0f\u5230\u5927\uff0c\u5728\u4ed6\u7684\u5fc3\u76ee\u4e2d\uff0c\u73ca\u5609\u90fd\u662f\u6e29\u5a49\u79c0\u4e3d\u7684\u59d0\u59d0\uff0c\u662f\u61a7\u61ac\u548c\u68a6\u60f3\uff0c\u743c\u6069\u5b9e\u5728\u4e0d\u613f\u610f\u53bb\u7834\u574f\u8fd9\u4e2a\u5b8c\u7f8e\u5f62\u8c61\u2014\u2014\u6216\u8005\u8bf4\u4ed6\u4e0d\u6562\u3002\u5728\u8fd9\u4e2a\u4e16\u754c\u4e0a\uff0c\u4ed6\u5df2\u7ecf\u6ca1\u6709\u522b\u7684\u4eb2\u4eba\uff0c\u5982\u679c\u8fde\u76f8\u4f9d\u4e3a\u547d\u957f\u5927\u7684\u59d0\u59d0\u90fd\u7a81\u7136\u53d8\u5f97\u4e0d\u80fd\u4fe1\u4efb\uff0c\u90a3\u8fd9\u4eba\u751f\u4e5f\u672a\u514d\u592a\u60b2\u60e8\u4e86\u70b9\u3002</p><p>\u201c\u6216\u8bb8\uff0c\u771f\u7684\u5c31\u662f\u5076\u7136\u649e\u4e0a\u5427\uff0c\u201d\u743c\u6069\u5b89\u6170\u7740\u81ea\u5df1\uff0c\u201c\u5de7\u5408\u8fd9\u79cd\u4e1c\u897f\uff0c\u4e16\u754c\u4e0a\u6bd5\u7adf\u8fd8\u662f\u5b58\u5728\u7684\uff0c\u5b8c\u5168\u7684\u9634\u8c0b\u8bba\u4e5f\u672a\u89c1\u5f97\u6b63\u786e\u3002\u201d</p><p>\u6b63\u60f3\u7740\uff0c\u7a81\u7136\u611f\u89c9\u4e0b\u8eab\u4f20\u6765\u4e00\u80a1\u6e29\u70ed\uff0c\u539f\u6765\u662f\u8299\u857e\u72c4\u89c1\u4ed6\u6c89\u601d\u4e0d\u8bed\uff0c\u81ea\u5df1\u4e00\u4e2a\u4eba\u767e\u65e0\u804a\u8d56\uff0c\u7d22\u6027\u5c06\u8138\u8d34\u5728\u4ed6\u817f\u95f4\uff0c\u57cb\u5934\u670d\u52a1\u8d77\u6765\u3002\u5c0f\u5973\u5b69\u7684\u53e3\u8154\u6e29\u6696\u6e7f\u6da6\uff0c\u9999\u820c\u6ed1\u817b\uff0c\u542e\u5438\u52fe\u5f15\uff0c\u4e0d\u5230\u7247\u523b\u5c31\u8ba9\u743c\u6069\u6709\u4e86\u53cd\u5e94\uff0c\u7a81\u7136\u81a8\u80c0\u8d77\u6765\u7684\u6eda\u70eb\u4e4b\u7269\u5c06\u5c0f\u5634\u585e\u5f97\u6ee1\u6ee1\uff0c\u51e0\u4e4e\u5bb9\u7eb3\u4e0d\u4e0b\u3002</p><p>\u5979\u6162\u6162\u5410\u51fa\uff0c\u4ef0\u8d77\u4fcf\u8138\uff0c\u5f97\u610f\u5730\u51b2\u7740\u743c\u6069\u7b11\u8d77\u6765\uff0c\u4eff\u4f5b\u5c31\u50cf\u521a\u521a\u5077\u5230\u4e86\u871c\u7cd6\u7684\u5c0f\u72d0\u72f8\u3002\u743c\u6069\u6b63\u7406\u4e0d\u6e05\u695a\u5934\u7eea\uff0c\u6709\u4e9b\u5fc3\u70e6\u610f\u4e71\uff0c\u88ab\u5979\u8fd9\u4e00\u6311\u9017\uff0c\u6b32\u706b\u71c3\u70bd\uff0c\u6682\u65f6\u4e5f\u5c31\u629b\u5f00\u90a3\u4e9b\u4e71\u4e03\u516b\u7cdf\u7684\u60f3\u6cd5\uff0c\u4fef\u8eab\u5c06\u5c0f\u5973\u5b69\u6309\u5012\uff0c\u6b63\u51c6\u5907\u957f\u9a71\u76f4\u5165\uff0c\u8299\u857e\u72c4\u7a81\u7136\u60ca\u53eb\u4e86\u4e00\u58f0\u3002</p><p>\u201c\u54ce\u5440\uff01\u201d</p><p>\u743c\u6069\u4e00\u6014\uff0c\u5c31\u89c1\u5979\u5fd9\u4e0d\u8fed\u5730\u7528\u624b\u6342\u4f4f\u4e0b\u4f53\uff0c\u4e00\u8fb9\u614c\u614c\u5f20\u5f20\u5730\u4ece\u5e8a\u8fb9\u626f\u8fc7\u6bdb\u5dfe\u6765\uff0c\u201c\u600e\u4e48\u4e86\uff1f\u201d\u4ed6\u95ee\uff0c\u4f46\u968f\u5373\u4e5f\u5c31\u53cd\u5e94\u8fc7\u6765\uff0c\u8fd9\u662f\u5973\u5b69\u5b50\u6bcf\u4e2a\u6708\u90a3\u51e0\u5929\u5230\u4e86\u3002</p><p>\u539f\u672c\u60c5\u6b32\u6b63\u6d53\uff0c\u7a81\u7136\u53d1\u751f\u8fd9\u79cd\u4e8b\u60c5\uff0c\u81ea\u7136\u4e0d\u514d\u626b\u5174\u3002\u8299\u857e\u72c4\u5306\u5306\u6536\u62fe\u5b8c\u6bd5\uff0c\u7a7f\u4e0a\u4e00\u4ef6\u767d\u8272\u5185\u88e4\uff0c\u201c\u5bf9\u4e0d\u8d77\u554a\uff0c\u201d\u5979\u8f7b\u58f0\u8bf4\uff0c\u201c\u6211\u4e5f\u6ca1\u60f3\u5230\u7a81\u7136\u2026\u2026\u201d</p><p>\u743c\u6069\u628a\u5979\u62b1\u8fdb\u6000\u91cc\uff0c\u201c\u8bf4\u4ec0\u4e48\u5462\uff0c\u50bb\u4e2b\u5934\u3002\u201d</p><p>\u201c\u8981\u4e48\uff0c\u201d\u5c0f\u5973\u5b69\u54ac\u7740\u5634\u5507\uff0c\u201c\u7528\u540e\u9762\u597d\u4e86\u2026\u2026\u201d</p><p>\u743c\u6069\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u5f00\u59cb\u5f88\u75bc\u7684\u3002\u201d</p><p>\u201c\u6211\u4e0d\u6015\u3002\u201d</p><p>\u201c\u4f46\u4f60\u59d0\u59d0\u80af\u5b9a\u6015\u554a\uff0c\u201d\u743c\u6069\u634f\u634f\u5979\u7684\u8138\u86cb\uff0c\u201c\u6211\u53ef\u4e0d\u60f3\u5979\u51b2\u8fc7\u6765\u62ff\u5251\u780d\u6211\u3002\u201d</p><p>\u201c\u90a3\u627e\u4e2a\u673a\u4f1a\u628a\u5979\u4e5f\u2026\u2026\u201d\u8299\u857e\u72c4\u8138\u4e0a\u7ea2\u7ea2\u7684\uff0c\u201c\u4e0d\u5c31\u884c\u4e86\u3002\u201d</p><p>\u743c\u6069\u6709\u4e9b\u6115\u7136\uff0c\u4f4e\u5934\u770b\u6000\u91cc\u7684\u5c0f\u5973\u5b69\uff0c\u5dee\u70b9\u4ee5\u4e3a\u81ea\u5df1\u542c\u9519\u4e86\uff0c\u201c\u4f60\u9f13\u52b1\u6211\u53bb\u63a8\u5012\u4f60\u59d0\u59d0\uff1f\u201d</p><p>\u201c\u55ef\uff0c\u53cd\u6b63\u5c31\u50cf\u4f60\u8bf4\u7684\u554a\uff0c\u6211\u4eec\u7684\u8eab\u4f53\u611f\u5e94\u662f\u76f8\u8054\u7ed3\u7684\uff0c\u6211\u53c8\u4e0d\u60f3\u88ab\u522b\u7684\u7537\u4eba\u2026\u2026\u201d\u5979\u7684\u58f0\u97f3\u8d8a\u6765\u8d8a\u8f7b\uff0c\u201c\u5c31\u4fbf\u5b9c\u4f60\u597d\u4e86\u3002\u201d</p><p>\u201c\u8fd9\u79cd\u5bf9\u8bdd\u2026\u2026\u83ab\u975e\u6211\u5f53\u771f\u5177\u6709\u4f20\u8bf4\u4e2d\u4e3b\u89d2\u7684\u738b\u8005\u4e4b\u6c14\u4e48\u2026\u2026\u201d</p><p>\u201c\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u6ca1\u4ec0\u4e48\u3002\u201d</p><p>\u8299\u857e\u72c4\u4e5f\u4e0d\u518d\u591a\u95ee\uff0c\u5c06\u8138\u8d34\u5728\u4ed6\u6000\u91cc\uff0c\u201c\u6211\u4eec\u8981\u56de\u4f0a\u5361\u6c99\u57ce\u4e86\u5417\uff1f\u201d\u5979\u7a81\u7136\u95ee\u3002</p><p>\u201c\u662f\u554a\u3002\u201d</p><p>\u201c\u6211\u60f3\u2026\u2026\u518d\u591a\u5446\u51e0\u5929\u597d\u4e0d\u597d\u3002\u201d</p><p>\u201c\u5514\uff1f\u201d</p><p>\u743c\u6069\u6709\u4e9b\u8be7\u5f02\uff0c\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u4ed6\u5947\u602a\uff0c\u201c\u4f60\u4e0d\u60f3\u56de\u53bb\uff1f\u201d</p><p>\u201c\u4e5f\u4e0d\u662f\u2026\u2026\u201d\u8299\u857e\u72c4\u72b9\u8c6b\u7740\uff0c\u201c\u53ea\u662f\u4e0d\u60f3\u90a3\u4e48\u5feb\u2026\u2026\u201d</p><p>\u201c\u53ef\u662f\u4f60\u4e0a\u6b21\u4e0d\u662f\u8bf4\uff0c\u4e0d\u559c\u6b22\u8fd9\u5ea7\u57ce\u5e02\u5417\u3002\u201d</p><p>\u201c\u662f\u4e0d\u559c\u6b22\u3002\u201d</p><p>\u743c\u6069\u66f4\u7591\u60d1\u4e86\uff0c\u201c\u90a3\u662f\u4e3a\u4ec0\u4e48\uff1f\u201d</p><p>\u5c0f\u5973\u5b69\u54ac\u7d27\u5634\u5507\u3002</p><p>\u201c\u5728\u8fd9\u91cc\uff0c\u867d\u7136\u4ec0\u4e48\u90fd\u4e0d\u597d\uff0c\u4f46\u81f3\u5c11\u6211\u80fd\u5929\u5929\u966a\u5728\u4f60\u8eab\u8fb9\u554a\uff0c\u201d\u5979\u8f7b\u58f0\u8bf4\uff0c\u201c\u56de\u53bb\u4ee5\u540e\uff0c\u6050\u6015\u5c31\u4e0d\u884c\u4e86\u2026\u2026\u201d</p><p>\u743c\u6069\u9ed8\u7136\u3002</p><p>\u201c\u6211\u5176\u5b9e\u2026\u2026\u4e5f\u6ca1\u5e0c\u671b\u4f60\u662f\u6211\u4e00\u4e2a\u4eba\u7684\uff0c\u201d\u5979\u5782\u4e0b\u773c\u5e18\uff0c\u201c\u6211\u4e0d\u4ecb\u610f\u4f60\u8fd8\u4f1a\u559c\u6b22\u4e0a\u522b\u4eba\uff0c\u8fd8\u4f1a\u6709\u522b\u7684\u5973\u5b69\u5b50\u7684\u3002\u6211\u53ea\u60f3\u80fd\u4e00\u76f4\u966a\u5728\u4f60\u8eab\u8fb9\uff0c\u90a3\u5c31\u53ef\u4ee5\u4e86\u3002\u201d</p><p>\u743c\u6069\u6258\u8d77\u5979\u7684\u4e0b\u5df4\uff0c\u201c\u8299\u857e\u72c4\u3002\u201d</p><p>\u201c\u55ef\uff1f\u201d</p><p>\u201c\u6211\u4e0d\u4f1a\u8ba9\u4f60\u79bb\u5f00\u6211\u3002\u201d</p><p>\u201c\u771f\u7684\uff1f\u201d</p><p>\u201c\u771f\u7684\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u7b54\u5e94\u4f60\uff0c\u65e0\u8bba\u5c06\u6765\u53d1\u751f\u4ec0\u4e48\uff0c\u6211\u90fd\u4e0d\u4f1a\u8ba9\u4f60\u79bb\u5f00\u6211\u3002\u201d</p><p>\u5c0f\u5973\u5b69\u987f\u65f6\u7b11\u9010\u989c\u5f00\uff0c\u4e56\u4e56\u5730\u8d34\u5728\u4ed6\u6000\u91cc\u3002\u201c\u7761\u5427\uff0c\u201d\u743c\u6069\u629a\u6478\u7740\u5979\u7684\u79c0\u53d1\uff0c\u201c\u660e\u5929\u8fd8\u6709\u4e8b\u60c5\u5462\u3002\u201d</p><p>\u8299\u857e\u72c4\u55ef\u4e86\u4e00\u58f0\uff0c\u4e56\u4e56\u628a\u8eab\u4f53\u8d34\u8fc7\u6765\uff0c\u6324\u5728\u6000\u91cc\uff0c\u6b63\u8981\u5165\u7761\uff0c\u7a81\u7136\u60f3\u8d77\u4e00\u4ef6\u4e8b\u60c5\u6765\u3002\u201c\u5bf9\u4e86\uff0c\u743c\u6069\uff0c\u6211\u5dee\u70b9\u5fd8\u4e86\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u4f60\u548c\u83f2\u5c14\u4f26\u5c0f\u59d0\u51fa\u53bb\u4ee5\u540e\uff0c\u6765\u4e86\u4e00\u4e2a\u5ba2\u4eba\u3002\u201d</p><p>\u201c\u5ba2\u4eba\uff1f\u201d</p><p>\u201c\u55ef\uff0c\u4e00\u4e2a\u9ed1\u6697\u7cbe\u7075\uff0c\u6211\u4e0d\u8ba4\u8bc6\uff0c\u4ece\u6765\u6ca1\u89c1\u8fc7\uff0c\u770b\u8863\u7740\u6253\u626e\u4e5f\u4e0d\u50cf\u662f\u83f2\u5c14\u4f26\u5bb6\u65cf\u7684\u6210\u5458\uff0c\u4e0d\u77e5\u9053\u600e\u4e48\u5230\u4e86\u8fd9\u91cc\uff0c\u8fd8\u81ea\u79f0\u662f\u4f60\u670b\u53cb\u3002\u201d</p><p>\u201c\u6211\u670b\u53cb\uff1f\u201d\u743c\u6069\u6115\u7136\uff0c\u80fd\u591f\u79f0\u5f97\u4e0a\u4ed6\u670b\u53cb\u7684\u539f\u672c\u5c31\u5be5\u5be5\u65e0\u51e0\uff0c\u81f3\u4e8e\u9ed1\u6697\u7cbe\u7075\u2026\u2026\u90a3\u538b\u6839\u4e0d\u53ef\u80fd\u554a\u3002</p><p>\u201c\u4ed6\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d</p><p>\u201c\u4ed6\u4e0d\u80af\u8bf4\uff0c\u201d\u8299\u857e\u72c4\u8bf4\uff0c\u201c\u4f46\u4ed6\u7559\u4e0b\u4e86\u4e00\u5f20\u540d\u7247\uff0c\u8bf4\u7b49\u4f60\u56de\u6765\u4ea4\u7ed9\u4f60\u2014\u2014\u4f46\u90a3\u5f20\u540d\u7247\u5f88\u5947\u602a\uff0c\u4e0a\u9762\u4e00\u4e2a\u5b57\u90fd\u6ca1\u6709\u3002\u201d</p><p>\u201c\u62ff\u6765\u6211\u770b\u770b\u3002\u201d</p><p>\u8299\u857e\u72c4\u8d77\u8eab\u4e0b\u5e8a\uff0c\u53d6\u4e86\u540d\u7247\u56de\u6765\u3002\u743c\u6069\u63a5\u8fc7\uff0c\u662f\u4e00\u5f20\u7528\u79d8\u94f6\u6253\u5236\u7684\u957f\u65b9\u5f62\u8584\u7247\uff0c\u4e0a\u9762\u786e\u5b9e\u534a\u4e2a\u5b57\u90fd\u6ca1\u6709\u3002\u4ed6\u6b63\u51c6\u5907\u4e22\u5230\u4e00\u8fb9\uff0c\u8003\u8651\u662f\u4e0d\u662f\u6709\u4eba\u8ddf\u81ea\u5df1\u5f00\u73a9\u7b11\uff0c\u773c\u89d2\u4f59\u5149\u7a81\u7136\u77a5\u89c1\u4e86\u4ec0\u4e48\u3002</p><p>\u5b9a\u775b\u518d\u770b\uff0c\u90a3\u5f20\u540d\u7247\u4e0a\u6162\u6162\u6d6e\u73b0\u4e24\u884c\u5b57\u6765\u3002\u6b63\u4e2d\u5199\u7740\u201c\u4fe1\u606f\u5c31\u662f\u529b\u91cf\u201d\uff0c\u4e0b\u9762\u8fd8\u6709\u4e00\u884c\u5c0f\u5b57\u3002</p><p>\u201c\u5f53\u7f57\u4e1d\u6740\u6b7b\u8d39\u9a6c\u7f57\xb7\u83ab\u65af\u5fb7\u6797\uff08\u7cbe\u7075\u7684\u6d41\u6d6a\u4e4b\u795e\uff09\u65f6\uff0c\u6211\u5728\u6c89\u9ed8\u4e4b\u6b4c\u7b49\u4f60\u3002\u201d</p></article>"}}}]);
