(window.webpackJsonp=window.webpackJsonp||[]).push([[271],{293:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u56db\u5341\u4e8c\u8282 \u6b27\u51ef\u7684\u65b0\u60c5\u62a5",content:"<article><h2>\u7b2c\u56db\u5341\u4e8c\u8282 \u6b27\u51ef\u7684\u65b0\u60c5\u62a5</h2><p>\u86db\u540e\u5927\u9053\u5c3d\u5934\u7684\u7f57\u4e1d\u795e\u6bbf\u4e0a\u65b9\uff0c\u9b54\u6cd5\u5236\u9020\u51fa\u7684\u5e7b\u8c61\u7f13\u7f13\u6d41\u8f6c\uff0c\u6700\u7ec8\u518d\u5ea6\u51dd\u56fa\u3002\u7f57\u4e1d\u4ee5\u8d64\u88f8\u8eab\u4f53\u7684\u7f8e\u8273\u5353\u5c14\u5973\u6027\u5f62\u8c61\u51fa\u73b0\uff0c\u800c\u7ad9\u5728\u5979\u5bf9\u9762\u7684\uff0c\u662f\u4e00\u4f4d\u770b\u8d77\u6765\u75b2\u5026\u3001\u908b\u9062\u3001\u9634\u6c89\u7740\u8138\u7684\u7cbe\u7075\uff0c\u4ed6\u662f\u8d39\u9a6c\u7f57\xb7\u83ab\u65af\u5fb7\u6797\uff0c\u7cbe\u7075\u7684\u6d41\u6d6a\u4e4b\u795e\uff0c\u800c\u4e14\u66fe\u7ecf\u662f\u7f57\u4e1d\u7684\u604b\u4eba\uff0c\u62dc\u5012\u4e8e\u5979\u7684\u88d9\u4e0b\u3002\u5f53\u7136\uff0c\u4ed6\u6700\u7ec8\u8fd8\u662f\u6446\u8131\u4e86\u7f57\u4e1d\u7684\u9ed1\u6697\u9b45\u529b\uff0c\u907f\u514d\u548c\u5979\u4e00\u8d77\u5760\u5165\u6df1\u6e0a\u3002\u56e0\u6b64\u4e4b\u6545\uff0c\u4ed6\u4e5f\u662f\u8718\u86db\u5973\u795e\u6700\u618e\u6068\u7684\u4ec7\u654c\u4e4b\u4e00\u3002</p><p>\u7f57\u4e1d\u4eb2\u543b\u7740\u6614\u65e5\u604b\u4eba\u7684\u8138\u988a\uff0c\u540c\u65f6\u5c06\u5315\u9996\u523a\u8fdb\u4ed6\u7684\u80f8\u819b\uff0c\u8fd9\u4e00\u5e55\u666f\u8c61\u5c06\u4f1a\u5728\u63a5\u4e0b\u6765\u7684\u4e00\u4e2a\u65f6\u8fb0\u91cc\u4eff\u4f5b\u4e0a\u6f14\uff0c\u9884\u793a\u7740\u74dc\u7406\u5fb7\u65af\u57ce\u8fdb\u5165\u6df1\u591c\u4e86\u3002</p><p>\u5e7d\u6697\u5730\u57df\u91cc\u6ca1\u6709\u65e5\u6708\u661f\u5149\uff0c\u539f\u672c\u662f\u6ca1\u6709\u5730\u8868\u4e16\u754c\u7684\u65e5\u591c\u4e4b\u5206\uff0c\u4f46\u5353\u5c14\u4eec\u548c\u4eba\u7c7b\u4e00\u6837\uff0c\u4e5f\u9700\u8981\u996e\u98df\u4f11\u606f\uff0c\u867d\u7136\u4e0d\u5fc5\u201c\u7761\u7720\u201d\uff0c\u4f46\u5374\u540c\u6837\u5fc5\u987b\u901a\u8fc7\u201c\u51a5\u60f3\u201d\u6765\u6062\u590d\u7cbe\u529b\uff0c\u672c\u8d28\u4e0a\u5176\u5b9e\u6ca1\u6709\u533a\u522b\uff0c\u53ea\u4e0d\u8fc7\u51a5\u60f3\u65f6\u53ef\u4ee5\u7ad9\u7740\u4e5f\u53ef\u4ee5\u5750\u7740\uff0c\u672a\u5fc5\u4e00\u5b9a\u8981\u8eba\u5728\u5e8a\u4e0a\u7f62\u4e86\u3002\u6b64\u65f6\u6b63\u662f\u5353\u5c14\u4eec\u6700\u9002\u5b9c\u8fdb\u884c\u51a5\u60f3\u7684\u65f6\u95f4\u6bb5\uff0c\u4e5f\u5c31\u88ab\u5b9a\u4e3a\u8fd9\u5ea7\u57ce\u5e02\u7684\u591c\u665a\u3002\u8857\u9053\u4e0a\u4eba\u8ff9\u7a00\u5c11\uff0c\u9664\u4e86\u5c11\u6570\u5de1\u903b\u7684\u58eb\u5175\u4e4b\u5916\u770b\u4e0d\u5230\u5176\u4ed6\uff0c\u5353\u5c14\u4eec\u5927\u591a\u56de\u5bb6\u4f11\u606f\u53bb\u4e86\uff0c\u82e5\u5728\u4ee5\u524d\uff0c\u5012\u8fd8\u4e0d\u81f3\u4e8e\u8fd9\u4e48\u51b7\u6e05\uff0c\u56e0\u4e3a\u6709\u6570\u91cf\u5e9e\u5927\u7684\u5974\u96b6\uff0c\u7136\u800c\u5982\u4eca\u5b83\u4eec\u90fd\u5df2\u7ecf\u4e0d\u5b58\u5728\u4e86\u3002</p><p>\u5bf9\u4e8e\u74dc\u7406\u5fb7\u65af\u57ce\u8fd9\u79cd\u5b9e\u884c\u7b49\u7ea7\u68ee\u4e25\u7684\u5974\u96b6\u5236\u7684\u5730\u65b9\u6765\u8bf4\uff0c\u8fd9\u771f\u662f\u4e00\u573a\u5927\u707e\u96be\u3002\u4e00\u4e2a\u57ce\u5e02\uff0c\u4f5c\u4e3a\u52b3\u5de5\u7684\u5974\u96b6\u51e0\u4e4e\u6b7b\u5149\uff0c\u53ea\u7559\u4e0b\u5750\u4eab\u5176\u6210\u7684\u8d35\u65cf\u548c\u5927\u91cf\u7684\u519b\u4eba\uff0c\u90a3\u5b83\u4e5f\u5dee\u4e0d\u591a\u8981\u5d29\u6e83\u4e86\u3002\u5f53\u7136\uff0c\u60c5\u51b5\u4f1a\u6162\u6162\u597d\u8f6c\u7684\uff0c\u5e7d\u6697\u5730\u57df\u91cc\u4ece\u6765\u5c31\u4e0d\u7f3a\u5c11\u5730\u7cbe\u3001\u72d7\u5934\u4eba\u6216\u8005\u98df\u4eba\u9b54\u4e4b\u6d41\uff0c\u74dc\u7406\u5fb7\u65af\u57ce\u7684\u9644\u8fd1\u4e5f\u6709\u4e00\u4e9b\u8fd9\u6837\u7684\u602a\u7269\u805a\u5c45\u5730\u3002\u867d\u7136\u6570\u91cf\u4e0d\u591a\uff0c\u89c4\u6a21\u5f88\u5c0f\uff0c\u4f46\u597d\u5728\u5b83\u4eec\u7e41\u6b96\u80fd\u529b\u8d85\u5f3a\uff0c\u518d\u8fc7\u51e0\u5341\u5e74\u4e0a\u767e\u5e74\uff0c\u8fd9\u5ea7\u5353\u5c14\u57ce\u5e02\u4e5f\u5c31\u80fd\u5927\u4f53\u6062\u590d\u5143\u6c14\u4e86\u3002</p><p>\u524d\u63d0\u662f\uff0c\u5b83\u8fd8\u80fd\u7ee7\u7eed\u5b58\u5728\u4e0a\u767e\u5e74\u3002</p><p>\u743c\u6069\u7ad9\u5728\u9ad8\u5d16\u4e0a\uff0c\u4fef\u89c6\u7740\u8fd9\u5ea7\u9ed1\u6697\u4e2d\u7684\u57ce\u5e02\uff0c\u84dd\u8272\u3001\u7eff\u8272\u548c\u6697\u7d2b\u8272\u7684\u5996\u706b\u5728\u5404\u4e2a\u5bb6\u65cf\u7684\u5b85\u9662\u4e2d\u7ffb\u817e\u7740\uff0c\u5ffd\u660e\u5ffd\u6697\uff0c\u5e7b\u5316\u51fa\u5404\u79cd\u8718\u86db\u6216\u8005\u6076\u9b54\u7684\u5f62\u72b6\u3002\u4ed6\u6c89\u601d\u4e86\u4e00\u4f1a\uff0c\u5524\u8d77\u80f8\u9488\u91cc\u7684\u6d6e\u7a7a\u672f\uff0c\u4ece\u60ac\u5d16\u4e0a\u76f4\u63a5\u98d8\u843d\uff0c\u7136\u540e\u6cbf\u7740\u8857\u9053\u671d\u4e0b\u57ce\u533a\u8d70\u53bb\u3002</p><p>\u74dc\u7406\u5fb7\u65af\u57ce\u4eba\u53e3\u867d\u7136\u591a\uff0c\u9762\u79ef\u5012\u4e0d\u7b97\u6781\u5927\uff0c\u8d70\u4e86\u5c0f\u534a\u4e2a\u65f6\u8fb0\uff0c\u743c\u6069\u5df2\u7ecf\u8fdb\u5165\u5170\u83ab\u96f7\u65af\u533a\uff0c\u8fdc\u8fdc\u770b\u89c1\u6c89\u9ed8\u4e4b\u6b4c\u7684\u5de8\u5927\u62db\u724c\u3002\u524d\u65e5\u7684\u66b4\u4e71\u8ba9\u8fd9\u91cc\u5927\u90e8\u4efd\u5546\u5e97\u3001\u9152\u9986\u548c\u6309\u6469\u9662\u90fd\u88ab\u6467\u6bc1\uff0c\u80fd\u591f\u5e78\u5b58\u4e0b\u6765\u7684\uff0c\u5927\u591a\u90fd\u662f\u6709\u96c4\u539a\u7684\u540e\u53f0\u80cc\u666f\uff0c\u5e73\u65f6\u517b\u6709\u8db3\u591f\u7684\u6253\u624b\u770b\u5bb6\u62a4\u9662\uff0c\u5173\u952e\u65f6\u523b\u624d\u80fd\u9876\u4f4f\uff0c\u6c89\u9ed8\u4e4b\u6b4c\u662f\u745e\u8d39\u5fb7\u5bb6\u65cf\u7684\u4ea7\u4e1a\uff0c\u81ea\u7136\u4e5f\u5b89\u7136\u65e0\u6059\u3002</p><p>\u53ea\u662f\u5982\u4eca\uff0c\u7b2c\u56db\u5bb6\u65cf\u8986\u706d\uff0c\u6c89\u9ed8\u4e4b\u6b4c\u81ea\u7136\u4e5f\u6539\u6362\u95e8\u5ead\uff0c\u6210\u4e86\u83f2\u5c14\u4f26\u5bb6\u65cf\u7684\u4ea7\u4e1a\u3002\u743c\u6069\u8d70\u8fdb\u9152\u5e97\u7684\u6b63\u95e8\uff0c\u53d1\u73b0\u90a3\u4f4d\u539f\u672c\u603b\u662f\u559c\u6b22\u61d2\u6d0b\u6d0b\u8db4\u5728\u67dc\u53f0\u540e\u7684\u79c3\u9876\u7070\u77ee\u4eba\u5df2\u7ecf\u4e0d\u89c1\u4e86\uff0c\u53d6\u800c\u4ee3\u4e4b\u7684\u662f\u4e00\u4f4d\u8863\u9970\u534e\u4e3d\u7684\u5353\u5c14\u7537\u6027\uff0c\u743c\u6069\u5bf9\u4ed6\u6709\u70b9\u5370\u8c61\uff0c\u770b\u4e86\u51e0\u773c\uff0c\u60f3\u8d77\u6765\u662f\u83f2\u5c14\u4f26\u5bb6\u65cf\u7684\u4e00\u4e2a\u65c1\u7cfb\u5b50\u5f1f\uff0c\u7b97\u8d77\u6765\u8fd8\u662f\u7ef4\u5eb7\u5c3c\u4e9a\u7684\u5802\u5144\uff0c\u56e0\u4e3a\u80fd\u529b\u5e73\u5e73\uff0c\u6b66\u6280\u9b54\u6cd5\u7686\u65e0\u6240\u64c5\u957f\uff0c\u7d20\u6765\u4e5f\u4e0d\u53d7\u91cd\u89c6\uff0c\u5982\u4eca\u88ab\u6253\u53d1\u51fa\u6765\u7ba1\u7406\u6c89\u9ed8\u4e4b\u6b4c\uff0c\u5012\u662f\u9887\u6709\u4e9b\u5fd7\u5f97\u610f\u6ee1\u7684\u6837\u5b50\u3002\u4ed6\u4e5f\u8ba4\u8bc6\u743c\u6069\uff0c\u77e5\u9053\u8fd9\u4e2a\u4eba\u7c7b\u5982\u4eca\u5f88\u5f97\u4e3b\u6bcd\u770b\u91cd\uff0c\u4e0d\u6562\u6020\u6162\uff0c\u8fde\u5fd9\u4eb2\u81ea\u8fce\u4e86\u4e0a\u6765\u3002</p><p>\u201c\u6709\u4e00\u4f4d\u5148\u751f\u7b49\u60a8\u5f88\u4e45\u4e86\uff0c\u201d\u4ed6\u6bb7\u52e4\u5730\u8bf4\uff0c\u540c\u65f6\u9012\u8fc7\u6765\u4e00\u9762\u94f6\u724c\uff0c\u201c\u7b2c\u4e09\u5341\u4e8c\u53f7\u623f\u95f4\u3002\u201d</p><p>\u743c\u6069\u5df2\u7ecf\u6765\u8fc7\u4e00\u6b21\uff0c\u4e5f\u7b97\u719f\u6089\u8def\u5f84\uff0c\u5f84\u76f4\u7a7f\u8fc7\u65c1\u8fb9\u5c0f\u95e8\uff0c\u8d70\u4e0b\u697c\u68af\uff0c\u6cbf\u7740\u957f\u5eca\u524d\u884c\u3002\u8def\u8fc7\u7b2c\u5341\u4e03\u53f7\u623f\u95f4\u7684\u65f6\u5019\uff0c\u7565\u7565\u505c\u987f\u4e86\u4e00\u4e0b\uff0c\u8003\u8651\u8981\u4e0d\u8981\u8fdb\u53bb\u770b\u770b\u3002\u4e0a\u6b21\u4ed6\u6765\u8fd9\u91cc\uff0c\u5c31\u662f\u8fdb\u8fd9\u4e2a\u623f\u95f4\uff0c\u91cc\u9762\u662f\u67d0\u4e2a\u5df2\u7ecf\u8986\u706d\u5bb6\u65cf\u7684\u957f\u5973\uff0c\u4ece\u5979\u53e3\u4e2d\u8fd8\u5f97\u5230\u4e86\u4e00\u4e9b\u6709\u4ef7\u503c\u7684\u60c5\u62a5\u3002\u4f46\u8fd9\u4e2a\u5ff5\u5934\u53ea\u662f\u5728\u8111\u4e2d\u8f6c\u4e86\u8f6c\uff0c\u6700\u7ec8\u8fd8\u662f\u6253\u6d88\u4e86\uff0c\u4e0d\u8fc7\u662f\u840d\u6c34\u76f8\u9022\uff0c\u53c8\u8c08\u4e0d\u4e0a\u4ec0\u4e48\u4ea4\u60c5\uff0c\u96be\u4e0d\u6210\u8fd8\u8981\u53bb\u63a2\u671b\u8fd1\u51b5\u4e48\u3002</p><p>\u63a8\u5f00\u7b2c\u4e09\u5341\u4e8c\u53f7\u623f\u95f4\u7684\u95e8\uff0c\u91cc\u9762\u5df2\u7ecf\u5750\u7740\u4e00\u4e2a\u4eba\uff0c\u4e0d\u662f\u9ed1\u6697\u7cbe\u7075\uff0c\u800c\u662f\u4e2a\u4eba\u7c7b\uff0c\u6234\u5bbd\u6cbf\u5e3d\uff0c\u7a7f\u4e00\u8eab\u9ec4\u8272\u98ce\u8863\uff0c\u6b63\u5728\u60a0\u95f2\u81ea\u5f97\u5730\u6446\u5f04\u7740\u4e00\u526f\u68cb\u5c40\uff0c\u9762\u524d\u653e\u7740\u4e24\u676f\u9ed1\u8611\u9152\u3002\u770b\u89c1\u743c\u6069\u8fdb\u6765\uff0c\u62ac\u624b\u6253\u4e86\u4e2a\u62db\u547c\u3002</p><p>\u201c\u665a\u4e0a\u597d\uff0c\u743c\u6069\uff0c\u201d\u4ed6\u7b11\u772f\u772f\u7684\uff0c\u201c\u53c8\u89c1\u9762\u4e86\uff0c\u8fd9\u4e2a\u4e16\u754c\u771f\u5c0f\u554a\u3002\u201d</p><p>\u201c\u662f\u554a\uff0c\u201d\u743c\u6069\u56de\u7b54\uff0c\u201c\u4e3a\u4ec0\u4e48\u8d70\u5230\u54ea\u91cc\u90fd\u4f1a\u649e\u4e0a\u4f60\u5462\uff0c\u6b27\u51ef\u5148\u751f\u3002\u201d</p><p>\u201c\u5728\u5965\u7075\u591a\u5c14\u7684\u65f6\u5019\u6211\u4e0d\u5c31\u8bf4\u8fc7\u561b\uff0c\u6211\u4eec\u5f88\u5feb\u5c31\u4f1a\u518d\u89c1\u5230\u7684\uff0c\u201d\u6b27\u51ef\u8bf4\uff0c\u70ed\u5fc3\u5730\u9012\u8fc7\u4e00\u676f\u9152\u6765\uff0c\u201c\u8fd9\u8bf4\u660e\u547d\u8fd0\u7684\u4e1d\u7ebf\uff0c\u65e9\u5c31\u5df2\u7ecf\u628a\u4f60\u6211\u8054\u7ed3\u5728\u4e00\u8d77\u554a\u3002\u201d</p><p>\u201c\u7b49\u7b49\uff0c\u201d\u743c\u6069\u8d76\u5feb\u6253\u65ad\uff0c\u201c\u522b\u8bf4\u8fd9\u4e48\u5bb9\u6613\u8ba9\u4eba\u8bef\u89e3\u7684\u8bdd\u597d\u4e0d\u597d\u3002\u6211\u53ef\u662f\u5949\u516c\u5b88\u6cd5\u7684\u826f\u597d\u516c\u6c11\uff0c\u7edd\u4e0d\u60f3\u548c\u6076\u9b54\u626f\u4e0a\u5173\u7cfb\u3002\u201d</p><p>\u6b27\u51ef\u7684\u773c\u775b\u772f\u7f1d\u8d77\u6765\uff0c\u201c\u4f60\u77e5\u9053\u6211\u7684\u8eab\u4efd\u4e86\u554a\u3002\u201d</p><p>\u201c\u731c\u4e5f\u731c\u5230\u4e86\u5427\uff0c\u201d\u743c\u6069\u6ca1\u597d\u6c14\u5730\u8bf4\uff0c\u201c\u9664\u4e86\u4f60\u8fd8\u80fd\u6709\u8c01\u3002\u201d</p><p>\u4ee5\u524d\u5728\u62c9\u6c83\u514b\u7684\u5deb\u5e08\u4e4b\u5893\u91cc\uff0c\u5438\u8840\u9b3c\u8389\u6cd5\u513f\u5c31\u66fe\u7ecf\u6697\u793a\u8fc7\uff1a\u201c\u6b27\u51ef\u5e76\u975e\u4eba\u7c7b\u201d\u3002\u540e\u6765\u5728\u9634\u9b42\u57ce\uff0c\u519b\u706b\u5546\u4eba\u9093\u80af\u56e0\u4e3a\u67aa\u819b\u7206\u70b8\u800c\u4ea1\uff0c\u6b27\u51ef\u6070\u597d\u5728\u573a\uff0c\u53ca\u65f6\u65bd\u6cd5\u62a2\u4e0b\u4e86\u7075\u9b42\uff0c\u5ff5\u8bf5\u7684\u5c31\u662f\u6076\u9b54\u5492\u8bed\u3002\u56e0\u4e3a\u6b64\u4e8b\uff0c\u743c\u6069\u5f53\u65f6\u548c\u6885\u83f2\u65af\u8bf4\u8d77\uff0c\u5c31\u5df2\u7ecf\u6709\u70b9\u6000\u7591\u6b27\u51ef\u5176\u5b9e\u662f\u4e2a\u6076\u9b54\uff0c\u4f46\u6885\u83f2\u65af\u53cd\u5bf9\uff0c\u8bf4\u4ee5\u4ed6\u7684\u884c\u4e8b\u98ce\u683c\u6765\u770b\u66f4\u50cf\u662f\u9b54\u9b3c\u3002\u743c\u6069\u4e5f\u6ca1\u628a\u8fd9\u4ef6\u4e8b\u60c5\u592a\u653e\u5728\u5fc3\u4e0a\u3002\u5982\u4eca\u5148\u662f\u542c\u4e9a\u5f53\u65af\u63d0\u8d77\uff0c\u7d27\u63a5\u7740\u5c31\u53c8\u6536\u5230\u6b27\u51ef\u7684\u540d\u7247\uff0c\u6574\u4e2a\u4e8b\u60c5\u524d\u540e\u4e00\u8054\u7cfb\uff0c\u6392\u9664\u5de7\u5408\uff0c\u5927\u4f53\u4e5f\u5c31\u77e5\u9053\u662f\u600e\u4e48\u56de\u4e8b\u4e86\u3002</p><p>\u201c\u4e0d\u8fc7\u8bdd\u8bf4\u56de\u6765\uff0c\u6211\u8fd8\u771f\u6ca1\u60f3\u5230\u4f60\u662f\u4e2a\u6076\u9b54\u554a\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u770b\u4f60\u7684\u884c\u4e8b\u98ce\u683c\uff0c\u4f3c\u4e4e\u548c\u4e5d\u5c42\u5730\u72f1\u90a3\u4e9b\u9b54\u9b3c\u66f4\u63a5\u8fd1\u5462\u3002\u201d</p><p>\u201c\u4ec0\u4e48\u5730\u65b9\u90fd\u4f1a\u6709\u7279\u4f8b\u561b\uff0c\u201d\u6b27\u51ef\u6297\u8bae\uff0c\u201c\u522b\u4ee5\u4e3a\u6211\u4eec\u6076\u9b54\u5c31\u662f\u6ca1\u8111\u5b50\u7684\u7b28\u86cb\u554a\uff0c\u683c\u62c9\u5179\u7279\u53ef\u662f\u6bd4\u9b54\u9b3c\u8fd8\u50cf\u9b54\u9b3c\u5462\u3002\u201d</p><p>\u201c\u90a3\u662f\u4f60\u4ee5\u524d\u7684\u8001\u677f\u5427\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u542c\u8bf4\u4f60\u6700\u8fd1\u8df3\u69fd\u4e86\u3002\u201d</p><p>\u201c\u662f\u554a\uff0c\u201d\u6b27\u51ef\u53f9\u6c14\uff0c\u201c\u5176\u5b9e\u6211\u672c\u6765\u4e5f\u4e0d\u60f3\u7684\uff0c\u8fd9\u5e74\u5934\u627e\u4e2a\u5de5\u4f5c\u4e0d\u5bb9\u6613\u554a\uff0c\u7136\u800c\u683c\u62c9\u5179\u7279\u90a3\u8fb9\u5f85\u9047\u5b9e\u5728\u592a\u5dee\u4e86\u2026\u2026\u7b49\u7b49\uff0c\u6211\u4e3a\u4ec0\u4e48\u8981\u548c\u4f60\u8bf4\u8fd9\u4e9b\u3002\u201d</p><p>\u743c\u6069\u7ffb\u7ffb\u773c\u76ae\uff0c\u201c\u6211\u4e5f\u538b\u6839\u6ca1\u6253\u7b97\u542c\u4f60\u8bf4\u8fd9\u4e9b\u5427\u3002\u201d</p><p>\u201c\u597d\u5427\u597d\u5427\uff0c\u4e0d\u8c08\u8fd9\u4e9b\uff0c\u201d\u6b27\u51ef\u8bf4\uff0c\u201c\u6700\u8fd1\u5fd9\u5f97\u56e2\u56e2\u8f6c\uff0c\u968f\u53e3\u53d1\u53e5\u7262\u9a9a\u3002\u4e0d\u8fc7\u4f60\u770b\u8d77\u6765\u6c14\u8272\u4e0d\u9519\u554a\u3002\u201d</p><p>\u201c\u81ea\u7136\uff0c\u5982\u679c\u6ca1\u6709\u67d0\u4e2a\u5bb6\u4f19\u4e71\u6253\u5c0f\u62a5\u544a\u90a3\u6211\u5c31\u66f4\u5fc3\u60c5\u6109\u5feb\u4e86\u3002\u201d</p><p>\u6b27\u51ef\u54c8\u54c8\u5927\u7b11\uff0c\u201c\u522b\u8bf4\u90a3\u4e48\u96be\u542c\uff0c\u743c\u6069\uff0c\u201d\u4ed6\u8bf4\uff0c\u644a\u5f00\u624b\u8868\u793a\u65e0\u5948\uff0c\u201c\u751f\u610f\u5f52\u751f\u610f\u561b\u3002\u201d</p><p>\u201c\u751f\u610f\uff1f\u201d</p><p>\u201c\u662f\u554a\uff0c\u4f60\u77e5\u9053\u7684\uff0c\u51fa\u552e\u60c5\u62a5\uff0c\u8d29\u5356\u4fe1\u606f\uff0c\u6211\u5c31\u662f\u5403\u8fd9\u7897\u996d\u7684\u561b\uff0c\u603b\u8981\u6709\u804c\u4e1a\u9053\u5fb7\u3002\u4e9a\u5f53\u65af\u5411\u6211\u6253\u542c\u4f60\u7684\u6d88\u606f\uff0c\u6070\u597d\u6211\u77e5\u9053\uff0c\u4ed6\u53c8\u51fa\u5f97\u8d77\u4ef7\uff0c\u6240\u4ee5\u5462\u2026\u2026\u201d</p><p>\u201c\u6240\u4ee5\u4f60\u5dee\u70b9\u628a\u6211\u5bb3\u6b7b\u4e86\u3002\u201d</p><p>\u201c\u6ca1\u90a3\u56de\u4e8b\uff0c\u201d\u6b27\u51ef\u6325\u6325\u624b\uff0c\u201c\u6211\u53ef\u662f\u5728\u4ed6\u9762\u524d\u6781\u529b\u66ff\u4f60\u7f8e\u8a00\u7684\uff0c\u4e13\u95e8\u6361\u597d\u542c\u7684\u8bf4\uff0c\u5982\u4eca\u5728\u4e9a\u5f53\u65af\u5fc3\u91cc\uff0c\u53ea\u6015\u90fd\u4ee5\u4e3a\u4f60\u662f\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u7684\u79c1\u751f\u5b50\u4e86\u2026\u2026\u201d</p><p>\u743c\u6069\u6b63\u5728\u559d\u9152\uff0c\u4e00\u53e3\u545b\u4e86\u51fa\u6765\uff0c\u201c\u4f60\u8bf4\u4ec0\u4e48\uff01\u201d\u4ed6\u77aa\u7740\u6b27\u51ef\u3002</p><p>\u201c\u5176\u5b9e\u6211\u6ca1\u8bf4\u8fd9\u53e5\u8bdd\uff0c\u201d\u6b27\u51ef\u5f88\u65e0\u8f9c\u5730\u644a\u5f00\u624b\uff0c\u201c\u6211\u53ea\u662f\u6697\u793a\u4f60\u548c\u5e03\u96f7\u7eb3\u65af\u5173\u7cfb\u5bc6\u5207\uff0c\u975e\u6bd4\u5bfb\u5e38\uff0c\u8bf4\u4ed6\u5bf9\u4f60\u6089\u5fc3\u683d\u57f9\uff0c\u975e\u5e38\u770b\u91cd\uff0c\u8fd8\u8bf4\u4f60\u4eec\u5f88\u76f8\u50cf\u2014\u2014\u5176\u5b9e\u6211\u7684\u610f\u601d\u662f\u4f60\u4eec\u6027\u683c\u5f88\u50cf\uff0c\u4e0d\u662f\u957f\u76f8\u2026\u2026\u8c01\u77e5\u9053\u90a3\u4e2a\u8001\u5353\u5c14\u5fc3\u601d\u4e0d\u6b63\uff0c\u80e1\u4e71\u7406\u89e3\u2026\u2026\u201d</p><p>\u201c\u2026\u2026\u6211\u548c\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u54ea\u91cc\u50cf\u4e86\uff1f\u201d</p><p>\u201c\u6211\u89c9\u5f97\u50cf\u800c\u5df2\uff0c\u201d\u6b27\u51ef\u8bf4\uff0c\u201c\u800c\u4e14\u4f60\u62c5\u5fc3\u4ec0\u4e48\uff0c\u5982\u4eca\u9634\u9b42\u57ce\u8fdb\u5c55\u987a\u5229\uff0c\u5965\u56fe\u3001\u5951\u5fb7\xb7\u7eb3\u6492\u3001\u4e9a\u65af\u79d1\u5c14\u3001\u65af\u7434\u8fbe\u83b1\u6069\u90fd\u5df2\u7ecf\u987a\u5229\u653b\u5360\uff0c\u6c99\u739b\u65af\u57ce\u7ed3\u76df\uff0c\u6574\u4e2a\u5317\u5e7d\u6697\u5730\u57df\u5df2\u7ecf\u6709\u5927\u534a\u843d\u5165\u638c\u63e1\uff0c\u53ea\u6709\u9b54\u7d22\u5e03\u83b1\u57ce\u8fd8\u6ca1\u62ff\u4e0b\u3002\u8fd9\u79cd\u65f6\u5019\uff0c\u4e9a\u5f53\u65af\u51b3\u4e0d\u4f1a\u4e3a\u4e86\u4f60\u5f97\u7f6a\u9634\u9b42\u57ce\u7684\u3002\u201d</p><p>\u201c\u90a3\u662f\u4ed6\u4e0d\u77e5\u9053\u6211\u6765\u505a\u4ec0\u4e48\u2026\u2026\u8bf4\u5230\u8fd9\u91cc\uff0c\u4f60\u662f\u600e\u4e48\u9a97\u4ed6\u76f8\u4fe1\u6211\u662f\u6765\u627e\u4e2a\u4ec0\u4e48\u4e1c\u897f\u7684\uff1f\u201d</p><p>\u201c\u5582\u5582\uff0c\u4f60\u8fd9\u662f\u5728\u8d28\u7591\u4e00\u4e2a\u8bda\u5b9e\u5546\u4eba\u7684\u4fe1\u8a89\uff0c\u201d\u6b27\u51ef\u6297\u8bae\uff0c\u201c\u6211\u4ece\u6765\u4e0d\u4f1a\u9a97\u4eba\u7684\u3002\u201d</p><p>\u743c\u6069\u51b7\u7b11\uff0c\u201c\u90a3\u4ed6\u600e\u4e48\u4f1a\u6709\u8fd9\u79cd\u53e4\u602a\u60f3\u6cd5\uff1f\u201d</p><p>\u201c\u8fd9\u548c\u6211\u65e0\u5173\uff0c\u201d\u6b27\u51ef\u8bf4\uff0c\u201c\u4ed6\u95ee\u6211\u4f60\u6765\u74dc\u7406\u5fb7\u65af\u57ce\u662f\u4ec0\u4e48\u76ee\u7684\uff0c\u6211\u5c31\u544a\u8bc9\u4ed6\u4f60\u662f\u5949\u4e86\u5bc6\u4ee4\uff0c\u524d\u6765\u627e\u4e1c\u897f\u7684\u3002\u201d</p><p>\u201c\u6211\u4ec0\u4e48\u65f6\u5019\u63a5\u5230\u5bfb\u5b9d\u7684\u5bc6\u4ee4\u4e86\uff1f\u201d</p><p>\u201c\u4f60\u4e0d\u662f\u8981\u6765\u627e\u4e00\u4e2a\u53ef\u4ee5\u4f5c\u4e3a\u9b54\u50cf\u5236\u9020\u7684\u57fa\u5730\u4e48\uff1f\u201d</p><p>\u201c\u662f\u554a\u3002\u201d</p><p>\u201c\u8fd9\u4e2a\u57fa\u5730\u4e0d\u662f\u4e2a\u4e1c\u897f\u5417\uff1f\u6240\u4ee5\u6211\u8bf4\u4f60\u662f\u5949\u547d\u6765\u627e\u4e2a\u4e1c\u897f\u7684\uff0c\u8fd9\u6709\u95ee\u9898\u5417\uff1f\u201d</p><p>\u201c\u2026\u2026\u4f60\u8fd9\u4e48\u8bf4\u5f88\u5bb9\u6613\u8ba9\u4eba\u8bef\u89e3\u7684\u3002\u201d</p><p>\u6b27\u51ef\u644a\u5f00\u624b\uff0c\u201c\u90a3\u5c31\u662f\u4ed6\u7684\u7406\u89e3\u95ee\u9898\uff0c\u548c\u6211\u6ca1\u5173\u7cfb\u4e86\u3002\u4e0d\u8fc7\u8bf4\u8d77\u6765\uff0c\u4e5f\u771f\u5947\u602a\u554a\uff0c\u201d\u6b27\u51ef\u6478\u6478\u4e0b\u5df4\uff0c\u82e5\u6709\u6240\u601d\u5730\u8bf4\uff0c\u201c\u6211\u544a\u8bc9\u4ed6\u4f60\u662f\u6765\u81ea\u9634\u9b42\u57ce\uff0c\u8bf4\u4f60\u5949\u547d\u800c\u6765\u627e\u4e00\u4ef6\u4e1c\u897f\u2026\u2026\u7136\u540e\u4ed6\u4e0d\u77e5\u9053\u600e\u4e48\u641e\u7684\uff0c\u80e1\u4e71\u731c\u60f3\uff0c\u5c31\u8ba4\u5b9a\u4f60\u662f\u770b\u4e2d\u4e86\u83f2\u5c14\u4f26\u5bb6\u85cf\u5b9d\u5e93\u91cc\u7684\u67d0\u4ef6\u5b9d\u8d1d\u2026\u2026\u201d</p><p>\u743c\u6069\u6c89\u9ed8\u7740\uff0c\u4ed6\u5df2\u7ecf\u542c\u660e\u767d\u4e86\u6b27\u51ef\u8bdd\u8bed\u4e2d\u7684\u6697\u793a\u3002\u6b27\u51ef\u8bf4\u743c\u6069\u6765\u81ea\u9634\u9b42\u57ce\uff0c\u6765\u5bfb\u627e\u67d0\u4ef6\u4e1c\u897f\uff0c\u4e9a\u5f53\u65af\u5c31\u80fd\u76f4\u63a5\u60f3\u5230\u662f\u6765\u627e\u4ec0\u4e48\u2014\u2014\u8fd9\u4e5f\u5c31\u662f\u8bf4\uff0c\u83f2\u5c14\u4f26\u5bb6\u65cf\u7684\u5b9d\u5e93\u91cc\uff0c\u786e\u5b9e\u662f\u6709\u67d0\u4ef6\u4e1c\u897f\uff0c\u662f\u548c\u9634\u9b42\u57ce\uff0c\u6216\u8005\u8bf4\u53e4\u8010\u745f\u745e\u5c14\u5e1d\u56fd\u6709\u5bc6\u5207\u5173\u7cfb\uff0c\u662f\u4ed6\u4eec\u7406\u5f53\u5fd7\u5728\u5fc5\u5f97\u7684\u5b9d\u7269\uff0c\u6240\u4ee5\u4e9a\u5f53\u65af\u624d\u4f1a\u5148\u5165\u4e3a\u4e3b\u5730\u76f4\u63a5\u8ba4\u5b9a\u3002</p><p>\u800c\u4e14\u4e9a\u5f53\u65af\u4f3c\u4e4e\u8bf4\u8fc7\u4e00\u53e5\u201c\u53cd\u6b63\u90a3\u4ef6\u4e1c\u897f\u5bf9\u6211\u4eec\u6ca1\u7528\u3002\u201d</p><p>\u4e16\u754c\u4e0a\u7684\u5b9d\u7269\u867d\u7136\u591a\uff0c\u4f46\u80fd\u540c\u65f6\u7b26\u5408\u8fd9\u4e9b\u6761\u4ef6\u7684\u2026\u2026\u4ee5\u743c\u6069\u7684\u89c1\u8bc6\uff0c\u4f3c\u4e4e\u5be5\u5be5\u65e0\u51e0\u3002</p><p>\u201c\u4e9a\u5f53\u65af\u624b\u91cc\u662f\u6709\u8010\u745f\u5377\u8f74\u5427\u3002\u201d\u743c\u6069\u6162\u6162\u95ee\u3002</p><p>\u6b27\u51ef\u9732\u51fa\u4e00\u4e1d\u7b11\u610f\uff0c\u201c\u4e0d\u662f\u4e9a\u5f53\u65af\u624b\u91cc\uff0c\u662f\u83f2\u5c14\u4f26\u5bb6\u65cf\u7684\u5b9d\u5e93\u91cc\uff0c\u53ea\u6709\u4e3b\u6bcd\u624d\u80fd\u6253\u5f00\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u786e\u5b9e\u662f\u6709\u4e00\u9875\u5377\u8f74\uff0c\u9057\u843d\u5230\u4e86\u77ee\u4eba\u624b\u91cc\uff0c\u540e\u6765\u77ee\u4eba\u738b\u56fd\u88ab\u5353\u5c14\u6bc1\u706d\uff0c\u8fd9\u9875\u5377\u8f74\u4e5f\u5c31\u968f\u540c\u5176\u4ed6\u5b9d\u7269\u4e00\u8d77\u88ab\u74dc\u7406\u5fb7\u65af\u57ce\u62a2\u5230\u3002\u6240\u4ee5\u4f60\u5148\u53bb\u4f0a\u5361\u6c99\u57ce\uff0c\u4e9a\u5f53\u65af\u8fd8\u4ee5\u4e3a\u76ee\u7684\u5c31\u662f\u4e3a\u4e86\u5bfb\u627e\u5377\u8f74\u3002\u201d</p><p>\u8010\u745f\u5377\u8f74\uff0c\u662f\u8010\u745f\u745e\u5c14\u5e1d\u56fd\u7684\u81f3\u5b9d\uff0c\u743c\u6069\u867d\u7136\u8fd8\u4e0d\u592a\u6e05\u695a\u5b83\u7684\u6765\u5386\uff0c\u4f46\u4e5f\u77e5\u9053\u91cd\u8981\u6027\u3002\u5965\u6c83\u624b\u91cc\u53ea\u6709\u51e0\u9875\u6b8b\u7ae0\uff0c\u90fd\u73cd\u800c\u91cd\u4e4b\u5730\u653e\u5728\u85cf\u5b9d\u5e93\u7684\u4e2d\u5fc3\uff0c\u5e03\u96f7\u7eb3\u65af\u8d39\u52b2\u529b\u6c14\u548c\u585e\u5c14\u7ed3\u76df\uff0c\u4e5f\u672a\u5c1d\u4e0d\u662f\u4e3a\u4e86\u4ece\u8428\u624e\u65af\u5766\u624b\u4e2d\u5f97\u5230\u90a3\u4e24\u9875\u5377\u8f74\u3002\u80fd\u8ba9\u4ed6\u4eec\u8fd9\u79cd\u5927\u4eba\u7269\u5982\u6b64\u770b\u91cd\uff0c\u5176\u4ef7\u503c\u4e5f\u5c31\u4e0d\u8a00\u800c\u55bb\u4e86\u3002\u66f4\u4f55\u51b5\uff0c\u5965\u6c83\u66fe\u7ecf\u63d0\u8fc7\uff0c\u6b63\u662f\u56e0\u4e3a\u6709\u4e86\u8010\u745f\u5377\u8f74\uff0c\u53e4\u8010\u745f\u5e1d\u56fd\u624d\u80fd\u521b\u5efa\u90a3\u6837\u8f89\u714c\u7684\u9b54\u6cd5\u6587\u660e\u3002\u53ea\u662f\u5377\u8f74\u540e\u6765\u6563\u5931\u9057\u843d\uff0c\u4e0d\u77e5\u6240\u7ec8\uff0c\u6ca1\u60f3\u5230\u8fd9\u5ea7\u5e7d\u6697\u5730\u57df\u7684\u5353\u5c14\u57ce\u5e02\u91cc\uff0c\u5c45\u7136\u4e5f\u6709\u4e00\u9875\u3002</p><p>\u5f97\u628a\u5b83\u5f04\u5230\u624b\uff0c\u743c\u6069\u6697\u60f3\u3002\u8fd9\u6837\u7684\u5b9d\u7269\uff0c\u5018\u82e5\u80fd\u636e\u4e3a\u5df1\u6709\u81ea\u7136\u6700\u597d\uff0c\u8fd9\u53ef\u662f\u5deb\u5e08\u7684\u81f3\u5b9d\u3002\u5c31\u7b97\u5b9e\u5728\u4e0d\u884c\uff0c\u4e0a\u4ea4\u7ed9\u9634\u9b42\u57ce\uff0c\u4e5f\u80fd\u6362\u6765\u4e0d\u83f2\u7684\u597d\u5904\u3002</p><p>\u65e2\u7136\u4e8b\u60c5\u5df2\u7ecf\u660e\u4e86\uff0c\u743c\u6069\u5fc3\u5934\u7684\u7591\u60d1\u4e5f\u57fa\u672c\u89e3\u5f00\u3002\u201c\u4e9a\u5f53\u65af\u7528\u4ec0\u4e48\u6362\u4e86\u4f60\u8fd9\u4e9b\u60c5\u62a5\uff1f\u201d\u4ed6\u95ee\u6b27\u51ef\uff0c\u201c\u522b\u544a\u8bc9\u6211\u662f\u514d\u8d39\u7684\u3002\u201d</p><p>\u201c\u600e\u4e48\u53ef\u80fd\uff0c\u201d\u6b27\u51ef\u4e0d\u5c51\uff0c\u201c\u751f\u610f\u5f52\u751f\u610f\uff0c\u89c4\u77e9\u662f\u89c4\u77e9\uff0c\u8c01\u4e5f\u4e0d\u80fd\u7834\u574f\u3002\u201d</p><p>\u201c\u90a3\u4f60\u8fd9\u6b21\u90fd\u6572\u8bc8\u5230\u4e86\u4ec0\u4e48\uff1f\u201d\u743c\u6069\u5012\u786e\u5b9e\u6709\u4e9b\u597d\u5947\u3002</p><p>\u6b27\u51ef\u62cd\u4e86\u62cd\u4ed6\u9762\u524d\u7684\u68cb\u76d8\uff0c\u201c\u5c31\u662f\u8fd9\u4e2a\u3002\u201d</p><p>\u201c\u8fd9\u4e2a\uff1f\u201d\u743c\u6069\u4e00\u6014\uff0c\u4ed6\u867d\u7136\u4e00\u8fdb\u623f\u95f4\u5c31\u53d1\u73b0\u6b27\u51ef\u5728\u6446\u5f04\u68cb\u5c40\uff0c\u4f46\u4e5f\u6ca1\u592a\u5728\u610f\uff0c\u5982\u4eca\u5fc3\u4e8b\u653e\u4e0b\uff0c\u5b9a\u775b\u518d\u770b\uff0c\u53d1\u73b0\u90a3\u662f\u4e00\u526f\u8428\u74e6\u68cb\u3002</p><p>\u8428\u74e6\u68cb\u662f\u4e00\u79cd\u5353\u5c14\u5f53\u4e2d\u6d41\u884c\u7684\u6218\u68cb\u6e38\u620f\uff0c\u662f\u6a21\u62df\u5353\u5c14\u7684\u5bb6\u65cf\u6218\u4e89\u800c\u8bbe\u8ba1\u51fa\u6765\u7684\u3002\u68cb\u76d8\u4e0a\u5212\u51fa\u683c\u5b50\uff0c\u7ea2\u9ed1\u4e24\u79cd\u989c\u8272\u7684\u68cb\u5b50\u5728\u91cc\u9762\u79fb\u52a8\uff0c\u5f53\u7136\u6ca1\u6709\u4ec0\u4e48\u56fd\u738b\u738b\u540e\u6216\u8005\u4e3b\u6559\u6218\u8f66\uff0c\u800c\u662f\u4e3b\u6bcd\u3001\u796d\u53f8\u3001\u5deb\u5e08\u3001\u6218\u58eb\u548c\u98df\u4eba\u9b54\u5974\u96b6\uff0c\u89c4\u5219\u5012\u4e5f\u5f88\u7b80\u5355\uff0c\u4f46\u6bd4\u8d77\u5176\u4ed6\u7684\u68cb\u7c7b\u6e38\u620f\uff0c\u5b83\u5e26\u6709\u5f88\u5f3a\u7684\u968f\u673a\u6027\u3002\u5728\u6bcf\u6b21\u884c\u52a8\u524d\uff0c\u73a9\u5bb6\u90fd\u4f1a\u6295\u63b7\u5370\u6709\u8718\u86db\u56fe\u6848\u7684\u9ab0\u5b50\uff0c\u5982\u679c\u8718\u86db\u6700\u7ec8\u671d\u5411\u67d0\u4f4d\u73a9\u5bb6\u2014\u2014\u8fd9\u8bf4\u660e\u4ed6\u53d7\u795e\u540e\u7737\u987e\uff0c\u90a3\u4e48\u4ed6\u5c31\u53ef\u4ee5\u79fb\u52a8\u5bf9\u624b\u7684\u67d0\u7c92\u68cb\u5b50\uff0c\u6253\u51fb\u5b83\u653b\u51fb\u8303\u56f4\u5185\u7684\u540c\u8272\u68cb\u5b50\u3002\u8fd9\u79cd\u89c4\u5219\u770b\u4f3c\u8352\u8bde\uff0c\u5176\u5b9e\u4e5f\u662f\u6df1\u6709\u5bd3\u610f\u7684\uff1a\u5373\u4f7f\u5728\u9762\u5bf9\u4e25\u91cd\u7684\u5916\u90e8\u5a01\u80c1\u65f6\uff0c\u5353\u5c14\u8fd8\u662f\u503e\u5411\u4e8e\u4f18\u5148\u6253\u51fb\u540c\u65cf\u3002</p><p>\u743c\u6069\u770b\u7740\u6b27\u51ef\u7684\u8fd9\u5e45\u8428\u74e6\u68cb\uff0c\u6ca1\u4ec0\u4e48\u7279\u522b\u4e4b\u5904\uff0c\u4e24\u6392\u68cb\u5b50\u6574\u9f50\u5730\u6446\u653e\u5728\u6708\u7eb9\u77f3\u5236\u6210\u7684\u68cb\u76d8\u4e0a\uff0c\u7ea2\u8272\u68cb\u5b50\u662f\u7ea2\u7389\u9ad3\u6240\u5236\uff0c\u9ed1\u8272\u68cb\u5b50\u5219\u662f\u9ed1\u66dc\u77f3\u6750\u6599\uff0c\u867d\u7136\u90fd\u662f\u9887\u73cd\u8d35\u7684\u5b9d\u77f3\uff0c\u4f46\u4e5f\u7b97\u4e0d\u5f97\u4ec0\u4e48\u3002</p><p>\u201c\u8fd9\u5e45\u68cb\u2026\u2026\u201d\u743c\u6069\u7ffb\u6765\u8986\u53bb\u770b\u4e86\u534a\u5929\uff0c\u5b9e\u5728\u770b\u4e0d\u51fa\u95e8\u9053\uff0c\u53ea\u597d\u865a\u5fc3\u8bf7\u6559\uff0c\u201c\u6709\u4ec0\u4e48\u7279\u522b\u7684\uff1f\u201d</p><p>\u6b27\u51ef\u968f\u624b\u62ff\u8d77\u4e00\u679a\u9ed1\u8272\u68cb\u5b50\uff0c\u4e22\u5728\u5730\u4e0a\uff0c\u5239\u90a3\u95f4\u6d53\u70df\u817e\u8d77\uff0c\u968f\u7740\u8f7b\u5fae\u7684\u7206\u70b8\u58f0\uff0c\u4e00\u53ea\u72f0\u72de\u7684\u98df\u4eba\u9b54\u6b66\u58eb\u51fa\u73b0\u5728\u623f\u95f4\u91cc\uff0c\u5b83\u989d\u751f\u767d\u89d2\uff0c\u8eab\u62ab\u91cd\u7532\uff0c\u624b\u4e2d\u63e1\u7740\u5de8\u5927\u7684\u72fc\u7259\u68d2\uff0c\u51f6\u795e\u6076\u715e\u4e00\u822c\u77aa\u7740\u743c\u6069\uff0c\u53ea\u662f\u4f3c\u4e4e\u53d7\u5230\u4ec0\u4e48\u7ea6\u675f\uff0c\u4e0d\u80fd\u52a8\u5f39\uff0c\u8fde\u58f0\u97f3\u90fd\u53d1\u4e0d\u51fa\u534a\u70b9</p><p>\u201c\u8fd9\u68cb\u76d8\u4e0a\u6709\u4e8c\u5341\u56db\u679a\u68cb\u5b50\uff0c\u5c31\u7b49\u4e8e\u662f\u4e8c\u5341\u56db\u4e2a\u5f3a\u529b\u62a4\u536b\uff0c\u201d\u6b27\u51ef\u62db\u4e86\u62db\u624b\uff0c\u90a3\u53ea\u98df\u4eba\u9b54\u53c8\u5316\u4f5c\u4e00\u56e2\u70df\u96fe\uff0c\u518d\u5ea6\u53d8\u6210\u68cb\u5b50\u56de\u5230\u68cb\u76d8\u4e0a\uff0c\u201c\u968f\u65f6\u5019\u547d\uff0c\u7edd\u5bf9\u5fe0\u8bda\uff0c\u65e0\u9700\u4f11\u5047\uff0c\u4e0d\u9886\u85aa\u6c34\u2026\u2026\u5982\u4f55\uff1f\u201d</p><p>\u201c\u8fd9\u2026\u2026\u8fd9\u662f\u600e\u4e48\u505a\u51fa\u6765\u7684\uff1f\u201d</p><p>\u201c\u8fd9\u4e2a\u6211\u600e\u4e48\u77e5\u9053\uff0c\u201d\u6b27\u51ef\u56de\u7b54\uff0c\u201c\u53cd\u6b63\u8fd9\u662f\u5deb\u5e08\u5b66\u9662\u91cc\u6700\u6709\u4ef7\u503c\u7684\u5b9d\u7269\uff0c\u636e\u8bf4\u662f\u5386\u53f2\u4e0a\u4e00\u4e2a\u53eb\u68b5\u94ce\u5c14\u7684\u5353\u5c14\u5927\u5deb\u5e08\u5236\u4f5c\u7684\u2026\u2026\u4f60\u4e0d\u662f\u70bc\u91d1\u5e08\u4e48\uff0c\u8fd9\u5e94\u8be5\u5c5e\u4e8e\u4f60\u7684\u4e13\u4e1a\u8303\u7574\u5427\uff0c\u600e\u4e48\u53cd\u800c\u8981\u95ee\u6211\u3002\u201d</p><p>\u201c\u5443\uff0c\u201d\u743c\u6069\u6709\u4e9b\u8138\u7ea2\uff0c\u7d22\u6027\u8f6c\u79fb\u4e86\u8bdd\u9898\uff0c\u201c\u5148\u4e0d\u7ba1\u8fd9\u526f\u68cb\u3002\u4f60\u8fd9\u6b21\u627e\u6211\u6765\uff0c\u603b\u4e0d\u4f1a\u662f\u7279\u5730\u4e3a\u4e86\u89e3\u91ca\u8fd9\u4e9b\u4e8b\u60c5\u7684\u5427\uff0c\u201d\u4ed6\u95ee\u6b27\u51ef\uff0c\u201c\u6709\u4f55\u6307\u6559\uff0c\u76f4\u8bf4\u5427\u3002\u201d</p><p>\u201c\u8fd9\u4e2a\u4e48\uff0c\u201d\u6b27\u51ef\u70b9\u70b9\u5934\uff0c\u201c\u786e\u5b9e\u662f\u6709\u4ef6\u4e8b\u60c5\u8981\u62dc\u6258\u4f60\u5462\u3002\u201d</p><p>\u201c\u4e0d\u4f1a\u5427\uff0c\u201d\u743c\u6069\u6709\u4e9b\u4e0d\u592a\u76f8\u4fe1\uff0c\u201c\u6709\u4ec0\u4e48\u4e8b\u60c5\u4f60\u90fd\u529e\u4e0d\u5230\u7684\uff0c\u8fd8\u9700\u8981\u6211\u5e2e\u5fd9\uff1f\u201d</p><p>\u6b27\u51ef\u53f9\u6c14\uff0c\u201c\u6ca1\u529e\u6cd5\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u627e\u522b\u4eba\u6ca1\u7528\uff0c\u53ea\u80fd\u627e\u4f60\u4e86\u3002\u201d</p><p>\u201c\u8bf4\u8bf4\u770b\uff0c\u201d\u743c\u6069\u4e5f\u6709\u4e9b\u597d\u5947\u8d77\u6765\uff0c\u201c\u4e0d\u8fc7\u4e8b\u5148\u8bf4\u660e\uff0c\u6211\u53ef\u6ca1\u7b54\u5e94\u4ec0\u4e48\u3002\u201d</p><p>\u201c\u7b80\u5355\uff0c\u201d\u6b27\u51ef\u556a\u5730\u6253\u4e86\u4e2a\u54cd\u6307\uff0c\u201c\u4f60\u6253\u7b97\u505a\u4ec0\u4e48\uff0c\u4e9a\u5f53\u65af\u53c8\u5728\u60f3\u4ec0\u4e48\uff0c\u6211\u5927\u81f4\u4e5f\u90fd\u77e5\u9053\u3002\u4e0d\u8fc7\u743c\u6069\uff0c\u201d\u4ed6\u6162\u6162\u8bf4\uff0c\u201c\u6211\u5efa\u8bae\uff0c\u4e8b\u60c5\u4e0d\u8981\u505a\u5f97\u592a\u7edd\u3002\u201d</p><p>\u743c\u6069\u76b1\u7709\uff0c\u201c\u4f60\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\u3002\u201d</p><p>\u201c\u5f88\u7b80\u5355\uff0c\u201d\u6b27\u51ef\u8bf4\uff0c\u201c\u6bd4\u5982\u8fd9\u57ce\u5e02\u91cc\u8fd9\u4e48\u591a\u7267\u5e08\uff0c\u6b7b\u4e0a\u5341\u4e2a\u516b\u4e2a\u65e0\u6240\u8c13\uff0c\u51e0\u5341\u4e0a\u767e\u4e5f\u6ca1\u5173\u7cfb\uff0c\u4f46\u522b\u8d76\u5c3d\u6740\u7edd\u4e3a\u597d\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u7559\u70b9\u4f59\u5730\u561b\uff0c\u201d\u6b27\u51ef\u8bf4\uff0c\u201c\u4e00\u4e2a\u6700\u7b80\u5355\u7684\u9053\u7406\uff1a\u6bc1\u706d\u5bb9\u6613\uff0c\u5efa\u7acb\u5374\u96be\u3002\u8fd9\u5ea7\u57ce\u5e02\u5386\u7ecf\u4e07\u5e74\uff0c\u624d\u6709\u5982\u4eca\u7684\u89c4\u6a21\uff0c\u8fd9\u4e0d\u662f\u4e00\u671d\u4e00\u5915\u4e4b\u529f\u3002\u5982\u679c\u4f60\u7559\u7740\u5b83\uff0c\u4ee5\u540e\u6216\u8bb8\u8fd8\u6709\u673a\u4f1a\u964d\u4f0f\u5b83\uff0c\u62e5\u6709\u5b83\uff0c\u7edf\u6cbb\u5b83\uff0c\u81f3\u5c11\u4ece\u5b83\u8eab\u4e0a\u5f97\u5230\u66f4\u591a\u7684\u597d\u5904\u3002\u4f46\u5982\u679c\u4f60\u6bc1\u4e86\u5b83\uff0c\u90a3\u5c31\u4ec0\u4e48\u90fd\u6ca1\u4e86\u3002\u201d</p><p>\u743c\u6069\u5927\u7b11\u8d77\u6765\uff0c\u201c\u5c31\u56e0\u4e3a\u5c06\u6765\u7684\u4e00\u70b9\u5fae\u5f31\u5230\u51e0\u4e4e\u53ef\u4ee5\u5ffd\u7565\u4e0d\u8ba1\u7684\u53ef\u80fd\u6027\uff0c\u4f60\u5c31\u8981\u6211\u73b0\u5728\u7ed9\u81ea\u5df1\u7559\u4e0b\u654c\u4eba\u548c\u9ebb\u70e6\u2014\u2014\u8fd9\u9053\u7406\u600e\u4e48\u4e5f\u8bf4\u4e0d\u901a\u5427\u3002\u201d</p><p>\u201c\u7684\u786e\u8bf4\u4e0d\u901a\uff0c\u201d\u6b27\u51ef\u627f\u8ba4\uff0c\u201c\u4e0d\u8fc7\u5982\u679c\u4f60\u77e5\u9053\u4e0b\u9762\u8fd9\u4e2a\u6d88\u606f\uff0c\u6216\u8bb8\u5c31\u4f1a\u8003\u8651\u6211\u7684\u5efa\u8bae\u4e86\u2014\u2014\u653e\u5fc3\uff0c\u8fd9\u6b21\u662f\u514d\u8d39\u7684\u3002\u201d</p><p>\u201c\u4ec0\u4e48\u6d88\u606f\uff1f\u201d</p><p>\u201c\u7f57\u4e1d\u5e76\u6ca1\u6709\u6b7b\uff0c\u201d\u6b27\u51ef\u8bf4\uff0c\u8f7b\u8f7b\u79fb\u52a8\u4e86\u4e00\u4e0b\u68cb\u5b50\uff0c\u201c\u800c\u4e14\uff0c\u5979\u5f88\u5feb\u5c31\u4f1a\u56de\u6765\u4e86\u3002\u201d</p></article>"}}}]);
