(window.webpackJsonp=window.webpackJsonp||[]).push([[274],{296:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u56db\u5341\u4e94\u8282 \u5192\u72af\u5973\u796d\u53f8",content:"<article><h2>\u7b2c\u56db\u5341\u4e94\u8282 \u5192\u72af\u5973\u796d\u53f8</h2><p>\u201c\u5979\u662f\u4eca\u5e74\u7684\u6bd5\u4e1a\u751f\u7b2c\u4e00\u540d\u3002\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u8f7b\u58f0\u89e3\u91ca\u3002</p><p>\u90a3\u4e5f\u5c31\u662f\u8bf4\uff0c\u51c6\u5907\u53ec\u5524\u8ff7\u8bf1\u9b54\u524d\u6765\u548c\u5979\u4ea4\u5408\u4e86\u3002\u5982\u679c\u8fd0\u6c14\u597d\uff0c\u5c31\u6709\u53ef\u80fd\u50cf\u4e3b\u6bcd\u59ae\u745f\u4e00\u6837\uff0c\u8bde\u4e0b\u9b54\u88d4\u5353\u5c14\uff0c\u8fd9\u88ab\u8ba4\u4e3a\u662f\u795e\u540e\u7684\u795d\u798f\u3002</p><p>\u7136\u800c\uff0c\u62e5\u6709\u9b54\u88d4\u5353\u5c14\u7684\u745e\u8d39\u5fb7\u5bb6\u65cf\uff0c\u4e0d\u662f\u521a\u521a\u88ab\u62b9\u53bb\u4e86\u5b58\u5728\u7684\u75d5\u8ff9\u4e48\uff0c\u8fde\u4e3b\u6bcd\u90fd\u9001\u4e0a\u4e86\u796d\u53f0\uff0c\u8fd9\u795e\u540e\u7684\u795d\u798f\uff0c\u672a\u514d\u592a\u8fc7\u8bbd\u523a\u4e86\u5427\u3002</p><p>\u743c\u6069\u4e0d\u52a8\u58f0\u8272\u5730\u60f3\u7740\uff0c\u53d1\u73b0\u8d5e\u7f8e\u86db\u540e\u7684\u7977\u6587\u518d\u6b21\u54cd\u4eae\u8d77\u6765\uff0c\u5973\u796d\u53f8\u4eec\u9f50\u58f0\u5ff5\u8bf5\uff0c\u793c\u5802\u56db\u5468\u7684\u516b\u4e2a\u706b\u7089\u70c8\u706b\u718a\u718a\uff0c\u7130\u5c16\u51e0\u4e4e\u707c\u70e7\u5230\u4e86\u5929\u82b1\u677f\u3002\u4f46\u6b63\u4e2d\u7684\u706b\u7089\u4f9d\u65e7\u4fdd\u6301\u539f\u72b6\uff0c\u6ca1\u6709\u53cd\u5e94\u3002</p><p>\u6b21\u5e2d\u6559\u5b98\u535a\u514b\u672c\u53d6\u8fc7\u4e00\u672c\u539a\u91cd\u7684\u4e66\uff0c\u5feb\u901f\u7ffb\u9605\u5230\u5176\u4e2d\u4e00\u9875\uff0c\u7136\u540e\u9012\u7ed9\u8d64\u88f8\u7684\u5973\u5b66\u751f\u3002\u743c\u6069\u8fdc\u8fdc\u671b\u53bb\uff0c\u53ea\u770b\u89c1\u90a3\u4e66\u9875\u4e0a\u6709\u51e0\u5341\u4e2a\u9ed1\u8272\u7684\u5355\u8bcd\u98d8\u6d6e\u79fb\u52a8\uff0c\u4f46\u770b\u4e0d\u6e05\u5230\u5e95\u662f\u4ec0\u4e48\u3002\u5b66\u751f\u63a5\u8fc7\u4e66\uff0c\u4f38\u624b\u968f\u610f\u4e00\u6307\uff0c\u70b9\u4e2d\u4e86\u67d0\u4e2a\u5355\u8bcd\u3002</p><p>\u201c\u963f\u82e5\u83ab\u683c\u65af\u5fd2\uff01\u201d</p><p>\u6559\u5b98\u5927\u58f0\u5ff5\u51fa\u6765\uff0c\u8fd9\u663e\u7136\u662f\u4e2a\u540d\u5b57\uff0c\u5b66\u751f\u8ddf\u7740\u91cd\u590d\uff0c\u7136\u540e\u662f\u5728\u573a\u6240\u6709\u7684\u7267\u5e08\u540c\u58f0\u91cd\u590d\u3002\u706b\u7089\u4e2d\u7684\u9b54\u6cd5\u70c8\u7130\u8f70\u7136\u817e\u8d77\uff0c\u989c\u8272\u81ea\u7ea2\u8f6c\u6697\u9ec4\uff0c\u7070\u767d\u8272\u70df\u96fe\u5f25\u6f2b\u51fa\u6765\uff0c\u77ac\u95f4\u7b3c\u7f69\u4e86\u6574\u4e2a\u793c\u5802\uff0c\u4f46\u4e1d\u6beb\u4e0d\u5f71\u54cd\u89c6\u89c9\u3002\u4e00\u80a1\u9999\u751c\u4e2d\u5e26\u7740\u8f7b\u5fae\u9ebb\u611f\u7684\u6c14\u5473\u7f13\u7f13\u6563\u53d1\uff0c\u8ba9\u5728\u573a\u7684\u6240\u6709\u4eba\u90fd\u6709\u4e00\u79cd\u98d8\u98d8\u6b32\u9189\u7684\u611f\u89c9\uff0c\u4eff\u4f5b\u50cf\u662f\u5438\u98df\u4e86\u9ed1\u83b2\u82b1\u7c89\u672b\uff08\u4e00\u79cd\u6bd2\u54c1\uff0c\u5deb\u5e08\u4e2d\u5c24\u5176\u6d41\u884c\uff09\u3002\u743c\u6069\u52aa\u529b\u4fdd\u6301\u7740\u5934\u8111\u6e05\u660e\uff0c\u76ef\u7740\u706b\u7089\uff0c\u4ed6\u611f\u89c9\u70c8\u7130\u548c\u70df\u96fe\u4e2d\u6709\u4ec0\u4e48\u4e1c\u897f\u5728\u5f71\u5f71\u7ef0\u7ef0\u5730\u6210\u578b\u3002</p><p>\u201c\u963f\u82e5\u83ab\u683c\u65af\u5fd2\uff01\u201d</p><p>\u5b66\u751f\u5f20\u5f00\u53cc\u81c2\uff0c\u8ba9\u8d64\u88f8\u7684\u8eab\u4f53\u5b8c\u5168\u9762\u5411\u706b\u7089\uff0c\u4e00\u904d\u53c8\u4e00\u904d\u5730\u5ff5\u8bf5\u7740\u8fd9\u4e2a\u540d\u5b57\uff0c\u743c\u6069\u6b64\u65f6\u5df2\u7ecf\u660e\u767d\u8fc7\u6765\u3002\u90a3\u672c\u4e66\u4e0a\u8bb0\u8f7d\u7684\uff0c\u5fc5\u5b9a\u662f\u7f57\u4e1d\u624b\u4e0b\u5404\u4e2a\u8ff7\u8bf1\u9b54\u7684\u771f\u540d\uff0c\u800c\u5b66\u751f\u968f\u673a\u9009\u62e9\u5176\u4e00\u53ec\u5524\u3002\u706b\u7130\u66f4\u52a0\u731b\u70c8\uff0c\u5c06\u706b\u7089\u672c\u8eab\u5b8c\u5168\u541e\u566c\uff0c\u5149\u7ebf\u4e5f\u8d8a\u53d1\u660e\u4eae\u523a\u773c\uff0c\u743c\u6069\u5012\u8fd8\u7f62\u4e86\uff0c\u5176\u4ed6\u5353\u5c14\u90fd\u5df2\u7ecf\u6709\u4e9b\u627f\u53d7\u4e0d\u4f4f\uff0c\u53cc\u76ee\u6d41\u6cea\uff0c\u4f46\u796d\u53f8\u4eec\u4f9d\u65e7\u9ad8\u58f0\u5ff5\u8bf5\u7977\u6587\uff0c\u4e00\u523b\u4e0d\u505c\u3002\u7ef4\u5eb7\u5c3c\u4e9a\u4e5f\u53c2\u4e0e\u5176\u4e2d\uff0c\u5979\u7684\u8138\u4e0a\u795e\u60c5\u5e84\u4e25\u800c\u8654\u8bda\uff0c\u548c\u5e73\u65f6\u6240\u89c1\u5b8c\u5168\u4e0d\u540c\u3002</p><p>\u743c\u6069\u73af\u987e\u56db\u5468\uff0c\u53d1\u73b0\u90a3\u4e9b\u6765\u81ea\u53e6\u5916\u4e24\u5ea7\u5b66\u9662\u7684\u6bd5\u4e1a\u751f\u4eec\u540c\u6837\u4e5f\u52a0\u5165\u4e86\u7948\u7977\u7684\u884c\u5217\uff0c\u4ed6\u4eec\u7684\u795e\u60c5\u8654\u8bda\uff0c\u4f46\u5939\u6742\u7740\u4e00\u4e9b\u5e0c\u5180\u548c\u6e34\u671b\u3002\u201c\u8fd9\u4e48\u5174\u81f4\u52c3\u52c3\uff0c\u662f\u7b49\u7740\u770b\u73b0\u573aA\u7247\u4e48\uff1f\u201d\u4ed6\u5600\u5495\u7740\uff0c\u4f46\u4e00\u53ea\u8ff7\u8bf1\u9b54\u548c\u4e00\u4e2a\u5353\u5c14\uff0c\u8fd9\u5dee\u4e0d\u591a\u5e94\u8be5\u5f52\u7c7b\u5230\u4eba\u517d\u7c7b\u578b\u4e86\uff0c\u5168\u65e0\u534a\u70b9\u7f8e\u611f\u53ef\u8a00\uff0c\u6709\u4ec0\u4e48\u597d\u770b\u7684\u3002</p><p>\u70df\u96fe\u5f25\u6f2b\uff0c\u8d8a\u53d1\u6d53\u5bc6\uff0c\u90a3\u79cd\u4ee4\u4eba\u8f7b\u98d8\u98d8\u7684\u9ebb\u9189\u611f\u4e5f\u8d8a\u6765\u8d8a\u5f3a\uff0c\u4eff\u4f5b\u4ece\u53e3\u9f3b\u548c\u6bcf\u4e2a\u6bdb\u5b54\u6e17\u900f\u8fdb\u8eab\u4f53\u3002\u793c\u5802\u91cc\u7684\u6e29\u5ea6\u5feb\u901f\u5347\u9ad8\uff0c\u5728\u5353\u5c14\u7684\u7ea2\u5916\u89c6\u89c9\u4e2d\u6b64\u65f6\u5468\u56f4\u65e9\u5c31\u9c9c\u7ea2\u4e00\u7247\uff0c\u51e0\u4e4e\u90fd\u8981\u5206\u8fa8\u4e0d\u51fa\u4eba\u5f71\uff0c\u743c\u6069\u4e5f\u5fcd\u4e0d\u4f4f\u6709\u60f3\u8131\u8863\u670d\u7684\u51b2\u52a8\uff0c\u7136\u540e\u4ed6\u9887\u6709\u4e9b\u60ca\u8bb6\u5730\u53d1\u73b0\u4e0d\u5c11\u7267\u5e08\u90fd\u5df2\u7ecf\u5f00\u59cb\u8fd9\u4e48\u505a\u4e86\u3002\u796d\u53f8\u4eec\u53d6\u4e0b\u534e\u4e3d\u7684\u5934\u76d4\u6446\u653e\u5728\u706b\u7089\u8fb9\u6cbf\uff0c\u62c9\u5f00\u5e26\u5b50\uff0c\u8ba9\u4e1d\u7ec7\u957f\u888d\u6ed1\u843d\u5730\u9762\uff0c\u9732\u51fa\u88f8\u4f53\uff0c\u6676\u83b9\u7684\u6c57\u73e0\u4ece\u8eab\u4e0a\u6eda\u843d\uff0c\u8d8a\u53d1\u663e\u5f97\u808c\u80a4\u5ae9\u6ed1\u7ec6\u81f4\uff0c\u72b9\u5982\u4e4c\u9ed1\u7684\u7ef8\u7f0e\uff0c\u6563\u53d1\u7740\u8bf1\u4eba\u7684\u5149\u6cfd\u3002</p><p>\u743c\u6069\u54bd\u4e86\u53e3\u53e3\u6c34\uff0c\u6216\u8bb8\u662f\u56e0\u4e3a\u623f\u95f4\u91cc\u592a\u70ed\u4e86\u3002</p><p>\u8033\u8fb9\u7684\u7267\u5e08\u96c6\u4f53\u7948\u7977\u58f0\u7f25\u7f08\u800c\u7a7a\u7075\uff0c\u4eff\u4f5b\u6e10\u6e10\u8fdc\u53bb\uff0c\u552f\u6709\u5973\u5b66\u751f\u5ff5\u8bf5\u6076\u9b54\u771f\u540d\u7684\u58f0\u97f3\u66f4\u52a0\u6e05\u695a\u800c\u6025\u5207\uff0c\u201c\u963f\u82e5\u83ab\u683c\u65af\u5fd2\uff01\u201d\u5979\u5927\u58f0\u547c\u5524\u7740\uff0c\u706b\u7089\u4e2d\u7684\u70c8\u7130\u718a\u718a\u7ffb\u817e\uff0c\u51e0\u4e4e\u8981\u707c\u70e7\u5230\u5979\u7684\u8eab\u4f53\uff0c\u5374\u534a\u6b65\u90fd\u4e0d\u9000\u907f\u3002</p><p>\u706b\u7130\u7ffb\u817e\u7740\uff0c\u8282\u8282\u5347\u9ad8\uff0c\u5448\u87ba\u65cb\u72b6\u626d\u66f2\uff0c\u5411\u56db\u5468\u8ff8\u5c04\u7740\u706b\u661f\uff0c\u4f46\u65e0\u4eba\u8eb2\u907f\u3002\u6700\u7ec8\uff0c\u4f34\u968f\u7740\u9065\u8fdc\u800c\u4f4e\u6c89\u7684\u5636\u543c\uff0c\u4e00\u53ea\u5de8\u5927\u7684\u3001\u8c7a\u72d7\u4f3c\u7684\u8111\u888b\u4ece\u706b\u7130\u4e2d\u94bb\u51fa\u6765\uff0c\u7d27\u63a5\u7740\u51fa\u73b0\u7684\u662f\u56db\u53ea\u722a\u5b50\uff0c\u4e0a\u9762\u4e24\u53ea\u8f83\u4e3a\u7c97\u58ee\uff0c\u672b\u7aef\u662f\u8783\u87f9\u822c\u7684\u94b3\u5b50\uff0c\u4e0b\u9762\u4e24\u53ea\u5219\u8f83\u4e3a\u7626\u5c0f\uff0c\u8fd1\u4f3c\u4eba\u624b\uff0c\u8fd9\u6b63\u662f\u8ff7\u8bf1\u9b54\uff0c\u548c\u5f53\u65e5\u5728\u5de8\u9b54\u5c71\u8109\u88ab\u743c\u6069\u7838\u6b7b\u7684\u662f\u540c\u7c7b\u3002</p><p>\u8ff7\u8bf1\u9b54\u7684\u4e0a\u534a\u8eab\u7a7f\u51fa\u706b\u7130\uff0c\u7f13\u7f13\u6253\u91cf\u7740\u793c\u5802\u91cc\u6240\u6709\u7684\u4eba\uff0c\u7136\u540e\u5c06\u76ee\u5149\u505c\u7559\u5728\u65c1\u8fb9\u7684\u5973\u5b66\u751f\u8eab\u4e0a\uff0c\u5b83\u8eab\u4e0a\u6563\u53d1\u51fa\u7684\u5f3a\u5927\u5a01\u538b\u4ee4\u540e\u8005\u5fc3\u60ca\u80c6\u6218\uff0c\u4e00\u65f6\u5fd8\u4e86\u7ee7\u7eed\u4eea\u5f0f\u3002\u5728\u8eab\u4fa7\u7684\u6b21\u5e2d\u6559\u5b98\u62d4\u51fa\u86c7\u97ad\uff0c\u5728\u5979\u80cc\u4e0a\u91cd\u91cd\u62bd\u4e86\u4e00\u4e0b\uff0c\u75bc\u75db\u8ba9\u5b66\u751f\u56de\u8fc7\u795e\u6765\uff0c\u5979\u614c\u5fd9\u4e3e\u8d77\u53cc\u624b\uff0c\u7528\u66f4\u54cd\u4eae\u3001\u66f4\u6025\u5207\uff0c\u800c\u4e14\u5145\u6ee1\u8bf1\u60d1\u7684\u58f0\u97f3\u547c\u5524\u7740\u6076\u9b54\u7684\u540d\u5b57\u3002</p><p>\u8ff7\u8bf1\u9b54\u5b8c\u5168\u8e0f\u51fa\u706b\u7130\uff0c\u6bd4\u743c\u6069\u4e0a\u6b21\u89c1\u5230\u7684\u90a3\u53ea\u66f4\u8981\u9ad8\u5927\uff0c\u6c79\u6d8c\u6f8e\u6e43\u7684\u90aa\u6076\u6c14\u606f\u50cf\u6f6e\u6c34\u4e00\u6837\u4ee5\u5b83\u4e3a\u4e2d\u5fc3\u6269\u6563\u5f00\u6765\uff0c\u8fd9\u663e\u7136\u4e0d\u662f\u4e2a\u6295\u5f71\uff0c\u800c\u662f\u672c\u4f53\u3002\u5973\u5b66\u751f\u505c\u6b62\u547c\u5524\uff0c\u4ef0\u89c6\u7740\u6076\u9b54\uff0c\u5979\u770b\u8d77\u6765\u65e2\u5174\u594b\u53c8\u7d27\u5f20\uff0c\u8eab\u4f53\u5728\u4e0d\u7531\u81ea\u4e3b\u5730\u8f7b\u5fae\u98a4\u6296\uff0c\u4e24\u6761\u5e76\u62e2\u7684\u957f\u817f\u51e0\u4e4e\u8981\u7ad9\u7acb\u4e0d\u4f4f\uff0c\u7136\u540e\u5979\u88ab\u8ff7\u8bf1\u9b54\u7528\u4e24\u53ea\u8f83\u5c0f\u7684\u722a\u5b50\u62b1\u4e86\u8d77\u6765\u3002\u8fd9\u4e00\u6076\u9b54\u4e00\u5353\u5c14\u7684\u4f53\u578b\u662f\u5982\u6b64\u7684\u76f8\u5dee\u60ac\u6b8a\uff0c\u4fbf\u5982\u5de8\u4eba\u548c\u513f\u7ae5\uff0c\u4ee5\u81f3\u4e8e\u743c\u6069\u6000\u7591\u63a5\u4e0b\u6765\u5c31\u4f1a\u6709\u547d\u6848\u53d1\u751f\u3002</p><p>\u5145\u6ee1\u75db\u695a\u7684\u60e8\u53eb\u4ece\u5973\u5b66\u751f\u53e3\u4e2d\u53d1\u51fa\uff0c\u9c9c\u8840\u4ece\u5979\u7684\u4e0b\u8eab\u6ea2\u51fa\uff0c\u987a\u7740\u5927\u817f\u6d41\u6dcc\u4e0b\u6765\u3002\u796d\u53f8\u5b66\u9662\u7684\u7b2c\u4e00\u540d\u6bd5\u4e1a\u751f\uff0c\u81ea\u7136\u4e0d\u53ef\u80fd\u662f\u5904\u5973\uff0c\u4f46\u8ff7\u8bf1\u9b54\u8fd9\u6837\u5e9e\u5927\u7684\u4ea4\u5408\u5bf9\u8c61\u663e\u7136\u8fd8\u662f\u521d\u6b21\u4f53\u9a8c\uff0c\u5979\u7684\u8eab\u4f53\u4eff\u4f5b\u90fd\u88ab\u6495\u88c2\u5f00\u3002\u5c3d\u7ba1\u5982\u6b64\uff0c\u5e76\u6ca1\u6709\u4eba\u4e0a\u53bb\u6551\u63f4\uff0c\u7267\u5e08\u4eec\u5ff5\u8bf5\u7977\u6587\u7684\u58f0\u97f3\u518d\u5ea6\u54cd\u8d77\uff0c\u4e2d\u592e\u706b\u7089\u4e2d\u7684\u70c8\u7130\u7f13\u7f13\u4f4e\u77ee\u4e0b\u53bb\uff0c\u6e10\u6e10\u7184\u706d\uff0c\u800c\u5468\u56f4\u7684\u516b\u4e2a\u706b\u7089\u4e2d\u5219\u6301\u7eed\u4e0d\u65ad\u5730\u5192\u51fa\u7c89\u7ea2\u8272\u7684\u96fe\u6c14\uff0c\u5c06\u6574\u4e2a\u793c\u5802\u53d8\u6210\u4e86\u6726\u80e7\u7684\u4e16\u754c\u3002</p><p>\u743c\u6069\u6e38\u76ee\u56db\u987e\uff0c\u53d1\u73b0\u4e0d\u77e5\u4f55\u65f6\u9664\u4ed6\u4e4b\u5916\u7684\u6240\u6709\u4eba\u2014\u2014\u5305\u62ec\u7267\u5e08\u3001\u796d\u53f8\u5b66\u9662\u7684\u5b66\u751f\u4ee5\u53ca\u53e6\u5916\u4e24\u5ea7\u5b66\u9662\u7684\u6bd5\u4e1a\u751f\u4eec\u2014\u2014\u90fd\u5df2\u7ecf\u4e00\u4e1d\u4e0d\u6302\uff0c\u6210\u53cc\u6210\u5bf9\u5730\u7ede\u7f20\u5728\u4e00\u8d77\uff0c\u751a\u81f3\u8fd8\u6709\u4e09\u4eba\u6216\u8005\u56db\u4eba\u7684\u6df7\u6218\u3002\u4ed6\u5f00\u59cb\u660e\u767d\u4e3a\u4ec0\u4e48\u8fd9\u4e9b\u5b66\u751f\u4f1a\u5982\u6b64\u5174\u594b\uff0c\u5e76\u975e\u662f\u56e0\u4e3a\u6709\u673a\u4f1a\u73b0\u573a\u89c2\u6469\uff0c\u800c\u662f\u81ea\u5df1\u4e5f\u53ef\u4ee5\u4e0b\u573a\u53c2\u4e0e\u3002\u7ef4\u5eb7\u5c3c\u4e9a\u80fd\u7528\u4e00\u53e5\u8bdd\u963b\u6b62\u4e86\u8299\u857e\u72c4\uff0c\u539f\u56e0\u5927\u7ea6\u4e5f\u5c31\u5728\u6b64\u4e86\u3002</p><p>\u8ff7\u8bf1\u9b54\u5e9e\u5927\u7684\u8eab\u5f71\u5728\u70df\u96fe\u4e2d\u82e5\u9690\u82e5\u73b0\uff0c\u5b83\u6000\u4e2d\u7684\u5973\u5b66\u751f\u4f9d\u65e7\u53d1\u51fa\u75db\u82e6\u7684\u54c0\u9e23\uff0c\u4f46\u6bd4\u8d77\u5f00\u59cb\u65f6\u5df2\u7ecf\u591a\u4e86\u51e0\u4e1d\u6109\u60a6\u7684\u6ecb\u5473\u3002\u743c\u6069\u65ad\u5b9a\u8fd9\u706b\u7089\u4e2d\u6563\u53d1\u51fa\u6765\u7684\u7c89\u7ea2\u8272\u70df\u96fe\u6709\u8ff7\u60d1\u5fc3\u667a\u3001\u50ac\u53d1\u60c5\u6b32\u7684\u4f5c\u7528\uff0c\u800c\u4f5c\u4e3a\u5deb\u5e08\uff0c\u4ed6\u672c\u80fd\u5730\u5fcc\u8bb3\u5934\u8111\u4e27\u5931\u6e05\u660e\uff0c\u8fd9\u662f\u5371\u9669\u7684\u5f81\u5146\u3002\u4ed6\u6b63\u51c6\u5907\u79bb\u5f00\uff0c\u4e00\u53cc\u624b\u81c2\u4ece\u80cc\u540e\u73af\u62b1\u8fc7\u6765\uff0c\u8f7b\u8f7b\u6402\u4f4f\u8170\uff0c\u7d27\u63a5\u7740\u6574\u4e2a\u4eba\u8d34\u4e0a\u4ed6\u7684\u80cc\uff0c\u9694\u7740\u5deb\u5e08\u888d\u743c\u6069\u751a\u81f3\u53ef\u4ee5\u611f\u89c9\u5230\u90a3\u4e24\u9897\u786c\u786c\u7684\u51f8\u8d77\u3002\u4ed6\u5fae\u5fae\u56de\u5934\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u6b63\u5c06\u8138\u8d34\u8fc7\u6765\uff0c\u5979\u7684\u773c\u4e2d\u6cdb\u7740\u6e7f\u6da6\u7684\u96fe\u6c14\uff0c\u53e3\u4e2d\u547c\u5438\u6025\u4fc3\u800c\u6eda\u70eb\uff0c\u201c\u5728\u6bd5\u4e1a\u5178\u793c\u4e0a\u4e34\u9635\u8131\u9003\uff0c\u4f1a\u88ab\u8ba4\u4e3a\u662f\u5bf9\u795e\u540e\u7684\u4eb5\u6e0e\u5462\u3002\u201d\u5979\u4f4e\u4f4e\u5730\u8bf4\u3002</p><p>\u201c\u6211\u4e0d\u5728\u4e4e\u4eb5\u6e0e\u795e\u540e\uff0c\u201d\u743c\u6069\u4e5f\u8f7b\u58f0\u56de\u7b54\uff0c\u201c\u4e0d\u8fc7\u62d2\u7edd\u7f8e\u4e3d\u7684\u5c0f\u59d0\u90a3\u5c31\u592a\u8fc7\u65e0\u793c\u4e86\u3002\u201d</p><p>\u4ed6\u8f6c\u8fc7\u8eab\uff0c\u5c06\u7ef4\u5eb7\u5c3c\u4e9a\u62e6\u8170\u62b1\u4e86\u8d77\u6765\uff0c\u7a0d\u7a0d\u671d\u56db\u9762\u4e00\u770b\uff0c\u5927\u8e0f\u6b65\u5411\u793c\u5802\u5916\u8d70\u53bb\u3002\u96fe\u6c14\u5f25\u6f2b\uff0c\u743c\u6069\u4e5f\u770b\u4e0d\u6e05\u695a\uff0c\u4e00\u8def\u4e0a\u611f\u89c9\u8e29\u5230\u4e86\u597d\u51e0\u5bf9\u6b63\u5728\u6b22\u597d\u7684\u60c5\u4eba\uff0c\u5e78\u597d\u6ca1\u6709\u8c01\u6765\u627e\u4ed6\u7b97\u8d26\uff0c\u66f4\u65e0\u4eba\u963b\u62e6\u3002\u987a\u7740\u6765\u65f6\u7684\u8def\u5f84\uff0c\u4ed6\u8d70\u51fa\u4e86\u793c\u5802\uff0c\u4e00\u8def\u7a7f\u8fc7\u957f\u5eca\u8d70\u5230\u5916\u9762\u7684\u9732\u5929\u5e73\u53f0\u4e0a\uff0c\u5c06\u90a3\u4e9b\u70bd\u70ed\u7684\u5598\u606f\u3001\u4f4e\u6c89\u7684\u5636\u543c\u548c\u65ad\u65ad\u7eed\u7eed\u7684\u60e8\u53eb\u58f0\u8fdc\u8fdc\u629b\u4e4b\u8111\u540e\u3002</p><p>\u201c\u770b\u8d77\u6765\u4f60\u6bd4\u8f83\u5bb3\u7f9e\u3002\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u8f7b\u58f0\u7b11\u7740\u8bc4\u4ef7\u3002</p><p>\u201c\u51c6\u786e\u5730\u8bf4\uff0c\u6211\u559c\u6b22\u6709\u5355\u72ec\u7684\u7a7a\u95f4\u548c\u5145\u8db3\u7684\u65f6\u95f4\uff0c\u4ee5\u4fbf\u4e8e\u6211\u4eec\u80fd\u591f\u4ece\u5bb9\u4e0d\u8feb\u3002\u201d</p><p>\u4ed6\u628a\u7ef4\u5eb7\u5c3c\u4e9a\u4ece\u6000\u4e2d\u653e\u4e0b\u5730\u6765\uff0c\u770b\u4e86\u4e00\u773c\u56db\u5468\uff0c\u8fd9\u91cc\u5f88\u5b89\u9759\uff0c\u80fd\u591f\u6709\u8d44\u683c\u8fdb\u5165\u4e3b\u5efa\u7b51\u7684\u4eba\u90fd\u5728\u793c\u5802\u4e2d\uff0c\u5176\u4f59\u5b66\u751f\u5219\u4f4f\u5728\u4e0b\u9762\u7684\u516b\u6761\u86db\u817f\u4e2d\uff0c\u4e0d\u6562\u4e0a\u6765\u3002\u796d\u53f8\u5b66\u9662\u5730\u52bf\u5f88\u9ad8\uff0c\u4ece\u6b64\u5904\u53ef\u4ee5\u4fef\u89c6\u6574\u4e2a\u57ce\u5e02\uff0c\u4e00\u9635\u591c\u98ce\u5439\u6765\uff0c\u4ed6\u611f\u89c9\u5934\u8111\u53d8\u5f97\u6e05\u9192\u4e86\u4e9b\uff0c\u8f7b\u5fae\u7684\u6655\u7729\u548c\u9ebb\u9189\u611f\u6d88\u9664\u4e86\uff0c\u7136\u540e\u624d\u53d1\u73b0\u81ea\u5df1\u7684\u6597\u7bf7\u5df2\u7ecf\u88ab\u8131\u4e0b\u3002</p><p>\u201c\u6211\u5fd8\u4e86\u63d0\u9192\u4f60\uff0c\u4e0d\u5e94\u8be5\u7a7f\u592a\u591a\u7684\u8863\u670d\u3002\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u8bf4\uff0c\u4fee\u957f\u7684\u624b\u6307\u5728\u5deb\u5e08\u7684\u957f\u888d\u4e0a\u8f7b\u8f7b\u5212\u8fc7\uff0c\u539f\u672c\u5c31\u4e0d\u591a\u7684\u7ebd\u6263\u4e00\u7c92\u7c92\u5e94\u624b\u800c\u5f00\uff0c\u5979\u81ea\u5df1\u5219\u65e9\u5df2\u7ecf\u5168\u8eab\u8d64\u88f8\u3002</p><p>\u5f88\u5feb\uff0c\u743c\u6069\u7684\u6700\u540e\u4e00\u4ef6\u5185\u8863\u4e5f\u88ab\u8131\u4e86\u4e0b\u6765\uff0c\u5353\u5c14\u5c11\u5973\u683c\u683c\u5a9a\u7b11\u7740\uff0c\u53cc\u624b\u6309\u5728\u4ed6\u7684\u80a9\u5934\uff0c\u8f7b\u8f7b\u7528\u529b\uff0c\u793a\u610f\u4ed6\u8eba\u4e0b\u53bb\u3002\u743c\u6069\u6447\u4e86\u6447\u5934\uff0c\u201c\u6211\u66f4\u559c\u6b22\u53e6\u5916\u4e00\u79cd\u59ff\u52bf\u3002\u201d</p><p>\u201c\u55ef\uff1f\u201d</p><p>\u743c\u6069\u547d\u4ee4\u5979\u8d70\u5230\u5e73\u53f0\u8fb9\u7f18\uff0c\u53cc\u624b\u6293\u4f4f\u56f4\u680f\uff0c\u5f2f\u4e0b\u8170\uff0c\u5c06\u8bf1\u4eba\u7684\u81c0\u90e8\u9ad8\u9ad8\u7fd8\u8d77\u3002\u7ef4\u5eb7\u5c3c\u4e9a\u5f88\u5feb\u660e\u767d\u4e86\u5bf9\u65b9\u7684\u610f\u601d\uff0c\u5979\u7a0d\u7a0d\u72b9\u8c6b\uff0c\u770b\u5f97\u51fa\u5e76\u4e0d\u4e60\u60ef\uff0c\u4f46\u8fd8\u662f\u4f9d\u8a00\u7167\u505a\u4e86\u3002\u743c\u6069\u8d70\u5230\u5979\u8eab\u540e\uff0c\u53cc\u624b\u63e1\u4f4f\u7ea4\u7ec6\u7684\u8170\u80a2\uff0c\u5bf9\u51c6\u6b63\u786e\u7684\u4f4d\u7f6e\u3002</p><p>\u201c\u5982\u679c\u6709\u4eba\u5f80\u8fd9\u91cc\u770b\u7684\u8bdd\uff0c\u4ed6\u4f1a\u5927\u9971\u773c\u798f\u3002\u201d\u743c\u6069\u8bf4\u3002</p><p>\u201c\u6ca1\u6709\u4eba\u4f1a\u770b\u5f97\u5230\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u56de\u7b54\uff0c\u201c\u8ddd\u79bb\u592a\u8fdc\u4e86\u3002\u201d</p><p>\u4f46\u8fd9\u6b63\u662f\u743c\u6069\u60f3\u8981\u7684\u6548\u679c\uff0c\u4ece\u7406\u8bba\u4e0a\u6709\u66b4\u9732\u7684\u53ef\u80fd\uff0c\u5b9e\u9645\u4e0a\u5374\u53c8\u4e0d\u4f1a\u88ab\u770b\u89c1\uff0c\u8fd9\u4f1a\u589e\u52a0\u5fc3\u7406\u7684\u523a\u6fc0\uff0c\u4ed6\u6ca1\u6709\u7279\u6b8a\u7231\u597d\uff0c\u5e76\u4e0d\u559c\u6b22\u5728\u5927\u5ead\u5e7f\u4f17\u4e4b\u4e0b\u8868\u6f14\u3002</p><p>\u4ed6\u6162\u6162\u8038\u8eab\u633a\u5165\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u5598\u606f\u7740\uff0c\u4e00\u79cd\u5145\u5b9e\u7684\u611f\u89c9\u4f20\u904d\u5168\u8eab\uff0c\u201c\u6211\u4ece\u6ca1\u8bd5\u8fc7\u88ab\u8fd9\u6837\u2026\u2026\u4ece\u540e\u9762\u2026\u2026\u201d\u5979\u8bf4\uff0c\u201c\u4e0d\u8fc7\u4f3c\u4e4e\u611f\u89c9\u8fd8\u4e0d\u574f\u3002\u201d</p><p>\u201c\u4f60\u4f1a\u559c\u6b22\u4e0a\u7684\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u76f8\u4fe1\u6211\uff0c\u800c\u4e14\u4eca\u665a\u4f60\u4f1a\u6709\u66f4\u591a\u7684\u60ca\u559c\u3002\u201d</p><p>\u4ed6\u5b8c\u5168\u8fdb\u5165\uff0c\u7136\u540e\u9000\u51fa\uff0c\u63a5\u7740\u518d\u6b21\u8fdb\u5165\uff0c\u901f\u5ea6\u4e00\u6b21\u6bd4\u4e00\u6b21\u52a0\u5feb\uff0c\u51b2\u51fb\u7684\u529b\u9053\u4e5f\u8d8a\u6765\u8d8a\u91cd\u3002\u7ef4\u5eb7\u5c3c\u4e9a\u5927\u58f0\u547b\u541f\u7740\uff0c\u53cc\u624b\u6293\u7d27\u680f\u6746\uff0c\u6307\u5173\u8282\u56e0\u4e3a\u7528\u529b\u8fc7\u5ea6\u800c\u6709\u4e9b\u53d1\u767d\uff0c\u5979\u611f\u89c9\u81ea\u5df1\u5c31\u50cf\u60ca\u6d9b\u9a87\u6d6a\u4e2d\u7684\u4e00\u53f6\u5c0f\u821f\uff0c\u5018\u82e5\u4e0d\u5982\u6b64\u5c31\u968f\u65f6\u53ef\u80fd\u88ab\u649e\u98de\u51fa\u53bb\u3002\u56e0\u4e3a\u5f2f\u7740\u8170\uff0c\u80f8\u90e8\u663e\u5f97\u683c\u5916\u9971\u6ee1\uff0c\u968f\u7740\u7537\u4eba\u8170\u90e8\u7684\u52a8\u4f5c\uff0c\u524d\u540e\u8bf1\u4eba\u5730\u7529\u8361\u7740\u3002\u743c\u6069\u677e\u5f00\u63e1\u7740\u8170\u80a2\u7684\u53cc\u624b\uff0c\u6539\u4e3a\u6293\u4f4f\u80f8\u53e3\uff0c\u52a8\u4f5c\u66f4\u52a0\u5feb\u4e86\u3002</p><p>\u9ed1\u6697\u7cbe\u7075\u62e5\u6709\u5929\u751f\u7684\u654f\u611f\u4f53\u8d28\uff0c\u80fd\u591f\u5728\u6027\u7231\u4e2d\u83b7\u5f97\u66f4\u591a\u5feb\u611f\uff0c\u6240\u4ee5\u5979\u4eec\u4e50\u6b64\u4e0d\u75b2\u2014\u2014\u5f53\u7136\uff0c\u8fd9\u4e5f\u662f\u56e0\u4e3a\u5e7d\u6697\u5730\u57df\u4e2d\u5b9e\u5728\u7f3a\u4e4f\u5176\u4ed6\u5a31\u4e50\u6d3b\u52a8\u7684\u7f18\u6545\u3002\u6ca1\u8fc7\u591a\u4e45\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u7684\u8eab\u4f53\u5c31\u53d8\u5f97\u7d27\u7ef7\u8d77\u6765\uff0c\u4f34\u968f\u7740\u4e00\u9635\u5267\u70c8\u7684\u4f53\u5185\u75c9\u631b\uff0c\u6f6e\u6c34\u6d8c\u51fa\uff0c\u6109\u60a6\u7684\u611f\u89c9\u987a\u7740\u6bcf\u6839\u795e\u7ecf\u62b5\u8fbe\u672b\u68a2\uff0c\u5979\u53d1\u51fa\u4e00\u9635\u957f\u957f\u7684\u547b\u541f\uff0c\u7136\u540e\u51e0\u4e4e\u8981\u762b\u8f6f\u4e0b\u6765\u3002</p><p>\u743c\u6069\u505c\u6b62\u4e0d\u52a8\uff0c\u6258\u7740\u5979\u7684\u8eab\u4f53\uff0c\u7b49\u5f85\u7740\uff0c\u4e24\u4eba\u4f9d\u65e7\u4fdd\u6301\u7740\u73b0\u6709\u7684\u59ff\u52bf\u3002\u201c\u5f88\u68d2\uff0c\u743c\u6069\uff0c\u201d\u8fc7\u4e86\u51e0\u5206\u949f\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u6e10\u6e10\u6062\u590d\u8fc7\u6765\uff0c\u5634\u89d2\u9732\u51fa\u5fae\u7b11\uff0c\u201c\u4e0d\u8fc7\u8981\u8ba9\u4e00\u4f4d\u5973\u796d\u53f8\u6ee1\u610f\uff0c\u8fd9\u8fd8\u4e0d\u591f\u3002\u201d</p><p>\u201c\u6211\u4e5f\u8fd9\u4e48\u89c9\u5f97\u3002\u201d\u743c\u6069\u8bf4\u3002</p><p>\u4ed6\u518d\u6b21\u8fd0\u52a8\u8d77\u6765\uff0c\u5353\u5c14\u5c11\u5973\u521a\u521a\u5e73\u7f13\u4e0b\u6765\u7684\u547c\u5438\u53c8\u5f00\u59cb\u53d8\u5f97\u6025\u4fc3\uff0c\u5145\u6ee1\u8bf1\u60d1\u7684\u547b\u541f\u58f0\u4ece\u5979\u53e3\u4e2d\u6324\u51fa\uff0c\u4e00\u5f00\u59cb\u8fd8\u6709\u4e9b\u523b\u610f\u538b\u4f4e\uff0c\u540e\u6765\u4fbf\u6beb\u65e0\u987e\u5fcc\u3002\u5f88\u5feb\uff0c\u5979\u5c31\u518d\u4e00\u6b21\u6500\u4e0a\u9ad8\u5cf0\u3002</p><p>\u7136\u540e\u53c8\u4e00\u6b21\u3002</p><p>\u518d\u4e00\u6b21\u3002</p><p>\u201c\u8fd9\u662f\u7b2c\u51e0\u6b21\u4e86\uff1f\u201d\u743c\u6069\u4f4e\u58f0\u95ee\uff0c\u4ed6\u4e5f\u6709\u4e9b\u7d2f\u4e86\uff0c\u4e0a\u8eab\u4fef\u538b\u5728\u7ef4\u5eb7\u5c3c\u4e9a\u7684\u80cc\u4e0a\uff0c\u540c\u65f6\u7528\u819d\u76d6\u6491\u7740\u5979\u7684\u8eab\u4f53\uff0c\u5426\u5219\u5979\u4e00\u5b9a\u4f1a\u50cf\u8f6f\u6ce5\u4e00\u6837\u762b\u4e0b\u53bb\u3002\u521a\u624d\u8fde\u7eed\u7684\u957f\u65f6\u95f4\u5267\u70c8\u8fd0\u52a8\u4e5f\u4ee4\u4ed6\u6709\u4e9b\u5934\u6655\u773c\u82b1\uff0c\u770b\u6765\u6700\u8fd1\u6709\u4e9b\u5ffd\u89c6\u4e86\u8eab\u4f53\u953b\u70bc\u2026\u2026\u6216\u8005\u8bf4\uff0c\u8fd1\u4e00\u6bb5\u65f6\u95f4\u4ee5\u6765\u6bd4\u8f83\u7f3a\u4e4f\u953b\u70bc\u7684\u673a\u4f1a\u3002</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u6ca1\u6709\u56de\u7b54\uff0c\u5979\u5df2\u7ecf\u9677\u5165\u5931\u795e\u72b6\u6001\uff0c\u773c\u775b\u534a\u95ed\u7740\uff0c\u7259\u9f7f\u54ac\u7740\u4e0b\u5507\uff0c\u4eff\u4f5b\u8fd8\u5728\u56de\u5473\u3002\u743c\u6069\u4e0d\u518d\u8be2\u95ee\uff0c\u4ed6\u62ac\u8d77\u624b\u6307\uff0c\u6162\u6162\u4ece\u5353\u5c14\u4e30\u6ee1\u7684\u81c0\u4e0a\u5212\u8fc7\uff0c\u62b5\u8fbe\u9884\u60f3\u7684\u76ee\u7684\u5730\uff0c\u90a3\u91cc\u5448\u6f02\u4eae\u7684\u6de1\u8910\u8272\uff0c\u7cbe\u81f4\u7684\u653e\u5c04\u72b6\u7eb9\u8def\uff0c\u4ed6\u6b23\u8d4f\u7247\u523b\uff0c\u8f7b\u8f7b\u70b9\u4e86\u4e00\u70b9\u3002</p><p>\u5c11\u5973\u7684\u8eab\u4f53\u9661\u7136\u4e00\u98a4\uff0c\u88ab\u6307\u5c16\u78b0\u89e6\u7684\u90e8\u4f4d\u672c\u80fd\u5730\u6536\u7f29\uff0c\u7136\u540e\u653e\u677e\uff0c\u5c31\u5982\u4e00\u6735\u4e4d\u653e\u7684\u96cf\u83ca\uff0c\u8fd9\u8ba9\u743c\u6069\u6709\u4e9b\u5947\u602a\uff0c\u4ed6\u4e5f\u7b97\u7ecf\u9a8c\u4e30\u5bcc\uff0c\u770b\u8fd9\u53cd\u5e94\uff0c\u5012\u50cf\u662f\u6b64\u5730\u8fd8\u4ece\u6ca1\u88ab\u4eba\u5f00\u53d1\u8fc7\u4e00\u822c\u3002\u4ed6\u518d\u6b21\u5c06\u98df\u6307\u6307\u5c16\u62b5\u4f4f\u5165\u53e3\uff0c\u4e00\u70b9\u70b9\u5730\u63a2\u5165\u8fdb\u53bb\u3002</p><p>\u201c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u60ca\u95ee\u3002</p><p>\u743c\u6069\u4e0d\u7b54\uff0c\u624b\u6307\u7ee7\u7eed\u524d\u8fdb\uff0c\u76f4\u81f3\u524d\u4e24\u4e2a\u6307\u8282\u90fd\u6ca1\u5165\u5c11\u5973\u4f53\u5185\u3002\u7ef4\u5eb7\u5c3c\u4e9a\u4f3c\u4e4e\u60f3\u8981\u53cd\u6297\uff0c\u4f46\u5979\u521a\u521a\u88ab\u63a8\u4e0a\u4e00\u6b21\u53c8\u4e00\u6b21\u9ad8\u6f6e\uff0c\u6b64\u65f6\u8eab\u4f53\u7ef5\u8f6f\uff0c\u7528\u4e0d\u51fa\u534a\u70b9\u529b\u6c14\uff0c\u53ea\u80fd\u542c\u4e4b\u4efb\u4e4b\u3002\u743c\u6069\u611f\u89c9\u81ea\u5df1\u7684\u624b\u6307\u88ab\u52d2\u5f97\u7d27\u7d27\uff0c\u51e0\u4e4e\u8981\u88ab\u5939\u65ad\u4e00\u822c\uff0c\u8fd9\u66f4\u52a0\u8bc1\u5b9e\u4e86\u4ed6\u7684\u731c\u6d4b\u3002</p><p>\u201c\u542c\u8bf4\u5728\u4e3e\u884c\u796d\u7940\u548c\u6bd5\u4e1a\u5178\u793c\u4e4b\u524d\uff0c\u5973\u796d\u53f8\u4eec\u90fd\u8981\u6e05\u6d01\u8eab\u4f53\uff0c\u201d\u743c\u6069\u629a\u6478\u7740\u5979\u7684\u88f8\u80cc\uff0c\u201c\u4e5f\u5305\u62ec\u8fd9\u91cc\u9762\u5417\uff1f\u201d</p><p>\u201c\u662f\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u56de\u7b54\uff0c\u201c\u4f46\u4f60\u4e0d\u80fd\u2026\u2026\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\u4e0d\u80fd\uff1f\u201d\u743c\u6069\u53cd\u95ee\u3002</p><p>\u201c\u4f60\u662f\u4e2a\u7537\u6027\uff0c\u4f60\u4e0d\u80fd\u5728\u672a\u7ecf\u5973\u796d\u53f8\u8bb8\u53ef\u4e4b\u4e0b\u2026\u2026\u201d</p><p>\u743c\u6069\u7528\u884c\u52a8\u6253\u65ad\u4e86\u5979\u7684\u8bdd\uff0c\u4ed6\u4ece\u5c11\u5973\u8eab\u4f53\u91cc\u9000\u51fa\uff0c\u7a0d\u7a0d\u62ac\u8d77\uff0c\u7136\u540e\u731b\u529b\u523a\u5165\u3002\u7ef4\u5eb7\u5c3c\u4e9a\u5c16\u53eb\u8d77\u6765\uff0c\u8eab\u4f53\u5267\u70c8\u626d\u52a8\uff0c\u7a81\u5982\u5176\u6765\u7684\u5de8\u5927\u75db\u695a\u8ba9\u5979\u6682\u65f6\u6062\u590d\u4e86\u4e00\u4e9b\u53cd\u6297\u7684\u529b\u6c14\uff0c\u743c\u6069\u51e0\u4e4e\u8981\u88ab\u5979\u6323\u8131\u3002\u5deb\u5e08\u77a5\u4e86\u773c\u65c1\u8fb9\u5730\u9762\uff0c\u4f38\u624b\u865a\u7a7a\u4e00\u6293\uff0c\u5c06\u8131\u4e0b\u7684\u957f\u888d\u63e1\u5728\u638c\u4e2d\uff0c\u7136\u540e\u9ed8\u8bf5\u4e00\u53e5\u5492\u8bed\u3002</p><p>\u957f\u888d\u81ea\u52a8\u62e7\u6210\u7ef3\u7d22\uff0c\u5c06\u7ef4\u5eb7\u5c3c\u4e9a\u7684\u53cc\u624b\u624b\u8155\u548c\u680f\u6746\u7262\u7262\u7ed1\u5728\u4e00\u8d77\uff0c\u8fd9\u79cd\u6d3b\u5316\u7ef3\u6cd5\u672f\u4ed6\u65e9\u5c31\u5b66\u4f1a\uff0c\u4e5f\u4e00\u76f4\u8003\u8651\u8fc7\u4f7f\u7528\uff0c\u4f46\u90fd\u6ca1\u4ec0\u4e48\u673a\u4f1a\uff0c\u5982\u4eca\u53cd\u5012\u662f\u7b2c\u4e00\u6b21\u3002\u201c\u653e\u677e\u8eab\u4f53\uff0c\u5c0a\u656c\u7684\u5973\u796d\u53f8\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u62cd\u7740\u5979\u4e30\u6ee1\u7684\u81c0\u90e8\uff0c\u201c\u4f60\u5c06\u4f1a\u611f\u89c9\u5230\u53e6\u4e00\u79cd\u5feb\u4e50\u3002\u201d</p><p>\u201c\u4f60\u5e94\u8be5\u88ab\u5582\u8718\u86db\u2026\u2026\u201d</p><p>\u743c\u6069\u65e0\u89c6\u8eab\u4e0b\u5c11\u5973\u7684\u8bc5\u5492\uff0c\u4ed6\u7f13\u6162\u800c\u575a\u51b3\u5730\u8fdb\u5165\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u53d1\u51fa\u75db\u82e6\u7684\u54c0\u9e23\uff0c\u773c\u6cea\u5fcd\u4e0d\u4f4f\u6d41\u6dcc\u51fa\u6765\uff0c\u4f5c\u4e3a\u7f57\u4e1d\u7684\u5973\u796d\u53f8\uff0c\u5979\u5b66\u4e60\u8fc7\u5404\u79cd\u6b8b\u9177\u800c\u6709\u6548\u7387\u7684\u5211\u7f5a\uff0c\u56e0\u6b64\u4e5f\u975e\u5e38\u4e86\u89e3\u8eab\u4f53\u6784\u9020\uff0c\u6e05\u695a\u77e5\u9053\u81ea\u5df1\u7684\u67d0\u4e2a\u90e8\u4f4d\u5df2\u7ecf\u88ab\u6495\u88c2\u5f00\u6765\uff0c\u6e29\u70ed\u7684\u8840\u6db2\u987a\u7740\u5927\u817f\u6d41\u4e0b\u3002\u5728\u5353\u5c14\u793e\u4f1a\uff0c\u5973\u6027\u6bd4\u7537\u6027\u66f4\u6709\u5730\u4f4d\uff0c\u800c\u5973\u796d\u53f8\u5219\u6700\u6709\u5730\u4f4d\uff0c\u5373\u4f7f\u662f\u5728\u5e8a\u4e0a\uff0c\u5979\u4e5f\u4ece\u6765\u662f\u88ab\u7537\u6027\u53d6\u60a6\u7684\u5bf9\u8c61\u800c\u4e0d\u662f\u76f8\u53cd\uff0c\u4ece\u6765\u6ca1\u6709\u54ea\u4e2a\u5927\u80c6\u7684\u5bb6\u4f19\u80c6\u6562\u8fd9\u6837\u4fb5\u72af\u5979\u7684\u8eab\u4f53\uff0c\u4f46\u5982\u4eca\u8fd9\u4e00\u5207\u6b63\u5728\u53d1\u751f\u3002\u5373\u4f7f\u5979\u5bf9\u8fd9\u4e2a\u4eba\u7c7b\u9887\u6709\u597d\u611f\uff0c\u4f46\u8fd9\u79cd\u4e25\u91cd\u7684\u5192\u72af\u4f9d\u65e7\u4e0d\u80fd\u88ab\u5bb9\u5fcd\uff0c\u5728\u5fc3\u4e2d\uff0c\u5973\u796d\u53f8\u5df2\u7ecf\u7528\u5404\u79cd\u6700\u9ed1\u6697\u7684\u8bc5\u5492\u4e3a\u743c\u6069\u8bb8\u4e0b\u4e0a\u767e\u79cd\u6b8b\u9177\u7684\u7ed3\u5c40\uff0c\u4f46\u8fd9\u5e76\u4e0d\u80fd\u4ee4\u5979\u7684\u73b0\u5b9e\u5904\u5883\u6709\u4e1d\u6beb\u6539\u5584\u3002\u66f4\u4ee4\u5979\u611f\u5230\u60f6\u6050\u7684\u662f\uff0c\u5ea6\u8fc7\u521d\u671f\u7684\u5267\u75db\u548c\u96be\u53d7\u4e4b\u540e\uff0c\u5979\u7684\u8eab\u4f53\u4f3c\u4e4e\u5f00\u59cb\u9690\u9690\u6709\u4e86\u53cd\u5e94\uff0c\u4e00\u80a1\u5947\u602a\u7684\u611f\u89c9\u6162\u6162\u6d8c\u8d77\uff0c\u548c\u5e73\u65f6\u201c\u6b63\u5e38\u201d\u65b9\u5f0f\u6240\u5e26\u6765\u7684\u611f\u89c9\u4e0d\u540c\uff0c\u4f46\u53c8\u4f3c\u4e4e\u6709\u6240\u76f8\u4f3c\u3002</p><p>\u743c\u6069\u6ca1\u6709\u5ffd\u7565\u5973\u796d\u53f8\u7684\u53cd\u5e94\uff0c\u201c\u6211\u8bf4\u8fc7\uff0c\u4f60\u4f1a\u559c\u6b22\u7684\u3002\u201d\u4ed6\u4e0d\u7d27\u4e0d\u6162\u5730\u91cd\u590d\u7740\uff0c\u52a8\u4f5c\u6e10\u6e10\u52a0\u5feb\uff0c\u6362\u6765\u4e86\u7ef4\u5eb7\u5c3c\u4e9a\u4e00\u8fde\u4e32\u6df1\u6df1\u6d45\u6d45\u7684\u547b\u541f\uff0c\u4f46\u5df2\u7ecf\u5e76\u4e0d\u50cf\u5f00\u59cb\u65f6\u90a3\u6837\u75db\u82e6\u3002\u5353\u5c14\u7684\u654f\u611f\u4f53\u8d28\u518d\u4e00\u6b21\u53d1\u6325\u4e86\u4f5c\u7528\uff0c\u6e10\u6e10\u5730\uff0c\u5979\u5df2\u7ecf\u80fd\u591f\u57fa\u672c\u9002\u5e94\u3002</p><p>\u201c\u4f60\u8fd9\u8be5\u4e0b\u6df1\u6e0a\u7684\u6df7\u86cb\uff0c\u201d\u5c3d\u7ba1\u5982\u6b64\uff0c\u5979\u4f9d\u65e7\u5fcd\u4e0d\u4f4f\u5730\u5492\u9a82\u7740\uff0c\u201c\u4f60\u80c6\u6562\u5192\u72af\u5973\u796d\u53f8\u2026\u2026\u201d</p><p>\u201c\u556a\uff01\u201d</p><p>\u743c\u6069\u91cd\u91cd\u5728\u5979\u4e30\u81c0\u4e0a\u62cd\u4e86\u4e00\u638c\uff0c\u201c\u8fd9\u662f\u60e9\u7f5a\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u4f60\u5bf9\u522b\u7684\u7537\u6027\u4ec0\u4e48\u6001\u5ea6\uff0c\u6211\u6ca1\u6709\u5174\u8da3\u7ba1\uff0c\u4e5f\u4e0d\u60f3\u77e5\u9053\u3002\u4f46\u5982\u679c\u5728\u6211\u9762\u524d\u518d\u8bf4\u8fd9\u79cd\u8bdd\uff0c\u90a3\u6211\u4f1a\u7acb\u523b\u8ba9\u83f2\u5c14\u4f26\u5bb6\u65cf\u51cf\u5c11\u4e00\u540d\u5973\u796d\u53f8\u3002\u201d</p><p>\u201c\u4f60\u6562\uff01\u201d</p><p>\u201c\u4f60\u53ef\u4ee5\u8bd5\u8bd5\u770b\u3002\u201d\u743c\u6069\u8bf4\u3002</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u54ac\u7740\u5634\u5507\uff0c\u4e0d\u6562\u518d\u8bf4\uff0c\u743c\u6069\u6ee1\u610f\u5730\u6293\u8d77\u5979\u7684\u94f6\u8272\u79c0\u53d1\uff0c\u5728\u624b\u6307\u4e0a\u7f20\u7ed5\u6210\u5377\uff0c\u201c\u653e\u677e\u4e9b\uff0c\u201d\u4ed6\u8bf4\uff0c\u58f0\u97f3\u53d8\u5f97\u67d4\u548c\uff0c\u201c\u8fd9\u4e5f\u5f88\u5feb\u4e50\uff0c\u4e0d\u662f\u5417\uff1f\u800c\u4e14\u6211\u4f1a\u5e2e\u4f60\u5f97\u5230\u4f60\u60f3\u8981\u7684\u4e00\u5207\u3002\u201d</p><p>\u743c\u6069\u611f\u89c9\u81ea\u5df1\u4e5f\u6709\u4e9b\u6cc4\u610f\uff0c\u5728\u8fd9\u4e2a\u5e73\u53f0\u4e0a\u5df2\u7ecf\u548c\u7ef4\u5eb7\u5c3c\u4e9a\u7f20\u7ef5\u4e86\u8d85\u8fc7\u4e00\u4e2a\u65f6\u8fb0\uff0c\u4ed6\u52a0\u5feb\u52a8\u4f5c\uff0c\u51c6\u5907\u7ed3\u675f\uff0c\u6b63\u5728\u6b64\u65f6\uff0c\u7a81\u7136\u4ece\u957f\u5eca\u90a3\u8fb9\u4f20\u6765\u4e00\u9635\u5608\u6742\u614c\u4e71\u7684\u53eb\u558a\u58f0\u3002</p></article>"}}}]);
