(window.webpackJsonp=window.webpackJsonp||[]).push([[275],{297:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u56db\u5341\u516d\u8282 \u5178\u793c\u4e0a\u7684\u610f\u5916",content:"<article><h2>\u7b2c\u56db\u5341\u516d\u8282 \u5178\u793c\u4e0a\u7684\u610f\u5916</h2><p>\u4e3e\u884c\u6bd5\u4e1a\u5178\u793c\u7684\u5927\u793c\u5802\u4e2d\uff0c\u539f\u672c\u5728\u6fc0\u60c5\u72c2\u6b22\u7684\u9ed1\u6697\u7cbe\u7075\u4eec\u5982\u4eca\u6b63\u60ca\u614c\u5931\u63aa\uff0c\u8d64\u88f8\u7740\u8eab\u4f53\uff0c\u8fde\u8863\u670d\u90fd\u987e\u4e0d\u4e0a\u7a7f\uff0c\u4e89\u5148\u6050\u540e\u5730\u60f3\u8981\u9003\u79bb\u6b64\u5730\uff0c\u7136\u800c\u793c\u5802\u53ea\u6709\u4e00\u4e2a\u72ed\u957f\u7684\u8d70\u5eca\u901a\u9053\uff0c\u5018\u82e5\u6709\u7ec4\u7ec7\u6709\u7eaa\u5f8b\u5730\u64a4\u9000\u5012\u7f62\u4e86\uff0c\u8fd9\u6837\u4e71\u7cdf\u7cdf\u4e00\u56e2\uff0c\u7ed3\u679c\u53ef\u60f3\u800c\u77e5\u3002\u8fd9\u4e9b\u5353\u5c14\u7684\u9053\u5fb7\u6559\u80b2\u53c8\u5f88\u4e0d\u6210\u529f\uff0c\u867d\u7136\u4e0a\u4e0b\u5c0a\u5351\u7684\u89c2\u5ff5\u662f\u5f88\u7262\u9760\uff0c\u7136\u800c\u771f\u5230\u4e86\u5371\u9669\u7684\u65f6\u5019\uff0c\u4e2a\u4e2a\u4e89\u5148\u4fdd\u547d\uff0c\u6ca1\u6709\u8c01\u5927\u558a\u8ba9\u9886\u5bfc\u5148\u8d70\uff0c\u4ee5\u81f3\u4e8e\u539f\u672c\u5904\u4e8e\u6700\u5916\u56f4\u7684\u5b66\u751f\u5012\u662f\u8dd1\u51fa\u6765\u4e0d\u5c11\uff0c\u800c\u4f4d\u4e8e\u4e2d\u5fc3\u7684\u5973\u796d\u53f8\u4eec\u53cd\u800c\u534a\u4e2a\u90fd\u6ca1\u9003\u8131\uff0c\u88ab\u8feb\u8fd1\u8ddd\u79bb\u9762\u5bf9\u9020\u6210\u6df7\u4e71\u7684\u5371\u9669\u6839\u6e90\u3002</p><p>\u4e5f\u5c31\u662f\u90a3\u53ea\u540d\u53eb\u963f\u82e5\u83ab\u683c\u65af\u5fd2\u7684\u8ff7\u8bf1\u9b54\u4e86\u3002</p><p>\u6bd5\u4e1a\u5178\u793c\u539f\u672c\u4e00\u5207\u987a\u5229\u8fdb\u884c\uff0c\u5973\u796d\u53f8\u4eec\u5012\u4e5f\u7f62\u4e86\uff0c\u8fd9\u4e9b\u5b66\u751f\u4eec\u88ab\u5173\u5728\u5b66\u9662\u91cc\u53d7\u8bad\uff0c\u52a8\u8f84\u5c31\u662f\u6570\u5341\u5e74\uff08\u796d\u53f8\u5b66\u9662\u5b66\u5236\u4e94\u5341\u5e74\u3001\u5deb\u5e08\u5b66\u9662\u4e03\u5341\u5e74\u3001\u683c\u6597\u5b66\u9662\u4e09\u5341\u5e74\uff09\uff0c\u9664\u4e86\u5076\u5c14\u7684\u8282\u5047\u65e5\u80fd\u591f\u51fa\u53bb\u653e\u98ce\u900f\u6c14\uff0c\u5e73\u65f6\u7684\u751f\u6d3b\u53ef\u8c13\u662f\u67af\u71e5\u65e0\u804a\u5230\u4e86\u9876\u70b9\u3002\u800c\u4e14\u56e0\u4e3a\u5353\u5c14\u7684\u793e\u4f1a\u5236\u5ea6\uff0c\u796d\u53f8\u5b66\u9662\u91cc\u5168\u662f\u6e05\u4e00\u8272\u7684\u5973\u6027\uff0c\u800c\u53e6\u5916\u4e24\u5ea7\u5b66\u9662\u91cc\u57fa\u672c\u90fd\u662f\u7537\u6027\uff08\u4e5f\u6709\u6781\u5c11\u6570\u5973\u6027\u4f1a\u83b7\u51c6\u8fdb\u5165\u683c\u6597\u5b66\u9662\uff0c\u4f46\u8fd9\u662f\u7279\u4f8b\u4e2d\u7684\u7279\u4f8b\uff0c\u53ef\u4ee5\u5ffd\u7565\u4e0d\u8ba1\uff09\uff0c\u771f\u53ef\u8c13\u662f\u5fcd\u65e0\u53ef\u5fcd\uff0c\u5982\u4eca\u597d\u4e0d\u5bb9\u6613\u83b7\u5f97\u4e86\u89e3\u8131\u7684\u673a\u4f1a\uff0c\u81ea\u7136\u6b23\u559c\u82e5\u72c2\uff0c\u5c31\u5982\u5e72\u67f4\u70c8\u706b\uff0c\u4e00\u70b9\u5c31\u7740\u3002</p><p>\u6bd5\u4e1a\u751f\u7684\u7b2c\u4e00\u540d\u662f\u88ab\u6076\u9b54\u9884\u5b9a\u7684\uff0c\u5176\u4ed6\u4eba\u4e0d\u5fc5\u671f\u5f85\uff0c\u597d\u5728\u53cd\u6b63\u53ea\u662f\u6210\u7ee9\u7b2c\u4e00\uff0c\u53c8\u4e0d\u662f\u5bb9\u8c8c\u8eab\u6750\u7b2c\u4e00\uff0c\u5012\u4e5f\u65e0\u9700\u4ecb\u610f\u3002\u5f53\u8ff7\u8bf1\u9b54\u88ab\u6210\u529f\u53ec\u5524\u51fa\u540e\uff0c\u6bd5\u4e1a\u5178\u793c\u5c31\u8fdb\u5165\u4e86\u6700\u540e\u7684\u9ad8\u6f6e\uff0c\u6240\u6709\u53c2\u4e0e\u8005\u80a2\u4f53\u7ede\u7f20\uff0c\u5e2d\u5730\u4e3a\u5e8a\uff0c\u5728\u8fd9\u795e\u5723\u7684\u793c\u5802\u4e2d\u4e0a\u6f14\u7fa4\u4ea4\u5927\u4f1a\uff0c\u538b\u6839\u5c31\u6ca1\u8c01\u6ce8\u610f\u743c\u6069\u548c\u7ef4\u5eb7\u5c3c\u4e9a\u7684\u79bb\u5f00\uff0c\u81f3\u4e8e\u90a3\u53ea\u8ff7\u8bf1\u9b54\u5c31\u66f4\u61d2\u5f97\u7406\u776c\u4e86\u3002</p><p>\u6fc0\u60c5\u4ea4\u5abe\u4e4b\u540e\uff0c\u5927\u90e8\u4efd\u4eba\u90fd\u5df2\u7ecf\u5728\u8eab\u4f53\u7684\u6c89\u6c89\u75b2\u5026\u548c\u7cbe\u795e\u7684\u6781\u5ea6\u6109\u60a6\u4e2d\u7761\u53bb\uff0c\u5c0f\u90e8\u5206\u4eba\u4f9d\u65e7\u5728\u6325\u6c57\u5982\u96e8\u5730\u52aa\u529b\u5de5\u4f5c\uff0c\u6bd5\u7adf\u5353\u5c14\u5973\u6027\u7684\u80c3\u53e3\u662f\u5f88\u96be\u6ee1\u8db3\u7684\u3002\u5c31\u5728\u6b64\u65f6\uff0c\u793c\u5802\u4e2d\u592e\u7a81\u7136\u53d1\u51fa\u4e00\u58f0\u77ed\u6682\u800c\u5c16\u9510\u7684\u60e8\u53eb\uff0c\u968f\u5373\u5c31\u50cf\u88ab\u6390\u65ad\u8116\u5b50\u4f3c\u7684\u560e\u7136\u800c\u6b62\u3002</p><p>\u867d\u7136\u53ea\u662f\u7a0d\u77ac\u5373\u901d\uff0c\u5353\u5c14\u4eec\u6bd5\u7adf\u662f\u6d78\u6ce1\u5728\u5371\u9669\u548c\u9634\u8c0b\u4e2d\u957f\u5927\u7684\u79cd\u65cf\uff0c\u5373\u4fbf\u5728\u8fd9\u6837\u7684\u60c5\u5f62\u4e0b\u4ed6\u4eec\u4f9d\u65e7\u4fdd\u7559\u7740\u57fa\u672c\u7684\u8b66\u6212\u5fc3\u3002\u51e0\u4e4e\u6240\u6709\u4eba\u90fd\u62ac\u8d77\u5934\u6765\uff0c\u770b\u5411\u60e8\u53eb\u58f0\u7684\u6765\u6e90\uff0c\u7136\u540e\u4ed6\u4eec\u5413\u4e86\u4e00\u8df3\u3002</p><p>\u8ff7\u8bf1\u9b54\u7ad9\u7acb\u7740\uff0c\u5b83\u6000\u4e2d\u7684\u5973\u5b66\u751f\u5219\u5df2\u7ecf\u53d8\u6210\u4e86\u4e00\u5177\u5c38\u4f53\u2014\u2014\u51c6\u786e\u5730\u8bf4\u662f\u4e24\u534a\u3002\u6076\u9b54\u6293\u4f4f\u5979\u7684\u53cc\u817f\uff0c\u786c\u751f\u751f\u4ece\u4e2d\u6495\u5f00\uff0c\u9c9c\u8840\u548c\u6c61\u7269\u6e85\u5728\u5b83\u7684\u8138\u548c\u80f8\u819b\u4e0a\uff0c\u8d8a\u53d1\u663e\u5f97\u72f0\u72de\u53ef\u6016\u3002</p><p>\u5353\u5c14\u4eec\u8df3\u4e86\u8d77\u6765\uff0c\u6218\u58eb\u614c\u4e71\u5730\u53bb\u6478\u6254\u5728\u5730\u4e0a\u7684\u8170\u5e26\uff0c\u90a3\u4e0a\u9762\u6302\u7740\u6b66\u5668\uff0c\u5deb\u5e08\u5bfb\u627e\u81ea\u5df1\u7684\u957f\u888d\uff0c\u4ed6\u4eec\u5fc5\u987b\u501f\u52a9\u6750\u6599\u624d\u80fd\u65bd\u6cd5\uff0c\u5973\u796d\u53f8\u4eec\u76f8\u5bf9\u9547\u5b9a\u4e00\u4e9b\uff0c\u4e00\u65b9\u9762\u5979\u4eec\u6bd5\u7adf\u6700\u64c5\u957f\u548c\u6076\u9b54\u6253\u4ea4\u9053\uff0c\u4e00\u65b9\u9762\u662f\u56e0\u4e3a\u65bd\u5c55\u795e\u672f\u6240\u5fc5\u987b\u7684\u5723\u5fbd\u5c31\u6302\u5728\u8116\u5b50\u4e0a\u5462\uff0c\u5c31\u7b97\u662f\u6b22\u597d\u65f6\u90fd\u6ca1\u6709\u53d6\u4e0b\u6765\u3002</p><p>\u867d\u7136\u795e\u540e\u5df2\u7ecf\u4e0d\u518d\u8d50\u4e88\u65b0\u7684\u795e\u672f\uff0c\u4f46\u5927\u591a\u6570\u796d\u53f8\u603b\u8fd8\u662f\u7559\u7740\u4e00\u4e24\u624b\u4fdd\u547d\u7684\u62db\u6570\u3002</p><p>\u8ff7\u8bf1\u9b54\u5b8c\u5168\u65e0\u89c6\u8fd9\u4e9b\u51e1\u4eba\u7684\u614c\u4e71\uff0c\u5b83\u543c\u53eb\u7740\uff0c\u6325\u821e\u7740\u8783\u87f9\u4f3c\u7684\u524d\u722a\u653b\u51fb\u7740\u5468\u56f4\u7684\u5353\u5c14\uff0c\u52a8\u4f5c\u6beb\u65e0\u7ae0\u6cd5\uff0c\u770b\u8d77\u6765\u5c31\u50cf\u662f\u53d1\u4e86\u75af\u3002\u8ddd\u79bb\u5b83\u6700\u8fd1\u7684\u662f\u5b66\u9662\u7684\u6559\u5b98\uff0c\u5979\u4eec\u5230\u5e95\u89c1\u591a\u8bc6\u5e7f\uff0c\u5306\u5fd9\u6563\u5f00\u3002\u201c\u516b\u9b54\u9635\uff01\u201d\u535a\u514b\u672c\u5389\u58f0\u6012\u559d\uff0c\u5c31\u8fd1\u6307\u6325\u516b\u4e2a\u5973\u796d\u53f8\u56f4\u56f0\u8ff7\u8bf1\u9b54\u3002\u516b\u9b54\u9635\u662f\u7f57\u4e1d\u8d50\u4e0b\u7684\u4e00\u79cd\u795e\u672f\u9635\u6cd5\uff0c\u5fc5\u987b\u516b\u4f4d\u7267\u5e08\u624d\u80fd\u591f\u4f7f\u7528\uff0c\u80fd\u591f\u6c47\u805a\u5f3a\u5927\u7684\u7cbe\u795e\u529b\u91cf\u6765\u653b\u51fb\u654c\u4eba\uff0c\u5bb6\u65cf\u6218\u4e89\u4e2d\u6bd4\u8f83\u5e38\u89c1\uff0c\u4f46\u5728\u6bd5\u4e1a\u5178\u793c\u4e0a\u7528\u51fa\u6765\uff0c\u90a3\u5012\u8fd8\u662f\u6709\u53f2\u4ee5\u6765\u7684\u7b2c\u4e00\u6b21\u3002</p><p>\u56e0\u4e3a\u5728\u74dc\u7406\u5fb7\u65af\u57ce\u4e0a\u4e07\u5e74\u7684\u5386\u53f2\u4e0a\uff0c\u4ece\u6ca1\u53d1\u751f\u8fc7\u8ff7\u8bf1\u9b54\u5931\u63a7\u7684\u5148\u4f8b\u3002</p><p>\u8fd9\u662f\u4e2a\u9b54\u6cd5\u4e16\u754c\uff0c\u51e1\u4eba\u548c\u6076\u9b54\u6253\u4ea4\u9053\uff0c\u5012\u4e5f\u4e0d\u7b97\u4ec0\u4e48\u60ca\u4e16\u9a87\u4fd7\u7684\u4e8b\u60c5\u3002\u5c31\u4ee5\u8fd9\u5ea7\u74dc\u7406\u5fb7\u65af\u57ce\u4e3a\u4f8b\uff0c\u524d\u5929\u8986\u706d\u7684\u745e\u8d39\u5fb7\u5bb6\u65cf\uff0c\u5deb\u5e08\u4eec\u5c31\u5f88\u64c5\u957f\u53ec\u5524\u6076\u9b54\uff1b\u800c\u7f57\u4e1d\u672c\u8eab\u5c31\u5c45\u4f4f\u6df1\u6e0a\uff0c\u624b\u4e0b\u6076\u9b54\u65e0\u6570\uff0c\u5979\u7684\u5973\u796d\u53f8\u4eec\u66f4\u64c5\u957f\u6b64\u9053\u3002\u4f46\u8fd9\u6b21\u53ec\u5524\u8ff7\u8bf1\u9b54\uff0c\u60c5\u5f62\u662f\u6bd4\u8f83\u7279\u6b8a\u7684\u3002</p><p>\u6076\u9b54\u5e76\u4e0d\u662f\u4ec0\u4e48\u597d\u813e\u6c14\u7684\u5b58\u5728\uff0c\u5b83\u4eec\u559c\u6012\u65e0\u5e38\uff0c\u51f6\u72e0\u6b8b\u66b4\uff0c\u8be1\u8bc8\u591a\u7aef\uff0c\u800c\u4e14\u4e0d\u8bb2\u534a\u70b9\u4fe1\u8a89\u3002\u51e1\u4eba\u53ec\u5524\u6076\u9b54\uff0c\u65e0\u8bba\u662f\u5deb\u5e08\u8fd8\u662f\u7267\u5e08\uff0c\u53ea\u8981\u4e0d\u662f\u7b28\u5230\u5bb6\uff0c\u4e8b\u5148\u90fd\u4f1a\u505a\u597d\u5145\u8db3\u7684\u51c6\u5907\uff0c\u5404\u79cd\u7ea6\u675f\u514b\u5236\u7684\u624b\u6bb5\u51c6\u5907\u9f50\u5168\uff0c\u4e00\u65e6\u5bf9\u65b9\u4e0d\u542c\u8bdd\u5c31\u7528\u5f3a\uff0c\u6bd5\u7adf\u6076\u9b54\u5c31\u53ea\u5403\u8fd9\u4e00\u5957\u3002\u5018\u82e5\u758f\u4e8e\u9632\u8303\uff0c\u7ed3\u679c\u88ab\u6076\u9b54\u7ed9\u6495\u6210\u788e\u7247\uff0c\u90a3\u53ef\u602a\u4e0d\u5f97\u522b\u4eba\u3002\u4f46\u6bd5\u4e1a\u5178\u793c\u4e0a\u53ec\u5524\u7684\u6076\u9b54\u53c8\u6709\u6240\u4e0d\u540c\uff0c\u5b83\u540d\u5217\u796d\u7940\u4e66\u4e2d\uff0c\u5fc5\u5b9a\u662f\u7f57\u4e1d\u7684\u4eb2\u4fe1\u90e8\u5c5e\uff0c\u7edd\u65e0\u80c6\u91cf\u5728\u8fd9\u79cd\u573a\u5408\u6492\u91ce\u800c\u89e6\u6012\u86db\u540e\u3002\u53cd\u6b63\u53c8\u4e0d\u662f\u4ec0\u4e48\u82e6\u5dee\u4e8b\uff0c\u9ad8\u9ad8\u5174\u5174\u4ece\u6df1\u6e0a\u91cc\u6765\u4e00\u8d9f\uff0c\u6709\u7f8e\u5973\u81ea\u52a8\u6295\u6000\u9001\u62b1\uff0c\u5c31\u5f53\u662f\u514d\u8d39\u65c5\u6e38\u3002</p><p>\u56e0\u4e3a\u8fd9\u79cd\u7f18\u6545\uff0c\u6574\u4e2a\u793c\u5802\u91cc\u8fde\u4e2a\u6700\u57fa\u672c\u7684\u53cd\u90aa\u6076\u6cd5\u9635\u90fd\u6ca1\u5e03\u7f6e\uff0c\u6559\u5b98\u5c31\u76f4\u63a5\u6307\u5bfc\u5b66\u751f\u5f53\u573a\u53ec\u5524\u6076\u9b54\u3002\u6309\u7167\u60ef\u4f8b\uff0c\u8ff7\u8bf1\u9b54\u6b64\u65f6\u4e5f\u5dee\u4e0d\u591a\u5e94\u8be5\u5fc3\u6ee1\u610f\u8db3\u5730\u8fd4\u56de\u6df1\u6e0a\u4e86\uff0c\u7136\u800c\u5b83\u5374\u4e0d\u77e5\u9053\u53d1\u4e86\u4ec0\u4e48\u75af\uff0c\u7a81\u7136\u653b\u51fb\u8d77\u5728\u573a\u7684\u4eba\u6765\u3002</p><p>\u4e00\u9635\u614c\u4e71\u4e4b\u540e\uff0c\u516b\u540d\u7267\u5e08\u7ec8\u4e8e\u7ad9\u5b9a\u4e86\u4f4d\u7f6e\uff0c\u624b\u6309\u5723\u5fbd\u540c\u58f0\u5ff5\u8bf5\u7977\u6587\uff0c\u4e00\u80a1\u5f3a\u6709\u529b\u7684\u65e0\u5f62\u5a01\u538b\u767b\u65f6\u51fa\u73b0\uff0c\u6682\u65f6\u904f\u5236\u4f4f\u8ff7\u8bf1\u9b54\u3002\u535a\u514b\u672c\u89c1\u72b6\u7a0d\u7a0d\u5fc3\u5b89\uff0c\u5979\u7d27\u63a5\u7740\u53d1\u53f7\u65bd\u4ee4\uff0c\u8ba9\u5b66\u751f\u5168\u4f53\u79bb\u5f00\uff0c\u6559\u5b98\u7559\u4e0b\u5e94\u654c\u3002\u8fd9\u5012\u5e76\u4e0d\u662f\u9ad8\u98ce\u4eae\u8282\u6216\u8005\u52c7\u4e8e\u4efb\u4e8b\uff0c\u800c\u662f\u56e0\u4e3a\u7ecf\u8fc7\u524d\u65e5\u53db\u4e71\uff0c\u5b66\u751f\u539f\u672c\u5c31\u6240\u5269\u4e0d\u591a\uff0c\u5018\u82e5\u518d\u6709\u6b7b\u4f24\uff0c\u90a3\u5979\u53ef\u5c31\u6ca1\u529e\u6cd5\u5411\u6267\u653f\u8bae\u4f1a\u4ea4\u5f85\u4e86\u3002\u81f3\u4e8e\u6559\u5b98\u5219\u6ca1\u5173\u7cfb\uff0c\u6b7b\u4e86\u53cd\u6b63\u4e0d\u7b97\u8fdb\u6bcf\u5e74\u7684\u6b7b\u4ea1\u540d\u989d\u91cc\u3002\u518d\u8bf4\uff0c\u7559\u7740\u8fd9\u4e9b\u83dc\u9e1f\u4e5f\u6ca1\u6709\uff0c\u5e2e\u4e0d\u4e0a\u5fd9\uff0c\u53ea\u4f1a\u788d\u624b\u788d\u811a\u7f62\u4e86\u3002</p><p>\u5f53\u6240\u6709\u7684\u5b66\u751f\u79bb\u5f00\u4e4b\u540e\uff0c\u535a\u514b\u672c\u518d\u5ea6\u901a\u8fc7\u5fbd\u7ae0\u53d1\u51fa\u4e00\u9053\u6307\u4ee4\uff0c\u5c06\u793c\u5802\u552f\u4e00\u7684\u95e8\u5c01\u95ed\u8d77\u6765\uff0c\u5979\u662f\u5b66\u9662\u7684\u8d1f\u8d23\u4eba\uff0c\u62e5\u6709\u8fd9\u4e2a\u6743\u529b\uff0c\u5176\u4ed6\u7267\u5e08\u9664\u975e\u7528\u5f3a\u529b\u51b2\u649e\uff0c\u5426\u5219\u65e0\u6cd5\u7528\u547d\u4ee4\u6253\u5f00\u3002\u8fd9\u6837\u505a\u4e00\u65b9\u9762\u662f\u4e3a\u4e86\u9632\u6b62\u6076\u9b54\u6d41\u7a9c\uff0c\u4f24\u5bb3\u5b66\u751f\uff0c\u4e00\u65b9\u9762\u4e5f\u662f\u903c\u8feb\u5728\u573a\u7684\u6240\u6709\u7267\u5e08\u540c\u4ec7\u654c\u5ffe\uff0c\u5fc5\u987b\u5c3d\u529b\u4e00\u6218\u3002</p><p>\u516b\u9b54\u9635\u538b\u5236\u4f4f\u4e86\u8ff7\u8bf1\u9b54\uff0c\u4f46\u8fd9\u4e5f\u4ec5\u4ec5\u53ea\u662f\u6682\u65f6\u7684\uff0c\u56e0\u4e3a\u8fd9\u79cd\u9635\u6cd5\u539f\u672c\u5c31\u6301\u7eed\u4e0d\u4e86\u591a\u4e45\u2014\u2014\u8bf4\u5f97\u51c6\u786e\u70b9\uff0c\u5b83\u5c31\u662f\u4e09\u677f\u65a7\uff0c\u6216\u8005\u8bf4\u72b9\u5982\u6d6a\u5934\uff0c\u5f00\u5934\u51f6\u731b\uff0c\u540e\u52b2\u4e0d\u8db3\uff0c\u535a\u514b\u672c\u5bf9\u6b64\u81ea\u7136\u975e\u5e38\u6e05\u695a\uff0c\u5979\u4f30\u7b97\u5341\u79d2\u949f\u5185\u6076\u9b54\u5c31\u4f1a\u8131\u56f0\u800c\u51fa\u3002\u6bd5\u4e1a\u5178\u793c\u4e0a\u53ec\u5524\u51fa\u7684\u8ff7\u8bf1\u9b54\u90fd\u662f\u7f57\u4e1d\u7684\u4eb2\u4fe1\u90e8\u5c5e\uff0c\u975e\u662f\u5bfb\u5e38\u6076\u9b54\u53ef\u6bd4\uff0c\u4f55\u51b5\u8fd9\u6765\u7684\u662f\u672c\u4f53\u800c\u975e\u6295\u5f71\u3002\u5982\u679c\u6ca1\u6709\u4e27\u5931\u795e\u672f\uff0c\u5728\u573a\u8fd9\u4e48\u591a\u796d\u53f8\uff0c\u8f7b\u800c\u6613\u4e3e\u4e5f\u5c31\u628a\u5b83\u6253\u6210\u788e\u7247\u4e86\uff0c\u4f46\u5982\u4eca\u8bf4\u8fd9\u79cd\u8bdd\u81ea\u7136\u5df2\u7ecf\u6beb\u65e0\u610f\u4e49\u3002</p><p>\u8d81\u7740\u8fd9\u77ed\u6682\u7684\u95f4\u9699\uff0c\u535a\u514b\u672c\u5411\u8ff7\u8bf1\u9b54\u53d1\u51fa\u4e00\u9053\u5fc3\u7075\u6307\u4ee4\uff0c\u201c\u5b89\u9759\u4e0b\u6765\uff0c\u963f\u82e5\u83ab\u683c\u65af\u5fd2\uff01\u201d\u5979\u5728\u5fc3\u4e2d\u5389\u58f0\u8bf4\uff0c\u201c\u4f60\u8fd9\u662f\u5728\u4eb5\u6e0e\u795e\u540e\uff01\u201d</p><p>\u6ca1\u6709\u56de\u5e94\u3002</p><p>\u535a\u514b\u672c\u518d\u5ea6\u5c1d\u8bd5\uff0c\u8fd9\u6b21\u5979\u6253\u7b97\u76f4\u63a5\u63a7\u5236\u6076\u9b54\uff0c\u4f5c\u4e3a\u4e00\u540d\u9ad8\u9636\u796d\u53f8\u2014\u2014\u800c\u4e14\u662f\u74dc\u7406\u5fb7\u65af\u57ce\u91cc\u4f4d\u9636\u6700\u9ad8\u7684\u4e94\u540d\u796d\u53f8\u4e4b\u4e00\uff0c\u5979\u6709\u8fd9\u4e2a\u80fd\u529b\u3002\u7136\u800c\u5f88\u5947\u602a\uff0c\u5979\u7684\u610f\u8bc6\u88ab\u963b\u9694\u4e86\uff0c\u4e00\u4e1d\u4e00\u6beb\u90fd\u6e17\u900f\u4e0d\u8fdb\u6076\u9b54\u7684\u5934\u8111\u91cc\uff0c\u8fd9\u5728\u4ee5\u5f80\u662f\u6ca1\u6709\u5148\u4f8b\u7684\u3002</p><p>\u201c\u5b83\u662f\u5192\u5145\u7684\uff0c\u5b83\u4e0d\u662f\u795e\u540e\u7684\u90e8\u5c5e\uff1f\u8fd8\u662f\u5b83\u88ab\u795e\u540e\u7684\u654c\u4eba\u6536\u4e70\u4e86\uff1f\u201d</p><p>\u8fd9\u4e2a\u5ff5\u5934\u4ece\u8111\u4e2d\u6025\u901f\u95ea\u8fc7\uff0c\u4f46\u968f\u5373\u88ab\u5426\u5b9a\u4e86\u3002\u53ec\u5524\u4f7f\u7528\u7684\u662f\u6076\u9b54\u7684\u771f\u540d\uff0c\u7edd\u65e0\u5192\u540d\u9876\u66ff\u7684\u53ef\u80fd\uff0c\u5426\u5219\u53ec\u5524\u8005\u4e00\u5b9a\u4f1a\u53d1\u73b0\u3002\u81f3\u4e8e\u8bf4\u88ab\u6536\u4e70\uff0c\u8fd9\u79cd\u53ef\u80fd\u6027\u4e5f\u4e0d\u9ad8\uff0c\u796d\u7940\u4e66\u91cc\u6076\u9b54\u7684\u540d\u5b57\u81f3\u5c11\u6709\u4e0a\u5343\u4e2a\uff0c\u5230\u5e95\u53ec\u5524\u54ea\u4e00\u53ea\u4e0d\u662f\u4e8b\u5148\u9884\u5b9a\uff0c\u800c\u662f\u4e34\u65f6\u7531\u5973\u5b66\u751f\u6307\u5b9a\u7684\uff0c\u4e16\u754c\u4e0a\u603b\u4e0d\u81f3\u4e8e\u6709\u8fd9\u4e48\u5de7\u7684\u4e8b\u60c5\u3002</p><p>\u201c\u96be\u9053\u8bf4\uff0c\u795e\u540e\u771f\u7684\u5df2\u7ecf\u2026\u2026\u4ee5\u81f3\u4e8e\u8fde\u5979\u624b\u4e0b\u7684\u6076\u9b54\u90fd\u80c6\u6562\u80cc\u53db\u4e86\uff1f\u201d</p><p>\u867d\u7136\u968f\u7740\u65f6\u95f4\u7684\u63a8\u79fb\uff0c\u7267\u5e08\u4eec\u5df2\u7ecf\u6e10\u6e10\u63a5\u53d7\u4e86\u7f57\u4e1d\u6d88\u5931\u7684\u4e8b\u5b9e\uff0c\u751a\u81f3\u6162\u6162\u6000\u7591\u795e\u540e\u771f\u7684\u5df2\u7ecf\u906d\u9047\u4e86\u4e0d\u6d4b\u3002\u4f46\u5979\u4eec\u6bd5\u7adf\u662f\u6700\u8654\u8bda\u7684\u4fe1\u5f92\uff0c\u5373\u4f7f\u5076\u7136\u6cdb\u8d77\u8fd9\u79cd\u60f3\u6cd5\uff0c\u4e5f\u4f1a\u7acb\u523b\u5f3a\u884c\u538b\u5236\u4e0b\u53bb\uff0c\u4e0d\u6562\u591a\u60f3\u3002\u7136\u800c\u5982\u4eca\uff0c\u8ff7\u8bf1\u9b54\u7684\u7a81\u7136\u653b\u51fb\uff0c\u4ee4\u535a\u514b\u672c\u7684\u8111\u4e2d\u518d\u6b21\u51fa\u73b0\u8fd9\u4e2a\u5927\u9006\u4e0d\u9053\u7684\u5ff5\u5934\u3002</p><p>\u56e0\u4e3a\u662f\u6bd5\u4e1a\u5178\u793c\uff0c\u6240\u6709\u5973\u796d\u53f8\u90fd\u662f\u8f7b\u88c5\u4e0a\u9635\uff0c\u8eab\u4e0a\u9664\u4e86\u4e00\u4ef6\u796d\u53f8\u888d\u548c\u4e00\u9876\u88c5\u9970\u7528\u7684\u5934\u76d4\u4e4b\u5916\u522b\u65e0\u4ed6\u7269\uff0c\u5e73\u65f6\u643a\u5e26\u7684\u90a3\u4e9b\u5377\u8f74\u3001\u9b54\u6756\u90fd\u7559\u5728\u4e86\u623f\u95f4\u91cc\u3002\u5982\u4eca\u4e8b\u51fa\u7a81\u7136\uff0c\u5973\u796d\u53f8\u4eec\u8fde\u8863\u670d\u90fd\u6ca1\u6765\u5f97\u53ca\u7a7f\u4e0a\uff0c\u66f4\u522b\u63d0\u5176\u4ed6\uff0c\u8981\u56de\u53bb\u53d6\u663e\u7136\u4e5f\u5df2\u7ecf\u6765\u4e0d\u53ca\u4e86\uff0c\u8fd9\u5374\u6709\u4e9b\u9ebb\u70e6\u3002</p><p>\u201c\u6ca1\u529e\u6cd5\u4e86\u3002\u201d</p><p>\u535a\u514b\u672c\u4e24\u6b21\u5c1d\u8bd5\u548c\u6076\u9b54\u6c9f\u901a\u5931\u8d25\uff0c\u4e5f\u5c31\u65e0\u5948\u653e\u5f03\uff0c\u5979\u4ece\u8116\u5b50\u4e0a\u7684\u9879\u94fe\u4e2d\u6458\u4e0b\u4e00\u679a\u6696\u8272\u6676\u73e0\u5b50\uff0c\u6258\u5728\u638c\u5fc3\uff0c\u51c6\u5907\u91ca\u653e\u91cc\u9762\u50a8\u5b58\u7684\u4e00\u4e2a\u5f3a\u529b\u6cd5\u672f\u3002\u8fd9\u662f\u5979\u672c\u62df\u7528\u6765\u4fdd\u547d\u7684\u624b\u6bb5\uff0c\u5c31\u8fd9\u6837\u6d6a\u8d39\u8fd8\u771f\u820d\u4e0d\u5f97\uff0c\u4f46\u5018\u82e5\u4e0d\u8d76\u5feb\u6536\u62fe\u6389\u8fd9\u4e2a\u6076\u9b54\uff0c\u522b\u7684\u796d\u53f8\u6ca1\u4ec0\u4e48\u5927\u4e8b\uff0c\u81ea\u5df1\u521a\u521a\u5750\u4e0a\u7684\u5b9d\u5ea7\u53ea\u6015\u53c8\u8981\u62f1\u624b\u8ba9\u4eba\u4e86\u3002\u6b63\u7565\u7565\u4e00\u8e0c\u8e87\uff0c\u8ff7\u8bf1\u9b54\u53d1\u51fa\u4e00\u58f0\u4f4e\u543c\uff0c\u56db\u81c2\u632f\u5f00\uff0c\u6392\u5c71\u5012\u6d77\u7684\u6c14\u6d6a\u7ffb\u6eda\u800c\u6765\uff0c\u793c\u5802\u91cc\u6240\u6709\u4eba\u90fd\u7981\u4e0d\u4f4f\u9000\u540e\u4e24\u6b65\uff0c\u8fde\u4e32\u7684\u7830\u7830\u7830\u58f0\u54cd\u8d77\uff0c\u56f4\u56f0\u6076\u9b54\u7684\u516b\u4e2a\u7267\u5e08\u5df2\u7ecf\u6709\u56db\u4e2a\u88ab\u9707\u5012\u5728\u5730\uff0c\u53e3\u9f3b\u4e03\u7a8d\u6d41\u8840\u8eab\u4ea1\uff0c\u53e6\u5916\u56db\u4e2a\u60ca\u60f6\u540e\u9000\uff0c\u516b\u9b54\u9635\u663e\u7136\u5df2\u7ecf\u5931\u6548\u4e86\u3002</p><p>\u8ff7\u8bf1\u9b54\u505a\u4e86\u4e00\u4e2a\u975e\u5e38\u5947\u602a\u7684\u52a8\u4f5c\uff0c\u5b83\u4f4e\u4e0b\u5934\uff0c\u50cf\u725b\u4e00\u6837\u671d\u535a\u514b\u672c\u649e\u8fc7\u53bb\u3002\u6559\u5b98\u5306\u5fd9\u5ff5\u51fa\u5492\u8bed\uff0c\u4e00\u5708\u65e0\u5f62\u7684\u65cb\u8f6c\u5200\u950b\u4ece\u6696\u8272\u6676\u73e0\u5b50\u91cc\u8ff8\u53d1\u51fa\u6765\uff0c\u547c\u5578\u7740\u5207\u5f00\u7a7a\u6c14\uff0c\u649e\u4e0a\u4e86\u8ff7\u8bf1\u9b54\u7684\u8eab\u4f53\u3002\u4eff\u4f5b\u7ede\u8089\u673a\u4e00\u822c\uff0c\u6076\u9b54\u7684\u8eab\u4e0a\u77ac\u95f4\u591a\u51fa\u51e0\u5341\u4e2a\u6df1\u6df1\u7684\u521b\u53e3\uff0c\u9ed1\u8272\u7684\u8840\u6db2\u6c69\u6c69\u5192\u51fa\uff0c\u9752\u8272\u7684\u76ae\u8089\u788e\u672b\u6a2a\u98de\uff0c\u5b83\u53d7\u4f24\u4e86\uff0c\u5f88\u4e25\u91cd\uff0c\u4f46\u5e76\u4e0d\u81f4\u547d\u3002</p><p>\u201c\u8be5\u6b7b\uff01\u201d</p><p>\u535a\u514b\u672c\u7709\u5934\u5927\u76b1\uff0c\u5979\u521a\u624d\u8fd9\u4e00\u51fb\u660e\u660e\u662f\u7784\u51c6\u5bf9\u65b9\u7684\u8981\u5bb3\u53bb\u7684\uff0c\u4f46\u5229\u5203\u58c1\u969c\u89e6\u53ca\u6076\u9b54\u8eab\u4f53\u7684\u65f6\u5019\uff0c\u83ab\u540d\u5176\u5999\u504f\u79fb\u4e86\u70b9\uff0c\u7ed3\u679c\u6ca1\u80fd\u4e00\u4e3e\u594f\u529f\u3002\u597d\u5728\u6076\u9b54\u6bd5\u7adf\u53d7\u4f24\u4e25\u91cd\uff0c\u4e00\u65f6\u6ca1\u6709\u903c\u8feb\u8ffd\u51fb\u3002\u5979\u4e00\u8fb9\u540e\u9000\u7740\uff0c\u4e0d\u5f97\u4e0d\u53c8\u4ece\u9879\u94fe\u4e0a\u518d\u53d6\u4e0b\u4e00\u679a\u9ed1\u73cd\u73e0\u6765\uff0c\u6b63\u8981\u518d\u5ea6\u5ff5\u8bf5\u5492\u8bed\uff0c\u773c\u89d2\u4f59\u5149\u77a5\u89c1\u4e00\u76f4\u7ad9\u5728\u5899\u8fb9\u7684\u8fe6\u6cd5\xb7\u83f2\u5c14\u4f26\uff0c\u540e\u8005\u6b63\u9762\u5e26\u51b7\u7b11\u770b\u7740\u81ea\u5df1\uff0c\u53cc\u624b\u62b1\u80f8\uff0c\u660e\u663e\u6253\u7b97\u8896\u624b\u65c1\u89c2\u3002</p><p>\u5bf9\u4e8e\u5353\u5c14\u6765\u8bf4\uff0c\u8fd9\u65e0\u53ef\u539a\u975e\uff0c\u56e2\u7ed3\u53cb\u7231\u8fd9\u79cd\u6982\u5ff5\u672c\u6765\u5c31\u88ab\u5979\u4eec\u5f53\u4f5c\u7b11\u8bdd\u3002\u800c\u4e14\u8fd9\u4e24\u4eba\u539f\u672c\u4e0d\u5bf9\u4ed8\uff0c\u7b2c\u4e00\u5bb6\u65cf\u548c\u7b2c\u4e8c\u5bb6\u65cf\u7684\u957f\u5973\uff0c\u660e\u91cc\u6697\u91cc\u81ea\u7136\u662f\u5929\u751f\u7684\u654c\u624b\u3002\u4f55\u51b5\u8fe6\u6cd5\u521a\u521a\u88ab\u8d76\u4e0b\u53f0\uff0c\u535a\u514b\u672c\u53d6\u4ee3\u4e86\u4f4d\u7f6e\uff0c\u65b0\u4ec7\u65e7\u6028\u52a0\u8d77\u6765\uff0c\u8981\u6307\u671b\u5979\u5e2e\u5fd9\u624d\u602a\u3002</p><p>\u7136\u800c\u2026\u2026\u5979\u7ad9\u5f97\u4f4d\u7f6e\u5f88\u4e0d\u5bf9\uff0c\u8ddd\u79bb\u95e8\u5f88\u8fdc\uff0c\u8ddd\u79bb\u6076\u9b54\u5374\u5f88\u8fd1\uff0c\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u535a\u514b\u672c\u82e5\u662f\u88ab\u8ff7\u8bf1\u9b54\u6740\u4e86\uff0c\u8fe6\u6cd5\u5c31\u5e94\u8be5\u662f\u63a5\u4e0b\u6765\u7684\u7b2c\u4e8c\u4e2a\u76ee\u6807\u3002\u5979\u600e\u4e48\u4f1a\u5982\u6b64\u9547\u5b9a\u81ea\u82e5\uff1f</p><p>\u535a\u514b\u672c\u8111\u4e2d\u7075\u5149\u4e00\u95ea\uff0c\u4e00\u4e2a\u7b54\u6848\u547c\u4e4b\u6b32\u51fa\u3002</p><p>\u201c\u662f\u5979\u5728\u63a7\u5236\u6076\u9b54\uff01\u201d</p><p>\u4f9d\u636e\u771f\u540d\u53ec\u5524\u6076\u9b54\uff0c\u4e0d\u5b58\u5728\u5192\u540d\u9876\u66ff\u7684\u60c5\u51b5\uff1b\u968f\u673a\u6307\u5b9a\uff0c\u4e5f\u57fa\u672c\u6392\u9664\u88ab\u795e\u540e\u7684\u654c\u4eba\u6536\u4e70\u7684\u53ef\u80fd\uff1b\u800c\u4e14\u8fd9\u53ea\u8ff7\u8bf1\u9b54\u4e00\u76f4\u597d\u597d\u7684\uff0c\u7a81\u7136\u53d1\u72c2\uff0c\u4e5f\u4e0d\u50cf\u662f\u65e9\u6709\u9884\u8c0b\u3002\u535a\u514b\u672c\u8111\u4e2d\u539f\u672c\u8f6c\u4e86\u51e0\u4e2a\u5ff5\u5934\uff0c\u7740\u5b9e\u89c9\u5f97\u8e4a\u8df7\u5f97\u5f88\uff0c\u53ea\u662f\u5927\u654c\u5f53\u524d\uff0c\u65e0\u6687\u5206\u795e\u3002\u5982\u4eca\u88ab\u8fe6\u6cd5\u7684\u6a21\u6837\u4e00\u63d0\u9192\uff0c\u9661\u7136\u53cd\u5e94\u8fc7\u6765\uff1a\u83ab\u975e\u8ff7\u8bf1\u9b54\u662f\u88ab\u5979\u6697\u4e2d\u63a7\u5236\u4e86\uff1f</p><p>\u5c31\u50cf\u535a\u514b\u672c\u521a\u624d\u5c1d\u8bd5\u7684\u90a3\u6837\uff0c\u7f57\u4e1d\u7267\u5e08\u90fd\u53ef\u4ee5\u548c\u795e\u540e\u624b\u4e0b\u7684\u6076\u9b54\u6c9f\u901a\uff0c\u751a\u81f3\u53ef\u4ee5\u76f4\u63a5\u63a7\u5236\uff0c\u4f4d\u9636\u8d8a\u9ad8\u7684\u7267\u5e08\uff0c\u63a7\u5236\u80fd\u529b\u8d8a\u5f3a\u3002\u535a\u514b\u672c\u5df2\u7ecf\u662f\u4f4d\u9636\u6700\u9ad8\u7684\u7267\u5e08\u4e4b\u4e00\uff0c\u5979\u521a\u624d\u6253\u7b97\u63a7\u5236\u6076\u9b54\u5374\u5931\u8d25\u4e86\uff0c\u8fd9\u5f88\u6709\u53ef\u80fd\u662f\u53e6\u5916\u4e00\u4f4d\u6bd4\u5979\u66f4\u5f3a\u5927\u2014\u2014\u81f3\u5c11\u662f\u540c\u6837\u5f3a\u5927\u2014\u2014\u7684\u7267\u5e08\uff0c\u62a2\u5148\u4e0b\u624b\u63a7\u5236\u4e86\u76ee\u6807\u3002</p><p>\u7f57\u4e1d\u7684\u7267\u5e08\uff0c\u4f4d\u9636\u9ad8\u4f4e\u770b\u4f7f\u7528\u7684\u86c7\u97ad\u5c31\u6e05\u695a\uff0c\u86c7\u9996\u8d8a\u591a\u5219\u4f4d\u9636\u8d8a\u9ad8\uff0c\u4e03\u9996\u5c31\u662f\u9876\u70b9\u3002\u73b0\u5982\u4eca\u5728\u6574\u4e2a\u74dc\u7406\u5fb7\u65af\u57ce\u4e2d\uff0c\u53ea\u6709\u56db\u540d\u7267\u5e08\u6709\u8fd9\u6837\u7684\u6b8a\u8363\uff1a\u83f2\u5c14\u4f26\u5bb6\u65cf\u7684\u4e3b\u6bcd\u548c\u957f\u5973\u8fe6\u6cd5\uff0c\u535a\u514b\u672c\u5bb6\u65cf\u7684\u4e3b\u6bcd\u548c\u957f\u5973\u62c9\u6587\u3002\u5c31\u8fde\u66fe\u7ecf\u7684\u7b2c\u56db\u5bb6\u65cf\u745e\u8d39\u5fb7\uff0c\u4e3b\u6bcd\u59ae\u745f\u4e5f\u53ea\u6709\u516d\u9996\u86c7\u97ad\u3002</p><p>\u63a7\u5236\u6076\u9b54\u9700\u8981\u8ddd\u79bb\u5f88\u8fd1\uff0c\u4e24\u5927\u5bb6\u65cf\u7684\u4e3b\u6bcd\u90fd\u4e0d\u5728\u573a\uff0c\u552f\u4e00\u80fd\u591f\u548c\u62c9\u6587\xb7\u535a\u514b\u672c\u5bf9\u6297\u7684\uff0c\u5c31\u53ea\u6709\u8fe6\u6cd5\u3002</p><p>\u5ff5\u5934\u95ea\u7535\u4f3c\u7684\u6025\u901f\u4ece\u8111\u4e2d\u63a0\u8fc7\uff0c\u65b0\u4efb\u6b21\u5e2d\u6559\u5b98\u8d8a\u60f3\u8d8a\u89c9\u5f97\u81ea\u5df1\u7684\u63a8\u65ad\u6b63\u786e\uff0c\u8fe6\u6cd5\u8138\u4e0a\u5632\u8bbd\u7684\u51b7\u7b11\u6b64\u523b\u770b\u8d77\u6765\u5206\u5916\u72f0\u72de\u3002\u535a\u514b\u672c\u5927\u543c\u8d77\u6765\uff0c\u201c\u83f2\u5c14\u4f26\uff0c\u4f60\u84c4\u610f\u7834\u574f\u4eea\u5f0f\u2026\u2026\u201d</p><p>\u8fe6\u6cd5\u7684\u8138\u8272\u4e00\u53d8\uff0c\u6b63\u8981\u8bf4\u8bdd\uff0c\u4e00\u9053\u9ed1\u5f71\u201c\u547c\u201d\u5730\u4ece\u773c\u524d\u63a0\u8fc7\uff0c\u5e26\u8d77\u51cc\u5389\u98ce\u58f0\u3002\u8ff7\u8bf1\u9b54\u642c\u8d77\u8eab\u8fb9\u7684\u706b\u7089\uff0c\u5f53\u4f5c\u77f3\u5757\u4f3c\u7684\u671d\u535a\u514b\u672c\u7838\u6765\u3002\u6559\u5b98\u987e\u4e0d\u5f97\u6307\u8d23\u8fe6\u6cd5\uff0c\u72fc\u72c8\u5730\u6251\u5730\u8eb2\u5f00\uff0c\u53e6\u5916\u4e24\u4f4d\u6765\u81ea\u535a\u514b\u672c\u5bb6\u65cf\u7684\u7267\u5e08\u6b64\u65f6\u7ec8\u4e8e\u6709\u4e86\u52a8\u4f5c\uff0c\u5979\u4eec\u540c\u65f6\u91ca\u653e\u4e86\u4e00\u9053\u9ed1\u6697\u5229\u7bad\uff0c\u6682\u65f6\u963b\u6b62\u4e86\u8ff7\u8bf1\u9b54\u7684\u8fdb\u4e00\u6b65\u653b\u51fb\u3002</p><p>\u201c\u6740\u4e86\u5b83\uff01\u201d\u535a\u514b\u672c\u5389\u58f0\u547d\u4ee4\u7740\u6240\u6709\u7267\u5e08\uff0c\u201c\u5426\u5219\u6211\u4e00\u4e2a\u4e2a\u628a\u4f60\u4eec\u53d8\u6210\u86db\u5316\u7cbe\u7075\uff01\u201d</p><p>\u6216\u8bb8\u662f\u8fd9\u79cd\u660e\u663e\u4e0d\u53ef\u80fd\u5151\u73b0\u7684\u5a01\u80c1\u8d77\u4e86\u4f5c\u7528\uff0c\u5728\u573a\u7684\u5341\u51e0\u4e2a\u7267\u5e08\u5f00\u59cb\u53d1\u52a8\u653b\u51fb\u3002\u535a\u514b\u672c\u5219\u671d\u8fe6\u6cd5\u6251\u53bb\uff0c\u5979\u5df2\u7ecf\u8ba4\u5b9a\u662f\u5bf9\u65b9\u5728\u6363\u9b3c\uff0c\u53ea\u8981\u6253\u5012\u63a7\u5236\u8005\uff0c\u6076\u9b54\u4e5f\u5c31\u76f8\u5bf9\u5bb9\u6613\u641e\u5b9a\u4e86\u3002\u8fe6\u6cd5\u731d\u4e0d\u53ca\u9632\u4e4b\u4e0b\uff0c\u4e00\u65f6\u843d\u4e86\u4e0b\u98ce\uff0c\u82e6\u82e6\u652f\u6491\u7740\uff0c\u5b66\u9662\u4e2d\u4e24\u4f4d\u4f4d\u9636\u6700\u9ad8\u7684\u7267\u5e08\u5c31\u8fd9\u6837\u5f53\u573a\u6597\u6bb4\u8d77\u6765\u3002</p><p>\u7136\u800c\u535a\u514b\u672c\u7684\u731c\u6d4b\u4f3c\u4e4e\u9519\u4e86\uff0c\u8ff7\u8bf1\u9b54\u4e1d\u6beb\u6ca1\u6709\u56e0\u4e3a\u8fe6\u6cd5\u88ab\u538b\u5236\u800c\u6c14\u7130\u964d\u4f4e\uff0c\u53cd\u800c\u66f4\u52a0\u56a3\u5f20\u72c2\u66b4\u8d77\u6765\u3002\u56db\u67c4\u95ea\u7740\u6df7\u4e71\u5149\u6cfd\u7684\u900f\u660e\u6218\u9524\u5728\u5b83\u624b\u4e2d\u51fa\u73b0\uff0c\u98de\u7838\u51fa\u53bb\uff0c\u5176\u4e2d\u4e24\u67c4\u843d\u7a7a\uff0c\u4e00\u67c4\u7838\u4e2d\u4e86\u67d0\u4e2a\u7267\u5e08\u7684\u8111\u888b\uff0c\u5979\u5f53\u5373\u540e\u4ef0\u5012\u5730\uff0c\u6700\u540e\u4e00\u67c4\u5219\u659c\u523a\u91cc\u6b63\u649e\u5728\u535a\u514b\u672c\u7684\u8170\u95f4\u3002</p><p>\u8d64\u88f8\u7684\u8eab\u4f53\u5b8c\u5168\u62b5\u6297\u4e0d\u4f4f\u8fd9\u6837\u7684\u653b\u51fb\uff0c\u6559\u5b98\u767b\u65f6\u5f2f\u4e0b\u8170\u6765\uff0c\u5018\u82e5\u5e73\u65f6\uff0c\u5012\u4e5f\u7f62\u4e86\uff0c\u8ff7\u8bf1\u9b54\u8fd9\u6df7\u6c8c\u4e4b\u9524\u5e76\u4e0d\u7b97\u4ec0\u4e48\u9ad8\u660e\u62db\u6570\uff0c\u4e0d\u4f1a\u9020\u6210\u771f\u6b63\u4f24\u5bb3\u3002\u4f46\u5982\u4eca\u6b63\u662f\u4e89\u6597\u7684\u65f6\u5019\uff0c\u54ea\u91cc\u5bb9\u5f97\u8fd9\u4e9b\u8bb8\u7684\u7834\u7efd\uff0c\u8fe6\u6cd5\u6b63\u5de6\u652f\u53f3\u7ecc\uff0c\u9669\u8c61\u73af\u751f\uff0c\u773c\u770b\u5bf9\u624b\u7a81\u7136\u5f2f\u8170\uff0c\u4e0d\u5047\u601d\u7d22\u5c31\u662f\u4e00\u97ad\u671d\u80cc\u4e0a\u7838\u4e0b\u3002</p><p>\u4e03\u9897\u86c7\u5934\u540c\u65f6\u54ac\u4e2d\u4e86\u810a\u80cc\uff0c\u4e03\u79cd\u4e0d\u540c\u7684\u6bd2\u6db2\u6ce8\u5165\u8eab\u4f53\uff0c\u535a\u514b\u672c\u767b\u65f6\u5267\u70c8\u98a4\u6296\u8d77\u6765\uff0c\u6454\u5012\u5728\u5730\u4e0d\u80fd\u52a8\u5f39\u3002\u603b\u7b97\u5979\u662f\u9ad8\u9636\u796d\u53f8\uff0c\u5bf9\u6bd2\u7d20\u6709\u5f88\u5f3a\u7684\u62b5\u6297\u80fd\u529b\uff0c\u4e0d\u81f3\u4e8e\u5f53\u5373\u6bd9\u547d\u3002\u8fe6\u6cd5\u5012\u5e76\u4e0d\u5728\u4e4e\uff0c\u5728\u573a\u6240\u6709\u7267\u5e08\u90fd\u53ef\u4ee5\u4f5c\u8bc1\uff0c\u662f\u535a\u514b\u672c\u9996\u5148\u53d1\u52a8\u653b\u51fb\uff0c\u5979\u53ea\u662f\u88ab\u8feb\u81ea\u536b\u3002\u81f3\u4e8e\u63a7\u5236\u6076\u9b54\u7834\u574f\u4eea\u5f0f\u4e4b\u7c7b\uff0c\u8fe6\u6cd5\u5012\u662f\u6709\u6043\u65e0\u6050\uff0c\u5e76\u4e0d\u5bb3\u6015\u3002</p><p>\u793c\u5802\u4e2d\u7684\u5c40\u52bf\u66f4\u52a0\u6df7\u4e71\uff0c\u8ff7\u8bf1\u9b54\u5df2\u7ecf\u53c8\u6253\u5012\u4e86\u4e09\u540d\u7267\u5e08\uff0c\u800c\u5b83\u81ea\u5df1\u4e5f\u4f24\u75d5\u7d2f\u7d2f\uff0c\u4e00\u53ea\u524d\u80a2\u88ab\u70e7\u6210\u4e86\u7070\u70ec\uff0c\u80f8\u53e3\u88ab\u5229\u5203\u58c1\u969c\u5207\u5f00\u7684\u4f24\u53e3\u968f\u7740\u5267\u70c8\u8fd0\u52a8\u4e0d\u65ad\u6269\u5927\u3002\u5b83\u60f3\u9003\u79bb\u6b64\u5730\uff0c\u8fd4\u56de\u6df1\u6e0a\uff0c\u7136\u800c\u529e\u4e0d\u5230\uff0c\u6709\u4e00\u79cd\u5f3a\u5927\u7684\u529b\u91cf\u538b\u5236\u4f4f\u4e86\u5b83\uff0c\u903c\u8feb\u6076\u9b54\u4fef\u9996\u542c\u547d\u3002</p><p>\u6709\u4e24\u4e2a\u80c6\u602f\u7684\u7267\u5e08\u89c1\u52bf\u4e0d\u5999\uff0c\u5df2\u7ecf\u60f3\u8981\u9003\u8dd1\uff0c\u4f46\u5979\u4eec\u649e\u4e0d\u5f00\u88ab\u5c01\u4f4f\u7684\u95e8\u3002\u6b63\u5728\u6b64\u65f6\uff0c\u5916\u9762\u7684\u957f\u5eca\u91cc\u4f20\u6765\u811a\u6b65\u58f0\uff0c\u7d27\u63a5\u7740\u662f\u5371\u9669\u7684\u5492\u8bed\u541f\u5531\u3002\u8fd8\u6ca1\u7b49\u53cd\u5e94\u8fc7\u6765\uff0c\u95e8\u8f70\u7136\u4e00\u58f0\u88ab\u649e\u98de\u4e86\uff0c\u6c79\u6d8c\u7684\u6c14\u6d6a\u81ea\u5916\u51b2\u5165\uff0c\u7d27\u63a5\u7740\u8d70\u8fdb\u6765\u4e24\u4e2a\u4eba\uff0c\u540e\u9762\u662f\u65b0\u52a0\u5165\u7684\u6559\u5b98\u7ef4\u5eb7\u5c3c\u4e9a\uff0c\u524d\u9762\u5219\u662f\u5979\u9080\u8bf7\u6765\u7684\u5ba2\u4eba\uff0c\u90a3\u4e2a\u53eb\u743c\u6069\u7684\u4eba\u7c7b\u5deb\u5e08\u3002</p><p>\u203b\u203b\u203b</p><p>\u65e9\u5728\u9a9a\u52a8\u521a\u8d77\u65f6\uff0c\u5916\u9762\u9732\u5929\u5e73\u53f0\u4e0a\u7684\u743c\u6069\u548c\u7ef4\u5eb7\u5c3c\u4e9a\u5c31\u5df2\u7ecf\u542c\u5230\u4e86\u52a8\u9759\uff0c\u4f46\u4ed6\u4eec\u5e76\u6ca1\u6709\u7acb\u523b\u8d76\u53bb\u2014\u2014\u540e\u8005\u662f\u8eab\u4f53\u53d7\u5236\uff0c\u65e0\u6cd5\u52a8\u5f39\uff0c\u524d\u8005\u662f\u538b\u6839\u6ca1\u8fd9\u4e2a\u6253\u7b97\u3002</p><p>\u201c\u771f\u4e0d\u9519\uff0c\u796d\u53f8\u5c0f\u59d0\uff0c\u201d\u743c\u6069\u62cd\u6253\u7740\u5353\u5c14\u5c11\u5973\u4e30\u6ee1\u7684\u81c0\u90e8\uff0c\u201c\u800c\u4e14\u6211\u6ca1\u60f3\u5230\u4f60\u8fd9\u91cc\u8fd8\u662f\u5904\u5973\u3002\u201d</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u6ca1\u6709\u56de\u7b54\uff0c\u4e00\u76f4\u4ee5\u6765\u7684\u6323\u624e\u5df2\u7ecf\u8ba9\u5979\u4e27\u5931\u4e86\u529b\u6c14\uff0c\u800c\u4e14\u73b0\u5728\u611f\u89c9\u4e5f\u4e0d\u662f\u90a3\u4e48\u96be\u53d7\u3002\u4e8b\u5b9e\u4e0a\uff0c\u5979\u5df2\u7ecf\u6e10\u6e10\u6709\u4e9b\u559c\u6b22\u4e0a\u4e86\uff0c\u53ea\u662f\u4e00\u65f6\u4e0d\u613f\u610f\u627f\u8ba4\u3002\u201c\u793c\u5802\u91cc\u597d\u50cf\u53d1\u751f\u4e86\u4ec0\u4e48\u3002\u201d\u5979\u5598\u606f\u7740\uff0c\u65ad\u65ad\u7eed\u7eed\u5730\u8bf4\u3002</p><p>\u201c\u6ca1\u5173\u7cfb\uff0c\u201d\u743c\u6069\u4e0d\u4ee5\u4e3a\u7136\uff0c\u201c\u53cd\u6b63\u548c\u6211\u4eec\u65e0\u5173\u3002\u201d</p><p>\u4ed6\u81ea\u7136\u53ef\u4ee5\u8fd9\u4e48\u60f3\uff0c\u4f46\u7ef4\u5eb7\u5c3c\u4e9a\u5c31\u4e0d\u80fd\u5982\u6b64\u4e86\uff0c\u65e0\u8bba\u600e\u4e48\u8bf4\u5979\u4e5f\u662f\u796d\u53f8\uff0c\u800c\u4e14\u662f\u65b0\u4efb\u6559\u5b98\uff0c\u5982\u4eca\u6bd5\u4e1a\u5178\u793c\u4e0a\u660e\u663e\u53d1\u751f\u4e86\u4ec0\u4e48\u610f\u5916\u4e8b\u60c5\uff0c\u5426\u5219\u51b3\u4e0d\u4f1a\u6709\u5982\u6b64\u5f20\u7687\u5931\u63aa\u7684\u58f0\u97f3\uff0c\u800c\u4e14\u8fd8\u6709\u5b66\u751f\u8dd1\u51fa\u6765\u3002\u4f46\u5979\u6b64\u523b\u53cc\u624b\u88ab\u7262\u7262\u675f\u7f1a\u5728\u680f\u6746\u4e0a\uff0c\u8eab\u8f6f\u5982\u68c9\uff0c\u5018\u82e5\u4e0d\u662f\u743c\u6069\u5728\u80cc\u540e\u652f\u6491\u7740\uff0c\u65e9\u5c31\u5df2\u7ecf\u8dea\u5012\u4e0b\u53bb\uff0c\u8fde\u5934\u8111\u90fd\u5df2\u7ecf\u662f\u6655\u6655\u6c89\u6c89\uff0c\u4ec0\u4e48\u90fd\u505a\u4e0d\u4e86\uff0c\u53ea\u80fd\u4efb\u7531\u6b3a\u51cc\u3002</p><p>\u5c31\u5728\u6b64\u65f6\uff0c\u7a81\u7136\u611f\u89c9\u743c\u6069\u4ece\u81ea\u5df1\u7684\u8eab\u4f53\u91cc\u5b8c\u5168\u9000\u4e86\u51fa\u53bb\uff0c\u7136\u800c\u5979\u5e76\u6ca1\u6709\u611f\u89c9\u5230\u5bf9\u65b9\u53d1\u5c04\u3002\u6b63\u7591\u60d1\u95f4\uff0c\u4e00\u53ea\u624b\u629a\u4e0a\u8116\u9888\uff0c\u5c06\u90a3\u4e32\u4ee3\u8868\u6559\u5b98\u8eab\u4efd\u7684\u9879\u94fe\u6458\u4e86\u4e0b\u6765\u3002</p><p>\u201c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d</p><p>\u743c\u6069\u7528\u884c\u52a8\u56de\u7b54\u4e86\u8fd9\u4e2a\u95ee\u9898\uff0c\u4ed6\u7a0d\u7a0d\u538b\u4f4e\u8eab\u4f53\uff0c\u91cd\u65b0\u8d70\u524d\u95e8\uff0c\u53f3\u624b\u5374\u637b\u7740\u9879\u94fe\uff0c\u5c06\u516b\u679a\u6676\u83b9\u5254\u900f\u7684\u73e0\u5b50\u4e00\u7c92\u4e00\u7c92\u5730\u585e\u8fdb\u4e86\u5353\u5c14\u7684\u7f8e\u81c0\u4e2d\uff0c\u586b\u8865\u4e86\u521a\u521a\u79bb\u5f00\u7684\u7a7a\u865a\u3002\u706b\u70ed\u7684\u8089\u4f53\u548c\u51b0\u51b7\u7684\u5b9d\u77f3\u4e00\u524d\u4e00\u540e\u540c\u65f6\u8fdb\u5165\u4f53\u5185\uff0c\u76f8\u4e92\u523a\u6fc0\u7740\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u518d\u4e00\u6b21\u5730\u5fcd\u4e0d\u4f4f\u547b\u541f\u8d77\u6765\uff0c\u5168\u8eab\u6bcf\u4e00\u6839\u795e\u7ecf\u7d27\u7ef7\uff0c\u8fde\u811a\u8dbe\u5934\u90fd\u7981\u4e0d\u4f4f\u5730\u5f2f\u66f2\u3002</p><p>\u6700\u7ec8\uff0c\u743c\u6069\u6ee1\u610f\u5730\u653e\u5f00\u4e86\u5979\uff0c\u5374\u6ca1\u6709\u5c06\u9879\u94fe\u53d6\u51fa\uff0c\u4e5f\u4e0d\u5141\u8bb8\u5979\u53d6\u51fa\u3002\u4e24\u4eba\u5306\u5306\u7a7f\u4e0a\u8863\u670d\u8d76\u56de\u793c\u5802\uff0c\u5374\u53d1\u73b0\u95e8\u5df2\u7ecf\u88ab\u5c01\u4e0a\u4e86\u3002\u4e0d\u8fc7\u8fd9\u96be\u4e0d\u5012\u743c\u6069\uff0c\u4ed6\u76f4\u63a5\u7528\u5492\u8bed\u786c\u649e\u5f00\u4e86\u95e8\uff0c\u7136\u540e\u53d1\u73b0\u539f\u672c\u9999\u8273\u6deb\u7cdc\u7684\u6bd5\u4e1a\u5178\u793c\u5df2\u7ecf\u5f04\u5f97\u4e00\u584c\u7cca\u6d82\u4e86\u3002</p><p>\u8ff7\u8bf1\u9b54\u539f\u672c\u5df2\u7ecf\u91cd\u4f24\u5c06\u6b7b\uff0c\u743c\u6069\u6070\u5230\u597d\u5904\u5730\u8865\u4e0a\u4e00\u53d1\u89e3\u79bb\uff0c\u5b8c\u7f8e\u5730\u6bc1\u5c38\u706d\u8ff9\uff0c\u53cd\u5012\u662f\u628a\u529f\u52b3\u5168\u90fd\u62a2\u4e86\u8fc7\u6765\uff0c\u867d\u7136\u4ed6\u5176\u5b9e\u4e5f\u65e0\u610f\u4e89\u7ade\u3002\u7ecf\u8fc7\u8fd9\u4e00\u573a\u95f9\u817e\uff0c\u5b66\u9662\u91cc\u7684\u5b66\u751f\u5012\u662f\u4e0d\u66fe\u53d7\u5230\u4ec0\u4e48\u4f24\u4ea1\uff0c\u9664\u4e86\u7b2c\u4e00\u540d\u6bd5\u4e1a\u751f\u88ab\u8ff7\u8bf1\u9b54\u6240\u6740\uff0c\u4f46\u6559\u5b98\u5374\u5f53\u573a\u6b7b\u4e86\u4e09\u540d\uff0c\u4ee5\u53ca\u4e24\u540d\u53d7\u4f24\u8005\uff0c\u5176\u4e2d\u5c31\u5305\u62ec\u521a\u521a\u63a5\u4efb\u6b21\u5e2d\u6559\u5b98\u7684\u62c9\u6587\xb7\u535a\u514b\u672c\u3002\u5979\u88f8\u4f53\u6328\u4e86\u8fe6\u6cd5\u4e00\u97ad\uff0c\u6bd2\u7d20\u6ce8\u5165\uff0c\u9669\u4e9b\u4e27\u547d\uff0c\u603b\u7b97\u662f\u7528\u89e3\u6bd2\u836f\u5242\u6551\u4e86\u56de\u6765\uff0c\u53ea\u662f\u5341\u5929\u534a\u6708\u4e4b\u5185\uff0c\u6015\u662f\u8d77\u4e0d\u4e86\u5e8a\u4e86\u3002</p><p>\u5fd9\u4e71\u5b8c\u6bd5\uff0c\u743c\u6069\u548c\u7ef4\u5eb7\u5c3c\u4e9a\u8fd4\u56de\u83f2\u5c14\u4f26\u5b85\u9662\uff0c\u4e00\u8def\u4e0a\u4e24\u4eba\u6c89\u9ed8\u4e0d\u8bed\u3002\u5feb\u5230\u9ad8\u5d16\u7684\u65f6\u5019\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u7a81\u7136\u505c\u4f4f\u811a\u6b65\u3002</p><p>\u201c\u4f60\u65e9\u77e5\u9053\u90a3\u4e2a\u8ff7\u8bf1\u9b54\u4f1a\u53d1\u72c2\u5427\uff1f\u201d\u5979\u51b7\u6de1\u5730\u95ee\u3002</p></article>"}}}]);
