(window.webpackJsonp=window.webpackJsonp||[]).push([[278],{301:function(p){p.exports={chapterTitle:null,title:"\u7b2c278\u7ae0 \u5bb6\u65cf\u9ebb\u70e6",content:"<article><h2>\u7b2c278\u7ae0 \u5bb6\u65cf\u9ebb\u70e6</h2><p>\u3000\u3000\u5c06\u848b\u8587\u9001\u56de\u5bb6\uff0c\u5f20\u606a\u5f00\u8f66\u8f7d\u7740\u5510\u5a67\u53bb\u770b\u4ed6\u5bb6\u7684\u65b0\u623f\u5b50\u3002</p><p>\u3000\u3000\u8fd9\u6b21\u6362\u623f\u5b50\uff0c\u4e0d\u4ec5\u662f\u5f20\u606a\u5bb6\u3001\u5f20\u77e5\u975e\u5bb6\uff0c\u8c22\u665a\u6674\u4e5f\u8981\u5c06\u5c0f\u9526\u6e56\u7554\u7684\u90a3\u680b\u522b\u5885\u697c\u8fd8\u7ed9\u5510\u5b66\u8c26\u5bb6\uff0c\u90a3\u91cc\u6bd5\u7adf\u662f\u5e02\u91cc\u7ed9\u5e02\u59d4\u9886\u5bfc\u51c6\u5907\u7684\u4f4f\u5b85\uff0c\u4e0d\u80fd\u4e00\u76f4\u9738\u5360\u4e0b\u53bb\u3002</p><p>\u3000\u3000\u4e5d\u4e94\u5e74\u5f00\u53d1\u51fa\u6765\u7684\u5546\u54c1\u4f4f\u5b85\u5f88\u6709\u9650\uff0c\u9ad8\u6863\u522b\u5885\u4f4f\u5b85\u66f4\u5c11\uff0c\u5c31\u7b97\u5076\u5c14\u6709\uff0c\u4e5f\u8981\u7b49\u5230\u673a\u4f1a\u624d\u884c\u3002\u5f88\u591a\u9ad8\u6863\u4f4f\u5b85\u90fd\u662f\u4f01\u4e8b\u4e1a\u673a\u5173\u81ea\u5df1\u641e\u7684\u798f\u5229\u623f\uff0c\u53ea\u5728\u5185\u90e8\u5206\u914d\uff0c\u6839\u672c\u4e0d\u4f1a\u5411\u5916\u754c\u53d1\u552e\uff0c\u4e5f\u6ca1\u6709\u6743\u5229\u5411\u5916\u754c\u53d1\u552e\u3002\u60f3\u4f4f\u597d\u623f\u5b50\uff0c\u6839\u672c\u6ca1\u6709\u9009\u62e9\uff0c\u53ea\u6709\u7b49\u5f85\u673a\u4f1a\u3002\u82e5\u662f\u4e13\u95e8\u4e70\u5757\u5730\u5efa\u4e09\u680b\u522b\u5885\uff0c\u4e5f\u592a\u715e\u6709\u4ecb\u4e8b\u4e86\u3002</p><p>\u3000\u3000\u8fd9\u6b21\u90ae\u7535\u5c40\u5728\u6587\u5c71\u996d\u5e97\u4e1c\u8fb9\u3001\u5c0f\u9526\u6e56\u7684\u6b63\u5357\u8fb9\u65b0\u5efa\u4e86\u804c\u5de5\u4f4f\u5b85\u533a\uff0c\u7ad9\u5728\u9526\u6e56\u5927\u9053\u4e0a\u770b\u8fc7\u53bb\uff0c\u90fd\u662f\u516d\u5c42\u7684\u804c\u5de5\u5bbf\u820d\u697c\uff0c\u4f46\u662f\u4ece\u5c0f\u533a\u7684\u5927\u95e8\u62d0\u8fdb\u53bb\uff0c\u8fd8\u6709\u4e00\u9053\u95e8\uff0c\u91cc\u9762\u5efa\u7740\u8fd1\u5341\u51e0\u680b\u4e24\u5c42\u7684\u5c0f\u6d0b\u697c\uff0c\u5b8f\u8fdc\u5b9e\u4e1a\u662f\u627f\u5efa\u5355\u4f4d\uff0c\u5f20\u606a\u63d0\u51fa\u8981\u6362\u623f\u5b50\uff0c\u5f20\u77e5\u975e\u5c31\u79c1\u4e0b\u91cc\u8ddf\u90ae\u7535\u5c40\u8981\u4e86\u4e09\u680b\u5c0f\u697c\u62b5\u4e00\u90e8\u5206\u7684\u5efa\u9020\u6b3e\u3002</p><p>\u3000\u3000\u636e\u8bf4\u5c0f\u533a\u91cc\u8fd8\u4f1a\u8bbe\u4e00\u9053\u5185\u5c97\uff0c\u6d0b\u697c\u533a\u7684\u6cbb\u5b89\u73af\u5883\u4f1a\u975e\u5e38\u7684\u597d\u3002</p><p>\u3000\u3000\u5f20\u606a\u60f3\u60f3\u4e5f\u53f9\u606f\uff0c\u5c0f\u9526\u6e56\u7554\u7684\u8fd9\u4e9b\u4f18\u8d28\u5730\u6bb5\uff0c\u90fd\u5df2\u7ecf\u8ba9\u5e02\u91cc\u7684\u5f3a\u52bf\u90e8\u95e8\u5206\u5272\u5e72\u51c0\u4e86\u3002</p><p>\u3000\u3000\u665a\u6674\u5df2\u7ecf\u9009\u4e0b\u4e00\u680b\uff0c\u5f20\u606a\u8ba9\u4ed6\u5988\u8d76\u8fc7\u6765\u770b\u623f\u5b50\uff0c\u4e5f\u662f\u4ece\u5269\u4e0b\u7684\u4e24\u680b\u91cc\u9009\u4e00\u680b\uff0c\u5f53\u7136\u8981\u5bf9\u5176\u4ed6\u7684\u5c0f\u697c\u52a8\u4e86\u5fc3\u601d\uff0c\u4e5f\u53ef\u4ee5\u8ddf\u90ae\u7535\u5c40\u79c1\u4e0b\u91cc\u8c03\u6d4e\uff0c\u4e0d\u8fc7\u5b9e\u5728\u6ca1\u6709\u5fc5\u8981\u505a\u90a3\u4e48\u8ba8\u4eba\u538c\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u5f20\u77e5\u975e\u8ddf\u90ae\u7535\u5c40\u8bf4\u8d77\u5f20\u77e5\u884c\u8981\u642c\u8fc7\u6765\uff0c\u8fd8\u6709\u4e00\u680b\u7ed9\u8c22\u665a\u6674\u5f53\u4e34\u65f6\u4f4f\u5904\uff0c\u90ae\u7535\u5c40\u5df2\u7ecf\u5c06\u6700\u597d\u7684\u4e09\u680b\u4e34\u6e56\u6d0b\u697c\u8ba9\u51fa\u6765\uff0c\u4e5f\u4e0d\u53ef\u80fd\u6709\u4ec0\u4e48\u4e0d\u6ee1\u610f\u7684\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u5f00\u8f66\u8fdb\u4e86\u5c0f\u533a\uff0c\u770b\u89c1\u6700\u4e1c\u5317\u89d2\u4e0a\u5e76\u6392\u7684\u4e24\u680b\u697c\u91cc\u90fd\u4eae\u4e86\u706f\uff0c\u5c0f\u53d4\u5728\u5de6\u8fb9\u7684\u4e00\u680b\u697c\u7684\u7a97\u5b50\u53e3\u63a2\u5934\u63a2\u8111\uff0c\u60f3\u5fc5\u662f\u770b\u5230\u4ed6\u4eec\u7684\u8f66\u5b50\u8fc7\u6765\u3002</p><p>\u3000\u3000\u56e0\u4e3a\u7ea7\u522b\u4e0e\u89c4\u683c\u7684\u539f\u56e0\uff0c\u90ae\u7535\u5c40\u7684\u5c0f\u6d0b\u697c\u6bd4\u5e02\u59d4\u7684\u522b\u5885\u8981\u5c0f\u4e00\u53f7\uff0c\u623f\u5f62\u5374\u66f4\u7f8e\u89c2\u4e00\u4e9b\uff0c\u9752\u77f3\u8d34\u9762\u7684\u57fa\u5ea7\uff0c\u5510\u5a67\u8df3\u4e0b\u8f66\uff0c\u5174\u594b\u7684\u8bf4\uff1a\u201c\u554a\uff0c\u4ee5\u540e\u8d70\u8def\u5c31\u80fd\u8fc7\u6765\u770b\u4f60\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u8fd9\u91cc\u79bb\u5e02\u59d4\u7684\u522b\u5885\uff0c\u5c31\u9694\u7740\u6587\u5c71\u996d\u5e97\u4e0e\u6d77\u5dde\u4e2d\u7b49\u5e08\u8303\u5b66\u6821\uff0c\u6563\u6b65\u8d70\u8fc7\u53bb\uff0c\u4e5f\u53ea\u8981\u5341\u51e0\u5206\u949f\uff0c\u7684\u786e\u662f\u5f88\u8fd1\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u4f38\u624b\u8f7b\u8f7b\u62cd\u4e86\u62cd\u5510\u5a67\u7684\u8138\u86cb\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u8981\u4e0d\u7ed9\u4f60\u7559\u4e2a\u623f\u95f4\uff1f\u4f60\u76f4\u63a5\u4f4f\u8fdb\u6765\u66f4\u597d\u3002\u201d</p><p>\u3000\u3000\u5510\u5a67\u5fae\u7f9e\u7684\u8bf4\uff1a\u201c\u4f60\u5012\u60f3\u5f97\u7f8e\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u8ddf\u5510\u5a67\u8d70\u8fdb\u53bb\uff0c\u9664\u4e86\u7238\u5988\u8ddf\u5c0f\u53d4\uff0c\u5c0f\u5a76\u5a76\u8ddf\u5802\u59b9\u5f20\u73ab\u90fd\u5728\u91cc\u9762\u3002</p><p>\u3000\u3000\u201c\u600e\u4e48\u8fd9\u4e48\u665a\u624d\u8fc7\u6765\uff1f\u201d\u6881\u683c\u73cd\u5e26\u7740\u8d23\u5907\u7684\u8bed\u6c14\u95ee\uff0c\u201c\u5927\u5bb6\u90fd\u7b49\u4f60\u597d\u4e00\u4f1a\u513f\u4e86\u3002\u201d\u4e0d\u8fc7\u770b\u5230\u5f20\u606a\u8eab\u540e\u5a07\u7f9e\u5f97\u8ddf\u5c0f\u5ab3\u5987\u4e00\u6837\u7684\u5510\u5a67\uff0c\u90a3\u4e9b\u6028\u6c14\u5c31\u70df\u6d88\u4e91\u6563\u4e86\u4e00\u5e72\u4e8c\u51c0\uff0c\u62c9\u8fc7\u5510\u5a67\u7684\u624b\uff0c\u201c\u4f60\u4eec\u5e74\u8f7b\u4eba\u773c\u5149\u65f6\u9ae6\uff0c\u5feb\u5e2e\u4f60\u59e8\u60f3\u60f3\u770b\uff0c\u8fd9\u623f\u5b50\u8981\u600e\u4e48\u5e03\u7f6e\u624d\u5408\u9002\uff0c\u8bf4\u4e0d\u5b9a\u4f60\u4ee5\u540e\u8ddf\u5f20\u606a\u8fd8\u5f97\u4f4f\u8fd9\u680b\u623f\u5b50\u91cc\u3002\u201d</p><p>\u3000\u3000\u201c\u54b3\u2026\u2026\u201d\u5f20\u606a\u8f7b\u54b3\u4e86\u4e24\u58f0\uff0c\u201c\u5988\uff0c\u5510\u5a67\u8fd8\u5728\u8bfb\u9ad8\u4e8c\uff0c\u4f60\u8bf4\u8fd9\u8bdd\u662f\u4e0d\u662f\u65e9\u4e86\u4e9b\uff1f\u201d</p><p>\u3000\u3000\u201c\u6709\u4ec0\u4e48\u65e9\u7684\uff1f\u5510\u5a67\u8fd9\u4e48\u6f02\u4eae\uff0c\u8fd9\u4e48\u806a\u660e\uff0c\u6211\u8981\u4e0d\u65e9\u5b9a\u4e0b\u6765\uff0c\u6307\u4e0d\u5b9a\u8ba9\u54ea\u4e2a\u7537\u5b69\u5b50\u7ed9\u62a2\u8dd1\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u6309\u7167\u5f20\u606a\u7684\u610f\u89c1\uff0c\u8ba9\u5bb6\u88c5\u8bbe\u8ba1\u5e08\u51fa\u51e0\u5957\u65b9\u6848\u968f\u4fbf\u9009\u4e00\u5957\u4e70\u9f50\u5bb6\u4ff1\u5c31\u884c\uff0c\u5988\u5988\u5374\u504f\u504f\u8981\u4eb2\u624b\u5e03\u7f6e\u623f\u5b50\uff0c\u6216\u8bb8\u5973\u4eba\u505a\u8fd9\u4e9b\u4e8b\u60c5\u6700\u6709\u6210\u5c31\u611f\uff0c\u770b\u7740\u5988\u5988\u62c9\u7740\u5510\u5a67\u3001\u5c0f\u5a76\u5a76\u70ed\u5207\u7684\u8ba8\u8bba\u8d77\u6765\uff0c\u8fde\u5f20\u73ab\u4e2b\u5934\u7247\u5b50\u5934\u4e5f\u51d1\u8fc7\u53bb\uff0c\u5f20\u606a\u5f88\u65e0\u5948\uff0c\u8ddf\u5c0f\u53d4\u3001\u7238\u7238\u5230\u5c4b\u5b50\u540e\u9762\u53bb\u804a\u5929\u3002</p><p>\u3000\u3000\u8ddf\u5e02\u59d4\u7684\u522b\u5885\u4e00\u6837\uff0c\u5c4b\u5b50\u540e\u9762\u4e5f\u6709\u4e00\u4e2a\u4e34\u6e56\u7684\u5ead\u9662\uff0c\u867d\u7136\u5c0f\u4e00\u4e9b\uff0c\u5374\u5f88\u7cbe\u81f4\uff0c\u94c1\u827a\u62a4\u680f\u5916\u8fd8\u6709\u786c\u6728\u642d\u6210\u7684\u4e34\u6c34\u53f0\u9636\uff0c\u53ef\u4ee5\u5f53\u5c0f\u7801\u5934\u7528\uff0c\u5f20\u606a\u60f3\u7740\u53ef\u4ee5\u4e70\u4e00\u6761\u5c0f\u8247\u7cfb\u5728\u90a3\u91cc\u3002</p><p>\u3000\u3000\u201c\u4f60\u5927\u4f2f\u8fd9\u6b21\u6709\u4e9b\u5c0f\u9ebb\u70e6\u2026\u2026\u201d\u5f20\u77e5\u884c\u4ece\u5f20\u77e5\u975e\u624b\u91cc\u63a5\u8fc7\u70df\uff0c\u6ca1\u7406\u4f1a\u5f20\u606a\uff0c\u65e0\u8bba\u518d\u600e\u4e48\u5c06\u5f20\u606a\u5f53\u4f5c\u6210\u5e74\u4eba\uff0c\u90fd\u4e0d\u4f1a\u4e3b\u52a8\u8ba9\u4ed6\u62bd\u70df\u3002</p><p>\u3000\u3000\u5f20\u606a\u6123\u4e86\u6123\uff0c\u4ed6\u867d\u7136\u5bf9\u5927\u4f2f\u4e00\u76f4\u6709\u6210\u89c1\uff0c\u4f46\u662f\u5728\u5916\u4eba\u770b\u6765\uff0c\u4ed6\u4eec\u6bd5\u7adf\u662f\u4e00\u4e2a\u5bb6\u65cf\u91cc\u7684\uff1a\u201c\u4ec0\u4e48\u9ebb\u70e6\uff1f\u201d</p><p>\u3000\u3000\u201c\u5f20\u5955\u56de\u4e1c\u793e\u4e4b\u540e\u8ddf\u4eba\u5bb6\u5408\u80a1\u5f00\u4e86\u5bb6\u516c\u53f8\uff0c\u6295\u5230\u516c\u53f8\u91cc\u7684\u94b1\u8ba9\u5408\u4f19\u7684\u90a3\u5c0f\u5b50\u5377\u8dd1\u4e86\u2026\u2026\u201d\u5f20\u77e5\u975e\u5e2e\u5f20\u77e5\u884c\u70b9\u4e0a\u70df\uff0c\u201c\u6570\u76ee\u5012\u662f\u4e0d\u5927\uff0c\u516d\u5341\u591a\u4e07\uff0c\u5173\u952e\u90a3\u94b1\u662f\u7ecf\u8d38\u5c40\u7684\u6b3e\u5b50\u3002\u6211\u5c06\u5f20\u5955\u8fd9\u6df7\u5c0f\u5b50\u8d76\u56de\u4e1c\u793e\uff0c\u4ed6\u4eec\u5fc3\u91cc\u4e5f\u6068\u4e86\u6c14\uff0c\u4ec0\u4e48\u4e8b\u90fd\u6342\u7740\uff0c\u4e0d\u8ddf\u8fd9\u8fb9\u8bf4\u4e00\u58f0\uff0c\u8fd9\u65f6\u5019\u6342\u4e0d\u4f4f\u4e86\uff0c\u624d\u6253\u7535\u8bdd\u8fc7\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u632a\u7528\u516c\u6b3e\u516d\u5341\u591a\u4e07\u4e5f\u4e0d\u662f\u5c0f\u6570\u5b57\uff0c\u5f20\u606a\u770b\u4e86\u5c0f\u53d4\u4e00\u773c\uff1a\u201c\u6342\u4e0d\u4f4f\u662f\u6307\u5df2\u7ecf\u7ed9\u67e5\u51fa\u6765\u4e86\uff1f\u201d</p><p>\u3000\u3000\u5f20\u77e5\u975e\u70b9\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u8981\u662f\u8fd8\u6ca1\u6709\u7ed9\u67e5\u51fa\u6765\uff0c\u516d\u5341\u591a\u4e07\u7684\u6b3e\u5b50\uff0c\u53ca\u65f6\u8865\u4e0a\u53bb\uff0c\u4e5f\u7b97\u4e0d\u4e0a\u4ec0\u4e48\u9ebb\u70e6\u4e8b\uff0c\u4e0d\u8fc7\u73b0\u5728\u4e5f\u53ea\u662f\u5185\u90e8\u638c\u63e1\uff0c\u7ed9\u4e86\u4e2a\u671f\u9650\u8fd8\u6b3e\u2026\u2026\u8fd9\u5927\u6982\u4e5f\u662f\u770b\u5728\u4e8c\u54e5\u7684\u9762\u5b50\u4e0a\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u6709\u4e9b\u5fc3\u70e6\uff0c\u62fe\u8d77\u4e00\u4e9b\u788e\u77f3\uff0c\u4e00\u7c92\u7c92\u7684\u4e22\u6c34\u91cc\u53bb\uff0c\u4ed6\u771f\u60f3\u6492\u624b\u4e0d\u7ba1\u8fd9\u4e9b\u9e1f\u4e8b\uff0c\u4f46\u662f\u5927\u4f2f\u5012\u4e0b\u6765\uff0c\u5916\u4eba\u4f1a\u600e\u4e48\u770b\u4ed6\u5f20\u5bb6\uff1f</p><p>\u3000\u3000\u5f20\u77e5\u975e\u770b\u4e86\u5f20\u77e5\u884c\u4e00\u773c\uff0c\u5df2\u7ecf\u4e0d\u77e5\u4e0d\u89c9\u4e2d\uff0c\u5927\u5bb6\u90fd\u628a\u5f20\u606a\u770b\u6210\u8fd9\u4e2a\u5bb6\u65cf\u7684\u91cd\u5fc3\u4e86\uff0c\u7279\u522b\u662f\u5f20\u606a\u8fd9\u77ed\u77ed\u4e00\u5e74\u65f6\u95f4\u91cc\uff0c\u660e\u91cc\u6697\u91cc\u7684\u8d44\u4ea7\u90fd\u4e0a\u4ebf\u4e86\uff0c\u8fd9\u79cd\u5947\u8ff9\u53d1\u751f\u4efb\u4f55\u4eba\u8eab\u4e0a\uff0c\u90fd\u4f1a\u8d62\u5f97\u8eab\u8fb9\u4eba\u8db3\u591f\u7684\u5c0a\u91cd\u3002</p><p>\u3000\u3000\u8fd9\u79cd\u4e8b\u60c5\uff0c\u672c\u6765\u5f20\u77e5\u975e\u672c\u53ef\u4ee5\u8ddf\u5f20\u77e5\u884c\u5546\u91cf\u7740\u62ff\u4e3b\u610f\uff0c\u8fd8\u662f\u89c9\u5f97\u6709\u5fc5\u8981\u6765\u95ee\u4e00\u4e0b\u5f20\u606a\u7684\u610f\u89c1\u3002</p><p>\u3000\u3000\u5f20\u606a\u5c06\u624b\u91cc\u7684\u77f3\u5b50\u4e22\u5b8c\uff0c\u624d\u8bf4\u9053\uff1a\u201c\u8fd9\u4e8b\u5f97\u78e8\u4e00\u78e8\uff0c\u4e0d\u5f97\u6559\u8bad\u4e0d\u884c\uff0c\u7279\u522b\u662f\u5f20\u5955\uff0c\u4ed6\u4ee5\u540e\u7684\u8def\u8fd8\u957f\u7740\u5462\uff0c\u5c0f\u53d4\u4f60\u4e5f\u4e0d\u80fd\u4e00\u76f4\u8ddf\u7740\u540e\u9762\u5e2e\u4ed6\u8865\u6f0f\u5b50\u3002\u201d</p><p>\u3000\u3000\u5f20\u77e5\u884c\u70b9\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e8b\u62d6\u4e00\u62d6\u4e5f\u597d\uff0c\u4ed6\u4eec\u5fc3\u91cc\u4e0d\u662f\u6068\u7740\u6c14\u5417\uff0c\u90a3\u5c31\u8ba9\u4ed6\u7ee7\u7eed\u6068\u7740\u5427\uff0c\u4e5f\u5f97\u8ba9\u4ed6\u4eec\u8ba4\u8bc6\u5230\u8fd9\u4e2a\u4e16\u754c\u5e76\u4e0d\u603b\u662f\u56f4\u7ed5\u7740\u4e00\u5c0f\u64ae\u4eba\u5728\u8f6c\u2026\u2026\u6211\u770b\u8001\u5927\u8fd9\u6b21\u9000\u4e0b\u6765\u7b97\u4e86\uff0c\u4e0d\u8981\u4ee5\u540e\u518d\u641e\u5f97\u5927\u5bb6\u63aa\u624b\u4e0d\u53ca\u3002\u201d</p><p>\u3000\u3000\u201c\u78e8\u4e00\u78e8\u4ed6\u4eec\u7684\u813e\u6c14\u4e5f\u597d\uff0c\u201d\u5f20\u77e5\u975e\u5fc3\u91cc\u4e5f\u6709\u4e9b\u607c\u706b\uff0c\u8981\u662f\u94b1\u6b3e\u7ed9\u522b\u4eba\u5377\u8dd1\u7acb\u5373\u901a\u77e5\u4ed6\uff0c\u4ed6\u5fc3\u91cc\u8fd8\u597d\u8fc7\u4e00\u4e9b\uff0c\u5927\u54e5\u8fd9\u662f\u5230\u4e86\u8d70\u6295\u65e0\u8def\u7684\u65f6\u5019\u518d\u60f3\u8d77\u6765\u627e\u8fd9\u8fb9\uff0c\u4ed6\u4eec\u4e00\u5b9a\u662f\u5bf9\u5f20\u5955\u7ed9\u8d76\u56de\u4e1c\u793e\u7684\u4e8b\u60c5\u803f\u803f\u4e8e\u6000\uff0c\u201c\u62d6\u4e5f\u5c31\u62d6\u5341\u51e0\u5929\uff0c\u6345\u5230\u5e02\u91cc\uff0c\u6bd5\u7adf\u8ba9\u4e8c\u54e5\u8138\u4e0a\u4e5f\u96be\u582a\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u54a7\u7740\u5634\u5e72\u7b11\uff1a\u201c\u8ba9\u4ed6\u4eec\u75db\u82e6\u5341\u51e0\u5929\u4e5f\u662f\u597d\u7684\u2026\u2026\u201d\u4e0d\u6653\u5f97\u5802\u5144\u5f20\u5955\u7b49\u5bb6\u91cc\u65e0\u6743\u65e0\u52bf\u4e4b\u540e\uff0c\u6027\u60c5\u4f1a\u4e0d\u4f1a\u6539\u53d8\uff0c\u7adf\u7136\u5408\u4f19\u505a\u751f\u610f\u8ba9\u5408\u4f19\u4eba\u5c06\u94b1\u6b3e\u90fd\u5377\u8dd1\u4e86\uff0c\u8fd8\u771f\u4e22\u5206\u7684\u3002</p><p>\u3000\u3000\u5f20\u77e5\u975e\u6325\u624b\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u8bf4\u8fd9\u4e9b\u4e0d\u5f00\u5fc3\u7684\u4e8b\uff0c\u7231\u8fbe\u7535\u5b50\u4eca\u5e74\u7684\u6536\u6210\u600e\u4e48\u6837\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u8bf4\u9053\uff1a\u201c\u672c\u6765\u80fd\u7ed9\u81ea\u5df1\u4e03\u516b\u767e\u4e07\u538b\u5c81\u94b1\u82b1\u82b1\uff0c\u53ef\u662f\uff0c\u4e1c\u4e00\u644a\u4e8b\uff0c\u897f\u4e00\u644a\u4e8b\uff0c\u5230\u6700\u540e\u8fd8\u662f\u7a77\u9b3c\u4e00\u4e2a\u3002\u201d</p><p>\u3000\u3000\u4e1c\u4e00\u644a\u4e8b\uff0c\u662f\u7ed9\u8bb8\u9e3f\u4f2f\u4e24\u767e\u591a\u4e07\u4fee\u7f2e\u68cb\u9986\u6062\u590d\u65e7\u89c2\uff0c\u897f\u4e00\u644a\u4e8b\u662f\u6307\u5728\u8c61\u5c71\u5357\u8fb9\u4e70\u4e0b\u4e24\u767e\u4ea9\u5730\u641e\u79cd\u690d\u56ed\u3002</p><p>\u3000\u3000\u5f20\u77e5\u975e\u8be7\u5f02\u7684\u8bf4\uff1a\u201c\u7231\u8fbe\u4eca\u5e74\u7684\u644a\u5b50\u94fa\u5f97\u8fd9\u4e48\u5927\uff0c\u8fd8\u6709\u8d62\u4f59\uff1f\u201d\u8f6c\u5934\u5bf9\u5f20\u77e5\u884c\u82e6\u7b11\u7740\u8bf4\uff0c\u201c\u524d\u4e9b\u65e5\u5b50\uff0c\u6211\u8fd8\u8ddf\u8fd9\u5c0f\u5b50\u8bf4\uff0c\u6211\u505a\u5341\u5e74\u90fd\u62b5\u4e0d\u4e0a\u4ed6\u4e00\u5e74\u7684\uff0c\u73b0\u5728\u770b\u6765\uff0c\u6211\u8f9b\u82e6\u5341\u5e74\uff0c\u90fd\u62b5\u4e0d\u4e0a\u4ed6\u4e00\u4e2a\u6708\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5f71\u789f\u673a\u771f\u8fd9\u4e48\u8d5a\u94b1\uff1f\u201d\u5f20\u77e5\u884c\u4e5f\u6709\u4e9b\u8be7\u5f02\uff0c\u9526\u6e56\u7684\u6210\u529f\uff0c\u8fd8\u53ef\u4ee5\u8bf4\u8ddf\u5e02\u91cc\u653f\u7b56\u4e0a\u7684\u652f\u6301\u6709\u5f88\u5927\u7684\u5173\u7cfb\uff0c\u4f46\u662f\u7231\u8fbe\u7535\u5b50\u7684\u6210\u529f\u7edd\u5bf9\u80fd\u591f\u5766\u7136\u9762\u5bf9\u5404\u65b9\u9762\u7684\u8d28\u7591\u3002</p><p>\u3000\u3000\u201c\u773c\u4e0b\u8fd8\u53ef\u4ee5\uff0c\u521a\u521a\u65f6\u884c\uff0c\u51e0\u4e4e\u6ca1\u6709\u4ec0\u4e48\u7ade\u4e89\u5bf9\u624b\uff0c\u5229\u6da6\u662f\u8981\u9ad8\u4e00\u4e9b\uff0c\u8ddf\u6240\u6709\u7684\u5bb6\u7535\u884c\u4e1a\u4e00\u6837\uff0c\u8fc7\u4e00\u4e24\u5e74\uff0c\u5c31\u6ca1\u6709\u90a3\u4e48\u9ad8\u7684\u5229\u6da6\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6709\u7ade\u4e89\u5bf9\u624b\u4e5f\u662f\u5f53\u7136\u7684\uff0c\u5229\u6da6\u8bf1\u4eba\u554a\uff0c\u4e2d\u56fd\u4eba\u5bf9\u4ec0\u4e48\u90fd\u4e0d\u4e0a\u5fc3\uff0c\u5c31\u5bf9\u6765\u94b1\u7684\u4e8b\u4e0a\u5fc3\uff0c\u6211\u542c\u8bf4\u4ec5\u57ce\u5357\u5c31\u6709\u56db\u4e94\u5bb6\u4f01\u4e1a\u51c6\u5907\u4e0a\u5f71\u789f\u673a\u9879\u76ee\u3002\u201d\u5f20\u77e5\u975e\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u662f\u554a\uff0c\u7b49\u4ed6\u4eec\u90fd\u505a\u8d77\u6765\uff0c\u8212\u5766\u7684\u65e5\u5b50\u5c31\u8981\u7ed3\u675f\u4e86\u3002\u201d\u5f20\u606a\u8bed\u6c14\u91cc\u88c5\u51fa\u4e9b\u65e0\u5948\uff0c\u8138\u8272\u5374\u5f02\u5e38\u7684\u8f7b\u677e\u3002\u5bf9\u4e8e\u5229\u6da6\u9ad8\u7684\u884c\u4e1a\uff0c\u4f1a\u5728\u77ed\u65f6\u95f4\u91cc\u6d8c\u51fa\u5927\u6279\u7684\u8ddf\u98ce\u8005\uff0c\u8fd9\u662f\u56fd\u5185\u7684\u5fc5\u7136\u73b0\u8c61\uff0c\u4f55\u51b5\u5f71\u789f\u673a\u6574\u673a\u7ec4\u88c5\u7684\u6280\u672f\u95e8\u69db\u5e76\u4e0d\u9ad8\u3002</p><p>\u3000\u3000\u201c\u4e56\u4e56\uff0c\u8fd9\u79cd\u65e5\u5b50\u8fc7\u534a\u5e74\u5c31\u8212\u5766\u6b7b\u4e86\uff0c\u4f60\u8fd8\u6307\u671b\u8fc7\u4e00\u8f88\u5b50\uff0c\u8fd9\u4e00\u5e74\u6765\uff0c\u7231\u8fbe\u7535\u5b50\u7684\u644a\u5b50\u91cc\u603b\u5171\u90fd\u6295\u5165\u4e0a\u4ebf\u7684\u8d44\u91d1\u4e86\u5427\uff0c\u8fd9\u53ef\u90fd\u662f\u7a7a\u624b\u7ffb\u51fa\u6765\u7684\u8d44\u4ea7\u554a\uff01\u201d\u7231\u8fbe\u7535\u5b50\u7684\u57fa\u5efa\uff0c\u51e0\u4e4e\u90fd\u662f\u5b8f\u8fdc\u5305\u4e0b\u6765\u7684\uff0c\u5c31\u7b97\u5f20\u606a\u4e0d\u660e\u8bf4\uff0c\u5f20\u77e5\u975e\u4e5f\u80fd\u4f30\u7b97\u51fa\u7231\u8fbe\u7535\u5b50\u4e00\u5e74\u6295\u5165\u4e86\u591a\u5c11\u8d44\u91d1\uff0c\u5e76\u8d2d\u3001\u65b0\u5efa\uff0c\u52a0\u4e0a\u5e02\u573a\u8fd0\u8425\uff0c\u7231\u8fbe\u7535\u5b50\u4e00\u5e74\u5dee\u4e0d\u591a\u6295\u5165\u4e0a\u4ebf\u5143\u7684\u8d44\u91d1\uff0c\u8fd9\u4e9b\u8d44\u91d1\u4e00\u5f00\u59cb\u7edd\u5927\u90e8\u5206\u662f\u5229\u7528\u73b0\u6b3e\u8ba2\u8d27\u7684\u6a21\u5f0f\u4ece\u7ecf\u9500\u5546\u7f51\u7edc\u91cc\u6e90\u6e90\u4e0d\u65ad\u6c72\u53d6\u51fa\u6765\u7684\uff0c\u5f20\u606a\u6295\u5165\u7684\u94b1\u5f88\u6709\u9650\uff0c\u7231\u8fbe\u7535\u5b50\u7684\u8d1f\u503a\u7387\u4e00\u5ea6\u9ad8\u8fbe90\uff05\uff0c\u7b49\u5230\u6d77\u88d5\u6295\u94b1\u8fdb\u53bb\uff0c\u7231\u8fbe\u7535\u5b50\u5df2\u7ecf\u6210\u4e86\u89c4\u6a21\uff0c\u5f20\u77e5\u975e\u4e5f\u6ca1\u60f3\u5230\u5f71\u789f\u673a\u7684\u66b4\u5229\u4e00\u5e74\u5c31\u5c06\u8fd9\u4e48\u5927\u7684\u7a9f\u7abf\u586b\u4e0a\u4e86\u3002</p></article>"}}}]);
