(window.webpackJsonp=window.webpackJsonp||[]).push([[279],{301:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e94\u5341\u8282 \u6218\u524d\u7684\u4ea4\u6613",content:"<article><h2>\u7b2c\u4e94\u5341\u8282 \u6218\u524d\u7684\u4ea4\u6613</h2><p>\u8d70\u5728\u5bc2\u9759\u7a7a\u65f7\u7684\u5deb\u5e08\u5b66\u9662\u91cc\uff0c\u8db3\u97f3\u5f62\u6210\u6e05\u6670\u7684\u56de\u58f0\uff0c\u743c\u6069\u6e05\u695a\u5730\u611f\u89c9\u5230\u56db\u5468\u7684\u6c89\u6c89\u9ed1\u6697\u4e2d\u6709\u65e0\u6570\u53cc\u773c\u775b\u5728\u7aa5\u4f3a\uff0c\u5f80\u5f80\u8fd8\u6709\u900f\u660e\u5e7d\u9b42\u5f20\u7259\u821e\u722a\u8fce\u9762\u800c\u6765\uff0c\u900f\u4f53\u800c\u8fc7\uff0c\u4f46\u4ed6\u5e76\u4e0d\u5728\u610f\u3002\u8fd9\u4e9b\u90fd\u662f\u5b66\u9662\u91cc\u7684\u6e38\u7075\u3002</p><p>\u9b54\u6cd5\u662f\u95e8\u5371\u9669\u7684\u5b66\u95ee\uff0c\u5353\u5c14\u66f4\u662f\u5371\u9669\u751f\u7269\uff0c\u8fd9\u4e24\u8005\u53e0\u52a0\u8d77\u6765\uff0c\u5bfc\u81f4\u74dc\u7406\u5fb7\u65af\u57ce\u7684\u5deb\u5e08\u5b66\u9662\u6b7b\u4ea1\u7387\u6781\u9ad8\uff0c\u7a33\u5c45\u4e09\u5927\u5b66\u9662\u4e4b\u9996\u3002\u9664\u4e86\u90a3\u4e9b\u6b63\u5e38\u7684\u98ce\u9669\u4e4b\u5916\uff0c\u8fd8\u6709\u4eba\u4e3a\u9020\u6210\u7684\u4f24\u4ea1\uff0c\u4f8b\u5982\u4e9a\u5f53\u65af\u4ee5\u524d\u7684\u5bfc\u5e08\uff0c\u7814\u7a76\u5728\u5e7d\u6697\u5730\u57df\u7cbe\u786e\u4f20\u9001\u7684\u6cd5\u95e8\uff0c\u5c31\u62ff\u81ea\u5df1\u7684\u5b66\u5f92\u505a\u8bd5\u9a8c\uff0c\u7ed3\u679c\u9664\u4e86\u4e9a\u5f53\u65af\u6700\u5e78\u8fd0\uff0c\u4fa5\u5e78\u4e0d\u6b7b\uff0c\u4ed6\u7684\u540c\u5b66\u4eec\u6302\u6389\u4e00\u6279\u3002\u8fd9\u5e76\u975e\u7279\u4f8b\uff0c\u800c\u662f\u5e38\u6001\uff0c\u5f88\u591a\u5bfc\u5e08\u90fd\u8fd9\u4e48\u5e72\u3002</p><p>\u5728\u9b54\u6cd5\u4e16\u754c\u91cc\uff0c\u6b7b\u4ea1\u5e76\u4e0d\u4e00\u5b9a\u610f\u5473\u7740\u7ec8\u7ed3\u3002\u74dc\u7406\u5fb7\u65af\u57ce\u662f\u4e2a\u53d8\u6001\u7684\u5730\u65b9\uff0c\u5404\u5927\u5bb6\u65cf\u90fd\u6709\u62ff\u5bb6\u65cf\u5deb\u5e08\u7684\u5e7d\u7075\u5f53\u201c\u5b88\u95e8\u8005\u201d\u7684\u4f20\u7edf\uff0c\u540c\u6837\u7684\uff0c\u5deb\u5e08\u5b66\u9662\u91cc\u7684\u5bfc\u5e08\u4eec\u4e5f\u4e0d\u4f1a\u8f7b\u6613\u6d6a\u8d39\u90a3\u4e9b\u5012\u9709\u86cb\u7684\u7075\u9b42\uff0c\u5927\u591a\u628a\u4ed6\u4eec\u53d8\u6210\u4e86\u6e38\u7075\uff08\u4e00\u79cd\u8fd1\u4f3c\u5e7d\u7075\uff0c\u4f46\u66f4\u4f4e\u7ea7\uff0c\u66f4\u4e0d\u5177\u6709\u5a01\u80c1\u7684\u865a\u4f53\u5f62\u6001\uff0c\u6ca1\u6709\u72ec\u7acb\u7684\u81ea\u6211\u610f\u8bc6\uff0c\u4ec5\u4ec5\u6709\u6700\u7b80\u5355\u7684\u667a\u80fd\uff09\uff0c\u5145\u5f53\u52a9\u624b\u3001\u5974\u4ec6\u6216\u8005\u95f9\u949f\u3002\u4e9a\u5f53\u65af\u4e0a\u6b21\u5728\u6e56\u5fc3\u5c9b\u5c7f\u548c\u743c\u6069\u5546\u8c08\uff0c\u90a3\u4e2a\u659f\u9152\u7684\u9690\u5f62\u4ec6\u5f79\uff0c\u5c31\u662f\u67d0\u4e2a\u5b66\u5f92\u7684\u7075\u9b42\u6240\u5316\u3002</p><p>\u771f\u8981\u8bba\u8d77\u6765\uff0c\u8fd9\u79cd\u4f5c\u6cd5\u662f\u5bf9\u86db\u540e\u7684\u5192\u72af\uff0c\u5bf9\u795e\u660e\u7684\u4e0d\u656c\u3002\u4f46\u65e2\u7136\u6709\u201c\u5b88\u95e8\u8005\u201d\u8fd9\u79cd\u5148\u4f8b\u5728\u524d\uff0c\u5deb\u5e08\u4eec\u7684\u8fd9\u79cd\u5c0f\u52a8\u4f5c\u53c8\u53ea\u5c40\u9650\u4e8e\u5b66\u9662\u4e4b\u5185\uff0c\u6240\u4ee5\u796d\u53f8\u4eec\u4e5f\u5c31\u7741\u4e00\u53ea\u773c\uff0c\u95ed\u4e00\u53ea\u773c\uff0c\u7b97\u662f\u9ed8\u8bb8\u3002\u80fd\u8fdb\u5deb\u5e08\u5b66\u9662\u7684\u4eba\u6bd5\u7adf\u662f\u6781\u5c11\u6570\uff0c\u5de6\u53f3\u4e5f\u5c31\u90a3\u70b9\u5fae\u4e0d\u8db3\u9053\u7684\u6570\u76ee\uff0c\u86db\u540e\u4e5f\u4e0d\u5728\u4e4e\u3002</p><p>\u8fd9\u4e9b\u6e38\u7075\u5728\u5b66\u9662\u91cc\u98d8\u6d6e\u51fa\u6ca1\u7740\uff0c\u5f80\u5f80\u4f1a\u7ed9\u65b0\u5165\u5b66\u7684\u5b66\u5f92\u9020\u6210\u4e00\u4e9b\u56f0\u6270\uff0c\u4f46\u5176\u5b9e\u6ca1\u6709\u591a\u5927\u7684\u7834\u574f\u529b\uff0c\u5bf9\u4e8e\u743c\u6069\u81ea\u7136\u662f\u66f4\u52a0\u6ca1\u6709\u59a8\u788d\u4e86\uff0c\u4ed6\u4f69\u6234\u7740\u7684\u4ee3\u8868\u8eab\u4efd\u7684\u94f6\u7070\u8272\u91d1\u5c5e\u62a4\u8155\uff0c\u6e05\u695a\u8868\u660e\u4e86\u6b64\u4eba\u662f\u5b66\u9662\u7684\u5bfc\u5e08\u2014\u2014\u800c\u4e14\u662f\u9ad8\u9636\u5bfc\u5e08\uff0c\u4fdd\u62a4\u4ed6\u4e0d\u53d7\u5b66\u9662\u4e2d\u9632\u5fa1\u7ed3\u754c\u3001\u9b54\u6cd5\u9677\u9631\u6216\u8005\u6e38\u7075\u7684\u4f24\u5bb3\uff0c\u5f53\u7136\uff0c\u5176\u4ed6\u5bfc\u5e08\u5728\u81ea\u5df1\u623f\u95f4\u91cc\u7279\u522b\u5e03\u7f6e\u7684\u9664\u5916\u3002</p><p>\u743c\u6069\u7a7f\u8fc7\u957f\u5eca\uff0c\u8e0f\u4e0a\u87ba\u65cb\u5f62\u4e0a\u5347\u7684\u697c\u68af\uff0c\u6bcf\u5f53\u4ed6\u8fc8\u51fa\u4e00\u6b65\uff0c\u540e\u9762\u7684\u697c\u68af\u677f\u5c31\u4f1a\u51ed\u7a7a\u6d88\u5931\uff0c\u4ee4\u4eba\u611f\u89c9\u50cf\u662f\u5728\u6b65\u6b65\u98de\u5347\u2014\u2014\u5f53\u7136\u8fd9\u4e2a\u4e16\u754c\u91cc\u6ca1\u6709\u8fd9\u79cd\u6982\u5ff5\u3002\u5728\u8f6c\u8fc7\u4e24\u4e2a\u5f2f\u4e4b\u540e\uff0c\u743c\u6069\u8d70\u5230\u4e86\u4e00\u95f4\u529e\u516c\u5ba4\u524d\uff0c\u6572\u4e86\u6572\u95e8\u3002</p><p>\u201c\u9996\u5e2d\u5deb\u5e08\u9601\u4e0b\u3002\u201d</p><p>\u529e\u516c\u5ba4\u91cc\u4f20\u51fa\u8f7b\u5fae\u7684\u52a8\u9759\uff0c\u7136\u540e\u662f\u4e9a\u5f53\u65af\u7684\u8bf4\u8bdd\u58f0\uff0c\u201c\u8bf7\u8fdb\uff0c\u5170\u5c3c\u65af\u7279\u5bfc\u5e08\uff0c\u201d\u4ed6\u975e\u5e38\u5ba2\u6c14\u5730\u79f0\u547c\u743c\u6069\u7684\u65b0\u5934\u8854\uff0c\u201c\u6211\u6b63\u5728\u7b49\u4f60\u3002\u201d</p><p>\u95e8\u81ea\u52a8\u79fb\u5f00\uff0c\u743c\u6069\u8d70\u8fdb\uff0c\u4ed6\u4e00\u773c\u770b\u89c1\u4e86\u4e9a\u5f53\u65af\u3002\u9996\u5e2d\u5deb\u5e08\u6b63\u5750\u5728\u5bbd\u5927\u7684\u5ea7\u6905\u4e2d\uff0c\u628a\u73a9\u7740\u4e00\u5757\u7eff\u8272\u83f1\u5f62\u6c34\u6676\uff0c\u91cc\u9762\u4f3c\u4e4e\u6709\u7f15\u7f15\u91d1\u4e1d\u9690\u73b0\u3002\u9664\u6b64\u4e4b\u5916\uff0c\u623f\u95f4\u91cc\u8fd8\u6709\u4e00\u4e2a\u5353\u5c14\u5973\u6027\u3002\u56e0\u4e3a\u6b63\u8dea\u5728\u5730\u4e0a\uff0c\u8138\u57cb\u5728\u4e9a\u5f53\u65af\u7684\u817f\u95f4\uff0c\u80cc\u5bf9\u7740\u743c\u6069\uff0c\u53c8\u662f\u8d64\u88f8\u8eab\u4f53\uff0c\u65e0\u6cd5\u4ece\u9762\u5bb9\u8863\u9970\u4e0a\u5224\u65ad\u8eab\u4efd\uff0c\u4e0d\u8fc7\u603b\u89c9\u5f97\u80cc\u5f71\u4f3c\u4e4e\u6709\u4e9b\u719f\u6089\u7684\u6837\u5b50\u3002</p><p>\u201c\u8bf7\u5750\uff0c\u5170\u5c3c\u65af\u7279\u5bfc\u5e08\uff0c\u201d\u4e9a\u5f53\u65af\u70b9\u5934\u793a\u610f\uff0c\u201c\u7a0d\u7b49\u7247\u523b\u3002\u201d</p><p>\u201c\u6ca1\u5173\u7cfb\u3002\u201d\u743c\u6069\u8bf4\uff0c\u5728\u65c1\u8fb9\u5750\u4e0b\u6765\u3002</p><p>\u4e9a\u5f53\u65af\u62cd\u4e86\u62cd\u8eab\u4e0b\u5353\u5c14\u5973\u6027\u7684\u80a9\u90e8\uff0c\u793a\u610f\u5979\u52a0\u5feb\u901f\u5ea6\u3002\u623f\u95f4\u91cc\u5f88\u5bc2\u9759\uff0c\u743c\u6069\u80fd\u591f\u6e05\u695a\u5730\u542c\u89c1\u53e3\u8154\u541e\u5410\u58f0\uff0c\u5927\u7ea6\u53c8\u8fc7\u4e86\u4e94\u5206\u949f\u2014\u2014\u6216\u8005\u7a0d\u957f\u4e00\u70b9\uff0c\u5353\u5c14\u5973\u6027\u7684\u5934\u505c\u6b62\u4e86\u4e0a\u4e0b\u6446\u52a8\uff0c\u5979\u7684\u54bd\u5589\u4e2d\u53d1\u51fa\u5495\u5495\u7684\u541e\u54bd\u58f0\uff0c\u6700\u540e\u8d77\u8eab\u7ad9\u4e86\u8d77\u6765\u3002</p><p>\u76f4\u5230\u6b64\u65f6\uff0c\u743c\u6069\u624d\u770b\u6e05\u695a\u5979\u7684\u8138\uff0c\u8fd9\u4ee4\u4ed6\u5403\u4e86\u4e00\u60ca\uff0c\u201c\u83f2\u5c14\u4f26\u5c0f\u59d0\uff1f\u201d</p><p>\u6614\u65e5\u7684\u7b2c\u4e00\u5bb6\u65cf\u957f\u5973\uff0c\u57ce\u4e2d\u4f4d\u9636\u6700\u9ad8\u7684\u796d\u53f8\u4e4b\u4e00\uff0c\u8fe6\u6cd5\xb7\u83f2\u5c14\u4f26\uff0c\u6b64\u65f6\u6b63\u5168\u8eab\u8d64\u88f8\u9762\u65e0\u8868\u60c5\u5730\u7ad9\u5728\u4e9a\u5f53\u65af\u8eab\u65c1\uff0c\u4eff\u4f5b\u5973\u4ec6\u4e00\u822c\uff0c\u5982\u679c\u4e0d\u662f\u5979\u7684\u5634\u89d2\u8fd8\u6709\u521a\u521a\u6ea2\u51fa\u7684\u4e73\u767d\u8272\u6db2\u4f53\uff0c\u743c\u6069\u51e0\u4e4e\u8981\u6000\u7591\u81ea\u5df1\u51fa\u73b0\u5e7b\u89c9\u3002</p><p>\u5979\u5e94\u8be5\u5df2\u7ecf\u6b7b\u4e86\uff0c\u524d\u4e9b\u65e5\u5b50\uff0c\u4e9a\u5f53\u65af\u8d81\u7740\u7267\u5e08\u96c6\u4f1a\u7684\u65f6\u673a\uff0c\u7387\u9886\u5deb\u5e08\u4f0f\u51fb\uff0c\u5c06\u83f2\u5c14\u4f26\u4e3b\u6bcd\u548c\u5176\u4ed6\u5973\u513f\u5c3d\u6570\u6740\u6b7b\uff0c\u5176\u4e2d\u4e5f\u5305\u62ec\u8fe6\u6cd5\u3002\u5982\u82e5\u4e0d\u7136\uff0c\u54ea\u91cc\u8f6e\u5f97\u5230\u7ef4\u5eb7\u5c3c\u4e9a\u7ee7\u4f4d\u2026\u2026\u773c\u524d\u7684\u8fd9\u4f4d\uff0c\u600e\u4e48\u770b\u90fd\u662f\u6d3b\u4eba\uff0c\u5e76\u975e\u4ea1\u7075\u554a\u3002</p><p>\u201c\u6211\u7684\u9b54\u6cd5\u51fa\u4e86\u70b9\u610f\u5916\uff0c\u201d\u4e9a\u5f53\u65af\u770b\u51fa\u743c\u6069\u7684\u7591\u60d1\uff0c\u201c\u6ca1\u6709\u771f\u6b63\u6740\u6b7b\u5979\u3002\u201d</p><p>\u743c\u6069\u8981\u662f\u76f8\u4fe1\u8fd9\u79cd\u8bf4\u6cd5\u5c31\u6709\u9b3c\u4e86\uff0c\u9b54\u6cd5\u51fa\u610f\u5916\u6ca1\u80fd\u5f53\u573a\u6740\u6b7b\uff0c\u90a3\u540e\u6765\u53d1\u73b0\u4e86\u96be\u9053\u4e0d\u80fd\u8865\u4e00\u5200\u4e48\u3002\u770b\u73b0\u5728\u8fd9\u60c5\u5f62\uff0c\u5206\u660e\u5c31\u662f\u4e9a\u5f53\u65af\u65e9\u5c31\u5bf9\u8fe6\u6cd5\u6709\u4e0d\u8f68\u4f01\u56fe\uff0c\u8d81\u673a\u91d1\u5c4b\u85cf\u5a07\u2026\u2026\u7b49\u7b49\uff0c\u4e9a\u5f53\u65af\u662f\u524d\u4efb\u83f2\u5c14\u4f26\u4e3b\u6bcd\u7684\u5f1f\u5f1f\uff0c\u8fe6\u6cd5\u662f\u957f\u5973\uff0c\u4e5f\u5c31\u662f\u8bf4\u9762\u524d\u8fd9\u4e24\u4f4d\u662f\u53d4\u53d4\u548c\u4f84\u5973\u2026\u2026</p><p>\u597d\u5427\uff0c\u53cd\u6b63\u9ed1\u6697\u7cbe\u7075\u539f\u672c\u5c31\u662f\u6df7\u4e71\u7684\u79cd\u65cf\uff0c\u5144\u5f1f\u59d0\u59b9\u4e71\u4f26\u7684\u4e8b\u60c5\u743c\u6069\u4e5f\u65f6\u6709\u8033\u95fb\u2014\u2014\u4e3b\u8981\u56e0\u4e3a\u5979\u4eec\u662f\u6bcd\u7cfb\u793e\u4f1a\uff0c\u4eb2\u5c5e\u5bb6\u5ead\u89c2\u5ff5\u6de1\u8584\u3002\u65e2\u7136\u5982\u6b64\uff0c\u4e5f\u5c31\u4e0d\u5fc5\u5927\u60ca\u5c0f\u602a\u4e86\u3002</p><p>\u4f46\u95ee\u9898\u662f\uff0c\u8fe6\u6cd5\u8fd8\u6d3b\u7740\uff0c\u90a3\u6309\u89c4\u77e9\u5979\u624d\u662f\u4e3b\u6bcd\u7684\u7ee7\u627f\u4eba\u2026\u2026</p><p>\u201c\u73b0\u5728\u5979\u5df2\u7ecf\u4e0d\u662f\u83f2\u5c14\u4f26\u4e86\uff0c\u201d\u4e9a\u5f53\u65af\u8bf4\uff0c\u201c\u66fe\u7ecf\u7684\u83f2\u5c14\u4f26\u5bb6\u65cf\u957f\u5973\u5df2\u7ecf\u6b7b\u53bb\u3002\u201d</p><p>\u201c\u54e6\u3002\u201d</p><p>\u743c\u6069\u5bf9\u8fd9\u4e2a\u95ee\u9898\u5176\u5b9e\u4e5f\u4e0d\u600e\u4e48\u5173\u5fc3\uff0c\u53ea\u662f\u7a81\u7136\u770b\u89c1\u4e00\u4e2a\u539f\u672c\u5e94\u8be5\u5df2\u7ecf\u6b7b\u53bb\u7684\u4eba\u7a81\u7136\u53c8\u6d3b\u751f\u751f\u5730\u51fa\u73b0\u5728\u9762\u524d\uff0c\u6709\u4e9b\u60ca\u8bb6\u7f62\u4e86\u3002\u968f\u53e3\u5c94\u5f00\u8bdd\u9898\uff0c\u95f2\u8c08\u51e0\u53e5\uff0c\u4e24\u4eba\u4fbf\u5f00\u59cb\u5207\u5165\u6b63\u9898\uff0c\u8c08\u8bba\u6b64\u6b21\u7b79\u5212\u653b\u6253\u4f0a\u5361\u6c99\u57ce\u7684\u4e8b\u60c5\u3002</p><p>\u201c\u6709\u4ef6\u4e8b\u60c5\u5f88\u9ebb\u70e6\uff0c\u201d\u4e9a\u5f53\u65af\u76b1\u7740\u7709\uff0c\u7ffb\u9605\u624b\u4e0a\u7684\u4e00\u4efd\u8d44\u6599\uff0c\u201c\u518d\u8fc7\u4e09\u5929\u5c31\u662f\u9884\u5b9a\u7684\u51fa\u5f81\u65f6\u95f4\u4e86\uff0c\u7136\u800c\u6709\u5f88\u591a\u5bb6\u65cf\u7684\u58eb\u5175\u4e0d\u613f\u610f\u53c2\u6218\u3002\u201d</p><p>\u201c\u539f\u56e0\u5462\uff1f\u201d</p><p>\u201c\u5f88\u591a\uff0c\u201d\u4e9a\u5f53\u65af\u8bf4\uff0c\u201c\u4f46\u6709\u4e00\u4e2a\u539f\u56e0\u662f\u65b0\u589e\u52a0\u7684\uff1a\u6c89\u9ed8\u4e4b\u6b4c\u3002\u201d</p><p>\u201c\u5514\uff1f\u201d\u743c\u6069\u6ca1\u542c\u660e\u767d\uff0c\u201c\u8fd9\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d</p><p>\u201c\u4f60\u8fd9\u51e0\u5929\u53bb\u8fc7\u6c89\u9ed8\u4e4b\u6b4c\u5417\uff1f\u201d</p><p>\u201c\u6ca1\u6709\u3002\u201d</p><p>\u201c\u4f60\u5236\u5b9a\u7684\u4ef7\u683c\u592a\u4f4e\uff0c\u51e0\u4e4e\u628a\u5168\u57ce\u7684\u7537\u6027\u90fd\u5438\u5f15\u8fc7\u6765\u4e86\uff0c\u201d\u4e9a\u5f53\u65af\u8bf4\uff0c\u4ece\u8bed\u6c14\u542c\u4e0d\u51fa\u662f\u8d5e\u6210\u8fd8\u662f\u53cd\u5bf9\uff0c\u201c\u73b0\u5728\u6c89\u9ed8\u4e4b\u6b4c\u7684\u95e8\u53e3\u4e00\u5929\u5341\u4e8c\u4e2a\u65f6\u8fb0\u6392\u7740\u957f\u961f\uff0c\u6bcf\u4e2a\u623f\u95f4\u6bcf\u65f6\u6bcf\u523b\u90fd\u662f\u7206\u6ee1\u3002\u539f\u672c\u7684\u89c4\u77e9\u662f\u4e00\u6b21\u53ea\u51c6\u4e00\u4eba\uff0c\u540e\u6765\u6ca1\u529e\u6cd5\uff0c\u6539\u6210\u4e00\u6b21\u4e24\u4eba\u2014\u2014\u4eca\u5929\u4e0a\u5348\u6211\u542c\u5230\u7684\u6d88\u606f\uff0c\u5df2\u7ecf\u662f\u4e00\u6b21\u540c\u65f6\u5141\u8bb8\u8fdb\u5165\u4e94\u4eba\u4e86\u2026\u2026\u201d</p><p>\u201c\u7b49\u7b49\uff0c\u201d\u743c\u6069\u6253\u65ad\uff0c\u201c\u4e00\u6b21\u4e24\u4e2a\u4e09\u4e2a\u6211\u90fd\u80fd\u7406\u89e3\uff0c\u4e94\u4e2a\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u5353\u5c14\u5973\u6027\u4f3c\u4e4e\u4e5f\u4e0d\u6bd4\u4eba\u7c7b\u591a\u4e24\u4e2a\u5730\u65b9\u2026\u2026\u201d</p><p>\u201c\u8fd9\u4e2a\u6211\u4e5f\u4e0d\u6e05\u695a\uff0c\u201d\u4e9a\u5f53\u65af\u627f\u8ba4\uff0c\u201c\u6211\u6ca1\u53bb\u5b9e\u5730\u89c2\u6469\u8fc7\uff0c\u4f46\u8fd9\u4e2a\u6d88\u606f\u662f\u80af\u5b9a\u4e0d\u4f1a\u9519\u7684\u3002\u201d</p><p>\u743c\u6069\u8bd5\u56fe\u60f3\u50cf\u4e00\u4e2a\u5353\u5c14\u5973\u796d\u53f8\u540c\u65f6\u5e94\u4ed8\u4e94\u4e2a\u7537\u6027\u7684\u573a\u666f\uff0c\u4f46\u6700\u7ec8\u51b3\u5b9a\u5ffd\u7565\u8fc7\u53bb\u3002\u201c\u6ca1\u6b7b\u4eba\u5427\uff1f\u201d\u4ed6\u95ee\u3002</p><p>\u201c\u90a3\u5012\u6ca1\u6709\u3002\u201d</p><p>\u743c\u6069\u5728\u5fc3\u4e2d\u6697\u6697\u8d5e\u53f9\u5353\u5c14\u5973\u6027\u7684\u5fcd\u8010\u529b\uff0c\u5982\u679c\u6362\u4e86\u4eba\u7c7b\uff0c\u8fd9\u6837\u591c\u4ee5\u7ee7\u65e5\u65e5\u4ee5\u7ee7\u591c\u6050\u6015\u65e9\u5c31\u4e00\u547d\u545c\u547c\u4e86\u3002\u201c\u4e5f\u5c31\u662f\u8bf4\uff0c\u73b0\u5728\u5404\u5bb6\u65cf\u7684\u7537\u6027\u4eec\u90fd\u5fd9\u7740\u5728\u90a3\u4e9b\u5973\u796d\u53f8\u7684\u8eab\u4e0a\u51b2\u950b\uff0c\u5bf9\u4e8e\u6253\u4ed7\u7684\u5174\u81f4\u5c31\u4e0d\u5927\u4e86\u3002\u201d</p><p>\u201c\u539f\u672c\u5c31\u4e0d\u5927\uff0c\u73b0\u5728\u81ea\u7136\u66f4\u4f4e\u4e86\u3002\u201d</p><p>\u201c\u8fd9\u4e2a\u4e48\uff0c\u5176\u5b9e\u4e0d\u96be\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u628a\u8fd9\u4e9b\u5973\u796d\u53f8\u4eec\u4e5f\u5e26\u53bb\u4e0d\u5c31\u884c\u4e86\uff1f\u201d</p><p>\u201c\u55ef\u2026\u2026\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u6211\u662f\u8bf4\uff0c\u8fd9\u6b21\u653b\u6253\u4f0a\u5361\u6c99\u57ce\uff0c\u628a\u6c89\u9ed8\u4e4b\u6b4c\u91cc\u90a3\u4e9b\u5973\u796d\u53f8\u4eec\u4e5f\u968f\u519b\u5e26\u4e0a\u5c31\u662f\u4e86\uff0c\u201d\u743c\u6069\u89e3\u91ca\uff0c\u201c\u884c\u519b\u6253\u6218\uff0c\u4e5f\u662f\u5f88\u8f9b\u82e6\u7684\u4e8b\u60c5\uff0c\u4f55\u51b5\u8fd8\u8981\u5192\u6027\u547d\u98ce\u9669\u3002\u65e2\u7136\u5982\u6b64\uff0c\u5bf9\u6218\u58eb\u7406\u5f53\u6709\u6240\u9f13\u52b1\uff0c\u6709\u6240\u5956\u8d4f\uff0c\u6240\u6709\u51fa\u5f81\u8005\u81ea\u7136\u90fd\u6709\u8d44\u683c\u514d\u8d39\u2026\u2026\u201d</p><p>\u8bdd\u97f3\u672a\u843d\uff0c\u4e9a\u5f53\u65af\u5df2\u7ecf\u53cc\u624b\u4e00\u62cd\uff0c\u201c\u597d\u4e3b\u610f\uff0c\u201d\u4ed6\u79f0\u8d5e\uff0c\u201c\u8fd9\u6837\u4e00\u6765\uff0c\u539f\u672c\u7684\u9ebb\u70e6\u5c31\u53d8\u6210\u4e86\u6fc0\u52b1\u2026\u2026\u4f60\u771f\u662f\u4e2a\u5929\u624d\uff0c\u5170\u5c3c\u65af\u7279\u5bfc\u5e08\u3002\u201d</p><p>\u743c\u6069\u81ea\u7136\u6ca1\u5174\u8da3\u544a\u8bc9\u4ed6\uff0c\u4e4b\u6240\u4ee5\u60f3\u51fa\u8fd9\u4e2a\u4e3b\u610f\uff0c\u662f\u53d7\u4e86\u8bb0\u5fc6\u4e2d\u67d0\u4e2a\u53eb\u505a\u65e5\u672c\u7684\u5c9b\u56fd\u542f\u53d1\u3002\u201c\u60a8\u8fc7\u5956\u4e86\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u4e0d\u4ec5\u5982\u6b64\uff0c\u5176\u5b9e\u8fd8\u53ef\u4ee5\u9881\u4e0b\u547d\u4ee4\uff1a\u51e1\u662f\u5728\u8fd9\u573a\u6218\u6597\u4e2d\u529f\u52cb\u5353\u8457\u8005\uff0c\u90fd\u6709\u8d44\u683c\u5728\u8fd9\u4e9b\u5973\u796d\u53f8\u4e2d\u81ea\u7531\u6311\u9009\u4e00\u4eba\uff0c\u4f5c\u4e3a\u79c1\u6709\u8d22\u4ea7\u2026\u2026\u201d</p><p>\u201c\u79c1\u6709\u8d22\u4ea7\uff1f\u201d</p><p>\u201c\u5c31\u662f\u5974\u96b6\u554a\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u8fd9\u4e0d\u662f\u5f88\u6b63\u5e38\u7684\u60c5\u5f62\u5417\uff1f\u4f60\u53bb\u8fc7\u5730\u8868\uff0c\u5e94\u8be5\u77e5\u9053\u8fd9\u5728\u4eba\u7c7b\u5f53\u4e2d\u662f\u5f88\u5e38\u89c1\u7684\u3002\u201d</p><p>\u201c\u6211\u4eec\u4e5f\u4f7f\u7528\u5974\u96b6\u2026\u2026\u53ef\u662f\u5728\u6211\u4eec\u5353\u5c14\u5f53\u4e2d\uff0c\u5973\u6027\u662f\u7edd\u4e0d\u53ef\u80fd\u4f5c\u4e3a\u5974\u96b6\u7684\uff0c\u66f4\u522b\u63d0\u5973\u796d\u53f8\u2026\u2026\u201d</p><p>\u201c\u73b0\u5728\u5979\u4eec\u5df2\u7ecf\u4e0d\u662f\u5973\u796d\u53f8\u4e86\u561b\uff0c\u201d\u743c\u6069\u644a\u5f00\u624b\uff0c\u201c\u795e\u540e\u4f1a\u63a5\u53d7\u8fd9\u4e9b\u65e0\u80fd\u7684\u796d\u53f8\uff1f\u5979\u4eec\u662f\u5931\u8d25\u8005\uff0c\u662f\u6218\u4fd8\uff0c\u5982\u4f55\u5904\u7f6e\u81ea\u7136\u53d6\u51b3\u4e8e\u80dc\u5229\u8005\u2014\u2014\u4e5f\u5c31\u662f\u6211\u4eec\u4e86\u3002\u81f3\u4e8e\u8bf4\u5973\u6027\u4e0d\u80fd\u6210\u4e3a\u5974\u96b6\uff0c\u90a3\u53ea\u662f\u4ee5\u524d\u7684\u89c4\u77e9\uff0c\u800c\u6211\u4eec\u90fd\u77e5\u9053\u4e0e\u65f6\u4ff1\u8fdb\u7684\u5fc5\u8981\u6027\u3002\u66f4\u4f55\u51b5\u2026\u2026\u201d\u4ed6\u77a5\u4e86\u4e00\u773c\u65c1\u8fb9\u7684\u8fe6\u6cd5\uff0c\u6697\u793a\u4e9a\u5f53\u65af\u4f60\u73b0\u5728\u4e0d\u662f\u5206\u660e\u5c31\u5728\u8fd9\u4e48\u5e72\u7684\u4e48\u3002</p><p>\u4e9a\u5f53\u65af\u4e5f\u610f\u8bc6\u5230\u4e86\u8fd9\u70b9\uff0c\u201c\u5443\uff0c\u4f60\u8bf4\u5f97\u5bf9\uff0c\u201d\u4ed6\u627f\u8ba4\uff0c\u201c\u786e\u5b9e\uff0c\u6709\u4e9b\u53e4\u8001\u7684\u89c4\u77e9\u5e94\u8be5\u505a\u51fa\u76f8\u5e94\u7684\u6539\u53d8\u4e86\u3002\u201d</p><p>\u201c\u56e0\u4e3a\u73b0\u5b9e\u5c31\u5728\u6539\u53d8\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u800c\u4e14\u5e94\u8be5\u6539\u53d8\u5f97\u66f4\u591a\u3002\u201d</p><p>\u4e9a\u5f53\u65af\u9ed8\u9ed8\u70b9\u5934\uff0c\u4f46\u6ca1\u6709\u7ee7\u7eed\u8fd9\u4e2a\u8bdd\u9898\uff0c\u201c\u4f60\u51c6\u5907\u8fd4\u56de\u9634\u9b42\u57ce\u4e86\u5427\u3002\u201d</p><p>\u201c\u5feb\u4e86\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u89e3\u51b3\u4e86\u4f0a\u5361\u6c99\u57ce\uff0c\u6b63\u597d\u548c\u6211\u7684\u540c\u4f34\u6c47\u5408\uff0c\u7136\u540e\u56de\u53bb\u590d\u547d\u3002\u201d</p><p>\u201c\u771f\u9057\u61be\uff0c\u201d\u4e9a\u5f53\u65af\u60cb\u60dc\uff0c\u201c\u4e0d\u80fd\u548c\u4f60\u8fd9\u6837\u4f18\u79c0\u7684\u5deb\u5e08\u5171\u4e8b\uff0c\u662f\u6211\u7684\u4e00\u5927\u635f\u5931\u3002\u201d</p><p>\u201c\u6211\u4e5f\u540c\u6837\u8fd9\u4e48\u8ba4\u4e3a\uff0c\u201d\u743c\u6069\u5fae\u7b11\uff0c\u201c\u4e0d\u8fc7\u6211\u60f3\uff0c\u5c06\u6765\u672a\u5fc5\u6ca1\u6709\u518d\u5408\u4f5c\u7684\u673a\u4f1a\u2026\u2026\u5bf9\u4e86\uff0c\u201d\u4ed6\u4eff\u4f5b\u6f2b\u4e0d\u7ecf\u5fc3\u5730\u8bf4\uff0c\u201c\u4e0a\u6b21\u60a8\u63d0\u5230\uff0c\u60a8\u4ee5\u524d\u7684\u5bfc\u5e08\u81f4\u529b\u4e8e\u5e7d\u6697\u5730\u57df\u7cbe\u786e\u4f20\u9001\u65b9\u9762\u7684\u7814\u7a76\u3002\u201d</p><p>\u201c\u662f\u7684\uff0c\u4e0d\u8fc7\u4ed6\u6ca1\u6210\u529f\u3002\u201d</p><p>\u201c\u63a5\u8fd1\u6210\u529f\u4e86\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u81f3\u5c11\u5728\u60a8\u8eab\u4e0a\u6210\u529f\u4e86\u3002\u201d</p><p>\u201c\u8fd9\u5012\u6ca1\u9519\uff0c\u201d\u4e9a\u5f53\u65af\u627f\u8ba4\uff0c\u201c\u4f60\u5bf9\u5b83\u4e5f\u6709\u5174\u8da3\uff1f\u201d</p><p>\u201c\u81ea\u7136\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u975e\u5e38\u6709\u5174\u8da3\u3002\u60a8\u77e5\u9053\u7684\uff0c\u5e7d\u6697\u5730\u57df\u53ef\u4e0d\u662f\u4ec0\u4e48\u5927\u9053\u5766\u9014\uff0c\u65e2\u6ca1\u6709\u65c5\u5e97\u4e5f\u6ca1\u6709\u9152\u9986\uff0c\u6ca1\u6709\u67d4\u8f6f\u8212\u9002\u7684\u5e8a\u94fa\u548c\u9999\u751c\u7684\u7f8e\u9152\uff0c\u8fde\u9a6c\u8f66\u90fd\u6ca1\u6709\uff0c\u504f\u504f\u5374\u6709\u5c42\u51fa\u4e0d\u7a77\u7684\u5371\u9669\u602a\u517d\u3002\u4ece\u9634\u9b42\u57ce\u6765\u6b64\uff0c\u6211\u82b1\u8d39\u4e86\u4e24\u4e2a\u6708\uff0c\u906d\u9047\u4e86\u79cd\u79cd\u9ebb\u70e6\uff0c\u9014\u4e2d\u635f\u5931\u4e86\u4e09\u540d\u540c\u4f34\u2014\u2014\u90a3\u7b80\u76f4\u662f\u6076\u68a6\u4e00\u822c\u7684\u7ecf\u5386\u3002\u201d</p><p>\u201c\u662f\u554a\uff0c\u201d\u4e9a\u5f53\u65af\u8d5e\u540c\uff0c\u201c\u6211\u4e5f\u66fe\u7ecf\u6709\u8fc7\u4e00\u6b21\u8fd9\u6837\u7684\u7ecf\u5386\uff0c\u90a3\u786e\u5b9e\u662f\u592a\u6050\u6016\u4e86\u3002\u201d</p><p>\u201c\u800c\u6211\u73b0\u5728\u8981\u56de\u53bb\uff0c\u201d\u743c\u6069\u8868\u793a\u65e0\u5948\uff0c\u201c\u6211\u53ef\u5b9e\u5728\u4e0d\u60f3\u518d\u91cd\u590d\u4e00\u6b21\u6076\u68a6\u4e86\u3002\u5982\u679c\u6709\u529e\u6cd5\u80fd\u591f\u76f4\u63a5\u56de\u5230\u5730\u8868\uff0c\u7136\u540e\u518d\u56de\u9634\u9b42\u57ce\uff0c\u6211\u60f3\u65c5\u9014\u4f1a\u6109\u5feb\u5f97\u591a\u3002\u201d</p><p>\u4e9a\u5f53\u65af\u6c89\u9ed8\u4e86\u4e00\u4f1a\uff0c\u62ac\u624b\u53d1\u51fa\u4e00\u4e2a\u65e0\u58f0\u7684\u6307\u4ee4\uff0c\u4e00\u53ea\u9690\u5f62\u5974\u4ec6\u98d8\u8fc7\u6765\uff0c\u5782\u624b\u4f8d\u7acb\u3002\u201c\u7b2c\u4e09\u53f7\u4e66\u67b6\u7b2c\u4e8c\u6392\uff0c\u53f3\u6570\u7b2c\u4e03\u4e2a\u5323\u5b50\uff0c\u628a\u91cc\u9762\u7684\u4e1c\u897f\u53d6\u6765\u3002\u201d\u4e9a\u5f53\u65af\u5429\u5490\u3002</p><p>\u8fc7\u4e86\u7247\u523b\uff0c\u9690\u5f62\u5974\u4ec6\u98d8\u56de\u6765\uff0c\u900f\u660e\u7684\u624b\u81c2\u4e2d\u6258\u7740\u4e00\u4efd\u88c5\u8ba2\u597d\u7684\u4e66\u5377\u3002</p><p>\u201c\u6211\u56de\u5230\u57ce\u5e02\u7684\u65f6\u5019\uff0c\u5bfc\u5e08\u5c31\u5df2\u7ecf\u5728\u5bb6\u65cf\u5185\u6218\u4e2d\u6b7b\u53bb\u4e86\uff0c\u8fd9\u662f\u4e8b\u540e\u6211\u6240\u80fd\u627e\u5230\u7684\u6240\u6709\u76f8\u5173\u7684\u8d44\u6599\uff0c\u201d\u9996\u5e2d\u5deb\u5e08\u8bf4\uff0c\u793a\u610f\u9690\u5f62\u5974\u4ec6\u628a\u4e66\u5377\u9012\u7ed9\u743c\u6069\uff0c\u201c\u6211\u66fe\u7ecf\u8bd5\u7740\u5b8c\u5584\u5b83\uff0c\u4f46\u6ca1\u6709\u6210\u529f\uff0c\u6bd5\u7adf\u8fd9\u79cd\u7a7a\u95f4\u8de8\u8d8a\u9b54\u6cd5\u5e76\u975e\u6211\u7684\u64c5\u957f\uff0c\u6216\u8bb8\u4f60\u53ef\u4ee5\u8bd5\u8bd5\u3002\u7136\u800c\u6211\u5fc5\u987b\u518d\u6b21\u63d0\u9192\u7684\u662f\uff0c\u5170\u5c3c\u65af\u7279\u5bfc\u5e08\uff0c\u8fd9\u5e76\u4e0d\u662f\u4e00\u4e2a\u6210\u719f\u53ef\u9760\u7684\u7814\u7a76\u6210\u679c\uff0c\u5b83\u98ce\u9669\u5f88\u5927\uff0c\u975e\u5e38\u5927\u3002\u6211\u662f\u6d3b\u7740\u7ad9\u5728\u4f60\u9762\u524d\uff0c\u53ef\u6211\u5f53\u65f6\u7684\u4e09\u4e2a\u540c\u5b66\u5168\u90fd\u6d88\u5931\u5f97\u65e0\u5f71\u65e0\u8e2a\uff0c\u4e0d\u77e5\u9053\u88ab\u9001\u5230\u77f3\u5934\u4e2d\u95f4\u8fd8\u662f\u5ca9\u6d46\u91cc\uff0c\u751a\u81f3\u662f\u53e6\u5916\u7684\u4f4d\u9762\u53bb\u4e86\u3002\u201d</p><p>\u201c\u6211\u4f1a\u8c28\u614e\u7684\uff0c\u201d\u743c\u6069\u6536\u8d77\u8d44\u6599\uff0c\u201c\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u6211\u4e0d\u4f1a\u62ff\u81ea\u5df1\u7684\u6027\u547d\u5192\u9669\u7684\uff0c\u5bf9\u4e0d\u5bf9\u3002\u201d</p><p>\u201c\u5f53\u7136\uff0c\u201d\u4e9a\u5f53\u65af\u4e5f\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u5e0c\u671b\u80fd\u591f\u5e2e\u4e0a\u5fd9\u3002\u201d</p><p>\u4e8b\u60c5\u8c08\u59a5\uff0c\u743c\u6069\u4e5f\u5c31\u51c6\u5907\u8d77\u8eab\u544a\u8f9e\uff0c\u4e9a\u5f53\u65af\u5374\u51fa\u8a00\u633d\u7559\uff0c\u9080\u8bf7\u4ed6\u548c\u81ea\u5df1\u4e00\u8d77\u6765\u201c\u4eab\u7528\u201d\u4ed6\u7684\u65b0\u5973\u5974\u3002\u743c\u6069\u6709\u4e9b\u5fc3\u52a8\uff0c\u4ed6\u8fd8\u4ece\u6765\u6ca1\u8bd5\u8fc7\u8fd9\u79cd\u5f62\u5f0f\u76843P\uff0c\u4f46\u6700\u540e\u8fd8\u662f\u62d2\u7edd\u4e86\uff0c\u7406\u7531\u662f\u4ed6\u8fd8\u6709\u522b\u7684\u4e8b\u60c5\u8981\u529e\uff0c\u4f46\u771f\u5b9e\u7684\u539f\u56e0\u662f\uff1a\u4ed6\u6bd4\u8f83\u5bb3\u7f9e\uff0c\u4e0d\u60f3\u5f53\u7740\u53e6\u5916\u4e00\u4e2a\u7537\u6027\u7684\u9762\u628a\u81ea\u5df1\u8131\u5149\u3002</p></article>"}}}]);
