(window.webpackJsonp=window.webpackJsonp||[]).push([[281],{304:function(p){p.exports={chapterTitle:null,title:"\u7b2c281\u7ae0 \u751f\u65e5\u793c\u7269",content:"<article><h2>\u7b2c281\u7ae0 \u751f\u65e5\u793c\u7269</h2><p>\u3000\u3000\u5f20\u606a\u6709\u65f6\u516c\u53f8\u7684\u4e8b\u60c5\u7ed3\u675f\u8f83\u65e9\uff0c\u4e00\u822c\u4e5f\u4e0d\u4f1a\u76f4\u63a5\u56de\u5bb6\uff0c\u90fd\u5148\u8f6c\u5230\u90ae\u7535\u5c40\u7684\u804c\u5de5\u5bbf\u820d\u65b0\u9526\u56ed\u53bb\u770b\u4e00\u773c\uff0c\u6700\u8fd1\u4ed6\u5988\u4eba\u5341\u6709\u4e03\u516b\u4eba\u4f1a\u5728\u90a3\u91cc\uff0c\u5373\u4f7f\u65b0\u623f\u5b50\u91cc\u6ca1\u6709\u4eba\uff0c\u4e5f\u53ef\u4ee5\u5230\u9694\u58c1\u665a\u6674\u4f4f\u7684\u6d0b\u697c\u91cc\u6b47\u4e00\u6b47\u811a\u3002\u665a\u6674\u5143\u65e6\u8fc7\u540e\u5c31\u642c\u8fdb\u65b0\u9526\u56ed\u7684\u5c0f\u6d0b\u697c\uff0c\u6881\u683c\u73cd\u4e5f\u6253\u7b97\u5728\u6625\u8282\u4e4b\u524d\u642c\u8fdb\u6765\uff0c\u5012\u662f\u5f20\u77e5\u975e\u4e0d\u7740\u6025\uff0c\u4ed6\u73b0\u5728\u4f4f\u7684\u4e5f\u591f\u5bbd\u655e\uff0c\u539f\u6765\u7684\u4f4f\u5904\u79bb\u5973\u513f\u5f20\u73ab\u7684\u5b66\u6821\u4e5f\u8fd1\u4e00\u4e9b\u3002</p><p>\u3000\u30002\u67087\u65e5\u665a\u4e0a\uff0c\u5929\u7a7a\u98d8\u7740\u5c0f\u96ea\uff0c\u5f20\u606a\u60f3\u7740\u8bb8\u601d\u8fd9\u51e0\u5929\u5c31\u8981\u56de\u6d77\u5dde\uff0c\u8fd9\u51e0\u5929\u4e00\u76f4\u575a\u6301\u81ea\u5df1\u5f00\u8f66\uff0c\u514d\u5f97\u8bb8\u601d\u56de\u6765\u540e\u518d\u628a\u5085\u4fca\u4ed6\u4eec\u64b5\u5f00\u6709\u63a9\u8033\u76d7\u94c3\u7684\u5acc\u7591\u3002</p><p>\u3000\u3000\u968f\u7740\u5361\u5e26\u7684\u8f6c\u52a8\uff0c\u9f50\u79e6\u90a3\u7565\u5e26\u4f24\u611f\u7684\u6b4c\u58f0\u8f7b\u8f7b\u7684\u4f20\u51fa\u6765\uff0c\u6709\u7740\u72ec\u884c\u8352\u51c9\u8349\u539f\u4e2d\u7684\u51c4\u7f8e\uff0c\u611f\u89c9\u6005\u7136\uff0c\u5374\u53c8\u6c89\u9759\u584c\u5b9e\uff0c\u5b9e\u5728\u9002\u5408\u4e00\u4eba\u5728\u96ea\u591c\u91cc\u72ec\u81ea\u5df1\u7684\u542c\u3002</p><p>\u3000\u3000\u5f20\u606a\u4ece\u4e5d\u4e09\u5e74\u5f00\u59cb\u542c\u9f50\u79e6\u7684\u6b4c\uff0c\u90a3\u4e9b\u6e05\u51c0\u3001\u5bc2\u5bde\u3001\u72ec\u5b64\u7684\u58f0\u97f3\uff0c\u4f34\u968f\u4e86\u4ed6\u5ea6\u8fc7\u4e86\u5341\u4e94\u4e2a\u6625\u79cb\uff0c\u5f20\u606a\u4e4b\u6240\u4ee5\u8981\u5e2e\u6c5f\u9edb\u513f\u5706\u68a6\uff0c\u4e5f\u662f\u4ece\u5979\u7684\u58f0\u97f3\u91cc\u542c\u51fa\u6e05\u51c0\u3001\u5bc2\u5bde\u3001\u72ec\u5b64\u7684\u611f\u89c9\u51fa\u6765\uff0c\u90fd\u65e0\u6cd5\u60f3\u8c61\u7981\u9522\u5728\u5979\u90a3\u7edd\u4e16\u5bb9\u989c\u4e0e\u5b8c\u7f8e\u8eaf\u4f53\u4e0b\uff0c\u7adf\u662f\u600e\u4e48\u4e00\u9897\u6e05\u51c0\u3001\u5b64\u5bc2\u7684\u7075\u9b42\uff01</p><p>\u3000\u3000\u4e5d\u4e94\u5e74\u7684\u9f50\u79e6\u4e0e\u738b\u7956\u8d24\u8fd8\u662f\u4e16\u95f4\u604b\u60c5\u7684\u6977\u6a21\u3002\u4e5d\u4e03\u5e74\u7684\u51ac\u5b63\uff0c\u5bf9\u4e8e\u5f20\u606a\u6765\u8bf4\uff0c\u662f\u4e00\u4e2a\u5931\u610f\u7684\u51ac\u5b63\uff0c\u90a3\u65f6\u5510\u5a67\u51fa\u56fd\u624d\u534a\u5e74\uff0c\u800c\u9648\u5b81\u8fd8\u6ca1\u6709\u51fa\u73b0\uff0c\u5904\u4e86\u51e0\u4e2a\u5973\u5b69\u5b50\uff0c\u90fd\u6ca1\u6709\u611f\u89c9\uff0c\u53cd\u800c\u628a\u81ea\u5df1\u7684\u5fc3\u601d\u5904\u4e71\u4e86\u3002\u5f20\u606a\u8ba9\u675c\u98de\u62c9\u53bb\u8d74\u9f50\u79e6\u7684\u6f14\u5531\u4f1a\uff0c\u5f20\u606a\u5750\u5728\u524d\u9762\uff0c\u65c1\u8fb9\u7684\u4eba\u62fc\u4e86\u8001\u547d\u6325\u821e\u7740\u70b9\u71c3\u7684\u6253\u706b\u673a\uff0c\u5f53\u9f50\u79e6\u7b2c\u4e09\u6b21\u5531\u8d77\u300a\u5927\u7ea6\u5728\u51ac\u5b63\u300b\u65f6\u3002\u5168\u573a\u4e00\u8d77\u53eb\u558a\uff1a\u201c\u738b\u7956\u8d24\u2026\u2026\u201d\u5f20\u606a\u770b\u7740\u9f50\u79e6\u773c\u775b\u91cc\u5206\u660e\u542b\u7740\u6cea\u6c34\u3002\u5f20\u606a\u6b64\u65f6\u8fd8\u8bb0\u5f97\u96f6\u56db\u5e74\uff0c\u4e5f\u5c31\u662f\u516b\u5e74\u4e4b\u540e\u5728\u7535\u89c6\u91cc\u770b\u5230\u5bf9\u9f50\u79e6\u7684\u4e13\u8bbf\uff0c\u4ed6\u90a3\u65f6\u5df2\u7ecf\u80fd\u5b89\u9759\u8c41\u7136\u7684\u8bf4\u8d77\u4ed6\u8ddf\u738b\u7956\u8d24\u7684\u5206\u624b\uff0c\u5f20\u606a\u770b\u5230\u90a3\u5219\u8bbf\u8c08\uff0c\u7adf\u7136\u6709\u4e00\u79cd\u8bf4\u4e0d\u51fa\u7684\u9707\u52a8\uff0c\u987f\u89c9\u5c81\u6708\u5982\u901d\u3001\u65af\u4eba\u8001\u53bb\u7684\u611f\u89c9\u3002</p><p>\u3000\u3000\u628a\u7740\u65b9\u5411\u76d8\uff0c\u7a7f\u8fc7\u90ae\u653f\u5c40\u804c\u5de5\u5bbf\u820d\u533a\uff0c\u4ece\u5185\u5c97\u95e8\u62d0\u8fdb\u771f\u6b63\u610f\u4e49\u4e0a\u7684\u65b0\u9526\u56ed\uff0c\u770b\u7740\u65b0\u623f\u5b50\u91cc\u4eae\u7740\u706f\uff0c\u5f20\u606a\u7981\u4e0d\u4f4f\u60f3\u5c06\u8f66\u6084\u7136\u505c\u5728\u5c4b\u524d\uff0c\u5b89\u9759\u7684\u5750\u5728\u8f66\u91cc\u542c\u4f1a\u513f\u6b4c\u3002</p><p>\u3000\u3000\u91cd\u65b0\u56de\u5230\u4e5d\u56db\u5e74\uff0c\u6709\u7740\u5c11\u5e74\u7684\u8eab\u4f53\uff0c\u5374\u6ca1\u6709\u5c11\u5e74\u7684\u5fc3\uff0c\u5e38\u5e38\u4f1a\u6000\u5ff5\u672a\u66fe\u53d1\u751f\u8fc7\u7684\u5f80\u4e8b\uff0c\u8fd9\u4e9b\u5f80\u4e8b\u5927\u6982\u4e5f\u4e0d\u4f1a\u518d\u53d1\u751f\u4e86\u5427\uff0c\u6bd5\u7adf\u662f\u81ea\u5df1\u53e6\u4e00\u6bb5\u4eba\u751f\uff0c\u867d\u7136\u5145\u6ee1\u632b\u6298\uff0c\u5374\u5e76\u975e\u6ca1\u6709\u6000\u604b\u7684\u5730\u65b9\u3002</p><p>\u3000\u3000\u8fc7\u4e86\u597d\u4e45\uff0c\u5f20\u606a\u624d\u6ce8\u610f\u770b\u5230\u665a\u6674\u4f4f\u7684\u697c\u524d\u505c\u7740\u4e00\u8f86\u7c07\u65b0\u7684\u8f7f\u8f66\uff0c\u94f6\u7070\u8272\u7684\u8f66\u8eab\uff0c\u6709\u7740\u8fd0\u52a8\u8f7f\u8f66\u7684\u98ce\u683c\uff0c\u4fa7\u9762\u7ebf\u6761\u975e\u5e38\u7684\u51cc\u5389\uff0c\u5f20\u606a\u77ac\u65f6\u5c31\u8ba4\u5f97\u8fd9\u6b3e\u7b49\u5230\u5341\u5e74\u540e\u624d\u56fd\u4ea7\u5316\u7684VOLVO S40\uff0c\u5373\u4f7f\u5341\u5e74\u540e\u56fd\u4ea7\u5316\u7684S40\uff0c\u4e5f\u662fC\u7ea7\u8f7f\u8f66\u7684\u9876\u5cf0\u4e4b\u4f5c\uff0c\u5927\u6982\u4e0e\u5b9d\u9a6c\u4e09\u7cfb\u3001\u5965\u8feaA4\uff0c\u5904\u4e8e\u540c\u4e00\u6c34\u51c6\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u6bb5\u65f6\u95f4\u5bf9\u8f7f\u8f66\u4e5f\u5f88\u5173\u6ce8\uff0c\u90fd\u4e0d\u6653\u5f97VOLVO\u5df2\u7ecf\u63a8\u51faS40\uff1b\u8fd9\u6837\u7684\u4e00\u6b3e\u8f7f\u8f66\u505c\u5728\u665a\u6674\u4f4f\u7684\u697c\u524d\uff0c\u5f20\u606a\u9996\u5148\u60f3\u5230\u53ef\u80fd\u662f\u8c22\u5251\u5357\u90a3\u5bb6\u4f19\u53c8\u8fc7\u6765\u53d7\u8c11\u4e86\uff0c\u4e0d\u8fc7\u900f\u8fc7\u81ea\u5bb6\u7684\u7a97\u6237\uff0c\u665a\u6674\u8ddf\u5988\u5988\u5728\u4e00\u8d77\u804a\u5929\uff0c\u5979\u4eec\u6ca1\u6709\u6ce8\u610f\u5230\u5f20\u606a\u7684\u8f66\u5df2\u7ecf\u505c\u5728\u5c4b\u5916\uff0c\u5f20\u606a\u5fc3\u60f3\u665a\u6674\u4e00\u5b9a\u662f\u7ed9\u5988\u5988\u62c9\u8fc7\u6765\u8ba8\u8bba\u623f\u95f4\u600e\u4e48\u5e03\u7f6e\u5408\u9002\u3002</p><p>\u3000\u3000\u8fd9\u51e0\u5929\u6c14\u6e29\u9661\u7136\u964d\u4f4e\uff0c\u5e7c\u513f\u56ed\u63d0\u524d\u653e\u5047\uff0c\u82b7\u5f64\u5df2\u7ecf\u56de\u7701\u57ce\u5230\u5f90\u5b66\u5e73\u3001\u5468\u53d4\u60e0\u8eab\u8fb9\u3002</p><p>\u3000\u3000\u665a\u6674\u4f4f\u7684\u697c\u91cc\uff0c\u9664\u4e86\u4fdd\u59c6\uff0c\u4e0d\u50cf\u6709\u5ba2\u4eba\u7684\u6837\u5b50\uff0c\u5f20\u606a\u6709\u4e9b\u5947\u602a\uff0c\u5c06\u97f3\u54cd\u5173\u6389\uff0c\u63a8\u95e8\u4e0b\u4e86\u8f66\uff0c\u5ba4\u5916\u7684\u6c14\u6e29\u771f\u7684\u5f88\u4f4e\uff0c\u9661\u7136\u8d70\u51fa\u8f66\uff0c\u5f20\u606a\u5934\u90fd\u5dee\u70b9\u7f29\u8116\u5b50\u91cc\u3002</p><p>\u3000\u3000\u5f20\u606a\u8d70\u4e0a\u53f0\u9636\uff0c\u6309\u4e86\u95e8\u94c3\uff0c\u867d\u7136\u65b0\u623f\u5b50\u6309\u88c5\u4e86\u667a\u80fd\u5e94\u7b54\u7cfb\u7edf\uff0c\u4e0d\u8fc7\u4ee5\u5f20\u606a\u7684\u89c1\u8bc6\uff0c\u77e5\u9053\u5c31\u7b97\u5728\u4e5d\u4e94\u5e74\uff0c\u667a\u80fd\u5e94\u7b54\u7cfb\u7edf\u4e5f\u53ef\u4ee5\u505a\u5f97\u66f4\u597d\uff0c\u4e0d\u8fc7\u60f3\u60f3\u5c0f\u53d4\u7684\u5b8f\u8fdc\u5b9e\u4e1a\u4e00\u5e74\u591a\u524d\uff0c\u8fd8\u662f\u53bf\u91cc\u5efa\u7b51\u961f\uff0c\u80fd\u6709\u8fd9\u6837\u7684\u610f\u8bc6\u5df2\u7ecf\u4e0d\u9519\u4e86\u3002</p><p>\u3000\u3000\u6881\u683c\u73cd\u63a2\u5934\u51fa\u6765\u4e00\u770b\uff0c\u89c1\u662f\u5f20\u606a\uff0c\u4e00\u8fb9\u8d70\u8fc7\u6765\u5f00\u95e8\uff0c\u4e00\u8fb9\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u6709\u94a5\u5319\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd8\u6ca1\u6709\u6b63\u5f0f\u4f4f\u8fdb\u6765\uff0c\u8c01\u628a\u94a5\u5319\u5e26\u8eab\u4e0a\uff1f\u201d\u5176\u4ed6\u5bb6\u4ff1\u8fd8\u6ca1\u6709\u9f50\u5168\uff0c\u7a7a\u8c03\u5012\u662f\u5148\u88c5\u4e0a\u4e86\uff0c\u5ba4\u5185\u6e29\u5ea6\u86ee\u9ad8\uff0c\u665a\u6674\u90fd\u628a\u5916\u5957\u8131\u4e86\uff0c\u53ea\u7a7f\u7740\u5b9d\u84dd\u8272\u7684\u7f00\u6709\u4e0d\u8bf1\u94a2\u7eb9\u9970\u6813\u6263\u7684\u7f8a\u7ed2\u886b\uff0c\u5f20\u606a\u5bf9\u5979\u7b11\u4e86\u7b11\uff0c\u95ee\u5979\uff0c\u201c\u4f60\u623f\u5b50\u5916\u9762\u505c\u7684\u90a3\u8f86VOLVO\uff0c\u662f\u8c01\u7684\uff1f\u201d</p><p>\u3000\u3000\u201c\u7ed9\u4f60\u7684\u3002\u201d\u665a\u6674\u7b11\u7740\u6307\u7740\u5f20\u606a\u3002</p><p>\u3000\u3000\u201c\u7ed9\u6211\u7684\uff1f\u201d\u5f20\u606a\u6307\u4e86\u6307\u81ea\u5df1\u7684\u9f3b\u5934\uff0c\u6709\u4e9b\u7591\u60d1\u7684\u95ee\u3002</p><p>\u3000\u3000\u201c\u4eca\u5929\uff0c\u4f60\u4e0d\u662f\u53ef\u80fd\u62e5\u6709\u4e00\u8f86\u81ea\u5df1\u7684\u8f66\u5b50\u5417\uff1f\u201d\u665a\u6674\u772f\u7740\u773c\u775b\u7b11\u7740\uff0c\u4e30\u8174\u660e\u4eae\u7684\u8138\u5e9e\u72b9\u5982\u660e\u6708\u4e00\u6837\u6d01\u767d\u3002</p><p>\u3000\u3000\u5f20\u606a\u56de\u5934\u95ee\u5979\u5988\u5988\uff1a\u201c\u4eca\u5929\u51e0\u53f7\uff0c\u662f2\u67087\u65e5\uff1f\u201d</p><p>\u3000\u3000\u201c\u5916\u9762\u90a3\u8f66\u662f\u7ed9\u5c0f\u606a\u7684\uff0c\u4e0d\u4f1a\u592a\u7eb5\u5bb9\u4ed6\uff1f\u201d\u6881\u683c\u73cd\u4e5f\u6709\u4e9b\u8bb6\u5f02\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u591a\u4e8f\u665a\u6674\u8bb0\u5f97\u5c0f\u606a\u751f\u65e5\uff0c\u6211\u4eec\u90fd\u4e60\u60ef\u6b63\u6708\u91cc\u7ed9\u4ed6\u4e0b\u7897\u957f\u5bff\u9762\u62c9\u5012\u3002\u201d</p><p>\u3000\u3000\u4e5d\u516d\u5e74\u7684\u6625\u8282\u683c\u5916\u7684\u665a\uff0c\u5f20\u606a\u7684\u751f\u65e5\u516c\u5386\u662f2\u67087\u65e5\uff0c\u518d\u8fc7\u534a\u4e2a\u6708\u624d\u5230\u6625\u8282\uff0c\u5f20\u606a\u4e60\u60ef\u8fc7\u519c\u5386\u7684\u751f\u65e5\uff0c\u6839\u672c\u6ca1\u6709\u610f\u8bc6\u4ed6\u4ece\u4eca\u5929\u8d77\u5c31\u53ef\u4ee5\u6b63\u5f0f\u62e5\u6709\u4e00\u8f86\u8f66\u3002</p><p>\u3000\u3000\u5f20\u606a\u5f20\u4e86\u5f20\u5634\uff0c\u4ed6\u90fd\u6253\u7b97\u6700\u8fd1\u7ed9\u81ea\u5df1\u8ba2\u8f86\u597d\u8f66\uff0c\u6ca1\u60f3\u5230\u665a\u6674\u65e9\u5c31\u7ed9\u4ed6\u51c6\u5907\u597d\u4e86\uff0c\u8be7\u5f02\u7684\u95ee\u665a\u6674\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u6211\u559c\u6b22\u8fd9\u6b3e\u8f66\uff1f\u201d</p><p>\u3000\u3000\u201c\u524d\u4e9b\u5929\u4f60\u4e0d\u662f\u5728\u770b\u6709\u5173VOLVO\u7684\u6742\u5fd7\uff1f\u6b63\u597dVOLVO\u63a8\u51fa\u7b2c\u4e00\u6b3e\u56db\u95e8\u8dd1\u8f66\uff0c\u6211\u5c31\u6258\u6211\u5728\u6b27\u6d32\u7684\u540c\u5b66\u8ba2\u4e86\u4e00\u8f86\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u62cd\u62cd\u8111\u888b\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u8c22\u8c22\u665a\u6674\u59d0\uff0c\u4e0d\u8fc7\u65e9\u77e5\u9053\u665a\u6674\u59d0\u4f1a\u9001\u6211\u8f66\uff0c\u5c31\u770b\u6709\u62a5\u9053\u5170\u535a\u57fa\u5c3c\u7684\u6742\u5fd7\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5170\u535a\u57fa\u5c3c\uff1f\u201d\u6881\u683c\u73cd\u5012\u662f\u51c6\u5907\u7684\u590d\u8ff0\u4e86\u4e00\u904d\u53d1\u97f3\uff0c\u201c\u4ec0\u4e48\u8f66\u5b50\uff1f\u201d</p><p>\u3000\u3000\u665a\u6674\u7b11\u4e86\u8d77\u6765\uff0c\u71a0\u71a0\u751f\u8f89\u7684\u7738\u5b50\u5b9a\u775b\u770b\u7740\u5f20\u606a\uff0c\u6709\u7740\u65e0\u6cd5\u5f53\u7740\u6881\u683c\u73cd\u7684\u9762\u8868\u9732\u7684\u60c5\u611f\u5728\u5979\u7684\u773c\u775b\u91cc\u95ea\u52a8\uff0c\u8f7b\u58f0\u7684\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u4f1a\u559c\u6b22\u7684\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u624b\u6307\u70b9\u4e86\u70b9\u989d\u5934\uff0c\u4ed6\u7684\u6027\u606a\u8fd8\u771f\u65e0\u6cd5\u5c06\u9020\u578b\u62c9\u98ce\u7684\u5170\u535a\u57fa\u5c3c\u5f00\u51fa\u53bb\uff0c\u4e5f\u4e0d\u53ef\u80fd\u5728\u9ad8\u901f\u8def\u4e0a\u98da\u51fa\u4e09\u767e\u516c\u91cc\u4ee5\u4e0a\u7684\u65f6\u901f\u6765\u3002\u90fd\u906d\u9047\u4e24\u6b21\u8f66\u7978\u4e86\uff0c\u5bf9\u5f20\u606a\u6765\u8bf4\uff0c\u6c7d\u8f66\u7684\u5b89\u5168\u6027\u80fd\u662f\u4ed6\u6700\u770b\u4e2d\u7684\uff0c\u53ea\u662fS40\u4e4b\u524d\u7684VOLVO\u8f7f\u8f66\u5f88\u5c11\u6709\u8f66\u578b\u4eae\u773c\u7684\u3002\u5f20\u606a\u5c06\u4ed6\u5f00\u6765\u7684\u8f66\u7684\u94a5\u5319\u4e22\u7ed9\u4ed6\u5988\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u521a\u62ff\u5230\u9a7e\u8bc1\uff0c\u7b49\u4f1a\u513f\u81ea\u5df1\u5c06\u8f66\u5f00\u56de\u53bb\uff0c\u6211\u8ddf\u665a\u6674\u59d0\u8bd5\u65b0\u8f66\u53bb\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u8981\u8bf7\u665a\u6674\u59d0\u5728\u5916\u9762\u559d\u987f\u9152\u5e86\u795d\u4e00\u4e0b\u751f\u65e5\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u90fd\u8fd9\u4e48\u665a\u4e86\uff0c\u8fd8\u8bd5\u4ec0\u4e48\u8f66\uff0c\u559d\u4ec0\u4e48\u9152\u2026\u2026\u201d\u6881\u683c\u73cd\u8bdd\u6ca1\u8bf4\u5b8c\uff0c\u5f20\u606a\u5c31\u62c9\u7740\u665a\u6674\u51fa\u53bb\u4e86\uff0c\u65e0\u5948\u7684\u6447\u4e86\u6447\u5934\uff0c\u53c8\u6ca1\u6709\u628a\u63e1\u5c06\u5916\u9762\u7684\u8f66\u5355\u72ec\u5f00\u56de\u53bb\uff0c\u7ed9\u5f20\u77e5\u884c\u6253\u4e86\u7535\u8bdd\uff0c\u8ba9\u4ed6\u8fc7\u6765\u63a5\u5979\u3002\u5176\u5b9e\u65b0\u5c45\u5df2\u7ecf\u5e03\u7f6e\u5dee\u4e0d\u591a\u4e86\uff0c\u665a\u4e0a\u5927\u53ef\u4ee5\u4f4f\u4e0b\u6765\uff0c\u867d\u7136\u4e0d\u4f1a\u5927\u8086\u529e\u4e54\u8fc1\u9152\uff0c\u4f46\u662f\u4e5f\u8981\u9009\u597d\u65e5\u5b50\u624d\u4f1a\u6b63\u5f0f\u5165\u4f4f\u3002</p><p>\u3000\u3000\u665a\u6674\u8eab\u5b50\u503e\u8fc7\u6765\uff0c\u5e2e\u7740\u5c06\u8f66\u94a5\u5319\u63d2\u4e0a\uff0c\u5f20\u606a\u626d\u52a8\u8f66\u94a5\u5319\u542f\u52a8\uff0c\u542c\u7740\u5f15\u64ce\u5fae\u9e23\u7684\u53d1\u52a8\u8d77\u6765\uff0c\u95fb\u7740\u65b0\u9c9c\u7684\u76ae\u9769\u5473\u9053\uff0c\u5f20\u606a\u6253\u7740\u65b9\u5411\u76d8\uff0c\u5c06\u8f66\u5f00\u51fa\u65b0\u9526\u56ed\u3002</p><p>\u3000\u3000\u5929\u5bd2\u5730\u51bb\uff0c\u591c\u867d\u7136\u4e0d\u6df1\uff0c\u4f46\u8def\u4e0a\u51e0\u4e4e\u6ca1\u6709\u884c\u4eba\uff0c\u53ea\u542c\u89c1\u5f15\u64ce\u7684\u5fae\u9e23\u5728\u98a4\u6296\uff0c\u5728\u57ce\u5c71\u8def\u4e0a\u7a0d\u7a0d\u62c9\u4e86\u4e00\u4e0b\u901f\u5ea6\uff0cS40\u5e76\u4e0d\u4ee5\u901f\u5ea6\u5236\u80dc\uff0c\u6700\u9ad8\u65f6\u901f\u5dee\u4e0d\u591a\u4e5f\u5c31\u5728\u4e8c\u767e\u516c\u91cc\u5de6\u53f3\uff0c\u4e0d\u8fc7\u6700\u7b26\u5408\u5f20\u606a\u7684\u5fc3\u610f\u3002</p><p>\u3000\u3000\u201c\u8c22\u8c22\u4f60\uff0c\u665a\u6674\u2026\u2026\u201d\u5f20\u606a\u5c06\u8f66\u505c\u5728\u8def\u7259\u5b50\u4e0a\uff0c\u505c\u5728\u4e00\u76cf\u5df2\u7ecf\u7184\u6389\u7684\u8def\u706f\u67f1\u4e0b\u6765\uff0c\u8fdc\u5904\u7684\u8def\u706f\u5149\u4f20\u6765\u5df2\u7ecf\u5f88\u5f31\uff0c\u5f20\u606a\u95ee\u665a\u6674\uff0c\u201c\u8981\u4e0d\u8981\u501f\u6211\u7684\u6000\u62b1\u7ed9\u4f60\u7528\u4e00\u7528\uff1f\u201d</p><p>\u3000\u3000\u665a\u6674\u5634\u89d2\u542b\u7740\u6e29\u67d4\u7684\u6d45\u7b11\uff0c\u6ca1\u6709\u8bf4\u8bdd\uff0c\u5f80\u5f20\u606a\u8fd9\u8fb9\u79fb\u4e86\u79fb\uff0c\u4f9d\u7740\u4ed6\u7684\u8eab\u5b50\u3002</p><p>\u3000\u3000\u5f20\u606a\u6253\u5f00\u5929\u7a97\uff0c\u5c06\u5ea7\u4f4d\u8c03\u4f4e\uff0c\u4e24\u4eba\u5e76\u6392\u4ef0\u8eba\u7740\uff0c\u53ef\u4ee5\u770b\u89c1\u7a97\u5916\u6e05\u51b7\u7684\u660e\u6708\uff0c\u4e24\u4eba\u8eab\u4f53\u4f9d\u7740\uff0c\u4e5f\u4e0d\u89c9\u5f97\u51b7\u3002</p><p>\u3000\u3000\u4e60\u60ef\u5fae\u5f31\u7684\u5149\u7ebf\uff0c\u5f20\u606a\u4fa7\u5934\u770b\u7740\u665a\u6674\u7684\u4e30\u8174\u800c\u5a07\u8273\u7684\u8138\u5e9e\uff0c\u5979\u4f9d\u4e4e\u611f\u89c9\u5230\u5f20\u606a\u5728\u770b\u5979\uff0c\u4e5f\u4fa7\u8fc7\u5934\u6765\uff0c\u660e\u4eae\u773c\u7738\u91cc\u85cf\u7740\u65e0\u67d4\u7684\u6e29\u67d4\u4e4b\u60c5\uff0c\u6ca1\u6709\u60c5\u6b32\u71c3\u70e7\u7684\u6fc0\u70c8\uff0c\u4f46\u662f\u5fc3\u4e2d\u6700\u67d4\u8f6f\u7684\u90a3\u4e2a\u5730\u65b9\u88c5\u6ee1\u65e0\u53ef\u9053\u540d\u7684\u6e29\u67d4\u4e4b\u611f\uff0c\u5e73\u9759\u5982\u6c34\u7684\u5fc3\u5883\u6380\u8d77\u7740\u4e9b\u8bb8\u7684\u6ce2\u6f9c\u3002\u665a\u6674\u975e\u5e38\u4eab\u53d7\u8fd9\u6837\u7684\u611f\u89c9\uff0c\u4e0d\u7528\u62c5\u5fc3\u6d53\u70c8\u7684\u60c5\u6b32\u4e4b\u706b\u65e0\u53ef\u904f\u5236\u7684\u5c06\u6240\u6709\u4e00\u5207\u90fd\u6467\u6bc1\uff0c\u770b\u7740\u5f20\u606a\u6df1\u9083\u7684\u9ed1\u8272\u773c\u7738\u5728\u5fae\u5fae\u7684\u95ea\u52a8\uff0c\u5979\u7684\u8138\u4e0a\u4e5f\u6d6e\u8d77\u65e0\u7aef\u7684\u59a9\u5a9a\u3002</p><p>\u3000\u3000\u5f20\u606a\u4f38\u8fc7\u624b\uff0c\u57ab\u5230\u665a\u6674\u7684\u9888\u8116\u5b50\u4e0b\uff0c\u8ba9\u5979\u6795\u7740\u81ea\u5df1\u7684\u624b\u81c2\uff0c\u55c5\u7740\u5979\u53d1\u9645\u4f20\u6765\u597d\u95fb\u7684\u9999\u6ce2\u9999\u6c14\uff0c\u4e5f\u7981\u4e0d\u4f4f\u6709\u4e9b\u610f\u4e71\u60c5\u8ff7\uff0c\u8f7b\u8f7b\u6273\u8fc7\u5979\u7684\u8138\uff0c\u5728\u5979\u5a07\u6da6\u7684\u7ea2\u5507\u8f7b\u8f7b\u5544\u4e86\u4e00\u53e3\u3002</p><p>\u3000\u3000\u201c\u6211\u7238\u5988\u53ef\u90fd\u4e0d\u628a\u6211\u7684\u751f\u65e5\u5f53\u56de\u4e8b\uff0c\u8fd8\u6ca1\u6709\u6b63\u5f0f\u6536\u5230\u4e00\u4efd\u751f\u65e5\u793c\u7269\u5462\u3002\u201d</p><p>\u3000\u3000\u201c\u90a3\u6211\u6bcf\u5e74\u90fd\u8bb0\u5f97\u7ed9\u4f60\u9001\u4efd\u751f\u65e5\u793c\u7269\uff0c\u201d\u665a\u6674\u95fb\u7740\u5f20\u606a\u6d53\u51bd\u7684\u5e26\u7740\u7537\u4eba\u6c14\u606f\u7684\u9f3b\u606f\uff0c\u547c\u5438\u6709\u4e9b\u4e71\uff0c\u4e24\u624b\u8f7b\u8f7b\u6298\u653e\u5728\u5f20\u606a\u7684\u80f8\u819b\u4e0a\uff0c\u611f\u89c9\u4ed6\u5fc3\u810f\u5f3a\u6709\u529b\u7684\u8df3\u52a8\uff0c\u201c\u522b\u4eba\u90fd\u4f1a\u8bb0\u5f97\u4f60\u7684\u519c\u5386\u751f\u65e5\uff0c\u6211\u5c31\u4e13\u95e8\u5e2e\u4f60\u8fc7\u516c\u5386\u751f\u65e5\u5427\u3002\u201d</p></article>"}}}]);
