(window.webpackJsonp=window.webpackJsonp||[]).push([[288],{310:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u56db\u8282 \u5174\u8870",content:"<article><h2>\u7b2c\u56db\u8282 \u5174\u8870</h2><p>\u6240\u8c13\u6954\u5f62\u6587\u5b57\uff0c\u662f\u6307\u5c45\u4f4f\u5728\u5730\u7403\u4e24\u6cb3\u6d41\u57df\u7684\u82cf\u7f8e\u5c14\u4eba\u521b\u9020\u7684\u4e00\u79cd\u6587\u5b57\uff0c\u56e0\u4e3a\u5176\u65f6\u8fd8\u6ca1\u6709\u9020\u7eb8\u672f\uff0c\u6587\u5b57\u90fd\u662f\u7528\u82a6\u6746\u6216\u6728\u68d2\u505a\u6210\u7684\u4e09\u89d2\u5c16\u5934\u7b14\uff0c\u5728\u6ce5\u677f\u4e0a\u4e66\u5199\u800c\u6210\uff0c\u7136\u540e\u667e\u5e72\u518d\u8fdb\u884c\u70e7\u5236\u3002\u8fd9\u6837\u5199\u51fa\u6765\u7684\u5b57\uff0c\u843d\u7b14\u5904\u5370\u75d5\u8f83\u4e3a\u6df1\u5bbd\uff0c\u63d0\u7b14\u5904\u8f83\u4e3a\u7ec6\u72ed\uff0c\u770b\u8d77\u6765\u5448\u5c16\u5934\u6954\u5f62\uff0c\u662f\u4ee5\u5f97\u540d\u3002</p><p>\u6954\u5f62\u6587\u5b57\u548c\u4e2d\u56fd\u6c49\u5b57\u3001\u57c3\u53ca\u5723\u4e66\u5b57\u5e76\u5217\uff0c\u79f0\u4e3a\u53e4\u4ee3\u4e09\u5927\u8c61\u5f62\u6587\u5b57\uff0c\u743c\u6069\u81ea\u7136\u662f\u4e45\u95fb\u5176\u540d\u7684\u3002\u53ea\u8981\u6709\u57fa\u672c\u7684\u5386\u53f2\u5e38\u8bc6\uff0c\u8c01\u4e0d\u77e5\u9053\u6954\u5f62\u6587\u5b57\u2014\u2014\u5c31\u7b97\u6ca1\u6709\u5386\u53f2\u5e38\u8bc6\uff0c\u603b\u4e5f\u542c\u8fc7\u67d0\u9996\u300a\u7231\u5728\u897f\u5143\u524d\u300b\uff0c\u6b4c\u8bcd\u91cc\u5c31\u63d0\u5230\u3002\u5f53\u7136\uff0c\u4ec5\u4ec5\u542c\u95fb\uff0c\u5e76\u4e0d\u610f\u5473\u7740\u80fd\u591f\u8ba4\u51fa\u6765\uff0c\u743c\u6069\u4e4b\u6240\u4ee5\u8ba4\u5f97\uff0c\u7eaf\u7cb9\u662f\u4e2a\u4e0d\u5927\u4e0d\u5c0f\u7684\u610f\u5916\u3002</p><p>\u56e0\u4e3a\u4ed6\u4e0a\u8f88\u5b50\u662f\u5b66\u6cd5\u5f8b\u7684\u3002</p><p>\u5bf9\u4e8e\u6cd5\u5b66\u9662\u7684\u5b66\u751f\u800c\u8a00\uff0c\u201c\u6c49\u7a46\u62c9\u6bd4\u6cd5\u5178\u201d\u4e43\u662f\u5e38\u8bc6\uff0c\u8fd9\u662f\u53e4\u5df4\u6bd4\u4f26\u56fd\u738b\u6c49\u7a46\u62c9\u6bd4\u9881\u5e03\u7684\u4e00\u90e8\u6210\u6587\u6cd5\u5178\uff0c\u662f\u4eba\u7c7b\u6700\u65e9\u7684\u51e0\u5927\u6cd5\u5178\u4e4b\u4e00\u3002\u5df4\u6bd4\u4f26\u662f\u627f\u7ee7\u82cf\u7f8e\u5c14\u800c\u8d77\u7684\u6587\u660e\uff0c\u4f9d\u65e7\u6cbf\u7528\u6954\u5f62\u6587\u5b57\uff0c\u8fd9\u90e8\u8457\u540d\u7684\u6cd5\u5178\u81ea\u7136\u4e5f\u4e0d\u4f8b\u5916\u3002\u743c\u6069\u5f53\u5e74\u8bfb\u4e66\u7684\u65f6\u5019\uff0c\u5b66\u5230\u8fd9\u4e00\u7ae0\u8282\uff0c\u4e00\u65f6\u597d\u5947\uff0c\u4e8e\u662f\u5c31\u627e\u4e86\u4e9b\u76f8\u5173\u8d44\u6599\u6765\u770b\uff0c\u56e0\u6b64\u5bf9\u6954\u5f62\u6587\u5b57\u6709\u4e9b\u76f4\u89c2\u5370\u8c61\u3002</p><p>\u6c49\u7a46\u62c9\u6bd4\u6cd5\u5178\u662f\u795e\u6388\u6cd5\u5178\uff0c\u901a\u7bc7\u5ba3\u79f0\u56fd\u738b\u53d7\u547d\u4e8e\u795e\u660e\u7edf\u6cbb\u56db\u65b9\uff0c\u79c9\u627f\u795e\u610f\u9881\u5e03\u5f8b\u6cd5\uff0c\u52a8\u8f84\u62ff\u795e\u5f53\u540e\u53f0\uff0c\u6240\u4ee5\u91cc\u9762\u201c\u795e\u660e\u201d\u8fd9\u4e2a\u8bcd\u51fa\u73b0\u5f97\u5f88\u9891\u7e41\u3002\u743c\u6069\u770b\u5f97\u591a\u4e86\uff0c\u81ea\u7136\u773c\u719f\uff0c\u540e\u6765\u53c8\u5f97\u77e5\u82cf\u7f8e\u5c14\u4eba\u4ee5\u661f\u8fb0\u4e3a\u795e\u660e\uff0c\u6240\u4ee5\u8fd9\u4e24\u4e2a\u8bcd\u7684\u5b57\u5f62\u975e\u5e38\u8fd1\u4f3c\uff0c\u89c9\u5f97\u6709\u8da3\uff0c\u4fbf\u8bb0\u4e86\u4e0b\u6765\u3002</p><p>\u6954\u5f62\u6587\u5b57\u539f\u672c\u662f\u5199\u5728\u6ce5\u677f\u4e0a\uff0c\u843d\u7b14\u65f6\u8f83\u4e3a\u7528\u529b\uff0c\u5370\u75d5\u5bbd\u6df1\uff0c\u63d0\u7b14\u65f6\u529b\u9053\u6700\u5f31\uff0c\u81ea\u7136\u663e\u5f97\u7ec6\u7a84\uff0c\u6574\u4e2a\u7b14\u5212\u4fbf\u5448\u9510\u4e09\u89d2\u5f62\uff0c\u5916\u8868\u7279\u5f81\u660e\u663e\uff0c\u539f\u672c\u662f\u5f88\u5bb9\u6613\u8fa8\u8ba4\u51fa\u7684\u3002\u7136\u800c\u6885\u83f2\u65af\u7684\u8fd9\u4e9b\u8d44\u6599\u90fd\u662f\u5979\u81ea\u5df1\u6284\u5f55\u800c\u6765\uff0c\u5e76\u975e\u539f\u59cb\u7248\u672c\uff0c\u867d\u7136\u5e76\u65e0\u9519\u8bb9\uff0c\u4f46\u5979\u7528\u6765\u4e66\u5199\u7684\u662f\u9e45\u6bdb\u7b14\u548c\u517d\u76ae\u7eb8\uff0c\u81ea\u7136\u5199\u4e0d\u51fa\u90a3\u79cd\u6954\u5f62\u6548\u679c\uff0c\u53cd\u6b63\u8fd9\u4e5f\u4e0d\u5f71\u54cd\u9605\u8bfb\u2014\u2014\u7136\u800c\u649e\u4e0a\u743c\u6069\u8fd9\u79cd\u7565\u77e5\u76ae\u6bdb\u7684\u5916\u884c\u4eba\uff0c\u4e00\u5f00\u59cb\u4fbf\u600e\u4e48\u4e5f\u60f3\u4e0d\u8d77\u6765\u4e86\uff0c\u76f4\u5230\u6885\u83f2\u65af\u8bf4\u51fa\u201c\u661f\u8fb0\u201d\u548c\u201c\u795e\u660e\u201d\uff0c\u4ed6\u624d\u9661\u7136\u60f3\u8d77\u5178\u6545\uff0c\u53cd\u5e94\u8fc7\u6765\u3002</p><p>\u743c\u6069\u8bfb\u8fc7\u6c49\u7a46\u62c9\u6bd4\u6cd5\u5178\u539f\u6587\uff0c\u591a\u5c11\u4e5f\u8fd8\u8ba4\u5f97\u51e0\u4e2a\u5b57\uff0c\u53ea\u662f\u65f6\u95f4\u957f\u4e86\u6e10\u6e10\u9057\u5fd8\u3002\u5982\u4eca\u65e2\u7136\u60f3\u901a\uff0c\u56de\u5934\u518d\u6765\u770b\u8fd9\u5f20\u7eb8\uff0c\u548c\u8111\u4e2d\u8bb0\u5fc6\u5370\u8bc1\uff0c\u53c8\u8ba4\u51fa\u4e24\u4e2a\u5b57\u6765\uff0c\u66f4\u52a0\u786e\u4fe1\u8fd9\u5c31\u662f\u6954\u5f62\u6587\u5b57\u65e0\u7591\u3002\u7136\u800c\u95ee\u9898\u5728\u4e8e\uff1a\u5730\u7403\u4e0a\u7684\u6954\u5f62\u6587\u5b57\uff0c\u600e\u4e48\u4f1a\u8dd1\u5230\u8d39\u4f26\u6765\uff0c\u800c\u4e14\u8fd8\u662f\u4ec0\u4e48\u6069\u745f\u6587\u5b57\uff0c\u662f\u53e4\u4ee3\u4f0a\u739b\u65af\u5361\u5e1d\u56fd\u7684\u901a\u7528\u6587\u5b57\u4e4b\u4e00\u2026\u2026</p><p>\u5239\u90a3\u4e4b\u95f4\uff0c\u65e0\u6570\u79cd\u5ff5\u5934\u5728\u8111\u4e2d\u63a0\u8fc7\u3002</p><p>\u201c\u4ec0\u4e48\u662f\u6954\u5f62\u6587\u5b57\uff1f\u201d\u6885\u83f2\u65af\u95ee\u3002</p><p>\u743c\u6069\u6b63\u8981\u95ee\u5979\u89e3\u91ca\uff0c\u8f6c\u5ff5\u4e00\u60f3\uff0c\u81ea\u5df1\u5982\u4eca\u5176\u5b9e\u4e5f\u4e0d\u80fd\u5c31\u80af\u5b9a\u8fd9\u662f\u6954\u5f62\u6587\u5b57\uff0c\u521a\u624d\u7684\u60f3\u6cd5\u672a\u514d\u6709\u4e9b\u6b66\u65ad\u4e86\u3002\u4ec5\u4ec5\u51ed\u51e0\u4e2a\u5b57\u96f7\u540c\uff0c\u53ea\u6015\u505a\u4e0d\u5f97\u51c6\uff0c\u4f55\u51b5\u81ea\u5df1\u5bf9\u6954\u5f62\u6587\u5b57\u5176\u5b9e\u53ea\u77e5\u9053\u76ae\u6bdb\u4e2d\u7684\u76ae\u6bdb\uff0c\u8bf4\u4e0d\u5b9a\u5f88\u5927\u7a0b\u5ea6\u8fd8\u5e26\u6709\u81ea\u6211\u6697\u793a\u7684\u6210\u5206\u5728\u5185\u3002\u8c61\u5f62\u6587\u5b57\uff0c\u539f\u672c\u5c31\u662f\u4ee5\u56fe\u753b\u5f62\u5f0f\u8868\u610f\uff0c\u7528\u8fd1\u4f3c\u7684\u5b57\u5f62\u8868\u793a\u540c\u4e00\u4e2a\u610f\u601d\uff0c\u4e5f\u4e0d\u662f\u4ec0\u4e48\u532a\u5937\u6240\u601d\u7684\u4e8b\u60c5\u3002</p><p>\u4ee5\u4ed6\u7684\u6c34\u51c6\uff0c\u8981\u60f3\u5224\u5b9a\u662f\u5426\u6954\u5f62\u6587\u5b57\uff0c\u4ece\u5185\u5bb9\u4e0a\u662f\u529e\u4e0d\u5230\u7684\u3002\u6700\u7b80\u5355\u7684\u529e\u6cd5\uff0c\u5c31\u662f\u53bb\u770b\u6700\u521d\u7684\u539f\u6587\uff0c\u6700\u597d\u662f\u5199\u5728\u6ce5\u677f\u4e0a\u7684\uff0c\u770b\u5b57\u8ff9\u7b14\u5212\u3002\u5018\u82e5\u4e5f\u662f\u505a\u5c16\u5934\u6954\u5f62\uff0c\u90a3\u5c31\u5e94\u8be5\u6ca1\u9519\u4e86\uff0c\u4e16\u754c\u4e0a\u603b\u4e0d\u53ef\u80fd\u6709\u8fd9\u4e48\u5de7\u5408\u7684\u4e8b\u60c5\u3002</p><p>\u6b63\u81ea\u601d\u5fd6\uff0c\u6885\u83f2\u65af\u4ece\u65c1\u8fb9\u62ff\u51fa\u53e6\u5916\u51e0\u5f20\u7eb8\u6765\uff0c\u9012\u7ed9\u743c\u6069\uff0c\u201c\u90a3\u8fd9\u79cd\u6587\u5b57\u4f60\u8ba4\u8bc6\u5417\uff1f\u201d</p><p>\u743c\u6069\u770b\u4e86\u770b\uff0c\u8fd9\u53c8\u662f\u53e6\u5916\u4e00\u79cd\u8c61\u5f62\u6587\u5b57\uff0c\u4f46\u81ea\u5df1\u5168\u65e0\u5370\u8c61\uff0c\u5e94\u8be5\u662f\u4ece\u6ca1\u89c1\u8fc7\uff0c\u201c\u4e0d\u8ba4\u8bc6\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u8fd9\u662f\u4ec0\u4e48\u6587\u5b57\uff1f\u201d</p><p>\u201c\u8fd9\u662f\u7a46\u7f55\u745e\u5fb7\u6587\u5b57\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u548c\u6069\u745f\u6587\u5b57\u4e00\u6837\uff0c\u90fd\u662f\u4f0a\u739b\u65af\u5361\u5e1d\u56fd\u901a\u884c\u7684\u8bed\u8a00\u4e4b\u4e00\u3002\u201d</p><p>\u201c\u8fd9\u4f0a\u739b\u65af\u5361\u5230\u5e95\u6709\u51e0\u79cd\u6587\u5b57\uff1f\u201d</p><p>\u201c\u636e\u8bf4\u6709\u4e09\u79cd\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u4f0a\u739b\u65af\u5361\u8bed\u3001\u7a46\u7f55\u745e\u5fb7\u8bed\u548c\u6069\u745f\u8bed\u2014\u2014\u4f46\u4e5f\u6709\u4eba\u8bf4\u5176\u5b9e\u6709\u56db\u79cd\uff0c\u5177\u4f53\u7684\u6211\u5c31\u4e0d\u6e05\u695a\u4e86\u3002\u201d</p><p>\u743c\u6069\u6447\u6447\u5934\uff0c\u201c\u53bb\u56fe\u4e66\u9986\u3002\u201d\u4ed6\u5bf9\u6885\u83f2\u65af\u8bf4\u3002</p><p>\u203b\u203b\u203b</p><p>\u4f0a\u5361\u6c99\u57ce\u662f\u6614\u65e5\u6df1\u9083\u6749\u90a3\u5854\u738b\u56fd\u7684\u953b\u9020\u548c\u5b66\u672f\u4e2d\u5fc3\uff0c\u57ce\u4e2d\u6709\u4e00\u5ea7\u5de8\u5927\u7684\u56fe\u4e66\u9986\uff0c\u548c\u738b\u5bab\u3001\u83ab\u62c9\u4e01\u795e\u6bbf\u201c\u8fdc\u53e4\u7194\u7089\u201d\u5e76\u5217\u4e3a\u4e09\u5927\u5efa\u7b51\u3002\u56fe\u4e66\u9986\u957f\u591a\u7eb3\xb7\u88c2\u6b4c\u662f\u4e00\u4f4d\u5973\u6027\u76fe\u77ee\u4eba\uff0c\u79c3\u9876\uff0c\u6ee1\u8138\u76b1\u7eb9\uff0c\u770b\u8d77\u6765\u5e94\u8be5\u5df2\u7ecf\u975e\u5e38\u8001\u4e86\uff0c\u6234\u7740\u4e00\u526f\u84dd\u6c34\u6676\u78e8\u5236\u6210\u7684\u773c\u955c\uff0c\u7b11\u8d77\u6765\u6a21\u6837\u975e\u5e38\u548c\u853c\u53ef\u4eb2\uff0c\u4eff\u4f5b\u4e00\u4f4d\u6148\u7965\u7684\u8001\u5976\u5976\u3002\u7136\u800c\u743c\u6069\u5bf9\u5979\u4e1d\u6beb\u4e0d\u6562\u6020\u6162\uff0c\u201c\u88c2\u6b4c\u201d\u4e5f\u662f\u8457\u540d\u7684\u77ee\u4eba\u5bb6\u65cf\uff0c\u4e0d\u4e9a\u4e8e\u201c\u6864\u9aa8\u201d\uff0c\u548c\u9c81\u6587\u56fd\u738b\u6240\u5c5e\u7684\u201c\u77f3\u80a9\u201d\u5bb6\u65cf\u7d20\u6765\u4ea4\u597d\uff0c\u5728\u6444\u653f\u8bae\u4f1a\u4e2d\u4e5f\u7a33\u7a33\u5360\u4e86\u4e24\u5e2d\uff0c\u591a\u7eb3\u56fe\u4e66\u9986\u957f\u662f\u88c2\u6b4c\u5bb6\u65cf\u4e2d\u7684\u91cd\u8981\u4eba\u7269\uff0c\u8f7b\u5ffd\u4e0d\u5f97\u3002</p><p>\u743c\u6069\u9053\u660e\u6765\u610f\uff0c\u5f53\u7136\u4e0d\u4f1a\u63d0\u4ec0\u4e48\u6954\u5f62\u6587\u5b57\uff0c\u53ea\u8bf4\u5bf9\u4f0a\u739b\u65af\u5361\u5e1d\u56fd\u6709\u5174\u8da3\uff0c\u60f3\u8981\u6709\u66f4\u591a\u4e86\u89e3\uff0c\u5e0c\u671b\u80fd\u89c2\u6469\u539f\u59cb\u5178\u7c4d\u548c\u6587\u7269\u3002\u4ee5\u4ed6\u5982\u4eca\u7684\u8eab\u4efd\uff0c\u8fd9\u4e2a\u8981\u6c42\u5e76\u4e0d\u7b97\u8fc7\u5206\uff0c\u591a\u7eb3\u5374\u9762\u6709\u96be\u8272\uff0c\u539f\u6765\u56fe\u4e66\u9986\u91cc\u6709\u5173\u4f0a\u739b\u65af\u5361\u7684\u8d44\u6599\u5012\u662f\u6709\u4e00\u4e9b\uff0c\u7136\u800c\u4e5f\u5168\u90fd\u662f\u77ee\u4eba\u8f97\u8f6c\u8a8a\u5199\u6284\u5f55\u6765\u7684\uff0c\u54ea\u91cc\u6709\u743c\u6069\u6240\u8981\u7684\u6700\u521d\u539f\u4ef6\uff0c\u8fd9\u4e2a\u8981\u6c42\u538b\u6839\u5c31\u6ca1\u6cd5\u5b9e\u73b0\u3002</p><p>\u201c\u6211\u542c\u8bf4\u8fd9\u79cd\u6069\u745f\u6587\u5b57\u6700\u521d\u662f\u5199\u5728\u6ce5\u677f\u4e0a\u7684\uff0c\u201d\u743c\u6069\u8bd5\u63a2\u8bf4\uff0c\u201c\u524d\u5bbd\u540e\u7a84\uff0c\u7b14\u5212\u5448\u9510\u4e09\u89d2\u5f62\uff0c\u662f\u8fd9\u6837\u5417\uff1f\u201d</p><p>\u591a\u7eb3\u6447\u5934\uff0c\u201c\u6069\u745f\u6587\u5b57\u6211\u5b8c\u5168\u4e0d\u61c2\uff0c\u201d\u5979\u8bf4\uff0c\u5e26\u7740\u6b49\u610f\u5730\u7b11\u7b11\uff0c\u201c\u4e5f\u6ca1\u542c\u8bf4\u8fc7\u8fd9\u79cd\u8bf4\u6cd5\u2026\u2026\u6050\u6015\u5e2e\u4e0d\u4e0a\u4ec0\u4e48\u5fd9\u4e86\u3002\u201d</p><p>\u743c\u6069\u6709\u4e9b\u5931\u671b\uff0c\u8fd9\u6837\u4e00\u6765\uff0c\u4ed6\u6ca1\u529e\u6cd5\u786e\u5b9a\u8fd9\u662f\u5426\u5c31\u662f\u6954\u5f62\u6587\u5b57\u3002\u8fd9\u4e2a\u57fa\u672c\u524d\u63d0\u4e0d\u641e\u6e05\u695a\uff0c\u540e\u9762\u7684\u4e00\u5207\u63a8\u8bba\u548c\u731c\u6d4b\u4e5f\u5c31\u662f\u7a7a\u8c08\uff0c\u53ea\u80fd\u5148\u653e\u5728\u4e00\u8fb9\u4e86\u3002</p><p>\u201c\u90a3\u80fd\u5426\u8bf7\u6559\u4e00\u4e0b\u4f0a\u739b\u65af\u5361\u5e1d\u56fd\u662f\u600e\u4e48\u56de\u4e8b\u3002\u201d</p><p>\u6885\u83f2\u65af\u8ddf\u968f\u5927\u4e3b\u6559\u65e5\u4e45\uff0c\u4ece\u4ed6\u90a3\u91cc\u542c\u8fc7\u4e00\u4e9b\uff0c\u591a\u7eb3\u9986\u957f\u867d\u7136\u4e0d\u61c2\u6069\u745f\u6587\uff0c\u5bf9\u4f0a\u739b\u65af\u5361\u4e5f\u6709\u4e9b\u96f6\u788e\u4e86\u89e3\uff0c\u4ece\u65c1\u8865\u5145\u3002\u62fc\u51d1\u8d77\u6765\uff0c\u4e5f\u5c31\u6709\u4e86\u4e2a\u7c97\u7565\u7684\u5927\u81f4\u8f6e\u5ed3\u3002</p><p>\u4f0a\u739b\u65af\u5361\u662f\u4e00\u4e2a\u8fdc\u53e4\u7684\u4eba\u7c7b\u5e1d\u56fd\uff0c\u5efa\u7acb\u4e8e\u5927\u7ea6\u4e00\u4e07\u5e74\u524d\uff0c\u6bc1\u706d\u4e8e\u56db\u5343\u5e74\u524d\u2014\u2014\u7cbe\u786e\u7684\u65f6\u95f4\u4e0d\u6e05\u695a\uff0c\u6ca1\u6709\u8bb0\u8f7d\u3002\u548c\u8010\u745f\u4e00\u6837\uff0c\u4f0a\u739b\u65af\u5361\u4ee5\u9b54\u6cd5\u8457\u79f0\uff0c\u5deb\u5e08\u6267\u653f\u3002\u8010\u745f\u7684\u5deb\u5e08\u79f0\u4e3a\u201c\u5965\u672f\u5e08\u201d\uff0c\u4f0a\u739b\u65af\u5361\u7684\u5deb\u5e08\u5219\u79f0\u201c\u5947\u68b0\u5e08\u201d\uff0c\u5e1d\u56fd\u7684\u7687\u5e1d\u53c8\u79f0\u201c\u5947\u68b0\u5e08\u4e4b\u738b\u201d\uff0c\u662f\u5168\u56fd\u5deb\u5e08\u7684\u9886\u8896\u3002</p><p>\u201c\u5947\u68b0\u5e08\uff1f\u201d\u743c\u6069\u91cd\u590d\u7740\u8fd9\u4e2a\u8bcd\uff0c\u201c\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\u3002\u201d</p><p>\u201c\u636e\u8bf4\u4f0a\u739b\u65af\u5361\u7684\u5deb\u5e08\u5728\u5236\u4f5c\u9b54\u6cd5\u7269\u54c1\u4e0a\u6781\u6709\u4e13\u64c5\uff0c\u5c24\u5176\u7cbe\u4e8e\u5236\u9020\u6784\u88c5\u3001\u9b54\u50cf\u548c\u5404\u79cd\u795e\u5947\u673a\u68b0\uff0c\u56e0\u6b64\u5f97\u540d\u3002\u201d</p><p>\u201c\u54e6\u3002\u201d</p><p>\u9664\u4e86\u64c5\u957f\u5236\u9020\u6784\u88c5\u673a\u68b0\uff0c\u4f0a\u739b\u65af\u5361\u7684\u5deb\u5e08\u540c\u884c\u4eec\u5728\u7a7a\u95f4\u9b54\u6cd5\u4e0a\u4e5f\u9020\u8be3\u9ad8\u660e\uff0c\u4ed6\u4eec\u5efa\u7acb\u4e86\u904d\u5e03\u5168\u56fd\u5404\u5730\u7684\u4f20\u9001\u95e8\uff0c\u751a\u81f3\u80fd\u591f\u6253\u5f00\u901a\u5f80\u5f02\u6b21\u5143\u7684\u901a\u9053\uff0c\u8fdb\u884c\u4f4d\u9762\u65c5\u884c\uff0c\u636e\u8bf4\u8fd8\u66fe\u7ecf\u60f3\u5efa\u7acb\u8054\u7ed3\u7269\u8d28\u754c\u548c\u6df1\u6e0a\u7684\u6c38\u4e45\u4f4d\u9762\u4f20\u9001\u95e8\uff0c\u4e0d\u8fc7\u6700\u7ec8\u5931\u8d25\u4e86\u3002</p><p>\u201c\u4f20\u8bf4\u5728\u4f0a\u739b\u65af\u5361\u5e1d\u56fd\u7684\u5168\u76db\u65f6\u671f\uff0c\u4f20\u9001\u95e8\u904d\u5e03\u5168\u56fd\uff0c\u80fd\u591f\u8ba9\u6574\u652f\u519b\u56e2\u77ac\u95f4\u62b5\u8fbe\u76ee\u7684\u5730\u3002\u5947\u68b0\u5e08\u4eec\u5236\u9020\u5927\u91cf\u7684\u9b54\u50cf\u5080\u5121\u548c\u6784\u88c5\u4f53\uff0c\u4ee3\u66ff\u4eba\u529b\u2014\u2014\u4e0d\u4ec5\u5982\u6b64\uff0c\u4f0a\u739b\u65af\u5361\u8fd8\u6709\u4e03\u79d8\u5668\u3002\u201d</p><p>\u201c\u4e03\u79d8\u5668\uff1f\u201d</p><p>\u201c\u55ef\uff0c\u201d\u6885\u83f2\u65af\u70b9\u5934\uff0c\u201c\u4e03\u79d8\u5668\u662f\u4f0a\u739b\u65af\u5361\u5e1d\u56fd\u9b54\u6cd5\u6210\u5c31\u7684\u6700\u9ad8\u7ed3\u6676\uff0c\u638c\u63e1\u5728\u7687\u5ba4\u624b\u4e2d\uff0c\u662f\u6781\u5177\u5a01\u80fd\u7684\u9b54\u6cd5\u7269\u54c1\uff0c\u5171\u6709\u4e03\u4ef6\u3002\u636e\u8bf4\u53ea\u8981\u4e03\u79d8\u5668\u5728\u624b\uff0c\u7eb5\u7136\u662f\u795e\u7947\u4e5f\u8981\u656c\u754f\u4e09\u5206\u2014\u2014\u5f53\u7136\uff0c\u8fd9\u4e9b\u73b0\u5728\u4e5f\u65e0\u4ece\u8003\u8bc1\u4e86\uff0c\u53ea\u662f\u4e0a\u53e4\u6d41\u4f20\u4e0b\u6765\u7684\u4f20\u8bf4\uff0c\u5f88\u591a\u4eba\u90fd\u8ba4\u4e3a\u662f\u65e0\u7a3d\u4e4b\u8c08\uff0c\u4f46\u5927\u4e3b\u6559\u76f8\u4fe1\u4e03\u79d8\u5668\u662f\u786e\u5b9e\u5b58\u5728\u7684\u3002\u201d</p><p>\u62e5\u6709\u5982\u6b64\u5f3a\u5927\u7684\u9b54\u6cd5\u6280\u827a\uff0c\u53c8\u6709\u8fd9\u4f20\u8bf4\u4e2d\u7684\u4e03\u79d8\u5668\uff0c\u4f0a\u739b\u65af\u5361\u81ea\u7136\u662f\u76db\u6781\u4e00\u65f6\uff0c\u51e0\u4e4e\u7edf\u6cbb\u4e86\u6574\u4e2a\u8d39\u4f26\u5927\u9646\u7684\u4e1c\u90e8\uff0c\u4e43\u662f\u5f53\u65f6\u7b2c\u4e00\u7b49\u7684\u5927\u5e1d\u56fd\u3002</p><p>\u53ea\u662f\uff0c\u53e4\u5f80\u4eca\u6765\uff0c\u4e16\u95f4\u5c82\u6709\u4e0d\u6b7b\u4e4b\u4eba\uff0c\u5c82\u6709\u4e0d\u706d\u4e4b\u56fd\uff0c\u8f89\u714c\u5982\u8010\u745f\u745e\u5c14\uff0c\u5982\u4eca\u4e5f\u53ea\u5269\u4e0b\u4e00\u7247\u701a\u6d77\u9ec4\u6c99\u4e86\uff0c\u8fd9\u751f\u6b7b\u5174\u8870\u7684\u94c1\u5f8b\uff0c\u4f0a\u739b\u65af\u5361\u4e5f\u9003\u8131\u4e0d\u5f97\u3002\u5927\u7ea6\u5728\u8ddd\u4eca\u4e94\u5343\u591a\u5e74\u524d\uff0c\u4e00\u573a\u5927\u761f\u75ab\u5e2d\u5377\u4e86\u6574\u4e2a\u4f0a\u739b\u65af\u5361\uff0c\u5c06\u8fd1\u4e09\u5206\u4e4b\u4e00\u7684\u4eba\u53e3\u6b7b\u4ea1\uff0c\u5e1d\u56fd\u51e0\u4e4e\u56e0\u6b64\u5d29\u6e83\u3002\u552f\u4e00\u5e86\u5e78\u7684\uff0c\u662f\u5f53\u65f6\u5468\u8fb9\u654c\u4eba\u4e5f\u5168\u90fd\u906d\u5230\u761f\u75ab\uff0c\u65e0\u4e00\u4f8b\u5916\u7684\u5143\u6c14\u5927\u4f24\uff0c\u5426\u5219\u4f0a\u739b\u65af\u5361\u53ea\u6015\u5f53\u5373\u5c31\u4f1a\u4ea1\u56fd\u3002</p><p>\u63a5\u4e0b\u6765\u7684\u4e8b\u60c5\u5c31\u6bd4\u8f83\u532a\u5937\u6240\u601d\u4e86\uff0c\u6885\u83f2\u65af\u7684\u63cf\u8ff0\u7684\u65f6\u5019\u795e\u8272\u90fd\u6709\u4e9b\u4e0d\u4ee5\u4e3a\u7136\uff0c\u660e\u663e\u89c9\u5f97\u8fc7\u4e8e\u8352\u8c2c\u3002\u636e\u8bf4\u5728\u5927\u761f\u75ab\u4e4b\u540e\uff0c\u4eba\u53e3\u9510\u51cf\uff0c\u5f53\u65f6\u7684\u4f0a\u739b\u65af\u5361\u7687\u5e1d\u2014\u2014\u5947\u68b0\u5e08\u4e4b\u738b\u5bc7\u5766\u2014\u2014\u65e0\u5948\u4e4b\u4e0b\uff0c\u5192\u51fa\u4e86\u4e00\u4e2a\u6781\u5ea6\u75af\u72c2\u7684\u5ff5\u5934\uff0c\u4ed6\u96c6\u5408\u5168\u56fd\u5deb\u5e08\u4e4b\u529b\uff0c\u521b\u9020\u4e86\u4e24\u5ea7\u4f20\u9001\u95e8\uff0c\u8de8\u8d8a\u6676\u58c1\u7cfb\uff0c\u901a\u5f80\u4e00\u4e2a\u6ca1\u6709\u9b54\u6cd5\uff0c\u6ca1\u6709\u5deb\u5e08\u7684\u7269\u8d28\u754c\uff0c\u63a0\u593a\u5927\u91cf\u4eba\u53e3\u4f5c\u4e3a\u5974\u96b6\u3002\u6574\u4e2a\u7ed1\u67b6\u8fc7\u7a0b\u6301\u7eed\u4e86\u4e09\u56db\u767e\u5e74\uff0c\u4f0a\u739b\u65af\u5361\u4eba\u6293\u5230\u6570\u4ee5\u4e07\u8ba1\u7684\u5974\u96b6\uff0c\u4ee5\u4ed6\u4eec\u7684\u8840\u6c57\u727a\u7272\u4e3a\u4ee3\u4ef7\uff0c\u5e1d\u56fd\u7ec8\u4e8e\u4ece\u56f0\u5883\u4e2d\u8d70\u4e86\u51fa\u6765\uff0c\u6062\u590d\u7e41\u8363\u3002</p><p>\u4e24\u5ea7\u4f20\u9001\u95e8\uff0c\u901a\u5f80\u7684\u867d\u7136\u662f\u540c\u4e00\u4e2a\u4e16\u754c\uff0c\u4f46\u6293\u6765\u7684\u5974\u96b6\u5374\u662f\u4e24\u79cd\u4e0d\u540c\u7684\u4eba\uff0c\u6765\u81ea\u4e24\u4e2a\u4e0d\u540c\u5730\u533a\u3002\u4f0a\u739b\u65af\u5361\u4eba\u81ea\u7136\u4e0d\u5728\u610f\u8fd9\u5176\u4e2d\u533a\u522b\uff0c\u7edf\u79f0\u8fd9\u4e9b\u5974\u96b6\u4e3a\u201c\u7a46\u5170\u4eba\u201d\uff0c\u4f46\u5728\u5974\u96b6\u5185\u90e8\uff0c\u4ed6\u4eec\u53c8\u5206\u4e3a\u201c\u7a46\u7f55\u745e\u5fb7\u4eba\u201d\u548c\u201c\u6069\u745f\u4eba\u201d\u3002</p><p>\u968f\u7740\u5974\u96b6\u88ab\u6293\u6765\uff0c\u4ed6\u4eec\u539f\u672c\u7684\u6587\u660e\u81ea\u7136\u4e5f\u76f8\u5e94\u4f20\u64ad\u8fc7\u6765\uff0c\u6885\u83f2\u65af\u6284\u5f55\u6765\u7684\u8d44\u6599\uff0c\u4e00\u4efd\u662f\u7528\u6069\u745f\u6587\u5b57\u4e66\u5199\uff0c\u88ab\u743c\u6069\u8ba4\u4e3a\u5f88\u6709\u53ef\u80fd\u662f\u6954\u5f62\u6587\u5b57\uff0c\u53e6\u4e00\u4efd\u662f\u7a46\u7f55\u745e\u5fb7\u6587\u5b57\uff0c\u5f53\u7136\u4ed6\u538b\u6839\u4e0d\u8ba4\u8bc6\u3002\u8fd9\u4e24\u79cd\u6587\u5b57\uff0c\u867d\u7136\u662f\u4f0a\u739b\u65af\u5361\u5f53\u65f6\u901a\u884c\u7684\u8bed\u8a00\u4e4b\u4e00\uff0c\u5176\u5b9e\u662f\u5728\u5974\u96b6\u4e2d\u4f7f\u7528\uff0c\u5e76\u4e0d\u662f\u5b98\u65b9\u6587\u5b57\u3002\u4f0a\u739b\u65af\u5361\u7684\u5b98\u65b9\u6587\u5b57\uff0c\u81ea\u7136\u662f\u4f0a\u739b\u65af\u5361\u8bed\uff0c\u540c\u6837\u4e5f\u662f\u8c61\u5f62\u6587\u5b57\uff0c\u4f46\u5982\u4eca\u65e9\u5df2\u7ecf\u5931\u4f20\u2014\u2014\u6b64\u5916\uff0c\u636e\u8bf4\u5728\u7687\u5ba4\u548c\u9ad8\u5c42\uff0c\u8fd8\u6709\u53e6\u5916\u4e00\u79cd\u79d8\u5bc6\u901a\u884c\u7684\u6587\u5b57\uff0c\u4e0d\u77e5\u771f\u5047\u3002</p><p>\u5974\u96b6\u7684\u5230\u6765\uff0c\u867d\u7136\u4e3a\u4f0a\u739b\u65af\u5361\u5e1d\u56fd\u89e3\u51b3\u4e86\u5371\u673a\uff0c\u6446\u8131\u4e86\u56f0\u5883\uff0c\u4f46\u5374\u4e5f\u57cb\u4e0b\u4e86\u9690\u60a3\u3002\u4e00\u5343\u591a\u5e74\u540e\uff0c\u7a46\u7f55\u745e\u5fb7\u548c\u6069\u745f\u4e24\u5927\u795e\u7cfb\u7684\u795e\u7947\u4eec\u5723\u8005\u5316\u8eab\uff0c\u964d\u4e34\u5728\u4f0a\u739b\u65af\u5361\uff0c\u53f7\u79f0\u201c\u795e\u738b\u201d\uff0c\u7387\u9886\u5974\u96b6\u4eec\u8d77\u4e49\u9020\u53cd\u3002</p><p>\u201c\u7b49\u7b49\uff0c\u201d\u743c\u6069\u6253\u65ad\uff0c\u201c\u5b97\u6559\u5b66\u6211\u786e\u5b9e\u662f\u5f88\u5dee\u52b2\u4e86\uff0c\u4f46\u7a46\u7f55\u745e\u5fb7\u795e\u7cfb\u6211\u81f3\u5c11\u8fd8\u542c\u8bf4\u8fc7\u8fd9\u4e2a\u540d\u5b57\uff0c\u6069\u745f\u795e\u7cfb\u2026\u2026\u597d\u50cf\u5b8c\u5168\u6ca1\u5370\u8c61\u3002\u201d</p><p>\u201c\u56e0\u4e3a\u6069\u745f\u795e\u7cfb\u73b0\u5728\u5df2\u7ecf\u4e0d\u5b58\u5728\u4e86\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u5341\u4e94\u5e74\u524d\u7684\u52a8\u8361\u65f6\u4ee3\uff0c\u6069\u745f\u4e3b\u795e\u5409\u52d2\u4eca\u88ab\u6467\u6bc1\uff0c\u8fd9\u4e2a\u795e\u7cfb\u5c31\u7b97\u662f\u5f7b\u5e95\u6d88\u4ea1\u4e86\u3002\u201d</p><p>\u4f0a\u739b\u65af\u5361\u7684\u5947\u68b0\u5e08\u7eb5\u7136\u5b9e\u529b\u5f3a\u6a2a\uff0c\u7ec8\u7a76\u654c\u4e0d\u8fc7\u4e24\u5927\u795e\u7cfb\u8d85\u8fc7\u4e8c\u5341\u4f4d\u4ee5\u4e0a\u7684\u795e\u738b\u548c\u8fd1\u4e4e\u534a\u6570\u4ee5\u4e0a\u7684\u81e3\u6c11\u53db\u4e71\uff0c\u8282\u8282\u5931\u5229\u3002\u6570\u5341\u65e5\u540e\uff0c\u53db\u4e71\u8005\u653b\u5165\u7687\u57ce\uff0c\u672b\u4ee3\u7687\u5e1d\u3001\u5947\u68b0\u5e08\u4e4b\u738b\u6538\u74e6\u62c9\u548c\u7a46\u7f55\u745e\u5fb7\u795e\u738b\u8377\u9c81\u65af\u5728\u7687\u5bab\u51b3\u6218\uff0c\u843d\u8d25\u8eab\u6b7b\uff0c\u4f0a\u739b\u65af\u5361\u5e1d\u56fd\u5c31\u6b64\u8986\u706d\u3002</p><p>\u201c\u53ef\u662f\uff0c\u96be\u9053\u4f0a\u739b\u65af\u5361\u4eba\u7684\u795e\u660e\u5750\u89c6\u4e0d\u7406\u5417\uff1f\u201d\u743c\u6069\u63d0\u51fa\u6000\u7591\u3002</p><p>\u901a\u5e38\u6765\u8bf4\uff0c\u795e\u660e\u6781\u5c11\u4f1a\u91c7\u7528\u5982\u6b64\u6781\u7aef\u7684\u65b9\u5f0f\u6765\u5e72\u6d89\u7269\u8d28\u754c\u3002\u8010\u745f\u745e\u5c14\u5e1d\u56fd\u4ee5\u524d\u4e5f\u66fe\u7ecf\u5927\u89c4\u6a21\u4f7f\u7528\u4f8f\u5112\u5f53\u5974\u96b6\uff0c\u5e76\u4e0d\u66fe\u89c1\u4f8f\u5112\u8bf8\u795e\u4eb2\u81ea\u51b2\u4e0b\u6765\u7838\u573a\u5b50\u3002\u5982\u679c\u52a0\u5c14\xb7\u95ea\u91d1\uff08\u4f8f\u5112\u8bf8\u795e\u9886\u8896\uff09\u771f\u8fd9\u4e48\u505a\uff0c\u8010\u745f\u8bf8\u795e\u4e5f\u4e0d\u53ef\u80fd\u5750\u89c6\u4e0d\u7406\u3002\u7a46\u7f55\u745e\u5fb7\u548c\u6069\u745f\u8bf8\u795e\u8fd9\u4e48\u5e72\uff0c\u96be\u9053\u4f0a\u739b\u65af\u5361\u4eba\u7684\u795e\u660e\u5c31\u4e0d\u7406\u4e0d\u776c\uff1f</p><p>\u201c\u4f0a\u739b\u65af\u5361\u4eba\u6ca1\u6709\u795e\u660e\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u4ed6\u4eec\u662f\u65e0\u4fe1\u8005\u3002\u201d</p><p>\u201c\u5514\uff0c\u539f\u6765\u5982\u6b64\u3002\u201d</p><p>\u4f5c\u4e3a\u4eba\u7c7b\u5386\u53f2\u4e0a\u6700\u65e9\u7684\u9b54\u6cd5\u5e1d\u56fd\uff0c\u4f0a\u739b\u65af\u5361\u5e76\u4e0d\u51fa\u540d\uff0c\u751a\u81f3\u51e0\u4e4e\u6ca1\u4ec0\u4e48\u4eba\u77e5\u6653\uff0c\u8fde\u743c\u6069\u8fd9\u79cd\u5deb\u5e08\u90fd\u4e0d\u77e5\u9053\u3002\u4e0e\u4e4b\u76f8\u53cd\uff0c\u8010\u745f\u745e\u5c14\u5efa\u56fd\u8fd8\u8fdc\u5728\u4f0a\u739b\u65af\u5361\u4e4b\u540e\uff08\u8010\u745f\u5efa\u56fd\u4e8e\u8ddd\u4eca\u4e94\u5343\u5e74\u524d\uff09\uff0c\u5374\u540d\u58f0\u663e\u8d6b\u81f3\u6781\u3002\u8fd9\u5176\u4e2d\u81ea\u7136\u6709\u5f88\u591a\u539f\u56e0\uff0c\u6700\u91cd\u8981\u7684\u4e00\u70b9\uff0c\u662f\u56e0\u4e3a\u4f0a\u739b\u65af\u5361\u706d\u56fd\u4e4b\u540e\uff0c\u795e\u738b\u4e3a\u4e86\u9632\u6b62\u5947\u68b0\u5e08\u4eec\u590d\u8d77\uff0c\u5c3d\u53ef\u80fd\u6bc1\u6389\u4e86\u6240\u6709\u7684\u76f8\u5173\u6587\u660e\u548c\u8bb0\u8f7d\uff0c\u65f6\u95f4\u4e00\u957f\u81ea\u7136\u5c31\u6e10\u6e10\u6e6e\u6ca1\u65e0\u95fb\u3002</p><p>\u201c\u4f0a\u739b\u65af\u5361\u8986\u706d\u4e4b\u540e\uff0c\u5974\u96b6\u4eec\u5728\u795e\u738b\u7684\u7387\u9886\u4e0b\u5404\u81ea\u72ec\u7acb\u5efa\u56fd\uff0c\u662f\u4e3a\u7a46\u7f55\u745e\u5fb7\u548c\u6069\u745f\u4e24\u5927\u5e1d\u56fd\uff0c\u81f3\u4eca\u8fd8\u5b58\u5728\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u5982\u679c\u4f60\u5bf9\u6069\u745f\u6587\u5b57\u6216\u8005\u4f0a\u739b\u65af\u5361\u611f\u5174\u8da3\uff0c\u6709\u673a\u4f1a\u53ef\u4ee5\u53bb\u90a3\u91cc\u770b\u770b\uff0c\u6216\u8bb8\u80fd\u4e86\u89e3\u66f4\u591a\u2014\u2014\u4f46\u6211\u8981\u63d0\u9192\u7684\u662f\uff0c\u4e0d\u8981\u62b1\u592a\u9ad8\u5e0c\u671b\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u5f88\u7b80\u5355\uff0c\u5c31\u6bd4\u5982\u4f60\u624b\u4e0a\u8fd9\u4e9b\u6069\u745f\u6587\u5b57\uff0c\u5982\u679c\u73b0\u5728\u62ff\u53bb\u7ed9\u6069\u745f\u4eba\u770b\uff0c\u9664\u975e\u6781\u5c11\u6570\u4e13\u5bb6\uff0c\u5426\u5219\u534a\u4e2a\u90fd\u770b\u4e0d\u61c2\u3002\u8fd9\u4e24\u4e2a\u56fd\u5bb6\u867d\u7136\u5ef6\u7eed\u81f3\u4eca\uff0c\u4f46\u5c61\u906d\u6218\u4e71\uff0c\u6587\u5316\u4f20\u627f\u5176\u5b9e\u662f\u5b8c\u5168\u65ad\u88c2\u6389\u4e86\u3002\u201d</p><p>\u201c\u4e0d\u81f3\u4e8e\u5427\u3002\u201d</p><p>\u8bdd\u662f\u8fd9\u4e48\u8bf4\uff0c\u743c\u6069\u4e5f\u77e5\u9053\u8fd9\u5176\u5b9e\u975e\u5e38\u6709\u53ef\u80fd\u3002\u51e0\u5343\u5e74\u524d\u7684\u4e1c\u897f\uff0c\u80fd\u7559\u5b58\u591a\u5c11\u4e0b\u6765\u5f88\u503c\u5f97\u6000\u7591\u3002\u5728\u5730\u7403\u5386\u53f2\u4e0a\uff0c\u82cf\u7f8e\u5c14\u4eba\u4e5f\u4e0d\u8fc7\u5c31\u662f\u51e0\u5343\u5e74\u524d\u7684\u4e8b\u60c5\uff0c\u5982\u4eca\u8fde\u6954\u5f62\u6587\u5b57\u90fd\u65e9\u5df2\u7ecf\u5931\u4f20\uff0c\u9664\u4e86\u6781\u5c11\u6570\u4e13\u5bb6\u65e0\u4eba\u8ba4\u8bc6\u3002\u4e2d\u56fd\u53f7\u79f0\u6570\u5343\u5e74\u7684\u6587\u660e\u85aa\u706b\u4f20\u627f\uff0c\u7ef5\u5ef6\u4e0d\u7edd\uff0c\u7532\u9aa8\u6587\u7ffb\u51fa\u6765\u5927\u5bb6\u7167\u6837\u4e5f\u90fd\u77a0\u76ee\u7ed3\u820c\uff0c\u4e0d\u77e5\u6240\u8c13\u3002\u7a46\u7f55\u745e\u5fb7\u548c\u6069\u745f\u4eba\u4e0d\u8ba4\u8bc6\u53e4\u6587\u5b57\uff0c\u662f\u5f88\u6b63\u5e38\u7684\u4e8b\u60c5\u3002</p><p>\u6885\u83f2\u65af\u89c1\u591a\u8bc6\u5e7f\uff0c\u53c8\u6709\u4e00\u4e2a\u7cbe\u901a\u6b64\u9053\u7684\u5927\u4e3b\u6559\u5728\u65c1\u8fb9\uff0c\u5979\u8bf4\u5f97\u8bdd\uff0c\u53ef\u4fe1\u5ea6\u81ea\u7136\u662f\u5f88\u9ad8\u7684\u3002\u4f46\u5982\u6b64\u4e00\u6765\uff0c\u743c\u6069\u60f3\u5224\u65ad\u8fd9\u79cd\u6069\u745f\u6587\u5b57\u662f\u5426\u5c31\u662f\u6954\u5f62\u6587\u5b57\uff0c\u8fdb\u800c\u63a2\u7a76\u80cc\u540e\u7684\u5173\u7cfb\uff0c\u4fbf\u66f4\u4e0d\u5bb9\u6613\u4e86\u3002</p><p>\u800c\u4e14\u2026\u2026\u7a46\u7f55\u745e\u5fb7\u548c\u6069\u745f\u90fd\u5f88\u8fdc\uff0c\u975e\u5e38\u8fdc\uff0c\u4f4d\u4e8e\u7edd\u5883\u4e1c\u57df\uff0c\u5df2\u7ecf\u662f\u8d39\u4f26\u5927\u9646\u7684\u8fb9\u7f18\uff0c\u518d\u8fc7\u53bb\u5c31\u662f\u65e0\u5c3d\u8352\u91ce\u4e86\u3002\u743c\u6069\u76ee\u524d\u53bb\u8fc7\u6700\u8fdc\u7684\u5730\u65b9\u662f\u585e\u5c14\u2014\u2014\u800c\u8fd9\u4e24\u4e2a\u56fd\u5bb6\u6bd4\u585e\u5c14\u8fd8\u8981\u9065\u8fdc\u5f97\u591a\u3002\u8fd9\u4e2a\u4e16\u754c\u53ef\u6ca1\u6709\u98de\u673a\u822a\u73ed\u6216\u8005\u94c1\u8def\uff0c\u8981\u8fc7\u53bb\u4e00\u8d9f\u53ef\u4e0d\u5bb9\u6613\u3002</p><p>\u201c\u5982\u679c\u4f60\u771f\u6709\u5174\u8da3\uff0c\u8fd8\u4e0d\u5982\u6211\u4ecb\u7ecd\u5927\u4e3b\u6559\u7ed9\u4f60\u8ba4\u8bc6\u597d\u4e86\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u8bba\u53ca\u8fd9\u65b9\u9762\u7684\u9020\u8be3\uff0c\u4ed6\u7edd\u5bf9\u662f\u7b2c\u4e00\u6d41\u7684\u4e13\u5bb6\u3002\u201d</p><p>\u201c\u5514\u3002\u201d</p><p>\u743c\u6069\u542b\u7cca\u7b54\u5e94\u7740\uff0c\u4ed6\u4e0d\u592a\u60f3\u548c\u7267\u5e08\u6253\u4ea4\u9053\uff0c\u4f46\u53cd\u6b63\u8fd9\u4e5f\u662f\u4ee5\u540e\u7684\u4e8b\u60c5\u4e86\u3002\u5012\u662f\u521a\u624d\u6240\u8bf4\u5185\u5bb9\u91cc\u6709\u4e00\u70b9\uff0c\u5f15\u8d77\u4e86\u4ed6\u7684\u6d53\u539a\u5174\u8da3\u3002\u201c\u4f60\u521a\u624d\u8bf4\uff0c\u4f0a\u739b\u65af\u5361\u4eba\u6253\u5f00\u4f20\u9001\u95e8\uff0c\u4ece\u53e6\u4e00\u4e2a\u7269\u8d28\u754c\u91cc\u63a0\u593a\u4e86\u5927\u6279\u5974\u96b6\u2014\u2014\u4e5f\u5c31\u662f\u8bf4\uff0c\u7a46\u7f55\u745e\u5fb7\u4eba\u548c\u6069\u745f\u4eba\u5176\u5b9e\u6765\u81ea\u53e6\u5916\u7684\u4e16\u754c\uff1f\u201d</p><p>\u4e5f\u5c31\u662f\u7a7f\u8d8a\u3002</p><p>\u743c\u6069\u4e00\u76f4\u4ee5\u4e3a\u81ea\u5df1\u7684\u7a7f\u8d8a\u8eab\u4efd\u662f\u7edd\u65e0\u4ec5\u6709\u7684\u7279\u4f8b\uff0c\u4f46\u53c8\u6709\u8bf8\u591a\u96be\u89e3\u4e4b\u5904\uff0c\u6781\u6709\u53ef\u80fd\u548c\u81ea\u5df1\u4e3a\u4ec0\u4e48\u4f1a\u88ab\u770b\u4e2d\uff0c\u4f5c\u4e3a\u68cb\u5b50\u6709\u5173\uff0c\u7136\u800c\u60f3\u8981\u63a2\u7a76\u53c8\u6ca1\u6709\u534a\u70b9\u5934\u7eea\uff0c\u4e0d\u77e5\u9053\u4ece\u54ea\u91cc\u7740\u624b\u3002\u5982\u679c\u8bf4\uff0c\u5728\u5386\u53f2\u4e0a\u66fe\u7ecf\u53d1\u751f\u8fc7\u5982\u6b64\u5927\u89c4\u6a21\u7684\u96c6\u4f53\u7a7f\u8d8a\uff0c\u90a3\u5012\u5927\u53ef\u4ee5\u501f\u9274\u5206\u6790\u3002</p><p>\u4f0a\u739b\u65af\u5361\u7684\u5947\u68b0\u5e08\u6253\u5f00\u4f20\u9001\u95e8\uff0c\u901a\u5f80\u4e00\u4e2a\u6ca1\u6709\u9b54\u6cd5\uff0c\u6ca1\u6709\u5deb\u5e08\u7684\u4e16\u754c\u2014\u2014\u8fd9\u4e2a\u4e16\u754c\u4e0d\u4f1a\u662f\u5730\u7403\u5427\u3002</p><p>\u201c\u4f20\u8bf4\u800c\u5df2\uff0c\u201d\u6885\u83f2\u65af\u6447\u5934\uff0c\u201c\u524d\u9762\u8bf4\u7684\u90a3\u4e9b\u6709\u5173\u4f0a\u739b\u65af\u5361\u7684\u4e8b\u60c5\uff0c\u5f88\u5927\u7a0b\u5ea6\u4e0a\u90fd\u662f\u4f20\u95fb\uff0c\u5c31\u8fde\u5927\u4e3b\u6559\u90fd\u6ca1\u529e\u6cd5\u786e\u5b9a\u771f\u5047\uff0c\u4e5f\u6ca1\u6709\u53ef\u4fe1\u7684\u53f2\u6599\u4f50\u8bc1\u3002\u81f3\u4e8e\u8bf4\u7a46\u7f55\u745e\u5fb7\u548c\u6069\u745f\u4eba\u662f\u5f02\u4e16\u754c\u7684\u4eba\uff0c\u90a3\u4e2a\u6211\u4e0d\u76f8\u4fe1\u3002\u201d</p><p>\u4e0d\u76f8\u4fe1\u7684\u539f\u56e0\u5f88\u7b80\u5355\uff0c\u4ee5\u5982\u4eca\u7684\u9b54\u6cd5\u6c34\u51c6\u800c\u8bba\uff0c\u80fd\u591f\u8de8\u8d8a\u4f4d\u9762\uff0c\u4f8b\u5982\u8ba9\u4eba\u4ece\u7269\u8d28\u754c\u524d\u5f80\u6df1\u6e0a\uff0c\u4ece\u7f57\u4e1d\u7684\u8718\u86db\u9b54\u7f51\u524d\u5f80\u7cbe\u7075\u8bf8\u795e\u7684\u963f\u68b5\u591a\uff0c\u8fd9\u5df2\u7ecf\u662f\u6781\u81f4\u4e86\u3002\u4f0a\u739b\u65af\u5361\u5947\u68b0\u5e08\u7684\u4f5c\u6cd5\uff0c\u5374\u662f\u8de8\u8d8a\u6676\u58c1\u7cfb\uff0c\u8fd9\u6bd4\u8de8\u8d8a\u4f4d\u9762\u96be\u5ea6\u66f4\u8981\u9ad8\u5f97\u591a\u4e86\u3002\u6253\u4e2a\u7c97\u7565\u7684\u6bd4\u65b9\uff0c\u8de8\u8d8a\u4f4d\u9762\uff0c\u5c31\u50cf\u628a\u4eba\u4ece\u5730\u7403\u9001\u4e0a\u706b\u661f\uff0c\u6216\u8005\u9001\u4e0a\u5176\u4ed6\u661f\u7403\u2014\u2014\u8de8\u8d8a\u6676\u58c1\u7cfb\uff0c\u5374\u7b49\u4e8e\u662f\u8bf4\u8981\u4ece\u8fd9\u4e2a\u201c\u5b87\u5b99\u201d\u8f6c\u79fb\u5230\u53e6\u5916\u4e00\u4e2a\u201c\u5b87\u5b99\u201d\u4e2d\uff0c\u8fd9\u96be\u5ea6\u5b8c\u5168\u4e0d\u53ef\u540c\u65e5\u800c\u8bed\u3002</p><p>\u591a\u5143\u5b87\u5b99\u3001\u5e73\u884c\u7a7a\u95f4\u2014\u2014\u9b54\u6cd5\u5b66\u4e0a\u786e\u5b9e\u6709\u8fd9\u79cd\u7406\u8bba\uff0c\u4f46\u4e5f\u4ec5\u4ec5\u662f\u7406\u8bba\u800c\u5df2\u3002</p><p>\u201c\u4ee5\u73b0\u6709\u7684\u9b54\u6cd5\u6c34\u51c6\uff0c\u4e5f\u505a\u4e0d\u5230\u8fd9\u70b9\uff0c\u51e0\u5343\u5e74\u524d\u7684\u4f0a\u739b\u65af\u5361\u4eba\uff0c\u6211\u4e0d\u4fe1\u4ed6\u4eec\u80fd\u529e\u5230\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u81f3\u4e8e\u7a46\u7f55\u745e\u5fb7\u548c\u6069\u745f\u4eba\u7684\u6765\u5386\uff0c\u8fd9\u4e5f\u4e0d\u8fc7\u662f\u4f20\u8bf4\u800c\u5df2\uff0c\u8fde\u4ed6\u4eec\u81ea\u5df1\u90fd\u4e0d\u6e05\u695a\uff0c\u600e\u4e48\u80fd\u5f53\u771f\u3002\u201d</p><p>\u201c\u4e0d\u662f\u8bf4\u4f0a\u739b\u65af\u5361\u4eba\u5728\u7a7a\u95f4\u6cd5\u672f\u4e0a\u9020\u8be3\u5f88\u9ad8\u5417\uff1f\u201d\u743c\u6069\u63d0\u9192\u3002</p><p>\u201c\u9020\u8be3\u5f88\u9ad8\u4e5f\u4e0d\u662f\u65e0\u6240\u4e0d\u80fd\u3002\u4ed6\u4eec\u8fde\u5efa\u4e00\u5ea7\u8054\u7ed3\u7269\u8d28\u754c\u548c\u6df1\u6e0a\u7684\u4f20\u9001\u95e8\u90fd\u5931\u8d25\u4e86\uff0c\u8fd9\u79cd\u4e8b\u60c5\u53cd\u800c\u80fd\u6210\u529f\uff1f\u201d</p><p>\u201c\u800c\u4e14\uff0c\u4ee5\u60c5\u7406\u6765\u63a8\u6d4b\u7684\u8bdd\uff0c\u5982\u679c\u771f\u7684\u5b58\u5728\u53e6\u5916\u4e00\u4e2a\u6676\u58c1\u7cfb\uff0c\u6709\u53e6\u5916\u4e00\u4e2a\u7269\u8d28\u754c\u2014\u2014\u90a3\u4f60\u89c9\u5f97\u4ed6\u4eec\u7684\u5f62\u8c61\u4f1a\u548c\u6211\u4eec\u4e00\u6837\u5417\uff1f\u201d\u6885\u83f2\u65af\u7ee7\u7eed\u8bf4\uff0c\u201c\u6211\u89c1\u8fc7\u7a46\u7f55\u745e\u5fb7\u4eba\uff0c\u548c\u6211\u4eec\u7684\u957f\u76f8\u4e5f\u6ca1\u4ec0\u4e48\u5dee\u522b\uff0c\u6807\u51c6\u7684\u4eba\u7c7b\uff0c\u5982\u679c\u8bf4\u662f\u5f02\u4e16\u754c\u7684\u4eba\uff0c\u90a3\u4e5f\u672a\u514d\u592a\u8fc7\u51d1\u5de7\u4e86\u3002\u201d</p><p>\u4ece\u9053\u7406\u4e0a\u8bf4\uff0c\u6885\u83f2\u65af\u7684\u770b\u6cd5\u663e\u7136\u662f\u7406\u667a\u7684\uff0c\u8fd9\u5c31\u50cf\u62cd\u5916\u661f\u4eba\u7535\u5f71\uff0c\u5018\u82e5\u628a\u5916\u661f\u4eba\u8bbe\u8ba1\u5f97\u548c\u5730\u7403\u4eba\u4e00\u6837\uff0c\u5bfc\u6f14\u5b9a\u7136\u4f1a\u88ab\u75db\u9a82\uff0c\u552f\u6709\u5f04\u6210ET\u90a3\u79cd\u5947\u5f62\u602a\u72b6\u89c2\u4f17\u624d\u89c9\u5f97\u7b26\u5408\u60c5\u7406\u3002\u5927\u5343\u4e16\u754c\uff0c\u54ea\u6709\u90a3\u822c\u5de7\u5408\uff0c\u5916\u661f\u4eba\u5c31\u957f\u5f97\u548c\u5730\u7403\u4eba\u4e00\u6837\uff1f\u95ee\u9898\u5728\u4e8e\u743c\u6069\u81ea\u5df1\u5c31\u662f\u4e2a\u7a7f\u8d8a\u8005\uff0c\u4ed6\u8111\u4e2d\u6709\u7740\u6e05\u6e05\u695a\u695a\u7684\u6709\u5173\u53e6\u5916\u4e00\u4e2a\u4e16\u754c\u7684\u8bb0\u5fc6\u2014\u2014\u90a3\u4e2a\u4e16\u754c\u91cc\uff0c\u6ca1\u6709\u9b54\u6cd5\uff0c\u6ca1\u6709\u5deb\u5e08\uff0c\u4f46\u5374\u6070\u597d\u5c31\u6709\u548c\u8fd9\u4e2a\u4e16\u754c\u7684\u201c\u4eba\u7c7b\u201d\u957f\u5f97\u4e00\u6837\u7684\u4eba\u7c7b\uff0c\u975e\u5e38\u7b26\u5408\u8fd9\u4e2a\u4f0a\u739b\u65af\u5361\u4f20\u8bf4\u2026\u2026</p><p>\u4e0d\uff0c\u6709\u4e00\u70b9\u786e\u5b9e\u4e0d\u540c\uff0c\u743c\u6069\u7684\u8bb0\u5fc6\u91cc\uff0c\u5730\u7403\u662f\u6ca1\u6709\u795e\u660e\u7684\uff0c\u6700\u591a\u53ea\u5b58\u5728\u4e8e\u6545\u4e8b\u91cc\uff0c\u800c\u5728\u8fd9\u4e2a\u4f20\u8bf4\u4e2d\uff0c\u4e0d\u4f46\u6709\u5b9e\u5b9e\u5728\u5728\u7684\u795e\u660e\uff0c\u800c\u4e14\u8fd8\u8ffd\u6740\u8fc7\u6765\uff0c\u7387\u9886\u5974\u96b6\u6467\u6bc1\u4e86\u4f0a\u739b\u65af\u5361\u5e1d\u56fd\u3002</p><p>\u5f53\u7136\uff0c\u743c\u6069\u77e5\u9053\u81ea\u5df1\u7684\u8bb0\u5fc6\u672a\u5fc5\u5b8c\u5168\u5c5e\u5b9e\uff0c\u4f46\u6709\u5173\u90a3\u4e2a\u53eb\u505a\u201c\u5730\u7403\u201d\u7684\u4e16\u754c\u7684\u8bb0\u5fc6\u5b9e\u5728\u592a\u8fc7\u6e05\u6670\u786e\u51ff\uff0c\u6240\u6709\u7684\u7ec6\u8282\uff0c\u6240\u6709\u7684\u70b9\u70b9\u6ef4\u6ef4\uff0c\u90fd\u662f\u90a3\u6837\u786e\u5b9a\u65e0\u7591\u3002\u5c31\u7b97\u662f\u8bb0\u5fc6\u7f16\u7ec7\uff0c\u4e5f\u4e0d\u53ef\u80fd\u51ed\u7a7a\u751f\u9020\u51fa\u8fd9\u6837\u4e00\u4e2a\u5e9e\u5927\u3001\u590d\u6742\u800c\u5b8c\u7f8e\u7684\u865a\u6784\u4e16\u754c\u6765\u3002</p><p>\u201c\u4e0d\u4ec5\u5982\u6b64\uff0c\u5982\u679c\u628a\u8fd9\u4e2a\u4f20\u8bf4\u5f53\u771f\u7684\u8bdd\uff0c\u90a3\u4e48\u8fd8\u6709\u5f88\u591a\u5730\u65b9\u90fd\u6839\u672c\u8bf4\u4e0d\u901a\uff0c\u201d\u6885\u83f2\u65af\u7ee7\u7eed\u5206\u6790\uff0c\u201c\u6bd4\u5982\u8bf4\uff0c\u4f0a\u739b\u65af\u5361\u4eba\u4ece\u6ca1\u6709\u9b54\u6cd5\u7684\u5f02\u4e16\u754c\u63a0\u593a\u5974\u96b6\uff0c\u795e\u660e\u8ffd\u8fc7\u6765\u62a5\u590d\u2014\u2014\u65e2\u7136\u90a3\u4e2a\u4e16\u754c\u6709\u795e\u660e\uff0c\u90a3\u4e48\u5fc5\u5b9a\u6709\u7267\u5e08\uff0c\u6709\u4fe1\u5f92\uff0c\u53c8\u600e\u4e48\u53ef\u80fd\u6ca1\u6709\u9b54\u6cd5\u5462\uff1f\u795e\u660e\u65e2\u7136\u80fd\u591f\u4e8b\u540e\u62a5\u590d\uff0c\u4e3a\u4ec0\u4e48\u5f53\u65f6\u4e0d\u76f4\u63a5\u963b\u6b62\uff1f\u4f0a\u739b\u65af\u5361\u63a0\u593a\u5974\u96b6\u7684\u884c\u52a8\u6301\u7eed\u4e86\u51e0\u767e\u5e74\uff0c\u5982\u679c\u8981\u963b\u6b62\uff0c\u5b8c\u5168\u6709\u8db3\u591f\u7684\u65f6\u95f4\u5427\uff1f\u795e\u738b\u964d\u4e34\uff0c\u8ddd\u79bb\u4f0a\u739b\u65af\u5361\u63a0\u593a\u5974\u96b6\u7ed3\u675f\u90fd\u5df2\u7ecf\u8fc7\u53bb\u4e86\u4e00\u5343\u591a\u5e74\uff0c\u795e\u660e\u4e3a\u4ec0\u4e48\u8fd9\u6837\u59d7\u59d7\u6765\u8fdf\uff1f\u201d</p><p>\u201c\u8fd9\u4e2a\u2026\u2026\u90a3\u4f60\u7684\u610f\u601d\u662f\u8bf4\uff0c\u6574\u4e2a\u4e8b\u60c5\u90fd\u662f\u865a\u6784\u7684\u3002\u201d</p><p>\u201c\u4e0d\uff0c\u795e\u738b\u964d\u4e34\u662f\u4e8b\u5b9e\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u7a46\u7f55\u745e\u5fb7\u548c\u6069\u745f\u8bf8\u795e\u5723\u8005\u4e34\u51e1\uff0c\u4ee5\u51e1\u4eba\u8eab\u4efd\u7edf\u6cbb\u7740\u8fd9\u4e24\u4e2a\u56fd\u5bb6\uff0c\u8fd9\u662f\u786e\u5b9e\u6ca1\u9519\u7684\u3002\u5927\u4e3b\u6559\u8ba4\u4e3a\uff0c\u8fd9\u6bb5\u5386\u53f2\u5176\u5b9e\u5f88\u7b80\u5355\u3002\u7a46\u7f55\u745e\u5fb7\u548c\u6069\u745f\uff0c\u5176\u5b9e\u5f88\u53ef\u80fd\u5c31\u662f\u5f53\u65f6\u4f0a\u739b\u65af\u5361\u5468\u56f4\u7684\u57ce\u90a6\u6216\u8005\u90e8\u843d\uff0c\u88ab\u5e1d\u56fd\u5f81\u670d\u5974\u5f79\uff0c\u540e\u6765\u5728\u4ed6\u4eec\u4fe1\u4ef0\u7684\u795e\u660e\u6307\u5f15\u4e0b\u53db\u4e71\u6210\u529f\u3002\u81f3\u4e8e\u4f20\u9001\u95e8\u3001\u5f02\u4e16\u754c\u7684\u4e8b\u60c5\uff0c\u5c31\u662f\u827a\u672f\u52a0\u5de5\u4e86\uff0c\u4f0a\u739b\u65af\u5361\u5728\u7a7a\u95f4\u6cd5\u672f\u4e0a\u5353\u6709\u540d\u58f0\uff0c\u5c31\u88ab\u7528\u6765\u4f5c\u4e3a\u521b\u4f5c\u7d20\u6750\u3002\u201d</p><p>\u8fd9\u4e5f\u786e\u5b9e\u662f\u5f88\u5e38\u89c1\u7684\u4e8b\u60c5\uff0c\u5386\u53f2\u6d41\u4f20\u5f97\u4e45\u4e86\uff0c\u5f80\u5f80\u5c31\u4f1a\u628a\u5bfb\u5e38\u7684\u4e8b\u60c5\u795e\u79d8\u5316\u3002\u82e5\u5728\u5e73\u65f6\uff0c\u743c\u6069\u65e9\u5df2\u7ecf\u88ab\u6885\u83f2\u65af\u8bf4\u670d\uff0c\u7136\u800c\u5728\u8fd9\u4ef6\u4e8b\u60c5\u4e0a\uff0c\u4ed6\u4f9d\u65e7\u5f88\u6709\u7591\u8651\u3002\u53ea\u662f\u4ed6\u73b0\u5728\u65e2\u4e0d\u80fd\u786e\u5b9a\u6069\u745f\u6587\u5b57\u5c31\u662f\u6954\u5f62\u6587\u5b57\uff0c\u53c8\u4e0d\u719f\u6089\u4f0a\u739b\u65af\u5361\u7684\u5386\u53f2\uff0c\u6885\u83f2\u65af\u7684\u63a8\u8bba\u5408\u60c5\u5408\u7406\uff0c\u4ed6\u4e5f\u66f4\u6ca1\u529e\u6cd5\u53cd\u9a73\uff0c\u624b\u4e2d\u6ca1\u6709\u4efb\u4f55\u53ef\u8d44\u5224\u65ad\u7684\u57fa\u7840\uff0c\u4e5f\u5c31\u4e0d\u60f3\u592a\u65e9\u4e0b\u7ed3\u8bba\u4e86\u3002</p><p>\u201c\u53ef\u60dc\u81ea\u5df1\u53ea\u7565\u7565\u8ba4\u8bc6\u70b9\u6954\u5f62\u6587\u5b57\uff0c\u5374\u5bf9\u82cf\u7f8e\u5c14\u7684\u5b97\u6559\u795e\u8bdd\u8fd1\u4e4e\u4e00\u65e0\u6240\u77e5\uff0c\u201d\u743c\u6069\u60cb\u60dc\u7740\uff0c\u201c\u5426\u5219\u548c\u6069\u745f\u795e\u660e\u5bf9\u7167\u4e00\u4e0b\uff0c\u4e5f\u672a\u5fc5\u4e0d\u80fd\u53d1\u73b0\u70b9\u4ec0\u4e48\u4e1c\u897f\u3002\u201d</p><p>\u968f\u53e3\u8be2\u95ee\u6885\u83f2\u65af\u6709\u5173\u7a46\u7f55\u745e\u5fb7\u548c\u6069\u745f\u8fd9\u4e24\u5927\u795e\u7cfb\u795e\u660e\u7684\u60c5\u51b5\uff0c\u7ed3\u679c\u53d1\u73b0\u5979\u4e5f\u4e86\u89e3\u751a\u5c11\uff0c\u8fd9\u5012\u662f\u8ba9\u743c\u6069\u5f88\u60ca\u8bb6\u3002\u201c\u5723\u6b66\u58eb\u96be\u9053\u4e0d\u662f\u7cbe\u901a\u5b97\u6559\u5b66\u7684\u5417\uff1f\u201d</p><p>\u4e00\u95ee\u624d\u77e5\u9053\uff0c\u539f\u6765\u8fd9\u4e24\u5927\u795e\u7cfb\u538b\u6839\u5c31\u5728\u8d39\u4f26\u5927\u9646\u6ca1\u591a\u5c11\u5f71\u54cd\u3002\u7a46\u7f55\u745e\u5fb7\u548c\u6069\u745f\u90fd\u5904\u4e8e\u4e1c\u5357\u504f\u8fdc\uff0c\u53c8\u5404\u81ea\u6709\u4e00\u5957\u72ec\u7279\u7684\u4eba\u6587\u98ce\u4fd7\uff0c\u5411\u6765\u4fdd\u5b88\uff0c\u548c\u5927\u9646\u5176\u4ed6\u56fd\u5bb6\u683c\u683c\u4e0d\u5165\uff0c\u8fde\u8d38\u6613\u5f80\u6765\u90fd\u63a5\u8fd1\u4e8e\u96f6\uff0c\u5b97\u6559\u4fe1\u4ef0\u81ea\u7136\u4e5f\u5c40\u9650\u4e8e\u6b64\uff0c\u5916\u4eba\u57fa\u672c\u65e0\u4ece\u77e5\u6653\u3002\u743c\u6069\u80fd\u591f\u542c\u8bf4\u8fc7\u4e16\u754c\u4e0a\u6709\u4e00\u4e2a\u201c\u7a46\u7f55\u745e\u5fb7\u795e\u7cfb\u201d\uff0c\u5df2\u7ecf\u53ef\u4ee5\u503c\u5f97\u5927\u529b\u5938\u5956\u4e86\uff0c\u5927\u90e8\u4efd\u4eba\u662f\u538b\u6839\u8fde\u540d\u5b57\u90fd\u6ca1\u542c\u8bf4\u8fc7\u7684\u3002</p><p>\u201c\u795e\u738b\u6253\u8d25\u4e86\u4f0a\u739b\u65af\u5361\uff0c\u7a46\u7f55\u745e\u5fb7\u548c\u6069\u745f\u5efa\u7acb\u8d77\u6765\uff0c\u4f46\u7d27\u63a5\u7740\u8fd9\u4e24\u4e2a\u56fd\u5bb6\u5c31\u9677\u5165\u6c38\u6052\u4e0d\u4f11\u7684\u5206\u88c2\u4e2d\u4e86\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u7565\u5e26\u8ba5\u8bbd\uff0c\u201c\u51e0\u5343\u5e74\u4e00\u76f4\u6218\u4e71\u4e0d\u4f11\uff0c\u8c01\u6709\u5174\u8da3\u7406\u776c\u4ed6\u4eec\u4fe1\u4ec0\u4e48\u795e\u3002\u201d</p><p>\u201c\u542c\u8d77\u6765\u5f88\u6df7\u4e71\uff1f\u201d</p><p>\u201c\u662f\u6df7\u4e71\u6781\u4e86\u3002\u201d\u6885\u83f2\u65af\u7ea0\u6b63\u3002</p><p>\u5404\u79cd\u76f8\u5173\u8d44\u6599\u90fd\u4e25\u91cd\u532e\u4e4f\uff0c\u743c\u6069\u4e5f\u5c31\u53ea\u597d\u6682\u65f6\u628a\u8fd9\u4ef6\u4e8b\u653e\u5230\u4e00\u8fb9\uff0c\u6253\u7b97\u4ee5\u540e\u6709\u673a\u4f1a\u63a5\u89e6\u66f4\u591a\u7684\u8d44\u6599\u518d\u8bf4\u3002\u743c\u6069\u5411\u591a\u7eb3\u9986\u957f\u544a\u8f9e\uff0c\u548c\u6885\u83f2\u65af\u8d70\u51fa\u56fe\u4e66\u9986\uff0c\u53d1\u73b0\u65f6\u95f4\u5df2\u7ecf\u4e0d\u65e9\u4e86\u3002</p><p>\u4e00\u9635\u53ee\u53ee\u5f53\u5f53\u7684\u91d1\u5c5e\u649e\u51fb\u58f0\u987a\u7740\u98ce\u4ece\u8fdc\u5904\u4f20\u6765\u3002</p><p>\u201c\u6211\u53bb\u4e00\u8d9f\u51b6\u70bc\u573a\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u5b9a\u505a\u4e86\u4e00\u9876\u65b0\u5934\u76d4\uff0c\u5dee\u4e0d\u591a\u4e5f\u8be5\u5b8c\u5de5\u4e86\uff0c\u8fc7\u53bb\u770b\u770b\u3002\u201d</p></article>"}}}]);
