(window.webpackJsonp=window.webpackJsonp||[]).push([[288],{311:function(p){p.exports={chapterTitle:null,title:"\u7b2c170\u7ae0 \u597d\u4e8b\u5927\u5bb6\u5206\u4eab",content:"<article><h2>\u7b2c170\u7ae0 \u597d\u4e8b\u5927\u5bb6\u5206\u4eab</h2><p>\u3000\u3000\u5f20\u606a\u4e09\u6b65\u5e76\u4e00\u6b65\u7684\u7a9c\u4e0a\u4e8c\u697c\uff0c\u770b\u89c1\u82b7\u5f64\u7741\u5f00\u9ed1\u767d\u5206\u660e\u7684\u773c\u7738\u51dd\u89c6\u81ea\u5df1\uff0c\u5ae9\u767d\u5982\u8471\u7684\u624b\u6307\u6307\u7740\u60dc\u5bb9\u3001\u60dc\u7fbd\u4e24\u5c0f\u59d0\u59b9\u4e4b\u4e00\uff1a\u201c\u5979\u662f\u60dc\u5bb9\u3002\u201d\u5c06\u8fd1\u4e00\u5e74\u6765\u521d\u6b21\u5f00\u53e3\u8bf4\u8bdd\uff0c\u58f0\u97f3\u6709\u4e9b\u6da9\uff0c\u5f20\u606a\u4e0d\u6000\u7591\u8fd9\u662f\u4ed6\u4eca\u5e74\u6765\u542c\u5230\u6700\u60a6\u8033\u7684\u58f0\u97f3\uff0c\u7981\u4e0d\u4f4f\u559c\u6781\u800c\u6ce3\u3002</p><p>\u3000\u3000\u5468\u53d4\u60e0\u722c\u697c\u68af\u65f6\u4e00\u65f6\u5fc3\u6025\u819d\u76d6\u78d5\u53f0\u9636\u4e0a\uff0c\u4e5f\u4e0d\u89c9\u5f97\u75bc\uff0c\u542c\u5230\u82b7\u5f64\u771f\u7684\u5f00\u53e3\u8bf4\u8bdd\uff0c\u773c\u6cea\u54d7\u54d7\u76f4\u6d41\uff0c\u6402\u7740\u82b7\u5f64\uff0c\u5634\u89d2\u98a4\u6296\u7740\uff0c\u624b\u4e0d\u65ad\u62b9\u773c\u6cea\uff0c\u5374\u53c8\u5728\u4e0d\u505c\u7684\u7b11\uff0c\u4e0d\u6653\u5f97\u8bf4\u4ec0\u4e48\u597d\u3002\u5927\u5bb6\u770b\u4e86\u4e5f\u550f\u5618\u4e0d\u5df2\uff0c\u5f90\u5b66\u5e73\u4e0e\u5468\u53d4\u60e0\u5b98\u5c45\u9ad8\u4f4d\uff0c\u4f46\u53bb\u5e74\u590f\u5929\u4ee5\u6765\u906d\u53d7\u4e27\u5b50\u4e4b\u75db\uff0c\u8ddf\u666e\u901a\u906d\u53d7\u53d8\u6545\u7684\u4eba\u4e00\u6837\uff0c\u4e5f\u9971\u53d7\u75db\u82e6\uff0c\u82b7\u5f64\u7684\u5931\u8bed\u75c7\u8fd9\u4e00\u5e74\u6765\u66f4\u662f\u60ac\u5728\u4ed6\u4eec\u5fc3\u91cc\u7684\u5de8\u77f3\u3002</p><p>\u3000\u3000\u5f20\u77e5\u884c\u8bf4\u9053\uff1a\u201c\u5f20\u606a\uff0c\u5feb\u6253\u7535\u8bdd\u544a\u8bc9\u5f90\u7701\u957f\uff0c\u5feb\u8ba9\u4ed6\u77e5\u9053\u8fd9\u4e2a\u597d\u6d88\u606f\u3002\u201d\u5f20\u606a\u62b1\u7740\u82b7\u5f64\u5230\u697c\u4e0b\u7684\u5ba2\u5385\u91cc\uff0c\u53c8\u8bf4\u9053\uff1a\u201c\u8981\u8ba9\u665a\u6674\u59d0\u4e00\u56de\u5bb6\u5c31\u542c\u5230\u82b7\u5f64\u7684\u58f0\u97f3\u3002\u201d</p><p>\u3000\u3000\u542c\u5510\u5a67\u8bf4\uff0c\u8ba9\u5f20\u606a\u63ed\u7a7f\u82b7\u5f64\u4ece\u5979\u4eec\u8bf4\u8bdd\u7684\u65b9\u5f0f\u8fa9\u522b\u8c01\u662f\u59d0\u59d0\u8c01\u662f\u59b9\u59b9\u4e4b\u540e\uff0c\u60dc\u7fbd\u3001\u60dc\u5bb9\u4e24\u4eba\u5c31\u6539\u53d8\u8bf4\u8bdd\u7684\u65b9\u5f0f\uff0c\u82b7\u5f64\u4e00\u65f6\u9002\u5e94\u4e0d\u4e86\uff0c\u4f46\u5230\u5e95\u5979\u4eec\u4fe9\u4eba\u8bf4\u8bdd\u7684\u58f0\u97f3\u6709\u7ec6\u5fae\u5dee\u5f02\uff0c\u5f20\u606a\u542c\u4e0d\u51fa\u6765\uff0c\u5510\u5a67\u542c\u4e0d\u51fa\u6765\uff0c\u82b7\u5f64\u5374\u5206\u8fa8\u5f97\u51fa\uff0c\u731c\u9519\u51e0\u6b21\u5c31\u53c8\u5c06\u4e24\u4eba\u8fa8\u8ba4\u51fa\u6765\uff1b\u60dc\u5bb9\u3001\u60dc\u7fbd\u63a5\u4e0b\u6765\u7d22\u6027\u95ed\u5634\u4e0d\u8bf4\u8bdd\u8ba9\u82b7\u5f64\u731c\uff0c\u82b7\u5f64\u8fd8\u6ca1\u6709\u5f00\u59cb\u4e60\u5b57\uff0c\u53c8\u6ca1\u6709\u4e60\u8fc7\u54d1\u8bed\uff0c\u6ca1\u4eba\u5f00\u95ee\u5979\u8c01\u662f\u8c01\uff0c\u5979\u5c31\u662f\u731c\u5f97\u51fa\u6765\u4ec5\u51ed\u624b\u52bf\u4e5f\u65e0\u6cd5\u8868\u8fbe\u5979\u60f3\u8868\u8fbe\u7684\u610f\u601d\uff0c\u7ed9\u8fd9\u4e00\u903c\uff0c\u5979\u7adf\u5f00\u53e3\u6307\u7740\u60dc\u5bb9\u8bf4\uff1a\u201c\u4f60\u662f\u60dc\u5bb9\uff01\u201d</p><p>\u3000\u3000\u82b7\u5f64\u4e4b\u524d\u7ecf\u8fc7\u5f88\u591a\u5fc3\u7406\u6cbb\u7597\uff0c\u7701\u91cc\u7684\u4e13\u5bb6\u3001\u5317\u4eac\u7684\u4e13\u5bb6\u4e0d\u6653\u5f97\u8bf7\u4e86\u591a\u5c11\uff0c\u90fd\u4e0d\u89c1\u6548\u679c\uff0c\u5468\u53d4\u60e0\u8ddf\u5f90\u5b66\u5e73\u90fd\u7126\u6025\u5f97\u5f88\uff0c\u54ea\u91cc\u80fd\u60f3\u5230\u8ba9\u82b7\u5f64\u5f00\u53e3\u8bf4\u8bdd\u7adf\u662f\u8fd9\u4e48\u7b80\u5355\u7684\u4e8b\u60c5\uff1f\u5bf9\u60dc\u5bb9\u3001\u60dc\u7fbd\u4e24\u5c0f\u59d0\u59b9\u8d8a\u53d1\u559c\u6b22\uff0c\u53ea\u662f\u8fd9\u65f6\u5019\u559c\u4e0d\u81ea\u7981\uff0c\u5fc3\u601d\u90fd\u5728\u82b7\u5f64\u8eab\u4e0a\uff0c\u773c\u775b\u5df4\u671b\u7740\u82b7\u5f64\uff0c\u5e0c\u671b\u542c\u5230\u5979\u591a\u8bf4\u4e9b\u8bdd\uff0c\u54ea\u6015\u7b80\u5355\u7684\u4e00\u4e2a\u5b57\u773c\u3001\u4e00\u4e2a\u8bcd\u90fd\u7ed9\u5979\u5e26\u6765\u65e0\u6bd4\u7684\u6b22\u4e50\u3002</p><p>\u3000\u3000\u6253\u901a\u5f90\u5b66\u5e73\u79d8\u4e66\u674e\u4e49\u6c5f\u7684\u7535\u8bdd\uff0c\u5f90\u5b66\u5e73\u6b63\u5728\u53c2\u52a0\u4f1a\u8bae\uff0c\u5468\u53d4\u60e0\u7ba1\u4e0d\u4e86\u8fd9\u4e48\u591a\uff0c\u8ba9\u674e\u4e49\u6c5f\u627e\u4e08\u592b\u51fa\u6765\u63a5\u7535\u8bdd\u3002\u5f90\u5b66\u5e73\u4ece\u4f1a\u8bae\u5ba4\u51fa\u6765\uff0c\u63a5\u5230\u7535\u8bdd\u5148\u542c\u5230\u82b7\u5f64\u6c99\u54d1\u7684\u58f0\u97f3\uff0c\u9ad8\u5174\u5f97\u76f4\u62b9\u773c\u6cea\uff0c\u5728\u7535\u8bdd\u91cc\u54c4\u7740\u82b7\u5f64\u591a\u8bf4\u4e24\u53e5\u8bdd\uff0c\u867d\u7136\u82b7\u5f64\u521a\u5f00\u59cb\u8bf4\u8bdd\u65f6\u7684\u58f0\u97f3\u8fd8\u5f88\u751f\u6da9\uff0c\u4ed6\u542c\u4e86\u5374\u5982\u5929\u7c41\uff0c\u4f1a\u8bae\u8fd8\u5728\u8fdb\u884c\uff0c\u4ed6\u4e0d\u80fd\u5728\u5916\u9762\u803d\u6401\u592a\u4e45\uff0c\u5fc3\u60c5\u6fc0\u52a8\u7684\u542c\u5230\u4e00\u4f1a\u513f\uff0c\u624d\u4e0d\u820d\u7684\u5c06\u7535\u8bdd\u6302\u6389\u3002</p><p>\u3000\u3000\u8c22\u665a\u6674\u4ece\u516c\u53f8\u56de\u5230\u5c0f\u9526\u6e56\u7554\u7684\u522b\u5885\u65f6\uff0c\u4ece\u8f66\u91cc\u4e0b\u6765\uff0c\u62ac\u9636\u800c\u4e0a\uff0c\u770b\u7740\u5ba2\u5385\u91cc\u4f17\u4eba\u534a\u8e72\u534a\u8dea\u56f4\u7740\u82b7\u5f64\uff0c\u82b7\u5f64\u626d\u8fc7\u5979\u7cbe\u81f4\u800c\u5c0f\u5de7\u7684\u5934\u9885\uff0c\u6709\u4e9b\u72b9\u8c6b\u7684\u558a\u4e86\u58f0\uff1a\u201c\u5988\u5988\u2026\u2026\u201d</p><p>\u3000\u3000\u8fd9\u4e00\u523b\uff0c\u8c22\u665a\u6674\u5185\u5fc3\u6240\u6709\u7684\u575a\u5f3a\u90fd\u8ba9\u6cea\u6c34\u51b2\u57ae\uff0c\u6276\u7740\u73bb\u7483\u95e8\u6846\uff0c\u7adf\u751f\u4e0d\u51fa\u518d\u8fc8\u51fa\u4e00\u6b65\u7684\u529b\u6c14\uff0c\u8f6f\u8f6f\u7684\u5750\u5230\u53f0\u9636\u4e0a\uff0c\u5f20\u606a\u5c06\u82b7\u5f64\u62b1\u5230\u5979\u8eab\u8fb9\uff0c\u5979\u4e0d\u4e89\u6c14\u7684\u653e\u58f0\u54ed\u6ce3\u8d77\u6765\uff0c\u6cea\u6c34\u3001\u9f3b\u6d95\uff0c\u5b8c\u5168\u4e0d\u987e\u5f62\u8c61\u7684\u5c06\u82b7\u5f64\u6402\u5728\u6000\u91cc\uff0c\u5927\u54ed\u7279\u54ed\u3002\u82b7\u5f64\u8ba9\u5979\u5988\u5988\u4f24\u5fc3\u7684\u6a21\u6837\u5413\u7740\uff0c\u4e5f\u8ddf\u7740\u5927\u54ed\u8d77\u6765\u3002</p><p>\u3000\u3000\u5468\u53d4\u60e0\u597d\u4e0d\u5bb9\u6613\u6536\u4f4f\u6cea\u6c34\uff0c\u8ba9\u5979\u4eec\u8fd9\u4e00\u5f15\uff0c\u53c8\u8ddf\u7740\u54ed\u4e86\u8d77\u6765\u3002</p><p>\u3000\u3000\u6881\u683c\u73cd\u8d70\u8fc7\u5c06\u5979\u4eec\u6400\u8d77\u6765\uff0c\u773c\u775b\u91cc\u542b\u7740\u6cea\u6c34\uff0c\u5374\u7b11\u7740\u8bf4\uff1a\u201c\u8fd9\u6837\u7684\u597d\u4e8b\uff0c\u8fd8\u54ed\u4ec0\u4e48\u2026\u2026\u201d</p><p>\u3000\u3000\u95f9\u817e\u4e86\u597d\u4e45\u5927\u5bb6\u7684\u60c5\u7eea\u624d\u6e10\u6e10\u7a33\u5b9a\u4e0b\u6765\uff0c\u5f20\u606a\u5c06\u82b7\u5f64\u62b1\u5728\u6000\u91cc\uff0c\u8f6c\u8eab\u770b\u89c1\u5510\u5a67\u7684\u773c\u5708\u4e5f\u662f\u7ea2\u901a\u901a\uff0c\u5374\u662f\u60dc\u7fbd\u3001\u60dc\u5bb9\u4e24\u5c0f\u59d0\u59b9\u4e0d\u77e5\u9053\u53d1\u751f\u4ec0\u4e48\u4e8b\uff0c\u53ea\u89c9\u5f97\u82b7\u5f64\u54ed\u5f97\u53ef\u601c\uff0c\u62c9\u7740\u5f20\u606a\u7684\u8863\u89d2\uff0c\u7591\u60d1\u4e0d\u89e3\u7684\u770b\u7740\u82b7\u5f64\uff0c\u53c8\u4f38\u7740\u624b\u53bb\u591f\u5979\u7684\u5c0f\u624b\u3002</p><p>\u3000\u3000\u60c5\u7eea\u6fc0\u8361\uff0c\u5927\u5bb6\u90fd\u6ca1\u6709\u5403\u996d\u7684\u5fc3\u60c5\uff0c\u5750\u5230\u5927\u5385\u7684\u6c99\u53d1\u4e0a\uff0c\u90fd\u60f3\u65b9\u8bbe\u6cd5\u8ba9\u82b7\u5f64\u591a\u8bf4\u8bdd\uff0c\u751f\u6015\u8fd9\u4e00\u523b\u4f1a\u7a0d\u7eb5\u5373\u901d\uff0c\u7b49\u5230\u660e\u5929\u4f1a\u6210\u4e3a\u7a7a\u6b22\u559c\u4e00\u573a\u3002</p><p>\u3000\u3000\u5f20\u606a\u53c8\u8ddf\u8c22\u665a\u6674\u590d\u8ff0\u82b7\u5f64\u7a81\u7136\u80fd\u8bf4\u8bdd\u7684\u7f18\u7531\uff0c\u5468\u53d4\u60e0\u62c9\u7740\u60dc\u5bb9\u3001\u60dc\u7fbd\u4e24\u5c0f\u59d0\u59b9\u5165\u81ea\u5df1\u7684\u6000\u91cc\uff0c\u5bf9\u7740\u5085\u4fca\u592b\u5987\u8bf4\uff1a\u201c\u591a\u4e8f\u8fd9\u4e24\u4e2a\u5c0f\u59d0\u59b9\uff0c\u957f\u4e86\u8fd9\u4e48\u7cbe\u7075\uff0c\u6211\u8981\u5979\u4eec\u8ddf\u82b7\u5f64\u4e00\u8d77\u505a\u6211\u7684\u5b59\u5973\uff0c\u4e5f\u8ba9\u82b7\u5f64\u4ece\u5c0f\u6709\u4e24\u4e2a\u4f19\u4f34\u2026\u2026\u201d</p><p>\u3000\u3000\u674e\u660e\u745c\u5f85\u4eba\u5e94\u5bf9\u8981\u6bd4\u5085\u4fca\u548c\u7f13\u5a49\u8f6c\uff0c\u5979\u867d\u7136\u8ddf\u7740\u5085\u4fca\u6ca1\u4ec0\u4e48\u540e\u6094\uff0c\u5bf9\u60dc\u7fbd\u3001\u60dc\u5bb9\u4e24\u59d0\u59b9\u4ee5\u540e\u7684\u751f\u6d3b\u600e\u4e48\u53ef\u80fd\u6ca1\u6709\u61a7\u61ac\uff1f\u542c\u5230\u5468\u53d4\u60e0\u7684\u8bdd\uff0c\u987a\u7740\u5979\u7684\u53e3\u6c14\uff0c\u6b23\u559c\u7684\u8bf4\uff1a\u201c\u6709\u4f60\u505a\u60dc\u5bb9\u3001\u60dc\u7fbd\u7684\u5976\u5976\uff0c\u662f\u5979\u4eec\u5929\u5927\u7684\u798f\u6c14\u2026\u2026\u201d</p><p>\u3000\u3000\u5468\u53d4\u60e0\u53ea\u60f3\u628a\u5979\u5fc3\u91cc\u7684\u559c\u60a6\u8ddf\u522b\u4eba\u5206\u4eab\uff0c\u626d\u5934\u5bf9\u5f20\u77e5\u884c\u8bf4\uff1a\u201c\u660e\u745c\u73b0\u5728\u8fd8\u8981\u4e0a\u4e2d\u3001\u591c\u73ed\uff0c\u591a\u4e0d\u65b9\u4fbf\u7167\u987e\u4e24\u4e2a\u5c0f\u5b69\u5b50\uff0c\u6211\u4ece\u6765\u90fd\u4e0d\u5f00\u53e3\u95ee\u8fd9\u4e9b\u4e8b\u60c5\uff0c\u4eca\u5929\u7834\u4e00\u6b21\u4f8b\uff0c\u4f60\u4e0d\u80fd\u8ba9\u6211\u4e24\u4e2a\u5e72\u5b59\u5973\u6ca1\u6709\u4eba\u7167\u987e\uff0c\u4f60\u8981\u4e0d\u7b54\u5e94\uff0c\u6211\u53bb\u6c42\u5510\u5e02\u957f\u2026\u2026\u201d</p><p>\u3000\u3000\u674e\u660e\u745c\u7684\u5de5\u4f5c\u8c03\u52a8\u8981\u5510\u5b66\u8c26\u51fa\u9a6c\uff0c\u5c82\u4e0d\u662f\u8981\u95f9\u51fa\u5927\u52a8\u9759\uff1f\u5f20\u77e5\u884c\u8fde\u5fd9\u70b9\u5934\u7b54\u5e94\u9a6c\u4e0a\u5c31\u5e2e\u5fd9\u89e3\u51b3\u674e\u660e\u745c\u7684\u5de5\u4f5c\u95ee\u9898\uff0c\u5f00\u53e3\u95ee\u674e\u660e\u745c\u5e0c\u671b\u4ece\u4ec0\u4e48\u6837\u7684\u5de5\u4f5c\u3002\u674e\u660e\u745c\u3001\u5085\u4fca\u592b\u5987\u6bd5\u7adf\u8138\u76ae\u8584\uff0c\u5f20\u606a\u4ecb\u7ecd\u5979\u662f\u4ece\u7701\u8d22\u7ecf\u5b66\u9662\u8084\u4e1a\uff0c\u5728\u4e5d\u5341\u5e74\u4ee3\u4e5f\u5c5e\u4e8e\u534a\u4e2a\u77e5\u8bc6\u5206\u5b50\u3002\u5f20\u77e5\u884c\u53ea\u62c5\u5fc3\u674e\u660e\u745c\u662f\u666e\u901a\u5de5\u4eba\uff0c\u6709\u4e9b\u5de5\u4f5c\u4e0d\u597d\u5b89\u6392\uff0c\u542c\u8bf4\u662f\u56e0\u4e3a\u5bb6\u5ead\u539f\u56e0\u4ece\u7701\u8d22\u7ecf\u5b66\u9662\u8084\u4e1a\uff0c\u5c31\u8bf4\uff1a\u201c\u90a3\u884c\uff0c\u6211\u660e\u5929\u53bb\u95ee\u4e00\u4e0b\uff0c\u9009\u51e0\u4e2a\u5de5\u4f5c\u8ba9\u4f60\u6311\uff0c\u603b\u8981\u4fdd\u7ba1\u4f60\u6ee1\u610f\u2026\u2026\u201d</p><p>\u3000\u3000\u6362\u4e00\u4efd\u8212\u9002\u7684\u5de5\u4f5c\uff0c\u5bf9\u4e8e\u666e\u901a\u4eba\u53ef\u80fd\u662f\u4e00\u8f88\u5b50\u90fd\u505a\u4e0d\u6210\u7684\u4e8b\u60c5\uff0c\u5728\u5468\u53d4\u60e0\u3001\u5f20\u77e5\u884c\u7b49\u4eba\u770b\u6765\uff0c\u4e0d\u8fc7\u662f\u51e0\u53e5\u8bdd\u7684\u4e8b\u60c5\uff0c\u674e\u660e\u745c\u614c\u4e71\u800c\u4e0d\u63a9\u9970\u5fc3\u91cc\u7684\u60ca\u559c\uff0c\u5fd9\u8bf4\uff1a\u201c\u4e0d\u8981\u8fd9\u4e48\u52b3\u70e6\u5f20\u79d8\u4e66\u957f\uff0c\u60dc\u7fbd\u3001\u60dc\u5bb9\u5979\u5976\u5976\u7684\u8eab\u4f53\u4e5f\u4e0d\u5927\u597d\uff0c\u5085\u4fca\u73b0\u5728\u7ed9\u5f20\u606a\u5f00\u8f66\uff0c\u6211\u8981\u4e0a\u4e2d\u73ed\u3001\u591c\u73ed\uff0c\u60dc\u7fbd\u3001\u60dc\u5bb9\u5c31\u6015\u6ca1\u4eba\u7167\u987e\uff0c\u6211\u8981\u80fd\u5728\u4e2d\u836f\u5382\u6362\u6210\u5e38\u65e5\u73ed\u5c31\u53ef\u4ee5\u4e86\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u7740\u8bf4\uff1a\u201c\u53ea\u662f\u6362\u5e38\u65e5\u73ed\uff0c\u5c82\u4e0d\u662f\u663e\u5f97\u6211\u7238\u6ca1\u4ec0\u4e48\u80fd\u8010\uff1f\u8ba9\u6211\u7238\u5148\u627e\u51e0\u4e2a\u5de5\u4f5c\u8ba9\u4f60\u6765\u6311\u3002\u201d</p><p>\u3000\u3000\u5085\u4fca\u8a00\u8bed\u4e0d\u591a\uff0c\u5fc3\u91cc\u611f\u6fc0\u4e5f\u4e0d\u5584\u8868\u8fbe\uff1b\u4ed6\u59bb\u5b50\u674e\u660e\u745c\u60f6\u6050\u4e0d\u5b89\uff0c\u63a8\u8f9e\u4e0d\u6562\u52b3\u70e6\u5f20\u77e5\u884c\u4e3a\u5979\u7684\u4e8b\u60c5\u5927\u52a8\u5e72\u6208\uff0c\u5374\u662f\u987e\u5efa\u840d\u4ece\u5979\u7684\u8bdd\u542c\u51fa\u4e9b\u4e1c\u897f\u6765\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u5f20\u606a\u73b0\u5728\u6709\u4e13\u8f66\u63a5\u9001\uff0c\u53ef\u6bd4\u4ed6\u7238\u8fd8\u8981\u5a01\u98ce\uff0c\u201d\u529d\u674e\u660e\u745c\u8bf4\uff0c\u201c\u4f60\u4e5f\u4e0d\u8981\u63a8\u8f9e\u4e86\uff0c\u82b7\u5f64\u4eca\u5929\u80fd\u5f00\u53e3\u8bf4\u8bdd\uff0c\u4e0d\u6653\u5f97\u8ba9\u5927\u5bb6\u591a\u9ad8\u5174\uff0c\u6362\u5de5\u4f5c\u7684\u4e8b\u60c5\uff0c\u7b97\u4e0d\u4e0a\u591a\u9ebb\u70e6\u7684\u4e8b\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u542c\u987e\u5efa\u840d\u8bf4\u4ed6\u6709\u8f66\u63a5\u9001\u7684\u4e8b\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u5929\u6c14\u8981\u70ed\u8d77\u6765\u4e86\uff0c\u987e\u59e8\u51fa\u95e8\u4e5f\u4e0d\u65b9\u4fbf\uff0c\u5510\u4f2f\u4f2f\u5728\u5e02\u91cc\u6293\u516c\u8f66\u6539\u9769\uff0c\u987e\u59e8\u603b\u8981\u652f\u6301\u5510\u4f2f\u4f2f\u7684\u5de5\u4f5c\uff0c\u9526\u6e56\u6b63\u597d\u8fd8\u6709\u8f86\u8f66\u591a\u7740\uff0c\u4f60\u8981\u7528\u8f66\uff0c\u968f\u65f6\u901a\u77e5\u9526\u6e56\u5c31\u53ef\u4ee5\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u987e\u5efa\u840d\u7b11\u7740\u8bf4\uff1a\u201c\u6211\u5c31\u5e73\u65f6\u8ddf\u4f60\u5988\u901b\u901b\u8857\uff0c\u4f60\u8fd9\u4e48\u8bf4\u662f\u5fc3\u75bc\u5510\u5a67\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u5988\u2026\u2026\u201d\u5510\u5a67\u5a07\u55d4\u542b\u7f9e\uff0c\u7edd\u7f8e\u7684\u8138\u86cb\u67d3\u7740\u6de1\u6de1\u7684\u7ea2\u6655\uff0c\u6bd5\u7adf\u7ecf\u5e38\u7ed9\u4eba\u62ff\u5979\u4e0e\u5f20\u606a\u7684\u5173\u7cfb\u8bf4\u4e8b\uff0c\u627f\u53d7\u80fd\u529b\u4e5f\u5f3a\u4e86\u8bb8\u591a\uff0c\u62ff\u773c\u775b\u7784\u7740\u5f20\u606a\uff0c\u5634\u91cc\u5374\u8bf4\uff0c\u201c\u5988\uff0c\u4f60\u778e\u8bf4\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u5510\u5b66\u8c26\u7f29\u51cf\u884c\u653f\u5f00\u652f\uff0c\u9996\u6293\u516c\u8f66\u79c1\u7528\u95ee\u9898\uff0c\u6625\u8282\u540e\u5510\u5a67\u4e5f\u4e0d\u5f97\u4e0d\u4e58\u516c\u4ea4\u8f66\u5f80\u8fd4\u5e08\u9662\u4e0e\u4e00\u4e2d\u4e4b\u95f4\u3002\u5929\u6c14\u51b7\u7684\u65f6\u5019\u8fd8\u597d\uff0c\u5929\u6c14\u6e10\u70ed\uff0c\u4eba\u7a7f\u8863\u670d\u4e5f\u5355\u8584\uff0c\u516c\u4ea4\u8f66\u96be\u514d\u4f1a\u6709\u5360\u624b\u8db3\u4fbf\u5b9c\u7684\u9f8c\u9f8a\u4eba\uff0c\u5f20\u606a\u6015\u5510\u5a67\u5403\u4e8f\uff0c\u771f\u60f3\u8c03\u90e8\u8f66\u63a5\u9001\u5510\u5a67\u4e0a\u4e0b\u5b66\uff0c\u89c1\u987e\u5efa\u840d\u8fd9\u4e48\u8bf4\uff0c\u4ed6\u539a\u8138\u76ae\u4e5f\u4e0d\u6015\u522b\u4eba\u7b11\uff0c\u987a\u7740\u987e\u5efa\u840d\u7684\u53e3\u98ce\u5c31\u8bf4\uff1a\u201c\u90a3\u6211\u5c31\u8ba9\u9526\u6e56\u53f8\u673a\u73ed\u90a3\u8fb9\u6bcf\u5929\u5b89\u6392\u63a5\u9001\u5510\u5a67\u4e0a\u4e0b\u5b66\uff0c\u987e\u59e8\u8981\u7528\u8f66\u81ea\u5df1\u89e3\u51b3\u5427\u3002\u201d</p><p>\u3000\u3000\u987e\u5efa\u840d\u7b11\u7740\u8bf4\uff1a\u201c\u5927\u5bb6\u770b\u770b\uff0c\u6211\u5c31\u8bf4\u5427\uff0c\u5f20\u606a\u603b\u4e0d\u80fd\u8ba8\u597d\u6211\u4eec\u8fd9\u4e9b\u4e2d\u5e74\u5987\u5973\u3002\u201d</p><p>\u3000\u3000\u5927\u5bb6\u54c8\u54c8\u5927\u7b11\u3002</p><p>\u3000\u3000\u5510\u5a67\u4e0d\u751a\u5a07\u7f9e\uff0c\u4fcf\u8138\u6e17\u8840\u4f3c\u7684\u7ea2\u8273\uff0c\u8c22\u665a\u6674\u7684\u5fc3\u601d\u7a0d\u7a0d\u4ece\u82b7\u5f64\u8eab\u4e0a\u6536\u56de\u6765\uff0c\u770b\u7740\u5510\u5a67\u5c11\u5973\u5a07\u7f9e\u7684\u6a21\u6837\uff0c\u65e0\u8bba\u8c01\u90fd\u4f1a\u8ba4\u4e3a\u5510\u5a67\u4e0e\u5f20\u606a\u662f\u5929\u9020\u5730\u8bbe\u7684\u4e00\u5bf9\uff0c\u60f3\u5230\u8bb8\u601d\uff0c\u5fc3\u91cc\u53ea\u662f\u5fae\u5fae\u4e00\u53f9\uff1a\u5979\u751a\u81f3\u8fde\u66b4\u5149\u7684\u673a\u4f1a\u90fd\u6ca1\u6709\u3002</p><p>\u3000\u3000\u95f9\u817e\u4e86\u5f88\u665a\uff0c\u7b49\u5230\u5510\u5b66\u8c26\u90a3\u8fb9\u5306\u5fd9\u5e94\u916c\u5b8c\u8d76\u5230\u8fd9\u91cc\uff0c\u5927\u5bb6\u624d\u601d\u91cf\u8d77\u665a\u996d\u8fd8\u6ca1\u6709\u5403\uff0c\u8fd9\u65f6\u90fd\u611f\u89c9\u5230\u8179\u54cd\u5982\u96f7\uff0c\u6025\u5fd9\u8ba9\u53a8\u5e08\u51c6\u5907\u5f00\u996d\uff1b\u7528\u9910\u65f6\uff0c\u5f90\u5b66\u5e73\u5728\u5317\u4eac\u90a3\u8fb9\u7684\u4f1a\u8bae\u7ed3\u675f\uff0c\u53c8\u6253\u6765\u7535\u8bdd\u60f3\u542c\u82b7\u5f64\u7684\u58f0\u97f3\uff0c\u5927\u5bb6\u90fd\u542c\u4e0b\u624b\uff0c\u542c\u7740\u514d\u63d0\u7535\u8bdd\u91cc\u5f90\u5b66\u5e73\u6fc0\u52a8\u5fae\u98a4\u7684\u58f0\u97f3\uff0c\u65e0\u4e0d\u5728\u60f3\uff1a\u5f90\u5b66\u5e73\u4e5f\u662f\u6709\u666e\u901a\u4eba\u7684\u60c5\u611f\u3002</p><p>\u3000\u3000\u5f90\u5b66\u5e73\u660e\u5929\u5c31\u56de\u7701\u57ce\uff0c\u6709\u4e9b\u8feb\u4e0d\u53ca\u5f85\u7684\u60f3\u5230\u770b\u5230\u82b7\u5f64\uff0c\u4ed6\u53c8\u4e0d\u80fd\u968f\u968f\u4fbf\u4fbf\u5230\u6d77\u5dde\u6765\uff0c\u5c31\u8ba9\u5468\u53d4\u60e0\u660e\u5929\u5e26\u7740\u82b7\u5f64\u56de\u7701\u57ce\uff0c\u8fd8\u60f3\u89c1\u89c1\u60dc\u7fbd\u3001\u60dc\u5bb9\u4e24\u5c0f\u59d0\u59b9\uff0c\u4ed6\u5728\u7535\u8bdd\u91cc\u4e5f\u8ba4\u4e86\u8fd9\u4e24\u4e2a\u5e72\u5b59\u5973\u3002</p><p>\u3000\u3000\u5468\u53d4\u60e0\u4fbf\u8ba9\u674e\u660e\u745c\u4e5f\u8bf7\u51e0\u5929\u5047\uff0c\u597d\u597d\u9886\u7740\u4e24\u5c0f\u59d0\u59b9\u5230\u7701\u57ce\u73a9\u4e00\u73a9\u53bb\u3002</p></article>"}}}]);
