(window.webpackJsonp=window.webpackJsonp||[]).push([[289],{311:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e94\u8282 \u6545\u77e5",content:"<article><h2>\u7b2c\u4e94\u8282 \u6545\u77e5</h2><p>\u743c\u6069\u7b2c\u4e00\u6b21\u89c1\u5230\u6885\u83f2\u65af\u662f\u5728\u6c99\u6f20\u91cc\uff0c\u5f53\u65f6\u9047\u4e0a\u4e86\u9aa8\u866b\uff0c\u51e0\u4e2a\u7267\u5e08\u8054\u624b\u538b\u5236\u4e0d\u4e0b\uff0c\u6b64\u65f6\u5723\u6b66\u58eb\u81ea\u524d\u65b9\u63a2\u8def\u8d76\u56de\uff0c\u94f6\u76d4\u94f6\u7532\uff0c\u94f6\u5251\u767d\u9a6c\uff0c\u82f1\u6b66\u51db\u51db\uff0c\u4e00\u5251\u51fb\u6e83\u4e86\u602a\u7269\uff0c\u4eff\u4f5b\u6218\u795e\u964d\u4e16\u4e00\u822c\uff0c\u743c\u6069\u90fd\u6ca1\u60f3\u5230\u4f1a\u662f\u4e2a\u5973\u5b69\u5b50\uff0c\u540e\u6765\u8131\u4e0b\u5934\u76d4\u624d\u5bdf\u89c9\u8eab\u4efd\u3002\u5176\u540e\u906d\u9047\u4eba\u9762\u72ee\u4f0f\u51fb\uff0c\u5934\u76d4\u5728\u6df7\u6218\u4e2d\u9057\u5931\uff0c\u4e0d\u77e5\u6389\u5230\u54ea\u91cc\u53bb\u4e86\u3002</p><p>\u8fd9\u539f\u672c\u4e0d\u662f\u4ec0\u4e48\u5927\u4e8b\uff0c\u4e22\u4e86\u5934\u76d4\uff0c\u53bb\u5546\u5e97\u91cc\u518d\u4e70\u4e00\u4e2a\u5c31\u662f\uff0c\u4e5f\u5c5e\u5bfb\u5e38\uff0c\u4e00\u822c\u800c\u8a00\u6bd4\u8f83\u5927\u7684\u57ce\u5e02\u91cc\uff0c\u4f8b\u5982\u535a\u5f97\u4e4b\u95e8\u3001\u585e\u5c14\uff0c\u90fd\u4f1a\u6709\u4e13\u95e8\u7684\u5175\u5668\u94fa\u3002\u6885\u83f2\u65af\u4e0d\u662f\u90a3\u7b49\u5a07\u751f\u60ef\u517b\u7684\u5927\u5c0f\u59d0\uff0c\u867d\u7136\u4f5c\u4e3a\u5723\u6b66\u58eb\uff0c\u5728\u6559\u4f1a\u4e2d\u5730\u4f4d\u751a\u9ad8\uff0c\u5e73\u5e38\u751f\u6d3b\u5374\u5f88\u7b80\u6734\uff0c\u4e5f\u6ca1\u592a\u591a\u8bb2\u7a76\u3002\u4f46\u6bcf\u4e2a\u4eba\u90fd\u6709\u81ea\u5df1\u7279\u522b\u5728\u610f\u7684\u5730\u65b9\uff0c\u6885\u83f2\u65af\u4e5f\u4e0d\u4f8b\u5916\uff0c\u5979\u5728\u522b\u7684\u65b9\u9762\u4e0d\u751a\u5728\u4e4e\uff0c\u5bf9\u4e8e\u94e0\u7532\u5175\u5668\u8fd9\u65b9\u9762\u5374\u683c\u5916\u6311\u5254\uff0c\u7cbe\u76ca\u6c42\u7cbe\uff0c\u4e00\u8def\u884c\u6765\u6ca1\u6709\u770b\u5230\u4ec0\u4e48\u7279\u522b\u6ee1\u610f\u7684\u8d27\u8272\uff0c\u7d22\u6027\u5b81\u7f3a\u6bcb\u6ee5\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u5c31\u6401\u4e0b\u4e86\u3002</p><p>\u5f53\u7136\uff0c\u4f0a\u5361\u6c99\u57ce\u7684\u77ee\u4eba\u5de5\u5320\uff0c\u624b\u827a\u662f\u7edd\u5bf9\u53ef\u4ee5\u503c\u5f97\u4fe1\u4efb\u7684\u3002</p><p>\u743c\u6069\u5de6\u53f3\u65e0\u4e8b\uff0c\u4fbf\u966a\u7740\u6885\u83f2\u65af\u4e00\u8d77\u3002\u51b6\u70bc\u573a\u5efa\u5728\u5730\u4e0b\uff0c\u4e00\u7247\u70ed\u706b\u671d\u5929\uff0c\u4e03\u516b\u5341\u4e2a\u77ee\u4eba\u5728\u5176\u4e2d\u6c57\u6d41\u6d43\u80cc\u5730\u5fd9\u788c\u7740\uff0c\u5347\u817e\u7684\u706b\u5149\u3001\u6709\u8282\u594f\u7684\u91d1\u5c5e\u649e\u51fb\u58f0\u548c\u95f4\u6216\u54cd\u8d77\u7684\u6dec\u706b\u58f0\uff0c\u6784\u6210\u4e86\u5b8c\u7f8e\u7684\u4ea4\u54cd\u4e50\u3002\u770b\u89c1\u4e24\u4eba\u8fdb\u6765\uff0c\u4e00\u4e2a\u5149\u5934\u77ee\u4eba\u628a\u624b\u4e2d\u7684\u9524\u5b50\u4ea4\u7ed9\u52a9\u624b\uff0c\u8fce\u4e86\u4e0a\u6765\u3002</p><p>\u9c81\u6587\u66fe\u7ecf\u5411\u743c\u6069\u63d0\u8fc7\uff0c\u57ce\u4e2d\u9996\u5e2d\u953b\u9020\u5927\u5e08\u662f\u7d22\u5c3c\xb7\u6864\u9aa8\uff0c\u4e5f\u662f\u51b6\u70bc\u573a\u7684\u4e3b\u7ba1\uff0c\u6700\u64c5\u957f\u953b\u9020\u76d4\u7532\uff0c\u56e0\u4e3a\u5e74\u4e8b\u5df2\u9ad8\uff0c\u57fa\u672c\u662f\u5728\u5bb6\u4f11\u517b\uff0c\u5df2\u7ecf\u6781\u5c11\u4eb2\u81ea\u52a8\u624b\u4e86\uff0c\u51e0\u5e74\u4e5f\u672a\u5fc5\u6709\u4e00\u4ef6\u4f5c\u54c1\u3002\u73b0\u5728\u771f\u6b63\u8d1f\u8d23\u51b6\u70bc\u573a\u7684\u662f\u5979\u7684\u526f\u624b\u83ab\u6258\uff0c\u4e5f\u5c31\u662f\u9762\u524d\u7684\u8fd9\u4e2a\u5149\u5934\u77ee\u4eba\uff0c\u6700\u64c5\u957f\u5236\u4f5c\u5200\u5251\uff0c\u4e5f\u662f\u4e00\u4f4d\u201c\u953b\u9020\u5927\u5e08\u201d\u3002\u8fd9\u662f\u4f0a\u5361\u6c99\u57ce\u4e3a\u6280\u827a\u6700\u9ad8\u8d85\u7684\u5de5\u5320\u6388\u4e88\u7684\u79f0\u53f7\uff0c\u6574\u4e2a\u57ce\u4e2d\u4e5f\u4e0d\u8d85\u8fc7\u4e94\u4eba\u3002</p><p>\u83ab\u6258\u81ea\u7136\u8ba4\u8bc6\u743c\u6069\u548c\u6885\u83f2\u65af\uff0c\u89c1\u4ed6\u4eec\u7a81\u7136\u5230\u6765\uff0c\u4e0d\u6562\u6020\u6162\uff0c\u81ea\u5df1\u4e0a\u524d\u8fce\u63a5\u3002\u6885\u83f2\u65af\u8bf4\u660e\u4e86\u6765\u610f\uff0c\u83ab\u6258\u53eb\u4eba\u8fc7\u6765\u4e00\u95ee\uff0c\u539f\u6765\u5979\u5b9a\u505a\u7684\u5934\u76d4\u5c1a\u672a\u5b8c\u5de5\uff0c\u8fd8\u6b20\u6700\u540e\u51e0\u9053\u5de5\u5e8f\uff0c\u4f30\u8ba1\u8fd8\u9700\u8981\u4e09\u56db\u5929\u65f6\u95f4\u624d\u884c\u3002</p><p>\u767d\u8dd1\u4e00\u8d9f\uff0c\u743c\u6069\u4e0d\u514d\u6709\u4e9b\u5931\u671b\uff0c\u4f46\u65e2\u7136\u96be\u5f97\u6765\u4e86\uff0c\u4e5f\u6b63\u597d\u501f\u6b64\u53c2\u89c2\u53c2\u89c2\uff0c\u589e\u957f\u89c1\u8bc6\u3002\u636e\u77ee\u4eba\u4eec\u7684\u81ea\u5439\u81ea\u64c2\uff0c\u4f0a\u5361\u6c99\u57ce\u7684\u953b\u9020\u6280\u827a\u662f\u201c\u4e3e\u4e16\u65e0\u53cc\uff0c\u53ea\u5728\u795e\u4e0b\u201d\uff0c\u5f53\u7136\u8fd9\u4e2a\u201c\u795e\u201d\u662f\u6307\u77ee\u4eba\u8bf8\u795e\uff0c\u548c\u4eba\u7c7b\u795e\u5353\u5c14\u795e\u6ca1\u6709\u5173\u7cfb\u3002\u8fd9\u725b\u76ae\u5439\u5f97\u592a\u5927\uff0c\u743c\u6069\u4e0d\u514d\u6709\u4e9b\u534a\u4fe1\u534a\u7591\uff0c\u5982\u4eca\u6b63\u597d\u6709\u673a\u4f1a\u4eb2\u773c\u89c1\u8bc6\u4e00\u4e0b\u3002</p><p>\u83ab\u6258\u5e26\u7740\u4e24\u4f4d\u5ba2\u4eba\u53c2\u89c2\u51b6\u70bc\u573a\uff0c\u4e00\u8def\u6307\u70b9\u4ecb\u7ecd\u3002\u77ee\u4eba\u539f\u672c\u5c31\u7cbe\u64c5\u953b\u9020\uff0c\u4f0a\u5361\u6c99\u57ce\u7684\u5de5\u5320\u66f4\u662f\u6b64\u4e2d\u7fd8\u695a\uff0c\u4ed6\u4eec\u5236\u4f5c\u7684\u5175\u5668\u94e0\u7532\u8fdc\u9500\u5c0f\u534a\u4e2a\u5e7d\u6697\u5730\u57df\uff0c\u5ba2\u6237\u5305\u62ec\u5341\u51e0\u4e2a\u57ce\u5e02\uff0c\u5c31\u8fde\u5bc7\u6d9b\u9c7c\u4eba\u90fd\u4ece\u4ed6\u4eec\u624b\u4e2d\u8d2d\u4e70\u88c5\u5907\u3002\u6709\u6781\u5c11\u6570\u5546\u54c1\u8f97\u8f6c\u6d41\u4f20\u5230\u5730\u8868\u4e16\u754c\uff0c\u90fd\u4f1a\u88ab\u5949\u4e3a\u73cd\u54c1\uff0c\u5356\u51fa\u5929\u4ef7\u3002\u201c\u5f53\u7136\uff0c\u201d\u83ab\u6258\u5f3a\u8c03\uff0c\u201c\u6211\u4eec\u4e0d\u548c\u5353\u5c14\u505a\u4ea4\u6613\u3002\u201d</p><p>\u4ed6\u53d6\u8fc7\u4e00\u526f\u521a\u521a\u7145\u5236\u597d\u7684\u80f8\u7532\uff0c\u653e\u5728\u94c1\u7827\u4e0a\uff0c\u53cc\u624b\u6284\u8d77\u65c1\u8fb9\u7684\u5927\u9524\u5b50\u731b\u529b\u4e00\u7838\u3002\u4ee5\u5e38\u7406\u800c\u8bba\uff0c\u54ea\u6015\u8fd9\u80f8\u7532\u662f\u7cbe\u91d1\u953b\u9020\uff0c\u88ab\u8fd9\u6837\u91cd\u51fb\uff0c\u4e5f\u5e94\u8be5\u4f1a\u88ab\u6572\u9677\u4e0b\u53bb\u4e00\u5757\u624d\u5bf9\u3002\u4f46\u51fa\u4e4e\u610f\u6599\u7684\u662f\uff0c\u9707\u8033\u6b32\u804b\u7684\u91d1\u94c1\u4ea4\u9e23\u4e2d\uff0c\u9524\u5b50\u53cd\u5f39\u56de\u6765\uff0c\u80f8\u7532\u53ea\u662f\u7565\u7565\u6709\u4e9b\u8f7b\u5fae\u53d8\u5f62\uff0c\u4e0d\u4ed4\u7ec6\u770b\u90fd\u4e0d\u4f1a\u53d1\u89c9\u3002</p><p>\u201c\u8fd9\u662f\u51c6\u5907\u5356\u7ed9\u65af\u9c81\u8fea\u5c14\u8499\u666e\u7f57\u57ce\u7684\u5bc7\u6d9b\u9c7c\u4eba\u7684\uff0c\u53ea\u80fd\u7b97\u662f\u6b21\u7b49\u8d27\u8272\uff0c\u201d\u83ab\u6258\u4ecb\u7ecd\u8bf4\uff0c\u8bed\u6c14\u4e2d\u9887\u6709\u51e0\u5206\u81ea\u50b2\uff0c\u201c\u771f\u6b63\u4e0a\u7b49\u7684\u5175\u5668\u94e0\u7532\uff0c\u81ea\u7136\u53ea\u80fd\u7559\u7740\u7ed9\u6211\u4eec\u81ea\u5df1\u81ea\u7528\u3002\u201d</p><p>\u8fd9\u662f\u7406\u6240\u5f53\u7136\u7684\uff0c\u56fd\u4e4b\u5229\u5668\uff0c\u4e0d\u53ef\u8f7b\u6388\u4e88\u4eba\uff0c\u80fd\u5728\u5e7d\u6697\u5730\u57df\u91cc\u76d8\u8e1e\u7684\uff0c\u5927\u591a\u4e0d\u662f\u4ec0\u4e48\u826f\u5584\u4e4b\u8f88\uff0c\u4f0a\u5361\u6c99\u57ce\u81ea\u7136\u8981\u9632\u5907\u4e00\u624b\uff0c\u5018\u82e5\u628a\u7b2c\u4e00\u7b49\u7684\u5175\u7532\u5356\u51fa\u53bb\uff0c\u4ee5\u540e\u4e07\u4e00\u7ffb\u8138\u5c31\u662f\u81ea\u5df1\u5403\u4e8f\u4e86\u3002\u5012\u662f\u743c\u6069\u6709\u4e9b\u597d\u5947\uff0c\u5728\u4ed6\u770b\u6765\uff0c\u521a\u624d\u8fd9\u526f\u80f8\u7532\u7684\u8868\u73b0\u5df2\u7ecf\u975e\u5e38\u51fa\u8272\u4e86\uff0c\u8fd8\u7b97\u4e0d\u5f97\u4e0a\u7b49\uff0c\u90a3\u66f4\u4f18\u79c0\u7684\u5e94\u8be5\u662f\u4ec0\u4e48\u6837\uff1f</p><p>\u83ab\u6258\u6325\u624b\u547d\u4ee4\u624b\u4e0b\u4ece\u4ed3\u5e93\u91cc\u53c8\u53d6\u51fa\u4e00\u526f\u80f8\u7532\u6765\uff0c\u518d\u6b21\u6f14\u793a\u7ed9\u743c\u6069\u770b\u3002\u4f9d\u65e7\u662f\u4e00\u9524\u7838\u4e0b\uff0c\u7136\u800c\u80f8\u7532\u5168\u65e0\u534a\u70b9\u53d8\u5f62\uff0c\u5e76\u4e0d\u662f\u786c\u751f\u751f\u9876\u4f4f\uff0c\u800c\u662f\u5148\u8f7b\u5fae\u5185\u9677\uff0c\u63a5\u7740\u5f39\u56de\uff0c\u7f13\u51b2\u5316\u89e3\u4e86\u51b2\u51fb\u529b\u9053\u2014\u2014\u5b83\u662f\u6709\u97e7\u6027\u7684\u3002</p><p>\u743c\u6069\u4f38\u624b\u6478\u4e86\u6478\uff0c\u5c06\u5b83\u63d0\u4e86\u8d77\u6765\uff0c\u4ed4\u7ec6\u89c2\u5bdf\uff0c\u80f8\u7532\u7684\u8868\u9762\u51b0\u51b7\u5149\u6ed1\uff0c\u521a\u624d\u7684\u4e00\u51fb\u6ca1\u6709\u9020\u6210\u4efb\u4f55\u7834\u574f\uff0c\u751a\u81f3\u8fde\u75d5\u8ff9\u90fd\u6ca1\u6709\uff0c\u4e0d\u4ec5\u5982\u6b64\uff0c\u5b83\u8fd8\u5f88\u8f7b\u3002\u743c\u6069\u4e0d\u7a7f\u94e0\u7532\uff0c\u5bf9\u8fd9\u65b9\u9762\u4e5f\u4e0d\u662f\u5f88\u719f\u6089\uff0c\u4f46\u6bd5\u7adf\u662f\u8fd9\u4e48\u4e00\u5927\u5757\u91d1\u5c5e\uff0c\u603b\u80fd\u611f\u89c9\u51fa\u6765\u5206\u91cf\u4e0d\u5bf9\u3002</p><p>\u201c\u8fd9\u662f\u4ec0\u4e48\u6750\u8d28\uff1f\u201d</p><p>\u201c\u4e3b\u8981\u662f\u7cbe\u91d1\u3002\u201d</p><p>\u201c\u7cbe\u91d1\u600e\u4e48\u53ef\u80fd\u8fd9\u4e48\u8f7b\u3002\u201d\u743c\u6069\u8be7\u5f02\u3002</p><p>\u8fd9\u4e2a\u4e16\u754c\u7528\u6765\u953b\u9020\u5175\u5668\u76d4\u7532\u7684\u91d1\u5c5e\u4e3b\u8981\u662f\u4e09\u79cd\uff1a\u7cbe\u91d1\u3001\u79d8\u94f6\u548c\u94a2\u94c1\u3002\u94a2\u94c1\u81ea\u7136\u662f\u6700\u5e38\u89c1\u6700\u666e\u53ca\u7684\u6750\u6599\uff0c\u800c\u7cbe\u91d1\u79d8\u94f6\u5219\u662f\u7a00\u6709\u91d1\u5c5e\uff0c\u6bd4\u8f83\u73cd\u8d35\u3002\u7cbe\u91d1\u6781\u5176\u575a\u786c\uff0c\u4f46\u5374\u5206\u91cf\u5f88\u6c89\uff0c\u901a\u5e38\u662f\u7528\u6765\u953b\u9020\u6b66\u5668\uff0c\u505a\u6210\u94e0\u7532\u7a7f\u5728\u8eab\u4e0a\u56fa\u7136\u5b89\u5168\uff0c\u4f46\u5c31\u592a\u8fc7\u7b28\u91cd\u4e86\u3002\u79d8\u94f6\u7684\u786c\u5ea6\u548c\u94a2\u94c1\u76f8\u5f53\uff0c\u5b83\u7684\u4f18\u70b9\u662f\u8f7b\uff0c\u540c\u7b49\u4f53\u79ef\u7684\u79d8\u94f6\u6bd4\u94a2\u94c1\u8981\u8f7b\u4e00\u534a\uff0c\u53ea\u6709\u7cbe\u91d1\u7684\u56db\u5206\u4e4b\u4e00\uff0c\u6700\u9002\u5b9c\u953b\u9020\u94e0\u7532\uff0c\u7075\u6d3b\u8f7b\u4fbf\u3002\u5f53\u7136\u8fd9\u662f\u9488\u5bf9\u4eba\u7c7b\u800c\u8a00\uff0c\u77ee\u4eba\u4e2a\u4e2a\u5f3a\u58ee\u5982\u725b\uff0c\u62ff\u7cbe\u91d1\u505a\u94e0\u7532\u5012\u662f\u4e0d\u5947\u602a\uff0c\u4f46\u6ca1\u9053\u7406\u5206\u91cf\u8fd9\u4e48\u8f7b\u5427\u3002</p><p>\u201c\u4e3b\u8981\u662f\u7cbe\u91d1\uff0c\u4f46\u6df7\u6709\u79d8\u94f6\uff0c\u6240\u4ee5\u8f7b\u4fbf\u3002\u201d</p><p>\u201c\u554a\uff0c\u7cbe\u91d1\u548c\u79d8\u94f6\u80fd\u878d\u5408\u5417\uff1f\u201d</p><p>\u8fd9\u6b21\u63d0\u51fa\u7591\u95ee\u7684\u662f\u6885\u83f2\u65af\uff0c\u636e\u5979\u6240\u77e5\uff0c\u5f88\u591a\u5de5\u5320\u90fd\u66fe\u7ecf\u8003\u8651\u8fc7\uff0c\u628a\u7cbe\u91d1\u79d8\u94f6\u4e24\u79cd\u91d1\u5c5e\u878d\u5408\uff0c\u6253\u9020\u51fa\u65e2\u575a\u56fa\u53c8\u8f7b\u4fbf\u7684\u94e0\u7532\u51fa\u6765\uff0c\u4f46\u8fd9\u79cd\u52aa\u529b\u5168\u90fd\u5931\u8d25\u4e86\u3002\u4e24\u79cd\u91d1\u5c5e\u6027\u8d28\u6b67\u5f02\u592a\u5927\uff0c\u5c31\u7b97\u52c9\u5f3a\u878d\u5408\u4e86\uff0c\u5f04\u51fa\u6765\u7684\u5408\u91d1\u4e5f\u5b8c\u5168\u4e27\u5931\u4e86\u7cbe\u91d1\u548c\u79d8\u94f6\u5404\u81ea\u7684\u7279\u6027\uff0c\u6beb\u65e0\u610f\u4e49\u3002</p><p>\u83ab\u6258\u563f\u563f\u4e00\u7b11\uff0c\u795e\u6001\u95f4\u9887\u6709\u4e9b\u81ea\u5f97\uff0c\u201c\u4e0d\u662f\u878d\u5408\uff0c\u8fd9\u662f\u6211\u4eec\u53d1\u660e\u7684\u6298\u53e0\u953b\u6253\u6280\u672f\u3002\u201d</p><p>\u4f0a\u5361\u6c99\u57ce\u7684\u4ea7\u54c1\u4e4b\u6240\u4ee5\u8d28\u91cf\u597d\uff0c\u9996\u5148\u5728\u4e8e\u6750\u6599\u3002\u5728\u5730\u8868\u4e16\u754c\uff0c\u7528\u6765\u953b\u9020\u6b66\u5668\u76d4\u7532\u7684\u57fa\u672c\u90fd\u662f\u94a2\u94c1\uff0c\u7cbe\u91d1\u79d8\u94f6\u5f00\u91c7\u56f0\u96be\uff0c\u975e\u5e38\u7f55\u89c1\uff0c\u53ea\u6709\u6781\u5c11\u6570\u4eba\u624d\u4eab\u7528\u5f97\u8d77\u3002\u4f46\u5bf9\u4e8e\u4f0a\u5361\u6c99\u57ce\u7684\u77ee\u4eba\u4eec\u6765\u8bf4\uff0c\u4ed6\u4eec\u5c31\u751f\u6d3b\u5728\u5730\u4e0b\uff0c\u540c\u65f6\u53c8\u662f\u6316\u5751\u7684\u5927\u884c\u5bb6\uff0c\u57ce\u5e02\u5468\u56f4\u66f4\u6709\u6574\u4e2a\u5e7d\u6697\u5730\u57df\u6700\u4e30\u5bcc\u7684\u91d1\u5c5e\u77ff\u8109\uff0c\u5c31\u743c\u6069\u6240\u89c1\uff0c\u57ce\u4e2d\u6240\u6709\u6218\u58eb\u7684\u6b66\u5668\u3001\u94e0\u7532\u3001\u76fe\u724c\u7b49\u88c5\u5907\uff0c\u4e0d\u662f\u7cbe\u91d1\u5c31\u662f\u79d8\u94f6\uff0c\u94a2\u94c1\u6839\u672c\u770b\u4e0d\u4e0a\u773c\u3002\u5c31\u8fde\u74dc\u7406\u5fb7\u65af\u57ce\u7684\u5353\u5c14\uff0c\u4e5f\u53ea\u6709\u5c11\u6570\u7267\u5e08\u548c\u9ad8\u5c42\u7684\u6218\u58eb\u624d\u6709\u8d44\u683c\u7a7f\u7cbe\u91d1\u9501\u7532\uff0c\u548c\u77ee\u4eba\u5b8c\u5168\u65e0\u6cd5\u76f8\u6bd4\u3002</p><p>\u5373\u4fbf\u662f\u540c\u6837\u6750\u8d28\u7684\u91d1\u5c5e\uff0c\u4e5f\u662f\u6709\u7eaf\u5ea6\u533a\u522b\u7684\uff0c\u77ee\u4eba\u4e0d\u4f46\u64c5\u957f\u6316\u77ff\uff0c\u66f4\u64c5\u957f\u51b6\u70bc\u3002\u540c\u6837\u4e00\u5757\u7cbe\u91d1\uff0c\u4f0a\u5361\u6c99\u57ce\u62ff\u51fa\u6765\u7684\u4fbf\u8981\u5206\u91cf\u66f4\u6c89\u3001\u66f4\u52a0\u575a\u786c\uff1b\u540c\u6837\u4e00\u5757\u79d8\u94f6\uff0c\u4f0a\u5361\u6c99\u57ce\u62ff\u51fa\u6765\u7684\u4fbf\u66f4\u52a0\u8f7b\u5de7\u51e0\u5206\u3002\u8fd9\u5176\u4e2d\u7684\u5dee\u8ddd\u770b\u4f3c\u4e0d\u5927\uff0c\u6218\u573a\u4e0a\u5f80\u5f80\u5c31\u80fd\u51b3\u5b9a\u751f\u6b7b\u80dc\u8d1f\u3002</p><p>\u9664\u4e86\u6750\u6599\u4e0a\u7684\u4f18\u52bf\uff0c\u4fbf\u662f\u953b\u9020\u6280\u827a\u7684\u9ad8\u660e\u4e86\uff0c\u4f0a\u5361\u6c99\u57ce\u662f\u6614\u65e5\u6df1\u9083\u6749\u90a3\u5854\u738b\u56fd\u7684\u953b\u9020\u548c\u5b66\u672f\u4e2d\u5fc3\uff0c\u867d\u7136\u5982\u4eca\u77ee\u4eba\u738b\u56fd\u65e9\u5c31\u8986\u706d\uff0c\u4f46\u8fd9\u5ea7\u57ce\u5e02\u5374\u4e00\u76f4\u7a33\u7a33\u5c79\u7acb\u4e0d\u5012\uff0c\u4ece\u6765\u6ca1\u6709\u88ab\u653b\u9677\u88ab\u7834\u574f\u8fc7\uff0c\u5343\u4e07\u5e74\u7684\u4f20\u627f\u4e00\u76f4\u5ef6\u7eed\u4e0b\u6765\uff0c\u53d1\u660e\u4e86\u5404\u79cd\u795e\u5947\u83ab\u6d4b\u7684\u6280\u5de7\u3002\u4fbf\u5982\u521a\u624d\u83ab\u6258\u6240\u8bf4\u7684\u201c\u6298\u53e0\u953b\u6253\u201d\uff0c\u743c\u6069\u662f\u6ca1\u592a\u5f04\u660e\u767d\u5177\u4f53\u4ec0\u4e48\u610f\u601d\uff0c\u53cd\u6b63\u662f\u628a\u7cbe\u91d1\u79d8\u94f6\u6df7\u5408\u4e00\u5904\uff0c\u6253\u9020\u51fa\u6765\u7684\u4e1c\u897f\u65e2\u4fdd\u6301\u786c\u5ea6\uff0c\u53c8\u517c\u6709\u97e7\u6027\uff0c\u800c\u4e14\u6bd4\u5b8c\u5168\u7528\u7cbe\u91d1\u8981\u8f7b\u5de7\u5f97\u591a\uff0c\u4e43\u662f\u4f0a\u5361\u6c99\u57ce\u79d8\u800c\u4e0d\u5ba3\u7684\u6280\u672f\u4e4b\u4e00\u3002</p><p>\u517c\u6709\u6750\u6599\u548c\u6280\u672f\u4e0a\u7684\u4f18\u52bf\uff0c\u4f0a\u5361\u6c99\u57ce\u7684\u4ea7\u54c1\u8d28\u91cf\u4f18\u826f\uff0c\u4e5f\u4fbf\u662f\u60c5\u7406\u4e4b\u4e2d\u7684\u4e8b\u60c5\u4e86\uff0c\u77ee\u4eba\u4eec\u80fd\u591f\u6210\u529f\u62b5\u5fa1\u74dc\u7406\u5fb7\u65af\u57ce\u7684\u5353\u5c14\uff0c\u8fd9\u5175\u7532\u4e4b\u5229\u662f\u4e00\u5927\u501a\u4ed7\uff0c\u6bd5\u7adf\u94f6\u9f99\u56fd\u738b\u662f\u8fd1\u4e00\u5343\u4e09\u767e\u5e74\u624d\u6765\u7684\uff0c\u6b64\u524d\u53ef\u662f\u5b8c\u5168\u9760\u7740\u81ea\u5df1\u3002</p><p>\u743c\u6069\u4eb2\u773c\u89c1\u8bc6\u5230\u77ee\u4eba\u7684\u795e\u5947\u6280\u827a\uff0c\u53f9\u4e3a\u89c2\u6b62\uff0c\u4e0d\u7531\u5f97\u4e5f\u52a8\u4e86\u5fc3\u601d\u3002\u5deb\u5e08\u901a\u5e38\u4e0d\u80fd\u7a7f\u7532\uff0c\u6015\u59a8\u788d\u65bd\u6cd5\uff0c\u56e0\u6b64\u5927\u591a\u8106\u5f31\uff0c\u5728\u6218\u573a\u4e0a\u5f80\u5f80\u662f\u654c\u4eba\u4f18\u5148\u653b\u51fb\u7684\u5bf9\u8c61\uff0c\u4e00\u4e0d\u7559\u795e\u5c31\u6709\u6027\u547d\u4e4b\u5fe7\u3002\u9664\u975e\u5230\u4e86\u4e9a\u5f53\u65af\u8fd9\u79cd\u5730\u6b65\uff0c\u8eab\u4e0a\u968f\u65f6\u7b3c\u7f69\u5404\u79cd\u9632\u62a4\u6cd5\u672f\uff0c\u4e00\u53d7\u5230\u653b\u51fb\u5c31\u81ea\u52a8\u89e6\u53d1\uff0c\u80cc\u540e\u88ab\u7ef4\u5eb7\u5c3c\u4e9a\u5077\u88ad\u4e00\u67aa\u90fd\u523a\u4e0d\u6b7b\uff0c\u4f46\u8fd9\u6bd5\u7adf\u662f\u6781\u5c11\u6570\u3002\u743c\u6069\u4e5f\u6ca1\u5230\u8fd9\u4e2a\u7a0b\u5ea6\uff0c\u6240\u4ee5\u4ed6\u9876\u591a\u53c2\u52a0\u51e0\u4e2a\u4eba\u7684\u5c0f\u578b\u6597\u6bb4\uff0c\u771f\u6b63\u7684\u5927\u89c4\u6a21\u6218\u573a\u662f\u575a\u51b3\u4e0d\u4e0a\u53bb\u7684\uff0c\u5426\u5219\u968f\u4fbf\u51e0\u53ea\u6d41\u77e2\u659c\u523a\u98de\u6765\uff0c\u8bf4\u4e0d\u5b9a\u5c31\u82f1\u5e74\u65e9\u901d\u4e86\uff0c\u8fd9\u53ef\u4e07\u4e07\u4e0d\u662f\u5f00\u73a9\u7b11\u7684\u4e8b\u60c5\u3002</p><p>\u5965\u6c83\u4f20\u6388\u7684\u6280\u5de7\uff0c\u80fd\u591f\u8ba9\u4ed6\u7701\u7565\u6389\u7e41\u6742\u7684\u65bd\u6cd5\u52a8\u4f5c\uff0c\u5982\u6b64\u4e00\u6765\u76d4\u7532\u7684\u9650\u5236\u4f5c\u7528\u4e5f\u5c31\u5927\u5927\u524a\u5f31\u4e86\u3002\u4ee5\u743c\u6069\u7684\u4f53\u683c\uff0c\u7a7f\u90a3\u79cd\u5168\u8eab\u94e0\u7532\u53ea\u6015\u4f1a\u8fde\u8def\u90fd\u8d70\u4e0d\u52a8\uff0c\u4e0d\u5fc5\u5984\u60f3\uff0c\u4f46\u8f7b\u4fbf\u7684\u9501\u7532\u5374\u672a\u5c1d\u4e0d\u53ef\u4ee5\u8003\u8651\u3002</p><p>\u7b2c\u4e00\u6b21\u7ed9\u81ea\u5df1\u5b9a\u505a\u88c5\u5907\uff0c\u800c\u4e14\u8fd8\u662f\u8fd9\u7b49\u4fdd\u547d\u7684\u4e1c\u897f\uff0c\u81ea\u7136\u8981\u614e\u4e4b\u53c8\u614e\u3002\u518d\u4ed4\u7ec6\u4e00\u95ee\uff0c\u53d1\u73b0\u77ee\u4eba\u867d\u7136\u76f4\u7387\uff0c\u5374\u4e0d\u7b49\u4e8e\u4e0d\u4f1a\u5439\u725b\uff0c\u83ab\u6258\u8bf4\u5f97\u5929\u82b1\u4e71\u5760\uff0c\u4e5f\u57fa\u672c\u90fd\u662f\u5b9e\u60c5\uff0c\u4f46\u8fd8\u662f\u6709\u6240\u5938\u5927\u3002\u4f0a\u5361\u6c99\u57ce\u7684\u4ea7\u54c1\u5206\u4e0a\u4e2d\u4e0b\u4e09\u7b49\uff0c\u4e0b\u7b49\u5168\u90e8\u7528\u4e8e\u51fa\u552e\uff0c\u4e2d\u7b49\u5c31\u662f\u743c\u6069\u7b2c\u4e00\u6b21\u770b\u5230\u7684\u80f8\u7532\uff0c\u8bf4\u8981\u5356\u7ed9\u5bc7\u6d9b\u9c7c\u4eba\u7684\u90a3\u79cd\uff0c\u4e5f\u4f1a\u914c\u60c5\u51fa\u552e\u90e8\u5206\uff0c\u4e3b\u8981\u5356\u7ed9\u90a3\u4e9b\u8ddd\u79bb\u8f83\u8fdc\u6216\u8005\u5173\u7cfb\u8f83\u597d\uff0c\u5bf9\u81ea\u5df1\u6784\u4e0d\u6210\u5a01\u80c1\u7684\u987e\u5ba2\uff0c\u5927\u90e8\u4efd\u90fd\u7559\u4f5c\u81ea\u7528\uff0c\u81f3\u4e8e\u540e\u6765\u770b\u5230\u7684\u4e0a\u7b49\u8d27\u8272\uff0c\u539f\u672c\u5c31\u662f\u7528\u6765\u8bc4\u5b9a\u201c\u953b\u9020\u5927\u5e08\u201d\u7684\u6807\u51c6\u4e4b\u4e00\uff0c\u540c\u6837\u5c5e\u4e8e\u73cd\u7a00\u54c1\uff0c\u4ee3\u8868\u6700\u9ad8\u6210\u5c31\uff0c\u5426\u5219\u83ab\u6258\u4e5f\u4e0d\u81f3\u4e8e\u8fd8\u8981\u7279\u5730\u547d\u4eba\u4ece\u4ed3\u5e93\u91cc\u53bb\u53d6\u4e86\u3002</p><p>\u4f0a\u5361\u6c99\u57ce\u6709\u4e94\u4f4d\u953b\u9020\u5927\u5e08\uff0c\u773c\u524d\u7684\u83ab\u6258\u5c31\u662f\u5176\u4e2d\u4e4b\u4e00\uff0c\u4f46\u4ed6\u662f\u4ee5\u5236\u4f5c\u6b66\u5668\u8457\u79f0\uff0c\u6d3e\u4e0d\u4e0a\u7528\u573a\u3002\u64c5\u957f\u953b\u9020\u76d4\u7532\u7684\u6709\u4e24\u4f4d\uff0c\u6700\u4f73\u4eba\u9009\u81ea\u7136\u5c31\u662f\u9996\u5e2d\u953b\u9020\u5e08\u7d22\u5c3c\xb7\u6864\u9aa8\uff0c\u53ea\u662f\u5979\u65e9\u5df2\u9000\u4f11\u5728\u5bb6\uff0c\u8981\u8bf7\u52a8\u5979\u5374\u4e0d\u5bb9\u6613\uff0c\u5f53\u7136\uff0c\u4ee5\u743c\u6069\u73b0\u5728\u7684\u8eab\u4efd\u4e5f\u4e0d\u7b97\u592a\u96be\u3002</p><p>\u201c\u8bf4\u8d77\u6765\uff0c\u5728\u6c99\u739b\u65af\u57ce\u7684\u65f6\u5019\uff0c\u9c81\u6587\u56fd\u738b\u8fd8\u66fe\u7ecf\u8bb8\u8bfa\uff0c\u8bf4\u4f1a\u5e2e\u6211\u8bf7\u7d22\u5c3c\u5927\u5e08\u953b\u9020\u4e00\u526f\u76d4\u7532\u5462\uff0c\u201d\u743c\u6069\u5bf9\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u4ed6\u5e94\u8be5\u4e0d\u4f1a\u5fd8\u4e86\u5427\u3002\u201d</p><p>\u9c81\u6587\u5f53\u7136\u6ca1\u5fd8\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u5f88\u5bb9\u6613\u5f97\u5230\u89e3\u51b3\uff0c\u743c\u6069\u767b\u95e8\u62dc\u8bbf\uff0c\u7d22\u5c3c\u5927\u5e08\u4fbf\u5f88\u75db\u5feb\u5730\u7b54\u5e94\u4eb2\u81ea\u51fa\u624b\u64cd\u9524\u3002\u53ea\u4e0d\u8fc7\u2026\u2026</p><p>\u201c\u8981\u4e09\u56db\u4e2a\u6708\u624d\u80fd\u5b8c\u5de5\uff1f\u201d\u743c\u6069\u5012\u5438\u4e86\u53e3\u6c14\uff0c\u201c\u8fd9\u4e5f\u672a\u514d\u592a\u4e45\u4e86\u5427\u3002\u201d</p><p>\u201c\u8fd9\u5df2\u7ecf\u662f\u5f88\u4e50\u89c2\u7684\u4f30\u8ba1\u4e86\uff0c\u201d\u9c81\u6587\u8bf4\uff0c\u201c\u7d22\u5c3c\u5927\u5e08\u7684\u6280\u827a\u662f\u6ca1\u8bdd\u8bf4\uff0c\u4f46\u5c31\u662f\u6162\u3002\u201d</p><p>\u539f\u6765\u8fd9\u4f4d\u7d22\u5c3c\u5927\u5e08\u7684\u5883\u754c\u5df2\u7ecf\u5230\u4e86\u7531\u6280\u5165\u827a\u7684\u5730\u6b65\u4e86\uff0c\u628a\u6bcf\u4ef6\u4f5c\u54c1\u90fd\u5f53\u4f5c\u827a\u672f\u54c1\u770b\u5f85\uff0c\u7cbe\u76ca\u6c42\u7cbe\uff0c\u529b\u6c42\u5b8c\u7f8e\uff0c\u6700\u9ad8\u8bb0\u5f55\u662f\u66fe\u7ecf\u82b1\u4e86\u4e03\u5e74\u65f6\u95f4\u4e13\u5fc3\u953b\u9020\u4e00\u526f\u5168\u8eab\u94e0\u7532\u2014\u2014\u6700\u540e\u4f9d\u65e7\u89c9\u5f97\u4e0d\u6ee1\u610f\uff0c\u88ab\u5979\u6254\u4ed3\u5e93\u89d2\u843d\u4e86\u3002\u743c\u6069\u8ba2\u505a\u7684\u662f\u9501\u7532\uff0c\u6bd4\u94e0\u7532\u5bb9\u6613\u5f88\u591a\uff0c\u4f46\u4e09\u56db\u4e2a\u6708\u4e5f\u786e\u5b9e\u5df2\u7ecf\u662f\u4e50\u89c2\u4f30\u8ba1\u4e86\u3002</p><p>\u201c\u65e0\u6240\u8c13\uff0c\u201d\u743c\u6069\u5b89\u6170\u81ea\u5df1\uff0c\u201c\u53cd\u6b63\u6211\u53c8\u4e0d\u6025\u7740\u7528\u3002\u201d</p><p>\u4ed6\u786e\u5b9e\u4e0d\u7528\u7740\u6025\uff0c\u5982\u4eca\u8bf8\u4e8b\u5b89\u5b9a\uff0c\u4e00\u5207\u4e95\u4e95\u6709\u6761\uff0c\u6ca1\u4ec0\u4e48\u9700\u8981\u62c5\u5fc3\u7684\uff0c\u8be5\u505a\u7684\u5584\u540e\u5de5\u4f5c\u90fd\u5df2\u7ecf\u5b8c\u6210\uff0c\u6e10\u6e10\u4e5f\u6e05\u95f2\u4e0b\u6765\u3002\u548c\u77ee\u4eba\u6253\u4ea4\u9053\u5f88\u8f7b\u677e\uff0c\u6ca1\u6709\u592a\u591a\u7684\u5f2f\u5f2f\u7ed5\uff0c\u76f4\u6765\u76f4\u53bb\uff0c\u4ed6\u4eec\u6709\u5f88\u5f3a\u7684\u81ea\u8c6a\u611f\uff0c\u867d\u7136\u4e0d\u4f1a\u50cf\u5353\u5c14\u90a3\u6837\u628a\u5176\u4ed6\u79cd\u65cf\u89c6\u4e3a\u52a3\u7b49\uff0c\u4f46\u975e\u5e38\u5728\u4e4e\u201c\u8363\u8a89\u201d\u3001\u201c\u5c0a\u4e25\u201d\u3001\u201c\u4f53\u9762\u201d\u8fd9\u4e9b\u4e1c\u897f\uff0c\u53ea\u8981\u5c0f\u5fc3\u4e0d\u89e6\u72af\u5230\u8fd9\u4e9b\uff0c\u90a3\u4e48\u4e00\u5207\u90fd\u597d\u8bf4\u3002</p><p>\u6885\u83f2\u65af\u7684\u5934\u76d4\u4e5f\u5df2\u7ecf\u505a\u597d\uff0c\u9001\u4e86\u8fc7\u6765\uff0c\u5f0f\u6837\u7b80\u6d01\uff0c\u6ca1\u4ec0\u4e48\u82b1\u54e8\u88c5\u9970\uff0c\u53ea\u6709\u4e00\u4e9b\u6734\u7d20\u7684\u82b1\u7eb9\uff0c\u4f46\u770b\u5f97\u51fa\u5236\u4f5c\u8005\u5f88\u7528\u5fc3\uff0c\u8d28\u91cf\u4e5f\u662f\u4e00\u6d41\u3002\u5176\u5b9e\u743c\u6069\u4e0d\u751a\u559c\u6b22\u6885\u83f2\u65af\u6234\u7740\u5934\u76d4\uff0c\u5c24\u5176\u662f\u653e\u4e0b\u9762\u7f69\u4ee5\u540e\uff0c\u8fd9\u6837\u4e00\u6765\u5c31\u770b\u4e0d\u5230\u7f8e\u4e3d\u7684\u9762\u5bb9\u548c\u91d1\u53d1\u4e86\uff0c\u53ea\u662f\u7ec8\u7a76\u5b89\u5168\u7b2c\u4e00\uff0c\u5934\u53c8\u662f\u4eba\u4f53\u7684\u5173\u952e\u90e8\u4f4d\uff0c\u4e0d\u80fd\u8981\u6f02\u4eae\u4e0d\u8981\u6027\u547d\u3002</p><p>\u5bf9\u4e8e\u8010\u745f\u5377\u8f74\u7684\u7814\u7a76\u4f9d\u65e7\u6beb\u65e0\u534a\u70b9\u8fdb\u5c55\uff0c\u800c\u7075\u5438\u602a\u5deb\u5996\u7684\u547d\u5323\u4e5f\u4f9d\u65e7\u4e0d\u77e5\u9053\u5982\u4f55\u5904\u7f6e\uff0c\u8fd9\u4e24\u6837\u5b9d\u7269\u5728\u624b\u91cc\u653e\u7740\u504f\u504f\u53c8\u6d3e\u4e0d\u4e0a\u7528\u573a\uff0c\u743c\u6069\u51e0\u4e4e\u90fd\u8981\u653e\u5f03\u5e0c\u671b\u4e86\u3002\u95f2\u6687\u65e0\u4e8b\u7ecf\u5e38\u53bb\u57ce\u4e2d\u7684\u56fe\u4e66\u9986\u9605\u89c8\uff0c\u5012\u662f\u589e\u957f\u4e86\u4e0d\u5c11\u89c1\u95fb\uff0c\u6709\u5173\u8fdc\u53e4\u5386\u53f2\u7684\u4e86\u89e3\u4e86\u4e0d\u5c11\u3002\u77ee\u4eba\u4e0d\u50cf\u5353\u5c14\u6709\u9ed1\u6697\u89c6\u89c9\uff0c\u4f0a\u5361\u6c99\u57ce\u91cc\u5904\u5904\u90fd\u6709\u5149\u6e90\uff0c\u751f\u6d3b\u548c\u4ee5\u524d\u9634\u9b42\u57ce\u5c01\u5728\u5e7d\u5f71\u754c\u7684\u65f6\u5019\u6ca1\u591a\u5927\u5dee\u5f02\uff0c\u800c\u4e14\u98df\u7269\u8fd8\u4e30\u5bcc\u5f97\u591a\uff0c\u5ca9\u77f3\u548c\u690d\u7269\u8868\u9762\u957f\u6ee1\u5404\u79cd\u83cc\u7c7b\uff0c\u5730\u4e0b\u6cb3\u4e2d\u6709\u9c7c\uff0c\u77ee\u4eba\u4e5f\u5708\u517b\u6d1b\u65af\u517d\u4f5c\u4e3a\u4e3b\u8981\u7684\u8089\u98df\u6765\u6e90\uff0c\u53e6\u5916\u6b64\u6b21\u5353\u5c14\u4eec\u7559\u4e0b\u4e86\u4e0d\u5c11\u8725\u8734\u5750\u9a91\uff0c\u5168\u90fd\u88ab\u77ee\u4eba\u4eec\u6740\u4e86\u6539\u5584\u4f19\u98df\uff0c\u53cd\u6b63\u4ed6\u4eec\u9a91\u4e0d\u4e86\u3002\u6709\u6885\u83f2\u65af\u966a\u4f34\u5728\u8eab\u8fb9\uff0c\u5012\u4e5f\u8212\u9002\u81ea\u5728\u3002\u95f4\u6216\u548c\u8299\u857e\u72c4\u5077\u60c5\uff0c\u6885\u83f2\u65af\u5e94\u8be5\u77e5\u9053\uff0c\u4f46\u4e5f\u6ca1\u8bf4\u4ec0\u4e48\u3002\u552f\u4e00\u7684\u9057\u61be\uff0c\u5c31\u662f\u73ca\u5609\u4e0d\u5728\u8eab\u8fb9\uff0c\u5426\u5219\u4e00\u5207\u5c31\u5b8c\u7f8e\u4e86\u3002</p><p>\u6109\u5feb\u7684\u65f6\u5149\u603b\u662f\u6d41\u901d\u5f97\u98de\u5feb\uff0c\u8f6c\u773c\u4e4b\u95f4\u5c31\u8fc7\u53bb\u4e86\u51e0\u4e2a\u6708\uff0c\u6309\u7167\u4eba\u7c7b\u7684\u5386\u6cd5\uff0c\u73b0\u5728\u5df2\u7ecf\u662f1373DR\uff0c\u743c\u6069\u4e5f\u5df2\u7ecf\u5341\u516d\u5c81\u3002\u8fd9\u4e00\u5929\uff0c\u4ed6\u6b63\u5728\u548c\u6885\u83f2\u65af\u95f2\u8c08\uff0c\u4e00\u4e2a\u77ee\u4eba\u4fe1\u4f7f\u524d\u6765\u62a5\u544a\uff0c\u8bf4\u8fea\u74e6\u514b\u5e26\u9886\u4e00\u7fa4\u4eba\u7c7b\u8fdb\u57ce\u4e86\u3002</p><p>\u203b\u203b\u203b</p><p>\u743c\u6069\u6b64\u6b21\u6765\u5e7d\u6697\u5730\u57df\uff0c\u6700\u4e3b\u8981\u7684\u4efb\u52a1\u5c31\u662f\u5bfb\u627e\u4e00\u5904\u9002\u5408\u7684\u9b54\u50cf\u5236\u4f5c\u57fa\u5730\uff0c\u524d\u756a\u5954\u5fd9\uff0c\u76ee\u7684\u4e0d\u8fc7\u5728\u6b64\u800c\u5df2\u3002\u5728\u653e\u8d70\u7ef4\u5eb7\u5c3c\u4e9a\u4e4b\u540e\u7684\u7b2c\u4e09\u5929\uff0c\u743c\u6069\u5c31\u5199\u4e86\u4e24\u5c01\u957f\u957f\u7684\u4fe1\uff0c\u7136\u540e\u6d3e\u4eba\u628a\u4f8f\u5112\u8fea\u74e6\u514b\u8bf7\u6765\uff0c\u547d\u4ee4\u4ed6\u539f\u8def\u8fd4\u56de\uff0c\u8054\u7edc\u7b2c\u4e8c\u8fdc\u5f81\u5e08\u7684\u6307\u6325\u5b98\u96c5\u8fbe\u738b\u5b50\uff0c\u628a\u5176\u4e2d\u4e00\u5c01\u4fe1\u4ea4\u4e0a\uff0c\u6c47\u62a5\u6b64\u5904\u7684\u4e8b\u60c5\u8fdb\u5c55\uff0c\u540c\u65f6\u8bf7\u9634\u9b42\u57ce\u6d3e\u9063\u9b54\u50cf\u5236\u4f5c\u65b9\u9762\u7684\u4e13\u5bb6\u524d\u6765\u2014\u2014\u53e6\u5916\u4e00\u5c01\u4fe1\u662f\u5bb6\u4e66\uff0c\u7ed9\u59d0\u59d0\u73ca\u5609\u7684\uff0c\u8ba9\u7b2c\u4e8c\u8fdc\u5f81\u5e08\u7684\u4fe1\u4f7f\u8f6c\u56de\u9634\u9b42\u57ce\u3002</p><p>\u539f\u672c\u9634\u9b42\u57ce\u5c31\u548c\u9c81\u6587\u6709\u534f\u8bae\uff1a\u6551\u9192\u56fd\u738b\uff0c\u77ee\u4eba\u4fbf\u503e\u529b\u534f\u52a9\u9634\u9b42\u57ce\u5236\u4f5c\u9b54\u50cf\u519b\u56e2\u3002\u5982\u4eca\u867d\u7136\u56fd\u738b\u9a7e\u5d29\u4e86\u2014\u2014\u4f46\u4ed6\u786e\u5b9e\u662f\u5728\u751f\u547d\u7684\u6700\u540e\u4e00\u6bb5\u65f6\u95f4\u201c\u6e05\u9192\u201d\u7740\uff0c\u800c\u4e14\u8fd8\u7559\u4e0b\u9057\u8bcf\uff0c\u5b89\u6392\u540e\u4e16\uff0c\u7136\u540e\u5bff\u7ec8\u6b63\u5bdd\u3002\u751f\u8001\u75c5\u6b7b\uff0c\u6b64\u4e43\u4e16\u95f4\u5e38\u6001\uff0c\u5927\u9650\u5230\u4e86\uff0c\u81ea\u7136\u9b42\u5f52\u51a5\u51a5\uff0c\u8fd9\u4e5f\u602a\u4e0d\u5230\u743c\u6069\u7b49\u4eba\u5934\u4e0a\u3002\u9c81\u6587\u5f53\u4e86\u56fd\u738b\uff0c\u4f9d\u65e7\u606a\u5b88\u524d\u7ea6\uff0c\u5e76\u65e0\u53cd\u6094\u3002\u4e00\u573a\u8f9b\u82e6\uff0c\u4e5f\u7b97\u662f\u6709\u4e86\u7ed3\u679c\u3002</p><p>\u8def\u7ebf\u57fa\u672c\u662f\u719f\u6089\u7684\uff0c\u8fea\u74e6\u514b\u5e26\u9886\u7740\u4e00\u961f\u5168\u526f\u6b66\u88c5\u7684\u77ee\u4eba\u62a4\u536b\u51fa\u53d1\uff0c\u6599\u60f3\u4e0d\u81f3\u4e8e\u6709\u4ec0\u4e48\u95ea\u5931\u3002\u552f\u4e00\u7684\u95ee\u9898\u662f\u4f8f\u5112\u548c\u77ee\u4eba\u7684\u817f\u90fd\u975e\u5e38\u77ed\uff0c\u6ca1\u529e\u6cd5\u50cf\u4eba\u7c7b\u548c\u5353\u5c14\u4e00\u6837\u9a91\u9a6c\u9a91\u8725\u8734\uff0c\u53ea\u80fd\u9760\u4e24\u6761\u817f\u8d76\u8def\uff0c\u901f\u5ea6\u4fbf\u6162\u4e86\u3002\u539f\u672c\u743c\u6069\u4f30\u7b97\uff0c\u6765\u56de\u4e00\u8d9f\u5927\u6982\u8981\u82b1\u534a\u5e74\u65f6\u95f4\uff0c\u5012\u6ca1\u60f3\u5230\u4f1a\u8fd9\u4e48\u5feb\u3002</p><p>\u51fa\u6765\u8fce\u63a5\uff0c\u53d1\u73b0\u5176\u4e2d\u8fd8\u6709\u51e0\u4e2a\u4eba\u662f\u8ba4\u8bc6\u7684\uff0c\u90fd\u662f\u4ee5\u524d\u70bc\u91d1\u5b66\u9662\u91cc\u7684\u540c\u50da\uff0c\u867d\u7136\u4e0d\u7b97\u5f88\u719f\uff0c\u603b\u4e5f\u7ecf\u5e38\u7167\u9762\u3002\u6240\u8c13\u4ed6\u4e61\u9047\u6545\u77e5\uff0c\u4e5f\u662f\u4eba\u751f\u4e00\u5927\u5feb\u4e8b\uff0c\u4f55\u51b5\u6210\u5929\u770b\u7740\u8fd9\u7fa4\u77ee\u4eba\uff0c\u5982\u4eca\u80fd\u518d\u770b\u89c1\u4eba\u7c7b\u7684\u9762\u5b54\uff0c\u90fd\u89c9\u5f97\u683c\u5916\u4eb2\u5207\u3002</p><p>\u9c81\u6587\u56fd\u738b\u65e9\u5c31\u5df2\u7ecf\u5212\u5b9a\u573a\u5730\uff0c\u5b89\u6392\u4f4f\u5904\uff0c\u4e00\u4e00\u5b89\u7f6e\u4e0d\u63d0\u3002\u5236\u9020\u9b54\u50cf\u9700\u8981\u5927\u91cf\u7684\u91d1\u5c5e\u77ff\u77f3\uff0c\u4f0a\u5361\u6c99\u57ce\u7ecf\u5386\u5927\u6218\u672a\u4e45\uff0c\u5143\u6c14\u7a0d\u590d\uff0c\u867d\u7136\u8bb8\u8bfa\u76f8\u52a9\uff0c\u4e00\u65f6\u8fd8\u817e\u4e0d\u51fa\u592a\u591a\u4eba\u624b\uff0c\u53ea\u80fd\u6162\u6162\u6765\uff0c\u53cd\u6b63\u8fd9\u4e5f\u4e0d\u662f\u6025\u4e8e\u4e00\u65f6\u7684\u4e8b\u60c5\uff0c\u5148\u6253\u4e0b\u57fa\u7840\u518d\u8bf4\u3002</p><p>\u8fea\u74e6\u514b\u6b64\u6b21\u56de\u6765\uff0c\u5012\u662f\u5e26\u56de\u4e86\u4e0d\u5c11\u65b0\u7684\u6d88\u606f\u3002\u9996\u5148\u9a8c\u8bc1\u4e86\u4ee5\u524d\u6b27\u51ef\u5728\u74dc\u7406\u5fb7\u65af\u57ce\u8bf4\u7684\u6d88\u606f\uff0c\u9634\u9b42\u57ce\u987a\u5229\u653b\u5360\u5965\u56fe\u3001\u5951\u5fb7\xb7\u7eb3\u6492\u3001\u4e9a\u65af\u79d1\u5c14\u3001\u65af\u7434\u8fbe\u83b1\u6069\u7b49\u4e00\u5171\u516d\u5ea7\u57ce\u5e02\uff0c\u53ea\u8981\u518d\u5e73\u5b9a\u9b54\u7d22\u5e03\u83b1\u57ce\uff0c\u6574\u4e2a\u5317\u5e7d\u6697\u5730\u57df\u5c31\u57fa\u672c\u6ca1\u6709\u4ec0\u4e48\u591f\u5206\u91cf\u7684\u5bf9\u624b\u4e86\u3002</p><p>\u86db\u540e\u6c89\u5bc2\uff0c\u9b54\u7d22\u5e03\u83b1\u57ce\u4e5f\u548c\u5176\u4ed6\u57ce\u5e02\u4e00\u6837\u9677\u5165\u5185\u4e71\uff0c\u53c8\u6709\u7070\u77ee\u4eba\u524d\u6765\u653b\u6253\uff0c\u5185\u5916\u4ea4\u56f0\uff0c\u539f\u672c\u662f\u5929\u8d50\u826f\u673a\u3002\u53ea\u53ef\u60dc\u7b2c\u4e8c\u8fdc\u5f81\u5e08\u521a\u521a\u5f00\u5230\uff0c\u7f57\u4e1d\u6070\u597d\u590d\u82cf\uff0c\u5353\u5c14\u4eec\u8fc5\u901f\u5e73\u606f\u5185\u4e71\uff0c\u4e00\u81f4\u5bf9\u5916\uff0c\u9634\u9b42\u57ce\u53cd\u5012\u5403\u4e86\u70b9\u4e8f\uff0c\u6240\u6709\u7684\u6253\u7b97\u81ea\u7136\u5c3d\u6570\u843d\u7a7a\uff0c\u5982\u4eca\u9000\u4e86\u56de\u53bb\uff0c\u5148\u5fd9\u4e8e\u5de9\u56fa\u5df2\u6709\u5730\u76d8\u518d\u8bf4\u3002\u636e\u8fea\u74e6\u514b\u542c\u5230\u7684\u98ce\u58f0\uff0c\u56e0\u4e3a\u6269\u5f20\u592a\u5feb\uff0c\u5175\u529b\u4e0d\u8db3\uff0c\u6253\u4e0b\u7684\u51e0\u4e2a\u57ce\u5e02\u53c8\u6709\u4e9b\u4e0d\u7a33\u8ff9\u8c61\uff0c\u96f6\u661f\u53db\u4e71\u65f6\u6709\u53d1\u751f\u3002</p><p>\u201c\u8fd9\u4e00\u6218\u6709\u5f97\u6253\u4e86\u3002\u201d\u743c\u6069\u8bf4\u3002</p><p>\u5728\u5e7d\u6697\u5730\u57df\u5446\u5f97\u4e45\u4e86\uff0c\u8d8a\u53d1\u89c9\u5f97\u6b64\u5730\u60c5\u52bf\u590d\u6742\uff0c\u9634\u9b42\u57ce\u8fd9\u6b21\u80fd\u591f\u8fdb\u5c55\u5982\u6b64\u987a\u5229\uff0c\u56fa\u7136\u662f\u81ea\u5df1\u5b9e\u529b\u5f3a\u5927\uff0c\u66f4\u91cd\u8981\u7684\u5374\u662f\u7f57\u4e1d\u7a81\u7136\u6c89\u5bc2\uff0c\u8d81\u865a\u800c\u5165\u3002\u5982\u4eca\u5bf9\u624b\u7f13\u8fc7\u52b2\u6765\uff0c\u53ea\u6015\u4fbf\u6709\u5f97\u7ea0\u7f20\u4e86\u3002</p><p>\u597d\u5728\u81ea\u5df1\u6240\u5904\u4e4b\u5730\u9887\u4e3a\u5b89\u5168\uff0c\u8fd9\u4e00\u5927\u7247\u533a\u57df\uff0c\u53ea\u6709\u74dc\u7406\u5fb7\u65af\u548c\u4f0a\u5361\u6c99\u57ce\u4e24\u5bb6\u8f83\u5f3a\uff0c\u5176\u4f59\u867d\u7136\u4e5f\u6709\u4e9b\u5c0f\u57ce\u5e02\uff0c\u90fd\u788c\u788c\u4e0d\u8db3\u9053\uff0c\u65e0\u9700\u5728\u610f\uff0c\u800c\u4e14\u4e5f\u90fd\u662f\u874e\u8eab\u602a\u3001\u5bc7\u6d9b\u9c7c\u4eba\u4e4b\u6d41\u5360\u636e\uff0c\u5927\u591a\u4e0d\u548c\u5916\u754c\u6765\u5f80\u3002\u4e00\u65f6\u534a\u523b\uff0c\u6218\u706b\u662f\u6ce2\u53ca\u4e0d\u8fc7\u6765\u7684\u3002</p><p>\u4f5c\u4e3a\u9634\u9b42\u57ce\u7684\u76df\u53cb\uff0c\u6c99\u739b\u65af\u5012\u662f\u7a33\u82e5\u78d0\u77f3\uff0c\u53cd\u6b63\u5b83\u672c\u6765\u5c31\u662f\u5deb\u5e08\u6267\u653f\uff0c\u7267\u5e08\u6ca1\u6709\u5730\u4f4d\uff0c\u7f57\u4e1d\u662f\u6c89\u5bc2\u4e5f\u597d\uff0c\u662f\u590d\u82cf\u4e5f\u7f62\uff0c\u90fd\u6ca1\u4ec0\u4e48\u5f71\u54cd\uff0c\u53ea\u5f53\u4f5c\u4e0e\u5df1\u65e0\u5173\u3002\u4e0d\u4ec5\u5982\u6b64\uff0c\u4e3a\u4e86\u4ea4\u901a\u548c\u8054\u7edc\u65b9\u4fbf\uff0c\u9634\u9b42\u57ce\u548c\u6c99\u739b\u65af\u57ce\u7684\u5deb\u5e08\u4eec\u8054\u624b\uff0c\u5bfb\u627e\u5230\u51e0\u4e2a\u5730\u8109\u8f90\u5c04\u7684\u76f2\u70b9\uff0c\u5229\u7528\u5b83\u5efa\u7acb\u8d77\u6c38\u4e45\u4f20\u9001\u95e8\uff0c\u4f5c\u4e3a\u4e2d\u8f6c\u7ad9\uff0c\u628a\u9634\u9b42\u57ce\u3001\u5965\u56fe\u57ce\u548c\u6c99\u739b\u65af\u57ce\u8054\u6210\u4e86\u4e00\u7ebf\uff0c\u5927\u5927\u8282\u7701\u4e86\u8d76\u8def\u7684\u65f6\u95f4\u3002\u8fea\u74e6\u514b\u4e4b\u6240\u4ee5\u80fd\u591f\u6bd4\u9884\u671f\u65f6\u95f4\u66f4\u65e9\u8d76\u56de\u6765\uff0c\u4e5f\u6b63\u662f\u8fd9\u4e2a\u7f18\u6545\u3002</p><p>\u9664\u4e86\u8fd9\u4ef6\u4e8b\uff0c\u5176\u4ed6\u591a\u662f\u4e9b\u95f2\u6742\u4f20\u95fb\uff0c\u548c\u81ea\u5df1\u5173\u7cfb\u90fd\u4e0d\u5927\uff0c\u743c\u6069\u4e5f\u6ca1\u592a\u5728\u610f\uff0c\u6b63\u60f3\u95ee\u81ea\u5df1\u7ed9\u73ca\u5609\u7684\u5bb6\u4e66\u6709\u6ca1\u6709\u9001\u5230\uff0c\u7a81\u7136\u542c\u89c1\u8fea\u74e6\u514b\u8bf4\u201c\u4ece\u5730\u8868\u4f20\u6765\u7684\u6d88\u606f\uff0c\u8bf4\u662f\u53bb\u5e74\u6df1\u79cb\uff0c\u9ed1\u6697\u541b\u4e3b\u73ed\u6069\u590d\u6d3b\u4e86\u3002\u201d</p><p>\u743c\u6069\u5012\u7f62\u4e86\uff0c\u6885\u83f2\u65af\u95fb\u8a00\u5374\u8eab\u4f53\u8f7b\u8f7b\u4e00\u9707\uff0c\u663e\u7136\u5341\u5206\u60ca\u8bb6\uff0c\u8ffd\u95ee\u5177\u4f53\u8be6\u60c5\uff0c\u8fea\u74e6\u514b\u5374\u4e5f\u8bf4\u4e0d\u6e05\u695a\u3002\u4ed6\u672c\u6765\u5c31\u662f\u4f8f\u5112\uff0c\u53c8\u4e0d\u4fe1\u4ef0\u4eba\u7c7b\u795e\u660e\uff0c\u867d\u7136\u73ed\u6069\u590d\u6d3b\u662f\u4e00\u4ef6\u5927\u4e8b\uff0c\u4e0e\u4ed6\u800c\u8a00\u4e5f\u4e0d\u7b97\u4ec0\u4e48\uff0c\u53ea\u662f\u542c\u8bf4\u70b9\u96f6\u788e\u6d88\u606f\uff0c\u968f\u53e3\u8bf4\u8d77\u800c\u5df2\u3002</p><p>\u201c\u600e\u4e48\u4e86\uff0c\u201d\u743c\u6069\u6709\u4e9b\u4e0d\u89e3\uff0c\u201c\u795e\u7947\u590d\u6d3b\uff0c\u5f88\u532a\u5937\u6240\u601d\u5417\uff1f\u201d</p><p>\u6885\u83f2\u65af\u8f7b\u8f7b\u6447\u5934\uff0c\u201c\u590d\u6d3b\u7684\u662f\u73ed\u6069\u554a\u3002\u201d</p><p>\u201c\u73ed\u6069\u53c8\u600e\u4e48\u2026\u2026\u201d</p><p>\u8bdd\u8bf4\u5230\u4e00\u534a\uff0c\u743c\u6069\u7ec8\u4e8e\u60f3\u4e86\u8d77\u6765\uff0c\u8fd9\u73ed\u6069\u548c\u7c73\u5c14\u5bc7\u3001\u5df4\u5c14\u5e76\u79f0\u6b7b\u4ea1\u4e09\u795e\uff0c\u636e\u8bf4\u5173\u7cfb\u5bc6\u5207\uff0c\u90fd\u662f\u5728\u52a8\u8361\u5e74\u4ee3\u4e2d\u88ab\u6467\u6bc1\u4e86\u3002\u5982\u4eca\u73ed\u6069\u65e2\u7136\u590d\u6d3b\uff0c\u90a3\u53e6\u5916\u4e24\u4f4d\u795e\u7947\u5462\uff1f\u4f1a\u4e0d\u4f1a\u4e5f\u968f\u4e4b\u590d\u6d3b\uff1f\u7c73\u5c14\u5bc7\u5c31\u7f62\u4e86\uff0c\u743c\u6069\u6ca1\u5174\u8da3\u7406\u776c\uff0c\u53ef\u662f\u5df4\u5c14\u2026\u2026</p><p>\u636e\u8bf4\u5df4\u5c14\u5728\u52a8\u8361\u5e74\u4ee3\u7559\u4e0b\u6d77\u91cf\u7684\u5b50\u55e3\uff0c\u4e3a\u7684\u5c31\u662f\u6709\u671d\u4e00\u65e5\u501f\u6b64\u590d\u6d3b\uff0c\u5982\u4eca\u7edd\u5927\u90e8\u5206\u795e\u529b\u90fd\u5728\u6885\u83f2\u65af\u4f53\u5185\u2014\u2014\u4e5f\u5c31\u662f\u8bf4\uff0c\u5982\u679c\u5df4\u5c14\u771f\u8981\u590d\u6d3b\u7684\u8bdd\uff0c\u90a3\u8fd9\u4ef6\u4e8b\u80af\u5b9a\u4f1a\u7740\u843d\u5728\u6885\u83f2\u65af\u8eab\u4e0a\uff0c\u8fd9\u53ef\u5c31\u9ebb\u70e6\u5927\u4e86\u3002</p><p>\u5982\u679c\u80fd\u77e5\u9053\u73ed\u6069\u590d\u6d3b\u7684\u8be6\u60c5\uff0c\u4ece\u4e2d\u53c2\u7167\uff0c\u672a\u5c1d\u4e0d\u80fd\u9884\u4f5c\u51c6\u5907\u3002\u8fea\u74e6\u514b\u867d\u7136\u4e0d\u6e05\u695a\uff0c\u4f46\u540c\u6765\u7684\u8fd8\u6709\u4e03\u516b\u4e2a\u9634\u9b42\u57ce\u7684\u5deb\u5e08\uff0c\u603b\u8be5\u77e5\u9053\u5f97\u591a\u4e9b\u3002\u5982\u4eca\u4ed6\u4eec\u65c5\u9014\u52b3\u7d2f\uff0c\u65e9\u5df2\u7ecf\u4f11\u606f\uff0c\u7b49\u627e\u673a\u4f1a\u518d\u8be2\u95ee\u4fbf\u662f\u3002</p><p>\u4e3b\u610f\u5b9a\u4e86\uff0c\u4e5f\u5c31\u4e0d\u518d\u591a\u60f3\u3002\u8fea\u74e6\u514b\u53c8\u4ece\u6000\u4e2d\u53d6\u51fa\u4e00\u5c01\u4fe1\u6765\uff0c\u8bf4\u5bb6\u4e66\u5df2\u7ecf\u9001\u5230\uff0c\u8fd9\u662f\u73ca\u5609\u7684\u56de\u4fe1\u3002\u743c\u6069\u62c6\u5f00\u770b\u4e86\uff0c\u4e5f\u6ca1\u6709\u7279\u522b\u4e4b\u8bed\uff0c\u7b80\u7b80\u5355\u5355\u5199\u4e86\u4e24\u9875\u7eb8\uff0c\u8bf4\u4e86\u4e9b\u8fd1\u6765\u60c5\u51b5\uff0c\u671f\u671b\u4ed6\u65e9\u4e9b\u56de\u5bb6\u3002\u6b64\u5916\u7ed9\u6885\u83f2\u65af\u548c\u8299\u857e\u72c4\u4e5f\u5206\u522b\u6709\u4e00\u5c01\u4fe1\uff0c\u743c\u6069\u53d1\u73b0\u5012\u6bd4\u7ed9\u81ea\u5df1\u7684\u8fd9\u5c01\u66f4\u539a\u4e0a\u51e0\u5206\uff0c\u9887\u6709\u4e9b\u597d\u5947\u5185\u5bb9\uff0c\u4f46\u4e24\u4eba\u90fd\u4e0d\u80af\u900f\u9732\uff0c\u4e5f\u5c31\u7f62\u4e86\u3002</p><p>\u8fea\u74e6\u514b\u540c\u65f6\u8fd8\u5e26\u56de\u4e86\u96c5\u8fbe\u738b\u5b50\u7684\u59d4\u4efb\u72b6\uff0c\u5176\u4e2d\u5bf9\u743c\u6069\u7684\u529f\u7ee9\u5927\u52a0\u79f0\u8d5e\uff0c\u9887\u591a\u6ea2\u7f8e\u4e4b\u8bcd\uff0c\u6700\u540e\u628a\u4ed6\u7684\u519b\u8854\u63d0\u5347\u4e3a\u4e0a\u5c09\u3002\u867d\u8bf4\u7b2c\u4e8c\u8fdc\u5f81\u5e08\u8349\u521b\uff0c\u6b63\u662f\u7528\u4eba\u4e4b\u9645\uff0c\u743c\u6069\u6b64\u6b21\u4e5f\u7b97\u7acb\u6709\u5927\u529f\uff0c\u4f46\u664b\u5347\u901f\u5ea6\u5982\u6b64\u4e4b\u5feb\uff0c\u5374\u4e5f\u662f\u7f55\u89c1\u7684\u3002\u53ea\u662f\u519b\u8854\u867d\u7136\u5347\u4e86\uff0c\u5b9e\u9645\u7684\u597d\u5904\u5374\u4f9d\u65e7\u6ca1\u6709\u534a\u70b9\uff0c\u624b\u4e0b\u65e0\u5175\u65e0\u5c06\uff0c\u53ea\u53d1\u4e86\u4e00\u70b9\u519b\u9977\uff0c\u4e00\u5207\u5168\u90fd\u8981\u81ea\u5df1\u89e3\u51b3\u3002</p><p>\u201c\u65e2\u7136\u73b0\u5728\u5df2\u7ecf\u662f\u4e0a\u5c09\u4e86\uff0c\u5149\u6746\u53f8\u4ee4\u603b\u662f\u96be\u770b\uff0c\u827e\u5f25\u8587\u8bf4\u5f97\u4e0d\u9519\uff0c\u4e5f\u662f\u8981\u57f9\u690d\u4e9b\u81ea\u5df1\u7684\u52bf\u529b\uff0c\u201d\u743c\u6069\u601d\u5fd6\u7740\uff0c\u201c\u53ea\u662f\u8fd9\u58eb\u5175\u4ece\u54ea\u91cc\u6765\u5462\u3002\u201d</p><p>\u9634\u9b42\u57ce\u539f\u672c\u538b\u6839\u6ca1\u6709\u7b2c\u4e8c\u8fdc\u5f81\u5e08\uff0c\u662f\u4e3a\u4e86\u8fd9\u6b21\u653b\u6253\u5e7d\u6697\u5730\u57df\u4e34\u65f6\u521b\u7acb\u51fa\u6765\u7684\uff0c\u9aa8\u5e72\u662f\u838e\u5c14\u795e\u6bbf\u6d3e\u7cfb\uff0c\u5305\u62ec\u7267\u5e08\u3001\u6697\u6708\u6b66\u50e7\u7b49\uff0c\u5404\u961f\u7684\u7f16\u5236\u5176\u5b9e\u90fd\u6709\u5f88\u591a\u7a7a\u989d\uff0c\u53ea\u8981\u519b\u5b98\u6709\u80fd\u529b\u81ea\u884c\u8865\u5145\uff0c\u4e0a\u5934\u81ea\u7136\u4e0d\u4f1a\u5e72\u6d89\uff0c\u53cd\u800c\u4f1a\u52a0\u4ee5\u8ba4\u53ef\uff0c\u7167\u62e8\u7cae\u9977\uff0c\u5f88\u5bb9\u6613\u9020\u6210\u201c\u5175\u4e3a\u5c06\u6709\u201d\u7684\u683c\u5c40\u3002\u743c\u6069\u8eab\u5728\u6b64\u5904\uff0c\u5c71\u9ad8\u7687\u5e1d\u8fdc\uff0c\u5c31\u7b97\u62db\u4e0a\u51e0\u5343\u58eb\u5175\uff0c\u9634\u9b42\u57ce\u4f30\u8ba1\u4e00\u65f6\u4e5f\u6ca1\u529f\u592b\u7406\u776c\u2014\u2014\u95ee\u9898\u53ea\u5728\u4e8e\uff0c\u5175\u4ece\u54ea\u91cc\u6765\u5462\u3002</p><p>\u4f0a\u5361\u6c99\u57ce\u7684\u77ee\u4eba\uff1f\u8fd9\u4e2a\u5ff5\u5934\u7acb\u523b\u88ab\u5426\u51b3\u4e86\uff0c\u81ea\u5df1\u53ea\u6015\u8fd8\u8981\u5728\u8fd9\u91cc\u5446\u5f88\u957f\u4e00\u6bb5\u65f6\u95f4\uff0c\u6316\u4e3b\u4eba\u7684\u5899\u89d2\u5c82\u4e0d\u662f\u81ea\u627e\u9ebb\u70e6\u3002\u5e7d\u6697\u5730\u57df\u91cc\u7684\u751f\u7269\u867d\u7136\u591a\uff0c\u5374\u5927\u591a\u90fd\u4e0d\u662f\u826f\u5584\u4e4b\u8f88\uff0c\u5730\u7cbe\u72d7\u5934\u4eba\u4e4b\u6d41\u743c\u6069\u770b\u4e0d\u4e0a\u773c\uff0c\u773c\u9b54\u7075\u5438\u602a\u4e4b\u8f88\u53c8\u9a7e\u9a6d\u4e0d\u4f4f\uff0c\u81f3\u4e8e\u5353\u5c14\u81ea\u7136\u5c31\u66f4\u4e0d\u7528\u63d0\u4e86\u2026\u2026</p><p>\u201c\u672a\u5fc5\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u5982\u679c\u771f\u8981\u62db\u63fd\u5c5e\u4e0b\uff0c\u5176\u5b9e\u5353\u5c14\u4e5f\u662f\u5f88\u9002\u5408\u7684\u4eba\u9009\u3002\u201d</p><p>\u201c\u4f60\u5728\u5f00\u73a9\u7b11\u5427\u3002\u201d</p><p>\u201c\u4f60\u89c9\u5f97\u6211\u50cf\u662f\u5728\u5f00\u73a9\u7b11\u5417\uff1f\u201d</p><p>\u786e\u5b9e\u4e0d\u50cf\uff0c\u6885\u83f2\u65af\u5e76\u4e0d\u662f\u4e0d\u4f1a\u5f00\u73a9\u7b11\u2014\u2014\u4f46\u51b3\u4e0d\u4f1a\u5f00\u8fd9\u79cd\u6ca1\u6709\u6280\u672f\u542b\u91cf\u7684\u73a9\u7b11\uff0c\u5979\u65e2\u7136\u8fd9\u4e48\u8bf4\uff0c\u90a3\u5c31\u5fc5\u5b9a\u6709\u5176\u9053\u7406\u3002</p><p>\u201c\u600e\u4e48\u8bf4\uff1f\u201d\u743c\u6069\u95ee\u3002</p></article>"}}}]);
