(window.webpackJsonp=window.webpackJsonp||[]).push([[292],{314:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u516b\u8282 \u4ea1\u7075",content:"<article><h2>\u7b2c\u516b\u8282 \u4ea1\u7075</h2><p>\u743c\u6069\u7528\u6cd5\u6756\u5728\u7a7a\u6c14\u4e2d\u5feb\u901f\u5212\u4e86\u4e2a\u7b26\u53f7\uff0c\u5927\u7406\u77f3\u76ae\u80a4\u8fc5\u901f\u8513\u5ef6\u6574\u4e2a\u8eab\u4f53\uff0c\u5c06\u4ed6\u7262\u7262\u4fdd\u62a4\u8d77\u6765\uff0c\u7d27\u63a5\u7740\u53c8\u521b\u9020\u4e86\u4e00\u9762\u9690\u5f62\u529b\u573a\u62a4\u76fe\u73af\u7ed5\u5468\u8eab\u3002\u5f53\u5b8c\u6210\u8fd9\u4e24\u4e2a\u6cd5\u672f\u4e4b\u540e\uff0c\u4ed6\u5b9a\u4e0b\u795e\u6765\uff0c\u4fa7\u8fc7\u8138\u671d\u7740\u521a\u624d\u94f6\u8272\u957f\u7bad\u5c04\u51fa\u7684\u65b9\u5411\u770b\u53bb\uff0c\u963f\u5fd2\u59ae\u6b63\u85cf\u8eab\u4e8e\u4e00\u5757\u7a81\u51fa\u7684\u5ca9\u77f3\u4e4b\u540e\uff0c\u4e00\u624b\u6267\u9ed1\u8272\u77ed\u5f13\uff0c\u4e00\u624b\u4ece\u80cc\u540e\u7bad\u56ca\u91cc\u62bd\u51fa\u94f6\u8272\u957f\u7bad\uff0c\u642d\u5728\u5f26\u4e0a\uff0c\u6b63\u51c6\u5907\u518d\u6b21\u5c04\u51fa\u3002</p><p>\u6885\u83f2\u65af\u6ca1\u6709\u56de\u5934\uff0c\u4f46\u542c\u52a8\u9759\u4fbf\u77e5\u9053\u743c\u6069\u5df2\u7ecf\u5b8c\u6210\u4e86\u5bf9\u81ea\u5df1\u7684\u4fdd\u62a4\uff0c\u201c\u5c0f\u5fc3\u3002\u201d\u5979\u4f4e\u58f0\u8bf4\uff0c\u624b\u5f80\u8170\u95f4\u4e00\u6309\uff0c\u518d\u6325\u51fa\u6765\u7684\u65f6\u5019\u638c\u4e2d\u5df2\u7ecf\u63e1\u4f4f\u4e86\u94f6\u5251\uff0c\u8eab\u5f62\u50cf\u8c79\u5b50\u822c\u5f80\u524d\u5f39\u51fa\uff0c\u9aa4\u7136\u52a0\u901f\uff0c\u671d\u77f3\u5d16\u4e0a\u7684\u5077\u88ad\u8005\u51b2\u53bb\u3002</p><p>\u77f3\u5d16\u4e0d\u9ad8\uff0c\u6885\u83f2\u65af\u51b2\u5230\u8ddf\u524d\uff0c\u501f\u52bf\u4e00\u8dc3\u800c\u8d77\uff0c\u4fbf\u8e0f\u4e86\u4e0a\u53bb\u3002\u95ea\u5149\u5f39\u548c\u963f\u5fd2\u59ae\u7cbe\u51c6\u7684\u5c04\u51fb\u8ba9\u8fd8\u5269\u4e0b\u6765\u7684\u4e09\u4e2a\u5077\u88ad\u8005\u9677\u5165\u77ed\u6682\u7684\u614c\u4e71\uff0c\u4f46\u8f6c\u773c\u4e4b\u95f4\u4ed6\u4eec\u5c31\u9547\u5b9a\u4e0b\u6765\uff0c\u6709\u6761\u4e0d\u7d0a\u5730\u5c55\u5f00\u53cd\u51fb\u3002\u4e24\u4e2a\u5353\u5c14\u6325\u821e\u77ed\u5251\u5de6\u53f3\u5939\u653b\u6885\u83f2\u65af\uff0c\u4ed6\u4eec\u90fd\u7a7f\u7740\u76ae\u7532\uff0c\u8138\u4e0a\u6234\u7740\u6837\u5f0f\u5947\u7279\u7684\u7070\u8272\u9762\u5177\uff0c\u8eab\u624b\u654f\u6377\uff0c\u800c\u4e14\u663e\u7136\u7ecf\u5e38\u5171\u540c\u5bf9\u654c\uff0c\u914d\u5408\u5f97\u5341\u5206\u9ed8\u5951\u3002\u6885\u83f2\u65af\u94f6\u5251\u8fde\u6325\uff0c\u6b65\u6b65\u8fdb\u903c\uff0c\u867d\u7136\u7565\u5360\u4e0a\u98ce\uff0c\u4e00\u65f6\u4e4b\u95f4\u5374\u4e5f\u4e0d\u80fd\u53d6\u80dc\u3002\u7b2c\u4e09\u4e2a\u5353\u5c14\u8d81\u673a\u540e\u9000\uff0c\u62ac\u8d77\u624b\u4e0a\u7684\u91cd\u5f29\u671d\u7740\u743c\u6069\u53c8\u5c04\u51fa\u4e00\u77e2\u3002</p><p>\u5148\u89e3\u51b3\u5deb\u5e08\uff0c\u8fd9\u662f\u5e38\u8bc6\u3002</p><p>\u94c1\u77e2\u7834\u7a7a\u5c04\u6765\uff0c\u8fd9\u6b21\u901f\u5ea6\u51cc\u5389\u5f97\u591a\uff0c\u7830\u5730\u51fb\u788e\u4e86\u529b\u573a\u62a4\u76fe\uff0c\u743c\u6069\u8eb2\u95ea\u4e0d\u53ca\uff0c\u6b63\u88ab\u51fb\u4e2d\u80f8\u53e3\uff0c\u7206\u51fa\u51e0\u9897\u706b\u661f\uff0c\u4f46\u5e76\u6ca1\u80fd\u591f\u7a7f\u900f\u77f3\u80a4\u3002\u743c\u6069\u9000\u4e86\u4e24\u6b65\uff0c\u611f\u89c9\u6709\u4e9b\u80f8\u95f7\uff0c\u5410\u4e86\u53e3\u6c14\uff0c\u6cd5\u6756\u62ac\u8d77\uff0c\u6700\u9876\u7aef\u7684\u7eef\u7ea2\u4e4b\u6cea\u5b9d\u77f3\u7a81\u7136\u7206\u51fa\u4eae\u5149\uff0c\u201c\u53d8\u5f62\uff01\u201d</p><p>\u5353\u5c14\u6b63\u51c6\u5907\u518d\u5ea6\u7ed9\u91cd\u5f29\u4e0a\u77e2\uff0c\u9661\u7136\u8eab\u4f53\u4e00\u9707\uff0c\u50cf\u662f\u88ab\u65e0\u5f62\u7684\u5927\u624b\u7528\u529b\u634f\u4f4f\u4e00\u822c\uff0c\u731b\u5730\u538b\u7f29\u8d77\u6765\uff0c\u4f46\u8fc7\u4e86\u534a\u79d2\u949f\u53c8\u5f39\u56de\u539f\u72b6\uff0c\u62b5\u5fa1\u4f4f\u4e86\u8fd9\u4e2a\u6cd5\u672f\u3002\u743c\u6069\u7565\u7565\u6709\u4e9b\u8bb6\u5f02\uff0c\u4ed6\u8fd9\u51e0\u4e2a\u6708\u5728\u9b54\u6cd5\u7814\u4e60\u4e0a\u5e76\u672a\u677e\u61c8\uff0c\u867d\u7136\u8fd8\u4e0d\u80fd\u89e6\u6478\u66f4\u9ad8\u5c42\u9b54\u7f51\uff0c\u4f46\u5728\u53d8\u5f62\u8fd9\u4e2a\u6cd5\u672f\u4e0a\u5374\u662f\u4e0b\u4e86\u5927\u529f\u592b\uff0c\u81ea\u89c9\u6bd4\u4ee5\u5f80\u9887\u6709\u7cbe\u8fdb\uff0c\u6ca1\u60f3\u5230\u5c45\u7136\u8fd8\u662f\u5931\u624b\u3002</p><p>\u201c\u5353\u5c14\u7684\u5929\u8d4b\u6297\u9b54\u80fd\u529b\u679c\u7136\u4e0d\u540c\u51e1\u54cd\u3002\u201d</p><p>\u867d\u7136\u5fc3\u4e2d\u5982\u6b64\u611f\u53f9\uff0c\u4f46\u65bd\u6cd5\u52a8\u4f5c\u534a\u70b9\u4e0d\u505c\u3002\u5bf9\u624b\u4f7f\u7528\u7684\u662f\u91cd\u5f29\uff0c\u867d\u7136\u5a01\u529b\u5de8\u5927\uff0c\u4f46\u5374\u5fc5\u987b\u901a\u8fc7\u7ede\u8f6e\u624d\u80fd\u4e0a\u77e2\uff0c\u53d1\u5c04\u901f\u5ea6\u5f88\u7f13\u6162\u3002\u743c\u6069\u8d81\u673a\u518d\u5ea6\u65bd\u5c55\u4e0b\u4e00\u4e2a\u6cd5\u672f\uff0c\u5982\u679c\u6210\u529f\u7684\u8bdd\u4f1a\u628a\u5bf9\u624b\u6682\u65f6\u53d8\u6210\u4e00\u6ee9\u8f6f\u6ce5\uff0c\u5c31\u50cf\u62bd\u6389\u4e86\u6240\u6709\u7684\u9aa8\u9abc\u4f3c\u7684\uff0c\u4f9d\u65e7\u80fd\u884c\u52a8\uff0c\u4f46\u5374\u65e0\u6cd5\u518d\u653b\u51fb\u4ed6\u4eba\u3002\u8fd9\u662f\u5353\u5c14\u53d1\u660e\u7684\u6cd5\u672f\uff0c\u4ed6\u5728\u74dc\u7406\u5fb7\u65af\u57ce\u65f6\u5b66\u4f1a\u7684\u3002</p><p>\u4e24\u79d2\u949f\u4e4b\u540e\uff0c\u743c\u6069\u5b8c\u6210\u4e86\u6cd5\u672f\uff0c\u6b63\u8981\u91ca\u653e\u51fa\u53bb\uff0c\u5c16\u9510\u7684\u91d1\u5c5e\u7834\u7a7a\u58f0\u518d\u5ea6\u54cd\u8d77\uff0c\u94f6\u8272\u957f\u7bad\u95ea\u7535\u822c\u5c04\u6765\uff0c\u518d\u4e00\u6b21\u51c6\u786e\u5730\u547d\u4e2d\u654c\u4eba\u7684\u54bd\u5589\uff0c\u521a\u521a\u628a\u94c1\u77e2\u5b89\u4e0a\u91cd\u5f29\u7684\u5353\u5c14\u7ffb\u8eab\u5012\u5730\u3002</p><p>\u963f\u5fd2\u59ae\u770b\u51c6\u65f6\u673a\uff0c\u4e00\u51fb\u5f97\u624b\uff0c\u968f\u5373\u6536\u8d77\u77ed\u5f13\uff0c\u62d4\u51fa\u8170\u95f4\u94f6\u8272\u77ed\u5251\u4e5f\u671d\u77f3\u5d16\u4e0a\u51b2\u53bb\u3002\u6885\u83f2\u65af\u5df2\u7ecf\u628a\u5269\u4e0b\u4e24\u4e2a\u5353\u5c14\u903c\u5f97\u6b65\u6b65\u540e\u9000\uff0c\u4f46\u4e00\u65f6\u8fd8\u96be\u4ee5\u6740\u6b7b\u5bf9\u624b\uff0c\u4e09\u4eba\u6df7\u6218\u6210\u4e00\u56e2\uff0c\u963f\u5fd2\u59ae\u4e5f\u4e0d\u6562\u653e\u7bad\uff0c\u7d22\u6027\u81ea\u5df1\u51b2\u4e86\u4e0a\u6765\u3002</p><p>\u7cbe\u7075\u7d20\u6765\u4ee5\u64c5\u957f\u5251\u672f\u548c\u5f13\u7bad\u8457\u79f0\uff0c\u800c\u4f0a\u8389\u4e1d\u7fe0\u5219\u662f\u5353\u5c14\u7684\u821e\u8e48\u5973\u795e\uff0c\u540c\u65f6\u4e5f\u662f\u72e9\u730e\u4e0e\u5251\u672f\u5973\u795e\u2014\u2014\u963f\u5fd2\u59ae\u65e2\u662f\u7cbe\u7075\uff0c\u53c8\u662f\u4f0a\u8389\u4e1d\u7fe0\u7267\u5e08\uff0c\u9664\u4e86\u5728\u5f13\u7bad\u4e0a\u6709\u767e\u6b65\u7a7f\u6768\u4e4b\u80fd\uff0c\u5251\u672f\u9020\u8be3\u4e5f\u4e0d\u592a\u5dee\uff0c\u6709\u5979\u76f8\u52a9\uff0c\u5c40\u52bf\u987f\u65f6\u6709\u4e86\u660e\u663e\u6539\u89c2\u3002</p><p>\u743c\u6069\u53d6\u6d88\u4e86\u5df2\u7ecf\u51c6\u5907\u5b8c\u6210\u7684\u6cd5\u672f\uff0c\u4e3a\u4e24\u4f4d\u5973\u58eb\u53e0\u52a0\u4e0a\u86ee\u725b\u4e4b\u529b\u548c\u732b\u4e4b\u4f18\u96c5\uff0c\u8fd9\u662f\u7b80\u5355\u4f46\u5374\u5b9e\u7528\u7684\u6cd5\u672f\uff0c\u80fd\u5728\u77ed\u65f6\u95f4\u5185\u5927\u5e45\u5ea6\u63d0\u5347\u529b\u6c14\u548c\u7075\u6d3b\u6027\u3002\u5deb\u5e08\u4e00\u8fb9\u719f\u7ec3\u5730\u5b8c\u6210\u6cd5\u672f\uff0c\u4e00\u8fb9\u7a81\u7136\u89c9\u5f97\u6709\u4e9b\u6ed1\u7a3d\uff0c\u867d\u8bf4\u81ea\u5df1\u6ca1\u6709\u592a\u5f3a\u7684\u6027\u522b\u6b67\u89c6\uff0c\u4f46\u4e00\u822c\u8fd8\u662f\u4f1a\u5728\u6f5c\u610f\u8bc6\u91cc\u8ba4\u4e3a\u51b2\u950b\u9677\u9635\u8fd9\u79cd\u4e8b\u60c5\u5e94\u8be5\u7531\u7537\u6027\u627f\u62c5\uff0c\u5973\u6027\u7ad9\u5728\u540e\u65b9\u624d\u662f\uff0c\u8bb0\u5f97\u4ee5\u524d\u73a9\u6e38\u620f\uff0c\u5f80\u5f80\u4e5f\u90fd\u662f\u7537\u6027\u6218\u58eb\u914d\u5973\u6027\u5deb\u5e08\uff0c\u7136\u800c\u5982\u4eca\u600e\u4e48\u60c5\u51b5\u53cd\u4e86\u8fc7\u6765\u2026\u2026</p><p>\u66f4\u51c6\u786e\u5730\u8bf4\uff0c\u662f\u81ea\u4ece\u6709\u4e86\u6885\u83f2\u65af\uff0c\u5c31\u6210\u4e86\u8fd9\u79cd\u683c\u5c40\u3002\u6bcf\u6b21\u9047\u5230\u654c\u4eba\uff0c\u5979\u603b\u662f\u51b2\u5728\u524d\u9762\uff0c\u81ea\u5df1\u5219\u6210\u4e86\u540e\u65b9\u8f85\u52a9\u4eba\u5458\u2026\u2026\u8fd9\u611f\u89c9\uff0c\u4f3c\u4e4e\u603b\u4e0d\u592a\u5bf9\u52b2\u554a\u3002</p><p>\u65e0\u6687\u53bb\u8003\u8651\u8fd9\u4e9b\uff0c\u77f3\u5d16\u4e0a\u7684\u6218\u6597\u5df2\u7ecf\u8fc5\u901f\u63a5\u8fd1\u5c3e\u58f0\u3002\u6709\u4e86\u963f\u5fd2\u59ae\u7684\u534f\u52a9\uff0c\u6885\u83f2\u65af\u5f88\u5feb\u51fb\u6740\u4e86\u4e00\u540d\u5353\u5c14\uff0c\u968f\u5373\u5c06\u53e6\u5916\u4e00\u540d\u5236\u670d\u3002\u5f53\u786e\u5b9a\u518d\u6ca1\u6709\u5176\u4ed6\u654c\u4eba\u57cb\u4f0f\u540e\uff0c\u4e09\u4eba\u805a\u96c6\u5728\u4e00\u8d77\uff0c\u5f00\u59cb\u8baf\u95ee\u4fd8\u864f\u3002</p><p>\u201c\u7ef4\u4f26\u7684\u7267\u5e08\u3002\u201d\u6885\u83f2\u65af\u626f\u4e0b\u5353\u5c14\u8138\u4e0a\u7684\u5947\u7279\u9762\u5177\uff0c\u62ff\u5728\u624b\u4e2d\u770b\u4e86\u770b\uff0c\u505a\u51fa\u5224\u65ad\u3002</p><p>\u5353\u5c14\u7684\u9634\u8c0b\u4e4b\u795e\u7ef4\u4f26\uff0c\u5176\u5b9e\u548c\u4eba\u7c7b\u7684\u9634\u5f71\u4e0e\u76d7\u8d3c\u4e4b\u795e\u9a6c\u65af\u514b\u5f88\u50cf\uff0c\u6267\u638c\u7740\u8fd1\u4f3c\u7684\u795e\u804c\uff0c\u800c\u4e14\u90fd\u559c\u6b22\u4ee5\u8499\u9762\u7684\u5f62\u8c61\u793a\u4eba\u3002\u7ef4\u4f26\u7684\u5c0a\u79f0\u662f\u8499\u9762\u541b\u738b\uff0c\u7267\u5e08\u4e5f\u5927\u591a\u4eff\u6548\uff0c\u559c\u6b22\u4ee5\u9762\u5177\u906e\u63a9\u9762\u90e8\u3002\u4ed6\u4eec\u7684\u9762\u5177\u662f\u7279\u5236\u7684\uff0c\u4f7f\u7528\u79d8\u94f6\u3001\u4e1d\u7ef8\u548c\u73bb\u7483\u6df7\u5408\u76f8\u5d4c\u800c\u6210\uff0c\u5f62\u72b6\u6709\u4e9b\u7c7b\u4f3c\u8759\u8760\uff0c\u6885\u83f2\u65af\u4ee5\u524d\u867d\u7136\u4e0d\u66fe\u89c1\u8fc7\uff0c\u5374\u6709\u8033\u95fb\uff0c\u4e00\u770b\u5c31\u8ba4\u4e86\u51fa\u6765\u3002</p><p>\u963f\u5fd2\u59ae\u4e5f\u70b9\u4e86\u70b9\u5934\uff0c\u540c\u610f\u6885\u83f2\u65af\u7684\u5224\u65ad\u3002</p><p>\u201c\u4f60\u4e0d\u8ba4\u8bc6\u4ed6\uff1f\u201d\u743c\u6069\u6709\u4e9b\u5947\u602a\u3002\u8fd9\u4e00\u7247\u533a\u57df\uff0c\u5047\u8bbe\u4ee5\u4f0a\u5361\u6c99\u57ce\u4e3a\u4e2d\u5fc3\uff0c\u65b9\u5706\u5341\u4e94\u5929\u7684\u8def\u7a0b\u4e4b\u5185\uff0c\u53ea\u6709\u74dc\u7406\u5fb7\u65af\u57ce\u4e00\u5904\u5353\u5c14\u636e\u70b9\u2014\u2014\u73b0\u5728\u52a0\u4e0a\u6668\u70bc\u57ce\u4e86\u3002\u74dc\u7406\u5fb7\u65af\u57ce\u4e2d\u5168\u662f\u7f57\u4e1d\u4fe1\u5f92\uff0c\u4ece\u4e0d\u66fe\u542c\u95fb\u6709\u7ef4\u4f26\u7267\u5e08\u6d3b\u52a8\uff0c\u8fd9\u51e0\u4e2a\u5077\u88ad\u8005\u660e\u663e\u662f\u4ece\u6668\u70bc\u57ce\u6765\u7684\uff0c\u963f\u5fd2\u59ae\u96be\u9053\u6ca1\u89c1\u8fc7\uff1f</p><p>\u201c\u4e0d\u8ba4\u8bc6\uff0c\u201d\u963f\u5fd2\u59ae\u8f7b\u8f7b\u6447\u5934\uff0c\u201c\u6211\u5176\u5b9e\u624d\u6765\u6ca1\u591a\u4e45\u3002\u201d</p><p>\u5979\u524d\u51e0\u65e5\u624d\u62b5\u8fbe\u6668\u70bc\u57ce\uff0c\u968f\u540e\u5c31\u524d\u6765\u4f0a\u5361\u6c99\u57ce\u5bfb\u6c42\u63f4\u52a9\uff0c\u5bf9\u7ef4\u4f26\u548c\u9f50\u96c5\u6e29\u7eb1\u4e3d\u7684\u4fe1\u5f92\u5e76\u4e0d\u719f\u6089\uff0c\u4f55\u51b5\u4ece\u4eba\u7c7b\u548c\u7cbe\u7075\u7684\u773c\u4e2d\u770b\u6765\uff0c\u5353\u5c14\u6837\u5b50\u957f\u5f97\u90fd\u5dee\u4e0d\u591a\uff0c\u9664\u975e\u719f\u6089\u4e86\uff0c\u5426\u5219\u5f88\u96be\u5206\u8fa8\u51fa\u6765\u3002\u4e0d\u8fc7\u4e0d\u8ba4\u8bc6\u4eba\uff0c\u4e0d\u7b49\u4e8e\u4e0d\u80fd\u5224\u5b9a\u8eab\u4efd\uff0c\u8fd9\u9020\u578b\u72ec\u7279\u7684\u9762\u5177\u5c31\u662f\u6700\u597d\u7684\u8bc1\u660e\u3002</p><p>\u4e00\u95ee\u4e4b\u4e0b\uff0c\u624d\u77e5\u9053\u7f18\u7531\u3002\u8fd9\u51e0\u4e2a\u5077\u88ad\u8005\u786e\u5b9e\u6765\u81ea\u6668\u70bc\u57ce\uff0c\u90fd\u662f\u7ef4\u4f26\u7684\u4fe1\u5f92\uff0c\u9664\u4e86\u6700\u5f00\u59cb\u88ab\u963f\u5fd2\u59ae\u5c04\u6740\u7684\u90a3\u4f4d\u662f\u4e2a\u7267\u5e08\uff0c\u5176\u4f59\u4e09\u4eba\u90fd\u662f\u6e38\u8361\u8005\u3002\u4f0a\u8389\u4e1d\u7fe0\u4fe1\u5f92\u6d3e\u9063\u963f\u5fd2\u59ae\u524d\u5f80\u4f0a\u5361\u6c99\u57ce\u6c42\u63f4\uff0c\u4e0d\u77e5\u600e\u5f97\u6d88\u606f\u8d70\u6f0f\uff0c\u88ab\u7ef4\u4f26\u548c\u9f50\u96c5\u6e29\u7eb1\u4e3d\u7684\u4fe1\u5f92\u77e5\u6653\uff0c\u4fbf\u6d3e\u4eba\u62e6\u622a\u8ffd\u6740\u3002\u7ef4\u4f26\u4fe1\u5f92\u4e2d\u591a\u6709\u6e38\u8361\u8005\uff0c\u64c5\u957f\u8ffd\u8e2a\u6697\u6740\uff0c\u539f\u672c\u5341\u62ff\u4e5d\u7a33\uff0c\u53ea\u662f\u5e7d\u6697\u5730\u57df\u91cc\u5730\u5f62\u590d\u6742\uff0c\u9053\u8def\u8fc2\u56de\uff0c\u7b80\u76f4\u4fbf\u5982\u8ff7\u5bab\u4e00\u822c\uff0c\u4e0d\u540c\u533a\u57df\u5f80\u5f80\u8fe5\u7136\u76f8\u5f02\uff0c\u5916\u6765\u4eba\u5f88\u5bb9\u6613\u8ff7\u8def\uff0c\u52a0\u4e0a\u5f97\u77e5\u6d88\u606f\u53c8\u5df2\u7ecf\u665a\u4e86\uff0c\u4fbf\u6ca1\u80fd\u8ffd\u4e0a\u963f\u5fd2\u59ae\uff0c\u773c\u7741\u7741\u770b\u7740\u5979\u8fdb\u4e86\u77ee\u4eba\u57ce\u5e02\u3002\u4f0a\u5361\u6c99\u57ce\u57ce\u5899\u9ad8\u5927\uff0c\u9632\u5b88\u4e25\u5bc6\uff0c\u8fd9\u56db\u4e2a\u5353\u5c14\u5728\u5916\u9762\u8f6c\u60a0\u4e00\u65e5\uff0c\u59cb\u7ec8\u627e\u4e0d\u5230\u673a\u4f1a\u6f5c\u5165\uff0c\u53ea\u5f97\u56de\u53bb\u590d\u547d\u3002\u4e0d\u60f3\u9634\u5dee\u9633\u9519\uff0c\u8def\u4e0a\u53c8\u649e\u4e0a\u4e86\u963f\u5fd2\u59ae\u4e00\u884c\u4eba\u3002</p><p>\u4ed6\u4eec\u6ca1\u8fdb\u4f0a\u5361\u6c99\u57ce\uff0c\u81ea\u7136\u4e5f\u4e0d\u77e5\u9053\u963f\u5fd2\u59ae\u548c\u77ee\u4eba\u4e4b\u95f4\u7684\u626f\u76ae\u7ed3\u679c\uff0c\u53ea\u770b\u89c1\u7cbe\u7075\u4e00\u4e2a\u4eba\u8fdb\u4e86\u77ee\u4eba\u57ce\u5e02\uff0c\u9694\u65e5\u51fa\u6765\u5374\u591a\u4e86\u4e24\u4e2a\u4eba\u7c7b\uff0c\u4e0d\u7531\u5f97\u6478\u4e0d\u7740\u5934\u8111\uff0c\u641e\u4e0d\u6e05\u662f\u4ec0\u4e48\u540d\u5802\u3002\u5176\u4e2d\u4e00\u4eba\u5374\u773c\u5149\u9510\u5229\uff0c\u53d1\u73b0\u4e86\u6d1b\u65af\u517d\u80cc\u8d1f\u7684\u5305\u88f9\u4e2d\u90fd\u662f\u5175\u5668\u76d4\u7532\uff0c\u5927\u7ea6\u6709\u51e0\u5341\u526f\u7684\u6a21\u6837\u3002\u4f0a\u5361\u6c99\u57ce\u7684\u953b\u9020\u4e4b\u672f\u51fa\u795e\u5165\u5316\uff0c\u6574\u4e2a\u5e7d\u6697\u5730\u57df\u90fd\u662f\u95fb\u540d\u7684\uff0c\u4ed6\u4eec\u5c82\u4f1a\u4e0d\u77e5\uff0c\u770b\u773c\u524d\u8fd9\u60c5\u5f62\uff0c\u77ee\u4eba\u5c31\u7b97\u6ca1\u6709\u76f4\u63a5\u51fa\u5175\uff0c\u81f3\u5c11\u4e5f\u5df2\u7ecf\u662f\u503e\u5411\u4e8e\u5e2e\u52a9\u4f0a\u8389\u4e1d\u7fe0\u4fe1\u5f92\u4e86\uff0c\u5018\u82e5\u963f\u5fd2\u59ae\u771f\u628a\u8fd9\u4e9b\u5175\u5668\u76d4\u7532\u9001\u56de\u53bb\uff0c\u90a3\u5df1\u65b9\u76ee\u524d\u4ec5\u5b58\u7684\u4e00\u70b9\u4f18\u52bf\uff0c\u4e5f\u5c06\u4f1a\u8361\u7136\u65e0\u5b58\u4e86\u3002</p><p>\u8fd9\u56db\u4e2a\u5353\u5c14\u4e2d\u7684\u7267\u5e08\u9996\u9886\u5f53\u673a\u7acb\u65ad\uff0c\u7387\u9886\u624b\u4e0b\u62a2\u5148\u57cb\u4f0f\u5728\u8fd9\u77f3\u5d16\u4e0a\uff0c\u51c6\u5907\u6740\u4eba\u8d8a\u8d27\u3002\u4f5c\u4e3a\u90aa\u6076\u8005\uff0c\u4ed6\u4eec\u81ea\u6709\u90aa\u6076\u8005\u7684\u81ea\u89c9\uff0c\u4e0d\u4f1a\u5929\u771f\u5730\u4ee5\u4e3a\u4e5f\u80fd\u4eff\u6548\u751a\u81f3\u4ee3\u66ff\u4f0a\u8389\u4e1d\u7fe0\u4fe1\u5f92\u53bb\u548c\u77ee\u4eba\u5546\u8c08\u7ed3\u76df\uff0c\u4f46\u5982\u679c\u80fd\u62a2\u5230\u8fd9\u6279\u5175\u7532\uff0c\u90a3\u81f3\u5c11\u80fd\u5927\u5927\u589e\u5f3a\u5b9e\u529b\uff0c\u603b\u662f\u4e0d\u4f1a\u9519\u7684\u3002</p><p>\u201c\u5148\u5bf9\u4ed8\u90a3\u4e2a\u7537\u6027\uff0c\u4ed6\u770b\u8d77\u6765\u50cf\u4e2a\u5deb\u5e08\u3002\u201d</p><p>\u201c\u4ed6\u65c1\u8fb9\u7a7f\u94f6\u8272\u76ae\u7532\u7684\u5973\u4eba\u597d\u50cf\u4e0d\u5bb9\u6613\u5bf9\u4ed8\u2026\u2026\u201d</p><p>\u201c\u4e0d\u7528\u62c5\u5fc3\uff0c\u5979\u8fde\u6b66\u5668\u90fd\u6ca1\u62ff\u3002\u201d</p><p>\u4ee5\u56db\u5bf9\u4e09\uff0c\u53c8\u662f\u57cb\u4f0f\u5077\u88ad\uff0c\u539f\u672c\u9884\u6599\u662f\u6ca1\u6709\u4ec0\u4e48\u95ee\u9898\uff0c\u4f55\u51b5\u5728\u5730\u5e95\u8fd9\u79cd\u73af\u5883\uff0c\u5353\u5c14\u81ea\u4fe1\u600e\u4e48\u4e5f\u6bd4\u4e24\u4e2a\u4eba\u7c7b\u548c\u4e00\u4e2a\u7cbe\u7075\u66f4\u5360\u4f18\u52bf\u3002\u56e0\u4e3a\u770b\u7740\u743c\u6069\u660e\u663e\u662f\u4e2a\u5deb\u5e08\uff0c\u9996\u5148\u5c31\u671d\u4ed6\u653b\u51fb\uff0c\u5374\u4e0d\u60f3\u5c45\u7136\u88ab\u6885\u83f2\u65af\u6321\u4e86\u4e0b\u6765\uff0c\u5012\u662f\u5927\u5927\u51fa\u4e4e\u610f\u6599\u3002</p><p>\u5176\u5b9e\u8981\u771f\u8bba\u8d77\u6765\uff0c\u8fd9\u4e09\u4eba\u5f53\u4e2d\uff0c\u6885\u83f2\u65af\u81ea\u4e0d\u7528\u8bf4\uff0c\u4fbf\u662f\u963f\u5fd2\u59ae\u5f13\u7bad\u5728\u624b\uff0c\u4e5f\u6709\u767e\u6b65\u7a7f\u6768\u4e4b\u80fd\uff0c\u743c\u6069\u867d\u7136\u662f\u5deb\u5e08\uff0c\u53cd\u5012\u6700\u4e0d\u5177\u6709\u5a01\u80c1\uff0c\u56e0\u4e3a\u4ed6\u64c5\u957f\u7684\u662f\u53d8\u5316\u6cd5\u672f\uff0c\u5bf9\u6700\u5177\u653b\u51fb\u529b\u7684\u4e24\u5927\u5b66\u6d3e\u2014\u2014\u5851\u80fd\u672f\u548c\u4ea1\u7075\u672f\u534a\u70b9\u90fd\u4e0d\u4f1a\uff0c\u8fd9\u662f\u975e\u5e38\u7f55\u89c1\u7684\u3002</p><p>\u4e00\u7740\u7b97\u9519\uff0c\u6b65\u6b65\u88ab\u52a8\uff0c\u52a0\u4e0a\u53c8\u4e25\u91cd\u4f4e\u4f30\u4e86\u4e24\u4f4d\u7f8e\u5973\u7684\u5b9e\u529b\uff0c\u4e8e\u662f\u4fbf\u53c8\u4e86\u73b0\u5728\u7684\u7ed3\u679c\u3002\u597d\u5728\u7ef4\u4f26\u4fe1\u5f92\u522b\u7684\u4f18\u70b9\u6ca1\u6709\uff0c\u5374\u662f\u6700\u8bc6\u65f6\u52a1\uff0c\u65e2\u7136\u5df2\u7ecf\u843d\u5230\u5bf9\u65b9\u624b\u4e2d\uff0c\u4fbf\u8001\u8001\u5b9e\u5b9e\u5408\u4f5c\uff0c\u4e00\u4e94\u4e00\u5341\u5730\u4ec0\u4e48\u90fd\u8bf4\u4e86\u51fa\u6765\u3002</p><p>\u51fa\u4e86\u8fd9\u6b21\u610f\u5916\uff0c\u63a5\u4e0b\u6765\u7684\u8def\u9014\u743c\u6069\u7b49\u4eba\u5c31\u8d8a\u53d1\u5c0f\u5fc3\u8c28\u614e\u4e86\uff0c\u5c24\u5176\u662f\u4ed6\u8fd9\u4e2a\u6700\u8106\u5f31\u7684\u5deb\u5e08\uff0c\u65f6\u65f6\u786e\u4fdd\u81ea\u5df1\u7b3c\u7f69\u5728\u77f3\u80a4\u4e4b\u4e0b\uff0c\u9f99\u9cde\u76fe\u968f\u8eab\u73af\u7ed5\uff0c\u9632\u5907\u518d\u906d\u9047\u4e00\u6b21\u7a81\u7136\u88ad\u51fb\u3002\u201c\u53ef\u60dc\u6211\u7684\u9501\u7532\u8fd8\u6ca1\u9020\u597d\uff0c\u201d\u4ed6\u62b1\u6028\u7740\uff0c\u201c\u7d22\u5c3c\u5927\u5e08\u7684\u6548\u7387\u5b9e\u5728\u662f\u592a\u4ee4\u4eba\u53d1\u6307\u4e86\u3002\u201d</p><p>\u201c\u4f60\u4e0a\u6b21\u4e0d\u662f\u8bf4\u65e0\u6240\u8c13\u5417\uff1f\u201d\u6885\u83f2\u65af\u63d0\u9192\uff0c\u201c\u8fd8\u8bf4\u6162\u5de5\u51fa\u7ec6\u6d3b\u3002\u201d</p><p>\u201c\u90a3\u662f\u56e0\u4e3a\u6211\u6ca1\u60f3\u5230\u8fd9\u4e48\u5feb\u5c31\u53c8\u8981\u4e0a\u6218\u573a\u4e86\u3002\u201d</p><p>\u8bf4\u662f\u8fd9\u4e48\u8bf4\uff0c\u4e5f\u7eaf\u7cb9\u662f\u53d1\u53d1\u7262\u9a9a\u7f62\u4e86\uff0c\u65e2\u7136\u59d4\u6258\u4e86\u7d22\u5c3c\u5927\u5e08\u953b\u9020\uff0c\u7eb5\u7136\u662f\u743c\u6069\u4e5f\u4e0d\u6562\u53bb\u50ac\u4fc3\uff0c\u53ea\u80fd\u8010\u7740\u5fc3\u601d\u7b49\u3002\u953b\u9020\u5927\u5e08\u867d\u7136\u4e0d\u662f\u653f\u5e9c\u8981\u5458\uff0c\u5374\u5728\u4f0a\u5361\u6c99\u57ce\u5730\u4f4d\u8d85\u7136\uff0c\u8db3\u53ef\u4ee5\u4e0e\u516b\u4f4d\u6444\u653f\u8bae\u5458\u5206\u5ead\u6297\u793c\uff0c\u5c31\u8fde\u56fd\u738b\u90fd\u8981\u793c\u8ba9\u4e09\u5206\u3002</p><p>\u4ece\u4f0a\u5361\u6c99\u57ce\u5230\u6668\u70bc\u57ce\uff0c\u539f\u672c\u5927\u7ea6\u4e24\u65e5\u8def\u7a0b\uff0c\u5982\u4eca\u4e3a\u4e86\u9632\u6b62\u88ad\u51fb\uff0c\u884c\u8fdb\u901f\u5ea6\u5927\u5927\u653e\u7f13\u3002\u53ea\u662f\u4e16\u754c\u4e0a\u7684\u4e8b\u60c5\uff0c\u5f80\u5f80\u6015\u4ec0\u4e48\u5c31\u6765\u4ec0\u4e48\uff0c\u7b49\u505a\u597d\u4e86\u51c6\u5907\u53c8\u65e0\u5f71\u65e0\u8e2a\uff0c\u63a5\u4e0b\u6765\u4e00\u8def\u4e0a\u5e73\u9759\u5730\u5f97\u8fde\u53ea\u8725\u8734\u90fd\u6ca1\u770b\u89c1\u3002\u7b2c\u4e09\u65e5\u4e0b\u5348\uff0c\u743c\u6069\u7b49\u4eba\u5b89\u7136\u62b5\u8fbe\u4e86\u6668\u70bc\u57ce\u3002</p><p>\u203b\u203b\u203b</p><p>\u4e00\u5343\u591a\u5e74\u524d\uff0c\u4e00\u652f\u6d41\u4ea1\u7684\u7070\u77ee\u4eba\u5076\u7136\u6765\u5230\u6b64\u5730\uff0c\u5efa\u8d77\u4e86\u6668\u70bc\u57ce\u3002\u7070\u77ee\u4eba\u867d\u7136\u90aa\u6076\u6b8b\u5fcd\uff0c\u4f46\u4f9d\u65e7\u79c9\u627f\u4e86\u77ee\u4eba\u7684\u5929\u8d4b\uff0c\u6668\u70bc\u57ce\u6c60\u9ad8\u5927\uff0c\u5efa\u7b51\u575a\u56fa\uff0c\u8fd1\u4f3c\u4e00\u5ea7\u519b\u4e8b\u8981\u585e\u3002\u53ea\u662f\u7ec8\u7a76\u6ca1\u6709\u4f0a\u5361\u6c99\u57ce\u7684\u597d\u8fd0\u6c14\uff0c\u6709\u94f6\u9f99\u56fd\u738b\u548c\u8ff7\u9501\u5750\u9547\uff0c\u6700\u7ec8\u88ab\u74dc\u7406\u5fb7\u65af\u57ce\u7684\u5353\u5c14\u653b\u7834\uff0c\u57ce\u5e02\u4e5f\u5c31\u8352\u5e9f\u4e0b\u6765\uff0c\u51e0\u4e4e\u90fd\u88ab\u4eba\u6de1\u5fd8\u4e86\u3002</p><p>\u6309\u7167\u534f\u8bae\uff0c\u4f0a\u8389\u4e1d\u7fe0\u7684\u4fe1\u5f92\u5360\u636e\u6668\u70bc\u7684\u5357\u90e8\uff0c\u7ef4\u4f26\u548c\u9f50\u96c5\u6e29\u7eb1\u4e3d\u7684\u4fe1\u5f92\u5360\u636e\u5317\u90e8\uff0c\u53cc\u65b9\u5f7c\u6b64\u4e92\u76f8\u8b66\u60d5\uff0c\u5404\u81ea\u63d0\u9632\uff0c\u867d\u7136\u540c\u5728\u4e00\u57ce\uff0c\u5f7c\u6b64\u95f4\u5374\u5168\u65e0\u6765\u5f80\uff0c\u4eff\u4f5b\u654c\u519b\u4f3c\u7684\u3002\u963f\u5fd2\u59ae\u5f15\u743c\u6069\u548c\u6885\u83f2\u65af\u8fdb\u4e86\u57ce\uff0c\u81ea\u7136\u6709\u4eba\u4e0a\u6765\u8fce\u63a5\uff0c\u5378\u4e0b\u5305\u88b1\u4e0d\u63d0\u3002</p><p>\u4e00\u4e2a\u5c0f\u65f6\u4e4b\u540e\uff0c\u743c\u6069\u89c1\u5230\u4e86\u57ce\u4e2d\u4f0a\u8389\u4e1d\u7fe0\u4fe1\u5f92\u7684\u4f17\u4f4d\u9996\u9886\u3002</p><p>\u4e4b\u6240\u4ee5\u8bf4\u201c\u4f17\u4f4d\u201d\uff0c\u662f\u56e0\u4e3a\u4e00\u95f4\u5c0f\u5c0f\u7684\u77f3\u5ba4\u91cc\uff0c\u5c45\u7136\u5750\u4e86\u5341\u4e2a\u4eba\u3002\u4f0a\u8389\u4e1d\u7fe0\u6559\u4f1a\u6ca1\u4ec0\u4e48\u4e25\u660e\u6e05\u6670\u7684\u7ec4\u7ec7\u7ed3\u6784\uff0c\u9664\u4e86\u5973\u795e\u548c\u6700\u9ad8\u9886\u8896\u8475\u9732\u4e4b\u5916\uff0c\u4fbf\u662f\u4e00\u4e2a\u53c8\u4e00\u4e2a\u5206\u6563\u7684\u6559\u56e2\u5404\u81ea\u72ec\u7acb\u6d3b\u52a8\uff0c\u6216\u8005\u516d\u4e03\u4eba\uff0c\u6216\u8005\u5341\u4f59\u4eba\uff0c\u6216\u8005\u6570\u5341\u4eba\u4e0d\u7b49\uff0c\u5f7c\u6b64\u95f4\u4e5f\u6ca1\u4ec0\u4e48\u7edf\u5c5e\u3002\u6bcf\u4e2a\u6559\u56e2\u90fd\u7531\u4e00\u4f4d\u5730\u4f4d\u6700\u9ad8\u7684\u7267\u5e08\u9886\u5bfc\uff0c\u79f0\u4e3a\u201c\u6b4c\u548f\u8005\u201d\uff0c\u4f46\u8fd9\u5e76\u975e\u6559\u4f1a\u4f4d\u9636\uff0c\u4e5f\u4e0d\u662f\u795e\u804c\u5c01\u53f7\uff0c\u4ec5\u4ec5\u53ea\u662f\u4e00\u79cd\u79f0\u547c\u3002\u6b64\u65f6\u5728\u57ce\u4e2d\u7684\u516d\u5341\u4eba\uff0c\u5206\u522b\u6765\u81ea\u4e03\u4e2a\u6559\u56e2\u7684\u6b8b\u4f59\uff0c\u4e8e\u662f\u5c31\u6709\u4e86\u4e03\u4e2a\u6b4c\u548f\u8005\uff0c\u52a0\u4e0a\u963f\u5fd2\u59ae\uff08\u63f4\u519b\u7684\u9996\u9886\uff09\uff0c\u4fbf\u6709\u4e86\u516b\u4e2a\u5934\u76ee\u3002\u518d\u52a0\u4e0a\u743c\u6069\u548c\u6885\u83f2\u65af\u4e24\u4e2a\u5ba2\u4eba\uff0c\u4fbf\u771f\u6709\u6d4e\u6d4e\u4e00\u5802\u7684\u611f\u89c9\u4e86\u3002</p><p>\u201c\u90a3\u5018\u82e5\u6709\u4ec0\u4e48\u91cd\u8981\u7684\u4e8b\u60c5\uff0c\u4f60\u4eec\u5982\u4f55\u51b3\u7b56\uff1f\u201d\u743c\u6069\u6084\u58f0\u95ee\u963f\u5fd2\u59ae\u3002</p><p>\u201c\u81ea\u7136\u662f\u96c6\u4f53\u8868\u51b3\uff0c\u591a\u8005\u4e3a\u80dc\u3002\u201d</p><p>\u201c\u5514\uff0c\u5f88\u6c11\u4e3b\u3002\u201d</p><p>\u8d39\u4f26\u5927\u9646\u4e0a\u57ce\u90a6\u4f17\u591a\uff0c\u653f\u4f53\u6df7\u6742\uff0c\u5b9e\u884c\u8054\u90a6\u5236\u7684\u8bae\u4f1a\u5236\u7684\u6240\u5728\u591a\u6709\uff0c\u4fbf\u5982\u585e\u5c14\u7684\u7ea2\u888d\u5deb\u5e08\u4f1a\u3001\u5b89\u59c6\u7684\u516d\u4eba\u8bc4\u8bae\u4f1a\u3001\u6df1\u6c34\u57ce\u7684\u5341\u516d\u9886\u4e3b\uff0c\u90fd\u662f\u8457\u540d\u7684\u4f8b\u5b50\u3002\u53ea\u662f\u743c\u6069\u4e5f\u7565\u7565\u8bfb\u8fc7\u4e9b\u653f\u6cbb\u5b66\u8457\u4f5c\uff0c\u6c11\u4e3b\u56fa\u7136\u597d\uff0c\u5374\u5728\u201c\u6548\u7387\u201d\u4e0a\u9887\u4e3a\u6b20\u7f3a\uff0c\u82e5\u5728\u627f\u5e73\u65f6\u671f\u5c31\u7f62\u4e86\uff0c\u5982\u4eca\u8fd9\u79cd\u5371\u6025\u5b58\u4ea1\u7684\u5173\u5934\u8fd8\u641e\u8fd9\u4e00\u5957\uff0c\u90a3\u5c31\u662f\u81ea\u53d6\u6b7b\u8def\u3002</p><p>\u201c\u96be\u602a\u88ab\u7f57\u4e1d\u6559\u4f1a\u6253\u5f97\u4e1c\u5954\u897f\u9003\uff0c\u201d\u743c\u6069\u4e0d\u7531\u5f97\u7565\u5e26\u6076\u610f\u5730\u731c\u60f3\uff0c\u201c\u8fd9\u6837\u6563\u6f2b\u7684\u7ec4\u7ec7\uff0c\u80fd\u6210\u6c14\u5019\u624d\u602a\u3002\u201d</p><p>\u89c1\u4f17\u4eba\u5230\u9f50\uff0c\u963f\u5fd2\u59ae\u4fbf\u8d77\u8eab\uff0c\u5f00\u59cb\u8bb2\u8ff0\u8fd9\u6b21\u524d\u5f80\u4f0a\u5361\u6c99\u57ce\u7684\u7ecf\u8fc7\u3002\u743c\u6069\u767e\u65e0\u804a\u8d56\uff0c\u968f\u610f\u6253\u91cf\uff0c\u89c2\u5bdf\u5ba4\u5185\u8bf8\u4eba\uff0c\u7a81\u7136\u611f\u89c9\u4e00\u53ea\u67d4\u8f6f\u7684\u624b\u4ece\u65c1\u8fb9\u4f38\u8fc7\u6765\uff0c\u63e1\u4f4f\u4e86\u81ea\u5df1\u7684\u624b\u638c\u3002</p><p>\u4e00\u78b0\u4e4b\u4e0b\uff0c\u4fbf\u77e5\u9053\u662f\u6885\u83f2\u65af\uff0c\u8fd8\u4ee5\u4e3a\u53ea\u662f\u5bfb\u5e38\u4eb2\u6635\uff0c\u6b63\u4e0d\u5728\u610f\uff0c\u5374\u611f\u89c9\u6885\u83f2\u65af\u4f38\u51fa\u624b\u6307\uff0c\u5728\u4ed6\u7684\u638c\u5fc3\u6162\u6162\u5199\u8d77\u5b57\u6765\uff0c\u901a\u7528\u8bed\u6784\u8bcd\u7b80\u5355\uff0c\u5bb9\u6613\u5206\u8fa8\uff0c\u5374\u662f\u201c\u5de6\u8fb9\u7b2c\u56db\u4eba\u201d\u7684\u5b57\u6837\u3002</p><p>\u743c\u6069\u88c5\u4f5c\u6f2b\u4e0d\u7ecf\u5fc3\u7528\u773c\u89d2\u4f59\u5149\u77a5\u53bb\uff0c\u89c1\u90a3\u4e2a\u4f4d\u7f6e\u4e0a\u5750\u7740\u4e00\u4f4d\u5353\u5c14\uff0c\u5f53\u7136\u662f\u5973\u6027\u2014\u2014\u4f0a\u8389\u4e1d\u7fe0\u6559\u4f1a\u867d\u7136\u4e0d\u5982\u7f57\u4e1d\u6559\u4f1a\u90a3\u6837\u641e\u660e\u663e\u7684\u6027\u522b\u6b67\u89c6\uff0c\u4f46\u9ad8\u9636\u6210\u5458\u4e5f\u5168\u90fd\u662f\u5973\u6027\u3002\u5979\u76f8\u8c8c\u5e73\u5e38\uff0c\u8863\u9970\u666e\u901a\uff0c\u81ea\u4ece\u8fdb\u95e8\u4e4b\u540e\u5c31\u6ca1\u8bf4\u8fc7\u4ec0\u4e48\u8bdd\uff0c\u4e0d\u8fc7\u5782\u76ee\u9ed8\u5750\u800c\u5df2\u3002\u743c\u6069\u8bb0\u5f97\u4ecb\u7ecd\u7684\u65f6\u5019\uff0c\u8bf4\u5979\u53eb\u201c\u83ab\u857e\u7279\u201d\uff0c\u4e5f\u662f\u5f88\u5bfb\u5e38\u7684\u540d\u5b57\uff0c\u4ed6\u770b\u4e86\u4e00\u4f1a\uff0c\u6ca1\u53d1\u89c9\u6709\u4ec0\u4e48\u7279\u522b\u4e4b\u5904\uff0c\u4e0d\u7531\u5f97\u6709\u4e9b\u5947\u602a\u5730\u4fa7\u8138\u770b\u4e86\u770b\u6885\u83f2\u65af\uff0c\u4e0d\u77e5\u9053\u5979\u5230\u5e95\u662f\u4ec0\u4e48\u610f\u601d\u3002</p><p>\u6885\u83f2\u65af\u62bf\u4e86\u62bf\u5634\u5507\uff0c\u4f3c\u4e4e\u60f3\u8981\u89e3\u91ca\uff0c\u4f46\u5f53\u7740\u8fd9\u4e48\u591a\u4eba\u7684\u9762\u5374\u53c8\u4e0d\u597d\u8bf4\u8bdd\uff0c\u53ea\u662f\u5148\u8f7b\u8f7b\u6447\u4e86\u6447\u5934\u3002\u963f\u5fd2\u59ae\u6b64\u65f6\u5df2\u7ecf\u8bb2\u5230\u5979\u8fdb\u4e86\u4f0a\u5361\u6c99\u57ce\uff0c\u89d0\u89c1\u56fd\u738b\uff0c\u6b63\u8981\u8bf4\u5546\u8c08\u5185\u5bb9\uff0c\u6885\u83f2\u65af\u7a81\u7136\u5728\u65c1\u8fb9\u51fa\u58f0\u6253\u65ad\uff0c\u62a2\u5148\u8bf4\uff1a\u201c\u56fd\u738b\u548c\u4f17\u4f4d\u8bae\u5458\u8ba8\u8bba\u4e4b\u540e\uff0c\u51b3\u5b9a\u5168\u529b\u63f4\u52a9\u5404\u4f4d\uff0c\u643a\u624b\u8054\u76df\uff0c\u5171\u540c\u5bf9\u6297\u7f57\u4e1d\u7684\u90aa\u6076\u722a\u7259\u3002\u201d</p><p>\u963f\u5fd2\u59ae\u6b63\u5728\u8bf4\u8bdd\uff0c\u5979\u8fd9\u822c\u4ece\u65c1\u6253\u65ad\uff0c\u672c\u662f\u5341\u5206\u65e0\u793c\uff0c\u53ea\u662f\u56e0\u4e3a\u8bf4\u7684\u662f\u4e2a\u597d\u6d88\u606f\uff0c\u4ee5\u81f3\u4e8e\u8fd9\u4e9b\u5728\u573a\u7684\u6b4c\u548f\u8005\u90fd\u5927\u559c\u8fc7\u671b\uff0c\u65e0\u4eba\u4ecb\u610f\u3002\u743c\u6069\u548c\u963f\u5fd2\u59ae\u5374\u90fd\u662f\u4e00\u6014\uff0c\u53ea\u56e0\u4e3a\u6885\u83f2\u65af\u6240\u8a00\u6839\u672c\u5c31\u662f\u5b50\u865a\u4e4c\u6709\u7684\u4e8b\u60c5\u3002\u53ea\u662f\u4ed6\u4eec\u90fd\u548c\u6885\u83f2\u65af\u76f8\u8bc6\u6709\u5e74\uff0c\u77e5\u9053\u5979\u51b3\u4e0d\u662f\u80e1\u4e71\u8bf4\u8bdd\u7684\u4eba\uff0c\u8fd9\u4e48\u505a\u5fc5\u7136\u6709\u5176\u9053\u7406\uff0c\u4e5f\u90fd\u4e0d\u591a\u8bf4\u8bdd\uff0c\u9759\u89c2\u5176\u53d8\u3002</p><p>\u6885\u83f2\u65af\u63a5\u8fc7\u963f\u5fd2\u59ae\u8bdd\u5934\u7ee7\u7eed\u8bb2\u8ff0\uff0c\u9648\u8bf4\u4f0a\u5361\u6c99\u57ce\u7684\u7ed3\u76df\u8bda\u610f\uff0c\u53ea\u662f\u521a\u521a\u7ecf\u5386\u4e86\u4e00\u573a\u5927\u6218\u4e0d\u4e45\uff0c\u867d\u7136\u6210\u529f\u51fb\u8d25\u5353\u5c14\uff0c\u4f46\u4e5f\u9887\u6709\u635f\u4f24\uff0c\u800c\u4e14\u6b63\u5728\u7b79\u5212\u53e6\u4e00\u6b21\u519b\u4e8b\u884c\u52a8\uff0c\u6240\u4ee5\u4ed3\u4fc3\u4e4b\u95f4\u65e0\u6cd5\u76f4\u63a5\u6d3e\u5175\u652f\u63f4\u3002\u867d\u7136\u5982\u6b64\uff0c\u5374\u5e26\u6765\u4e86\u6700\u4e0a\u7b49\u7684\u5175\u7532\u4f5c\u4e3a\u63f4\u52a9\u3002\u53ea\u8981\u4f0a\u8389\u4e1d\u7fe0\u4fe1\u5f92\u80fd\u591f\u7a33\u4f4f\u5c40\u52bf\uff0c\u5c11\u5219\u534a\u6708\uff0c\u591a\u5219\u4e00\u6708\uff0c\u77ee\u4eba\u4fbf\u4f1a\u51fa\u5175\u524d\u6765\u76f8\u52a9\uff0c\u62b5\u5b9a\u5c40\u52bf\u3002\u65c1\u8fb9\u8fd9\u4f4d\u5170\u5c3c\u65af\u7279\u5148\u751f\u867d\u7136\u662f\u4eba\u7c7b\uff0c\u5374\u662f\u77ee\u4eba\u7684\u6444\u653f\u8bae\u5458\uff0c\u524d\u6b21\u5927\u8d25\u5353\u5c14\u4fbf\u591a\u8d56\u5176\u529b\uff0c\u6b64\u6b21\u4eb2\u81ea\u524d\u6765\uff0c\u6b63\u8868\u660e\u4e86\u4f0a\u5361\u6c99\u57ce\u7684\u8bda\u610f\uff0c\u7b49\u7b49\u3002</p><p>\u5979\u4f83\u4f83\u800c\u8c08\uff0c\u8a00\u8f9e\u6d41\u5229\uff0c\u610f\u6001\u8bda\u6073\uff0c\u4f17\u4eba\u77e5\u9053\u5979\u662f\u963f\u5fd2\u59ae\u7684\u6545\u53cb\uff0c\u53c8\u662f\u63d0\u5c14\u7684\u5723\u6b66\u58eb\uff0c\u81ea\u7136\u66f4\u4e0d\u4f1a\u6000\u7591\u2014\u2014\u5374\u4e0d\u77e5\u9053\u8fd9\u4f4d\u5723\u6b66\u58eb\u662f\u4ece\u6765\u4e0d\u5fcc\u8bb3\u6492\u8c0e\u7684\u3002\u743c\u6069\u548c\u963f\u5fd2\u59ae\u5fc3\u4e2d\u7591\u60d1\uff0c\u53ea\u662f\u4e0d\u597d\u591a\u95ee\u3002</p><p>\u8fd9\u6b21\u4f1a\u8bae\u6709\u4e24\u4e2a\u8bae\u9898\uff0c\u5176\u4e00\u662f\u963f\u5fd2\u59ae\u6c47\u62a5\u6b64\u6b21\u7ecf\u8fc7\uff0c\u5982\u4eca\u5374\u88ab\u6885\u83f2\u65af\u8d8a\u4fce\u4ee3\u5e96\u4e86\uff1b\u5176\u4e8c\u5219\u662f\u5546\u8c08\u4ee5\u540e\u7684\u884c\u52a8\u5bf9\u7b56\u3002\u5f85\u6885\u83f2\u65af\u8bf4\u5b8c\uff0c\u4f1a\u8bae\u4fbf\u5148\u6682\u505c\u4f11\u606f\uff0c\u743c\u6069\u773c\u89c1\u5de6\u53f3\u65e0\u4eba\uff0c\u6084\u58f0\u95ee\u5979\u5230\u5e95\u641e\u4ec0\u4e48\u540d\u5802\u3002\u6885\u83f2\u65af\u5fae\u5fae\u7b11\u4e86\u7b11\uff0c\u8bf4\u4e86\u53e5\u8bdd\u3002</p><p>\u201c\u90a3\u4e2a\u83ab\u857e\u7279\uff0c\u662f\u4e2a\u4ea1\u7075\u3002\u201d</p></article>"}}}]);
