(window.webpackJsonp=window.webpackJsonp||[]).push([[292],{315:function(p){p.exports={chapterTitle:null,title:"\u7b2c174\u7ae0 \u53f6\u5bb6\u8001\u4eba",content:"<article><h2>\u7b2c174\u7ae0 \u53f6\u5bb6\u8001\u4eba</h2><p>\u3000\u3000\u7ed9\u4ed6\u4eec\u6324\u5151\u7740\uff0c\u5f20\u606a\u53ea\u80fd\u9080\u6c5f\u9edb\u513f\u540c\u53bb\u89c1\u53f6\u5bb6\u7684\u5e55\u540e\u7075\u9b42\u4eba\u7269\u53f6\u7956\u8303\uff0c\u4e0d\u65b9\u4fbf\u4ecb\u7ecd\u6c5f\u9edb\u513f\uff0c\u90a3\u6837\u975e\u5f97\u628a\u8bb8\u601d\u3001\u8bb8\u7ef4\u626f\u51fa\u6765\u8bf4\u624d\u884c\u3002\u6c5f\u9edb\u513f\u7ed9\u4ee4\u5c0f\u71d5\u4e22\u4e0b\u6765\uff0c\u867d\u7136\u4e0d\u751a\u5a07\u7f9e\uff0c\u6682\u65f6\u53ea\u80fd\u8ddf\u5f20\u606a\u8d70\u3002</p><p>\u3000\u3000\u6c5f\u9edb\u513f\u7ea2\u7740\u8138\u4e0a\u4e86\u5f20\u606a\u7684\u8f66\uff0c\u53f6\u5efa\u658c\u4ed6\u4eec\u5750\u81ea\u5df1\u7684\u8f66\uff0c\u5f20\u606a\u4ecb\u7ecd\u6c5f\u9edb\u513f\u7ed9\u5085\u4fca\u8ba4\u8bc6\uff0c\u5085\u4fca\u5634\u89d2\u542b\u7740\u7b11\u8ddf\u6c5f\u9edb\u513f\u70b9\u5934\u6253\u62db\u547c\uff0c\u5fc3\u91cc\u60f3\uff1a\u8ddf\u5f20\u606a\u6709\u7275\u8fde\u7684\u5973\u5b69\u5b50\u600e\u4e48\u90fd\u6f02\u4eae\u5f97\u4e0d\u50cf\u8bdd\uff1f</p><p>\u3000\u3000\u6c5f\u9edb\u513f\u4e0e\u5f20\u606a\u4e5f\u4e0d\u7b97\u964c\u751f\u4e86\uff0c\u5750\u5230\u4ed6\u8f66\u91cc\uff0c\u7b49\u5085\u4fca\u8f6c\u8fc7\u8eab\u53bb\u5f00\u95e8\uff0c\u5979\u5c31\u6e10\u6e10\u6062\u590d\u81ea\u7136\uff0c\u95ee\u5f20\u606a\uff1a\u201c\u9ad8\u4e2d\u91cc\u90fd\u5feb\u671f\u672b\u8003\u8bd5\u4e86\u5427\uff0c\u4f60\u600e\u4e48\u8fd8\u6765\u7701\u57ce\u73a9\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u90fd\u4e0d\u77e5\u9053\u81ea\u5df1\u6709\u6ca1\u6709\u65f6\u95f4\u8d76\u56de\u6d77\u5dde\u53c2\u52a0\u671f\u672b\u8003\u8bd5\u3002</p><p>\u3000\u3000\u5e7f\u544a\u7684\u6295\u653e\u529b\u5ea6\u8981\u6309\u7167\u4ea7\u54c1\u7684\u5e02\u573a\u6ce2\u6bb5\u6709\u5e8f\u7684\u63a8\u8fdb\uff0c\u5728\u7ecf\u9500\u6e20\u9053\u8fd8\u6ca1\u6709\u5efa\u7acb\u8d77\u6765\u4e4b\u524d\uff0c\u5728\u4ea7\u54c1\u8fd8\u6ca1\u6709\u76f4\u63a5\u94fa\u5230\u7ec8\u7aef\u5e02\u573a\uff0c\u8fd8\u5904\u4e8e\u5e02\u573a\u8fdb\u5165\u9636\u6bb5\u7684\u524d\u671f\uff0c\u5e7f\u544a\u7684\u76ee\u6807\u975e\u5e38\u7b80\u5355\uff0c\u53ea\u5728\u4e8e\u5e02\u573a\u544a\u77e5\u3002</p><p>\u3000\u3000\u4e3a\u4e86\u65b9\u4fbf\u5feb\u901f\u7684\u5efa\u7acb\u7ecf\u9500\u6e20\u9053\uff0c\u4e00\u5b9a\u7684\u77e5\u540d\u5ea6\u4e0d\u53ef\u6216\u7f3a\u3002</p><p>\u3000\u3000\u60f3\u8981\u901a\u8fc7\u5e7f\u544a\u5feb\u901f\u6253\u54cd\u77e5\u540d\u5ea6\uff0c\u7535\u89c6\u4f20\u5a92\u5728\u4e5d\u5341\u5e74\u4ee3\u62e5\u6709\u5176\u4ed6\u4f20\u5a92\u65e0\u6cd5\u5ab2\u7f8e\u7684\u4f18\u52bf\uff0c\u56fd\u5185\u8fd9\u4e48\u591a\u5bb6\u7535\u89c6\u4f20\u5a92\uff0c\u592e\u89c6\u5177\u6709\u5176\u4ed6\u7535\u89c6\u4f20\u5a92\u65e0\u6cd5\u6297\u8861\u7684\u5730\u4f4d\u3002\u4e5d\u4e94\u5e74\u4e4b\u524d\uff0c\u6807\u738b\u7684\u6982\u5ff5\u8fd8\u4e0d\u662f\u5f88\u706b\u70ed\uff0c\u4f46\u662f\u5e7f\u544a\u7247\u5728\u592e\u89c6\u516b\u70b9\u6863\u7535\u89c6\u5267\u524d\u540e\u8d34\u7247\u4e2d\u64ad\u653e\u4e94\u79d2\u949f\u7684\u4ef7\u683c\u5df2\u7ecf\u8d85\u8fc7\u4e00\u4e07\uff0c\u4e00\u4e2a\u6708\u5e7f\u544a\u7d2f\u79ef\u64ad\u653e\u4e00\u767e\u4e94\u5341\u79d2\u7684\u65f6\u95f4\uff0c\u5f00\u4ef7\u5c31\u8981\u56db\u5341\u4e07\u3002</p><p>\u3000\u3000\u7231\u8fbe\u7535\u5b50\u76ee\u524d\u80fd\u8c03\u7528\u7684\u8d44\u91d1\u5341\u5206\u6709\u9650\uff0c\u800c\u4e14\u76ee\u524d\u8fd8\u53ea\u5904\u4e8e\u5e02\u573a\u8fdb\u5165\u9636\u6bb5\u7684\u524d\u671f\uff0c\u592e\u89c6\u4e94\u79d2\u5e7f\u544a\u52a0\u4e0a\u5176\u4ed6\u5a92\u4f53\u5e7f\u544a\u914d\u5408\uff0c\u5df2\u7ecf\u8db3\u591f\u3002\u4f46\u662f\u592e\u89c6\u4e94\u79d2\u5e7f\u544a\u80fd\u4e0d\u80fd\u53d6\u5f97\u8ba9\u4eba\u6ee1\u610f\u7684\u6548\u679c\uff0c\u5e7f\u544a\u7247\u7684\u5236\u4f5c\u4e0e\u62cd\u6444\u5341\u5206\u91cd\u8981\u3002\u6d77\u5dde\u5e02\u7684\u5e7f\u544a\u516c\u53f8\u5236\u4f5c\u6c34\u5e73\u90fd\u65e0\u6cd5\u8ba9\u5f20\u606a\u6ee1\u610f\uff0c\u867d\u7136\u7701\u57ce\u8fd9\u8fb9\u5df2\u7ecf\u8054\u7cfb\u4e0a\u4e00\u5bb6\u7701\u5185\u6709\u76f8\u5f53\u5236\u4f5c\u6c34\u51c6\u7684\u5e7f\u544a\u516c\u53f8\uff0c\u4f46\u662f\u4ea4\u51fa\u7684\u4e1c\u897f\u8fd8\u662f\u65e0\u6cd5\u8ba9\u5f20\u606a\u6ee1\u610f\uff0c\u4ed6\u8fd9\u6b21\u9664\u4e86\u540c\u76db\u946b\u8c08\u5408\u4f5c\u7684\u4e8b\u60c5\uff0c\u5c31\u662f\u8981\u4eb2\u81ea\u7763\u4fc3\u90a3\u5bb6\u5e7f\u544a\u516c\u53f8\u4ea4\u51fa\u8ba9\u4ed6\u6ee1\u610f\u7684\u4e1c\u897f\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u6709\u56de\u7b54\u6c5f\u9edb\u513f\u7684\u95ee\u9898\uff0c\u4fa7\u7740\u5934\u76ef\u7740\u5979\u660e\u4eae\u7684\u773c\u7738\u770b\uff1a\u201c\u9edb\u513f\u59d0\uff0c\u4f60\u4eec\u653e\u5047\u4e86\u6ca1\u6709\uff1f\u201d</p><p>\u3000\u3000\u201c\u653e\u5047\u8fd8\u65e9\uff0c\u5c31\u5269\u4e0b\u4e00\u95e8\u79d1\u76ee\u6ca1\u6709\u8003\uff0c\u6240\u4ee5\u624d\u6709\u65f6\u95f4\u51fa\u6765\u627e\u6691\u671f\u5de5\u554a\uff0c\u4ed6\u4eec\u90a3\u4e9b\u4eba\u90fd\u662f\u76db\u946b\u7684\u5417\uff1f\u4f60\u8ddf\u4ed6\u4eec\u5f88\u719f\u6089\u554a\uff01\u201d\u53f6\u5efa\u658c\u7ed9\u4ee4\u5c0f\u71d5\u540d\u7247\u65f6\uff0c\u6c5f\u9edb\u513f\u6ca1\u597d\u610f\u601d\u63a2\u5934\u53bb\u770b\u3002</p><p>\u3000\u3000\u201c\u90a3\u4e2a\u5e74\u9f84\u7a0d\u5927\u4e00\u70b9\u7684\u662f\u76db\u946b\u603b\u88c1\u53f6\u5efa\u658c\u2026\u2026\u201d\u5f20\u606a\u5c06\u53f6\u5efa\u658c\u3001\u4e01\u6587\u6021\u3001\u53f6\u5c0f\u6850\u3001\u90b5\u5fc3\u6587\u4ecb\u7ecd\u7ed9\u6c5f\u9edb\u513f\u542c\uff0c\u201c\u6211\u5bb6\u91cc\u7684\u516c\u53f8\u8ddf\u4ed6\u4eec\u6709\u4e9b\u4e1a\u52a1\u5f80\u6765\uff0c\u6240\u4ee5\u8ba4\u8bc6\u2026\u2026\u201d\u5f20\u606a\u8fd8\u5f3a\u8c03\u7684\u4e00\u4e0b\u662f\u201c\u5bb6\u91cc\u7684\u516c\u53f8\u201d\u3002</p><p>\u3000\u3000\u201c\u771f\u7684\uff1f\u201d\u6c5f\u9edb\u513f\u90a3\u53cc\u9ed1\u6f06\u4e00\u6837\u6676\u4eae\u7684\u773c\u7738\u76ef\u7740\u5f20\u606a\uff0c\u8f6f\u58f0\u8bf4\u9053\uff0c\u201c\u4f60\u5e2e\u6211\u8ddf\u4ed6\u4eec\u8bf4\u8bf4\uff0c\u5c31\u8ba9\u6211\u5728\u76db\u946b\u516c\u53f8\u505a\u6691\u671f\u5de5\uff0c\u5de5\u8d44\u4e0d\u8981\u90fd\u6ca1\u6709\u5173\u7cfb\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u53ea\u662f\u6691\u671f\u627e\u501f\u53e3\u7559\u5728\u5b66\u6821\uff0c\u90a3\u4e2a\u53f6\u5bb6\u5927\u5c11\u7237\u771f\u8fd9\u4e48\u70e6\u4eba\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u8bf4\u5462\uff1f\u201d\u6c5f\u9edb\u513f\u5a07\u67d4\u800c\u5e7d\u6028\u7684\u8bf4\uff0c\u201c\u6211\u7238\u7238\u7684\u5de5\u5382\u8981\u505a\u4ed6\u5bb6\u7684\u751f\u610f\uff0c\u53c8\u4e0d\u80fd\u5f97\u7f6a\u4ed6\uff0c\u518d\u8bf4\u6211\u5988\u5988\u6574\u5929\u5ff5\u53e8\uff0c\u6068\u4e0d\u5f97\u5c06\u5979\u5973\u513f\u96f6\u5241\u5206\u5207\u7740\u7684\u5356\u51fa\u53bb\u624d\u5f00\u5fc3\uff0c\u6211\u53ea\u6709\u8eb2\u5728\u5b66\u6821\u4e0d\u56de\u53bb\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u5fc3\u91cc\u4e00\u8f6f\uff0c\u8bf4\u9053\uff1a\u201c\u8981\u4e0d\u6211\u6765\u8bf7\u4f60\u505a\u6691\u671f\u5de5\uff1f\u201d</p><p>\u3000\u3000\u6c5f\u9edb\u513f\u6cae\u4e27\u7684\u8bf4\uff1a\u201c\u6211\u5988\u8fd9\u4eba\u5f88\u7ec6\u81f4\u7684\uff0c\u5979\u4f1a\u771f\u8d76\u8fc7\u6765\u770b\u6211\u6253\u5de5\u7684\u6837\u5b50\uff0c\u968f\u4fbf\u9a97\u9a97\u5979\u662f\u8fc7\u4e0d\u4e86\u5173\u7684\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u7740\u8bf4\uff1a\u201c\u771f\u7684\uff0c\u6211\u5bb6\u91cc\u5bf9\u6211\u5b66\u4e60\u4e5f\u6ca1\u6307\u671b\uff0c\u6211\u6b63\u5b66\u7740\u6253\u7406\u516c\u53f8\u7684\u4e8b\u60c5\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u771f\u7684\uff01\u201d\u6c5f\u9edb\u513f\u4e00\u542c\u80fd\u9a97\u8fc7\u5979\u5988\uff0c\u773c\u775b\u5c31\u7115\u53d1\u51fa\u8000\u4eba\u7684\u5149\u5f69\uff0c\u201c\u90a3\u4f60\u5bb6\u516c\u53f8\u5728\u54ea\u91cc\uff1f\u5f97\u8ba9\u6211\u5988\u5988\u8fc7\u6765\u770b\u4e00\u773c\uff0c\u5979\u624d\u6ca1\u8bdd\u8bf4\u3002\u201d</p><p>\u3000\u3000\u6d77\u88d5\u516c\u53f8\u867d\u7136\u53ea\u4fdd\u7559\u7684\u7a7a\u58f3\uff0c\u4f46\u6bd5\u7adf\u8fd8\u6709\u7a7a\u58f3\u5728\u90a3\u91cc\u6446\u7740\uff0c\u5f20\u606a\u7b11\u7740\u8bf4\uff1a\u201c\u6ca1\u95ee\u9898\uff0c\u4f60\u8981\u662f\u6691\u5047\u4f4f\u5b66\u6821\u4e0d\u65b9\u4fbf\uff0c\u6211\u501f\u5730\u65b9\u7ed9\u4f60\u4f4f\u3002\u201d\u6bcf\u5929\u4e5f\u65e9\u51fa\u665a\u5f52\u7684\uff0c\u4f4f\u5f90\u5b66\u5e73\u5bb6\u4e0d\u65b9\u4fbf\uff0c\u5f20\u606a\u4e0e\u5085\u4fca\u7559\u5728\u7701\u57ce\u4f1a\u4f4f\u665a\u6674\u7684\u516c\u5bd3\uff0c\u90a3\u91cc\u6c5f\u9edb\u513f\u4e5f\u8ddf\u7740\u8bb8\u7ef4\u53bb\u8fc7\u51e0\u6b21\uff0c\u5f20\u606a\u8bf4\u4e86\u5730\u65b9\uff0c\u6c5f\u9edb\u513f\u9ad8\u5174\u7684\u8bf4\u9053\uff1a\u201c\u592a\u597d\u4e86\uff0c\u5bbf\u820d\u91cc\u8fde\u7535\u98ce\u6247\u90fd\u6ca1\u6709\uff0c\u8fd9\u91cc\u7684\u590f\u5929\u53c8\u683c\u5916\u7684\u70ed\uff0c\u8fd8\u771f\u4e0d\u77e5\u9053\u600e\u4e48\u529e\u624d\u597d\u5462\u3002\u6797\u51b0\u6691\u5047\u56de\u8001\u5bb6\uff0c\u4ee4\u5c0f\u71d5\u3001\u8bb8\u7ef4\u6691\u5047\u90fd\u4f1a\u7559\u6821\uff0c\u5979\u4eec\u4eca\u665a\u4e0a\u5c31\u53ef\u80fd\u7b49\u4e0d\u53ca\u5230\u642c\u8fc7\u53bb\u4f4f\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u5fc3\u91cc\u60e8\u53eb\u4e86\u4e00\u58f0\uff0c\u96be\u9053\u6c5f\u9edb\u513f\u4e0d\u77e5\u9053\u597d\u4e1c\u897f\u8981\u85cf\u7740\u81ea\u5df1\u72ec\u4eab\u624d\u66f4\u6709\u4e50\u8da3\u5417\uff1f\u4ed6\u53ef\u6ca1\u60f3\u7740\u5c06\u8bb8\u601d\u3001\u4ee4\u5c0f\u71d5\u4e5f\u626f\u8fdb\u6765\uff0c\u53cd\u6b63\u7792\u4e0d\u8fc7\u4ee4\u4eba\u71d5\uff0c\u60f3\u5355\u72ec\u5c06\u6c5f\u9edb\u513f\u62d0\u51fa\u6765\u4e5f\u4e0d\u53ef\u80fd\u3002</p><p>\u3000\u3000\u8f66\u5b50\u8ddf\u7740\u524d\u9762\u4e24\u90e8\u8f66\u540e\u9762\uff0c\u51fa\u4e86\u57ce\u533a\u5f80\u897f\u76f4\u884c\u5dee\u4e0d\u591a\u6709\u534a\u5c0f\u65f6\uff0c\u624d\u5230\u53f6\u5bb6\u5728\u897f\u90ca\u7684\u79c1\u5b85\uff0c\u7701\u57ce\u662f\u5178\u578b\u7684\u6c5f\u5357\u4e18\u9675\u5730\u8c8c\u4e0e\u6c5f\u6dee\u5e73\u539f\u7684\u7f13\u51b2\u5e26\uff0c\u53f6\u5bb6\u7684\u79c1\u5b85\u5c31\u5efa\u5728\u4e00\u5ea7\u6d77\u62d4\u56db\u4e94\u5341\u7c73\u9ad8\u7684\u9752\u5c71\u811a\u4e0b\u3002</p><p>\u3000\u3000\u56fd\u5185\u57ce\u4e61\u5efa\u623f\u653f\u7b56\u4e0d\u5141\u8bb8\u51fa\u73b0\u5927\u8303\u56f4\u5708\u5730\u7684\u79c1\u5b85\uff0c\u4e5d\u5341\u5e74\u4ee3\u7684\u5bcc\u8c6a\u4eec\u5c31\u7b97\u62e5\u6709\u79c1\u5bb6\u5e84\u56ed\uff0c\u90a3\u4e5f\u8981\u6302\u7740\u516c\u53f8\u540d\u4e0b\uff0c\u4ee5\u9879\u76ee\u5f00\u53d1\u7684\u540d\u4e49\u5360\u5730\uff0c\u7b97\u4e0d\u4e0a\u771f\u6b63\u610f\u4e49\u7684\u79c1\u5b85\u3002</p><p>\u3000\u3000\u8fdc\u770b\u53bb\u662f\u7eaf\u4e2d\u5f0f\u7684\u5ead\u9662\uff0c\u8fde\u9662\u5899\u90fd\u76d6\u7740\u9752\u7816\u732b\u9762\u74e6\uff0c\u8f66\u5b50\u901a\u8fc7\u538b\u529b\u611f\u5e94\u95e8\u8fdb\u5165\u9662\u5b50\uff0c\u5f20\u606a\u624d\u5fc3\u6709\u611f\u6982\uff1a\u771f\u6b63\u8212\u670d\u7684\u79c1\u5b85\uff0c\u9ad8\u79d1\u6280\u5c11\u4e0d\u4e86\u3002</p><p>\u3000\u3000\u8ddf\u7740\u524d\u9762\u4e24\u90e8\u8f66\u505c\u4e0b\u6765\uff0c\u4e00\u540d\u987b\u53d1\u7686\u767d\u7684\u8001\u4eba\u624b\u6301\u7740\u62d0\u6756\u7ad9\u5728\u5802\u5c4b\u5eca\u6a90\u4e0b\uff0c\u76ee\u5149\u70af\u70af\u6709\u795e\uff0c\u5fc3\u8bf4\u4ed6\u5c31\u662f\u53f6\u5bb6\u7684\u80cc\u540e\u652f\u67f1\u53f6\u7956\u8303\u5427\uff0c\u5f20\u606a\u7d27\u8ddf\u7740\u53f6\u5efa\u658c\u4ed6\u4eec\u4e0b\u4e86\u8f66\u3002</p><p>\u3000\u3000\u201c\u4f60\u5c31\u662f\u5efa\u658c\u3001\u5c0f\u6850\u4e00\u76f4\u5938\u4e2a\u4e0d\u505c\u7684\u5c0f\u53cb\uff1f\u201d\u53f6\u7956\u8303\u624b\u6307\u7740\u5f20\u606a\uff0c\u58f0\u97f3\u5f02\u5e38\u7684\u6d2a\u54cd\u3002</p><p>\u3000\u3000\u201c\u53f6\u8001\u597d\uff0c\u201d\u5f20\u606a\u5728\u8fd9\u6837\u7684\u8001\u4eba\u9762\u524d\u53ea\u6709\u88c5\u5b59\u5b50\uff0c\u201c\u6211\u662f\u5f20\u606a\uff0c\u4e00\u76f4\u60f3\u7740\u8fc7\u6765\u62dc\u8bbf\u53f6\u8001\u3002\u201d\u4ed6\u5bf9\u6b64\u884c\u65e9\u6709\u9884\u611f\uff0c\u8f66\u91cc\u5c31\u5907\u597d\u793c\u7269\uff0c\u4ece\u5085\u4fca\u624b\u91cc\u63a5\u8fc7\u4e00\u76d2\u91ce\u5c71\u53c2\uff0c\u9012\u7ed9\u53f6\u7956\u8303\uff0c\u201c\u795d\u53f6\u8001\u9ad8\u5bff\u5ef6\u5e74\uff0c\u8eab\u4f53\u5065\u5eb7\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5475\uff0c\u5c0f\u53cb\u767b\u95e8\uff0c\u8fd8\u5e26\u7740\u793c\u7269\u770b\u671b\u6211\u8fd9\u4e2a\u8001\u5934\u5b50\uff01\u201d\u53f6\u7956\u8303\u4e5f\u4e0d\u63a8\u8fdf\uff0c\u5c06\u793c\u7269\u63a5\u8fc7\u6765\u4ea4\u7ed9\u4ed6\u8eab\u540e\u7684\u8b66\u536b\u5458\uff0c\u201c\u5916\u9762\u592a\u70ed\uff0c\u4f60\u4eec\u5e74\u8f7b\u4eba\u7684\u8eab\u4f53\u6bd4\u4e0d\u4e0a\u6211\u8fd9\u4e2a\u8001\u5b50\uff0c\u5feb\u8eb2\u5c4b\u5b50\u91cc\u6765\u5427\u3002\u201d</p><p>\u3000\u3000\u5802\u5c4b\u524d\u540e\u7a97\u6d1e\u5f00\uff0c\u6ca1\u6709\u7a7a\u8c03\u3001\u98ce\u6247\uff0c\u4f46\u662f\u7a7f\u5802\u98ce\u63a0\u8fc7\u808c\u80a4\uff0c\u5341\u5206\u6c81\u51c9\uff0c\u5728\u9662\u5b50\u91cc\u7ad9\u4e86\u4e00\u4f1a\u8eab\u4e0a\u6c81\u51fa\u7684\u6c57\u6c34\u8f6c\u773c\u95f4\u5c31\u5e72\u4e86\u3002</p><p>\u3000\u3000\u53f6\u7956\u8303\u662f\u4e2d\u7ec4\u90e8\u7684\u8001\u4eba\uff0c\u524d\u4e24\u5e74\u624d\u4ece\u653f\u534f\u526f\u4e3b\u5e2d\u7684\u4f4d\u5b50\u5f7b\u5e95\u9000\u4e0b\u6765\u5f52\u5bb6\u517b\u8001\uff0c\u867d\u8bf4\u5f7b\u5e95\u9000\u4e86\u4e0b\u6765\uff0c\u4f46\u662f\u5f71\u54cd\u529b\u8fd8\u5728\uff0c\u6bd5\u7adf\u53f6\u5bb6\u8fd9\u4e9b\u5e74\u5f00\u679d\u6563\u53f6\uff0c\u4ece\u653f\u3001\u7ecf\u5546\u3001\u6295\u620e\u7684\u5b50\u5f1f\u90fd\u6709\u4e0d\u4fd7\u7684\u6210\u7ee9\uff0c\u5f62\u6210\u9519\u7efc\u590d\u6742\u7684\u5173\u7cfb\u7f51\u7edc\uff0c\u53f6\u5efa\u658c\u6709\u5fc3\u6574\u5408\u53f6\u5bb6\u5728\u5546\u754c\u7684\u8d44\u6e90\uff0c\u901a\u8fc7\u5728\u534e\u4e1c\u5730\u533a\u4ee3\u7406\u7231\u7acb\u4fe1\u624b\u673a\uff0c\u521b\u5efa\u76db\u946b\u516c\u53f8\u624d\u77ed\u77ed\u4e0d\u5230\u4e24\u5e74\u7684\u65f6\u95f4\u5c31\u53d6\u5f97\u4e0d\u4fd7\u7684\u6210\u7ee9\u3002</p><p>\u3000\u3000\u53f6\u7956\u8303\u770b\u5f97\u6e05\u695a\uff0c\u8fd9\u4e2a\u56fd\u5bb6\u6b63\u53d1\u751f\u60ca\u5929\u52a8\u5730\u7684\u53d8\u5316\uff0c\u5728\u4e5d\u5341\u5e74\u4ee3\u4e4b\u524d\uff0c\u884c\u653f\u7684\u529b\u91cf\u65e0\u6cd5\u5f3a\u5927\uff0c\u4f46\u662f\u8d44\u672c\u7684\u529b\u91cf\u4ece\u4e5d\u5341\u5e74\u4ee3\u521d\u5f00\u59cb\u5c31\u6084\u7136\u5d1b\u8d77\uff0c\u6709\u65f6\u5019\u8d44\u672c\u7684\u529b\u91cf\u5f88\u8f7b\u6613\u5c31\u80fd\u8ba9\u4e00\u4e2a\u56fd\u5bb6\u4f4e\u4e0b\u9ad8\u8d35\u7684\u5934\u9885\u3002</p><p>\u3000\u3000\u53f6\u7956\u8303\u8fd9\u624d\u652f\u6301\u53f6\u5efa\u658c\u521b\u5efa\u76db\u946b\uff0c\u9010\u6b65\u5c06\u53f6\u5bb6\u5206\u6563\u7684\u5546\u4e1a\u8d44\u6e90\u90fd\u96c6\u4e2d\u4e00\u4e2a\u96c6\u56e2\u91cc\u53bb\uff0c\u800c\u53f6\u5efa\u658c\u4e3a\u53d6\u5f97\u7231\u7acb\u4fe1\u534e\u4e1c\u5730\u533a\u4ee3\u7406\u6743\uff0c\u8981\u6ca1\u6709\u53f6\u7956\u8303\u7684\u652f\u6301\uff0c\u4e5f\u65e0\u6cd5\u4ece\u677e\u6563\u7684\u53f6\u5bb6\u4eba\u90a3\u91cc\u7b79\u96c6\u56db\u4e94\u5343\u4e07\u7684\u8d44\u91d1\u3002</p><p>\u3000\u3000\u5728\u53f6\u5bb6\u5403\u8fc7\u4e2d\u996d\uff0c\u5927\u5bb6\u722c\u5230\u540e\u5c71\u7684\u5bc6\u6797\u91cc\u6563\u6b65\uff0c\u5f20\u606a\u4e0e\u53f6\u5efa\u658c\u3001\u90b5\u5fc3\u6587\u966a\u7740\u53f6\u7956\u8303\u8001\u4eba\u4e00\u8d77\u6cdb\u6cdb\u800c\u8c08\u7684\u8ba8\u8bba\u4e00\u4e9b\u7ecf\u6d4e\u7ecf\u8425\u4e0a\u7684\u95ee\u9898\uff0c\u5f20\u606a\u5012\u8ba4\u4e3a\u53f6\u7956\u8303\u66f4\u50cf\u4ece\u56fd\u5bb6\u8ba1\u5212\u53d1\u5c55\u59d4\u5458\u4f1a\u9000\u4e0b\u6765\u7684\u9ad8\u7ea7\u5b98\u5458\uff0c\u800c\u4e0d\u662f\u641e\u4e86\u4e00\u8f88\u5b50\u7ec4\u7ec7\u5173\u7cfb\u7684\u8001\u9769\u547d\u3002\u53f6\u5efa\u658c\u6709\u8fd9\u6837\u6709\u624b\u6bb5\u3001\u6709\u624b\u8155\u7684\u8001\u4eba\u5728\u80cc\u540e\u652f\u6301\uff0c\u771f\u662f\u4ed6\u7684\u5927\u5e78\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e4b\u524d\u8fd8\u5728\u5934\u75bc\u8981\u8ddf\u8001\u4eba\u8c08\u8bba\u4ec0\u4e48\u8bdd\u9898\u65b9\u5408\u9002\uff0c\u8fd9\u4e0b\u5b50\u6ca1\u6709\u4e00\u70b9\u4e0d\u9002\u3002</p><p>\u3000\u3000\u5c71\u540e\u6709\u4e00\u5c0f\u7247\u6e56\u6cca\uff0c\u957f\u6ee1\u91ce\u751f\u83f1\u89d2\uff0c\u6e56\u9762\u6d6e\u7740\u9752\u83f1\u83f1\u7684\u53f6\u5b50\uff0c\u8fd8\u6709\u4e00\u6761\u62d6\u8239\u659c\u7cfb\u5728\u5cb8\u8fb9\uff0c\u5f20\u606a\u65e9\u77e5\u9053\u6709\u8fd9\u4e48\u597d\u7684\u5730\u65b9\uff0c\u5e26\u7740\u82b7\u5f64\u4e0e\u60dc\u5bb9\u3001\u60dc\u7fbd\u4e24\u59d0\u59b9\u8fc7\u6765\u73a9\u4e5f\u597d\u7684\u3002</p><p>\u3000\u3000\u6c5f\u9edb\u513f\u8ddf\u7740\u4e01\u6587\u6021\u3001\u53f6\u5c0f\u6850\u5979\u4eec\u5750\u5728\u5cb8\u77f3\u4e0a\uff0c\u628a\u978b\u5b50\u8131\u6389\uff0c\u5c06\u767d\u5ae9\u7ec6\u817b\u7684\u7ea4\u8db3\u63a2\u8fdb\u6e56\u6c34\u91cc\uff0c\u8f7b\u8f7b\u7684\u6ed1\u52a8\uff0c\u770b\u7740\u6709\u5c0f\u9c7c\u6765\u5544\u5979\u7684\u811a\uff0c\u5403\u4e0d\u75d2\u7684\u8f7b\u7b11\uff0c\u8ba9\u5f20\u606a\u770b\u4e86\u6709\u4e9b\u5fc3\u52a8\u3002</p><p>\u3000\u3000\u5cb8\u8fb9\u6811\u836b\u4e0b\u6709\u51e0\u5757\u5929\u7136\u77f3\u5934\u56f4\u5728\u7684\u684c\u51f3\uff0c\u5012\u662f\u593a\u5929\u7136\u4e4b\u8da3\u3002\u53f6\u7956\u8303\u5750\u4e86\u4e0b\u6765\uff0c\u5bf9\u5f20\u606a\u8bf4\uff1a\u201c\u542c\u5efa\u658c\u8bf4\u5c0f\u53cb\u7684\u516c\u53f8\u4e5f\u751f\u4ea7\u5f71\u789f\u673a\u2026\u2026\u201d\u5f20\u606a\u6ca1\u60f3\u5230\u8fd9\u4e2a\u8bdd\u9898\u4f1a\u662f\u53f6\u7956\u8303\u8001\u4eba\u5148\u63d0\u51fa\u6765\uff0c\u4e0e\u53f6\u5efa\u658c\u5728\u4ed6\u8eab\u8fb9\u5750\u4e0b\u6765\uff0c\u90b5\u5fc3\u6587\u5374\u8ddf\u5085\u4fca\u8d70\u5230\u4e00\u8d77\uff0c\u867d\u7136\u4ed6\u53f6\u5c0f\u6850\u5173\u7cfb\u4eb2\u6635\uff0c\u770b\u6765\u8fd8\u6ca1\u6709\u8fdb\u5165\u53f6\u5bb6\u7684\u6838\u5fc3\u5c42\u3002</p></article>"}}}]);
