(window.webpackJsonp=window.webpackJsonp||[]).push([[293],{316:function(p){p.exports={chapterTitle:null,title:"\u7b2c175\u7ae0 \u56fd\u7f8e\u7535\u5668",content:"<article><h2>\u7b2c175\u7ae0 \u56fd\u7f8e\u7535\u5668</h2><p>\u3000\u3000\u5f20\u606a\u6ca1\u6709\u60f3\u5230\u4f1a\u662f\u53f6\u7956\u8303\u4e3b\u52a8\u8bf4\u8d77\u5f71\u789f\u673a\u7684\u8bdd\u9898\uff0c\u4ed6\u5728\u77f3\u51f3\u4e0a\u5750\u4e0b\u6765\uff0c\u6ca1\u6709\u6025\u7740\u8bf4\u8bdd\uff0c\u4fa7\u5934\u770b\u7740\u53f6\u5efa\u658c\u5750\u4e0b\u6765\uff0c\u624d\u4e0d\u614c\u4e0d\u5fd9\u7684\u8bf4\uff1a\u201c\u5de5\u5382\u90a3\u91cc\u51c6\u5907\u597d\u6295\u4ea7\u4e86\uff0c\u4e4b\u524d\u6709\u8ddf\u9e3f\u6ce2\u5e7f\u544a\u516c\u53f8\u7ea6\u5b9a\u5236\u4f5c\u5e7f\u544a\u7247\u7684\u4e8b\u5b9c\uff0c\u8fd9\u6b21\u4f1a\u5728\u7701\u91cc\u4f4f\u51e0\u5929\u2026\u2026\u201d</p><p>\u3000\u3000\u53f6\u5efa\u658c\u7b11\u7740\u8bf4\uff1a\u201c\u606a\u5c11\u4eb2\u81ea\u76d1\u5236\uff0c\u90a3\u5e7f\u544a\u7247\u4e00\u5b9a\u4f1a\u975e\u5e38\u7cbe\u5f69\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u6447\u4e86\u6447\uff0c\u8bf4\u9053\uff1a\u201c\u5e02\u573a\u8fdb\u5165\u524d\u671f\uff0c\u5e7f\u544a\u7684\u76ee\u7684\u5728\u4e8e\u5e02\u573a\u544a\u77e5\uff0c\u6709\u6548\u7684\u5e7f\u544a\u7247\u672a\u5fc5\u7cbe\u5f69\uff0c\u4f55\u51b5\u7231\u8fbe\u7535\u5b50\u624b\u91cc\u4e5f\u6ca1\u6709\u90a3\u4e48\u591a\u8d44\u91d1\u53bb\u7cbe\u5fc3\u6253\u78e8\u4e00\u652f\u7cbe\u5f69\u7684\u5e7f\u544a\u7247\u3002\u201d\u5f20\u606a\u5012\u4e5f\u4e0d\u8bb3\u8a00\u7231\u8fbe\u7535\u5b50\u76ee\u524d\u7684\u8d44\u91d1\u72b6\u51b5\uff0c\u201c\u7231\u8fbe\u7535\u5b50\u524d\u671f\u7684\u5e02\u573a\u63a8\u5e7f\u6700\u91cd\u8981\u7684\u4efb\u52a1\u4e0d\u4ec5\u662f\u8981\u5728\u56fd\u5185\u5efa\u7acb\u8d77\u7ecf\u9500\u5546\u7f51\u7edc\uff0c\u8fd8\u8981\u4ece\u7ecf\u9500\u5546\u90a3\u91cc\u83b7\u51fa\u4e00\u5b9a\u7684\u8d44\u91d1\u652f\u6491\u3002\u201d</p><p>\u3000\u3000\u53f6\u5efa\u658c\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5168\u56fd\u4e00\u767e\u591a\u5ea7\u5927\u4e2d\u578b\u57ce\u5e02\uff0c\u4f60\u4eec\u524d\u671f\u7684\u5de5\u4f5c\u91cf\u771f\u662f\u4e0d\u7b80\u5355\u554a\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u4e0d\u7b80\u5355\uff0c\u800c\u4e14\u6211\u53ea\u7ed9\u4e86\u7231\u8fbe\u7535\u5b50\u4e09\u4e2a\u6708\u7684\u65f6\u95f4\u3002\u201d\u82e5\u4e0d\u8ddf\u76db\u946b\u5408\u4f5c\uff0c\u7231\u8fbe\u7535\u5b50\u7684\u524d\u671f\u5e02\u573a\u63a8\u5e7f\u53ea\u80fd\u4ece\u9526\u6e56\u62bd\u4eba\uff0c\u65e0\u7591\u4e5f\u4f1a\u540c\u65f6\u589e\u52a0\u9526\u6e56\u90a3\u8fb9\u7684\u538b\u529b\u3002</p><p>\u3000\u3000\u53f6\u7956\u8303\u53ea\u8d1f\u8d23\u5f15\u8d77\u8bdd\u9898\uff0c\u4ed6\u5750\u5728\u90a3\u91cc\u624b\u6309\u7740\u62d0\u6756\uff0c\u772f\u7740\u773c\u775b\u9759\u9759\u770b\u7740\u6162\u6162\u5c06\u8bdd\u9898\u5f80\u6b63\u9898\u4e0a\u5f15\u7684\u4e24\u4eba\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u60ca\u8bb6\u7684\u8bf4\uff1a\u201c\u4e09\u4e2a\u6708\u7684\u65f6\u95f4\uff0c\u4e00\u767e\u5ea7\u57ce\u5e02\uff0c\u5c31\u7b97\u5175\u5206\u4e09\u8def\uff0c\u90a3\u4e5f\u8981\u4e09\u5929\u5c31\u653b\u514b\u4e00\u5ea7\u57ce\u5e02\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u7740\u8bf4\uff1a\u201c\u6211\u4eec\u524d\u671f\u7b79\u5907\u4e86\u534a\u5e74\u591a\u7684\u65f6\u95f4\uff0c\u53ea\u5728\u78e8\u783a\u4e00\u5251\uff0c\u5c31\u6307\u671b\u5251\u4e00\u51fa\u9798\u5927\u5288\u516b\u65b9\uff0c\u4e09\u4e2a\u6708\u7684\u65f6\u95f4\u52c9\u5f3a\u591f\u7528\uff0c\u4e3b\u8981\u76ee\u6807\u8fd8\u662f\u4e00\u7ebf\u57ce\u5e02\uff0c\u9700\u8981\u5bfb\u627e\u76db\u946b\u8fd9\u6837\u5728\u7535\u5b50\u4ea7\u54c1\u9886\u57df\u7684\u5927\u6e20\u9053\u5546\u5408\u4f5c\u2026\u2026\u201d</p><p>\u3000\u3000\u53f6\u5efa\u658c\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u76db\u946b\u81ea\u7136\u4f1a\u9f0e\u529b\u652f\u6301\u7231\u8fbe\u7535\u5b50\uff0c\u5176\u4ed6\u5927\u6e20\u9053\u5546\u53ea\u6015\u4e0d\u4f1a\u8f7b\u6613\u677e\u5f00\u53e3\u888b\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u77e5\u9053\u53f6\u5efa\u658c\u7684\u610f\u601d\uff0c\u7231\u8fbe\u7535\u5b50\u5728\u4e1a\u5185\u51e0\u4e4e\u6ca1\u6709\u4ec0\u4e48\u540d\u6c14\uff0c\u8d8a\u662f\u5b9e\u529b\u5f3a\u5927\u7684\u5927\u6e20\u9053\u5546\u8d8a\u662f\u559c\u6b22\u5360\u7528\u4f9b\u8d27\u5382\u5546\u7684\u8d44\u91d1\uff0c\u60f3\u8981\u8ba9\u90a3\u4e48\u8d22\u5927\u6c14\u7c97\u7684\u5927\u6e20\u9053\u5546\u9884\u4ed8\u8d27\u6b3e\u90a3\u51e0\u4e4e\u662f\u4e0d\u53ef\u80fd\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u70b9\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u96be\u5ea6\u662f\u5f88\u5927\uff0c\u4e0d\u8fc7\u7ecf\u9500\u5546\u7f51\u7edc\u7684\u8d28\u91cf\u76f4\u63a5\u51b3\u5b9a\u5e02\u573a\u7684\u5f00\u53d1\u8d28\u91cf\uff0c\u800c\u4e14\u7231\u8fbe\u7535\u5b50\u76ee\u524d\u7b79\u6b3e\u7684\u6e20\u9053\u5f88\u6709\u9650\u2026\u2026\u201d\u7231\u8fbe\u7535\u5b50\u5de5\u5382\u6ca1\u6709\u4ec0\u4e48\u8d44\u4ea7\u53ef\u4ee5\u62ff\u53bb\u62b5\u62bc\uff0c\u5b8b\u57f9\u660e\u5e2e\u5fd9\u89e3\u51b3\u4e94\u767e\u4e07\u8d37\u6b3e\u5df2\u7ecf\u662f\u6781\u9650\uff0c\u5c31\u7b97\u4ece\u9526\u6e56\u4e34\u65f6\u8c03\u7528\u8d44\u91d1\uff0c\u90a3\u4e5f\u662f\u676f\u6c34\u8f66\u85aa\uff0c\u5f20\u606a\u7684\u773c\u775b\u76ef\u7740\u90a3\u4e9b\u6e20\u9053\u5546\u7684\u53e3\u888b\uff0c\u5c31\u50cf\u76db\u946b\u8fd9\u6837\u7684\u5927\u6e20\u9053\u5546\uff0c\u54ea\u5bb6\u53e3\u888b\u91cc\u6ca1\u6709\u51e0\u5343\u4e07\u7684\u8d44\u91d1\uff1f\u6bcf\u5bb6\u53e3\u888b\u91cc\u6f0f\u51e0\u5341\u4e07\u51fa\u6765\uff0c\u90a3\u5bf9\u7231\u8fbe\u7535\u5b50\u4e5f\u662f\u5929\u6587\u6570\u5b57\u3002</p><p>\u3000\u3000\u542c\u5f20\u606a\u6ca1\u6709\u907f\u8bb3\u7231\u8fbe\u7535\u5b50\u5f53\u524d\u6240\u9762\u4e34\u7684\u95ee\u9898\uff0c\u53f6\u5efa\u658c\u95ee\u9053\uff1a\u201c\u5c31\u7b97\u7b79\u96c6\u5230\u8d44\u91d1\uff0c\u7231\u8fbe\u7535\u5b50\u5982\u4f55\u8fc5\u901f\u5ea6\u7684\u6269\u5927\u4ea7\u80fd\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff1a\u201c\u8981\u5728\u77ed\u65f6\u95f4\u91cc\u5efa\u7acb\u4e25\u5bc6\u7684\u5de5\u4e1a\u4f53\u7cfb\u96be\u5ea6\u5f88\u5927\uff0c\u5173\u952e\u8fd8\u5728\u4e8e\u524d\u671f\u7684\u6280\u672f\u79ef\u7d2f\uff0c\u90a3\u4e9b\u5c71\u5be8\u5382\u6700\u5feb\u80fd\u4e24\u4e2a\u6708\u5c31\u63a8\u51fa\u4ea7\u54c1\uff0c\u4ea7\u80fd\u6269\u5f20\u7684\u5468\u671f\u80fd\u6709\u591a\u957f\uff1f\u4f55\u51b5\u5e02\u573a\u4e5f\u4e0d\u662f\u4e00\u4e0b\u5b50\u5c31\u80fd\u5f00\u53d1\u51fa\u6765\u7684\uff0c\u76ee\u524d\u5de5\u5382\u5357\u4fa7\u8fd8\u6709\u4e00\u5757\u9884\u7559\u5730\uff0c\u53ea\u8981\u6709\u8d44\u91d1\u6301\u7eed\u7684\u6295\u5165\uff0c\u4e09\u4e2a\u6708\u540e\uff0c\u4ea7\u80fd\u6269\u5f20\u5230\u6bcf\u6708\u56db\u4e94\u5343\u53f0\u6ca1\u6709\u95ee\u9898\uff0c\u5173\u952e\u8fd8\u662f\u5143\u4ef6\u5907\u8d27\u5360\u7528\u7684\u8d44\u91d1\u91cf\u5f88\u5927\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u76ee\u524d\u7231\u8fbe\u7535\u5b50\u5de5\u5382\u7684\u4ea7\u80fd\u6709\u591a\u5c11\uff1f\u201d</p><p>\u3000\u3000\u201c\u4ea7\u80fd\u5dee\u4e0d\u591a\u6bcf\u6708\u4e24\u5343\u53f0\u5de6\u53f3\uff0c\u53d7\u8d44\u91d1\u6240\u9650\uff0c\u8fd9\u4e2a\u6708\u8bd5\u6295\u4ea7\u987a\u5229\u7684\u8bdd\uff0c\u4e0b\u4e2a\u6708\u80fd\u51fa\u4e00\u5343\u53f0\uff0c\u201d\u4e0e\u76db\u946b\u4e5f\u77e5\u6839\u77e5\u5e95\uff0c\u53f6\u5efa\u658c\u4e0d\u63a9\u9970\u5bf9\u5f71\u789f\u673a\u5e02\u573a\u7684\u5f3a\u70c8\u5174\u8da3\uff0c\u5f20\u606a\u4e5f\u6beb\u4e0d\u9690\u7792\u7231\u8fbe\u7535\u5b50\u76ee\u524d\u7684\u73b0\u72b6\uff0c\u201c\u7231\u8fbe\u7535\u5b50\u76ee\u524d\u53ea\u80fd\u575a\u6301\u8981\u6c42\u6e20\u9053\u5546\u73b0\u6b3e\u63d0\u8d37\uff0c\u54ea\u6015\u6bcf\u5bb6\u6e20\u9053\u5546\u63d0\u524d\u4e09\u5929\u6253\u51fa\u8d37\u6b3e\uff0c\u53ea\u8981\u751f\u4ea7\u4e0a\u8fd0\u7b79\u5f97\u597d\uff0c\u5927\u7ea6\u80fd\u591f\u83b7\u5f97\u4e00\u4e2a\u6708\u7684\u751f\u4ea7\u8d44\u91d1\uff0c\u5f53\u7136\u8fd8\u8981\u5206\u51fa\u4e00\u90e8\u5206\u7528\u4e8e\u5e02\u573a\u63a8\u5e7f\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u8fd9\u4e48\u7cbe\u6253\u7ec6\u7b97\uff0c\u771f\u591f\u82cf\u6d25\u4e1c\u4ed6\u53d7\u7684\u2026\u2026\u201d\u53f6\u5efa\u658c\u7b11\u7740\u8bf4\u3002</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u751f\u4ea7\u4e0a\u8fd8\u771f\u4e8f\u4e86\u6709\u82cf\u6d25\u4e1c\uff01\u201d\u73b0\u5728\u82cf\u6d25\u4e1c\u5c31\u662f\u91c7\u8d2d\u87ba\u4e1d\u9489\u90fd\u8981\u7cbe\u786e\u7684\u8ba1\u7b97\u6bcf\u4e2a\u6708\u7684\u7528\u91cf\u53ca\u91c7\u8d2d\u7684\u65f6\u673a\uff0c\u907f\u514d\u4e00\u6b21\u91c7\u8d2d\u91cf\u8fc7\u591a\u4f1a\u5360\u7528\u6709\u9650\u7684\u751f\u4ea7\u8d44\u91d1\uff0c\u6362\u4f5c\u522b\u4eba\uff0c\u5f20\u606a\u4e5f\u4e0d\u4f1a\u5c06\u5e02\u573a\u52a8\u4f5c\u7684\u65f6\u95f4\u8ba1\u7b97\u8fd9\u4e48\u7cbe\u7ec6\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u9887\u4e3a\u7fa1\u6155\u5f20\u606a\u6709\u8fd9\u4e48\u5f97\u529b\u7684\u52a9\u624b\uff0c\u76db\u946b\u6700\u7ec8\u8fd8\u662f\u8981\u9009\u62e9\u5728\u5b9e\u4f53\u4e0a\u6709\u53d1\u5c55\uff0c\u7ecf\u8425\u7684\u4eba\u624d\u4e5f\u662f\u6781\u4e3a\u6e34\u6c42\u7684\uff0c\u4f46\u662f\u773c\u4e0b\u4e0d\u662f\u7fa1\u6155\u8fd9\u4e2a\u7684\u65f6\u95f4\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u6b21\u8bf7\u606a\u5c11\u8fc7\u6765\uff0c\u4e3b\u8981\u8fd8\u662f\u60f3\u8ba8\u8bba\u4e00\u4e0b\uff0c\u76db\u946b\u4e0e\u7231\u8fbe\u7535\u5b50\u6709\u54ea\u4e9b\u5408\u4f5c\u7684\u673a\u4f1a\uff1f\u201d</p><p>\u3000\u3000\u4e0e\u76db\u946b\u5408\u4f5c\u7684\u597d\u5904\u5f88\u660e\u663e\uff0c\u5927\u5bb6\u77e5\u6839\u77e5\u5e95\uff0c\u76db\u946b\u5bf9\u5f71\u789f\u673a\u5e02\u573a\u4e5f\u6709\u5145\u5206\u7684\u7814\u7a76\uff0c\u751a\u81f3\u4e0d\u9700\u8981\u5f20\u606a\u8d39\u5507\u820c\uff0c\u4ed6\u4eec\u4e4b\u524d\u5c31\u5df2\u7ecf\u63d0\u51fa\u9884\u4ed8\u4e00\u5343\u4e07\u7684\u7b79\u7801\uff0c\u73b0\u5728\u53ea\u4f1a\u52a0\u7801\uff0c\u7231\u8fbe\u7535\u5b50\u53ef\u4ee5\u77ac\u65f6\u83b7\u5f97\u4e0a\u5343\u4e07\u7684\u751f\u4ea7\u8d44\u91d1\uff0c\u800c\u4e14\u76db\u946b\u5728\u534e\u4e1c\u5730\u533a\u6709\u6e20\u9053\u8d44\u6e90\u7684\u4f18\u52bf\uff0c\u53ef\u4ee5\u8bf4\u53ea\u8981\u534e\u4e1c\u603b\u4ee3\u6302\u4e0a\u76db\u946b\u8d38\u6613\u7684\u724c\u5b50\uff0c\u5728\u534e\u4e1c\u5730\u533a\u53d1\u5c55\u4e8c\u7ea7\u7ecf\u9500\u5546\u7684\u96be\u5ea6\u4f1a\u964d\u5230\u6781\u4f4e\uff0c\u5b8c\u5168\u53ef\u4ee5\u5c06\u56fd\u5185\u6700\u5927\u7684\u534e\u4e1c\u5730\u533a\u5e02\u573a\u4ea4\u7ed9\u76db\u946b\u53bb\u5f00\u53d1\u3002</p><p>\u3000\u3000\u8fd9\u4e48\u505a\u7684\u574f\u5904\u5c31\u662f\uff0c\u7231\u8fbe\u7535\u5b50\u4ee5\u540e\u7684\u6574\u4e2a\u534e\u4e1c\u5e02\u573a\u5c06\u53d7\u76db\u946b\u63a7\u5236\uff0c\u5e76\u4f1a\u7ed9\u76db\u946b\u5206\u51fa\u53bb\u8fd9\u5757\u5e02\u573a\u91cc\u76f8\u5f53\u4efd\u989d\u7684\u5229\u6da6\uff0c\u5f20\u606a\u5f53\u7136\u4e0d\u4f1a\u5c06\u8fd9\u5c42\u987e\u8651\u8bf4\u51fa\u6765\uff0c\u4ed6\u8fd8\u6709\u5176\u4ed6\u7684\u8003\u8651\uff0c\u8bf4\u9053\uff1a\u201c\u5f71\u789f\u673a\u4ea7\u4e1a\u7684\u8fdb\u5165\u95e8\u69db\u5f88\u4f4e\uff0c\u7231\u8fbe\u7535\u5b50\u4ece\u65e0\u5230\u6709\uff0c\u4e5f\u5c31\u51c6\u5907\u4e86\u516d\u4e2a\u6708\u7684\u65f6\u95f4\uff0c\u8fd8\u7b97\u51c6\u5907\u5145\u5206\u7684\uff0c\u76ee\u524d\u7684\u5e02\u573a\u5df2\u7ecf\u5177\u5907\u8bf1\u4eba\u7684\u5438\u5f15\u529b\uff0c\u5c31\u7b97\u5e02\u573a\u53ef\u4f9b\u5f00\u53d1\u7684\u6f5c\u529b\u518d\u5927\uff0c\u4e5f\u4f1a\u8fc5\u901f\u9971\u548c\uff0c\u6211\u9884\u6d4b\u5f71\u789f\u673a\u5230\u4e5d\u516b\u5e74\u521d\u5c31\u4f1a\u9971\u548c\uff0c\u90a3\u65f6\u5404\u5236\u9020\u5382\u5546\u4e4b\u95f4\u6700\u76f4\u63a5\u7684\u7ade\u4e89\u624b\u6bb5\u5c31\u662f\u964d\u4ef7\u9500\u552e\u4e89\u593a\u5e02\u573a\uff0c\u90a3\u4e9b\u6ca1\u6709\u6280\u672f\u79ef\u7d2f\u3001\u6ca1\u6709\u7ade\u4e89\u529b\u3001\u6216\u8005\u4f01\u4e1a\u8fd0\u8425\u95f4\u63a5\u6210\u672c\u8fc7\u9ad8\u7684\u5236\u9020\u5382\u5546\u5c31\u4f1a\u7ed9\u76f4\u63a5\u8e22\u51fa\u5e02\u573a\uff0c\u80fd\u751f\u5b58\u4e0b\u6765\u7684\u5236\u9020\u5382\u5546\u4e5f\u53ea\u80fd\u7ef4\u6301\u6781\u4f4e\u7684\u5229\u6da6\u7a7a\u95f4\uff0c\u5230\u90a3\u65f6\u7684\u5229\u6da6\u7a7a\u95f4\u53ea\u6015\u4e0d\u591f\u4e09\u7ea7\u7684\u7ecf\u9500\u4f53\u7cfb\u5206\u4eab\uff0c\u76db\u946b\u4f1a\u505a\u4ec0\u4e48\u9009\u62e9\uff1f\u201d</p><p>\u3000\u3000\u53f6\u5efa\u658c\u4e0d\u77e5\u9053\u5f20\u606a\u5982\u6b64\u51c6\u786e\u7684\u5224\u65ad\u57fa\u7840\u4ec0\u4e48\u6837\u7684\u5206\u6790\u4e4b\u4e0a\uff0c\u5fc3\u91cc\u4e5f\u4e0d\u6000\u7591\uff0c\u8fd9\u4ece\u7231\u7acb\u4fe1398\u7684\u706b\u7206\u884c\u60c5\u5c31\u5f97\u5230\u8bc1\u660e\uff0c\u4e4b\u524d\u53ef\u662f\u8c01\u90fd\u6ca1\u6709\u4ed6\u8fd9\u4e48\u5f3a\u70c8\u7684\u4fe1\u5fc3\u3002\u5f20\u606a\u7684\u4fe1\u5fc3\u8d8a\u5f3a\u70c8\uff0c\u53f6\u5efa\u658c\u4e5f\u8d8a\u5174\u594b\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u5f71\u789f\u673a\u5e02\u573a\u7684\u9ec4\u91d1\u671f\u4e5f\u5c31\u4e5d\u516d\u3001\u4e5d\u4e03\u4e24\u5e74\u95f4\uff0c\u6211\u76f8\u4fe1\u606a\u5c11\u7684\u5224\u65ad\uff0c\u65e2\u7136\u606a\u5c11\u62c5\u5fc3\u5230\u4e5d\u516b\u5e74\u540e\u4e09\u7ea7\u7684\u5206\u9500\u4f53\u7cfb\u4f1a\u663e\u5f97\u5197\u4f59\uff0c\u90a3\u76db\u946b\u4e5d\u516b\u5e74\u4e4b\u540e\u53ef\u4ee5\u5c06\u534e\u4e1c\u7ecf\u9500\u5546\u7f51\u7edc\u4ea4\u8fd8\u7ed9\u7231\u8fbe\u7535\u5b50\u81ea\u884c\u7ef4\u62a4\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u5c40\u9650\u4e8e\u5f71\u789f\u673a\uff0c\u6570\u5b57\u624b\u673a\u3001\u5bb6\u7528\u7535\u5668\uff0c\u90fd\u4f1a\u968f\u7740\u56fd\u5185\u7684\u6295\u8d44\u70ed\u7684\u5f71\u54cd\u800c\u9762\u4e34\u4ea7\u80fd\u6025\u5267\u6269\u5f20\u3001\u5229\u6da6\u7a7a\u95f4\u4e0d\u65ad\u538b\u7f29\u7684\u5c40\u9762\uff0c\u53f6\u603b\u53ef\u66fe\u60f3\u5230\u76db\u946b\u5982\u4f55\u5e94\u4ed8\u8fd9\u4e00\u5c40\u9762\uff1f\u201d</p><p>\u3000\u3000\u201c\u54b3\u2026\u2026\u201d\u53f6\u7956\u8303\u8f7b\u8f7b\u54b3\u4e86\u4e00\u58f0\uff0c\u4ed6\u8fd9\u65f6\u5019\u624d\u63d2\u7b2c\u4e00\u53e5\u8bdd\uff0c\u201c\u5c0f\u53cb\u662f\u8bf4\u6e20\u9053\u5546\u7684\u751f\u5b58\u7a7a\u95f4\u4f1a\u8d8a\u6765\u8d8a\u7a84\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u8bf4\u9053\uff1a\u201c\u4ece\u53d1\u8fbe\u56fd\u5bb6\u5546\u4e1a\u8d44\u672c\u7684\u53d1\u5c55\u6a21\u5f0f\u6765\u770b\uff0c\u56fd\u5185\u4e5f\u53ef\u80fd\u5b58\u5728\u8fd9\u79cd\u8d8b\u52bf\uff0c\u800c\u4e14\u53ef\u4ee5\u501f\u9274\u56fd\u5916\u6210\u719f\u7684\u6a21\u5f0f\uff0c\u8fd9\u4e2a\u8d8b\u52bf\u7684\u53d8\u5316\u5341\u5206\u8fc5\u901f\uff0c\u53ef\u80fd\u4e5f\u5c31\u8fd9\u4e09\u56db\u5e74\u65f6\u95f4\uff0c\u201d\u5f20\u606a\u8bf4\u8fd9\u756a\u8bdd\uff0c\u773c\u775b\u91cc\u53ef\u770b\u5230\u7684\u5374\u662f\u4eca\u540e\u5341\u51e0\u5e74\u95f4\u56fd\u5185\u7684\u56fd\u7f8e\u3001\u82cf\u5b81\u3001\u5927\u4e2d\u3001\u6c38\u4e50\u7b49\u8fd9\u4e9b\u5546\u4e1a\u8d44\u672c\u52bf\u529b\u7684\u53d1\u5c55\u5927\u52bf\uff0c\u201c\u5c31\u50cf\u7231\u8fbe\u7535\u5b50\uff0c\u5728\u5e02\u573a\u63a8\u5e7f\u4e4b\u521d\uff0c\u975e\u5e38\u4f9d\u8d56\u4e8e\u5927\u6e20\u9053\u5546\u7684\u8d44\u6e90\u505a\u5e02\u573a\uff0c\u4f46\u662f\u7b49\u7231\u8fbe\u7535\u5b50\u53d1\u5c55\u8d77\u6765\uff0c\u5fc5\u987b\u8981\u8003\u8651\u6446\u8131\u8fd9\u4e9b\u5927\u6e20\u9053\u5546\u800c\u725f\u6c42\u76f4\u63a5\u63a7\u5236\u5e02\u573a\u7684\u53ef\u80fd\u3002\u53ef\u4ee5\u9884\u89c1\uff0c\u5bb6\u7528\u7535\u5668\u7c7b\u4ea7\u54c1\u7684\u5e02\u573a\u4efd\u989d\uff0c\u6700\u5927\u7684\u4e00\u90e8\u5206\u4f1a\u96c6\u4e2d\u5728\u5927\u578b\u7684\u5546\u573a\u4e0e\u8d85\u7ea7\u5356\u573a\u91cc\uff0c\u53ef\u968f\u7740\u5546\u4e1a\u8d44\u672c\u7684\u53d1\u5c55\u4e0e\u517c\u5e76\uff0c\u8fd9\u4e9b\u5546\u573a\u4e0e\u8d85\u7ea7\u5356\u573a\u5c06\u53d6\u4ee3\u5927\u6e20\u9053\u5546\u5728\u5e02\u573a\u6d41\u901a\u9886\u57df\u7684\u8bdd\u8bed\u6743\uff0c\u5b8c\u5168\u53ef\u4ee5\u6446\u8131\u5927\u6e20\u9053\u5546\u76f4\u63a5\u4e0e\u5236\u9020\u5382\u5546\u76f4\u63a5\u5bf9\u8bdd\u2026\u2026\u201d</p><p>\u3000\u3000\u53f6\u7956\u8303\u4e0e\u53f6\u5efa\u658c\u9677\u5165\u6df1\u6df1\u7684\u601d\u7d22\uff0c\u8fd9\u5df2\u7ecf\u4e0d\u4ec5\u4ec5\u662f\u4e0e\u7231\u8fbe\u7535\u5b50\u7684\u5408\u4f5c\u7684\u95ee\u9898\u4e86\uff0c\u5f20\u606a\u4e00\u4e0b\u5b50\u5c31\u89e6\u53ca\u5230\u76db\u946b\u53d1\u5c55\u9053\u8def\u4e0a\u7684\u6838\u5fc3\u95ee\u9898\u3002\u53f6\u5efa\u658c\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u4e5f\u5c31\u662f\u76db\u946b\u5728\u53d1\u5c55\u6e20\u9053\u8d44\u6e90\u7684\u540c\u65f6\uff0c\u4e5f\u5fc5\u987b\u8003\u8651\u8f6c\u578b\u7684\u95ee\u9898\u2026\u2026\u4f46\u662f\u56fd\u5185\u6bd5\u7adf\u4e0e\u53d1\u8fbe\u56fd\u5bb6\u6709\u5f88\u5927\u5dee\u8ddd\uff0c\u6211\u4eec\u4e4b\u524d\u8003\u8651\u6709\u5341\u5e74\u5de6\u53f3\u7684\u7f13\u51b2\u65f6\u95f4\u624d\u4f1a\u6709\u5927\u7684\u8d8b\u52bf\u53d8\u5316\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u8bf4\u9053\uff1a\u201c\u6211\u4e0a\u6b21\u5728\u5317\u4eac\u770b\u5230\u4e00\u5bb6\u7535\u5668\u96f6\u552e\u5e97\uff0c\u8fd9\u5bb6\u540d\u4e3a\u56fd\u7f8e\u7535\u5668\u7684\u516c\u53f8\u65d7\u4e0b\u5728\u4e5d\u96f6\u5e74\u5c31\u91c7\u53d6\u65b0\u7684\u4f9b\u9500\u6a21\u5f0f\uff0c\u4ed6\u4eec\u65d7\u4e0b\u7684\u96f6\u552e\u5e97\u5df2\u7ecf\u6446\u8131\u4e2d\u95f4\u5546\uff0c\u4e0e\u4e0a\u6e38\u5382\u5bb6\u5b9e\u65bd\u76f4\u4f9b\u6a21\u5f0f\uff0c\u800c\u4e14\u8fd9\u5bb6\u516c\u53f8\u5728\u4e5d\u4e00\u5e74\u5c31\u7387\u5148\u5728\u300a\u5317\u4eac\u665a\u62a5\u300b\u520a\u767b\u4e2d\u7f1d\u62a5\u4ef7\u5e7f\u544a\uff0c\u8d70\u51fa\u4e86\u5750\u5e97\u7ecf\u8425\u7684\u4f20\u7edf\u6a21\u5f0f\uff0c\u4e0d\u6653\u5f97\u53f6\u603b\u6709\u6ca1\u6709\u5173\u6ce8\u8fc7\u8fd9\u5bb6\u89c4\u6a21\u8fd8\u4e0d\u662f\u5f88\u8d77\u773c\u7684\u516c\u53f8\u2026\u2026\u201d\u5f20\u606a\u53ef\u662f\u628a\u4eca\u5929\u5927\u540d\u9f0e\u9f0e\u7684\u56fd\u7f8e\u7535\u5668\u629b\u51fa\u6765\uff0c\u800c\u4e14\u56fd\u7f8e\u7684\u53d1\u5c55\u8def\u5f84\u4e5f\u6b63\u662f\u56fd\u5185\u5546\u4e1a\u8d44\u672c\u53d1\u5c55\u4e0e\u5bb6\u7528\u7535\u5668\uff08\u542b\u624b\u673a\uff09\u5e02\u573a\u6e20\u9053\u53d1\u5c55\u7684\u7f29\u5f71\uff01</p></article>"}}}]);
