(window.webpackJsonp=window.webpackJsonp||[]).push([[294],{317:function(p){p.exports={chapterTitle:null,title:"\u7b2c176\u7ae0 \u94f6\u9c7c\u7f8e\u4eba\u6d74",content:"<article><h2>\u7b2c176\u7ae0 \u94f6\u9c7c\u7f8e\u4eba\u6d74</h2><p>\u3000\u3000\u76db\u946b\u4e4b\u524d\u7684\u6ce8\u610f\u529b\u4e00\u76f4\u96c6\u4e2d\u5728\u901a\u8baf\u5668\u6750\u9886\u57df\uff0c\u76f4\u5230\u5f20\u606a\u521b\u7acb\u7231\u8fbe\u7535\u5b50\uff0c\u4ed6\u4eec\u624d\u5173\u6ce8\u5f71\u789f\u673a\u5e02\u573a\uff0c\u5bf9\u6574\u4e2a\u4f20\u7edf\u5bb6\u7535\u5e02\u573a\u4ee5\u53ca\u8ba1\u7b97\u673a\u5e02\u573a\u7684\u6e20\u9053\u89c2\u5bdf\u5e76\u4e0d\u591f\u3002</p><p>\u3000\u3000\u5f53\u65f6\u8fd1\u5728\u54ab\u5c3a\u7684\u82cf\u5b81\u7535\u5668\u4e5f\u53ea\u662f\u4e13\u8425\u7a7a\u8c03\u96f6\u552e\u3001\u6279\u53d1\u7684\u7ecf\u8425\u6a21\u5f0f\uff0c\u5230\u660e\u5e74\uff0c\u4e5f\u5c31\u662f\u4e5d\u516d\u5e74\uff0c\u82cf\u5b81\u7535\u5668\u624d\u5f00\u59cb\u6478\u7d22\u5bb6\u7535\u8fde\u9501\u7684\u9053\u8def\u3001\u81ea\u5efa\u96f6\u552e\u7ec8\u7aef\uff0c\u800c\u4e14\u82cf\u5b81\u9996\u5148\u9009\u62e9\u5728\u4e8c\u4e09\u7ebf\u57ce\u5e02\u626c\u5dde\u5e02\u8fdb\u884c\u5bb6\u7535\u8fde\u9501\u7684\u8bd5\u70b9\uff0c\u4e00\u76f4\u5230\u4e24\u5343\u5e74\uff0c\u82cf\u5b81\u624d\u5168\u9762\u8f6c\u578b\u505a\u5927\u578b\u7efc\u5408\u7535\u5668\u5356\u573a\u3002</p><p>\u3000\u3000\u867d\u7136\u6b64\u65f6\u8fd8\u53ea\u662f\u540d\u4e3a\u82cf\u5b81\u4ea4\u7535\u5bb6\u3001\u4e13\u8425\u7a7a\u8c03\u6279\u53d1\u3001\u96f6\u552e\u7684\u82cf\u5b81\u7535\u5668\u4e5d\u4e94\u5e74\u5df2\u7ecf\u662f\u56fd\u5185\u7a7a\u8c03\u5e02\u573a\u7684\u5927\u6e20\u9053\u5546\uff0c\u4f46\u662f\u53f6\u5bb6\u7684\u8d44\u6e90\u4e0e\u8d22\u529b\u4e1d\u6beb\u4e0d\u6bd4\u6b64\u65f6\u7684\u82cf\u5b81\u7535\u5668\u5dee\u591a\u5c11\uff0c\u867d\u7136\u5728\u96f6\u552e\u9886\u57df\u7684\u53d1\u5c55\u6709\u4e9b\u843d\u540e\uff0c\u4f46\u662f\u76db\u946b\u8d38\u6613\u80a1\u4efd\u6709\u9650\u516c\u53f8\u5728\u7701\u57ce\u76f4\u63a5\u590d\u5236\u56fd\u7f8e\u7535\u5668\u7684\u7ecf\u8425\u6a21\u5f0f\uff0c\u5374\u8981\u6bd4\u82cf\u5b81\u7535\u5668\u5728\u534e\u4e1c\u5730\u533a\u7387\u5148\u542f\u52a8\u4e09\u5e74\u5de6\u53f3\u7684\u65f6\u95f4\u3002\u5f53\u7136\uff0c\u76db\u946b\u7684\u4e3e\u52a8\u8bf4\u4e0d\u5b9a\u4f1a\u8ba9\u82cf\u5b81\u63d0\u524d\u542f\u52a8\u5168\u9762\u8f6c\u5165\u5bb6\u7535\u8fde\u9501\u4e1a\uff0c\u4e5f\u8bf4\u4e0d\u5b9a\u8ba9\u82cf\u5b81\u6253\u6d88\u5168\u9762\u8f6c\u578b\u7684\u5ff5\u5934\u3002</p><p>\u3000\u3000\u8d70\u5411\u7ec8\u7aef\u9500\u552e\u7684\u5546\u4e1a\u8d44\u672c\u5316\u662f\u50cf\u76db\u946b\u8fd9\u6837\u7684\u5927\u6e20\u9053\u5546\u6700\u4f73\u7684\u53d1\u5c55\u6a21\u5f0f\u4e4b\u4e00\uff0c\u5f20\u606a\u89c6\u76db\u946b\u4e3a\u6700\u91cd\u8981\u7684\u5546\u4e1a\u4f19\u4f34\uff0c\u76f4\u63a5\u63d0\u51fa\u56fd\u7f8e\u7535\u5668\u7684\u7ecf\u8425\u6a21\u5f0f\u7ed9\u4ed6\u4eec\u53c2\u8003\uff0c\u5c31\u662f\u5e0c\u671b\u4ed6\u4eec\u5c11\u8d70\u4e00\u4e9b\u5f2f\u8def\uff0c\u5c31\u662f\u5e0c\u671b\u76db\u946b\u80fd\u6709\u5f88\u597d\u7684\u53d1\u5c55\u6a21\u5f0f\u8fc5\u901f\u58ee\u5927\u8d77\u6765\uff0c\u65e5\u540e\u5728\u6ce2\u6f9c\u58ee\u9614\u7684\u5546\u4e1a\u5927\u6f6e\u6d41\u4e2d\u53ef\u4ee5\u76f8\u4e92\u4f9d\u4e3a\u52a9\u529b\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u8003\u8651\u4e86\u4e00\u4f1a\u513f\uff0c\u8bf4\u9053\uff1a\u201c\u56fd\u7f8e\u7535\u5668\u7684\u4e8b\u60c5\uff0c\u6211\u4e5f\u77e5\u9053\u4e00\u4e9b\uff0c\u53ea\u662f\u76db\u946b\u73b0\u5728\u8981\u7a81\u7136\u7684\u6324\u8fdb\u4f20\u7edf\u7684\u5bb6\u7535\u5e02\u573a\uff0c\u627e\u4e0d\u5230\u597d\u7684\u6954\u5165\u70b9\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5176\u4ed6\u7684\u5730\u65b9\u6211\u4e0d\u77e5\u9053\uff0c\u4f46\u662f\u6d77\u5dde\u5e02\u7684\u4f01\u4e1a\uff0c\u6211\u5fc3\u91cc\u591a\u5c11\u6709\u6570\uff0c\u76ee\u524d\u56fd\u5185\u7684\u5bb6\u7535\u5e02\u573a\u6b63\u4ece\u2018\u7d27\u7f3a\u7ecf\u6d4e\u2019\u5411\u2018\u8fc7\u5269\u7ecf\u6d4e\u2019\u8f6c\u53d8\uff0c\u6d77\u5dde\u5e02\u4e13\u8425\u5bb6\u7535\u6279\u53d1\u4e1a\u52a1\u7684\u4ea4\u5bb6\u7535\u516c\u53f8\u662f\u6d77\u5dde\u5e02\u5c5e\u7684\u56fd\u6709\u4f01\u4e1a\uff0c\u4ed6\u4eec\u5df2\u7ecf\u9996\u5148\u5bf9\u8fd9\u79cd\u53d8\u5316\u9002\u5e94\u4e0d\u8fc7\u6765\uff0c\u53bb\u5e74\u5df2\u7ecf\u51fa\u6765\u4e8f\u635f\uff0c\u4eca\u5e74\u4e0a\u534a\u5e74\u53ef\u80fd\u8fd8\u4f1a\u6301\u7eed\u4e8f\u635f\u2026\u2026\u201d</p><p>\u3000\u3000\u53f6\u5efa\u658c\u773c\u775b\u4e00\u4eae\uff0c\u63a5\u624b\u6d77\u5dde\u5e02\u4ea4\u5bb6\u7535\u516c\u53f8\u65e0\u7591\u662f\u8fc5\u901f\u6954\u5165\u4f20\u7edf\u5bb6\u7535\u9886\u57df\u7684\u6377\u5f84\uff0c\u53ea\u8981\u6d88\u5316\u6d77\u5dde\u5e02\u4ea4\u5bb6\u7535\u516c\u53f8\u7684\u6e20\u9053\u8d44\u6e90\uff0c\u5c31\u53ef\u4ee5\u8fc5\u901f\u5728\u7701\u57ce\u7b49\u5176\u4ed6\u4e00\u7ebf\u57ce\u5e02\u590d\u5236\uff0c\u5f20\u606a\u7684\u4e00\u5e2d\u8bdd\u6781\u5927\u7684\u542f\u53d1\u4e86\u53f6\u5efa\u658c\u53d1\u5c55\u76db\u946b\u7684\u601d\u8def\uff0c\u8fd9\u624d\u662f\u76db\u946b\u53ef\u4ee5\u575a\u5b9a\u4e0d\u79fb\u7684\u8d70\u4e0a\u5341\u5e74\u751a\u81f3\u6570\u5341\u5e74\u7684\u957f\u8fdc\u9053\u8def\u3002</p><p>\u3000\u3000\u53f6\u7956\u8303\u5012\u6ca1\u60f3\u5230\u5f20\u606a\u8fd9\u4e2a\u65c1\u89c2\u8005\u53cd\u800c\u6bd4\u4ed6\u4eec\u8fd9\u4e9b\u5c40\u5185\u4eba\u66f4\u6e05\u695a\u76db\u946b\u5e94\u8be5\u9009\u62e9\u7684\u53d1\u5c55\u65b9\u5411\uff0c\u4e14\u4e0d\u8bf4\u773c\u4e0b\u4e0e\u7231\u8fbe\u7535\u5b50\u5408\u4f5c\u662f\u76db\u946b\u826f\u597d\u7684\u673a\u4f1a\uff0c\u5f20\u606a\u8fd9\u6837\u7684\u5e74\u8f7b\u4eba\u4e5f\u662f\u53f6\u5bb6\u9700\u8981\u4ea4\u597d\u7684\u5546\u4e1a\u4f19\u4f34\uff0c\u53ea\u662f\u53ef\u60dc\u4ed6\u5728\u6d77\u5dde\u6709\u4e00\u756a\u81ea\u5df1\u7684\u4e8b\u4e1a\uff0c\u4e0d\u53ef\u80fd\u62db\u63fd\u5230\u53f6\u5bb6\u65d7\u4e0b\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u7b11\u7740\u8bf4\uff1a\u201c\u4f60\u65e2\u7136\u6709\u8fd9\u4e48\u597d\u7684\u601d\u8def\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u81ea\u5df1\u53bb\u505a\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff1a\u201c\u4eba\u529b\u65f6\u6709\u7a77\uff0c\u4e3e\u773c\u671b\u56fd\u5185\u5904\u5904\u85cf\u91d1\uff0c\u8fd8\u80fd\u5c06\u8fd9\u4e9b\u91d1\u5b50\u90fd\u6316\u5230\u81ea\u5bb6\u53bb\uff1f\u201d\u5fc3\u91cc\u60f3\u82cf\u5b81\u591a\u534a\u4e0d\u4f1a\u653e\u5f03\u5bb6\u7535\u8fde\u9501\u8fd9\u6761\u8def\uff0c\u76db\u946b\u5c4a\u65f6\u8981\u5728\u534e\u4e1c\u5730\u533a\u8d34\u8eab\u538b\u5236\u82cf\u5b81\uff0c\u6b64\u5916\u8fd8\u6709\u5728\u4e0a\u6d77\u53d1\u5bb6\u7684\u6c38\u4e50\u7535\u5668\uff0c\u5c31\u7b97\u53f6\u5bb6\u518d\u5bb6\u5927\u53f6\u5927\uff0c\u4e5f\u8981\u501f\u52a9\u5916\u529b\u786e\u5b9a\u4ed6\u4eec\u5728\u534e\u4e1c\u5730\u533a\u7684\u4f18\u52bf\u5730\u4f4d\uff0c\u4ed6\u4eec\u5c4a\u65f6\u8fd8\u4f1a\u4e0d\u4f1a\u62d2\u7edd\u7231\u8fbe\u7535\u5b50\u7684\u63f4\u624b\uff1f\u8981\u60f3\u5f97\u5230\u522b\u4eba\u66f4\u591a\u7684\u5c0a\u91cd\uff0c\u5c31\u8981\u5148\u5177\u5907\u66f4\u5f3a\u7684\u5b9e\u529b\u624d\u884c\uff0c\u73b0\u5728\u7684\u8d8a\u79c0\u516c\u53f8\u8fd8\u662f\u85cf\u5728\u86cb\u58f3\u91cc\u7684\u672a\u5b75\u5316\u7684\u80ce\u51e4\u800c\u5df2\uff0c\u603b\u8981\u7b49\u5230\u5979\u4e00\u9e23\u60ca\u4eba\u7684\u90a3\u4e00\u5929\u3002</p><p>\u3000\u3000\u5f20\u606a\u7684\u5ff5\u5934\u85cf\u7740\u6df1\uff0c\u8138\u4e0a\u542b\u7740\u7b11\uff0c\u4e1d\u6beb\u4e0d\u9732\u795e\u8272\uff0c\u4e14\u4e0d\u8bf4\u65e5\u540e\u7684\u6253\u7b97\uff0c\u773c\u4e0b\u4e3a\u76db\u946b\u6307\u660e\u4e00\u6761\u9053\u8def\uff0c\u5728\u63a5\u4e0b\u6765\u7684\u5408\u4f5c\u4e2d\uff0c\u53f6\u5efa\u658c\u8fd8\u597d\u610f\u601d\u65a4\u65a4\u8ba1\u8f83\uff1f</p><p>\u3000\u3000\u4f55\u51b5\u76db\u946b\u8981\u8fdb\u5165\u4f20\u7edf\u5bb6\u7535\u9886\u57df\uff0c\u4e5f\u8981\u5148\u4ece\u5bb6\u7535\u6e20\u9053\u6279\u53d1\u4e1a\u52a1\u6954\u5165\uff0c\u8fd9\u5bf9\u5f00\u53d1\u5f71\u789f\u673a\u5e02\u573a\u7684\u4f5c\u7528\u662f\u7acb\u7aff\u89c1\u5f71\u7684\u3002</p><p>\u3000\u3000\u53f6\u5bb6\u4eba\u4e5f\u8003\u8651\u8fc7\u76db\u946b\u65e5\u540e\u8f6c\u578b\u7684\u95ee\u9898\uff0c\u4f46\u4ed6\u4eec\u5bf9\u5e02\u573a\u7684\u8ba4\u8bc6\u6ca1\u6709\u5f20\u606a\u6240\u8bf4\u7684\u90a3\u4e48\u8feb\u5207\uff0c\u7136\u800c\u771f\u5b9e\u7684\u60c5\u51b5\u5374\u662f\u5f20\u606a\u6240\u8bf4\u7684\u90a3\u6837\uff0c\u5bb6\u7535\u5e02\u573a\u4ece\u4e5d\u5341\u5e74\u4ee3\u4e2d\u540e\u671f\u5168\u9762\u7684\u4ece\u201c\u7d27\u7f3a\u5e02\u573a\u201d\u8f6c\u53d8\u4e3a\u201c\u8fc7\u5269\u5e02\u573a\u201d\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u4e0e\u53f6\u5bb6\u8001\u5934\u5b50\u7684\u601d\u8def\u6709\u4e9b\u8feb\u4e0d\u53ca\u5f85\u7684\u8f6c\u79fb\u5230\u76db\u946b\u65e5\u540e\u7684\u53d1\u5c55\u89c4\u5212\u4e0a\uff0c\u53f6\u5efa\u658c\u751a\u81f3\u60f3\u5230\u5f53\u524d\u9700\u8981\u53bb\u505a\u7684\u4e8b\u60c5\uff0c\u5168\u9762\u8003\u5bdf\u56fd\u5185\u5bb6\u7535\u5e02\u573a\u53ca\u56fd\u7f8e\u7ec8\u7aef\u96f6\u552e\u5e97\u7ecf\u8425\u6a21\u5f0f\uff0c\u4e0e\u6d77\u5dde\u5e02\u63a5\u89e6\u8ba8\u8bba\u5e02\u5c5e\u4ea4\u5bb6\u7535\u516c\u53f8\u7684\u95ee\u9898\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u6210\u4e3a\u7231\u8fbe\u7535\u5b50\u534e\u4e1c\u5730\u533a\u603b\u4ee3\uff0c\u4e3a\u7231\u8fbe\u7535\u5b50\u5728\u534e\u4e1c\u5730\u533a\u53d1\u5c55\u4e8c\u7ea7\u7ecf\u9500\u5546\u7f51\u7edc\u662f\u5f53\u524d\u6700\u8feb\u5207\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u5f20\u606a\u89c1\u53f6\u5efa\u658c\u8ddf\u53f6\u7956\u8303\u5fc3\u4e8b\u91cd\u91cd\u7684\u6837\u5b50\uff0c\u4ed6\u4eec\u4e00\u65f6\u95f4\u4e5f\u6ca1\u6709\u5fc3\u601d\u8ba8\u8bba\u4e0e\u7231\u8fbe\u7535\u5b50\u5408\u4f5c\u7684\u7ec6\u8282\uff0c\u77e5\u9053\u8981\u7ed9\u65f6\u95f4\u4ed6\u4eec\u81ea\u5bb6\u4eba\u8ba8\u8bba\uff0c\u7ad9\u4e86\u8d77\u6765\uff0c\u8d70\u5230\u6e56\u8fb9\uff0c\u770b\u7740\u9752\u8272\u7684\u6e56\u9762\uff0c\u6e56\u98ce\u5439\u6765\uff0c\u6c81\u4eba\u5fc3\u813e\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u62db\u624b\u5524\u90b5\u5fc3\u6587\u3001\u53f6\u5c0f\u6850\u3001\u4e01\u6587\u6021\u4e09\u4eba\u8fc7\u53bb\uff0c\u5f20\u606a\u5219\u8086\u65e0\u5fcc\u60ee\u7684\u5750\u5728\u77f3\u5824\u4e0a\uff0c\u4e5f\u5b66\u6c5f\u9edb\u513f\u90a3\u6837\uff0c\u5c06\u978b\u5b50\u8131\u6389\uff0c\u5c06\u811a\u4f38\u5165\u6c81\u51c9\u7684\u6e56\u6c34\u91cc\u3002</p><p>\u3000\u3000\u5085\u4fca\u5012\u662f\u77e5\u60c5\u8bc6\u8da3\uff0c\u6ca1\u6709\u51d1\u8fc7\u6765\uff0c\u6e56\u8fb9\u6709\u9493\u5177\uff0c\u4ed6\u521a\u521a\u8ddf\u90b5\u5fc3\u6587\u5728\u9493\u9c7c\u3002\u5f20\u606a\u5728\u9493\u9c7c\u4e0a\u4e00\u76f4\u662f\u5f31\u9879\uff0c\u4e5f\u5c31\u6ca1\u5fc5\u8981\u5728\u6c5f\u9edb\u513f\u9762\u524d\u732e\u4e11\u3002</p><p>\u3000\u3000\u6c5f\u9edb\u513f\u56de\u5934\u770b\u4e86\u4e00\u773c\u6811\u836b\u4e0b\u7684\u53f6\u5bb6\u4f17\u4eba\uff0c\u95ee\u5f20\u606a\uff1a\u201c\u4f60\u4eec\u521a\u521a\u5728\u8ba8\u8bba\u4ec0\u4e48\uff0c\u7709\u98de\u8272\u821e\u7684\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u4e0d\u662f\u8bf4\u5728\u5b66\u7740\u6253\u7406\u516c\u53f8\u7684\u4e1a\u52a1\u5417\uff0c\u8ddf\u4ed6\u4eec\u5b66\u4e60\u5462\uff1f\u201d\u5f20\u606a\u56de\u5934\u770b\u4e86\u4e00\u773c\uff0c\u53f6\u5efa\u658c\u6b63\u62ff\u51fa\u624b\u673a\uff0c\u4e0d\u6653\u5f97\u4ed6\u8fd9\u65f6\u5019\u8ddf\u8c01\u901a\u8bdd\u3002</p><p>\u3000\u3000\u6c5f\u9edb\u513f\u5bf9\u5f20\u606a\u7684\u8bdd\u6df1\u4fe1\u4e0d\u7591\uff0c\u4e5f\u66fe\u968f\u8bb8\u7ef4\u966a\u82b7\u5f64\u53bb\u8fc7\u4e00\u56de\u65b0\u6885\u82d1\uff0c\u77e5\u9053\u5f20\u606a\u5bb6\u91cc\u8ddf\u666e\u901a\u5e02\u6c11\u89c6\u4e4b\u4e3a\u5929\u7684\u7701\u957f\u5bb6\u6709\u7740\u5bc6\u5207\u7684\u5173\u7cfb\u3002\u5f20\u606a\u7684\u6210\u719f\u7a33\u91cd\u4f53\u8d34\uff0c\u90fd\u8ba9\u6c5f\u9edb\u513f\u611f\u89c9\u5230\u5341\u5206\u7684\u6e29\u99a8\uff0c\u6700\u91cd\u8981\u7684\uff0c\u662f\u5f20\u606a\u7684\u5e74\u9f84\u6bd4\u5979\u5c0f\uff0c\u8ba9\u6c5f\u9edb\u513f\u6709\u7a0d\u8bb8\u7684\u5fc3\u7406\u4f18\u52bf\uff0c\u5979\u53ef\u662f\u4ece\u5c0f\u5230\u5927\u90fd\u88ab\u540c\u5b66\u670b\u53cb\u7167\u987e\u7740\uff0c\u90fd\u88ab\u7167\u987e\u5f97\u6709\u4e9b\u817b\u70e6\u4e86\uff0c\u4f46\u662f\u60f3\u5230\u81ea\u5df1\u5728\u8fd9\u4e2a\u5c0f\u4e24\u5c81\u7684\u5c11\u5e74\u9762\u524d\u534a\u88f8\u8fc7\uff0c\u5fc3\u91cc\u603b\u6709\u4e9b\u7f9e\u6da9\uff0c\u5374\u66f4\u80fd\u751f\u4eb2\u8fd1\u4e4b\u60c5\u3002</p><p>\u3000\u3000\u770b\u7740\u5f20\u606a\u4e5f\u5b66\u81ea\u5df1\u5c06\u978b\u5b50\u8131\u6389\u5c06\u811a\u4f38\u8fdb\u6e56\u6c34\u91cc\uff0c\u6c5f\u9edb\u513f\u7b11\u7740\u8bf4\uff1a\u201c\u770b\u770b\u4f60\u80fd\u4e0d\u80fd\u5f15\u9c7c\u6765\u5544\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u7684\u76ae\u80a4\u767d\u7699\uff0c\u4f46\u7ec8\u4e0d\u80fd\u4e0e\u6c5f\u9edb\u513f\u90a3\u5982\u7ec6\u74f7\u5982\u8102\u7389\u7684\u7ea4\u7ea4\u96ea\u8db3\u76f8\u6bd4\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u8fd9\u91cc\u9c7c\u513f\u90fd\u597d\u8272\uff0c\u6211\u4e0d\u8ddf\u4f60\u6bd4\u3002\u201d</p><p>\u3000\u3000\u201c\u80e1\u8bf4\u516b\u9053\uff01\u201d\u6ca1\u6709\u65c1\u4eba\u5728\uff0c\u6c5f\u9edb\u513f\u4e5f\u80fd\u63a5\u53d7\u5f20\u606a\u7684\u73a9\u7b11\u8bdd\uff0c\u7c89\u8138\u5fae\u7ea2\u7684\u55d4\u9a82\u4ed6\uff0c\u96ea\u8db3\u5728\u6e56\u6c34\u91cc\u8f7b\u8f7b\u5212\u52a8\uff0c\u8bf4\u6765\u5947\u602a\uff0c\u6e56\u91cc\u90a3\u79cd\u7ec6\u68f1\u5f62\u7684\u94f6\u9cde\u5c0f\u9c7c\u5374\u56f4\u7ed5\u7740\u96ea\u8db3\u5468\u56f4\u7684\u6c34\u91cc\uff0c\u4e00\u5f85\u6c5f\u9edb\u513f\u7684\u96ea\u8db3\u505c\u6b62\u5212\u52a8\uff0c\u5c31\u6d8c\u4e0a\u6765\u5544\u5979\u7684\u8db3\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u5230\u8fd9\u79cd\u60c5\u5f62\u60f3\u8d77\u770b\u8fc7\u4e00\u7bc7\u62a5\u9053\uff0c\u8bf4\u7ed9\u6c5f\u9edb\u513f\u542c\uff1a\u201c\u65e5\u672c\u6709\u79cd\u6e29\u6cc9\u5c0f\u9c7c\uff0c\u5c31\u559c\u6b22\u5544\u4eba\u76ae\u80a4\uff0c\u65e5\u672c\u4eba\u6d17\u6e29\u6cc9\u6d74\uff0c\u5c31\u559c\u6b22\u653e\u517b\u8fd9\u79cd\u5c0f\u9c7c\uff0c\u8ba9\u5c0f\u9c7c\u5544\u98df\u76ae\u80a4\u91cc\u7684\u788e\u5c51\uff0c\u7f8e\u5bb9\u7684\u6548\u679c\u975e\u5e38\u597d\uff0c\u9edb\u513f\u59d0\u59d0\u4e5f\u53ef\u4ee5\u4e0b\u6e56\u8bd5\u8bd5\uff0c\u8bf4\u4e0d\u5b9a\u6e56\u91cc\u7684\u5c0f\u9c7c\u4f1a\u8ba9\u9edb\u513f\u59d0\u66f4\u6f02\u4eae\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u53c8\u80e1\u8bf4\u4e86\uff0c\u201d\u6c5f\u9edb\u513f\u6a2a\u76ee\u770b\u7740\u5f20\u606a\uff0c\u201c\u90a3\u8fd8\u4e0d\u5f97\u8131\u5149\u8863\u670d\u2026\u2026\u201d\u53c8\u89c9\u5f97\u8fd9\u4e48\u8bf4\u592a\u66a7\u6627\uff0c\u7c89\u8138\u4e00\u7ea2\uff0c\u5374\u5f20\u606a\u987f\u65f6\u9677\u5165\u53d1\u5446\u7684\u6837\u5b50\uff0c\u8138\u4e0a\u5374\u53d1\u70eb\u4e86\uff0c\u8f7b\u8f7b\u63a8\u4e86\u4ed6\u4e86\u4e00\u4e0b\uff0c\u201c\u4f60\u778e\u60f3\u4ec0\u4e48\u5462\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u5f53\u7136\u662f\u5728\u60f3\u6c5f\u9edb\u513f\u8131\u5149\u5149\u7ed9\u6e56\u6c34\u91cc\u7684\u94f6\u9cde\u5c0f\u9c7c\u5544\u6ee1\u5168\u8eab\u7684\u60c5\u5f62\uff0c\u77ac\u65f6\u53e3\u6c34\u90fd\u5dee\u70b9\u6d41\u51fa\u6765\uff0c\u8f6c\u5ff5\u53c8\u60f3\u8d77\u5f53\u521d\u95ef\u8bb8\u7ef4\u5bbf\u820d\u65f6\u770b\u5230\u6c5f\u9edb\u513f\u88f8\u7761\u7684\u60c5\u5f62\uff1a\u90a3\u534a\u7247\u9732\u51fa\u7684\u96ea\u767d\u4e30\u4e73\u4e0a\u7684\u4e00\u70b9\u5ae3\u7ea2\u3001\u5982\u7f8a\u8102\u7389\u822c\u7ec6\u817b\u7684\u5927\u817f\u3001\u7c73\u5947\u8001\u9f20\u7684\u5361\u901a\u5185\u88e4\u3001\u4e30\u76db\u51cc\u4e71\u7684\u79c0\u53d1\u3001\u5a07\u8273\u52a8\u4eba\u7684\u4e00\u89d2\u7ea2\u5507\u2026\u2026\u611f\u89c9\u9f3b\u5934\u9178\u8fa3\uff0c\u7adf\u4f3c\u8981\u6d41\u9f3b\u8840\u7684\u5148\u5146\uff0c\u8ba9\u6c5f\u9edb\u513f\u8fd9\u8f7b\u8f7b\u4e00\u63a8\uff0c\u4eba\u5dee\u70b9\u6389\u6e56\u91cc\u53bb\uff0c\u60ca\u9192\u8fc7\u6765\u3002</p><p>\u3000\u3000\u6309\u8bf4\u8981\u80fd\u62e5\u6709\u8bb8\u601d\u8fd9\u6837\u7edd\u7f8e\u5973\u5b50\u7684\u771f\u60c5\uff0c\u6362\u4f5c\u8c01\u90fd\u4f1a\u6ee1\u8db3\uff0c\u8fd8\u6709\u5510\u5a67\u8fd9\u4fcf\u4e3d\u5c11\u5973\u7684\u503e\u5fc3\u76f8\u8bb8\uff0c\u5f20\u606a\u518d\u4e0d\u6ee1\u8db3\uff0c\u771f\u662f\u79bd\u517d\u4e86\uff0c\u4f46\u5f20\u606a\u65e0\u6cd5\u5426\u8ba4\u90a3\u77ac\u95f4\u6c5f\u9edb\u513f\u8ba9\u4ed6\u60c5\u6b32\u6d8c\u52a8\uff0c\u6216\u8bb8\u662f\u524d\u4e16\u53d7\u8fc7\u592a\u6df1\u7684\u60c5\u4f24\u3001\u4e4b\u540e\u53c8\u653e\u7eb5\u60c5\u6b32\u7684\u7f18\u6545\uff0c\u91cd\u56de\u4e5d\u56db\u5e74\u4e4b\u540e\u5bf9\u8fd9\u6837\u7edd\u7f8e\u7684\u5973\u4eba\u6ca1\u6709\u592a\u5f3a\u7684\u62b5\u6297\u529b\u3002</p><p>\u3000\u3000\u60f3\u8d77\u6216\u8bb8\u4ece\u6765\u5c31\u4e0d\u7b97\u53d1\u751f\u8fc7\u7684\u4f24\u5fc3\u5f80\u4e8b\uff0c\u5f20\u606a\u672a\u514d\u6709\u4e9b\u9eef\u7136\u795e\u4f24\uff0c\u5fc3\u91cc\u4e0d\u6653\u5f97\u8fd8\u8981\u4e0d\u8981\u6309\u7167\u65e2\u5b9a\u7684\u4eba\u751f\u8f68\u8ff9\u8fdb\u5165\u4e1c\u6d77\u5927\u5b66\u53bb\u4e0e\u90a3\u4e2a\u8ba9\u81ea\u5df1\u6ee1\u5fc3\u51c4\u51c9\u7684\u5973\u5b69\u89c1\u4e0a\u4eca\u751f\u7684\u4e00\u9762\u3002</p><p>\u3000\u3000\u201c\u5728\u60f3\u4ec0\u4e48\uff1f\u201d\u6c5f\u9edb\u513f\u89c1\u5f20\u606a\u8138\u8272\u77ac\u606f\u4e07\u53d8\uff0c\u660e\u660e\u521a\u521a\u8fd8\u4e00\u756a\u8272\u772f\u772f\u7684\u6a21\u6837\uff0c\u8f6c\u773c\u95f4\u53c8\u90a3\u662f\u90a3\u4e48\u5fe7\u4f24\uff0c\u773c\u775b\u91cc\u7684\u6ca7\u6851\u8ba9\u5979\u770b\u4e86\u5fc3\u91cc\u4e5f\u83ab\u540d\u7684\u60b8\u75db\uff0c\u5fc3\u91cc\u60f3\u7740\uff1a\u4f60\u7a76\u7adf\u662f\u4ec0\u4e48\u6837\u7684\u4eba\u554a\uff0c\u5728\u4f17\u4eba\u7684\u773c\u91cc\uff0c\u4f60\u5c31\u50cf\u5929\u4e4b\u5a07\u5b50\u4e00\u6837\u5149\u5f69\u593a\u76ee\uff0c\u4e3a\u4ec0\u4e48\u5374\u4f1a\u6709\u8fd9\u4e48\u5fe7\u4f24\u7684\u773c\u795e\uff1f</p><p>\u3000\u3000\u8981\u8bf4\u6210\u5e74\u4eba\u8272\u772f\u772f\u7684\u773c\u795e\u4f1a\u8ba9\u6c5f\u9edb\u513f\u538c\u6076\uff0c\u5f20\u606a\u5076\u5c14\u9732\u51fa\u7684\u8d2a\u5a6a\u7684\u773c\u795e\u5374\u8ba9\u6c5f\u9edb\u513f\u53ea\u611f\u5230\u65e0\u7aef\u7684\u7f9e\u6da9\uff0c\u6216\u8bb8\u662f\u6c5f\u9edb\u513f\u8ba4\u5b9a\u5f20\u606a\u6bd4\u81ea\u5df1\u5e74\u9f84\u5c0f\u7684\u7f18\u6545\uff0c\u90a3\u79cd\u5c11\u5e74\u7684\u770b\u4e0a\u53bb\u6beb\u65e0\u5fc3\u673a\u7684\u76f4\u7387\u773c\u795e\u603b\u4e0d\u80fd\u8ba9\u4eba\u751f\u538c\u3002</p></article>"}}}]);
