(window.webpackJsonp=window.webpackJsonp||[]).push([[299],{321:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u4e94\u8282 \u4ee3\u53f7",content:"<article><h2>\u7b2c\u5341\u4e94\u8282 \u4ee3\u53f7</h2><p>\u5fb7\u53e4\u62c9\u6014\u4e86\u4e00\u6014\uff0c\u6ca1\u660e\u767d\u600e\u4e48\u56de\u4e8b\uff0c\u743c\u6069\u5374\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u806a\u660e\uff01\u201d\u4ed6\u79f0\u8d5e\uff0c\u677e\u4e86\u53e3\u6c14\u3002</p><p>\u6885\u83f2\u65af\u7684\u94f6\u5251\u201c\u7737\u604b\u201d\u662f\u63d0\u5c14\u6240\u8d50\uff0c\u4e0d\u4f46\u950b\u9510\u65e0\u6bd4\uff0c\u800c\u4e14\u80fd\u591f\u7f29\u5c0f\uff0c\u6885\u83f2\u65af\u4e0d\u7528\u5b83\u65f6\u5c31\u653e\u5728\u8170\u95f4\u3002\u743c\u6069\u539f\u672c\u53ea\u4ee5\u4e3a\u662f\u643a\u5e26\u65b9\u4fbf\uff0c\u5374\u6ca1\u60f3\u5230\u5bf9\u654c\u65f6\u4e5f\u6709\u8fd9\u7b49\u5999\u7528\u3002</p><p>\u5168\u573a\u89c2\u4f17\u7eb7\u7eb7\u9f13\u638c\uff0c\u5979\u4eec\u5927\u591a\u90fd\u662f\u4f0a\u8389\u4e1d\u7fe0\u4fe1\u5f92\uff0c\u4e0d\u5e2e\u81ea\u5df1\u7684\u6559\u53cb\uff0c\u5374\u660e\u663e\u503e\u5411\u6885\u83f2\u65af\u8fd9\u4e2a\u5916\u4eba\uff0c\u7531\u6b64\u53ef\u89c1\u201c\u91d1\u95ea\u95ea\u201d\u7684\u4eba\u7f18\u7740\u5b9e\u4e0d\u4f73\u3002\u80dc\u8d1f\u5df2\u5206\uff0c\u6885\u83f2\u65af\u81ea\u7136\u4e5f\u6ca1\u771f\u6253\u7b97\u6740\u4e86\u5bf9\u624b\uff0c\u6b63\u5f85\u6536\u56de\u5251\uff0c\u65e5\u7cbe\u7075\u5374\u5f88\u4e0d\u670d\u6c14\uff0c\u5927\u58f0\u6297\u8bae\uff0c\u8ba4\u4e3a\u8fd9\u5b8c\u5168\u662f\u7528\u5351\u9119\u624b\u6bb5\u53d6\u80dc\uff0c\u4e0d\u80fd\u7b97\u6570\u3002</p><p>\u201c\u6839\u636e\u6b66\u5668\u7684\u7279\u6027\uff0c\u91c7\u7528\u5408\u9002\u7684\u6218\u672f\uff0c\u662f\u7406\u6240\u5f53\u7136\u7684\u4e8b\u60c5\uff0c\u201d\u6885\u83f2\u65af\u5fae\u5fae\u4e00\u7b11\uff0c\u201c\u4f60\u5982\u679c\u4e0d\u662f\u7528\u8fd9\u67c4\u5f2f\u5200\uff0c\u90a3\u79cd\u501f\u52a9\u683c\u6321\u53cd\u5f39\u6765\u51cf\u5c11\u8f6c\u6362\u95f4\u9699\uff0c\u8fde\u7eed\u653b\u51fb\u7684\u6253\u6cd5\uff0c\u5e94\u8be5\u4e5f\u7528\u4e0d\u51fa\u6765\u5427\u3002\u201d</p><p>\u201c\u8fd9\u5b8c\u5168\u662f\u4e24\u7801\u4e8b\uff01\u201d</p><p>\u6885\u83f2\u65af\u79c0\u7709\u626c\u8d77\uff0c\u201c\u90a3\u597d\uff0c\u518d\u6765\u3002\u201d</p><p>\u65e5\u7cbe\u7075\u54fc\u4e86\u4e00\u58f0\uff0c\u63e1\u8d77\u5f2f\u5200\u6446\u597d\u67b6\u5f0f\uff0c\u5374\u4e0d\u4e3b\u52a8\u8fdb\u653b\u3002\u6885\u83f2\u65af\u63d0\u8d77\u94f6\u5251\uff0c\u53c8\u662f\u671d\u7740\u5de6\u80a9\u659c\u5411\u5288\u4e86\u4e0b\u6765\uff0c\u6beb\u65e0\u534a\u70b9\u82b1\u5de7\u3002</p><p>\u201c\u94ff\u201d\u5730\u4e00\u58f0\uff0c\u5200\u5251\u518d\u5ea6\u76f8\u649e\uff0c\u65e5\u7cbe\u7075\u6b32\u5f85\u6545\u6280\u91cd\u65bd\uff0c\u5374\u53d1\u73b0\u5f2f\u5200\u6ca1\u6709\u5982\u9884\u671f\u822c\u8fc5\u901f\u5f39\u56de\u2014\u2014\u56e0\u4e3a\u5728\u76f8\u649e\u7684\u4e00\u77ac\u95f4\uff0c\u6885\u83f2\u65af\u7a81\u7136\u64a4\u9500\u4e86\u529b\u9053\uff0c\u8fd9\u4e00\u5251\u5f00\u5934\u6765\u52bf\u975e\u5e38\u8fc5\u731b\uff0c\u649e\u4e0a\u5f2f\u5200\u7684\u65f6\u5019\u5374\u662f\u7ef5\u8f6f\u65e0\u529b\uff0c\u8f7b\u8f7b\u5de7\u5de7\u5c31\u88ab\u8361\u5f00\u3002\u4f46\u4e5f\u6b63\u56e0\u4e3a\u5982\u6b64\uff0c\u65e5\u7cbe\u7075\u7684\u5f2f\u5200\u4e5f\u6ca1\u80fd\u501f\u529b\u5f39\u56de\uff0c\u539f\u672c\u8ba1\u5212\u4e2d\u7684\u4e00\u51fb\u987f\u65f6\u843d\u7a7a\uff0c\u6574\u4e2a\u8eab\u4f53\u5df2\u7ecf\u84c4\u52bf\u5f85\u53d1\uff0c\u7a81\u7136\u53d1\u751f\u8fd9\u79cd\u610f\u5916\u53d8\u6545\uff0c\u767b\u65f6\u4e00\u6ede\u3002</p><p>\u9ad8\u624b\u76f8\u4e89\uff0c\u4e00\u4e1d\u7684\u7a7a\u9699\u5df2\u7ecf\u8db3\u591f\uff0c\u5bd2\u5149\u4e00\u95ea\uff0c\u6885\u83f2\u65af\u7684\u94f6\u5251\u53c8\u62b5\u4e0a\u4e86\u65e5\u7cbe\u7075\u7684\u54bd\u5589\u3002</p><p>\u201c\u501f\u52bf\u53cd\u5f39\uff0c\u8fde\u7eed\u653b\u51fb\uff0c\u8fd9\u786e\u5b9e\u662f\u5f88\u5de7\u5999\u7684\u6218\u672f\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u4f46\u5f88\u5927\u7a0b\u5ea6\u662f\u5efa\u7acb\u5728\u5bf9\u624b\u7684\u2018\u914d\u5408\u2019\u4e4b\u4e0a\uff0c\u800c\u4f60\u5e76\u4e0d\u662f\u603b\u80fd\u63a7\u5236\u5c40\u52bf\u7684\u3002\u201d</p><p>\u65e5\u7cbe\u7075\u54ac\u4e86\u54ac\u5634\u5507\uff0c\u201c\u518d\u6765\uff01\u201d</p><p>\u4e3a\u4e86\u907f\u514d\u91cd\u8e48\u8986\u8f99\uff0c\u5979\u8fd9\u6b21\u53d8\u5f97\u8c28\u614e\u4e86\uff0c\u51fa\u5200\u603b\u662f\u9884\u7559\u4e09\u5206\u529b\uff0c\u514d\u5f97\u5230\u65f6\u5019\u6536\u4e0d\u56de\u6765\u3002\u4f46\u8fd9\u5bfc\u81f4\u7684\u7ed3\u679c\uff0c\u5c31\u662f\u5979\u7684\u653b\u51fb\u548c\u683c\u6321\u90fd\u4e0d\u5982\u4ee5\u524d\u51cc\u5389\u8fc5\u901f\u4e86\uff0c\u6240\u4ee5\u5931\u8d25\u662f\u5fc5\u7136\u7684\u3002\u6885\u83f2\u65af\u5288\u51fa\u4e00\u5251\uff0c\u5728\u5979\u683c\u6321\u65f6\u731b\u7136\u53d1\u529b\uff0c\u77ac\u95f4\u5251\u4e0a\u7684\u52b2\u9053\u589e\u5f3a\u4e86\u4e00\u500d\uff0c\u6beb\u65e0\u60ac\u5ff5\u5730\u5c31\u7838\u98de\u4e86\u65e5\u7cbe\u7075\u7684\u5f2f\u5200\u3002</p><p>\u82e5\u662f\u5e38\u4eba\uff0c\u63a5\u8fde\u8f93\u4e86\u4e09\u6b21\uff0c\u65e9\u5c31\u5df2\u7ecf\u6c14\u9981\uff0c\u7136\u800c\u8fd9\u4f4d\u91d1\u95ea\u95ea\u5374\u662f\u5fc3\u6c14\u9ad8\u50b2\u4e4b\u8f88\uff0c\u4f9d\u65e7\u4e0d\u80af\u627f\u8ba4\u5931\u8d25\uff0c\u8981\u6c42\u518d\u6765\u3002</p><p>\u518d\u6765\u5979\u53c8\u8f93\u4e86\uff0c\u8fd9\u4e00\u6b21\u6885\u83f2\u65af\u6ca1\u6709\u4f7f\u7528\u4efb\u4f55\u673a\u5de7\uff0c\u4f46\u5c31\u8fde\u743c\u6069\u8fd9\u79cd\u5916\u884c\u90fd\u770b\u51fa\u4e86\u70b9\u95e8\u9053\u3002\u8981\u8bba\u901f\u5ea6\uff0c\u6885\u83f2\u65af\u5176\u5b9e\u662f\u6bd4\u65e5\u7cbe\u7075\u900a\u8272\u4e00\u7b79\u7684\uff0c\u4f46\u5979\u5374\u603b\u80fd\u5728\u5bf9\u624b\u7684\u653b\u51fb\u521a\u521a\u53d1\u52a8\u65f6\u5c31\u505a\u51fa\u51c6\u786e\u7684\u5e94\u5bf9\uff0c\u4eff\u4f5b\u80fd\u672a\u535c\u5148\u77e5\u4f3c\u7684\u3002</p><p>\u201c\u4f60\u501f\u52a9\u53cd\u5f39\u6765\u653b\u51fb\uff0c\u90a3\u4e48\u6211\u4e5f\u5c31\u53ef\u4ee5\u9884\u6d4b\u4f60\u5927\u81f4\u7684\u653b\u51fb\u65b9\u5411\uff0c\u751a\u81f3\u53ef\u4ee5\u8bf1\u5bfc\u4f60\u505a\u51fa\u6211\u5e0c\u671b\u7684\u653b\u51fb\uff0c\u201d\u6885\u83f2\u65af\u89e3\u91ca\uff0c\u201c\u518d\u52a0\u4e0a\u5df2\u7ecf\u6253\u4e86\u56db\u6b21\uff0c\u4f60\u7684\u62db\u672f\u6211\u4e5f\u6e10\u6e10\u719f\u6089\uff0c\u6240\u4ee5\u5f88\u5bb9\u6613\u5c31\u80fd\u63d0\u524d\u5c01\u4f4f\u4f60\u7684\u5200\u2014\u2014\u5176\u5b9e\u4f60\u8fd9\u79cd\u6253\u6cd5\uff0c\u867d\u7136\u770b\u8d77\u6765\u5f88\u5de7\u5999\uff0c\u7ec8\u7a76\u6709\u4e9b\u534e\u800c\u4e0d\u5b9e\u3002\u201d</p><p>\u65e5\u7cbe\u7075\u9ed8\u7136\u7247\u523b\uff0c\u5f03\u5200\u8ba4\u8f93\uff0c\u867d\u7136\u8138\u8272\u4f9d\u65e7\u9634\u6c89\uff0c\u5374\u4e5f\u6ca1\u6709\u518d\u591a\u8bf4\u4ec0\u4e48\u3002\u53ea\u662f\u81ea\u6b64\u4e4b\u540e\uff0c\u6574\u4e2a\u4eba\u7684\u6c14\u7130\u90fd\u6536\u655b\u4e86\u5f88\u591a\u3002</p><p>\u203b\u203b\u203b</p><p>\u4e8b\u60c5\u8fc7\u53bb\uff0c\u5927\u5bb6\u4e5f\u5c31\u6e10\u6e10\u6de1\u5fd8\uff0c\u53cd\u5012\u662f\u65e5\u7cbe\u7075\u548c\u6885\u83f2\u65af\u7684\u5173\u7cfb\u4e0d\u77e5\u4f55\u65f6\u53d8\u5f97\u4eb2\u8fd1\u8d77\u6765\u3002\u4e24\u4eba\u7ecf\u5e38\u5728\u4e00\u8d77\u6bd4\u8bd5\u6b66\u6280\uff0c\u953b\u70bc\u5251\u672f\uff0c\u5f88\u6709\u5171\u540c\u8bed\u8a00\u3002\u8fd9\u539f\u672c\u4e5f\u662f\u5f88\u5bfb\u5e38\u7684\u4e8b\u60c5\uff0c\u6240\u8c13\u4e0d\u6253\u4e0d\u76f8\u8bc6\uff0c\u65e0\u8bba\u662f\u6545\u4e8b\u8fd8\u662f\u73b0\u5b9e\u4e2d\u90fd\u591a\u5f97\u5f88\uff0c\u4f46\u743c\u6069\u5374\u53e6\u6709\u770b\u6cd5\u3002</p><p>\u201c\u6211\u542c\u8bf4\u7cbe\u7075\u90fd\u662f\u53cc\u6027\u604b\u3002\u201d\u5728\u4e00\u6b21\u6b22\u597d\u4e4b\u540e\uff0c\u4ed6\u5bf9\u6885\u83f2\u65af\u8bf4\u3002</p><p>\u6885\u83f2\u65af\u6b63\u6709\u4e9b\u8ff7\u8ff7\u7cca\u7cca\uff0c\u8fd8\u6ca1\u5b8c\u5168\u6e05\u9192\uff0c\u8fc7\u4e86\u7247\u523b\u624d\u53cd\u5e94\u8fc7\u6765\uff0c\u201c\u54ea\u6709\u8fd9\u79cd\u8bf4\u6cd5\uff1f\u201d\u5979\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u4e0d\u4f1a\u662f\u4f60\u542c\u81ea\u5df1\u8bf4\u7684\u5427\u3002\u201d</p><p>\u201c\u4e0d\u662f\u554a\uff0c\u6211\u4e0a\u6b21\u5728\u70db\u5821\u7684\u65f6\u5019\uff0c\u5728\u4e00\u672c\u4e66\u4e0a\u770b\u5230\u7684\u3002\u201d</p><p>\u201c\u600e\u4e48\u53ef\u80fd\uff0c\u90a3\u4e66\u4ec0\u4e48\u540d\u5b57\uff1f\u201d</p><p>\u201c\u4e0d\u8bb0\u5f97\u4e86\uff0c\u53ea\u8bb0\u5f97\u5c01\u9762\u548c\u4e66\u9875\u5168\u90fd\u662f\u7c89\u7ea2\u8272\u7684\uff0c\u4e0d\u77e5\u9053\u662f\u600e\u4e48\u5f04\u51fa\u6765\u7684\u3002\u201d</p><p>\u201c\u7c89\u7ea2\u8272\uff0c\u90a3\u662f\u60c5\u8272\u5c0f\u8bf4\u5427\u3002\u201d</p><p>\u201c\u4e0d\u662f\u5c0f\u8bf4\u554a\uff0c\u5f88\u6b63\u7ecf\u7684\u8d44\u6599\u96c6\uff0c\u91cc\u9762\u8fd8\u6709\u5f88\u591a\u6709\u8da3\u7684\u6cd5\u672f\uff0c\u53ef\u60dc\u6211\u770b\u7684\u662f\u7b80\u5316\u7248\u672c\uff0c\u53ea\u6709\u7b80\u4ecb\u6ca1\u6709\u8be6\u7ec6\u5185\u5bb9\uff0c\u6bd4\u5982\u4ec0\u4e48\u8131\u8863\u672f\u3001\u9ad8\u6f6e\u8bc5\u5492\u2026\u2026\u201d</p><p>\u201c\u90a3\u53eb\u7c89\u7ea2\u4e4b\u4e66\uff01\u201d\u6885\u83f2\u65af\u5728\u4ed6\u989d\u5934\u4e0a\u8f7b\u8f7b\u6572\u4e86\u4e00\u4e0b\uff0c\u201c\u4e00\u7fa4\u65e0\u804a\u7684\u5deb\u5e08\u5f04\u51fa\u6765\u7684\u9a97\u4eba\u4e1c\u897f\uff0c\u4f60\u8fd8\u771f\u76f8\u4fe1\u3002\u201d</p><p>\u201c\u53ef\u662f\u6211\u89c9\u5f97\u8bf4\u5f97\u5f88\u6709\u9053\u7406\u554a\u2026\u2026\u53ef\u60dc\u6ca1\u6709\u5b8c\u5168\u7248\u672c\uff0c\u5426\u5219\u6211\u8fd8\u771f\u60f3\u8bd5\u8bd5\u2026\u2026\u201d</p><p>\u201c\u4e0d\u2014\u2014\u5141\u2014\u2014\u8bb8\uff01\u201d</p><p>\u8fd9\u6b21\u8c08\u8bdd\u4e0d\u4e86\u4e86\u4e4b\uff0c\u6ca1\u6709\u7ee7\u7eed\u3002\u8f6c\u773c\u4e4b\u95f4\uff0c\u5df2\u7ecf\u5230\u4e86\u7eff\u8349\u8282\uff0c\u53bb\u5e74\u6b63\u662f\u5728\u8fd9\u4e00\u5929\uff0c\u743c\u6069\u8d70\u51fa\u4e86\u9634\u9b42\u57ce\u3002\u56de\u9996\u8fc7\u53bb\u4e00\u5e74\u7684\u7ecf\u5386\uff0c\u603b\u6709\u79cd\u604d\u82e5\u68a6\u5e7b\u7684\u611f\u89c9\uff0c\u4eff\u4f5b\u8fd9\u4e00\u5207\u5e76\u975e\u771f\u5b9e\u3002</p><p>\u201c\u597d\u50cf\u628a\u4e00\u8f88\u5b50\u53ef\u80fd\u906d\u9047\u7684\u7cbe\u5f69\uff0c\u90fd\u63d0\u524d\u900f\u652f\u4e86\u4f3c\u7684\u3002\u201d</p><p>\u201c\u6211\u60f3\u8fd8\u65e9\u7740\u5462\u3002\u201d\u6885\u83f2\u65af\u82e5\u6709\u6df1\u610f\u5730\u56de\u7b54\u3002</p><p>\u201c\u8fd8\u662f\u5148\u60f3\u60f3\u600e\u4e48\u5bf9\u4ed8\u74dc\u7406\u5fb7\u65af\u5427\u3002\u201d\u5979\u8865\u5145\u3002</p><p>\u5c31\u5728\u6628\u5929\uff0c\u6d3e\u51fa\u7684\u5de1\u903b\u961f\u53d1\u73b0\u6668\u70bc\u57ce\u9644\u8fd1\u7684\u5730\u5e95\u901a\u9053\u91cc\uff0c\u6709\u5353\u5c14\u6d3b\u52a8\u8fc7\u7684\u75d5\u8ff9\uff0c\u5f88\u6709\u53ef\u80fd\u662f\u6765\u81ea\u74dc\u7406\u5fb7\u65af\uff0c\u8fd9\u5468\u56f4\u4e5f\u6ca1\u5176\u4ed6\u7684\u5353\u5c14\u57ce\u5e02\u4e86\u3002\u8fd9\u4e2a\u6d88\u606f\u8ba9\u5927\u5bb6\u90fd\u5f88\u7d27\u5f20\uff0c\u6668\u70bc\u57ce\u8352\u5e9f\u5343\u5e74\uff0c\u65e9\u5c31\u88ab\u4eba\u9057\u5fd8\uff0c\u8fd9\u4e5f\u662f\u4f0a\u8389\u4e1d\u7fe0\u4fe1\u5f92\u770b\u4e2d\u6b64\u5730\u7684\u4e00\u4e2a\u539f\u56e0\u3002\u539f\u672c\u9884\u8ba1\u53ea\u8981\u5c0f\u5fc3\u8c28\u614e\u4e9b\uff0c\u4f30\u8ba1\u4e09\u5e74\u4e94\u8f7d\uff0c\u751a\u81f3\u5341\u51e0\u5e74\u51e0\u5341\u5e74\uff0c\u90fd\u672a\u5fc5\u4f1a\u88ab\u74dc\u7406\u5fb7\u65af\u57ce\u53d1\u89c9\uff0c\u6709\u4ece\u5bb9\u51c6\u5907\u7684\u65f6\u95f4\uff0c\u6ca1\u60f3\u5230\u8fd9\u624d\u51e0\u4e2a\u6708\u5c31\u66b4\u9732\u4e86\u3002</p><p>\u201c\u5317\u8fb9\u6709\u6d88\u606f\u4f20\u6765\uff0c\u8bf4\u6885\u4e3d\u7c73\u5353\u548c\u9c81\u65af\xb7\u5fb7\u745e\u5c14\u88ab\u7f57\u4e1d\u6559\u4f1a\u653b\u7834\u4e86\u3002\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u6574\u7406\u7740\u8fd1\u4e00\u6bb5\u65f6\u95f4\u6765\u7684\u8d44\u6599\u3002\u6668\u70bc\u57ce\u5df2\u7ecf\u6d3e\u51fa\u4e86\u5c11\u91cf\u5fe0\u8bda\u53ef\u9760\u53c8\u7cbe\u660e\u80fd\u5e72\u7684\u5353\u5c14\uff0c\u524d\u5f80\u74dc\u7406\u5fb7\u65af\u6253\u63a2\u6d88\u606f\uff0c\u5012\u4e5f\u83b7\u5f97\u4e86\u4e0d\u5c11\u6709\u4ef7\u503c\u7684\u60c5\u62a5\u3002\u6309\u7167\u4e8b\u5148\u7684\u534f\u8bae\uff0c\u4e5f\u5411\u4f0a\u5361\u6c99\u57ce\u4f20\u4e86\u4e00\u4efd\uff0c\u4f5c\u4e3a\u77ee\u4eba\u63f4\u52a9\u7684\u56de\u62a5\u3002</p><p>\u201c\u54e6\uff0c\u6b27\u51ef\u66fe\u7ecf\u63d0\u5230\u8fc7\u8fd9\u4ef6\u4e8b\u3002\u201d</p><p>\u8fd9\u4e24\u5ea7\u5353\u5c14\u57ce\u5e02\u90fd\u4e0d\u4fe1\u4ef0\u7f57\u4e1d\uff0c\u6885\u4e3d\u7c73\u5353\u7684\u5c45\u6c11\u4e3b\u8981\u4fe1\u4ef0\u7ef4\u4f26\u548c\u9f50\u96c5\u6e29\u7eb1\u4e3d\uff0c\u9c81\u65af\xb7\u5fb7\u745e\u5c14\u5219\u662f\u5173\u7eb3\u5fb7\u6559\u4f1a\u7684\u5927\u672c\u8425\uff0c\u57ce\u4e2d\u516d\u4e07\u5353\u5c14\u5168\u90fd\u662f\u4ed6\u7684\u4fe1\u5f92\u3002\u5982\u4eca\u7f57\u4e1d\u7edf\u4e00\u5353\u5c14\u795e\u7cfb\uff0c\u81ea\u7136\u4e5f\u6709\u76f8\u5e94\u53d8\u52a8\u4e86\u3002</p><p>\u201c\u4f46\u4ed6\u6709\u6ca1\u6709\u8bf4\u8fc7\uff0c\u653b\u57ce\u7684\u519b\u961f\uff0c\u9664\u4e86\u7f57\u4e1d\u7684\u4fe1\u5f92\u4e4b\u5916\uff0c\u8fd8\u6709\u4e00\u4f19\u77f3\u5de8\u4eba\u548c\u4ea1\u7075\u3002\u201d</p><p>\u201c\u77f3\u5de8\u4eba\u548c\u4ea1\u7075\uff1f\u201d</p><p>\u8fd9\u5012\u662f\u4e2a\u610f\u5916\u7684\u6d88\u606f\uff0c\u7f57\u4e1d\u7684\u6559\u4f1a\u5bf9\u4ea1\u7075\u5386\u6765\u5e76\u4e0d\u53cb\u5584\uff0c\u5982\u4eca\u5c45\u7136\u4f1a\u8054\u624b\u653b\u57ce\uff0c\u7740\u5b9e\u4ee4\u4eba\u60ca\u8bb6\uff0c\u81f3\u4e8e\u77f3\u5de8\u4eba\u2026\u2026\u743c\u6069\u53ea\u77e5\u9053\u8fd9\u662f\u4e00\u79cd\u6bd4\u8f83\u7f55\u89c1\u7684\u5de8\u4eba\u4e9a\u79cd\uff0c\u5177\u4f53\u5c31\u4e0d\u6e05\u695a\u4e86\u3002</p><p>\u201c\u77f3\u5de8\u4eba\u6781\u5c11\uff0c\u4f46\u5e7d\u6697\u5730\u57df\u91cc\u6070\u597d\u6709\u4e00\u4e2a\u5730\u65b9\uff0c\u662f\u4e2a\u77f3\u5de8\u4eba\u805a\u5c45\u7684\u6751\u9547\uff0c\u201d\u6885\u83f2\u65af\u7ffb\u4e86\u7ffb\u8d44\u6599\uff0c\u201c\u51ef\u6069\u6d77\u59c6\u3002\u201d</p><p>\u201c\u5b83\u597d\u50cf\u8ddd\u79bb\u88ab\u653b\u7834\u7684\u4e24\u5ea7\u5353\u5c14\u57ce\u5e02\u90fd\u5f88\u8fdc\u5427\u3002\u201d\u743c\u6069\u8fd8\u9690\u7ea6\u6709\u70b9\u5370\u8c61\u3002</p><p>\u201c\u662f\uff0c\u4f46\u5b83\u8ddd\u79bb\u7075\u5438\u602a\u7684\u5965\u7075\u591a\u5c14\u5374\u975e\u5e38\u8fd1\u3002\u201d</p><p>\u201c\u54e6\u3002\u201d</p><p>\u8fd9\u4e2a\u8bdd\u9898\u6ca1\u6709\u7ee7\u7eed\u8fdb\u884c\uff0c\u8fdc\u65b9\u7684\u4e8b\u60c5\u6682\u65f6\u65e0\u9700\u64cd\u5fc3\uff0c\u8fd8\u662f\u5148\u8003\u8651\u600e\u4e48\u9762\u5bf9\u773c\u524d\u7684\u654c\u4eba\u518d\u8bf4\u3002\u5f53\u7136\uff0c\u77ed\u671f\u4e4b\u5185\uff0c\u74dc\u7406\u5fb7\u65af\u5e94\u8be5\u4e5f\u65e0\u529b\u8fdb\u653b\uff0c\u4f46\u5b83\u6839\u57fa\u6df1\u539a\uff0c\u6062\u590d\u8d77\u6765\u4fbf\u5feb\u5f97\u591a\uff0c\u65f6\u95f4\u8d8a\u957f\u5c31\u8d8a\u4e0d\u5229\u3002</p><p>\u201c\u5f97\u60f3\u529e\u6cd5\u7ed9\u5979\u4eec\u5236\u9020\u70b9\u9ebb\u70e6\u3002\u201d\u6885\u83f2\u65af\u8bf4\u3002</p><p>\u201c\u6211\u4eec\u66f4\u6ca1\u80fd\u529b\u4e3b\u52a8\u8fdb\u653b\u5427\u3002\u201d</p><p>\u201c\u90a3\u5f53\u7136\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u6211\u7684\u610f\u601d\u662f\uff1a\u6211\u4eec\u53bb\u6253\u52ab\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u74dc\u7406\u5fb7\u65af\u8fd9\u79cd\u89c4\u6a21\u7684\u5927\u57ce\u5e02\uff0c\u5546\u4e1a\u81ea\u7136\u53d1\u8fbe\uff0c\u548c\u5e7d\u6697\u5730\u57df\u7684\u5404\u5927\u57ce\u5e02\u90fd\u6709\u8d38\u6613\u5f80\u6765\u3002\u524d\u6bb5\u65f6\u95f4\u56e0\u4e3a\u7f57\u4e1d\u6c89\u5bc2\uff0c\u4e3b\u6bcd\u4eec\u6015\u8d70\u6f0f\u6d88\u606f\uff0c\u4e0b\u4e86\u5c01\u57ce\u4ee4\uff0c\u5546\u961f\u4e0d\u8bb8\u51fa\u57ce\uff0c\u5bfc\u81f4\u5e02\u573a\u840e\u7f29\uff0c\u635f\u5931\u5f88\u5927\u3002\u5982\u4eca\u8fd9\u9053\u7981\u4ee4\u5df2\u7ecf\u89e3\u9664\u4e86\uff0c\u6839\u636e\u60c5\u62a5\uff0c\u8fc7\u51e0\u5929\u5c31\u6709\u4e00\u652f\u5546\u961f\u4ece\u74dc\u7406\u5fb7\u65af\u51fa\u53d1\uff0c\u524d\u5f80\u5317\u65b9\u7684\u827e\u745e\u5fb7\u6797\u3002</p><p>\u4e16\u754c\u4e0a\u7684\u4e8b\u60c5\uff0c\u8981\u8bba\u6210\u672c\u4f4e\uff0c\u6536\u76ca\u5feb\uff0c\u83ab\u8fc7\u4e8e\u62a2\u52ab\uff0c\u4f55\u51b5\u8fd9\u8fd8\u662f\u80fd\u58ee\u5927\u81ea\u5df1\uff0c\u524a\u5f31\u654c\u624b\u7684\u826f\u7b56\u3002\u6885\u83f2\u65af\u7684\u63d0\u8bae\u7acb\u523b\u5f97\u5230\u8d5e\u540c\uff0c\u5927\u5bb6\u7d27\u9523\u5bc6\u9f13\u5730\u5f00\u59cb\u51c6\u5907\u3002</p><p>\u548c\u5730\u8868\u4e16\u754c\u6bd4\u8d77\u6765\uff0c\u5728\u5e7d\u6697\u5730\u57df\u91cc\u6253\u52ab\u5546\u961f\uff0c\u4f18\u52bf\u662f\u5bb9\u6613\u9690\u853d\uff0c\u53cd\u6b63\u5230\u5904\u90fd\u662f\u9ed1\u6697\uff1b\u52a3\u52bf\u5219\u662f\u4f60\u5f88\u96be\u786e\u5b9a\u5bf9\u65b9\u7684\u884c\u8fdb\u8def\u7ebf\u3002\u5730\u5e95\u901a\u9053\u7eb5\u6a2a\u4ea4\u9519\uff0c\u4eff\u4f5b\u8718\u86db\u7f51\u4e00\u822c\uff0c\u4ece\u74dc\u7406\u5fb7\u65af\u5230\u827e\u745e\u5fb7\u6797\uff0c\u4e2d\u95f4\u6709\u4e03\u516b\u6761\u8def\u7ebf\u53ef\u4f9b\u9009\u62e9\uff0c\u7ed9\u52ab\u532a\u5e26\u6765\u4e86\u5f88\u5927\u7684\u4e0d\u4fbf\u3002\u6668\u70bc\u57ce\u6d3e\u51fa\u7684\u63a2\u5b50\u6ca1\u6cd5\u6df7\u8fdb\u5546\u961f\u91cc\uff0c\u4e5f\u6253\u63a2\u4e0d\u51fa\u5177\u4f53\u4fe1\u606f\uff0c\u4e8e\u662f\u5927\u5bb6\u5546\u8bae\u4e4b\u540e\uff0c\u53ea\u80fd\u6311\u9009\u4e86\u4e09\u6761\u6700\u53ef\u80fd\u7684\u8def\u7ebf\uff0c\u5206\u5175\u57cb\u4f0f\u3002</p><p>\u7ecf\u8fc7\u8fd9\u6bb5\u65f6\u95f4\u7684\u6574\u987f\uff0c\u5982\u4eca\u5728\u6668\u70bc\u57ce\u91cc\uff0c\u683c\u5c40\u5df2\u7ecf\u5f62\u6210\u4e86\u4e09\u4e2a\u6d3e\u7cfb\u3002\u4f0a\u8389\u4e1d\u7fe0\u7684\u5353\u5c14\u4fe1\u5f92\u4f5c\u4e3a\u4e00\u6d3e\uff0c\u63a8\u9009\u4e86\u4e00\u4f4d\u6b4c\u548f\u8005\u7387\u9886\uff1b\u743c\u6069\u548c\u6885\u83f2\u65af\u6536\u7f16\u7684\u5353\u5c14\u6218\u4fd8\uff0c\u539f\u7ef4\u4f26\u548c\u9f50\u96c5\u6e29\u7eb1\u4e3d\u6559\u5f92\uff0c\u4e5f\u4f5c\u4e3a\u4e00\u6d3e\uff1b\u800c\u6240\u6709\u7684\u7cbe\u7075\u90fd\u5212\u5f52\u5230\u963f\u5fd2\u59ae\u624b\u4e0b\uff0c\u81ea\u6210\u4e00\u6d3e\u3002\u81f3\u4e8e\u91d1\u95ea\u95ea\uff0c\u81ea\u4ece\u4e0a\u6b21\u6218\u8d25\uff0c\u5979\u4fbf\u4e3b\u52a8\u653e\u5f03\u4e86\u9886\u5bfc\u6743\uff0c\u5982\u4eca\u662f\u57ce\u4e2d\u58eb\u5175\u7684\u6b66\u6280\u6559\u5b98\u3002</p><p>\u4e3a\u4e86\u4fbf\u4e8e\u79f0\u547c\uff0c\u6709\u4eba\u505a\u4e86\u4e09\u4e2a\u4ee3\u53f7\uff0c\u201c\u6545\u571f\u201d\u3001\u201c\u6d41\u4ea1\u201d\u548c\u201c\u65c5\u5c45\u201d\uff0c\u5206\u522b\u6307\u4ee3\u8fd9\u4e09\u4e2a\u6d3e\u7cfb\uff0c\u5df2\u7ecf\u88ab\u5e7f\u6cdb\u63a5\u53d7\u3002\u743c\u6069\u4e0d\u660e\u767d\u5176\u4e2d\u542b\u4e49\uff0c\u66fe\u7ecf\u5411\u6885\u83f2\u65af\u8bf7\u6559\u3002</p><p>\u201c\u8fd9\u4e09\u4e2a\u4ee3\u53f7\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d</p><p>\u201c\u90a3\u4e9b\u5353\u5c14\u539f\u672c\u8981\u53bb\u5730\u8868\uff0c\u73b0\u5728\u6539\u4e86\u8ba1\u5212\uff0c\u4f9d\u65e7\u7559\u5728\u5e7d\u6697\u5730\u57df\uff0c\u6240\u4ee5\u5c31\u53eb\u6545\u571f\u4e86\u3002\u201d</p><p>\u201c\u90a3\u6d41\u4ea1\u5462\uff1f\u201d</p><p>\u201c\u6211\u4eec\u624b\u4e0b\u7684\u8fd9\u4e9b\u5353\u5c14\uff0c\u65e2\u5931\u53bb\u4e86\u795e\u7947\uff0c\u53c8\u88ab\u9a71\u9010\u51fa\u4e86\u5bb6\u56ed\uff0c\u4e00\u8def\u6d41\u4ea1\u81f3\u6b64\u2014\u2014\u4e0d\u662f\u5f88\u7b26\u5408\u4e48\u3002\u201d</p><p>\u201c\u90a3\u65c5\u5c45\u7684\u610f\u601d\uff1f\u201d</p><p>\u201c\u90a3\u4e9b\u7cbe\u7075\u662f\u4ece\u5730\u8868\u8fc7\u6765\u5e2e\u5fd9\u7684\uff0c\u65e2\u4e0d\u662f\u571f\u8457\uff0c\u53c8\u4e0d\u662f\u6d41\u6c11\uff0c\u6682\u5c45\u4e8e\u6b64\uff0c\u7b97\u662f\u5ba2\u4eba\uff0c\u6240\u4ee5\u5c31\u662f\u65c5\u5c45\u4e86\u3002\u201d</p><p>\u201c\u8fd9\u4e2a\uff0c\u5012\u4e5f\u8bf4\u5f97\u901a\uff0c\u4f46\u603b\u89c9\u5f97\u602a\u602a\u7684\uff0c\u4e3a\u4ec0\u4e48\u8981\u53d6\u8fd9\u4e09\u4e2a\u540d\u5b57\u3002\u201d</p><p>\u201c\u54e6\uff0c\u90a3\u662f\u4e00\u672c\u82f1\u96c4\u4f20\u5947\u5c0f\u8bf4\u7684\u5377\u540d\uff0c\u201d\u6885\u83f2\u65af\u89e3\u91ca\uff0c\u201c\u5317\u5730\u8457\u540d\u7684\u541f\u6e38\u8bd7\u4eba\u8428\u5c14\u74e6\u591a\uff0c\u524d\u4e9b\u5e74\u51fa\u7248\u4e86\u4e00\u672c\u5c0f\u8bf4\uff0c\u51fa\u7248\u65f6\u5206\u4e09\u5377\uff0c\u5206\u522b\u4ee5\u6545\u571f\u3001\u6d41\u4ea1\u548c\u65c5\u5c45\u4e3a\u540d\u3002\u8fd9\u672c\u4e66\u5f88\u6709\u540d\u7684\uff0c\u4f60\u6ca1\u8bfb\u8fc7\uff1f\u201d</p><p>\u201c\u6ca1\u8bfb\u8fc7\u3002\u201d</p><p>\u201c\u5979\u4eec\u5168\u90fd\u8bfb\u8fc7\u8fd9\u672c\u4e66\u3002\u6545\u4e8b\u91cc\u7684\u4e3b\u89d2\u662f\u4e00\u4e2a\u9b54\u7d22\u5e03\u83b1\u57ce\u7684\u9ed1\u6697\u7cbe\u7075\uff0c\u5929\u624d\u7684\u6218\u58eb\uff0c\u6c38\u8fdc\u4fdd\u6301\u7740\u5584\u826f\u7684\u672c\u6027\u3002\u4ed6\u80cc\u53db\u4e86\u5bb6\u65cf\uff0c\u6446\u8131\u4e86\u7f57\u4e1d\u7684\u90aa\u6076\u63a7\u5236\uff0c\u6700\u7ec8\u91cd\u8fd4\u5730\u8868\uff0c\u548c\u4eba\u7c7b\u3001\u77ee\u4eba\u3001\u7cbe\u7075\u6210\u4e3a\u670b\u53cb\u3002\u8fd9\u672c\u4e66\u98ce\u9761\u4e00\u65f6\uff0c\u7eed\u96c6\u4e0d\u65ad\u2014\u2014\u636e\u8bf4\u6700\u65b0\u7684\u7eed\u96c6\u91cc\uff0c\u4e3b\u89d2\u5df2\u7ecf\u548c\u4e00\u4f4d\u4eba\u7c7b\u5973\u5b50\u7ed3\u5a5a\u4e86\u3002\u201d</p><p>\u201c\u5514\uff0c\u6211\u660e\u767d\u4e86\uff0c\u8fd9\u4e2a\u4e3b\u89d2\u5206\u660e\u662f\u4f0a\u8389\u4e1d\u7fe0\u7684\u4fe1\u5f92\uff0c\u6240\u4ee5\u5979\u4eec\u90fd\u8bfb\u8fc7\u8fd9\u672c\u4e66\u3002\u201d</p><p>\u201c\u9519\u4e86\uff0c\u821e\u8e48\u5973\u58eb\u7684\u6559\u4f1a\u539f\u672c\u662f\u60f3\u63a5\u7eb3\u4ed6\u7684\uff0c\u8c01\u6599\u88ab\u6885\u4e3d\u51ef\u7684\u4e00\u4e2a\u7267\u5e08\u6377\u8db3\u5148\u767b\uff0c\u62a2\u5148\u4e0b\u624b\u4e86\u3002\u201d</p><p>\u201c\u6885\u4e3d\u51ef\u2026\u2026\u6211\u8bb0\u5f97\u5979\u4e0d\u662f\u68ee\u6797\u5973\u795e\u4e48\uff0c\u4f3c\u4e4e\u626f\u4e0d\u4e0a\u5173\u7cfb\u5427\u3002\u201d</p><p>\u201c\u662f\u554a\uff0c\u539f\u672c\u5c31\u626f\u4e0d\u4e0a\u4ec0\u4e48\u5173\u7cfb\uff0c\u4e0d\u77e5\u9053\u90a3\u4e2a\u4f5c\u8005\u600e\u4e48\u60f3\u7684\u3002\u6240\u4ee5\u8fd9\u4e9b\u821e\u8e48\u5973\u58eb\u7684\u4fe1\u5f92\u90fd\u5f88\u607c\u706b\uff0c\u5927\u529b\u6279\u5224\uff0c\u53cd\u800c\u5f04\u5f97\u8fd9\u672c\u4e66\u5728\u6559\u4f1a\u5185\u90e8\u4eba\u4eba\u7686\u77e5\u4e86\u3002\u201d</p><p>\u201c\u660e\u767d\u4e86\u3002\u201d</p><p>\u786e\u5b9a\u4e86\u4e09\u6761\u53ef\u80fd\u7684\u8def\u7ebf\uff0c\u4fbf\u7531\u4e09\u6d3e\u4eba\u9a6c\u5404\u81ea\u8d1f\u8d23\u5176\u4e00\uff0c\u8c01\u9047\u4e0a\u5c31\u7531\u8c01\u89e3\u51b3\u3002\u743c\u6069\u4ece\u201c\u6d41\u4ea1\u201d\u4e2d\u62bd\u53d6\u4e86\u4e03\u540d\u6218\u58eb\uff0c\u52a0\u4e0a\u81ea\u5df1\u3001\u6885\u83f2\u65af\u548c\u5fb7\u53e4\u62c9\uff0c\u4e00\u5171\u5341\u4eba\uff0c\u9009\u53d6\u4e86\u4e00\u5904\u65ad\u5d16\u4e4b\u4e0a\u57cb\u4f0f\u7740\uff0c\u9759\u9759\u7b49\u5f85\u3002</p><p>\u6574\u6574\u7b49\u5f85\u4e86\u4e24\u5929\uff0c\u6ca1\u6709\u534a\u70b9\u52a8\u9759\uff0c\u743c\u6069\u4f30\u8ba1\u81ea\u5df1\u8fd9\u8fb9\u662f\u843d\u7a7a\u4e86\uff0c\u5546\u961f\u5e94\u8be5\u662f\u8d70\u522b\u7684\u8def\u3002\u5927\u5bb6\u90fd\u6709\u4e9b\u61c8\u6020\uff0c\u51c6\u5907\u6536\u5de5\u56de\u57ce\uff0c\u4fbf\u5728\u6b64\u65f6\uff0c\u524d\u65b9\u4f20\u6765\u52a8\u9759\uff0c\u4ece\u58f0\u97f3\u5224\u65ad\u662f\u6709\u4e00\u961f\u4eba\u6b63\u5728\u9760\u8fd1\uff0c\u6570\u91cf\u8fd8\u4e0d\u5c11\u3002</p><p>\u730e\u7269\u51fa\u73b0\u4e86\u3002</p></article>"}}}]);
