(window.webpackJsonp=window.webpackJsonp||[]).push([[3],{26:function(p){p.exports={chapterTitle:null,title:"\u7b2c100\u7ae0 \u8bf7\u541b\u5165\u74ee",content:"<article><h2>\u7b2c100\u7ae0 \u8bf7\u541b\u5165\u74ee</h2><p>\u3000\u300012\u67086\u65e5\uff0c\u8bb8\u9e3f\u4f2f\u5728\u6d77\u5dde\u65e5\u62a5\u53d1\u8868\u9898\u4e3a\u300a\u9b45\u529b\u6d77\u5dde\u7684\u57ce\u5e02\u4e4b\u672c\u300b\u7684\u6587\u7ae0\uff0c\u5360\u636e\u6574\u4e2a\u793e\u4f1a\u8bc4\u8bba\u7248\u6574\u7248\u7684\u7bc7\u5e45\uff0c\u63d0\u51fa\u6316\u6398\u81ea\u8eab\u7279\u70b9\u3001\u8fdb\u884c\u73af\u5883\u6574\u6cbb\u3001\u5851\u9020\u6d77\u5dde\u7279\u5f81\u3001\u8fdb\u884c\u57ce\u5e02\u5efa\u8bbe\u7684\u5168\u5c40\u89c2\u70b9\u3002</p><p>\u3000\u3000\u8fd9\u7bc7\u6587\u7ae0\u5168\u76d8\u5426\u5b9a\u4e00\u5e74\u524d\u5e02\u5e38\u59d4\u4f1a\u8bae\u63d0\u51fa\u5e76\u7531\u4eba\u5927\u5e38\u52a1\u4f1a\u8bae\u8ba8\u8bba\u901a\u8fc7\u7684\u57ce\u5e02\u89c4\u5212\u601d\u8def\u3002</p><p>\u3000\u3000\u8bb8\u9e3f\u4f2f\u662f\u4e00\u4e2a\u65e9\u5c31\u8fdc\u79bb\u6d77\u5dde\u5b98\u573a\u7684\u4eba\u7269\uff0c\u5728\u67d0\u4e9b\u4eba\u770b\u6765\u8fd9\u7bc7\u6587\u7ae0\u8fdc\u8fdc\u903e\u8d8a\u4ed6\u7684\u6c11\u95f4\u8eab\u4efd\uff0c\u5728\u4ed6\u4eec\u770b\u6765\uff0c\u8bb8\u9e3f\u4f2f\u5b8c\u5168\u6ca1\u6709\u5199\u8fd9\u7bc7\u6587\u7ae0\u7684\u7acb\u573a\uff0c\u4f46\u662f\u4ed6\u5374\u63d0\u51fa\u6765\u4e86\uff0c\u800c\u4e14\u5360\u636e\u6d77\u5dde\u65e5\u62a5\u793e\u8bba\u7248\u6574\u7248\u7684\u7bc7\u5e45\uff0c\u4e0d\u63d0\u8fd9\u7bc7\u6587\u7ae0\u5728\u5e02\u6c11\u4e2d\u5f15\u8d77\u7684\u8bae\u8bba\uff0c\u5bf9\u6d77\u5dde\u5e02\u5b98\u573a\u7684\u9707\u52a8\u662f\u5de8\u5927\u7684\u3002</p><p>\u3000\u3000\u4e07\u5411\u524d\u770b\u5230\u8fd9\u7bc7\u6587\u7ae0\uff0c\u7acb\u5373\u607c\u7f9e\u6210\u6012\u7684\u7ed9\u5ba3\u4f20\u90e8\u90e8\u957f\u6768\u6587\u6e05\u6253\u7535\u8bdd\u8d28\u95ee\u8fd9\u4e8b\u662f\u4e0d\u662f\u5ba3\u4f20\u90e8\u7684\u6388\u610f\uff0c\u6768\u6587\u6e05\u6b64\u524d\u8fd8\u7422\u78e8\u8bb8\u9e3f\u4f2f\u4e4b\u524d\u7684\u8eab\u4efd\uff0c\u6000\u7591\u662f\u4e07\u5411\u524d\u5728\u91cc\u9762\u6363\u9b3c\uff0c\u662f\u4ed6\u624d\u8ba9\u8bb8\u9e3f\u4f2f\u6574\u51fa\u8fd9\u7bc7\u6587\u7ae0\u3002\u65e2\u7136\u8ddf\u4e07\u5411\u524d\u65e0\u5173\uff0c\u6768\u6587\u6e05\u5f53\u7136\u80fd\u7406\u89e3\u4e07\u5411\u524d\u4e3a\u4ec0\u4e48\u4f1a\u607c\u7f9e\u6210\u6012\uff0c\u4e00\u4e2a\u5728\u4ed6\u8eab\u8fb9\u5de5\u4f5c\u4e86\u516d\u5e74\uff0c\u53ef\u4ee5\u8bf4\u5bf9\u4ed6\u8eab\u4e0a\u6bcf\u6839\u6c57\u6bdb\u90fd\u6e05\u695a\u7684\u4eba\uff0c\u6c89\u5bc2\u4e86\u8fd9\u4e48\u591a\u5e74\uff0c\u7a81\u7136\u641e\u51fa\u8fd9\u4e48\u5927\u52a8\u9759\uff0c\u5f53\u7136\u8981\u8ba9\u4ed6\u60ca\u614c\u751a\u81f3\u6050\u60e7\u4e86\u3002</p><p>\u3000\u3000\u6768\u6587\u6e05\u7ed9\u6d77\u5dde\u65e5\u62a5\u793e\u4e3b\u7bc7\u6302\u4e86\u7535\u8bdd\uff0c\u8be2\u95ee\u6b64\u4e8b\uff0c\u65e5\u62a5\u793e\u4e3b\u7bc7\u8bf4\u662f\u793e\u8bba\u7248\u7f16\u8f91\u8d1f\u8d23\u91c7\u7a3f\uff0c\u4ed6\u73b0\u5728\u624d\u77e5\u9053\u8fd9\u4e8b\uff0c\u5df2\u7ecf\u63a5\u4e86\u597d\u51e0\u4e2a\u7535\u8bdd\u8be2\u95ee\u6b64\u4e8b\u3002\u6768\u6587\u6e05\u604d\u7136\u77e5\u9053\u662f\u8c01\u5728\u5e55\u540e\u5e03\u7f6e\uff0c\u522b\u4eba\u4ece\u65e5\u62a5\u793e\u95ee\u4e0d\u51fa\u4e1c\u897f\uff0c\u90a3\u8fd8\u6b63\u5e38\uff0c\u65e5\u62a5\u793e\u4e5f\u6709\u81ea\u5df1\u7684\u7acb\u573a\u3002\u5ba3\u4f20\u90e8\u7ba1\u7740\u65e5\u62a5\u793e\uff0c\u6768\u6587\u6e05\u95ee\u4e0d\u51fa\u6240\u4ee5\u7136\u6765\uff0c\u90a3\u8868\u660e\u4e0d\u662f\u5c31\u5468\u5bcc\u660e\uff0c\u5c31\u662f\u5510\u5b66\u8c26\u5728\u5e55\u540e\u76f4\u63a5\u6307\u4f7f\uff0c\u5c31\u76ee\u524d\u7684\u5c40\u52bf\uff0c\u5468\u5bcc\u660e\u4e0e\u5510\u5b66\u8c26\u662f\u540c\u7a7f\u4e00\u6761\u88e4\u5b50\u7684\u3002</p><p>\u3000\u3000\u6768\u6587\u6e05\u51b3\u5b9a\u5728\u6b64\u4e8b\u4e0a\u4fdd\u6301\u6c89\u9ed8\uff0c\u4f46\u770b\u4e8b\u6001\u53d1\u5c55\uff0c\u624d\u505a\u51b3\u5b9a\u3002</p><p>\u3000\u3000\u7b2c\u4e8c\u5929\uff0c\u6d77\u5dde\u5927\u5b66\u57ce\u89c4\u9662\u6559\u6388\u5728\u6d77\u5dde\u65e5\u62a5\u520a\u767b\u9898\u4e3a\u300a\u57ce\u5e02\u89c4\u5212\u7684\u65b0\u89c6\u70b9\u300b\u7684\u6587\u7ae0\u3002</p><p>\u3000\u3000\u4e07\u5411\u524d\u607c\u7f9e\u6210\u6012\u7684\u4e8b\u60c5\u5374\u7531\u4ed6\u8eab\u8fb9\u7684\u5de5\u4f5c\u4eba\u5458\u4f20\u4e86\u51fa\u53bb\uff0c\u5f20\u606a\u542c\u5230\u7238\u7238\u8bf4\u8d77\u8fd9\u4e8b\uff0c\u95ee\u9053\uff1a\u201c\u662f\u4e0d\u662f\u5510\u4f2f\u4f2f\u5b89\u6392\u7684\uff0c\u9632\u6b62\u4e07\u5411\u524d\u89c1\u98ce\u8f6c\u8235\uff0c\u5c06\u529f\u52b3\u635e\u8fc7\u53bb\uff1f\u201d\u5f20\u77e5\u884c\u7b11\u7740\u8bf4\uff1a\u201c\u6ca1\u90a3\u4e48\u5bb9\u6613\uff0c\u5468\u5bcc\u660e\u4e0b\u5348\u7684\u4f1a\u8bae\u5df2\u7ecf\u8981\u6c42\u5e02\u89c4\u5212\u5c40\u3001\u5e02\u89c4\u5212\u8bbe\u8ba1\u9662\u7ec4\u7ec7\u76f8\u5173\u7684\u8ba8\u8bba\uff0c\u4e4b\u524d\u7684\u89c4\u5212\u5de5\u4f5c\u6682\u65f6\u505c\u4e0b\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u6b64\u65f6\u8ddf\u5468\u5bcc\u660e\u4e89\u529f\u52b3\u662f\u4e0d\u660e\u667a\u7684\u3002</p><p>\u3000\u3000\u6d77\u5dde\u65e5\u62a5\u6587\u5b66\u7248\u540c\u65f6\u53d1\u8d77\u201c\u6d77\u5dde\u4e4b\u79c0\u201d\u7684\u5f81\u6587\u6d3b\u52a8\uff0c\u7b2c\u4e09\u5929\u5c31\u63a8\u51fa\u6c99\u7530\u53e4\u8857\u7684\u5f81\u6587\u5b50\u4e13\u9898\uff0c\u63a5\u4e0b\u6765\u5c06\u5c06\u9646\u7eed\u63a8\u51fa\u8c61\u5c71\u68ee\u6797\u3001\u758f\u6e2f\u6cb3\u3001\u9526\u6e56\u3001\u6d77\u5dde\u9526\u7ee3\u7b49\u5b50\u4e13\u9898\uff0c\u5bf9\u8bb8\u9e3f\u4f2f\u6587\u7ae0\u4e2d\u7684\u5851\u9020\u6d77\u5dde\u7279\u5f81\u7684\u8bba\u70b9\u8fdb\u884c\u5ef6\u4f38\u3002</p><p>\u3000\u3000\u5f20\u606a\u66f4\u5173\u5fc3\u5b8b\u57f9\u660e\u7684\u6001\u5ea6\uff0c\u7ecf\u5386\u65b0\u5149\u9020\u7eb8\u5382\u4e00\u4e8b\u4e4b\u540e\uff0c\u5b8b\u57f9\u660e\u65e0\u7591\u4f1a\u575a\u5b9a\u7684\u7ad9\u5230\u5510\u5b66\u8c26\u8fd9\u4e00\u8fb9\u3002\u8fd9\u4e48\u8bf4\u4e5f\u4e0d\u5b8c\u5168\u5bf9\uff0c\u5b8b\u57f9\u660e\u5e94\u8be5\u662f\u575a\u5b9a\u4e0d\u79fb\u7684\u7ad9\u5230\u5f90\u5b66\u5e73\u8fd9\u4e00\u8fb9\uff0c\u4ed6\u73b0\u5728\u8fd8\u6ca1\u6709\u8d44\u683c\u8ddf\u5f90\u5b66\u5e73\u642d\u4e0a\u5173\u7cfb\uff0c\u6240\u4ee5\u624d\u9000\u800c\u6c42\u5176\u6b21\u7684\u652f\u6301\u5510\u5b66\u8c26\u3002\u5f20\u606a\u53ea\u5173\u5fc3\u5b8b\u57f9\u660e\u4f1a\u4e0d\u4f1a\u88ab\u8fd9\u6b21\u8206\u8bba\u653b\u52bf\u78b0\u51fa\u601d\u60f3\u7684\u706b\u5149\uff0c\u522b\u4eba\u51fa\u6765\u662f\u8bba\u6218\uff0c\u9020\u7eb8\u5382\u8001\u5382\u6539\u9020\u6210\u996e\u98df\u5e7f\u573a\u53ef\u662f\u5b9e\u5b9e\u5728\u5728\u7684\u53ef\u4ee5\u6700\u5148\u62ff\u51fa\u6765\u7684\u8bd5\u70b9\uff0c\u4e0d\u6653\u5f97\u8981\u4e0d\u8981\u518d\u8ba9\u4eba\u63d0\u9192\u4e00\u4e0b\u4ed6\u3002</p><p>\u3000\u3000\u5728\u6b64\u4e4b\u524d\uff0c\u5434\u5929\u5b9d\u3001\u90b5\u81f3\u521a\u51e0\u6b21\u8981\u6c42\u8bf7\u5403\u996d\uff0c\u5f20\u606a\u90fd\u4ee5\u5b66\u4e60\u5f88\u7d27\u4e3a\u7406\u7531\u63a8\u8131\u6389\u3002\u5434\u5929\u5b9d\u8fd9\u6bb5\u65f6\u95f4\u4e5f\u627e\u4e0d\u5230\u5f20\u77e5\u884c\u3002\u5728\u8bb8\u9e3f\u4f2f\u6587\u7ae0\u53d1\u8868\u7684\u5f53\u5929\uff0c\u5c0f\u53d4\u5f20\u77e5\u975e\u4e5f\u5f02\u5e38\u5174\u594b\uff0c\u4ed6\u8981\u7ec4\u7ec7\u4e13\u5bb6\u5b66\u8005\u6316\u6398\u758f\u6e2f\u6cb3\u7684\u8bdd\u9898\uff0c\u8fd8\u8981\u62a2\u5728\u5176\u4ed6\u5de5\u7a0b\u516c\u53f8\u524d\u9762\u4e3b\u52a8\u62c9\u62e2\u6d77\u5dde\u5e02\u4e2d\u5c0f\u578b\u5de5\u7a0b\u4f01\u4e1a\u6302\u9760\u5230\u5b8f\u8fdc\u540d\u4e0b\uff0c\u8fd8\u8981\u4e0e\u5176\u4ed6\u5de5\u7a0b\u4f01\u4e1a\u901a\u6c14\uff0c\u8ba9\u758f\u6e2f\u6cb3\u758f\u6d5a\u5de5\u7a0b\u6210\u4e3a\u6d77\u5dde\u5e02\u73af\u5883\u6574\u6cbb\u7684\u7b2c\u4e00\u6b65\u3002</p><p>\u3000\u300012\u67088\u65e5\uff0c\u6b63\u6cf0\u96c6\u56e2\u6b63\u5f0f\u6536\u8d2d\u5609\u4fe1\uff0c\u6700\u7ec8\u4ee5\u4e00\u5343\u4e00\u767e\u4e07\u7684\u73b0\u91d1\u6536\u8d2d\u5609\u4fe185\uff05\u7684\u80a1\u4efd\uff0c\u6d77\u6cf0\u4f1a\u5728\u7231\u7acb\u4fe1\u7b2c\u4e00\u6b3e\u4e2d\u6587\u6570\u5b57\u624b\u673aGH398\u63a8\u51fa\u4e4b\u524d\u4e0e\u5609\u4fe1\u5408\u5e76\uff0c\u5408\u5e76\u540e\u7684\u516c\u53f8\u603b\u90e8\u4f1a\u8fc1\u56de\u7701\u57ce\uff0c\u4e24\u5bb6\u516c\u53f8\u5408\u5e76\u540e\u7684\u4eba\u4e8b\u5b89\u6392\u4f1a\u5728\u7a0d\u540e\u51e0\u5929\u7684\u4f1a\u8bae\u4e2d\u8ba8\u8bba\u51fa\u6765\u3002\u6d77\u88d5\u516c\u53f8\u5728\u5404\u5730\u7684\u5206\u90e8\u5c06\u5b8c\u5168\u5265\u79bb\u51fa\u6765\uff0c\u5e76\u5165\u65b0\u516c\u53f8\u4e4b\u4e2d\u3002</p><p>\u3000\u3000\u4e0d\u5f97\u4e0d\u627f\u8ba4\uff0c\u8fd9\u662f\u6b63\u6cf0\u96c6\u56e2\u76f8\u5f53\u6210\u529f\u7684\u4e00\u6b21\u8868\u6f14\u3002</p><p>\u3000\u3000\u5982\u679c\u4e0d\u662f\u6b63\u6cf0\u96c6\u56e2\u7684\u5484\u5484\u903c\u4eba\uff0c\u5f20\u606a\u89c9\u5f97\u8fd9\u6837\u7684\u7ed3\u679c\uff0c\u5bf9\u4e09\u65b9\u90fd\u4e0d\u7b97\u592a\u574f\u3002\u6b63\u6cf0\u96c6\u56e2\u662f\u5927\u8d62\u5bb6\u4e0d\u5047\uff0c\u4f46\u662f\u6d77\u88d5\u516c\u53f8\u6446\u8131\u4ee5\u524d\u7684\u5305\u88b1\uff0c\u8fd8\u5728\u6700\u7ec8\u91cd\u7ec4\u540e\u7684\u516c\u53f8\u91cc\u4fdd\u7559\u4e0d\u4f4e\u4e8e18\uff05\u7684\u80a1\u4efd\uff0c\u81ea\u5df1\u867d\u7136\u6709\u4e9b\u4e8f\uff0c\u4f46\u662f\u5728\u4e0d\u5230\u534a\u5e74\u7684\u65f6\u95f4\u91cc\uff0c\u4ece\u4e2d\u62ff\u51fa\u4e09\u767e\u4e07\u7684\u73b0\u91d1\uff0c\u4e5f\u80fd\u8ba9\u4eba\u5b89\u6170\u4e86\u3002</p><p>\u3000\u3000\u8fd9\u4e00\u5929\u6700\u540e\u4e24\u5802\u81ea\u4fee\u8bfe\uff0c\u8fd8\u662f\u674e\u829d\u82b3\u5750\u73ed\uff0c\u8fd8\u5f97\u6328\u5230\u653e\u665a\u5b66\u624d\u80fd\u5f80\u6821\u5916\u8d70\u3002</p><p>\u3000\u3000\u5434\u5929\u5b9d\u7684\u4e30\u7530\u8f66\u505c\u5728\u6821\u95e8\u5916\u7684\u573a\u5730\u4e0a\uff0c\u770b\u89c1\u5f20\u606a\u8d70\u51fa\u6821\u95e8\uff0c\u7f13\u7f13\u7684\u5f00\u8fc7\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u7740\u5434\u5929\u5b9d\u5806\u7740\u7b11\u63a8\u95e8\u4e0b\u8f66\uff0c\u5750\u4ed6\u65c1\u8fb9\u7684\u90a3\u4e2a\u7626\u7626\u9ad8\u9ad8\u7684\u9752\u5e74\u4eba\u5374\u6ca1\u6709\u52a8\u4f5c\uff0c\u8138\u4e0a\u4f3c\u4e4e\u542b\u7740\u7b11\uff0c\u773c\u775b\u91cc\u5374\u5f88\u51b7\u6de1\u3002\u5f20\u606a\u53cc\u624b\u62b1\u80f8\u770b\u7740\u5434\u5929\u5b9d\u7ed5\u5230\u8f66\u8fb9\u6765\uff1a\u201c\u5434\u7ecf\u7406\u5728\u6821\u95e8\u53e3\u5835\u6211\uff0c\u6709\u4ec0\u4e48\u4e8b\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e0d\u8fc7\u6765\u627e\u4f60\uff0c\u89c1\u4e0d\u7740\u4f60\u4eba\u554a\uff0c\u201d\u5434\u5929\u5b9d\u773c\u775b\u7ed9\u7b11\u5bb9\u6324\u6210\u4e00\u6761\u7f1d\uff0c\u201c\u4f60\u8981\u53bb\u54ea\u91cc\uff0c\u6211\u9001\u4f60\uff1f\u201d</p><p>\u3000\u3000\u201c\u9001\u6211\u53bb\u524d\u95e8\u5427\u3002\u201d\u5f20\u606a\u6253\u5f00\u8f66\u95e8\u5750\u5230\u540e\u5ea7\u3002\u5434\u5929\u5b9d\u53c8\u7ed5\u5230\u9a7e\u9a76\u4f4d\u7ed9\u5f20\u606a\u4ecb\u7ecd\u4ed6\u65c1\u8fb9\u7684\u90a3\u4eba\uff0c\u201c\u606a\u5c11\uff0c\u8fd9\u662f\u82b1\u56ed\u9152\u5e97\u7684\u90b5\u603b\uff0c\u4e4b\u524d\u4f60\u5c0f\u53d4\u8ddf\u4f60\u63d0\u8fc7\u3002\u201d</p><p>\u3000\u3000\u90b5\u81f3\u521a\u8f6c\u8fc7\u8eab\u6765\uff0c\u4f38\u51fa\u624b\u8bf4\u9053\uff1a\u201c\u8fd9\u51e0\u5929\u4e00\u76f4\u542c\u5434\u5927\u54e5\u8bf4\u4f60\u7684\u4e8b\uff0c\u902e\u5230\u4eca\u5929\u624d\u89c1\u5230\u4f60\uff0c\u665a\u4e0a\u6709\u6ca1\u6709\u65f6\u95f4\u4e00\u8d77\u5403\u4e2a\u996d\uff1f\u201d</p><p>\u3000\u3000\u90b5\u81f3\u521a\u5927\u7ea6\u4e09\u5341\u4e09\u56db\u5c81\uff0c\u8138\u7626\u957f\uff0c\u80a4\u8272\u6709\u4e9b\u82cd\u767d\uff0c\u773c\u795e\u575a\u5b9a\uff0c\u521a\u521a\u7684\u4e00\u4e1d\u51b7\u6de1\uff0c\u8fd9\u65f6\u5019\u5df2\u7ecf\u770b\u4e0d\u5230\u4e86\uff0c\u6362\u4e0a\u6e29\u548c\u7684\u7b11\u5bb9\u3002</p><p>\u3000\u3000\u201c\u54e6\uff0c\u5403\u996d\u5c31\u7b97\u4e86\u3002\u201d\u5f20\u606a\u8ddf\u4ed6\u63e1\u4e86\u63e1\u624b\uff0c\u4eba\u8737\u5230\u540e\u6392\u5ea7\u6905\u4e0a\uff0c\u8138\u4e0a\u9732\u51fa\u6ca1\u6709\u8bf4\u8bdd\u5174\u8da3\u7684\u8868\u60c5\uff0c\u5076\u5c14\u901a\u8fc7\u540e\u89c6\u955c\uff0c\u770b\u89c1\u90b5\u81f3\u521a\u4e00\u76f4\u5728\u89c2\u5bdf\u81ea\u5df1\u3002</p><p>\u3000\u3000\u8f66\u5230\u524d\u95e8\u65b0\u6d77\u901a\u5927\u53a6\uff0c\u5f20\u606a\u8ddf\u5434\u5929\u5b9d\u62db\u547c\u4e00\u58f0\u5c31\u4e0b\u4e86\u8f66\uff0c\u8d70\u4e0a\u53f0\u9636\uff0c\u542c\u89c1\u5434\u5929\u5b9d\u5728\u540e\u9762\u558a\u4ed6\uff0c\u56de\u5934\u770b\u89c1\u5434\u5929\u5b9d\u4e0e\u90b5\u81f3\u521a\u4e5f\u4e0b\u4e86\u8f66\uff0c\u4ece\u540e\u9762\u8ffd\u4e0a\u6765\u3002\u5f20\u606a\u7ad9\u5728\u53f0\u9636\u4e0a\uff0c\u5c45\u9ad8\u4e34\u4e0b\u770b\u7740\u5434\u5929\u5b9d\u3001\u90b5\u81f3\u521a\u4e24\u4eba\uff1a\u201c\u8fd8\u6709\u4ec0\u4e48\u4e8b\uff1f\u201d</p><p>\u3000\u3000\u201c\u5475\u5475\uff0c\u201d\u5434\u5929\u5b9d\u5c34\u5c2c\u7684\u7b11\u4e86\u7b11\uff0c\u201c\u90a3\u4e2a\u996e\u98df\u5e7f\u573a\u9879\u76ee\uff0c\u600e\u4e48\u5c31\u6ca1\u6709\u52a8\u9759\u4e86\uff0c\u597d\u50cf\u5468\u5382\u957f\u4e0d\u8d1f\u8d23\u8fd9\u5757\u4e86\uff1f\u201d</p><p>\u3000\u3000\u5434\u5929\u5b9d\u7b49\u4e86\u591a\u5c11\u5e74\u60f3\u6324\u8fdb\u524d\u95e8\u505a\u9910\u996e\uff0c\u8981\u4e48\u627e\u4e0d\u5230\u5408\u9002\u7684\u5730\u65b9\uff0c\u8981\u4e48\u5bf9\u65b9\u7d22\u53d6\u7684\u4ee3\u4ef7\u592a\u9ad8\uff0c\u597d\u4e0d\u5bb9\u6613\u627e\u5230\u8fd9\u4e2a\u673a\u4f1a\uff0c\u800c\u4e14\u627e\u90b5\u81f3\u521a\u8ba8\u8bba\u8fc7\u8fd9\u4e2a\u9879\u76ee\uff0c\u5982\u679c\u80fd\u597d\u597d\u64cd\u4f5c\u7684\u8bdd\uff0c\u9020\u7eb8\u5382\u8001\u5382\u8fd9\u4e00\u5757\uff0c\u6781\u53ef\u80fd\u6210\u4e3a\u6d77\u5dde\u9910\u996e\u4e1a\u6700\u53d1\u8fbe\u7684\u5730\u6bb5\u3002</p><p>\u3000\u3000\u5177\u4f53\u7684\u60c5\u51b5\u4e0d\u662f\u5f88\u6e05\u695a\uff0c\u5434\u5929\u5b9d\u5374\u6653\u5f97\u8fd9\u4e2a\u5c11\u5e74\u80fd\u591f\u51b3\u5b9a\u5f88\u591a\u4e8b\u60c5\uff0c\u8ba9\u513f\u5b50\u5434\u5c0a\u9886\u7740\u4e09\u4e2a\u9752\u76ae\u53bb\u5b66\u6821\u9053\u6b49\uff0c\u4e5f\u662f\u4e3a\u4e86\u8ba8\u597d\u5f20\u606a\uff0c\u6ca1\u60f3\u5230\u8fd9\u4e8b\u4f1a\u8ba9\u5f20\u606a\u4e0d\u5feb\u3002\u4ed6\u63a5\u5230\u513f\u5b50\u7535\u8bdd\u540e\u8d76\u5230\u65b0\u6d77\u901a\u5927\u53a6\uff0c\u60f3\u8865\u6551\u4e00\u4e0b\uff0c\u6ca1\u9047\u5230\u4ed6\u4eba\u3002\u7b2c\u4e8c\u5929\u5c31\u88ab\u544a\u77e5\uff0c\u9020\u7eb8\u5382\u8001\u5382\u7684\u6539\u9020\u65b9\u6848\u6682\u65f6\u505c\u4e0b\u6765\uff0c\u8ffd\u95ee\u5468\u590d\uff0c\u5468\u590d\u4e5f\u53ea\u662f\u8bf4\u4e8b\u60c5\u4e0d\u518d\u5f52\u4ed6\u8d1f\u8d23\u4e86\uff0c\u4ec0\u4e48\u4e8b\u60c5\u90fd\u8981\u7b49\u5f20\u606a\u624d\u80fd\u51b3\u5b9a\u3002</p><p>\u3000\u3000\u5434\u5929\u5b9d\u8fd9\u624d\u6653\u5f97\u8fd8\u662f\u8f7b\u89c6\u4e86\u8fd9\u4e2a\u5c11\u5e74\uff0c\u8fd9\u51e0\u5929\u60f3\u8865\u6551\uff0c\u5948\u4f55\u627e\u4e0d\u5230\u4ed6\u4eba\uff0c\u4eca\u5929\u5728\u4e00\u4e2d\u6821\u95e8\u53e3\u7b49\u4e86\u5c0f\u534a\u5929\uff0c\u624d\u902e\u5230\u4eba\uff0c\u4e0d\u6599\u5f20\u606a\u597d\u50cf\u5fd8\u4e86\u90a3\u56de\u4e8b\u4f3c\u7684\u3002</p><p>\u3000\u3000\u201c\u55ef\uff0c\u4f60\u8bf4\u90a3\u4e2a\u4e8b\u554a\uff0c\u9020\u7eb8\u5382\u65b0\u5382\u90a3\u8fb9\u51c6\u5907\u6708\u4e2d\u6295\u4ea7\uff0c\u6700\u8fd1\u7684\u4e8b\u60c5\u6bd4\u8f83\u591a\uff0c\u5468\u590d\u4ed6\u4eec\u53ef\u80fd\u5fd9\u4e0d\u8fc7\u6765\uff0c\u624d\u628a\u8001\u5382\u7684\u4e8b\u60c5\u6682\u65f6\u4e22\u4e0b\u6765\u3002\u201d\u5f20\u606a\u4fe1\u53e3\u80e1\u626f\uff0c\u770b\u4e86\u90b5\u81f3\u521a\u4e00\u773c\uff0c\u7ee7\u7eed\u62fe\u9636\u800c\u4e0a\uff0c\u63a8\u95e8\u8fdb\u4e86\u65b0\u6d77\u901a\u5927\u53a6\u3002</p><p>\u3000\u3000\u201c\u8fd9\u9879\u76ee\u4ec0\u4e48\u65f6\u5019\u4f1a\u542f\u52a8\uff1f\u201d\u5434\u5929\u5b9d\u7d27\u8ddf\u7740\u8fdb\u4e86\u5927\u53a6\u3002</p><p>\u3000\u3000\u5f20\u606a\u5728\u7535\u68af\u524d\u505c\u4f4f\u811a\u6b65\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u6211\u5c0f\u5b69\u5b50\u5bb6\u5bb6\u7684\uff0c\u5728\u5b66\u6821\u91cc\u4e89\u5f3a\u6597\u72e0\u51fa\u51fa\u98ce\u5934\u7f62\u4e86\uff0c\u5434\u7ecf\u7406\u771f\u4ee5\u4e3a\u6211\u80fd\u51b3\u5b9a\u8fd9\u4e8b\uff1f\u201d</p><p>\u3000\u3000\u7535\u68af\u500f\u7136\u6253\u5f00\uff0c\u5468\u590d\u966a\u5b8b\u57f9\u660e\u4ece\u91cc\u9762\u51fa\u6765\uff0c\u4ed6\u4eec\u770b\u89c1\u5f20\u606a\u7ad9\u5728\u7535\u68af\u524d\uff0c\u6123\u4e86\u4e00\u4e0b\uff0c\u5b8b\u57f9\u660e\u4f38\u624b\u6293\u4f4f\u5f20\u606a\u7684\u80f3\u818a\uff1a\u201c\u902e\u4f60\u597d\u51e0\u5929\uff0c\u8ddf\u4f60\u8c08\u9910\u996e\u4f11\u95f2\u5e7f\u573a\u7684\u4e8b\uff0c\u4f60\u6123\u662f\u628a\u6211\u667e\u4e86\u597d\u51e0\u5929\uff0c\u4eca\u5929\u4f60\u53ef\u4e0d\u80fd\u518d\u8eb2\u7740\u6211\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u5b8b\u53d4\u53d4\uff0c\u77a7\u4f60\u8bf4\u7684\uff0c\u6211\u54ea\u91cc\u6562\u628a\u4f60\u667e\u4e00\u8fb9\u554a\uff1f\u201d\u5f20\u606a\u707f\u7136\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u4f60\u6709\u4ec0\u4e48\u4e8b\u4e0d\u4f1a\u76f4\u63a5\u95ee\u5468\u5382\u957f\uff1f\u201d</p><p>\u3000\u3000\u542c\u5f20\u606a\u8fd9\u4e48\u8bf4\uff0c\u5468\u590d\u6709\u4e9b\u8baa\u7136\uff0c\u5b8b\u57f9\u660e\u54c8\u54c8\u4e00\u7b11\uff1a\u201c\u8001\u5468\u53e3\u98ce\u7d27\uff0c\u6211\u4eca\u5929\u624d\u77e5\u9053\u9879\u76ee\u4ea4\u7ed9\u848b\u8587\u8d1f\u8d23\u4e86\uff0c\u6211\u5927\u6982\u4e86\u89e3\u4e00\u4e9b\u60c5\u51b5\uff0c\u8be6\u7ec6\u7684\u8d44\u6599\u8fd8\u5f97\u4f60\u6388\u6743\u3002\u201d\u770b\u7740\u7535\u68af\u95e8\u5df2\u7ecf\u9616\u4e0a\uff0c\u5434\u5929\u5b9d\u4e0e\u90b5\u81f3\u521a\u7ad9\u5728\u4e00\u8fb9\u6ca1\u6709\u8fdb\u53bb\uff0c\u7591\u60d1\u7684\u95ee\u4e86\u95ee\uff0c\u201c\u8fd9\u4e24\u4f4d\u662f\uff1f\u201d</p><p>\u3000\u3000\u201c\u5b8b\u533a\u957f\uff0c\u201d\u5434\u5929\u5b9d\u614c\u5fd9\u638f\u51fa\u540d\u7247\uff0c\u201c\u5efa\u90ba\u9152\u5e97\u7ecf\u7406\u5434\u5929\u5b9d\uff0c\u60a8\u6cbb\u4e0b\u7684\u8349\u6c11\u3002\u201d</p><p>\u3000\u3000\u90b5\u81f3\u521a\u9547\u5b9a\u4e9b\uff0c\u9012\u4e0a\u540d\u7247\uff1a\u201c\u90b5\u81f3\u521a\uff0c\u5728\u7535\u89c6\u91cc\u7ecf\u5e38\u542c\u5b8b\u533a\u957f\u7684\u8bb2\u8bdd\u3002\u201d</p><p>\u3000\u3000\u5b8b\u57f9\u660e\u63a5\u8fc7\u90b5\u81f3\u521a\u7684\u540d\u7247\uff0c\u8f7b\u58f0\u8bfb\u51fa\u6765\uff1a\u201c\u6d77\u5dde\u9526\u7ee3\u827a\u672f\u7814\u7a76\u534f\u4f1a\u7406\u4e8b\u3001\u6d77\u5dde\u5e02\u4f5c\u5bb6\u534f\u4f1a\u4f1a\u5458\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u7709\u5934\u6311\u4e86\u6311\uff0c\u6ca1\u60f3\u5230\u90b5\u81f3\u521a\u8fd8\u6709\u8fd9\u4e2a\u8eab\u4efd\uff0c\u521a\u770b\u5230\u4ed6\u4ece\u540d\u7247\u5939\u7684\u5e95\u5c42\u53d6\u51fa\u540d\u7247\uff0c\u770b\u6765\u4ed6\u968f\u8eab\u5e26\u7740\u4e0d\u540c\u7684\u540d\u5206\uff0c\u9047\u5230\u5b98\u5458\u62ff\u51fa\u6587\u5316\u4eba\u7684\u540d\u5206\uff0c\u9047\u5230\u6587\u5316\u4eba\u62ff\u51fa\u5546\u4eba\u7684\u540d\u5206\uff0c\u81f3\u5c11\u4e0d\u4f1a\u5728\u522b\u4eba\u5f31\u4e86\u4ed6\u81ea\u5df1\u7684\u6c14\u52bf\u3002</p><p>\u3000\u3000\u5b8b\u57f9\u660e\u770b\u90b5\u81f3\u521a\u7684\u76ee\u5149\u679c\u7136\u8981\u6bd4\u770b\u5434\u5929\u5b9d\u51dd\u91cd\u5f97\u591a\u3002</p><p>\u3000\u3000\u5f20\u606a\u5634\u89d2\u542b\u7740\u7b11\uff0c\u4e5f\u4e0d\u70b9\u7834\uff0c\u7ad9\u5728\u4e00\u65c1\u62bf\u7740\u5634\u4e0d\u8bf4\u8bdd\uff0c\u5bf9\u5b8b\u57f9\u660e\u8be2\u95ee\u7684\u773c\u795e\uff0c\u4e5f\u53ea\u4f5c\u672a\u770b\u89c1\u3002</p><p>\u3000\u3000\u5434\u5929\u5b9d\u8138\u4e0a\u5806\u7740\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u4e5f\u6b63\u8ddf\u5c0f\u5f20\u8ba8\u8bba\u9910\u996e\u5e7f\u573a\u7684\u4e8b\u60c5\u5462\uff0c\u6ca1\u60f3\u5230\u5b8b\u533a\u957f\u4e5f\u5173\u5fc3\u8fd9\u4e8b\u3002\u201d</p><p>\u3000\u3000\u90b5\u81f3\u521a\u542c\u5230\u5b8b\u57f9\u660e\u8ddf\u5f20\u606a\u8bf4\u8bdd\u65f6\u6709\u8ba8\u597d\u7684\u8bed\u6c14\uff0c\u8fd9\u4e9b\u5929\u542c\u5434\u5929\u5b9d\u8bf4\u5f20\u606a\u8fd9\u5c11\u5e74\u5982\u4f55\u7684\u4e86\u4e0d\u5f97\uff0c\u4ed6\u591a\u534a\u4e0d\u4fe1\uff0c\u4ed6\u53ea\u5bf9\u8fd9\u4e2a\u9879\u76ee\u611f\u5174\u8da3\uff0c\u4e5f\u4e0d\u8ba4\u4e3a\u4e00\u4e2a\u5c0f\u5b69\u5b50\u80fd\u505a\u591a\u5927\u7684\u4e3b\uff0c\u4e8b\u5b9e\u4f3c\u4e4e\u5374\u662f\u5982\u6b64\uff0c\u4ee4\u4ed6\u4e0d\u5f97\u4e0d\u4fe1\u3002</p><p>\u3000\u3000\u201c\u90a3\u884c\u554a\uff0c\u4f60\u4eec\u662f\u505a\u9910\u996e\u7684\u4e13\u4e1a\u4eba\u58eb\uff0c\u8fd9\u4e2a\u9879\u76ee\u4e00\u5b9a\u8981\u542c\u542c\u4f60\u4eec\u7684\u610f\u89c1\u3002\u201d\u5b8b\u57f9\u660e\u7b11\u7740\u8bf4\uff0c\u201c\u5f20\u606a\uff0c\u4f60\u8bf4\u662f\u4e0d\u662f\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5b8b\u53d4\u53d4\u8bf4\u662f\uff0c\u90a3\u5f53\u7136\u5c31\u662f\u4e86\u3002\u201d</p><p>\u3000\u3000\u4e94\u4e2a\u4eba\u4e58\u7535\u68af\u8fd4\u56de\u5341\u516b\u697c\uff0c\u5434\u5929\u5b9d\u3001\u90b5\u81f3\u521a\u4ee5\u4e3a\u80fd\u8ddf\u5b8b\u57f9\u660e\u6df1\u5165\u4ea4\u8c08\u4e9b\u4ec0\u4e48\uff0c\u4e24\u4eba\u62c9\u5728\u540e\u9762\uff0c\u5c0f\u58f0\u8ba8\u8bba\u665a\u4e0a\u8bf7\u5b8b\u57f9\u660e\u5230\u54ea\u91cc\u5403\u996d\u5408\u9002\u3002</p><p>\u3000\u3000\u5927\u529e\u516c\u5ba4\u91cc\uff0c\u5468\u6587\u658c\u5750\u5728\u90a3\u91cc\u770b\u62a5\u7eb8\uff0c\u5f20\u606a\u7b11\u7740\u8ddf\u4ed6\u6253\u4e86\u58f0\u62db\u547c\uff0c\u95ee\u4ed6\uff1a\u201c\u848b\u59d0\u5462\uff1f\u201d</p><p>\u3000\u3000\u848b\u8587\u63a8\u95e8\u8d70\u51fa\u5979\u81ea\u5df1\u7684\u529e\u516c\u5ba4\uff0c\u62b1\u6028\u7684\u8bf4\uff1a\u201c\u4f60\u6e05\u95f2\u4e86\u597d\u51e0\u5929\uff0c\u9664\u4e86\u8bb8\u601d\uff0c\u8c01\u90fd\u89c1\u4e0d\u7740\u4f60\u7684\u4eba\u554a\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u53cc\u624b\u62b1\u62f3\u6551\u9976\uff1a\u201c\u76f8\u4fe1\u4f60\u8ddf\u5468\u5382\u957f\u4f1a\u628a\u6240\u6709\u4e8b\u60c5\u529e\u5f97\u59a5\u59a5\u5f53\u5f53\uff0c\u996e\u98df\u5e7f\u573a\u7684\u8bbe\u8ba1\u7a3f\u8d76\u51fa\u6765\u6ca1\u6709\uff1f\u6211\u53ea\u8981\u5927\u6982\u7684\u3002\u201d</p><p>\u3000\u3000\u201c\u8d76\u51fa\u6765\u4e86\uff0c\u201d\u848b\u8587\u8f6c\u8eab\u56de\u81ea\u5df1\u7684\u529e\u516c\u5ba4\u62ff\u51fa\u7b56\u5212\u4e66\u4e0e\u8bbe\u8ba1\u7a3f\u9012\u7ed9\u5f20\u606a\uff0c\u201c\u627e\u4e86\u4e09\u5bb6\u8bbe\u8ba1\u5355\u4f4d\uff0c\u5404\u51fa\u4e86\u4e00\u4efd\uff0c\u65f6\u95f4\u5f88\u8d76\uff0c\u7ec6\u8282\u5730\u65b9\u505a\u5f97\u4e0d\u662f\u592a\u597d\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u6253\u5f00\u7c97\u7565\u7684\u770b\u4e86\u4e0b\uff0c\u95ee\u848b\u8587\uff1a\u201c\u6709\u51e0\u4efd\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e00\u5f0f\u4e03\u4efd\u3002\u201d</p><p>\u3000\u3000\u201c\u90a3\u884c\uff0c\u201d\u5f20\u606a\u5c06\u624b\u91cc\u7684\u8bbe\u8ba1\u7a3f\u3001\u7b56\u5212\u4e66\u6574\u7406\u597d\u9012\u7ed9\u5b8b\u57f9\u660e\uff0c\u201c\u5b8b\u53d4\u53d4\uff0c\u6211\u60f3\u5177\u4f53\u5185\u5bb9\uff0c\u4f60\u4e5f\u77e5\u9053\u4e86\uff0c\u6ca1\u6709\u4ec0\u4e48\u9700\u8981\u8ba8\u8bba\u7684\uff0c\u8fd9\u4efd\u65b9\u6848\u4e0d\u4ec5\u5305\u62ec\u9020\u7eb8\u5382\u6539\u9020\uff0c\u8fd8\u5305\u62ec\u4e24\u4fa7\u5e02\u6c11\u4f11\u95f2\u7eff\u5730\u4e0e\u5a31\u4e50\u573a\u73af\u5883\u7684\u6539\u9020\uff0c\u9020\u7eb8\u5382\u4e4b\u5916\u7684\u90e8\u5206\uff0c\u9700\u8981\u57ce\u5357\u533a\u653f\u5e9c\u6295\u5165\uff0c\u4f60\u4eec\u533a\u91cc\u8fd8\u8981\u8ba8\u8bba\u3002\u8fd9\u4efd\u7ed9\u4f60\uff0c\u4f60\u627e\u4eba\u628a\u7b56\u5212\u4e66\u6458\u8981\u7f16\u6210\u6587\u7ae0\uff0c\u914d\u4e0a\u6548\u679c\u56fe\uff0c\u5148\u62ff\u5230\u6d77\u5dde\u65e5\u62a5\u4e0a\u53d1\u51fa\u6765\u4e5f\u6ca1\u6709\u5173\u7cfb\uff0c\u8fd9\u53ea\u662f\u63d0\u8bae\u6848\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u77e5\u9053\u6211\u627e\u4f60\u662f\u4e3a\u8fd9\u4e8b\uff1f\u201d\u5b8b\u57f9\u660e\u8be7\u5f02\u7684\u95ee\u3002</p><p>\u3000\u3000\u201c\u8fd8\u6709\u522b\u7684\u4e8b\u60c5\uff1f\u201d\u5f20\u606a\u53cd\u95ee\u4ed6\u3002</p><p>\u3000\u3000\u201c\u662f\u6ca1\u6709\u4e86\uff0c\u201d\u5b8b\u57f9\u660e\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u4f60\u8fd9\u5c0f\u5b50\u592a\u9b3c\u4e86\uff0c\u6211\u8bf4\u8001\u8bb8\u600e\u4e48\u53ef\u80fd\u65e0\u7f18\u65e0\u6545\u7684\u5199\u90a3\u7bc7\u6587\u7ae0\u3002\u201d</p><p>\u3000\u3000\u201c\u8fd9\u4e2a\u4e0d\u80fd\u778e\u8bf4\uff0c\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u201c\u4f60\u8981\u95ee\u6211\u7684\u8bdd\uff0c\u6211\u53ea\u80fd\u8bf4\u4e0d\u77e5\u9053\u3002\u201d\u644a\u644a\u624b\uff0c\u53c8\u8bf4\u9053\uff0c\u201c\u5b8b\u53d4\u53d4\uff0c\u8981\u6ca1\u6709\u522b\u7684\u4e8b\u60c5\uff0c\u5c31\u4e0d\u59a8\u788d\u4f60\u529e\u6b63\u4e8b\u3002\u201d</p><p>\u3000\u3000\u5b8b\u57f9\u660e\u89c1\u5f20\u606a\u4e0b\u9010\u5ba2\u4ee4\uff0c\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8fd8\u4ee5\u4e3a\u4f60\u4f1a\u6572\u6211\u4e00\u987f\u996d\uff0c\u65e2\u7136\u8d76\u6211\u8d70\uff0c\u90a3\u6211\u5c31\u5148\u8d70\u4e86\uff0c\u6587\u7ae0\u600e\u4e48\u7f72\u540d\uff1f\u201d</p><p>\u3000\u3000\u201c\u65b0\u5149\u9020\u7eb8\u5382\u4e0e\u57ce\u5357\u533a\u653f\u5e9c\u8054\u5408\u7f72\u540d\uff0c\u4f60\u8981\u7f72\u4f60\u81ea\u5df1\u7684\u540d\u5b57\uff0c\u4e5f\u65e0\u6240\u8c13\u3002\u201d</p><p>\u3000\u3000\u5b8b\u57f9\u660e\u4e0d\u4e60\u60ef\u8ddf\u5f20\u606a\u4e89\u53e3\u820c\u4e4b\u5229\uff0c\u89c1\u5f20\u606a\u505a\u51fa\u4e0d\u9001\u7684\u624b\u52bf\uff0c\u7b11\u4e86\u7b11\uff0c\u62ff\u7740\u7b56\u5212\u4e66\u3001\u8bbe\u8ba1\u7a3f\u8d70\u51fa\u529e\u516c\u5ba4\uff0c\u5468\u590d\u72b9\u8c6b\u4e86\u4e00\u4f1a\u513f\uff0c\u8fd8\u662f\u6ca1\u6709\u8ddf\u7740\u51fa\u53bb\u9001\u4e00\u9001\u3002</p><p>\u3000\u3000\u5434\u5929\u5b9d\u3001\u90b5\u81f3\u521a\u6ca1\u6709\u60f3\u5230\u538b\u6839\u6ca1\u6709\u63a5\u89e6\u5b8b\u57f9\u660e\u7684\u673a\u4f1a\uff0c\u770b\u5230\u5f20\u606a\u3001\u5468\u590d\u3001\u848b\u8587\u4ed6\u4eec\u7ad9\u5728\u539f\u5730\uff0c\u90fd\u6709\u4e9b\u50bb\u4e86\uff0c\u5b9e\u5728\u65e0\u6cd5\u60f3\u8c61\u5b8b\u57f9\u660e\u4ece\u57ce\u5357\u533a\u8f96\u533a\u5185\u7684\u516c\u53f8\u8d70\u51fa\u53bb\uff0c\u7adf\u7136\u4f1a\u6ca1\u6709\u4eba\u76f8\u9001\u3002</p><p>\u3000\u3000\u90b5\u81f3\u521a\u8214\u4e86\u8214\u5634\u5507\uff0c\u5f88\u8270\u96be\u7684\u5206\u8fa8\u773c\u524d\u7684\u4e8b\u5b9e\uff0c\u95ee\u9053\uff1a\u201c\u8001\u5382\u6539\u5efa\u9910\u996e\u5e7f\u573a\u7684\u8ba1\u5212\u4f1a\u5148\u5728\u6d77\u5dde\u65e5\u62a5\u53d1\u8868\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e0d\u9519\uff0c\u201d\u5f20\u606a\u5e73\u9759\u7684\u770b\u7740\u90b5\u81f3\u521a\uff0c\u867d\u7136\u4ed6\u8fd8\u6ca1\u6709\u8d70\u4e0a\u9887\u5177\u4f20\u5947\u8272\u5f69\u7684\u8d44\u672c\u8fd0\u4f5c\u4e4b\u9014\uff0c\u5fc3\u60f3\u4ed6\u7684\u5fc3\u4e0d\u4f1a\u5c0f\uff0c\u8bf4\u9053\uff0c\u201c\u6d77\u5dde\u65e5\u62a5\u51e0\u5929\u8fde\u7eed\u62a5\u9053\u6240\u5f15\u53d1\u7684\u70ed\u8bae\u8bdd\u9898\uff0c\u6211\u60f3\u4f60\u4eec\u4e5f\u80fd\u6e05\u695a\uff0c\u8001\u5382\u6539\u5efa\u9910\u996e\u5e7f\u573a\uff0c\u52a0\u5468\u56f4\u73af\u5883\u7684\u6574\u6cbb\uff0c\u5b8c\u5168\u662f\u6954\u5408\u8fd9\u4e2a\u601d\u8def\u7684\u3002\u4ece\u5c0f\u91cc\u8bf4\uff0c\u662f\u9020\u7eb8\u5382\u81ea\u8eab\u7684\u5546\u4e1a\u7ecf\u8425\u884c\u4e3a\uff0c\u4f46\u662f\u5728\u57ce\u5357\u533a\u653f\u5e9c\u770b\u6765\uff0c\u610f\u4e49\u5b8c\u5168\u4e0d\u4e00\u6837\u2026\u2026\u201d</p><p>\u3000\u3000\u90b5\u81f3\u521a\u773c\u795e\u6709\u4e9b\u9eef\u7136\uff0c\u5434\u5929\u5b9d\u627e\u4ed6\u8ba8\u8bba\u8fd9\u4e2a\u9879\u76ee\u65f6\uff0c\u9887\u4e3a\u610f\u52a8\uff0c\u4ed4\u7ec6\u8003\u8651\u8fc7\uff0c\u4ee5\u4e3a\u6709\u4e1c\u897f\u53ef\u4ee5\u6307\u70b9\u4eba\u5bb6\uff0c\u6b64\u65f6\u5df2\u7ecf\u6ca1\u6709\u8fd9\u4e2a\u4fe1\u5fc3\u3002\u8fd8\u6ca1\u6709\u8c08\u5230\u7ec6\u5904\uff0c\u773c\u524d\u8fd9\u5c11\u5e74\u770b\u5f85\u95ee\u9898\u7684\u89d2\u5ea6\u6bd4\u5e38\u4eba\u8981\u9ad8\u5f97\u591a\uff0c\u4e5f\u8981\u8fdc\u5f97\u591a\uff0c\u96be\u9053\u8ddf\u4ed6\u7684\u5bb6\u5ead\u73af\u5883\u6709\u5173\uff1f</p><p>\u3000\u3000\u90b5\u81f3\u521a\u52aa\u529b\u7684\u627e\u5230\u4e00\u4e2a\u8bdd\u9898\uff0c\u95ee\u5f20\u606a\uff1a\u201c\u9526\u6e56\u51c6\u5907\u600e\u6837\u5bfb\u627e\u5408\u4f5c\u8005\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u57ce\u5357\u533a\u653f\u5e9c\u91cd\u89c6\u8d77\u6765\uff0c\u6211\u60f3\u4f1a\u6709\u4e00\u4e9b\u5408\u4f5c\u8005\u50cf\u90b5\u7ecf\u7406\u8fd9\u6837\u627e\u4e0a\u95e8\u6765\u7684\uff0c\u5f53\u7136\uff0c\u6211\u4eec\u4e5f\u7528\u5404\u79cd\u65b9\u5f0f\u4e3b\u52a8\u8054\u7cfb\uff0c\u4e5f\u4f1a\u4e3a\u52a0\u5165\u8fd9\u4e2a\u9879\u76ee\u7684\u9910\u996e\u4f01\u4e1a\u5411\u57ce\u5357\u533a\u653f\u5e9c\u4e89\u53d6\u4e00\u4e9b\u4f18\u60e0\u653f\u7b56\uff0c\u6bd5\u7adf\u8fd9\u4e2a\u9879\u76ee\u6709\u5f88\u5f3a\u7684\u793a\u8303\u4f5c\u7528\u3002\u8fd9\u6837\u4e00\u6765\uff0c\u6211\u4eec\u4e5f\u53ef\u4ee5\u63d0\u9ad8\u5bf9\u5408\u4f5c\u8005\u7684\u8981\u6c42\u3002\u201d</p><p>\u3000\u3000\u90b5\u81f3\u521a\u6653\u5f97\u5f20\u606a\u7684\u6700\u540e\u4e00\u53e5\u8bdd\u624d\u662f\u5173\u952e\uff0c\u8fd9\u4e2a\u9879\u76ee\u7684\u5404\u79cd\u6709\u5229\u6761\u4ef6\u6ca1\u6709\u5b8c\u5168\u5c55\u73b0\u51fa\u6765\uff0c\u9526\u6e56\u662f\u4e0d\u4f1a\u7ed9\u51fa\u5b9e\u8d28\u6027\u7684\u627f\u8bfa\uff0c\u65e0\u975e\u662f\u5728\u6700\u540e\u5173\u5934\u63d0\u9ad8\u79df\u91d1\u7684\u6807\u51c6\u3002</p><p>\u3000\u3000\u5f20\u606a\u5c06\u90b5\u81f3\u521a\u7684\u795e\u60c5\u770b\u5728\u773c\u91cc\uff0c\u5bf9\u848b\u8587\u8bf4\uff1a\u201c\u8be6\u7ec6\u7684\u60c5\u51b5\uff0c\u4f60\u8ddf\u90b5\u603b\u3001\u5434\u603b\u4ecb\u7ecd\u4e00\u4e0b\u2026\u2026\u201d\u9694\u7740\u534a\u900f\u9732\u7684\u78e8\u7802\u73bb\u7483\uff0c\u8bb8\u601d\u7684\u8eab\u5f71\u5728\u5979\u7684\u529e\u516c\u5ba4\u91cc\u4e00\u52a8\u4e0d\u52a8\uff0c\u5f20\u606a\u5c06\u90b5\u81f3\u521a\u3001\u5434\u5929\u5b9d\u4e22\u7ed9\u848b\u8587\uff0c\u63a8\u95e8\u8fdb\u5165\u8bb8\u601d\u7684\u529e\u516c\u5ba4\u3002</p><p>\u3000\u3000\u201c\u600e\u4e48\u4e86\uff1f\u201d\u5f20\u606a\u89c1\u8bb8\u601d\u773c\u775b\u91cc\u85cf\u7740\u54c0\u4f24\uff0c\u5979\u9762\u524d\u653e\u7740\u4e00\u5c01\u4fe1\u51fd\uff0c\u5f20\u606a\u770b\u4e0a\u4fe1\u51fd\u4e0a\u7684\u5370\u6233\u53ca\u4fe1\u5c01\u6807\u8bc6\uff0c\u5c31\u77e5\u9053\u662f\u600e\u4e48\u56de\u4e8b\u3002\u8fd9\u662f\u7701\u57ce\u897f\u971e\u533a\u68c0\u5bdf\u53d1\u6765\u7684\u516c\u51fd\u3002</p><p>\u3000\u3000\u4e01\u5411\u5c71\u6848\u79fb\u4ea4\u7ed9\u7701\u57ce\u4e0b\u8f96\u897f\u971e\u533a\u68c0\u5bdf\u9662\uff0c\u867d\u7136\u4e13\u6848\u7ec4\u5bf9\u8bb8\u601d\u505a\u51fa\u4e0d\u8d77\u8bc9\u7684\u51b3\u5b9a\uff0c\u4f46\u662f\u68c0\u5bdf\u9662\u4f9d\u7a0b\u5e8f\u8981\u590d\u6838\u8bb8\u601d\u6d89\u53ca\u7684\u6848\u60c5\uff0c\u8bb8\u601d\u9700\u8981\u5230\u7701\u57ce\u63a5\u53d7\u68c0\u5bdf\u9662\u7684\u8046\u8bad\uff0c\u68c0\u5bdf\u9662\u6709\u6743\u63a8\u5012\u4e13\u6848\u7ec4\u7684\u51b3\u5b9a\uff0c\u91cd\u65b0\u505a\u51fa\u8d77\u8bc9\u4e0e\u4e0d\u8d77\u8bc9\u7684\u51b3\u5b9a\u3002</p><p>\u3000\u3000\u5f20\u606a\u5750\u5230\u8bb8\u601d\u7684\u9762\u524d\uff0c\u6293\u8d77\u5979\u7684\u624b\uff0c\u51b0\u51b7\u65e0\u529b\uff0c\u5408\u638c\u5c06\u5979\u7684\u624b\u6342\u5728\u624b\u5fc3\u91cc\uff1a\u201c\u6211\u4e5f\u8981\u8fc7\u53bb\u7684\uff0c\u8fd9\u4e9b\u5929\u4e5f\u591f\u8f9b\u82e6\u7684\uff0c\u5c31\u5f53\u518d\u53bb\u7701\u57ce\u4f11\u5047\u597d\u4e86\u3002\u201d</p></article>"}}}]);
