(window.webpackJsonp=window.webpackJsonp||[]).push([[300],{322:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u516d\u8282 \u5b69\u5b50",content:"<article><h2>\u7b2c\u5341\u516d\u8282 \u5b69\u5b50</h2><p>\u8d70\u5728\u524d\u9762\u7684\u662f\u516d\u4e2a\u5353\u5c14\u6218\u58eb\uff0c\u867d\u7136\u90fd\u9a91\u7740\u8725\u8734\uff0c\u4f46\u884c\u8fdb\u901f\u5ea6\u5f88\u6162\uff0c\u4e5f\u662f\u4e3a\u4e86\u8b66\u6212\u3002\u5728\u4ed6\u4eec\u540e\u9762\u4fbf\u662f\u5546\u961f\uff0c\u5341\u51e0\u5934\u6d1b\u65af\u517d\u9a6e\u7740\u9f13\u9f13\u56ca\u56ca\u7684\u76ae\u888b\uff0c\u51e0\u4e2a\u5546\u4eba\u6a21\u6837\u7684\u5353\u5c14\u9a91\u7740\u8725\u8734\u8ddf\u968f\uff0c\u8eab\u65c1\u6709\u516b\u4e2a\u62a4\u536b\uff0c\u5176\u4e2d\u6709\u4e00\u4e2a\u7a7f\u7740\u957f\u888d\uff0c\u624b\u6301\u6cd5\u6756\uff0c\u660e\u663e\u662f\u5deb\u5e08\u3002</p><p>\u539f\u672c\u8fd9\u79cd\u4f0f\u51fb\u6218\uff0c\u4e0a\u6765\u5148\u62ff\u5f3a\u5f29\u6512\u5c04\u4e00\u901a\u662f\u6b63\u7ecf\uff0c\u628a\u5bf9\u65b9\u6253\u4e00\u4e2a\u660f\u5934\u8f6c\u5411\uff0c\u7136\u540e\u518d\u4e58\u52bf\u51b2\u51fb\u3002\u53ef\u60dc\u5982\u4eca\u6668\u70bc\u57ce\u4e2d\u6839\u672c\u6ca1\u6709\u51e0\u526f\u80fd\u7528\u7684\u5f29\uff0c\u77ee\u4eba\u5728\u8fd9\u65b9\u9762\u4e5f\u4e0d\u80af\u63d0\u4f9b\u63f4\u52a9\uff0c\u6240\u4ee5\u53ea\u80fd\u662f\u7a7a\u60f3\u3002\u5e78\u597d\u743c\u6069\u662f\u4e2a\u70bc\u91d1\u5e08\uff0c\u8fd9\u6bb5\u65f6\u95f4\u5728\u6668\u70bc\u57ce\u5468\u56f4\u8f6c\u60a0\uff0c\u53d1\u73b0\u4e86\u51e0\u79cd\u690d\u7269\uff0c\u8c03\u914d\u5728\u4e00\u8d77\u5236\u6210\u836f\u6c34\uff0c\u6709\u7c7b\u4f3c\u71c3\u70e7\u74f6\u7684\u4f5c\u7528\u3002\u6b64\u6b21\u6bcf\u4eba\u90fd\u914d\u53d1\u4e86\u4e24\u74f6\uff0c\u7b49\u654c\u4eba\u8fdb\u5c04\u7a0b\u8303\u56f4\uff0c\u5c31\u5f53\u624b\u69b4\u5f39\u6254\u51fa\u53bb\u3002</p><p>\u8d70\u8fd1\u4e86\u3002</p><p>\u4e00\u58f0\u4ee4\u4e0b\uff0c\u5341\u51e0\u74f6\u836f\u6c34\u51e0\u4e4e\u540c\u65f6\u7838\u4e86\u51fa\u53bb\uff0c\u5341\u51e0\u4e2a\u706b\u7403\u8f70\u7136\u7206\u5f00\uff0c\u867d\u7136\u6709\u4e9b\u7838\u504f\u4e86\uff0c\u6ca1\u6709\u8d77\u5230\u9884\u671f\u6548\u679c\uff0c\u4f46\u8fd8\u662f\u6709\u5f88\u591a\u547d\u4e2d\u76ee\u6807\u3002\u6700\u524d\u9762\u7684\u4e24\u4e2a\u5353\u5c14\u6218\u58eb\u8df3\u4e86\u8d77\u6765\uff0c\u56e0\u4e3a\u4ed6\u4eec\u5df2\u7ecf\u5b8c\u5168\u88ab\u706b\u7130\u5305\u88f9\uff0c\u8eab\u4e0a\u7684\u9b54\u6597\u7bf7\u867d\u7136\u80fd\u591f\u8d77\u5230\u4e00\u5b9a\u7684\u6297\u706b\u4f5c\u7528\uff0c\u4f46\u7ec8\u7a76\u4e5f\u652f\u6301\u4e0d\u4e86\u7247\u523b\u3002</p><p>\u5546\u4eba\u4eec\u6781\u529b\u7ea6\u675f\u7740\u53d7\u60ca\u7684\u6d1b\u65af\u517d\uff0c\u4ed6\u4eec\u5e76\u4e0d\u662f\u4f18\u5148\u653b\u51fb\u7684\u76ee\u6807\uff0c\u53cd\u5012\u65e0\u4e8b\u3002\u62a4\u536b\u4e2d\u552f\u4e00\u7684\u5deb\u5e08\u81ea\u7136\u662f\u91cd\u70b9\u5173\u7167\u5bf9\u8c61\uff0c\u81f3\u5c11\u6709\u4e09\u5206\u4e4b\u4e00\u7684\u71c3\u70e7\u836f\u6c34\u90fd\u7838\u5728\u4ed6\u8eab\u4e0a\u6216\u8005\u9644\u8fd1\uff0c\u4ed6\u7684\u957f\u888d\u867d\u7136\u4e5f\u9644\u6709\u9b54\u6cd5\uff0c\u4f46\u4e0b\u6446\u4e5f\u5df2\u7ecf\u8d77\u706b\uff0c\u70c8\u7130\u6b63\u5982\u6bd2\u86c7\u822c\u8fc5\u901f\u6e38\u4e0a\uff0c\u6253\u7b97\u628a\u8fd9\u4e2a\u5353\u5c14\u541e\u566c\u3002\u4f46\u5deb\u5e08\u6beb\u4e0d\u614c\u5f20\uff0c\u53ea\u662f\u4ece\u6597\u7bf7\u5185\u4fa7\u53e3\u888b\u91cc\u53d6\u51fa\u4e00\u679a\u62a4\u7b26\uff0c\u865a\u865a\u5f80\u4e0b\u4e00\u6309\uff0c\u4f4e\u58f0\u5410\u51fa\u4e2a\u8bcd\uff0c\u4e00\u80a1\u51b0\u51b7\u5f7b\u9aa8\u7684\u5bd2\u6c14\u987f\u65f6\u7b3c\u7f69\u5468\u56f4\uff0c\u6240\u6709\u4eba\u8eab\u4e0a\u7684\u706b\u7130\u5239\u90a3\u95f4\u90fd\u7184\u706d\u4e86\u3002</p><p>\u201c\u83f2\u5c14\u677e\u62a4\u7b26\u3002\u201d</p><p>\u743c\u6069\u8ba4\u8bc6\u8fd9\u4e2a\u62a4\u7b26\u3002\u5728\u90a3\u6b21\u5bf9\u4ed8\u745e\u8d39\u5fb7\u5bb6\u65cf\u7684\u6218\u5f79\u4e2d\uff0c\u743c\u6069\u4e00\u5f00\u59cb\u6740\u6b7b\u7684\u90a3\u4e2a\u5deb\u5e08\u8eab\u4e0a\u5c31\u6709\u4e00\u679a\uff0c\u88ab\u7ef4\u5eb7\u5c3c\u4e9a\u62ff\u53bb\uff1b\u540e\u6765\u5728\u795e\u5802\u91cc\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u88ab\u514b\u52b3\u62d3\u4e00\u8bb0\u9633\u708e\u5c04\u7ebf\u51fb\u4e2d\uff0c\u8eab\u9677\u706b\u6d77\uff0c\u4e9a\u5f53\u65af\u5f53\u65f6\u4e5f\u662f\u53d6\u51fa\u8fd9\u679a\u62a4\u7b26\u7184\u706d\u70c8\u7130\uff0c\u6551\u4e86\u5979\u4e00\u547d\u3002\u636e\u7ef4\u5eb7\u5c3c\u4e9a\u8bf4\uff0c\u552f\u6709\u5deb\u5e08\u5b66\u9662\u5bfc\u5e08\u624d\u62e5\u6709\u8fd9\u679a\u62a4\u7b26\uff0c\u743c\u6069\u4e5f\u5f53\u8fc7\u51e0\u5929\u5bfc\u5e08\uff0c\u53ef\u60dc\u65f6\u95f4\u592a\u77ed\uff0c\u8fd8\u6ca1\u6765\u5f97\u53ca\u62ff\u5230\u624b\u3002</p><p>\u773c\u524d\u7684\u8fd9\u4f4d\u5deb\u5e08\u662f\u4e00\u4f4d\u5b66\u9662\u5bfc\u5e08\uff1f</p><p>\u65e0\u6687\u591a\u60f3\u8fd9\u4e9b\uff0c\u5fb7\u53e4\u62c9\u5df2\u7ecf\u7387\u9886\u4ed6\u7684\u624b\u4e0b\u6740\u4e86\u4e0a\u53bb\uff0c\u53cc\u65b9\u5f00\u59cb\u4e71\u6218\uff0c\u743c\u6069\u548c\u6885\u83f2\u65af\u5219\u6ca1\u6709\u4e0a\u524d\u3002\u5546\u961f\u7684\u8fd9\u4e9b\u62a4\u536b\u5b9e\u529b\u4e0d\u5f31\uff0c\u867d\u7136\u88ab\u6253\u4e86\u4e2a\u63aa\u624b\u4e0d\u53ca\uff0c\u53c8\u5148\u6328\u4e86\u4e00\u987f\u706b\u70e7\uff0c\u4f46\u7acb\u523b\u5c31\u7a33\u4f4f\u4e86\u9635\u811a\uff0c\u7ec4\u6210\u9635\u578b\uff0c\u751a\u81f3\u5f00\u59cb\u53cd\u51fb\uff0c\u4ed6\u4eec\u4eba\u6570\u6bd5\u7adf\u5360\u4f18\u52bf\uff0c\u88c5\u5907\u4e5f\u66f4\u7cbe\u826f\u3002</p><p>\u6885\u83f2\u65af\u8010\u5fc3\u5730\u7b49\u5f85\u7740\uff0c\u76f4\u5230\u5bf9\u65b9\u7684\u9635\u578b\u56e0\u4e3a\u53cd\u653b\u800c\u51fa\u73b0\u7834\u7efd\uff0c\u7136\u540e\u5979\u4ece\u65ad\u5d16\u4e0a\u4e00\u8dc3\u800c\u4e0b\uff0c\u671d\u7740\u5353\u5c14\u5deb\u5e08\u51b2\u53bb\u3002</p><p>\u540c\u4e00\u65f6\u523b\uff0c\u743c\u6069\u63a5\u8fde\u6254\u51fa\u4e86\u4e09\u679a\u68cb\u5b50\uff1a\u4e00\u540d\u201c\u6218\u58eb\u201d\uff0c\u4e24\u540d\u201c\u98df\u4eba\u9b54\u201d\u3002</p><p>\u7ecf\u8fc7\u8fd9\u6bb5\u65f6\u95f4\u4ee5\u6765\u7684\u7814\u7a76\uff0c\u4ed6\u5bf9\u8fd9\u4e9b\u6218\u68cb\u9b54\u50cf\u7684\u4e86\u89e3\u66f4\u52a0\u6df1\u5165\u3002\u5176\u5b9e\u6218\u58eb\u548c\u98df\u4eba\u9b54\u4e00\u6837\uff0c\u90fd\u662f\u5c5e\u4e8e\u8089\u640f\u7c7b\u578b\uff0c\u4f46\u98df\u4eba\u9b54\u504f\u91cd\u86ee\u529b\uff0c\u9002\u5408\u5f3a\u51b2\u731b\u649e\uff0c\u800c\u6218\u58eb\u5219\u66f4\u52a0\u7075\u5de7\u3002\u743c\u6069\u521a\u624d\u5df2\u7ecf\u770b\u51c6\u5f62\u52bf\uff0c\u4e09\u4e2a\u9b54\u50cf\u4e00\u51fa\uff0c\u987f\u65f6\u963b\u65ad\u4e86\u5176\u4ed6\u62a4\u536b\u4eec\u56de\u63f4\u7684\u8def\u7ebf\u3002\u5982\u6b64\u4e00\u6765\uff0c\u5353\u5c14\u5deb\u5e08\u5c31\u5fc5\u987b\u5355\u72ec\u9762\u5bf9\u6885\u83f2\u65af\u3002</p><p>\u8fd8\u6709\u65ad\u5d16\u4e0a\u7684\u743c\u6069\u3002</p><p>\u9762\u5bf9\u6025\u901f\u51b2\u8fd1\u7684\u6885\u83f2\u65af\uff0c\u5353\u5c14\u5deb\u5e08\u4f9d\u65e7\u9547\u5b9a\u81ea\u82e5\uff0c\u6325\u624b\u6492\u51fa\u4e00\u628a\u7ea2\u5b9d\u77f3\u788e\u6676\uff0c\u5728\u4e0b\u4e00\u77ac\u95f4\u5b83\u4eec\u53d8\u6210\u4e86\u5341\u51e0\u53ea\u5168\u8eab\u706b\u7130\u8426\u7ed5\u7684\u5e9e\u5927\u8718\u86db\uff0c\u4e00\u7a9d\u8702\u5730\u51b2\u4e86\u4e0a\u6765\u3002\u6885\u83f2\u65af\u4e0d\u5f97\u4e0d\u505c\u4f4f\u524d\u51b2\u4e4b\u52bf\uff0c\u4e00\u5251\u626b\u51fa\uff0c\u5c06\u6700\u524d\u9762\u7684\u4e09\u53ea\u8718\u86db\u5168\u524a\u6210\u4e24\u534a\u3002</p><p>\u7136\u540e\u5b83\u4eec\u53d8\u6210\u4e86\u516d\u53ea\u8718\u86db\u3002</p><p>\u4e00\u5806\u706b\u7130\u8718\u86db\u51b2\u4e86\u4e0a\u6765\uff0c\u51e0\u4e4e\u8981\u5c06\u6885\u83f2\u65af\u6df9\u6ca1\uff0c\u5353\u5c14\u5deb\u5e08\u637b\u52a8\u7740\u624b\u6307\uff0c\u518d\u6b21\u5411\u6885\u83f2\u65af\u65bd\u5c55\u4e86\u4e00\u4e2a\u524a\u5f31\u6cd5\u672f\uff0c\u4f46\u7acb\u523b\u88ab\u743c\u6069\u89e3\u6d88\u4e86\u3002\u4e24\u4f4d\u5deb\u5e08\u4ee5\u6885\u83f2\u65af\u4e3a\u4e2d\u5fc3\u5c55\u5f00\u4e86\u4e00\u573a\u5feb\u901f\u7684\u6cd5\u672f\u653b\u9632\u6218\uff0c\u8c01\u4e5f\u6ca1\u6709\u5360\u4e0a\u98ce\u3002</p><p>\u6b64\u65f6\u6885\u83f2\u65af\u7ec8\u4e8e\u51b2\u4e86\u51fa\u6765\u3002</p><p>\u5979\u5de6\u624b\u63d0\u7740\u94f6\u5251\uff0c\u53f3\u624b\u5374\u6325\u7740\u4e00\u628a\u900f\u660e\u7684\u51b0\u6676\u957f\u5251\uff0c\u90a3\u662f\u5979\u7684\u5bd2\u51b0\u624b\u5957\u5316\u51fa\u6765\u7684\uff0c\u4ee5\u524d\u5728\u5de8\u9b54\u5c71\u8109\u91cc\u9760\u5b83\u5413\u8dd1\u4e86\u708e\u9b54\uff0c\u6b63\u662f\u8fd9\u79cd\u706b\u7130\u8718\u86db\u7684\u514b\u661f\u3002\u5353\u5c14\u5deb\u5e08\u663e\u7136\u6709\u4e9b\u610f\u5916\uff0c\u4ed6\u6025\u6025\u540e\u9000\u4e86\u534a\u6b65\uff0c\u6574\u4e2a\u4eba\u98d8\u6d6e\u8d77\u6765\uff0c\u907f\u5f00\u6885\u83f2\u65af\u7684\u653b\u51fb\u8303\u56f4\u3002</p><p>\u743c\u6069\u6293\u4f4f\u673a\u4f1a\u4ece\u6cd5\u6756\u9876\u7aef\u5c04\u51fa\u4e00\u9053\u7eff\u8272\u5c04\u7ebf\uff0c\u8fd9\u9053\u6cd5\u672f\u592a\u5371\u9669\uff0c\u53ea\u8981\u88ab\u51fb\u4e2d\uff0c\u8fd0\u6c14\u4e0d\u597d\u5219\u7acb\u523b\u5316\u4f5c\u5fae\u5c18\uff0c\u8fd0\u6c14\u597d\u4e5f\u8981\u91cd\u4f24\u53d7\u521b\uff0c\u4eba\u591a\u6df7\u6742\u7684\u65f6\u5019\u4ed6\u4e0d\u6562\u8f7b\u6613\u4f7f\u7528\u3002\u5982\u4eca\u5353\u5c14\u5deb\u5e08\u81ea\u5df1\u98d8\u6d6e\u8d77\u6765\uff0c\u6b63\u662f\u673a\u4f1a\u3002</p><p>\u4e00\u9762\u534a\u900f\u660e\u7684\u94f6\u76fe\u5728\u5353\u5c14\u5deb\u5e08\u8eab\u4fa7\u51fa\u73b0\uff0c\u7eff\u8272\u5c04\u7ebf\u6253\u5728\u76fe\u9762\u4e0a\u88ab\u6298\u5c04\u51fa\u53bb\uff0c\u5c06\u4ed6\u8eab\u540e\u7684\u77f3\u58c1\u6253\u51fa\u4e86\u5927\u6d1e\u3002\u743c\u6069\u5fae\u5fae\u76b1\u7709\uff0c\u4ed6\u4e00\u65f6\u60f3\u4e0d\u51fa\u6709\u5982\u4f55\u7834\u89e3\u5bf9\u624b\u7684\u6cd5\u672f\u504f\u8f6c\u62a4\u76fe\uff0c\u4fbf\u5728\u6b64\u65f6\uff0c\u6885\u83f2\u65af\u542f\u52a8\u4e86\u6d6e\u7a7a\u9774\uff0c\u5347\u4e0a\u534a\u7a7a\u3002</p><p>\u5353\u5c14\u5deb\u5e08\u6025\u901f\u5f80\u540e\u98d8\u6d6e\uff0c\u4ed6\u9000\u5f97\u5f88\u5feb\uff0c\u6885\u83f2\u65af\u5de6\u624b\u94f6\u5251\u6a2a\u626b\uff0c\u5df2\u7ecf\u843d\u4e86\u4e2a\u7a7a\uff0c\u968f\u5373\u5c06\u53f3\u624b\u7684\u51b0\u5251\u63b7\u4e86\u51fa\u53bb\uff0c\u51c6\u786e\u5730\u8d2f\u7a7f\u5bf9\u624b\u7684\u8179\u90e8\u3002\u5353\u5c14\u5deb\u5e08\u5636\u53eb\u7740\uff0c\u7ef4\u6301\u4e0d\u4f4f\u6cd5\u672f\uff0c\u4ece\u7a7a\u4e2d\u6454\u4e86\u4e0b\u6765\u3002</p><p>\u6885\u83f2\u65af\u6740\u6b7b\u5deb\u5e08\uff0c\u8f6c\u8eab\u653b\u51fb\u5176\u4ed6\u5353\u5c14\u62a4\u536b\uff0c\u5341\u5206\u949f\u4e4b\u540e\uff0c\u6218\u6597\u5f7b\u5e95\u7ed3\u675f\uff0c\u6240\u6709\u7684\u654c\u4eba\u90fd\u5df2\u7ecf\u5012\u4e0b\u3002\u5fc5\u987b\u627f\u8ba4\u7684\u662f\uff0c\u8fd9\u4e9b\u5546\u961f\u96c7\u4f63\u7684\u62a4\u536b\u7d20\u8d28\u5f88\u9ad8\uff0c\u743c\u6069\u624b\u4e0b\u7684\u5353\u5c14\u6218\u58eb\u6218\u6b7b\u4e00\u540d\uff0c\u5176\u4f59\u5168\u90fd\u5e26\u4f24\uff0c\u5fb7\u53e4\u62c9\u8eab\u5148\u58eb\u5352\uff0c\u53d7\u4f24\u6700\u91cd\uff0c\u597d\u5728\u4ed6\u662f\u5438\u8840\u9b3c\uff0c\u6709\u81ea\u6108\u80fd\u529b\u3002</p><p>\u771f\u6b63\u5927\u51fa\u98ce\u5934\u7684\u662f\u4e09\u4e2a\u9b54\u50cf\uff0c\u8d85\u8fc7\u4e00\u534a\u7684\u654c\u4eba\u90fd\u662f\u88ab\u5b83\u4eec\u6740\u6b7b\uff0c\u867d\u7136\u81ea\u8eab\u4e5f\u4f24\u75d5\u7d2f\u7d2f\uff0c\u4f46\u5176\u5b9e\u5e76\u4e0d\u4e25\u91cd\u3002\u8fd9\u6bb5\u65f6\u95f4\u743c\u6069\u5df2\u7ecf\u8bd5\u9a8c\u51fa\u6765\uff0c\u68cb\u5b50\u9b54\u50cf\u867d\u7136\u662f\u7ea2\u7389\u9ad3\u548c\u9ed1\u66dc\u77f3\u6240\u5236\uff0c\u4e0d\u5982\u91d1\u5c5e\u9b54\u50cf\u90a3\u6837\u575a\u56fa\u8010\u6253\uff0c\u4e5f\u76f8\u5bf9\u5bb9\u6613\u88ab\u9b54\u6cd5\u4f24\u5bb3\uff0c\u4f46\u53ea\u8981\u8fd4\u56de\u68cb\u5b50\u72b6\u6001\uff0c\u653e\u7f6e\u5728\u68cb\u76d8\u4e0a\uff0c\u4fbf\u80fd\u7f13\u6162\u81ea\u52a8\u4fee\u590d\u3002\u4ee5\u8fd9\u4e09\u4e2a\u9b54\u50cf\u7684\u635f\u4f24\u72b6\u51b5\uff0c\u4f11\u606f\u56db\u4e94\u5929\u5e94\u8be5\u5c31\u80fd\u5b8c\u5168\u590d\u539f\u3002</p><p>\u9a6e\u7740\u8d27\u7269\u7684\u6d1b\u65af\u517d\u6027\u60c5\u975e\u5e38\u6e29\u987a\uff0c\u867d\u7136\u521a\u624d\u53c8\u662f\u706b\u7130\u53c8\u662f\u780d\u6740\uff0c\u5374\u4e5f\u5e76\u6ca1\u6709\u56db\u6563\u9003\u51fa\u591a\u8fdc\uff0c\u5f88\u5feb\u5c31\u90fd\u88ab\u5fb7\u53e4\u62c9\u7b49\u4eba\u62e6\u622a\u56de\u6765\uff0c\u51c6\u5907\u5e26\u56de\u6668\u70bc\u57ce\u3002\u6885\u83f2\u65af\u8d70\u8fc7\u6765\uff0c\u770b\u89c1\u743c\u6069\u5bf9\u7740\u4e09\u4e2a\u9b54\u50cf\u82e5\u6709\u6240\u601d\uff0c\u4e0d\u7531\u5f97\u6709\u4e9b\u5947\u602a\uff0c\u201c\u600e\u4e48\u4e86\u3002\u201d</p><p>\u201c\u6211\u5728\u60f3\uff0c\u8fd9\u6218\u68cb\u9b54\u50cf\u5f88\u5f3a\u3002\u201d</p><p>\u201c\u662f\u554a\uff0c\u201d\u6885\u83f2\u65af\u70b9\u5934\uff0c\u201c\u786e\u5b9e\u975e\u5e38\u5f3a\u3002\u201d</p><p>\u201c\u4f46\u8fd9\u6837\u5f3a\u5927\u53c8\u597d\u7528\u7684\u9b54\u6cd5\u7269\u54c1\uff0c\u7406\u5f53\u662f\u5f88\u6709\u540d\u624d\u5bf9\uff0c\u4e3a\u4ec0\u4e48\u6211\u5728\u74dc\u7406\u5fb7\u65af\u7684\u65f6\u5019\u4ece\u6765\u6ca1\u542c\u90a3\u4e9b\u5353\u5c14\u4eec\u63d0\u8d77\u8fc7\u4e00\u661f\u534a\u70b9\uff0c\u201d\u743c\u6069\u76b1\u7740\u7709\u5934\uff0c\u201c\u7ed9\u6211\u7684\u611f\u89c9\uff0c\u5c31\u50cf\u662f\u90a3\u4e9b\u5353\u5c14\u4eec\u90fd\u538b\u6839\u4e0d\u77e5\u9053\uff0c\u6216\u8005\u8bf4\u90fd\u4e0d\u8bb0\u5f97\u4ed6\u4eec\u8fd8\u6709\u8fd9\u6837\u4e1c\u897f\u4f3c\u7684\u3002\u201d</p><p>\u201c\u8fd9\u4e1c\u897f\u5927\u6982\u53ea\u6709\u5deb\u5e08\u624d\u80fd\u7528\u5427\u2026\u2026\u201d</p><p>\u201c\u5353\u5c14\u5f53\u4e2d\u4e5f\u4e0d\u4e4f\u9ad8\u660e\u7684\u5deb\u5e08\uff0c\u5c31\u4ee5\u4e9a\u5f53\u65af\u6765\u8bf4\uff0c\u5982\u679c\u4ed6\u4f7f\u7528\u8fd9\u526f\u6218\u68cb\uff0c\u6548\u679c\u80af\u5b9a\u6bd4\u6211\u5f3a\u5f97\u591a\u3002\u4f46\u4e3a\u4ec0\u4e48\u4ed6\u4ece\u6765\u90fd\u4e0d\u7528\u5462\uff0c\u201d\u743c\u6069\u6447\u5934\uff0c\u201c\u5982\u679c\u6362\u4e86\u6211\u662f\u4ed6\uff0c\u51b3\u4e0d\u4f1a\u613f\u610f\u628a\u8fd9\u6837\u73cd\u8d35\u7684\u4e1c\u897f\u62ff\u53bb\u548c\u6076\u9b54\u505a\u4ea4\u6613\u3002\u201d</p><p>\u201c\u8fd9\u4e2a\u53ef\u4e0d\u597d\u8bf4\uff0c\u201d\u6885\u83f2\u65af\u51b7\u9759\u5730\u6307\u51fa\uff0c\u201c\u522b\u5fd8\u4e86\uff0c\u4f60\u8fd9\u526f\u8428\u74e6\u68cb\u5c31\u662f\u548c\u6076\u9b54\u505a\u4ea4\u6613\u5f97\u6765\u7684\u3002\u201d</p><p>\u201c\u8fd9\u4e2a\u4e48\u2026\u2026\u201d</p><p>\u4e0d\u77e5\u4e3a\u4ec0\u4e48\uff0c\u743c\u6069\u8fd8\u662f\u89c9\u5f97\u8fd9\u5176\u4e2d\u6709\u4e9b\u4e0d\u592a\u5bf9\u52b2\uff0c\u4f46\u6b64\u65f6\u5fb7\u53e4\u62c9\u4ece\u65c1\u8fb9\u8d70\u8fc7\u6765\uff0c\u201c\u5148\u751f\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u6211\u53d1\u73b0\u4e86\u4e00\u4e2a\u6d3b\u53e3\u3002\u201d</p><p>\u201c\u5514\uff1f\u201d</p><p>\u743c\u6069\u8f6c\u5934\u4e00\u770b\uff0c\u53d1\u73b0\u5fb7\u53e4\u62c9\u8eab\u65c1\u8fd8\u7ad9\u7740\u4e00\u4e2a\u964c\u751f\u7684\u5353\u5c14\u5973\u5b69\uff0c\u7a7f\u7740\u4e00\u8eab\u7565\u663e\u5bbd\u5927\u7684\u8863\u670d\uff0c\u4ece\u8eab\u6750\u4f53\u683c\u5224\u65ad\uff0c\u4ecb\u4e4e\u4e8e\u201c\u5b69\u5b50\u201d\u548c\u201c\u5c11\u5973\u201d\u4e4b\u95f4\uff0c\u5927\u6982\u76f8\u5f53\u4e8e\u4eba\u7c7b\u7684\u5341\u4e00\u4e8c\u5c81\u6c34\u51c6\uff0c\u4e00\u526f\u602f\u751f\u751f\u7684\u6a21\u6837\uff0c\u6b63\u671d\u8fd9\u8fb9\u770b\u8fc7\u6765\u3002</p><p>\u201c\u5979\u8ddf\u7740\u90a3\u4e9b\u5546\u4eba\u7684\uff0c\u201d\u5fb7\u53e4\u62c9\u89e3\u91ca\uff0c\u201c\u521a\u624d\u6211\u53bb\u7ed9\u6bcf\u4e2a\u6b7b\u8005\u8865\u5200\uff0c\u53d1\u73b0\u4e86\u5979\u3002\u201d</p><p>\u743c\u6069\u6709\u4e9b\u5947\u602a\u3002</p><p>\u8fd9\u6b21\u662f\u51fa\u6765\u62a2\u52ab\uff0c\u81ea\u7136\u4e0d\u80fd\u7559\u6d3b\u53e3\uff0c\u6240\u4ee5\u5fb7\u53e4\u62c9\u624d\u4f1a\u5bf9\u6bcf\u4e2a\u6b7b\u8005\u90fd\u518d\u8865\u4e00\u5200\uff0c\u4ee5\u9632\u4e07\u4e00\u3002\u5982\u4eca\u4ed6\u8fd9\u4e48\u505a\uff0c\u610f\u601d\u5206\u660e\u662f\u8981\u653e\u8fc7\u8fd9\u4e2a\u5973\u5b69\uff0c\u8fd9\u5374\u6709\u4e9b\u5947\u602a\u3002\u5982\u679c\u6362\u4e86\u662f\u6885\u83f2\u65af\u6216\u8005\u743c\u6069\u81ea\u5df1\uff0c\u770b\u5230\u662f\u4e2a\u5c0f\u5973\u5b69\uff0c\u8d77\u4e86\u607b\u9690\u4e4b\u5fc3\uff0c\u90a3\u5012\u8fd8\u7b97\u6b63\u5e38\uff0c\u4f46\u5fb7\u53e4\u62c9\u8fd9\u4e2a\u5438\u8840\u9b3c\u2026\u2026</p><p>\u201c\u5979\u7684\u773c\u775b\u662f\u7d2b\u8272\u7684\u3002\u201d\u5fb7\u53e4\u62c9\u8bf4\u3002</p><p>\u743c\u6069\u4ed4\u7ec6\u518d\u770b\uff0c\u5c0f\u5973\u5b69\u8138\u4e0a\u6709\u4e9b\u6ce5\u6c61\uff0c\u76f8\u8c8c\u4e0d\u751a\u6e05\u695a\uff0c\u4f46\u4e00\u53cc\u773c\u775b\u9887\u6709\u7075\u6c14\uff0c\u800c\u4e14\u786e\u5b9e\u662f\u7d2b\u8272\u7684\uff0c\u5f88\u6f02\u4eae\u2026\u2026\u4e0d\u8fc7\u8fd9\u53c8\u600e\u4e48\u4e86\uff1f</p><p>\u201c\u7d2b\u8272\u773c\u775b\uff0c\u5728\u5353\u5c14\u5f53\u4e2d\u662f\u975e\u5e38\u7f55\u89c1\u7684\uff0c\u201d\u6885\u83f2\u65af\u4f4e\u58f0\u89e3\u91ca\uff0c\u201c\u6709\u4e00\u79cd\u8bf4\u6cd5\uff1a\u7d2b\u8272\u773c\u775b\u7684\u5353\u5c14\u5f80\u5f80\u5584\u826f\u2014\u2014\u8428\u5c14\u74e6\u591a\u5199\u7684\u90a3\u672c\u5c0f\u8bf4\uff0c\u4e3b\u89d2\u5c31\u662f\u7d2b\u8272\u773c\u775b\u3002\u201d</p><p>\u743c\u6069\u6709\u4e9b\u54d1\u7136\u5931\u7b11\uff0c\u773c\u775b\u7684\u989c\u8272\u4e0d\u8fc7\u662f\u5929\u751f\u5916\u8c8c\uff0c\u6700\u591a\u548c\u8840\u7edf\u6709\u5173\uff0c\u600e\u4e48\u80fd\u4ee5\u6b64\u5224\u5b9a\u5584\u6076\u3002\u4f46\u8fd9\u4e5f\u4e0d\u662f\u4ec0\u4e48\u5927\u95ee\u9898\uff0c\u770b\u8fd9\u5c0f\u5973\u5b69\u7684\u8863\u9970\uff0c\u5e76\u975e\u8d35\u65cf\uff0c\u5e74\u9f84\u4e5f\u5f88\u5c0f\uff0c\u8fd8\u6ca1\u6765\u5f97\u53ca\u63a5\u53d7\u796d\u53f8\u6559\u80b2\uff0c\u5e94\u8be5\u8c08\u4e0d\u4e0a\u4ec0\u4e48\u5a01\u80c1\u3002</p><p>\u201c\u5e26\u5979\u56de\u53bb\u5427\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u4e00\u4e2a\u5b69\u5b50\u800c\u5df2\u3002\u201d</p><p>\u743c\u6069\u70b9\u70b9\u5934\u3002</p><p>\u6b64\u5730\u4e0d\u5b9c\u4e45\u7559\uff0c\u5927\u5bb6\u5feb\u901f\u6253\u626b\u6218\u573a\uff0c\u6d88\u9664\u75d5\u8ff9\uff0c\u628a\u5c38\u4f53\u6254\u5230\u504f\u50fb\u5c71\u6d1e\u4e2d\uff0c\u7275\u7740\u4e00\u961f\u6d1b\u65af\u517d\u56de\u57ce\u3002\u5fb7\u53e4\u62c9\u4f3c\u4e4e\u4e0d\u7ecf\u610f\u5730\u8d70\u5230\u743c\u6069\u8eab\u65c1\uff0c\u201c\u8c22\u8c22\u3002\u201d\u4ed6\u8f7b\u58f0\u8bf4\u3002</p><p>\u201c\u80fd\u544a\u8bc9\u6211\u539f\u56e0\u4e48\uff1f\u201d</p><p>\u5438\u8840\u9b3c\u6c89\u9ed8\u4e86\u51e0\u79d2\u949f\uff0c\u201c\u5979\u957f\u5f97\u5f88\u50cf\u8389\u8389\u4e1d\u3002\u201d</p></article>"}}}]);
